LangQuestLookup["deDE"] = {
[2] = {"Klaue von Scharfkralle", {"Der mächtige Hippogryph Scharfkralle wurde getötet und die Klaue der erschlagenen Bestie dient als Beweis für Euren Sieg. Senani Thunderheart im Splintertreeposten wird zum Beweis Eurer Tat sicher gern diese Trophäe sehen wollen."}, {"Bringt die Klaue von Scharfkralle zu Senani Thunderheart im Splintertreeposten in Ashenvale."}},
[5] = {"Jitters' knurrender Magen", {"Ich verstecke mich jetzt schon seit Wochen in dieser Geisterstadt, und die ganze Zeit habe ich nichts gegessen außer Maden und Unkraut! Ich brauche was Anständiges zum Essen, und ich werde mich nicht lumpen lassen.","Bringt mir ein Festessen, und ich bezahle Euch gut dafür.","Ich habe gehört, dass Küchenchef Grual aus der Taverne Zum roten Raben in Dunkelhain ganz ausgezeichnete schwärzliche Krebsküchlein macht..."}, {"Sprecht mit Küchenchef Grual."}},
[6] = {"Kopfgeld auf Garrick Padfoot", {"Garrick Padfoot, ein Dieb und Halsabschneider, der schon seit Wochen unseren Bauern und Händlern das Leben schwer macht, ist bei einem Schuppen in der Nähe der Weinberge gesehen worden. Die Weinberge liegen im Osten der Abtei, auf der anderen Seite der Brücke. Bringt mir den Kopf des Schurken und verdient Euch das Kopfgeld, das auf ihn ausgesetzt ist!","Doch seht Euch vor, <Name>. Garrick hat eine Bande von Rohlingen um sich versammelt, also werdet Ihr nicht leicht an ihn herankommen."}, {"Tötet Garrick Padfoot und bringt seinen Kopf zu Deputy Willem in der Nähe der Abtei von Nordhain."}},
[7] = {"Säuberung im Koboldlager", {"Eure erste Aufgabe ist eine Säuberung, <Name>. Ein Koboldklan hat sich in den Wäldern im Norden eingenistet. Begebt Euch dorthin und bekämpft das Koboldgezücht, das Ihr dort finden werdet. Dezimiert es, damit wir die Kobolde eines Tages gänzlich aus Nordhain vertreiben können."}, {"Tötet 10 von dem Koboldgezücht und kehrt dann zu Marshal McBride zurück."}},
[8] = {"Ein Schurkenhandel", {"Heda, Kamerad! Wollt Ihr nicht einem jungen Mann, der mehr als seinen Teil an hirnlosen Zombies und Spinnen umgelegt hat, einen Gefallen tun?","Ich hab hier einen Brief, der muss nach Brill... zu so einer Gastwirtin namens Renee Irgendwas. Ist eigentlich völlig egal, wie sie mit Nachnamen heißt.","Na egal, jedenfalls ist es ein netter kleiner Ort, wo lauter Opfer der Seuche wohnen, die versuchen, sich irgendwie durchs Leben zu schlagen. Und es wäre ein toller Ort für Euch, an dem Ihr Euch ausruhen könnt, falls das mal nötig wird. Ihr solltet Euch den wirklich mal anseh'n - macht es, und ich bezahl Euch auch noch gut dafür."}, {"Liefert den unscheinbaren Brief bei Gastwirtin Renee in Tirisfal ab."}},
[9] = {"Tod auf den Feldern", {"Jetzt seht Euch nur an, was aus diesem Ort geworden ist! In dieser Gegend lebten einst ehrbare Bauersleute. Die verdammten Diebe haben sie alle vertrieben. Aber mich nicht! Jetzt sieht es allerdings so aus, als hätten einige Erntebehüter sich in den Feldern breit gemacht.","Wenn Ihr Euch dem gewachsen fühlt, dann geht hinaus und tötet dort draußen 20 von ihnen. Wenn Ihr fertig seid, kommt zurück und holt Euch Eure Bezahlung ab. Und wenn Ihr alle in meinem Feld erwischt habt, dann räumt auch in den angrenzenden Feldern auf."}, {"Bauer Saldean möchte, dass Ihr 20 Erntebehüter tötet."}},
[10] = {"Die Erlösung von Scrimshank", {"Oh, das ist einfach schrecklich! Ich habe meinen besten Gehilfen, Unterfeldmesser Scrimshank, in die Wüste südöstlich von hier geschickt. Das ist ein Gebiet, das wir die Klaffende Schlucht nennen. Nun ja, er wird vermisst und in seinem letzten Bericht sprach er davon, dass es dort, wo er war, noch mehr dieser komischen Viecher gäbe. Könnte es sein, dass sie sich jetzt in zwei großen Gebieten der Wüste eingenistet haben?","Begebt Euch bitte für mich zur Klaffenden Schlucht und sucht nach Scrimshank! Das... oder findet wenigstens seine Feldmesserausrüstung. Ich fürchte, das ist alles, was von dem Armen übrig geblieben ist."}, {"Erforscht das Schicksal von Unterfeldmesser Scrimshank und bringt entweder ihn oder seine Feldmesserausrüstung nach Gadgetzan zum Leitenden Feldmesser Fizzledowser."}},
[11] = {"Kopfgeld auf die Flusspfotengnolle", {"Gnolle, brutale Wesen, die in dieser Gegend nichts zu suchen haben, sind entlang der Grenzen des Waldes von Elwynn gesehen worden. Ein großes Rudel, viel zu viele, als dass wir allein mit ihnen fertig werden könnten, hat sich in den Wäldern südlich von jenem Wachturm dort eingenistet. Eine weitere Gruppe hat sich im Gebiet nahe des Steinhügelsees im Osten eingenistet.","Die Armee von Stormwind wird jeden belobigen, der dabei hilft, die Gnolle zu töten. Bringt mir ihre bemalten Gnoll-Armbinden als Beweis für Eure Tat."}, {"Bringt Deputy Rainer in der Kaserne 8 bemalte Gnoll-Armbinden."}},
[12] = {"Die Volksmiliz", {"Die Volksmiliz hat nur ein einziges Ziel: Westfall zu verteidigen und den Frieden in dieses Land zurückzubringen. Doch leider muss der Frieden nur allzu oft mit Blut bezahlt werden.","Einer meiner Späher hat uns Kunde davon gebracht, dass eine Fallenstellerbande der Defias hier in der Nähe verheerende Schäden anrichtet. Mir liegen Berichte vor, dass einige Fallensteller der Defias in der Nähe des Jangoschachts im Nordwesten gesichtet wurden, ebenso bei Molsens Hof und bei Furlbrows Kürbishof. Wenn Ihr unseren Reihen beizutreten wünscht, dann erschlagt 15 Fallensteller der Defias und 15 Schmuggler der Defias und kehrt zu mir zurück."}, {"Gryan Stoutmantle möchte, dass Ihr 15 Fallensteller der Defias und 15 Schmuggler der Defias tötet und zu ihm auf die Späherkuppe zurückkehrt."}},
[13] = {"Die Volksmiliz", {"Eine Bande heimtückischer Plünderer der Defias ist dabei gesehen worden, wie sie den Goldküstensteinbruch, Moonbrook und Alexstons Bauernhof ausplünderten. Die Volksmiliz kann ein solches Verhalten nicht hinnehmen. Macht Euch unverzüglich auf den Weg, <Name>, und zeigt, dass das Licht in Westfall zugegen ist.","Der Goldküstensteinbruch befindet sich in der Nähe der Küste, westlich vom Turm. Im nächsten Schritt Eurer Ausbildung müsst Ihr 15 dieser üblen Plünderer der Defias und 15 Ausplünderer der Defias töten."}, {"Gryan Stoutmantle möchte, dass Ihr 15 Plünderer der Defias und 15 Ausplünderer der Defias tötet und zu ihm auf die Späherkuppe zurückkehrt."}},
[14] = {"Die Volksmiliz", {"Einige Defias konnten wir bislang nicht erwischen. Mein bester Späher berichtet, dass diese Defias in der ganzen Gegend auf Raubzug gehen und schon bis Süd-Westfall gekommen sind. Wir glauben, dass sie sich in den Dolchhügeln versteckt halten und dort ihren nächsten Schachzug planen. Schaltet diese Verbrecher im Namen der Volksmiliz aus."}, {"Gryan Stoutmantle möchte, dass Ihr 15 Straßenräuber der Defias, 5 Pfadpirscher der Defias und 5 Knöchelhauer der Defias tötet und zu ihm auf die Späherkuppe zurückkehrt."}},
[15] = {"Überprüfung der Echokammmine", {"Wie mir meine Späher mitteilen, ist das Koboldproblem größer als wir dachten, <Name>. Eine Gruppe Koboldtagelöhner hat ihr Lager in der Nähe der Echokammmine im Norden aufgeschlagen.","Begebt Euch zu der Mine und entfernt die Kobolde. Wir wissen, dass sich mindestens 10 von ihnen dort aufhalten. Tötet sie, seht nach, ob dort noch mehr von ihnen sind, kommt dann zu mir zurück und erstattet Bericht."}, {"Tötet 10 Koboldtagelöhner und erstattet dann Marshal McBride Euren Bericht."}},
[16] = {"Etwas zu trinken für Gerard", nil, nil},
[17] = {"Reagenzsuche in Uldaman", {"Ich habe von einer neu entdeckten Pflanze gehört, den magenta Funguskappen. Sie wachsen in Büscheln tief drinnen in der Uldaman-Grabungsstätte; vielleicht kann man sie auch anderswo finden, aber bisher ist das der einzige mir bekannte Ort, an dem sie gedeihen. Ich möchte ihren potenziellen Nutzen für die Alchimie erforschen und dafür brauche ich Euch.","Findet die magenta Funguskappen-Büschel und bringt mir ein Dutzend Kappen. Aber seid gewarnt - die Pflanzen stoßen vielleicht Giftsporen aus, wenn man sie anstößt.","Wenn Ihr das für mich tut, dann braue ich Euch einen Schwung von meinem berühmten Regenerierungselixier!"}, {"Bringt zwölf magenta Funguskappen nach Thelsamar zu Ghak Healtouch."}},
[18] = {"Bruderschaft der Diebe", {"Seit kurzem treibt sich eine neue Diebesbande in Nordhain herum. Sie nennen sich die \"Bruderschaft der Defias\" und sind auf der anderen Seite des Flusses im Osten gesehen worden.","Ich weiß nicht genau, was sie vorhaben, aber ich bin mir sicher, es ist nichts Gutes! Bringt mir die Kopftücher, die sie tragen, und ich werde Euch mit einer Waffe belohnen."}, {"Bringt 12 rote Sackleinenkopftücher zu Deputy Willem in der Nähe der Abtei von Nordhain."}},
[19] = {"Tharil'zun", {"Zwischen hier und Burg Steinwacht legen Kundschafter und Renegaten der Blackrock ständig Hinterhalte. Der Anführer der Kundschafter heißt Tharil'zun und diesen Orc wollen wir haben. Bringt mir den Kopf von Tharil'zun!"}, {"Bringt den Kopf von Tharil'zun ins Rotkammgebirge zu Marshal Marris."}},
[20] = {"Bedrohung durch die Blackrock", {"Die Orcs des Blackrockklans haben die brennende Steppe verlassen und Burg Steinwacht im Norden besetzt. Von dort aus ziehen sie marodierend durch das Gebiet nördlich von Seenhain, so dass dessen Bewohner sich nicht mehr aus der Stadt wagen und dort eingesperrt sind wie Vieh in einem zu engen Pferch.","Helft uns, diese Bedrohung von Seenhain zu nehmen! Tötet diese Orcs der Blackrock und bringt mir 10 ihrer kampferprobten Äxte zum Beweis.","Und obwohl die Blackrocks uns vor allem aus dem Norden bedrohen, haben wir auch gehört, dass sich noch weitere in der südöstlichen Ecke des Rotkammgebirges aufhalten. Wenn Ihr dort welche findet, solltet Ihr die auch jagen."}, {"Bringt 10 kampferprobte Äxte nach Seenhain zu Marshal Marris."}},
[21] = {"Scharmützel in der Echokammmine", {"Eure Untersuchungen beweisen, dass die Echokammmine gesäubert werden muss. Kehrt in die Mine zurück und helft dabei, sie von den Kobolden zu befreien.","Ihr dürft keine Zeit verlieren, <Name>. Je länger die Kobolde sich ungehindert in der Mine aufhalten können, umso festere Wurzeln fassen sie in Nordhain."}, {"Tötet 12 Koboldhilfsarbeiter und kehrt dann zu Marshal McBride in der Abtei von Nordhain zurück."}},
[22] = {"Pastete aus Geiferzahnleber", {"Die Zwiebeln sind geschält, der Knoblauch ist gehackt, der Rosmarin ist zerstoßen, die Kruste ist gebacken, das Dillkraut ist gewiegt und die Sauce steht auf dem Herd. Jetzt brauche ich für meine berühmte Fleischpastete nur noch 8 Geiferzahnlebern!"}, {"Salma Saldean braucht 8 Geiferzahnlebern für ihre Pastete aus Geiferzahnleber."}},
[23] = {"Ursangus' Tatze", {"Die Tatze des mächtigen Bären Ursangus stellt die Trophäe für diesen Teil der Jagd in Ashenvale dar. Auf der Tatze befindet sich ein kleines Halbmond-Emblem; die Bedeutung dieses Emblems bleibt jedoch ein Geheimnis.","Die Tatze soll zu Senani Thunderheart im Splintertreeposten gebracht werden, um zu beweisen, dass Ihr das mächtige Tier besiegt und diesen Teil der Jagd abgeschlossen habt."}, {"Bringt Ursangus' Tatze zu Senani Thunderheart im Splintertreeposten in Ashenvale."}},
[24] = {"Schattumbras Kopf", {"Der einstmals so flinke Schattumbra liegt jetzt regungslos vor Euch und der Sieg ist Euer. Der Kopf des erschlagenen Nachtsäblers ist eine passende Trophäe für diesen Kampf. Senani Thunderheart im Splintertreeposten wird eine solche Trophäe sehen wollen, um Euch den Erfolg in diesem Teil der Jagd in Ashenvale anzurechnen."}, {"Bringt Schattumbras Kopf zu Senani Thunderheart im Splintertreeposten in Ashenvale."}},
[25] = {"Stillstand am Steinkrallenpfad", {"Wenn Ihr Euch nützlich machen wollt, warum unternehmt Ihr dann nicht etwas wegen der Situation am Steinkrallenpfad?","Der Güterverkehr zwischen Ashenvale und dem Steinkrallengebirge steht still, weil der See zum Leben erwacht ist und alles angreift, was ihm zu nahe kommt... und das bedeutet, jeden auf dem Pfad. Noch weitaus nützlicher als einfach nur die Wasserelementare zu unterjochen, wäre es, wenn jemand den Pavillon auf dem Mystralsee auskundschaften würde... der, von dem aus man einen guten Blick auf den dortigen Außenposten der Allianz hat.","Na, wollt Ihr Euch nützlich machen?"}, {"Erledigt 12 beschmutzte Wasserelementare am Mystralsee östlich vom Steinkrallenpfad und südwestlich vom Splintertreeposten.","Erkundet den Pavillon am Mystralsee, der den nahen Außenposten der Allianz überblickt.","Kehrt zu Mastok Wrilehiss im Splintertreeposten in Ashenvale zurück."}},
[26] = {"Eine wertvolle Lektion", {"<Name>, ich habe Euren Fortschritt mit großem Genuss verfolgt. Ihr entwickelt Euch definitiv zu einem wertvollen Beschützer der Natur.","Es ist an der Zeit für Euch, mehr über einen der diversen Tieraspekte zu lernen, in die wir uns dank Cenarius' Segen verwandeln können... einen, der Euch die Beherrschung des Wassers ermöglicht. Dazu müsst Ihr zum Dorf Nighthaven in Moonglade reisen und mit Dendrite Starblaze vom Zirkel des Cenarius sprechen. Er wird Euch weiter anleiten."}, {"Sprecht mit Dendrite Starblaze im Dorf Nighthaven in Moonglade."}},
[27] = {"Eine wertvolle Lektion", {"<Name>, ich habe Euren Fortschritt mit großem Genuss verfolgt. Ihr entwickelt Euch definitiv zu einem wertvollen Beschützer der Natur.","Es ist an der Zeit für Euch, mehr über einen der diversen Tieraspekte zu lernen, in die wir uns dank Cenarius' Segen verwandeln können... einen, der Euch die Beherrschung des Wassers ermöglicht. Dazu müsst Ihr zum Dorf Nighthaven in Moonglade reisen und mit Dendrite Starblaze vom Zirkel des Cenarius sprechen. Er wird Euch weiter anleiten."}, {"Sprecht mit Dendrite Starblaze im Dorf Nighthaven in Moonglade."}},
[28] = {"Prüfung des Sees", {"Als Eure erste Prüfung müsst Ihr in den Tiefen des Elune'ara Sees nach einem Schreinschmuckstück suchen. Es zerfällt schnell, daher solltet Ihr Euch mit größter Eile zum Schrein von Remulos begeben. Benutzt das Schmuckstück am Schrein und sprecht dann dort mit Tajarri; sie ist eine der vertrauenswürdigsten Aufseherinnen von Moonglade.","Ihr habt nur eine gewisse Zeitspanne für diese Aufgabe zur Verfügung, und Ihr könntet in Konkurrenz zu anderen Druiden stehen, die ebenfalls nach Schmuckstücken suchen. Geistiges wie körperliches Geschick wird für Euren Erfolg unabdingbar sein.","Viel Glück, <Name>."}, {"Sucht ein Schreinschmuckstück im Elune'ara See und bringt es zum Schrein von Remulos im Nordwesten von Moonglade. Wenn Ihr dort seid, benutzt das Schrein-Schmuckstück.","Ihr müsst anschließend mit Tajarri am Schrein sprechen, um die Prüfung abzuschließen."}},
[29] = {"Prüfung des Sees", {"Als Eure erste Prüfung müsst Ihr in den Tiefen des Elune'ara Sees nach einem Schreinschmuckstück suchen. Es zerfällt schnell, daher solltet Ihr Euch mit größter Eile zum Schrein von Remulos begeben. Benutzt das Schmuckstück am Schrein und sprecht dann dort mit Tajarri; sie ist eine der vertrauenswürdigsten Aufseherinnen von Moonglade.","Ihr habt nur eine gewisse Zeitspanne für diese Aufgabe zur Verfügung, und Ihr könntet in Konkurrenz zu anderen Druiden stehen, die ebenfalls nach Schmuckstücken suchen. Geistiges wie körperliches Geschick wird für Euren Erfolg unabdingbar sein.","Viel Glück, <Name>."}, {"Sucht ein Schreinschmuckstück im Elune'ara See und bringt es zum Schrein von Remulos im Nordwesten von Moonglade. Wenn Ihr dort seid, benutzt das Schrein-Schmuckstück.","Ihr müsst anschließend mit Tajarri am Schrein sprechen, um die Prüfung abzuschließen."}},
[30] = {"Prüfung des Seelöwen", {"Für die zweite Prüfung benötigt Ihr die zwei Hälften, aus denen der Anhänger des Seelöwen besteht. Die eine Hälfte bezieht ihre Kraft aus der Beweglichkeit des Seelöwen-Aspektes beim Gleiten durchs Wasser; die andere Hälfte bezieht ihre Kraft aus der unglaublichen Ausdauer des Seelöwen-Aspektes. Druiden bedienen sich dieser beiden Eigenschaften gleichermaßen, um den Aspekt ihrer Wassergestalt zu verkörpern.","Sprecht mit den Bewohnern von Moonglade, um herauszufinden, wo sich die Teile befinden könnten, und bringt beide hierher, um sie miteinander zu vereinen. Bringt den Anhänger zu Dendrite Starblaze, wenn Ihr ihn geschaffen habt."}, {"Sucht die Anhängerhälfte der Wasserbeweglichkeit und die Anhängerhälfte der Wasserausdauer. Sprecht mit den Einwohnern von Moonglade, um Hinweise darauf zu erhalten, wo sich diese Gegenstände befinden könnten.","Formt den Anhänger des Seelöwen aus den beiden Anhängerhälften. Ihr müsst in der unmittelbaren Nähe des Schreins von Remulos sein, um dies zu tun.","Bringt den zusammengefügten Anhänger zu Dendrite Starblaze nach Nighthaven in Moonglade."}},
[31] = {"Wassergestalt", {"Ihr habt die notwendigen Lektionen gemeistert, die alle jungen Druiden lernen, bevor sie einen Wasseraspekt annehmen können. Kehrt jetzt zu Eurem Lehrer Turak Runetotem nach Thunder Bluff zurück. Zeigt ihm Euren Anhänger und beweist ihm damit, dass Ihr bereit seid zu lernen, was er Euch lehren kann. Er wird Eure Ausbildung abschließen und Euch ermöglichen, eins mit dem Wasser zu werden.","Auf Wiedersehen. Wir werden uns wieder sprechen."}, {"Kehrt nach Thunder Bluff zurück und zeigt Turak Runetotem den Anhänger des Seelöwen."}},
[32] = {"Aufstieg der Silithid", {"Hier ist Euer Bericht. Ihr solltet damit zu jemandem gehen, der ausreichend Kenntnis von solchen Dingen hat.","Ich kenne da einen Troll-Gelehrten mit Namen Zilzibin Drumlore. Er hat viel Zeit mit dem Studium der Lebensformen verbracht, die in der Ökologie von Azeroth einzigartig sind, und er ist noch dazu Historiker! Wenn Euch jemand helfen soll herauszufinden, was genau diese Bedrohung ausmacht, und wie sie zu stoppen ist, gebt ihm diesen Bericht. Hilfe können wir gar nicht genug haben.","Zil lebt in einem Haus auf der zweiten Ebene der Gasse in Orgrimmar."}, {"Bringt den Bericht nach Orgrimmar zu Zilzibin Drumlore."}},
[33] = {"Wölfe an der Grenze", {"Ich hasse diese grässlichen Waldwölfe! Aber Wolfsteaks esse ich für mein Leben gern. Bringt mir zähes Wolfsfleisch, dann gebe ich Euch etwas dafür, das Euch von Nutzen sein wird.","Zähes Wolfsfleisch bekommt Ihr, wenn Ihr die Waldwölfe und jungen Wölfe jagt, die in Nordhain durch das Land streifen."}, {"Bringt Eagan Peltskinner, den Ihr vor der Abtei von Nordshire findet, 8 Stücke zähes Wolfsfleisch."}},
[34] = {"Ein ungebetener Gast", {"Mein Garten ist schon wieder zertrampelt und verwüstet worden. Und ich weiß genau, was für ein Tier das getan hat. Das war dieser elende Eber, der, den Marshal Marris seit einer Weile immer \"Stopfwanst\" nennt.","Jetzt kann ich die Narzissen erst nächsten Herbst wieder anpflanzen. Hin! Alle hin, und das nur wegen diesem einen gefährlichen Eber. Bereitet dieser Plage ein Ende! Zeigt mir seinen Hauer und ich werde Euch nur zu gern belohnen. Die meiste Zeit scheint er damit zu verbringen, in den Feldern südwestlich von Seenhain nach Futter zu suchen, doch gelegentlich verirrt er sich auch hierher und in meinen Garten."}, {"Martie Jainrose aus Seenhain möchte, dass Ihr Stopfwanst tötet. Bringt ihr den Hauer des Ebers zum Beweis."}},
[35] = {"Weitere Sorgen", {"Falls Ihr befürchtet, dass die Gerüchte über das Auftauchen von Murlocs wahr sein könnten, dann tut das Folgende: Begebt Euch zur östlichen Elwynn-Brücke und sprecht mit Wache Thomas. Er ist seit einer Woche an der Brücke stationiert und wird deswegen wissen, wie es in der Gegend aussieht.","Bringt mir seinen Bericht."}, {"Marshal Dughan möchte, dass Ihr mit Wache Thomas sprecht."}},
[36] = {"Westfall-Eintopf", {"Ich hätte nie gedacht, dass jemals der Tag kommen würde, an dem ich den Hof verlasse. Aber in den Feldern wimmelt es nur so von Dieben und es ist hier für uns viel zu gefährlich geworden. Sobald Bauer Furlbrow den Wagen repariert hat, machen wir uns auf den Weg.","Könntet Ihr mir vielleicht einen Gefallen tun? Ich schreibe Euch schnell mein Rezept für Westfall-Eintopf auf. Seid so nett und bringt es Salma Saldean; sie wohnt auf dem Hof dort hinten. Ihr findet den Hof der Saldeans gleich hinter der Gabelung der Straße."}, {"Verna Furlbrow möchte, dass Ihr ihr Rezept für Westfall-Eintopf bei Salma Saldean abliefert."}},
[37] = {"Suche nach den vermissten Wachen", {"Vor ein paar Tagen haben wir zwei Wachen, Rolf und Malakai, losgeschickt, damit sie entlang des Flusses Nachforschungen anstellen, aber bisher sind sie noch nicht zurückgekehrt. Damit ich meinen Bericht fertig schreiben kann, muss ich wissen, was den beiden Männern zugestoßen ist.","Sucht den Fluss in Richtung Norden ab und seht zu, dass Ihr die Wachen findet... oder ihre Überreste..."}, {"Wache Thomas möchte, dass Ihr den Fluss in Richtung Norden nach Rolf und Malakai, den beiden vermissten Wachen, absucht."}},
[38] = {"Westfall-Eintopf", {"Wollt Ihr mir dabei helfen, Westfall-Eintopf zu kochen? Dann bringt mir die folgenden Zutaten:","3 Stücke sehniges Geierfleisch","3 Geiferzahnschnauzen","3 Murlocaugen","3 Okraschoten"}, {"Salma Saldean möchte 3 Stücke sehniges Geierfleisch, 3 Geiferzahnschnauzen, 3 Murlocaugen und 3 Okraschoten."}},
[39] = {"Zustellung von Thomas' Bericht", {"Berichtet Marshal Dughan, dass Malakai und Rolf ums Leben gekommen sind, und teilt ihm auch mit, dass wir die Murlocs in Ost-Elwynn mit unserer derzeitigen Truppenstärke hier nicht unter Kontrolle halten können.","Ich weiß, dass wir nicht viele überzählige Männer haben, aber vielleicht findet Dughan ja doch jemanden."}, {"Erstattet Marshal Dughan in Goldhain Bericht."}},
[40] = {"Gefahr aus dem Wasser", {"Eine neue Gefahr bedroht den Wald von Elwynn, <Name>! Murlocs kommen die Bäche und Flüsse im östlichen Elwynn hinaufgeschwommen, vertreiben die Fische und greifen einfache Leute an!","Ich habe Marshal Dughan bereits gewarnt, aber der macht sich größere Sorgen wegen der Gnolle und der Banditen. Er ist nicht überzeugt davon, dass die Murlocs eine Gefahr darstellen.","Bitte sprecht mit Dughan, <Name>, und bringt ihn dazu, dass er Verstärkung in den Osten schickt!"}, {"Remy \"Zweifach\" möchte, dass Ihr in Goldhain mit Marshal Dughan sprecht."}},
[45] = {"Rolfs Schicksal", {"Als Ihr die Gegend weiter absucht, findet Ihr die Abdrücke von Füßen mit Schwimmhäuten, die entlang dem Ufer des Steinhügelsees nach Osten führen. In der Ferne könnt Ihr im Osten gerade noch so ein Murlocdorf ausmachen.","Vielleicht fand Rolfs Leben dort sein Ende..."}, {"Durchsucht das Murlocdorf nach Rolf - oder nach Anzeichen für seinen Tod."}},
[46] = {"Kopfgeld auf die Murlocs", {"Die Armee von Stormwind hat ein Kopfgeld auf die Murloclauerer und Murlocfutterwühler ausgesetzt, die sich in Elwynn herumtreiben. Erlegt sie und bringt mir ihre zerrissenen Murlocflossen, dann wird die Armee von Stormwind Euch ansehnlich belohnen","Die Murlocs haben am Steinhügelsee nördlich von hier ein Dorf errichtet und ein weiteres ihrer Dörfer befindet sich im Süden, wo der Fluss sich teilt."}, {"Bringt 8 zerrissene Murlocflossen zu Wache Thomas, den ihr an der östlichen Elwynn-Brücke findet."}},
[47] = {"Tauschhandel mit Goldstaub", {"Manchmal haben die Kobolde in dieser Gegend Goldstaub dabei. Das Zeug könnte ich wirklich gut gebrauchen. Bringt mir etwas davon und ich mache Euch den besten Preis in der Stadt... den besten Preis in der Stadt!","Die Kobolde halten sich in der Tiefenschachtmine im Süden und im Bereich der Jaspismine im Nordosten auf."}, {"Bringt 10 Einheiten Goldstaub nach Goldhain zu Remy \"Zweifach\". Goldstaub findet Ihr bei den Kobolden im Wald von Elwynn."}},
[48] = {"Goldbrauner Samt", {"Weit im Süden, wo die Lianen sich ranken,","Liegt versteckt ein Brunnen, in dem fast versanken","Die wertvollen Wasser, so rein und so helle -","Bringt mir Wasser von der Heiligen Quelle."}, {"Bringt Grimbooze Donnerbräu Wasser von der Heiligen Quelle."}},
[49] = {"Goldbrauner Samt", {"Lang schon vermiss ich die Lieb meines Lebens,","Goldbraun und samtig, doch noch wart ich vergebens.","Bringt mir, dann vergeht jeder Zorn,","Je einen Sack Gerste, Roggen und Korn."}, {"Grimbooze Donnerbräu möchte einen Sack Gerste, einen Sack Roggen und einen Sack Korn haben."}},
[50] = {"Goldbrauner Samt", {"Die Maische anrühren, das ist mein Wille,","Doch fehlt mir dann auch noch eine Destille.","Für diese brauch ich ein starkes Metall -","Bringt mir Echtsilber, das ist gut in dem Fall!"}, {"Grimbooze Donnerbräu braucht Echtsilber."}},
[51] = {"Goldbrauner Samt", {"Noch ist er nicht fertig, doch schon frisch gebrannt,","Jetzt wird als Nächstes der Meiler bemannt,","Um den Whisky zu filtern. Drum bringt mir den Ast","Eines Bergahorns - wenn Ihr könnt, ohne Rast!"}, {"Bringt Grimbooze Donnerbräu den Ast eines Bergahorns."}},
[52] = {"Grenzschutz", {"Seid gegrüßt, <Name>. Je weiter wir uns von Goldhain entfernen, umso reizbarer sind die wilden Tiere. Und das Holzfällerlager des Osttals wird beinahe ständig von Wölfen und Bären angegriffen.","Wir könnten hier draußen wirklich Eure Hilfe gebrauchen."}, {"Tötet 8 Streuner und 5 junge Waldbären und kehrt dann zu Wache Thomas an der östlichen Elwynn-Brücke zurück."}},
[53] = {"Goldbrauner Samt", {"Das Getränk, das ich braue, das reine und klare,","Wird goldbraun und samtig im Laufe der Jahre.","Doch ehe er fertig, der Whisky, der weiche,","Braucht's noch ein Fass aus verkohlter Eiche."}, {"Bringt Grimbooze Donnerbräu ein verkohltes Eichenbündel."}},
[54] = {"Meldung in Goldhain", {"<Name>, Ihr habt bewiesen, dass Ihr als <Klasse> Euch um die Sicherheit von Nordhain sorgt. Nun erhaltet Ihr die Aufgabe, Euch auch dem Schutz des umliegenden Waldes von Elwynn zu widmen.","Wenn Ihr Euch dieser Aufgabe stellen wollt, dann nehmt diese Papiere, die bei Marshal Dughan in Goldhain abgeliefert werden müssen. Goldhain liegt an der südlichen Straße, hinter den Grenztoren."}, {"Liefert Marshal McBrides Unterlagen bei Marshal Dughan in Goldhain ab."}},
[55] = {"Morbent Fel", {"Morbent Fel hält sich in seinem Unterschlupf verborgen, dem Haus, das hoch oben auf dem Hügel im Osten über dem Friedhof von Rabenflucht thront. Doch seine Zeit in diesem Land neigt sich dem Ende entgegen...","Setzt Morbents Bann gegen ihn ein. Das wird seine schützenden Zauber machtlos machen.","Erschlagt ihn. Erschlagt ihn und errettet uns von seiner Verderbtheit. Werdet zum Instrument meiner Rache und damit <zum Helden/zur Heldin> des Dämmerwalds!"}, {"Verwendet Morbents Bann gegen Morbent Fel, tötet Morbent Fel und bringt Morbents Bann anschließend zu Sven in dessen Lager zurück."}},
[56] = {"Die Nachtwache", {"Als Kommandantin der Nachtwache habe ich geschworen, die Bürger von Dunkelhain zu beschützen. Stormwind hat alle offiziell ernannten Wachen von ihren Posten hier abgezogen; damit sind wir uns selbst überlassen.","Wenn Ihr glaubt, der Aufgabe gewachsen zu sein, könnten wir Eure Hilfe wirklich gebrauchen! Helft der Nachtwache und wagt Euch in den Friedhof \"Stille Gärten\", der ein Stückchen südlich von Dunkelhain liegt. Tötet dort 8 Skelettkieger und 6 Skelettmagier. Der Bedrohung durch die Untoten muss ein Ende gemacht werden!"}, {"Kommandantin Althea Ebonlocke aus Dunkelhain möchte, dass Ihr 8 Skelettkrieger und 6 Skelettmagier tötet."}},
[57] = {"Die Nachtwache", {"Ich habe berichtet bekommen, dass im Wald eine wahre Untotenplage ausgebrochen ist, <Name>. Wenn Ihr Euch der Aufgabe gewachsen fühlt, dann macht Euch auf den Weg zum Friedhof von Rabenflucht und erschlagt 15 dieser abscheulichen Skelettsatanskreaturen und 15 Skelettschrecken. Außerdem sollen im Friedhof \"Stille Gärten\", der sich ein Stückchen südlich von Dunkelhain befindet, Skelette gesehen worden sein. Die Untoten müssen aus dem Dämmerwald vertrieben werden!"}, {"Kommandantin Althea Ebonlocke aus Dunkelhain möchte, dass Ihr 15 Skelettsatanskreaturen und 15 Skelettschrecken tötet."}},
[58] = {"Die Nachtwache", {"Die Lage in Rabenflucht ist noch immer sehr bedenklich. Die Sicherheit von Dunkelhain liegt in Euren Händen, <Name>. Ich habe einfach nicht genug Behüter, um die Sicherheit in der Stadt zu gewährleisten. Deswegen müsst Ihr Euch erneut nach Rabenflucht aufmachen und das östliche Mausoleum von den Seuchenverbreitern säubern, die sich dort breit gemacht haben. Tötet 20 von ihnen!"}, {"Kommandantin Althea Ebonlocke aus Dunkelhain möchte, dass Ihr im östlichen Mausoleum von Rabenflucht 20 Seuchenverbreiter tötet."}},
[59] = {"Rüstung aus Tuch und aus Leder", {"Als Dank für Eure Gewitztheit und Euren Mut gebe ich Euch hier einen Gutschein, den Ihr gegen ein Rüstungsteil eintauschen könnt. Bringt den Gutschein ins Holzfällerlager des Osttals zu Sara Timberlain. Wenn Ihr ihr den Gutschein gebt, dann stellt sie die Rüstung für Euch her. Und wenn Ihr sie habt, tragt sie zur Verteidigung von Elwynn, <Name>.","Ihr findet das Holzfällerlager des Osttals im Osten jenseits von Wache Thomas' Posten."}, {"Gebt Sara Timberlain den Rüstungsgutschein der Armee von Stormwind."}},
[60] = {"Koboldkerzen", {"Guten Tag, <werter Herr/werte Dame>! Hättet Ihr einen Moment Zeit für mich?","Mein Bruder und ich führen in Stormwind eine Apotheke und ich bin hier, weil ich große Kerzen suche, ihres Wachses wegen. Könnt Ihr mir vielleicht helfen?","Große Kerzen könnt Ihr von Kobolden bekommen - und ich habe Gerüchte gehört, dass Kobolde sich in den Minen von Elwynn eingenistet haben: in der Tiefenschachtmine im Süden und in der Jaspismine im Osten. Ich schlage vor, dass Ihr Euch an einem dieser Orte auf die Suche nach Kerzen begebt."}, {"Bringt 8 große Kerzen nach Goldhain zu William Pestle."}},
[61] = {"Lieferung nach Stormwind", {"In Stormwind wartet mein Bruder Morgan auf die Lieferung der Kerzen. Ich habe selbst nicht die Zeit, um mich auf die Reise zu begeben, aber wenn Ihr ihm die Lieferung überbringen wollt, so wird er Euch gut bezahlen.","Ich habe die Kerzen schon fertig verpackt. Ihr findet Morgan in unserem Geschäft im Handelsdistrikt von Stormwind. Es heißt Pestles Apotheke."}, {"Bringt Williams Lieferung in den Handelsdistrikt von Stormwind zu Morgan Pestle."}},
[62] = {"Die Tiefenschachtmine", {"Die Mine in Nordhain ist nicht die einzige, mit der es Probleme gibt. Berichten zufolge ist auch die Tiefenschachtmine im Wald von Elwynn zu einem Tummelplatz für Kobolde geworden.","Erforscht die Mine, bestätigt diese Berichte und kehrt dann zu mir zurück. Die Mine liegt fast genau südlich von Goldhain, zwischen den Höfen der Stonefields und der Maclures."}, {"Erforscht die Tiefenschachtmine und kehrt dann nach Goldhain zu Marshal Dughan zurück."}},
[63] = {"Ruf des Wassers", {"An der Küste des Silberwalds befindet sich westlich des Grabmals ein Schrein, der von der Seuche besudelt ist. Die Geister dort sprechen nicht länger zu unserem Volk und es ist jetzt an Euch, die Manifestation dort zu besiegen und den Teich zu säubern.","Nehmt die Armschienen des Elementars und gebt sie zusammen mit den verbleibenden Tropfen von Brines Trank in die Kohlenpfanne. Das sollte ausreichen, um einen reinen Geist zu beschwören, mit dem Ihr kommunizieren könnt.","Bringt mir einen Beweis dafür, dass Ihr die Tat vollbracht habt, dann belohne ich Euch mit Eurem Totem."}, {"Besiegt die verderbte Wassermanifestation und legt die Armschienen der verderbten Manifestation und die verbleibenden Tropfen reinsten Wassers im Silberwald auf die Kohlenpfanne von Everfount."}},
[64] = {"Das vergessene Erbstück", {"Es war fürchterlich! Verna hat mich aufgeweckt, als sie draußen auf dem Feld so einen Radau gehört hat. Das ganze Feld war voller Rowdys! Wir sind in solcher Eile aufgebrochen und da hab ich vergessen, meine Taschenuhr einzupacken. Die Uhr hat mir Vernas Vater an unserem Hochzeitstag geschenkt und es wird mir fast schlecht, wenn ich dran denke, dass diese Diebe sie jetzt haben. Ich hab die Taschenuhr im Kleiderschrank in unserem Haus liegen lassen. Haltet nach dem Kürbisfeld im Westen Ausschau, dann kommt Ihr zu unserem Hof - Ihr könnt ihn gar nicht verfehlen. Wenn Ihr mir meine Uhr wiederbringen würdet, wäre ich Euch sehr dankbar."}, {"Bauer Furlbrow möchte, dass Ihr seine Taschenuhr holt. Sie befindet sich im Kleiderschrank des Bauernhauses auf der Kürbisfarm im Westen."}},
[65] = {"Die Bruderschaft der Defias", {"Die Bruderschaft der Defias nennt sich die Bande von Schuften, von der die braven Leute von Westfall aus dem Land vertrieben werden. Ihr müsst für mich diese Bande bespitzeln. Wir müssen erfahren, wer der Kopf der Gruppe ist und wo sie ihr Versteck hat. In Seenhain findet Ihr im Gasthaus einen Schurken namens Wiley, der mir noch einen Gefallen schuldet.","Begebt Euch nach Seenhain im Rotkammgebirge, östlich von Elwynn, und findet heraus, was Ihr könnt."}, {"Gryan Stoutmantle möchte, dass Ihr in Seenhain mit Wiley sprecht."}},
[66] = {"Die Legende von Stalvan", {"Letzte Nacht verspürte ich in meinem Geist eine schreckliche Erschütterung. Ich wusste, dass sich meine Enkelin Alyssa in großer Gefahr befand. Ich befragte die Karten und aus ihnen starrte mich die Fratze des Todes höhnisch an.","Ich begab mich auf eine lange Reise durch eine dunkle Trance und schließlich gelang es mir, einen Hinweis auf dieses schreckliche Geheimnis zu erhaschen. Ein Name erschloss sich mir, der Name Stalvan.","Sucht den Amtmann im Rathaus auf und schaut, ob Ihr nicht mehr über diese Person herausfinden könnt. Ich fürchte um unser aller Leben."}, {"Sucht Amtmann Daltry im Rathaus von Dunkelhain auf."}},
[67] = {"Die Legende von Stalvan", {"Stalvan, hm? Lasst mich im Stadtverzeichnis nachsehen.","Stalvan... Stalvan... mal sehen. Ah, hier ist es! Ich habe hier einen Eintrag über einen gewissen Stalvan Mistmantle. Die letzte bekannte Adresse ist die Schule in Moonbrook. Liebe Güte, das nenne ich veraltet!","Würdet Ihr mir einen Gefallen tun? Falls Ihr zufällig nach Moonbrook kommt, dann lasst es mich wissen, wenn Ihr an neue Informationen über diese Person geraten solltet. Ich habe meine Unterlagen gern auf dem neuesten Stand."}, {"Begebt Euch zur Schule in Moonbrook und bringt Amtmann Daltry alle neuen Informationen über Stalvan Mistmantle."}},
[68] = {"Die Legende von Stalvan", {"In der alten Schließkiste findet Ihr einen verstaubten, nie abgeschickten Brief."}, {"Bringt Amtmann Daltry den verstaubten, nie abgeschickten Brief."}},
[69] = {"Die Legende von Stalvan", {"Ach du liebes bisschen! Das ist mir beim ersten Mal ja glatt entgangen! Hier im Verzeichnis steht beim Eintrag für diesen Stalvan direkt unter der ersten Adresse noch eine weitere, die zum Teil durchgestrichen ist. Es scheint, als wäre er zum Gasthaus Zur Höhle des Löwen drüben in Goldhain unterwegs gewesen. Vielleicht solltet Ihr dort nachfragen, <Name>."}, {"Begebt Euch zum Gasthaus Zur Höhle des Löwen und fragt dort nach, ob jemand schon einmal den Namen Stalvan gehört hat."}},
[70] = {"Die Legende von Stalvan", {"Den Namen Stalvan hab ich schon mal gehört... Ach, jetzt fällt's mir wieder ein.","Vor vielen Jahren betrat an einem stürmischen Abend ein Bote dieses Haus, der Unterkunft für die Nacht suchte. Es war noch nicht ganz Mitternacht, da kam der Mann schreiend die Treppe heruntergerannt. Er war bleich wie der Tod vor Angst. Nur mit seinem Nachtgewand bekleidet verschwand er im Regen.","In der Eile vergaß er seine Briefe in der Truhe oben und kam nie zurück, um sie abzuholen. Es ist ein Brief dabei von diesem Stalvan, den Ihr erwähntet, adressiert an den Kanaldistrikt in Stormwind. Ihr dürft ihn gern haben."}, {"Holt Stalvans niemals zugestellten Brief aus der Truhe und liefert ihn im Kanaldistrikt von Stormwind ab."}},
[71] = {"Meldung an Thomas", {"Nun habt Ihr beide Medaillons. Bringt sie an die Brücke zu Wache Thomas, damit er erfährt, welches Schicksal seine beiden Kameraden erlitten haben."}, {"Bringt die Medaillons von Rolf und Malakai zu Wache Thomas an die östliche Elwynn-Brücke."}},
[72] = {"Die Legende von Stalvan", {"Mein Vater war der Verwalter des Besitzes, lange bevor ich es wurde. Nach dem Massaker war er derjenige, der das Blut aufwischen und aufräumen musste. Aber das tut nichts zur Sache.","Die letzten Gelder aus dem Flintridge-Fonds sind versiegt und jetzt müssen die letzten Stücke aus dem Familienbesitz versteigert werden. Daran sind nur diese Steuereintreiber schuld. Aber wenn Ihr wirklich dringend mehr erfahren wollt, dann dürft Ihr diesen Kram hier gern durchsehen. Wer weiß, was Ihr findet."}, {"Durchsucht die verpackten Besitztümer nach Hinweisen."}},
[74] = {"Die Legende von Stalvan", {"In der Kiste findet Ihr verschiedene Dinge: muffige Erbstücke, ein Familienportrait, einige Jagdtrophäen und ein paar alte Bücher. Fast schon am Boden der Kiste findet Ihr unter einer Keramikvase eine zerrissene Tagebuchseite."}, {"Die zerrissene Tagebuchseite, die Ihr gefunden habt, gibt Euch den nächsten Hinweis."}},
[75] = {"Die Legende von Stalvan", {"Als ich hier eingezogen bin, stand oben schon eine Truhe mit Pergamenten darin. Kurz nach meinem Einzug habe ich sie mir einmal angesehen, aber bald darauf konnte ich nur noch verschwommen sehen und da hab ich's dann gelassen.","Tut einem alten, halb blinden Mann einen Gefallen und seht Euch selbst mal in der Kiste oben um, ob Ihr vielleicht etwas findet, das Euch bei Eurer Suche nach diesem Stalvan weiterhelfen kann. Ich bin mir ziemlich sicher, dass da eine verblasste Tagebuchseite dabei ist, die Euch interessieren müsste. Bringt sie mir und ich werde sehen, was ich tun kann, um Euch zu helfen."}, {"Marshal Haggard möchte, dass Ihr in der Kiste oben nach der verblassten Tagebuchseite sucht."}},
[76] = {"Die Jaspismine", {"Dank Euch wissen wir jetzt, dass Kobolde sich in der Tiefenschachtmine eingenistet haben. Jetzt brauchen wir noch einen Späher, der die weiter entfernte Jaspismine untersucht.","Erforscht die Jaspismine und bestätigt mir, ob sich dort tatsächlich Kobolde aufhalten. Die Mine erreicht Ihr, indem Ihr der Straße nach Osten folgt, bis Ihr zum Turm von Azora kommt. Wenn Ihr Euch am Turm nach Norden wendet, dann findet Ihr die Mine im Vorgebirge."}, {"Erforscht die Jaspismine und kehrt dann nach Goldhain zu Marshal Dughan zurück, um ihm Bericht zu erstatten."}},
[77] = {"Eine schwierige Situation", {"Ich besitze Originalverpackungsmaterial für die Welle, die Dran Droffers will. Ich werde Euch dabei helfen, die Welle für Maltons ungehobelten Vater zu verpacken... für einen Unkostenbeitrag in Höhe zweier weiterer Flaschen besagter Welle. Sie befinden sich in Kisten, die Ihr in dem verlassenen Stützpunkt der Horde in der Nähe von Skulk Rock findet.","Das heißt, dass es eine anstrengende Aufgabe wird. Ihr werdet bei den Ruinen stinkende Brühschleimer und Brühschlammer vorfinden; angeblich werden sie von einer Art Oberbrühschlammer geführt. Beschafft trotzdem die Welle und kehrt hierher zu mir zurück, um sie zu verpacken."}, {"Holt 10 Flaschen Hinterland-Honigwelle aus den Kisten, die sich rund um den verlassenen Stützpunkt der Horde in Skulk Rock befinden."}},
[78] = {"Die Legende von Stalvan", {"Ich kenne jemanden, der Euch vielleicht helfen könnte. Damals, als ich noch die Wachen von Stormwind kommandierte, haben wir immer in der Taverne Zum roten Raben in Dunkelhain gesessen und etwas getrunken. Smitts, der Gastwirt dort, kannte sich mit den örtlichen Lehren ziemlich gut aus. Zeigt ihm diese Seite, mal sehen, was er dazu zu sagen hat."}, {"Bringt die verblasste Tagebuchseite zu Schankwart Smitts."}},
[79] = {"Die Legende von Stalvan", {"Ich habe die Geschichten um diesen Stalvan jahrelang verfolgt. Als vor ein paar Jahren diese Adligen, die hier zu Besuch waren, ermordet wurden, hab ich Haggard bei seinen Nachforschungen begleitet. Ich habe damals diese schlammverschmierten Seiten gefunden, aber es ist uns nie gelungen, die Handschrift mit diesem Verrückten in den Wäldern in Verbindung zu bringen. Eure Spur der Beweise zeigt aber, dass er schuldig ist. Bringt dies sofort zu Kommandant Ebonlocke und sagt ihr, was Ihr entdeckt habt!"}, {"Bringt Kommandant Ebonlocke die schlammverschmierte Seite."}},
[80] = {"Die Legende von Stalvan", {"Ich beobachte diesen Widerling Stalvan jetzt schon eine ganze Weile. Aber wenn diese Seite tatsächlich von seiner Hand geschrieben wurde, dann beweist das seine Schuld ohne den letzten Zweifel.","So lange, wie Amtmann Daltry das Stadtverzeichnis jetzt schon verwaltet, ist er ein wahrer Experte darin geworden, Handschriften zu identifizieren. Zeigt ihm diese Seite und stellt fest, ob die Schrift dieselbe ist wie diejenige der Unterschrift im Verzeichnis."}, {"Zeigt Amtmann Daltry die blutbefleckte Tagebuchseite."}},
[81] = {"Wellenlieferung", {"Ich habe zusätzliche Vorsichtsmaßnahmen getroffen und nicht nur Drans verpackte Welle in dem Fass sicher untergebracht, sondern auch die zusätzlichen Wellenflaschen, die er klaut. Auch wenn ich nicht viel von Dran halte, so gibt es doch nichts Schlimmeres als ein durchgeschütteltes Wellenbouquet. Bitte, bitte, keine Ursache.","Bringt jetzt Euer Fass zu Dran Droffers in seinem Kramladen in Orgrimmar. Entbietet meine besten Grüße bitte auch Malton und lasst ihn wissen, dass uns sein Besuch eine große Ehre wäre, falls es ihn jemals nach einem feinen Getränk und noch feineren Trinkkumpanen gelüstet."}, {"Liefert das Fass nach Orgrimmar an Dran Droffers."}},
[82] = {"Erkundung des Giftigen Unterschlupfes", {"Tja, diese Berichte über die Käfer... wir wissen, dass sie die Teiche trocken legen, die sich dort befanden, wo sich der Giftige Unterschlupf befindet. Was wir NICHT wissen, ist, na ja, alles andere.","Wir müssen als Erstes herausfinden, wer unser Feind ist! Begebt Euch bitte in den Giftigen Unterschlupf und besorgt ein paar Stücke dieser Tierwesen. Holt fünf der dortigen Centipaar und bringt sie dem Alchimisten Pestlezugg zur Untersuchung. Wenn er sich damit beschäftigt, werden wir schon herausfinden, worum es sich handelt!"}, {"Bringt fünf Centipaarinsektenteile vom Giftigen Unterschlupf nach Gadgetzan zum Alchimisten Pestlezugg."}},
[83] = {"Rote Leinenwaren", {"Die Mitglieder der Defias in Nordhain tragen Masken aus Sackleinen, die Masken der Defias in Elwynn dagegen sind aus gutem Leinen, aus dem ich Qualitätsleinenwaren herstellen kann.","Bringt mir rote Leinenkopftücher, dann stelle ich daraus etwas für Euch her.","Die Mitglieder der Defias haben Lager, die sich über ganz Elwynn verteilen."}, {"Bringt 6 rote Leinenkopftücher ins Holzfällerlager des Osttals zu Sara Timberlain."}},
[84] = {"Zurück zu Billy", {"Hier, bitte schön. Und wenn Ihr Billy die Pastete gebt, dann sagt ihm, ich hoffe, er erstickt daran!"}, {"Bringt die Schweinebauchpastete zu Billy Maclure in den Weinbergen der Maclures."}},
[85] = {"Die verlorene Halskette", {"Ich habe meine Halskette verloren und ich glaube, dieser Tunichtgut Billy Maclure hat sie genommen! Normalerweise treibt er sich immer wie eine kleine Ratte in den Weinbergen der Maclures östlich von hier herum.","Bringt mir meine Halskette zurück, wenn Ihr das Herz einer alten Witwe erfreuen wollt."}, {"Sprecht mit Billy Maclure."}},
[86] = {"Pastete für Billy", {"Wenn ich 'ne Pastete kriegen würde, dann könnte ich Euch vielleicht sagen, wer die Kette hat. Ach, und wisst Ihr was? Diese alte Dame Bernice auf dem anderen Hof da macht ganz tolle Schweinebauchpasteten.","Wenn Ihr ihr ein paar Brocken Eberfleisch von den Ebern bringt, die immer um unsere Höfe herumstromern, und ihr sagt, dass es für einen guten Zweck ist, dann backt sie Euch vielleicht eine Pastete."}, {"Bringt 4 Brocken Eberfleisch zu Tantchen Bernice Stonefield auf den Hof der Stonefields."}},
[87] = {"Goldzahn", {"Ich hab in der Nähe von der Tiefenschachtmine gespielt und da hab ich, glaub ich, die Kette von der alten Dame fallen ge... äääh, gesehen. Fragt mich nicht, wie die dahin gekommen ist, ich war's nicht!","Na egal, jedenfalls hab ich diesen großen Kobold gesehen, der hatte einen Goldzahn und der hat die Kette aufgehoben und ist in die Mine gerannt. Wenn Ihr diesen Kobold findet, dann findet Ihr auch die Halskette, ehrlich! Ich schwör's!"}, {"Bringt Berenices Halskette auf den Hof der Stonefields zu \"Tantchen\" Berenice Stonefield."}},
[88] = {"Prinzessin muss sterben!", {"Die Brackwells haben ein Schwein, eine Sau namens Prinzessin, die schon viele Preise gewonnen hat. Dass dieses Tier so riesig geworden ist, das kommt nur daher, dass es immer hier herübergeschlichen kommt und mein Gemüse frisst!","Und deswegen muss Prinzessin sterben, bevor sie wieder unsere Felder heimsucht! Bringt mir ihr Halsband zum Beweis, dass Ihr die Sau abgestochen habt, dann gebe ich Euch etwas, das Euch für Euren Aufwand entschädigt.","Ihr findet Prinzessin normalerweise bei Brackwells Kürbisbeet, noch hinter dem Hof der Maclures und von dort aus im Osten gesehen. Ich hoffe, Ihr erwischt die Sau, bevor sie wieder hungrig wird und hierher kommt!"}, {"Tötet Prinzessin, nehmt ihr Halsband an Euch und bringt es zu Mutter Stonefield auf den Hof der Stonefields."}},
[89] = {"Die Immerruhbrücke", {"Während der letzten Invasion der Orcs waren wir gezwungen, unser Eisen einzuschmelzen, um Kugeln, Schwerter und Rüstungen herzustellen. Wir haben Stormwind benachrichtigt, dass wir eine neue Lieferung Materialien benötigen. Aber eine Bande von Rotkammgnollen hat die Karawane überfallen und sich dann in die Hügel hinter Seenhain zurückgezogen.","Jetzt gehen uns langsam die Vorräte aus, die wir brauchen, um die Brücke wieder aufzubauen. Wenn Ihr mir 5 Eisenspitzen und 5 Eisennieten bringt, <Name>, dann sollt Ihr dafür belohnt werden.","Und jetzt an die Arbeit!"}, {"Bringt 5 Eisenspitzen und 5 Eisennieten nach Seenhain zu Großknecht Oslow."}},
[90] = {"Gewürzter Wolfskebab", {"Gewürzter Wolfskebab ist eine Spezialität des Hauses! Natürlich kann ich Euch welchen machen. Aber ich brauche erst die Zutaten dafür. Bringt mir 10 magere Wolfflanken sowie Würzkräuter aus Stormwind. Hier im Wald streifen so viele Wölfe herum, da werdet Ihr kein Problem haben, an die Wolfflanken zu kommen, da bin ich mir sicher. Und für die Kräuter sucht ihr am besten Felicia Gump in ihrem Blumenladen im Kanaldistrikt von Stormwind auf."}, {"Beschafft Euch 10 magere Wolfflanken und Würzkräuter aus Stormwind und kehrt dann zu Küchenchef Grual nach Dunkelhain zurück."}},
[91] = {"Das Gesetz des Solomon", {"Ein Feind Stormwinds hat sich im östlichen Vorläufer des Rotkammgebirges niedergelassen. Dieser verderbte Schurke bedient sich des Übernatürlichen, um unsere Stadt und ihre Einwohner in Angst und Schrecken zu versetzen. Er betreibt sein übles Geschäft unter dem Namen Morganth.","Morganth kontrolliert jetzt den Schattenfellklan. Die Gnolle tun alles, was er ihnen befiehlt. Wie es das Gesetz von Seenhain vorsieht, steht auf die Unterstützung eines im Königreich gesuchten Verbrechers die Todesstrafe.","Diese abscheulichen Gnolle verdienen den Tod!","Gebt 10 Schattenfellanhänger bei mir ab, dann erhaltet Ihr eine Belohnung."}, {"Bringt 10 Schattenfellanhänger ins Rathaus von Seenhain zu Büttel Conacher."}},
[92] = {"Rotkammgulasch", {"Ich würde liebend gern Rotkammgulasch für Euch kochen. Aber es gibt da ein kleines Problem. Erst kürzlich musste ich während des Orcaufstands eine ganze Brigade von Soldaten der Armee von Stormwind verpflegen. Jetzt ist meine Vorratskammer leer. Aber wenn Ihr mir die Zutaten besorgt, dann mache ich das gern für Euch.","Bringt mir einfach 5 Stücke zähes Kondorfleisch, 5 große Geiferzahnschnauzen und 5 Portionen knuspriges Spinnenfleisch."}, {"Küchenchefin Breanna aus Seenhain möchte 5 Stücke zähes Kondorfleisch, 5 große Geiferzahnschnauzen und 5 Portionen knuspriges Spinnenfleisch."}},
[93] = {"Schwärzliche Krebsküchlein", {"Ich verrate Euch ein kleines Geheimnis: Schwärzliche \"Krebs\"-Küchlein werden eigentlich aus Spinnenbeinen gemacht! Ich weiß, das ist irgendwie eklig, aber die Küchlein schmecken lecker, ein bisschen scharf und eignen sich ganz ausgezeichnet zum Knabbern. Wenn Ihr mir einige klebrige Spinnenbeine bringt, dann zaubere ich euch ein paar.","Ich habe gehört, dass sich Giftweberspinnen ausgezeichnet dafür eignen - sie haben ihr Nest im Norden, zwischen dem Vorgebirge und dem Fluss."}, {"Beschafft 6 klebrige Spinnenbeine und bringt sie nach Dunkelhain zu Küchenchef Grual."}},
[94] = {"Ein wachsames Auge", {"Mein Widersacher Morganth ist ein böser Magier von großer Macht, der im Turm von Ilgalar im Rotkammgebirge lebt. Ich habe eine Methode entwickelt, mit der ich ihn ausspionieren kann: Mit dem Auge von Azora ist es mir möglich, ihn von weitem zu sehen.","Doch um seine Gedanken zu erforschen... ja, dazu benötige ich noch weitere Hilfe.","Wenn Ihr gewillt seid, dies zu tun, dann bringt meine Glyphe von Azora zum Turm von Ilgalar. Legt sie auf die Löwenstatue in der Nähe des Turms.","Sobald dieses getan ist, wird die Macht von Azora mir Visionen von Morganths zukünftigen Plänen gewähren."}, {"Bringt die Glyphe von Azora zur Löwenstatue in der Nähe des Turms von Ilgalar im Rotkammgebirge."}},
[95] = {"Svens Rache", {"Früher arbeitete ich auf einem der Höfe im Südosten... bis einmal in meiner Abwesenheit Dunkle Reiter vom Gebirgspass der Totenwinde herunterkamen, meinen Hof überfielen und meine Familie abschlachteten.","Als ich zurückkehrte, sah ich eine schattenhafte Gestalt, die sich bei meiner Scheune herumtrieb und dort etwas vergrub. Er floh, ehe ich ihn erreichen konnte, und ich hatte keine Zeit, mich mit ihm aufzuhalten, da ich den Dunklen Reitern dicht auf den Fersen war. Und so habe ich nie entdeckt, was die Gestalt dort versteckte.","Wenn Ihr herausfinden könntet, was jene schattenhafte Gestalt dort vergrub, dann wäre ich Euch dankbar. Das Versteck befindet sich hinter dem alten Baumstumpf in der Nähe meiner Scheune."}, {"Begebt Euch zu Svens Hof und findet heraus, was hinter dem alten Baumstumpf vergraben wurde."}},
[96] = {"Ruf des Wassers", {"Nehmt Splitter. Nehmt Splitter und gebt Leben.","Gebt Leben und versteht. Versteht, Wasser ist Leben... Verderbnis beendet Leben.","Ihr schützt und gebt Leben mit der Macht des Wassers."}, {"Bringt den Splitter des Wassers in das Brachland zu Islen Waterseer."}},
[97] = {"Die Legende von Stalvan", {"Kommandant Ebonlocke muss unverzüglich erfahren, dass die Handschriften identisch sind, <Name>! Ihr Verdacht hat sich also bestätigt."}, {"Berichtet Kommandant Ebonlocke die Neuigkeiten."}},
[98] = {"Die Legende von Stalvan", {"Stalvan Mistmantle hat ein Leben der Verworfenheit gelebt und unschuldige Opfer starben von seiner Hand. Zweifellos hat er noch zahllose andere Verbrechen begangen und nun wird Dunkelhain von diesem Wahnsinnigen bedroht. Das Licht allein weiß, welche schmutzigen Pläne er jetzt ausheckt. Deswegen brauche ich Eure Hilfe, <Klasse>. Begebt Euch zu seiner Hütte ein kleines Stück nördlich der Stadt und schaltet Stalvan ein für alle Mal aus.","Sobald die Tat vollbracht ist, begebt Euch zu Madame Eva und zeigt Ihr den Ring seiner Familie. Schließlich war es ihre Vorahnung, die zu dieser entsetzlichen Entdeckung führte. Doch jetzt kann ganz Dunkelhain dank ihr wieder ruhiger schlafen."}, {"Tötet Stalvan Mistmantle und zeigt Madame Eva den Ring seiner Familie."}},
[99] = {"Arugals Torheit", {"Je mehr ich über Arugals Magie erfahre, umso mehr wächst meine Verachtung für diesen jämmerlichen Narren. Bald werde ich meine Studien zu seiner so genannten 'Arznei' beendet haben.","Mein Wissen wird vollständig sein, sobald ich herausgefunden habe, welche Verzauberung die seltsamen Zustände in Pyrewood verursacht. Tagsüber scheinen die Arbeiter dort ganz gewöhnliche Menschen zu sein. Doch sobald die Sonne untergeht, verwandeln sich die Dorfbewohner in Moonrage-Worgs.","Ich muss Energie aus den verzauberten Fesseln ziehen, die Arugal auf sie gewirkt hat. Bringt mir 6 verzauberte Pyrewood-Fesseln, <Name>."}, {"Bringt Dalar Dawnweaver 6 Pyrewood-Fesseln zum Grabmal."}},
[100] = {"Ruf des Wassers", {"Noch während die Zauberformel, die Ihr gesprochen habt, verhallt, zuckt ein Meer von Lichtern um Euch herum auf. Das Zischen der Wassertropfen, die mit den Flammen in Berührung kommen, lässt nach und Eure überreizten Sinne spüren, wie die verderbten Geister in Eurer Nähe vernichtet werden."}, {"Sprecht mit der schwachen Wassermanifestation im Silberwald."}},
[101] = {"Das Totem des Gegenschlages", {"Nun, da übernatürliche Mächte den Wald durchziehen, besteht ganz zweifellos die Notwendigkeit, sich zu schützen, <Name>. Leise flüstern mir die Winde von einer großen Gefahr, die auf Euch lauert und der Ihr Euch in nicht allzu ferner Zukunft gegenübersehen werdet.","Auch ein tapferer <Klasse> wie Ihr sollte sich schützen. Falls Ihr dies wünscht, so bringt mir 10 Ghulfangzähne, 10 Skelettfinger und 5 Phiolen Spinnengift. Dann stelle ich Euch ein Totem des Gegenschlages her und belege es mit einem Zauber, durch den es all denjenigen schadet, die gegen Euch Gewalt anwenden wollen."}, {"Bringt 10 Ghulfangzähne, 10 Skelettfinger und 5 Phiolen Spinnengift nach Dunkelhain zu Madame Eva."}},
[102] = {"Auf Patrouille durch Westfall", {"Stormwind hat uns im Stich gelassen. Der üble Gestank der Verderbtheit weht über die Ebenen von Westfall. Dies war und ist mein Heimatland und ich werde die Bürger, die sich zum Bleiben entschlossen haben, nicht verlassen. Wir, die wir früher Bauern waren, werden Widerstand leisten.","Eure Aufgabe, so Ihr sie denn annehmt, wird es sein, im Grasland von Westfall auf Patrouille zu gehen. Spürt die widerwärtigen Gnolle auf, die augenscheinlich mit den Dieben aus den Todesminen zusammenarbeiten, und tötet sie. Bringt mir 8 Gnolltatzen und ich werde Euren Mut zu belohnen wissen."}, {"Bringt 8 Gnolltatzen auf die Späherkuppe zu Captain Danuvin."}},
[103] = {"Bewahrer der Flamme", {"Die Nacht, in der die Familie des Leuchtturmwärters starb, war schrecklich. Hilflos musste ich mit ansehen, wie Old Murk-Eye den Angriff befehligte. Doch was geschehen ist, ist geschehen. Nun sorge ich mich vor allem um das Leben der Seeleute, die das große Meer besegeln und deren Schiffe den trügerischen Felsen vor der Küste gefährlich nahe kommen. Da hier niemand mehr lebt, der die Flamme bewacht, obliegt die Verantwortung nun mir.","Helft mir dabei, die Fackel am Leuchten zu erhalten, und bringt mir 5 Fläschchen Öl von den Erntemonstern."}, {"Bringt 5 Fläschchen Öl zu Captain Grayson beim Leuchtturm von Westfall."}},
[104] = {"Bedrohung an der Küste", {"Als ich auf den Felsen mein Leben aushauchte, hatte ich keine Ahnung, was das Leben nach dem Tode für mich bereithalten würde. In jener Nacht war der Leuchtturm erloschen, weil Old Murk-Eye die Familie des Leuchtturmwärters vertrieben hatte. Die Familie kehrte zurück und zündete die Flamme wieder an, doch Old Murk-Eye brachte die willensschwächeren Murlocs dazu, den Leuchtturm noch einmal mit ihm zusammen anzugreifen. Beim zweiten Mal hatte die Familie nicht so viel Glück. Vor meinen Augen starben sie alle, ohne sich wehren zu können.","Tötet Old Murk-Eye, wenn Ihr ihn am Ufer seht, und bringt mir eine seiner Schuppen, dann werdet Ihr eine Belohnung erhalten."}, {"Bringt Captain Grayson am Leuchtturm von Westfall eine Schuppe von Old Murk-Eye."}},
[105] = {"Weh dir, Andorhal", {"Endlich ist die Zeit gekommen Andorhal anzugreifen und den Lich, der die Stadt kontrolliert, zu vertreiben.","Der Lich - Araj der Zerstörer - herrscht von innerhalb der Ruinen der Stadt aus. Er wird von zahlreichen Angehörigen der Geißel bewacht, die über die Überreste des Stadtzentrums wachen, auch sein Gefolge an Leibwächtern ist ausgezeichnet. Ihr werdet einige Verbündete brauchen, um auch nur einen Hauch von einer Chance zu haben, sich ihm gegenüber stellen zu können.","Vernichtet Araj, <Name>, und bringt mir einen Splitter seines Phylakteriums als Beweis!"}, {"Bringt den Splitter von Arajs Phylakterium zu Hochexekutor Derrington ins Bollwerk in den westlichen Pestländern."}},
[106] = {"Junge Liebende", {"Oh, ein Fluch lastet auf mir! Mein Herz gehört Tommy Joe Stonefield, aber unsere Familien sind aufs Ärgste miteinander verfeindet. Daher kann ich mich nicht mit ihm treffen, obwohl ich mich doch so sehr danach sehne, sein geliebtes Gesicht zu sehen!","Bitte nehmt diesen Brief und gebt ihn Tommy Joe. Normalerweise hält er sich am Fluss im Westen des Hofs der Stonefields auf, das ist auch von hier aus genau westlich."}, {"Gebt Tommy Joe Stonefield Maybells Liebesbrief."}},
[107] = {"Nachricht an William", {"Ich möchte wetten, William Pestle hat einen Trank, der unsere beiden Liebenden zusammenbringen kann.","Hier, bringt William diese Nachricht. Er wohnt im Gasthaus Zur Höhle des Löwen in Goldhain."}, {"Bringt Oma Stonefields Nachricht zu William Pestle."}},
[109] = {"Meldung bei Gryan Stoutmantle", {"Es scheint mir, <Klasse>, als hättet Ihr schon etliche Kämpfe überstanden. Falls Ihr das noch nicht getan habt, solltet Ihr Euch bei Gryan Stoutmantle melden. Er ist der Anführer der Volksmiliz, die es sich zur Aufgabe gemacht hat, das Ackerland von Westfall zu beschützen. Ich möchte wetten, er könnte Eure Hilfe gebrauchen. Normalerweise findet man ihn im Steinturm auf der Späherkuppe, ein kleines Stückchen von der Straße weg, mitten in Westfall."}, {"Sprecht mit Gryan Stoutmantle. Normalerweise findet man ihn im Steinturm auf der Späherkuppe, ein kleines Stückchen von der Straße weg, mitten in Westfall."}},
[110] = {"Insektenteil-Analyse", {"Ich werde einige Zeit brauchen, um Scrimshanks Feldmesserausrüstung durchzusehen... und seine persönlichen Angelegenheiten zu regeln. Mein Bauch sagt mir, dass dies nicht der letzte Verlust ist, den wir durch die Käfer hinnehmen müssen.","Geht bitte zu Alchimist Pestlezugg, um zu sehen, ob er etwas mit den Insektenteilen anfangen konnte, die wir ihm geschickt haben. Wenn sie fertig sind, bringt sie mir."}, {"Begebt Euch nach Gadgetzan zu Alchimist Pestlezugg und erkundigt Euch nach der Analyse der Insektenteile."}},
[111] = {"Gespräch mit Oma", {"Bitte sprecht mit meiner Großmutter, <Name>. Wenn irgendjemand einen Weg weiß, wie ich mit Maybell zusammen sein kann, dann ist sie das.","Ihr findet sie in unserem Haus, östlich von hier."}, {"Sprecht mit Oma Stonefield."}},
[112] = {"Die Suche nach dem Tang", {"Ich könnte einen Unsichtbarkeitsschnaps für Maybell brauen, damit sie sich aus den Weinbergen der Maclures fortstehlen und Tommy Joe sehen kann. Aber um den Schnaps herzustellen, brauche ich etwas Kristalltang.","Dieser Tang wächst zwar normalerweise im Meer, aber manchmal wird er auch von den Murlocs gesammelt. Geht und schaut, ob die Murlocs am Kristallsee welchen haben. Der Kristallsee liegt ein kleines Stück östlich von Goldhain."}, {"Bringt 4 Kristalltangwedel nach Goldhain zu William Pestle."}},
[113] = {"Insektenteil-Analyse", {"Der Leitende Feldmesser Fizzledowser muss diesen Bericht sofort sehen. Eigentlich muss jeder, der lesen kann, diesen Bericht sehen. Wir stehen einer großen und ernsten Bedrohung gegenüber, die nicht nur Tanaris und Gadgetzan, sondern das ganze südliche Kalimdor betrifft, wenn wir nichts unternehmen. Möglicherweise ist sogar ganz Azeroth gefährdet. Dieses Ding macht nicht den Eindruck, als würde es das Interesse verlieren.","Lasst Fizzledowser ein Duplikat des Berichts anfertigen, den Ihr dann an Eure Verbündeten verteilen könnt. Jeder muss von dieser Bedrohung erfahren!"}, {"Bringt den Bericht nach Gadgetzan zum Leitenden Feldmesser Fizzledowser. Lasst Euch von ihm eine Kopie des Berichts geben, wie Alchimist Pestlezugg gefordert hat."}},
[114] = {"Die Flucht", {"Bringt der jungen Maybell diesen Unsichtbarkeitsschnaps. Seine Wirkung sollte so lange anhalten, dass sie Tommy Joe besuchen kann."}, {"Bringt Maybell Maclure den Unsichtbarkeitsschnaps."}},
[115] = {"Schattenmagie", {"Von den Orcs des Blackrockklans wurden zur Unterstützung ihrer Angriffe auf das Rotkammgebirge Schattenzauberer rekrutiert, die von einer dunklen Macht erfüllte Vorrichtungen mit sich führten - Mitternachtskugeln. Diese Kugeln haben den Verteidigern vom Rotkammgebirge schwere Verluste zugefügt und es ist von äußerster Wichtigkeit, dass wir diese dämonbesudelten Gegenstände aus dem Konflikt entfernen.","Durchsucht erschlagene Schattenzauberer der Blackrock nach Mitternachtskugeln und bringt sie mir. Ich werde die Kugeln dann vernichten lassen, denn ohne sie wäre die Welt ein wesentlich besserer Ort."}, {"Bringt 3 Mitternachtskugeln nach Seenhain zu Marshal Marris."}},
[116] = {"Trockene Zeiten", {"Mir werden hier gerade die Vorräte knapp, <Name>. Alle Flaschen sind fast leer, und die letzte Schnapslieferung hätte schon längst hier eintreffen sollen. Die Orcinvasion ist einfach die Hölle.","Vielleicht könnt Ihr Euch ja für mich an die Arbeit machen?","Ihr müsst für mich bei Grimbooze Donnerbräu in den Westfall-Hügeln ein Fässchen Donnerbräu Lagerbier besorgen, ebenso ein Fässlein Merlot aus Stormwind, eine Flasche Fusel aus Dunkelhain und einen Schlauch mit süßem Rum aus Goldhain. Bringt mir all diese Dinge, dann werde ich dafür sorgen, dass Ihr belohnt werdet."}, {"Barkeeper Daniels aus Seenhain braucht ein Fässchen Donnerbräu Lagerbier, ein Fässlein Merlot, eine Flasche Fusel und einen Schlauch mit süßem Rum."}},
[117] = {"Donnerbräu", {"Ihr müsst eilen, <mein Freund/Madame>, sollt nicht ruhen, noch rasten,","Denn für unser Lagerbier lohnt sich's zu hasten.","Es schmeckt allen Männern, es schmeckt allen Frauen -","Doch ich brauche noch Hopfen, um das Bier zu brauen."}, {"Bringt Grimbooze Donnerbräu 5 Hopfen, damit er sein besonderes Bier brauen kann."}},
[118] = {"Der Preis für Hufeisen", {"Ich habe so viel Eisen dafür verwenden müssen, die Wachen von Stormwind auszurüsten, dass ich nicht mehr genug Eisen übrig habe, um die Pferde im Stall zu beschlagen!","Bringt diese Nachricht zu Schmied Argus nach Goldhain. Darin erkläre ich ihm mein Problem und bitte ihn um eine Lieferung Hufeisen."}, {"Bringt Verners Nachricht zu Schmied Argus nach Goldhain."}},
[119] = {"Zurück zu Verner", {"Gut, er soll seine 50 Hufeisen haben.","Hier sind sie, schön in eine Kiste verpackt und bereit zum Abmarsch. Sagt Verner, dass es ein Vergnügen war, mit ihm Geschäfte zu machen, und sagt ihm, dass er unbedingt die Nachricht lesen soll, die ich ihm an die Kiste geheftet habe!"}, {"Kehrt zu Verner Osgood ins Rotkammgebirge zurück. Gebt ihm die Kiste mit Hufeisen."}},
[120] = {"Botschaft nach Stormwind", {"Wir leben in schwierigen Zeiten, <Klasse>. Unsere Ortschaft ist einer ständigen Belagerung ausgesetzt. Wenn wir keine Verstärkung bekommen, ist unsere Niederlage so gut wie besiegelt. Die Botschaft, die ich Euch nun anvertrauen werde, ist von äußerster Wichtigkeit. Bringt diesen Bericht unverzüglich zu General Marcus Jonathan nach Stormwind. Sobald Ihr die Nachricht abgeliefert habt, kehrt umgehend zu mir zurück und überbringt mir alle Neuigkeiten, ganz gleich, ob gut oder schlecht.","Doch nun eilt!"}, {"Magistrat Solomon hat Euch einen Bericht übergeben, der bei General Marcus Jonathan in Stormwind abgeliefert werden muss. Der Richter möchte, dass Ihr zu ihm zurückkehrt, sobald Ihr die Nachricht zugestellt habt."}},
[121] = {"Botschaft nach Stormwind", {"Während ich auf Antwort vom König warte, möchte ich, dass Ihr diesen Brief zu Magistrat Solomon bringt.","Weggetreten, <Klasse>!"}, {"Bringt General Marcus Jonathans Antwortschreiben nach Seenhain zu Magistrat Solomon."}},
[122] = {"Bauchschuppen", {"Ich brauche einige Bauchschuppen von schwarzen Großdrachenwelpen, damit ich die Hufeisen bezahlen kann, die Argus mir aus Goldhain geschickt hat. Wenn Ihr mir 6 solcher Schuppen besorgt, dann kann ich mit einem Teil davon Argus bezahlen - und habe dann immer noch einen Rest übrig, aus dem ich etwas für Euch herstellen kann.","Schwarze Großdrachenwelpen fliegen häufig über das Gebiet südlich von Seenhain, aber sie wandern auch gern herum. Haltet einfach die Augen offen und jagt sie, wenn Ihr sie seht."}, {"Sammelt 6 Welpenbauchschuppen von schwarzen Großdrachenwelpen ein und bringt sie ins Rotkammgebirge zu Verner Osgood."}},
[123] = {"Der Sammler", {"Bei der Notiz handelt es sich um eine Tabelle mit Daten und Zeiten, zu denen jemand, der in der Notiz nur als \"der Sammler\" bezeichnet wird, je eine Lieferung Gold aus den Minen des Waldes von Elwynn erhalten soll.","Aus dem Plan geht hervor, dass sich der Sammler anscheinend in der Nähe vom Brackwell-Kürbisbeet in Ost-Elwynn aufhält.","Das klingt, als sei es wichtig. Ihr solltet Marshal Dughan in Goldhain davon berichten."}, {"Begebt Euch zu Marshal Dughan nach Goldhain und gebt ihm den Zeitplan des Sammlers."}},
[124] = {"Das Bellen der Gnolle", {"Als ob Orc-Angriffe nicht genug wären! Jetzt schleichen auch noch Gnoll-Schläger und Gnoll-Mystiker auf dem Hügelkamm nördlich von meinem Stall herum und fallen meine Pferde an, wenn diese sich auch nur etwas zu weit vom Stall entfernen...","Wenn Ihr diese Gnolle beseitigen könntet, wären meine Pferde und ich Euch sehr dankbar."}, {"Tötet 10 Schläger der Rotkammgnolle und 8 Mystiker der Rotkammgnolle und kehrt anschließend zu Verner Osgood zurück."}},
[125] = {"Die verlorenen Werkzeuge", {"Ich könnte hier wirklich etwas Hilfe gebrauchen, <Name>. Jetzt, wo die Stadt belagert wird, bekommen wir hier nur schwer Nachschub. Meine Werkzeuge sollten mit dem Karren von Goldhain aus angeliefert werden, aber die Brücke wurde in die Luft gejagt. Also legten wir die Werkzeuge in ein Boot, aber die Orcs beschossen es mit einem Katapult und trafen auch noch. Na, und bei meinem Pech ist mein Werkzeugkasten natürlich geradewegs bis auf den Grund des Sees gesunken.","Wenn Ihr mir meinen Werkzeugkasten wiederbeschafft, <Name>, dann soll es Euer Schaden nicht sein."}, {"Großknecht Oslow aus Seenhain möchte, dass Ihr seinen Werkzeugkasten vom Grunde des Immerruhsees heraufholt."}},
[126] = {"Geheul in den Hügeln", {"Die Gnolle sind immer noch da draußen, <Name>. Ich kann sie bellen hören, wenn sie in den Hügeln oberhalb von Seenhain jagen. Und das Geheul eines Gnolls ist lauter als das aller anderen - das von Jauler, ihrem Anführer.","Tötet Jauler und vertreibt sein Rudel in alle vier Winde! Bringt mir seine Tatze zum Beweis.","Aber seid vorsichtig! Dieser Gnoll ist nicht umsonst der Anführer seines Rudels. Er ist kein einfacher Köter..."}, {"Bringt Jaulers Tatze nach Seenhain zu Verner Osgood."}},
[127] = {"Fischhandel", {"Der Immerruhsee ist für seinen gefleckten Sonnenfisch berühmt. Die Nachfrage nach diesen Fischen ist enorm hoch - und ich habe bald keine mehr vorrätig!","Bringt mir 10 Stück, dann werden wir uns schon handelseinig werden. Und wenn Ihr nicht selbst angeln möchtet oder könnt - na ja, vielleicht bekommt Ihr die Fische ja von den Murlocs!"}, {"Bringt 10 gefleckte Sonnenfische nach Seenhain zu Dockmeister Baren."}},
[128] = {"Kopfgeld auf die Blackrocks", {"Der Blackrockklan muss vernichtet werden. Der Feind ist in unser Land eingedrungen. Diese Berge unterliegen der Oberhoheit des Königs von Stormwind. Aus bestimmten Quellen haben wir erfahren können, dass die Anführer jeder Einheit im Klan \"Helden\" genannt werden. Ihr findet sie in ihrem Lager im Norden, ein kleines Stück westlich von der Straße, die in die brennende Steppe führt.","Magistrat Solomon möchte diese garstigen Bestien tot sehen. Erschlagt 15 Helden der Blackrock und kehrt zu mir zurück."}, {"Tötet 15 Helden der Blackrock, dann erhaltet Ihr von Wache Howe aus Seenhain eine Belohnung."}},
[129] = {"Ein geschenktes Mittagessen", {"Könntet Ihr mir einen Gefallen tun? Ich habe für Wache Parker ein Mittagessen gemacht, aber er ist gerade auf Patrouille unterwegs... Er ist groß und stark und noch dazu eine Wache von Stormwind, also kann er auf sich selbst aufpassen, aber ich bin nur eine kleine Stadtbewohnerin - für mich ist das da draußen viel zu gefährlich!","Wenn Ihr ihm also sein Mittagessen bringt und anschließend wieder herkommt, dann bekommt Ihr auch ein Mittagessen von mir - umsonst! Parker patrouilliert die Straße, die in den Dämmerwald führt."}, {"Bringt Parkers Mittagessen zu Wache Parker. Er ist auf der Straße nach Dunkelhain auf Patrouille."}},
[130] = {"Besuch im Kräuterladen", {"Könnt Ihr Darcy ein paar Blumen von mir bringen, wenn Ihr wieder zu ihr geht? Die Blumen bekommt Ihr im Kräuterladen von Seenhain, am westlichen Ende des Ortes.","Aber wenn Ihr die Blumen holt, dann sagt Martie, der Kräuterkundigen, nicht, für wen sie bestimmt sind oder wer Euch beauftragt hat, ja?"}, {"Sprecht mit der Kräuterkundigen des Rotkammgebirges, Martie Jainrose."}},
[131] = {"Narzissen für eine Dame", {"Hier habt Ihr Euren Blumenstrauß. Ich habe Narzissen für Euch ausgesucht. Narzissen sind meine Lieblingsblumen."}, {"Gebt Darcy den Narzissenstrauß."}},
[132] = {"Die Bruderschaft der Defias", {"Was ich Euch jetzt erzähle, könnte mich das Leben kosten. Die Bande der Defias plant irgendwas Großes. Das Letzte, was ich gehört habe, war, dass sie mit unterschiedlichen Gnollen, Kobolden und sogar Goblins zusammenarbeiten.","Bringt Stoutmantle diese Nachricht. Darin steht alles, was ich zu dem Thema sagen kann."}, {"Bringt Wileys Nachricht nach Westfall zu Gryan Stoutmantle."}},
[133] = {"Ein Ghulbildnis", {"Ich wohne so weit weg von der Stadt und dem Schutz, den sie bietet, dass es ein Wunder ist, dass die Ghuls und die wandelnden Toten mich noch nicht aufgefressen haben! Tatsächlich hat erst gestern ein Rudel Knochenkauer gegen die Wände meines Hauses gehämmert.","Ich möchte ein Bildnis herstellen - eine Art \"Vogelscheuche\", nur für Ghuls. Dafür brauche ich ein paar Ghulrippen. Die bekommt Ihr von Fleischfressern, Knochenkauern, Hirnfressern, Verfaulten und Seuchenverbreitern.","Wenn Ihr mir die Rippen beschafft, bezahle ich Euch in barer Münze."}, {"Sammelt 7 Ghulrippen und bringt sie zu Calvinius in seiner Hütte."}},
[134] = {"Ogerdiebe", {"Vor ein paar Wochen wurde ich von einer Bande Ogern angegriffen, als ich draußen Kräuter sammelte, ziemlich weit von meinem Haus entfernt. Ich lief davon und musste eine Kiste mit wertvollen Werkzeugen und Kräutern zurücklassen.","Nachdem sie mich vertrieben hatten, stolzierten die Oger zurück zum Ogerhort im südlichen Dämmerwald. Ich bin mir sicher, dass meine Kiste irgendwo in der Nähe des Hügels sein muss.","Bitte, <Name>, beschafft mir diese Kiste wieder, denn sie fehlt mir sehr."}, {"Bringt Calvinius' Kiste zu Calvinius."}},
[135] = {"Die Bruderschaft der Defias", {"Ich möchte zu gern wissen, was Wiley meinte, als er die Steinmetze erwähnte. Vielleicht ist ihm da nur etwas rausgerutscht. Könnten die Defias irgendwas mit den Steinmetzen zu tun haben? Nur ein Mann weiß das mit Sicherheit: Mathias Shaw, Kopf von SI:7. Zeigt ihm Wileys Nachricht und stellt fest, ob er noch etwas zu diesem immer verzwickter werdenden Rätsel beitragen kann. Falls Ihr Probleme habt, Shaw zu finden, seht in der Kaserne in der Altstadt nach."}, {"Bringt Wileys Nachricht nach Stormwind zu Mathias Shaw."}},
[136] = {"Käpt'n Sanders Versteckter Schatz", {"Wenn Ihr das hier lesen tut, dann heißt das, dass der alte Käpt'n Sander in den Fluten begraben is'. Also tut mein Schatz jetz' Euch gehör'n, Ihr braucht bloß den Hinweisen folgen.","Erst müsst Ihr ma' meine Schließkiste finden. Wahrscheinlich is' die jetz' da an der Westküste von Westfall in der Nähe von dem Schiffswrack schon halb im Sand verschwunden. 's gibt da an der Küste massig Schiffswracks, aber bloß einen rostigen Anker. Wenn Ihr den Anker finden tut, dann habt Ihr auch meine Schließkiste gefunden. Da müsst ihr reingucken, denn da steht der nächste Hinweis drin."}, {"Findet Käpt'n Sanders Schließkiste und durchsucht sie, um den nächsten Hinweis zu erhalten."}},
[138] = {"Käpt'n Sanders versteckter Schatz", {"Der Hinweis auf den Schatz lautet folgendermaßen: Gut gemacht, Kumpel! Jetz' müsst Ihr Euch imma nach Ost'n halten. Östlich von der Straße die Steilhänge hoch. Schaut Euch nach so 'nem ollen verfallenen Kamin nahe bei der Straße um. Dort findet Ihr'n altes Fass mit Eurem nächsten Hinweis drin."}, {"Findet das alte Fass in der Nähe des verfallenen Kamins und durchsucht es nach Eurem nächsten Hinweis."}},
[139] = {"Käpt'n Sanders Versteckter Schatz", {"Als Ihr das Fass durchsucht, findet Ihr ein weiteres Stück Pergament. Darauf steht: Also, von dem Fass dort müsst Ihr Euch nach Norden halten tun. Un' dann geht Ihr immer der Nase nach schnurstracks geradeaus, bis Ihr den leeren Henkelkrug sehen tut, der da irgendwo bei so 'ner einzeln stehenden Windmühle oben auf dem Steilhang über dem Meer liegt. Wenn Ihr Euch bei dem Henkelkrug umguckt, dann findet Ihr vielleicht sogar, was Ihr sucht."}, {"Durchsucht den leeren Henkelkrug neben der Windmühle nach dem nächsten Hinweis."}},
[140] = {"Käpt'n Sanders Versteckter Schatz", {"Und tatsächlich, ganz unten, fast am Boden des alten Henkelkruges findet sich der nächste Hinweis auf Sanders Schatz. An manchen Stellen ist die Tinte verwischt und das Papier riecht nach Whisky, aber zum Teil kann man den Text noch lesen: Jetz', wo Ihr mein'n alten Whiskykrug gefunden habt, habt Ihr den Schatz schon so gut wie in der Tasche. Von der Flasche aus müsst Ihr Euch einfach nach Westen halten und runter zum Strand geh'n. Und dann heißt es schwimmen! Schwimmt schnurstracks nach Westen, bis Ihr an der Insel seid, da wo meine Schatztruhe drauf is'!"}, {"Findet Käpt'n Sanders Truhe und öffnet sie. Darin befindet sich Eure Belohnung."}},
[141] = {"Die Bruderschaft der Defias", {"Die Gilde der Steinmetze wurde von einem Mann namens Edwin van Cleef geführt. Van Cleef war dafür verantwortlich, dass Stormwind wieder aufgebaut wurde, nachdem die Orcs es im Ersten Krieg dem Boden gleichgemacht hatten. Anscheinend waren van Cleef und seine Leute nicht zufrieden damit, wie der König sie behandelte, nachdem der Wiederaufbau vollendet war. Das könnte den einen oder anderen Sachverhalt erklären.","Ich habe Eurem Meister in Westfall einen ausführlicheren Bericht geschrieben. Bringt ihn sofort zu ihm!"}, {"Bringt Shaws Bericht nach Westfall zu Gryan Stoutmantle."}},
[142] = {"Die Bruderschaft der Defias", {"Wir müssen herausfinden, wo genau sich das Versteck der Defias befindet. <Name>, meine Späher berichten mir, dass ein Bote der Defias auf der Straße zwischen Moonbrook, dem Goldküstensteinbruch und dem Jangoschacht gesehen worden ist. Ich möchte, dass Ihr ihn fangt. Wenn er Widerstand leistet, tötet ihn und bringt mir alles, was er bei sich trägt."}, {"Spürt den Boten der Defias in Westfall auf und bringt die Nachricht, die er bei sich hat, zu Stoutmantle."}},
[143] = {"Botschaft nach Westfall", {"Irgendetwas stimmt mit der Armee von Stormwind nicht. Sie hätte längst mit einem größeren Aufgebot hier sein müssen. Doch Zeit ist ein Luxus, den wir nicht haben. Ich werde nicht dabeistehen und tatenlos zusehen, wie die Bewohner von Seenhain ihr Leben geben. Nein. Ich muss versuchen, von anderer Stelle her Hilfe zu gewinnen. Ich habe gehört, dass sich in Westfall eine Volksmiliz gebildet hat.","Bringt dieses Hilfegesuch ihrem Anführer, Gryan Stoutmantle. Vielleicht können Stoutmantles Männer uns hier unterstützen, bis der König Verstärkung schickt."}, {"Magistrat Solomon möchte, dass Ihr sein Hilfegesuch nach Westfall zu Gryan Stoutmantle bringt."}},
[144] = {"Botschaft nach Westfall", {"Es schmerzt mich, Magistrat Solomons Schreiben zu lesen. Offensichtlich hat er von dem Krieg, der hier in Westfall tobt, keine Ahnung. Sonst wäre er nicht so naiv, Hilfe von der Miliz zu erwarten. Wenn Stormwind nicht auch uns im Stich gelassen hätte, dann hätte es keinen Grund gegeben, die Miliz ins Leben zu rufen.","Bringt Eurem Herrn im Rotkammgebirge diese Antwort, <Klasse>. Und lasst ihn wissen, dass mir das Herz schwer wird, wenn ich an all die tapferen Leute denke, die ihr Leben verloren haben."}, {"Kehrt mit Gryan Stoutmantles Antwort zu Magistrat Solomon zurück."}},
[145] = {"Botschaft nach Dunkelhain", {"Ein weiteres Mal brauche ich Eure Hilfe. Ich habe an Lord Ebonlocke geschrieben und ihn gebeten, seine gut ausgebildete Nachtwache hierher zu schicken, damit diese bei der Verteidigung von Seenhain hilft. Es ist von ausnehmender Wichtigkeit, dass diese Nachricht ihn so bald wie möglich erreicht. Doch Ihr müsst äußerste Vorsicht walten lassen. Ebonlocke ist der Bürgermeister von Dunkelhain, und das liegt im Herzen des Dämmerwalds. Entfernt Euch nicht von der Straße."}, {"Magistrat Solomon möchte, dass Ihr einen Brief zu Lord Ebonlocke nach Dunkelhain bringt."}},
[146] = {"Botschaft nach Dunkelhain", {"Wenn mir Magistrat Solomon nicht persönlich bekannt wäre, würde ich sagen, der Mann hat den Verstand verloren. Die Nachtwache nach Seenhain schicken, weil Stormwind die Hilfe verweigert? Die Nachtwache wurde gegründet, weil Stormwind uns verließ, als diese elende Magie langsam das Land überzog. Wenn ich meine Nachtwache jetzt fortschicke, dann würde diese Stadt vom Bösen überrannt werden, noch ehe die Wachen den Waldrand erreicht hätten.","Bringt Eurem Meister in Seenhain dieses Antwortschreiben."}, {"Kehrt mit Lord Ebonlockes Antwortschreiben zu Magistrat Solomon zurück."}},
[147] = {"Menschenjagd", {"Wenn der \"Sammler\" Gold aus unseren Minen entnimmt, dann bestiehlt er das Königreich! Führt den Sammler seiner gerechten Strafe zu und bringt mir den Ring, der in dem Abholplan, den Ihr mir gegeben habt, erwähnt wurde.","Im Abholplan steht, dass der Sammler sich am Brackwell-Kürbisbeet versteckt hält. Dort solltet Ihr nach ihm suchen."}, {"Findet den \"Sammler\" und tötet ihn, dann kehrt mit dem Ring des Sammlers zu Marshal Dughan zurück."}},
[148] = {"Lieferung aus Dunkelhain", {"Ach, was soll ich armer, alter Mann nur machen? Ich brauche einige Vorräte aus Dunkelhain, aber das Dorf ist so weit weg und ich bin so alt und schwach... ich würde die Reise niemals überstehen.","Könnt Ihr nach Dunkelhain reisen und mir etwas Geisterhaarfaden mitbringen? Den bekommt Ihr im Haus von Madame Eva am Dorfplatz von Dunkelhain."}, {"Sprecht mit Madame Eva."}},
[149] = {"Geisterhaarfaden", {"Es gibt da einen armen, traurigen Geist, die blinde Mary, die in einem alten Bauernhaus in den Hügeln südlich von Dunkelhain spukt.","Hier, nehmt diesen spektralen Kamm und bittet die blinde Mary, sich die Haare zu kämmen. Bringt mir anschließend den Kamm zurück, dann entnehme ich ihm das Geisterhaar, das ich für den Faden brauche.","Und seht Euch vor, denn in der Nähe dieses Hauses lauern üble Dinge..."}, {"Bringt der blinden Mary den spektralen Kamm."}},
[150] = {"Murlocwilderer", {"Die Murlocs werden zu einem Problem. Große Gruppen der Fischwesen werfen entlang der Ufer des Immerruhsees Anker und fischen, was das Zeug hält! Sie müssen verschwinden, ehe sie den ganzen See leer gefischt haben.","Geht Murlocs jagen! Bringt mir 8 ihrer Flossen, dann habe ich vielleicht etwas für Euch..."}, {"Bringt 8 Murlocflossen nach Seenhain zu Dockmeister Baren."}},
[151] = {"Die arme Graumähne", {"Die arme Graumähne! Das arme Pferd ist völlig entkräftet nach all der Arbeit, die wir ihm aufgebürdet haben. Ich habe es gefüttert, ehe wir den Hof verließen, aber wir haben natürlich nicht damit gerechnet, dass uns der Wagen kaputtgehen würde. Wenn Ihr der Stute ein paar Hände voll Hafer vom Feld bringen könntet, wäre ich Euch dankbar.","Ich wette, es lässt sich rund um die Höfe in Westfall etwas Hafer finden; Ihr müsst nur diesen entsetzlichen Maschinen aus dem Weg gehen, die hier alles übernommen haben. Es gibt einige Höfe südwestlich von hier."}, {"Verna Furlbrow in Westfall möchte, dass Ihr ihr 8 Hände voll Hafer bringt."}},
[152] = {"Ärger an der Küste", {"Die vielen Schiffswracks entlang der Küste sind Euch ja vermutlich schon aufgefallen. Das große Meer ist überaus tückisch. Die Küste von Westfall muss freigehalten werden, damit die Seeleute sicher sind, wenn sie unsere Ufer erreichen. Aber die Murlocs sind ein Problem.","Tötet 7 Gezeitenjäger, 7 Krieger, 7 Orakel und 7 Küstenläufer, dann sorge ich dafür, dass Ihr belohnt werdet."}, {"Tötet 7 Gezeitenjäger, 7 Krieger, 7 Orakel sowie 7 Küstenläufer und kehrt dann zu Captain Grayson beim Leuchtturm von Westfall zurück."}},
[153] = {"Rote Lederkopftücher", {"Die Defias sind ständig in Bewegung. Ich überwache ihre Aktivitäten jetzt schon seit einiger Zeit. Und dabei habe ich festgestellt, dass viele Bandenmitglieder an den roten Lederkopftüchern zu erkennen sind, die sie tragen.","Bringt mir 15 dieser Kopftücher, dann werde ich dafür sorgen, dass Ihr belohnt werdet."}, {"Bringt 15 rote Lederkopftücher auf die Späherkuppe zu Späher Galiaan."}},
[154] = {"Rückkehr mit dem Kamm", {"NEIN!!!!!","Nehmt ihn weg, nehmt diesen Kamm weg!","Ich bin ein Monster und niemand wird mich jemals wieder für schön halten!"}, {"Bringt den Geisterhaarkamm zu Madame Eva nach Dunkelhain."}},
[155] = {"Die Bruderschaft der Defias", {"Stoutmantle denkt also wirklich, ein <Volk> wie Ihr könne mich beschützen? Tja, Ihr werdet wohl reichen müssen. Bringt am besten ein paar Freunde mit.","Ihr wisst ja, wie's läuft, oder? Ihr haltet mir den Rücken frei und ich bringe Euch zum Versteck der Defias. Aber Ihr solltet besser in meiner Nähe bleiben, denn die Bande der Defias will jetzt meinen Kopf. Wenn die mich mit Euch sehen, werden sie versuchen, mich umzubringen.","Sagt mir einfach Bescheid, wenn Ihr und irgendwelche Freunde, die Ihr noch zusammentrommeln könnt, bereit seid loszuziehen."}, {"Eskortiert den Verräter der Defias zum Geheimversteck der Defiasbruderschaft. Sobald der Verräter der Defias Euch gezeigt hat, wo sich van Cleef und seine Männer aufhalten, kehrt Ihr mit der Information zu Gryan Stoutmantle zurück."}},
[156] = {"Das Sammeln von Faulblüten", {"Wenn Ihr mir ein paar Faulblüten bringt, dann kann ich Euch einen Kessel Zombiesaft brauen.","Faulblüten wachsen an seltsamen Orten. Vor allem, habe ich gehört, findet man sie in den Schädeln von Skelettschrecken und Skelettsatanskreaturen - nicht, dass ich selbst schon mal nah genug dran war, um nachzusehen!","Der Friedhof von Rabenflucht und die stillen Gärten sind voll von diesen Skeletten. Wenn Ihr die auf Eurer Suche nach den Faulblüten auch noch dezimiert, dann schlagt Ihr zwei Fliegen mit einer Klappe, und ganz Dunkelhain wäre Euch dankbar."}, {"Sammelt 8 Faulblüten und bringt sie nach Dunkelhain zu Schankwart Smitts."}},
[157] = {"Abliefern des Fadens", {"Hier ist Euer Faden, <Name>. Man kann tausend Dinge damit machen, zum Beispiel böse Geister binden oder unbelebte Gegenstände mit Leben erfüllen."}, {"Bringt Calvinius den Geisterhaarfaden in seine Hütte nördlich des Friedhofs von Rabenflucht."}},
[158] = {"Zombiesaft", {"Ich brauche eine Flasche eines bestimmten Schnapses, und zwar Zombiesaft. Schankwart Smitts aus der Taverne Zum roten Raben in Dunkelhain sollte welchen haben.","Und bitte sputet Euch, <Name>. Die Nächte sind kalt und ich brauche den Schnaps, um meine Adern wieder mit Feuer zu erfüllen!"}, {"Sprecht mit Schankwart Smitts."}},
[159] = {"Saftlieferung", {"Hier habt Ihr Euren Zombiesaft...","... aber passt mir mit dem Zeug auf. Es ist stark genug, um die Toten zu erwecken!"}, {"Bringt Calvinius den Zombiesaft in seine Hütte."}},
[160] = {"Ein Brief für den Bürgermeister", {"Bringt diese Nachricht zu Ello Ebonlocke, dem Bürgermeister von Dunkelhain.","Er und ich sind alte Freunde, und wenn er den Zettel liest und herausfindet, was Ihr für mich getan habt... dann freut er sich bestimmt genauso sehr, wie ich das tue! Hahahaha!"}, {"Bringt Calvinius' Brief zu Ello Ebonlocke, dem Bürgermeister von Dunkelhain."}},
[161] = {"Eine dunkle Bedrohung", {"Falls mein Verdacht sich bestätigt, ist dies eine Art Sprengpulver. Es muss von einem Sprengstoffexperten analysiert werden, damit wir genau wissen, mit was wir es hier zu tun haben. Ashlan Stonesmirk wurde dem Regiment zugeteilt, das Dun Modr und den Thandol-Übergang bewachen soll. Ihr müsst für mich eine schwierige und gefahrvolle Reise auf Euch nehmen, <Name>.","Reist durch das Algaz-Tor, folgt der Straße durch das Sumpfland und sucht Stonesmirk in Dun Modr auf. Folgt meinem Rat: Bleibt auf den Straßen und lasst Euch nicht zum Anhalten bewegen!"}, {"Chefingenieur Hinderweir möchte, dass Ihr das sonderbar riechende Pulver nach Dun Modr zu Ashlan Stonesmirk, dem Sprengstoffexperten, bringt."}},
[162] = {"Aufstieg der Silithid", {"Ihr solltet mit diesem Bericht zu jemandem gehen, der ausreichend Kenntnis von solchen Dingen hat.","Ich kenne da diese Nachtelfen-Gelehrte namens Gracina Spiritmight. Sie hat viel Zeit mit dem Studium der Lebensformen verbracht, die in der Ökologie von Azeroth einzigartig sind, und sie ist noch dazu Historikerin! Wenn Euch jemand dabei helfen soll herauszufinden, was genau diese Bedrohung ausmacht, und noch viel wichtiger, wie sie zu stoppen ist, gebt ihr den Bericht. Wir werden die Hilfe brauchen.","Das Letzte, was ich hörte, war, dass sie im Tempel des Mondes in Darnassus lebt."}, {"Bringt den Insektenanalysebericht nach Darnassus zu Gracina Spiritmight."}},
[163] = {"Rabenflucht", {"Irgendetwas stimmt nicht in Rabenflucht, <Name>. Calor schwört, dass jemand - oder etwas - in den Gebäuden dort umgeht. Zwei Nächte in Folge hat er jetzt schon gesehen, wie sich die Schatten bewegten, und in der Nacht vorher habe er Licht in einem der Fenster gesehen, sagte er.","Wenn Euch, wie Ihr sagt, die Sicherheit des Dämmerwalds am Herzen liegt, dann findet heraus, welches Monster in Rabenflucht umgeht."}, {"Findet heraus, was in Rabenflucht umgeht."}},
[164] = {"Lieferung an Sven", {"Kennt Ihr Sven? Das ist mal ein Mann, der das Werk des Lichtes vollbringt! Er und seine Anhänger haben ein Lager am nordwestlichen Rand des Dämmerwalds.","Leider sind sie so wenige, dass noch nicht einmal ein einziger Mann abkömmlich ist, um Vorräte aus der Stadt zu holen. Würdet Ihr diese Vorräte bei ihm abliefern?"}, {"Liefert die Vorräte an Sven."}},
[165] = {"Der Einsiedler", {"Es gab einmal einen alten Mann, der recht häufig in die Stadt kam, um Vorräte einzukaufen, aber ich habe ihn jetzt schon ziemlich lange nicht mehr gesehen. Er lebt draußen in einer Hütte, von der aus man den Friedhof von Rabenflucht überblicken kann, wenn ich mich recht erinnere. Vielleicht solltet Ihr einmal nachsehen gehen, ob dort alles in Ordnung ist."}, {"Schaut in der Nähe des Friedhofs von Rabenflucht nach dem alten Mann in der Hütte."}},
[166] = {"Die Bruderschaft der Defias", {"Jetzt gibt es für Euch nur noch eine Aufgabe zu erledigen. Edwin van Cleef muss getötet werden. Zwar stimmt es mich traurig, einen Menschen zum Tode zu verurteilen, doch es ist zum Besten des Volkes von Westfall, wenn van Cleef ein für alle Mal aus dem Verkehr gezogen wird. Bringt mir den Kopf des Schurken, sobald die Tat getan ist."}, {"Tötet Edwin van Cleef und bringt seinen Kopf zu Gryan Stoutmantle."}},
[167] = {"Oh Bruder...", {"Wir waren tief unten in einer großen Mine in Westfall, deren Eingang unter einer Scheune in Moonbrook versteckt lag. Keine Ahnung, wo dieses elende Diebesgesindel herkam. Egal, jedenfalls stürzte der Minentunnel über uns ein. Ich hab's nach draußen geschafft, aber die anderen ... na ja ... von den anderen hat seither niemand mehr etwas gehört.","Ihr seht aus, als könntet Ihr vielleicht etwas mehr Glück haben, wenn Ihr dort hineingeht. Wenn Ihr nach meinem Bruder suchen könntet, wäre ich Euch dankbar. Er trug immer sein Forscherliga-Abzeichen bei sich. Wenn Ihr ihn selbst nicht finden könnt, dann würde zumindest das Abzeichen mir etwas Seelenfrieden verschaffen."}, {"Bringt Großknecht Thistlenettles Forscherliga-Abzeichen nach Stormwind zu Wilder Thistlenettle."}},
[168] = {"Die Suche nach Andenken", {"Viele meiner Freunde starben an jenem schrecklichen Tag, als der Minentunnel einstürzte. Wenn Eure Abenteuer Euch in jenen Abschnitt der Mine in Moonbrook führen sollten, dann haltet doch bitte die Augen offen nach jedem Zeichen, anhand dessen man sie identifizieren könnte. Wenn Ihr zum Beispiel ihre Gewerkschaftsausweise finden solltet, so bringt sie mir, dann sehe ich zu, dass ihre Familien informiert werden und damit wenigstens eine Art Schlussstrich unter diesen schrecklichen Unfall ziehen können."}, {"Beschafft 4 Gewerkschaftsausweise und bringt sie nach Stormwind zu Wilder Thistlenettle."}},
[169] = {"GESUCHT: Gath'Ilzogg", {"Gesucht! Gath'llzogg, Rädelsführer hinter den Angriffen der Blackrock auf Burg Steinwacht und den Überfällen auf die Ortschaft Seenhain.","Gath'llzogg ist äußerst gefährlich. Berichten zufolge hat er sich in der kürzlich eroberten Burg eingenistet und plant dort die weiteren Verbrechen des Blackrockklans gegen die menschliche Bevölkerung. Die Orcplage muss um jeden Preis eingedämmt werden. Der tapferen Seele, die Magistrat Solomon den Kopf von Gath'llzogg übergeben kann, winkt eine großzügige Belohnung."}, {"Erledigt Gath'llzogg und bringt seinen Kopf zu Magistrat Solomon in Seenhain, um die Belohnung zu erhalten."}},
[170] = {"Eine neue Bedrohung", {"Ich hoffe, Ihr seid hier, um uns zu helfen, <Klasse>. Nach dem letzten Angriff der Troggs können wir alle Hilfe gebrauchen, die wir kriegen können.","Ich habe gehört, die kleinen Biester sind überall im ganzen Land aufgetaucht, und wie es scheint, ist das Coldridge-Tal keine Ausnahme. Sie sind kreuz und quer in den Hügeln im Südosten und in der Nähe des gefrorenen Sees gesehen worden.","Und das ist noch nicht alles. Vor ein paar Nächten haben sie unser Lager im Westen angegriffen und überrannt!","Wir haben hier zu wenig Leute, <Jungchen/Mädel>, und wir brauchen ein paar starke Arme, die dabei helfen, die Troggs zurückzutreiben."}, {"Balir Frosthammer möchte, dass Ihr 6 Troggs der Felsenkiefer und 6 bullige Troggs der Felsenkiefer tötet."}},
[171] = {"Tageseltern der Allianz", {"Ihr wart die ganze Zeit so gut zu mir, <Name>. Nie hätte ich erwartet, während der Kinderwoche so viel Spaß zu haben! Danke schön.","Ich wünschte, all die anderen Waisenkinder hätten das Glück, so tolle Dinge zu unternehmen, wie wir. Meint Ihr, wir können Craggle Schlingerkreisel wiederfinden und bekommen einen Schaumstoffschwertständer von ihm? Ich glaube, er schiebt noch immer seinen Karren an den Kanälen rund um den Handelsdistrikt herum. So könnten alle anderen im Waisenhaus ihren Spaß haben!","Danach wird es Zeit für mich, nach Hause zu gehen."}, {"Kauft einen Schaumstoffschwertständer von Craggle Schlingerkreisel und bringt dann Euren Schützling in das Waisenhaus von Sturmwind zurück, indem Ihr die Pfeife des Menschenwaisenkindes an Waisenmatrone Nachtigall im Kathedralenviertel übergebt."}},
[172] = {"Die Kinderwoche", {"Ihr wollt uns tatsächlich hier im Waisenhaus zur Hand gehen? Seid gesegnet, Freund. Eure Unterstützung in der Kinderwoche ist für uns von unschätzbarem Wert.","Nehmt diese Pfeife. Sie kann sieben Tage lang verwendet werden - über die ganze Dauer der Kinderwoche hinweg. Durch Benutzung der Pfeife, könnt Ihr mit dem Waisenkind sprechen, auf das Ihr aufpasst. Verwendet sie erneut, um das Kind wieder zu entlassen.","Das Kind ist schon ganz aufgeregt und freut sich darauf Euch kennenzulernen. Worauf wartet Ihr noch <Name>, benutzt die Pfeife und trefft Euren Schützling."}, {"Benutzt die Pfeife des orcischen Waisenkindes um während der Kinderwoche mit Eurem Schützling zu sprechen."}},
[173] = {"Worgs in den Wäldern", {"Es scheint, als würden dunkle Dinge unaufhaltsam in den Dämmerwald gezogen. Meister Carevin hat sich der Aufgabe verschrieben, das Böse und die Ketzerei zu vertreiben. Dank unserer Anstrengungen lebt das Volk von Dunkelhain in Sicherheit.","Ihr glaubt, Ihr seid würdig, Euch unserer Gruppe anzuschließen?","Einst dachte ich wie Ihr. Die Selbstzufriedenheit der Wache hatte mich meiner Illusionen beraubt und so schloss ich mich Meister Carevin an. Wenn Ihr Euch beweisen möchtet, so kann dies nicht durch Worte geschehen.","Messt Eure Fertigkeiten an den Schattenwirkern der Nachtheuler im Schattenhain und im verlassenen Obstgarten."}, {"Tötet für Calor aus Dunkelhain 6 Schattenwirker der Nachtheuler."}},
[174] = {"Der Blick zu den Sternen", {"Mein Freund Cog, der Gnom, hat mir versprochen, mir beim Bau eines Gerätes zu helfen, mit dem ich in die Sterne blicken kann. Jenseits der Magie existiert eine Wahrheit, mit deren Hilfe sich die seltsame Verwandlung des Waldes erklären lässt.","Bringt mir eine Bronzeröhre, die von einem geschickten Ingenieur hergestellt wurde. Wenn man nach Cogs Blaupause geht, ist das der erste Schritt."}, {"Viktori Prism'Antras aus Dunkelhain möchte, dass Ihr ihm eine Bronzeröhre bringt."}},
[175] = {"Der Blick zu den Sternen", {"Laut Cogs Blaupause brauche ich jetzt etwas, mit dem Licht reflektiert werden kann. Ich erinnere mich an eine Frau namens Mary, die hier in der Gegend eine gewisse Bekanntheit erlangte, weil sie immer einen Handspiegel bei sich trug. Vielleicht könnt Ihr diese Mary aufspüren und sie fragen, ob sie ihn uns für unser Vorhaben vielleicht leiht, weil wir ja eine reflektierende Oberfläche brauchen... Ich glaube, sie wohnte früher im südlichen Dämmerwald..."}, {"Findet Mary und überredet sie, einen Gegenstand mit reflektierender Oberfläche zu Viktoris Projekt beizusteuern."}},
[176] = {"GESUCHT: \"Hogger\"", {"Gesucht: Hogger","Ein riesiger Gnoll, Hogger, streunt im südwestlichen Elwynn durch den Wald. Er hat bislang alle Versuche, ihn zu fangen, vereiteln können.","Die Armee von Stormwind hat ein großzügiges Kopfgeld auf den Gnoll ausgesetzt. Kopfgeldjäger, die sich die Belohnung verdienen möchten, sollten einen Beweis für Hoggers Tod bei Marshal Dughan in Goldhain vorlegen."}, {"Erschlagt den Gnoll Hogger und bringt seine gewaltige Gnoll-Klaue zu Marshal Dughan."}},
[177] = {"Der Blick zu den Sternen", {"Der Handspiegel war eines meiner wertvollsten Besitztümer bis zu... dem Vorfall. Danach warf ich den Spiegel hinter der Scheune fort. Es gab da einen irrsinnigen Ghul, der sich früher dort hinten herumgetrieben hat. Vielleicht hat der ihn aufgehoben und mitgenommen. Gerüchten zufolge haust die teuflische Bestie im Friedhof \"Stille Gärten\"."}, {"Nehmt dem irrsinnigen Ghul den Handspiegel der blinden Mary ab und gebt ihn Viktori in Dunkelhain."}},
[178] = {"Wiederbeschaffung für Theocritus", {"<Ihr hört eine körperlose Stimme:>","Ihr müsst nicht erschrecken. Ich bin Theocritus, Hochmagier des Turms von Azora im Wald von Elwynn. Der Anhänger, den Ihr in Händen haltet, ist ein Mittel zur Kommunikation zwischen den Schattenfellgnollen und Morganth, ihrem Meister.","Nach langen Monaten der Forschung glaube ich, dass nun auch ich durch diese Anhänger kommunizieren kann. Wenn Ihr diese Nachricht hören könnt, dann war mein Zauber erfolgreich.","Bringt mir diesen Anhänger und ich werde Euch für Eure Dienste belohnen."}, {"Bringt den verblassten Schattenfellanhänger zu Theocritus, dem Magier."}},
[179] = {"Zwergenausstatter", {"Was haben wir denn hier? Ihr seht aus, als könntet Ihr was brauchen, was Euch die Hände warm hält, hm?","Ich sag Euch, was da helfen würde: ein paar schöne, warme Handschuhe. Und weil ich so ein netter Kerl bin, geb ich Euch doch gern so ein Paar. Unter einer Bedingung, versteht sich.","Ihr müsst mir etwas Wolfsfleisch besorgen. Ist doch 'n guter Handel, oder? Ihr bringt mir etwas Wolfsfleisch und ich sorge dafür, dass Ihr Euch keine Frostbeulen an den Fingern holt. Na, was sagt Ihr?"}, {"Sten Stoutarm hätte gern 8 Stücke zähes Wolfsfleisch."}},
[180] = {"GESUCHT: Lieutenant Fangor", {"Gesucht: Lieutenant Fangor","Bekannter Anführer des Gnollklans der Schattenfelle; arbeitet für Morganth, den Feind der Stadt und des Königreiches. Auf Geheiß von Magistrat Solomon wird der Tod dieses üblen Verräters an Stormwind gewünscht. Zeigt dem Magistraten seine Tatze zum Beweis, dass er tot ist."}, {"Tötet Lieutenant Fangor und kehrt mit seiner Tatze zu Magistrat Solomon nach Seenhain zurück."}},
[181] = {"Der Blick zu den Sternen", {"Jetzt brauchen wir nur noch einen weiteren Gegenstand, um das Gerät fertig zu stellen. Laut Cogs Blaupause wird eine Linse oder etwas in der Art benötigt. Die einzige Linse, von der ich weiß, dass sie groß genug wäre, wird sehr schwierig zu beschaffen sein.","Es gibt da einen Oger namens Zzarc'Vul, der im Ogerhort im Süden des Dämmerwalds haust. Wenn Ihr könnt, bringt mir sein Monokel, dann werde ich das als unsere Linse verwenden."}, {"Findet Zzarc'Vul im südlichen Ogerhort des Dämmerwalds und bringt sein Monokel zu Viktori nach Dunkelhain."}},
[182] = {"Die Trollhöhle", {"Mein Bruder Senir und ich wurden in unterschiedliche Ecken von Dun Morogh entsandt, um dort die von den Trollen ausgehende Gefahr zu untersuchen. Der Senat hat mit den Troggs bereits alle Hände voll zu tun, deswegen kann er keinen weiteren Ärger gebrauchen.","Nach allem, was ich gesehen habe, ist die Position der Trolle hier im Coldridgetal nicht sonderlich gut - sie sind vor allem in der südlichen Höhle zu finden. Ich würde sagen, die Armee brauchen wir nicht. Einige starke Arme sollten mehr als ausreichend sein.","Möchtet Ihr vielleicht mithelfen? Ich bin ermächtigt, Euch für Eure Dienste eine Entschädigung anzubieten."}, {"Grelin Whitebeard möchte, dass Ihr 14 Trollwelpen der Frostmane tötet."}},
[183] = {"Der Eberjäger", {"Es geht doch nichts über einen Tag auf der Eberjagd, wie?","Na ja, hier im Coldridgetal gibt es so viele Eber, dass es fast schon keinen Spaß mehr macht. Man muss sie gar nicht mehr reizen. Sie sind schon aggressiv und angriffslustig, ohne dass man nachhilft. Um genau zu sein, streifen in letzter Zeit so viele Eber durch die Gegend, dass es gefährlich für mich geworden ist, auf die Jagd zu gehen.","Ich mach's kurz: Wenn Ihr mir dabei helfen könntet, einige der Eber zu töten, dann würde ich das zu schätzen wissen."}, {"Talin Keeneye möchte, dass Ihr 12 kleine Klippeneber tötet."}},
[184] = {"Furlbrows Urkunde", {"Dies ist eine Besitzurkunde für ein Stück Ackerland in Westfall. Es ist von einem gewissen Theodore Furlbrow und seiner Frau Verna unterzeichnet. Auf der Rückseite der Besitzurkunde stehen ein paar hastig hingekritzelte Worte:","\"Wir haben Furlbrow etwas bearbeitet und ihm seine Besitzurkunde abgeluchst. Wäre vielleicht ganz nützlich, wenn Ihr Euch auch so eine fälschen wolltet.","Um die Furlbrows müssen wir uns jedenfalls keine Sorgen machen. Zuletzt sah ich sie kurz hinter Westfall in einem Wagen mit gebrochener Achse.\"","Ihr denkt, die Furlbrows hätten ihre Besitzurkunde vielleicht gern zurück ..."}, {"Bringt Furlbrows Besitzurkunde zu Bauer Furlbrow."}},
[185] = {"Beherrschen der Tigerjagd", {"Als ich noch ein kleines Mädchen war, brachte mein Vater mir die Kunst der Tigerjagd bei.","Ihr werdet feststellen, dass die jungen Katzen sich wesentlich leichter aufspüren und töten lassen. Deswegen fangen wir auch klein an. Man findet die jungen Tiger oftmals in der Nähe des Expeditionslagers.","Übt Euch im Fährtenlesen und seht zu, ob Ihr nicht einige der Wildtiere zur Strecke bringen könnt."}, {"Ajeck Rouack von Nesingwarys Expedition möchte, dass Ihr 10 junge Schlingendorntiger tötet."}},
[186] = {"Beherrschen der Tigerjagd", {"Ihr macht Fortschritte, <Klasse>. Jetzt lasst uns sehen, wie Ihr Euch gegen die ausgewachsenen Katzen anstellt. Tötet diesmal 10 Schlingendorntiger."}, {"Ajeck Rouack von Nesingwarys Expedition möchte, dass Ihr 10 Schlingendorntiger tötet."}},
[187] = {"Beherrschen der Tigerjagd", {"Jetzt werde ich den Einsatz erhöhen und Eure Fähigkeiten wirklich prüfen. Beweist mir, dass Ihr 10 alte Schlingendorntiger töten könnt. Wenn Ihr das schafft, dann seid Ihr bereit für Eure letzte Herausforderung, ehe ich Euch in Meister Nesingwarys Gegenwart <zum Meisterjäger/zur Meisterjägerin> ernenne.","Nicht nur sind die alten Schlingendorntiger am schwierigsten zu finden, sie sind auch am schwierigsten zu besiegen."}, {"Ajeck Rouack von Nesingwarys Expedition möchte, dass Ihr 10 alte Schlingendorntiger tötet."}},
[188] = {"Beherrschen der Tigerjagd", {"Hier ist die letzte Aufgabe, die ich Euch auferlegen werde. Wir sind schon seit Wochen einer schwer zu fassenden Tigerin auf der Spur. Wir nennen das Tier Sin'Dall. Vielleicht gelingt Euch ja, was noch kein <Volk> vor Euch geschafft hat: Spürt Sin'Dall auf und bringt sie zur Strecke. Bringt mir ihre Tatze zum Beweis für Eure Leistung.","Aber die Tigerin aufzuspüren, wird nicht leicht sein."}, {"Ajeck Rouack von Nesingwarys Expedition möchte, dass Ihr Sin'Dall tötet und mit ihrer Tatze zurückkehrt."}},
[189] = {"Ohren der Bloodscalp", {"Verdammte Trolle! Sie haben im Dschungel schon etliche unserer Agenten getötet und ich habe bereits einige meiner besten Wachen in Scharmützeln mit ihnen verloren. Was das alles kostet! Wie soll ich das nur Baron Revilgaz erklären?","Entschieden handeln... Denk nach, Kebok... Du bist auf den Posten, den du heute hast, nicht durch Rumsitzen gekommen... Ha, ich hab's! Ihr... <Name>, richtig? Ihr helft mir doch, oder? Es gibt auch Geld zu verdienen!","Folgender Handel... Ihr bringt mir die Ohren von 15 Trollen der Bloodscalp und ich bezahle Euch gut dafür."}, {"Verschafft Euch 15 Ohren der Bloodscalp und bringt sie Kebok in Booty Bay."}},
[190] = {"Beherrschen der Pantherjagd", {"Wenn Ihr zusammen mit dieser Elitegruppe, die Hemet zusammengestellt hat, an der Jagd teilnehmen wollt, dann müsst Ihr erst beweisen, dass Ihr <ein fähiger Pantherjäger/eine fähige Pantherjägerin> seid. Aber wir fangen ganz einfach an, keine Sorge. Schauen wir mal, wie Ihr Euch beim Töten von 10 jungen Panthern anstellt.","Die Tiere aufzuspüren ist nur die halbe Miete."}, {"Sire S. J. Erlgadin von Nesingwarys Expedition möchte, dass Ihr 10 junge Panther tötet."}},
[191] = {"Beherrschen der Pantherjagd", {"Jetzt seid Ihr bereit, Euch an einen Gegner zu wagen, der eine Nummer schwieriger ist. Wenn Ihr Euch wirklich als würdig erweisen wollt, mit solchen Leuten wie den Großwildjägern zu verkehren, dann müsst Ihr beweisen, dass Ihr 10 Panther töten könnt.","Diese Bestien sind schon etwas zäher. Die lassen sich nicht so leicht erlegen wie die Jungtiere, mit denen Ihr Euch bis jetzt abgegeben habt."}, {"Sire S. J. Erlgadin von Nesingwarys Expedition möchte, dass Ihr 10 Panther tötet."}},
[192] = {"Beherrschen der Pantherjagd", {"Jetzt kommt der schwierige Teil. Ein echter Pantherjäger beweist seine Fähigkeiten damit, dass er Schattentatzenpanther erlegt, die tödlichsten Vertreter ihrer Art im ganzen Schlingendorntal. Beweist uns, dass Ihr 10 dieser Bestien zur Strecke bringen könnt.","Und wenn Ihr glaubt, die seien schwer aufzuspüren, dann wartet nur, bis Ihr versucht, eine von ihnen zu erlegen..."}, {"Sire S. J. Erlgadin von Nesingwarys Expedition möchte, dass Ihr 10 Schattentatzenpanther tötet."}},
[193] = {"Beherrschen der Pantherjagd", {"Ihr habt schon fast bewiesen, dass Ihr die Kunst der Pantherjagd meisterlich versteht. Schon seit längerem pirscht ein Panther namens Bhag'thera durch den Dschungel. Bis jetzt ist er unserer Jagdgesellschaft immer ausgewichen. Seht zu, ob es Euch mit Euren Fähigkeiten gelingt, den großen Bhag'thera zu töten.","Bringt mir den Fangzahn von Bhag'thera, dann habt Ihr Euch meinen Respekt redlich verdient!"}, {"Sire S. J. Erlgadin von Nesingwarys Expedition möchte, dass Ihr ihm den Fangzahn von Bhag'thera bringt."}},
[194] = {"Beherrschen der Raptorenjagd", {"Soso, Ihr glaubt also, Eure Jagdfertigkeiten sind erstklassig? Das lässt sich ganz leicht feststellen. Zieht hinaus in den Dschungel und tötet 10 Schlingendornraptoren. Dann werden wir ja sehen, wie gut Ihr als Großwildjäger wirklich seid.","Und nein, ich werde Euch nicht sagen, wo Ihr sie finden könnt! Die Viecher aufzuspüren ist Teil der Aufgabe."}, {"Hemet Nesingwary möchte, dass Ihr 10 Schlingendornraptoren tötet."}},
[195] = {"Beherrschen der Raptorenjagd", {"Jetzt machen wir die Herausforderung ein wenig schwieriger und schauen mal, ob Ihr dem gewachsen seid. Begebt Euch in den Dschungel und erlegt 10 dieser elenden Schmetterschwanzraptoren. Zeigt uns, was Ihr so drauf habt!"}, {"Hemet Nesingwary möchte, dass Ihr 10 Schmetterschwanzraptoren tötet."}},
[196] = {"Beherrschen der Raptorenjagd", {"Mal sehen, wie Ihr mit der nächsten Herausforderung fertig werdet. Dort draußen treibt sich eine sehr scheue Art von Raptoren herum. Wir nennen sie Dschungelpirscher. Sie sind sehr viel schwieriger aufzuspüren und zu erlegen als die anderen Arten, an denen Ihr Euch bisher versucht habt. Tötet 10 Dschungelpirscher, dann verrate ich Euch, wie Ihr zu einem noch besseren Jagderlebnis kommt."}, {"Hemet Nesingwary möchte, dass Ihr 10 Dschungelpirscher tötet."}},
[197] = {"Beherrschen der Raptorenjagd", {"Da Ihr Euch im Dschungel als so tatkräftig erwiesen habt, lasst mich Euch von einem wilden Raptor erzählen, den sogar ich nicht erlegen konnte. Ajeck hier nennt diesen verschlagenen Raptor Tethis.","Wenn Ihr Euch als <wahrer Meister/wahre Meisterin> erweisen wollt, dann erlegt Tethis und bringt mir seine Kralle. Es wäre eine Leistung, die bisher noch keinem anderen Großwildjäger gelungen ist."}, {"Hemet Nesingwary möchte, dass Ihr Tethis tötet, einen schwer fassbaren, gefährlichen Raptoren des Schlingendorntals."}},
[198] = {"Vorräte für den Gefreiten Thorsen", {"Hmm. Ja, es gibt tatsächlich etwas, das Ihr tun könntet. Lieutenant Doren und seine Leute oben im Norden warten auf ihre turnusmäßige Vorratslieferung von uns.","Die Vorräte wurden noch nicht bei ihnen abgeliefert und jetzt, wo die Trolle ständig angreifen, brauche ich jemanden, der das erledigt. Dorens Lager liegt im Nordwesten der Straße, dort, wo man vom Dämmerwald aus ins Schlingendorntal kommt. Gebt die Vorräte dem Gefreiten Thorsen. Wenn er nicht im Lager ist, dann patrouilliert er südlich davon. Er wird sie in Empfang nehmen und verstauen.","Aber dass eines klar ist: Wenn Ihr versagt, dann müsst Ihr mir Ersatz leisten."}, {"Liefert die verschiedenen Goblin-Vorräte bei dem Gefreiten Thorsen ab."}},
[199] = {"Eine dunkle Bedrohung", {"Ihr nehmt eine Probe von dem sonderbar riechenden Pulver."}, {"Kehrt zu Chefingenieur Hinderweir zurück und zeigt ihm den Hinweis, den Ihr entdeckt habt."}},
[200] = {"Buchmacher Herod", {"Wenn Kurzen Thorsens Geheimnis kennt, dann haben wir einen Spion in unseren Reihen. Wir müssen herausfinden, wie das Geheimnis verraten werden konnte!","Thorsens Kontaktperson in Kurzens Lager war der Buchhalter Herod. Herod ist Kurzens Nachschuboffizier und weiß alles über den Betrieb in Kurzens Truppenlager. Wenn Ihr Herods Unterlagen oder Aufzeichnungen beschaffen könntet, dann finden wir vielleicht heraus, wie Thorsen entdeckt werden konnte.","Als wir noch unter Kurzen dienten, war das Büro des Buchhalters im oberen Stock des einzigen zweistöckigen Gebäudes im Lager. Seine Aufzeichnungen sind vermutlich immer noch dort zu finden."}, {"Findet Buchhalter Herods Aufzeichnungen."}},
[201] = {"Untersuchung des Lagers", {"Ich habe Berichte gehört, nach denen eine Gruppe von Jägern ihr Lager flussaufwärts vom Bergbaucamp der Venture Company am Nazferitisee aufgeschlagen hat. Das wäre mir normalerweise völlig egal, aber ich habe gehört, dass Hemet Nesingwary einer der Jäger dort sein soll. Das ist zwar absolut unwahrscheinlich, aber... na ja, er hat sehr gute Beziehungen, wenn Ihr wisst, was ich meine, und man kann nie vorsichtig genug sein.","Wollt Ihr nicht hin und Euch das Lager einmal anschauen, <Name>, und sehen, was die vorhaben?"}, {"Lokalisiert das Jägerlager und teilt dann Krazek in Booty Bay mit, wo es sich befindet."}},
[202] = {"Colonel Kurzen", {"Ihr habt Euren Wert wieder und wieder unter Beweis gestellt und Euer Einfallsreichtum scheint sich mit dem von Kurzen selbst messen zu können. Ihr könntet die Rettung für unseren Rebellentrupp sein... falls Ihr Euch gegen Kurzen stellen und siegen könnt!","Er erteilt die Befehle an seine Männer von tief aus dem Vorratslager heraus. Ihr müsst Euch Zutritt zu dieser Höhle verschaffen, an seinen Verteidigern vorbei, und dann Kurzen und seinen Führungsoffizieren gegenübertreten.","Lebt wohl, <Name>. All meine Hoffnung, ebenso das Schicksal meiner Männer, ruht in Euren Händen."}, {"Tötet 6 von Kurzens Elitesoldaten, 4 von Kurzens Stabschefs und bringt Kurzens Kopf ins Rebellenlager zu Lieutenant Doren."}},
[203] = {"Die zweite Rebellion", {"Wir kämpfen einen aussichtslosen Kampf gegen Colonel Kurzen. Nicht nur, dass der Mann ein brillanter Taktiker ist, seine Männer sind uns auch noch zahlenmäßig mindestens 10 zu 1 überlegen! Um überleben zu können, müssen wir seine eigenen, auf Heimlichkeit basierenden Taktiken gegen ihn anwenden.","Betretet sein Lager im Osten und tötet seine Leute. So viele Ihr könnt. Und verschwindet wieder, bevor seine Verstärkung Euch in die Zange nehmen kann."}, {"Tötet 15 von Kurzens Dschungelkämpfern.","Kehrt ins Rebellenlager zu Sergeant Yohwa zurück."}},
[204] = {"Schlechte Medizin", {"Um überhaupt eine Chance gegen Kurzen zu haben, müssen wir ihm die Möglichkeiten beschneiden, seine Truppen zu heilen. Er hat um sich eine große Anzahl an Medizinmännern versammelt, die seine Streitkräfte mit Dschungelarzneien heilen und unsere Kämpfer mit Giftfarnextrakt vergiften.","Wir brauchen dieses Arzneien, um unsere eigenen Leute zu heilen, und wir brauchen eine Probe dieser Extrakte, damit wir sie untersuchen und unser eigenen Arzneien herstellen können. Sucht in Kurzens Truppenlager nach beidem: Seine Medizinmänner haben die Arzneien und die Extrakte werden vermutlich irgendwo in den Vorratskästen im Lager verstaut sein."}, {"Bringt Sergeant Yohwa im Rebellenlager 7 Dschungelarzneien und 1 Giftfarnextrakt."}},
[205] = {"Troll-Hexerei", {"Während ich die Legenden studiere, die Ihr beschafft habt, sammelt bitte für mich die Gerätschaften der bösen Trollmagie. Begebt Euch in das Gebiet der Skullsplittertrolle, eines zweiten Stammes weit entfernt im Süden, und beschafft Euch dort Fetische der Skullsplitter von ihren Hexendoktoren und Mystikern."}, {"Bringt Bruder Nimetz 4 Fetische der Skullsplitter ins Rebellenlager."}},
[206] = {"Mai'Zoth", {"Kurzen mag vielleicht tot sein, doch das, was ihn zum Bösen trieb, ist noch am Leben! Mittels meiner Studien habe ich Folgendes entdeckt: Ein Ogermagier namens Mai'Zoth lebt in einem Ogerhort in den östlichen Bergen des Schlingendorntals. Gerüchten zufolge wendet er Magie an, mit deren Hilfe er in die Gedanken anderer eindringen und diese kontrollieren kann, und ich bin mir sicher, dass er es war, der Kurzen verderbt hat.","Bleibt Mai'Zoth am Leben, dann beeinflusst er vielleicht noch andere und vielleicht würde dann sogar ein zweiter Kurzen entstehen.","Tötet ihn und bringt mir sein Gedankenauge, das Artefakt, mit dem er andere Wesen verderbt."}, {"Bringt das Gedankenauge zu Bruder Nimetz ins Rebellenlager."}},
[207] = {"Das Geheimnis um Kurzen", {"Colonel Kurzen war einst ein großer, ein edler Mann - ich muss herausfinden, wie es zu seiner Entartung kommen konnte! Es ist möglich, dass er dem Einfluss von Trollmagie unterliegt. Um diesen Verdacht zu bestätigen, muss ich einige ihrer Legenden studieren.","In den Trollruinen des Schlingendorntals liegen 4 Schrifttafeln versteckt. Ich muss wissen, was auf diesen Tafeln geschrieben steht! Schreibt die Zeichen ab, die Ihr auf den Tafeln findet, und bringt mir die Abschriften.","Die erste Schrifttafel befindet sich in den Ruinen von Bal'lal, die zweite im finsteren Riff und die dritte und vierte Schrifttafel findet Ihr in den Ruinen von Zul'Kunda."}, {"Findet die 4 Schrifttafeln und bringt ihre Legenden zu Bruder Nimetz zurück."}},
[208] = {"Großwildjagd", {"<Name>, Ihr seid <ein Jäger, der/eine Jägerin> die; unserer Gesellschaft wahrlich würdig ist. Von den Schlachtfeldern von Lordaeron bis in den tiefsten Dschungel hinein habe ich selten jemanden gesehen, der so geschickt mit den Waffen umgegangen ist wie Ihr.","Ihr habt die furchterregende Sin'Dall, den schwer fassbaren Bag'thera und die tückische Tethis erlegt. Doch die wahre Trophäe ist der Kopf von König Bangalash. Dieser weiße Tiger ist der Grund, warum ich hier bin. Ich habe schon seit geraumer Zeit ein Auge auf ihn geworfen. Wenn Ihr König Bangalash tötet, dann steht ein für alle Mal fest, dass Euer jägerisches Können von niemandem übertroffen wird."}, {"Hemet Nesingwary möchte, dass Ihr ihm den Kopf von König Bangalash, dem großen weißen Tiger, bringt."}},
[209] = {"Hauer der Skullsplitter", {"Es ist teuer und anstrengend, genug Tiger zu töten, um damit den Bedarf an Tigerzähnen für den Export auf die Inseln in den südlichen Meeren decken zu können. Glücklicherweise haben wir eine gewisse Technik entwickelt, mit deren Hilfe wir aus einfachem hornähnlichen Material nicht nachweisbare Fälschungen herstellen können.","Erstaunlicherweise eignen sich die Hauer von Skullsplittertrollen dafür am besten.","Und bevor Ihr irgendwas sagt - was der Käufer nicht weiß, macht ihn nicht heiß, hab ich nicht Recht? Bringt mir so viele Hauer wie möglich, damit wir mit der monatlichen Lieferung anfangen können!"}, {"Besorgt 18 Hauer der Skullsplitter und bringt sie zu Kebok."}},
[210] = {"Kochen mit Krazek", {"Meistens bin ich es, der hier im Lager kocht. Und das war auch immer ganz schön viel Arbeit, bis ich von einem Goblin-Händler in Booty Bay einen mechanischen Kochtopf gekauft habe. Der Topf ist einfach unglaublich. Er hat mir unzählige Stunden Arbeit erspart - bis er kaputtgegangen ist!","Könnt Ihr den Topf dem Goblin zurückbringen, damit er ihn mir repariert? Der Händler heißt Krazek."}, {"Bringt Krazeks Spezialtopf zu Krazek, dem Goblin."}},
[211] = {"Weh dir, Andorhal", {"Endlich ist die Zeit gekommen, Andorhal anzugreifen und den Lich, der die Stadt kontrolliert, zu vertreiben.","Ihr habt vielleicht einen Beschwörungskristall in den von Euch markierten Wachtürmen bemerkt. Wir glauben, dass diese dem Lich die Kraft geben, die er zur Kontrolle der Stadt benötigt. Ihr müsst alle vier ausschalten. Wird einer davon zerstört, dürfte das zweifellos weitere Truppen des Lichs auf den Plan rufen. Wenn alle vier vernichtet wurden, dürfte der Lich selbst auftauchen, um das Herz der Stadt zu verteidigen.","Vernichtet den Lich, <Name>, und bringt mir einen Splitter seines Phylakteriums als Beweis!"}, {"Bringt den Splitter von Arajs Phylakterium zu Kommandant Ashlam Valorfist auf der Chillwindspitze in den westlichen Pestländern."}},
[212] = {"Eine kalte Mahlzeit", {"Eines meiner berühmtesten Gerichte ist gekühlter Basilisk an Limonenvinaigrette. Das Problem ist nur: Das Fleisch lässt sich unmöglich frisch halten und ich muss das Gericht heute Abend anlässlich des Geburtstags eines Edelmannes servieren!","Wenn Ihr mir also schnell einen gekühlten Basiliskenschenkel von einem frisch getöteten Kaltaugenbasilisken bringen könnt, dann schulde ich Euch ein Festessen, das eines Königs würdig wäre!","Kaltaugenbasilisken streifen durch die Stranddünen im Nordwesten des Schlingendorntals, also habt Ihr eine lange Reise vor Euch. Und macht schnell!"}, {"Tötet einen Kaltaugenbasilisken, beschafft Euch einen gekühlten Basiliskenschenkel und bringt ihn Angus Stern im Blauen Eremiten.","<Um diese Aufgabe erfolgreich abschließen zu können, dürft Ihr Euren Geist nicht freigeben.>"}},
[213] = {"Feindliche Übernahme", {"Diesmal ist er zu weit gegangen! Schlimm genug, dass Gelriz dabei ist, die von den Handelsfürsten eingesetzten Moguln rauszudrängen, jetzt versucht er auch noch, den berüchtigtsten aller Piraten mit hineinzubringen!","Das lässt Revilgaz sich nicht bieten und er hat mir aufgetragen, das Problem auf meine Weise zu lösen.","Was meine Weise ist? Diebstahl, ganz einfach. Die Geologen der Venture Company zeigen ein auffälliges Interesse an den seltsamen blauen Kristallen, die sie bisweilen in den Minen finden. Bringt mir einige Proben dieser Steine von den Geologen; es ist mir völlig egal, was Ihr tun müsst, um an sie heranzukommen."}, {"Beschafft 8 gerommelte Kristalle und bringt sie nach Booty Bay zu Kebok."}},
[214] = {"Rote Seidenkopftücher", {"Es ist kein Geheimnis, dass das Markenzeichen der Defiasbande ihre roten Kopftücher sind. Aber wir haben herausgefunden, dass das Material, aus dem diese Kopftücher gemacht sind, den Rang des Bandenmitglieds bezeichnet.","Jetzt, wo wir das wissen, möchte ich, dass Ihr so viele hochrangige Mitglieder der Defias eliminiert, wie Ihr könnt. Die Bandenmitglieder mit der höchsten Position findet Ihr zweifellos in van Cleefs Geheimversteck. Wenn Ihr fertig seid, bringt mir 10 rote Seidenkopftücher zum Beweis, dass sie tot sind, dann werde ich Euch belohnen. Viel Glück, <Name>."}, {"Späherin Riell am Turm auf der Späherkuppe möchte, dass Ihr ihr 10 rote Seidenkopftücher bringt."}},
[215] = {"Geheimnisse im Dschungel", {"Danke, dass Ihr mich gerettet habt, <Name>. Ich weiß, wie es aussieht, aber lasst mich bitte erklären!","Ich hatte von Lieutenant Doren den Auftrag erhalten, mich wieder Kurzens Männern anzuschließen. Ich sollte sie ausspionieren und den Rebellen Informationen bringen, die sie bitter benötigen. Was Ihr hier miterlebt habt, war das, was ich für meine Chance hielt, Kurzens Truppenlager zu infiltrieren. Ich habe mich geirrt und ich muss Euch nochmals danken, dass Ihr mich gerettet habt.","Diese Mission war streng geheim, Ihr dürft also niemandem davon erzählen. Aber wenn Ihr mit Doren sprecht, dann wird der Euch alles erklären."}, {"Sprecht im Rebellenlager mit Lieutenant Doren."}},
[216] = {"Hart auf hart", {"Die Distelfellfurbolgs bedrohen unsere Expansion! Das Lager der Distelfelle östlich von uns blockiert den Weg zwischen uns hier und dem Splintertreeposten. Dieser Weg würde das Dorf Astranaar umgehen; ohne ihn überlassen wir der Allianz eine zusätzliche Möglichkeit der Ausbreitung.","<Name>, wir dürfen keine Lücke in unseren Expansionspfaden erlauben!","Begebt Euch ins Distelfelllager und dünnt die Furbolgs etwas aus. Ihr werdet für diese Aufgabe entschädigt werden, aber noch viel wichtiger ist, dass Ihr Eure Pflicht der Horde gegenüber tun werdet."}, {"Schaltet 12 Rächer der Distelfelle und 12 Schamanen der Distelfelle aus. Man findet sie am häufigsten östlich vom Zoramstrand im Distelfelllager. Wenn das erledigt ist, kehrt zu Karang Amakkar in den Zoram'gar-Außenposten in Ashenvale zurück."}},
[217] = {"Verteidigung der Länder des Königs", {"<Name>, unsere Späher haben den Anführer des jüngsten Eindringens der Troggs ermitteln können. Es scheint, als folgten diese Bestien am Loch Modan einem Häuptling namens Grawmug. Grawmug wird jedoch aufs Schwerste bewacht. Zwei seiner Elitewachen, Knirscher und Muskelprotz, weichen nie von seiner Seite.","Wenn Ihr Euch dem gewachsen fühlt, <Name>, dann möchte ich, dass Ihr das Attentat auf Grawmug und seine beiden Schläger anführt. Wenn die Führungsriege der Troggs tot ist, dann haben wir vielleicht eine Chance, diese Bestien wieder unter die Erde zu treiben."}, {"Tötet den Anführer der Troggs, Grawmug, sowie seine beiden Wachen, Knirscher und Muskelprotz, und erstattet dann Captain Rugelfuss im südlichen Wachturm Bericht."}},
[218] = {"Das gestohlene Tagebuch", {"Mein Tagebuch! Sie haben es in die Höhle gebracht. Der, der es hatte, war ein großer Kerl mit seltsamen Zeichen auf der Haut und im Gesicht. Viel mehr konnte ich leider nicht sehen.","Ihr hattet ja mit den Trollen Erfolg, vielleicht könntet Ihr es mir wiederbeschaffen?"}, {"Grelin Whitebeard möchte, dass Ihr Grik'nir den Kalten tötet und ihm sein Tagebuch wiederbeschafft."}},
[219] = {"Vermisst", {"Freunde der Allianz! Endlich!","Helft... bitte...","Das Letzte, an das ich mich erinnere, ist, dass diese abscheulichen Orcs unser Regiment überwältigten. Die meisten Männer wurden getötet; ich selbst erhielt einen Schlag an den Kopf und alles wurde schwarz. Jetzt werde ich in dieser Höhle gefangen gehalten. Bitte helft mir; ich bin schwer verletzt.","Ich brauche Hilfe auf dem Weg zurück in die Stadt... bitte... Ihr seid meine letzte Hoffnung..."}, {"Beschützt Corporal Keeshan auf dem Rückweg ins Rotkammgebirge."}},
[220] = {"Ruf des Wassers", {"Islen hat noch eine weitere Aufgabe für Euch, ehe Ihr zur Belohnung Euer nächstes Totem erhaltet. Die Phiole mit Wasser, die ich Euch gegeben habe, wird für diese Aufgabe gebraucht, um was auch immer es sich dabei handeln mag.","Geht nun und sucht sie auf. Wir werden uns eines Tages wieder begegnen."}, {"Bringt die Phiole mit reinstem Wasser in das Brachland zu Islen Waterseer."}},
[221] = {"Worgs in den Wäldern", {"Habt Ihr vielleicht schon festgestellt, dass einige größere Worgs zusammen mit den Schattenwirkern im Wald herumziehen? Nach allem, was wir wissen, machen diese Dunkelläufer den Großteil der Worg-Population aus.","Auf meinen Wanderungen habe ich außerdem festgestellt, dass sie in den verlassenen Obsthain im Südwesten der Stadt eingefallen sind. Diese Worgs sind etwas zäher als die Letzten, denen Ihr begegnet seid.","Also seht Euch vor."}, {"Tötet für Calor aus Dunkelhain 12 Dunkelläufer der Nachtheuler."}},
[222] = {"Worgs in den Wäldern", {"Eure früheren Leistungen haben mich überzeugt, dass Ihr in der Lage seid, Euch mit den zähesten Worgs zu messen, die sich in den Wäldern eingenistet haben.","Von all den Worgs, die plötzlich hier zu finden sind, haben sich die Faulreißer und die Besudelten als die gefährlichsten herausgestellt. Sie haben sich in der Nähe einiger Höhlen und in der Mine im Süden niedergelassen.","Noch aus weiter Entfernung sieht man die Lichter ihrer Feuer..."}, {"Tötet für Calor aus Dunkelhain einige Worgs, und zwar 8 Faulreißer der Nachtheuler und 8 besudelte Nachtheuler."}},
[223] = {"Worgs in den Wäldern", {"Hier, bitte schön, <Name>. Bringt Meister Carevin diese Nachricht.","<Schnell zieht er ein Stück verblasstes Pergament hervor und reicht es Euch.>","Noch ein paar mehr wie Ihr, dann sind wir bald mehr Kämpfer als die Nachtwache! Vielleicht können wir dann das Werk vollenden, an dem wir wenigen uns heute noch vergebens abmühen."}, {"Bringt Calors Notiz zu Jonathan Carevin."}},
[224] = {"Verteidigung der Länder des Königs", {"Wir müssen den Loch beschützen, <Name>! Jetzt, wo so viele der Soldaten des Königs tapfer auf weit entfernten Schlachtfeldern kämpfen, sind wir nahe davor, an der Heimatfront überwältigt zu werden. Aus jedem Spalt graben sich Troggs an die Oberfläche! Die Trogg-Plage stellt für Ironforge die größte Gefahr dar. Diese widerwärtigen Mutanten müssen vernichtet werden.","Ihr, <tapferer Abenteurer/tapfere Abenteurerin>, müsst Euch für uns hinauswagen und der Bedrohung durch die Troggs ein Ende bereiten. Erschlagt 10 Stonesplitter-Troggs und 10 Stonesplitter-Späher und erstattet dann wieder Bericht."}, {"Gebirgsjäger Cobbleflint vom südlichen Wachturm möchte, dass Ihr 10 Stonesplitter-Troggs und 10 Stonesplitter-Späher tötet."}},
[225] = {"Das verwitterte Grab", {"Auf dem verwitterten Grabstein steht einfach:","MORGAN LADIMORE","<Als Ihr den Ort weiter untersucht, stellt Ihr fest, dass die Erde über dem Grab kürzlich umgegraben wurde und dass ein guter Teil der Erde entfernt worden zu sein scheint...>"}, {"Sprecht in Dunkelhain mit Sirra Von'Indi."}},
[226] = {"Wölfe auf den Fersen", {"Sven und mir stehen jetzt gefährliche Tage ins Haus, wo doch der Totenbeschwörer im Osten sitzt und all das. Und weil wir hier draußen allein sind, müssen wir auch selbst jagen, wenn wir etwas essen wollen. Es kommt mir so vor, als würden jedes Mal, wenn ich mit etwas Fleisch zum Lager zurückgehe, verhungernde oder tollwütige Terrorwölfe aus dem Wald kommen, die auch etwas abhaben möchten. Ja, eines steht fest, das Leben hier draußen ist gefährlich!","Aber wenn Ihr einige der Wölfe für uns erledigen könntet, dann wäre das Leben für uns etwas leichter. Meist streunen sie nördlich und östlich von hier herum, in der Nähe des Flusses."}, {"Tötet 12 verhungernde Terrorwölfe und 8 tollwütige Terrorwölfe und kehrt dann zu Lars in Svens Lager an der westlichen Grenze des Dämmerwalds zurück."}},
[227] = {"Morgan Ladimore", {"<Er sucht in den Regalen und kommt mit einem ledergebundenen Buch zurück.>","Wenn Ihr mehr wissen wollt, dann fragt Althea. Sie ist diejenige, die in letzter Zeit von dem... Ärger... mit ihm am meisten mitbekommen hat."}, {"Sprecht in Dunkelhain mit Kommandantin Althea Ebonlocke."}},
[228] = {"Mor'Ladim", {"Meine Späher berichten mir, dass Mor'Ladim durch den ganzen Dämmerwald wandert und auf dem Friedhof einen seltsamen, gewundenen Weg einschlägt. Wir haben ihn hinter dem Haus auf dem Hügel begraben, wisst Ihr, wo das ist?","Es gibt nichts, was ich tun könnte, um Euch zu helfen, aber ich wünsche Euch viel Glück."}, {"Tötet Mor'Ladim und bringt seinen Schädel nach Dunkelhain zu Kommandantin Althea Ebonlocke."}},
[229] = {"Die überlebende Tochter", {"Ich hätte Euch dies schon früher sagen sollen...","Wie soll ich sagen...","Morgan dachte zwar, dass seine ganze Familie umgekommen sei, aber seine Tochter Sarah Ladimore lebt noch: Sie ist jetzt eine Behüterin. Die... Umstände... , unter denen ihr Vater zu Tode gekommen ist, haben sie immer sehr aufgewühlt. Vielleicht könntet Ihr zu ihr gehen und ihr mitteilen, was geschehen ist."}, {"Sprecht in Dunkelhain mit Behüterin Sarah Ladimore."}},
[230] = {"Svens Lager", {"Als Ihr durch den Haufen wühlt, findet Ihr ein kleines, schmutzbeflecktes Buch.","Das Buch hat keinen Titel, aber... es enthält Informationen, die Sven bestimmt interessieren."}, {"Bringt das Buch von Svens Hof zu Sven."}},
[231] = {"Die Liebe einer Tochter", {"Hier, nehmt dies und legt es auf sein Grab. Vielleicht... vielleicht weiß er dann irgendwie, dass es mir gut geht und dass keiner von uns ihn für das verantwortlich macht, was geschehen ist.","<Jetzt hat sie Tränen in den Augen, als sie einen Ring von der rechten Hand zieht und ihn Euch übergibt.>","Und... danke, <Name>."}, {"Bringt Sarah Ladimores Ring zu Morgan Ladimores Grab."}},
[232] = {"Kurierdienst für Apothekerin Zinge", {"Die Königliche Apothekenvereinigung benötigt Eure Hilfe. Hört gut zu und wir können beide von meinem Plan profitieren.","Apotheker Keever im nächsten Raum mag... begabt sein..., aber seine Forschungen könnten Früchte tragen, wenn sie von einem gewandten Forscher... oder einer gewandten Forscherin verfolgt würden. Zum Glück bin ich eine solche Forscherin. Bringt diese Bestellung zu Alessandro Luca, dem Eigentümer von Blaumond Dies-und-Das. Er wird Euch ein Testset geben. Wenn Ihr es habt, bringt es zu mir zurück und ich werde Euch Eure Aufgabe im Detail erläutern."}, {"Bringt die Bestellung nach Undercity zu Alessandro Luca."}},
[233] = {"Postbeförderung im Coldridgetal", {"Hm, ich nehme nicht an, dass Ihr mir einen Gefallen tun würdet, <Jungchen/Mädchen>? Heute kam ein Stapel Briefe durch den Pass, aber ich habe nicht die Zeit, sie weiterzuleiten.","Sie sind alle an Talin Keeneye adressiert. Ihr findet ihn ein Stück entlang der Straße im Westen. Er hat sein Lager am gefrorenen See aufgeschlagen.","Was meint Ihr?"}, {"Liefert den Stapel Briefe bei Talin Keeneye ab."}},
[234] = {"Postbeförderung im Coldridgetal", {"Wenn ich mich recht erinnere, hat Grelin sein Lager im Südosten, ein Stück die Straße hinunter. Ich bin sicher, er wartet schon sehnsüchtig auf seine Post."}, {"Liefert den Brief bei Grelin Whitebeard ab."}},
[235] = {"Die Jagd in Ashenvale", {"Achtung, junge Abenteurer! Die Wildnis von Ashenvale erwartet Euch!","Die Horde hat eine starke Präsenz in den Ländern nördlich des Brachlandes aufgebaut. Unsere beiden Außenposten - der Splintertreeposten und der Zoramstrandposten - wollen der Horde zu Ruhm gereichen! Diejenigen, die sich beweisen möchten, sollten dort nach Anleitung suchen. Wichtiger Hinweis: Senani Thunderheart im Splintertreeposten, direkt nördlich des Brachlandes, sucht Abenteurer, die an einer großen Jagd in Ashenvale teilnehmen wollen!"}, {"Sprecht mit Seneni Thunderheart im Splintertreeposten in Ashenvale."}},
[237] = {"Verteidigung der Länder des Königs", {"Gebirgsjäger Cobbleflint hat nur Gutes über Euch zu berichten gehabt, <Volk>. Daher werde ich Euch eine Mission von äußerster Wichtigkeit anvertrauen. Wir müssen den Druck auf die eindringenden Trogg-Streitkräfte aufrechterhalten, bis unsere Brüder, die Zwerge, von der Front der Allianz zurückkehren.","Begebt Euch in die südlichen Hügel und tötet 10 Stonesplitter-Schädelhauer und 10 Stonesplitter-Seher. Eure Angriffe werden uns etwas Zeit verschaffen. Erstattet Bericht, sobald Ihr die Aufgabe erfüllt habt."}, {"Gebirgsjäger Gravelgaw im südlichen Wachturm möchte, dass Ihr 10 Stonesplitter-Schädelhauer und 10 Stonesplitter-Seher tötet und ihm anschließend Bericht erstattet."}},
[238] = {"Kurierdienst für Apothekerin Zinge", {"Das Set ist fertig, <Name>. Die Apothekerin Zinge wird es zweifellos so schnell wie möglich haben wollen. Bringt es ihr ohne Verzögerung und lasst sie wissen, dass ich die Kosten für das Set mit auf die Rechnung der Gesellschaft schreiben werde. Richtet ihr bitte außerdem meine wärmsten Grüße aus.","Also los, steht nicht einfach so rum. So ist nun mal das Leben eines einfachen Kuriers, stimmt's?"}, {"Bringt das Feldprobenset nach Undercity zu Apothekerin Zinge."}},
[239] = {"Hilfe für die Weststromgarnison", {"Die Garnison an unserer westlichen Grenze berichtet von steigenden Aktivitäten der Gnolle und der Diebe. Sie bitten uns um die Entsendung weiterer Soldaten aus Stormwind... aber wir können einfach keine entbehren!","Wenn Ihr helfen könntet - wir könnten die Hilfe wirklich gebrauchen. Geht, sprecht mit Deputy Rainer in der Weststromgarnison und fragt ihn, was getan werden muss.","Die Garnison liegt an der Straße im Westen. Wenn Ihr die Brücke über den kleinen Bach überquert habt, liegt sie auf der rechten Seite."}, {"Begebt Euch zur Weststromgarnison und sprecht mit Deputy Rainer."}},
[240] = {"Rückkehr zu Jitters", {"Und voilà - \"Krebs\"-Küchlein à la Grual!"}, {"Kehrt mit den schwärzlichen Krebsküchlein zu Jitters zurück."}},
[243] = {"Auf ins Feld", {"Ihr werdet Euch in die Tanariswüste begeben und die Gewebebeschaffenheit der Basilisken, Skorpiden und Hyänen testen, die dort heimisch sind. Beschafft Gewebeproben und untersucht sie mit dem Testset; Ihr müsst davon ausgehen, dass eine Reihe der Proben unnütz sind. Ich benötige von jeder Kreaturenart acht akzeptable Proben.","Außerdem verfügen die Goblins von Gadgetzan über eine Kraftquelle, die mit dieser Art von modifiziertem Set verwendet werden muss. Sprecht mit Chefingenieur Bilgewhizzle und erwerbt eine für das Set.","Kehrt erst zu mir zurück, wenn Ihr erfolgreich wart."}, {"Sprecht in Gadgetzan mit Chefingenieur Bilgewhizzle über eine Kraftquelle für das Testset."}},
[244] = {"Eindringende Gnolle", {"Seid gegrüßt, <Klasse>. Meine Aufgabe ist es, auf diesem Stück Straße zu patrouillieren. Obwohl die Straße im Augenblick sicher ist, habe ich nördlich und östlich von hier einige Lager von Gnollen gesehen.","Seenhain muss erfahren, dass die Gnollstreitkräfte sich sammeln! Meldet Euch bei Deputy Feldon in Seenhain und berichtet ihm von den Gnollen. Wenn Ihr dies tut, dann wird Feldon Euch bestimmt den Sold eines Spähers zahlen.","Gewöhnlich inspiziert Feldon das Gebiet südlich der Brücke nach Seenhain."}, {"Erstattet Deputy Feldon Bericht."}},
[245] = {"Gefahr auf acht Beinen", {"Seid gegrüßt, <Klasse>. Vielleicht habt Ihr die Spinnen gesehen, die die Grenze im Westen überrannt haben? Mit den achtbeinigen Monstern werden wir nicht fertig und der Kommandant hat nicht genug Leute, um sie so weit weg von Dunkelhain einsetzen zu können.","Ich bitte Euch wirklich nur sehr ungern darum, aber Ihr seht aus, als könntet Ihr mit ihnen fertig werden. Ich kann Euch keine großen Schätze versprechen, aber ich kann Euch belohnen, wenn Ihr dabei helft, die dreckigen Biester auszurotten.","Ach, ein Ratschlag noch: Weicht ihrem Gift aus, wenn Ihr könnt."}, {"Erledigt 15 Zwerggiftweberspinnen und meldet Euch dann wieder bei Behüter Dodds im Dämmerwald."}},
[246] = {"Einschätzung der Gefahr", {"Wir können keinen der Männer hier entbehren, aber mit diesen Gnollen, von denen Ihr mir berichtet habt, muss etwas passieren.","Späht das südliche Rotkammgebirge nach Gnollen aus. Bedrängt sie, tötet so viele Ihr könnt und erstattet mir dann Bericht über ihre Anzahl. Gebt mir auch eine Einschätzung der Gefahr, die sie darstellen. Möglicherweise findet Ihr Lager dieser Gnolle entlang der südlichen Straße im Rotkammgebirge.","Tut dies für die Armee von Stormwind, <Name>, und Ihr erhaltet eine Belohnung."}, {"Tötet 10 Bastarde der Rotkammgnolle und 6 Wilderer der Rotkammgnolle und meldet Euch anschließend wieder bei Deputy Feldon in Seenhain."}},
[247] = {"Die Jagd ist abgeschlossen", nil, nil},
[248] = {"Weitere Beobachtungen", {"Seit einer Weile schon beobachte ich den Turm von Ilgalar und ich habe herausgefunden, dass Morganth im Konflikt mit den Orcs von Burg Steinwacht liegt. Den Grund für ihren Streit kenne ich nicht, aber vielleicht könnt Ihr mir ja dabei helfen, diesem Rätsel auf die Spur zu kommen.","Nehmt eine weitere Glyphe von Azora und begebt Euch dann auf die Spitze des unversehrten Wachturms neben der Burg Steinwacht.","Findet einen passenden Gegenstand, auf dem Ihr die Glyphe ablegen könnt. Eine Truhe oder ein Krug - irgendein Ort, an dem die Glyphe nicht auffällt - ist für meine Zwecke geeignet."}, {"Steigt in den unversehrten Wachturm neben der Burg Steinwacht hinauf.","Findet einen passenden Behälter für die Glyphe von Azora und legt diese dort ab."}},
[249] = {"Morganth", {"Nach langen Beobachtungen habe ich jetzt Morganths Pläne durchschaut.","Er sucht nach der Sense von Elune... und vielleicht hat er sie bereits gefunden! Wenn dem so ist, dann werden das Rotkammgebirge und das Königreich Stormwind schon bald seine dunkle Macht zu spüren bekommen.","Ich selbst bin leider nicht bereit, mich gegen Morganth zu stellen, doch ich fürchte, er muss genau jetzt aufgehalten werden. Daher müsst Ihr Euch Zutritt zum Turm von Ilgalar verschaffen und Morganth entgegentreten.","Besiegt ihn und bringt mir seinen Schattenanhänger. Wenn Ihr dies vermögt, dann schuldet das Königreich Euch viel."}, {"Besiegt Morganth.","Bringt seinen Schattenanhänger zu Theocritus am Turm von Azora im Wald von Elwynn."}},
[250] = {"Eine dunkle Bedrohung", {"Den Sternen sei Dank, jemand sorgt sich um den Damm. Erst die Zerstörung des Thandol-Übergangs, dann die Plünderung von Dun Modr! Ich hege keinerlei Zweifel, dass der Damm das Ziel des nächsten Anschlags der Dunkeleisenzwerge sein wird. Der größte Teil meiner Sicherheitsleute wurde an die Front der Allianz versetzt, einschließlich meines besten Kontrolleurs.","Und deswegen brauche ich Euch, <Name>. Ich habe einige Pioniere der Dunkeleisenzwerge gesehen, die an der östlichen Rampe des Damms herumlungerten. Untersucht den Bereich und bringt einen Hinweis zurück."}, {"Untersucht den Bereich um die östliche Dammrampe und bringt Chefingenieur Hinderweir einen Hinweis, der Auskunft darüber geben könnte, was die Dunkeleisenzwerge vorhaben."}},
[251] = {"Übersetzung von Calvinius' Notiz", {"Bringt den Zettel, den Ihr mir gabt, zu Sirra Von'Indi. Er ist sehr gelehrt und kennt viele Sprachen und alte Künste - wenn überhaupt jemand diese Nachricht übersetzen kann, dann ist er das."}, {"Bringt den Brief an Ello zu Sirra Von'Indi."}},
[252] = {"Übersetzung für Ello", {"Oh je! Wenn das, was hier steht, wahr ist, dann hat Calvinius etwas... Unsägliches getan.","Bringt die übersetzte Notiz besser schnell zu Ello zurück.","Beeilt Euch, denn ich fürchte, Unheil rollt bereits auf unsere Stadt zu..."}, {"Bringt Lord Ello Ebonlocke den übersetzten Brief."}},
[253] = {"Braut des Einbalsamierers", {"Der Einbalsamierer... diesen Namen kennen wir aus einer Geschichte, die wir unseren Kindern erzählen. Er war ein freundlicher Alchimist, den der Tod seiner Frau Eliza in den Wahnsinn trieb. Er wollte sie wiederbeleben und so wandte er schwarze Magie an und pflanzte sein eigenes Herz in die Brust seiner toten Gattin ein.","Dies stellte Eliza wieder her, doch nun war sie verflucht... ein unstillbarer Hunger auf Menschenfleisch hatte sich ihrer bemächtigt und der Einbalsamierer musste sie in ihrem Grab liegen lassen.","Seine Macht ruht in seinem Herzen. Findet das Grab seiner Frau, holt sein Herz und bringt es mir, solange es noch schlägt. Wenn Ihr dies tut, wird seine Macht gebrochen sein."}, {"Findet Elizas Grab. Beschafft das Herz des Einbalsamierers, das sie in sich trägt, und kehrt zu Ello Ebonlocke zurück."}},
[254] = {"Graben in der Erde", nil, nil},
[255] = {"Söldner", {"Zwei Monate! Zwei Monate, nur um dann aus Ironforge zu hören zu bekommen, dass eine Handvoll Soldaten zu unserer Verteidigung abgestellt wird, und zwei weitere Monate, bis sie ankommen! Schlimm genug, dass unsere Stadt ohne Schutz ist, aber die Ausgrabung und der Damm - der Damm! - sind ebenfalls allen Angriffen schutzlos ausgesetzt.","Ich habe also keine Wahl, scheint mir. Ich brauche gedungene Kämpfer.","Wie steht es mit Euch? Ihr seht mir tüchtig aus, <Klasse>. Eine Gruppe Oger hat im Nordosten des Sees ihr Lager aufgeschlagen. Vielleicht könntet Ihr ein paar weitere Kämpfer rekrutieren und uns dabei helfen, die Bedrohung für unsere Stadt zu beseitigen?"}, {"Magistrat Bluntnose aus Thelsamar hat Euch angeheuert, um 4 Oger der Mo'grosh, 4 Schläger der Mo'grosh und 4 Vollstrecker der Mo'grosh zu töten."}},
[256] = {"GESUCHT: Chok'sul", {"Auf Befehl des Magistrats von Thelsamar:","Der Tod von Chok'sul, dem Oger und mutmasslichen Anführer und Kopf hinter den Angriffen auf die Stadt Thelsamar, den Stonewroughtdamm und die Ausgrabungsstätte, wird unter allen Umständen gewünscht. Es darf zu jedem Mittel gegriffen werden, das notwendig ist.","Eine Barentschädigung kommt zur Auszahlung, wenn der Magistrat einen Beweis für Chok'suls Tod erhält.","Zuletzt wurde Chok'sul am Ogerlager im nordöstlichen Teil von Loch Modan gesehen."}, {"Tötet Chok'sul und bringt Magistrat Bluntnose seinen Kopf."}},
[257] = {"Das Prahlen eines Jägers", {"Ihr glaubt wohl, Ihr könnt Euch mit Daryl dem Kühnen messen, hm? Das will ich auch nicht hoffen! Aber Ihr dürft es natürlich gern versuchen, wenn Ihr denn meint... Hier habt Ihr eine Herausforderung, der Ihr bestimmt nicht gewachsen seid. Also nehmt's nicht zu schwer, wenn Ihr damit nicht fertig werdet.","Ein Schwarm Bussarde nistet hier am Loch Modan. Warum versucht Ihr nicht, einige von denen zu erledigen? Ich sag Euch was. Wenn Ihr meine Herausforderung annehmt und es Euch in 15 Minuten gelingt, dann gebe ich Euch meinen Bogen oder mein Gewehr.","<Er mustert Euch abschätzig.>","Scheint ja nicht, als ob Ihr viel zu verlieren hättet."}, {"Tötet 6 Bergbussarde und kehrt innerhalb von 15 Minuten zu Daryl dem Jüngling in die Weitschreiter-Lodge zurück."}},
[258] = {"Die Herausforderung eines Jägers", {"<Name>ath, stimmt's? Ich seh schon, Ihr platzt fast vor Stolz, dass Ihr den ersten Test bestanden habt. Aber ich hab Euch ja auch schon gesagt, dass das keine große Tat war.","Ihr solltet Euch mal an der Eberjagd versuchen. Glaubt mir, das sind andere Eber als im Coldridgetal, also solltet Ihr Euch vorsehen. Diesmal gebe ich Euch nur 12 Minuten.","Seid nicht zu traurig, wenn Ihr versagt. Ich würde Euch das Hemd geben, das ich am Leibe trage, wenn Ihr es schaffen würdet!","Habe ich Euch schon mal erzählt, wie ich an meine berühmte Narbe gekommen bin? Nein? Also, das war vor zwei Jahren..."}, {"Tötet 5 alte Bergeber und kehrt innerhalb von 12 Minuten zu Daryl dem Jüngling in die Weitschreiter-Lodge zurück."}},
[261] = {"Auf dem Scharlachroten Pfad", {"Ich werde offen mit Euch sein. Wir sind im Krieg mit der Geißel. Sie ist ein Übel, das unser Volk verdirbt und unser Land infiziert. Sie muss aufgehalten werden, bevor sie uns übermannt und unsere Welt mit Schatten überzieht. Wir vom scharlachroten Kreuzzug haben geschworen, die Geißel mit Leib und Seele zu bekämpfen.","Wenn Ihr auf unserer Seite steht, dann nehmt Euren Mut zusammen und beweist Eure Zugehörigkeit - zerstört die untoten Verheerer von Desolace im südlich gelegenen Tal der Knochen.","Tut dies, und der Kreuzzug wird Euch mit offenen Armen aufnehmen."}, {"Vernichtet 30 untote Verheerer und kehrt dann zu Bruder Anton auf die Nijelspitze zurück."}},
[262] = {"Die Schattengestalt", {"Ich muss wissen, wen ich in der Nacht, in der meine Familie ermordet wurde, in den Schatten habe herumschleichen sehen.","Bitte, <Name>, Ihr habt euren Einfallsreichtum bereits bewiesen. Bitte findet diese schattenhafte Gestalt. Ich muss wissen, was sie für Geheimnisse kennt!","Ich habe keine Hinweise, die Ihr nicht bereits hättet, aber hier - nehmt das Buch, das Ihr auf meinem Hof gefunden habt. Vielleicht können einige Stadtbewohner das Buch mit seinem Besitzer in Verbindung bringen."}, {"Nehmt das Buch, das Ihr auf Svens Hof gefunden habt, und zeigt es den Einwohnern von Dunkelhain."}},
[263] = {"Verteidigung der Länder des Königs", {"Die Gebirgsjäger sagen mir, dass Ihr recht tapfer und fähig seid, <Name>. Ein <Volk> wie Ihr wird hier draußen gebraucht. Wir bekommen das Trogg-Problem einfach nicht unter Kontrolle. Die Reserve wurde zur Front gerufen, und wir sind hier auf uns allein gestellt. Aber jetzt seid ja Ihr hier, ein erfahrener <Klasse>, also wollen wir doch mal sehen, was Ihr ausrichten könnt.","Zieht los und tötet 10 Schamanen der Splittersteintroggs und 10 Knochenknacker der Splittersteintroggs. Wir werden sehen, ob Ihr Eurem Ruf gerecht werdet, <Trogg-Töter/Trogg-Töterin>."}, {"Gebirgsjäger Wallbang im südlichen Wachturm möchte, dass Ihr 10 Schamanen der Splittersteintroggs und 10 Knochenknacker der Splittersteintroggs tötet."}},
[264] = {"Bis dass der Tod uns scheidet", {"Dreckiger Abschaum! Lässt mich wegen seinem verdammten Kreuzzug hier sitzen. Nur, weil \"das Licht das Wichtigste ist, was wir gegen die Bedrohung durch die Untoten besitzen\".","Und was ist mit seinen Kindern?! Was ist mit MIR?! Nacht für Nacht hab ich hier gesessen und geduldig auf ihn gewartet ... hab immer den zweiten Platz hinter seiner verfluchten Pflicht eingenommen!","Tja, hier könnt Ihr sehen, was Euch \"Gerechtigkeit\" einbringt, <Name>! Er ist tot, und ich ertrage genau das, was er aufzuhalten versuchte!","Nehmt diesen Plunder und legt ihn auf seine Gruft in dem Grabmal. Ich will nichts mehr damit zu tun haben ... oder mit ihm!"}, {"Legt Clarices Anhänger auf Yurivs Grab in Silberwald."}},
[265] = {"Die Schattensuche geht weiter", {"Hier habt Ihr Euer Buch zurück, <Name>, und viel Glück noch bei Eurer Suche. Vielleicht können Euch ja andere in der Stadt mehr Hinweise geben, wenn Ihr sie fragt.","Ich habe jedenfalls noch einen für Euch - obwohl der Autor dieses Buches in Dunkelhain fremd ist, wurde das Buch selbst hier gekauft."}, {"Überprüft, ob andere Einwohner Hinweise auf die schattenhafte Gestalt haben."}},
[266] = {"Nachforschungen im Gasthaus", {"Hier habt Ihr Euer Buch zurück, und noch viel Glück bei Euren Nachforschungen.","Vielleicht wollt Ihr Eure Suche auf das Gasthaus ausweiten. Wenn die Person, die Ihr sucht, einige Zeit hier in der Stadt verbracht hat, dann hat sie dort vielleicht jemand gesehen."}, {"Sucht im Gasthaus nach Hinweisen auf die schattenhafte Gestalt."}},
[267] = {"Die Bedrohung durch die Troggs", {"<Klasse>, Ihr seid Euch vielleicht nicht unbedingt der Bedrohung durch die Troggs bewusst, die ihren Schatten über das Land der Zwerge wirft. Nachdem die Reserve von Ironforge zur Front der Allianz gerufen wurde, bleibt uns nur ein Bruchteil der Verteidigungskräfte, die nötig wären, um dieses Land zu schützen. Meinem Regiment wurde aufgetragen, über das Tor hier zu wachen, und wir können unseren Posten aufgrund der Gefahr einer Invasion nicht verlassen.","Es muss aber etwas Druck auf diese verdammten Troggs ausgeübt werden, die in den Hügeln lauern. Wenn Ihr Euch dem gewachsen fühlt, startet einen Angriff auf die Troggs. Bringt mir 8 Trogg-Steinzähne als Beweis."}, {"Bringt Captain Rugelfuss im südlichen Wachturm 8 Trogg-Steinzähne."}},
[268] = {"Rückkehr zu Sven", {"Das Buch, das Ihr gefunden habt, war nur zur Hälfte fertig geschrieben. Ich musste es auf Svens Hof zurücklassen, als ich hierher floh...","Hier. Nehmt mein vollständiges Tagebuch und bringt es zu Sven. Es beschreibt wesentlich genauer, was auf seinem Hof passierte.","Obwohl dieses Tagebuch in den letzten Wochen meine einzige Gesellschaft war, gebe ich es gern auf, wenn ich mir dadurch erspare, Sven und seiner Wut zu begegnen."}, {"Kehrt mit Jitters' vollständigem Tagebuch zu Sven zurück."}},
[269] = {"Auf der Suche nach Weisheit", {"Geht nach Stormwind und sprecht dort mit Bischof Farthing in der Kathedrale des Lichts. Gebt ihm Jitters' Tagebuch und sagt ihm, dass Ihr vorhabt, Euch Morbent Fel zu stellen.","Auch wenn Stormwind Dunkelhain aufgegeben hat, ist Farthing doch eine gute Seele und sehr weise. Er könnte Euch bei Eurem Versuch, uns zu retten, behilflich sein.","Eure Mission ist gefahrvoll, aber beeilt Euch dennoch, <Name>. Mit jeder verstreichenden Stunde verfinstern sich die Schatten über dem Dämmerwald mehr und mehr."}, {"Gebt Bischof Farthing in Stormwind Jitters' vollständiges Tagebuch."}},
[270] = {"Die verlorene Flotte", {"Vor vielen Jahren sank die dritte Flotte von Kul'Tiras vor der Küste des Sumpflandes, einem Moorgebiet nördlich des Zwergenkönigreichs von Ironforge. Auf einem der Schiffe befand sich eine Ladung Lichtgeschmiedetes Eisen.","Dieses Metall ist wertvoll für uns, da Gegenstände, die daraus gefertigt werden, mit heiliger Wahrheit zuschlagen. Wenn Ihr Morbent Fel entgegentreten wollt, dann werdet Ihr eine solche Waffe brauchen.","Geht zum Hafen von Menethil und sprecht mit meinem zwergischen Kollegen Glorin Steelbrow. Wenn dieses verlorene Metall überhaupt gefunden werden kann, dann weiß er, wo man danach suchen muss."}, {"Geht zum Hafen von Menethil im Sumpfland und sprecht mit Glorin Steelbrow."}},
[271] = {"Vyrins Rache", {"Ihr seid nicht zufällig daran interessiert, es unserem ach so kühnen Kühnen ein wenig heimzuzahlen? Es schmerzt mich, das zu sagen, aber Daryl hat ein gewisses Talent.","Vor einem Jahr war ein großer Bär das Ziel der Weitschreiter-Lodge, aber keiner der Jäger hatte Glück mit ihm. Also dachte sich Daryl, er könnte sich mal an ihm versuchen. Das einzige, was er nach seinem Versuch vorweisen konnte, war die hässliche Narbe über seinem Auge.","Erlegt den Bären und ich bin mir sicher, dass sogar Daryl mal sprachlos wäre.","Der Bär, Old Sooty, hält sich irgendwo in der Nähe seines Baus in den Hügeln über Thelsamar auf."}, {"Tötet Old Sooty und zeigt Daryl dem Jüngling in der Weitschreiter-Lodge dann Euer Werk."}},
[272] = {"Prüfung des Seelöwen", {"Für die zweite Prüfung benötigt Ihr die zwei Hälften, aus denen der Anhänger des Seelöwen besteht. Die eine Hälfte bezieht ihre Kraft aus der Beweglichkeit des Seelöwen-Aspektes beim Gleiten durchs Wasser; die andere Hälfte bezieht ihre Kraft aus der unglaublichen Ausdauer des Seelöwen-Aspektes. Druiden bedienen sich dieser beiden Eigenschaften gleichermaßen, um den Aspekt ihrer Wassergestalt zu verkörpern.","Sprecht mit den Bewohnern von Moonglade, um herauszufinden, wo sich die Teile befinden könnten, und bringt beide hierher, um sie miteinander zu vereinen. Bringt den Anhänger zu Dendrite Starblaze, wenn Ihr ihn geschaffen habt."}, {"Sucht die Anhängerhälfte der Wasserbeweglichkeit und die Anhängerhälfte der Wasserausdauer. Sprecht mit den Einwohnern von Moonglade, um Hinweise darauf zu erhalten, wo sich diese Gegenstände befinden könnten.","Formt den Anhänger des Seelöwen aus den beiden Anhängerhälften. Ihr müsst in der unmittelbaren Nähe des Schreins von Remulos sein, um dies zu tun.","Bringt den zusammengefügten Anhänger zu Dendrite Starblaze nach Nighthaven in Moonglade."}},
[273] = {"Nachschub für die Ausgrabungsstätte", {"Es ist noch nicht zu spät, Ihr könnt gern losziehen und Huldar suchen. Er kann noch nicht sehr weit gekommen sein, denn so stark sie auch sein mögen, können Miran und Saean dennoch nicht allzu schnell laufen, wenn sie mit diesen Fässern vollgepackt sind.","Sie nehmen die übliche Route zur Ausgrabungsstätte, südwärts aus der Stadt raus und dann an der Krümmung des Lochs entlang.","Ihr solltet keine Probleme haben, sie zu finden, solange sie nicht bereits dort angekommen sind."}, {"Sprecht mit Huldar."}},
[274] = {"Eine dunkle Bedrohung", {"Dies ist kein gewöhnliches Sprengpulver. Seht diese winzigen Silberkristalle. Und der eindeutige Geruch! Es ist sonnenklar, dass es sich hier um Zephyriumpulver handelt. Zephyrium an sich ist harmlos. Aber wenn es nass wird, könnte es ganz Ironforge aus dem Berg sprengen.","Die chemische Reaktion kann entschärft werden, indem man vier Komponenten mischt: Lauerergift, einen zerstoßenen Kristall der Mo'grosh, eine Krokiliskenträne und diese Entschärfungspaste. Jetzt geht und erklärt es Hinderweir, ehe es zu spät ist!"}, {"Ashlan Stonesmirk möchte, dass Ihr zum Chefingenieur Hinderweir zurückkehrt und ihn von der neuen Entdeckung in Kenntnis setzt."}},
[275] = {"Schwärendes Land", {"Ich weiß nicht warum, aber irgendetwas schwärt im Sumpfland. Moorkrabbler haben sich dort ausgebreitet. Sie sind wie kranke, fiebrige Blasen auf dem Land, und um das Fieber zu besiegen, müssen sie erledigt und dem Sumpf wieder zugeführt werden.","Die Moorkrabbler watscheln mitten in den Wasserwegen des Sumpflandes herum."}, {"Tötet 12 Moorkrabbler und kehrt dann zu Rethiel dem Sumpfhüter im Sumpfland zurück."}},
[276] = {"Stapfende Pfoten", {"Die Moosfellgnolle lebten viele Jahre friedlich im Sumpfland, aber in letzter Zeit wächst ihre Anzahl. Ihre Füße trampeln die Büsche und Blumen des Moores platt, und ihre garstigen Äxte schlagen zu viel Holz für ihre Feuer.","Sie befinden sich nicht mehr im Gleichgewicht mit dem Land. Wenn das Sumpfland überleben soll, müssen die Gnolle dezimiert werden.","Tötet 15 Gnolle der Moosfelle und 10 Bastarde der Moosfelle. Jagt sie im Süden, nahe dem Zwergentor von Algaz. Wenn Ihr das getan habt, kehrt zu mir zurück."}, {"Tötet 15 Gnolle der Moosfelle sowie 10 Bastarde der Moosfelle und kehrt dann zu Rethiel dem Sumpfhüter im Sumpfland zurück."}},
[277] = {"Feuer-Tabu", {"Die Moosfelle fällen die Bäume des Sumpflandes und verwenden das Fleisch der Bäume, um sich mit Feuer zu wärmen. Ich werde diesen Schmerz nicht länger ertragen!","Entledigt die Moosfelle ihrer kunstlosen Feuersteine, der Werkzeuge, die sie zum Feuermachen verwenden. Die meisten Moosfelle tragen diese Feuersteine bei sich, ausgenommen die kleineren Gnolle der Moosfelle und Bastarde der Moosfelle."}, {"Bringt Rethiel dem Sumpfhüter 9 kunstlose Feuersteine."}},
[278] = {"Eine dunkle Bedrohung", {"Fangt gleich damit an, die Stoffe zum Entschärfen zusammenzusuchen, <Name>. Lauerergift kann von den im Loch Modan heimischen Spinnen gewonnen werden. Krokiliskentränen findet man ebenfalls hier im Loch. Ein Kristall der Mo'grosh hingegen dürfte sehr schwierig zu bekommen sein. Die Oger im Nordosten bauen sie ab, aber die Kristalle sind ein sehr seltener Rohstoff.","Wir müssen einen Angriff der Dunkeleisenzwerge abwehren können! Kommt zurück, wenn Ihr alle erforderlichen Dinge zusammenhabt, und ich werde die Mixtur vorbereiten."}, {"Chefingenieur Hinderweir möchte, dass Ihr Lauerergift, einen Kristall der Mo'grosh und eine Krokiliskenträne besorgt."}},
[279] = {"Klauen aus der Tiefe", {"Murlocs kriechen aus den tiefen Wassern und bauen ihre Dörfer entlang der Küste. Sie überfallen unsere Fischer und Händler und müssen aufgehalten werden.","Einer dieser Murlocs, Gobbler, streift mit den anderen Murlocs der Blaukiemen umher und plündert die Händler entlang der Straße aus, wobei sie sich danach immer in die Sicherheit der nahegelegenen Hütten zurückziehen. Unsere Händler sind in Gefahr, und wir sind bereit, für ihre Sicherheit zu zahlen.","Gobbler strolcht in der kleinen Bucht nördlich von hier herum. Findet ihn, erschlagt seine Artgenossen und bringt mir seinen Kopf."}, {"Tötet 12 Murlocs der Blaukiemen.","Erschlagt Gobbler und nehmt Euch seinen Kopf.","Bringt Gobblers Kopf zu Karl Boran in den Hafen von Menethil"}},
[280] = {"Eine dunkle Bedrohung", {"Heute habe ich einige Aufrührer der Dunkeleisenzwerge gesehen, die mit einem Fässchen in Richtung Damm geschwommen sind. Es waren keine Gebirgsjäger zur Hand, um sie aufzuhalten! Ich fürchte, sie haben das Zephyrium am Fuße des Dammes deponiert, und es könnte jede Minute hochgehen! Lasst mich diese ganzen Zutaten zur Entschärfungsmixtur zusammenmischen. Na bitte!","Ihr müsst jetzt diese Mixtur mitnehmen und in das Fässchen einrühren, bevor es zu spät ist! Beeilt Euch!"}, {"Chefingenieur Hinderweir will, dass Ihr zum Fuß des Dammes hinunterschwimmt, das Pulverfässchen ausfindig macht und die Entschärfungsmixtur hineinrührt, um eine Explosion zu verhindern."}},
[281] = {"Waren-Wiederbeschaffung", {"Vor nicht allzu langer Zeit wurde ein Handelsschiff, die Blind Princess, von Murlocs überfallen. Obwohl das Schiff unversehrt entkam, wurde ein Großteil seiner Fracht von den Kreaturen über Bord gezogen, darunter eine äußerst wertvolle Statuette von Terenas Menethil. Wir möchten, dass die Statuette geborgen wird.","Durchsucht die Hütten der Murloc an der Küste nach Kisten, Fässern und anderen Spuren von verlorener Fracht. Findet die Menethil-Statuette und bringt sie mir wieder."}, {"Durchsucht die Kisten und Fässer in den Murloclagern an der Küste."}},
[282] = {"Senirs Beobachtungen", {"Ich hatte ursprünglich vor, meinen Lehrling mit meinem Bericht zu meinem Bruder Senir zu schicken, aber ich würde mich viel besser fühlen, wenn er in etwas... verlässlicheren Händen wäre. Natürlich nur, wenn es Euch nichts ausmacht?","Hmm... Ihr müsstet den Tunnel nehmen, um nach Kharanos zu gelangen.","Sprecht mit Gebirgsjäger Thalos, bevor Ihr durch den Tunnel geht. Er ist mittlerweile vollständig von den Troggs überrannt.","Folgt der Straße zurück nach Anvilmar und geht dann weiter nach Osten zum Tunnel. Thalos ist ganz in seiner Nähe stationiert."}, {"Sprecht mit Gebirgsjäger Thalos."}},
[283] = {"Eine dunkle Bedrohung", {"Das Fässchen zischt ein wenig."}, {"Die Entschärfungsmixtur scheint gewirkt zu haben. Kehrt zu Chefingenieur Hinderweir zurück und berichtet ihm die guten Neuigkeiten."}},
[284] = {"Die Suche geht weiter", {"Ihr durchsucht gründlich diese Ansammlung von Murlochütten, findet aber keine Spur von der Menethil-Statuette.","Vielleicht gibt es in einer der beiden nahe gelegenen Hütten im Norden und Nordosten mehr Hinweise."}, {"Sucht nach der Menethil-Statuette."}},
[285] = {"Suche in weiteren Hütten", {"Dieses Grüppchen Murlochütten offenbart auch nach einer gründlichen Überprüfung nichts.","... Aber Ihr seid Euch sicher, dass der nächste Anhaltspunkt, den Ihr findet, nützlich sein wird.","Eine der Hütten nördlich von hier muss die Menethil-Statuette enthalten!"}, {"Sucht weiter in den Hütten der Murlocs nach der Menethil-Statuette."}},
[286] = {"Rückgabe der Statuette", {"Nachdem Ihr das Fass aus dem Dreck gezogen und geöffnet habt, findet Ihr eine in dünnes Stroh eingepackte, silbrige Figur. Sie stellt einen jungen, langhaarigen Paladin mit erhobenem Hammer dar.","Dies kann nur die Menethil-Statuette sein! Ihr müsst sie nun zu Karl Boran in den Hafen von Menethil zurückbringen."}, {"Bringt Karl Boran die Menethil-Statuette."}},
[287] = {"Höhle der Frostmane", {"Ich bin mit meinem Bericht etwas im Rückstand und könnte Eure Hilfe brauchen, <Name>. Ich habe gerade herausgefunden, wo sich die Trolle verschanzt haben, aber ich habe so viele Trolle gesehen, dass ich mich nicht getraut habe, hineinzugehen.","Was ich von Euch möchte, ist Folgendes: Geht runter zur Höhle, schaut Euch ein wenig darin um, tötet ein paar Trolle und kommt dann wieder hierher zurück.","Nehmt die Straße nach Norden, raus aus Kharanos. Sobald Ihr an der Brücke ankommt, folgt dem zugefrorenen Fluss westwärts, bis Ihr den Iceflowsee erreicht. Am linken Ufer werdet Ihr Brewnall vorfinden. Die Höhle liegt südwestlich des Dorfes."}, {"Erforscht die Höhle der Frostmane und tötet 5 Kopfjäger der Frostmane für Senir Whitebeard in Kharanos."}},
[288] = {"Die Dritte Flotte", {"Ich war einst erster Maat auf der HERRIN DER MEERE, einem Linienschiff der dritten Flotte von Kul Tiras, der verlorenen Flotte. Verloren deshalb, weil die Flotte auf ihrer letzten Reise von roten Großdrachen angegriffen wurde. Großdrachen, die von Orcs beherrscht wurden!","Wir hatten keine Chance gegen das Drachenfeuer... und unsere Flotte ging just vor dieser Küste hier brennend unter. Ich bin der einzige Überlebende von diesen armen Schiffen.","... Oh, mir brummt der Schädel! Ich werde einen frischen Drink brauchen, wenn ich mit diesem Kater klarkommen und meine Geschichte fortsetzen soll."}, {"Spendiert dem ersten Maat Fitzsimmons einen Krug Met."}},
[289] = {"Die verfluchte Crew", {"<Gestärkt setzt Fitzsimmons seine Geschichte fort...>","Ich bin der einzige Überlebende der unglückseligen dritten Flotte von Kul Tiras, die vor der Küste im Norden vernichtet wurde. Aber obwohl ich das einzige Mitglied der Flotte bin, das noch atmet, bin ich nicht der einzige, der noch herumläuft! Denn auf einem unserer Schiffe war ein großer und schrecklicher Schatz versteckt, und dessen unheimliche Macht hält die Seelen meiner Brüder in den verrottenden Wracks gefangen.","Wenn Ihr allerdings einige meiner Brüder befreien könnt, könnt Ihr vielleicht auch den Fluch brechen, der auf ihnen lastet."}, {"Vernichtet 13 verfluchte Seeleute, 5 verfluchte Matrosen und den ersten Maat Snellig. Bringt Snelligs Schnupftabakdose zum ersten Maat Fitzsimmons im Hafen von Menethil."}},
[290] = {"Brechen des Fluchs", {"Um den Fluch zu brechen und alle meine Brüder zu befreien, müsst Ihr das... Ding... das auf meinem Schiff verstaut ist, bergen und reinigen! Ich weiß nicht, was es war, aber ich weiß, dass es sicher in der Geldkassette des Schiffs verwahrt wurde, zu dem nur Kapitän Halyndor den Schlüssel hatte.","Mein alter Kapitän erscheint mir immer noch in meinen Träumen. Ich bin mir sicher, dass er auf meinem untergegangenen Schiff, der INTREPID, herumläuft. Besorgt Euch den Schlüssel von ihm und öffnet damit die Geldkassette der INTREPID.","Anschließend, wenn Ihr das verfluchte Ding habt, bringt es zu Glorin Steelbrow, dem Priester, hier in der Stadt."}, {"Besorgt den Geldkassettenschlüssel der INTREPID von Kapitän Halyndor.","Verwendet den Schlüssel, um die Geldkassette der INTREPID zu öffnen."}},
[291] = {"Die Berichte", {"Wunderbar! Überbringt Senator Barin Redstone meinen Bericht. Er ist ein missmutiger Geselle, also stört Euch nicht an seinem wenig sonnigen Gemüt. Er ist in Ironforge, in der Halle, in der König Magni Hof hält.","Ihr kennt den Weg nach Ironforge nicht? Nehmt die Straße aus Kharanos heraus nach Norden, überquert die Brücke und folgt dann der Straße nach Osten den Berghang hinauf. Einige große Banner flankieren diese Straße.","Ach ja, und könntet Ihr es vielleicht vermeiden, zu erwähnen, dass Ihr mir geholfen habt? Ich kann die ja nicht glauben lassen, ich würde hier draußen nicht hart arbeiten, nicht wahr?"}, {"Überbringt Senator Barin Redstone in Ironforge Senirs Bericht."}},
[292] = {"Das Auge von Paleth", {"In der offenen Truhe erblickt Ihr eine geschwärzte Kugel, die in einem gruseligen Licht pulsiert. Dies ist der Schatz, von dem Fitzsimmons gesprochen hat: ein verfluchtes Auge von Paleth.","Bringt das verfluchte Auge von Paleth zum Zwergen-Priester Glorin Steelbrow im Hafen von Menethil. Fragt ihn, wie man den Fluch des Auges brechen kann."}, {"Bringt Glorin Steelbrow das verfluchte Auge von Paleth."}},
[293] = {"Die Reinigung des Auges", {"Ich kann den Fluch nicht von dem Auge von Paleth nehmen. Die Augen wurden von Menschen-Priestern hergestellt, daher werdet Ihr nach Stormwind gehen müssen, um jemanden zu finden, der Euch helfen kann. Sprecht mit Erzbischof Benedictus. Er steht der Kathedrale des Lichts in Stormwind vor. Er kann helfen.","Das hoffe ich jedenfalls, um euretwillen."}, {"Bringt Erzbischof Benedictus das verfluchte Auge von Paleth. Ihr findet Benedictus in der Kathedrale des Lichts, in der Stadt Stormwind."}},
[294] = {"Ormers Rache", {"Die Lage ist ernst, soviel ist sicher. Als wir diese Knochen freigelegt haben, hat das die Raptoren angelockt. Diese dreckigen Bestien haben meine Brüder getötet und mich, Merrin und den armen Ausgrabungsleiter hier festgesetzt.","Helft mit, das Sumpfland von diesen Raptoren zu säubern, <Name>. Scheckige Raptoren und Scheckige Kreischer befinden sich direkt westlich der Klippe hier. Tötet jeweils 10, wenn Ihr könnt.","Das wäre ein guter Anfang für die Rache, die ich mir für sie ausgedacht habe."}, {"Ormer Ironbraid von der Whelgars Ausgrabungsstätte möchte, dass Ihr 10 scheckige Kreischer und 10 scheckige Raptoren tötet."}},
[295] = {"Ormers Rache", {"Es ist an der Zeit, diese abscheulichen Raptoren ihren Blutdurst bereuen zu lassen. Gleich da unten gibt es Dutzende von scheckigen Sensenklauen- und Scharfzahnraptoren. Lasst diese scheußlichen Kreaturen bezahlen, indem Ihr 10 von jeder Sorte erschlagt."}, {"Ormer Ironbraid möchte, dass Ihr 10 scheckige Sensenklauen- und 10 scheckige Scharfzahnraptoren tötet und dann zu ihm zu Whelgars Ausgrabungsstätte zurückkommt."}},
[296] = {"Ormers Rache", {"Während Ihr da unten wart, ist mir aufgefallen, dass eines der Biester aus der Menge herausstach. Es war größer und bedrohlicher. Ich könnte wetten, das ist derjenige, der die anderen hierher geführt hat und für die Störung der Ausgrabungsstätte verantwortlich ist.","Ich erbitte eine letzte Aufgabe von Euch, <Name>. Sorgt dafür, dass Sarlatan seine gerechte Strafe erhält. Und wenn man die Schwere seiner Verbrechen bedenkt, bedeutet Gerechtigkeit in diesem Falle den Tod! Bringt mir eine seiner Krallen als Beweis, dass er tot ist."}, {"Ormer Ironbraid von der Whelgars Ausgrabungsstätte möchte, dass Ihr Sarlatan tötet und mit einer seiner Krallen zu ihm zurückkehrt, sobald Ihr die Aufgabe erledigt habt."}},
[297] = {"Götzen sammeln", {"Vor einer Weile, kurz bevor die Troggs in der Stätte auftauchten, hatten wir eine große Anzahl seltsame, verzierte Götzen gefunden. Leider hatten wir keine Gelegenheit, sie zu untersuchen, denn kurz nach ihrer Entdeckung haben uns die Troggs aus den Ruinen verjagt. Und diese Götzen haben eine seltsame Wirkung auf die Troggs. Sie verfallen dadurch in Raserei!","Bringt mir 8 Götzen - ich möchte sie studieren, und ich will sie nicht mehr in den Händen der Troggs sehen. Ihr könnt die Götzen bei den Troggs finden, die die Stätte heimsuchen."}, {"Bringt Magmar Fellhew 8 verzierte Steingötzen."}},
[298] = {"Bericht über den Fortschritt der Ausgrabungen", {"Ich muss einen Fortschrittsbericht nach Thelsamar schicken - einen Fortschrittsbericht und eine Neufassung meiner Anfrage nach mehr Sprengpulver! Hier ist der Bericht. Bringt ihn Jern Hornhelm. Er ist unser örtlicher Kontaktmann in Thelsamar.","Keine schwierige Aufgabe, aber sie muss erledigt werden."}, {"Bringt Ironbands Fortschrittsbericht zu Jern Hornhelm in Thelsamar."}},
[299] = {"Enthüllung der Vergangenheit", {"Kurz vor der Invasion habe ich eine große Schrifttafel gefunden, die man den Goazstein nennt. Der übersetzte Text reißt an 4 Stellen ab. Der Text spricht von einem \"göttlichen Plan\" und einer \"schicksalhaften Prophezeiung\".","Ich fürchte, uns läuft die Zeit davon. Ich bin zu alt, um hinunterzugehen und diesen Bestien zu trotzen. Aber Ihr seid stark.","Durchkämmt die Ausgrabungsstätte und sucht die 4 fehlenden Tafelfragmente: Ados, Modr, Golm und Neru. Sucht sie in alten Artefakten oder dort, wo das Erdreich locker ist. Bringt sie mir, auf dass ich damit beginnen kann, ihre Geheimnisse zu enträtseln!"}, {"Ausgrabungsleiter Whelgar wünscht, dass Ihr die Ausgrabungsstätte nach den 4 fehlenden Tafel-Fragmenten durchkämmt, Ados, Modr, Golm und Neru."}},
[301] = {"Meldung in Ironforge", {"Wir werden eine Bewilligung brauchen, um Ironband mehr Sprengpulver zu beschaffen. Ihr müsst seinen Bericht zum Hauptquartier der Forscherliga in Ironforge bringen.","Hier ist der Bericht. Gebt ihn Ausgrabungsleiter Stormpike und vergesst nicht, Euch zu ducken, nachdem Ihr ihn abgeliefert habt. Stormpike ist ein Hitzkopf, und es wird ihn nicht erfreuen, wenn er von Ironbands langsamem Vorankommen an der Ausgrabungsstätte hört.","Ihr findet Stormpike in der Versammlungshalle der Forscher."}, {"Bringt Ironbands Fortschrittsbericht zu Ausgrabungsleiter Stormpike."}},
[302] = {"Pulver für Ironband", {"Wenn Ironband immer noch Sprengpulver braucht, dann, bei Magni, soll er es haben! Ich habe die Bewilligung bereits mit einem Boten nach Thelsamar zu Jern Hornhelm vorausgeschickt. Er wird das Pulver und andere Vorräte zum Transport zu Ironband bereitstellen.","Ach, und noch was: Ich will, dass Ihr persönlich dafür Sorge tragt, dass diese Pulverlieferung ihren Bestimmungsort erreicht. Die letzte kam nicht an, also haltet Euren Verstand wach und Eure Augen offen."}, {"Sprecht in Thelsamar mit Jern Hornhelm."}},
[303] = {"Der Dunkeleisenkrieg", {"Der Krieg tobt daheim ebenso wie in der Fremde. Während mächtige Zwergenkrieger im Namen der Allianz in weit abgelegenen Gegenden ihr Leben lassen, fallen unsere eigenen Gebiete den bösen Taten der Dunkeleisenzwerge zum Opfer. Sie haben den Thandol-Übergang verwüstet. Dun Modr fiel kurz darauf.","Ihr müsst Kräfte sammeln und die Front zurückdrängen. Wenn Ihr 15 Dunkeleisenzwerge, 5 Tunnelgräber der Dunkeleisenzwerge, 5 Saboteure der Dunkeleisenzwerge und 5 Demolierer der Dunkeleisenzwerge töten könnt, dann kommen die Reserven vielleicht rechtzeitig, um den Vormarsch zu beenden.","Und jetzt beeilt Euch!"}, {"Motley Garmason in Dun Modr möchte, dass Ihr 15 Dunkeleisenzwerge, 5 Tunnelgräber der Dunkeleisenzwerge, 5 Saboteure der Dunkeleisenzwerge und 5 Demolierer der Dunkeleisenzwerge tötet."}},
[304] = {"Eine grausige Aufgabe", {"Viele meiner Soldaten fielen in der Schlacht von Dun Modr. Mein eigener Bruder kam um, als die Westbrücke über dem Thandol-Übergang zerstört wurde. Jawohl, die Dunkeleisenzwerge sind eine teuflische Brut. Soweit Roggo das in Erfahrung bringen konnte, ist der Anführer dieses Verbrecherpacks im Sumpfland ein Hexenmeister namens Balgaras der Niederträchtige. Er ist allerdings ein ziemlicher Feigling. Er kommandiert seine Soldaten herum, bleibt aber selbst im Hintergrund, jegliche Gefahr meidend.","Wenn Ihr diesen rückgratlosen Abschaum finden könnt, erschlagt ihn und bringt mir sein Ohr. Ich werde Euch dafür entlohnen, <Klasse>."}, {"Tötet Balgaras den Niederträchtigen und bringt Longbraid dem Grimmigen vor Dun Modr sein Ohr."}},
[305] = {"Auf der Suche nach dem Ausgrabungstrupp", {"Wie schön, Ihr müsst der Späher sein, den Longbraid zu schicken versprach. Ihr könnt Euch nicht vorstellen, wie besorgt ich war.","Meine Frau, Merrin, hat mir im letzten Monat jede Woche Briefe geschickt. Sie gehört zum Whelgar-Ausgrabungstrupp. Aber es sind fast zwei Wochen seit unserem letzten Briefwechsel vergangen. Irgendetwas ist bestimmt schief gelaufen!","Longbraid versprach, dass er einen Späher schicken würde, der sich dort mal umsieht, wenn wir nicht bald etwas vom Trupp hören. Ich nehme mal an, dieser Späher seid Ihr, <Name>. Findet Merrin in der Ausgrabungsstätte im Osten!"}, {"Tarrel Rockweaver möchte, dass Ihr zur Ausgrabungsstätte reist und dort Kontakt mit Merrin Rockweaver aufnehmt."}},
[306] = {"Auf der Suche nach dem Ausgrabungstrupp", {"Es war grauenhaft! Wir hatten einige seltene Artefakte enthüllt und fingen an, welche von diesen großen Knochen zu finden.","Bevor wir wussten, wie uns geschah, überrannten diese Scheckigen Raptoren die Ausgrabungsstätte. Viele ließen ihr Leben.","Ormer hier rettete - ganz tapfer - den Ausgrabungsleiter und mich. Er verteidigte uns, während wir Schutz suchten. Jetzt sind wir gefangen. Und unsere harte Arbeit liegt in Trümmern.","Hier, nehmt diese Nachricht mit zurück zu meinem süßen Tarrel. Sie wird ihm die Situation klarmachen."}, {"Kehrt mit Merrins Nachricht zu Tarrel Rockweaver zurück."}},
[307] = {"Schmutzige Pfoten", {"Die Silberbachmine im Osten hat sich schon vor langer Zeit erschöpft. Sie wurde vom Bund der Bergarbeiter in ein Lagerhaus umgewandelt, aber jetzt sind dort Kobolde eingezogen und befingern mit ihren schmutzigen Pfoten gute zwergische Werkzeuge!","Wir werden dieses Ungeziefer noch früh genug ausmerzen, aber der Bund wünscht, dass jemand ihre Gerätschaften aus der Mine schafft, bevor wir Krieger da reinstürmen und was kaputtmachen. Das wird ein harter Abstieg – Ihr solltet vielleicht Unterstützung mitnehmen.","Die Ausstattung wird in Kisten des Bergarbeiterbunds über die Mine verteilt aufbewahrt. Viel Glück."}, {"Geht zur Silberbachmine und sucht 4 Sätze Bergarbeiter-Ausstattung zusammen.","Kehrt dann zu Gebirgsjäger Stormpike zurück."}},
[308] = {"Jarvens Ablenkung", nil, nil},
[309] = {"Beschützen der Fracht", {"Bereit zum Aufbruch, <Name>?","Als Erstes müssen wir dieses Pulver zu Ironband bringen. Für mich allein wäre es zu viel zu tragen und diese Gegend kann gefährlich werden - und wer weiß, was diese Dunkeleisenzwerge noch für mich bereithalten?","Ich werde mich viel besser fühlen, wenn Ihr mitkommt."}, {"Stellt sicher, dass sowohl Miran als auch seine Warenlieferung sicher an der Ausgrabungsstätte ankommen und setzt dann Ausgrabungsleiter Ironband davon in Kenntnis."}},
[310] = {"Bittere Rivalen", {"Ich muss zugeben, diese Donnerbräus stellen ein paar gute Getränke her. Aber sie müssen noch lernen, dass ihres nicht das einzige ordentliche Gebräu ist! Vielleicht könnt Ihr mir helfen, ihnen diese Lehre zu erteilen...","Hier, nehmt dieses Fass mit Gerstenbräu. Schleicht Euch in den Keller der Brauerei Donnerbräu in Kharanos und vertauscht es mit einem ihrer Fässer Donnerbier. Dann werden wir ja sehen, ob ihre Kundschaft mein Gebräu lieber mag als ihres!","Ach ja, und falls die Fässer bewacht sein sollten, müsstet Ihr eventuell den Wächter irgendwie ablenken..."}, {"Vertauscht im Keller der Brauerei Donnerbräu in Kharanos ein Fass Donnerbier mit einem Fass Gerstenbräu."}},
[311] = {"Rückkehr zu Marleth", {"Nachdem Ihr ein Fass von Marleths Gerstenbräu im Keller der Brauerei Donnerbräu abgestellt habt, schnappt Ihr Euch zum Beweis ein Fass Donnerbier..."}, {"Bringt Marleth Gerstenbräu in Brewnall das Fass mit Donnerbier."}},
[312] = {"Tundra MacGranns gestohlene Vorräte", {"Ich hatte einen Monatsvorrat Trockenfleisch gesalzen und für die kalte Jahreszeit weggeschlossen. Weggeschlossen deshalb, damit die Bären nicht rankommen! Aber während ich unterwegs auf der Jagd war, hat sich diese Bestie, Eisbart der Alte, mit meinem Fleisch-Spind davongemacht. Er wird allerdings nicht durch das Thorium kommen. Ich wiederum werde verhungern, wenn ich mein Fleisch nicht wiederbekomme.","Ihr seht verstohlener aus als ich. Vielleicht könnt Ihr meinen Vorrat wiederbeschaffen? Ich würde es mit meinem steifen Bein niemals schnell genug in die Höhle hinein und wieder heraus schaffen."}, {"Holt Tundra MacGranns Trockenfleisch aus dem gestohlenen Fleisch-Spind in der Höhle von Eisbart dem Alten."}},
[313] = {"Der altgediente Bau", {"Nicht jeder ist dazu geschaffen, eine Belagerungsmaschine zu fahren. Dazu braucht man eine eiserne Hand und Nerven aus Stahl... zum Glück besitze ich beides! Wie steht's mit Euch? Wie groß ist euer Talent? Wollt Ihr es mir nicht zeigen?","Wie auch immer... ich versuche gerade, meine Maschine, Trollplow, etwas gemütlicher zu machen. Wie wäre es, wenn Ihr mir einen Haufen Wendigomähnen aus dem altgedienten Bau westlich von Kharanos besorgt? Diese Mähnen könnten einen tollen Teppich für das Innendeck abgeben!"}, {"Sammelt 8 Wendigomähnen und bringt sie Pilot Stonegear."}},
[314] = {"Die Herde beschützen", {"Wir haben mitten in der Nacht die Schreie gehört. Heute morgen fehlten der Herde dann tatsächlich zwei Widder. Diese heimtückische Bestie, Vagash genannt, hat sich über unsere Existenzgrundlage hergemacht. Da der Großteil von König Magnis Armee irgendwo in weit entfernten Landen an der Seite der Allianz kämpft, ist niemand mehr da, der Vagash in Schach halten könnte.","Vielleicht seid Ihr tapfer genug, die Bestie aufzuspüren und zu erschlagen. Bringt mir einen Fangzahn der Bestie, und ich werde Euch belohnen. Vagash lauert direkt oberhalb der Ranch hier, aber seid gewarnt, die Bestie ist tödlich."}, {"Rudra Amberstill möchte, dass Ihr Vagash erschlagt und einen seiner Fangzähne zu ihr auf die Widder-Ranch bringt."}},
[315] = {"Das Perfekte Starkbier", {"Ich bin von einem Vorhaben besessen, dem Vorhaben, das perfekte Starkbier herzustellen. Ich weiß, dass ich es schaffen kann - das Brauen liegt mir im Blut. Ich muss nur das richtige Rezept finden...","Weit oben in den Bergen im Osten bauen die Trolle der Frostmane eine Pflanze an, Schimmerkraut genannt. Sie verwenden sie in ihren seltsamen Stammesritualen. Wir Zwerge haben bisher keine wirkliche Verwendung dafür gefunden, aber sie hat einen einzigartigen Geschmack... und ich möchte damit in meinen Gebräuen herumexperimentieren.","Besorgt mir etwas Schimmerkraut von den Sehern der Frostmane oder klaut es aus den Schimmerkrautkörben der Trolle."}, {"Bringt Rejold Gerstenbräu in Brewnall 6 Schimmerkräuter."}},
[317] = {"Vorräte für Jetsteam", {"Ich bereite mich gerade darauf vor, eine Mission für die Belagerungsbrigade in Angriff zu nehmen. Sie wird ziemlich lange dauern, und ich muss Jetsteam mit Vorräten für einen ganzen Monat beladen. Könntet Ihr also nicht, während Steelgrill an meinem Panzer arbeitet, ein wenig für mich auf die Jagd gehen?","Ich werde ein paar Pelze als Bettzeug und etwas Eberfleisch zum Essen brauchen. Ihr könnt das Fleisch von Ebern und die Pelze von Bären erbeuten... Ihr findet beide in den Schneefeldern südlich des altgedienten Baus."}, {"Sammelt 4 Brocken Eberfleisch und 2 dicke Bärenfelle und liefert sie bei Pilot Bellowfiz in Steelgrills Depot ab."}},
[318] = {"Immerschimmer", {"Rejold Gerstenbräu experimentiert mit seinen Gebräuen. Ein paar davon schmecken gut, einige schlecht, und einige... nun, einige hauen selbst den stärksten Zwerg um.","Er lebt in Brewnall, westlich von Kharanos, und ich will Immerschimmer, eines seiner Getränke. Es wird mich warmhalten, während ich in der Kälte unterwegs bin.","Außerdem brennt das gesegnete Zeug so heiß, dass ich es in Jetsteams Brennofen kippen kann, um einen Energieschub zu erzeugen! Diese nette Taktik hat mich schon aus mehr als einer haarigen Situation gerettet."}, {"Besorgt von Rejold Gerstenbräu in Brewnall ein Fässlein Immerschimmer."}},
[319] = {"Ein Gefallen für etwas Immerschimmer", {"Ich habe ein Fässlein mit Immerschimmer zur Hand, aber... könnt Ihr mir einen Gefallen tun? Brewnall könnte ein wenig Hilfe gebrauchen gegen die wilden Tiere, die die nahegelegenen Schneefelder durchstreifen. Manchmal kommen sie sehr nah.","Wir haben keine Angst vor diesen Bären und Katzen und Ebern - kein Zwerg, der etwas auf sich hält, hätte das - aber wenn Ihr Euch für uns um diese Tiere kümmern könntet, hätten wir mehr Zeit für unser Handwerk und unsere Braukunst."}, {"Tötet 6 Eisklauenbären, 8 alte Klippeneber sowie 8 Schneeleoparden und kehrt dann zu Rejold Gerstenbräu nach Brewnall zurück."}},
[320] = {"Rückkehr zu Bellowfiz", {"Hier habt Ihr Euer Fässlein mit Immerschimmer, <Name>. Und wie gesagt, vielen Dank für die Jagd kürzlich."}, {"Gebt Pilot Bellowfiz in Steelgrills Depot das Fässlein mit Immerschimmer."}},
[321] = {"Lichtgeschmiedetes Eisen", {"Diese Schiffswracks hier vor der Küste sind die verlorene dritte Flotte von Kul'Tiras, die während des letzten großen Krieges von roten Drachen verbrannt und versenkt wurde.","Die Flotte stammte aus Lordaeron, bis auf ein Schiff, einen elfischen Zerstörer: die Flying Osprey. Es transportierte eine Ladung Lichtgeschmiedetes Eisen und schloss sich der dritten Flotte aus Sicherheitsgründen an. Welche Ironie, dass es von denselben Drachen versenkt wurde...","Genug Geschichte. Die Flying Osprey liegt südlich vom Hafen von Menethil auf Grund. Wenn Ihr das Lichtgeschmiedetes Eisen darin finden könnt, bringt es zu mir."}, {"Durchsucht das Wrack der FLYING OSPREY im Sumpfland."}},
[322] = {"Gesegnete Waffe", {"Im Zwergendistrikt von Stormwind gibt es einen zwergischen Waffenschmied, der Lichtgeschmiedetes Eisen verarbeiten kann. Er heißt Grimand Elmore.","Ich habe das von Euch gefundene Eisen in eine Kiste gepackt. Bringt sie zu Grimand und erzählt ihm Eure Geschichte. Ich bin mir sicher, er wird jemandem, der auf einem Kreuzzug wie dem Euren ist, gefällig sein und Euch eine Waffe schmieden - eine Waffe, vor der sich sogar Morbent Fel fürchten wird."}, {"Bringt Grimand Elmore in Stormwind die Kiste mit Lichtschmiedeblöcken."}},
[323] = {"Bewährungsprobe", {"Morbent Fel ist ein Totenbeschwörer und ein Verbündeter der dunklen Reiter. Ich würde Euch ja erlauben, Euch der Macht von Morbent Fel entgegenzustellen, aber ich werde Euch erst gegen diese widerwärtige Satanskreatur kämpfen lassen, wenn ich Eure wahre Stärke kenne.","Ihr habt mir Eure Tapferkeit bereits bewiesen, aber wenn Ihr dem Totenbeschwörer wirklich trotzen wollt, dann beweist Euer Geschick zuerst an seinen Dienern."}, {"Vernichtet 15 Skeletträuber, 3 Skelettheiler sowie 3 Skelettwärter und kehrt danach zu Sven zurück."}},
[324] = {"Die verlorenen Blöcke", {"Nachdem Ihr den einzelnen Lichtschmiedeblock aus der voll gelaufenen Kiste genommen habt, ist Euch klar, dass die Murlocs wohl den Rest gestohlen haben. Jagt die Murlocräuber in der Nähe des Wracks der Flying Osprey, um an das verloren gegangene Lichtgeschmiedetes Eisen zu kommen."}, {"Sammelt 5 Lichtschmiedeblöcke und kehrt dann zu Glorin Steelbrow im Sumpfland zurück."}},
[325] = {"Bewaffnet und bereit", {"Hier ist die Waffe gegen die Satanskreatur, von der Ihr spracht. Ihr seid bestens vorbereitet, <Name>. Viel Glück, und ich freue mich schon darauf, von Eurer Ruhmestat zu hören!"}, {"Bringt Morbents Bann zu Sven im Dämmerwald."}},
[328] = {"Der versteckte Schlüssel", {"In den Berichten wird kein Wort über den Gefreiten Thorsen verloren, jedoch im Einband eines von Herods Geschäftsbüchern findet sich ein kleiner Messingschlüssel.","Und nach genauerem Studium seiner Notizen finden sich in den verschiedenen Dokumenten einige Verweise auf eine \"sichere Geldkassette\". Die Geldkassette befindet sich irgendwo innerhalb des Vorratslagers - des Höhlenkomplexes weiter hinten in Kurzens Truppenlager.","Wenn diese sichere Geldkassette gefunden wird und der kleine Messingschlüssel ihn öffnen kann, dann kann das Geheimnis um Thorsens Entdeckung vielleicht offenbart werden."}, {"Verwendet den kleinen Messingschlüssel, um Buchhalter Herods Geldkassette zu öffnen."}},
[329] = {"Der entlarvte Spion", {"Unter den Wertgegenständen in der Geldkassette befindet sich eine schlammverschmierte, gekritzelte Notiz. Wenn sie auch kaum lesbar ist, so scheint es, als wäre es eine Abhandlung über den Gefreiten Thorsen. Sie beschreibt ausführlich seine Patrouillenrouten, mit wem er so spricht und wie er seine Zeit bei den Rebellen verbringt. Sie ist nur mit einem einzelnen Buchstaben unterschrieben: -M.","Dennoch muss, wer auch immer diese Notiz verfasst hat, stets in Thorsens Nähe gewesen sein. Wer auch immer diese Notiz verfasst hat, ist der Verräter im Rebellen-Lager."}, {"Bringt die schlammverschmierte Notiz zu Lieutenant Doren im Rebellen-Lager."}},
[330] = {"Patrouillen-Pläne", {"Die Notiz, die Ihr mir gegeben habt, ist mit einem \"-M\" unterzeichnet... Ich weiß nicht, was das heißen könnte.","Aber... die Notiz sagt auch aus, dass diese Person letzte Woche die gleichen Patrouillenrouten wie Thorsen hatte. Also muss, wer auch immer mit Thorsen letzte Woche auf Patrouille war, der Verräter sein!","Geht zu Corporal Sethman. Er kümmert sich um die Patrouillenpläne und wird Euch sagen können, wer mit Thorsen unterwegs war."}, {"Sprecht mit Corporal Sethman."}},
[331] = {"Meldung bei Doren", {"Wollte Lieutenant Doren, dass Ihr Euch mit diesen Patrouillen-Informationen wieder bei ihm meldet?"}, {"Sprecht mit Lieutenant Doren."}},
[332] = {"Weinladen-Werbung", {"Wenn Ihr Euch mit irgendwem über Weine unterhaltet, werdet Ihr erfahren, dass wir Gallinas die besten Weine in ganz Stormwind verkaufen. Wir sind auch nicht weit weg, immer noch innerhalb des Handelsdistrikts entlang der herrlichen Kanäle der Stadt.","Hier, nehmt dieses Flugblatt. Bringt es meiner Schwester Suzetta in unseren Laden, und Ihr erhaltet eine Gratis-Flasche unseres berühmten Pinot Noir. Ihr werdet es nicht bereuen!"}, {"Geht zum Weingut Gallina und gebt Suzetta Gallina den Wein-Gutschein, um eine kostenlose Flasche Wein zu erhalten."}},
[333] = {"Harlan braucht Nachschub", {"Wir hatten hier in der letzten Zeit viel Betrieb. Es scheint so, als wollte wirklich jeder Rüstungen und robuste Kleidung kaufen. Als würden alle eine kalte, harte Jahreszeit erwarten...","Aber das sind alles Zukunftssorgen. Meine heutige Sorge ist eher, dass mir die gestrickten Kleidungsstücke ausgehen. Ich brauche eine weitere Fuhre von unserem Lieferanten.","Wenn Ihr diesen Auftrag zu Rema Schneider in die Schneiderei und Anpasserei am Kanal bringen könntet, währe ich Euch sehr dankbar."}, {"Geht zur Schneiderei und Anpasserei am Kanal und bringt Rema Schneider Harlan Bagleys Kleidungs-Auftrag."}},
[334] = {"Eine Lieferung für Thurman", {"Mein Sohn Thurman ist Lehrling beim Konfektionsgeschäft Larson im Magierviertel. Er hatte es heute etwas eilig und vergaß seine Schere und Nadeln. Ich weiß, ein großer <Klasse> wie Ihr hat bestimmt wichtige Aufgaben zu erledigen, aber ohne sein Handwerkszeug kann er seine Lehrarbeiten nicht erledigen!","Bitte, <Name>. Könnt Ihr meinem Sohn sein Nähkästchen vorbeibringen? Das Konfektionsgeschäft Larson ist einer von zwei Kleiderläden im Magierviertel - es ist das, das etwas weiter drin liegt, ganz in der Nähe des Magierturms."}, {"Geht zum Konfektionsgeschäft Larson im Magierviertel von Stormwind und gebt Thurman Schneider sein Nähkästchen."}},
[335] = {"Ein nobles Bräu", {"Lord Wishock sorgt im Oberhaus ganz schön für Unruhe. Er wurde auf unseren kleinen \"Betrieb\" hier unten aufmerksam gemacht und propagiert jetzt eine genaue Untersuchung. Ich habe einen Plan, um Wishock... zu beschäftigen... aber ich benötige Eure Hilfe.","Es gibt eine Blume, die in Stalvans Garten in den Außenbezirken von Dunkelhain wächst. Diese kleine weiße Blume ist als Tilloaträne bekannt. Ich werde außerdem eine Bisamwurzel brauchen, die man nur am Fuße des Wasserfalls tief unter dem Stonewroughtdamm im Sumpfland findet."}, {"Zardeth Blackclaw möchte, dass Ihr ihm eine Tilloaträne aus Dunkelhain und eine Bisamwurzel aus dem Sumpfland bringt."}},
[336] = {"Ein nobles Bräu", {"Lord Wishock hat, wie alle Menschen, seine Schwächen. Bei seiner handelt es sich um eine Vorliebe für alkoholische Getränke. Seine Scheinheiligkeit ist verblüffend. Dieser spießige Trottel verbringt seine Tage damit zu versuchen, unsere kleine \"Taverne\" schließen zu lassen, während er seine Nächte im Obergeschoss zubringt und dabei Rechnungen anschreiben lässt, die er niemals bezahlt. Na, mal sehen, wie Wishock dieses besondere Gebräu schmeckt, das ich für ihn vorbereitet habe: Blackclaw-Starkbier. Es haut ziemlich rein, <Name>.","Bringt es ihm und seht, wie es ihm schmeckt. Höchstwahrscheinlich treibt sich der alte Trottel in der Halle der Bittsteller herum."}, {"Bringt Lord Wishock in der Halle der Bittsteller das Blackclaw-Starkbier."}},
[337] = {"Ein altes Geschichtsbuch", {"Obwohl der Einband dieses Buches von Kratzern überzogen und von Schmutz verkrustet ist, kann man auf dem Buchrücken noch immer die Worte \"Die Geschichte von Stormwind\" lesen. Wenn man das Buch aufmacht, kann man das Siegel der Bibliothek von Stormwind erkennen, das deutlich auf der ersten Seite prangt.","Es sieht so aus, als hätte jemand dieses Buch ausgeliehen, aber nie zurückgegeben."}, {"Bringt das Buch: \"Die Geschichte von Stormwind\" in die Bibliothek von Stormwind zurück."}},
[338] = {"Die Grünen Hügel des Schlingendorntals", {"Meister Nesingwary wird furchtbar wütend sein, wenn er das herausfindet!","Er vertraute mir das Manuskript für seinen Roman, \"Die Grünen Hügel des Schlingendorntals\", zum Korrekturlesen an. Es lief auch alles glatt, aber dann wehte ein großer Windstoß durch das Lager und hat die Seiten überall verteilt.","Ich konnte die meisten Seiten wieder zusammensuchen, aber viele sind noch über den halben Dschungel verstreut. Sammelt die Seiten, mit denen man die vier Kapitel vervollständigen kann.","Ich werde Euch helfen, sie zu ordnen. Bringt mir die Kapitel zurück, wenn Ihr alle vier beisammen habt."}, {"Sammelt die fehlenden Seiten des Manuskripts von \"Die Grünen Hügel des Schlingendorntals”. Wenn alle vier Kapitel vollständig sind, bringt sie zu Barnil zurück."}},
[339] = {"Kapitel I", nil, nil},
[340] = {"Kapitel II", {"Kapitel II war voll gestopft mit aufregenden Geschichten von der Panther- und Tigerjagd. Ich könnte mir dafür in den Hintern treten, dass ich die Seiten habe davonfliegen lassen.","Es sieht aus, als hätte ich alle Seiten wiedergefunden, bis auf die Seiten 10, 11, 14 und 16."}, {"Bringt Barnil Stonepot die Seiten 10, 11, 14 und 16 von Nesingwarys \"Die Grünen Hügel des Schlingendorntals\", um Kapitel II zu vervollständigen."}},
[341] = {"Kapitel III", {"Kapitel III gefiel mir am besten. Es erzählt von Sire Erlgadins und Lady Rouacks erstem Blattschuss mit einem Zwergen-Gewehr. Und am Ende von Kapitel III, Mann, solche eine Spannung!","Wenn ich das richtig sehe, brauchen wir noch die Seiten 18, 20, 21 und 24, um das Kapitel fertig zu stellen. Diese Seiten sind irgendwo da draußen..."}, {"Bringt Barnil Stonepot die Seiten 18, 20, 21 und 24 von Nesingwarys \"Die Grünen Hügel des Schlingendorntals”, um Kapitel III zu vervollständigen."}},
[342] = {"Kapitel IV", {"Ah, Kapitel IV; Das große Finale! Und was für ein Ende es war!","Uns fehlen nur drei Seiten aus diesem Kapitel. Wir brauchen die Seiten 25, 26 und 27, um Kapitel IV wieder zusammenzusetzen."}, {"Bringt Barnil Stonepot die Seiten 25, 26 und 27 von Nesingwarys \"Die Grünen Hügel des Schlingendorntals\", um Kapitel IV zu vervollständigen."}},
[343] = {"Reden über Kraft", {"Ich werde eine Rede über die Kraft des Geistes halten, und ich würde dazu gern einige Auszüge aus einem bestimmten Buch über Metallurgie zitieren - \"Belastbarkeit von Eisen\". Eine Abschrift dieses Buches dürfte sich in der königlichen Bibliothek der Burg Stormwind finden. Könnt Ihr es für mich holen? Wenn dem so ist, dann mögen die Segnungen des Lichts mit Euch sein!","Die königliche Bibliothek ist sehr groß, aber der oberste Bibliothekar, Milton Sheaf, kennt jedes Regal, als wäre es sein eigenes Kind. Sprecht mit ihm, und er wird Euch das Buch holen."}, {"Geht in die königliche Bibliothek der Feste von Stormwind und sprecht mit Milton Sheaf. Er kann das Buch über Metallurgie für Euch finden, das Bruder Kristoff für seine Rede benötigt."}},
[344] = {"Bruder Paxton", {"Belastbarkeit von Eisen? Ja, ich kenne das Buch. Ziemlich gut, vor einigen Jahrzehnten von dem Zwergenminenarbeiter Margulf Blaggon geschrieben. Er verbrachte den Großteil seines Lebens tief in den Bergen von Khaz Modan, Erze schürfend. Kluger Kerl!","Aber wo war ich doch gleich? Ach ja, die \"Belastbarkeit von Eisen\"! Ich fürchte, das Buch wurde in die Abtei von Nordhain verlegt. Der Verwalter der Bibliothek der Abtei, Bruder Paxton, wollte eine Kopie des Buches für seine eigenen Regale anfertigen.","Das war allerdings vor Monaten. Ich frage mich, warum er so lange dafür braucht..."}, {"Sprecht mit Bruder Paxton in der Abtei von Nordhain."}},
[345] = {"Tinten-Zutaten", {"Ich will meine Abschrift von \"Belastbarkeit von Eisen\" schon länger fertig stellen, aber...","Nun, ich stelle für die Bücher, die ich abschreibe, meine Tinte selbst her, versteht Ihr? Es handelt sich um mein eigenes Rezept. Sie verläuft nicht, sie schmiert nicht und sie trocknet sehr schnell. Dummerweise trocknet sie ein wenig zu schnell! Meine letzte Portion ist ausgetrocknet, bevor ich die Abschrift beenden konnte, und ich hatte bisher keine Gelegenheit, mehr Tinte herzustellen!","Um mehr herzustellen, benötige ich Rethbaneisenerz aus dem Rotkammgebirge. Großknecht Oslow in Seenhain ist mein üblicher Lieferant."}, {"Sprecht mit Großknecht Oslow in Seenhain und fragt ihn nach Rethbaneisenerz."}},
[346] = {"Rückkehr zu Kristoff", {"Nachdem ich jetzt mit der Abschrift fertig bin, könnt Ihr das Original von \"Belastbarkeit von Eisen\" haben. Ich danke Euch für Eure Geduld, und wenn Ihr Milton seht, bitte sagt ihm, die lange Verzögerung täte mir Leid."}, {"Kehrt zu Bruder Kristoff auf den Kathedralenplatz zurück. Gebt ihm das Buch: \"Belastbarkeit von Eisen\"."}},
[347] = {"Rethbanerz", {"Rethbanerz wurde nach dem Mann benannt, der es als Erster entdeckt hat, Bart Rethban. Wenn es geschmolzen wird, ist es nicht so stark wie Eisen, aber sein Erz hat eine dunkle, glatte Farbe, die einige Schreiber in ihren Farbtinten verwenden. Man kann es in den Rethbanhöhlen nördlich von hier finden.","Wir haben normalerweise etwas davon auf Lager, aber wir sind zu sehr damit beschäftigt, uns auf Orcangriffe vorzubereiten, als dass wir welches sammeln könnten. Und nur so nebenbei... In den Rethbanhöhlen leben jetzt Rotkammgnolle und deren Zwangsarbeiter bauen das Erz selbst ab.","Wenn Ihr also welches benötigt, müsst Ihr es Euch selbst besorgen."}, {"Sammelt 5 Fuhren Rethbanerz und bringt sie Bruder Paxton in der Bibliothek von Nordhain."}},
[348] = {"Schlingendornfieber", {"Krank... so... furchtbar k-k-krank...","... habe mich mit Schlingendornfieber angesteckt... tödliches V-v-virus...","... einziges Heilmittel... wäre... wäre es... das Herz von M-m-m-mokk dem Wilden zu essen... schwer zu fassendes, weißes Wildtier...","... Hexendoktor Unbagwa... Einziger, der es b-b-beschwören kann...","... sucht Unbagwa... versteckte Höhle am südlichen Kap...","... schnell... sterbe... sterbe..."}, {"Sucht Hexendoktor Unbagwa und lasst ihn Mokk den Wilden beschwören. Bringt das Herz von Mokk zu Fin Fizracket."}},
[349] = {"Schlingendornfieber", nil, nil},
[350] = {"Auf einen alten Freund verlassen", {"Marzon würde ein Mitglied der Defias kontaktieren und er hat Verbindungen zu Lescovar...","Da van Cleef und Thredd tot sind, wäre es schwierig, einen Richtspruch gegen Lescovar zu erwirken, denn Eure Beweise sind wertlos ohne deren Zeugnis. Und dabei wird noch nicht einmal die Tatsache berücksichtigt, dass Lescovar ja ein Adliger ist und viele Beziehungen hat. Das stellt ihn gewissermaßen über das Gesetz.","Wir werden weitere Hilfe benötigen. Ich tu das nur äußerst ungern, aber ich kenne jemanden, der uns helfen wird. Ihr findet Trias im Handelsdistrikt der Stadt."}, {"Sprecht mit Elling Trias in Stormwind."}},
[351] = {"Findet OOX-17/TN!", {"Ihr habt ein merkwürdiges Gerät aus Metall entdeckt, das die Form eines Eis hat. Als Ihr einen der Knöpfe berührt, erwacht das Ei zu Leben und verwandelt sich in eine Art Gnom-Roboter-Apparat! Aus dem Inneren des Roboteis erklingt eine rasselnde Stimme.","\"Mein Name ist Oglethorpe und mein Leitroboter ist verloren gegangen! Ich würde Euch belohnen, wenn Ihr ihn aufspürt! Bringt dem Roboter bitte diesen Signalgeber. Hervorragend, jetzt habe ich die Koordinaten für Euch berechnet! Er ist in der Nähe der Klaffenden Schlucht in Tanaris abgestürzt!\""}, {"Bringt das Notsignal zum Leitroboter von Oglethorpe."}},
[353] = {"Stormpikes Lieferung", {"Die Stormpikes sind ein geachteter Zwergenklan und bekannt für ihren feinen und kritischen Geschmack. Daher ist es nicht verwunderlich, dass Gringer Stormpike, ein Gebirgsjäger aus Ironforge, mich beauftragte, ihm eine Waffe zu schmieden.","Die Waffe ist fertig, aber... Gebirgsjäger Stormpike ist weit weg, am fernen Loch Modan. Wenn Ihr ohnehin vorhabt, nordwärts zu reisen, könntet Ihr ihm dieses Päckchen überbringen?","Meine letzte Nachricht von Gebirgsjäger Stormpike besagte, dass er im nördlichen Wachturm des Loch Modan stationiert ist."}, {"Bringt das Päckchen für Stormpike zu Gebirgsjäger Stormpike am Loch Modan."}},
[354] = {"Todesfälle in der Familie", {"Die Familie Agamand war die wohlhabendste in Tirisfal. Ich habe früher in ihrer Mühle gearbeitet... vor der Seuche.","Als die Geißel das erste Mal auftauchte, befestigten die Agamands ihre Heimstatt und überredeten ihre Angestellten zu bleiben und bei der Verteidigung zu helfen. Wir waren Narren, aber wir waren loyale Narren.","Die Agamands haben uns in ihrem Stolz zum Untod verdammt. Und heute sind sie Diener der Geißel!","Dient den Verlassenen, indem Ihr die Agamands besiegt, die der Seuche zum Opfer fielen. Dient mir, indem Ihr mir ihre Überreste bringt."}, {"Bringt Coleman Farthing in Brill Gregors Überreste, Nissas Überreste und Thurmans Überreste."}},
[355] = {"Gespräch mit Sevren", {"Ihr habt mir die Überreste der Agamands gegeben und meinen Rachedurst gestillt. Aber Agamands Mühlen sind eine Bedrohung für alle Verlassenen, nicht nur für mich.","Ich habe mit Magistrat Sevren über Eure Heldentaten gegen die Agamands geredet, und er wünscht, mit Euch zu sprechen."}, {"Sprecht mit Magistrat Sevren in Brill."}},
[356] = {"Patrouille zur Rückendeckung", {"Die Verteidiger auf dem Bollwerk, die Tirisfal vor den Pestländern schützen, sind immer wachsam. Aber manchmal schafft es die Geißel, an ihnen vorbeizuschlüpfen.","Unser Erfolg am Bollwerk hängt an einer Ein-Fronten-Schlacht - wir können keinen Angriff von hinten zulassen, und noch weniger können wir zulassen, dass die Versorgungslinie unserer Verteidiger unterbrochen wird.","Unterstützt das Bollwerk. Patrouilliert ostwärts und erschlagt alle Untoten, die Ihr findet. Seid auf Balnirs Bauernhof im Osten besonders gründlich. Es hat sich zu einem Zufluchtsort für eindringende Untote entwickelt."}, {"Tötet 8 blutende Schrecken und 8 wandernde Geister und meldet Euch dann wieder bei Linnea in ihrem Lager."}},
[357] = {"Die Identität des Lichs", {"Es gibt einen Lich, der auf einer Insel im Blendwassersee im Norden haust. Obwohl er ein Verlassener mit freiem Willen ist, glaubt er, dass alle anderen Untoten Sklaven der Geißel sind, und hetzt seine Diener auf jeden, der sich ihm nähert.","Aber da er ein Verlassener ist, wird die Königin ihn haben wollen. Und wenn er in der Totenbeschwörung bewandert ist, dann wäre sein Wissen... recht nützlich für uns.","Um etwas über seine Identität und sein Können herauszufinden, muss ich mir sein Zauberbuch ansehen. Stehlt Euch in sein Lager, verschafft es Euch und kehrt dann zu mir zurück."}, {"Bringt das Zauberbuch des Lichs zu Bethor Iceshard in Undercity."}},
[358] = {"Grabräuber", {"Die Massengräber südwestlich von Garrens Schlupfwinkel weiter im Norden wurden geschaffen, um der... eindrucksvollen... Anzahl Todesfälle gerecht zu werden, die Tirisfal erlitt, als die Seuche zum ersten Mal über uns kam.","Den Toten in diesen Gräbern blieb der Untod bisher erspart, aber die Geißel schickt jetzt Moderfellgnolle, um die Leichen einzusammeln, um sie zur Verstärkung ihrer Armeen einzusetzen. Das können wir nicht zulassen!","Ihr habt zweierlei Aufgaben: Erschlagt die Moderfelle am Massengrab und in Garrens Schlupfwinkel und nehmt Euch von ihnen das Einbalsamierungssekret, das ihnen Leben verleiht."}, {"Tötet Grabräuber der Moderfelle und Bastarde der Moderfelle.","Bringt Magistrat Sevren in Brill 8 Einbalsamierungssekrete."}},
[359] = {"Verlassenen-Pflichten", {"Ich brauche einen aktuellen Bericht von unserem Außenposten der Todeswache im Osten.","Ich muss wissen, ob mehr Streitkräfte der Geißel am Bollwerk vorbei und nach Tirisfal herein geschlichen sind. Unsere Wachsamkeit an der Grenze zu den Pestländern muss aufrechterhalten werden - wir können die Geißel hier nicht stärker Fuß fassen lassen!","Reist südwärts die Straße entlang und biegt an der Gabelung nach Osten zum Außenposten ab. Sprecht mit Todeswache Linnea. Sie hat die Informationen, die ich benötige."}, {"Sprecht mit Todeswache Linnea."}},
[360] = {"Rückkehr zum Magistrat", {"Kehrt jetzt zu Magistrat Sevren mit den Informationen zurück, die ich euch gegeben habe. Er wird sie so schnell wie möglich haben wollen, also sputet Euch.","Und solltet Ihr unterwegs welche von der Geißel sehen, schaltet sie aus - wir können sie nicht unbehelligt durch unser Land spazieren lassen."}, {"Kehrt zu Magistrat Sevren nach Brill zurück."}},
[361] = {"Ein nie zugestellter Brief", {"Dies ist ein Brief, der von Thurman, dem ältesten Sohn von Gregor Agamand, geschrieben wurde. Er ist an Yvette Farthing adressiert, aber er scheint nicht bei ihr angekommen zu sein..."}, {"Findet Yvette Farthing und bringt Ihr den Brief von Thurman Agamand."}},
[362] = {"Die Spuk-Mühle", {"Devlin Agamand war der jüngere von zwei Söhnen der Familie Agamand und im Leben hätten sie unterschiedlicher nicht sein können. Thurman war groß und sanft, während sein jüngerer Bruder schwach und scharfzüngig war.","Als Agamands Mühlen der Seuche anheim fielen, war ich nicht überrascht zu hören, dass Devlin ihr schnell erlag. Sein verrücktes Gebrabbel kann man heute noch nahe der Straße hören, die zu Agamands Mühlen führt.","Ich sammle die Überreste der Agamands und ich möchte den armen Devlin. Findet ihn, vernichtet ihn und bringt mir seine Knochen."}, {"Erschlagt Devlin Agamand und bringt Devlins Überreste zu Coleman Farthing nach Brill."}},
[363] = {"Unsanftes Erwachen", {"Wurde auch Zeit, dass Ihr aufwacht. Wir wollten Euch gerade mit den anderen ins Feuer werfen, aber es sieht so aus, als hättet Ihr es geschafft.","Ich bin Mordo, der Verwalter der Deathknell-Gruft. Und Ihr seid kein Sklave des Lichkönigs mehr.","Sprecht mit Schattenpriester Sarvis in der Kapelle am Fuße des Hügels, er wird Euch mehr über das erzählen, was Ihr wissen müsst."}, {"Sprecht mit Schattenpriester Sarvis."}},
[364] = {"Die Hirnlosen", {"Wir Verlassenen liegen im Krieg mit der Geißel, der Armee des Lichkönigs: durch Nekromantie erweckte Armeen von Untoten, grausige Bestien des Nordens und gepeinigte Schreckgespenster.","Der nördliche Teil des Dorfes wurde von den Hirnlosen überrannt, und diese müssen vernichtet werden. Vernichtet sie, zeigt ihnen keine Gnade, auch wenn sie unsere früheren Brüder und Schwestern gewesen sein mögen. Die Gefallenen sind nichts als Sklaven des Lichkönigs."}, {"Schattenpriester Sarvis will, dass Ihr 8 hirnlose Zombies und 8 elende Zombies tötet."}},
[365] = {"Felder des Kummers", {"Was haben wir denn hier? Ihr seht wie ein Neuling aus, <Klasse>. Wenn Ihr darauf hofft, Euch der dunklen Fürstin zu beweisen, dann müsst Ihr die Gebräuche der Verlassenen erlernen.","Im Westen werdet Ihr einen Hof vorfinden. Menschen suchen das Land heim wie Schimmel eine verrottende Leiche. Und um es noch schlimmer zu machen, patrouilliert ganz in der Nähe der Scharlachrote Kreuzzug von seinem Turm aus. Erteilt diesem Abschaum eine Lektion und stehlt 10 ihrer kostbaren Kürbisse.","Sobald Ihr 10 beisammen habt, bringt sie zu Apotheker Johaan nach Brill."}, {"Stehlt 10 Kürbisse vom Gehöft im Westen, direkt nördlich von Deathknell und bringt sie zu Apotheker Johann nach Brill."}},
[366] = {"Rückgabe des Buches", {"Vor langer Zeit waren Gunther und ich, wie auch der jetzt der Geißel dienende Totenbeschörer Thule Rabenklaue, Freunde und Schüler beim Kirin Tor, einer Magiergesellschaft in Dalaran.","Gunther und ich fielen beide der Seuche anheim, aber ich dachte, er wäre entweder tot geblieben oder für die Armee des Lichkönigs erweckt worden. Es tut gut zu wissen, dass er frei ist.","Wir müssen ihn für uns gewinnen. Ich habe einen Zauber auf dieses Buch gelegt, der ihm, sobald er ihn sieht, zeigen wird, dass ich ebenfalls frei vom Einfluss des Lichkönigs bin. Ich hoffe, dass er sich uns anschließt, wenn er das erfährt."}, {"Bringt Gunthers Zauberbuch zu ihm auf die \"Gunthers Zufluchtsort\" genannte Insel zurück."}},
[367] = {"Eine neue Seuche", {"Lady Sylvanas hat sich an die königliche Apothekervereinigung gewandt. Die dunkle Fürstin glaubt, dass unser Wissen, gepaart mit der neuentdeckten Magie, der Schlüssel zu Arthas' Untergang sein wird. Sie hat uns aufgefordert, eine neue Seuche zusammenzubrauen, eine Seuche, tödlicher als jedes Leiden in ganz Azeroth. Diese neue Krankheit wird Arthas' Armee der Geißel ins Verderben stürzen.","Meine Studien zeigen, dass das Blut von Bestien der Schlüssel sein könnte. Bringt mir 5 Phiolen Schattenhundblut, damit ich meine Theorie ausprobieren kann."}, {"Apotheker Johaan in der Stadt Brill möchte, dass Ihr 5 Phiolen Schattenhundblut sammelt."}},
[368] = {"Eine neue Seuche", {"Während Ihr unterwegs wart und Proben für mich gesammelt habt, haben meine Experimente ergeben, dass noch weitere Reagenzien benötigt werden, wenn sich diese Krankheit ordentlich ausbreiten soll. Ein paar unglückliche Opfer zu vergiften, ist eine Kleinigkeit. Eine ganze Welt erkranken zu lassen, erweist sich als etwas komplizierter.","Ich werde 5 Finsterflossenschuppen von den Murlocs in der Umgebung brauchen. Ihr findet die Kreaturen entlang der Küste im Norden oder Westen von hier."}, {"Apotheker Johaan in der Stadt Brill benötigt 5 Finsterflossenschuppen von den Murlocs in Tirisfal."}},
[369] = {"Eine neue Seuche", {"Während Ihr sammeln wart, habe ich einen alten Text in einem meiner Bücher entdeckt, der darauf hindeutet, dass eine uralte Seuche Tausende unschuldiger Opfer ausgelöscht hat. Man fand später heraus, dass der tödliche Erreger im Gift der Nachtweberspinnen erhalten blieb.","Bringt mir etwas Gift eines heimtückischen Nachtwebers, um dieses Experiment abzuschließen. Ich möchte sehen, ob das ansteckende Element des Giftes mit meinem neuen Gebräu harmoniert. Gerüchten zufolge leben die Spinnen im östlichen Tirisfal."}, {"Apotheker Johaan in der Stadt Brill möchte, dass Ihr ihm 4 Proben vom Gift eines heimtückischen Nachtwebers bringt."}},
[370] = {"Im Krieg mit dem Scharlachroten Kreuzzug", {"Die strategischen Dokumente von Exekutor Arren enthalten alle Angaben, die wir brauchen, um die menschliche Plage aus den Ländern zu tilgen. Nach diesen Informationen hat der Scharlachrote Kreuzzug Captain Perrine mit einer Brigade dem verfallenen Turm südwestlich von Brill zugeteilt.","Tötet Perrine, sowie 3 Zeloten und 3 Missionare und erstattet mir dann Meldung."}, {"Exekutor Zygand in Brill möchte, dass Ihr Captain Perrine, 3 Scharlachrote Zeloten und 3 Scharlachrote Missionare tötet."}},
[371] = {"Krieg mit dem Scharlachroten Kreuzzug", {"Wir befinden uns noch immer im Krieg, und der Scharlachrote Kreuzzug gewinnt an Stärke. Der Bericht, den mir Exekutor Arren schickte, zeigt auf, dass die Scharlachroten Kreuzzügler ihre Überfälle von dem verfallenen Turm im südöstlichen Tirisfal, nahe Balnirs Bauernhof, unter dem Kommando von Captain Vachon ausgeführt haben.","Tötet Vachon, zusammen mit 5 Scharlachroten Ordensmitgliedern. Das sollte sich als vernichtender Schlag für den Kreuzzug erweisen!"}, {"Exekutor Zygand in Brill hat Euch damit beauftragt, Captain Vachon und 5 Scharlachrote Ordensmitglieder zu erschlagen."}},
[372] = {"Krieg mit dem Scharlachroten Kreuzzug", {"Scharlachrote Kreuzzügler haben vom verfallenen Turm im nördlichen Tirisfal, hinter Faols Ruheplatz, Überfälle ausgeführt. Nach den Informationen, die wir haben, führt ein unbarmherziger Kommandant mit Namen Captain Melrache diese bösartige Truppe an.","Ich vertraue Euch eine besondere und gefahrvolle Mission an. Erschlagt Melrache und seine beiden Leibwachen im Namen der dunklen Fürstin."}, {"Exekutor Zygand in der Stadt Brill möchte, dass Ihr Captain Melrache und seine beiden Leibwachen ermordet."}},
[373] = {"Der nie verschickte Brief", {"Als Ihr Edwin van Cleef durchsucht, findet Ihr unter anderem einen nie verschickten Brief. Er ist adressiert an \"Baros Alexston, Stadtarchitekt von Stormwind, Rathaus, Kathedralenplatz\".","Er scheint erst vor kurzem geschrieben und versiegelt worden zu sein."}, {"Bringt den Brief nach Stormwind zum Stadtarchitekten Baros Alexston."}},
[374] = {"Beweise des Ablebens", {"Exekutor Zygand sagt mir, dass Ihr Scharlachrote Kreuzzügler töten wart. In Tirisfal wimmelt es nur so von denen.","Varimathras, Offizier der dunklen Fürstin, kümmert sich persönlich darum, dass der Abschaum Azeroths, die Menschen, völlig ausgelöscht wird. Hier in Tirisfal stehen wir einer besonders lästigen Menschenbrut gegenüber: dem Scharlachroten Kreuzzug.","Begebt Euch hinaus und führt eine Offensive gegen den Scharlachroten Kreuzzug, <Klasse>. Bringt mir 10 scharlachrote Insignienringe zum Beweis Eurer Loyalität Varimathras und der dunklen Fürstin gegenüber."}, {"Bringt Todeswache Burgess in Brill 10 scharlachrote Insignienringe."}},
[375] = {"Die Kälte des Todes", {"Es ist jetzt so kalt. Die Seuche des Untodes kriecht durch meine Adern wie eine eisige Schlange. Ich werde bald den Hirnlosen Zustand erreichen. Doch auch ein besiegeltes Schicksal wird mich nicht davon abhalten, unserer dunklen Fürstin zu dienen. Als der Ruf ertönte, nähte ich Leichensäcke für die gefallenen Soldaten von Sylvanas' mächtiger Armee.","Jetzt zittern meine Hände vor lauter Kälte. Wenn Ihr mir fünf Nachtsaugerpelze und etwas groben Faden bringen würdet, könnte ich mir eine Decke nähen. Helft mir, <Name>, auf dass ich der Sache weiter dienen kann."}, {"Bringt Gretchen Dedmar in Brill fünf Nachtsaugerpelze und etwas groben Faden."}},
[376] = {"Die Verdammten", {"Meine Pflichten umfassen, die verwundeten Krieger zu versorgen, Rüstung und Kleidung zu schneidern und Schattenpriester Sarvis in jeder erdenklichen Weise zu unterstützen.","Es sieht so aus, als würdet Ihr ebenfalls in seine Dienste aufgenommen... wahrscheinlich um die Hirnlosen zu jagen, wie ich ihn kenne. Tja, wenn Ihr dabei in einem Stück bleiben wollt - und ich bezweifle nicht, dass Ihr das wollt -, dann kann ich Euch vielleicht helfen. Mir gehen die Tatzen und Flügel aus, und wenn Ihr mir einige davon bringt, besorge ich Euch eine Rüstung. Ihr werdet im Norden eine Menge Wölfe und Fledermäuse finden."}, {"Novize Elreth benötigt 6 Aasfressertatzen und 6 Nachtsaugerflügel."}},
[377] = {"Verbrechen und Strafe", {"Als wäre die Vernachlässigung der Einwohner des Dämmerwalds nicht schlimm genug, nein, die jüngste Entscheidung des Oberhauses, Dextren Ward in Stormwind ins Gefängnis zu werfen, anstatt den Schurken - wie durch Lord Ebonlockes Urteil gefordert - zu enthaupten, ist ein Schlag ins Gesicht für den Rat von Dunkelhain.","Ward wurde dabei erwischt, wie er Leichen aus dem Friedhof an Morbent Fel verkaufte, ein Verbrechen, auf das der Tod steht. Und dennoch fordert Stormwind seine Gefangensetzung.","Ermordet Ward im Verlies von Stormwind - bringt mir seine Hand - und ich werde Euch belohnen."}, {"Ratsherr Millstipe von Dunkelhain will, dass Ihr ihm die Hand von Dextren Ward bringt."}},
[378] = {"Tief empfundener Zorn", {"Ihr habt Eure Loyalität zu König Magni bewiesen, <Klasse>. Und Euer Hass auf den Dunkeleisenabschaum ist genau so groß wie mein eigener.","Es gibt da eine Aufgabe, die ich selbst beenden möchte, aber ich bin durch meine Ehre verpflichtet, bei Longbraid zu bleiben. Jedoch hat Roggo Informationen gesammelt, die belegen, dass Kam Deepfury einer der Verschwörer beim Angriff auf den Thandol-Übergang war. Durch Deepfurys Pläne verlor Longbraid seine Sippe.","Deepfury wird aus politischen Gründen im Verlies von Stormwind festgehalten. Ich will ihn tot sehen, <Name>. Für Longbraid! Bringt mir seinen Kopf!"}, {"Motley Garmason in Dun Modr verlangt Kam Deepfurys Kopf."}},
[379] = {"Durstlöscher", {"Ihr möchtet eine Kraftquelle. Da kann ich Euch helfen. Wisst Ihr, was ich möchte? Ich möchte etwas zu Trinken! Bringt mir fünf Wasserbeutel eines Wüstenläufers und ich werde Euch die 4711-FTZ geben.","Und jetzt... ratet einmal, wo Ihr solche Wasserbeutel finden könnt! Warum geht Ihr nicht zu einem der Wüstenläufernomaden östlich von hier und bittet ihn um einen? Lasst mich wissen, ob das klappt."}, {"Bringt Chefingenieur Bilgewhizzle in Gadgetzan 5 Wasserbeutel eines Wüstenläufers."}},
[380] = {"Nachtweber", {"Eine unserer größten Schwierigkeiten besteht darin, die Rohstoffe zu besorgen, die wir zum Überleben brauchen. Gold war schon zu Hochzeiten der Macht der Allianz selten.","Es gibt eine Goldmine im Nordwesten, die von Spinnen überrannt wurde. Wir brauchen das Gold dieser Mine, aber wir können es ja schlecht holen, während die Spinnen darin herumkrabbeln. Ich habe nicht viel Personal übrig für diese Aufgabe, also müssen wir sie Schritt für Schritt lösen.","Begebt Euch dorthin und schaut mal, was Ihr für uns ausrichten könnt, <Name>."}, {"Exekutor Arren will, dass Ihr 10 junge Nachtweber und 8 Nachtweber tötet."}},
[381] = {"Der Scharlachrote Kreuzzug", {"Ihr werdet erfreut sein zu hören, dass wir anscheinend Fortschritte in der Mine machen. Der Dank dafür gebührt zu einem nicht unerheblichen Maß Euren Bemühungen. Nun können wir uns anderen Belangen zuwenden.","Meine Späher haben berichtet, dass eine Abteilung des Scharlachroten Kreuzzugs südöstlich von hier ein Lager errichtet. Der Scharlachrote Kreuzzug ist eine verabscheuungswürdige Organisation, die uns jagt, und sie wird nicht ruhen, ehe nicht alle Untoten - ganz gleich, ob Teil der Geißel des Lichkönigs oder nicht - vernichtet sind. Wir müssen ihnen zuvorkommen!","Seid vorsichtig, denn der unheilige Eifer dieser Leute macht sie zu gefährlichen Gegnern."}, {"Bringt Exekutor Arren insgesamt 12 scharlachrote Armbinden von scharlachroten Konvertiten und scharlachroten Initianden."}},
[382] = {"Der Rote Bote", {"Berichte meiner Vorgesetzten in Brill weisen darauf hin, dass ein Agent des Scharlachroten Kreuzzugs vom Scharlachroten Kloster hierher nach Deathknell entsendet wurde. Nach diesen Berichten führt dieser Bote wichtige Informationen mit sich. Ich kann nicht mit Einzelheiten dienen, aber was auch immer es ist, wir müssen es haben!","Ihr seid die qualifizierteste Person, die ich darauf ansetzen kann, <Name>. Findet diesen Boten und bringt mir alle Informationen, die er bei sich trägt."}, {"Tötet Meven Korgal, den Boten, im Lager der Kreuzzügler und kehrt danach mit allen Informationen, die Ihr findet, zu Exekutor Arren zurück."}},
[383] = {"Entscheidende Informationen", {"Das muss zu meinem Vorgesetzten, Exekutor Zygand, nach Brill gebracht werden. Mit diesen Informationen können wir dem Scharlachroten Kreuzzug einen entscheidenden Schlag versetzen. Eure Dienste waren hier in Deathknell nützlich, aber ich benötige jemanden, der dies zu Zygand bringt, und ich denke, er wird angemessenere Arbeiten für Euch finden.","Nehmt die Straße nach Norden, hinaus aus Deathknell. Kurz nachdem Ihr nach Tirisfal hineinkommt, trefft Ihr auf eine Kreuzung. Nehmt die Abzweigung nach Osten und behaltet diese Richtung dann bei. Ihr werdet unterwegs am Coldhearth-Anwesen vorbeikommen."}, {"Bringt die Dokumente des Scharlachroten Kreuzzugs zu Exekutor Zygand nach Brill."}},
[384] = {"Eberrippchen in Biersauce", {"Meine Tavernengäste mögen nichts so sehr wie meine Eberrippchen in Biersauce. Das einzige Problem dabei ist, dass der ortsansässige Fallensteller, der mich bisher mit den Zutaten versorgt hat, in die Armee des Königs eingetreten ist, um im Krieg an der Allianzfront zu helfen.","Vielleicht könnt Ihr mir helfen? Wenn Ihr mir sechs Klippeneberrippchen bringt und ein Rhapsody-Malzbier von der Taverne hier besorgt, bekommt Ihr das Familienrezept für meine berühmten Eberrippchen in Biersauce, und nicht zu vergessen natürlich eine Kostprobe! Das Geheimnis liegt im Malz!"}, {"Ragnar Donnerbräu in Kharanos benötigt 6 Klippeneberrippchen und einen Krug Rhapsody-Malzbier."}},
[385] = {"Krokiliskenjagd", {"So manchen Jäger zieht es an den Loch Modan, um unsere berühmten Kroks zu erlegen. Irgendein Händler sucht immer Krokiliskenhäute für seine Kleidungsstücke oder Rüstungen und es gibt auch einige Leute, denen ihr Fleisch mundet.","Wir handeln ein wenig damit, aber nicht wirklich viel, denn die Krokilisken sind wild und haben sich auf den Inseln im Loch verschanzt. Aber ich will Euch nicht davon abbringen, denn es ist ein unvergessliches Erlebnis, mit den Kiefern der Bestien zu ringen.","Ach ja, das eine Mal damals..."}, {"Besorgt 5 Stücke Krokiliskenfleisch und 6 Krokiliskenhäute für Marek Ironheart in der Weitschreiter-Lodge."}},
[386] = {"Verbrechen lohnt sich nicht", {"Targorr der Schreckliche diente als oberster Exekutor unter Gath'llzogg. Er ging grausam vor, selbst nach den Standards der dreckigen Orcs. Menschen, die tapfer gekämpft hatten, um das Königreich zu verteidigen, wurden nur zu seiner Belustigung gefoltert. Heute wird er im Verlies von Stormwind gefangen gehalten und wurde zum Tode verurteilt. Dennoch stimmt irgendetwas nicht. Einer dieser bürokratischen Adeligen hat seine Exekution ausgesetzt. Ich bin mir sicher, hier wird eine Schandtat geplant.","Macht Targorr dem Schrecklichen ein Ende, <Name>. Reist zum Verlies und enthauptet ihn, bevor man uns überlistet."}, {"Bringt Wache Berton in Seenhain den Kopf von Targorr dem Schrecklichen."}},
[387] = {"Niederschlagung des Aufstandes", {"Der Aufstand muss niedergeschlagen werden! Ich brauche Eure Hilfe. Wenn jemand mitbekommt, dass das Gefängnis überrannt wurde, verliere ich meinen Job! Solch übles Spiel habe ich noch nie erlebt. Aber was jetzt zählt ist, die Gefangenen zur Sicherheit Stormwinds niederzuringen.","Die Strafe für Auflehnung lautet Tod. Tötet einige dieser Abweichler! Das sollte die Angelegenheit wieder unter Kontrolle bringen."}, {"Aufseher Thelwater aus Stormwind will, dass Ihr im Verlies 10 gefangene Defias, 8 eingekerkerte Defias und 8 Aufrührer der Defias tötet."}},
[388] = {"Die Farbe von Blut", {"Verbitterung fließt wie ein kalter Fluss durch meine Adern. Mein Enkel, Mac, war so ein guter Junge. Gerade mal 14 Jahre alt. Als die Armee an die Front geschickt wurde, griff Mac zu den Waffen, um die Stadt zu verteidigen. Er war eine Wache im Verlies, wo diese elenden Defias eingesperrt sind. Mac wies ein paar der Gefangenen dafür zurecht, dass sie ihre Wolldecken zerrissen, um Schals daraus zu machen, die ein Symbol ihrer Bande darstellen. Dafür wurde Mac hinterhältig erstochen.","Ich will Rache. Bringt mir 10 ihrer kostbaren Wollkopftücher. Sie haben die Farbe von Blut."}, {"Nikova Raskol von Stormwind will, dass Ihr 10 rote Wollkopftücher für sie sammelt."}},
[389] = {"Bazil Thredd", {"Van Cleef und ich waren beide Mitglieder der Steinmetzgilde. Unser Hauptprojekt war der Wiederaufbau Stormwinds nach dem Krieg.","Als wir unsere Aufgabe erfüllt hatten, wurden wir betrogen. Die Adligen verweigerten uns die Bezahlung für unsere Arbeit. Einigen der älteren Steinmetze wurden Posten in der Regierung angeboten, aber van Cleef lehnte aus Loyalität zu den übrigen Steinmetzen ab. Er führte eine Revolte an und verließ, Rache schwörend, die Stadt.","Sein Helfer, Bazil Thredd, könnte vielleicht mehr über van Cleefs Pläne wissen. Er wird im Verlies gefangen gehalten."}, {"Sprecht mit Aufseher Thelwater im Verlies."}},
[391] = {"Aufstand im Verlies", {"Lasst uns eine Sache klarstellen, <Name>: Ich traue Euch nicht. Aber wie die Sache im Moment aussieht, werdet Ihr ohnehin keinen Unterschied machen.","Also wird das folgendermaßen ablaufen: Geht da runter und redet mit Thredd. Wenn Ihr wirklich auf unserer Seite seid, dann eliminiert ihn und bringt mir seinen Kopf, wenn Ihr fertig seid. Und falls sich herausstellt, dass Ihr auf seiner Seite seid und nicht herauskommt... werdet Ihr zusammen mit dem Rest dieser Würmer sterben, wenn wir Euch finden."}, {"Tötet Bazil Thredd und bringt seinen Kopf mit zurück zu Aufseher Thelwater im Verlies."}},
[392] = {"Der seltsame Besucher", {"In den ersten Jahren von Thredds Gefangenschaft bekam er niemals Besuch. Ich ging davon aus, dass er für die Bruderschaft keinen Nutzen mehr hatte und sie ihn daher fallen ließ.","Aber vor einigen Monaten hat sich das alles geändert; er bekam regelmäßig Besuch ... einmal, manchmal sogar zweimal pro Woche. Es war ein seltsamer Mann, so ein ruhiger Typ. Ich hatte da so meinen Verdacht, aber alle seine Papiere waren in Ordnung.","Laut seinen Dokumenten war sein Name Maelik; hier habt Ihr seine Beschreibung. Ich glaube nicht, dass er noch einmal kommen wird, um Thredd zu besuchen."}, {"Bringt die versiegelte Beschreibung von Thredds Besucher nach Stormwind zu Baros Alexston."}},
[393] = {"Schatten der Vergangenheit", {"Ich habe wirklich keine Ahnung, <Name>. Aber es gibt da jemand anderen, der Euch vielleicht helfen kann, und wie es der Zufall so will, schuldet dieser Jemand mir einen Gefallen, weil... egal, das brauchen wir jetzt nicht zu vertiefen. Ihr könnt Meister Mathias Shaw vom SI:7 in der Kaserne in der Altstadt finden. Shaw hat seine Finger in den meisten zwielichtigen oder heimlichen Geschäften der Stadt.","Wenn also jemand etwas weiß, dann er."}, {"Bringt die versiegelte Beschreibung von Thredds Besucher nach Stormwind zu Meister Mathias Shaw."}},
[394] = {"Der Kopf des Ganzen", {"Sucht Shaw in der Altstadt. Teilt ihm mit, dass Ihr Euch um die Bedrohung durch Lescovar gekümmert habt, und berichtet ihm von Eurer Beteiligung an den Plänen des Lords gegen die Stadt. Ich bin sicher, er wird stolz sein, einen weiteren Helden unter den Bürgern Stormwinds zu haben.","Er wird Euch ganz bestimmt zu einem Treffen mit dem Prinzen oder einem anderen Adligen schicken. Lasst Euch das nicht zu Kopfe steigen, <Name>. Die Adligen können noch so sehr glauben, dass sie die Stadt beherrschen, aber einige Dinge bleiben für die im Dunklen."}, {"Sprecht mit Mathias Shaw in Stormwind."}},
[395] = {"Das Ende der Bruderschaft", {"Ach, das hätte ich fast vergessen. Alexston kam vorbei, als Ihr unterwegs wart, und wünschte mit Euch zu sprechen. Zweifellos möchte er den Adligen über die Ränkespiele der Defiasbruderschaft berichten.","Ein kleiner Rat, <Name>: Sollte Euch jemand fragen, würde ich zwar alle Details offen legen, aber dabei nicht erwähnen, dass es Eure Hand war, die Lescovar erschlug. Vertraut mir. Ich... kenne mich aus... mit solchen Dingen.","Und was Trias angeht, nun, sprecht mit niemandem über ihn... niemals... wenn Ihr es vermeiden könnt."}, {"Sprecht mit Baros Alexston in Stormwind."}},
[396] = {"Eine Audienz beim König", {"Ich habe einen Bericht an den König vorbereitet. Und Ihr sollt sein Überbringer sein, zusammen mit einer Auflistung Eurer Ergebnisse. Macht Euch keine Sorgen, er wird Euch zweifellos für die Erfüllung Eurer Pflicht gegenüber den Leuten von Stormwind loben.","Eure Audienz findet in Kürze statt, also solltet Ihr Euch besser beeilen, denn es ist schon ein Stück Weges bis zur Burg Stormwind."}, {"Liefert Baros Alexstons Bericht über die Bruderschaft der Defias bei König Varian Wrynn in der Burg Stormwind ab."}},
[397] = {"Ihr habt uns gut gedient", {"Wie ich höre, hat Lord Wishock sein Getränk genossen.","Ihr habt uns gut gedient, <Name>. Die Hexenmeister von Stormwind werden nun, dank Euch, ohne Störung ihrem Tagewerk nachgehen können. Ich habe ein besonderes Geschenk vorbereitet, um Euch für Eure Mühen zu entlohnen. Sprecht mit meinem Diener, Zggi, um Eure Belohnung zu erhalten."}, {"Sprecht mit Zggi, dem Diener von Zardeth von den Blackclaw, um Eure Belohnung zu erhalten."}},
[398] = {"GESUCHT: Madenauge", {"Gesucht: Madenauge!","Die selbst für einen Gnoll außerordentlich niederträchtige Kreatur wurde für ihre Verbrechen an den Verlassenen zum Tode verurteilt. Dieser verabscheuungswürdige Verbrecher hat Leichen für die Armee des Lichkönigs gestohlen. Madenauge wurde zuletzt gesehen, als er die Gegend nahe Garrens Schlupfwinkel nördlich von Brill plünderte.","Die Stadt Brill bietet hiermit eine Belohnung für die fähige Seele, die die Exekution durchführt. Zeigt Exekutor Zygand die Tatze des Verbrechers, wenn die Tat getan ist.","Gnade ist etwas für die Schwachen."}, {"Tötet Madenauge und kehrt dann mit seiner Tatze zu Exekutor Zygand nach Brill zurück, um eine Belohnung zu erhalten."}},
[399] = {"Ein bescheidener Anfang", {"Es kommt mir so vor, als wäre es ewig her, dass ich als Junge in Westfall auf dem Hof gearbeitet habe. Man sagt ja, man kann niemals in der Zeit zurückgehen, und es stimmt wohl. In meinem Fall trifft das sogar gleich doppelt zu, denn das Heim meiner Familie wurde niedergebrannt und von Dieben besetzt.","Ich habe mit meinem Vater über den Verbleib einiger meiner Habseligkeiten gesprochen, darunter mein erster Kompass. Er konnte sie nicht retten. Allerdings meint er auch, dass sie auf dem Hof versteckt sein müssten.","Ihr findet den Alexstons Bauernhof westlich der Späherkuppe. Könntet Ihr vielleicht dorthin gehen und mir meinen Kompass wiederbringen?"}, {"Begebt Euch zu Baros Alexstons Haus in Westfall und sucht nach seinem Kompass. Übergebt ihm diesen dann am Kathedralenplatz von Stormwind."}},
[400] = {"Werkzeuge für Steelgrill", {"Beldin Steelgrill ist der Besitzer der örtlichen Maschinenschlosserei, und er ist der beste Monteur von Belagerungsmaschinen, den es gibt! Allerdings geht er nicht sehr gnädig mit seinen Werkzeugen um. Ich schwöre Euch, er macht bald mehr Bogenlichtschraubenschlüssel kaputt, als wir ihm liefern können!","Wir haben gerade seine letzte Werkzeugbestellung fertig. Wenn Ihr sie ihm anliefert, wird er sich sicherlich erkenntlich zeigen.","Sein Geschäft, Steelgrills Depot, liegt etwas nordöstlich von Kharanos. Und nebenbei, es handelt sich dort um einen örtlichen Treffpunkt für die Veteranen unter den Piloten der Belagerungsmaschinen, also hört Euch ruhig ein wenig um, während Ihr dort seid."}, {"Liefert Beldin Steelgrill seine Werkzeuge."}},
[401] = {"Wartet, bis Sirra fertig ist", {"Ich bin fast mit der Übersetzung dieser Notiz fertig. Wenn Ihr in ein paar Minuten wiederkommt, dürfte ich fertig sein."}, {"Wartet einen Moment und sprecht dann erneut mit Sirra Von'Indi."}},
[403] = {"Das bewachte Fass mit Donnerbier", nil, nil},
[404] = {"Eine eitrige Aufgabe", {"Teile der Geißel sind nach und nach in Tirisfal eingedrungen und suchen die Gegend westlich von Brill heim, nahe der alten Brücke.","Geht dorthin und schlagt die Faulenden Toten und Verheerten Kadaver zurück, die Ihr dort vorfindet. Zerstreut ihre Knochen und bringt uns ihre eitrigen Klauen, dann werden die Todeswachen Euch entlohnen."}, {"Bringt Todeswache Dillinger in Brill 7 eitrige Klauen."}},
[405] = {"Der abtrünnige Lich", {"Bethor Iceshard, ein hochrangiger und mächtiger Magier aus Undercity im Süden, befiehlt mir, ihm einen Agenten zu schicken, der seinen Wert gegen die Geißel bereits bewiesen hat. Ihr, <Name>, werdet dieser Agent sein.","Zeigt Bethor diese Befehle. Er wird Euch daraufhin über Eure Mission aufklären. Ich kenne keine Details, aber es hat etwas damit zu tun, einen eigenwilligen Lich zu rekrutieren. Und es wird gefährlich sein.","Also macht Euch bereit, <Klasse>. Ihr dürft nicht scheitern.","Ihr dürftet Bethor im Magieviertel von Undercity finden."}, {"Zeigt Bethor Iceshard in Undercity Sevrens Befehle."}},
[407] = {"Felder des Kummers", {"Harmlose Kürbisse, oder? Zumindest sehen sie so aus. Wenn wir Arthas' Vormarsch im Norden und die menschliche Plage im Süden stoppen wollen, müssen wir alle Möglichkeiten unseres untoten Daseins nutzen.","Mit etwas Einfallsreichtum wird sogar aus einem einfachen Kürbis ein Agent für unsere dunkle Fürstin. Dieser Kürbis wird sich, wenn nach meiner jüngsten Formel behandelt, als eine besondere Leckerei erweisen.","Es wurde schon wieder ein Scharlachroter Zelot gefangen genommen, der derzeit im Keller der Taverne Zur Galgenschlinge gefangen gehalten wird. Bringt dem Trottel diesen Kürbis."}, {"Bringt den getränkten Kürbis zum gefangenen Scharlachroten Zeloten, der im Keller der Taverne Zur Galgenschlinge gefangen gehalten wird."}},
[408] = {"Die Familiengruft", {"Die Gegend um Agamands Mühlen hat sich zu einem Außenposten der Geißel entwickelt. Am stärksten sind sie in der Gruft der Familie Agamand, wo ihr örtlicher militärischer Anführer, ein Skelett namens Captain Dargol, sein Hauptquartier hat. Er hat die Vorfahren der Agamands durch Nekromantie wiedererweckt und hat vor, sie gegen uns einzusetzen.","Dies darf nicht geschehen, und Ihr müsst es verhindern. Reist zur Gruft der Familie Agamand und besiegt die erweckten Vorfahren der Agamands.","Und bringt mir den Schädel von Captain Dargol."}, {"Tötet 8 Klagende Vorfahren und 8 Faulende Vorfahren.","Tötet Captain Dargol und bringt seinen Schädel zu Magistrat Sevren nach Brill."}},
[409] = {"Treuebeweis", {"Ihr behauptet, Ihr wärt nicht dem Willen des Lichkönigs unterworfen, und die Verzauberung, die Bethor auf mein Buch legte, untermauert dies. Aber bevor ich Euch glaube, muss ich den Beweis haben, dass Ihr der Geißel feindlich gegenübersteht.","Es gibt einen alten, blutigen Altar kurz vor der Südküste dieser Insel. Er wird von einer mächtigen Dienerin der Geißel, Lillith Nefara, verwendet.","Nehmt eine Kerze des Heranlockens aus dieser Kiste hier. Entzündet sie auf dem Altar, und Lillith wird erwachen. Vernichtet Lillith, und ich werde Euch glauben, dass Ihr ein Gegner des Lichkönigs seid."}, {"Beschafft Euch eine Kerze des Heranlockens.","Beschwört Lillith Nefara und tötet sie.","Kehrt zu Gunther auf seiner Insel zurück."}},
[410] = {"Der Schlummernde Schatten", nil, nil},
[411] = {"Die Rückkehr des abtrünnigen Lichs", {"Bringt meinem alten Kameraden Bethor diesen Netheredelstein. Er wird uns ermöglichen, miteinander zu kommunizieren... und ich habe ihm eine Menge zu erzählen."}, {"Bringt den Netheredelstein zu Bethor Iceshard nach Undercity."}},
[412] = {"Operation Rekombobulationsgerät", {"Nach genauerer Analyse der Situation in Gnomeregan sieht es so aus, als hätten wir nicht nur dabei versagt, die Troggs auszulöschen, sondern als hätten wir dabei auch noch einen Großteil des Volkes der Gnome in schreckliche, hirnlose, bösartige Lepragnome verwandelt.","Ozzie und ich haben vor, den schrecklichen Lepraeffekt mit unserer jüngsten Erfindung wieder rückgängig zu machen: der Rekombobulator. Die Maschine ist beinahe fertig, aber wir brauchen dringend einige Restabilisationszahnräder und gyromechanische Getriebeteile. Besorgt uns ein paar davon bei den Lepragnomen drüben in Gnomeregan."}, {"Bringt Razzle Sprysprocket in Kharanos 8 Restabilisationszahnräder und 8 gyromechanische Getriebeteile."}},
[413] = {"Schimmerstarkbier", {"Ich möchte, dass mein Bruder Wellart mein neues Schimmerstarkbier ausprobiert. Er ist Gebirgsjäger und im südlichen Wachturm stationiert, einem von zwei Türmen an der Grenze zum Loch Modan.","Er mag vielleicht kein Bierbrauer sein wie der Rest von uns Gerstenbräus, aber er trinkt gern mal einen, und ich bin mir sicher, er wird dies hier mögen.","Könnt Ihr ihm dieses Fass Schimmerstarkbier für mich bringen?"}, {"Bringt das Fass mit Schimmerstarkbier zu Gebirgsjäger Gerstenbräu."}},
[414] = {"Starkbier für Kadrell", {"Ich habe inzwischen mehr von diesem Schimmerstarkbier getrunken, das mein Bruder gebraut hat - das ist großartiges Zeug! Ein Gebirgsjägerkumpel von mir muss das mal probieren. Er heißt Kadrell und patrouilliert normalerweise entlang der Straße durch Thelsamar beim Loch Modan.","Um nach Thelsamar zu gelangen, müsst Ihr südostwärts durch den Tunnel gehen und dann nach Nordosten abbiegen, wenn der Pfad auf eine Straße trifft. Dieser Straße folgt Ihr direkt bis nach Thelsamar.","Gebt Gebirgsjäger Kadrell dieses Fässlein Schimmerstarkbier und schaut mal, was er davon hält!"}, {"Bringt das Fässlein mit Schimmerstarkbier zu Gebirgsjäger Kadrell."}},
[415] = {"Rejolds neues Gebräu", {"Rejold Gerstenbräu aus Brewnall hat nach Euch gefragt.","Er möchte mit Euch über ein neues Getränk reden, das er gebraut hat... er sagte etwas davon, dass Ihr ihm vielleicht dabei helfen könntet. Vielleicht wollt Ihr also wieder nach Brewnall zurückkehren, um herauszufinden, worum es geht.","Es könnte etwas Freibier für Euch drin sein..."}, {"Sprecht mit Rejold Gerstenbräu."}},
[416] = {"Rattenfang", {"Wir haben im Westen ein Problem mit einer echten Plage. Kobolde der Tunnelratten sind ins Vorgebirge eingedrungen, haben dort ihre Baue eingerichtet und verdrecken unser Land mit ihrem Abfall!","Wir wollen, dass sie ausgelöscht werden. Jagt sie, bringt mir ihre Ohren und kassiert das Kopfgeld.","Ihr findet die Kobolde der Tunnelratten und ihre Baue westlich von Thelsamar überall über den Berghang verteilt."}, {"Bringt Gebirgsjäger Kadrell in Thelsamar 12 Tunnelrattenohren."}},
[417] = {"Pilotenrache", {"Hildelve schrieb in sein Tagebuch, dass er von Marterklaue, einem \"gewaltigen Eisklauenbären\", angegriffen wurde. Er hat die Bestie verjagt, aber nicht, ohne vorher übel zugerichtet zu werden.","Wenn man sich den Zustand von Hildelves Leiche so ansieht, muss Marterklaue später wiedergekommen sein und ihn getötet haben.","Jedenfalls schrieb Hildelve in sein Tagebuch einen letzten Wunsch:","Rache an Marterklaue."}, {"Tötet Marterklaue.","Bringt Pilot Hammerfoot die räudige Klaue des Bären und Hildelves Tagebuch."}},
[418] = {"Thelsamar-Blutwurst", {"Es gibt hier in Thelsamar immer genug Mäuler zu stopfen, mit all den Kindern, die ein und aus gehen, und den Arbeitern von den Ausgrabungsstätten, die nach einem arbeitsreichen Tag hereinkommen. Wir sind berühmt für unsere Blutwurst, Ihr habt sie nicht zufällig schon einmal probiert?","Nein? Nun, hierzulande muss man für seine Mahlzeiten arbeiten, und glaubt nur nicht, dass Ihr eine Ausnahme seid, nur weil Ihr ein toller <Klasse> seid.","Ich brauche Bärenfleisch, Ebergedärme für die Pelle und Spinnensekret als Würzmittel. Bringt mir das alles und überlasst dann Vidra das Kochen!"}, {"Bringt Vidra Hearthstove in Thelsamar 3 Stück Bärenfleisch, 3 Portionen Ebergedärme und 3 Portionen Spinnensekret."}},
[419] = {"Der vermisste Pilot", {"Mein Freund Mori Hildelve, ebenfalls ein Pilot der Belagerungsmaschinen, hat sich in den Hügeln verirrt. Wir haben nach einem seltenen Erz gesucht, das man für hochwertiges Sprengpulver benötigt, und während unserer Suche hat er seine Maschine einen steilen Hügel hinaufgefahren und sie dabei kaputt gemacht!","Immer noch überzeugt davon, dass es in diesen Bergen Erz gibt, hat er mich beauftragt, unsere Maschinen zu bewachen, während er zu Fuß weitersuchen wollte.","Das ist jetzt mehrere Tage her, und ich habe in den Hügeln des Nachts das Knurren von Raubtieren gehört. Mori ist ein zäher Bursche, aber ich mache mir Sorgen.","Bitte, <Name>. Sucht ihn."}, {"Findet Pilot Hildelve."}},
[420] = {"Senirs Beobachtungen", {"Die Troggs im Tunnel sind extrem feindselig und scheinen nicht zu zögern, durch den Tunnel reisende Leute anzugreifen. Ihr seid aber offenbar hart genug im Nehmen, also solltet ihr wahrscheinlich sicher sein.","Eine Wegbeschreibung? Wenn Ihr mit Senir sprechen wollt, findet Ihr ihn in Kharanos.","Arbeitet Euch durch den Tunnel und folgt einfach weiter der Straße, wenn Ihr auf der anderen Seite herauskommt, denn sie führt direkt nach Kharanos."}, {"Überbringt Senir Whitebeard in Kharanos Grelins Bericht."}},
[421] = {"Wertbeweis", {"Lady Sylvanas hat Varimathras damit beauftragt, die Gebiete der Menschen und Zwerge im Süden zu erobern.","Aber dieser Idiot Arugal - ein Scharlatan aus Dalaran und heutzutage eine verfluchte Kreatur der Burg Shadowfang - hat auf verantwortungslose Weise mit seiner Magie den strategischen Stützpunkt des Silberwaldes verheert.","Ich brauche einen geschickten Kämpfer, der dabei hilft, die Suppe auszulöffeln, die Arugal uns eingebrockt hat. Beweist Euch mir, indem Ihr 5 Moonrage-Weißskalps tötet. Man findet die elenden Bestien oft nur ein kleines Stück abseits der Straße, am Fuße des Hügels dort unten."}, {"Dalar Dawnweaver auf dem Grabmal will, dass Ihr 5 Moonrage-Weißskalps tötet."}},
[422] = {"Arugals Torheit", {"Wie Arugal in die Kirin Tor aufgenommen wurde, ist mir schleierhaft. Seine Zauberkunst schien so billig wie schlecht nachgemachter Schmuck.","Am wichtigsten ist es erst einmal, herauszufinden, welche Art von Magie Arugal genau verwendet hat, damit ich sie gegen ihn verwenden und den Silberwald für die dunkle Fürstin einnehmen kann.","Arugal hielt sich zuerst auf der Weizenfarm etwas nördlich der Brücke auf. Einer der Todespirscher berichtete, dass er einige Zauberbücher gesehen hätte, sie aber nicht beschaffen konnte. Beschafft mir den Zauber namens 'Arznei des Arugal' aus diesen Büchern."}, {"Beschafft Dalar Dawnweaver auf dem Grabmal die Arznei des Arugal."}},
[423] = {"Arugals Torheit", {"Bei der Untersuchung von Arugals Arbeit haben sich meine schlimmsten Befürchtungen bestätigt. Der alte Taugenichts war nicht einmal qualifiziert genug, um die Nachttöpfe in Dalaran zu leeren, davon, die Kirin Tor in ihrer schwersten Stunde zu repräsentieren, ganz zu schweigen. Narren!","Arugal benutzte verzauberte Gegenstände, um seine schwache Magie zu verstärken. Ich muss diese Gegenstände selbst untersuchen. Zieht los und erschlagt so lange Moonrage-Nimmersatts und Moonrage-Dunkelseelen, bis Ihr genug ihrer verzauberten Fesseln für meine Nachforschungen gesammelt habt. Man hat diese üblen Kreaturen im Norden und Osten gesehen."}, {"Bringt Dalar Dawnweaver am Grabmal 6 Nimmersattfesseln und 3 Dunkelseelenfesseln."}},
[424] = {"Arugals Torheit", {"Ich werde tatsächlich länger brauchen, als ich gedacht hätte, die dunklen Geheimnisse hinter den Verzauberungen, die Arugal verwendete, zu entdecken.","In der Zwischenzeit müsst Ihr Euch um ein kleines Problem kümmern, das unsere Dunkelpirscher entdeckt haben. Es sieht so aus, als hätte Arugal seiner Magie erlaubt, sich bis zur Tiefenfelsmine in den Hügeln im Südosten auszubreiten.","Diese Mine wäre eine ziemlich nützliche Ressource für Varimathras' Vormarsch.","Ich will, dass Ihr Grimson den Blassen, den besudelten Großknecht der Mine, enthauptet. Mit seinem Tod sollte die Mine uns gehören."}, {"Tötet Grimson den Blassen und bringt seinen Kopf zu Dalar Dawnweaver auf das Grabmal."}},
[425] = {"Ivar der Verfaulte", {"Mein Bruder Quinn und ich wurden beauftragt, den Silberwald auszukundschaften und Hochexekutor Hadrec Bericht zu erstatten. Aber wir kamen nicht weit - als wir über diese Farm gingen, wurden wir von einem Ghul namens Ivar der Verfaulte angegriffen.","Wir entkamen Ivar, aber er verletzte Quinn schwer. Dank des Trankes, den Ihr ihm gegeben habt, heilt er, aber Ivar könnte wiederkommen, bevor Quinn sich erholt hat.","Tötet Ivar, damit Quinn genug Zeit hat, sich zu regenerieren. Ivar hält sich üblicherweise in dem Schuppen da drüben auf und wartet darauf, dass meine Wachsamkeit nachlässt."}, {"Tötet Ivar den Verfaulten und bringt Ivars Kopf zu Rane Yorick auf dem Ivar-Feld."}},
[426] = {"Die überrannte Mühle", {"Die Geißel versucht, bei Agamands Mühlen einen Stützpunkt zu errichten. Wenn ihnen das gelingt, können sie weitere Angriffe innerhalb von Tirisfal unternehmen.","Es wurden klare Befehle erteilt. Die Diener der Geißel in den Mühlen müssen vernichtet werden.","Geht zu den Mühlen. Sammelt schartige Rippen von klappernden Skelettsoldaten und Trümmerschädelsoldaten sowie geschwärzte Schädel von dunklen Skelettmagiern.","Um zu den Mühlen zu gelangen, folgt der Straße westwärts. Nachdem Ihr die Brücke überquert habt, nehmt die erste Abzweigung nach Norden und folgt diesem Weg. Wenn Ihr die Windmühlen seht, beginnt die Schlacht."}, {"Sammelt 5 schartige Rippen sowie 3 geschwärzte Schädel und kehrt dann zu Todeswache Dillinger nach Brill zurück."}},
[427] = {"Im Krieg mit dem Scharlachroten Kreuzzug", {"Die Dokumente, die Exekutor Arren uns zur Verfügung stellte, verschaffen uns genau die Atempause, die wir in unserem Kampf gegen den Scharlachroten Kreuzzug benötigen. Wir kennen jetzt dessen genaue Aufenthaltsorte überall in Tirisfal.","Aber leider haben die Todeswachen noch ganz andere Sorgen. Die Armee des Lichkönigs wächst jede Nacht an. Wir benötigen jemanden, der wie Ihr gern \"Initiative\" zeigt, um den Scharlachroten Kreuzzug ins Grab zu bringen.","Beweist mir, dass Ihr der dunklen Fürstin dienen könnt, indem Ihr nach Westen zum Turm hinter Sollidens Bauernhof reist und dort 10 Scharlachrote Krieger erschlagt."}, {"Exekutor Zygand von Brill verlangt, dass Ihr 10 Scharlachrote Krieger tötet."}},
[428] = {"Die verlorenen Todespirscher", {"Vor einigen Wochen wurden zwei unserer Todespirscher, Rane und Quinn Yorick, auf eine Aufklärungsmission durch den nördlichen Silberwald geschickt. Wir haben nichts mehr von ihnen gehört.","Sie könnten der Geißel zum Opfer gefallen sein, doch wenn dem so ist, will ich es bestätigt wissen. Findet diese Todespirscher.","Ihre erste Aufgabe war es, die Bauernhöfe im nördlichen Silberwald auszukundschaften. Ihr solltet dort mit Eurer Suche beginnen."}, {"Findet die Todespirscher Quinn und Rane Yorick."}},
[429] = {"Wilde Herzen", {"Mein Bruder Quinn wurde von dem Ghul Ivar dem Verfaulten schwer verletzt, und ich glaube nicht, dass er ohne magische Hilfe wieder vollständig genesen wird. Auch wenn ich kein Apotheker bin, so weiß ich doch, dass unsere Apotheker Heiltränke aus verfärbten Worgherzen herstellen können.","Sammelt einige solcher Herzen und bringt sie zu Apotheker Renferrel am Grabmal im Süden. Kommt dann mit dem Trank hierher zurück.","Zwischen hier und Maldens Obsthain im Osten werdet Ihr jede Menge Worgs vorfinden."}, {"Sammelt 6 verfärbte Worgherzen und bringt sie zu Apotheker Renferrel auf dem Grabmal."}},
[430] = {"Rückkehr zu Quinn", {"Hier habt Ihr den Trank, mit dem Ihr Quinn Yoricks Erholung beschleunigen könnt.","Wenn Ihr mich jetzt entschuldigen mögt, ich habe wirklich wichtige Dinge zu erledigen..."}, {"Bringt Quinn Yorick auf dem Ivar-Feld, nördlich des Grabmals, Quinns Trank."}},
[431] = {"Kerze des Heranlockens", nil, nil},
[432] = {"Diese verfluchten Troggs!", {"Diese vom Licht verfluchten Troggs verwandeln meinen Arbeitsplatz in eine Ruine. Schaut Euch das an, da geht schon wieder ein Pulverfass hoch.","Oh, ich wünschte ich könnte da runtergehen und allen einzeln ihre dürren Hälse umdrehen! Ich will sie alle töten!","Das kann nicht gut für meine Gesundheit sein, all dieser Stress und diese Wut... Was haben sie mir gelassen? Nichts!","Ich würde sie persönlich umbringen, aber ich ziele nicht mehr so gut wie früher. Geht in den Steinbruch und tötet einige dieser dreckigen Mistviecher! Ich werde Euch bezahlen! Hauptsache, Ihr verletzt sie, tötet sie!"}, {"Tötet für Großknecht Stonebrow im Gol'Bolar Steinbruch 6 Schädelhauer der Felsenkiefer."}},
[433] = {"Im Dienste des Volkes", {"Als Mitglied des Senats und der Forscherliga habe ich mir vorgenommen, mich um den Teil der Trogg-Plage zu kümmern, die unser Land heimsucht.","Sie haben den Gol'Bolar Steinbruch wirklich ziemlich verwüstet, und das ohne erkennbaren Grund. Als wir tiefer in die Erde hineingruben, strömten sie heraus, zerstörten unsere Ausrüstung und verjagten die Minenarbeiter. Wir können nicht viel mehr tun, als das ganze Pack auszulöschen, alles wieder aufzubauen und dann wieder an die Arbeit zu gehen.","Wenn Ihr mir mit den Troggs helft, werde ich Euch gern für Eure Zeit, die Ihr dafür opfert, entschädigen."}, {"Tötet für Senator Mehr Stonehallow im Gol'Bolar Steinbruch 10 Knochenknacker der Felsenkiefer."}},
[434] = {"Der Angriff!", {"Nachdem ich dem Spionagebot die Verkleidung angelegt habe, begibt er sich hinein, um mit Lescovar zu sprechen. Die Wachen sollten ihn ohne Probleme durchlassen.","Während Ihr Material für mich gesammelt habt, habe ich Marzon darüber informiert, dass es für ihn und Lescovar an der Zeit sei, sich wieder zu treffen. Ich habe sogar einige falsche Informationen verlautbaren lassen.","Lescovar wird seine Wachen wegschicken und hier draußen im Garten auf Marzon warten. Tretet in Aktion, wenn Ihr etwas über die Defias hört. Ihr müsst sie beide eliminieren. Kapiert?","Wenn Ihr das erledigt habt, sprecht Ihr mit Trias."}, {"Bleibt im Garten, bis Lord Gregor Lescovar und Marzon die Stille Klinge ihr Gespräch beendet haben. Schaltet sie danach beide aus, bevor Ihr nach Stormwind zu Elling Trias zurückkehrt."}},
[435] = {"Eskorte für Erland", {"Die Wölfe da draußen wollen nicht abhauen, und jedesmal, wenn ich einen Schritt aus der Tür mache, schauen sie mich böse an. Sie müssen unglaublich hungrig sein, wenn sie mich für essenswert halten.","Ich muss mich bei meiner Kampfgefährtin, Todespirscherin Rane Yorick, melden. Und um zu Rane zu kommen, brauche ich Hilfe gegen diese Wölfe.","Bitte begleitet mich! Ach ja, da draußen sind eine Menge Wölfe, wir hätten also eine bessere Chance, wenn Ihr Freunde dabei hättet, die ebenfalls helfen können..."}, {"Begleitet Erland durch die Wölfe hindurch zu Rane Yorick."}},
[436] = {"Ironbands Ausgrabungsstätte", {"Ausgrabungsleiter Ironband leitet die Ausgrabung einiger alter Ruinen östlich des Sees. Er hat zuletzt wenig Fortschritte gemacht, besonders, wenn man all den Nachschub bedenkt, den wir ihm geschickt haben.","Ironband ist ein bodenständiger, ehrlicher Zwerg, der gern Resultate sieht, und daher mache ich mir Sorgen, dass da irgendwelche Kräfte gegen ihn arbeiten.","Geht zu Ironbands Ausgrabungsstätte und sprecht mit Magmar Fellhew. Er kümmert sich um die Einzelheiten an der Stätte und wird wissen, warum es so langsam voran geht.","Um zur Ausgrabungsstätte zu gelangen, müsst Ihr um die Südspitze des Sees herum und dann nach Osten gehen."}, {"Sprecht mit Forscher Fellhew."}},
[437] = {"Das Todesfeld", {"Unsere ursprünglichen Spähberichte über den Silberwald zeigen, dass die Moderfelle auf einer alten Farm im Norden, nahe dem Todesfeld, einen Stützpunkt haben. Wir haben kleine Angriffe auf sie gestartet, aber jedesmal werden sie von der Banshee Nachtseele unterstützt, die ihre Magie einsetzt, um unsere Truppen in die Flucht zu schlagen.","Wir wollen Nachtseele vernichtet sehen.","Geht ins Todesfeld und greift die Moderfelle an. Falls Ihr Nachtseele nicht seht, tötet so lange Moderfellgnolle, bis sie auftaucht, und besiegt sie dann.","Ihre Essenz wird zu Staub zerfallen. Bringt mir diesen Staub als Beweis."}, {"Tötet Gnolle der Moderfelle auf dem Todesfeld.","Sobald Nachtseele auftaucht, tötet sie ebenfalls.","Bringt Nachtseeles Essenz zu Hochexekutor Hadrec auf das Grabmal."}},
[438] = {"Die verfallene Fähre", {"<Name>, wir haben ein neues Nest der Geißel entdeckt: Die verfallene Fähre. Die Fähre liegt östlich des Grabmals und wird von Moderfellgnollen und anderen Untoten schwer bewacht.","Es muss einen Grund dafür geben, warum die Geißel dieses Gebiet besetzt hat. Geht zur verfallenen Fähre und findet heraus, warum die Geißel sich dort aufhält."}, {"Geht zur verfallenen Fähre.","Sucht nach dem Grund, warum die Geißel dort ist."}},
[439] = {"Auf den Spuren der Moderfelle", {"Aus den Leichen ragt eine nicht eingewickelte Hand heraus. Es handelt sich um die Hand einer Frau, von der Seuche pockennarbig, aber relativ frisch.","An der Hand befindet sich ein Ring. Als Ihr den Ring entfernt und untersucht, entdeckt Ihr eine Gravur:","\"Für Deliah\"."}, {"Bringt Deliahs Ring zu Hochexekutor Hadrec auf das Grabmal."}},
[440] = {"Der gravierte Ring", {"Wenn Ihr den Ehemann oder Geliebten finden wollt, der Deliah ihren Ring gab... dann sprecht mit Magistrat Sevren in Brill. Er sollte alle Dokumente über die jüngsten Todesfälle haben und wird mit etwas Glück herausfinden können, wer Deliah ist.","Aber ich würde meine Hoffnungen nicht zu hoch stecken. Wer auch immer ihr den Ring gab, ist wahrscheinlich tot. Oder, wenn er untot ist, kümmert Deliah ihn wahrscheinlich nicht mehr."}, {"Bringt Deliahs Ring zu Magistrat Sevren nach Brill."}},
[441] = {"Raleigh und Undercity", {"Deliah hatte einen Ehemann, Raleigh, der nun in Undercity sein Zuhause hat. Wenn die Deliah, die in Brill wohnte, die ist, der dieser Ring gehörte, dann wird es Raleigh wissen.","Raleigh ist im Handwerksviertel von Undercity."}, {"Bringt Deliahs Ring zu Raleigh."}},
[442] = {"Sturm auf die Insel Fenris", {"Es ist an der Zeit, den Standort der Geißel im Silberwald zu stürmen: die Insel Fenris.","Ihr müsst Euch noch einmal auf diese Insel begeben und Thule Rabenklaue vernichten. Ich will seinen Kopf!","Diese Mission ist so gefahrvoll, wie sie entscheidend ist, <Name>. Ihr dürft nicht scheitern."}, {"Bringt Hochexekutor Hadrec auf dem Grabmal Thules Kopf."}},
[443] = {"Moderfellsekret", {"Bevor wir einen Angriff auf die Insel Fenris starten können, müssen wir erst herausfinden, wer hinter den Moderfellgnollen steht. Unsere Apotheker glauben, ihr Ursprung liegt in der seltsamen Flüssigkeit, die durch ihre Körper fließt.","Geht zur Insel Fenris, östlich der verfallenen Fähre, und sammelt Moderfellsekret von den Gnollen dort. Bringt es zu Apotheker Renferrel, draußen vor dem Grabmal."}, {"Bringt 8 Flaschen Moderfellsekret zu Apotheker Renferrel auf das Grabmal."}},
[444] = {"Die Ursprünge der Moderfelle", {"Ich habe das Sekret, das Ihr mir gebracht habt, in seine Grundbestandteile aufgelöst. Meine Ergebnisse sind nicht sehr aufschlussreich, aber ich konnte ermitteln, dass Hexenmeister- und Totenbeschwörermagie zur Herstellung des Sekrets verwendet wurden.","Während ich weiter das Sekret untersuche, möchte ich, dass Ihr eine Probe davon zu Bethor Iceshard nach Undercity bringt. Bethor ist kein Apotheker, aber sein Wissen von der Magie könnte uns helfen, das Geheimnis um die Moderfelle zu lüften.","Geht. Ihr dürftet Bethor im Magieviertel von Undercity finden."}, {"Bringt die Sekretprobe zu Bethor Iceshard nach Undercity."}},
[445] = {"Lieferung in den Silberwald", {"Zeit ist ein Luxus, den sich diejenigen, die für Lady Sylvanas arbeiten, nicht leisten können. Aber das wisst Ihr inzwischen sicher selbst.","Als Mitglied der königlichen Apothekervereinigung ist es meine Pflicht, mein Wissen mit meinen Kollegen zu teilen, auf dass unsere gemeinsamen Bemühungen der dunklen Fürstin eines Tages die neue Seuche bescheren werden, die sie so ersehnt.","Bringt diese Erkenntnisse zu Apotheker Renferrel, der beim Grabmal im Silberwald stationiert ist. Folgt der Straße von Brill aus nach Süden. Geht dann nach Undercity südwestwärts."}, {"Bringt Apotheker Johaans Erkenntnisse zu Apotheker Renferrel im Silberwald."}},
[446] = {"Thule Rabenklaue", {"Dieses Sekret wurde von einem alten Kollegen von mir hergestellt: Thule Rabenklaue. Vor dem großen Krieg war er ein junger, aufstrebender Magier, aber als der Lichkönig immer mächtiger wurde, verriet er seine Brüder und verbündete sich mit der Geißel.","Ich hätte nicht gedacht, dass er noch lebt, geschweige denn, dass er die Moderfelle bei ihren Vorstößen gegen uns anführt.","Ich kenne viele seiner Zauber und kann dabei helfen, einen Trank zu brauen, der ihnen entgegenwirkt. Nehmt diese Spruchrolle mit zurück zu Apotheker Renferrel. Auf ihr stehen die Beschwörungsformeln, die man für den Trank braucht."}, {"Bringt Bethors Spruchrolle zu Apotheker Renferrel auf das Silberwald-Grabmal."}},
[447] = {"Rezept für den Tod", {"Arthas hat Truppen von schier endloser Zahl. Doch mit einer neuen Seuche könnten wir sowohl die Armee der Geißel als auch das menschliche Ungeziefer ein für alle Mal auslöschen.","Meine Studien haben bewiesen, dass Spinnenblut, vermischt mit einem aus dem Herzen eines ergrauten Bären gewonnenen Toxin, zu einem tödlichen Elixier wird. Sammelt Proben von den Spinnen im Huschdunkel im Nordwesten und von den Bären, die im Silberwald umherstreifen. Bringt diese Reagenzien zu Meisterapotheker Faranell von der königlichen Apothekervereinigung in Undercity."}, {"Sammelt 6 Herzen von ergrauten Bären und 6 Proben Huschendes Blut und bringt diese zu Meisterapotheker Faranell nach Undercity."}},
[448] = {"Meldung bei Hadrec", {"Bevor Ihr einen Angriff auf die Insel Fenris unternehmt, meldet Euch bei Hochexekutor Hadrec. Er könnte zusätzliche Informationen oder Ausrüstung haben, die Ihr brauchen werdet."}, {"Meldet Euch bei Hochexekutor Hadrec auf dem Grabmal."}},
[449] = {"Der Bericht der Todespirscher", {"Auch wenn Quinns Wunden heilen, möchte ich doch unseren Bericht so schnell wie möglich zu Hochexekutor Hadrec bringen. Er enthält Informationen, die für die Besetzung des Silberwaldes durch die Verlassenen von höchster Wichtigkeit sind."}, {"Bringt den Todespirscherbericht zu Hochexekutor Hadrec auf das Grabmal."}},
[450] = {"Rezept für den Tod", {"Ihr könnt der königlichen Apothekervereinigung noch weiter zu Diensten sein, <Name>.","Als Arugal seine elenden Zauber über den Wald gelegt hat, verfluchte er eines unserer viel versprechendsten Mitglieder, Apotheker Berard. Als hirnloser Diener von Arugal ist Berard jetzt für uns nutzlos. Aber seine Erkenntnisse könnten sich als wertvoll erweisen. Er untersuchte eine längst vergessene Seuche, die vor Ewigkeiten einmal alles Leben im Lordameresee getötet hat.","Bergt Berards Tagebuch aus seinem Studierzimmer im Gasthaus von Pyrewood und bringt es zu Apotheker Renferrel."}, {"Bergt Apotheker Berards Tagebuch aus Pyrewood und bringt es zu Apotheker Renferrel auf das Grabmal."}},
[451] = {"Rezept für den Tod", {"Laut Berards Tagebuch könnte sich das Moos von Seeschleichern und Seekrabblern bei unserer Suche nach einer neuen Seuche als nützlich erweisen. Diese Bestien leben auf den Inseln im Lordameresee im Osten.","Er erwähnt außerdem einen sehr seltenen verhärteten Tumor, den er bei einem Finsterflossenmurloc entdeckte. Anscheinend war der Tumor so selten, dass Berard nur einen einzigen auftreiben konnte.","Sammelt Proben von diesem Moos sowie von dem seltenen verhärteten Tumor und bringt beides zu Meisterapotheker Faranell nach Undercity."}, {"Bringt 6 Proben Seeschleicher-Moos, 6 Proben Seekrabbler-Moos und einen verhärteten Tumor zu Meisterapotheker Faranell nach Undercity."}},
[452] = {"Der Pyrewood-Überfall", {"Ihr kommt gerade rechtzeitig, <Name>!","Der Rat von Pyrewood wird jeden Moment zurückkehren. Arugal muss sich in der Burg um andere Dinge kümmern. Er benutzt den Rat als reine Schachfiguren, um die hirnlosen Diener von Pyrewood unter seiner Kontrolle zu halten. Aber ich glaube, dass die Söhne von Arugal sich ohne die Führung durch den Rat von Pyrewood gegen ihren Meister stellen werden.","Ich werde nicht allein mit ihnen allen fertig. Ihr müsst mir helfen. Gemeinsam können wir den Rat für Lady Sylvanas zerschlagen."}, {"Helft Todespirscher Faerleia, den Rat von Pyrewood zu töten."}},
[453] = {"Auf der Suche nach der Schattengestalt", {"Viel Glück, <Name>. Ich hoffe, Ihr könnt den Kerl finden, und dass er dann noch am Leben ist.","Wenn er die Sicherheit von Dunkelhain hinter sich gelassen hat, könnte er leicht Wildtieren zum Opfer gefallen sein."}, {"Findet die Schattengestalt. Eure Hinweise:","Er stammt nicht aus Dunkelhain.","Er ist eine nervöse, hektische Person.","Er verließ Dunkelhain und ging nach Westen."}},
[454] = {"Nach dem Hinterhalt", {"Wir hatten vor dieser... Geschichte die Pulverfässer in mehreren Fuhren transportiert. Jetzt müssen wir die Arbeit aufteilen. Miran nimmt die Fässer, und ich passe auf die anderen auf.","Ich fühle mich nicht wohl dabei, Miran den Rest der Fracht ganz allein transportieren zu lassen. Vielleicht könntet Ihr ihn auf seiner nächsten Tour begleiten, wenn er fertig zur Abreise ist?"}, {"Sprecht mit Miran."}},
[455] = {"Der Algaz-Spießrutenlauf", {"Orcs vom Dragonmawklan haben im Sumpfland eine Zufluchtstätte. Und jetzt haben sie sich nach Dun Algaz ausgebreitet. Wir können nicht zulassen, dass sie ihren Einfluss auf dieses Gebiet behalten, sonst wird der Loch Modan vom Hafen von Menethil, unserer Hafenfestung im Sumpfland, abgeschnitten.","Brecht durch Dun Algaz in das dahinterliegende Sumpfland durch und tötet dabei ein paar Orcs. Meldet Euch bei Valstag Ironjaw im Hafen von Menethil.","Nach Menethil kommt Ihr, indem Ihr Dun Algaz durchquert, anschließend nordwärts der Straße folgt und Euch dann an der Biegung westwärts haltet.","Wir verlassen uns auf Euch, <Name>. Ihr dürft nicht scheitern."}, {"Durchquert Dun Algaz.","Tötet 8 Späher der Dragonmaw und 6 Grunzer der Dragonmaw.","Meldet Euch bei Valstag Ironjaw im Hafen von Menethil."}},
[456] = {"Das Gleichgewicht der Natur", {"Seid gegrüßt, <Name>. Ich bin Konservator Ilthalaine. Meine Aufgabe in Shadowglen ist es, für den Erhalt des Gleichgewichts der Natur zu sorgen.","Die Frühjahrsniederschläge waren dieses Jahr besonders heftig, was dafür sorgte, dass einige Tiere des Waldes gediehen, während andere darunter litten.","Unglücklicherweise sind die Bestände an Nachtsäblern und Distelebern zu stark angewachsen. Shadowglen kann nur eine gewisse Menge Futter für diese Tiere erzeugen. Zieht los, <Klasse>, und dünnt die Eber- und die Säblerpopulation etwas aus, auf dass die Harmonie der Natur erhalten bleibt."}, {"Tötet 7 junge Nachtsäbler sowie 4 junge Disteleber und kehrt dann zu Konservator Ilthalaine zurück."}},
[457] = {"Das Gleichgewicht der Natur", {"Die Jungbestände einiger Kreaturen hier im Shadowglen auszudünnen, war ein guter Anfang, <Name>, aber es gibt immer noch etwas zu tun.","Die Ressourcen des Waldes werden sich zu schnell erschöpfen, wenn sich niemand um das Problem kümmert. Mutter Naturs Wildtiere zu töten, ist ein notwendiges Übel zum Besten von allen, die sich das Land teilen. Begebt Euch in den Wald und erschlagt im Namen des Gleichgewichts räudige Nachtsäbler und Disteleber."}, {"Konservator Ilthalaine verlangt von Euch, 7 räudige Nachtsäbler und 7 Disteleber zu töten."}},
[458] = {"Die Beschützerin des Waldlandes", {"Was ein Glück, dass Ihr hier seid, <Klasse>. Vom Wispern der Waldgeister wurden mir seltsame Neuigkeiten zugetragen.","Die geheimnisvolle Beschützerin des Waldlandes, Tarindrella, ist nach Shadowglen zurückgekehrt. Jahrelang war die Gegenwart der Dryade in den Wäldern Kalimdors nicht zu spüren. Sicherlich stimmt irgendetwas nicht, wenn sie jetzt wieder in diese Gegend zurückgekommen ist.","Sucht Tarindrella und findet heraus, was sie in unseren Hain führt. Eine der Schildwachen berichtete, sie südwestlich von Aldrassil gesehen zu haben."}, {"Sucht die Dryade, die als Tarindrella bekannt ist."}},
[459] = {"Die Beschützerin des Waldlandes", {"Etwas Böses braut sich im Wald von Teldrassil zusammen. Schaut Euch die Hügel an, in denen die friedlichen Furbolgs früher hausten. Sie haben ihre Heime verlassen und sammeln sich unter dem Banner des Knarzklauenstamms.","Nur die Verderbtheit des bösartigen Teufelsmooses könnte eine solche Wandlung geschehen lassen. Die Grells und Grellkin suchen die Gegend heim und bedrohen die Einwohner von Shadowglen.","Spürt diese Grells und Grellkins auf, <Name>, und überzeugt Euch, ob sie wirklich unter dem Einfluss des bösartigen Teufelsmooses stehen."}, {"Sammelt 8 Stücke Teufelsmoos und bringt sie zu Tarindrella."}},
[460] = {"In Stücken begraben", {"Ihr seht nicht aus wie ein Moderfell. Gut. Ich hatte die Hoffnung schon aufgegeben...","Mein Name ist Alaric und ich war einst Thules oberster Diener und Leibwächter. Er nutzte seine Magie, um mich vor der Pest des Untodes zu \"schützen\", ein schöner Schutz... denn sein Zauber bürdete mir ein Unleben auf, dem ich nicht entrinnen kann. Und nachdem er diese verfluchten der Moderfellgnolle erschaffen hatte, hat Thule mich geköpft... und meinen Kopf den Gnollen zum Spielen gegeben!","Bitte, bringt mich zu meinem Körper. Ich glaube, die Gnolle haben ihn hier in der Burg Fenris, nahe den Stallungen, in einem namenlosen Grab verscharrt."}, {"Bringt Alarics Kopf zu seinem Grab."}},
[461] = {"Die versteckte Nische", {"Ich kenne eine versteckte Nische in der Burg, die einen Gegenstand enthält, der für Thule und seine alten Kollegen sehr wichtig ist. Außerdem enthält sie noch etwas, das Ihr nützlich finden könntet. Bringt mich dorthin!","Die Nische ist im Südwestturm der Burg, hinter einem staubigen Regal.","Nehmt meinen Körper mit, aber lasst die Stiefel hier. Die haben mir sowieso nie richtig gepasst."}, {"Bringt Alaric zur versteckten Nische."}},
[463] = {"Der Sumpfhüter", {"Ihr habt vom Sumpfhüter gehört? Ihr sucht doch nicht etwa nach ihm, oder? Nun, ich behaupte ja, Ihr seid verrückt, wenn dem so ist, aber wer bin ich, dass ich <einen Narren/eine Närrin> davon abhalte, in den Tod zu gehen?","Falls Ihr ihn sucht - hmmm, ich habe gehört, er sei in den Marschen, ostwärts von dort, wo die Straße nach Dun Modr abzweigt. Er lungert dort bei den Kroks und Schlimmerem herum!","Ach, und lasst Euer Geld besser hier. Ihr werdet es dort, wo Ihr hingeht, nicht brauchen... und Ihr wollt doch sicher nicht, dass der alte Grünling sich an Eurem Gold einen Zahn ausbricht, wenn er Euch mittendurch beißt."}, {"Findet den Sumpfhüter."}},
[464] = {"Kriegsflaggen", {"Der orcische Dragonmawklan war am Ende des zweiten Krieges so gut wie vernichtet, nachdem sie die Kontrolle über ihre Schoßtierchen, die roten Drachen, verloren hatten. Die Drachen waren nicht sehr freundlich zu ihren früheren Herren und machten gnadenlos Jagd auf die Dragonmaw. Die wenigen, die überlebten, flohen aus Grim Batol in die Berge östlich von Whelgars Ausgrabungsstätte.","Jetzt haben sie an Stärke gewonnen und besitzen ein Lager nordöstlich von Menethil.","Wir wollen, dass Ihr dieses Lager angreift. Sammelt und bringt mir ihre Kriegsflaggen als Beweis."}, {"Bringt Captain Stoutfist 8 Kriegsflaggen der Dragonmaw."}},
[465] = {"Nek'roshs Schachzug", {"Während Ihr die Dragonmaw bekämpft habt, haben wir mehr über deren Pläne herausgefunden.","Nek'rosh hat Katapulte gebaut und beabsichtigt, Menethil zu belagern! Und dank der Kämpfe in Dun Algaz sind wir effektiv von Verstärkungen abgeschnitten!","Ihr müsst diese Katapulte zerstören. Sie befinden sich im Lager der Dragonmaw.","Hier, nehmt diesen Zunder. Kämpft Euch zu den Katapulten durch und lasst sie mit dem Zunder in Flammen aufgehen.","Und nachdem Ihr das getan habt, findet und tötet Nek'rosh. Wir müssen dieser Dragonmawbestie den Kopf abschlagen."}, {"Zerstört die Katapulte der Dragonmaw."}},
[466] = {"Auf der Suche nach Pyrophor", {"Ich habe gehört, im Sumpfland wurde in einer Höhle nordwestlich vom Algaz-Tor ein seltenes Erz namens Pyrophor gefunden. Pyrophor ist instabil und schwer anzuwenden, ohne sich dabei selbst in die Luft zu sprengen, aber ich bin mir sicher, wenn man es auf die richtige Art und Weise verhüttet, könnte man daraus Kanonenkugeln mit einer ziemlichen Durchschlagskraft herstellen.","Besorgt mir Pyrophorerz und ich finde jemanden, der mutig genug ist, damit zu experimentieren.","Oh, und um dieses Erz aus einer Ader zu schürfen, müsst Ihr Euch mit Bergbau auskennen. Wenn dem nicht so ist, dann... nehmt einen Freund mit in diese Höhle, der es tut!"}, {"Bringt 6 Ladungen Pyrophorerz zu Pilot Stonegear nach Dun Morogh."}},
[467] = {"Stonegears Suche", {"Pilot Stonegear liebt seine Belagerungsmaschine, wie jeder Pilot es auch sollte! Und er ist immer begierig, neue Wege zu finden, die Feuerkraft seines Panzers zu vergrößern!","Er hat Gerüchte über ein seltenes Erz gehört und sucht jetzt nach jemandem, der es für ihn auftreibt. Wenn es Euch also in den Fingern juckt, dann sucht Stonegear in Steelgrills Depot in Dun Morogh auf und schaut mal, was er will."}, {"Sprecht mit Pilot Stonegear."}},
[468] = {"Meldung bei Gebirgsjäger Rockgar", {"Gebirgsjäger Rockgar sucht nach Freiwilligen für eine Mission durch Dun Algaz und in das Sumpfland hinein.","Wenn Ihr also Lust habt, Euch Eure Stiefel in dieser sumpfigen Gegend im Norden schmutzig zu machen, dann sprecht mit Rockgar in Dun Algaz. Dun Algaz liegt weiter die Straße entlang im Norden, hinter dem Wachturm."}, {"Sprecht mit Gebirgsjäger Rockgar."}},
[469] = {"Tägliche Lieferung", {"Seid gegrüßt!","Ihr seid nicht zufällig in Richtung Menethil unterwegs? Falls nicht, würde ich Euch empfehlen, dort Halt zu machen, denn es ist ein guter Ort, um Waren zu kaufen oder etwas trinken zu gehen und sich auszuruhen.","Tja, wenn Ihr also zufälligerweise in diese Richtung unterwegs seid, dann habt Ihr doch sicher nichts dagegen, dieses Bündel Krokiliskenhäute zu James Halloran, dem Gerber, zu bringen?","Ich würde es ja selbst tun, aber meine Frau ist in letzter Zeit ziemlich launisch, und ich würde es gern so lange wie möglich aufschieben, ihr zu begegnen."}, {"Liefert das Bündel Krokiliskenhäute bei dem Gerber James Halloran im Hafen von Menethil ab."}},
[470] = {"Stochern im Brühschlamm", {"Bei meinem wöchentlichen Besuch an Ironbeards Grabmal wurde ich von grässlichen, triefenden Brühschlammern angegriffen! Natürlich geriet ich in Panik.","Ich habe meine Tasche nach einem geworfen, aber das zeigte keinerlei Wirkung! Der Brühschlammer saugte sie einfach auf... Glücklicherweise gab mir das genug Zeit wegzulaufen.","Die schlechte Nachricht ist, dass ich dringend den Inhalt meiner Tasche benötige!","Ich weiß nicht, nach welchem Brühschlammer ich meine Tasche geworfen habe; sie sehen alle gleich aus! Ironbeards Grabmal liegt nordöstlich von hier, in der Nähe von Dun Modr. Ich danke Euch wirklich sehr!"}, {"Einer der Brühschlammer bei Ironbeards Grabmal hat Sidas Tasche. Bergt sie und bringt sie ihr zurück in den Hafen von Menethil."}},
[471] = {"Lehrlingspflichten", {"Ich habe meinen Lehrling in die Marschen geschickt, um einige unserer berühmten riesigen Sumpflandkrokilisken zu häuten. Als er nicht zurückkam, bin ich losgezogen, um ihn zu suchen... Sagen wir einfach, es war kein schöner Anblick.","Die Schwester sagt, er könnte durchkommen, aber derweil brauche ich immer noch meine Häute!","Und wenn Ihr noch ein paar von den Monstern töten wollt, hätte ich nichts dagegen, nach allem, was sie getan haben. Ihr findet die Kroks, wenn Ihr die Küste entlang nach Norden geht, hinter den Hütten der Murlocs."}, {"Sammelt 6 riesige Krokiliskenhäute und bringt sie zu James Halloran in den Hafen von Menethil."}},
[472] = {"Der Fall von Dun Modr", {"Dun Modr ist an die Dunkeleisenzwerge gefallen!","Meine Wunden sind schwer, <Klasse>. Ein Großteil meines Regiments wurde getötet! Diese Rohlinge der Dunkeleisenzwerge haben uns angegriffen, bevor wir uns nach dem Überfall auf den Thandol-Übergang wieder sammeln konnten.","Unser Anführer, Longbraid, blies zum Rückzug. Als wir die Stadt verließen, wurde ich von einer verirrten Axt in den Rücken getroffen und alles wurde schwarz um mich.","Ich wachte hier in Menethil auf, aber ich fürchte um meine Kameraden. Ich hege allerdings noch Hoffnung. Vielleicht ist Longbraid noch am Leben! Seht nach, ob Ihr ihn in der Nähe von Dun Modr finden könnt, <Volk>!"}, {"Sucht nahe der Stadt von Dun Modr nach Longbraid."}},
[473] = {"Meldung bei Captain Stoutfist", {"<Name>. Der Dragonmawklan der Orcs zeigt nicht nur in Algaz Stärke. Captain Stoutfist wünscht mit denen zu sprechen, die Hilfe gegen diese Orcs anbieten wollen. Er ist in der Burg, im Kriegsraum."}, {"Sprecht mit Captain Stoutfist."}},
[474] = {"Sieg über Nek'rosh", {"Jetzt, nachdem die Katapulte abgebrannt wurden, sucht und tötet Häuptling Nek'rosh."}, {"Tötet Häuptling Nek'rosh.","Bringt Nek'roshs Kopf zu Captain Stoutfist."}},
[475] = {"Ein beunruhigender Wind", {"Ein beunruhigender Wind weht durch den Wald.","Gaerolas Talvethren dient den schlafenden Druiden der Kralle in den Grabhügeln von Ban'ethil als Großer Aufseher. Seine Pflicht als auserwählter Beschützer der Schlafenden ist es, deren Sicherheit zu gewährleisten, auf dass sie ihrem Pakt mit Ysera gerecht werden.","Aber es gab länger keine Nachricht von Gaerolas, und ich werde etwas nervös. Reist nach Osten, zum Dorf Starbreeze, und bringt mir einen Bericht von Gaerolas, auf dass ich meine Sorgen begraben kann, da ich weiß, dass meine träumenden Brüder sicher schlummern."}, {"Sucht Gaerolas Talvethren im Dorf Starbreeze auf."}},
[476] = {"Die Verderbnis der Knarzklauen", {"Der Knarzklauenstamm ist verderbt worden!","Die einstmals friedliebenden Furbolgs haben sich gegen die Beschützer des Waldes gewandt. Sie haben mich hinterrücks überfallen, als ich auf dem Weg zum Grabhügel von Ban'ethil war und liefen weiter, um Starbreeze zu plündern. Ursal der Raufer, ihr Häuptling, benutzt die bösen Mächte des Teufelsmooses, um sie wahnsinnig werden zu lassen.","Meine Wunden sind zu schwer, ich kann nicht zu Athridas zurückkehren und ihm diese schlimme Nachricht überbringen. Junger <Klasse>, diese Aufgabe bleibt Euch überlassen. Wir können nur hoffen, dass die verrückten Knarzklauen noch nicht bis nach Ban'ethil vorgedrungen sind!"}, {"Kehrt zu Athridas Bearmantle nach Dolanaar zurück."}},
[477] = {"Grenzübergänge", {"Wie Ihr vielleicht bereits wisst, die Stadt Ambermill ist weiterhin eine Quelle der menschlichen Opposition, die nötige Unterstützung erhalten sie meist durch die Hexer von Dalaran.","Ich weiß nicht, wie ihr Plan aussieht, aber ihr Interesse an einem Hinterwäldler-Dorf wie Ambermill zeigt mir, dass es von größerer Bedeutung sein muss.","Von Hillsbrad aus haben sie wagenweise Kisten geliefert. Viele dieser Vorräte werden in ein kleines Camp nördlich von Pyrewood gebracht. Bringt mir den Inhalt einer dieser Kisten."}, {"Holt den Inhalt einer der Kisten von den Hexern aus Dalaran. Ihr findet Pyrewood im Süden."}},
[478] = {"Karten und Runen", {"<Schnell entnehmt Ihr den Anhänger aus der Kiste und setzt den Deckel wieder auf.>"}, {"Bringt den seltsamen Anhänger zu Schattenpriester Allister in dem Grabmal."}},
[479] = {"Ambermill-Nachforschungen", {"Dalar versucht die Quelle für die Zaubereien der Hexer zu finden. In der Zwischenzeit müssen wir ihren Fortschritt mit allen Mitteln aufhalten...","Alle Herbeizauberer, Magier und Beschützer tragen mit Sicherheit ihre Anhänger. Nehmt sie ihnen ab und bringt sie hierher.","Folgt der Hauptstraße in südlicher Richtung und nehmt dann die östliche Abzweigung nach Ambermill."}, {"Beschafft 8 Anhänger aus Dalaran für Schattenpriester Allister beim Grabmal."}},
[480] = {"Der Wirker", {"Ein Erzmagier von großer Macht leitet die Reaktivierung des Leyenergieknotens. Angesichts ihres Fortschritts liegt unsere einzige Hoffnung, sie noch aufzuhalten, darin, ihn zu töten. Das ist eine schwierige Tat, denn ohne Zweifel wird er von anderen Magiern und Wärtern beschützt.","Ihr müsst ihn schnellstens erledigen. Bringt mir seinen Stab, sobald er tot ist. Geht jetzt."}, {"Tötet den Erzmagier von Dalaran und bringt seinen Stab zu Schattenpriester Allister in dem Grabmal."}},
[481] = {"Dalars Analyse", {"Bringt den Anhänger zu Dalar Dawnweaver, vielleicht kann er Licht in das Dunkel der Hexerpläne bringen."}, {"Bringt den mit Runen gravierten Anhänger zu Dalar Dawnweaver in dem Grabmal."}},
[482] = {"Dalaran Absichten", {"Natürlich! Wie konnte ich das vergessen?","Ambermill war einer der Orte, die von den Kirin Tor gekennzeichnet wurden, weil dort ein ruhender Leyenergieknoten liegt. Mit Sicherheit haben die Hexer vor, diesen Knoten wiederzuerwecken und seine Energien für etwas Größeres zu nutzen.","Das dürfen wir nicht zulassen...","Um die Leyenergien des Gebietes zu aktivieren, bedarf es einer großen Macht. Wir können sie aufhalten, indem wir die Anhänger der Hexer an uns nehmen.","Bringt diese Information zu Allister, er wird dann über das weitere Vorgehen entscheiden."}, {"Sprecht mit dem Schattenpriester Allister in dem Grabmal."}},
[483] = {"Die Relikte des Erwachens", {"Eindringlinge der Knarzklauen wurden beim Überfall im Westen des Ban'ethil-Grabhügels gesehen.","Wenn wir ihnen nicht zur Hilfe kommen, werden die schlummernden Druiden auf ewig im Smaragdgrünen Traum gefangen gehalten, vollkommen ahnungslos ihrem Schicksal ausgeliefert. Das empfindliche Ritual des Großen Schlafes kann ohne die Relikte des Erwachens nicht unterbrochen werden.","Reist zum Höhlenbau und bringt den Rabenkrallentalisman, den schwarzen Federkiel, den Saphir des Himmels sowie die Rune des Nistens herbei. Die Druiden bewahren sie in heiligen Truhen auf. Bringt sie zu mir, damit ich das Weck-Ritual vorbereiten kann."}, {"Bringt die Relikte des Erwachens zu Athridas Bearmantle nach Dolanaar."}},
[484] = {"Häute junger Krokilisken", {"Ich bin James Halloran, der Gerber. Meine gegerbten Krokiliskenhäute sind nah und fern begehrt, Lieferungen gehen per Schiff zu allen Häfen der Welt.","Habt Ihr Einar getroffen? Er ist einer der Jäger, die mir Krokiliskenhäute bringen.","Mir fehlen zurzeit weichere Häute, wie die von jungen Krokilisken.","Da ich bald keine mehr habe, bin ich bereit, Euch einen Spitzenpreis für einen Stapel der Häute zu bezahlen. Ihr könnt die jungen Kroks in den Marschen gleich außerhalb der Stadt finden."}, {"Besorgt Euch 4 Häute junger Krokilisken für James Halloran im Hafen von Menethil."}},
[485] = {"Sucht OOX-09/HL!", {"Ihr habt ein merkwürdiges Gerät aus Metall entdeckt, das die Form eines Eis hat. Als Ihr einen der Knöpfe berührt, erwacht das Ei zu Leben und verwandelt sich in eine Art Gnom-Roboter-Apparat! Aus dem Inneren des Roboteis erklingt eine rasselnde Stimme.","\"Mein Name ist Oglethorpe Obnoticus und mein Leitroboter ist abgestürzt! Ich werde Euch belohnen, wenn Ihr ihn aufspürt. Bringt dem Roboter bitte diesen Signalgeber!\"","\"Ich habe jetzt die Koordinaten des Roboters für Euch berechnet. Er ist in der Nähe von Skulk Rock abgestürzt, dort, wo der Fluss entspringt!\""}, {"Bringt das Notsignal zu Oglethorpes Leitroboter an der Quelle des Flusses im Hinterland, nahe Skulk Rock."}},
[486] = {"Ursal der Raufer", {"Jetzt ist die Zeit gekommen, die Druiden der Kralle zu retten. <Name>, sollten wir jetzt versagen, sind sie für immer im Schlaf verloren.","Ich werde die Relikte des Erwachens vorbereiten und das Ritual durchführen. Damit meine Arbeit Wirkung zeigen kann, muss die verfluchte Bestie, die für diese schreckliche Situation verantwortlich ist, getötet werden. Erst dann ist das Ritual vollkommen.","Es war Ursal der Raufer, der sich in die Berufung unserer Brüder eingemischt hat, und er ist es auch, der nun bezahlen muss, damit sie befreit werden können. Begebt Euch nach Südwesten zur Höhle der Knarzklauen und erschlagt ihn."}, {"Tötet Ursal den Raufer und kehrt zu Athridas Bearmantle nach Dolanaar zurück."}},
[487] = {"Die Straße nach Darnassus", {"Die Straße nach Darnassus muss sicher bleiben. Reisende, die von Dolanaar nach Darnassus fuhren, haben von ruchlosen Angriffen der verderbten Furbolgs des Knarzklauenstammes berichtet.","Jeden Tag gelangen auf diesem Weg wichtige Nachrichten und auch Waren von und nach Darnassus. Es darf einfach nicht sein, dass eine schurkische Bande von Heiden alle terrorisiert.","<Klasse>, bewaffnet Euch im Namen des Heiligen Waldes. Ihr Bau liegt irgendwo unterhalb dieser Position. Erschlagt 6 Wegelagerer der Knarzklauen und berichtet mir dann."}, {"Erschlagt 6 Wegelagerer der Knarzklauen und kehrt zu Schildwache Amara Nightwalker außerhalb Dolanaars zurück."}},
[488] = {"Zenns Aufträge", {"Ihr seid bereit zu arbeiten, wie ich sehe. Ihr habt Glück, denn es vergeht kein Tag, an dem ich mir nicht wünschte, ein junger <Klasse> wäre hier, der mir zur Hand geht.","Ihr seht, <Name> , ich kann Euch zu Eurem Glück verhelfen und mit Dingen versorgen, von denen Ihr nie zu träumen wagtet. Damit dies jedoch geschieht, müsst Ihr mir gewisse Dinge beschaffen.","Meine Angelegenheiten im Wald erfordern oft gewisse... Reagenzien. Bringt mir 3 Nachtsäbler-Fangzähne, 3 Strigideulenfedern und 3 Streifen Waldweberspinnenseide.","Das soll unser kleines Geheimnis bleiben, <Volk>."}, {"Bringt Zenn Foulhoof außerhalb von Dolanaar 3 Nachtsäblerfangzähne, 3 Strigideulenfedern und 3 Streifen Waldweberspinnenseide."}},
[489] = {"Sucht Befreiung!", {"Der Rat des Waldes hat die Meldung erhalten, dass Ihr Zenn Foulhoof behilflich gewesen seid. Der Satyr ist ein Feind des Waldes. Als <Volk> müsstet Ihr eigentlich mehr Verstand haben, als den Wald zu entweihen, indem Ihr Kreaturen der Natur tötet.","Ihr müsst Eure Ehre in den Augen des Rates wiederherstellen, wenn Ihr weiterhin ein Freund von Teldrassil bleiben wollt.","Erteilt Foulhoof eine Lektion, dann habt ihr Eure Schuld beglichen. Teufelszapfen sind verderbte Samen, die von Bäumen fallen. Sie sondern grünen Rauch ab.","Gebt Foulhoof einige davon. Er wird denken, dass Ihr ihm eine harmlose Leckerei gebracht habt."}, {"Sammelt 3 Teufelszapfen und gebt sie Zenn Foulhoof außerhalb von Dolanaar."}},
[491] = {"Zauberstab an Bethor", {"Thule erzählte mir einmal, dass er und zwei Kollegen vor langer Zeit einen magischen Zauberstab herstellten, der aus den Lehrlingszauberstäben zusammengewoben wurde, den jeder Magier besaß. Dieser Zauberstab war ein Symbol ihrer Freundschaft, und obwohl Thule ihn hinterher verachtete, konnte er sich doch weder davon trennen noch ihn den anderen überlassen. Also stahl er den Zauberstab und versteckte ihn in Burg Fenris.","Hier. Bringt den Zauberstab zu Bethor Iceshard, einem Freund von Thule aus vergangenen Zeiten. Ich bin sicher, er möchte ihn haben."}, {"Bringt den gewebten Zauberstab zu Bethor Iceshard in das Magieviertel von Undercity."}},
[492] = {"Eine neue Seuche", {"Berichten der Todeswachen zufolge wurde gerade ein weiterer dieser dummen Zwergen-Gebirgsjäger gefangen genommen. Die Todeswächter benutzen gern den Keller der Taverne Zur Galgenschlinge als Übergangszelle, bis man sich \"richtig\" um die Gefangenen kümmern kann.","Warum... kümmert... Ihr Euch nicht um den gefangenen Gebirgsjäger und schaut, wie er das Spezialgetränk verträgt, das ich für ihn gemacht habe? Das ist nur ein kleiner Eindruck dessen, was die dunkle Fürstin für den Rest von Azeroth geplant hat."}, {"Bringt Johaans Spezialgetränk zum gefangenen Gebirgsjäger."}},
[493] = {"Reise in das Vorgebirge von Hillsbrad", {"Die Königliche Apothekervereinigung steht unter enormem Druck seitens der dunklen Fürstin, eine neue Seuche zu entwickeln. Wir arbeiten sehr hart und haben bereits einige Fortschritte gemacht. Wir denken, dass der Erfolg sich umso schneller einstellen wird, wenn wir innerhalb der Vereinigung Informationen austauschen.","Aus diesem Grunde möchte ich, dass Ihr meine letzten Funde an Apotheker Lydon in Tarrens Mühle überbringt, einem kleinen Städtchen, das sich im Vorgebirge von Hillsbrad befindet. Die Reise wird sehr lange dauern.","Begebt Euch Richtung Süden und bleibt auf den Straßen. Beachtet die Schilder auf das Genaueste!"}, {"Bringt die Funde von Apotheker Renferrel zu Apotheker Lydon nach Tarrens Mühle im Vorgebirge von Hillsbrad."}},
[494] = {"Zeit zum Zuschlagen", {"Schweigt und passt auf, <Klasse>.","Ich wurde an diesen Ort geschickt, um die Stadt Hillsbrad zu sondieren. Es handelt sich um eine Aufklärungsmission. Dabei ist von äußerster Wichtigkeit, dass Ihr sofort die Hochexekutorin Darthalia in Tarrens Mühle benachrichtigt. Bestellt ihr, ich, Todespirscherin Lesh, lasse ihr folgende Nachricht ausrichten:","\"Der Schrei des Raben locket vom Westen her.\"","Folgt der Straße gen Osten und beachtet die Schilder genau. Nun aber schnell! Macht Euch auf den Weg nach Tarrens Mühle. Es ist Eile geboten!"}, {"Reist nach Tarrens Mühle, um die Nachricht von Todespirscherin Lesh an Hochexekutorin Darthalia zu überbringen."}},
[495] = {"Die Krone des Willens", {"Mug'Thol und seine Ogerkriegsmeute wurde nach Alterac geschickt, um die Krone des Willens für Lady Sylvanas zu holen. Anstatt sie zurückzubringen, hat der Idiot allerdings versucht, sie aufzusetzen... Oger können wohl einfach keinen Dingen widerstehen, die glitzern und schimmern, nehme ich an.","Seine stümperhaften Aktionen sind allerdings zu einem Problem geworden. Die Krone des Willens hat ihm geholfen, der Besessenheit zu widerstehen, die ihm auferlegt wurde, damit er Lady Sylvanas dient.","Die dunkle Fürstin möchte ein Exempel statuieren. Begebt Euch nach Tarrens Mühle in Hillsbrad und meldet Euch bei Melisara, meinem dortigen Offizier."}, {"Meldet Euch bei Melisara in Tarrens Mühle im Vorgebirge von Hillsbrad."}},
[496] = {"Elixier des Leidens", {"Ah, und wieder ein ekelhafter Tag in Tarrens Mühle. Diese ganze gesunde Luft verdirbt mir gründlich die Laune, <Name>.","Je schneller wir die Menschen hier erkranken lassen können, desto besser. Ich habe intensive Studien über mögliche tödliche Zutaten für meine Gebräue durchgeführt, aber ich habe nicht die Zeit, sie alle zu sammeln.","Wenn Ihr Euch nützlich machen wollt, dann besorgt mir Folgendes: 10 Graubärzungen sowie das sehr seltene und schwer zu findende Krabblersekret. Bären und Krabbler könnt Ihr gleich außerhalb von Tarrens Mühle finden."}, {"Apotheker Lydon aus Tarrens Mühle möchte 10 Graubärzungen und etwas Krabblersekret."}},
[498] = {"Die Rettung", {"Ich und einige andere Krieger wurden vom Kriegshäuptling geschickt, um ein Objekt aus Durnholde zu holen und dieses so genannte Syndikat zu untersuchen.","Als wir nach Durnholde kamen, um uns umzusehen, wurden wir von einer großen Anzahl der Menschen überrascht. Wir haben mutig gekämpft, aber allein ihre Anzahl war so überwältigend groß, um uns zu besiegen. Sie haben einige von uns als Gefangene genommen, aber ich konnte entkommen.","Durnholde befindet sich nicht weit südöstlich von uns, nur über den Fluss. Drull und Tog'thar werden dort gefangen gehalten, Ihr müsst sie retten."}, {"Krusk in Tarrens Mühle braucht Euch, um Drull und Tog'thar aus der Burg Durnholde zu befreien."}},
[499] = {"Elixier des Leidens", {"Es wird einen Moment dauern, diese Komponenten zu vermengen, damit ein, wie ich es nenne, Elixier des Leidens entsteht.","Dann mal los! Ah, es gibt doch nichts Schöneres in diesem elendiglichen Vorgebirge, als ein wenig Leiden in die Welt zu bringen.","Lasst uns mal überprüfen, ob unser kleiner Trank auch seine gewünschte Wirkung hat.","Gebt meiner Assistentin Umpi einen kleinen Schluck von dem, was die dunkle Fürstin sowohl für Menschen als auch für die Geißel geplant hat."}, {"Gebt Umpi das Elixier des Leidens"}},
[500] = {"Crushridge-Kopfgeld", {"Die Crushridge-Oger haben einen Ogerhort im Alteracgebirge nahe der verfallenen Stadt Alterac gebaut. Meine Späher berichteten mir, dass sie auch die Ruinen da erobert haben.","Wir dürfen aber nicht zulassen, dass sie sich da fest einnisten. Falls sie sich dort zu sicher fühlen, werden sie bestimmt ins Vorgebirge eindringen und wären dann praktisch vor unserer Haustür!","Begebt Euch nach Norden in das Alteracgebirge auf Ogerjagd. Bringt mir die schmutzigen Knöchelknochen, die sie tragen, und Ihr habt Euch ein hübsches Kopfgeld verdient."}, {"Sammelt 9 schmutzige Knöchelknochen von Crushridge-Ogern im Alteracgebirge. Bringt sie zu Marshal Redpath in Southshore."}},
[501] = {"Elixier der Schmerzen", {"Oh, <Name>..., in Hillsbrad blühen die Blumen und die Luft ist so klar und rein. Ich muss in diesem ganzen Elend baden. Ich sehe aus meinem verdreckten Fenster und sehne den Tag herbei, an dem unsere neue Seuche dieser Welt ihren wohlverdienten Tod bringt.","So gern ich auch den ganzen Tag hier herumsitzen und mit Grübeln verbringen würde, meine Pflicht gegenüber der dunklen Fürstin ruft mich. Würdet Ihr mir helfen? Meine Forschungen führen mich zu der Annahme, dass sich aus Berglöwenblut ein vorzüglicher Todestrank herstellen lässt. Bringt mir etwas davon, dann können wir ein bisschen Spaß haben."}, {"Bringt 10 Phiolen mit Berglöwenblut zu Apotheker Lydon in Tarrens Mühle."}},
[502] = {"Elixier der Schmerzen", {"Ah, es geht doch nichts über den Geruch von frischem Blut im Vorgebirge!","Dann lasst uns eine Prise davon und ein wenig hiervon vermischen - und der einzig wahre Spaß kann beginnen! Die Kreation aus diesem Blut möchte ich Elixier der Schmerzen nennen. Wenn meine Berechnungen korrekt sind, sollte dieses Gebräu der Anfang von etwas Wunderbarem für Lady Sylvanas sein. Aber wir müssen erst mal einen Testlauf machen.","Bringt dieses Elixier zum nördlichen Hof in den Feldern von Hillsbrad im Südwesten. Lasst uns sehen, wie Stanley, dem kleinen Hund von Bauer Ray, dieses 'Leckerli' so schmeckt."}, {"Füttert Stanley mit dem Elixier der Schmerzen."}},
[503] = {"Gol'dir", {"Soweit ich das aus den Dokumenten, die Ihr herbrachtet, ersehen kann, wird Gol'dir in einem großen Syndikatsstützpunkt im Norden festgehalten, höchstwahrscheinlich im Alteracgebirge. Er ist schon zu lange dort gefangen und muss endlich befreit werden."}, {"Befreit Gol'dir und kehrt dann zu Krusk in Tarrens Mühle zurück."}},
[504] = {"Crushridge-Kriegstreiber", {"Nun, da Ihr die Bekanntschaft der Crushbridge-Oger gemacht habt, möchte ich, dass Ihr ihnen so richtig zeigt, was eine Harke ist...","Geht zu den Ruinen von Alterac und sucht die Crushridge-Kriegstreiber. Ihr sollt eine große Anzahl von ihnen erschlagen - das ist der einzige Weg, um diese Schläger von dem Gebiet der Allianz fern zu halten."}, {"Erschlagt 15 Crushridge-Kriegstreiber und kehrt dann zu Marshal Redpath in Southshore zurück."}},
[505] = {"Auftragsmörder des Syndikats", {"Ich bin der neue Magistrat von Southshore. Ich wurde erst kürzlich ernannt, nachdem der vorherige Magistrat ermordet worden ist. Die Mörder wurden nie gefunden, aber unsere Nachforschungen haben ergeben, dass sie höchstwahrscheinlich vom Syndikat angeworben wurden - einer Gruppe von Dieben, angeführt von schurkischen Edlen des jetzt gefallenen Königsreichs von Alterac.","Das Syndikat hat ein Lager nördlich von Tarrens Mühle, das von der Horde besetzt ist, und unsere neuesten Hinweise führen genau in dieses Lager. Erschlagt die Syndikatsmitglieder, die Ihr dort vorfindet."}, {"Tötet 12 Schleicher des Syndikats und 8 Diebe des Syndikats, kehrt dann zurück zu Magistrat Henry Maleb nach Southshore."}},
[506] = {"Blackmoores Vermächtnis", {"Vor ein oder zwei Tagen erhielt ich Besuch vom Anführer dieses Syndikats. Sein Name war Aliden Perenolde und er nannte sich selbst Lord dieses Landes. Er vertraute mir seine Pläne an, die Orcs, die sich gegen ihn gewandt hatten, zu Sklaven zu machen, genau wie es einst auch sein Vater und Mentor, der geächtete Blackmoore, geplant hatte.","Aber viel wichtiger war, dass er seine Geliebte mitbrachte, ein zartes Mädchen namens Elysa. Um ihren Hals trug sie den Anhänger, den der Kriegshäuptling als sein Eigen wünscht.","Geht jetzt, ich gehe meine eigenen Wege, nachdem ich meine Geschäfte hier erledigt habe."}, {"Bringt die von Gol'dir gesammelten Informationen zu Krusk in Tarrens Mühle."}},
[507] = {"Lord Aliden Perenolde", {"Es ist unumstritten, dass Perenolde getötet werden und seine Verderbtheit ausgelöscht werden muss. Sein Herrenhaus liegt weit nördlich von hier, an den Ufern des Lordameresees. <Name>, tut Eure Pflicht, holt danach den Anhänger, den der Kriegshäuptling zu haben wünscht, und kehrt dann hierher zurück."}, {"Tötet Lord Aliden Perenolde und befragt seine Geliebte Elysa nach Tarethas Anhänger."}},
[508] = {"Tarethas Geschenk", {"Den Anhänger? Aber natürlich könnt Ihr ihn haben... Nehmt ihn! Nichts soll mich an ihn erinnern... nur bitte geht, bitte geht in Frieden..."}, {"Bringt Tarethas Anhänger zu Krusk nach Tarrens Mühle."}},
[509] = {"Elixier der Pein", {"Ein neuer Tag, ein neues Elixier. Wenn ich was zu sagen hätte, würde ich diese lächerlichen Menschen und die Geißel die Sache einfach unter sich ausmachen lassen. Sie scheinen ohnehin absolut darauf versessen zu sein, sich gegenseitig umzubringen.","Aber leider hat es sich unsere dunkle Fürstin in den Kopf gesetzt, das Ganze ein wenig zu beschleunigen. Also warum diese düsteren Tage nicht dazu nutzen und hier ein wenig Tod und Krankheit verbreiten?","<Name>, das ist mein Plan: Ihr macht Euch auf den Weg zur Nethander-Siedlung. Das ist südlich von Tarrens Mühle und östlich des Flusses und Southshore. Sammelt in den Feldern dort 6 Morastschnauzenkappen für mein nächstes Elixier."}, {"Bringt 6 Morastschnauzenkappen zu Apotheker Lydon in Tarrens Mühle."}},
[510] = {"Furchteinflößende Pläne", {"Unter diesen Dokumenten befindet sich eine Liste der benötigten Vorräte. Das sind unter anderem ganz banale Dinge (Nahrung, Waffen, Kleidung) wie auch etwas exotischere Ausrüstungsgegenstände (Masken, Öl, Gift).","Aber am auffälligsten ist die Dringlichkeit dieses Auftrags - diese Vorräte werden schnellstens benötigt, um eine Unternehmung zu starten, die in dem Dokument als 'wichtige Mission gegen Southshore' beschrieben wird."}, {"Bringt die furchteinflößenden Pläne zu Magistrat Henry Maleb nach Southshore."}},
[511] = {"Verschlüsselter Brief", {"Einer der Briefe auf diesem Tisch ist verschlüsselt und nicht zu ergründen."}, {"Bringt den verschlüsselten Brief zum Meister der Lehren Dibbs nach Southshore."}},
[512] = {"Tod der Edlen", {"Ich fürchte, die Führung, die hinter dem Syndikat steht, will Southshore unbedingt ruinieren. Wir müssen diese Anführer mit aller Kraft schlagen, bevor sie ihre Pläne in die Tat umsetzen können.","Geht zu den Ruinen von Strahnbrad, im Alteracgebirge im Norden. Wir glauben, dass das Syndikat dieses Gebiet als Hauptquartier benutzt.","Wir wissen, dass diese Bande von Halsabschneidern von ehemaligen Adligen von Alterac angeführt wird. Sie mögen sich als normale Agenten des Syndikats verkleiden, aber wenn Ihr sie tötet, werdet Ihr einen Edlen an dem Siegelring erkennen, den er trägt.","Bringt mir diese Siegelringe."}, {"Bringt 7 Alteracsiegelringe zu Magistrat Henry Maleb nach Southshore."}},
[513] = {"Elixier der Pein", {"Gerade dachte ich noch, ein weiterer Tag ohne jegliches Amüsement, und da kommt Ihr und bringt diese wunderbaren Morastschnauzenkappen.","Zuerst werde ich sie zu einem feinen Gemisch zusammenrühren. Dann werden wir Meisterapotheker Faranell in Undercity benötigen, damit er seine Paste des Verfalls hinzufügt, und dann geht der Spaß erst richtig los!","Hier, <Name>, nehmt dieses Morastschnauzengemisch und bringt es schnell zu Meister Faranell."}, {"Bringt das Morastschnauzengemisch zu Meisterapotheker Faranell in Undercity."}},
[514] = {"Brief an Stormpike", {"Ich habe einen Kollegen in Ironforge, der bei der Entschlüsselung dieses Briefes behilflich sein kann. Begebt Euch zur Bibliothek in der Halle der Forscher in Ironforge und sprecht mit Ausgrabungsleiter Stormpike. Richtet ihm aus, dass ich Euch geschickt habe, und zeigt ihm diesen Brief. Wenn es jemanden nördlich der Blackrockspitze gibt, der das hier lesen kann, dann ist er das."}, {"Bringt den geschickt verschlüsselten Brief zu Ausgrabungsleiter Stormpike nach Ironforge."}},
[515] = {"Elixier der Pein", {"Ein Morastschnauzengemisch? Das ist wirklich brillant! Warum ist mir das nicht eingefallen?","Lasst mich meine Paste des Verfalls diesem Teufelsgebräu hinzufügen.","Damit die verunreinigenden Agenzien in dieser Morastschnauzenmixtur aktiviert werden, benötigt Lydon starken Trollbluttrank, sowie einige Säbelflossenschuppen und Fetzenflossenaugen von der Südküste Hillsbrads. Er wird wissen, was zu tun ist, sobald Ihr alle Reagenzien zusammengetragen habt. Ich kann es kaum abwarten zu erfahren, wie sein Experiment ablaufen wird!"}, {"Bringt die Morastschnauzenmixtur, einen starken Trollbluttrank, 5 Säbelflossenschuppen und 5 Fetzenflossenaugen zu Apotheker Lydon nach Tarrens Mühle."}},
[516] = {"Beren's Peril", {"Ich habe Berichte erhalten, dass eine Gruppe Untoter sich zusammenrottet, um einen Angriff gegen uns vorzubereiten. Mit diesen Informationen können wir den Spieß umdrehen und sie zuerst angreifen, damit wäre ihr Plan erledigt.","Leider sind meine Informationen gelinde gesagt unvollständig. Laut Bericht verstecken sie sich an einem Ort, der als Beren's Peril bekannt ist. Ihr genauer Standort ist unbekannt, aber es scheint eine Höhle in den Hügeln zu sein, nahe einem Nest der Hexer von Dalaran.","Ich vertraue auf Euren Einfallsreichtum. Findet sie und macht alles dem Erdboden gleich."}, {"Findet zuerst Beren's Peril, tötet 6 Zwangsarbeiter von Rabenklaue sowie 6 Wächter von Rabenklaue und kehrt dann zu Schattenpriester Allister in das Grabmal zurück."}},
[517] = {"Elixier der Pein", {"Das neue Elixier ist endlich bereit für einen Test. Aber warum solch eine vollkommene, höchst ansteckende Seuche an einen einfachen Frosch oder Hund verschwenden? Wir sollten uns in Lady Sylvanas Namen eine kleine Freude erlauben.","Geht nach Dun Garok. Ihr kennt doch die Kasernen, in denen diese widerlichen kleinen Zwerge umhersausen. Ihr stehlt ein Fässchen Shindigger-Starkbier für mich. Sobald ich dieses Fässchen habe, können wir beide, Ihr und ich, eine kleine Party feiern, <Name>."}, {"Bringt ein Fässchen mit Shindigger-Starkbier zu Apotheker Lydon nach Tarrens Mühle."}},
[518] = {"Die Krone des Willens", {"Mug'Thol und seine Crushridge-Oger haben die Ruinen von Alterac überrannt. Ihm eine schwere Strafe zukommen zu lassen, ohne die Anzahl der Oger zu verringern, wird nicht einfach sein.","Wir machen sie erst weich und sehen dann weiter.","Verstanden? Gut. Dann geht."}, {"Tötet 14 Crushridge-Raufer für Melisara in Tarrens Mühle."}},
[519] = {"Die Krone des Willens", {"Targ, Muckrake und Glommus sind Mug’Thols Offiziere. Sie sind ihm loyal ergeben, aber wer weiß schon, was einen Oger loyal sein lässt.","Bringt mir ihre Köpfe, <Name>, und dann muss nur noch Mug'Thols Kopf, mitsamt Krone, rollen."}, {"Tötet Targ, Muckrake und Glommus und bringt ihre Köpfe zu Melisara nach Tarrens Mühle."}},
[520] = {"Die Krone des Willens", {"Nun, da Mug'Thols Offiziere tot sind, muss nur noch Mug'Thol selbst getötet und die Krone des Willens wieder zurückgebracht werden. Ich bin mir ziemlich sicher, dass er sie nur äußerst unwillig wieder hergeben wird. Außerdem wird er Euch wahrscheinlich bereits erwarten, in Anbetracht des... Ablebens... seiner Kumpanen.","Findet Mug'Thol und bringt mir seinen Kopf und die Krone. Der Crushridge-Widerstand wird damit beendet sein."}, {"Tötet Mug'Thol und bringt seinen Kopf mit der Krone des Willens zu Melisara nach Tarrens Mühle."}},
[521] = {"Die Krone des Willens", {"Es bleibt nur noch, die Krone des Willens zu Sharlindra nach Undercity zu bringen. Sie wird dafür sorgen, dass sie an Lady Sylvanas weitergeleitet wird. Ich warne Euch, auch nur daran zu denken, sie für Eure eigenen Zwecke zu verwenden, <Name>, Mug'Thols Schicksal sollte Beispiel genug sein."}, {"Bringt die Krone des Willens zu Sharlindra in Undercity."}},
[522] = {"Auftragsmördervertrag", {"Dies ist ein Vertrag für die Ermordung des Magistrats Henry Maleb. Er wurde mit einem unverwechselbaren Siegelring in rotem Wachs gestempelt und versiegelt."}, {"Bringt den Auftragsmörder-Vertrag zu Magistrat Maleb nach Southshore."}},
[523] = {"Ableben des Barons", {"Der Vertrag, den Ihr gebracht habt, wurde von Baron Vardus unterschrieben, einem Edlen aus dem ehemaligen Königreich von Alterac, und jemand der bekannt ist für seine Kaltherzigkeit und brutale Vorgehensweisen. Es überrascht mich nicht, dass jemand wie der Baron dahinter steckt.","Findet diesen Mann. Findet ihn und führt ihn seiner gerechten Strafe zu. Er befindet sich sicherlich irgendwo in den Schlupfwinkeln des Syndikats - vielleicht in den Ruinen von Strahnbrad oder irgendwo in dem Tal im Norden davon."}, {"Bringt den Kopf von Baron Vardus zu Magistrat Maleb nach Southshore."}},
[524] = {"Elixier der Pein", {"Nebenan halten die Todeswachen einige Bauern als Geiseln gefangen. Hochexekutorin Darthalia hatte vermutet, dass sich einer der Militärstrategen aus Stormwind unter ihnen versteckte. Anscheinend wurde dieser Militärstratege gestern Nacht tot in den Pestländern gefunden. Darthalia denkt ja immer so nett an mich und hat entschieden, dass die Gefangenen mir zu überlassen sind.","Ich denke, es ist höchste Zeit, dass wir für diese Bauern eine kleine Party schmeißen. Hört sich doch nach einer tollen Zeit an!","Stellt dieses besudelte Fässchen auf den Teppich in ihrem Zimmer. Seuchenbräu für alle!"}, {"Stellt das besudelte Fässchen auf den Teppich für die gefangenen Bauern."}},
[525] = {"Weitere Geheimnisse", {"Ich habe den Brief dechiffriert, aber ich weiß nicht, was ich davon halten soll. Ihr habt ihn unter Dieben im Vorgebirge von Alterac gefunden? Sehr seltsam...","Ihr solltet ihn zum Magistrat nach Southshore bringen. Auch wenn dieser Brief geheimnisvoll klingt, seine Stadt liegt Alterac am nächsten, wenn eine Bedrohung sich zusammenbraut, dann sollte gerade er davon erfahren."}, {"Bringt den entschlüsselten Brief zu Magistrat Maleb nach Southshore."}},
[526] = {"Lichtschmiedeblöcke", {"Ihr mögt zwar einen Lichtschmiedeblock in dieser Truhe gefunden haben, aber das reicht nicht aus, um irgendetwas von Nutzen daraus zu schmieden.","Ihr solltet zurückgehen und nach weiteren suchen. Vielleicht tragen die Murlocs in der Nähe des Schiffswracks Lichtschmiedeblöcke mit sich herum."}, {"Sammelt 5 Lichtschmiedeblöcke und kehrt dann zu Glorin Steelbrow im Sumpfland zurück."}},
[527] = {"Kampf um Hillsbrad", {"Steht gefälligst stramm, wenn Ihr mit mir sprecht, <Klasse>.","Ich stehe unter direktem Kommando von Varimathras. Wir sollen der menschlichen Plage Einhalt gebieten, bis unsere Apotheker eine neue Pest entwickeln können.","Unseren Informationen zufolge bietet Hillsbrad einen guten Angriffspunkt. Eure erste Aufgabe ist darauf gerichtet, die Nahrungsversorgung und Infrastruktur zu unterbrechen.","Begebt Euch nach Westen. Überfallt die nördlichsten Höfe der Felder von Hillsbrad. Und stellt sicher, dass die Landeigner, Bauer Ray und Bauer Getz, ebenfalls exekutiert werden."}, {"Tötet 6 Landarbeiter von Hillsbrad, 6 Bauern von Hillsbrad, Bauer Ray und Bauer Getz und meldet Euch dann wieder bei Darthalia in Tarrens Mühle."}},
[528] = {"Kampf um Hillsbrad", {"Die Berichte vom Angriff auf die nördlichen Höfe haben die Stadtbevölkerung in Panik versetzt. Vorzüglich.","Unsere Todespirscher melden, dass Arbeiter in die Felder ins südliche Hillsbrad flüchten. Diese Narren glauben doch tatsächlich, dass sie sich in den Obsthainen verstecken und so ihrer Verdammnis entkommen können.","Beweist ihnen das Gegenteil, <Name>. Kehrt zu den Feldern von Hillsbrad zurück und macht die feigen Arbeiter ausfindig. Vernichtet sie im Namen der dunklen Fürstin.","Weggetreten!"}, {"Tötet 15 Arbeiter von Hillsbrad und erstattet Bericht bei Darthalia in Tarrens Mühle."}},
[529] = {"Kampf um Hillsbrad", {"Die Menschen in Hillsbrad leisten unerwarteten Widerstand. Das Problem scheint darin zu bestehen, dass die Bewohner durch den örtlichen Schmied mit Waffen versorgt werden. Wir müssen alle Versorgungsmöglichkeiten abschneiden.","Eure Aufgabe ist es, Schmied Verringtan zu eliminieren. Tötet auch gleich seine Lehrlinge. Wenn Ihr schon dort seid, bringt auch gleich eine Eisenlieferung aus ihrem Vorratslager mit zurück. Nach der Eroberung von Hillsbrad werden diese Vorräte uns gelegen kommen, wenn wir mit der Verwüstung von Southshore beginnen."}, {"Tötet Schmied Verringtan und 4 Schmiedelehrlinge von Hillsbrad.","Besorgt eine Eisenlieferung und erstattet Bericht bei Darthalia in Tarrens Mühle."}},
[530] = {"Rache eines Ehemanns", {"Valdred, einst ein guter Freund von mir, ist der Mörder meiner Frau. Auch wenn ich keine Trauer empfinden kann, so sehne ich mich doch nach Rache. Wenn Ihr Valdred tötet und mir seine verfluchten Hände bringt, werde ich Euch belohnen.","Das Letzte, was ich hörte, war, dass er sich im südlichen Silberwald beim Greymane-Wall aufhält und in das Königreich von Gilneas flüchten will."}, {"Tötet Valdred Moray.","Bringt seine Hände zu Raleigh Andrean nach Undercity."}},
[531] = {"Vyrins Rache", {"Licht! Bringt solche Dinge nicht in die Lodge... das ist unhygienisch, unaussprechlich, un... un... unhygienisch!","Bringt es bitte einfach nur raus hier, ja? Niemand will Eure kleine blutige Trophäe sehen, nu wirklich nicht!"}, {"Bringt Old Sootys Kopf zu Vyrin Swiftwind in die Weitschreiter-Lodge."}},
[532] = {"Kampf um Hillsbrad", {"Hillsbrad ist ein einziges Chaos und wir sind der Meinung, dass die Stadt bald fallen wird. Nichtsdestotrotz müssen wir weiterhin an unserer Ruchlosigkeit festhalten bis der menschliche Abschaum ein für alle Mal von diesem Vorgebirge verschwunden ist.","Trotz ihrer überwältigenden Verluste sammeln die Menschen sich weiterhin um ihre Führungskraft und kämpfen weiter. Wir müssen ihren Anführer, Magistrat Burnside eliminieren und mit ihm den ganzen Stadtrat. Zerstört ihr heißgeliebtes politisches Dokument, die Proklamation von Hillsbrad, gleich mit. Und wenn Ihr schon dabei seid, stehlt das Stadtregister."}, {"Tötet Magistrat Burnside und 5 Ratsherren von Hillsbrad. Zerstört die Proklamation von Hillsbrad. Stehlt das Stadtregister von Hillsbrad. Meldet Euch danach bei Darthalia in Tarrens Mühle."}},
[533] = {"Unterwanderung", {"Endlich haben wir eine Spur und können nach Hinweisen auf Gol'dirs Aufenthaltsort suchen. Die Menschen haben ihn immer wieder umquartiert, aber wir konnten unsere Suche einengen und konzentrieren uns jetzt auf das große Syndikatlager nördlich von hier.","Begebt Euch dorthin, <Name>, und beschafft alle Informationen, an die Ihr herankommen könnt."}, {"Holt Informationen über Gol'dirs Aufenthaltsort für Krusk in Tarrens Mühle ein."}},
[535] = {"Valik", {"Arrrgh... Verletzt, sterbend und den Feind direkt vor Augen...","Da Ihr mich ja sowieso töten werdet, könntet Ihr mir da vielleicht zuvor ein Southshore-Starkbier bringen - Euresgleichen trinkt doch auch, oder? - und ich werde Euch sagen, was Ihr wissen wollt."}, {"Bringt einen Becher mit Southshore-Starkbier zu Valik in das Lager des Syndikats."}},
[536] = {"Die Küste entlang", {"Von der Stadtwache von Stormwind nach Southshore versetzt, eine Stadt, die man gleich wieder verlassen sollte... wie's aussieht. Der Gestank vom Hafen Tag und Nacht.","Ganz zu schweigen vom alten Redpath... Will, dass ich mich um die Murlocs kümmere. Ha!","Kann mich kaum aufraffen, die Unterschlüpfe dieser übergroßen Fische unten am Weststrand aufzusuchen. Wenn Ihr für mich gehen und Euch um sie kümmern würdet, wäre ich Euch sehr dankbar."}, {"Tötet 10 Gezeitenjäger der Fetzenflossen und 10 Orakel der Fetzenflossen für Lieutenant Farren Orinelle in Southshore."}},
[537] = {"Der Dunkle Rat", {"Der Brief, den Ihr mir gebracht habt, wurde von einem Wesen geschrieben, das sich selbst Nagaz nennt, ein Mitglied der Arguswacht. Ich weiß nicht, was diese Arguswacht ist, aber wenn sie mit dem Syndikat zusammenarbeiten, dann sind sie eine Bedrohung.","Ich möchte, dass Ihr Nagaz und die Schattenmagier der Arguswacht, die in diesem Brief erwähnt werden, findet. Ihr sollt sie finden und dann töten.","Sucht im Oberland, wo das Syndikat am stärksten ist. Es liegt nördlich von Strahnbrad.","Mit etwas Glück könnt Ihr die Abgesandten der Arguswacht dort finden."}, {"Tötet 4 Schattenmagier der Arguswacht.","Bringt den Kopf von Nagaz zu Magistrat Maleb nach Southshore."}},
[538] = {"Southshore", {"<Name>. Wenn Ihr einer langen Reise nicht abgeneigt seid, dann habe ich eine neue Aufgabe für Euch, Stormwind erneut bei der Bestückung seiner Bibliothek behilflich zu sein.","Das alte Königreich von Alterac ist nach dem großen Krieg gefallen und es besaß eine umfangreiche Büchersammlung. Es ist eine große Tragödie, dass die Stadt verfallen ist, aber es ist weitaus tragischer, dass all ihr Wissen verloren geht! Wir müssen retten, was zu retten ist.","Southshore liegt den Ruinen von Alterac am nächsten. Begebt Euch dorthin und sprecht mit dem Meister der Lehren Dibbs. Er kann Euch den Weg zu den Ruinen erklären und welche Bücher dort zu finden sind."}, {"Sprecht mit dem Meister der Lehren Dibbs in Southshore."}},
[539] = {"Kampf um Hillsbrad", {"Das Stadtregister von Hillsbrad deutet an, dass der Azurschacht unter der Kontrolle der Allianz steht. Außerdem ist die Mine eine erstklassige Quelle für Eisenerz für die Waffenkammern der Allianz.","Auch wenn wir der Stadt von Hillsbrad einen entscheidenden Schlag versetzen konnten, unterstützt und beschützt die Allianz den Azurschacht immer noch aufs Äußerste.","Die Mine liegt genau im Süden von Hillsbrad. Begebt Euch dorthin und tötet den Großknecht, einen Menschen namens Bonds. Tötet auch gleich seine Minenarbeiter. Das sollte der Allianz ein deutliches Zeichen setzen."}, {"Tötet Großknecht Bonds sowie 10 Minenarbeiter von Hillsbrad und erstattet Bericht bei Darthalia in Tarrens Mühle."}},
[540] = {"Wissen bewahren", {"Diese barbarischen Oger haben sich in den Ruinen von Alterac ausgebreitet und es schüttelt mich bei dem Gedanken, was sie mit den wertvollen Büchern dort anstellen könnten. Ihr müsst retten, was immer Ihr könnt!","Geht zu den Ruinen von Alterac und sucht dort nach Folianten, die die Oger gestohlen haben. Nehmt, was immer Ihr finden könnt, und bringt alles zu mir, aber sucht insbesondere ein ganz bestimmtes Buch: Der Arm von Gri'lek. Es enthält alte Trolllehren, die ich erlernen muss. Die Ruinen von Alterac waren der letzte bekannte Ort, an dem es ein intaktes Exemplar dieses Buches geben soll."}, {"Bringt 5 geborgene Folianten und das abgenutzte Lederbuch, das 'Der Arm von Gri'lek' enthält, zum Meister der Lehren Dibbs nach Southshore."}},
[541] = {"Kampf um Hillsbrad", {"Zwerge helfen Menschen? Diese verdammten, dreckigen kleinen Bestien.","Wir werden die Stärke dieser so genannten Allianz testen. Wir werden ja sehen, wie sehr diese kleinen Wadenbeißer den Menschen noch helfen wollen, nachdem sie unseren Zorn zu spüren bekommen haben. Wir wissen ganz genau, wo sie in diesem Gebiet ihre Hochburg haben.","Sie haben eine Kaserne namens Dun Garok im südöstlichen Hillsbrad entlang des Thoradin-Walls errichtet. Die letzte Order in diesem Kampf ist die Belagerung von Dun Garok, <Klasse>. Captain Eisenberg, ihr Anführer muss sterben."}, {"Begebt Euch nach Dun Garok, tötet 10 Gebirgsjäger, 8 Scharfschützen, 4 Priester sowie Captain Eisenberg und erstattet danach Bericht bei Darthalia in Tarrens Mühle."}},
[542] = {"Rückkehr zu Milton", {"Ich werde einige Zeit benötigen, um das Buch Der Arm von Gri'lek zu studieren, das Ihr mir gebracht habt. Ich bin sicher, Milton kann es kaum erwarten, die anderen geborgenen Bücher in die Sammlung von Stormwind zu integrieren.","Hier sind die Bücher, die Ihr aus den Ruinen von Alterac mitgebracht habt, verpackt und katalogisiert. Bringt sie zu Milton Sheaf in Stormwind und ich versichere Euch, dass er sehr dankbar sein wird."}, {"Bringt die Folianten von Alterac zu Milton Sheaf nach Stormwind."}},
[543] = {"Die Perenolde-Tiara", {"Die Perenolde-Tiara ist ein altes Erbstück der \"Edlen\" von Perenolde - der Herrscherfamilie von Alterac, bevor das Königreich verfiel. Es heißt, dass sie von Meisterjuwelieren in Ironforge hergestellt und mit den schönsten Smaragden besetzt wurde.","Als Alterac zerstört wurde, ging die Tiara verloren, aber Gerüchten zufolge soll ein Ogermagier, Grel'borg der Horter, die Tiara gefunden haben und jetzt in den Ruinen von Alterac auf der Suche nach weiteren Schätzen umherwandern.","Wir möchten, dass Ihr die Tiara für uns beschafft."}, {"Bringt die Perenolde-Tiara zu Remington Ridgewell nach Stormwind."}},
[544] = {"Gefängnis-Einbruch", {"Eigentlich kam ich zu Forschungszwecken nach Tarrens Mühle, aber jetzt muss ich eine Krise verhindern. Wisst Ihr, vier Verlassene sind vor einigen Monaten aus Undercity geflohen. Sie haben ihren Brüdern den Rücken zugekehrt, aber was viel schlimmer ist... sie haben die dunkle Fürstin bestohlen.","Diese Diebe sind in ein gesichertes Gewölbe eingebrochen und haben vier Artefakte gestohlen; Gegenstände, die unsere Apotheker für bestimmte Studien benötigen. Sylvanas will, dass diese Artefakte zurückgebracht werden.","Die Diebe sind aus Undercity nach Dalaran geflohen und diese Hexer haben sie in Quarantäne in das Lordamere-Internierungslager gesteckt."}, {"Findet diese Verräter, sammelt ihre Artefakte ein und kehrt dann zu Magus Voidglare nach Tarrens Mühle zurück."}},
[545] = {"Patrouillen von Dalaran", {"Die Hexer von Dalaran haben eine riesige, magische Kuppel um ihren Stadtkern errichtet. Einige glauben, dass es zum Schutz gegen die Gewalt unserer Zeit gemacht wurde, andere wiederum sagen, dass sie einfach Ruhe zum Studieren und Pläneschmieden haben wollen.","Mir persönlich ist der Grund egal. Aber mir ist nicht egal, dass die verfallenen Vororte der Stadt, die außerhalb der Kuppel liegen, möglicherweise wertvolle magische Schätze beherbergen.","Aber dieses Gebiet wird von den Hexern und ihren Elementarsklaven patrouilliert.","Also jagt sie und kehrt erst zurück, wenn ihre Anzahl einigermaßen reduziert worden ist."}, {"Tötet 6 Beschwörer von Dalaran sowie 12 Elementarsklaven und kehrt dann zu Magus Voidglare nach Tarrens Mühle zurück."}},
[546] = {"Andenken des Todes", {"Ich habe gehört, Ihr wurdet von Darthalia eingezogen, um Krieg gegen die Menschen von Hillsbrad zu führen. Ich bin ganz grün vor Neid...","Ihr habt den ganzen Spaß... Menschen erschlagen, die Stadt plündern, Unschuldige terrorisieren..., und ich stecke hier fest und darf in Tarrens Mühle Wache schieben.","Vielleicht habt Ihr ja etwas Mitleid mit einer alten Todeswache wie mir? Wisst Ihr, ich sammle nämlich menschliche Schädel. Und Ihr werdet eine recht lange Zeit gegen Menschen kämpfen. Bringt mir im Verlauf Eures langen Kampfes 30 Schädel, und ich werde Euch für Eure Mühe entlohnen."}, {"Todeswache Samsa aus Tarrens Mühle will 30 menschliche Schädel aus Hillsbrad."}},
[547] = {"Humberts Schwert", {"Hochexekutorin Darthalia hat mich hergeschickt, um Dun Garok aus der Ferne zu beobachten. Ich hatte mein Lager gerade außer Sichtweite aufgeschlagen. Am Morgen, als ich Wasser kochen wollte, ging ich Feuerholz sammeln.","Als ich ins Lager zurückkam, entdeckte ich, dass meine Ausrüstung verschwunden war.","Anscheinend waren ein paar Zwergenspäher von Dun Garok vorbeigekommen und hatten alles mitgenommen. Ich brauche mein Schwert! Wenn Ihr es für mich zurückholen könntet, wäre ich Euch sehr dankbar. So viele Zwerge hier... es wird ganz schön schwierig sein, den kleinen dreckigen Dieb zu finden, der es gestohlen hat."}, {"Bringt das Schwert von Todeswache Humbert aus Dun Garok zu ihm nach Tarrens Mühle zurück."}},
[549] = {"GESUCHT: Syndikatspersonal", {"In Lady Sylvanas Namen sollen alle Mitglieder der Organisation, die sich das Syndikat nennt, getötet werden. Diejenigen, die bereits gefangen genommen wurden, werden gemeinschaftlich hingerichtet. Wir wissen, dass sie sich im Moment in den Ruinen der Burg Durnholde im Südosten von Tarrens Mühle sammeln.","Für alle diejenigen, die Beweise ihrer Taten zu Hochexekutorin Darthalia bringen, ist eine reiche Belohnung ausgesetzt."}, {"Tötet 10 Schurken des Syndikats und 10 Wachmänner des Syndikats. Kehrt danach zu Hochexekutorin Darthalia nach Tarrens Mühle zurück."}},
[550] = {"Kampf um Hillsbrad", {"Da Ihr mit solcher Verbissenheit und Ehre in der Schlacht von Hillsbrad gekämpft habt, habe ich für Euch diese Belobigung geschrieben, in der Eure Heldentaten im Kampf angemessen gerühmt werden, sodass das Oberkommando sie anerkennt.","Bringt die versiegelte Belobigung zu Varimathras nach Undercity. Geht erhobenen Hauptes, <Klasse>."}, {"Bringt Darthalias versiegelte Belobigung zu Varimathras nach Undercity."}},
[551] = {"Das Verhexte Pergament", {"Dieses Pergament ist mit einer Verzauberung versehen, die es unlesbar macht. Nur ein Wort oben am Blattanfang ist lesbar:","Nagaz"}, {"Bringt den verschlüsselten Brief zum Meister der Lehren Dibbs nach Southshore."}},
[552] = {"Helculars Rache", {"Meister Helcular hatte seine Transformation in einen Lich fast vollständig abgeschlossen, als ein menschlicher Lynch-Mob seinen rituellen Unterschlupf in den Hügeln entdeckte und ihn brutal ermordete. Was die Angelegenheit noch schlimmer macht, ist, dass sie seinen Leichnam auf dem Friedhof von Southshore begraben haben.","Diese Missachtung eines großen Hexers ist einfach nicht akzeptabel. Als sein Lehrling werde ich dafür sorgen, dass der Regen der Rache auf Southshore fallen wird.","Aber zuerst benötigen wir Helculars Rute. Yetis befinden sich jetzt in seinem rituellen Unterschlupf. Bestimmt hat sich eine dieser Bestien die Rute angeeignet."}, {"Holt Helculars Rute von dem Yeti und bringt sie zu Novize Thaivand nach Tarrens Mühle zurück."}},
[553] = {"Helculars Rache", {"Hier, nehmt die Rute von Helcular. Ihr werdet sie brauchen.","Helcular stellte 3 zeremonielle Scheiterhaufen als Teil seines mächtigsten Zaubers auf. Die Flamme von Azel und die Flamme von Veraz liegen in den Vorgebirgshöhlen. Die Flamme von Uzel liegt in einer Höhle über diesen Höhlen, weiter nördlich in den Bergen.","Die Rute von Helcular muss mit jeder der drei Flammen aufgeladen werden. Sobald das Ritual abgeschlossen ist, stoßt Ihr die Rute in Helculars Grab in Southshore, der streng bewachten Menschen-Stadt im Süden. Die Rache wird unser sein!"}, {"Ladet die Rute von Helcular mit der Macht der Flamme von Azel, der Flamme von Veraz und der Flamme von Uzel auf.","Stoßt die aufgeladene Rute in Helculars Grab in Southshore."}},
[554] = {"Stormpikes Entschlüsselung", {"Ausgrabungsleiter Stormpike ist ein Meister der Dechiffrierung und Entschlüsselung. Wenn Ihr das Pergament, das Ihr gefunden habt, zu ihm bringt, kann er mit etwas Glück herausfinden, was darin steht. Im Moment studiert Stormpike im Ausgrabungsleiter-Hauptquartier in Ironforge.","Wenn Ihr ihn seht, richtet ihm aus, dass Ihr mit mir gesprochen habt, - ich habe ihm in letzter Zeit einiges zu tun gegeben."}, {"Bringt das verhexte Pergament zu Ausgrabungsleiter Stormpike nach Ironforge."}},
[555] = {"Milde Schildkrötensuppe", {"Wenn es etwas Wissenswertes über Southshore gibt, dann dass wir hier die beste Küche nördlich von Stormwind haben!","Nehmt als Beispiel nur mal mein Rezept für Schildkrötensuppe. Ich habe Leute getroffen, die sogar von Dunkelhain hergekommen sind, nur um eine Schüssel voll davon zu kosten. Da wir gerade davon sprechen, in letzter Zeit konnte ich keine mehr kochen. Ich bin früher selbst bis kurz hinter Dalaran an den Lordameresee gereist, um Schnappkiefer zu jagen, aber das ist jetzt zu gefährlich. Wenn Ihr mir etwas Schildkrötenfleisch von den Schnappkiefern aus dem Norden bringt und dazu noch feine Gewürze, dann werd ich eine kochen!"}, {"Bringt 10 Stücke Schildkrötenfleisch und einige feine Gewürze zu Küchenchef Jessen nach Southshore."}},
[556] = {"Steinmarken", {"Ein bemerkenswertes Talent der Hexer von Dalaran ist ihre Vertrautheit mit den Elementaren: mächtige Kreaturen aus anderen Dimensionen. Die Hexer benutzen magische Marken als Mittel, diese Elementare zu kontrollieren. Magus Voidglare hätte gern einige dieser Marken, damit er sie studieren kann.","Begebt Euch auf das Gebiet der Hexer von Dalaran, westlich von Hillsbrad, und sammelt die Marken ein.","Jeder Einwohner von Dalaran hat solche Marken, aber ich schlage vor, dass Ihr im Internierungslager auf die Suche geht. Dort ist es sicherer als in Dalaran selbst."}, {"Bringt 10 abgenutzte Steinmarken zu Bewahrer Bel'varil nach Tarrens Mühle."}},
[557] = {"Armschienen der Bindung", {"Der Magus wird die Steinmarken, die Ihr gesammelt habt, einige Zeit lang intensiv untersuchen, aber ich weiß, dass er später mit Bestimmtheit auch Exemplare ihrer Gegenstücke haben will: Armschienen der Bindung. Diese befinden sich an den Handgelenken von echten Elementaren und binden sie in die Dienste der Magier.","Macht Jagd auf Elementare in den Ruinen von Dalaran und sammelt ihre Armschienen der Erdbindung, und der Magus wird sehr erfreut sein."}, {"Bringt 4 Armschienen der Erdbindung zu Bewahrer Bel'varil nach Tarrens Mühle."}},
[558] = {"Jainas Autogramm", {"Ich habe gehört, dass Lady Jaina Prachtmeer aus Theramore eine der größten Heldinnen ist, die die Allianz je gesehen hat. Wenn ich mal groß bin, möchte ich auch ein Held der Allianz werden!","Kennt Ihr Jaina, <Name>? Tut Ihr mir einen Gefallen? Kriege ich ein Autogramm von ihr? Ich weiß gar nicht, wie ich sie ansprechen soll, und wahrscheinlich ist sie sowieso viel zu beschäftigt, um mich zu beachten... aber was ist mit Euch? Ihr seid doch genau wie sie ein Abenteurer! Euch gibt sie bestimmt ein Autogramm!","Bitte, <Name>. Bitte..."}, {"Reist nach Theramore und versucht für Euren Schützling ein Autogramm von Lady Jaina Prachtmeer zu ergattern."}},
[559] = {"Farrens Beweis", {"Was haltet Ihr davon, <Name>? Redpath war nicht zufrieden mit den ganzen Murlocs, die wir getötet haben - seid nur nicht so bescheiden, Ihr wart ja auch dran beteiligt. Jetzt verlangt er von mir, dass ich ihren Tod beweise und kalte, schleimige Murlocköpfe abhacke und sie ihm bringe, um seine Anerkennung zu erlangen."}, {"Sammelt 10 Murlocköpfe von den Murlocs am Weststrand für Lieutenant Farren Orinelle in Southshore."}},
[560] = {"Farrens Beweis", {"Wisst Ihr, <Name>, ich würde die Köpfe am liebsten gar nicht anfassen... Da Ihr sie nun schon mal angefasst habt, würde es Euch sehr viel ausmachen, sie zu Redpath zu bringen?"}, {"Bringt den Sack mit den Murlocköpfen zu Marshal Redpath nach Southshore."}},
[561] = {"Farrens Beweis", {"Bitte informiert Lieuntenant Orinelle über meinen Wunsch, er möge seine Aufmerksamkeit erneut auf die Nagaplage am Oststrand richten. Sobald er Resultate erzielt hat, soll er persönlich bei mir Bericht erstatten."}, {"Sprecht mit Lieutenant Farren Orinelle in Southshore."}},
[562] = {"Stormwind Ho!", {"Mein ehemaliger Mentor bei der Stadtwache von Stormwind hat mir eine Nachricht zukommen lassen, dass sich das Oberkommando der Allianz langsam Sorgen macht um die Bedrohung, die von den Naga ausgeht. Er hat gehört, dass es gute Aussichten auf eine Beförderung - oder zumindest Versetzung - für jeden gibt, der sich in dieser Angelegenheit als hilfreich erweist.","Es gibt da nur ein winzigkleines Problem... Ich schaff die Naga nicht, <Name>. Klar, ich kann eine Kneipenschlägerei auflösen, oder einen aggressiven Bettler wegschaffen, aber die Naga sind einfach zu viel für mich. Ihr müsst mir helfen, <Name>!"}, {"Tötet 10 Küstenjäger der Säbelflossen und 10 Sirenen der Säbelflossen für Lieutenant Farren Orinelle in Southshore."}},
[563] = {"Versetzungen", {"Es dürfte kein Problem sein, meinen Bericht zu beachten. Der neue Captain der Wachen, Major Samuelson, war mein Mentor, während ich in Stormwind stationiert war. Der Bericht sollte ihm mehr als genug Grund geben, mich wieder in die Stadt zu versetzen. Aber ich brauche Euch, damit Ihr meinen Bericht zu ihm bringt... der alte Redpath soll nichts davon mitkriegen."}, {"Bringt Farrens Bericht zu Major Samuelson in die Burg Stormwind."}},
[564] = {"Teure Bedrohung", {"Nix macht mich wütender als wenn ich diese schönen Pferdchen verlieren tu. Und glaubt's mir, wir hab'n 'ne ganze Menge verloren, an die eklichen untoten Unholde, das verdammte Syndikatsgesocks und all die anderen tunichgute Schurken, die hier so rumlaufen tun.","Aber ich schwör beim Grab von mein alten Opa, 'ne größere Gefahr für uns're Pferdchen als die vonne Berglöwen in Alterac tut's nich' geben tun. Komm' alle Nase lang vonne Berge runner und machen mir dat Leben schwer.","Und Ihr seid also 'n Held? Dann geht mal und tut was dagegen unternehm' tun!"}, {"Darren Malvew aus Southshore will, dass Ihr 8 Berglöwen und 10 titanische Berglöwen tötet."}},
[565] = {"Bartolos Yetifellumhang", {"Ich, der große Bartolo, werde für Euch den weltberühmten, allianzweit bekannten Yetifellumhang anfertigen.","Ich wette mit Euch: Auch wenn Ihr die ganze Welt bereist, findet Ihr keinen besseren Umhang. Oh, wartet mal, was ist das, kleiner <Volk>? \"Bartolo, es gibt keinen feineren Umhang!\" Ja, ja! Das habe ich schon öfter gehört.","Bartolo benötigt einen Wollstoffballen, einen Umhang des Hügelwächters von einem geschickten Lederer, feinen Faden und natürlich Yetifell. Bringt mir diese Dinge und Ihr werdet ein glücklicher Kunde!"}, {"Bringt Bartolo Ginsetti in Southshore einen Wollstoffballen, einen Umhang des Hügelwächters, feinen Faden und 10 Haufen Yetifell."}},
[566] = {"GESUCHT: Baron Vardus", {"Laut Befehl der Hochexekutorin Darthalia wird der unter dem Namen Baron Vardus bekannte Mensch wegen Verbrechen gegen die Verlassenen gesucht. Vardus ist bekannt als hochrangiges Mitglied der Organisation, die sich das Syndikat nennt.","Zuletzt wurde er im Oberland des Alteracgebirges im Norden von Tarrens Mühle gesehen.","Er soll getötet werden. Für den Erhalt der Belohnung müssen Beweise seines Todes zu Hochexekutorin Darthalia gebracht werden."}, {"Tötet Baron Vardus und bringt seinen Kopf zu Hochexekutorin Darthalia in Tarrens Mühle."}},
[567] = {"Gefährlich!", {"Gefährlich!","Die folgenden Menschen aus Hillsbrad werden als gefährlich erachtet. Ein Kopfgeld ist von Hochexekutorin Darthalia auf sie ausgesetzt worden.","Amtmann Horatio Whitesteed. Gesucht wegen Mordes an Todeswache Toma.","Bürger Wilkes. Gesucht wegen Mordes an Apotheker Eli.","Minenarbeiter Hackett. Gesucht wegen Mordes an Todespirscher Fry.","Bäuerin Kalaba. Gesucht wegen hinterhältigem Überfall auf Vorräte von Undercity.","Jeder dieser Feinde hat sich versteckt und wird nur sehr schwer zu finden sein. Auf die Nachricht ihres Todes wird eine Belohnung gewährt."}, {"Hochexekutorin Darthalia in Tarrens Mühle hat ein Kopfgeld auf Amtmann Horrace Whitesteed, Bürger Wilkes, Minenarbeiter Hackett und Bäuerin Kalaba ausgesetzt."}},
[568] = {"Die Verteidigung von Grom'gol", {"Throm-ka, <Klasse>!","Eure Ankunft in Grom'Gol kommt wahrlich zur rechten Zeit. Als Kommandant des Kriegshäuptlingsstützpunkts hier im Dschungel bin ich durch meine Ehre gebunden und verpflichtet, die Sicherheit aller Mitglieder der Horde zu gewährleisten. Unsere Aufgabe, eine sichere Versorgungskette nach Stonard zu gewährleisten wird durch einige der örtlichen Einwohner schwer behindert.","Ich gebe Euch den Auftrag, die Raptorpopulation außerhalb Grom'gols zu reduzieren. Sobald Ihr nennenswerte Fortschritt erzielt habt, erstattet mir Bericht und Ihr erhaltet einen neuen Auftrag."}, {"Kommandant Aggro'gosh vom Basislager von Grom'gol will, dass Ihr 15 Schmetterschwanzraptoren tötet."}},
[569] = {"Die Verteidigung von Grom'gol", {"Als wir in diesem Dschungel ankamen, hatten wir nicht vor, die hier ansässigen Stämme zu bekämpfen. Unsere Mission, Stonard zu versorgen, hat höchste Priorität. Aber es gibt Komplikationen. Eine hier ansässige Gruppe von Ogern hat die Ruinen von Mizjah im Südosten von Grom'gol besetzt. Trotz unserer Warnungen überfallen sie weiterhin unsere Nachschubkarawanen.","Ich beauftrage Euch hiermit, Euch mit diesen unkooperativen Ogern auseinander zu setzen.","Reist zu den Ruinen und versucht, sie aus diesem Gebiet zu vertreiben. Das sollte ihnen zumindest ein eindeutiges Zeichen setzen."}, {"Kommandant Aggro'gosh vom Basislager von Grom'gol will, dass Ihr 10 Ogerschläger und 5 Ogerhexendoktoren tötet."}},
[570] = {"Mok'Thardins Verzauberung", {"Die Macht der Dschungelmagie muss erkannt und kann dann von der Horde genutzt werden. Meine Rituale waren bislang erfolgreich. Ich kann sehr mächtige Verzauberungen wirken. Für Euch, mutiger <Klasse>, werde ich einen wirklich ganz besonderen Gegenstand herstellen.","Bringt mir einige Schattentatzenpantherkrallen und den Fangzahn einer Tigerin. Es darf aber nicht irgendein Fangzahn sein. Er muss sich in einem makellosen Zustand befinden.","Diese Bestien lauern im gesamten Dschungel, aber Ihr könnt einige ganz in der Nähe finden. Überquert den Fluss von den Mizjah-Ruinen aus in südöstlicher Richtung."}, {"Scharfseher Mok'thardin im Basislager von Grom'gol will 8 Schattentatzenkrallen und einen makellosen Tigerinnenfangzahn."}},
[571] = {"Mok'Thardins Verzauberung", {"Was ich nun von Euch benötige, ist sehr schwer zu bekommen.","Ich benötige ein magisches Band, um all diese Federn, Krallen und den makellosen Fangzahn zu befestigen. Nur die Sehne eines alten Gorillas ist dazu geeignet.","Eine solche könnt Ihr nur von einem alten Nebeltalgorilla bekommen. Sie sind oft auf Nahrungssuche im Osten von Booty Bay.","Die Sehne muss in vollkommenem Zustand sein. Ihr werdet wahrscheinlich mehrere Tiere töten müssen, bis Ihr genau das erhaltet, was ich benötige, aber das ist es in jedem Fall wert."}, {"Bringt die Sehne eines alten Gorillas zu Scharfseher Mok'thardin nach Grom'gol."}},
[572] = {"Mok'Thardins Verzauberung", {"Die Dinge, die Ihr von den katzenartigen Bestien des Tales gesammelt habt, sind nur die erste Voraussetzung für einen machtvoll verzauberten Gegenstand. Diese Dschungelornamente, in Verbindung mit meinem Schamanenritual, werden Euch mit einem äußerst nützlichen Werkzeug versehen, mit dessen Hilfe Ihr der Horde und dem Kriegshäuptling beistehen könnt.","Aber jetzt müsst Ihr für mich einige Dschungelpirscherfedern sammeln. Für gewöhnlich tragen die Bestien diese Federn an ihren Armbändern. Ihr findet einen Stamm dieser Raptoren direkt südlich von Grom'gol, nicht weit von der großen Arena."}, {"Bringt 10 Dschungelpirscherfedern zu Scharfseher Mok'thardin nach Grom'gol."}},
[573] = {"Mok'Thardins Verzauberung", {"Es fehlt nun nur noch ein Reagenz, um die Verzauberung zu vervollständigen.","Im Süden, nahe der Westküste, liegt ein Heiliger Quell, hoch über den Steilhängen. Dieser Quell ist das Objekt großer Konflikte zwischen religiösen und mystischen Gelehrten. Jede einzelne Gruppe in Azeroth vertritt die Auffassung, dass das Quellwasser nur für das eigene Volk göttlich gemacht wurde.","Meine letzte Aufgabe für Euch: Bringt mir Wasser der Heiligen Quelle. Aber ich warne Euch: Eine Expedition von Forschern der Naga ist auf dem Weg zu der Quelle. Mit ihnen müsst Ihr Euch ebenfalls befassen."}, {"Scharfseher Mok'thardin von Grom'gol benötigt Wasser der Heiligen Quelle. Er will auch, dass Ihr 10 Forscher der Naga tötet."}},
[574] = {"Spezialtruppe", {"Nun, da viele von seinen regulären Truppen gefallen sind, müsst Ihr es mit seinen hochtrainierten Streitkräften aufnehmen. Wenn sie nicht in der Wildnis unsere Rebellensoldaten erschlagen, kann man sie tief innerhalb von Kurzens Truppenlager finden, in einer Höhle, die sie das Vorratslager nennen.","Ich kenne einige von Kurzens Truppen persönlich und weiß, dass sie geschickt, loyal und grausam sind. Dies ist keine einfache Aufgabe, mit der ich Euch betraue, <Name>.","Nachdem Ihr sie besiegt habt, meldet Euch bei Lieutenant Doren, meinem befehlshabenden Offizier. Er wird Euren Bericht aus erster Hand haben wollen."}, {"Tötet 10 von Kurzens Kommandosoldaten und 6 von Kurzens Kopfschrumpfer.","Erstattet Bericht bei Lieutenant Doren im Rebellenlager."}},
[575] = {"Angebot und Nachfrage", {"Der beste Ledermacher der südlichen Meere und der Alten wie der Neuen Welt. So nennen sie mich. Mich! Drizzlik! Der beste Le... ähem. Nun ja.","Meine Lederwaren sind nah und fern als absolut vorzüglich bekannt, erste Wahl der wahren Kenner!","Gerade habe ich eine Bestellung von Direktor Riddlevox vom Tüftlerverband über ein Dutzend seiner Lieblingsstiefel aus der Exzelsiorlinie erhalten. Unsere Verkaufsschlager, ehrlich gesagt.","Für die Grundform benötige ich Krokiliskenhäute von den Kroks entlang des Flusses im Norden des Schlingendorntals."}, {"Bringt 2 große Flusskrokiliskenhäute zu Drizzlik nach Booty Bay."}},
[576] = {"Ein Auge offen halten", {"Haltet ein! Wer da?","Grrr, ich bin so gut wie nutzlos ohne mein verzaubertes Spezialauge. Als ob es nich' schon schlimm genug is', dass ich meine Augen im Kampf verlor'n hab! Aber zum Glück haben wir in Menethil angelegt und ein reisender Hexer hat 'n Spezial-Glasauge verzaubert, mit dem ich wieder was sehen kann.","Gestern Nacht hab'n mir aber 'n paar von den Blutsegelbukanier'n auf'm Weg nach Haus aufgelauert. Die ham mir doch echt mein Auge geklaut! Scheint, als hätt'n die Burschen Booty Bay gefunden.","Könnt'er 'nem alten Seebären helfen und mir mein Auge zurückholen?"}, {"Dizzy One-Eye in Booty Bay möchte, dass Ihr sein Auge von den Blutsegelbukanieren zurückholt."}},
[577] = {"Etwas Zusammenbau benötigt", {"Nun, ich habe die Grundform der Stiefel so weit und sie werden auch sehr gut, aber die Häute, die Ihr mir brachtet, werden leider nicht für die inneren Schichten ausreichen.","\"Was sollen wir tun, Drizzlik?\" Diese Frage brennt Euch zweifelsohne auf den Lippen, und Ihr habt wirklich Glück, dass Ihr mit Drizzlik sprecht, denn er wird Euch genauestens erklären, was wir zu tun gedenken!","Die inneren Schichten sieht sowieso nie jemand, also werden wir uns zum Ausfüllen der Stiefel einige der billigeren, dickeren Häute der Krokos holen, die um den Nazferitisee leben."}, {"Bringt 5 Schnappkieferkrokiliskenhäute zu Drizzlik nach Booty Bay."}},
[578] = {"Der Stein der Gezeiten", {"Wenn es wirklich einen Goblinmagier und Wasserelementare gibt, die auf dieser Insel ihr Unwesen treiben, dann heißt das, dass der Stein der Gezeiten wirklich mir gehören kann!","Ich will, dass Ihr diese Spukinsel findet und herausfindet, wie es dort zugeht. Oh, und falls Ihr mehr über den Stein der Gezeiten herausfinden wollt, bringt diesen Schein zur Bibliothek von Stormwind, sie sollten dort ein Exemplar des Buches für Euch finden, in dem ich zuerst von der Legende gelesen habe.","Sollte es möglich sein, dass Gazban wirklich den Stein gefunden hat...?"}, {"Findet die Spukinsel für Baron Revilgaz in Booty Bay."}},
[579] = {"Bibliothek von Stormwind", nil, nil},
[580] = {"Whiskey Slim's verlorener Grog", {"Wir waren dabei, uns tüchtig einen hinter die Binde zu gießen, konnten 's Hinterland schon sehen. Dachten schon, die See wollte uns ganz und gar verschlingen.","Ol' Slim weiß alles über flüssigen Mut! Der Käpt'n hatte eine Kiste mit Pupellyverbos-Portwein, den wir auf'm Weg nach Stormwind von 'nem Freibeuter geholt hatten.","Als der Sturm uns dann vom Hinterland aus getroffen hat, bin ich also in den Grog rein. Große Welle kam und hat die Flaschen über Bord gespült. Der Käpt'n wird mich bestimmt mit der Katze streicheln, wenn Ihr mir nich' helft, die wieder zurückzubringen!"}, {"Whiskey Slim in Booty Bay möchte, dass Ihr ihm die Flaschen Pupellyverbos-Portwein bringt, die er verloren hat."}},
[581] = {"Die Suche nach Yenniku", {"Unheil droht. Unheil für den jüngsten Sohn meines Häuptlings, Yenniku. Der Sitte unseres Volkes nach wurde er den Gurubashitrollen von Zul'Gurub gegeben, aber nachdem mein Stamm, der Darkspearstamm, mit dem Orc Thrall gegangen ist, war der arme Yenniku für uns verloren. Ich wurde aus dem fernen Orgrimmar zurück ins Schlingendorntal geschickt, um nach dem Sohn meines Häuptlings zu suchen.","Und ich glaube, dass der Bloodscalpstamm ihn hat.","Wenn Ihr mir helfen wollt, dann jagt die Bloodscalps. Ihr könnt sie im Norden finden. Sammelt ihre Hauer und kehrt zu mir zurück."}, {"Bringt 9 Hauer der Bloodscalp zu Nimboya in das Basislager von Grom'gol."}},
[582] = {"Kopfjägerei", {"Wir müssen herausfinden, ob Yenniku tot ist.","Erschlagt die Kopfjäger der Bloodscalp und nehmt ihnen ihre Schrumpfköpfe ab. Ihr müsst viele, viele Köpfe sammeln und ich werde sie überprüfen, um sicherzugehen, dass Yennikus Kopf nicht dabei ist.","Sollte Yenniku von den Bloodscalps getötet worden sein, möge ein Fluch für 600 Jahre auf den Geist ihres Häuptlings, Gan'zulah, kommen!","Geh, <Name>. Viele Kopfjäger könnt Ihr bei den Ruinen von Zuuldaia und Zul'Kunda an der Küste im Norden finden."}, {"Bringt 20 Schrumpfköpfe zu Nimboya in das Basislager von Grom'gol."}},
[583] = {"Willkommen im Dschungel", {"Willkommen im Schlingendorntal!","Ihr seid es Euch vielleicht nicht bewusst, aber der Zwerg dort drüben ist der wunderbare Hemet Nesingwary, weitbekannter Kriegsheld der Allianz und Meistergroßwildjäger. Er ist nicht gerade sehr bekannt dafür, dass er Fremde in seinem Lager sehr mag, aber Ihr seht aus, als hättet Ihr schon das eine oder andere Abenteuer erlebt, <Klasse>.","Da er selbst ein erfahrener Kriegsveteran ist, hat Meister Nesingwary eine Schwäche für andere Helden.","Geht und sprecht mit ihm. Vielleicht kann er Euch einige Ratschläge für die Jagd mitgeben."}, {"Sprecht mit Hemet Nesingwary."}},
[584] = {"Köpfe des Bloodscalpklans", {"Yenniku war nicht unter den Köpfen, der Großen Schlange sei Dank. Aber wir sind noch weit entfernt davon, ihn zu finden.","Wir müssen die Wahrheit herausfinden, und ich fürchte, dass nur der Häuptling der Bloodscalp und sein Hexendoktor sie kennen. Sie sind Feinde des Darkspearstammes und werden nicht freiwillig reden, also müssen wir die Wahrheit von ihren toten Lippen erzwingen!","Bringt mir die Köpfe von Gan'zulah und Nezzliok dem Düsteren und legt sie in diesen Kessel.","Sie halten sich in den Tiefen von Zul'Kunda auf. Bringt ihre Köpfe... wir werden sie schon zum Sprechen bringen."}, {"Bringt Gan'zulahs Kopf und Nezzlioks Kopf zum blubbernden Kessel im Basislager von Grom'gol."}},
[585] = {"Mit Nezzliok sprechen", {"<Der Kopf von Nezzliok spricht.>","Es stimmt. Ich weiß, wo das Kind der Darkspears hingebracht wurde. Ihr habt mich besiegt und meinen Geist gebunden, aber ohne Gegenleistung sage ich nichts!","Die Bloodscalps streiten mit den Skullsplitters. Um mein Wohlwollen zu erhalten, stehlt 3 der Trophäenschädel von den Skullsplittern und legt sie hierher zu uns, sodass mein Geist im Nether Sklaven zur Verfügung hat. Ein Schädel muss aus ihrem Trophäenstapel in Balia'mah, der zweite von Ziata'jai und der dritte von Zul'Mamwe stammen."}, {"Bringt eine Balia'mah-Trophäe, eine Ziata'jai-Trophäe und eine Zul'Mamwe-Trophäe zum blubbernden Kessel im Basislager von Grom'gol."}},
[586] = {"Mit Gan'zulah sprechen", {"<Der Kopf von Gan'zulah spricht.>","Verflucht seid Ihr! Und auch Eure Vorfahren!","Nur Blut und Rache können meine Wut beschwichtigen; wenn das Euer Wunsch ist, dann... tut Folgendes:","Werft Euch in das Herz der Skullsplitter im Osten. Erschlagt die schrecklichsten unter ihnen, und wenn Ihr überleben solltet... dann tretet gegen ihren Häuptling, Ana'thek den Grausamen, an. Ha! Bringt mir seine zerschmetterte Rüstung, wenn Ihr es vermögt!","Möge er Eure Glieder ausreißen und Euch der Fäulnis ausliefern und den Geiern zum Fraß vorwerfen."}, {"Tötet 8 Jäger der Skullsplitter, 6 Kopfjäger der Skullsplitter und 4 Berserker der Skullsplitter.","Bringt die zerbrochene Rüstung von Ana'thek zum blubbernden Kessel in das Basislager von Grom'gol."}},
[587] = {"Auf dem Schnupftabak", {"Ah, das Leben auf See. Ich sag nicht, dass es das wahre Leben ist, aber es ist mein Leben.","Wenn ich in den Hafen komme, such ich mir eine gute Flasche Grog und sehr guten Schnupftabak. Schlechte Angewohnheiten, ich weiß, aber irgendein Laster muss'n Pirat ja nun mal haben , nich?","Also könnt Ihr Euch vielleicht vorstellen, wie niedergeschlagen ich war, als ich herausfand, dass der Kräuterkundige nichts von dem Zeug mehr da hatte. Erzählte mir, dass seine Lieferung von den verdammten Räubern der Blutsegelbukaniere geklaut worden ist.","Ich wette, ein furchtloser <Klasse> wie Ihr könnte sie überreden, die Ware zu teilen, nich?"}, {"Deeg in Booty Bay möchte, dass Ihr ihm 15 Teile Schnupftabak bringt."}},
[588] = {"Yennikus Schicksal", {"<Nezzliok spricht>","Ihr habt mich und meinen Häuptling beschwichtigt, also werde ich Euch verraten, was Ihr zu wissen wünscht...","Der junge Yenniku ist für seinen Klan verloren.","Er wurde von dem Hexendoktor Zanzil, dem Ausgestoßenen, verschleppt. Sein Körper und sein Geist werden jetzt durch Zanzil kontrolliert und nur eine mächtige Magie kann ihn wieder befreien.","Eine Magie, über die Ihr nicht verfügt, <Klasse>.","Möglicherweise können Eure Verbündeten bei den Darkspear Euch etwas Hoffnung auf Yennikus Rettung bieten. Aber dies bezweifle ich."}, {"Sprecht mit Kin'weelay."}},
[589] = {"Die singenden Kristalle", {"Die Legenden berichten von einem Juwel, das tief im Herzen des Schlingendorntals versteckt liegt. Wir nennen es das Gedankenauge. Es verfügt über große Macht. Wenn Ihr dieses Auge finden könnt, dann können wir es vielleicht nutzen, um den verlorenen Yenniku aus der Dunkelheit zurückzubringen.","Ich werde mit den Geistern des Dschungels beraten, um das Gedankenauge zu finden. Während ich das tue, geht Ihr in die Kristalladermine, die sich südöstlich von hier befindet, und sammelt pulsierende blaue Splitter von den Eisenkieferbasilisken. Die Splitter gibt es nur sehr selten, aber wir benötigen ihre Magie, um das Gedankenauge unserem Willen zu unterwerfen."}, {"Bringt 3 pulsierende blaue Splitter zu Kin'weelay in das Basislager von Grom'gol."}},
[590] = {"Ein Schurkenhandel", {"Was?","Was?!","Ihr wollt Geld? Ich schulde Euch kein Geld.","Brief? Was für ein Brief? Oh, Ihr wollt also kämpfen? Nun, gut Kumpel... dann lasst uns anfangen! Schulde Euch Geld..."}, {"Besiegt Calvin Montague in Deathknell."}},
[591] = {"Das Gedankenauge", {"Das Gedankenauge befindet sich bei einem Ogermagier, in den Tiefen des Ogerhügels der Mosh'Ogg östlich von hier. Sein Name ist Mai'Zoth. Ich kenne ihn noch von der Zeit, als sich die Darkspears im Schlingendorntal aufhielten - er ist bösartig und seine Magie ist stark.","Und wenn er im Besitz des Gedankenauges ist, dann ist seine Magie noch stärker.","Ihr müsst gegen Mai'Zoth antreten und ihm das Gedankenauge abnehmen. Es ist eine gefährliche Aufgabe, aber es ist die einzige Hoffnung, die wir haben, Yenniku zu retten."}, {"Bringt das Gedankenauge zu Kin'weelay in das Basislager von Grom'gol."}},
[592] = {"Yennikus Rettung", {"Ich habe das Gedankenauge in einen Seelenedelstein umgeformt. Somit hat er zweierlei Zwecke: Erst verwendet Ihr ihn, um Yenniku zu betäuben, und sobald er betäubt ist, legt Ihr den Seelenedelstein auf seinen Kopf, um seine Seele einzufangen.","Dann bringt Ihr den Edelstein zu Nimboya.","Yenniku ist bei Zanzil dem Ausgestoßenen, in den Ruinen von Aboraz, entlang des Kristallufers am fernen Kap des Schlingendorntals."}, {"Bringt den gefüllten Seelenedelstein zu Nimboya."}},
[593] = {"Den Seelenedelstein füllen", nil, nil},
[594] = {"Flaschenpost", {"In der Notiz steht Folgendes in recht guter Handschrift:","An alle, die mir helfen können:","Bitte, ich benötige dringend Hilfe. Ein großer Blaumähnengorilla hält mich auf einer Insel in den südlichen Meeren gefangen.","Die Sternbilder am Nachthimmel sind mir nicht vertraut und von Dschungelbäumen verdeckt, aber an klaren Tagen kann ich die wilden Ufer im Nordwesten erkennen und im Südwesten etwas, das große Segelschiffe sein könnten.","Ich flehe Euch an, mein möglicher Retter, bitte helft mir."}, {"Findet den Schreiber dieser Flaschenpost."}},
[595] = {"Die Blutsegelbukaniere", {"Ihr scheint ja sehr erpicht auf Arbeit zu sein, Kumpel. Bin nicht sicher, warum eine Landratte wie Ihr, <Volk>, und ein <Klasse> noch dazu, den Schwarzmeerräubern Hilfe anbietet, aber ich werd Euch nicht fortschicken.","Unser Schiff hat gerade hier in Booty Bay angedockt und es heißt, dass unsere Rivalen - diese verdammten, dreckigen Blutsegelbukaniere - sich im gesamten Süden des Schlingendorntals herumtreiben.","Meine Jungs sind aber zu beschäftigt, sich einen anzusaufen und Mädchen nachzugaffen! Was ich brauch, ist ein Späher.","Begebt Euch zur Bucht im Norden von Booty Bay an der ungezähmten Küste und schaut, was Ihr so findet."}, {"Erster Maat Crazz will, dass Ihr die Bucht nördlich von Booty Bay überprüft."}},
[596] = {"Blutige Knochenhalskette", {"Ich wurde nach Grom'gol geschickt, um Nimboya auf seiner Suche zu begleiten, aber... Ich habe meine eigenen Gründe, hier zu sein.","Der Bloodscalpstamm ist der Feind unseres Stammes. Wenn wir Trophäen unserer Feinde sammeln, erhalten wir Macht über sie. Ihr könnt unserem Stamm behilflich sein, indem Ihr unsere Feinde jagt.","Sie leben im Norden. Erschlagt die Trolle der Bloodscalp und nehmt ihre blutigen Knochenhalsketten mit. Wenn Ihr viele davon sammelt, wird Eure Belohnung groß sein."}, {"Bringt 25 blutige Knochenhalsketten zu Kin'weelay in das Basislager von Grom'gol."}},
[597] = {"Die Blutsegelbukaniere", {"Die Karte zeigt Booty Bay mit einem roten \"X\" genau an dem Punkt, wo Ihr gerade steht.","Der hingekritzelte Text lautet:","\"Leute, errichtet ein Lager an dieser Stelle und verhaltet euch schön unauffällig. Wartet auf das Signal vom südlichen Kommandoposten, bevor ihr angreift. Wenn sich jemand euch nähert, tötet ihr ihn.\""}, {"Berichtet dem ersten Maat Crazz in Booty Bay von Euren Funden."}},
[598] = {"Spaltknochenhalskette", {"Nun, da Ihr Bloodskalp-Trophäen gesammelt habt, habe ich eine weitere Aufgabe für Euch. Der Skullsplitterstamm der Trolle lebt im Südwesten und wir bekämpfen sie schon seit Jahren. Obwohl keine Darkspears mehr im Schlingendorntal leben, sind wir immer noch dem Konflikt mit den Skullsplittern verpflichtet.","Also, jagt sie. Sammelt ihre Spaltknochenhalsketten und kehrt zu mir zurück. Helft uns in unserem Krieg und Ihr werdet die Freundschaft der Darkspears erlangen."}, {"Bringt 25 Spaltknochenhalsketten zu Kin'weelay in das Basislager von Grom'gol."}},
[599] = {"Die Blutsegelbukaniere", {"Neee, isses denn wahr! Also planen die Blutsegelbukaniere einen Angriff auf Booty Bay? Diese Schurken haben vielleicht Nerven. Booty Bay wird immer von den Schwarzmeerräubern beherrscht werden!","Das ist ein größeres Unterfangen als ich dachte. Erstattet Flottenmeister Seehorn sofort Bericht über alles, was Ihr erfahren habt! Der alte Seebär ist irgendwo in der Stadt..."}, {"Erstattet Flottenmeister Seehorn Bericht über den geplanten Angriff der Blutsegelbukaniere."}},
[600] = {"Venture Company-Bergbau", {"Die Venture Company hat ein paar Sachen im Schlingendorntal am Laufen, die hart arbeitende Goblins, wie ich es einer bin, davon abhalten, ehrliches Gold zu verdienen. Bitte, Ihr müsst mir helfen!","Die Venture Company betreibt einen Stollen in der Nähe der Kristalladermine im Norden. Sie kommen nicht in die Mine hinein wegen der ganzen Basilisken, aber trotzdem sind sie in der Lage, singende Kristalle aus den umgebenden Hügeln herauszugraben.","Nehmt ihnen die Kristalle ab und zeigt ihnen, dass sie im Dschungel nichts zu melden haben.","Und... ähm... bringt mir diese Kristalle als Beweis!"}, {"Bringt 10 singende blaue Kristalle zu Crank Fizzlebub nach Booty Bay."}},
[601] = {"Wasserelementare", {"Die Wasserelementare wurden ohne Zweifel von Gazban, dem Goblinhexer beschworen, von dem man behauptet, er habe den Stein der Gezeiten entdeckt. Wie Ihr vielleicht wisst, verflucht der Stein der Gezeiten seinen Träger, sodass er ständig zwischen Sein und Nicht-Sein wandelt.","Vielleicht können wir einen Weg finden, dem Fluch entgegenzuwirken. Ein Magier von Dalaran kann die magischen Überreste einer der Armschienen des Wasserelementars für uns analysieren. Ihr müsst wieder zurück zur Insel und einige Exemplare davon herbringen."}, {"Holt 6 Wasserelementararmschienen für Baron Revilgaz in Booty Bay."}},
[602] = {"Magische Analyse", {"Wie ich gehört habe, sind die Magier von Dalaran sehr damit beschäftigt, ihre zerstörte Stadt wieder aufzubauen. Ich bin aber trotzdem sicher, dass Ihr einen finden könnt, der sich etwas Zeit für diese Armschienen nehmen wird. Denn wenn ich etwas über Magier weiß, dann, dass sie einem magischen Rätsel einfach nicht widerstehen können.","Nehmt diese Tasche mit Wasserelementararmschienen und findet heraus, ob sie in irgendeinem Zusammenhang mit dem Fluch des Steins stehen. Sucht vor allem nach dem Erzmagier Ansirem Runeweaver. Ich hatte schon zuvor mit ihm zu tun und er verfügt über unglaubliches Wissen."}, {"Bringt die Wasserelementararmschienen nach Dalaran zu Erzmagier Ansirem Runeweaver."}},
[603] = {"Ansirems Schlüssel", {"Der Magier, der im Besitz dieses Steins ist, verstärkt seine Macht durch ein Zentrum... eine Art Altar. Ihr solltet in der Lage sein, dies zu unterbrechen, indem Ihr es überwältigt.","Wie es der Zufall will, weiß ich auch genau womit: ein alter Dolch, ein weiteres Trollrelikt. Es gibt da nur ein... winzigkleines Problem.","Wisst Ihr, ich fand den Dolch auf einer Forschungsreise im Schlingendorntal, aber ich gab ihn meiner eigenwilligen Tochter, Catelyn, die mir nach Booty Bay weglief, um Piratin zu werden."}, {"Sprecht mit Catelyn in Booty Bay."}},
[604] = {"Die Blutsegelbukaniere", {"Crazz lobt Eure Hingabe an die Schwarzmeerräuber in hohen Tönen. Ich werde Euch mit einer Mission betrauen, die für die Flotte von höchster Bedeutsamkeit ist.","Bespitzelt den so genannten südlichen Kommandoposten, den die Blutsegelbukaniere an den wilden Ufern aufgebaut haben. Überfallt das Lager. Bringt mir ihre Karten zusammen mit ihren schriftlichen Befehlen. Wir werden ganz genau herausfinden, was dieser Abschaum so vorhat."}, {"Flottenmeister Seehorn in Booty Bay will, dass Ihr 10 Schwadroneure der Blutsegelbukaniere tötet und die Blutsegelkarten sowie die Blutsegelbefehle zurückbringt."}},
[605] = {"Singende blaue Splitter", {"Die singenden Kristalle gibt es nur im Schlingendorntal und sie sind für gewisse Gruppierungen äußerst wertvoll. Ich kann diese Kristalle bewegen, aber die verfluchte Venture Company macht es einem ehrlichen Unternehmer wie mir fast unmöglich, überhaupt welche zu sammeln.","Ich würde Euch gern anheuern.","Die Basilisken des Schlingendorntals essen die Kristalle. Dadurch erhalten sie ihre harte Haut und manchmal können Kristalle von ordentlicher Qualität von den Häuten abgesammelt werden.","Ihr könnt sie von jedem Basilisken sammeln, aber die nicht ganz so bösartigen leben an der Küste südlich von Zul'Kunda, im Norden."}, {"Bringt 10 singende Kristallsplitter zu Crank Fizzlebub."}},
[606] = {"Zitterhand Angst einjagen", {"Wenn ich etwas nicht leiden kann, dann sind das Leute, die ihre Spielschulden nicht bezahlen! Und es gibt 'ne Menge Leute, die mir was schulden. Ob Ihr mir wohl beim Eintreiben helfen könnt?","Da ist zuerst mal \"Zitterhand\" Phillipe unten bei den Docks. Er schuldet mir eine Menge nach dem Spiel letzte Woche und ich will mein Geld jetzt! Aber um da dranzukommen, müssen wir ihm gehörig Angst einjagen.","Verlasst die Stadt und jagt alte Nebeltalgorillas. Sucht Euch ein paar nette Innereien heraus und bringt sie zu Zitterhand. Sagt ihm ja nicht, dass sie von einem Tier stammen, er soll ruhig das Schlimmste annehmen."}, {"Bringt 5 Nebeltalinnereien zu \"Zitterhand\" Phillipe nach Booty Bay."}},
[607] = {"Rückkehr zu MacKinley", {"Hier, nehmt diesen Beutel und gebt ihn Seewolf. Und sagt ihm, dass ich ihm alles zurückzahlen wollte! Ich schwöre!!","Ich hab nur ein bisschen viel getrunken und mich verlaufen, und... Ihr wisst ja, wie es so geht. Er wird es verstehen - Ihr müsst nicht erst grob werden!"}, {"Bringt Zitterhands Bezahlung zu \"Seewolf\" MacKinley."}},
[608] = {"Die Blutsegelbukaniere", {"Dieser verdammte Firallon! Könnte kein Boot in einem Stück über den Kristallsee bringen, ganz zu schweigen eine ganze Flotte gegen uns führen.","Die Blutsegelbukaniere werden für ihre Sorglosigkeit teuer bezahlen. Jetzt, da wir ihren Angriffsplan kennen, können wir unseren Gegenschlag starten, bevor sie überhaupt merken, was los ist.","Aber warum sollten wir die ganze Flotte schicken, wenn eine mutige Gruppe von Abenteurern das auch allein schaffen kann? <Name>, nehmt einige von Euren geschicktesten Kameraden zu Firallons Ankerplatz und tötet die Führungskräfte der Blutsegelbukaniere. Ihre Flotte wird bald danach sinken ..."}, {"Flottenmeister Seehorn in Booty Bay will, dass Ihr Kapitän Stillwasser, Käpt'n Kielhol und Flottenmeister Firallon tötet."}},
[609] = {"Voodooschulden", {"Meine Liste wird kürzer, aber da sind immer noch Leute, die mir was schulden.","Als Nächste haben wir da Maury \"Klumpfuß\" Wilkins, Jon-Jon die Krähe und Chucky \"Zehn Daumen\".","Die Schulden dieser räudigen Hunde stehen schon seit Monaten aus und ich dachte schon, dass sie die Stadt verlassen hätten, nur um nicht zahlen zu müssen! Später hab ich gehört, dass sie jetzt verflucht und verhext sind und in den Dschungelruinen ihr Unwesen treiben. Aber ihr Schicksal ist mir ziemlich egal - ich will nur, was mir zusteht!","Sie halten sich in den Ruinen von Aboraz und den Ruinen von Jubuwal nordöstlich von hier auf. Sucht sie und holt das Geld."}, {"Bringt Maurys Klumpfuß, Jon-Jons goldenes Handfernrohr und Chuckys gewaltigen Ring zu \"Seewolf\" MacKinley in Booty Bay."}},
[610] = {"Schönling Duncan", {"Mein VATER hat Euch geschickt? Dachte, der alte Knacker hätte mich mittlerweile aufgegeben.","Es war ja nicht unbedingt sein Fehler, dass ich weggerannt bin, er hätte einfach nicht erwarten dürfen, dass ich Lust auf ein langweiliges Leben hab, alte muffige Bücher lese und mich mit grauhaarigen, steifen, hochnäsigen Magiern abgebe!","Wenn Ihr meinen Dolch braucht, müsst Ihr ihn von \"Schönling\" Duncan wiederholen, einem der Blutsegelbukaniere. Rammt einfach was zwischen seine Schultern und er wird ihn sicherlich sofort rausrücken."}, {"Holt Catelyns Dolch von \"Schönling\" Duncan."}},
[611] = {"Der Fluch der Gezeiten", {"Ihr sucht also nach einer Art Altar? Nun, ich bin zwar kein Experte für Trollruinen wie mein Vater, aber ich habe von einer versunkenen Trollstadt nah bei der Insel gehört, die Ihr sucht. Vielleicht solltet Ihr dort mal suchen.","Ihr habt mein Messer für mich wiedergeholt, also könnt Ihr es Euch auch borgen... Ich will es aber wiederhaben, wenn Ihr fertig seid!"}, {"Zerstört den Altar der Gezeiten mit Catelyns Klinge, tötet Gazban und bringt dann den Stein der Gezeiten sowie Catelyns Klinge zurück zu Baron Revilgaz."}},
[613] = {"Maurys Fuß aufbrechen", {"Verflucht noch eins! Dieser Blödmann Maury hat ein Schloss an seinem Fuß befestigt und ich kann es nicht öffnen! Seine Beute ist in dem Fuß, und das sind seine Schulden!","Wir müssen den Schlüssel finden, dann kriegen wir auch das Schloss auf. Maury hat mit den Ogern der Mosh'Ogg gespielt, in ihrem Hügel im Nordosten. Als er noch lebte, erzählte er mir, dass sie ihn das letzte Mal von dort verjagt hätten, weil er zu viel gewonnen habe. Vielleicht haben die Oger ja seinen Schlüssel.","Wenn sie ihn haben sollten, müsstet Ihr eine ganze Menge von ihnen beseitigen, bevor Ihr den richtigen findet."}, {"Bringt Maurys Schlüssel zu \"Seewolf\" MacKinley in Booty Bay."}},
[614] = {"Die Truhe des Kapitäns", {"Früher hab ich ein Schiff von hier bis ganz nach Ratchet, in Kalimdor gesegelt. Ich war ein erfolgreicher Kapitän mit einem einwandfreien Ruf.","Bis...","Diese verfluchten Meeresriesen mich ruinierten! Sie haben mein Schiff zerschmettert, meine Crew umgebracht und mich in ein Rettungsboot nach Booty Bay gesteckt. Einer dieser Riesen, Gorlash, hat meine Kapitänstruhe gestohlen. Er sagte, er wolle sie als Schnupftabakdose. Diese Dreistigkeit!","Ich habe gehört, dass Gorlash an der Küste östlich von hier sein Unwesen treibt. Findet ihn und bringt mir meine Truhe zurück! Tut das, und Ihr habt Euch die Dankbarkeit eines Kapitäns verdient!"}, {"Bringt Smotts' Truhe zu Hecklebury Smotts nach Booty Bay."}},
[615] = {"Der Entersäbel des Kapitäns", {"Nachdem ich mein Schiff an diese Riesen verlor, kaufte ich mir ein neues. Ich hab es SMOTTS' REVENGE genannt, Vorräte gekauft, eine Crew angeheuert und Segel gesetzt, um diese Schurken ausfindig zu machen. Ich habe sie auch gefunden, aber... sie haben mich besiegt. Sie haben die SMOTTS' REVENGE zerschmettert, meine zweite Crew umgebracht und mich in einem weiteren Rettungsboot ausgesetzt.","Dieses Mal hat ein anderer Riese, Negolash, meinen Entersäbel gestohlen.","Stellt Euch Negolash und bringt mir meinen Entersäbel!","Sprecht mit Sprogger. Er war mein Smutje auf der SMOTTS' REVENGE und hat den letzten Angriff überlebt. Er kann Euch helfen, den Riesen zu finden."}, {"Sprecht mit Sprogger."}},
[616] = {"Die Spukinsel", {"Für Baron Revilgaz höre ich mich immer nach Gerüchten von seltsamen und ungewöhnlichen Dingen in Booty Bay und im Schlingendorntal um. Normalerweise gebe ich ja nicht viel auf Geistergeschichten, aber ich habe ein paar Seeleute in der Taverne über eine Insel nahe der Küste reden hören, auf der es spuken soll. Sie erzählten von Wasserelementaren und einem rasenden Goblingeist.","Wenn Ihr mit Revilgaz sprechen solltet, erzählt Ihr ihm davon?"}, {"Sprecht mit Baron Revilgaz in Booty Bay."}},
[617] = {"Bündelweise Akiris", {"Sucht'er Arbeit, <Name>? Hab vielleicht was für Euch, wenn'er Euch was traut.","Mein Freund Groy verdient sich dumm un' dämlich mit dem Verkauf von dem Zeuch, das die Naga Akirisschilfrohr nennen: is' so 'ne Art wertvolles Meeresschilfrohr. Bin nich' sicher, für was es gut is', aber er bezahlt gut für Bündel von dem Zeuch.","Wenn Ihr mir 10 Stängel von dem Zeuch bringt, sind wa im Geschäft. Die Naga wurden zuletzt am Strand nördlich von hier gesehen."}, {"Bringt 10 Stängel Akirisschilfrohr zu Freibeuter Bloads nach Booty Bay."}},
[618] = {"Negolash entgegentreten", {"Negolash ist ein hungriger Riese.","Als die Riesen unser Schiff zerstörten, ging Negolash schnurstracks in meine Kombüse. Er hatte keinen Appetit auf die Crew wie die anderen Riesen. Negolash wollte Wein... und meine gegrillten Bussardflügel. Die sind meine Spezialität. Für das Rezept musste ich bis ins Ödland reisen!","Wenn Ihr den Riesen anlocken wollt, dann besorgt Euch etwas Wein und einen Haufen Bussardflügel. Legt sie in unser altes Rettungsboot, an der Küste südöstlich von hier. Sobald Negolash das Essen riecht, wird er bestimmt kommen!"}, {"Bringt 10 gegrillte Bussardflügel und 5 Flaschen Dschungelrankenwein zu Käpt'n Smotts Rettungsboot.","Tötet Negolash und bringt Smotts' Entersäbel zu Captain Smotts ins Schlingendorntal."}},
[619] = {"Negolash anlocken", nil, nil},
[621] = {"Zanzils Geheimnis", {"Zanzil der Ausgestoßene hält sich mit seinen Anhängern in den Ruinen von Aboraz und Jubuwai auf, nordöstlich von Booty Bay. Sein Stamm hat ihn verbannt. Warum? Nun...","Zanzil hat eine Methode, den Geist von anderen zu beugen, und seine Anführer fürchten ihn. Er benutzt eine Mixtur von Dschungelpflanzen, die den Willen seiner Anhänger unterdrücken und ihre Körper stärken.","Seine Mixtur könnte sehr wertvoll für diejenigen sein, die andere zu kontrollieren wünschen.","Bringt mir Proben seiner Mixtur und ich werde einen Nutzen für sie finden."}, {"Bringt 12 Proben von Zanzils Mixtur zu Crank Fizzlebub nach Booty Bay."}},
[622] = {"Rückkehr zu Corporal Kaleb", {"Während Ihr fort wart, habe ich den Topf, den Ihr mir brachtet, repariert. Ich habe sogar noch eine zusätzlich Dampfpfeife angebracht, und seht mal! Da ist ein neues Hackbeil am Boden, das kann Beinknochen durchtrennen.","Ja, ja, Krazeks Spezialtopf weiß, was Köche wünschen! Sein Besitzer wird bestimmt erfreut sein."}, {"Bringt Krazeks reparierten Topf zu Corporal Kaleb im Rebellenlager."}},
[623] = {"Bündelweise Akiris", {"Ach ja, <Name>, fein dass Ihr hier seid. Während Eurer Abwesenheit hab ich 'ne Nachricht von Groy gekricht. Er sachte, dass er mit der letzt'n Lieferung sehr zufrieden war und dass 'n Kunde von ihm noch mehr von dem Zeuch hab'n will und das sofort.","Ich fahr erst inner Woche wieder nach Theramore, aber wenner den Weg nich' scheut, dann könnter auch gleich das Zeuch für mich mitnehmen.","Abenteuerlustich? Wissen tut er ja, wo die Marschen von Dustwallow sin', nich'? Ich wär aber vorsichtich, Theramore kann gefährlich sein, wenn man inne falschen Kreise kommt."}, {"Bringt ein Bündel Akirisschilfrohr zu Freibeuter Groy nach Theramore."}},
[624] = {"Cortellos Rätsel", {"Eine Brücke verziert und stark frequentiert","Unter ihr werdet Ihr finden was Euch sehnlichst erwartet","Manche nennen es Hinweis andere Tipp","Es liegt östlich der Schlucht der Totenwinde und südlich vom Rotkammgebirge","Für unendliche Zeit genau dort wird es liegen","In den Sümpfen des Elends sich für immer wiegen"}, {"Löst das Rätsel!"}},
[625] = {"Cortellos Rätsel", {"Im fernen Kalimdor wartet ein Land","Die Hoffnung hielt dort den Gefahren nicht stand","Das Wetter östlich des Brachlandes ist harsch","Dunkel und Tod droh'n in der Dustwallowmarsch","Doch in einer Höhle, Richtung Südwest","Liegt ein Hinweis für alle, die wagen den Test."}, {"Löst das Rätsel!"}},
[626] = {"Cortellos Rätsel", {"Nach Lordaeron! Schwerer Aufruhr dort wütet","Soldatenmut wird mit dem Tode vergütet","Sucht Euren Weg durch den Aufstand dort","und Eure Such' ist beendet an einem Ort","Am Fuße der großen Fälle, entlang der Küste im Hinterland","Im Wasser dort tief unten, nehmt Ihr Euren Schatz in die Hand!"}, {"Löst das Rätsel!"}},
[627] = {"Gefallen für Krazek", {"Es gibt eine Berghöhle in der Schlucht der Trockenstoppel, weit im Norden des Arathihochlands. In dieser Höhle findet sich ein seltenes Erz: Blutstein. Der Blutstein in dieser Höhle ist zwar nicht von höchster Qualität, aber auch Blutstein von minderer Qualität ist wertvoll... für diejenigen, die wissen, wie er zu nutzen ist.","Ich hätte gerne etwas davon.","Ich werde Euren Spezialtopf reparieren, aber während ich daran arbeite, möchte ich, dass Ihr mir einige Blutsteinproben bringt. Bringt mir genügend und Ihr erhaltet einen Bonus.","Oh, und falls Ihr nicht wisst, wie man ihn abbaut... findet jemanden, der es kann!"}, {"Bringt 4 Ladungen geringes Blutsteinerz zu Krazek nach Booty Bay."}},
[628] = {"Exzelsior", {"Die Innenseiten sind fertig gestellt, jetzt fehlt nur noch die äußere Schicht. Dafür verwende ich nur die besten Häute der alten Krokilisken. Sie sind widerstandsfähiger und eine bessere Leinwand - wenn Ihr es so nennen wollt - für die Muster und Akzente, die Drizzliks Exzelsiorlinie ausmachen.","Es ist allerdings äußerst schwierig, diese vollkommene Haut zu finden; die Einzige, die meinen Plänen gerecht wird, ist die eines alten Salzwasserkrokilisken. Die Alten kommen nur heraus, um die anderen Salzwasserkroks zu verteidigen, also werdet Ihr wahrscheinlich einige töten müssen, um einen von der Sorte herauszulocken."}, {"Bringt die Haut eines alten Krokilisken zu Drizzlik nach Booty Bay."}},
[629] = {"Das finstere Riff", {"Vor langer Zeit, als ein großes Beben die Welt erschütterte, versank eine alte Trollstadt in den Wassern der ungezähmten Küste im Nordwesten. Wir nennen diesen Ort das finstere Riff, denn Murlocs bewohnen jetzt die Ruinen der Stadt und greifen jeden an, der sich ihnen nähert.","In diesen alten Ruinen liegt eine alte Schrifttafel, die die uralte Geschichte von Gri'lek erzählt, einem Helden der Trolllegenden. Diese Geschichte ist dem Darkspearstamm heilig und auch wenn die Tafel zu groß ist, um sie zu bewegen, so möchte ich doch einen Splitter davon, damit ich ihn in unserer neuen Heimat in Orgrimmar in einen Schrein legen kann."}, {"Bringt 1 Schrifttafel-Splitter zu Kin'weelay in das Basislager von Grom'gol."}},
[630] = {"Flaschenpost", {"<Name>, ich setze meine ganze Hoffnung in Euch und jeden, den Ihr gewinnen könnt, um mich zu befreien. Der große Blaumähnenkönig Mukla hat mich gefangen genommen. Er befehligt die Bestien dieser Insel und sie halten mich mit Hilfe dieser Kette gegen meinen Willen hier fest. Wenn Ihr ihn töten und den Schlüssel für diese Fessel finden könnt, stehe ich tief in Eurer Schuld.","Ich habe ihn oft auf der südöstlichen Seite der Insel gehört, aber bitte seid vorsichtig: Man darf ihn nicht unterschätzen."}, {"Erschlagt König Mukla und kehrt mit dem Schlüssel zu Prinzessin Poobah zurück, um sie zu befreien."}},
[631] = {"Der Thandol-Übergang", {"Eine enorme Explosion donnerte durch die Hügel hinweg. 's war furchtbar, sag ich Euch!","Gute Soldaten starben bei der Verteidigung des Thandol-Übergangs. Longbraid sagt, wir werden uns noch an den Dunkeleisenzwergen rächen.","Aber wir befinden uns in einer Welt voller Schmerz, und Verstärkung ist noch lange nicht in Sicht. Longbraid hat Ol' Rustlocke als Späher zur östlichen Brücke geschickt, um rauszufinden, ob wir sie sichern können.","Das ist aber schon Tage her. Und jetzt, wo Dun Modr belagert wird, haben wir nicht genug Leute, um nach ihm zu suchen.","Vielleicht könnt Ihr ihn finden..."}, {"Rhag Garmason will, dass Ihr Ol' Rustlocke findet."}},
[632] = {"Der Thandol-Übergang", {"Es sieht ganz so aus, als sei der arme Ol' Rustlocke in einen Hinterhalt der Dunkeleisenzwerge geraten. Ihr bemerkt ein Pergament neben seinen geballten Fäusten.","Erstattet Rhag Garmason Eure traurigen Neuigkeiten und zeigt ihm, was Ihr gefunden habt."}, {"Meldet Euch bei Rhag Garmason zurück und zeigt ihm das Pergament, das Ihr bei Rustlockes Leichnam gefunden habt."}},
[633] = {"Der Thandol-Übergang", {"Um Himmelswillen, nein! Armer Rustlocke. Das ist wirklich eine fürchterliche Tragödie.","Sein Tod darf nicht vergebens sein. Das Pergament, das Ihr entdeckt habt, zeigt an, dass Kam Deepfury, der hinter dem eigentlichen Angriff steckt, dafür gesorgt hat, dass ein Behälter mit Sprengstoff hinter dem Thandol-Übergang im Arathihochland versteckt wurde.","Wenn dieser Sprengstoff zur Brücke gelangt, wird unsere Hauptversorgungsverbindung in den Norden gekapp! Der Behälter muss zerstört werden, <Name>! Kehrt zu mir zurück, wenn Ihr Eure Mission erfüllt habt.","Ihr seid unsere einzige Hoffnung."}, {"Zerstört den Sprengstoffbehälter"}},
[634] = {"Hilfegesuch an die Allianz", {"Ich bitte Euch noch um einen kleinen Gefallen, <Klasse>. Wir erwarten die Reserven von Ironforge. Roggo hat jedoch von einem Lager der Allianz im Norden des Arathihochlands bei der Zuflucht erfahren.","Reist dorthin und macht Captain Nials ausfindig. Fragt den guten Captain, ob die Allianz einige Soldaten erübrigen kann, die uns behilflich sein können, Dun Modr und den Thandol-Übergang zurückzuerobern."}, {"Macht Captain Nials bei der Zuflucht ausfindig."}},
[635] = {"Kristall in den Bergen", {"Dieser Splitter pulsiert während Ihr ihn haltet...","Ihr habt das Gefühl, als ob ein Bitten vom Splitter ausgeht, als ob jemand versucht, mit Euch zu kommunizieren, jemand, der verzweifelt Eure Hilfe sucht.","Vor Eurem inneren Auge erscheint eine Vision - die nördlichen Berge von Arathi und ein blitzender Kristall, der dort aus dem Boden ragt.","Ihr spürt, wie es Euch dorthin zieht."}, {"Findet den Kristall aus Eurer Vision."}},
[636] = {nil, nil, nil},
[637] = {"Sully Balloos Brief", {"Der durchnässte und zerrissene Umschlag löst sich in Euren Händen auf und Euch bleiben nur einige Seiten eines Briefes, der mit viel Mühe geschrieben wurde.","Der Brief ist adressiert an eine Mrs. Sara Balloo in Ironforge und beginnt mit den Worten, \"Meine liebe Sara ...\"."}, {"Bringt Sully Balloos Brief zu Sara Balloo."}},
[638] = {"Trollbane", {"Da Thoras Trollbane tot ist und Stromgarde in Schutt und Asche liegt, hat Vol'jin Anweisungen gegeben, dass wir alles tun sollen, um das legendäre Schwert Trol'kalar den menschlichen Händen zu entreißen, auf dass wir es in zukünftigen Kämpfen gegen die Trolle von Zul'Gurub nutzen können.","Zengu, einer unserer erfahrenen Jäger, wurde nach Hammerfall im Nordosten des Arathihochlands entsandt, um seine Beschaffung zu leiten."}, {"Reist nach Hammerfall im Arathihochland und sprecht mit Zengu."}},
[639] = {"Siegelzeichen von Strom", {"In den alten Zeiten kämpften Trolle gegen das Arathorianische Reich und unseren geschicktesten Feind, Lord Ignaeus von Strom, genannt Trollbane. Er ritt in die Schlacht und schwenkte dabei sein Schwert Trol'kalar - was in ihrer alten Sprache Trolltöter heißt.","Aber jetzt sind die Söhne von Trollbane geschwächt, ihre Festung ist dem Verfall preisgegeben.","Das Schwert wird von mehreren Siegelzeichen beschützt, die unter den Überlebenden von Stromgarde verteilt wurden. Wir müssen sie alle bekommen.","Das Erste wurde von dem Syndikat mitgenommen, einer ihrer Männer in Stromgarde sollte es haben."}, {"Holt das Siegelzeichen von Strom für Zengu in Hammerfall."}},
[640] = {"Das zerbrochene Siegelzeichen", {"Thoras Trollbanes Grabmal wird durch drei Siegelzeichen beschützt - das Erste davon habt Ihr dem Syndikat entwendet. Es wird allerdings schwieriger sein, an die anderen beiden heranzukommen, da sie sich bei der Miliz von Stromgarde befinden und diese sich gut verschanzt hat.","Das Siegelzeichen von Thoradin wurde von Thoras' Sohn, Galen, in 11 Teile aufgeteilt und unter den Verteidigern von Stromgarde verteilt. Holt die Stücke und bringt sie zu Tor'gan, dem Trollschamanen, er kann sie wieder zum vollständigen Siegelzeichen binden."}, {"Holt die 11 Siegelzeichen-Fragmente von den Verteidigern in Stromgarde und bringt sie zu Tor'gan in Hammerfall."}},
[641] = {"Siegelzeichen des Thoradin", {"Die Rekonstruktion ließ sich problemlos durchführen. Das Siegelzeichen kann nun benutzt werden, um das Siegel auf Trollbanes Grabmal aufzuschließen. Bringt es zu Zengu, er hat ohne Zweifel weitere Aufgaben, die Ihr erfüllen müsst."}, {"Bringt das wiederhergestellte Siegelzeichen von Thoradin zu Zengu in Hammerfall."}},
[642] = {"Die gefangene Prinzessin", {"Mein Name ist Myzrael. Ich bin eine Prinzessin der Erde und meine Häscher, die Riesen, halten mich tief unterhalb des Arathihochlandes fest. Diese Kristallsplitter sind meine einzige Möglichkeit, mit der oberen Welt zu sprechen.","Bitte helft mir! Kobolde der Trockenstoppel, die Verbündete der Riesen sind, haben einen Splitter wie diesen hier in ihrer Schlucht im Osten. Um die Gruppe zu aktivieren, müsst Ihr die Körner von Myzrael von den Kobolden holen und sie an der Gruppe anwenden.","Ich flehe Euch an, <Name>, helft mir!"}, {"Sammelt 12 Körner von Myzrael und bringt sie zu den regenbogenfarbenen Splittern in der Schlucht der Trockenstoppel."}},
[643] = {"Siegelzeichen von Arathor", {"Obwohl man kaum behaupten kann, dass sie Stromgarde kontrollieren, geschweige denn das gesamte Arathihochland, marschiert der menschliche Prinz trotzdem mit den Überbleibseln seiner Kavallerie durch das Land. Ich versteh ja nicht, warum er das tut, aber wer versteht schon, wie Menschen ticken, hm?","Was auch immer seine Gründe sein mögen, er hat eines der Siegelzeichen Lieutenant Heldenruf, dem Anführer seiner Kavallerie übergeben. Findet ihn und bringt mir sein Siegelzeichen zurück."}, {"Holt das Siegelzeichen von Arathor und bringt es Zengu in Hammerfall."}},
[644] = {"Siegelzeichen von Trollbane", {"Das letzte Siegelzeichen, das benötigt wird, um das Siegel auf dem Grabmal von Thoras Trollbane zu öffnen, wird von seinem Sohn höchstpersönlich aufbewahrt. Er hält sich tief innerhalb Stromgardes auf, in einer Kapelle in den von der Miliz bewachten Distrikten. Prinz Galen wird gut bewacht und da er selbst ein ausgezeichneter Krieger ist, wird diese Aufgabe wahrscheinlich die schwierigste sein."}, {"Tötet Prinz Galen Trollbane und bringt das Siegelzeichen von Trollbane zu Zengu in Hammerfall."}},
[645] = {"Trol'kalar", {"Was ist das? Die vier Siegelzeichen haben sich zu einem Siegelzeichen zusammengefügt. Ohne Zweifel ist dieses in der Lage, die Schutzzeichen zu zerbrechen, die sich auf Trollbanes Grabmal befinden.","Trollbanes sterbliche Überreste sind im Sanktum hinter der Kapelle in Stromgarde zur Ruhe gebettet worden. Holt Trol'kalar."}, {"Holt Trol'kalar aus Trollbanes Grabmal in Stromgarde."}},
[646] = {"Trol'kalar", {"Das Licht verglüht langsam, das Schwert Trol'kalar, das sich in einer steinernen Scheide befindet, ist freigegeben."}, {"Bringt Trol'kalar zu Zengu nach Hammerfall."}},
[647] = {"MacKreels Fusel", {"Was hab ich gestern gesoffen! Longbraid würde mich sofort köpfen, wenn er wüsste, dass ich während meiner Wache eingepennt bin.","Mein Schädel brummt vielleicht. Fühlt sich an, als wäre eine ganze Brigade von Belagerungsmaschinen drübergerollt, dieses Gerumpel.","Oh weh, so spät schon! Hab Braumeister Bilger in Southshore versprochen, dass ich meine Schulden mit ein wenig Fusel bei ihm bezahlen würde.","Und ich hab nur noch 15 Minuten! Könntet Ihr ihm diese Lieferung nicht bringen, bitte? Und beeilt Euch!"}, {"Bringt MacKreels Fusel zu Braumeister Bilger nach Southshore."}},
[648] = {"Rettet OOX-17/TN!", {"Aus dem Roboter ertönt erneut die blecherne Stimme des Gnoms:","\"Ich muss OOX-17/TN an einen offenen, sicheren Ort schaffen, wo er die langwierige Startprozedur beginnen kann. Er hat eine eingebaute Tarnvorrichtung, aber ich brauche etwas Zeit, um sie von hier aus wieder funktionstüchtig zu machen. Ihr müsst den Roboter von seinem derzeitigen Standort nach, sagen wir, Steamwheedle bringen! Das sollte der perfekte Ort für mein Vorhaben sein, und bis Ihr dort seid, sollte ich auch alles vorbereitet haben und online gehen können.\"","\"Eskortiert ihn sicher bis zum Hafen und kommt dann zu mir nach Booty Bay! Oglethorpe Obnoticus - Ende!\""}, {"Eskortiert OOX-17/TN nach Steamwheedle und meldet Euch dann bei Oglethorpe Obnoticus in Booty Bay."}},
[649] = {"Bergung der Welle", {"Ich habe eine kleine Bergungsaktion für Euch, und falls Ihr ein Liebhaber von feiner Welle seid, so wie ich, dann werdet Ihr diese Aufgabe lieben. Welle!","Das ist ein Getränk, Schafskopf.","Ein verlassener Außenposten der Horde verfügt über ein Lager mit Hinterland-Honigwelle aus der Zeit, als sie noch im Geschäft waren. Solch feine Welle bekommt Ihr sonst nirgendwo! Einer von Maltons Strohmännern weiß Bescheid, also begebt Ihr Euch dorthin und bringt mir eine Kiste verpackter Welle und zwei für meine eigene Sammlung! Sprecht mit Malton, er wird Euch die Einzelheiten mitteilen."}, {"Sprecht mit Malton Droffers in Orgrimmar."}},
[650] = {"Bergung der Welle", {"Der Elf, mit dem Ihr sprechen müsst, ist Gilveradin Sunchaser. Er erkennt jeden Wein, mit Jahrgang und allem, allein am Geruch! Er kann die Welle verpacken, die wir benötigen, daher müsst Ihr mit ihm sprechen. Sechs Flaschen müssen verpackt werden, zwei für meinen Vater, und so viele wie er selbst will... und glaubt mir, er wird welche wollen. Wenn Ihr die Welle verpackt habt und auch die zwei Flaschen für meinen Vater, dann kommt hierher zurück!","Gilveradins Lager befindet sich in der Nähe einer Hochelf-Lodge im Hinterland, und diese Hochelfen führen nichts Gutes im Schilde. Glaubt mir."}, {"Sprecht mit Gilveradin Sunchaser; sein Lager befindet sich im Hinterland."}},
[651] = {"Steine der Bindung", {"Vier Steine der Bindung sind in ganz Arathi verteilt. Diese Steine halten mich unter der Erde fest.","Um sie zu öffnen, müsst Ihr den stärksten öffnen: den Stein der inneren Bindung. Um das tun zu können, müsst Ihr Schlüssel von den drei Übrigen sammeln.","Diese Steine werden von Wächtern bewacht, aber wenn Ihr es geschickt anstellt oder Glück habt, findet Ihr vielleicht einen Weg, sie auszuschalten."}, {"Holt den brennenden Schlüssel, den Wappenschlüssel und den donnernden Schlüssel vom Stein der westlichen Bindung, dem Stein der östlichen Bindung und dem Stein der äußeren Bindung."}},
[652] = {"Den Hauptstein zerbrechen", {"Das Öffnen der Steine der Bindung hat den Großteil meiner Fesseln gelöst, aber ein Schloss hält mich immer noch gefangen. Es ist der Hauptstein, den Ihr in diesem Kreis von Steinen sehen könnt. Und der Schlüssel wird von einem meiner Häscher bewahrt, dem Steinbehüter Fozruk.","Ihr müsst diesen Schlüssel, den die Steinbehüter die Rute der Ordnung nennen, von Fozruk in Besitz nehmen und mit ihm den Hauptstein öffnen.","<Name>, Ihr habt schon so viel für mich getan. Nur noch eine kleine Hilfe... und meine Dankbarkeit wird die Erde in ihren Grundfesten erschüttern!"}, {"Findet und tötet Fozruk. Bringt die Rute der Ordnung zum Hauptstein im Arathihochland."}},
[653] = {"Myzraels Verbündete", {"<Myzrael spricht.>","Frei! ICH BIN FREI! Ich bin frei und kann meine Kräfte sammeln, verborgen vor denen, die mich gefangen nahmen. Denn wenn ich mich ihnen jetzt stellen müsste, würden sie mich sicherlich überwältigen und erneut einkerkern.","Aber bald werde ich mich den Riesen stellen und sie werden es bitter bereuen, mich als Gefangenen gehalten zu haben!","Ihr seid ein edelmütiger Verbündeter, <Name>. Ich werde Eure Hilfe bald wieder benötigen. Wenn ich bereit bin, müsst Ihr mich an die Oberfläche beschwören.","Sprecht mit Gerrig Bonegrip. Er befindet sich in Ironforge in einem Geschäft im düsteren Viertel. Er weiß, wie ich beschworen werden kann."}, {"Sprecht mit Gerrig Bonegrip im düsteren Viertel von Ironforge."}},
[654] = {"Tanaris-Feldproben", {"Nach Aktivierung der Kraftquelle beginnt das Feldtestset zu glühen und zu vibrieren. Es scheint, als würde es funktionieren wie geplant.","Laut Chefingenieur Bilgewhizzle habt Ihr zwei Stunden Zeit, um die benötigten Daten in der Tanariswüste zu sammeln. Falls Ihr sie nicht innerhalb von zwei Stunden sammelt, gehen alle Daten verloren und Ihr müsst eine weitere Kraftquelle von ihm erstehen.","Kehrt zu ihm zurück, wenn Ihr Eure Aufgabe erfüllt habt."}, {"Sammelt akzeptable Proben von 8 Basilisken, 8 Hyänen und 8 Skorpionen. Bringt das Testset zurück nach Gadgetzan zu Chefingenieur Bilgewhizzle, bevor die Kraftquelle erschöpft ist."}},
[655] = {"Hammerfall", {"Ich erinnere mich, als wär es gestern gewesen... So viele Jahre ohne Hoffnung, mit Holz und Stahl, die mich an diesen fürchterlichen Ort banden. Und dann endlich, endlich! Da war der Kriegshäuptling. Doomhammer in seiner Mitternachtsrüstung, sein Kriegshammer hoch erhoben.","Es war... zu schrecklich... Dass der Schlag eines Feiglings unseren größten Helden niederschlagen konnte. Er kam von hinten angestürmt auf seinem Pferd und dann rammte er seine Lanze einfach durch ihn hindurch... Ich fiel auf die Knie. Welche Hoffnung gab es noch? Welche Hoffnung gibt es noch..."}, {"Sprecht mit Tor'gan in Hammerfall."}},
[656] = {"Beschwören der Prinzessin", {"Ihr müsst Myzrael besiegen, bevor ihre Kraft größer wird. Wenn Ihr es nicht tut, wird sie ihre Häscher herausfordern, sobald sie genug Stärke gesammelt hat! Einige glauben zwar, dass man sie kontrollieren könnte, aber wenn sie kontrolliert werden kann, warum haben die Riesen sie dann überhaupt in Fesseln gelegt?","Seltsame Logik, nicht?","Um sie zu besiegen, beschwört sie bei den Splittern von Myzrael, wo Ihr zuerst mit ihr spracht. Besiegt sie und sammelt ihre geisterhaften Fesseln ein. Bindet diese dann an die Splitter von Myzrael.","Tut das, und sie wird erneut gefangen sein."}, {"Geht zu den Splittern von Myzrael, beschwört und besiegt sie.","Sammelt ihre geisterhaften Fesseln, und bindet diese an die Splitter von Myzrael."}},
[657] = {"Hinweise auf eine neue Seuche?", {"<Quae beginnt, den Ordner durchzusehen.>","Das IST interessant. Es scheint, als habe Apotheker Jorell an einigen tödlichen Experimenten hier in Go'Shek gearbeitet. Bin nicht sicher, was das mit Hillsbrad zu tun hat, aber Phin würde es nicht gefallen, wenn wir das einfach so laufen ließen.","Während Ihr fort wart, hatten wir ein Zusammentreffen mit ein paar Orcs und ich bin nicht in der geeigneten Verfassung, um Kinelory zu helfen. Wenn Ihr es vermögt ... Sie wird Schutz gut gebrauchen können, wenn sie zum Hof geht, um zu stehlen, was Jorell noch nicht nach Tarrens Mühle geschickt hat."}, {"Sprecht mit Kinelory."}},
[658] = {"Hinweise auf eine neue Seuche?", {"Phin hat Euch also geschickt, um nach uns zu sehen? Wundert mich nicht - er macht sich zu viele Sorgen.","Wir beobachten den Hof seit Tagen, und Kin sagt, es sei noch gar nicht so lange her, dass sie einen Kurier aus dem kleinen Haus da südlich hat kommen sehen.","Sie erzählt viel, aber ich kann ihrem Instinkt immer trauen, wenn wir uns auf gefährlichem Terrain befinden.","Ihr seid nicht zufällig ein geschickter Fährtenleser, oder? Wir glauben, dass der Kurier Notizen nach Tarrens Mühle bringen könnte. Wenn wir diese Dokumente in die Hände bekommen könnten, könnten wir vielleicht Näheres über die Vorgänge in Hillsbrad erfahren."}, {"Findet den Kurier der Verlassenen und bringt den versiegelten Ordner zu Quae in der Nähe von Go'Sheks Hof."}},
[659] = {"Hinweise auf eine neue Seuche?", {"Seid mir gegrüßt, <Name>. Ich bin der Anführer einer Abenteurergruppe, die die seltsamen Vorgänge in diesem Gebiet untersucht.","Mein Cousin in Hillsbrad musste letztens seinen Hund Stanley einschläfern lassen, weil das Tier seltsamerweise auf eine immense Größe heranwuchs und jeden bösartig angegriffen hat, der ihm näher kam.","Ich glaube, dass die Untoten darin verwickelt sind, und wir suchen nach Anzeichen, die das bestätigen.","Zwei meiner Kameraden, Quae und Kinelory, haben sich zur Go'Sheks Hof im Arathihochland aufgemacht und haben seit Tagen nichts von sich hören lassen. Könntet Ihr sie für mich finden? Ich mache mir langsam Sorgen."}, {"Findet Quae und Kinelory in der Nähe von Go'Sheks Hof im Arathihochland."}},
[660] = {"Hinweise auf eine neue Seuche?", {"Nun gut, <Name>. Bereit, wenn Ihr es seid.","Sollen wir?"}, {"Beschützt Kinelory bis sie zu Quae zurückkehrt."}},
[661] = {"Hinweise auf eine neue Seuche?", {"Kinelory sah nach einer Nacht in der Taverne schon schlechter aus als jetzt. Ihr habt sie offensichtlich sehr gut beschützt.","Diese Gegenstände, die sie von der Farm mitgebracht hat, sollten Phin zumindest einen Anhaltspunkt davon geben, was in Arathi so vor sich geht. Wir werden hier noch ein Weilchen bleiben, nur um sicherzugehen, dass nach Eurem Angriff kein Gegenschlag kommt. Wärt Ihr bereit, nach Southshore zurückzukehren und mit Phin zu sprechen?"}, {"Findet Phin Odelic in Southshore."}},
[662] = {"Tiefsee-Bergung", {"O'Breen führte die Schiffe in die Bucht. Wir waren ziemlich sicher, dass der Schatz dort zu finden sei. Die HARBINGER konnte noch sicher andocken, aber als die TOLLE MINNA und die SILBERGEIST näher kamen, senkte sich der Meeresspiegel plötzlich ab. So was Seltsames hab ich mein Lebtag nich' gesehn!","Diese beiden Schiffe sind auf irgendetwas aufgelaufen... eine Art Tempel im Riff. Sie sind irre schnell gesunken, eine echte Tragödie.","Wir müssen ihre Logbücher und auch ihre Karten wiederbekommen, bevor wir hier wieder weg sind. Wir dürfen diese Informationen nicht einfach für neugierige Augen zurücklassen."}, {"Der erste Maat Nilzlix will, dass Ihr die Karten und Logbücher von der TOLLEN MINNA und der SILBERGEIST zurückholt."}},
[663] = {"Land Ho!", {"Lolo sieht was Winziges, es ist ein <Volk>! Immerzu Ausschau halten, ja, ja, das tut Lolo!","Willkommen in der Faldirbucht. Kapitän O'Breen hat gesagt, dass wir nur für ein paar Stunden hier wären. Grad lang genug, um den Schatz einzusammeln und zurück nach Booty Bay zu segeln.","Aber Lolo denkt, dass wir noch sehr lange hier bleiben werden. Wir haben die anderen beiden Schiffe von unserer Formation verloren. Die arme SILBERGEIST und die arme TOLLE MINNA. Sie ruhen jetzt auf dem Grund des Meeres!","Lolo schlägt vor, dass Ihr mit Kapitän O'Breen sprecht, wenn Ihr länger hier bleiben wollt."}, {"Sprecht mit Kapitän O'Breen in der Faldirbucht."}},
[664] = {"Ertrunkene Trauer", {"Grr, 's ist ein elendes Leben. Ich sollte in meinem nassen Grab liegen und nicht hier am Feuer sitzen und frische Luft atmen.","Die Crew hat ihr Bestes gegeben um meine wunderschöne TOLLE MINNA zu retten. Gesegnet seien die Jungs, gesegnet ihre Herzen.","O'Breen hat es sicher ans Ufer geschafft, aber wir hatten nicht so viel Glück. Das Meer sank einfach auf uns herab, ich schwöre es!","In der einen Sekunde lagen wir auf dem Wasser und in der nächsten stürmte das Wasser auf uns ein. Und diese Bestien! Haben alle Überlebenden getötet. Elender Nagaabschaum. Mir gingen die Lichter aus und ich kam hier oben wieder zu mir, gerettet.","Ich will diese Biester tot sehen."}, {"Captain Steelgut in der Faldirbucht will, dass Ihr 10 Räuber der Säbelflossen und 3 Zauberhexerinnen der Säbelflossen tötet."}},
[665] = {"Versunkener Schatz", {"Da wir jetzt richtige Schwarzmeerräuber sind, ist es nun unsere Aufgabe, Herrn O'Breen bei der Suche nach dem verlorenen Elfenschatz behilflich zu sein.","Es ist fast unmöglich, die Edelsteine ohne Hilfe im dunklen Meer zu finden. Der Doktor hat eine Schutzbrille konstruiert, die dabei hilft. Er muss die Schutzbrille mit der Energie aufladen, die von dem verzauberten Stein in der Höhle auf dem Hügel dort ausgeht.","Aber die Höhle ist verflucht! Sobald wir uns ihr nähern, werden wir überfallen. Wenn Ihr mich verteidigt, kann ich die Energie vom Stein in die Schutzbrille leiten."}, {"Begleitet Professor Phizzlethorpe zur Höhle und zurück."}},
[666] = {"Versunkener Schatz", {"Der Schatz befand sich so lange auf dem Meeresboden, dass die Edelsteine von einer dicken Kalkschicht umgeben sind. Aber die Macht, die in dieser Schutzbrille gefangen ist, wird Euch ermöglichen, sie leicht zu finden.","Ein wenig gnomischer Einfallsreichtum ist schon was Schönes!","Also nehmt Euch die Edelsteinjagdschutzbrille, <Name>, und versucht, ein wenig vom verlorenen Schatz für Kapitän O'Breen einzusammeln.","Ich würde ja selbst hinuntertauchen, aber... ähem... ich, nun ich muss mich um äußerst wichtige wissenschaftliche Dinge kümmern, oben auf dem sicheren, trockenen Land... ähem, ja."}, {"Doktor Draxlegauge in der Faldirbucht will, dass Ihr 10 Elfen-Edelsteine einsammelt und die Edelsteinjagdschutzbrille zurückgebt, sobald Ihr fertig seid."}},
[667] = {"Tod von unten", {"Das ist übel. Es scheint, als ob die Säbelflossen sich zum Angriff sammeln.","Naga sind bekannt für ihre Ruchlosigkeit im Kampf. Sie werden so lange angreifen, bis ich tot bin.","Ich schätze mein eigenes Leben nicht höher als das meiner Crew, aber Ihr müsst helfen, mich zu verteidigen. Die Crew ist nicht in der Lage, ohne meine Kenntnisse des Meeres nach Booty Bay zurückzufinden.","Die Naga werden vom Meer her kommen. Ihr müsst mit mir hier oben bleiben, um den Angriff abzuwehren. Bemannt die Kanone und treibt sie zurück.","Seid Ihr bereit, <Name>?"}, {"Beschützt Shakes O'Breen während des Angriffs."}},
[668] = {"Versunkener Schatz", {"Wir sollten Kapitän O'Breen nicht warten lassen. Er wird diese Edelsteine sofort sehen wollen. Darum sind wir ja schließlich hier!","Und nach ein paar Wochen Zusammenleben mit diesen Piraten sind der Professor und ich zum Schluss gekommen, dass es ganz und gar nicht gut für unsere Gesundheit ist, wenn sie denken, dass wir uns ihre Schätze einverleiben wollen.","Hier, <Name>, bringt das zu O'Breen."}, {"Bringt die Elfen-Edelsteine zu Kapitän O'Breen."}},
[669] = {"Versunkener Schatz", {"Flottenmeister Seehorn will über unseren Fund sofort informiert werden.","Aber, wie Ihr selbst sehen könnt, sind wir nicht unbedingt in der Lage, von hier wegzukommen. Es ist nicht nur der zu niedrige Wasserstand, sondern auch diese verdammten Viecher, die wir gestört haben, und die jetzt jede unserer Bewegungen aufs Genaueste beobachten.","Ihr könnt den Schwarzmeerräubern einen sehr großen Dienst erweisen, wenn Ihr Flottenmeister Seehorn in Booty Bay davon in Kenntnis setzen könntet, dass wir den Schatz gefunden haben und dabei sind, weitere Steine zu bergen. Bringt ihm diese Probe zum Beweis."}, {"Bringt die Probe zu Flottenmeister Seehorn nach Booty Bay."}},
[670] = {"Versunkener Schatz", {"Sagt, <Name>, Ihr seht aus wie jemand, der einem Abenteuer nicht abgeneigt ist. Meine Flotte liegt hier in Booty Bay fest, bis wir unsere Vorräte auffüllen können.","Hier und auch außerhalb braut sich so einiges zusammen und ich muss ein paar äußerst geheime Nachrichten an Shakes O'Breen übermitteln, der vor der Arathi-Küste festsitzt.","Kann ich auf Euch vertrauen, dass Ihr diese Nachricht sicher und unberührt überbringt?"}, {"Bringt Seehorns Brief zu Kapitän O'Breen im Arathihochland."}},
[671] = {"Elende Magien", {"Ich fühlte etwas Fremdes in der Luft... ein Gefühl, das mein Blut köcheln lässt. Ich hatte auch schon einen Verdacht. Ich bin ausgezogen, um die Quelle meiner Unruhe aufzuspüren, und im Northfold-Anwesen im Westen fand ich restliche Spuren einer Dämonenbeschwörung. Als ich näher kam, begann mein Blut zu sieden und die Wut in mir wuchs.","Auf dem Boden entdeckte ich ein Blutsteinamulett, das bei Dämonenbeschwörungen als Hilfe benutzt wird. Das Amulett, das ich fand, war jedoch entleert. Ich benötige ein weiteres, um es zu untersuchen, aber ich wage nicht, zum Anwesen zurückzukehren. Könnt Ihr mir ein paar bringen?"}, {"Holt 10 Blutsteinamulette für Tor'gan in Hammerfall."}},
[672] = {"Mut machen", {"Wir haben versucht, es ihm gemütlich zu machen, ihn aufzumuntern, haben ihm Geschichten erzählt von unserer mächtigen Stadt in Orgrimmar in Kalimdor, aber nichts, gar nichts hat ihn berührt.","Geschichten von unserem jungen Kriegshäuptling, Thrall, der die Rüstung und den Hammer von Orgrim trägt... aber vielleicht... Vielleicht! Eine kleine Demonstration von unserer neu gefundenen Schamanen-Magie könnte ihm neue Hoffnung geben. Einen Versuch ist es wert!","Ein kleines Objekt könnten wir für ihn machen, ja... Bringt mir ein paar Raptoraugen und ich werde sehen, was ich tun kann."}, {"Bringt 10 Hochlandraptorenaugen von Hochlandschreitern und Hochlandhauern zu Tor'gan in Hammerfall."}},
[673] = {"Elende Magien", {"Ich habe keinerlei Zweifel, dass ein mächtiger Hexenmeister sich innerhalb der Mauern Stromgardes aufhält. Von Zeit zu Zeit hat dieser Hexenmeister mit Hilfe eines arkanen magischen Relikts abscheuliche Dämonen in großer Zahl herbeibeschworen. Wir müssen ihm die Quelle seiner Energie nehmen.","Sucht den Hexenmeister und tötet ihn. Bringt mir alle magischen Objekte, die Ihr bei ihm finden könnt, und ich werde die geeigneten Schritte unternehmen, sie zu zerstören, damit ihre Macht nicht mehr dazu benutzt wird, dieses Land weiterhin zu besudeln."}, {"Bergt Marez Cowls Blutsteinkugel und bringt sie zu Tor'gan in Hammerfall."}},
[674] = {"Mut machen", {"Hier, übergebt dieses Amulett Gor'mul und beobachtet, ob die Kraft der Raptoren das Feuer des Kriegers, das einst in seinen Adern brannte, erneut erwecken kann."}, {"Gebt das Raptorkrallen-Amulett an Gor'mul."}},
[675] = {"Mut machen", {"<Gor'mul wirft das Amulett zu Boden.>","Tor'gans hat Euch geschickt, oder? Pah! Warum er mir gegenüber Gnade empfindet, geht über meinen Verstand. Gnade wurde mir verweigert, als es mir nicht vergönnt war, mit meinem Kriegshäuptling im Kampf zu sterben. Das Schicksal hat mir einen harten Schlag versetzt ..."}, {"Sprecht mit Tor'gan."}},
[676] = {"Der Hammer kann fallen", {"Schwächlinge... alle miteinander. Ihr da, <Klasse>, was schaut Ihr so dämlich? Denkt wohl, Ihr seid was Besonderes, was? Wollt Ihr's nicht mal beweisen?","Der Außenposten von Hammerfall in Arathi wird angegriffen. Wenn wir der Besatzung nicht bald Hilfe schicken, steht ihr Ableben unmittelbar bevor.","Aber bevor Ihr jetzt einfach losrennt, möchtet Ihr vielleicht Eure Fähigkeiten erst testen. Nordöstlich von Stromgarde in Arathi befindet sich ein Ogerhort, der von den Felsfaustogern bewohnt wird. Wenn Ihr die Oger und Vollstrecker dort in den Griff bekommt, erstattet Drum Fel in Hammerfall Bericht."}, {"Tötet 8 Oger der Felsfäuste und 10 Vollstrecker der Felsfäuste und sucht dann Drum Fel im Außenposten von Hammerfall im Arathihochland auf."}},
[677] = {"Zu den Waffen", {"Die Trolle der Witherbark und die Felsfaustoger von Arathi arbeiten zusammen daran, uns von diesem Land zu vertreiben, aber unser Schicksal liegt weder in ihren noch in irgendwessen anderen Händen. Wir werden sie erledigen und ihnen zeigen, dass dies auch unsere Heimat ist.","Die Trolle haben sich von uns abgewandt, die Oger würden uns als Nahrung und Bettvorleger benutzen, wenn wir es zuließen - das darf nicht sein.","Wir werden unseren Angriff auf die Trolle der Witherbark südlich von hier beginnen. Erschlagt sie in Scharen und kehrt erst zurück, wenn ihre Lager dick mit Blut bedeckt sind."}, {"Erschlagt 10 Axtwerfer der Witherbark, 10 Kopfjäger und 8 Hexendoktoren und kehrt zu Drum Fel im Außenposten von Hammerfall zurück."}},
[678] = {"Zu den Waffen", {"Eure Kraft ist offensichtlich, <Name>. Dann zeigt mal, wie Ihr Euch mit echten Feinden schlagt.","Die Felsfaustoger haben einen Unterschlupf südwestlich von hier, Richtung der Zwergenbrücke, und, wenn ich mich nicht täusche, werden die Raufer und Magi dort eine etwas größere Herausforderung darstellen - für uns waren sie das bislang zumindest.","Erschlagt so viele wie Ihr irgend könnt und kommt persönlich zu mir, wenn Ihr erfolgreich seid, oder erscheint mir als Geist, wenn Ihr versagt. Ich werde Eure Beharrlichkeit in der Schlacht in jedem Falle preisen."}, {"Tötet 10 Schläger der Felsfäuste und 4 Magi der Felsfäuste und sucht dann Drum Fel im Außenposten von Hammerfall auf."}},
[679] = {"Zu den Waffen", {"Der Kopf der Bestie kann abgetrennt werden... mit einem mächtigen Schlag, <Name>. Ihr habt alle Zeichen dafür gezeigt, dass Ihr in der Lage seid, uns mit diesem letzten mächtigen Schlag zu helfen. Die Anführer dieser Angriffe haben ihren Unterschlupf innerhalb der Ruinen von Stromgarde gewählt, einem grellen Symbol menschlicher Schwachheit.","Findet und erschlagt diese Lords der Felsfäuste und ihre speichelleckenden Schamanen, die versuchen, uns aus diesem Land zu vertreiben."}, {"Tötet 15 Schamanen der Felsfäuste und 10 Lords der Felsfäuste und kehrt zu Drum Fel im Außenposten von Hammerfall im Arathihochland zurück."}},
[680] = {"Die echte Bedrohung", {"Ihr seid meinem Gemahl ein treuer Diener, <Klasse>, aber er weiß nicht, warum Hammerfall angegriffen wird.","Or'Kalar, Mugh'thols Helfer in Stromgarde hasst meinen Gemahl aufs Äußerste, und er wünscht sich nichts mehr, als ihn tot zu sehen, da mein Gemahl ihn vor Jahren einmal besiegte.","Die Vorzeichen verrieten mir, dass Or'Kalar eine Falle für meinen Gemahl ausgelegt hat. Aus diesem Grund habe ich Hinweise auf den Aufenthaltsort des Ogers vor ihm versteckt.","Mein Gemahl wird Hals über Kopf losschlagen, sobald er vom Aufenthaltsort des Ogers erfährt, und ich wollte Eure Hilfe bei der Tötung der Kreatur erbitten."}, {"Erschlagt Or'Kalar und bringt seinen Kopf zu Korin Fel in den Außenposten von Hammerfall."}},
[681] = {"Northfold-Anwesen", {"Wir kämpfen einen Kampf um Stromgarde, und wir verlieren ihn. Die Oger, das Syndikat und wir haben unsere einst große Stadt geteilt und jede Fraktion bekämpft die andere bis zu einem blutigen Unentschieden.","Aber unsere Streitkräfte schwinden, während unsere Feinde immer stärker werden.","Wir brauchen Hilfe aus der Stadt Southshore, aber Vorräte sind nur schwer zu bekommen, solange das Syndikat das Gebiet zwischen uns kontrolliert.","Begebt Euch zum Syndikatsstützpunkt im Northfold-Anwesen und tötet sie alle. Dann wird der Druck auf unsere Versorgungszüge ein wenig nachlassen und ein größerer Teil wird durchkommen."}, {"Tötet 10 Straßenräuber des Syndikats und 6 Söldner des Syndikats.","Kehrt zu Captain Nials bei der Zuflucht zurück."}},
[682] = {"Abzeichen von Stromgarde", {"Das Syndikat in Stromgarde bekämpft das Kontingent unserer Miliz dort. Um diesen Pöbel dazu zu bringen, sich mit unseren disziplinierteren Streitkräften zu schlagen, haben die Syndikatsanführer ein Kopfgeld auf unsere Soldaten ausgesetzt und belohnen jedes Abzeichen von Stromgarde, das toten Milizleuten abgenommen wurde, mit Gold.","Wir wollen diese Abzeichen zurück - wir dürfen es diesen Halsabschneidern nicht erlauben, Trophäen von unseren mutigen Gefallenen zu rauben!","Begebt Euch in das vom Syndikat kontrollierte Gebiet von Stromgarde und jagt ihnen unsere Abzeichen ab."}, {"Bringt Abzeichen von Stromgarde zu Captain Nials bei der Zuflucht."}},
[683] = {"Sara Balloos Hilfegesuch", {"<Name>, ich habe nur eine kleine Bitte an Euch.","Bringt diese Mitteilung, die ich vorbereitet habe, zu unserem großen Monarchen von Ironforge, König Magni Bronzebeard. Es ist fast lächerlich, diese kleine Aufgabe von Euch zu verlangen, von jemandem, der für die Sache der Allianz bislang eine so große Unterstützung war.","Aber in meinem von Trauer übermannten Zustand vor den König zu treten, wäre meinem Anliegen nicht im Geringsten dienlich. Das versteht Ihr sicher."}, {"Bringt Sara Balloos Notiz zu König Magni Bronzebeard von Ironforge."}},
[684] = {"GESUCHT: Marez Cowl", {"Es wurde ein Kopfgeld auf Marez Cowl ausgesetzt, einer hochrangigen Beraterin und Agentin des Syndikats. Sie wurde zuletzt in dem vom Syndikat kontrollierten Gebiet von Stromgarde gesehen.","Das Kopfgeld kann von Captain Nials abgeholt werden.","Ich warne Euch: Marez ist eine geschickte und gerissene Hexenmeisterin und bekannt dafür, dass sie sich mit Dämonen abgibt. Bei ihrer Verfolgung ist äußerste Vorsicht geboten."}, {"Bringt Marez' Kopf zu Captain Nials bei der Zuflucht."}},
[685] = {"GESUCHT: Falkenstein und Otto", {"Die Miliz von Stromgarde hat Kopfgelder auf Lord Falkenstein und seine Leibwache Otto ausgesetzt. Falkenstein zeichnet für die Bemühungen des Syndikats im Arathihochland verantwortlich und sein Tod wäre eine erhebliche Störung dieser Bemühungen.","Seine Leibwache Otto ist zwar kein strategisch wichtiges Ziel, aber er ist ein starker Gegner und hat Dutzende unserer Verteidiger auf dem Gewissen.","Ihre Kopfgelder können bei Captain Nials abgeholt werden."}, {"Bringt Ottos und Falkensteins Köpfe zu Captain Nials bei der Zuflucht."}},
[686] = {"Ehrerbietung von einem König", {"Ich erwarte nicht, dass Zwerge wie die gute Frau Balloo es verstehen, warum so ein Krieg für das größere Wohl unseres Volkes ertragen werden muss. Der Verlust ihres Gemahls und der Verlust jedes einzelnen Zwerges, der im Namen der Allianz kämpft, lastet schwer auf meiner Seele.","Auch wenn ich nicht in der Lage bin, ihren Gemahl aus dem wirbelnden Nether zurückzuholen, so kann ich ihm doch meine Ehrerbietung erweisen.","Ich werde einen Gedenkstein für Sully Balloo bei Großsteinmetz Marblesten in Auftrag geben.","Benachrichtigt ihn sofort, <Klasse>. Ihr werdet die Durchführung meines Auftrags beaufsichtigen."}, {"König Magni Bronzebeard will, dass Ihr mit Großsteinmetz Marblesten sprecht."}},
[687] = {"Theldurin der Verirrte", {"Theldurin der Verirrte war einst ein Mitglied unseres Ordens, dem Twilight-Hammer. Aber er wurde verrückt und hat unsere Lehren verdammt. Bevor er uns davonlief, hat er die Rolle von Myzrael gestohlen! Wir konnten ihn nicht einfangen, aber Gerüchte besagen, dass er sich im Ödland versteckt, sabbelnd und wahnsinnig.","Ihr müsst die Rolle finden, denn nur so könnt Ihr die Fürstin zu uns zurückbringen. Und dafür müsst Ihr Theldurin finden."}, {"Findet Theldurin den Verirrten."}},
[688] = {"Myzraels Verbündete", {"<Myzrael spricht.>","Frei! ICH BIN FREI! Ich bin frei und kann meine Kräfte sammeln, verborgen vor denen, die mich gefangen nahmen. Denn wenn ich mich ihnen jetzt stellen müsste, würden sie mich sicherlich überwältigen und erneut einsperren.","Aber bald werde ich mich den Riesen stellen und sie werden es bitter bereuen, mich als Gefangene gehalten zu haben!","Ihr seid ein edelmütiger Verbündeter, <Name>. Ich werde Eure Hilfe bald wieder benötigen. Wenn ich bereit bin, werdet Ihr mich an die Oberfläche beschwören müssen.","Sprecht mit Zaruk in Hammerfall. Er weiß, wie ich beschworen werden kann."}, {"Sprecht mit Zaruk in Hammerfall."}},
[689] = {"Ehrerbietung von einem König", {"Unser guter König will also, dass wir einen Gedenkstein zu Ehren eines Soldaten mit Namen Sully Balloo errichten? Und Ihr sagt, dass der arme Kerl bei der Tragödie am Thandol-Übergang gestorben sei?","Bah, wird denn jemals Frieden auf Azeroth herrschen?","Wie dem auch sei, ein Auftrag vom König hat die allerhöchste Priorität. Und um ein wahrhaft episches und bleibendes Werk zu schaffen, will ich mit dem besten Stein arbeiten, der verfügbar ist. Begebt Euch nach Darrow Hill in Hillsbrad und besorgt mir etwas Alteracgranit."}, {"Großsteinmetz Marblesten aus Ironforge will 5 Stücke Alteracgranit."}},
[690] = {"Malins Gesuch", {"Seid gegrüßt, <Name>. Ihr seht aus wie ein Abenteurer und ich habe eine Aufgabe, die erledigt werden muss.","Erzmagus Trelane, mein früherer Meister, ist vor nicht allzu langer Zeit aus Stromgarde im Arathihochland geflohen und er hat mich gebeten, bei der Wiederbeschaffung einiger Gegenstände, die er zurückließ, behilflich zu sein.","Die Stadt wird belagert und er hat versucht, den Turm von Arathor zu verteidigen, aber ohne Erfolg. Ich habe bereits Agenten im Hochland, aber sie haben mich gebeten, weitere Hilfe zu schicken. Seid Ihr interessiert?"}, {"Sucht Skuerto bei der Zuflucht im Arathihochland auf."}},
[691] = {"Das Gewicht in Gold wert", {"Erzmagier Malin schickte Skuerto und mich hierher, um für ihn ein paar Reagenzien zu besorgen. Ich hatte keine Ahnung, wie ich die Gegenstände beschaffen sollte, aber da Ihr jetzt hier seid, hat sich mein Problem wohl gelöst.","Ich brauche Folgendes: Hauer der Witherbark, Medizinbeutel der Witherbark und ein Schattenjägermesser.","Könntet Ihr mir das besorgen? Die Witherbark leben südöstlich von Arathi."}, {"Sucht für Lehrling Kryten bei der Zuflucht 10 Hauer der Witherbark, 4 Medizinbeutel der Witherbark und 1 Schattenjägermesser."}},
[692] = {"Die verlorenen Fragmente", {"Die Rolle von Myzrael... die Rolle von Myzrael?! Die wollt Ihr doch wohl nicht haben! Ihre Magie kann Myzrael an die Oberwelt locken, und wenn Ihr das tut, sind wir alle in Gefahr!","Oder... vielleicht ist es schon zu spät. Vielleicht ist sie schon frei. Vielleicht sammelt sie nur ihre Kraft, bevor sie sich auf uns stürzt. Wenn es so ist, BRAUCHT Ihr die Rolle!","Zu dumm, dass sie zerstört wurde. Wütende Elementare haben sie gestohlen und kämpfen jetzt um die Fragmente.","Wenn Ihr diese Fragmente jedoch sammeln könntet, können wir sie vielleicht wieder zusammensetzen..."}, {"Bringt Theldurin dem Verirrten das zerrissene Rollenfragment, das zerknüllte Rollenfragment und das angesengte Rollenfragment."}},
[693] = {"Zauberstab gegen Faust", {"He, <Name>. Tut mir Leid, dass ich früher Euch gegenüber so kurz angebunden war... und nein, das sollte jetzt kein Wortspiel sein.","Ich habe herausgefunden, wo Trelanes erster Gegenstand gelandet ist: Kor'gresh in der Halle der Felsfäuste hat ihn. Und so wie er seinen Leuten die Hölle heiß gemacht hat, damit sie nicht aus der Reihe tanzen, muss er wohl kurz davor sein herauszufinden, wie man seine gesamte Macht nutzen kann.","Die Halle der Felsfäuste liegt südlich von hier und östlich vom Thandol-Übergang. Findet diesen Oger und holt den Zauberstab.","Während Ihr weg seid, werde ich versuchen herauszufinden, was wir wegen Stromgarde unternehmen können."}, {"Sucht Trelanes Zauberstab der Herbeirufung und bringt ihn Skuerto bei der Zuflucht."}},
[694] = {"Trelanes Verteidigung", {"Na, erholt?","Das hier ist der nächste Schritt: Um den Turm von Arathor in Stromgarde herum wimmelt es nur so von Ogern. Wir müssen die Verteidigungsanlagen des Turms aktivieren, wenn wir eine Chance gegen sie haben und es lebend zu den Gegenständen schaffen wollen. Dazu brauchen wir ein paar Edelsteine, an denen die Schamanen der Felsfäuste Gefallen gefunden haben. Sie nahmen die aus dem Turm, aber sie werden nicht mehr lang in ihrem Besitz bleiben.","Holt einen dieser Edelsteine und bringt ihn Kryten - er kennt den Zauber, mit dem man sie verzaubern kann, sodass sie wie ein Schlüssel funktionieren."}, {"Sucht einen azurblauen Achat und bringt ihn Lehrling Kryten bei der Zuflucht."}},
[695] = {"Des Lehrlings Verzauberung", {"Bitte sehr, <Name>. Der Edelstein muss nur gegen einen der Sockel nahe dem Fuß des Turms gepresst werden, um dessen Verteidigung zu aktivieren. Benutzt ihn, wenn Ihr bereit seid, den Turm zu betreten und nach den restlichen Gegenständen Trelanes zu suchen.","Viel Glück. Ich warte hier im Lager auf Eure Rückkehr. Oh, und sprecht mit Skuerto, bevor Ihr geht. Er gibt Euch die restlichen Details, die Ihr braucht."}, {"Sprecht mit Skuerto."}},
[696] = {"Angriff auf den Turm", {"Danach müsst Ihr suchen: Trelanes Phylakterium, Trelanes Kugel, Trelanes Glutachat.","Alle drei Dinge sollten sich innerhalb des Turms in Truhen befinden und es sollte Euch keine Schwierigkeiten bereiten, diese Truhen selbst zu öffnen; Ihr müsst Euch nur vor den Ogern in Acht nehmen.","Die Verteidigungen des Turms kümmern sich nicht selbstständig um die Oger, Ihr müsst Eure Waffen jederzeit griffbereit haben. Wenn diese Oger herausfinden, dass Ihr dort seid, um sie zu bestehlen, werden sie Euch in die Mangel nehmen.","Seid vorsichtig - und viel Glück."}, {"Sucht Trelanes Phylakterium, Trelanes Kugel und Trelanes Glutachat und bringt ihn Skuerto bei der Zuflucht."}},
[697] = {"Malins Gesuch", {"Bringt diesen Brief nach Stormwind und lasst Malin wissen, dass Kryten und ich bald wieder zu Hause sind. Ich zweifle nicht daran, dass er Euch reichlich für Eure Hilfe uns gegenüber belohnen wird, <Name>.","Sichere Reise."}, {"Bringt dem Erzmagier Malin im Magierviertel von Stormwind den versiegelten Brief."}},
[698] = {"Mangelnde Reserven", {"Ich versuche, einige Vorräte für unseren Außenposten in der Nähe des Strandes im Osten zusammenzutragen - Tok'Kar tut, was er kann, aber neben dem Dienst als Wache und Kochen für seine Soldaten ist nicht mehr viel Zeit übrig vom Tag.","Wann immer sie können, kochen sie dort mit Begeisterung Sägezahnfleisch, aber ich hatte einfach keine Zeit, ihnen eine neue Ladung rüberzuschicken. Würdet Ihr mir da aushelfen?","Die jungen Sägezähne sind nicht so schmackhaft wie die älteren, macht also einen Bogen um sie; gebt ihnen ein oder zwei Jahre, um zur vollen Größe heranzuwachsen."}, {"Bringt Tok'Kar in den Sümpfen des Elends 8 Stück unverarbeitete Sägezahnflanken."}},
[699] = {"Mangelnde Reserven", {"Man hat uns wegen der Murlocplage entlang der Küste hier stationiert, um andere vor Gefahren jeglicher Art, die Stonard bedrohen könnten, zu beschützen - oder zu warnen, falls deren Streitkräfte zu groß werden. Das gibt uns nicht viel Zeit, um auszuruhen, und wir könnten weitere Hilfe Eurerseits gebrauchen, wenn Ihr gewillt seid.","Die Sägezahnschnapperklauen geben erstklassige Waffen ab, wenn sie richtig vorbereitet werden. Bringt mir ein paar ihrer Klauen, dann werde ich zusehen, dass Ihr eine unserer Waffen als Dank für das Frischfleisch und die angenehme Gesellschaft bekommt."}, {"Bringt Tok'Kar in den Sümpfen des Elends 6 Sägezahnschnapperklauen."}},
[700] = {"Ehrerbietung von einem König", {"Ah, welch edle Steine doch aus Lordaeron kommen. Was für eine Schande, dass in diesem Land ein solches Chaos herrscht.","Aber der Gedenkstein ist sehr schön geworden! Ich glaube, der König wird damit sehr zufrieden sein.","Würdet Ihr so gütig sein und Seiner Majestät eine Nachricht senden, dass der Gedenkstein wie gefordert fertig gestellt wurde?"}, {"Erstattet Meldung an König Magni Bronzebeard."}},
[701] = {"Arglist des Raptors", {"Mit ein paar weiteren Dingen kann ich einen Gegenstand schaffen, der Gor'mul mit der Magie der Arglist des Raptors erfüllt! Wenn ihn die Macht des Raptors durchströmt, wird Gor'mul unsere neue Macht erkennen und den Krieger neu entdecken, der in ihm schlummert!","Um den Gegenstand und den Zauber zu schaffen, werden Raptorherzen benötigt."}, {"Besorgt für Tor'gan 12 Raptorherzen von Hochlandfleischpirschern."}},
[702] = {"Arglist des Raptors", {"Nehmt diese Kugel und nutzt ihre Energie, um Gor'mul die Essenz des Raptors zu spenden. Sie wird seinen Geist erfrischen und ihm einen neuen Lebensinhalt geben. Sobald er wieder normal ist, können wir ihn über die aktuelle Situation der Horde informieren und wir werden einen großen Krieger bei unseren unaufhörlichen Kämpfen an unserer Seite haben."}, {"Gebt Gor'mul Tor'gans Kugel."}},
[703] = {"Gegrillte Bussardflügel", {"Oh ja, meine gegrillten Bussardflügel sind ein Renner! Die Sauce ist gehaltvoll und das Fleisch würzig. Und der Duft lockt alle hungrigen Mäuler in einem Umkreis von Meilen an!","Ich habe alles, was ich brauche, um eine Menge zu kochen. Alles... außer Bussardflügel!","Wenn Ihr etwas davon haben möchtet, besorgt mir Bussardflügel. Bringt Ihr mir genügend davon, gebe ich Euch vielleicht sogar das Rezept dafür!"}, {"Bringt Rigglefuzz 4 Bussardflügel."}},
[704] = {"Agmonds Schicksal", {"Der letzte Bericht, den ich von Agmond erhalten habe, ist sehr beunruhigend, umso mehr, da er jetzt tot ist! Er erwähnte, dass seine Buddler an der Grabungsstätte vor Uldaman \"uralte Urnen\" entdeckt hätten. Ich fürchte, dass die dort gefundenen Urnen die Troggs erzürnt haben, ebenso wie die Götzen, die ich dort fand.","Ich möchte, dass Ihr uns die Urnen bringt, damit wir sie untersuchen können. Geht zur Grabungsstätte Uldaman, sammelt die Urnen ein und kehrt dann zu mir zurück."}, {"Bringt Ausgrabungsleiter Ironband am Loch Modan 4 verzierte Steinurnen."}},
[705] = {"Perlentauchen", {"Das Ödland ist eine unwirtliche Gegend voller heimtückischer Raubtiere und dreister Aasfresser. Unheimlich, besonders für einen so kleinen Goblin. Ich muss gewitzt sein, wenn ich überleben möchte!","Ich kenne das Rezept für Blitzstrahlbomben. Die benutze ich, um wilde Tiere zu verjagen. Leider geht mir langsam eine der Zutaten aus: zu Pulver zermahlene blaue Perlen. Bringt mir welche, dann soll es Euer Schaden nicht sein.","He, und ich hoffe, Ihr habt gute Stiefel an. Die blauen Perlen, die ich brauche, stammen von Muscheln am finsteren Riff.","Genau, dem finsteren Riff im Schlingendorntal!"}, {"Bringt Rigglefuzz im Ödland 9 blaue Perlen."}},
[706] = {"Feuerverzauberungen", {"Als Lehrling Feranor Steeltoes habe ich seine Spezialtechnik erlernt, wie man feurige Verzauberungen auf Waffen wirkt. Als ich das letzte Mal von ihm hörte, arbeitete er mit einer Jägergruppe in einer Schmiede, oben in Lordaeron.","Ich kann Euch nicht viel darüber sagen, an was genau er dort arbeitete, nur dass dazu Blut eines noch schlagenden Herzens eines seltenen Schwarzdrachen-Welpen nötig ist. Ohne Neuigkeiten aus Ironforge habe ich nicht viel zu tun. Wenn Ihr mir ein Herz eines Welpen aus der Lethlorklamm bringt, zeige ich Euch meine Fertigkeiten!"}, {"Besorgt Sigrun Ironhew ein Schwarzdrachenherz."}},
[707] = {"Ironband sucht Euch!", {"<Name>. Ich habe Berichte von Ausgrabungsleiter Ironband von der Ausgrabungsstätte am Loch Modan erhalten - er braucht jemanden, der ihm die Rennerei auf einer unserer anderen Ausgrabungen abnimmt.","Ich habe keine weiteren Details, aber wenn Ihr Euch ein bisschen die Füße vertreten wollt, dann sprecht mit Ironband."}, {"Sprecht auf Ironbands Ausgrabungsstätte am Loch Modan mit Ausgrabungsleiter Ironband."}},
[708] = {"Der Flugschreiber", {"Die Oberfläche dieses metallenen Flugschreibers ist stark angegriffen, so, als hätte man ihn in Säure getaucht... er muss lange im Magen des Riesenkondors gelegen haben.","Ihr dreht die Kiste um und entdeckt ein Symbol, das in den Boden eingeprägt ist. Unter dem Symbol stehen folgende Wörter:","\"Motor Nr. 19 - Belagerungsbrigade von Ironforge\""}, {"Bringt den zerfressenen Flugschreiber in die Halle der Waffen in Ironforge."}},
[709] = {"Lösung der Verdammnis", {"Oh je! Oh je! Unheil naht! Hört auf mich! Unheil!","Nein, ich darf nicht darüber sprechen... darf nicht ihre Namen aussprechen, sie könnten mich hören! Aber wenn wir mehr über sie lernen, ja, mehr... dann könnten wir vielleicht lernen, wie man sie aufhalten kann!","Vielleicht.","Geht nach Uldaman. Nein! Ich hab's gesagt! Ihr müsst... die Schrifttafel von... Ryun'eh finden. Ja, findet sie! Und bringt mich nicht dazu, diesen Namen noch einmal auszusprechen!","Sie ist verborgen an diesem Ort, in einer uralten Truhe. Da ist sie versteckt... in einer Truhe, die gefertigt wurde, lang bevor Zwerge solche Werke vollbrachten."}, {"Bringt Theldurin dem Verirrten die Schrifttafel von Ryun'eh."}},
[710] = {"Element-Studie: Fels", {"Ich habe die Arbeit an meinem neuesten Projekt hier im Ödland erst mal eingestellt: meine Fesseln der Elementarbindung.","Wenn sie vollendet sind, werden Magier noch mächtigere Elementare beschwören und sie in noch größerer Zahl nutzen können. Ich brauche zunächst kleine Felssplitter, um einige meiner neuesten Angriffszauber und ein paar der Dingsdas zu testen, die ich zusammengebastelt habe.","Die Splitter kann man leicht von den kleinen Felselementaren westlich von hier sammeln. Würdet Ihr mir ein paar bringen?"}, {"Sucht 10 kleine Felssplitter und bringt sie Lotwil Veriatus im Ödland."}},
[711] = {"Element-Studie: Fels", {"Ihr habt Euch als fähiger erwiesen als all meine Lehrlinge zusammen, <Name>.","Für die nächsten Aktionen ist etwas mehr Krafteinsatz erforderlich. Wenn Ihr vorsichtig genug seid, könnt Ihr bei Felselementaren große Steinplatten finden. Ihre Körper sind kräftiger und größer und deshalb kommt man bei ihnen eher an Teile heran, die groß genug für meine Tests sind. Ihr findet sie weiter westlich von hier oder südöstlich entlang des Gebirgsrandes.","Lasst Euch Zeit, ich brauche eine Weile, um diese kleineren Stücke zu testen, die Ihr mir gebracht habt."}, {"Bringt Lotwil Veriatus im Ödland 3 große Steinplatten."}},
[712] = {"Element-Studie: Fels", {"<Name>, ich brauche noch ein weiteres Teil von den Elementaren hier im Ödland: die Armschienen der Steinbindung von den großen Felselementaren. Meinen Berechnungen nach sollten diese Armschienen ausreichen, um die Kraft meiner Fesseln zu beurteilen.","Bei meinen Wahrsageversuchen über die großen Felselementare sah ich eine Gruppe von ihnen südwestlich von hier, in der Nähe von ein paar Ogern, wenn ich mich recht erinnere.","Bringt mir 5 ihrer Armschienen, damit ich ein paar in Reserve habe. Servo ist berüchtigt dafür, meine Reagenzien und Forschungen zu verlegen."}, {"Bringt Lotwil Veriatus im Ödland 5 Armschienen der Steinbindung."}},
[713] = {"Kühlaufsätze setzen sich durch", {"Lasst mich Euch etwas über Frostöl erzählen: Nichts kann einen Motor oder ein Getriebe besser vor Überhitzung schützen.","Seine Kühleigenschaften sind außerordentlich und seine Langlebigkeit als Schmiermittel ist ohnegleichen. Aber das ist nicht alles: Es trägt auch dazu bei, die Verzauberung magischer Gegenstände zu erhalten.","Ich brauche eine Phiole davon, um die magische Kapazität meiner Fesseln zu erhöhen und sie mächtig genug zu machen, um unseren beschworenen Freund davon abzuhalten, uns alle umzubringen.","Bro'kin, einer meiner Goblin-Freunde, kann Euch lehren, wie man es herstellt. Er wohnt in Alterac."}, {"Sucht Frostöl und bringt es Lotwil Veriatus im Ödland."}},
[714] = {"Gyro... Was?", {"Ihr habt Eure Arbeit überdurchschnittlich gut gemacht, <Name>.","<Lotwil ist offensichtlich durch seine Arbeit abgelenkt.>","Falls wir uns in Kharanos jemals wiedersehen, scheut Euch nicht, zu mir zu kommen und nach Arbeit zu fragen.","Upps! Schnell, nehmt das... ja, das da. Danke.","Jetzt brauche ich nur noch eines. Es ist ein kleines Teil, aber leider ziemlich ausschlaggebend: ein Gyrochronatom.","Wenn Ihr irgendwo einen auftreiben und mir bringen würdet, könnte ich das hier fertig stellen."}, {"Bringt Lotwil Veriatus im Ödland einen Gyrochronatom."}},
[715] = {"Flüssigstein", {"Ich glaube, die Gegenstände, die Ihr Lotwil besorgt habt, werden unsere geringste Sorge sein, <Name>. Andererseits habt Ihr Euch in den Wissenschaften, die Lotwil so heilig sind, mehr als talentiert gezeigt; also wie wäre es, wenn Ihr jetzt mir ein bisschen helft?","Ich brauche einen Unsichtbarkeitstrank und einen Heiltrank; beides könnte sich als nützlich erweisen, sobald er erst mit seinem Ding angefangen hat.","Dafür gebe ich Euch dieses Rezept für einen Trank, den ich entwickelt habe, als ich noch bei Lotwil in die Lehre ging. Er basiert auf einigen der Forschungen, die er hier im Ödland durchführte."}, {"Bringt Lucien Tosselwrench im Ödland einen Heiltrank und einen geringen Unsichtbarkeitstrank."}},
[716] = {"Stein ist besser als Stoff", {"Ich weiß, wie knickerig Lotwil sein kann, <Name>, also lasst mich etwas als Belohnung für all Eure Mühen fertigen. Vielleicht kann es Euch sogar vor der Scheußlichkeit schützen, die Lotwil gerade erfindet.","Ich habe alle zusätzlichen Steine verarbeitet, die Ihr für ihn gesammelt hattet, und kann Euch ein paar hübsche Armschienen machen, die Ihr möglicherweise gebrauchen könnt. Sie sind natürlich verzaubert.","Ich brauche nur noch gemusterte bronzene Armschienen, die ein Schmied hergestellt hat, um die fertig zu stellen, die ich für Euch mache."}, {"Bringt Lucien Tosselwrench im Ödland einige gemusterte bronzene Armschienen."}},
[717] = {"Erdbeben", {"Es ist offensichtlich, dass wir nicht mehr gewährleisten können, Schattenbringer und Hematus noch länger in Lethlor festzuhalten. Die schwarzen Großdrachen haben sich dort niedergelassen, und dass es den Ogern gelungen ist, das Zeichen zu stehlen, weist darauf hin, dass drastischere Maßnahmen ergriffen werden müssen.","Das Gefängnis der Drachen wird von drei Runensteinen gebildet, die man nur mit Hilfe des Zeichens der Erde wegnehmen kann. Diese Runensteine können dann dazu verwendet werden, das Siegel zu zerbrechen, das sie gefangen hält.","<Name>... sorgt dafür, dass sie süß träumen... für immer."}, {"Benutzt das Zeichen der Erde, um die Amethyst-, Opal- und Diamantsäulen zu aktivieren und die Runensteine zu holen.","Legt die Runensteine auf das Siegel der Erde, um Schattenbringer und Hematus zu befreien.","Tötet sie und bringt dann Garek Schattenbringers Bindung, Hematus' Ketten und das Zeichen der Erde."}},
[718] = {"Illusionen", {"Wir hatten es so eilig, die Ausgrabungsstätte von Uldaman zu verlassen, dass wir uns gezwungen sahen, unterwegs viele Wagen und Nachschubkisten zurückzulassen. Die Oger von Camp Kosh haben sich auf unsere zurückgelassene Ausrüstung gestürzt wie Geier auf faulende Kadaver. Riechen auch nicht besser. Als die Kadaver, meine ich.","Wie dem auch sei, sie haben sich den Wagen mit unseren Waffen und anderem Zeug geschnappt und deshalb - tja, es ist ein bisschen peinlich - konnten wir uns nichts zum Essen beschaffen.","Wir stecken echt ganz schön in der Klemme. Könnt Ihr uns vielleicht helfen?"}, {"Holt die Nachschubkiste für Sigrun Ironhew."}},
[719] = {"Ein Zwerg und sein Werkzeug", {"He, <Name>. Seid lieber vorsichtig. Es sind Dunkeleisenzwerge in der Nähe - scheinen vom Schattenschmiedeklan zu sein.","Sie haben gerade die Ausgrabungsstätte angegriffen, auf der ich arbeite - haben fast alle dort umgebracht, darunter meinen Boss Hammertoe. Ich bin gerade noch lebendig weggekommen.","Die Stätte liegt nördlich von hier und ich versuche, mir einen Plan auszudenken, wie wir einen Teil unserer Vorräte zurückbekommen könnten, insbesondere meine Glückshacke.","Meint Ihr, Ihr könntet mir ein bisschen helfen? Einer von diesen verdammten Dunkeleisenzwergen muss meine Hacke haben! Es könnte jeder von denen sein."}, {"Sucht Ryedols Glückshacke und bringt sie Ausgrabungsleiter Ryedol in der Hammertoe-Grabungsstätte."}},
[720] = {"Ein Hoffnungsschimmer", {"<Text wurde mit Kreide auf die Karte gekritzelt.>","\"Ryedol,","Sie bringen mich nach Uldaman. Schickt Hilfe.","- Hammertoe\""}, {"Sucht Ausgrabungsleiter Ryedol und teilt ihm mit, dass Hammertoe Grez noch am Leben ist."}},
[721] = {"Ein Hoffnungsschimmer", {"<Name>, geht Ihr ihn suchen? Sucht Ihr Hammertoe in Uldaman?","Wenn diese dreckigen Zwerge der Schattenschmiede ihn als Geisel genommen haben, um herauszubekommen, was er über die Titanenausgrabungen weiß, könnte ganz Ironforge in großer Gefahr sein.","Seit langer Zeit schon trachten die Dunkeleisenzwerge nach Magie, um noch mächtigere Golems zu erschaffen. Hammertoe hatte dasselbe vor, aber natürlich nur, um sie aufzuhalten.","Vielleicht lassen sie ihn am Leben, bis sie herausbekommen haben, was er weiß; Ihr müsst Euch beeilen, wenn Ihr ihn retten wollt!"}, {"Sucht in Uldaman nach Hammertoe Grez."}},
[722] = {"Amulett der Geheimnisse", {"Ihr seid mutig... (((hustet))) dass Ihr Euch wegen mir nach Uldaman wagt, <Klasse>.","Wenn Ihr wirklich hier seid, um dem... ollen Hammertoe zu helfen, dann... (((hustet)))... müsst Ihr nach... meinem Amulett suchen.","Grubenschatten hat es... Magregan Grubenschatten. Muss hier irgendwo sein... in den Ruinen, das ist sicher. Das Amulett wird sie... (((hustet)))... genau zu den Rollen führen, wenn sie keiner aufhält. Es ist an Euch, <Name>. Bitte... holt das Amulett.","Die Rollen werden es den Dunkeleisenzwergen ermöglichen, noch mächtigere Golems zu erschaffen. Ich weiß es einfach. Spür's in meinen Knochen."}, {"Sucht Hammertoes Amulett und bringt es ihm nach Uldaman."}},
[723] = {"Ein Funken Hoffnung", {"<Hammertoe fasst an seine Wunden und braucht einen Moment, um sich zu sammeln, bevor er weiterspricht.>","Findet Ryedol, <Name>. Bringt ihm mein Amulett. <hustet> Er... <hustet>... wird wissen, was zu tun ist; er weiß, dass ich nach den Wurzeln unseres Volks suchte, und er... <hustet>... weiß, dass es meine Aufgabe war, den Schattenschmiedeklan aufzuhalten. Sie dürfen die Rollen nicht finden... <hustet>"}, {"Bringt Ausgrabungsleiter Ryedol im Ödland Hammertoes Amulett."}},
[724] = {"Ein Funken Hoffnung", {"Okay, okay... Wer könnte wissen, was zu tun ist?","Historiker Karnik! Genau!","Er lebt in Ironforge. Er kann helfen. Er und Hammertoe haben zusammengearbeitet, um in Uldaman nach den verschollenen Artefakten zu suchen.","Schätze, die Zwerge der Schattenschmiede haben rausgefunden, dass die beiden mehr wussten, als sie sagten.","Erzählt Karnik, was mit Hammertoe geschehen ist, und gebt ihm das Amulett - er wird Euch dann sagen, was als Nächstes zu tun ist."}, {"Bringt Historiker Karnik in Ironforge Hammertoes Amulett."}},
[725] = {"Kunde über die Bedrohung breitet sich aus", {"<Name>, lauft los und findet so schnell wie möglich Berater Belgrum; sagt ihm, was Ihr gesehen habt, und fragt ihn, wie wir weiter vorgehen sollen. Diese Notiz wird ihm alles Weitere erklären.","In der Zwischenzeit werde ich mir die Zeit nehmen, um mit Hammertoes Geist zu sprechen. Vielleicht weiß er etwas, das uns helfen könnte, die Aktivitäten der Zwerge der Schattenschmiede hier im Ödland zu unterbinden. Wir können nur beten."}, {"Sucht Berater Belgrum und gebt ihm die Notiz."}},
[726] = {"Kunde über die Bedrohung breitet sich aus", {"<Berater Belgrum erreicht das Ende der Notiz und für einen Moment weiten sich seine Augen.>","Ach, das sind schlimme Nachrichten.","<Name>, bitte kehrt zu Historiker Karnik zurück und sagt ihm, dass er im Auftrag des Königs schnell und entschlossen gemäß dieser Information handeln soll. Er muss die Aktivitäten der Zwerge der Schattenschmiede im Ödland unterbinden."}, {"Sprecht mit Historiker Karnik."}},
[727] = {"Auf nach Ironforge zu 'Yagyins Zusammenstellung'", {"Die Schrifttafel erklärt vieles. Aber nicht genug! Nicht annähernd genug!","Es gibt ein Buch, eine Zusammenstellung, die Yagyin vor Unzeiten verfasst hat. Die... Leute, vor denen ich geflohen bin... bewahren dieses Buch an vielen verschiedenen Orten auf. Und die höchstgebildeten von ihnen studieren es. Es enthält riesiges Wissen. Wissen, das uns beschützen kann!","Geht zu Gerrig Bonegrip. Er befindet sich in Ironforge in einem Geschäft im düsteren Viertel. Zeigt ihm dieses Siegelzeichen, dann wird er Euch für einen der ihren halten. Fragt ihn nach 'Yagyins Zusammenstellung'... und bringt sie mir!"}, {"Gebt Gerrig Bonegrip im düsteren Viertel von Ironforge das Siegelzeichen des Hammers."}},
[728] = {"Auf nach Undercity zu 'Yagyins Zusammenstellung'", {"Die Schrifttafel erklärt vieles. Aber nicht genug! Nicht annähernd genug!","Es gibt ein Buch, eine Zusammenstellung, die Yagyin vor Unzeiten verfasst hat. Die... Leute, vor denen ich geflohen bin... bewahren dieses Buch an vielen verschiedenen Orten auf. Und die höchstgebildeten von ihnen studieren es. Es enthält riesiges Wissen. Wissen, das uns beschützen kann!","Sucht den Bewahrer Bel'dugur in Undercity von Lordaeron auf. Zeigt ihm dieses Siegelzeichen, dann wird er Euch für einen der ihren halten. Fragt ihn nach 'Yagyins Zusammenstellung'... und bringt sie mir!"}, {"Bringt dem Bewahrer Bel'dugur in Undercity das Siegelzeichen des Hammers."}},
[729] = {"Der geistesabwesende Ausgrabungsleiter", {"Was war es doch für eine Ehre, als Meister Greywhisker mir auftrug, unter der Führung des großen Ausgrabungsleiters Remtravel zu arbeiten. Jeder an der Akademie in Ironforge kannte Remtravels großartige Entdeckungen.","Aber der Ausgrabungsleiter ist sich nicht so recht ... ähm ... bewusst ... was um ihn herum so vor sich geht.","Wir hatten Hinweise auf eine große Kultur entdeckt. Schreckliche Golems brachen aus dem Boden hervor und überrannten die Grabungsstätte. Remtravel schien gar nichts bemerkt zu haben. Ich rannte nach Auberdine, um Hilfe zu holen.","Bitte reist nach Süden und seht nach, ob es dem Ausgrabungsleiter gut geht!"}, {"Reist nach Süden und seht nach Ausgrabungsleiter Remtravel."}},
[730] = {"Ärger in Dunkelküste?", {"Wie schön, ein <Klasse>, der an den großartigen archäologischen Wundern unserer Welt interessiert ist.","Oftmals wird unser Wirken von unseren Freunden der Allianz nur als reines Hobby abgetan. Aber was viele nicht einsehen wollen ist, dass die jüngsten Entdeckungen in Khaz Modan bewiesen haben, dass eine große und mächtige Kraft ganz Azeroth bedroht, von Lordaeron bis nach Kalimdor.","Ich bin höchst besorgt um meine Mannschaft, die ich nach Dunkelküste geschickt habe. Meine Leute haben seit Wochen nichts mehr von sich hören lassen.","Reist nach Auberdine und sucht nach Hinweisen auf ihren Aufenthaltsort."}, {"Reist nach Auberdine und sucht nach Hinweisen auf das Zwergenausgrabungsteam."}},
[731] = {"Der geistesabwesende Ausgrabungsleiter", {"Also dann, Hollee... ach ja, richtig, Ihr seid ja gar nicht Hollee. Wo ist Hollee?","Pah, das ist ja jetzt auch nebensächlich. Wir müssen diesen Handfeger finden... Ich meine, das rätselhafte Fossil! Wir müssen dieses Fossil finden. Die Forscherliga in Darnassus wird ein paar Hinweise darauf haben wollen, was ich hier die ganze Zeit getrieben habe.","Lasst es mich wissen, wenn Ihr bereit seid, dann machen wir uns auf die Jagd nach diesem Feger... Ich meine, Fossil! Wir sollten irgendeine Niederschrift finden, damit die Liga zufrieden ist.","Also, seid Ihr so weit?"}, {"Beschützt Ausgrabungsleiter Remtravel, während er nach dem rätselhaften Fossil sucht, und kehrt dann zur Archäologin Hollee in Auberdine zurück."}},
[732] = {"Erdbeben", {"Ich habe von Sigrun gehört, dass Ihr ihm eine große Hilfe gewesen seid. Vielleicht darf ich Eure Hilfe ja auch in Anspruch nehmen. Mein Meister, Krasus von den Kirin Tor, hat mich hergeschickt, um die Probleme zu lösen, die sich in der Lethlorklamm zusammenbrauen.","Ich darf nicht preisgeben, was genau meine Mission ist oder was ich unternehmen soll, aber seid versichert, dass es von großer Wichtigkeit ist.","Eine Bande nomadisierender Oger, die gelegentlich in der Nähe vom Camp Boff gesichtet wurden, hat einen Gegenstand gestohlen, das so genannte \"Zeichen der Erde\", und ich muss es wiederhaben. Bringt es mir und ich verrate Euch mehr."}, {"Beschafft für Garek das Zeichen der Erde."}},
[733] = {"Schnorrerei", {"Es wird hier ganz schön haarig werden, wenn es mir nicht gelingt, Material aufzutreiben, um ein paar Verteidigungslinien und andere Notwendigkeiten zu konstruieren. Mir ist aufgefallen, dass die Oger in diesem Gebiet über jede Menge Metallschrott verfügen. Vielleicht sind die Geschichten über Belagerungsmaschinen im Ödland doch gar nicht so weit hergeholt...","Wie dem auch sei, die Ausgrabungsleiter machen sich langsam Sorgen um die Lage in unserem kleinen Lager, deshalb würde ich gern, sobald es geht, mit der Arbeit beginnen.","Das Hauptlager der Oger liegt bei der Staubspeiergrotte, eine ordentliche Strecke von hier aus Richtung Westen."}, {"Bringt Sigrun 7 Teile Metallschrott."}},
[734] = {"Das wird schwierig", {"Wunderbar, wunderbar.","<Lotwil sieht Euch einen Moment lang an, bevor er sich wieder seinen Fesseln zuwendet.>","Sprecht mit Lucien, <Name>. Ich brauche ein Zahnrad Nr. 5. Bringt es mir bitte, ja?"}, {"Sprecht im Ödland mit Lucien Tosselwrench."}},
[735] = {"Der Stern, die Hand und das Herz", {"Yagyins Zusammenstellung', eh? Wollt Ihr vielleicht irgendwo eine neue Ortsgruppe aufbauen? Egal, es steht mir nicht zu, Euch das zu fragen.","Ich werde Euch helfen, weil ich ein Exemplar davon besitze, das ich entbehren kann. Und eine Hand wäscht die andere, stimmt's? Daher werden wir groß.","Ich brauche drei mächtige Gegenstände. Bringt sie mir, dann gebe ich Euch die Zusammenstellung.","Ich werde ihre Namen nicht nennen. Man könnte uns belauschen. Aber wartet, ich schreibe sie auf."}, {"Bringt Gerrig Bonegrip in Ironforge die Gegenstände in Bonegrips Notiz."}},
[736] = {"Der Stern, die Hand und das Herz", {"Ihr braucht 'Yagyins Zusammenstellung'? Dann wollt Ihr also eine neue Herde sammeln? Natürlich... und natürlich werde ich Euch helfen, <Bruder/Schwester>.","Aber Ihr müsst mir auch helfen.","Ich brauche drei mächtige Gegenstände. Bringt sie mir, dann gehört die Zusammenstellung Euch.","Ich darf die Namen dieser Gegenstände nicht aussprechen, aber ich werde sie aufschreiben.","Bringt mir die Gegenstände, die auf dieser Liste stehen. Wenn Ihr das schafft, seid Ihr wahrlich würdig, ein eigenes Kapitel über unseren Orden zu beginnen."}, {"Bringt dem Bewahrer Bel'dugur in Undercity die Gegenstände von Bel'dugurs Notiz."}},
[737] = {"Verbotenes Wissen", {"Ich habe die Zusammenstellung für Euch, <Name>. Ich muss Euch wohl nicht sagen, dass Ihr sie sorgfältig behandeln solltet. Und dass Ihr sie nicht in unfreundliche Hände gelangen lassen solltet.","Dieses Buch enthält viele Wahrheiten...","... und die Wahrheit kann manchmal wehtun."}, {"Bringt Theldurin 'Yagyins Zusammenstellung'."}},
[738] = {"Sucht Agmond", {"Ausgrabungsleiter Agmond hatte die Verantwortung über unsere Ausgrabungsstätten im Ödland, im Südwesten. Ich sagte \"hatte\", weil... also, ich habe schon seit Wochen nichts mehr von ihm gehört. Ich möchte, dass Ihr Agmond sucht, damit er mir Bericht erstatten kann.","Zuletzt hörte ich, dass sich Agmond in der südlichsten Grabungsstätte im Ödland aufhalten würde - einer winzigen Unternehmung hinter der Festung Angor. Ich würde dort mit der Suche beginnen."}, {"Sucht Agmond."}},
[739] = {"Murdaloc", {"Agmonds Spuren führen nach Westen. Anhand der Blutspuren auf den Felsen und im Sand ist unschwer zu erraten, dass sich der Zwerg ein ganzes Stück weit geschleppt haben muss.","Und hier ist er dann gestorben.","Bei näherer Betrachtung ist das Wort \"Murdaloc\" erkennbar, das grob aus Sandsteinen des Ödlands zusammengestückelt wurde.","Direkt neben Agmonds toter Hand."}, {"Tötet Agmonds Mörder Murdaloc.","Tötet 12 Knochenknacker der Steingrufttroggs.","Erstattet Ausgrabungsleiter Ironband am Loch Modan Bericht."}},
[741] = {"Der geistesabwesende Ausgrabungsleiter", {"Der Ausgrabungsleiter will also das geheimnisvolle Fossil zur Forscherliga nach Darnassus schicken? Es schmeckt mir gar nicht, Dunkelküste ohne ihn zu verlassen.","Hier, <Name>, bringt Ihr dem Chefarchäologen Greywhisker in Darnassus das rätselhafte Fossil."}, {"Bringt dem Chefarchäologen Greywhisker in Darnassus das rätselhafte Fossil."}},
[742] = {"Die Jagd in Ashenvale", {"Achtung, junge Abenteurer! Die Wildnis von Ashenvale erwartet Euch!","Die Horde hat eine starke Präsenz in den Ländern nördlich des Brachlandes aufgebaut. Unsere beiden Außenposten - der Splintertreeposten und der Zoramstrandposten - wollen der Horde zu Ruhm gereichen! Diejenigen, die sich beweisen möchten, sollten dort nach Anleitung suchen. Wichtiger Hinweis: Senani Thunderheart im Splintertreeposten, direkt nördlich des Brachlandes, sucht Abenteurer, die an einer großen Jagd in Ashenvale teilnehmen wollen!"}, {"Sprecht mit Seneni Thunderheart im Splintertreeposten in Ashenvale."}},
[743] = {"Windfuriengefahr", {"Danke, dass Ihr Euch die Zeit genommen habt, mit mir zu sprechen, <Name>. Ich bin Ruul, Krieger und Lehrer.","Ihr habt offensichtlich ein Alter erreicht, in dem Ihr Euch auf Eure Aufgaben vorbereiten müsst, wenn Ihr auf Abenteuer weit außerhalb von Mulgore aus seid.","Wenn Ihr Eure Kraft erproben möchtet, versucht Euch zuerst an den Windfurienharpyien im Südosten. Sie treiben sich an den Bergrändern fernab der Straße herum.","Sie gehören mit zu unseren natürlichen Feinden hier in Mulgore und sind gut geeignet, um Eure Fertigkeiten einzuschätzen."}, {"Bringt Ruul Eagletalon in Bloodhoof 8 Windfurienkrallen."}},
[744] = {"Vorbereitungen auf die Zeremonie", {"<Name>, mein Bruder wird schon bald vor Häuptling Bloodhoof treten und es ist mir eine Ehre, einen Kopfputz für ihn anfertigen zu dürfen.","Ich möchte Euch um einen Gefallen bitten, während ich die letzten dieser Lederriemen gerbe. Ich habe keine Zeit mehr, um genügend Federn zu suchen, und ich habe mich gefragt, ob Ihr wohl noch ein paar für mich sammeln würdet?","Ihr könnt Federn in der richtigen Größe bei den Harpyien finden, die ganz weit im Norden und nordwestlich von Thunder Bluff leben. Ich brauche nur 6 azurblaue und 6 bronzene Federn, um die Ausführung zu vervollständigen."}, {"Sammelt 6 azurblaue und 6 bronzene Federn und bringt sie Eyahn Eagletalon in Thunder Bluff."}},
[745] = {"Gemeinsame Nutzung des Landes", {"Viele Konflikte verwunden dieses Land, <Name>. Ich hoffe, dass Ihr nicht so viele wie ich ertragen müsst. Es gibt nur einen, bei dem ich um Eure Mithilfe bitten möchte. Er wird Euch deutlich machen, wie schrecklich sogar eine kleine Bedrohung für das Land sein kann, wenn man sie ignoriert.","Bleichmähnengnolle haben sich südlich von Bloodhoof in einer Höhle im Westen niedergelassen. Sie missachten unsere Versuche, mit ihnen zu kommunizieren, und vernichten ungezügelt die Tierwelt Mulgores.","Worte allein genügen nicht mehr als Antwort."}, {"Tötet 10 Gerber der Bleichmähnen, 8 Kürschner der Bleichmähnen und 5 Wilderer der Bleichmähnen und kehrt dann zu Baine Bloodhoof in Bloodhoof zurück."}},
[746] = {"Zwergen-Buddelei", {"Im Nordwesten bereiten Zwerge eine Grabungsstätte vor. Sie glauben, dass Geheimnisse in der Erde verborgen seien, und das stimmt auch; dies ist jedoch kein Grund, das Land bei der Suche nach diesen Lehren auszuhöhlen und zu besudeln.","Sammelt die Grabwerkzeuge der Zwerge ein, zerstört sie und kehrt mit den zerbrochenen Werkzeugen der Zwerge zu mir zurück.","Ihr könnt die Werkzeuge in einer Schmiede zerschlagen. Ihr findet eine in Thunder Bluff, aber jede Schmiede, sogar eine Schmiede im Zwergenlager, eignet sich dazu."}, {"Sammelt 5 Ausgrabungsleiterhacken ein.","Zerschlagt die Ausgrabungsleiterhacken in einer Schmiede, um zerbrochene Werkzeuge zu erhalten.","Bringt Baine Bloodhoof in Bloodhoof 5 zerbrochene Werkzeuge."}},
[747] = {"Die Jagd beginnt", {"Ihr seht viel versprechend aus, und ich denke, Ihr werdet Euch dem Stamm gegenüber beweisen. Vielleicht wird man Euch eines Tages in der großen Stadt Thunder Bluff willkommen heißen. Bevor das jedoch möglich ist, müsst Ihr Euch meinem Vater, Häuptling Hawkwind, beweisen.","Wir hier oben auf der Red Cloud Mesa sind stolz auf unsere hervorragenden Jagdfertigkeiten. Tauren jagen aus Notwendigkeit und als sportlichen Wettkampf. Unsere Fleischvorräte gehen zur Neige und wir brauchen Federn, um Kleidung herzustellen. Jagt die in der Nähe herumwandernden Ebenenschreiter und beweist Euch, indem Ihr für Nachschub für das Dorf sorgt."}, {"Grull Hawkwind im Camp Narache möchte, dass Ihr ihm 7 Ebenenschreiterfedern und 7 Stücke Ebenenschreiterfleisch bringt."}},
[748] = {"Vergiftetes Wasser", {"Goblins und ihre Gefolgsleute haben unsere heiligen Wasserbrunnen besudelt! Das dürfen wir nicht zulassen.","Um alle Brunnen zu säubern, muss ich ein Säuberungstotem herstellen und Ihr müsst dieses Totem dann zu den Brunnen bringen und ein Säuberungsritual durchführen.","Zuerst werden wir ein Säuberungstotem für den Wasserbrunnen von Winterhoof herstellen. Dabei muss uns das Land behilflich sein. Jagt Präriewölfe wegen ihrer Tatzen und erwachsene Ebenenschreiter wegen ihrer Krallen. Sie sind im Südwesten zu finden.","Kehrt mit den Tatzen und Krallen zu mir zurück, <Name>."}, {"Bringt Mull Thunderhorn in Bloodhoof 6 Präriewolftatzen und 4 Ebenenschreiterkrallen."}},
[749] = {"Die überfallene Karawane", {"Vor ein paar Tagen haben wir am Nordufer des Stonebullsees eine bewachte Karawane der Venture Company entdeckt. Wir haben bei früheren Kämpfen mit ihnen zwei unserer Leute verloren, deshalb haben wir mit der Karawane kurzen Prozess gemacht.","Wir haben ihre Nachschubkisten zurückgelassen, weil wir dachten, unsere Fackeln würden alles Wertvolle zerstören, aber da man in der Nähe der ausgebrannten Wagen Bergungstrupps der Venture Company gesichtet hat, haben wir uns wohl geirrt.","Würdet Ihr zur Karawane gehen und den Inhalt der Kisten untersuchen?"}, {"Morin Cloudstalker möchte, dass Ihr den Inhalt einer Nachschubkiste der überfallenen Karawane untersucht."}},
[750] = {"Die Jagd geht weiter", {"Ein Tauren, der in der Fertigkeit des Jagens bewandert ist, weiß, dass seine Beute nicht nur eine Trophäe ist. Die Wildtiere der Ebenen versorgen uns mit allem, was wir zum Überleben brauchen. Ihr würdet die Ältesten ziemlich beeindrucken, wenn Ihr ein paar hochgeschätzte Bergpumapelze erjagen würdet. Ihr findet die Wildtiere in den Hügeln im Süden.","Unsere Kinder brauchen Kleidung und unsere Zelte müssen repariert werden."}, {"Grull Hawkwind im Camp Narache möchte, dass Ihr ihm 10 Bergpumapelze bringt."}},
[751] = {"Die überfallene Karawane", {"Als Ihr den Deckel der Kiste aufstemmt, findet Ihr sie vollgestopft mit einem Sortiment aus Erzen und Mineralien, weitere Beweise für den Bergbau der Venture Company in Mulgore. Oben auf dem Erz und den Mineralien liegt ein fest verschnürtes Bündel Papiere, das Ihr an Euch nehmt.","Vom Vorgebirge im Osten her könnt Ihr schwach Arbeitsgeräusche hören und Ihr seht Rauch von einem großen Feuer aufsteigen."}, {"Bringt Morin Cloudstalker in der Nähe von Bloodhoof die Dokumente der Venture Company."}},
[752] = {"Eine bescheidene Bitte", {"Alle Mitglieder des Stammes teilen die Harmonie des Lebens miteinander. Wir leben und arbeiten zusammen.","Mit der Verpflichtung, füreinander da zu sein, haben wir eine große Verantwortung übernommen. Ich habe eine bescheidene Bitte an Euch.","Meine Mutter ist heute Morgen fortgegangen, um Wasser aus dem Brunnen südöstlich von Narache zu holen. Das ist jetzt schon eine Weile her. Würdet Ihr wohl an meiner Stelle nach ihr sehen, während ich mich hier um Stammesangelegenheiten kümmere?"}, {"Häuptling Hawkwind möchte, dass Ihr seine Mutter beim Brunnen südöstlich vom Camp Narache sucht."}},
[753] = {"Eine bescheidene Bitte", {"Ich habe in meinem Leben so viele Pfade beschritten und meinen alten Beinen mangelt es an der Energie, die sie einst hatten. Ich kann aber immer noch meine Pflichten innerhalb des Stammes erfüllen. Manchmal braucht eine alte Frau eben nur etwas länger dafür.","Aber Ihr scheint mir ein eifriger <Klasse> zu sein. Wir sollten Eure jugendliche Vitalität auf die Probe stellen. Holt eine Wasserkanne vom Brunnen und bringt sie meinem Sohn, dem Häuptling im Camp Narache.","Vergesst nicht, dass selbst die bescheidenste Tat die Anerkennung der Ahnen finden kann."}, {"Holt eine Wasserkanne vom Wasserbrunnen.","Bringt die Kanne Häuptling Hawkwind im Camp Narache, das nordwestlich des Wasserbrunnens liegt."}},
[754] = {"Säuberung von Winterhoof", {"Ich habe das Totem angefertigt, um den Wasserbrunnen von Winterhoof zu säubern. Jetzt müsst Ihr es zum Brunnen bringen und ein Säuberungsritual durchführen. Ihr findet ihn im Südosten, bewacht von elenden Goblins!","Das wird keine leichte Aufgabe, aber Ihr müsst es tun, wenn wir jemals wieder sein Wasser trinken sollen.","Viel Glück, <Name>."}, {"Benutzt das Säuberungstotem von Winterhoof beim Wasserbrunnen von Winterhoof und kehrt dann zu Mull Thunderhorn zurück."}},
[755] = {"Riten der Erdenmutter", {"Eure Bereitschaft, für die Tauren von Narache eine bescheidene Bitte zu erfüllen, und Euer Eifer zu lernen sind edle Charakterzüge, <Name>. Ich weiß, eines Tages wird man in Thunder Bluff von Euch als einer bedeutenden Person sprechen, <Klasse>.","Bevor es so weit ist, müsst Ihr jedoch die Riten der Erdenmutter bestehen, derer es drei gibt.","Die erste Prüfung ist der Ritus der Kraft. Begebt Euch zu Seher Graytongue und sagt ihm, dass Häuptling Hawkwind Euch schickt.","Ihr findet die Behausung des Sehers direkt südlich des Lagers Narache in den Hügeln verborgen."}, {"Begebt Euch zu Seher Graytongue, der in den Hügeln direkt südlich des Lagers Narache lebt."}},
[756] = {"Thunderhorn-Totem", {"Um das Säuberungstotem von Thunderhorn zu schaffen, benötigt Ihr die Klauen der Raubtiere der Mulgoreebene. Jagt Präriepirscher wegen ihrer Pirscherklauen und Flachlandpumas wegen ihrer Pumaklauen und kehrt dann zu mir zurück.","Ihr findet die Präriepirscher und Flachlandpumas im Osten und Westen."}, {"Bringt Mull Thunderhorn 6 Pirscherklauen und 6 Pumaklauen."}},
[757] = {"Ritus der Kraft", {"Die Riten der Erdenmutter sind die Schritte, die ein junger Tauren gehen muss, um sich in Thunder Bluff Respekt zu verschaffen.","Ihr müsst zuerst den Ritus der Kraft bestehen. Bei dieser Prüfung müsst Ihr Euren Mut beweisen, indem Ihr Feinde des Stammes tötet.","Der Borstennackenstamm aus der Dornrankenklamm im Osten ist in unser Stammesterritorium eingedrungen. Sie überfallen unsere Jägergruppen und rauben in der Dunkelheit unsere Dörfer aus.","Zeigt Euren Wagemut, indem Ihr diese Bösewichte zur Strecke bringt und dem Häuptling des Lagers Narache ihre Gürtel zum Beweis Eurer Taten vorlegt."}, {"Tötet die Borstennacken in der Dornrankenklamm und bringt Häuptling Hawkwind im Camp Narache 12 Gürtel der Borstennacken."}},
[758] = {"Säuberung von Thunderhorn", {"Das Totem ist bereit, <Name>. Und wiederum ist es Eure Aufgabe, das Ritual der Säuberung durchzuführen.","Geht zum Wasserbrunnen von Thunderhorn nördlich des Dorfes und benutzt das Säuberungstotem von Thunderhorn bei Eurem Ritual. Kehrt danach zu mir zurück.","Mögen Euch die Geister leiten."}, {"Benutzt dasSäuberungstotem von Thunderhorn beim Wasserbrunnen von Thunderhorn und kehrt dann zu Mull zurück."}},
[759] = {"Wildmane-Totem", {"Es gibt nur noch einen Wasserbrunnen, der gesäubert werden muss; er trägt den Namen des Wildmaneklans. Damit dieser Brunnen wieder rein wird, muss das Land die Zähne eines wilden Raubtiers opfern, die eines Präriewolfalphatiers. Findet die Alphatiere im Norden und kehrt dann zu mir zurück, wenn Ihr Eure Jagd beendet habt.","Alphatiere streifen oft am Fuße unserer mächtigen Stadt Thunder Bluff umher."}, {"Bringt Mull Thunderhorn in Bloodhoof 8 Präriealphazähne."}},
[760] = {"Säuberung von Wildmane", {"Die Geister sprechen von Eurer Säuberung der Wasserbrunnen von Winterhoof und Thunderhorn, junger <Name>. Das Totem von Wildmane ist fertig und die letzte Aufgabe erwartet Euch.","Der Wasserbrunnen von Wildmane liegt nördlich von Thunder Bluff. Führt das Ritual durch! Heilt das Land vom Gift der Goblins! Lasst das Wasser wieder klar und rein fließen!"}, {"Benutzt das Säuberungstotem von Wildmane beim Wasserbrunnen von Wildmane und kehrt dann zu Mull Thunderhorn zurück."}},
[761] = {"Sturzfliegerjagd", {"Der Sturzflieger ist ein gewitzter Vogel, der schwer zu finden und zu jagen ist. Eine Sammlung Sturzfliegerkiele ist ein Zeichen der Geschicklichkeit und Entschlossenheit eines Jägers.","Wenn Ihr gewillt seid, diese Aufgabe anzunehmen, zieht in die Ebene hinaus und jagt die Sturzflieger.","Bringt mir ihre Kiele und macht Eurem Klan Ehre.","Sturzflieger findet man überall in Mulgore, aber Ihr müsst sehr scharfe Augen haben, um sie zu entdecken, und Ihr braucht flinke Hufe, um sie zu fangen."}, {"Bringt Harken Windtotem in Bloodhoof 8 Trophäen-Sturzfliegerkiele."}},
[762] = {"Ein Botschafter des Bösen", {"Ragnaros persönlich schickte den Gesandten Infernus zur Festung Angor, damit er dort die Suche der Zwerge der Schattenschmiede nach in Uldaman verschollenen Artefakten organisiert.","Unser Volk hat bereits einen enormen Verlust erlitten, aber in letzter Zeit hat sich die Lage offensichtlich noch verschlimmert. Ich wage kaum zu schätzen, wie viele Artefakte sie finden und gegen Ironforge einsetzen könnten.","Unser Ziel ist einfach, <Klasse>. Kehrt ins Ödland zurück und beseitigt den Botschafter Infernus. Sobald die Bedrohung aus der Welt geschafft ist, kehrt mit einem Beweis Eures Sieges zu Berater Belgrum zurück.","Es liegt jetzt in Euren Händen."}, {"Tötet den Botschafter Infernus in der Festung Angor und bringt Berater Belgrum in Ironforge einen Beweis seines Tod."}},
[763] = {"Riten der Erdenmutter", {"Damit Ihr die Riten der Erdenmutter fortsetzen könnt, müsst Ihr noch zwei weitere Prüfungen bestehen. Es ist an der Zeit, Eure Erfahrungen zu erweitern, <Name>.","Reist nach Bloodhoof und sprecht mit Häuptling Baine Bloodhoof, dem Sohn von Cairne. Dort könnt Ihr Eure Reise fortsetzen und Euch die Anerkennung der Ältesten von Thunder Bluff verdienen.","Bringt Baine dieses Totem. Er wird meine Schnitzereien erkennen und Euch helfen.","Folgt der Straße, die aus dem Camp Narache herausführt, und beeilt Euch. Schweift nicht von der Straße ab, Ihr könntet Euch sonst verirren."}, {"Bringt Baine Bloodhoof in Bloodhoof Hawkwinds Totem. Folgt der Straße, die aus dem Camp Narache herausführt."}},
[764] = {"Die Venture Company", {"Das erklärt auch die Aufstockung von Angestellten und Ausrüstung der Venture Company, die wir in Mulgore gesehen haben. Diese Goblins... ihre Firma wächst viel zu schnell, als dass es gut für sie wäre. Es heißt doch, zeigt man einem Goblin etwas, kann man gleich im Hintergrund die Registrierkasse klingeln hören.","Gierige kleine Teufel. Bedauerlich für ihre geschäftlichen Vorhaben, aber wir Tauren können es nicht zulassen, dass sie ihre Arbeiten hier in unserem Land durchführen. Begebt Euch zu ihrer Mine nordöstlich der überfallenen Karawane und schickt ihnen eine Nachricht."}, {"Tötet für Morin Cloudstalker in der Nähe von Bloodhoof 14 Tagelöhner der Venture Co. und 6 Vorsteher der Venture Co."}},
[765] = {"Vorsteher Fizsprocket", {"Laut der Dokumente, die Ihr von der Karawane geborgen habt, hat die Venture Company viele Pläne parat, um zu versuchen, uns von unserem Land hier zu vertreiben und damit freie Bahn zur Plünderung des Landes zu haben.","Ich bin entsetzt und es stößt mich ab, auf welches Niveau man sich herablassen kann, um seine Ziele durchzusetzen; ich will, dass der führende Kopf hinter ihren Plänen vernichtet wird. Er heißt Vorsteher Fizsprocket, Ihr findet ihn in der Mine der Venture Co. östlich der überfallenen Karawane. Bringt mir sein Klemmbrett, vielleicht finden wir damit ja etwas Nützliches heraus."}, {"Tötet Vorsteher Fizsprocket und bringt Morin Cloudstalker in der Nähe von Bloodhoof dessen Klemmbrett."}},
[766] = {"Mazzranache", {"Mein Großvater erzählte mir Geschichten von seinen Kämpfen gegen einen Schreiter namens Mazzranache. Er erzählte mir von seinen dämonischen roten Augen, rasiermesserscharfen Krallen und seinem giftigen Biss.","Wie der Zufall so spielt, stieß ich auf einer meiner Reisen durch die Ebene auf ihn und trug eine üble Bisswunde an meiner Schulter davon. Er war so Furcht erregend wie ihn mein Großvater beschrieben hatte. Ich werde ein paar nur sehr schwer zu findende Körperteile von Tieren benötigen, um die Infektion durch seinen Biss zu säubern: ein Herz eines Präriewolfs, einen Knochen eines Flachlandpumas, eine Schuppe eines Ebenenschreiters und einen Muskelmagen eines Sturzfliegers.","Schnell, Zeit ist hier entscheidend."}, {"Bringt Maur Raincaller in Bloodhoof ein Herz eines Präriewolfs, einen Knochen eines Flachlandpumas, eine Schuppe eines Ebenenschreiters und einen Muskelmagen eines Sturzfliegers."}},
[767] = {"Ritus der Sicht", {"Der Ritus der Sicht, einer der Riten der Erdenmutter, wird Euch helfen, den Respekt der Ältesten von Thunder Bluff zu erlangen.","Unser Volk hat gelernt, dass das Land unser allerheiligster Versorger ist. Um am Ritus der Sicht teilzunehmen, müsst Ihr mit dem geistigen Anführer des Dorfes sprechen, Zarlman Two-Moons."}, {"Sprecht in Bloodhoof mit Zarlman Two-Moons."}},
[768] = {"Leder sammeln", {"Seid gegrüßt. Ihr habt so einen Glanz in den Augen; Ihr brennt darauf, in die Ebene zurückzukehren, um zu jagen. Möget Ihr Eurem Klan Ehre machen!","Ich arbeite mit den Häuten von Wildtieren und fertige daraus Kleidung und Rüstung für die Leute von Thunder Bluff.","Wenn Ihr Wildtiere jagt und mir ihre Häute bringt, fertige ich Euch etwas daraus."}, {"Bringt Veren Tallstrider in Thunder Bluff 12 Stücke leichtes Leder."}},
[769] = {"Kodobalgtasche", {"Die Kodowildtiere von Mulgore sind stark und robust - Charaktereigenschaften, die wir sehr schätzen. Wenn Ihr in der Lederverarbeitung bewandert seid und wissen möchtet, wie man eine Kodobalgtasche fertigt, bringt mir die Materialien, die ich für mein Handwerk brauche.","Dann teile ich mein Wissen mit Euch."}, {"Bringt Veren Tallstrider in Thunder Bluff 4 Einheiten leichtes Leder und 4 grobe Fäden."}},
[770] = {"Der Dämonennarbenumhang", {"Der struppige Balg von Geistheuler trägt immer noch die schreckliche Wunde, die der Wolf im Kampf gegen die Brennende Legion davongetragen hat.","Vielleicht weiß jemand in Bloodhoof, wie Geistheuler starb..."}, {"Sucht jemanden, der etwas über Geistheuler weiß."}},
[771] = {"Ritus der Sicht", {"Um den Ritus der Sicht zu bestehen, müsst Ihr die Zutaten sammeln, die für das Wasser der Seher benötigt werden:","Ich brauche Quellsteine, die man in der Nähe von Wasserbrunnen in Mulgore findet. Diese kombiniere ich mit Bernsteinkorn, das von den mächtigen Bäumen in unserem Land fällt.","Diese Reagenzien enthalten magische Elemente, und wenn sie erhitzt und vor dem Stammesfeuer verzehrt werden, bekommt man eine Vision. Folgt ihr, dann wird sie Euch zum nächsten Schritt dieser heiligen Suche führen."}, {"Sammelt 2 Quellsteine und 2 Bernsteinkörner und bringt sie Zarlman Two-Moons in Bloodhoof."}},
[772] = {"Ritus der Sicht", {"Hier ist es nun, das Wasser der Seher.","Wenn Ihr bereit seid, trinkt das Wasser in der Nähe des Stammesfeuers. Nachdem Ihr das heilige Wasser getrunken habt, wird es einen Moment dauern, bis die Vision vor dem Feuer erscheint.","Dann liegt es bei Euch, ihr zu Eurer Bestimmung zu folgen ..."}, {"Trinkt vor dem Stammesfeuer in Bloodhoof das Wasser der Seher und folgt der Vision, sobald sie erscheint."}},
[773] = {"Ritus der Weisheit", {"Um die Anerkennung der Ältesten von Thunder Bluff zu verdienen, müsst Ihr als Nächstes den Ritus der Weisheit absolvieren.","Jetzt, da Ihr den Ritus der Sicht bestanden habt, geben Euch die Vorfahrengeister der Teufelsfelsen den Segen unserer Vorfahren. Nur jene, die von dem Wasser der Seher getrunken haben, können diesen Segen erhalten.","Reist von Thunder Bluff gen Osten zu den Teufelsfelsen und sprecht mit dem Vorfahrengeist, <Name>."}, {"Reist von Thunder Bluff gen Osten zu den Teufelsfelsen und sprecht mit dem Vorfahrengeist."}},
[775] = {"Reise nach Thunder Bluff", {"Jetzt wird man Euch in unserer großen Stadt verehren. Ein <Volk>, der würdig ist, die Riten der Erdenmutter abzuschließen, hat diese Ehre verdient.","Für Eure letzte Aufgabe sucht unseren edlen Anführer Cairne Bloodhoof auf, der auf der höchsten Mesa in Thunder Bluff residiert."}, {"Sprecht in Thunder Bluff mit Cairne Bloodhoof."}},
[776] = {"Riten der Erdenmutter", {"Ihr habt die Riten der Erdenmutter absolviert und Euch einen Platz in Thunder Bluff verdient.","Um den Platz zu verteidigen, den Ihr Euch so hart erarbeitet habt, müsst Ihr allerdings auch weiterhin Euren Wert für unser Volk beweisen.","Wir sind Jäger, <Name>. Unten in Mulgore gibt es einen mächtigen Kodo namens Arra'chea. Zeigt mir Euer Können im Fährtenlesen und Jagen, indem Ihr mir das Horn von Arra'chea bringt."}, {"Cairne Bloodhoof in Thunder Bluff möchte, dass Ihr ihm das Horn von Arra'chea bringt."}},
[777] = {"Das wird schwierig", {"Bitte sehr, das ist für Euch.","Ich werde hier drüben sein... in Deckung.","Gesellt Euch ruhig zu mir."}, {"Bringt Lotwil Veriatus ein Zahnrad Nr. 5."}},
[778] = {"Das wird schwierig", {"Haha! Fertig! Die Fesseln sind fertig!","Der Moment ist gekommen, Lucien. Der Moment ist gekommen, Servo!","Seid Ihr bereit, Zeuge der perfekten Verschmelzung von Alchimie, Ingenieurskunst und des Arkanen zu werden, <Name>?","Ich werde jetzt einen mächtigen Elementar beschwören. Das ist heute ein Bahn brechender Tag für uns alle."}, {"Besiegt den Fam'retor-Wächter und bringt Lotwils Fesseln der Elementarbindung zu Lotwil Veriatus zurück."}},
[779] = {"Siegel der Erde", nil, nil},
[780] = {"Die Kampfeber", {"Die Kampfeber der Dornrankenklamm im Osten sind in unser Stammesjagdterritorium eingedrungen. Sie wurden von den Borstennackenstachelebern, mit denen wir uns im Krieg befinden, abgerichtet, niederträchtig zu sein.","Tötet diese elenden Kreaturen und bringt mir ein paar ihrer Schnauzen und Flanken, damit wir daraus einen Eintopf für unsere Jungen zubereiten können."}, {"Grull Hawkwind im Camp Narache möchte, dass Ihr für ihn Kampfeber tötet und ihm 8 Kampfeberschnauzen und 8 Kampfeberflanken bringt."}},
[781] = {"Angriff auf Camp Narache", {"Nachdem Ihr die Karte sorgfältig studiert habt, erkennt Ihr, dass Eure Entdeckung die geheimen Pläne des Grimmhauer-Kriegshäuptlings für eine ausgedehnte Belagerung des Lagers Narache sind.","Häuptling Hawkwind wird diese Informationen sicherlich gut gebrauchen können!"}, {"Bringt Häuptling Hawkwind im Camp Narache die Angriffspläne der Borstennacken."}},
[782] = {"Zerbrochene Allianzen", {"Durch die Allianz der Horde mit dem Großdrachen Deathwing während des zweiten Krieges wissen wir, dass zwei ihrer Offiziere, Schattenbringer und Hematus, in der Lethlorklamm weit im Osten eingesperrt sind. Wir müssen unsere verderbten Verbündeten von damals zum Stillschweigen bringen, um die Ehre der Horde wiederherzustellen!","Wir hatten Tho'grun und seine Gruppe Ogersöldner angeheuert, damit sie uns helfen, das Zeichen der Erde in die Hände zu bekommen, ein Schlüssel, mit dem das Gefängnis des Drachen geöffnet werden kann. Tho'grun hat uns jedoch verraten, uns überrumpelt und ihn sich selbst angeeignet. Begebt Euch zum Camp Boff und holt ihn zurück!"}, {"Tötet Tho'grun und bringt Gorn in Kargath das Zeichen der Erde."}},
[783] = {"Die Bedrohung von innen", {"Ich hoffe, Ihr habt Euren Gürtel festgezurrt, <Klasse>, denn es gibt hier in Nordhain viel Arbeit.","Und ich meine damit nicht Landarbeit.","Die Wachen von Stormwind haben alle Hände voll zu tun, um den Frieden zu bewahren; viele von uns weilen in fernen Ländern und viele Bedrohungen rücken immer näher. Deshalb rekrutieren wir jeden, der gewillt ist, seine Heimat zu verteidigen. Und die Allianz.","Falls Ihr hier seid, um dem Aufruf zu folgen, sprecht mit meinem Vorgesetzten, Marshal McBride. Er ist in der Abtei hinter mir."}, {"Sprecht mit Marshal McBride."}},
[784] = {"Vernichtet die Verräter", {"Angeführt von Admiral Proudmoore drangen die Menschen von Kul Tiras nach Durotar vor und brachen damit den Pakt, den der Kriegshäuptling vor Jahren mit Jaina Proudmoore geschlossen hatte, um Archimonde zu besiegen.","Die Menschen-Aggressoren wurden zurückgeschlagen und Burg Tiragarde fiel. Kürzlich jedoch hat die Reserve des Admirals unter der Führung von Lieutenant Benedict die Burg zurückerobert und stellt erneut eine Bedrohung für unser Heimatland dar. Diese Menschen haben keinen Respekt vor Diplomatie.","Beweist Eure Ehre und reist gen Süden zur Burg Tiragarde, um die Menschen-Eindringlinge zu eliminieren."}, {"Tötet 10 Matrosen von Kul Tiras, 8 Marinesoldaten von Kul Tiras sowie Lieutenant Benedict und kehrt dann zu Gar'Thok in Klingenhügel zurück."}},
[786] = {"Vereitelung der Kolkaraggression", {"Sprecht leiser, <Klasse>. Die Zentauren der Kolkar liegen gleich hinter jener Kuppe im Westen der Kolkarklippe.","Während ihres Angriffs letzte Nacht schlich ich mich in ihr Dorf und fand heraus, dass diese dreckigen Bestien einen dreistufigen Angriff auf die Trolle und Orcs von Durotar geplant hatten.","Ihre Invasion muss verhindert werden. Vielleicht könnt Ihr die Streitmacht zusammenziehen, die nötig ist, um die Kolkarklippe zu unterwandern und deren Angriffspläne zu vernichten.","Zuletzt sah ich, dass Sie sich auf drei ihrer Anführer verteilten."}, {"Lar Prowltusk außerhalb von Sen'jin möchte, dass Ihr die 3 Sets der Angriffspläne zerstört, die innerhalb der Kolkarklippe verwahrt werden."}},
[787] = {"Die neue Horde", {"Throm-ka, <Klasse>. Ich bin Eitrigg, von Thrall beauftragt, die Ausbildung der Rekruten zu beaufsichtigen.","Die Horde ist nicht mehr das, was sie einmal war. Es gab eine Zeit, da ich die Horde verließ, enttäuscht durch den wachsenden Einfluss der machthungrigen Marionetten der Brennenden Legion. Während meines Exils wurde ich von einer Gruppe Menschen gefangen genommen, aber dann von dem Kriegshäuptling gerettet. Er erzählte mir von seiner Vision einer Horde, frei von dämonischen Einflüssen, von den Schamanen geleitet, und so kehrte ich zurück.","Gornek wird weitere Anweisungen für Euch haben."}, {"Meldet Euch bei Gornek beim Höhlenbau."}},
[788] = {"Scharfe Zähne", {"Als Erstes werden wir Euer Rückgrat etwas kräftigen. Ich könnte Euch ins Brachland schicken, um Kodos zu jagen, aber mal ehrlich, lebend seid Ihr uns nützlicher als tot.","Ich glaube, die scheckigen Eber nördlich von hier wären genau das Richtige für Euch."}, {"Tötet 10 scheckige Eber und kehrt dann zu Gornek beim Höhlenbau zurück."}},
[789] = {"Stich des Skorpiden", {"Sowohl mächtige Krieger als auch tollpatschige Anfänger fallen dem Giftstachel der Skorpiden zum Opfer. Nordwestlich von hier findet Ihr eine größere Ansammlung Skorpiden. Bringt mir 10 ihrer Schwänze als Beweis für Eure Leistungsfähigkeit im Kampf.","Das Gegengift für ihren Stich wird aus genau dem Gift, das aus ihren Stacheln extrahiert wurde, gewonnen. Wir haben immer einen großen Vorrat an Gegengift für dieses Skorpidgift, um Grünschnäbel wie Euch damit zu heilen...","Aber Ihr werdet das doch bestimmt nicht brauchen, oder?"}, {"Bringt 10 Skorpidarbeiterschwänze zu Gornek im Höhlenbau."}},
[790] = {"Sarkoth", {"<Klasse>! Ich dachte schon, ich müsste hier draußen sterben und niemand würde es bemerken. Auf der Jagd nach den Skorpiden des Tals stieß ich auf ein besonders heimtückisches Exemplar. Ich stürzte mich auf ihn und konnte einer seiner Klauen einen ordentlichen Draufschlag versetzen, bevor sie sich um mein Bein schloss.","Ich hatte jedoch nicht mit seinem Stecher gerechnet, den er tief in meine Brust trieb, mit dem er mein Fleisch zerschnitt und mir einen großen Blutverlust verursachte. Bitte, Ihr müsst den Skorpid für mich töten! Meine Ehre darf nicht besudelt werden! Da oben auf dem Plateau im Süden habe ich gegen ihn gekämpft."}, {"Tötet Sarkoth und bringt seine Klaue zu Hana'zua."}},
[791] = {"Akzeptiert Euer Schicksal", {"Das Alter hat mich nutzlos für die Schlacht gemacht. Jetzt mache ich mich eben auf andere Weise nützlich.","Von diesem Aussichtspunkt aus achte ich auf Eindringlinge. Während unsere Kraft hier zunimmt, muss ich mein Signalhorn immer seltener benutzen.","Um mir die Zeit zu vertreiben, fertige ich Dinge an, die den jüngeren und fähigeren Kriegern bei der Verteidigung unserer Heimat helfen können.","Für Euch könnte ich eine Tasche für Eure Habseligkeiten anfertigen. Falls ein solcher Gegenstand Euch nützlich sein könnte, bringt mir etwas Segeltuch, ein Material, das unter Menschen und Zentauren verbreitet ist."}, {"Furl Scornbrow im Wachturm von Klingenhügel möchte 8 Segeltuchreste haben."}},
[792] = {"Üble Familiare", {"Ich denke, das Tal der Prüfungen wird Euch viel lehren, <Klasse>.","Man hat mich eigentlich ins Tal geschickt, um Euch anzuleiten, aber ich habe dort eine sich ausbreitende Besudelung entdeckt...","Eine Gruppe, die sich die 'Burning Blade' nennt, hat hier im Tal der Prüfungen einen Koven für ihre Mitglieder. Sie lungern im Nordosten in einer Höhle herum und ihre üblen Familiare kriechen daraus hervor, um Chaos anzurichten.","Als erste Aktion gegen die Burning Blade bitte ich Euch, diese Familiare zu vernichten. Tötet so viele Ihr könnt, und falls Ihr überlebt, kehrt zu mir zurück."}, {"Tötet 12 üble Familiare.","Kehrt dann zu Zureetha Fargaze beim Höhlenbau zurück."}},
[793] = {"Zerbrochene Allianzen", {"Das Siegel der Erde wird von drei Runensteinen gebildet, die in der Lethlorklamm auf Säulen aus Diamant, Opal und Amethyst liegen. Ihr könnt die Runensteine mit Hilfe des Zeichens der Erde entfernen und so das Siegel der Erde brechen.","Seid jedoch vorsichtig, <Name>, die Schwarzdrachen Schattenbringer und Hematus sollte man nicht unterschätzen. Tötet sie und kehrt mit ihren Bindungen zu mir zurück.","Um die Klamm zu erreichen, müsst Ihr in den östlichsten Teil des Ödlandes reisen. Lasst jedoch Vorsicht walten, da die Klamm zahllose Drachen beherbergt."}, {"Benutzt das Zeichen der Erde, um die Säulen aus Diamant, Opal und Amethyst zu aktivieren und die Runensteine zu holen.","Legt die Runensteine auf das Siegel der Erde, um Schattenbringer und Hematus zu befreien.","Tötet sie und bringt dann Gorn Schattenbringers Bindung, Hematus' Ketten und das Zeichen der Erde."}},
[794] = {"Medaillon der Burning Blade", {"Durch meine hellseherischen Kräfte weiß ich, dass im Koven der Burning Blade ein Gegenstand der Macht verborgen ist, der durch Bestien und schwarze Magie bewacht wird.","Er wird das Medaillon der Burning Blade genannt und Eure nächste Aufgabe besteht darin, es zu finden und aus dem Koven zu entfernen.","Seid jedoch vorsichtig, denn das Medaillon könnte im Besitz eines Agenten der Burning Blade sein, und falls dem so ist, ist die Macht dieses Agenten weitaus stärker als die der Familiare, auf die Ihr bereits gestoßen seid.","Geht, <Name>. Ihr findet den Koven in einer Höhle im Norden und Westen."}, {"Bringt Zureetha Fargaze beim Höhlenbau das Medaillon der Burning Blade."}},
[795] = {"Siegel der Erde", nil, nil},
[804] = {"Sarkoth", {"Was Ihr für mich getan habt, gibt mir Zuversicht. Ich darf nicht einfach so gehen! Ich muss durchhalten!","Ohne Hilfe schaffe ich jedoch den Weg zurück zum Höhlenbau nicht. Bitte, <Name>, kehrt zum Höhlenbau zurück und berichtet Gornek von meiner Lage. Vielleicht kann er mir helfen."}, {"Informiert Gornek beim Höhlenbau über Hana'zuas Notlage."}},
[805] = {"Bericht erstatten in Sen'jin", {"Eure Prüfungen durch die Burning Blade sind beendet... jedenfalls hier im Tal. Ich möchte aber, dass Ihr von Euren Funden berichtet.","Begebt Euch in das Trolldorf Sen'jin und sucht nach Meister Gadrin. Sen'jin liegt östlich hinter dem Tal und dann geht es an der Weggabelung nach rechts.","Berichtet Gadrin von den Burning Blade und dass sie das Tal der Prüfungen erreicht haben. Bringt von ihm in Erfahrung, ob sie Sen'jin schon erreicht haben.","Geht, <Name>, und beeilt Euch. Ich fürchte, dass das Böse, das wir im Koven der Burning Blade entdeckt haben, nur der Vorbote eines viel größeren Übels ist..."}, {"Sprecht mit Meister Gadrin in Sen'jin."}},
[806] = {"Dunkle Stürme", {"Wir können den Burning Blade nicht erlauben, in Durotar Fuß zu fassen! Wir müssen sie vernichten, bevor das Böse zu schwären beginnt!","Ich habe selbst Nachforschungen unternommen und herausgefunden, dass ein Hexenmeister der Burning Blade, der Goblin Fizzle Dunkelsturm, nahe dem Donnergrat im Nordwesten kampiert. Er und seine Kultdiener verbreiten dort Chaos.","Sucht und besiegt Fizzle und bringt mir seine Klaue!"}, {"Bringt Fizzles Klaue zu Orgnil Soulscar in Klingenhügel."}},
[808] = {"Minshinas Schädel", {"Ich höre die Stimme meines Bruders Minshina, der in meinen Träumen zu mir spricht.","Er wurde von Zalazane, dem Hexenmeister auf den Echoinseln im Osten, gefangen genommen. Und er ist tot.","Aber der Tod bedeutet nicht Freiheit für meinen Bruder. Minshinas Geist ist durch Zalazanes Magie in seinem eigenen Schädel gefangen. In meinen Träumen sehe ich ihn zusammen mit weiteren Schädeln in einem Kreis der Macht auf der Größten der Echoinseln. Solange er dort bleibt, ist die Seele meines Bruders verdammt.","Bitte, <Name>. Sucht den Kreis und bringt Minshinas Schädel zurück. Bringt ihn mir.","Befreit ihn!"}, {"Holt Minshinas Schädel aus dem Kreis der Macht auf den Echoinseln.","Bringt ihn Meister Gadrin in Sen'jin."}},
[809] = {"Ak'Zeloth", {"Seit dem letzten großen Krieg, in dem die Brennende Legion besiegt wurde, suche ich nach den Quellen dämonischer Verderbnis innerhalb der Orc-Gesellschaft. Das Halsband, das Ihr mir brachtet, bestätigt meine Befürchtungen.","Es gehört den Burning Blade, einem Kult, der sich um einen Gegenstand dämonischer Macht schart. Er wird 'Dämonensaat' genannt und im Brachland hoch über dem Glutnebelgipfel aufbewahrt. Er muss zerstört werden!","Geht zum Farwatch-Posten an der Grenze zum Brachland im Westen und sprecht dort mit meinem Assistenten Ak'Zeloth. Er wird Euch weitere Anweisungen geben."}, {"Sprecht im Brachland mit Ak'Zeloth."}},
[812] = {"Heilmittel benötigt", {"<Name>, Ihr hättet den Zeitpunkt nicht besser wählen können. Ich hoffe nur, dass ich Euch auch zu Eurer Eile beglückwünschen kann.","Während eines Kampfes mit ein paar Siechstachelskorpiden war ich unvorsichtig und ich wurde von einem von ihnen heftig gestochen. Während wir uns unterhalten, spüre ich, wie sein Gift mich schwächt. Bei dieser Geschwindigkeit bleibt mir vielleicht noch eine Stunde zu leben. Aber selbst dazu brauche ich Eure Hilfe...","Kor'ghan in Orgrimmar weiß, wie man das Gegengift herstellt. Sucht ihn... und zwar schnell, <Name>. Ich werde nicht mehr lange durchhalten. Er sollte sich in der Kluft der Schatten befinden."}, {"Sucht Kor'ghan in Orgrimmar und besorgt das Siechstachelgegengift. Bringt das Gegengift dann zu Rhinag an der nordwestlichen Grenze von Durotar."}},
[813] = {"Suche nach dem Gegengift", {"Siechstachelskorpide sind eine der tödlichsten Skorpidarten in ganz Durotar. Es war dumm von Rhinag, sie zu jagen, ohne etwas von dem Gegengift mit sich zu führen, aber momentan ist eine ordentliche Standpauke das Letzte, was er benötigt.","Bringt mir ein paar Giftbeutel von den Siechstachelskorpiden in der Nähe des Eingangs nach Orgrimmar, dann stelle ich ein Gegengift für ihn her.","Beeilt Euch, <Name>. Einer der Unseren braucht Eure Hilfe."}, {"Bringt Kor'ghan in Orgrimmar 4 Giftbeutel eines Siechstachelskorpiden."}},
[815] = {"Zerbrecht ein paar Eier", {"Pah! Jetzt hätte ich doch beinahe vergessen, Frühstück zu machen.","<Name>, setzt Euch in Bewegung und bringt mir ein paar Schwanzpeitschereier. Ich brauche mindestens drei davon, wenn ich genug für die morgige Mahlzeit haben möchte.","Die Blutkrallenschwanzpeitscher können ganz schön wild sein und beschützen ihre Eier bis in den Tod. Manchmal vergraben sie ihre Eier allerdings zu tief in der Erde, um sie sicher ausgraben zu können, aber reist doch zu den Echoinseln südöstlich von Sen'jin, dort findet man sie gewöhnlich überall auf den Inseln verteilt."}, {"Bringt dem Koch Torka in Klingenhügel 3 Schwanzpeitscher-Eier."}},
[816] = {"Verloren, aber nicht vergessen", {"<Name>, bitte, könnt Ihr mir helfen? Mein Sohn Kron ist vor Tagen auf die Jagd gegangen und immer noch nicht zurück. Er wollte nach Westen in Richtung des Brachlandes, um dort am Ufer des Südstroms Krokilisken zu jagen. Ich fürchte das Schlimmste.","So stark wie er ist, hat ihn sein starrköpfiger Stolz doch immer wieder in Schwierigkeiten gebracht. Ich habe ihn gewarnt, dass die Krokilisken mächtig und heimtückisch sind. Er wurde wütend und stürmte davon.","Falls Ihr ihn nicht finden könnt, so bringt mir doch wenigstens einen Hinweis darauf, was aus ihm geworden ist ... und wenn Ihr dazu jedem einzelnen Krokilisken an den Ufern des Südstroms den Bauch aufschlitzen müsst."}, {"Bringt Misha Tor'kren auf dem Bauernhof nordwestlich von Klingenhügel einen Hinweis auf Krons Schicksal."}},
[817] = {"Praktische Beute", {"Viele der Bälge, die wir benutzen, stammen von den Durotar-Tigern, <Name>. Decken, Rüstungen, Zelte: Es gibt viele Gründe, warum wir Jagd auf die Wildtiere machen, und ebenso viele Gründe, warum wir sie leben lassen.","Die Zeit ist gekommen, dass wir den Bestand dezimieren. Wir werden immer mehr, und unser Bedarf übersteigt bald unsere Bestände. Ich brauche mehr Bälge, wenn ich geeignete Dinge für unser Volk anfertigen möchte.","Bringt mir 4 Durotar-Tigerfelle, dann werde ich Euch belohnen. Ihr findet sie auf den Inseln südlich von hier."}, {"Bringt Vel'rin Fang in Sen'jin 4 Durotar-Tigerfelle."}},
[818] = {"Ein geistvolles Getränk", {"Meine Augen sind zwar nicht mehr so scharf, aber ich kann immer noch gut genug sehen. Immer öfter muss ich mich auf meine Alchimie-Fertigkeiten verlassen, um mir bei Dingen, die mir früher leicht fielen, nun mit Magie zu helfen. Ich weigere mich jedoch, einen Lehrling einzustellen - bislang ist mir noch kein Troll oder Orc begegnet, der sich als würdig genug erwiesen hätte.","Seid Ihr dessen würdig? Ja, natürlich seid Ihr das... natürlich glaubt Ihr, dass Ihr es seid.","Ich brauche ein paar Dinge. Würdet Ihr sie für mich besorgen?","Ich brauche intakte Makruraaugen und Phiolen mit Kriechersekret. Ihr findet das überall in Durotar, wo es Makruras oder Kriecher gibt. Wir sehen uns bald wieder."}, {"Bringt Meister Vornal in Sen'jin 4 intakte Makruraaugen und 8 Phiolen Kriechersekret."}},
[819] = {"Chens leeres Fässchen", {"Auf einem kleinen Schild an einem Ende des Fässchens steht:","\"Chen Stormstout -","Möge es Euren Geist erheben und Euch Mut machen.\""}, {"Sucht jemanden, der etwas über Chens leeres Fässchen weiß."}},
[821] = {"Chens leeres Fässchen", {"Möchtet Ihr eine Kostprobe von Chens Namensvetter? Ahh, das Stormstout ist ein gehaltvolles Gebräu. Chen gab das Rezept an meinen Mentor weiter und mein Mentor gab es mir. Ich möchte, dass Ihr mir ein paar Dinge besorgt, aber eins möchte ich Euch sagen: Der Kick ist es wert.","Bringt mir 5 Savannenlöwenhauer von beliebigen Savannenlöwen, 5 Ebenenschreiternieren von beliebigen Ebenenschreitern und 1 Donnerechsenhorn von einer Sturmschnauze. Das sollte bestens funktionieren.","Ihr findet diese Zutaten überall im Brachland."}, {"Bringt Braumeister Drohn in Ratchet 5 Savannenlöwenhauer, 5 Ebenenschreiternieren und 1 Donnerechsenhorn."}},
[822] = {"Chens leeres Fässchen", {"Gutes Zeug, dieses Stormstout, was?","Ich habe noch ein Rezept, das ich von Chen gelernt habe. Hättet Ihr Interesse? Die Herstellung dauert nicht lange und es könnte Euch nützlich sein, wenn Ihr Eure Abenteuer fortsetzen wollt.","Bringt mir 5 Blitzschlagdrüsen einer beliebigen Sturmechse, 1 Donnerfalkenspeicheldrüse von großen Donnerfalken und eine Kodoleber einer beliebigen Kodoart des Brachlandes.","Wie ich sagte, das Zeug hat es in sich; das ist das Troggbräu, das ich kürzlich erwähnte."}, {"Bringt Braumeister Drohn in Ratchet 5 Blitzschlagdrüsen, 1 Donnerfalkenspeicheldrüse und 1 Kodoleber."}},
[823] = {"Bericht an Orgnil", {"Auch wir in Sen'jin sind nicht ohne eigene Probleme und wir danken Euch für Eure Hilfe. Aber die Neuigkeiten, die Ihr über die Burning Blade bringt, können Probleme für jedermann bedeuten.","In Klingenhügel ist ein Orc stationiert, Orgnil Soulscar, der Durotar vor solch Bösem wie den Burning Blade bewacht! Geht zu Orgnil und berichtet ihm von unserer Not sowie von Euren Neuigkeiten über das Tal der Prüfungen. Er wird diese Dinge wissen wollen.","Klingenhügel liegt im Norden."}, {"Sprecht mit Orgnil Soulscar in Klingenhügel."}},
[824] = {"Je'neu vom Irdenen Ring", {"Der Irdene Ring ist eine schamanistische Gruppe, die sich dem Studium und dem Erhalt der Elemente widmet. Sie hat bereits mehr über verrückte alte Götter und uralte Legenden und Lehren vergessen, als die meisten Gelehrten jemals in Erfahrung bringen werden.","Zu Eurem Glück kenne ich jemanden hier in Ashenvale, der Mitglied des Irdenen Rings ist. Geht hinüber zum Zoram'gar-Außenposten an der Küste des Zoramstrandes; zeigt Je'neu Sancrea, was Ihr gefunden habt. Wenn überhaupt jemand die Bedeutung dieser Kugel herausfinden kann, dann er."}, {"Bringt die beschmutzte Wasserkugel zu Je'neu Sancrea im Zoram'gar-Außenposten in Ashenvale."}},
[825] = {"Aus den Wrackteilen geborgen...", {"Einer meiner aufmerksamsten Späher hat mir Kunde gebracht, dass die Trümmer von Proudmoores Flotte immer noch an der Küste Durotars liegen, gleich östlich von Burg Tiragarde.","Es ist kein Geheimnis, dass die Menschen, die sich mit diesen elenden kleinen, als Gnome bekannte Kreaturen verbündet haben, über reichliches Wissen auf dem Gebiet der Mechanik verfügen. Wir müssen so viel wie möglich über unsere potenziellen Feinde in Erfahrung bringen. Dann kann unser Volk von diesem neuen Wissen profitieren.","Schwimmt durch die Wrackteile, <Name>, und bringt mir die Werkzeuge der Allianz."}, {"Gar'Thok von Klingenhügel möchte, dass Ihr 3 Gnomen-Werkzeuge aus den Wrackteilen vor der Küste holt."}},
[826] = {"Zalazane", {"Der Hexendoktor Zalazane lebt auf den Echoinseln im Osten. Das sind die Inseln, die wir einst Heimat nannten.","Von dort aus entsendet er seine Trolle zum Festland, wo sie unser Volk verhexen und immer mehr von uns in ihren Bann ziehen.","Er muss aufgehalten werden.","Besiegt Zalazane und seine Diener - ehemalige Trolle der Darkspear, die jetzt nicht mehr zu uns gehören. Bringt mir seinen Kopf, dann weiß ich, dass seine Schreckensherrschaft vorbei ist."}, {"Besiegt Zalazane.","Tötet 8 Voodootrolle und 8 verhexte Trolle.","Bringt Gadrin Zalazanes Kopf."}},
[827] = {"Die Knochenhöhle", {"<Name>. Die Burning Blade haben die Knochenhöhle östlich von Orgrimmar besetzt. Sie führen in dieser Höhle schlimme Rituale durch und fügen sich durch Senghalsbänder selbst Brandwunden zu.","Ich glaube, indem sie diese Halsbänder tragen, bringen die Kultisten sich im Einklang mit dämonischen Mächten. Um das zu bestätigen, benötige ich jedoch ein paar dieser Halsbänder, um sie zu studieren.","Geht zur Knochenhöhle und besorgt ein paar Senghalsbänder von den Kultisten, die Ihr dort findet. Bringt sie mir, dann werde ich ihre Geheimnisse enthüllen."}, {"Besorgt von den Kultisten an der Knochenhöhle ein paar Senghalsbänder.","Bringt sie Margoz in seinem Lager."}},
[828] = {"Margoz", {"Margoz, einer unserer Schamanen, weiß mehr über die Verderbnis der Burning Blade. Er sprach von einer Höhle namens Knochenhöhle in den Bergen - gleich vor den Toren Orgrimmars! - dort soll sich eine große Gruppe von Kultisten der Burning Blade eingenistet haben.","Bevor Ihr jedoch zur Knochenhöhle geht, sprecht mit Margoz. Er ist weise und sein Rat wird geschätzt.","Er lagert im Nordosten zwischen der Küste und der Staubwindklamm.","Folgt seinem Rat, aber was immer Margoz auch sagt, <Name>, ich möchte, dass Ihr diese Kultisten vernichtet!"}, {"Sprecht mit Margoz."}},
[829] = {"Neeru Fireblade", {"Die Senghalsbänder, die Ihr mir brachtet, sind mächtige, dämonische Werkzeuge. Ihre Herkunft zu bestimmen liegt jedoch leider außerhalb meiner Künste als Schamane. Wir werden einen Hexenmeister bitten müssen, sie zu studieren.","Bringt ein Senghalsband zu Neeru Fireblade. Er ist zwar ein talentierter Hexenmeister, doch nutzt er seine Mächte nur, um Dämonen zu bekämpfen, und er behauptet, dass seine Forschungen auf dem Gebiet des Okkultismus gutartiger Natur sind. Ob es stimmt oder nicht, wir brauchen dennoch seine Hilfe gegen den Dämonenkult in Durotar.","Ihr findet Neeru in Orgrimmar in der Kluft der Schatten."}, {"Bringt Neeru Fireblade in Orgrimmar ein Senghalsband-Muster."}},
[830] = {"Die Befehle des Admirals", {"Ihr öffnet den alten, verwitterten Umschlag und findet darin ein offiziell aussehendes Dokument. Ihr erkennt das Siegel Admiral Proudmoores.","Es sieht wichtig aus. Vielleicht wäre Gar'Thok, Kommandant von Klingenhügel, an diesen Informationen interessiert."}, {"Bringt Gar'Thok in Klingenhügel Admiral Proudmoores Befehle."}},
[831] = {"Die Befehle des Admirals", {"Menschen kann man nicht trauen. Wir haben immer mit wachsamen Augen an ihrer Seite gekämpft, weil wir wussten, dass sie uns eines Tages verraten würden.","Leider war Admiral Proudmoores Tod nicht genug, um sein Vermächtnis des Betrugs aufzuhalten. Der Menschenabschaum hatte vor seinem Ableben alles gut durchgeplant.","Seine Herrschaft würde, wie es scheint, nicht einmal mit Benedict enden. Wer weiß, wann die nächste Welle von Proudmoores Männern an unseren Küsten landen wird.","Wir müssen diese Befehle sofort zu Nazgrel in Orgrimmar bringen! Man findet ihn in Thralls Kammer."}, {"Bringt Nazgrel in Thralls Kammer in Orgrimmar Admiral Proudmoores Befehle."}},
[832] = {"Brennende Schatten", {"<Die Stimme aus dem Anhänger erreicht direkt Euren Verstand.>","Gazz'uz?","Gazz'uz... Bericht!","Man hat von Eurer Entdeckung in der Knochenhöhle gehört. Ihr müsst Euch auf einen Angriff vorbereiten!","Gazz'uz, ich befehle Euch, mir zu antworten! Sagt etwas, sonst werde ich dafür sorgen, dass Neeru Fireblade von Eurer Anwesenheit erfährt, dann wird es Euch schlecht ergehen.","Versucht nicht, Eure Fertigkeiten mit denen Neerus zu messen...","... Gazz'uz, seid Ihr da...?"}, {"Bringt Neeru Fireblade dieses Auge."}},
[833] = {"Eine heilige Bestattung", {"Nur die tapfersten Tauren werden bei den Teufelsfelsen, unserer heiligen Begräbnisstätte, bestattet. Es ist eine Ehre, die jenen großen Kriegern zuteil wird, die dazu beitrugen, Thunder Bluff zu gründen und zu verteidigen, und jenen, die ihr Leben für das Wohl ihres Stammes und ihrer Häuptlinge gegeben haben.","Nun sieht es jedoch so aus, als wäre es einer üblen Bedrohung gelungen, in unser heiliges Land einzudringen. Eine Gruppe Borstennacken-Eindringlinge verheert das Grabmal und ich bin zu alt und gebrechlich, um sie selbst zu vertreiben.","Sie müssen mit Gewalt von dort verjagt werden, <Name>."}, {"Der Hüter des Wissens Raintotem möchte, dass Ihr bei den Teufelsfelsen 8 Eindringlinge der Borstennacken tötet."}},
[834] = {"Wind in der Wüste", {"Ich bin Rezlak, einer von Gazlowes Jungs. Boss hat mich geschickt, den Orcs hier in Durotar unter die Arme zu greifen. Soweit läuft alles klar, abgesehen von den Karawanen. Ich schaff's einfach nicht, sie zu sichern! Das erschwert mir meinen Job ganz schön, wisst Ihr?","Die letzte Lieferung, meine Güte, die hatten garantiert, dass sie durchkommt! Aber sie wurde von den Staubschwingenharpyien von... wo war das noch... in der Klingenschlucht geklaut.","Ich muss dieses Zeug zurückhaben, sonst kriege ich hier nie mehr was zu tun! Folgt dem großen Canyon nach Süden, dort findet Ihr eine Klamm direkt westlich und östlich der Wände."}, {"Holt 5 Säcke Vorräte und bringt sie Rezlak in der Nähe von Orgrimmar."}},
[835] = {"Sicherung der Linien", {"Gazlowe sagte mal zu mir: \"Rezlak. Eines musst du im Leben wissen: Wenn du was richtig gemacht haben willst, dann tu's selbst.\"","Ich kann es mir nicht leisten, all unsere Karawanen zu verlieren, also muss ich die Sache selbst in die Hand nehmen. Die Staubwindklamm liegt hinter dem Ostteil der Klingenschlucht. Wenn wir alle Harpyien dort töten, ist die Route wieder sicher.","Natürlich gab's da noch was, das Gazlowe mir beigebracht hat, und das ist: \"Wenn die Kohle stimmt, kriegt man alles.\" Also, werdet Ihr mir helfen, <Name>?"}, {"Tötet für Rezlak in der Nähe der Staubwindklamm 12 Wilde der Staubschwingen und 8 Sturmhexen der Staubschwingen."}},
[836] = {"Rettet OOX-09/HL!", {"Aus dem Roboter ertönt erneut die blecherne Stimme des Gnoms:","\"Ich muss OOX-09/HL an einen offenen, sicheren Ort schaffen, wo er die langwierige Startprozedur beginnen kann. Er hat eine eingebaute Tarnvorrichtung, aber ich brauche etwas Zeit, um sie von hier aus wieder funktionstüchtig zu machen. Eskortiert den Roboter von seinem derzeitigen Standort... hmmm, ja, zur Küste jenseits der Aussichtsklippen! Das sollte der perfekte Ort für mein Vorhaben sein, und bis Ihr dort seid, sollte ich auch alles vorbereitet haben und online gehen können.\"","\"Eskortiert ihn sicher bis zur Küste und kommt dann zu mir nach Booty Bay! Oglethorpe Obnoticus - Ende!\""}, {"Eskortiert OOX-09/HL zur Küste jenseits der Aussichtsklippen und meldet Euch dann bei Oglethorpe Obnoticus in Booty Bay."}},
[837] = {"Übergriff", {"Als wir hier ankamen, war ein Großteil des Landes im Besitz der Grimmhauerstacheleber, die sich als ein ziemlicher Dorn im Auge erwiesen. Durch unsere Anstrengungen konnten wir einen großen Teil von ihnen vertreiben, aber es gibt doch noch welche, die sich in einigen Gebieten verschanzt haben.","Das geht jetzt jedoch schon viel zu lange so. Zu unserer eigenen Sicherheit dürfen wir nicht zulassen, dass sich die Grimmhauer in unserem Land festsetzen. Ihre Lager befinden sich westlich von hier. Sucht nach dem Dornengestrüpp, dann findet Ihr sie. Heute vertreiben wir sie aus Durotar, morgen vielleicht schon aus Kalimdor."}, {"Tötet für Gar'Thok in Klingenhügel 4 Stacheleber der Grimmhauer, 4 Späher der Grimmhauer, 4 Staubläufer der Grimmhauer und 4 Schlachtwachen der Grimmhauer."}},
[838] = {"Scholomance", {"Auf der Insel Caer Darrow liegt die Scholomance - eine Schule, die als Festung für die Geißel dient. Unser Feind haust dort, wo er sicher Forschungen betreiben kann, die seine Macht stärken. Eine Tür versperrt den Weg, und ganz gleich was wir versuchen, der Zutritt ist nur mit dem richtigen Schlüssel möglich.","Apotheker Dithers weiß viel über die Scholomance und hat eine Idee, wie ein Schlüssel zu erlangen ist. Ihr habt mir Euren Wert als jemand bewiesen, der knifflige Dinge erledigen kann; ich vertraue Euch nun diese Aufgabe an. Sprecht mit ihm über diese Angelegenheit."}, {"Sprecht mit Apotheker Dithers am Bollwerk, Westliche Pestländer."}},
[840] = {"Freiwilliger der Horde", {"Hmmm, Ihr seht mir recht stark aus.","Hört mal, mein guter Freund Kargal braucht ein paar neue Rekruten für den Dienst im Brachland. Ich weiß, dass Ihr immer nur das Richtige für die Horde tun wollt. Das ist jetzt Eure Chance.","Bringt Kargal diesen Anwerbungsbrief und seht, ob er ihn unterzeichnet."}, {"Folgt der westlichen Straße aus Klingenhügel ins Brachland und über die Brücke.","Macht bei dem Außenposten der Orcs auf der anderen Seite Halt.","Gebt Kargal Battlescar im Brachlandaußenposten Euren Anwerbungsbrief."}},
[841] = {"Eine weitere Kraftquelle?", {"Ich stelle mal eine Vermutung an - Ihr habt, welch weltbeendende, ruchlose Tat Ihr auch unternommen habt, diese Tat nicht rechtzeitig beendet. Nein, wartet, noch besser... Ihr habt die Sache in den Sand gesetzt! Nun, was auch immer der Grund sein mag, ich verfüge über eine weitere Kraftquelle, die ich Euch verkaufen werde. Dieses Mal wird sie Euch aber zehn, JAWOHL ZEHN Wasserbeutel eines Wüstenläufers kosten!","Die Zeiten hier sind schwer, <Name>, und Kraftquellen wachsen nicht auf Bäumen! Hmm, aber was wäre wohl, wenn sie es täten... Vielleicht könnten wir einen Kraftquellenbaum MACHEN..."}, {"Bringt Chefingenieur Bilgewhizzle in Gadgetzan 10 Wasserbeutel eines Wüstenläufers im Austausch gegen eine weitere Kraftquelle."}},
[842] = {"Crossroads-Wehrpflicht", {"Bringt diesen unterzeichneten Anwerbungsbrief nach Crossroads.","Sergra Darkthorn, verflucht sei sie, hat dort das Kommando. Sie ist eine grässliche Schamanin, die Euch erst schwindelig redet, bevor Sie Euch etwas Nützliches tun lässt. Thrall ist davon überzeugt, dass sie etwas ganz Besonderes ist, aber ich bin mir da nicht so sicher.","Macht Euch selbst ein Bild davon, Jungspund ..."}, {"Folgt der Straße von Kargals fernem Farwatch-Außenposten nach Westen.","Wendet Euch bei der Einmündung nach links und folgt dann der Straße in südliche Richtung.","Sucht Sergra Darkthorn an der Kreuzung zweier Straßen in Crossroads auf."}},
[843] = {"Ganns Rekultivierung", {"In Einsamkeit wandere ich auf diesen Straßen.","Die Zwerge Bael Modans haben keinen Respekt vor meinem Land. Entsetzliche Schläge reißen tiefe Wunden in den Boden und brüllende Maschinen zerfetzen die Hügel. Land, das meinem Stamm einst als Heimat und Versorger diente, ist jetzt von zerstörerischen Zwergen durchfurcht.","Alle Versuche, zu einer diplomatischen Einigung zu kommen, sind fehlgeschlagen. Jetzt ist die Zeit für entschlossenes Handeln gekommen.","Vertreibt die Zwerge mit Gewalt von der Bael Modan-Ausgrabung und bringt mir das Tagebuch ihres Anführers, Ausgrabungsleiter Khazgorm."}, {"Gann Stonespire möchte, dass Ihr 15 Ausgräber von Bael'dun und 5 Großknechte von Bael'dun tötet.","Bringt Gann Stonespire dann Khazgorms Tagebuch."}},
[844] = {"Ebenenschreiter-Bedrohung", {"Eure erste Beute wird einfach sein.","Die Ebenenschreiter im Osten fallen immer wieder über unsere Lebensmittelvorräte her und sind zu einer Plage geworden.","Vernichtet die Ebenenschreiter und bringt mir ihre Schnäbel."}, {"Besorgt 7 Ebenenschreiterschnäbel und bringt sie Sergra Darkthorn in Crossroads."}},
[845] = {"Die Zhevras", {"Die Zhevras sind etwas wilder als die Ebenenschreiter, auch wenn sie nicht die stärksten Wildtiere im Brachland sind. Aber macht Euch keine Sorgen - wir werden Euch bei Gelegenheit gegen größere Beute ausschicken. Vorerst könnt Ihr jedoch noch ein bisschen Übung gebrauchen.","Jagt die Zhevras im Norden und Süden und sammelt ihre Hufe. Bringt sie mir, und vielleicht werden wir Euch als Nächstes eine schwierigere Aufgabe stellen."}, {"Erschlagt Zhevra-Läufer, um für Sergra Darkthorn in Crossroads 4 Zhevra-Hufe zu erhalten."}},
[846] = {"Ganns Rache", {"Es ist klar, dass ein Ausgrabungsleiter von Sprengstoff und diesen lärmenden Fahrzeugen abhängig ist, um den Boden aufzureißen. Wir müssen damit rechnen, dass schon bald ein neuer Großknecht die Sache übernimmt.","Ich möchte den Zwergen ihre eigene Medizin zu schmecken geben. Wenn wir laut diesen Plänen Nitroglyzerin mit Holzbrei und Natriumnitrat mischen, erhalten wir eine Sprengladung. Es ist bekannt, dass die Scharfschützen, Soldaten und Offiziere in der Burg diese Dinge besitzen.","Holt diese Sachen und bringt sie mir; ich werde daraus eine Sprengladung basteln, die die Zwerge so schnell nicht vergessen werden!"}, {"Gann Stonespire möchte, dass Ihr ihm 6 Phiolen Nitroglyzerin, 6 Teile Holzbrei und 6 Proben Natriumnitrat bringt."}},
[847] = {"Arglist des Raptors", {"Diese... diese Macht... Ist das die Macht der Schamanen, die Tor'gan mir zeigen wollte? Ich muss sagen, das ist wirklich interessant. Sagt Tor'gan, dass er mich überzeugt hat... jedenfalls für den Augenblick. Ich werde mir anhören, was er zu sagen hat."}, {"Sprecht mit Tor'gan."}},
[848] = {"Pilzsporen", {"Die Oasen des Brachlandes verbergen ein Mysterium. In ihren Wassern fließt Lebensenergie, die alle Pflanzen und Wildtiere erquickt, die davon trinken.","Erquickt und verändert.","Es gibt einen Pilz, der in der Nähe dieser Oasen wächst. Seine Sporen besitzen Eigenschaften, die wir, die Apotheker Lordaerons, sehr nützlich finden.","Bringt mir diese Sporen, dann seid Ihr unserer Dankbarkeit gewiss."}, {"Bringt dem Apotheker Helbrim in Crossroads 4 Pilzsporen."}},
[849] = {"Ganns Rache", {"Wenn wir uns an Khazgorms Pläne halten, sollte es eigentlich kein Problem sein, eine Sprengladung herzustellen.","Ah, fertig.","Also, <Name>, ich möchte Euch mit einer besonderen Aufgabe betrauen. Nehmt diese Dynamitstange und jagt damit den Flugapparat bei Bael Modan in die Luft.","Dieser Flugapparat ist nicht nur ein wichtiger Bestandteil der Infrastruktur der Grabungsstätte, sondern auch eine Kriegswaffe. Vor allem ist sie aber ein Symbol der Macht und eine Beleidigung des Andenkens an meinen Stamm.","Macht mich stolz, indem Ihr diese heldenhafte Aufgabe erledigt, dann soll es Euer Schaden nicht sein."}, {"Zerstört den Flugapparat bei Beal Modan und kehrt dann zu Gann Stonespire zurück."}},
[850] = {"Anführer der Kolkar", {"Die Zentauren haben die Tauren seit Jahren geplagt. Die Kolkarzentauren des Brachlandes sind seit kurzem zu einer echten Bedrohung geworden. Normalerweise sind sie in diesen Landen eher unorganisiert, aber es haben sich unter ihnen neue Anführer erhoben. Sie sammeln sich.","Wir müssen diese Zentaurenanführer vernichten, um unsere Position hier zu bewahren.","Bringt mir den Kopf von Barak Kodobann. Er lagert in der Nähe der vergessenen Teiche im Norden.","Besiegt ihn und kehrt dann zu mir zurück."}, {"Bringt Regthar Deathgate westlich von Crossroads Baraks Kopf."}},
[851] = {"Verog der Derwisch", {"Der Zentaur Verog der Derwisch durchwandert das Brachland und wird daher schwer auffindbar sein. Er hat jedoch ein Zentaurenkommandozelt bei der brackigen Oase im Südosten. Vielleicht könnt Ihr ihn zu Euch locken.","Reist zur brackigen Oase im Südosten und greift Zentauren in der Nähe des Kommandozeltes an. Es wird gefährlich werden, aber wenn Ihr genug Zentauren tötet, werden sie Alarm schlagen. Und Verog wird kommen.","Bringt mir seinen Kopf, dann lege ich ihn neben den von Barak Kodobann."}, {"Bringt Regthar Deathgate westlich von Crossroads Verogs Kopf."}},
[852] = {"Hezrul Blutmal", {"Hezrul Blutmal ist der Anführer der Kolkarzentauren im Brachland. Er ist wild, brutal und schlau. Könnte man ihn besiegen, würde das die Kolkars spalten und zerschlagen und die Bedrohung durch sie enorm entschärfen.","Also tötet ihn. Und bringt mir genau wie zuvor bei Barak und Verog Hezruls Kopf.","Er führt sein Volk von der blühenden Oase im Süden aus an."}, {"Bringt Regthar Deathgate westlich von Crossroads Hezruls Kopf."}},
[853] = {"Apothekerin Zamah", {"Ich habe die Sporen, die Ihr mir gabt, zu einer Emulsion verarbeitet. Die muss ich jetzt meiner Kollegin schicken, der Apothekerin Zamah. Wenn Ihr sie ihr bringen könntet, würde sie Euch bestimmt etwas aus ihrem Vorrat alchimistischer Waren anbieten.","Sie lebt bei den Teichen der Visionen in einer Höhle unter den Schamanen von Thunder Bluff. Der Pfad zu dieser Höhle ist gut versteckt, aber Ihr könnt ihn am Steilhang der Anhöhe der Geister finden.","Beeilt Euch. Die Emulsion verliert nach einer Weile ihre Wirksamkeit und muss vorher luftdicht verschlossen und weiterverarbeitet sein."}, {"Bringt der Apothekerin Zamah in Thunder Bluff die weiterverarbeiteten Sporen, bevor die Zeit abgelaufen ist."}},
[854] = {"Reise nach Crossroads", {"Ich habe Eure Ankunft vorausgesehen, junger <Klasse>. Aber vielleicht seid Ihr ja gar nicht mehr so jung.","Ich glaube, dass es an der Zeit ist, dass Ihr Euch tiefer in dieses Land vorwagt. Ich fühle, dass Ihr zu Größerem berufen seid.","Crossroads wartet auf Euch, denn die Orcs, mit denen wir eng verbunden sind, brauchen Unterstützung. Folgt dieser Straße weiter nach Osten und dann weiter nach Norden bis nach Crossroads. Sucht Thork in seinen Mauern, er hat den Segen der Erdenmutter."}, {"Sprecht mit Thork in Crossroads im Brachland."}},
[855] = {"Zentaurenarmschienen", {"Die Kolkar sind eine echte Bedrohung für die Besitzansprüche der Horde im Brachland und wir müssen deren Truppen unbedingt dezimieren. Deshalb habe ich eine lohnenswerte Aufgabe für Euch.","Erschlagt ihre Krieger, während Ihr Euch auf dem Territorium der Kolkar befindet, und sammelt Zentaurenarmschienen ein. Kommt zurück, wenn Ihr eine ordentliche Menge Armschienen beisammen habt, dann wird Euch die Horde reich belohnen."}, {"Bringt Regthar Deathgate westlich von Crossroads 15 Zentaurenarmschienen."}},
[857] = {"Die Träne der Monde", {"Ich hatte die Macht! In diesen meinen Händen! Und ich möchte sie wiederhaben! Wiederhaben möchte ich sie!","Diese dreckigen Ausgräber haben sie entdeckt... sie war so wunderschön. Die Träne der Monde war die erste größere Entdeckung, die wir in Bael Modan gemacht haben.","Sie hätte mir gehören sollen! Mir!","General Twinbraid war eifersüchtig auf die Macht, die sie mir verlieh; er sperrte sie weg, in der Burg. Sagte, sie sei verflucht! Dieser Dummkopf!","Andere wollten sie auch haben und sie mussten daran gehindert werden! Dieser Bastard Twinbraid jagte mich weg.","Jetzt möchte ich sie wiederhaben. Wiederhaben möchte ich sie!"}, {"Feegly der Verbannte möchte, dass Ihr ihm die Träne der Monde zurückbringt."}},
[858] = {"Zündung", {"Euch hat nicht etwa Sputtervale geschickt? Ich stecke hier in der Klemme. Ich bin hier reingesprungen, ohne daran zu denken, dass ich einen Schlüssel brauche, um die Antriebswelle des Schredders aufzuschließen. Einer der anderen Schredderbediener fragte mich, ob alles in Ordnung sei, und da bin ich in Panik geraten! Statt ihm zu sagen, dass ich meinen Schlüssel nicht dabei hatte, sagte ich ihm, dass es irgendeine mechanische Störung gäbe.","Wir müssen hier weg, und zwar schnellstens! Geht hinauf zum Kontrollraum oben auf dem Kran, der Vorsteher sollte einen Schlüssel für diesen Schredder haben.","Helft mir hier raus!"}, {"Holt den Zündschlüssel und bringt ihn Wizzlecrank."}},
[860] = {"Sergra Darkthorn", {"Wenn Ihr den Pfad der Jäger beschreiten möchtet, dann führt Euch Eure Reise ins Brachland. Die dortigen Wildtiere sind willensstark und unglaublich wild. Ihr werdet beim Jagen dort viel lernen und Euer Geist wird sich entfalten.","Sprecht mit Sergra Darkthorn. Sie wird im Brachland Eure Führerin sein.","Sergra ist in Crossroads zu finden. Um zu ihr zu gelangen, müsst Ihr von Bloodhoof aus gen Osten ins Brachland reisen und dann an der Weggabelung nach Norden abbiegen. Und seid vorsichtig auf dieser Reise - das Brachland hält für die Unvorbereiteten und Unachtsamen große Gefahren bereit."}, {"Sprecht in Crossroads mit Sergra Darkthorn."}},
[861] = {"Der Weg der Jäger", {"Euch hat das Jagdfieber gepackt, wie ich sehe. Auch ich war einst wanderlustig...","Wollte wandern und jagen. Denn die Jagd ist die größte Ehre für einen Tauren.","Wenn Ihr ernsthaft das Leben eines Jägers führen wollt, dann wird Euch Melor Stonehoof den Weg weisen. Er lebt in Thunder Bluff auf der Anhöhe der Jäger.","Bringt ihm die Klauen der Flachlandstreuner von Mulgore, um ihm Eure Fertigkeit und Eure Entschlossenheit zu beweisen. Sie sind zäh und schlau - angemessene Beute für einen wie Euch, <Klasse>, der so jung wie Ihr auf dem Pfad der Jäger wandeln möchte."}, {"Bringt Melor Stonehoof in Thunder Bluff 4 Flachlandstreunerklauen."}},
[862] = {"Grubenratteneintopf", {"Ein <Klasse> wie Ihr muss hier im Brachland doch ziemlich hungrig sein. Ich bin den ganzen Tag am Verhungern.","Ich sag Euch was, ich bereite Euch einen feinen Eintopf zu. Ich brauche nur ein paar Grubenratten.","Scheint so, als würden diese dreckigen Zwerge in Bael Modan von unser aller Lieblingsspeise überrannt. Also reist gen Süden nach Bael Modan und bringt mir ein paar Grubenratten.","Ihr dürft Euch erst einen erfahrenen Abenteurer nennen, wenn Ihr Euch ein bisschen mit der Rattenjagd beschäftigt habt! Ha!"}, {"Bringt Grub östlich von Crossroads 8 Grubenratten."}},
[863] = {"Die Flucht", {"Ich würde mal sagen, ich kann das nach und nach lernen... Kann ja nicht so schwer sein. Ein paar Knöpfchen hier, ein oder zwei Hebelchen da... Also, seid Ihr bereit zu gehen?"}, {"Schützt Wizzlecrank und den gestohlenen Goblinschredder auf dem Weg zu Sputtervalve in Ratchet."}},
[864] = {"Kehrt zu Apothekerin Zinge zurück", {"\"Wir sind so weit, die Kraftquelle ist entfernt und die Daten sind gesichert. Also dann, sichere Reisen und all das. Ich hoffe, das ruchlose Wissenschaftsexperiment hat sich für Euch ausgezahlt.\"","Der Goblin wendet sich von Euch ab, um sich den Belangen der Gadgetzan Water Company zu widmen. Ihr verstaut das versiegelte Testset sicher für die Reise zurück nach Undercity. Dort wartet Apothekerin Zinge auf Euch und das versiegelte Feldtestset."}, {"Bringt das versiegelte Feldtestset nach Undercity zu Apothekerin Zinge."}},
[865] = {"Raptorhörner", {"Die Raptoren des Brachlandes sind intelligenter als die Raptoren anderer Länder. Und ich glaube, dass ihre ganze Intelligenz in ihren Hörnern sitzt! Wenn dem so ist, könnte ich ihre Hörner zu Pulver zermahlen und benutzen, um \"Klugheitstränke\" zu kreieren. Dafür würde ich ein Vermögen bekommen!","Und Ihr könntet mir helfen; sucht und bringt mir intakte Raptorhörner von Bluthornsensenklauen. Sie durchstreifen den Süden sowie den Norden des Brachlandes an der Grenze zum Wald von Ashenvale."}, {"Sammelt 5 intakte Raptorhörner von Bluthornsensenklauen und bringt sie Mebok Mizzyrix in Ratchet."}},
[866] = {"Wurzelproben", {"Die Kräuter des Brachlandes sind anders als Kräuter anderer Länder. Ihre Eigenschaften sind zwar fast dieselben, aber das Oasenwasser hier verändert die Pflanzen; sie sind ein klein bisschen anders.","Ich möchte diese Unterschiede studieren... um zu sehen, ob man daraus nicht Kapital schlagen kann!","Falls Ihr in der Kräuterkunde bewandert seid, sammelt doch ein paar Wurzelproben der Kräuter, die Ihr im Brachland findet. Bringt mir diese Proben, damit ich sie mit Kräutern aus anderen Regionen vergleichen kann."}, {"Bringt Mebok Mizzyrix in Ratchet 8 Wurzelproben."}},
[867] = {"Räuberische Harpyien", {"Die verdammten Harpyien haben unsere Karawanen lang genug geplündert; es ist an der Zeit, dass wir einigen von ihnen die Hälse abschneiden und ihre Anzahl verkleinern.","Ich hab mich bei ihren Nestern mal ein bisschen umgeschaut und herausgefunden, wer da wen anführt. Der vernichtendste Angriff wird einen guten Teil ihrer niederen Befehlskette zerstören und dann können wir uns bis zur Spitze vorarbeiten.","Wir fangen langsam an; schaltet die Harpyien der Hexenschwingen und Wildfedern der Hexenschwingen im Nordwesten aus und bringt mir 8 Krallen der Hexenschwingen. Das sollte ein guter Anfang sein..."}, {"Sammelt 8 Krallen der Hexenschwingen.","Bringt sie Darsok Swiftdagger in Crossroads."}},
[868] = {"Eierjagd", {"He, <Name>. Man hat mich nach Crossroads geschickt, damit ich über das Land wache und meinen Meistern in Orgrimmar über eventuelle Vorkommnisse berichte.","Eines meiner Studienobjekte sind die insektenartigen Kreaturen, die man im Süden auf dem Feld der Riesen findet. Wir wissen nur wenig über diese Kreaturen, also haben wir es uns zur Aufgabe gemacht, mehr über sie zu erfahren. Sie scheinen intelligent zu sein, intelligenter als normale Tiere.","Nehmt diese Grabgabel und grabt ein paar ihrer Eier aus den Hügeln aus, aber seid vorsichtig: einmal aufgeschreckt, werden sie Euch angreifen."}, {"Bringt Korran in Crossroads 12 Silithideneier und die Grabgabel."}},
[869] = {"Raptorendiebe", {"Vor kurzem wurde eine Ladung Silber aus unserem Wachturm entwendet. Sie war als Sold für die Crossroads-Wachen gedacht und wir wollen das Silber zurückhaben.","Das Seltsamste daran ist... wir haben einen der Diebe in der Nacht des Raubes erwischt. Und... es war ein Raptor! Unglaublich!","Ich weiß nicht, wofür Raptoren Silber brauchen könnten. Aber das ist mir auch egal - ich will die Raptoren tot sehen, damit sie uns nicht wieder bestehlen!","Jagt Raptoren im Brachland. Sammelt ihre Köpfe für mich!"}, {"Bringt Gazrog in Crossroads 12 Raptorenköpfe."}},
[870] = {"Die vergessenen Teiche", {"Vor langer Zeit war das Brachland ein üppiger Ort voller Leben. Aber Krieg und Katastrophen verheerten das Land, verbrannten es und jetzt ist es nur noch eine ausgedörrte Hülse. So ist es nun einmal und es macht mich traurig.","In den letzten Jahren jedoch sind Oasen im Brachland entstanden und das Leben blüht wieder auf. Und wir Druiden spüren eine Macht von ganz tief unten an die Oberfläche dringen.","Wir müssen ihre Quelle finden. Reist zu den vergessenen Teichen nordwestlich von Crossroads. Sucht in den dortigen Gewässern nach einer Quelle der Macht und kommt dann hierher zurück."}, {"Berichtet Tonga Runetotem von Euren Entdeckungen."}},
[871] = {"Vereitelung der Angriffe", {"Alle Stacheleber sind unsere Feinde, <Name>. Manche sind nur lästiger als andere.","Der Grimmhauerstamm hat unsere Nachschublinien von Durotar aus angegriffen, was uns nicht wenig Ärger bereitet. Ich habe Späher ausgeschickt, um den Anführer dieser Überfälle zu finden, aber bis es so weit ist, könnte es uns schon helfen, wenn Ihr diese dreckigen Schweinemenschen etwas dezimieren würdet.","Beginnt im Nordosten in Richtung Durotar. Ihr könnt ihre Baue immer an den riesigen, dornigen Ranken erkennen, die aus dem Boden wachsen. Sucht sie und erschlagt sie."}, {"Tötet 8 Wassersucher, 8 Dornwirker sowie 3 Jäger des Grimmhauerstamms und kehrt dann zu Thork in Crossroads zurück."}},
[872] = {"Das Ende der Unterbrechungen", {"Einer meiner Späher war Zeuge eines Angriffs auf eine Karawane aus Durotar, <Name>. Die Übeltäter waren in der Tat vom Grimmhauerstamm der Stacheleber. Ein ganz bestimmter Stacheleber führte den Überfall an: Kreenig Grantelschnauze. Man hat ihn nordöstlich von hier gesehen, gleich südlich der Straße von Durotar. Wenn diese Bedrohung jemals ein Ende finden soll, dann muss Kreenig sterben.","Damit diese Angriffe auch wirklich aufhören, würde ich sagen, dass wir das Ganze auf die Spitze treiben sollten. Tötet Kreenig und andere seines Stammes und kehrt dann zu mir zurück, wenn Ihr seine Hauer habt."}, {"Tötet 8 Geomanten der Grimmhauer, 8 Verteidiger der Grimmhauer und Kreenig Grantelschnauze.","Bringt dann Thork in Crossroads Kreenig Grantelschnauzes Hauer."}},
[873] = {"Isha Awak", {"Isha Awak ist der Herr über diese Gewässer. Groß ist seine Kraft und edel sein Stolz. Die Menschen an der Küste fürchten ihn, denn er hat großen Schaden unter ihnen angerichtet.","Ich jedoch fürchte ihn nicht. Ich bin froh, dass er hier ist. Er ist eine würdige Herausforderung und eine ehrenhafte Beute.","Wenn Ihr bereit seid, schwimmt hinaus und sucht nach Isha Awak, der Tiefsten Verdammnis.","Sein Geist wohnt in seinem Herzen, und dieses schlagen zu hören heißt, Euer Schicksal zu kennen."}, {"Bringt Mahren Skyseer das Herz von Isha Awak."}},
[874] = {"Mahren Skyseer", {"Ihr habt die Wildtiere des Brachlandes gejagt. Euer Geist ist stark. Ein Jäger muss jedoch immer vorbereitet sein. Ein wahrer Jäger kann auf allen Pfaden seine Beute finden. Weder Berg noch Sumpf wird ihn wanken lassen.","Nicht einmal das Meer.","Sucht meine Schwester Mahren. Sie jagt die großen Wassertiere entlang der Küste. Sie wird Euch etwas über sie lehren.","Seid jedoch vorsichtig, während Ihr nach ihr sucht, denn die Küste des Brachlandes ist in den Händen der Menschen."}, {"Sprecht mit Mahren Skyseer."}},
[875] = {"Offiziere der Harpyien", {"Hahaha, <Name>. Ihr wollt noch mehr? Gut, das hör ich gern.","Dieses Mal möchte ich, dass Ihr ein paar der Offiziere der Harpyien in den trockenen Hügeln meuchelt. Nur Töter der Hexenschwingen können Offiziere werden und Ihr erkennt sie an den Ringen, die sie tragen. Sie sind ein übler Haufen, aber sie führen ein paar der Unterlinge in diesem Gebiet an. Schaltet Ihr 6 davon aus, wird das ein Entscheidungsschlag gegen ihr Matriarchat sein.","Sorgt dafür, dass sie schmerzvoll sterben, <Name>. Wir möchten, dass diese Dirnen von Harpyien lernen, wie wahnwitzig es ist, sich mit der Horde anzulegen."}, {"Sammelt 6 Harpyienoffiziersringe von den Tötern der Hexenschwingen und kehrt dann zu Darsok Swiftdagger in Crossroads zurück."}},
[876] = {"Serena Blutfeder", {"Ihr habt das mit den Offizieren und Unterlingen so gut erledigt, dass ich Euch noch um einen letzten Dienst bitten möchte.","Serena Blutfeder ist die Schwester einer Harpyie namens Blutfeder, die Rexxar vor einer ganzen Weile erschlug. Offensichtlich wurden die Karawanen der Horde angegriffen, um ihre Schwester zu rächen.","Ich möchte, dass Ihr ihr die Kehle durchschneidet und mir ihren Kopf bringt. Er soll die nächste Karawane schmücken, die wir losschicken... Gebt diesen Harpyien etwas, über das sie nachdenken können."}, {"Erschlagt Serena Blutfeder und bringt Darsok Swiftdagger in Crossroads ihren Kopf."}},
[877] = {"Die brackige Oase", {"Erdrisse wie jene, die Ihr bei den vergessenen Teichen gefunden habt, könnte es auch bei anderen Oasen im Brachland geben. Wenn es so ist, sind diese Risse vielleicht die Lebensquelle dieser Oasen.","Wir müssen das überprüfen.","Hier, nehmt diese Samen. Sie sind tot und unfruchtbar, aber bringt sie dennoch zur brackigen Oase im Südosten. Falls es dort einen Riss gibt, legt die Samen hinein und ... wartet ab."}, {"Kehrt zu Tonga in Crossroads zurück, nachdem Ihr die brackige Oase untersucht habt."}},
[878] = {"Stämme im Krieg", {"Ihr müsst meine Feinde schlagen. <schnaubt> Ihr müsst Quetschzahn und dem Grimmhauerstamm helfen, indem Ihr die Borstennacken schwächt. Beide Stämme sind sehr spirituell - gebunden an die Erde und ihre Kraft. <schnaubt> Sie gibt uns Stärke in allen Dingen... besonders im Krieg.","Wenn ihr herausfinden wollt, wer Thork <schnaubt> und der Horde wirklich solche Angst bereitet, dann helft Quetschzahn! Ihr greift die spirituellen Anführer der Borstennacken im Norden an und schwächt sie, sodass die Grimmhauer sie besiegen und das Brachland beherrschen können."}, {"Erledigt 6 Wassersucher der Borstennacken, 12 Dornenwirker der Borstennacken sowie 12 Geomanten der Borstennacken und kehrt zu Quetschzahn im Camp Taurajo im Brachland zurück."}},
[879] = {"Verräter in den eigenen Reihen", {"Drei meines Stammes kamen aus dem Kral, um die Überfälle gegen die Horde anzuführen, <Volk>. <schnaubt> Sie sind ruchlos und schlau und wenn Ihr sie besiegt, dann werden Crossroads und auch der Großteil des Brachlandes Frieden haben... <schnaubt> zumindest vor dem Grimmhauerstamm. <schnaubt>","Sucht nach Nak, Kuz und Lok Orcbane. Sie befinden sich weit im Süden des Brachlandes. Einer ist ein Zauberer, ein weiterer ein Fährtenleser und ihr Anführer, den sie Orcbane nennen, <schnaubt> ist ein Krieger. Tötet sie, <Volk>, so wie sie mich töteten."}, {"Tötet Nak, Kuz und Lok Orcbane und bringt Quetschzahn im Camp Taurajo im Brachland ihre Schädel."}},
[880] = {"Veränderte Wesen", {"Eure Entdeckungen sind erstaunlich, <Name>. Diese Oasen haben Eigenschaften, die von einer Quelle außerhalb stammen müssen. Oder vielleicht von einer inwärtigen.","Ich muss wissen, auf welche Weise diese Felsspalten die Wildtiere beeinflussen, die vom Wasser der Oasen trinken.","Jagt Oasen-Schnappkiefer in der blühenden Oase und der brackigen Oase. Bringt mir ihre Schalen, damit ich sie untersuchen kann."}, {"Bringt Tonga Runetotem in Crossroads 8 veränderte Schnappkieferschalen."}},
[881] = {"Echeyakee", {"Whitemist, Echeyakee in der Sprache der Tauren, ist der König der Savannenkatzen. Er jagt mit solcher Verstohlenheit, dass man sagt, er sei wie ein dünner, weißer Nebel auf der Erde. Und er tötet so schnell, dass seine Beute weder Angst noch Schmerz verspürt.","Die Tauren sagen, er sei sowohl Gnade als auch Tod.","Ihr werdet erfahren, ob das wahr ist, denn ich schicke Euch aus, um Echeyakee zu jagen. Sein Unterschlupf befindet sich im Nordosten von Crossroads, zwischen den Knochen des riesigen Kodos.","Geht jetzt. Blast dieses Horn, wenn Ihr seinen Unterschlupf erreicht. Blast das Horn und er wird kommen."}, {"Bringt Sergra Darkthorn in Crossroads Echeyakees Balg."}},
[882] = {"Ishamuhale", {"Ishamuhale - Speerzahn - ist der wildeste Bluthornraptor des Brachlandes. Er jagt weder zum Spaß noch für Nahrung. Er jagt, weil Jagen seine Leidenschaft ist. Er tötet, weil Töten seine Natur ist.","Und Ihr werdet Euch mit seiner Natur vertraut machen, <Name>, denn Euer Weg folgt jetzt den Krallenspuren Ishamuhales.","Beginnt mit der Jagd. Erlegt seine Lieblingsbeute, ein Zhevra, und bringt den Kadaver zum toten Baum im Nordwesten von Ratchet. Ishamuhale wird den Kadaver riechen und von ihm angezogen werden.","Seid bereit, wenn er kommt."}, {"Bringt Jorn Skyseer im Camp Taurajo Ishamuhales Fangzahn."}},
[883] = {"Lakota'mani", {"Lakota'manis Huf ist ein Symbol für die Kraft der großen Kodos."}, {"Bringt Jorn Skyseer im Camp Taurajo Lakota'manis Huf."}},
[884] = {"Owatanka", {"Wenn man Owatankas Schwanzstachel berührt, stieben Funken auf."}, {"Bringt Jorn Skyseer im Camp Taurajo Owatankas Schwanzstachel."}},
[885] = {"Washte Pawne", {"Eine Berührung mit der Feder von Washte Pawne ist schmerzhaft."}, {"Bringt Jorn Skyseer im Camp Taurajo Washte Pawnes Feder."}},
[886] = {"Die Oasen des Brachlandes", {"Die Druiden von Thunder Bluff spüren eine große Macht, die östlich von Mulgore ins Brachland sickert.","Tonga Runetotem wurde entsandt, um die Quelle dieser Macht ausfindig zu machen, aber wir befürchten, dass er Hilfe brauchen wird.","Reist ins Brachland und sprecht mit Tonga. Er wird in Crossroads sein. Um dorthin zu gelangen, folgt der Straße von Bloodhoof aus nach Osten. Betretet das Brachland und wendet Euch bei der Weggabelung weiter nach Norden. Geht weiter Richtung Norden, dann gelangt Ihr nach Crossroads.","Und haltet Euch immer auf der Straße, <Name>, denn das Brachland ist ein raues Land."}, {"Sprecht in Crossroads mit Tonga Runetotem."}},
[887] = {"Südmeerfreibeuter", {"Ich wünschte, Vize-Admiral Grezzlik würde bessere Arbeit bei der Sicherung der Meere für unsere Handelsschiffe leisten. Bei all dem Gold, das die Handelsbarone in die Handelsflotten gesteckt haben, macht es mich rasend zu sehen, wie viele Piraten unbehelligt unterwegs sind, meine Schiffe überfallen und meine Waren stehlen!","Mir kam zu Ohren, dass die Südmeerfreibeuter südlich von hier ein Lager aufgeschlagen haben. Sie sind mir ein echter Dorn im Auge, und wenn sich Grezzlik nicht um sie kümmert, könnt Ihr mir vielleicht helfen, sie loszuwerden?"}, {"Tötet für Gazlowe in Ratchet 12 Briganten der Südmeerfreibeuter und 6 Kanoniere der Südmeerfreibeuter."}},
[888] = {"Gestohlene Beute", {"Ich hätte zwar gern, dass Ihr mir all meine Waren zurückbringt, aber das dürfte wohl unmöglich sein. Es gibt jedoch ein paar Dinge, die ich unbedingt wiederhaben muss!","Wie Ihr seht, ist mein Observatorium fast einsatzbereit, aber es fehlt noch die kleinere der zwei Linsen. Die erste Linse ist mit der Karawane aus Durotar gekommen, aber die zweite wurde per Schiff verschickt und ist nie hier eingetroffen. Ebenso ist meine Stiefellieferung von Drizzlik aus Booty Bay nicht hier angekommen!","Begebt Euch zum Piratenlager und seht mal nach, ob Ihr die Dinge dort finden könnt."}, {"Bringt Gazlowe in Ratchet die Stiefellieferung und die Teleskoplinse zurück."}},
[889] = {"Geist des Windes", {"Wenn die Ranken aus der Erde brechen, treten oftmals auch Mineralvorkommen an die Oberfläche.","Ein ganz spezielles Mineral, das in der Nähe der Ranken zu finden ist, heißt Bluterz; der Borstennackenstamm glaubt, das Bluterz sei heilig und ein Teil von Agammagans Körper. Sie tragen manchmal Splitter davon als Glücksbringer bei sich.","Ich glaube, ich könnte aus diesem Erz eine neue Legierung herstellen, wenn ich genug davon hätte. Sucht südlich von hier auf der anderen Seite des ausgetrockneten Flussbetts nach Stammesmitgliedern der Borstennacken und bringt mir so viele Blutsplitter, wie Ihr könnt."}, {"Bringt Tatternack Steelforge in Crossroads 20 Blutsplitter."}},
[890] = {"Die verloren gegangene Lieferung", {"Ich warte schon eine Ewigkeit auf meine letzte Warenlieferung aus Booty Bay! Ich bin mir ziemlich sicher, dass die Seeräuber sie gestohlen haben; würdet Ihr wohl, nur um sicher zu sein, zu den Docks gehen und Dizzywig fragen, ob meine Waren vielleicht ohne mein Wissen bereits ins Lagerhaus gebracht wurden?","Hier, nehmt mein Geschäftsbuch mit zu Dizzywig, damit er meine Inventaraufzeichnungen mit seinen Büchern gegenprüft."}, {"Bringt Werftmeister Dizzywig Gazlowes Geschäftsbuch."}},
[891] = {"Die Kanonen von Northwatch", {"Wieder ist ein Schiff meiner Flotte diesen übereifrigen Menschen der Feste Northwatch in die Hände gefallen!","Man muss sich um diese Bastarde kümmern. Ihr Verfolgungswahn hat sie in eine Art Raserei versetzt. Diese schießwütigen, rücksichtslosen Rüpel schießen auf alles, was sich bewegt.","Reist gen Süden entlang der Händlerküste zur Feste und belagert ihre Streitkräfte. Bringt mir die Medaillen ihrer Soldaten als Beweis. Und erschlagt um meiner Flotte willen Captain Hellstieg und ihre übereifrigen Kanoniere!"}, {"Kapitän Thalo'thas Brightsun aus Ratchet möchte, dass Ihr 10 Medaillen von Theramore sammelt und Captain Hellstieg, Kanonier Knoch und Kanonier Hecksler tötet."}},
[892] = {"Die verloren gegangene Lieferung", {"Laut meinen Aufzeichnungen ist alles in Ordnung, <Name>. Geht zurück und sagt Gazlowe, dass er wohl Pech gehabt hat. Es würde mich nicht überraschen, wenn sich die Piraten seine Waren geschnappt hätten.","Ich kann leider nicht mehr für ihn tun. Oh... Ihr könnt ihm allerdings sagen, dass ich ein paar Dinge aus Undermine für ihn habe, falls er die Zeit findet, sie abzuholen."}, {"Bringt Gazlowe in Ratchet sein Geschäftsbuch zurück."}},
[893] = {"Ausgewählte Waffen", {"Die Stacheleber des Grimmhauerstamms im Süden, hinter dem Feld der Riesen, haben keine erfahrenen Schmiede, soweit ich weiß, aber Sie haben anscheinend begonnen, stabilere Waffen zu entwickeln. Ich hätte gern ein paar verschiedene Arten davon, um etwas über ihre Techniken zu erfahren, so möglich.","Wenn Ihr der Horde Ehre bringen wollt, dann bringt mir Exemplare von ihren Waffen. Besorgt mir einen Dolch von ihren Schleichern, einen Zauberstab von ihren Sehern und einen Schild von ihren Kriegsberserkern. Das sollte reichen!"}, {"Bringt Tatternack Steelforge im Camp Taurajo im Brachland einen Grimmhauerrückenstecher, einen verkohlten Grimmhauerzauberstab und einen Grimmhauerkriegsschild."}},
[894] = {"Samophlang", {"Die Venture Company hat weit nördlich von hier, südwestlich des Schlickermoores, eine kleine Forschungseinrichtung errichtet. Was die Leute dort tun, weiß ich nicht genau, aber ich habe herausgefunden, dass sie dort mit etwas experimentieren, das 'Samophlang' genannt wird.","Was zum Teufel ist ein Samophlang? Na ja, was immer es auch ist, ich möchte es untersuchen, also brauche ich jemanden, der eines beschafft.","Ich habe ein Exemplar der Bedienungsanleitung für ihr Steuersystem in die Finger bekommen; Ihr solltet also herausfinden können, wie man das Samophlang daraus entfernt."}, {"Verschafft Euch Zugang zur Steuerkonsole in der Forschungseinrichtung der Venture Company."}},
[895] = {"GESUCHT: Baron Küstenschipper", {"GESUCHT!","Baron Küstenschipper, Kapitän der GNADENLOS","Captain Küstenschipper aus Gilneas befehligt Schiffe der Südmeerfreibeuter und wird wegen Piraterie gesucht. Seine Überreste müssen in noch erkennbarem Zustand sein!","- Gazlowe"}, {"Bringt Gazlowe in Ratchet Baron Küstenschippers Kopf."}},
[896] = {"Des Minenarbeiters Vermögen", {"Als Werftmeister eines so geschäftigen Hafens wie Ratchet habe ich mein Ohr immer am Puls der Zeit. Ich weiß alles über den Verkehr von Waren und Geld zwischen hier und Booty Bay.","Die jüngste Neuigkeit, die ich erfahren habe, betrifft die Felsadermine der Venture Company nordöstlich des Schlickermoores. Einer der Minenarbeiter hat dort einen faustgroßen Smaragd gefunden. Ich kenne ein paar Käufer, die sich die Finger nach so etwas ablecken würden... und ich würde bei einem Verkauf Halbe-Halbe machen."}, {"Beschafft Euch von einem der Vorarbeiter oder Vollstrecker der Venture Company den Katzenaugensmaragd und bringt ihn zu Dizzywig in Ratchet."}},
[897] = {"Der Ernter", {"Diese seltsame, insektenartige Kreatur scheint außerhalb der natürlichen Ordnung zu stehen, so als würde sie nicht ins Brachland gehören."}, {"Bringt den Ernterkopf zu Jorn Skyseer."}},
[898] = {"Aus der Gefangenschaft befreit", {"Endlich! Jemand rettet mich!","Ich kann es nicht glauben, dass Ihr an den Wachen vorbeigekommen seid. Diese Fanatiker aus Theramore müssen verrückt sein. Sie haben unser Schiff versenkt und mich, den einzigen Überlebenden, eingesperrt. Sie nannten mich ihren Feind und eine Gefahr für die Allianz.","Ich! Eine Gefahr für diesen Riesenschwindel, den sie Allianz nennen? Ich war Decksmann auf einem Fuseltransporter zwischen Ratchet und Booty Bay.","Genug geredet. Helft Ihr mir, zurückzukommen nach Ratchet, ja? Lasst es mich wissen, wenn Ihr bereit seid, hier auszubrechen."}, {"Begleitet Gilthares Firebough sicher zurück zu Kapitän Brightsun in Ratchet."}},
[899] = {"Von Hass zerfressen", {"Vielleicht kennt Ihr diesen Schmerz der Ungewissheit, <Klasse>, vielleicht auch nicht. Doch wisset: Ich stehe hier jeden Tag, vom Aufgang der Sonne bis zu ihrem Untergang, und suche den Horizont nach diesen Monstern ab. Ich habe jeden Schweinemenschen getötet, den ich seitdem gesehen habe, doch der Durst nach ihrem Blut ist noch lange nicht gestillt. Vielleicht hätte ich mich nach Taurajo begeben sollen, statt nördlich nach Crossroads.","Ihr, <Klasse>, könntet mir helfen.","Tötet sie. Tötet so viele Ihr könnt und bringt mir ihre Hauer, dann werden wir ihren Tod miteinander feiern."}, {"Bringt Mankrik in Crossroads 60 Stacheleberhauer der Borstennacken."}},
[900] = {"Samophlang", {"Die drei Steuerventile sind momentan geöffnet. Die verschiedenen Anzeigen der Steuerkonsole führen Euch zu der Annahme, dass man sie zunächst schließen muss, bevor der Apparat ausgeschaltet werden kann."}, {"Schließt das Treibstoffzufuhrventil, das Regelventil und das Hauptsteuerventil, und benutzt dann noch einmal die Steuerkonsole."}},
[901] = {"Samophlang", {"Da der Apparat jetzt ausgeschaltet ist, kann die Steuerkonsole geöffnet und das Samophlang entfernt werden. Alles andere sollte unangetastet bleiben und Ihr benötigt jetzt nur noch den Schlüssel, mit dem die Konsole geöffnet werden kann."}, {"Besorgt Euch bei Tüftler Sniggles den Konsolenschlüssel, um damit die Steuerkonsole zu öffnen."}},
[902] = {"Samophlang", {"Nach dem Öffnen der Front der Steuerkonsole zeigt sich ein dichtes Gewirr aus Kabeln, Schläuchen und anderen seltsamen, mechanischen Objekten - die meisten davon scheinen keinen praktischen Nutzen zu haben. Ihr schiebt sie beiseite und arbeitet Euch tiefer in die Konsole vor, bis Ihr schließlich das Samophlang gefunden habt. Mit einem Ruck zieht Ihr es aus der Halterung."}, {"Bringt das Samophlang zu Sputtervalve in Ratchet."}},
[903] = {"Streuner des Brachlandes", {"Es ist nun an der Zeit, etwas zu jagen, das sich wirklich wehrt. Die Streuner des Brachlandes sind von zäher Art. Zäh, erfinderisch, listig... und tödlich. Ihr findet sie zwischen den hohen Gräsern. Ihr werdet viele südwestlich von Crossroads finden.","Seid vorsichtig, wenn Ihr sie jagt, <Name>, oder Ihr werdet feststellen, dass Ihr nicht mehr Jäger, sondern Gejagter seid."}, {"Besorgt für Sergra Darkthorn in Crossroads 7 Streunerklauen von den Savannenstreunern."}},
[905] = {"Die wütenden Sensenklauen", {"Tja, <Name>, die Jagd wird schwieriger. Jetzt müsst Ihr Eure Beute besiegen und dann Euren Weg zu ihrem Unterschlupf finden.","Jagt die Bluthornraptoren im Süden. Erledigt sie und nehmt Euch die Federn, die sie tragen. Legt die Federn in die Sensenklauennester südwestlich der brackigen Oase. Zeigt ihren Brüdern, dass Ihr sie nicht fürchtet!"}, {"Tötet Bluthornraptoren und nehmt ihre Federn mit. Benutzt die Federn bei den drei Sensenklauennestern. Kehrt zu Sergra Darkthorn in Crossroads zurück."}},
[906] = {"Verräter in den eigenen Reihen", {"Bringt Loks Kopf zu Thork in Crossroads, <Volk>. <schnaubt> Er sollte ohne Worte wissen, was meinem Stamm zugestoßen ist - ha, nicht dass er denken würde, Quetschzahn hätte bei <schnaubt> solchen Dingen geholfen. Aber ich bin sicher, er wird Euch für das Ausführen einer solch großen Tat belohnen. <schnaubt>","Ich werde Euch auch weiterhin mit Agammagans Macht segnen, solange ich in diesem Käfig verbleibe, <Volk>. Bis dann, lebt wohl."}, {"Bringt Loks Kopf zu Thork in Crossroads im Brachland."}},
[907] = {"Wütende Donnerechsen", {"Nun denn, <Name>, jetzt müsst Ihr den großen Donnerechsen gegenübertreten. Diese Bestien sind so groß, dass der Boden erbebt, wenn sie über die Ebenen stampfen. Seid vorsichtig, wenn Ihr gegen sie kämpft, denn ein einziger falscher Schritt, und schon findet Ihr Euch unter ihren Füßen wieder.","Ihr findet die Donnerechsen im südlichen Brachland. Man kennt sie als Sturmschnauzen, Sturmechsen und Donnerköpfe. Kehrt zu mir zurück, nachdem Ihr sie überwältigt habt."}, {"Bringt das Blut von 3 Donnerechsen ins Camp Taurajo zu Jorn Skyseer."}},
[910] = {"Unten an den Docks", {"Ich hab' Geschichten gehört von riesigen Schiffen, die zwischen Ratschet und Beutebucht hin- und hersegeln. Es wäre so toll, auf dem Ozean zu segeln und fantastische Seeschlachten erleben zu können. Wenn ich erwachsen bin, werde ich vielleicht auch ein mächtiger Seefahrer der Horde!","Könnt Ihr mir bitte die Docks von Ratschet zeigen? Ich verspreche, auch nicht ins Wasser zu fallen oder Ärger zu machen. Ich will nur einmal die großen Schiffe sehen und das salzige Meerwasser riechen!"}, {"Bringt Euren Schützling zu den Docks von Ratschet im Brachland."}},
[911] = {"Das Tor zur Grenze", {"Sie sagen, dass das Eschental das neue Grenzland und der Schutzwall von Mor'shan im Brachland das Tor dorthin wären. Es wäre so toll, ein Pionier zu sein und alles, was man zum Überleben braucht, in der Wildnis zu sammeln! Sie sagen, dass dies der Ort ist, an welchem in Zukunft die Horde und die Allianz ihre Kämpfe austragen werden. Ich möchte unbedingt dahin!","Können wir bitte zum Schutzwall von Mor'shan gehen? Ich würde so gerne mal das Grenzland sehen! Ich möchte sehen, wie die Horde diese stinkenden Nachtelfen von dort vertreibt."}, {"Begleitet Euren Schützling zum Schutzwall von Mor'shan im nördlichen Teil des Brachlands, kurz bevor man das Eschental betritt."}},
[913] = {"Der Ruf des Donnerfalken", {"Der Donnerfalke, <Name>, ist eine ungestümes Wildtier. Es ist an der Zeit für Euch, ihm gegenüberzutreten. Ihr müsst herausfinden, wo er umherstreift, und mir seine Flügel als Beweis für Eure erfolgreiche Jagd bringen.","Wenn Ihr das tut, wird Eure Zeit mit mir sich ihrem Ende nähern."}, {"Sucht und erschlagt einen Donnerfalken und bringt Jorn Skyseer in Taurajo seine Flügel."}},
[914] = {"Anführer der Giftzähne", {"Die Druiden in den Höhlen des Wehklagens, die Druiden des Giftzahns, sind eine Verirrung der Natur. Einst gehörten sie einem Orden edler Druiden an, deren Plan es war, das Brachland zu heilen, doch jetzt trachten sie danach, das Land ihren eigenen verderbten Träumen gleichzumachen.","Die Druiden des Giftzahns haben vier Anführer, und jeder besitzt einen Traumedelstein. Selbst jetzt noch verfolgen mich ihre Gesichter! Besiegt die Anführer und bringt mir ihre Edelsteine, dann wird das Brachland endlich wieder Frieden haben.","Geht, <Name>. Sie halten sich tief in den Höhlen des Wehklagens verborgen."}, {"Bringt die Edelsteine von Kobrahn, Anacondra, Pythas und Serpentis nach Thunder Bluff zu Nara Wildmane."}},
[915] = {"Lecker Eis", {"EISCREME! Oh, könnt Ihr mir bitte, bitte, bitte, etwas Eiscreme besorgen? Am liebsten mag ich Erdbeere und es gibt einfach kein besseres Erdbeereis als das von Tigule und Foror. Das ist mein liebstes Eis auf der ganzen Welt!","Früher gab es das immer auf der Rennstrecke in der Schimmernden Ebene, aber ich habe mal gehört, dass man es jetzt auch in der Stadt kaufen kann. Kriege ich bitte eins? Bitte? Am liebsten Tigules und Forors Eiscreme."}, {"Besorgt für Euren Schützling etwas Erdbeereis. Der Kleine scheint die Marke 'Tigules und Forors Eiscreme' zu bevorzugen."}},
[916] = {"Waldwebergift", {"Ich kam nach Shadowglen, um hier die Waldweberspinnen zu beobachten, die in der Schattenweberhöhle leben. Sie sind mit einer wesentlich kleineren Spinnenart verwandt. Ich glaube, der Weltbaum hat einen nicht unerheblichen Effekt auf sie und ich hätte gern ein paar Exemplare von ihnen, um dies zu belegen.","Als Erstes hätte ich gern etwas von ihrem Gift. Sammelt Giftbeutel der Waldweberspinnen, die in und um die Schattenweberhöhle herum zu finden sind. Dann kann ich ihren und das Gift ihrer kleineren Verwandten auf Gemeinsamkeiten hin untersuchen."}, {"Bringt Gilshalan Windwalker in Aldrassil 10 Waldwebergiftbeutel."}},
[917] = {"Waldweberei", {"Jetzt, da ich das Spinnengift habe, hätte ich gern auch ein paar lebende Exemplare, um sie zu studieren. Leider kann ich Euch nicht darum bitten, eine lebende Riesenspinne für mich einzufangen, <Klasse>. Eine Riesenspinne kann ich allein sowieso nicht bewältigen!","Wenn Ihr mir allerdings ein unausgebrütetes Ei besorgen könntet, wäre es viel einfacher, lebende Exemplare zu bekommen, und ich könnte dafür sorgen, dass die noch nicht ausgeschlüpften Exemplare ordentlich unter Verschluss gehalten werden.","Tief in der Schattenweberhöhle gibt es angeblich ein Nest. Bitte, beschafft mir ein Ei aus diesem Nest und bringt es mir."}, {"Bringt Gilshalan in Aldrassil ein Waldweberei."}},
[918] = {"Bäumlingssamenkörner", {"Die Bäumlinge von Teldrassil sind Naturelementare. In ihnen spiegelt sich in gewisser Weise die natürliche Ordnung von Flora und Fauna unseres großen Baums wider.","Daher ist es höchst beunruhigend zu sehen, dass die Bäumlinge so wütend geworden sind.","Ich glaube, es hat etwas mit dem Erdboden zu tun. Ich habe mich viel mit verschiedenen Düngemethoden für Pflanzen beschäftigt und würde sie gern an Bäumlingssamenkörnern ausprobieren. Würdet Ihr bitte in der Gegend des Al'Amethsees ein paar Bäumlingssamenkörner sammeln und mir bringen?"}, {"Bringt Denalan am Al'Amethsee 8 Bäumlingssamenkörner."}},
[919] = {"Bäumlingssprossen", {"Kleine Bäumlinge sprießen rund um das Wasser des Al'Amethsees. Ich fürchte, dass man diesen Schösslingen nicht mehr helfen kann - wir sollten versuchen, sie auszureißen, bevor sie groß genug sind, um Probleme zu bereiten.","Wenn Ihr auf Euren Wanderungen um den See Bäumlingssprossen seht, reißt sie bitte aus. Helft dabei, unser Land rein zu halten!"}, {"Bringt Denalan am Al'Amethsee 12 Bäumlingssprossen."}},
[920] = {"Tenarons Einladung", {"Ihr seid <Name>, nicht wahr? Ich hörte, dass Tenaron nach Euch gefragt hat. Ihr findet ihn in dem am höchsten gelegenen Raum in Aldrassil.","Wir mögen zwar ein langes Leben haben, aber Ihr solltet ihn dennoch nicht allzu lang warten lassen."}, {"Sprecht in Shadowglen über den Dächern von Aldrassil mit Tenaron Stormgrip."}},
[921] = {"Krone der Erde", {"Es ist an der Zeit herauszufinden, was Euer Schicksal ist, <Name>. Doch bevor Ihr bereit seid, in die Welt hinter unseren verzauberten Wäldern hinauszuziehen, gibt es noch vieles, das Ihr über unsere jüngste Vergangenheit lernen müsst.","Seit der Schlacht am Berg Hyjal hat sich für unser Volk so viel verändert. Nordrassil ist nur noch ein Schatten ihres früheren Selbst, ihre Macht wurde benutzt, um Archimonde in die Knie zu zwingen und die Brennende Legion zurückzutreiben ...","Ihr müsst eine Aufgabe erledigen. Geht zum Mondbrunnen nördlich von Aldrassil und bringt mir eine Phiole mit Wasser daraus."}, {"Füllt die Kristallphiole und bringt sie Tenaron Stormgrip über den Dächern von Aldrassil."}},
[922] = {"Rellian Greenspyre", {"<Name>, würdet Ihr wohl eines der Samenkörner, die Ihr mir besorgt habt, meinem Freund Rellian Greenspyre bringen? Er ist Druide in Darnassus, und bei unserem letzten Gespräch machte er deutlich, dass er an meiner Arbeit mit den Bäumlingen interessiert sei. Er hatte selbst ein paar Ideen und würde sich über ein Exemplar dieser Samenkörner sicherlich freuen.","Danke, <Name>. Ihr wart mir eine große Hilfe. Ich hoffe, dass ich Euch eines Tages die Früchte meiner Arbeit zeigen kann.","Ihr findet Rellian normalerweise die Wege der Enklave des Cenarius in Darnassus entlangwandernd."}, {"Bringt Rellian Greenspyre in Darnassus ein Bäumlingssamenkorn."}},
[923] = {"Tumore", {"Bosheit wuchert in den Bäumlingen. Wir versuchen, die Ursache dafür zu finden, aber bis dahin... Aus Sorge um Teldrassils Sicherheit müssen wir alle Bäumlinge fällen, denen nicht mehr geholfen werden kann. Diejenigen, die die Gegend um den Wellspringsee im Norden von Teldrassil durchstreifen, sind am schlimmsten betroffen. Sie müssen ausgemerzt werden!","Vernichtet alle Bäumlinge, die Ihr dort findet, und sammelt die moosartigen Tumore, die auf ihnen wachsen. Bringt mir die Tumore, damit ich sie verbrennen kann."}, {"Bringt Rellian Greenspyre in Darnassus 5 moosartige Tumore."}},
[924] = {"Die Dämonensaat", {"Die Dämonensaat ist ein mächtiges Werkzeug der Brennenden Legion. Sie lebt in einer Höhle des Glutnebelgipfels, oberhalb eines dämonischen Altars des Feuers.","Ich habe Kraftsteine vorbereitet, die, wenn sie in der Nähe der Saat platziert werden, deren Verbindung zur Legion unterbrechen.","Nehmt einen Stein vom Tisch und bringt ihn zum Altar des Feuers oben auf dem Glutnebelgipfel. Reist nach Westen und bei der Weggabelung weiter nach Norden. Der Gipfel liegt zu Eurer Linken. Die sicherste Aufstiegsmöglichkeit zur Spitze bietet sich an der Nordseite.","Und beeilt Euch - die Energie des Steins nimmt mit der Zeit ab."}, {"Nehmt einen fehlerhaften Kraftstein. Bringt ihn zum Altar des Feuers, bevor dessen Wirkung erlischt, und kehrt dann zu Ak'Zeloth zurück."}},
[925] = {"Cairnes Hufabdruck", {"Ich habe gehört, dass Cairne Bluthuf einer der größten Helden ist, den die Horde je gesehen hat. Wenn ich mal groß bin, möchte ich auch ein Held der Horde werden!","Kennt Ihr Cairne, <Name>? Tut Ihr mir einen Gefallen? Kriege ich ein Autogramm von ihm? Ich weiß gar nicht, wie ich ihn ansprechen soll, und wahrscheinlich ist er sowieso viel zu beschäftigt, um mich zu beachten...aber was ist mit Euch? Ihr seid doch genau wie er ein Abenteurer! Euch gibt er bestimmt ein Autogramm!","Bitte, <Name>. Bitte..."}, {"Reist nach Donnerfels und versucht für Euren Schützling einen Hufabdruck von Cairne Bluthuf zu ergattern."}},
[926] = {"Fehlerhafter Kraftstein", nil, nil},
[927] = {"Das moosumschlungene Herz", {"Das Herz von Schwarzmoos dem Stinker ist mit einem dunklen, öligen Moos bedeckt. Dass es sich wirklich um ein Herz handelt, kann man eigentlich nur daran erkennen, dass es langsam und rhythmisch schlägt... Ein Herzschlag, der sogar jetzt noch anhält.","Vielleicht würde Denalan dieses Herz gern einmal sehen."}, {"Bringt Denalan am Al'Amethsee das moosumschlungene Herz."}},
[928] = {"Krone der Erde", {"Ich könnte Euch zwar noch so viel mehr über die Mondbrunnen und Teldrassil erzählen, doch leider muss ich Euch fortschicken. Corithras Moonrage erwartet Euch. Ich habe das Wasser der Phiole, die Ihr mir brachtet, in dieses Gefäß geschüttet, damit Ihr es ihm bringen könnt.","Fragt nach Corithras. Ihr findet ihn am Mondbrunnen in Dolanaar. Folgt der Straße südlich von Aldrassil, die aus Shadowglen hinausführt und dann weiter auf dem Kopfsteinpflaster in Richtung Westen.","Bleibt unbedingt immer auf der Straße, <Name>. Es gibt neuerdings ziemlich gefährliche Bestien in den Wäldern."}, {"Bringt Corithras Moonrage in Dolanaar das teilweise gefüllte Gefäß."}},
[929] = {"Krone der Erde", {"Lasst mich Euch zunächst ein bisschen mehr von der Aufgabe erzählen, die Euch erwartet. Die Druiden in Darnassus verbrauchen das Wasser der Mondbrunnen Teldrassils, und deshalb muss ihr Mondbrunnen von Zeit zu Zeit wieder aufgefüllt werden. Ihr könnt mit diesen speziell gefertigten Phiolen Wasser aus den Mondbrunnen schöpfen.","Nehmt dieses Gefäß zum Mondbrunnen außerhalb von Starbreeze im Osten mit und füllt es mit Wasser. Kehrt dann zu mir zurück. Wenn Ihr Eure Aufgabe erledigt habt, erzähle ich die Geschichte da weiter, wo Tenaron aufgehört hat ..."}, {"Füllt die Jadephiole und bringt sie Corithras Moonrage in Dolanaar zurück."}},
[930] = {"Die leuchtende Frucht", {"Unter den Wedeln dieser leuchtenden Pflanze befindet sich eine große, runde Frucht.","Denalan würde diese Frucht bestimmt gern studieren, da seid Ihr Euch sicher."}, {"Bringt Denalan am Al'Amethsee die leuchtende Frucht."}},
[931] = {"Der schimmernde Wedel", {"Die Wedel der Pflanze schimmern im Dämmerlicht des Waldes und verleihen ihr eine wabernde, pulsierende Aura.","Ihr glaubt, dass Denalan bestimmt gern ein Exemplar davon hätte."}, {"Bringt Denalan am Al'Amethsee einen der schimmernden Wedel."}},
[932] = {"Krankhafter Hass", {"Ich muss Euch warnen, <Name>, das hier muss absolut unter uns bleiben. Die Satyrn bereiten uns schon genug Ungelegenheiten, aber dieser eine hier scheint sich zu einer echten Bedrohung zu entwickeln.","Lord Melenas ist sein Name. Er lebt in der Nähe des Teufelsfelsens in einer Höhle, in der er ein großes Kontingent Grellkrieger um sich versammelt hat. Sein Herz ist so schwarz wie die Nacht und er führt etwas äußerst Übles im Schilde.","Ihr müsst ihn in der nahe gelegenen Höhle gleich nördlich von hier suchen und mir seinen Kopf bringen."}, {"Tötet Lord Melenas und bringt Tallonkai Swiftroot in Dolanaar seinen Kopf."}},
[933] = {"Krone der Erde", {"Südöstlich des Ortseinganges von Darnassus, an den Ufern der Teiche von Arlithrien, gibt es noch einen Mondbrunnen. Die Schildwachen können aufgrund von Angriffen und der ständig schlimmer werdenden Hinterhältigkeit der Knarzklauenfurbolgs kaum durch dieses Gebiet patrouillieren.","Seid auf der Hut, wenn Ihr Euch dem Brunnen nähert, und haltet Eure Waffen stets griffbereit."}, {"Füllt die Turmalinphiole und bringt sie Corithras Moonrage in Dolanaar."}},
[934] = {"Krone der Erde", {"Mit Teldrassil stand es jedoch nicht zum Besten. Staghelms sorgfältig durchdachte Pläne für den neuen Weltbaum entwickelten sich nicht so, wie er gehofft hatte, denn es gab ein kleines Problem. Ein Problem, das die Ursache für viele weitere Probleme Teldrassils ist.","Wie dem auch sei, dazu komme ich später. Ihr müsst den letzten Mondbrunnen nordwestlich von hier in der Lichtung des Orakels aufsuchen. Unter den Ästen des Orakelbaums befindet sich der wichtigste und mächtigste unserer Brunnen. Beschafft eine Phiole seines Wassers und bringt sie mir."}, {"Füllt die Amethystphiole und bringt sie Corithras Moonrage in Dolanaar."}},
[935] = {"Krone der Erde", {"Ohne den Segen Alexstraszas der Lebensbinderin und Nozdormus des Zeitlosen gab es Makel in Teldrassils Wachstum. Man berichtet von seltsamen Wildtieren, die sich buchstäblich aus dem Nährboden des Baums erhoben haben, und von wahnsinnigen Furbolgs, die durchziehende Reisende angreifen.","Ich kann nur hoffen, dass der Erzdruide schnellstens eine Lösung für das Problem findet. Ich werde alles Wasser der Phiolen, die Ihr mir brachtet, in dieses Gefäß schütten, damit Ihr es nach Darnassus bringen könnt.","Bringt es Fandral Staghelm, Ihr findet ihn im Druidenhain."}, {"Bringt dem Erzdruiden Fandral Staghelm in Darnassus das gefüllte Gefäß."}},
[936] = {"Erzdruide Runetotem helfen", {"Entschuldigung, <Klasse>, könnt Ihr einen Moment Eurer Zeit für mich erübrigen?","Der Zirkel des Cenarius in Thunder Bluff ist sehr an erfahrenen Abenteurern wie Euch interessiert, die bei einem wichtigen Forschungsprojekt helfen können. Ich weiß zwar nichts Genaues, kann Euch aber sagen, dass kein Geringerer als Hamuul Runetotem an der Spitze dieses Projektes steht.","Bitte... wenn Ihr interessiert seid, sprecht direkt mit ihm auf der Anhöhe der Ältesten in Thunder Bluff."}, {"Sprecht in Thunder Bluff mit Erzdruide Hamuul Runetotem."}},
[937] = {"Die verzauberte Lichtung", {"Man hat mich mit einer kleinen Gruppe Schildwachen hierher beordert, um den Orakelbaum vor den Harpyien zu beschützen, die überall im Tal Nester gebaut haben. Wir versuchen, sie langsam aber sicher zurückzudrängen.","Beim Versuch des Orakelbaums, einen Läufer mit einem Bericht nach Darnassus zu entsenden, wurde dieser von einer Gruppe eben jener Harpyien angegriffen und getötet.","Wenn Ihr glaubt, dieser Aufgabe gewachsen zu sein, begebt Euch zu ihren Nestern, erschlagt sie und bringt mir zum Beweis Eurer Taten deren Gürtel."}, {"Beschafft 6 Blutfedergürtel und bringt sie der Schildwache Arynia Cloudsbreak in der Lichtung des Orakels."}},
[938] = {"Nebel", {"Das Fell der Säblerkatze ist von einer auffälligen grauen Farbe, die sich nur wenig vom sie umgebenden Wald abhebt. Wie sie da liegt, erkennt Ihr, dass sie schwer verletzt ist und tiefe Wunden an Rücken und Bauchseite aufweist.","Sie hebt ihren Kopf und starrt Euch an, ihre glänzenden blauen Augen voll wacher Intelligenz. Sie scheint Euch folgen zu wollen."}, {"Begleitet Nebel zur Schildwache Arynia Cloudsbreak beim Mondbrunnen in der Nähe des Orakelbaums."}},
[939] = {"Flöte des Xavaric", {"Ihr untersucht die einfache Holzflöte, die Ihr nach dem Sieg über Xavaric aufgehoben habt.","Das knorrige Holz fühlt sich fest an, aber es scheint, als würden sich die Spiralen und Muster auf der Oberfläche bewegen und herumwirbeln, als Ihr sie in den Händen dreht. Plötzlich überkommt Euch ein Gefühl der Furcht...","Ihr schüttelt den Kopf, sicher, dass Euch Euer Gehirn einen Streich spielt.","Möglicherweise tragen andere Satyrn Hinweise darüber mit sich, was Ihr da gefunden habt. Bringt Euren Fund zu dem Druiden Eridan Bluewind, der im südlichen Teil des Teufelswalds lebt."}, {"Bringt die Flöte von Xavaric und 5 Jadefeuerteufelsbinder zu Eridan Bluewind im südlichen Teil des Teufelswalds."}},
[940] = {"Teldrassil", {"Der Wald flüstert von Euren wagemutigen Heldentaten, <Name>. Sobald die Harpyien aus ihren Nestern vertrieben waren, spürte ich, wie sich große Ruhe im Wald verbreitete, und dass sich die Kreaturen des Waldes wieder sicher fühlten. Ich habe nicht gewagt, noch einen Boten zum Erzdruiden zu schicken, aber ich weiß, dass Ihr dafür sorgen werdet, dass ihn meine Nachricht sicher erreicht.","Bringt ihm dies und wisset, dass der Wald Euch auf all seinen Wegen beschützen wird, sodass Ihr den Wald aus Stein sicher erreicht."}, {"Überbringt dem Erzdruiden Fandral Staghelm in Darnassus den Bericht des Orakelbaums."}},
[941] = {"Das Einpflanzen des Herzens", {"Ich habe das üble Moos von dem Herzen entfernt, aber es bleibt dennoch besudelt...","Versucht, das Herz in meinem Blumentopf einzusetzen. Er ist mit einer nährstoffreichen Krume gefüllt, die das Herz säubern und heilen kann."}, {"Pflanzt das besudelte Herz in Denalans Blumentopf ein."}},
[942] = {"Der geistesabwesende Ausgrabungsleiter", {"Dieses Fossil hat eine auffallende Ähnlichkeit mit einem anderen, das ich einst in Ironforge sah. Ein Archäologe namens Flagongut brachte es zur jährlichen Forscherliga-Konferenz mit. Er dachte, das Fossil besäße eine besondere Macht, die man irgendwie freisetzen könnte.","Das Letzte, was ich von Flagongut hörte war, dass er sich in der Deepwater-Taverne im Hafen von Menethil verschanzt hatte und dort auf eine Eskorte zu Whelgars Ausgrabungsstätte wartete. Sucht ihn auf und zeigt ihm Remtravels Fossil. Vielleicht kann er die Geheimnisse der Vergangenheit lüften.","Das Schiff nach Menethil liegt in Auberdine."}, {"Bringt das rätselhafte Fossil zum Archäologen Flagongut im Hafen von Menethil."}},
[943] = {"Der geistesabwesende Ausgrabungsleiter", {"Ausgrabungsleiter Whelgar ist jetzt im Besitz des Originalfundstücks. Er hält sich in seiner Ausgrabungsstätte im Sumpfland auf. Als ich den Stein von Relu entdeckte, glaubte ich, dass er der Schlüssel sei, mit dem das Rätsel des Fossils gelöst werden könne.","Auf dem Weg zu Whelgar wurde ich dann von Raptoren angegriffen und das Relikt ging verloren. Ich weiß nicht, welche dieser scheckigen Bestien das Relikt verschlungen hat, aber falls es Euch gelingen sollte, es zurückzuholen, könnte ich die Macht dieser Artefakte freisetzen.","Bringt mir den Stein von Relu und das Originalfossil."}, {"Der Archäologe Flagongut im Hafen von Menethil möchte, dass Ihr ihm den Stein von Relu und Flagonguts Fossil bringt."}},
[944] = {"Die Meistergleve", {"Wie seltsam, dass sich Naga hier herumtreiben, hier in Dunkelküste. Aber was noch seltsamer ist... es sind auch welche vom Klan der Twilight-Hammer da. Dieser Kult verehrt die alten, alten Lords der Erde. Lords, die schon vor Unzeiten besiegt wurden.","Die Meistergleve im Süden ist ein Ort, an dem ein solcher Lord einst gefallen ist.","Begebt Euch dorthin und sucht nach weiteren Anhängern der Twilight-Hammer-Kultisten.","Nehmt diese Wahrsagephiole. Erschafft damit eine Wahrsageschale, nachdem Ihr die Meistergleve durchsucht habt. Sprecht dann mit mir mittels der Schale."}, {"Tragt Informationen zusammen und benutzt dann die Wahrsagephiole, um eine Wahrsageschale zu erschaffen. Benutzt die Schale, um mit Onu zu sprechen."}},
[945] = {"Therylunes Flucht", {"Hilfe!","Diese dreckigen Kultisten haben mich umzingelt, während ich durch die Meistergleve spazierte. Was für ein Glück, dass ich so gut im Verstecken bin!","Könnt Ihr mir hier raushelfen? Ich sollte auch meiner Schwester Therysil Bescheid geben, dass es mir gut geht. Sie wohnt in Ashenvale im Süden, beim Schrein von Aessina."}, {"Verhelft Therylune zur Flucht und teilt dann Therysil beim Schrein von Aessina mit, dass ihre Schwester in Sicherheit ist."}},
[947] = {"Höhlenpilze", {"Ich entwickle gerade einen Trank, für den rare Pilze erforderlich sind. Pilze, die nur in einer bestimmten Höhle wachsen. Diese Höhle liegt hinter Cliffspring Falls im Osten, leicht nördlich entlang der Berge.","Ich würde ja selbst dorthin gehen, aber der Hain der Uralten hat mir geraten, mich von diesem Ort fern zu halten. Unsere altehrwürdigen Verbündeten spüren, dass die Höhle ein Versteck für etwas Neues, Böses in Dunkelküste ist.","Bitte, <Name>, sammelt doch ein paar Pilze für mich, und wenn Ihr schon dabei seid, könntet Ihr auch gleich die Höhle erkunden, um die Befürchtungen der Urtume eventuell zu bestätigen."}, {"Bringt Barithras Moonshade in Auberdine 5 Schorfstänglinge und 1 Todeskappe."}},
[948] = {"Onu", {"Onu, ein Urtum der Lehren im Hain der Uralten, hat von Eurer Reise nach Cliffwaterfalls erfahren und möchte Euch sprechen. Der Hain liegt im Süden, im Schatten der Berge.","Die Urtume sind geduldig und weise, <Name>. Aber wenn Onu Euch um Rat bezüglich dessen ersucht, was Ihr in den Falls gesehen habt, fürchte ich, dass es wichtig ist."}, {"Sprecht im Hain der Uralten mit Onu."}},
[949] = {"Das Twilight-Lager", {"Niemand weiß, was der Klan der Twilight-Hammer in der Meistergleve vorhat. Aber es muss etwas Böses sein.","Durchsucht ihr Lager bei der Meistergleve... Sucht nach etwas, das uns ihre Absichten verraten könnte.","Wir müssen unbedingt wissen, was sie im Schilde führen, bevor wir einen Plan schmieden können."}, {"Sucht im Lager des Klans der Twilight-Hammer in der Meistergleve nach einem Hinweis."}},
[950] = {"Rückkehr zu Onu", {"Der Blick in den Twilight-Folianten kann einen wahnsinnig machen. Der Text verschwimmt und tanzt vor Euren Augen, kaum dass Ihr ihn einen Moment betrachtet habt, und die einst so wunderschönen Illustrationen verzerren sich zu grotesken Formen und Mustern.","Ihr könnt gerade noch eine Seite des irrsinnigen Gekritzels herausreißen, bevor Ihr ohnmächtig werdet."}, {"Bringt Onu das irrsinnige Gekritzel, das Ihr gefunden habt."}},
[951] = {"Mathystra-Relikte", {"Das Pergament, das Ihr in der Meistergleve gefunden habt, ist eine Seite aus einem alten Text... geschrieben von Elfen! Es ist übelst verflucht und besudelt von der Verderbnis des Klans der Twilight-Hammer, aber es scheint, als würde der Kult alte Elfenmagie benutzen, um seine alten Lords in unsere Welt zurückzuholen.","Reist zu den Ruinen von Mathystra im Nordosten und sucht nach alten Relikten unter dem Gras und den zerbrochenen Steinen. Indem wir sie studieren, können wir vielleicht herausbekommen, auf welche Weise der Klan der Twilight-Hammer die Magie der Elfen nutzen will."}, {"Bringt 6 Mathystra-Relikte zu Onu in den Hain der Uralten."}},
[952] = {"Der Hain der Uralten", {"Wenn Ihr genug Zeit hattet, um für den Orakelbaum Nachrichten zu übermitteln, kann ich Euch sicherlich dazu bewegen, eine Nachricht in den Hain der Uralten in Dunkelküste zu bringen, südlich von Auberdine.","Ihr werdet höchstwahrscheinlich ein sicheres Transportmittel wie einen Hippogryphen benötigen, aber ich baue darauf, dass Ihr das regeln könnt. Bringt dies Onu, dem Urtum der Lehren. Er wartet auf eine Nachricht von mir, so wie ich auf Neuigkeiten aus der Lichtung des Orakels wartete."}, {"Bringt Fandrals Nachricht zu Onu in den Hain der Uralten in Dunkelküste, südlich von Auberdine."}},
[953] = {"Der Untergang von Ameth'Aran", {"Im Osten findet Ihr die Ruinen von Ameth'Aran. Sie werden heute von den ruhelosen Geistern der Hochgeborenen bewohnt, die einst dort lebten. Damals jedoch war es ein Ort, an dem die Diener Azsharas ihre mächtige Magie ungestört praktizieren konnten.","Man hat mich geschickt, die Ruinen zu erkunden und ich stieß auf zwei große Schrifttafeln, auf denen die Geschichten Ameth'Arans und seines Unterganges beschrieben werden. Während ich die Runen las, traten Geister an mich heran und ich floh.","Bitte, wenn es Euch möglich ist, begebt Euch zu den Ruinen und entziffert an meiner Statt die Schrifttafeln."}, {"Studiert die Schrifttafeln, die von Ameth'Aran und ihrem Untergang erzählen, und kehrt dann zur Schildwache Tysha Moonblade in Dunkelküste zurück."}},
[954] = {"Bashal'Aran", {"Die Ruinen von Bashal'Aran im Osten sind von dämonischen Dienern überrannt worden. Die Grimmlinge und Satyrn haben sich in dem Gebiet niedergelassen und nähren sich von den magischen Energien dort. Ihre Macht wächst, je länger sie sich dort aufhalten.","Dennoch habe ich festgestellt, dass es dort einen Schrein gibt, dem sie nicht zu nahe kommen wollen. An der Westseite der Ruinen auf einem kleinen Steilhang entspringt eine seltsame blaue Aura ... Das muss eine Erklärung für das Widerstreben der Dämonen sein.","Ich hätte gern, dass Ihr das untersucht."}, {"Sucht in den Ruinen von Bashal'Aran nach der Quelle der seltsamen blauen Aura."}},
[955] = {"Bashal'Aran", {"Sollte ich jemals gehalten sein, die Geschichte meines Lebens zu erzählen, würde dies zweifellos die Grenzen Eurer Geduld überstrapazieren. Sagen wir einfach, dass mein Leben lang und schmerzvoll war, und dass diese meine spektrale Gestalt die schlimmste Pein von allem ist.","Ich werde durch Magie hier festgehalten. Mögen meine Worte auch unaufrichtig erscheinen, so versichere ich Euch doch, dass ich über die Maßen dankbar wäre, würdet Ihr mir helfen, den Grund für meine Gefangenschaft herauszufinden. Ein Siegel bindet mich, und vielleicht finde ich einen Hinweis darauf, wenn ich den Ohrschmuck der Grimmlinge und Grells nur näher betrachten könnte."}, {"Beschafft für Asterion in Bashal'Aran 8 Grell-Ohrringe."}},
[956] = {"Bashal'Aran", {"Wenn die Grells erst kürzlich mit dem Siegel in Berührung gekommen sind, das mich an dieses ewige Gefängnis bindet, glaube ich, den Grund dafür zu wissen. Zweifellos ist das Siegel in den Besitz des Satyrs gelangt, der sie anführt.","Ich bin mir ganz sicher, dass es so ist, <Name>. Einer der Satyrn muss es besitzen. Wenn Ihr es an Euch bringen könntet, würde es mich der Überwindung der Gitterstäbe meines Gefängnisses so nahe bringen, dass es mich zu Tränen rühren würde."}, {"Beschafft das alte Mondstein-Siegel und bringt es Asterion in Bashal'Aran."}},
[957] = {"Bashal'Aran", {"Es war die Kunst eines der mächtigsten Hochgeborenen, der das Siegel erschuf, das mein Gefängnis war. In Ameth'Aran, den Ruinen im Süden, die diesen hier aufs Haar gleichen, gibt es sogar heute noch eine uralte Flamme, blau ist ihre Farbe. In dieser Flamme könnte man dieses Siegel zerstören.","Achtet auf die Ruinen, <Name>!"}, {"Zerstört das alte Mondstein-Siegel in der uralten Flamme in Ameth'Aran und kehrt dann zu Asterion in Bashal'Aran zurück."}},
[958] = {"Werkzeuge der Hochgeborenen", {"<Klasse>. Ich möchte Euch um etwas bitten.","Habt Ihr die Ruinen von Ameth'Aran gesehen? Falls nicht, findet Ihr sie auf der Ostseite der Hauptstraße, ein paar Straßen weiter südlich. Einst waren sie die Heimat vieler mächtiger Hochgeborener, doch heute sind sie Zeugnis der Zerstörung, die sie mit ihrem Dilettantismus angerichtet haben.","Die Schildwachen sagten mir, dass die Geister der Hochgeborenen immer noch umgehen und dass sie ihre alten, magischen Utensilien immer noch benutzen. Wir müssen uns diese Relikte aneignen, damit wir sie zerstören können."}, {"Sucht sieben Hochgeborenenrelikte und bringt sie Thundris Windwirker in Auberdine."}},
[959] = {"Ärger auf den Docks", {"Upps!","Wie Ihr seht, ist mir ein kleines Malheur passiert! Die Morgenlieferung aus Booty Bay war umfangreich. Wirklich gewaltig! Noch nie habe ich so viele Kisten gesehen. Tja, ich wollte die Sache etwas beschleunigen, deshalb habe ich begonnen mehr abzuladen, als ich auf einmal bewältigen konnte. Und dann ist alles zusammengebrochen.","Bevor ich wusste, was überhaupt los war, sah ich diese Nervensäge Zausel den Verrückten mit einer Flasche 99-jährigem Portwein wegrennen. Hab ihn bis zu den Höhlen des Wehklagens verfolgt. Da wollte ich auf keinen Fall rein.","Vielleicht seid Ihr ja mutig genug, um nach ihm zu suchen?"}, {"Kranführer Bigglefuzz in Ratchet möchte, dass Ihr Zausel dem Verrückten, der sich in den Höhlen des Wehklagens versteckt, die Flasche mit 99-jährigem Portwein wieder abnehmt."}},
[961] = {"Onu meditiert", nil, nil},
[962] = {"Schlangenflaum", {"Die Königliche Apothekervereinigung, die in der großen Undercity Lordaerons ansässig ist, hat mich geschickt, um eine ganz besondere Aufgabe zu erledigen, <Name>. Vielleicht möchtet Ihr mich und damit auch die dunkle Fürstin bei unseren Bemühungen, die Verlassenen weiterzubringen, unterstützen.","Ich studiere seit kurzem eine rare Pflanzenart namens Schlangenflaum. Ich denke, dass dieses Kraut in größeren Mengen außerordentliches Potenzial besitzt.","Leider findet man Schlangenflaum nur in den dunkelsten Winkeln der Höhlen des Wehklagens, einem gefährlichen Höhlensystem im Brachland."}, {"Die Apothekerin Zamah in Thunder Bluff möchte, dass Ihr zehn Schlangenflaum für sie sammelt."}},
[963] = {"In ewiger Liebe", {"Nach den Schlachten um den Brunnen der Ewigkeit hörte ich, dass Ameth'Aran zerstört wurde und alle Bewohner ums Leben kamen, darunter auch meine geliebte Anaya.","Ich hätte nie gedacht, dass mich noch heute, Tausende von Jahren später, die Erinnerungen an Anaya um den Schlaf bringen würden. Ich wanderte wie in Trance durch die Wälder von Dunkelküste und fand mich schließlich in den Ruinen von Ameth'Aran wieder... wo ich den gequälten Geist meiner Geliebten sah.","Sie muss befreit werden, aber ich kann es nicht über mich bringen, dies zu tun. Ihr Geist muss zerstört werden."}, {"Befreit den Geist von Anaya Dawnrunner und bringt ihren Anhänger zu Cerellean Whiteclaw in Auberdine."}},
[964] = {"Skelettfragmente", {"Der Schlüssel zur Scholomance wird 'Skelettschlüssel' genannt. Er muss aus den Überresten eines Skeletts geschmiedet werden- eigentlich aus mehreren - und er darf nur durch die stärksten Metalle in einer geeigneten Form gehärtet werden. Ein Signet der Macht von einem Wesen, das das Portal zur Scholomance natürlich öffnen kann, gibt dem Schlüssel seine endgültige Funktion.","Aber eines nach dem anderen, <Name>. Wir benötigen Skelettfragmente für das Schmieden des Schlüssels. Skelette innerhalb von Andorhal sollten den gewünschten Effekt bringen, aber die außerhalb der Mauern könnten vielleicht auch ausreichen."}, {"Bringt 15 Skelettfragmente zu Apotheker Dithers am Bollwerk, Westliche Pestländer."}},
[965] = {"Der Turm von Althalaxx", {"Grüße, <Volk>. Ich bin Elissa Starbreeze und es ist meine Aufgabe, Auberdine vor Schaden zu schützen.","Aus diesem Grund habe ich Balthule Shadowstrike ausgeschickt, um die seltsamen Vorkommnisse rund um den Turm von Althalaxx im Nordosten zu beobachten.","Er hätte eigentlich schon längst wieder zurück sein sollen. Daher fürchte ich, dass er im Wald auf eine unvorhersehbare Gefahr gestoßen ist. Ich würde es außerordentlich zu schätzen wissen, wenn Ihr nach ihm suchen und dafür sorgen würdet, dass es ihm gut geht."}, {"Sucht Balthule Shadowstrike in der Nähe des Turms von Althalaxx in Dunkelküste."}},
[966] = {"Der Turm von Althalaxx", {"Eine Gruppe Hexenmeister hat sich in und um den Turm herum niedergelassen. Ich wollte ja schon früher nach Auberdine zurückkehren, um Elissa Bericht zu erstatten, aber ich befürchtete, hier etwas zu verpassen, solange ich fort wäre.","Delgren vermutete, dass sich eine solche Gesellschaft beim Turm versammeln würde, aber er wusste nicht, warum.","Ein paar Pergamentfetzen, die die Hexenmeister bei sich führten, sind mir in die Hände gefallen, aber ich brauche noch mehr, um das Rätsel zu lösen. Ich warne Euch jedoch, geht lieber nicht in den Turm hinein, denn die Hexenmeister dort sind noch viel mächtiger!"}, {"Sammelt 4 abgenutzte Pergamentfetzen für Balthule Shadowstrike in der Nähe des Turms von Althalaxx."}},
[967] = {"Der Turm von Althalaxx", {"Mein Meister, Delgren der Läuterer, ist ein Paladin, der gütigerweise seine Unterstützung bei der Verteidigung unseres Waldes vor den Streitkräften der Dämonen und Untoten angeboten hat. Er hat mich viel über das Heilige Licht und die Kampfkunst gelehrt.","Delgren muss sofort über die Machenschaften des Kults informiert werden.","Ihr findet ihn ganz weit südlich von hier in Maestras Posten in Ashenvale. Beeilt Euch, ich fürchte nämlich, dass die Bedrohung durch den dunklen Strangs stündlich größer wird."}, {"Überbringt Delgren dem Läuterer in Ashenvale Balthules Brief."}},
[968] = {"Die Macht in der Tiefe", {"Obwohl das Buch ziemlich abgenutzt und zerfleddert ist, ist der Titel auf dem Buchdeckel noch gut lesbar:","'Die Macht in der Tiefe'","In diesem Buch findet sich eine Auflistung unaussprechlicher Namen mit viel sagenden Betitelungen, Verehrungsmethoden und bevorzugten Opferritualen ... bei denen sehr oft lebende, humanoide Kreaturen verwendet werden.","Auf der Innenseite des Einbands stehen die Worte: 'Bonegrips Runen und Verhängnisse, Düsteres Viertel, Ironforge. Inhaber: Gerrig Bonegrip.'","Wenn Bonegrip derartige Bücher verkauft, nimmt er dieses hier vielleicht zurück."}, {"Bringt das Buch: 'Die Macht in der Tiefe' zu Gerrig Bonegrip im düsteren Viertel in Ironforge."}},
[969] = {"Das Glück sei mit Euch", {"Ah, ich seh's Euch direkt an: Ihr möchtet, dass ich Euch einen Glücksbringer anfertige. Mau'ari weiß immer Bescheid!","Im Süden, findet Ihr eine Schlucht und Riesen, die komplett aus Eis bestehen. Die Riesen tragen die Splitter manchmal bei sich, aber Ihr könnt sie auch selbst in der Schlucht sammeln. Diese Splitter haben mächtige reflektierende Eigenschaften.","Bringt mir 10 Frosthagelsplitter und mein Glücksbringer wird Euch gehören."}, {"Sammelt 10 Frosthagelsplitter für die Hexendoktorin Mau'ari in Everlook."}},
[970] = {"Der Turm von Althalaxx", {"Balthules Brief verkündet Unheil. Dieser Kult des dunklen Strangs ist mir ein Dorn im Auge, der entfernt werden muss. Ich hatte nordöstlich von hier in Ordil'Aran schon einmal mit dem Abschaum des dunklen Strangs zu tun. Eines seiner Mitglieder besitzt einen Seelenedelstein, der, wie ich glaube, das Geheimnis der Macht des Kults enthält.","Bringt ihn mir, dann kann ich die darin zu findenden Geheimnisse entschlüsseln."}, {"Beschafft einen leuchtenden Seelenedelstein und bringt ihn Delgren dem Läuterer in Maestras Posten."}},
[971] = {"Wissen in der Tiefe", {"Wie Ihr vielleicht wisst, trage ich Lehren zusammen. Alte Lehren. Mächtige Lehren. Lehren, die Portale öffnen, und Lehren, die die Schlafenden erwecken können.","Es kursieren Gerüchte um einen alten Teil einer Lehre, das 'Lorgalis-Manuskript', das sich tief unten in den Blackfathom-Tiefen in Ashenvale befinden soll. Dieser Ort ist die ehemalige Heimat längst verstorbener Elfen. Elfen, die über großes Wissen verfügten, bevor ihre Stadt zerfiel.","Sucht in den Blackfathom-Tiefen nach dem Manuskript. Wenn Ihr das tut, werde ich Euch das nie vergessen. Auch nicht ... nach dem Ende aller Tage."}, {"Bringt das 'Lorgalis-Manuskript' zu Gerrig Bonegrip in Ironforge."}},
[972] = {"Wassersapta", nil, nil},
[973] = {"Der Turm von Althalaxx", {"Ilkrud Magthrull. Ja, ich kenne ihn. Er ist ein mächtiger Orc-Hexenmeister, der im Fire Scar-Schrein lebt, einem äußerst bösen Ort im südwestlichen Ashenvale.","Bislang ist er nur ein geringes Ärgernis und ich hatte noch nichts mit ihm zu tun, aber es sieht so aus, als wäre jetzt seine Zeit gekommen."}, {"Bringt Delgren dem Läuterer in Maestras Posten Ilkrud Magthrulls Folianten."}},
[974] = {"Die Quelle finden", {"Die heißen Quellen hier sind eine Quelle der Geheimnisse für mich. Woher kommt die Hitze?","Wenn ich mir überlege, wie nah diese Teiche beim Feuerfedergrat sind, frage ich mich wirklich, ob der Vulkan etwas damit zu tun hat. Ich habe ein Thermometer entwickelt, um die Temperaturen am Vulkan zu messen, und ich würde gern die heißeste Stelle finden. Es zeigt die Messungen in Grad Kraklenheit an!","Hier, nehmt es und messt die Temperaturen am Grat, wann immer Ihr eine heiße Stelle seht!"}, {"Krakle im Un'Goro Krater möchte, dass Ihr den heißesten Bereich des Feuerfedergrats findet.","Immer, wenn Ihr eine heiße Stelle findet, klickt Ihr das Thermometer mit der rechten Maustaste an, um die Temperatur zu überprüfen. Sucht, bis Ihr die heißeste Stelle gefunden habt."}},
[975] = {"Behälter von Mau'ari", {"Also, bevor ich Euch dies'n Glücksbringer geb, erklär ich Euch besser, wie er zu verwenden is'.","Wenn Ihr den Glücksbringer bei Euch tragt, werdet Ihr feststell'n, dass die Kreaturen, gegen die Ihr kämpft, bestimmte Gegenstände fallen lassen. Wenn Ihr den Glücksbringer nich' dabei habt, werdet Ihr sie nich' find'n. Kapiert?","Ihr erkennt diese speziellen Gegenstände am Namen - sie werden E'ko genannt. Wenn Ihr solche Gegenstände findet, bringt Ihr sie einfach der alten Mau'ari, und sie hilft Euch.","Wartet hier, während ich Euren Glücksbringer anfertige."}, {"Wartet einen Moment und sprecht dann erneut mit Hexendoktorin Mau'ari, um den Glücksbringer zu erhalten."}},
[976] = {"Nachschub für Auberdine", {"Delgren hat mich gebeten, der Schildwache Starbreeze in Auberdine Nachrichten und Nachschub zu bringen, aber es schmeckt mir gar nicht, allein dorthin zu gehen. Im Wald sind gefährliche Feinde zugange und ich möchte nicht riskieren, dass der Nachschub und die Informationen in die falschen Hände geraten.","Wenn Ihr und ein paar andere als Wachen mit mir kämen, könnte ich sofort gehen. Mit Eurem Schutz zweifle ich nicht daran, dass wir diese Dinge sicher nach Auberdine bringen können."}, {"Sprecht mit Delgren dem Läuterer bei Maestras Posten, nachdem Ihr Feero sicher durch Ashenvale geführt habt."}},
[977] = {"Sind wir endlich da, Yeti?", {"Nachdem ich jetzt alles habe, was ich benötige, um die mechanischen beweglichen Teile abzudecken, benötige ich nur noch eine weitere Sache. Na ja, eigentlich zwei. Zwei Hörner!","Die Eisdistelpatriarchen und -matriarchen haben die größten Hörner, also will ich natürlich diese. Diese Yetiart bewohnt eine Höhle im Südosten. Es gibt viele von ihnen; Ihr solltet keine Probleme haben, sie zu finden.","Denkt daran, ich will nur die bestaussehenden - keine beschädigten oder zerbrochenen Hörner, bitte!"}, {"Sammelt 2 makellose Yetihörner für Umi Rumplesnicker in Everlook."}},
[978] = {"Mondbestrahltes Wildekin", {"Wildekinfedern aus dem Hinterland scheinen Spuren magischer Fähigkeiten zu enthalten. Diese Kräfte... sie scheinen von den Kreaturen nicht bewusst eingesetzt zu werden, es scheint eher, als wären sie der Art angeboren; ein Teil ihres innersten Wesens.","Während meiner Studien habe ich gehört, dass eine große Population von Wildekins die schneebedeckten Länder von Winterspring bewohnt. Könnten diese Kreaturen über die gleichen Kräfte verfügen? Helft mir bitte, eine Antwort zu finden, <Name>. Begebt Euch nach Winterspring und sammelt die auf dem Boden gefallenen Federn ein. Kehrt dann zu mir zurück."}, {"Sammelt 10 mondbestrahlte Federn aus Winterspring und kehrt dann zu Erelas Ambersky in Rut'theran zurück."}},
[979] = {"Ranshalla suchen", {"Ja, ja, diese Federn scheinen die gleiche magische Verzauberung zu enthalten, die mir schon zuvor auffiel... Auch wenn ich mir nicht sicher bin, was wir vorfinden werden, solltet Ihr weiterhin nach Antworten suchen, <Name>.","Eine meiner Kolleginnen reiste kürzlich nach Winterspring, um die Wildekin dort in ihrer heimischen Umgebung zu beobachten. Warum sucht Ihr sie nicht auf und seht, was sie herausgefunden hat?"}, {"Sucht nach Ranshalla im östlichen Winterspring."}},
[980] = {"Die neuen Quellen", {"Tja, ich habe von anderen heißen Quellen an einem Ort namens Winterspring gehört, von hier aus gesehen weit im Norden. Das Merkwürdige ist, dass es dort in der Nähe keine Vulkane gibt. Interessant, ich weiß.","Also, wenn Ihr mehr herausfinden möchtet, solltet Ihr Euch nach Winterspring begeben und mit einer Freundin von mir sprechen. Ihr Name ist Donova Snowden und sie hält sich derzeit bei den heißen Quellen dort auf.","Ich danke Euch erneut, dass Ihr mir mit meinem Thermometer geholfen habt! Auf dann!"}, {"Reist nach Winterspring und sprecht mit Donova Snowden."}},
[981] = {"Der Turm von Althalaxx", {"Für all das, was Ihr geleistet habt, habt Ihr eine ordentliche Belohnung verdient. Kehrt zu Delgren zurück und sagt ihm, dass Ihr Athrikus Narassin eigenhändig den Garaus gemacht habt, und dass der Kult des dunklen Strangs zerschlagen ist."}, {"Sprecht mit Delgren dem Läuterer in Maestras Posten."}},
[982] = {"Tiefer Ozean, weites Meer", {"Nahe der Ufer von Dunkelküste im Norden liegen zwei havarierte Schiffe - die SILBERMORGEN und die NEBELSCHLEIER. Vor einiger Zeit kollidierten die beiden Schiffe wegen der verfluchten Murlocs, als sie über das weite Meer nach Auberdine segelten. Jetzt liegen sie auf dem Grund des Ozeans als Trophäen für diese Satanskreaturen.","Beide Kapitäne haben es in dieser Nacht nicht geschafft, ihre Schiffe zu verlassen. Ihre Logbücher und anderen Habseligkeiten befinden sich immer noch dort unten in Schließkassetten. Ich möchte Euch bitten, sie für uns zu beschaffen; es würde den Mannschaftsmitgliedern, die sich noch hier aufhalten, viel bedeuten."}, {"Beschafft die Schließkassette der SILBERMORGEN und die Schließkassette der NEBELSCHLEIER für Gorbold Steelhand in Auberdine. Beide Gegenstände findet Ihr an Bord der Schiffswracks nördlich des Dorfes."}},
[983] = {"Dröhnkiste 827", {"He! Helft mir bei meiner neuesten Erfindung, der Dröhnkiste. Man kann damit mit Leuten sprechen, die weit weg sind!","Vielleicht habt Ihr sie schon mal gesehen, das sind kleine Kästchen mit Hebeln daran. Ihr einziges Problem ist, dass sie ständig gewartet werden müssen.","Jede hat ein anderes Problem, aber ich hab mir etwas ziemlich Schlaues ausgedacht. Ich habe alle in der Nähe von Kreaturen platziert, die über die passenden Teile verfügen, um die jeweilige Maschine zu reparieren. Momentan hat Dröhnkiste 827 den Geist aufgegeben. Sie steht südlich von Auberdine, also gleich in der Nähe. Es werden sechs Kriecherbeine benötigt, um sie zu reparieren. Ich bezahl Euch auch dafür ..."}, {"Sammelt 6 Kriecherbeine und baut sie in Dröhnkiste 827 ein."}},
[984] = {"Wie groß ist die Bedrohung?", {"Einige meiner Brüder konnten in Teldrassil vor einem verderbten Furbolg gerettet werden und ich habe geschworen, weitere Gräueltaten zu verhindern, bevor noch mehr von uns verletzt werden... oder schlimmer.","Ich habe auch in Dunkelküste schon die ersten Anzeichen von Verderbnis entdeckt, jedoch noch nichts gefunden, was auf Ausbreitung hindeutet. Ich denke, es wäre logisch, wenn man die Untersuchungen bei den Furbolgs fortsetzen würde. Würdet Ihr wohl eines ihrer Lager aufsuchen und zu mir zurückkehren, falls Ihr dort irgendwelche Anzeichen von Verderbnis entdeckt?"}, {"Sucht in Dunkelküste nach einem verderbten Furbolglager und kehrt dann zu Terenthis in Auberdine zurück."}},
[985] = {"Wie groß ist die Bedrohung?", {"Ihr habt Euer Talent im Ausspähen unserer Feinde bereits unter Beweis gestellt, <Name>. Aber habt Ihr auch das Zeug dazu, sie zu bekämpfen?","Nicht alle Abenteurer ziehen ein offenes Gefecht der Kunst der Verstohlenheit und des Ausweichens vor.","Wenn Ihr glaubt, der Aufgabe gewachsen zu sein, solltet Ihr Euch das Furbolglager südlich von Auberdine vornehmen. Das stellt momentan die größte Bedrohung für unser Volk dar. Ihr werdet dort ein paar Mitglieder des Schwarzfellstamms finden. Tötet 8 Pfadfinder und 5 Windsprecher und kommt dann zu mir zurück."}, {"Tötet 8 Pfadfinder der Schwarzfelle und 5 Windsprecher und kehrt dann zu Terenthis in Auberdine zurück."}},
[986] = {"Ein verirrter Meister", {"<Name>, Eure Fertigkeiten haben mir bei meinen Bemühungen sehr geholfen. Kann ich es wagen, Euch dennoch erneut zu bitten, Grimmklaue und seinem Meister Volcor zu helfen? Ich könnte Euch dazu einen magischen Umhang anfertigen, mit dem Ihr ungehindert an den Kreaturen Dunkelküstes vorbeigelangt, während Ihr nach ihm sucht.","Um den Umhang anzufertigen, benötige ich fünf Mondpirscherpelze von einer Mondpirschermatriarchin oder einem Mondpirscherpatriarchen, um genügend Material zur Verfügung zu haben. Die Katzen findet Ihr südlich von hier, in der Nähe des Flusses Wildbend, oder noch weiter im Süden, in der Nähe von Ashenvale."}, {"Sucht 5 feine Mondpirscherpelze und bringt sie Terenthis in Auberdine."}},
[990] = {"Marsch nach Ashenvale", {"<Name>, meine Herrin Raene Wolfrunner erwartet Euch in Astranaar in Ashenvale. Mit Eurer Hilfe könnten wir vielleicht wenigstens eines Teils der Verderbnis Herr werden, die sich dort breit gemacht hat.","Geht von hier aus nach Süden und haltet Euch dicht an der Straße. Wenn Ihr Euch an diese Richtungsangaben haltet, erreicht Ihr problemlos Euer Ziel."}, {"Sucht Raene Wolfrunner in Ashenvale auf."}},
[991] = {"Raenes Säuberung", {"<Name>, ein alter Freund von mir unterstützt hier in Ashenvale ebenfalls die Schildwachen, aber er ist noch nicht zurückgekehrt.","Er ging einer Spur nach, die zu einem Gegenstand führte, der seiner Meinung nach die Angriffe der Furbolgs auf unser Volk verlangsamen könnte - eine Rute, die von einem mittlerweile verstorbenen, bösen Hexer geschaffen wurde.","Vor seiner Abreise erwähnte er, dass er nach einem Edelstein für die Rute suchen wolle.","Er meinte, dass dieser Edelstein möglicherweise in dem Schrein im Falathimsee am Fuße der Berge im Westen versteckt sei. Dort wurde der Edelstein jedenfalls aufbewahrt, bevor der Schrein überrannt worden ist.","Bitte, <Name>, findet meinen Freund."}, {"Sucht in Ashenvale nach Teronis."}},
[992] = {"Gadgetzan Wassersondierung", {"Wenn Ihr interessiert seid, hätten wir einen offenen Posten im Rang eines temporären Unterfeldmessers in der Gadgetzan Water Company.","Da die Nomaden unsere Brunnen erobern, müssen wir weitere freie Wasserquellen ausschöpfen! Es gibt Wasser in der Wüste, aber nur für die, die klug genug sind, sie erst zu erforschen.","Nehmt diesen Wünschelrutenapparat und zapft damit eine Wasserprobe vom Teich in der Nähe der Sandsorrow-Wache. Er befindet sich direkt in Sichtweite der Trolle dort. Bringt mir den angezapften Apparat zurück, wenn Ihr fertig seid!"}, {"Benutzt den unangezapften Wünschelrutenapparat in der Nähe des Teichs bei der Sandsorrow-Wache. Bringt den angezapften Wünschelrutenapparat zum Leitenden Feldmesser Fizzledowser nach Gadgetzan zurück, wenn Ihr die Probe entnommen habt."}},
[993] = {"Ein verirrter Meister", {"Der Umhang ist fertig, <Name>. Jetzt ist es an der Zeit, Volcor zu suchen. Ich hoffe nur, dass wir es noch rechtzeitig schaffen.","Die Magie des Umhangs hält nicht lang an, nachdem sie herbeigerufen wurde - etwa fünf Minuten. Es liegt an Euch zu entscheiden, wann Ihr sie benutzt: Ich schlage vor, dass Ihr damit wartet, bis Ihr Grimmklaue gefunden habt.","Falls es wahr ist, was er mir erzählte, findet Ihr seinen Meister in einer Höhle südlich von hier. Grimmklaue wird an der Straße auf Euch warten. Gebt ihm ein Signal, indem Ihr winkt, sobald Ihr ihn gesehen habt, dann wird er Euch den Weg zu seinem Meister weisen."}, {"Sucht Volcor in Dunkelküste und gebt ihm den verzauberten Mondpirscherumhang."}},
[994] = {"Flucht durch Gewalt", {"Gut, <Name>, dann sei es Gewalt. Lasst uns hier verschwinden, sobald Ihr bereit seid. Wir werden uns unseren Weg hinunter zur Straße erkämpfen - Grimmklaue dürfte immer noch dort sein. Sobald wir sie erreicht haben, können wir uns trennen.","Terenthis wird sofort Eure Version der Geschichte hören wollen, also geht nach Auberdine, sobald wir es hier herausgeschafft haben."}, {"Beschützt Volcor, bis Ihr die Straße erreicht habt und sprecht dann mit Terenthis in Auberdine."}},
[995] = {"Flucht durch Verstohlenheit", {"Na gut... wenn Ihr sicher seid, werden wir uns hier hinausschleichen. Lasst uns verschwinden, sobald Ihr bereit seid.","Vergesst nicht, Euch in Auberdine mit Terenthis zu treffen, sobald Euch die Flucht gelungen ist. Ich werde mich auf eigene Faust dort hinbegeben, da ich wahrscheinlich schneller bin als Ihr."}, {"Flieht aus der Furbolghöhle und trefft Euch in Auberdine mit Terenthis."}},
[996] = {"Verderbte Windblüte", nil, nil},
[997] = {"Denalans Erde", {"Seid Ihr nach Süden unterwegs? Zum Al'Amethsee? Wenn dem so ist, möchte ich Euch um einen Gefallen bitten...","Mein Kollege Denalan hat am Ostende des Sees ein Lager aufgeschlagen, in dem er die Pflanzenwelt Teldrassils studiert und Experimente betreibt. Er hat in Darnassus ein Gebinde rarer Erde bestellt, und die Lieferung kam erst kürzlich mit Verspätung hier in Dolanaar an.","Würdet Ihr ihm dieses Gebinde wohl bringen?"}, {"Bringt Denalan am Al'Amethsee das Gebinde rarer Erde."}},
[998] = {"Verderbte Windblüte", nil, nil},
[1000] = {"Die neue Grenze", {"Aufgemerkt, Helden!","Der Zirkel des Cenarius sucht nach einsatzfähigen Individuen, die dazu beitragen möchten, ihn bei der Erforschung der riesigen neuen Grenzgebiete Kalimdors zu unterstützen! Weit im Westen von Tanaris und Gadgetzan liegt eine abschreckende Ödnis voller unvorstellbarer Gefahren, und der Zirkel braucht ehrbare und gewillte Vertreter, die an seiner statt agieren.","Interessenten mögen bitte zwecks einer Audienz beim Zirkel des Cenarius vorstellig werden. Weitere Informationen erhaltet Ihr beim Erzdruiden Hamuul Runetotem auf der Anhöhe der Ältesten in Thunder Bluff."}, {"Sprecht auf der Anhöhe der Ältesten in Thunder Bluff mit dem Erzdruiden Hamuul Runetotem über den Aufruf des Zirkels des Cenarius zur Erforschung der Grenzgebiete von Kalimdor."}},
[1001] = {"Dröhnkiste 411", {"Aus dem Inneren der Maschine ist eine leise Stimme zu hören.","\"Hier spricht Wizbang! Die nächste Dröhnkiste befindet sich nördlich von Auberdine, am Strand.","Also, es ist die Dröhnkiste mit der Nummer 411 und es werden 3 Drescheraugen benötigt, um sie zu reparieren. Sie befinden sich direkt vor der Küste! Die Dunkelküstendrescher... *Hick*... Tut mir Leid...","Übrigens, wie beim letzten Mal wird die Dröhnkiste Eure Belohnung ausspucken, sobald Ihr die Sachen in die Maschine eingebaut habt. *Hick*... Was? Nein, mir geht's gut!\""}, {"Besorgt Euch 3 Drescheraugen von den Dunkelküstendreschern im tiefen Meer in der Nähe der Dröhnkiste 411."}},
[1002] = {"Dröhnkiste 323", {"Die Dröhnkiste gibt statische Geräusche von sich, als Wizbang zu sprechen beginnt.","\"Die nächste Dröhnkiste ist Nummer 323. Schteht nördlich von Auberdine, aber... wo's das Bier? Wa...? Oh, schteht direkt an der Straße, kann man gar nicht verfehlen, gleich neben der Brücke.\"","Wizbang murmelt wirres Zeug vor sich hin, bevor Ihr ein lautes Schluckgeräusch hört.","\"Der da fehlen Mondpirscherfangzähne... sechs davon.\""}, {"Sammelt 6 Mondpirscherfangzähne und baut sie in Dröhnkiste 323 ein."}},
[1003] = {"Dröhnkiste 525", {"Wizbangs zitternde Stimme übertönt die Blubber- und Tropfgeräusche, die aus dem Inneren der Maschine dringen.","\"Näxte is' die Straße weiter runter nach Süden. Is' leicht zu find'n... direkt am Straßenrand... neben ei'm Sssaun. Glaub, ich hab sie hinner'n paar Büscheln verschteckt... Büschen. Hehehe... Jaaa, Dröhnkiste 525 fehlen ergraute Skalps von dies'n grauhaarigen Dischtelbären. Mann, das schpricht sich blöd. Dissstelbär... Hehehe...\"","Ihr hört weitere Schluckgeräusche, dann schaltet sich die Maschine aus. Plötzlich erwacht sie jedoch wieder zum Leben.","\"4!\""}, {"Sammelt 4 ergraute Skalps von ergrauten Distelbären südlich von Auberdine und legt sie in die Dröhnkiste 525."}},
[1004] = {"Die neue Grenze", {"Aufgemerkt, Helden!","Der Zirkel des Cenarius sucht nach einsatzfähigen Individuen, die dazu beitragen möchten, ihn bei der Erforschung der riesigen neuen Grenzgebiete Kalimdors zu unterstützen! Weit im Westen von Tanaris und Gadgetzan liegt eine abschreckende Ödnis voller unvorstellbarer Gefahren, und der Zirkel braucht ehrbare und gewillte Vertreter, die an seiner statt agieren.","Interessenten mögen bitte zwecks einer Audienz beim Zirkel des Cenarius vorstellig werden. Weitere Informationen erhaltet Ihr beim Erzdruiden Hamuul Runetotem auf der Anhöhe der Ältesten in Thunder Bluff."}, {"Sprecht auf der Anhöhe der Ältesten in Thunder Bluff mit dem Erzdruiden Hamuul Runetotem über den Aufruf des Zirkels des Cenarius zur Erforschung der Grenzgebiete von Kalimdor."}},
[1007] = {"Die antike Statuette", {"Ich würde Euch gern eine Aufgabe übertragen, <Name>.","Ich war auf meinem kleinen Schiff und kreuzte über den versunkenen Ruinen von Zoram, als ich plötzlich von Naga angegriffen wurde, die aus dem Wasser stießen und mich mit ihren Klauen beharkten! Ich floh und nahm alle Vorräte mit, die ich tragen konnte, um dieses bescheidene Lager zu errichten.","Als ich jedoch die Küste erreichte und weglief ... verlor ich meinen liebsten Besitz.","Bitte, <Name>, geht zu dem Ort vor der Küste im Norden, an dem ich in den Hinterhalt geriet, und sucht dort nach einer antiken Statuette. Sie ist der Grund, warum ich die Gefahren des Zoramstrands auf mich genommen habe."}, {"Bringt Talen im Lager in der Nähe des Zoramstrands die antike Statuette."}},
[1008] = {"Der Zoramstrand", {"Böses lauert an unserer nordwestlichen Küste, die als Zoramstrand bekannt ist.","Es ist die Ruhestätte der untergegangenen Stadt Zoram, die während der großen Teilung zerstört wurde und im Meer versank. Die Nachtelfen verloren sie vor Unzeiten. Sie war verloren und fast vergessen.","Jetzt allerdings sind die Naga zurückgekehrt, aber warum, wissen wir nicht. Es ist auch unwichtig. Wir müssen diese Scheusale vernichten und zurück in die Tiefe treiben!","Kehrt zu mir zurück, wenn Ihr Eure Mission abgeschlossen habt."}, {"Bringt Shindrell Swiftfire in Astranaar 20 Köpfe von Rächerflossen."}},
[1009] = {"Ruuzel", {"Ich fürchte, die Geheimnisse der antiken Statuette werden für immer verborgen bleiben, wenn Ihr nicht den Schlüssel zu ihrem antiken Schloss findet.","Dieser Schlüssel ist ein Ring, der Ring von Zoram. Er gehörte einst den Herrschern dieser Stadt, bevor sie in den Wellen der großen Teilung unterging.","Über Jahre hinweg war der Ring verloren, aber die Naga haben ihn vor kurzem gefunden.","Ruuzel, ihr Anführer, haust auf einer Insel am Nordrand von Zoram. Bitte, <Name>, holt mir meinen Ring!"}, {"Bringt Talen im Lager in der Nähe des Zoramstrands den Ring von Zoram."}},
[1010] = {"Bathranshaar", {"Es gibt eine Pflanze, die früher einmal in den alten Ruinen von Bathrans Schlupfwinkel im Norden wuchs. Die Pflanze namens Bathranshaar war als Heilmittel für Krankheiten des Geistes bekannt.","Ein Kind im Dorf ist krank geworden und ich glaube, dass es sich nicht nur um eine körperliche Krankheit handelt. Würdet Ihr wohl zu Bathrans Schlupfwinkel reisen und dort für mich nach Bathranshaar suchen? Ich könnte es brauchen, wenn ich das Kind richtig behandeln soll.","Bathrans Schlupfwinkel liegt nördlich von Maestras Posten und gleich südlich der Grenze zu Dunkelküste."}, {"Bringt Orendil Broadleaf in Ashenvale 5 Bathranshaar-Pflanzen."}},
[1011] = {"Verlassenenkrankheit", {"Die Verlassenen, der untote Arm der Horde, haben sich in Ashenvale eingeschlichen und treiben sich in unseren Ruinen und Grabhügeln herum. Wir glauben, dass sie die Krankheit im Land der Nachtelfen verbreiten wollen. Wir müssen das genau wissen!","Die Verlassenen haben südlich von hier, in der Nähe des Dor'danil-Grabhügels, ein Lager, in dem sie Gifte und Krankheiten entwickeln und in Flaschen füllen. Kämpft Euch in das Lager hinein und stehlt eine Krankheitsflasche. Bringt mir eine solche Flasche. Ich werde sie testen und herausfinden, was sie bewirkt."}, {"Bringt Kayneth Stillwind in Forest Song eine Krankheitsflasche."}},
[1012] = {"Irrsinnige Druiden", {"Als ich mich mit den Giften der Verlassenen abplagte, hatte ich eine Vision. Ich sah die Druiden des Dor'danil-Grabhügels vergiftet... ermordet von den Untoten! Sie sind jetzt Geister, die getrennt von ihren Körpern und in den Wahnsinn getrieben ziellos umherirren.","Ihr müsst den Dor'danil-Grabhügel betreten und die früheren großen Anführer vernichten, damit die Geister der Druiden eines Tages wieder Frieden finden: Taneel Darkwood, Uthil Mooncall und Mavoris Cloudsbreak.","Tut dies und kehrt dann zu mir zurück."}, {"Tötet die Druiden Taneel Darkwood, Uthil Mooncall und Mavoris Cloudsbreak und kehrt dann zu Kayneth Stillwind in Forest Song zurück."}},
[1013] = {"Das Buch von Ur", {"<Name>, in der Burg Shadowfang gibt es ein Buch, das Buch von Ur, das ich unbedingt in meiner Sammlung haben möchte. Ur war einst ein großer Magier aus Dalaran bevor die Geißel kam. Seine Studien anderer Welten sind von großem Wert für... bestimmte Kreise der Verlassenen.","Betretet die Burg Shadowfang und sucht das Buch. Bringt es mir, dann kann ich unserer dunklen Fürstin von Euren Diensten berichten..."}, {"Bringt dem Bewahrer Bel'dugur im Apothekarium in Undercity das Buch von Ur."}},
[1014] = {"Arugal muss sterben", {"Arugal lebt immer noch in der Burg Shadowfang. Wir können Silberwald erst dann als strategische Festung für die dunkle Fürstin beanspruchen, wenn Arugal erschlagen ist.","Ich werde mich darum kümmern, seine Magie zu neutralisieren, <Name>. Ich überlasse es jedoch Euch, für Arugals Tod zu sorgen, den er mehr als verdient.","Reist zur Burg Shadowfang und setzt Arugals üblen Zaubern ein für alle Mal ein Ende. Bringt mir den Kopf dieses niederträchtigen Hexers!"}, {"Tötet Arugal und bringt Dalar Dawnweaver in dem Grabmal seinen Kopf."}},
[1015] = {"Die neue Grenze", {"Aufgemerkt, Helden!","Der Zirkel des Cenarius sucht nach einsatzfähigen Individuen, die dazu beitragen möchten, ihn bei der Erforschung der riesigen neuen Grenzgebiete Kalimdors zu unterstützen! Weit im Westen von Tanaris und Gadgetzan liegt eine abschreckende Ödnis voller unvorstellbarer Gefahren, und der Zirkel braucht ehrbare und gewillte Vertreter, die an seiner statt agieren.","Interessenten mögen bitte zwecks einer Audienz beim Zirkel des Cenarius vorstellig werden. Sprecht in Darnassus mit dem Erzdruiden Fandral Staghelm, er hat weitere Informationen!"}, {"Sprecht in der Enklave des Cenarius in Darnassus mit dem Erzdruiden Fandral Staghelm über den Aufruf des Zirkels des Cenarius zur Erforschung der Grenzgebiete von Kalimdor."}},
[1016] = {"Elementararmschienen", {"Nehmt diese Rolle der Bestimmung; sie kann den Schöpfer dieser Elementare bestimmen. Aber Ihr werdet etwas von ihnen benötigen, um es als Reagenz zum Fokussieren der Magie dieser Rolle zu verwenden; ihre Armschienen sollten hervorragend dafür geeignet sein.","Ein paar wenige intakte Armschienen dieser Kreaturen dürften reichen. Die anderen Armschienen werden für unsere Zwecke nutzlos sein, aber sie könnten einem Händler etwas wert sein, wenn Ihr sie verkaufen wollt. Nachdem Ihr die intakten Armschienen habt, versucht die Rolle zu verwenden. Kehrt mit den Ergebnissen zu mir zurück und dann werden wir weitersehen."}, {"Sammelt 5 intakte Elementararmschienen und wendet die Rolle der Bestimmung darauf an. Bringt danach der Schildwache Velene Starstrike in der Silberwindzuflucht die Rolle der Bestimmung."}},
[1017] = {"Magier-Beschwörer", {"Es gibt nur einen untoten Magier im Brachland, der in der Lage wäre, so viele Wasserelementare zu entweihen. Er ist auch der Einzige, der dumm genug wäre, es zu versuchen. Sarilus Foulborne sitzt wahrscheinlich jetzt gerade oben auf dem Glutnebelgipfel und lacht mich aus.","<Name>, Ihr müsst Euch in Feindesland begeben und diesen Übeltäter vernichten. Ich habe genug von seinem Schwachsinn und wir müssen ihm zeigen, was mit jenen geschieht, die die Schildwachen verärgern.","Vernichtet ihn und bringt mir seinen Kopf. Vorsicht allerdings, die Horde könnte nach Euch Ausschau halten."}, {"Erschlagt Sarilus Foulborne und bringt Schildwache Velene Starstrike in der Silberwindzuflucht seinen Kopf."}},
[1018] = {"Die neue Grenze", {"Aufgemerkt, Helden!","Der Zirkel des Cenarius sucht nach einsatzfähigen Individuen, die dazu beitragen möchten, ihn bei der Erforschung der riesigen neuen Grenzgebiete Kalimdors zu unterstützen! Weit im Westen von Tanaris und Gadgetzan liegt eine abschreckende Ödnis voller unvorstellbarer Gefahren, und der Zirkel braucht ehrbare und gewillte Vertreter, die an seiner statt agieren.","Interessenten mögen bitte zwecks einer Audienz beim Zirkel des Cenarius vorstellig werden. Weitere Informationen erhaltet Ihr beim Erzdruiden Hamuul Runetotem auf der Anhöhe der Ältesten in Thunder Bluff."}, {"Sprecht auf der Anhöhe der Ältesten in Thunder Bluff mit dem Erzdruiden Hamuul Runetotem über den Aufruf des Zirkels des Cenarius zur Erforschung der Grenzgebiete von Kalimdor."}},
[1019] = {"Die neue Grenze", {"Aufgemerkt, Helden!","Der Zirkel des Cenarius sucht nach einsatzfähigen Individuen, die dazu beitragen möchten, ihn bei der Erforschung der riesigen neuen Grenzgebiete Kalimdors zu unterstützen! Weit im Westen von Tanaris und Gadgetzan liegt eine abschreckende Ödnis voller unvorstellbarer Gefahren, und der Zirkel braucht ehrbare und gewillte Vertreter, die an seiner statt agieren.","Interessenten mögen bitte zwecks einer Audienz beim Zirkel des Cenarius vorstellig werden. Sprecht in Darnassus mit dem Erzdruiden Fandral Staghelm, er hat weitere Informationen!"}, {"Sprecht in der Enklave des Cenarius in Darnassus mit dem Erzdruiden Fandral Staghelm über den Aufruf des Zirkels des Cenarius zur Erforschung der Grenzgebiete von Kalimdor."}},
[1020] = {"Orendils Heilmittel", {"<Name>, ich habe ein Heilmittel entwickelt, mit dem man dem erkrankten Kind bestimmt helfen kann. Bringt es nach Astranaar und gebt es dem Vater des Kindes, Pelturas Whitemoon.","Um nach Astranaar zu gelangen, folgt der Straße gen Süden und dann gen Osten."}, {"Bringt Pelturas Whitemoon in Astranaar Orendils Heilmittel."}},
[1021] = {"Elende Satyrn! Die Dryaden sind in Gefahr!", {"Ich weiß zwar nicht, wer Ihr seid, aber ich brauche Eure Hilfe. Meine Schwestern wollten den Satyrn den Zweig des Cenarius abnehmen. Sie sagten, dass sie bald zurück sein werden, aber das war vor zwei vollen Tagen!","<Name>, Ihr müsst sie finden! Sie wollten nach den Ruinen eines Nachtelfentempels in Xavian im Nordosten suchen. Ich weiß nicht, was ich davon halten soll. Sucht nach Anilia, sie wollte sie bei der Suche führen."}, {"Sucht in Xavian nach Anilia."}},
[1022] = {"Das heulende Tal", {"Obwohl wir viele Mittel und große Bemühungen investiert haben, um auch die letzten Dämonen aus dem Teufelswald im Norden zu vertreiben, hatten wir nur wenig Erfolg damit. Wir konnten jedoch einen Großteil der Dämonen von Ashenvale fern halten.","Im Norden, nahe der Grenze zum Teufelswald, wurde der verfallene Mel'Thandris-Schrein von rätselhaften Wolfmenschen übernommen. Ihre Schreie, die einem das Blut in den Adern gerinnen lassen, haben dem als \"Das heulende Tal\" bekannten Gebiet seinen Namen gegeben. Der Foliant von Mel'Thandris, der im Schrein aufbewahrt wird, könnte etwas Licht darauf werfen, warum die Wolfmenschen hier sind."}, {"Begebt Euch ins heulende Tal, studiert den Folianten von Mel'Thandris und kehrt dann zur Schildwache Melyria Frostshadow beim Schrein von Aessina zurück."}},
[1023] = {"Raenes Säuberung", {"Während Ihr Teronis Habseligkeiten durchstöbert, stoßt Ihr auf ein Tagebuch, das noch in gutem Zustand ist.","Ihr nehmt das Tagebuch und lasst Teronis' Kadaver so wie er ist zurück.","Jetzt müsst Ihr nur noch eines tun: den Edelstein finden, den Teronis suchte. Raene deutete an, dass er sich in diesem Gebiet befinden würde. Vielleicht haben ihn die Murlocs ..."}, {"Sucht den leuchtenden Edelstein und bringt ihn zusammen mit Teronis' Tagebuch zu Raene Wolfrunner in Astranaar."}},
[1024] = {"Raenes Säuberung", {"Ihr werdet selbst Nachforschungen anstellen müssen, aber gemäß Teronis' Notizen scheint es unsere nächste Aufgabe zu sein, die anderen Teile der Rute zu finden, die Dartol erschuf. Ich würde mich zunächst zum Mondbrunnen östlich des Irissees begeben. Wenn Ihr bereit seid, folgt der Straße und wendet Euch gen Norden, wenn Ihr einen kleinen Sitzbereich zu Eurer Linken erreicht.","Dort findet Ihr eine Dryade namens Shael'dryn, die Euch eine bessere Führerin sein wird. Sucht sie auf.","Behaltet den Edelstein - Shael'dryn wird ihn brauchen, um Euch bei der Wiederherstellung der Rute zu helfen."}, {"Sucht Shael'dryn beim Mondbrunnen im Nordosten auf."}},
[1025] = {"Eine aggressive Verteidigung", {"Bevor Ihr zum Mondbrunnen aufbrecht, habe ich noch eine andere Aufgabe für Euch, wenn Ihr gern Eure Fertigkeiten auf die Probe stellen und dem Volk von Ashenvale helfen würdet.","Entlang der Straße, die nach Osten führt, haben Furbolgs kurz vor dem Falfarren Reisende angegriffen. Ihre gequälten Seelen nähren sich von Angst und sie stillen ihren Appetit mit dem Blut Unschuldiger.","Helft den Schildwachen, das Land zu schützen, indem Ihr diesen Monstern dieselbe Gnade zeigt, die sie uns erwiesen haben.","Ich glaube, ihre Lager liegen südlich der Straße ... seid vorsichtig."}, {"Tötet 1 Höhlenbehüter, 2 Ursa, 10 Totemiker sowie 12 Krieger der Fäulnisklauen und kehrt dann zu Raene Wolfrunner in Astranaar zurück."}},
[1026] = {"Raenes Säuberung", {"Ich wünschte, Ihr hättet bessere Nachrichten.","Raene hat Recht, ich weiß von Dartols Werk und den Treants, den Baumwesen in diesem Gebiet - zumindest wusste ich es einst. Heute sind sie verderbt, ihre Natur verzerrt.","Falls es Euer Ziel ist, das nächste Teil der Rute zu finden, solltet Ihr zunächst den Schlüssel suchen, mit dem die Truhe geöffnet werden kann, in dem es sich befindet.","Einer der Treants nordöstlich von hier in der Nähe des Teufelswalds hat ihn. Sobald Ihr ihn habt, sucht nach einem kleinen Tal westlich der Straße nahe der Grenze zum Teufelswald... die Truhe ist dort irgendwo versteckt."}, {"Sucht den hölzernen Schlüssel und das Teil von Dartols Rute, bevor Ihr zu Shael'dryn beim Mondbrunnen zurückkehrt."}},
[1027] = {"Raenes Säuberung", {"Das letzte Teil der Rute wurde in die Obhut der Druiden von Dor'danil gegeben. Ich habe keine Ahnung, wo sie es versteckt haben. Es tut mir Leid, dass ich Euch da nicht weiterhelfen kann.","Ich kann Euch nur sagen, folgt der Straße, die zum Brachland führt. Die Druiden suchten östlich dieser Straße Zuflucht, aber sie werden Euch nicht viel helfen können: Sie wurden kürzlich getötet ... Ich mag gar nicht darüber nachdenken.","Wenn Ihr das letzte Teil gefunden habt, bringt es mir, dann helfe ich Euch, die Rute wieder zusammenzusetzen."}, {"Sucht das letzte Teil von Dartols Rute und bringt es Shael'dryn beim Mondbrunnen."}},
[1028] = {"Raenes Säuberung", {"Hier habt Ihr die vollständige Rute, <Name>. Es war nicht schwer, sie wieder zusammenzusetzen... Den Schrein zu finden, der die Kraft hat, sie wieder aufzuladen, ist der schwierige Teil.","Mit etwas Konzentration und Einsatz sollte es für jemanden wie Euch, der bereits so viel erreicht hat, allerdings nicht zu schwer sein.","Geht von hier aus nach Südosten - es gibt nur wenige Wege, also seid vorsichtig - und behaltet immer den Norden im Auge. Der Schrein liegt in den Bergen verborgen, die dieses Tal umgeben.","Dort angekommen legt Ihr die Rute in den Schrein... der Rest ist Magie. Hehe."}, {"Sucht den verborgenen Schrein und verzaubert Dartols Rute erneut."}},
[1029] = {"Raenes Säuberung", {"Ich schlage vor, dass Ihr Raene aufsucht und sie wissen lasst, dass die Rute fertig gestellt ist. Sie weiß bestimmt, was Ihr als Nächstes tun solltet.","Viel Glück, <Name>."}, {"Kehrt zu Raene Wolfrunner in Astranaar zurück."}},
[1030] = {"Raenes Säuberung", {"Leider bin ich mir nicht ganz sicher, was er als Nächstes vorhatte.","Wenn es wahr ist, was er über die Macht der Rute sagte, könnt Ihr mit ihrer Hilfe eine völlig andere Gestalt annehmen. Welche Gestalt weiß ich allerdings nicht.","Vielleicht wollte er ihre Kräfte nutzen, um näher an die besudelten Furbolgs heranzukommen, aber selbst die nicht verderbten Kreaturen hätten ihn getötet, wäre er nicht vorsichtig gewesen. Durch die Rute verwandelt, hätte Dartol wahrscheinlich mit einem Furbolg gesprochen, der gegen die Auswirkungen des Teufelsmooses gefeit ist.","Ihr solltet Eure Suche südöstlich des Mystralsees beginnen."}, {"Sucht einen nicht verderbten Furbolg und benutzt die Macht von Dartols Rute der Transformation, um mit ihm zu sprechen."}},
[1031] = {"Der Zweig des Cenarius", {"Anilia legt Euch die Hände auf die Schultern und zieht Euch zu sich heran. Nach Luft ringend zeigt sie in Richtung des Bogens im Norden, bevor sich ihre Augen schließen und sie einen letzten, keuchenden Atemzug ausstößt. \"Er hat ihn... Geltharis...\" In Eurem Herzen wisst Ihr, dass es Anilias letzter Wunsch war, den Zweig des Cenarius wieder in den Händen der Dryaden zu sehen."}, {"Tötet Geltharis und bringt Illiyana beim Schrein von Aessina den Zweig des Cenarius."}},
[1032] = {"Satyrn schlachten!", {"Ich kann nicht glauben, dass Anilia von uns gegangen ist... Wir haben doch immer zusammen in den Wäldern gespielt. Ich fühle beinahe so etwas wie... Trauer.","Es gibt aber etwas, das mich vielleicht aufheitern könnte! Bitte, erschlagt die Satyrn, die mir Anilia genommen haben! Bringt mir ihre Hörner als Beweis Eurer Taten und Ihr könnt Euch auf ewig meines Dankes gewiss sein."}, {"Bringt Illiyana im Schrein von Aessina 16 Satyrhörner."}},
[1033] = {"Elunes Träne", {"Ich hatte gehofft, Orendils Heilmittel würde Relara retten, aber ich fürchte, es hat die Krankheit, die in meinem Kind wütet, nur gedämpft. Wir müssen eine stärkere Medizin finden! Zum Glück hat mir Orendil von weiteren Arzneien erzählt, sollte seine Erste versagen.","Träne von Elune ist eine Gesteinsart, die nur an einem Ort vorkommt: auf der Insel im Irissee, östlich von Astranaar. Orendil sagte, der Stein könne Verderbnis wegwaschen ... Bitte, <Name>, bringt mir eine Träne von Elune und betet, dass sich die Erkrankung meiner Tochter damit heilen lässt!"}, {"Bringt Pelturas in Astranaar die Träne von Elune."}},
[1034] = {"Die Sternenstaubruinen", {"Die Träne von Elune hat einen Teil der Erkrankung meiner Tochter weggewaschen. Ihre Alpträume haben nachgelassen und ich danke der Göttin für ihren Segen. Aber... immer noch wütet das Fieber in ihr.","Orendil erzählte mir von einem gefrorenen Staub, den man in den Sternenstaubruinen findet. Vielleicht kann man damit Relaras Fieber senken. Sammelt etwas von diesem Staub und kommt dann bitte wieder zu mir zurück.","Die Sternenstaubruinen liegen im Süden, <Name>. Möge Elune Eure Reise beschleunigen."}, {"Bringt Pelturas in Astranaar 5 Hand voll Sternenstaub."}},
[1035] = {"Fallenskysee", {"Relara hat kein Fieber mehr, aber sie ist immer noch sehr schwach. Ich glaube nicht, dass sie wieder zu Kräften kommt, wenn wir einfach nur abwarten. Ich glaube, dazu bedarf es der Kraft eines Mondsteins.","Wir haben Mondsteine früher immer beim Fallenskysee im Südosten gesammelt... bis die Schattendickichtbäumlinge kamen.","Ihr Anführer, das Schattendickichtorakel, hat alle gefallenen Mondsteine eingesammelt und hortet sie. Jetzt müsst Ihr Euch einen Mondstein von dem Orakel holen.","Und dazu werdet Ihr ihn wohl erschlagen müssen, fürchte ich."}, {"Holt von dem Schattendickichtorakel einen gefallenen Mondstein und bringt ihn Pelturas in Astranaar."}},
[1036] = {"Hierher, Taugenichts", {"<Name>... Ja, ich habe von Euch gehört. Ich habe gehört, dass Ihr kein Freund von Beutebucht seid - genau wie wir Blutsegelbukaniere. Flottenmeister Firallon sucht nach gesunden und tüchtigen <Männern/Frauen> wie Euch für eine spezielle Mission.","Wenn Ihr interessiert seid, begebt Euch zur Purpurschleier und sprecht mit ihm unter Deck. Sie ankert vor der wilden Küste östlich von hier, bei der Springflut und der Maid im Glück."}, {"Sprecht mit Flottenmeister Firallon an Bord der Purpurschleier vor der Küste des Schlingendorntals."}},
[1037] = {"Velinde Starsong", {"Velinde Starsong war hier in Ashenvale meine Vorgängerin. Zunächst schien es, als hätte sie die Lage im Teufelswald unter Kontrolle, aber nach und nach ließen ihre Bemühungen nach. Eines Tages verschwand sie einfach.","Man schickte mich hierher, um ihre Arbeit fortzusetzen. Ich fürchte jedoch, dass ich nichts über die Priesterin weiß. Vielleicht weiß Thyn'tel Bladeweaver, eine der Kommandantinnen der Schildwache, mehr über ihr Verschwinden, das ich nicht miterlebt habe.","Sie wird sicherlich wissen, wie wichtig diese Informationen sind."}, {"Sprecht auf der Terrasse der Krieger in Darnassus mit Thyn'tel Bladeweaver."}},
[1038] = {"Velindes Besitztümer", {"Der Foliant von Mel'Thandris hat Euch das gezeigt? Dann könnte es wohl nicht schaden, wenn ich Euch erlaubte, ihre Habseligkeiten durchzusehen. Mit diesem Schlüssel könnt Ihr die Truhe öffnen, in der wir ihre Sachen in der Schildwachenbaracke aufbewahren. Sie führte ein Tagebuch über ihre Pflichten und wenn es etwas zu erfahren gibt, dann daraus.","Ich sollte Euch noch sagen, dass die Schildwachen denken, dass sie Gründe für ihr Verschwinden hatte und damit rechnen, dass sie jederzeit wieder auftaucht. Die Priesterin hat in der Vergangenheit viel geleistet und unser Vertrauen verdient."}, {"Durchsucht Velindes Truhe nach ihrem Tagebuch und bringt es zusammen mit dem Schlüssel nach Darnassus zu Thyn'tel Bladeweaver."}},
[1039] = {"Der Brachlandhafen", {"Ratchet ist der einzige Hafen des Brachlandes. Höchstwahrscheinlich hat Velinde in Ratchet ein Handelsschiff gefunden, das sie zur Schwarzmeerbucht in Azeroth brachte. Wir haben nur wenig mit den Goblins zu tun, die den Hafen leiten, aber der Dockmeister sollte über das Kommen und Gehen von Schiffspassagieren informiert sein.","Folgt der Straße in südöstlicher Richtung durch Ashenvale, dann gelangt Ihr irgendwann ins Brachland. Seid auf der Hut, <Name>, Krieger der Horde streifen durch das Land. Im Hafen werdet Ihr jedoch sicher sein."}, {"Sprecht in Ratchet mit Werftmeister Dizzywig."}},
[1040] = {"Passage nach Booty Bay", {"Ah ja, da steht's ja. Hättet mir sagen sollen, dass sie vor so langer Zeit hier durchgekommen ist. Mal sehen.","Velinde. Hat eine Passage auf der BLACK OSPREY nach Booty Bay gebucht. Da hier nichts anderes steht, nehme ich an, dass sie den Hafen dort sicher erreicht hat.","Viel mehr Hilfe kann ich Euch nicht anbieten, aber sie hat mich nach einem Überlandtransport zur anderen Seite der Welt gefragt und ich habe ihr Ruzzgot vorgeschlagen, einen Karawanenführer aus Booty Bay. Kann sein, dass Velinde mit ihm gereist ist.","Und jetzt fort mit Euch. Ich hab zu tun."}, {"Nehmt ein Schiff nach Booty Bay und sprecht dort mit dem Karawanenführer Ruzzgot."}},
[1041] = {"Die Karawanenstraße", {"Da hab ich mich wohl in Euch getäuscht und das passiert mir nicht alle Tage.","Zufällig erinnere ich mich an diese Velinde, nach der Ihr sucht. Kommt nicht jeden Tag vor, dass eine Nachtelfenpriesterin mit einer schmutzigen alten - aber alle Transportwünsche erfüllenden - Karawane wie der meinigen reisen will.","Auf dem Weg nach Norden trennten wir uns, sie wollte weiter nach Dunkelhain. Der Amtmann dort macht alle möglichen Aufzeichnungen. Kann sein, dass er etwas Nützliches weiß.","Seid im Dschungel vorsichtig, der ist selbst in günstigsten Zeiten tödlich."}, {"Sprecht in Dunkelhain mit Amtmann Daltry."}},
[1042] = {"Die Familie Carevin", {"Nein, ich habe keinen Eintrag über eine Velinde Starsong, die sich in Dunkelhain aufgehalten haben soll... Wenn Ihr erlaubt, ich kann mir auch nicht vorstellen, dass eine Nachtelfenpriesterin sich in einem Gasthaus einquartiert, wenn Ihr wisst, was ich meine.","Diese Wolfmenschen, die Ihr erwähntet, von denen hab ich schon mal gehört. Erst kürzlich kam Calor mit einer ganzen Reihe Köpfe von denen in die Stadt zurück. Er arbeitet für die Familie Carevin. Sie jagen Dämonen, Untote und andere Monster. Sprecht mit Jonathan, er ist der Haushaltsvorstand."}, {"Sprecht mit Jonathan Carevin in Dunkelhain."}},
[1043] = {"Die Sense von Elune", {"Eure Geschichte klingt glaubhaft... Ich habe Eure Beweggründe zwar noch nicht ganz begriffen, aber wenn es zu Euren Angelegenheiten hier im Dämmerwald gehört, die Worgs im Wald auszurotten, dann können mir Eure Beweggründe auch herzlich egal sein, solange das Ergebnis stimmt.","Im Süden gibt es eine Mine, die von Worgs überrannt wurde... Sie kamen aus dem Nichts, aber nach allem, was wir wissen, hat man sie dort zuerst gesehen.","Geht Ihr nur Euren Angelegenheiten nach, aber ich möchte Euch bitten, dass Ihr mir Bescheid gebt, sobald Ihr etwas Wichtiges entdeckt. Wir sind für jede Hilfe gegen das Böse dankbar."}, {"Sucht nach Hinweisen auf die Sense von Elune und kehrt dann zu Jonathan Carevin in Dunkelhain zurück."}},
[1044] = {"Beantwortete Fragen", {"Wir werden das Worgproblem in den Griff bekommen, keine Frage. Das Böse, das Eure Freundin in unseren Wald gebracht hat, wird eingedämmt werden und ich nehme ihr ihre Handlungen nicht übel. In letzter Zeit gehen seltsame Dinge vor sich, <Name>, und die Dunkelheit kennt keine Atempausen.","Ich will Euch nicht länger aufhalten. Mir scheint, dass auch andere erfahren sollten, was Ihr in den Minen des Dämmerwalds entdeckt habt."}, {"Kehrt nach Darnassus zu Thyn'tel Bladeweaver zurück."}},
[1045] = {"Raenes Säuberung", {"Die Nachtelfen sind so alt wie die Zeit, aber sie zerstören ihre eigene Welt durch ihre Arroganz. Und jetzt zerstören sie auch noch unser Leben. Doch das kann man nicht mehr verhindern...","Ich gebe den Elfen für viele Dinge die Schuld, aber wir sind immer noch für unsere Handlungen selbst verantwortlich. Wir sind sogar trotz der Verderbnis noch zu jeder Menge Bösem fähig.","Ran Blutreißer ist einer dieser Bösen. Machthungrig, gierig... die Verderbnis hat ihn nur noch stärker gemacht. Mein Hass auf die Elfen lodert, aber ich wünsche ihren Kindern nichts Schlimmes, wir müssen uns selbst im Zaum halten."}, {"Tötet Ran Blutreißer sowie 4 Wachen von Blutreißer und kehrt dann zu Krolg in der Nähe des Mystralsees zurück."}},
[1046] = {"Raenes Säuberung", {"Kehrt zu Eurem eigenen Stamm zurück und lasst ihn von Eurem Sieg hier wissen.","Zeigt ihm Eure Trophäe und sonnt Euch in Eurem Ruhm..."}, {"Bringt Raene Wolfrunner in Astranaar Ran Blutreißers Schädel und Dartols Rute der Transformation."}},
[1047] = {"Die neue Grenze", {"Aufgemerkt, Helden!","Der Zirkel des Cenarius sucht nach einsatzfähigen Individuen, die dazu beitragen möchten, ihn bei der Erforschung der riesigen neuen Grenzgebiete Kalimdors zu unterstützen! Weit im Westen von Tanaris und Gadgetzan liegt eine abschreckende Ödnis voller unvorstellbarer Gefahren, und der Zirkel braucht ehrbare und gewillte Vertreter, die an seiner statt agieren.","Interessenten mögen bitte zwecks einer Audienz beim Zirkel des Cenarius vorstellig werden. Sprecht in Darnassus mit dem Erzdruiden Fandral Staghelm, er hat weitere Informationen!"}, {"Sprecht in der Enklave des Cenarius in Darnassus mit dem Erzdruiden Fandral Staghelm über den Aufruf des Zirkels des Cenarius zur Erforschung der Grenzgebiete von Kalimdor."}},
[1048] = {"In das Scharlachrote Kloster", {"Während die dunkle Fürstin sich um die dringende Angelegenheit des Vormarsches des Lichkönigs im Norden kümmert, sorge ich dafür, dass Dinge, die uns unmittelbar betreffen, entsprechend geregelt werden.","Die Menschenplage, die sich selbst Scharlachroter Kreuzzug nennt, hat sich als der schärfste Dorn in unserem Fleisch erwiesen. Tirisfal wimmelt förmlich von diesen kleinen roten Ameisen.","Begebt Euch zum Kloster, <Klasse>, und vernichtet Hochinquisitor Whitemane und seine obersten Offiziere."}, {"Tötet Hochinquisitor Whitemane, den Scharlachroten Kommandant Mograine, Herod, den Scharlachroten Helden sowie den Hundemeister Loksey und meldet Euch dann wieder bei Varimathras in Undercity."}},
[1049] = {"Kompendium der Gefallenen", {"Der irdene Ring hatte nur die besten Absichten, was unsere geplagten Brüder betraf. Aber wer weiß schon, welche Absichten jene verfolgen, die dem Ältestenrat Dinge einflüstern?","Die Geschichte der Verlassenen, mit denen wir uns verbündet haben, ist von Lug und Trug durchzogen. Zu übereilt gingen wir unseren Pakt ein. Vielleicht hätte Cairne gut daran getan, die Warnungen aus Orgrimmar zu beherzigen.","Tief im Inneren des Scharlachroten Klosters liegt das 'Kompendium der Gefallenen' verborgen, das von irren menschlichen Zeloten bewacht wird. Ihr Tun ist von ihrem Wahnsinn geprägt, aber ihre Forschungen könnten nützlich für uns sein."}, {"Holt das 'Kompendium der Gefallenen' aus dem Kloster in Tirisfal und bringt es zu Sage Truthseeker in Thunder Bluff."}},
[1050] = {"Mythologie der Titanen", {"Die Zukunft ist unausweichlich! Wir stürzen ihr entgegen wie Regentropfen, die auf das große Meer prasseln.","Aber vielleicht erfahren wir, indem wir die Vergangenheit studieren, was uns erwartet, und könnten uns besser auf das Unbekannte vorbereiten.","Bevor Lordaeron von all diesen schrecklichen Dingen heimgesucht wurde, gab es eine Bastion des Wissens, die in Tirisfal versteckt lag. Gerüchten zufolge soll es in der Bibliothek des alten Klosters ein Buch mit dem Titel 'Mythologie der Titanen' gegeben haben.","Bringt es mir, dann können wir vielleicht gemeinsam die Geheimnisse der Vergangenheit entschlüsseln!"}, {"Holt die 'Mythologie der Titanen' aus dem Kloster und bringt die der Bibliothekarin Mae Paledust in Ironforge."}},
[1051] = {"Vorrels Rache", {"Trotz meines freien Willens kann ich der dunklen Fürstin nicht mehr dienen. Ich fühle, dass meine Zeit in Azeroth zu Ende geht. Meine Lebenskraft ist zerschmettert.","Das hat mir Befrager Vishas angetan! Wochenlang quälte er mich! Nahm mir meinen Ehering und gab ihn seiner Frau. Dieser Bastard!","Rache ... Ich will Rache!","Der eingebildete Narr prahlte immer mit seiner ach so kostbaren Frau Nancy, die am Lordameresee lebte. Spürt diese Frau auf, erschlagt sie und bringt meiner Frau Monika in Tarrens Mühle meinen Ehering.","Dies ist mein letzter Wille ..."}, {"Bringt Monika Sengutz in Tarrens Mühle den Ehering von Vorrel Sengutz."}},
[1052] = {"Auf dem Scharlachroten Pfad", {"Wir vom scharlachroten Kreuzzug erheben Anspruch auf die Festungen zwischen Hearthglen und Tirisfal. Wir sind recht stolz auf unsere Bastionen der Säuberung in ganz Lordaeron.","Ihr habt Euch gegen die Untoten in Desolace bewiesen. Doch die wahre Bedrohung durch die Seuche liegt in Lordaeron.","Begebt Euch nach Southshore in den östlichen Königreichen. Sucht dort nach einem Kreuzzügler namens Raleigh der Andächtige. Gebt ihm dieses Empfehlungsschreiben, das mein Siegel trägt, dann wird er Euch zu einem ehrwürdigen Ort in unserem scharlachroten Kloster führen."}, {"Bringt Raleigh dem Andächtigen in Southshore Bruder Antons Empfehlungsschreiben."}},
[1053] = {"Im Namen des Lichts", {"Einst diente ich dem Scharlachroten Kreuzzug mit Ehre, Treue und Stolz. Ich hielt das Ziel, das sie verfolgten, für edel: Azeroth von den Untoten befreien.","Als ich jedoch im Kloster in Tirisfal weilte, erkannte ich, dass sie langsam den Bezug zur Wirklichkeit verloren. Jetzt halten sie jeden für verseucht, der nicht das Wams des Kreuzzuges trägt. Unschuldige Männer und Frauen wurden gefoltert, weil sie angeblich verseucht waren.","Der Scharlachrote Kreuzzug muss im Namen des Lichts zerschlagen werden. Ihr, <Name>, müsst ihr wahnwitziges Regime beenden."}, {"Tötet Hochinquisitor Whitemane, den Scharlachroten Kommandant Mograine, Herod, den Scharlachroten Helden sowie den Hundemeister Loksey und meldet Euch dann wieder bei Raleigh dem Andächtigen in Southshore."}},
[1054] = {"Die Auswahl der Bedrohung", {"Direkt im Norden Astranaars leben die Furbolgs vom Distelfellstamm. Meine Späher berichten, dass ihre Zahl sich in den letzten Monaten erhöht hat. Sollten es zu viele werden, könnten sie den Mut finden, Astranaar direkt anzugreifen.","Tötet so viele von ihnen, wie Ihr könnt, aber bringt mir den Schädel ihres Häuptlings zum Beweis dafür, dass ihr Bemühen im Keim erstickt wurde ... vorläufig.","Ich bleibe hier in Astranaar und warte auf Kunde von Euch. Elune sei mit Euch."}, {"Bringt Raene Wolfrunner in Astranaar Dal Blutklaues Schädel."}},
[1055] = {"Raenes Säuberung", {"Die Rute, jetzt wieder heil, liegt in Euren Händen. Shael'dryn wird von Eurem Erfolg hören wollen."}, {"Kehrt zu Shael'dryn beim Mondbrunnen zurück."}},
[1056] = {"Reise zum Steinkrallengipfel", {"Die Waldgeister sagten mir, dass Ihr mutig und zu reisen gewillt seid.","Im Süden, nicht weit vom Mystralsee entfernt, liegt ein Tunnel namens Steinkrallenpfad. Durch diesen Tunnel gelangt Ihr in ein Gebiet, das im Steinkrallengebirge als Scherwindklippe bekannt ist. Dort angelangt reist südwestlich weiter, am Felskesselsee vorbei und dann nach Norden, den steilen Abhang hinauf, bis Ihr den Steinkrallengipfel erreicht.","Dort werdet Ihr von Hüter Albagorm erwartet. Tut, was er sagt, <Volk>.","Die Reise wird gefährlich."}, {"Reist zu Bewahrer Albagorm auf dem Steinkrallengipfel."}},
[1057] = {"Die Rückeroberung des verbrannten Tals", {"Als Beschützer der Waldgeister könnt Ihr sicher unschwer erraten, warum ich beunruhigt bin.","Ihr seid durch diese Berge gereist, um diesen Gipfel zu erreichen. Sicherlich habt Ihr das Zerrbild gesehen, zu dem das Land geworden ist. Gier hat zur Zerstörung dieser heiligen Hügel geführt.","Die gierige motorisierte Armee der Industrialisten hat das verbrannte Tal im Süden in Brand gesteckt. Was noch schlimmer ist, die Blutfurien, die in unser Land eingedrungen sind, lassen uns nicht dorthin, um mit der Neuanpflanzung zu beginnen.","Vertreibt diese elenden Harpyien, <Name>!"}, {"Bewahrer Albagorm auf dem Steinkrallengipfel möchte, dass Ihr 7 Harpyien der Blutfurien, 7 Wegelagerinnen der Blutfurien, 7 Töterinnen der Blutfurien und 7 Wildfedern der Blutfurien erschlagt."}},
[1058] = {"Jin'Zils Waldmagie", {"Ich bin ja so aufgeregt!","Drei kleine Käfige mit drei kleinen Elfen! Aber was tun wir nur mit unseren süßen Baumschützer-Freunden? Na, lassen wir sie doch... wie nennen sie das doch gleich... Waldmagie schmecken!","Ich brauche einen ordentlichen Schuss Steinkrallensaft. Dann brauche ich ein paar Mondlichtläuferschnurrhaare von diesen schrecklichen Katzen. Das schreit nach einer Menge Renneraugen - man kann nie genug Augen haben. Oh, und bevor wir es vergessen, die wertvolle Schuppe eines Siechdrachen.","Schnell zum Steinkrallengipfel, <Name>, damit ich etwas Waldmagie zusammenbrauen kann!"}, {"Hexendoktor Jin'Zil in Malaka'jin möchte 5 Portionen Steinkrallensaft, 5 Schnurrhaare eines Mondlichtläufers, 30 Renner-Augen und die Schuppe eines Siechdrachen."}},
[1059] = {"Die Rückeroberung des verbrannten Tals", {"Die Blutfurien wurden besiegt. Sie werden sich jedoch schon bald wieder neu gruppieren, wenn wir nichts unternehmen.","Ganz weit im Süden des Brachlandes liegt ein Land, das als Tausend Nadeln bekannt ist. Wenn Ihr in Tausend Nadeln nach Westen reist bis zum üppigen Waldrand von Feralas, stoßt Ihr auf einen Druiden namens Falfindel Waywarder.","Dieser große und weise Druide kann große Waldgeister herbeirufen, die bei der Rekultivierung des verbrannten Tals helfen können.","Erzählt ihm von unserer Notlage! Sucht ihn sofort auf!"}, {"Reist zu Falfindel Waywarder an der Grenze zwischen Tausend Nadeln und Feralas."}},
[1060] = {"Brief an Jin'Zil", {"Ihr habt mir sehr geholfen, <Name>. Ihr seid ein echter Glücksbringer, wie eine Hasenpfote, und ich möchte, dass Ihr Euch das hier einmal anseht.","Die Harpyien stammen nicht aus dem Brachland. Sie kommen aus einem Ort namens Steinkrallengebirge, ganz weit nordwestlich von hier. Dort könnte es möglicherweise gefährlich für Euch sein, aber dieser Brief muss an einen alten Freund von mir überbracht werden, der dort lebt, ein Hexendoktor mit dem Namen Jin'Zil.","Er wird sich freuen zu hören, dass Serena Blutfeder tot ist, und ich glaube, Ihr werdet das Steinkrallengebirge sehr interessant finden."}, {"Überbringt Jin'Zil in seiner Höhle im Steinkrallengebirge Darsoks Brief."}},
[1061] = {"Die Geister des Steinkrallengebirges", {"Die Geister des Steinkrallengebirges sind verärgert, weil Goblins und ihre Gefolgsleute das Land ausplündern und brandschatzen. Das Land rief um Hilfe, und jene, die auf so etwas achten... haben es gehört.","Seereth Stonebreak, eine aufstrebende Schamanin der Horde, wurde in das Steinkrallengebirge geschickt. Sie berichtet von umfangreichen Rodungen durch die Goblins und bittet um Hilfe.","Trefft Euch im Steinkrallengebirge mit Seereth. Um zu ihr zu gelangen, folgt der Straße westlich aus Crossroads im Brachland. Sie hat in der Nähe des Hochwipfeltals ein Lager aufgeschlagen."}, {"Sprecht im Steinkrallengebirge mit Seereth Stonebreak."}},
[1062] = {"Goblin-Eindringlinge", {"Die von Goblins geleitete Venture Company ist in das Steinkrallengebirge umgezogen, um dort Bäume zu fällen und ganze Waldflächen zu brandroden. Die Geister dort sind außer sich vor Schmerz und Wut. Wir müssen der Venture Company Einhalt gebieten!","Begebt Euch nach Nordwesten, jenseits des Hochwipfeltals, und zur Scherwindklippe. Ihr werdet dort die Goblins und ihre Gefolgsleute bei der Arbeit vorfinden - zeigt ihnen, dass die Horde eine Ausbeutung des Steinkrallengebirges nicht billigt. Zeigt es ihnen in der Sprache, die sie am besten verstehen...","Gewalt."}, {"Tötet 15 Holzfäller der Venture Co. und kehrt dann zu Seereth Stonebreak an der Grenze des Steinkrallengebirges und dem Brachland zurück."}},
[1063] = {"Die Ältestengreisin", {"Um die Geister des Steinkrallengebirges wirklich zu beschwichtigen, müssen wir uns an die Taurenältesten wenden.","Ich kenne nicht viele ihrer Anführer, aber einen Namen kenne ich: Magatha. Ich habe gehört, dass sie sehr alt sein soll und sehr viel weiß. Wenn jemand die Tauren um geistige Hilfe bitten kann, dann sie.","Magatha lebt in der Tauren-Hauptstadt Thunder Bluff auf der Anhöhe der Ältesten - dem Steilhang im Nordosten der Stadt. Sprecht mit Magatha und berichtet ihr von der Not im Steinkrallengebirge."}, {"Sprecht in Thunder Bluff mit Magatha."}},
[1064] = {"Die Hilfe der Verlassenen", {"Die Tauren sind mit dem Land verbunden, und es schmerzt mich, von der Not im Steinkrallengebirge zu hören. Ich fürchte jedoch, dass wir zunächst die Krankheit ausmerzen müssen, bevor wir das Land heilen können.","Ist es nicht eine glückliche Fügung, dass wir mit den Verlassenen verbündet sind? Sie wissen viel über Krankheiten. Ich glaube, sie werden uns helfen können, was das gegenseitige Vertrauen zwischen unseren Völkern stärken wird.","Sprecht mit Apothekerin Zamah. Sie ist eine Gelehrte und Botschafterin der Verlassenen. Ihr findet sie bei den Teichen der Visionen unterhalb der Anhöhe der Geister."}, {"Sprecht bei den Teichen der Visionen mit der Apothekerin Zamah."}},
[1065] = {"Reise nach Tarrens Mühle", {"Um die Venture Company aus dem Steinkrallengebirge zu vertreiben, müssen wir extreme Maßnahmen ergreifen. Extrem, aber ohne Blutvergießen.","Einer meiner Kollegen hat vielleicht genau das Werkzeug, das wir benötigen. Er heißt Lydon und wohnt in Tarrens Mühle im Vorgebirge von Hillsbrad, das im weit entfernten Lordaeron liegt.","Um nach Hillsbrad zu gelangen, reist mit dem Zeppelin in unsere Hauptstadt, nach Undercity. Geht von dort aus nach Norden nach Tirisfal, dann südwestlich nach Silberwald und südlich nach Hillsbrad.","Sucht Lydon auf und gebt ihm diese Notiz. Sie wird ihm alles erklären."}, {"Bringt Apotheker Lydon in Tarrens Mühle Zamahs Notiz."}},
[1066] = {"Das Blut Unschuldiger", {"Ich werde ein Toxin herstellen, mit dem wir der Venture Company die Gewalt entziehen und sie schwach und sanftmütig werden lassen. Das sollte das Problem der Horde im Steinkrallengebirge lösen, meint Ihr nicht?","Um das Toxin herzustellen, benötige ich jedoch das Blut Unschuldiger, eine Zutat, an die man nur schwer herankommt.","Glücklicherweise beschäftigt das Syndikat, das ist eine Gruppe menschlicher Briganten in dieser Region, Schattenmagier ... und diese Magier beschaffen sich das Blut Unschuldiger für ihre eigenen unappetitlichen Machenschaften.","Ihr finded die Schattenmagier des Syndikats in der Burg Durnholde im Südosten."}, {"Bringt Apotheker Lydon in Tarrens Mühle 5 Phiolen mit dem Blut Unschuldiger."}},
[1067] = {"Rückkehr nach Thunder Bluff", {"Hier habt Ihr das Toxin. Wenn Ihr es gegen die Venture Company einsetzt, sollte es positive, vielleicht sogar ansteckende Ergebnisse erzielen. Ich muss sagen, es ist ziemlich aufregend zu sehen, dass mein Werk tatsächlich eingesetzt wird!","Bringt es aber erst der Apothekerin Zamah, bevor Ihr es einsetzt. Sie muss eine Anwendungsmethode für das Toxin entwickeln, um einen größtmöglichen Effekt zu erzielen."}, {"Bringt Apothekerin Zamah in Thunder Bluff Lydons Toxin."}},
[1068] = {"Schreddermaschinen", {"Es gibt eine Verbindung zwischen der spirituellen Magie der Schamanen und der natürlichen Magie der Druiden. Deshalb können die Schamanen auch das Flehen der Geister des Steinkrallengebirges hören. Vieles, das sie flüstern, verstehe ich nicht, aber eine Nachricht, eine Vision, ist eindeutig ...","Ich hatte die Vision zweier menschenähnlicher Maschinen, die auf der Scherwindklippe nordwestlich des Hochwipfeltals mit ungeheuerlicher Geschwindigkeit Bäume fällten. Diese Metallbestien müssen aufgehalten werden!","In meiner Vision trugen diese Maschinen seltsame Symbole:","XT:4 und XT:9."}, {"Vernichtet die Schredder XT:4 und XT:9 und kehrt dann zu Seereth Stonebreak an der Grenze zwischen dem Steinkrallengebirge und dem Brachland zurück."}},
[1069] = {"Eier der Kluftmoosspinnen", {"Omeletts aus Spinneneiern sind der letzte Schrei in Booty Bay! Das Problem ist nur... dass es in Booty Bay keine Eier gibt.","Das riecht nach einer Geschäftsidee!","An der Scherwindklippe und im Sishircanyon nordwestlich im Steinkrallengebirge, lebt die Kluftmoosspinne. Bringt mir ihre Eier, dann werde ich Euch reichlich entlohnen!","An der Scherwindklippe sind die Eihaufen unter den noch verbliebenen Bäumen zu finden.","Im Sishircanyon... tja, wenn Ihr einen schwachen Magen habt, solltet Ihr vielleicht nur die Eier an der Scherwindklippe sammeln..."}, {"Bringt Mebok Mizzyrix in Ratchet 15 Kluftmooseier."}},
[1070] = {"Wache halten im Steinkrallengebirge", {"Hallo, <Name>.","So wie Ihr ausseht, scheint Ihr voller Reiselust zu sein. Wart Ihr schon mal im Steinkrallengebirge? Es ist ein Land, das von der Venture Company und anderen Feinden der Nachtelfen heimgesucht wird.","Erst neulich schickte Schildwache Wolfrunner meine Freundin Kaela Shadowspear dorthin, um zu untersuchen, was mit dem Land geschehen ist, aber ich habe seitdem nichts mehr von ihr gehört. Was mir noch mehr Sorgen macht ist, dass die Allianz sie zusammen mit einem Gnomen-Magier losgeschickt hat ... das deutet definitiv auf Probleme hin.","Würdet Ihr sie suchen und mir dann Bericht erstatten?"}, {"Sucht Kaela Shadowspear, die auf der Scherwindklippe im Steinkrallengebirge den Taleingang beobachtet."}},
[1071] = {"Der Aufschub des Gnoms", {"Wisst Ihr, <Name>, ich bin Mitglied der Erleuchteten Vereinigung der Arkanologie, Alchimie und Ingenieurwissenschaften, und es ist mein Ziel... nein, meine PFLICHT!, den Nachtelfen zu zeigen, dass eine Mischung aus Magie, Mixturen und Mechanismen dazu beitragen kann, ihren Wald zu retten.","Es ist jedoch ein Kampf gegen Windmühlen, und die Goblins vermehren sich wie die Kaninchen, wie es scheint. Ich hatte sogar nicht mal Zeit, um meine neuesten Erfindungen zu bauen!","Ich brauche etwas Zeit, um zu planen und zu organisieren. Dazu brauche ich jedoch Eure Hilfe bei der Eindämmung der Goblins auf der Scherwindklippe im Osten..."}, {"Tötet 10 Holzfäller der Venture Co. und 10 Roder der der Venture Co. und kehrt dann zu Gaxim Rustfizzle im Steinkrallengebirge zurück."}},
[1072] = {"Ein alter Kollege", {"Das Gerät, das mir vorschwebt, wird meine am weitesten entwickelte Erfindung sein. Aber wir brauchen einen speziellen Trank, wenn es funktionieren soll. Ich glaube, wir sollten uns gleich an das gute Zeug halten, immerhin könnte das unsere letzte Mission sein.","Und deshalb brauchen wir einen hochwirksamen Sprengstoff: Nitromirglyceronium.","Die einzige Person, die NG-5 herstellen kann, ist ein alter Freund von mir: Lomac Gearstrip. Sucht in Tüftlerstadt nach ihm.","Wenn Ihr ihn überreden könnt, uns etwas NG-5 herzustellen, kann ich mich an die Platzierung meiner Geräte machen."}, {"Sprecht mit Lomac Gearstrip in Ironforge."}},
[1073] = {"Stümperhaftigkeit + Chemikalien = Riesenspaß!", {"Hahaha... Ihr braucht Nitromirglyceronium, um Goblins daran zu hindern, einen Wald zu zerstören?!","Es ist mir egal, ob Ihr Gaxim kennt oder nicht... Ich würde Euch das Zeug allein schon deshalb geben, weil ich sehen will, wie Ihr oder sie in klitzekleine Fetzen explodieren.","Es gibt allerdings ein Problem: Ich hab nichts mehr.","Aber ich sag Euch was: Bringt mir die richtigen Tränke, dann stelle ich sofort etwas für Euch her.","Das Mindeste, was Ihr tun könnt, ist, mir ein paar Eurer Alchimisten-Fertigkeiten vorzuführen, bevor ich Euch eine Kostprobe meiner größten Erfindung gebe."}, {"Bringt Lomac Gearstrip in Ironforge 4 schwache Manatränke und 2 Elixire der Seelenstärke."}},
[1074] = {"Stümperhaftigkeit + Chemikalien = Riesenspaß!", {"Bitte sehr, Fremdling. Seid jedoch vorsichtig damit... das Zeug ist hochexplosiv.","Falls Ihr wirklich ein Freund von Gaxim seid, grüßt ihn von mir. Falls nicht, dann viel Spaß beim Selbst-in-die-Luft-jagen."}, {"Bringt Gaxim Rustfizzle im Steinkrallengebirge das Nitromirglyceronium."}},
[1075] = {"Eine Rolle von Mauren", {"Gut, wenden wir uns jetzt dem Teil meines Planes mit der Fernzündung zu... oh, das hab ich ja noch gar nicht erklärt, stimmt's?","Also dann mal die Kurzversion: Mit etwas Magie kann ich dafür sorgen, dass der Sprengstoff hochgeht, ohne dass Ihr dabei sein müsst. Ich bastele ein Gerät zusammen, bei dem Ihr nur einen Knopf drücken müsst, damit das NG-5 explodiert, wenn Ihr bereit seid.","Ich brauche jedoch einen Zauber von einem Freund in Stormwind - er heißt Collin Mauren. Ihr findet ihn im dortigen Magierviertel. Er ist ein Mensch, daher solltet Ihr ihn kaum verfehlen können."}, {"Sucht Collin Mauren in Stormwind auf."}},
[1076] = {"Teufel in Westfall", {"Ah, ich glaube, ich hab's. Ein Nachrichtenzauber... Er dürfte den Effekt haben, nachdem er sucht.","Ich muss eine Rolle herstellen, auf die der Zauber geprägt ist. Dazu benötige ich die Überreste eines Staubteufels.","Warum geht Ihr nicht nach Westfall und sucht eine dieser elenden Kreaturen, während ich den Rest der Komponenten sammle? Sie sind gefährlich, aber ich bin mir sicher, dass Ihr schon damit klarkommt, zumal Ihr sogar gewillt seid, an einem von Gaxims Experimenten teilzunehmen.","Ich werde hier sein, wenn Ihr zurückkommt."}, {"Bringt Collin Mauren in Stormwind die Überreste eines Staubteufels."}},
[1077] = {"Speziallieferung für Gaxim", {"Bitte sehr, <Name>. Das sollte funktionieren.","Bitte grüßt Gaxim von mir, wenn Ihr ihn das nächste Mal seht. Ich freue mich schon so darauf, von diesem Abenteuer zu hören, wenn Ihr es überstanden habt.","Lebt wohl."}, {"Bringt Gaxim Rustfizzle im Steinkrallengebirge die Rolle der Nachrichtenübermittlung."}},
[1078] = {"Suche für Mauren", {"Immer wieder fragen mich Reisende über das Steinkrallengebirge aus. Scheint ein beliebter Ort unter Abenteurern zu sein; spielt keine Rolle, ob man Flügeldrachen oder Elementare sucht, oder ob man Geschäfte mit der Venture Company machen will.","Im verbrannten Tal tief im Steinkrallengebirge gab es mal eine Basiliskenart, deren Schuppen, wenn man sie zu Staub zermahlte, ein wunderbares Reagenz für einige der Zauber ergab, die ich ersonnen habe.","Wenn diese Basilisken noch leben, hätte ich liebend gern ein paar ihrer Schuppen.","Lasst Euch Zeit, ich hab keine Eile, aber ich kann Euch gut bezahlen."}, {"Bringt Collin Mauren in Stormwind 8 kristallisierte Schuppen."}},
[1079] = {"Geheimoperation Alpha", {"Das ist es, <Name>. Seht genau hin. Die Geräte sind fertig und einsatzbereit.","Nehmt diese Blätter - sie erklären mehr über die Zünder und den Sprengstoff und wo Ihr ihn platzieren solltet.","Denkt daran, der Erfolg Eurer Mission hängt nicht davon ab, wie viel Schaden Ihr anrichtet. Der Erfolg von Plan Alpha hängt davon ab, dass Ihr die Ingenieurspläne der Venture Company aus dem Sägewerk südlich des Felskesselsees holt."}, {"Holt die Ingenieurspläne der Venture Company aus dem Sägewerk auf der Scherwindklippe."}},
[1080] = {"Geheimoperation Beta", {"Plan Beta funktioniert genau wie Plan Alpha, <Name>.","Wir stiften Verwirrung und stehlen dann ihre wertvollen Dokumente.","Wir müssen unbedingt diese Dokumente haben... vergesst das nicht.","Die Blätter, die Ihr bereits habt, zeigen Euch, wo Ihr den Sprengstoff platzieren müsst. Kommt mir nur heil und mit diesen Dokumenten zurück..."}, {"Holt die Dokumente der Venture Company aus der Scherwindmine am Ostende der Scherwindklippe."}},
[1081] = {"Tyrandes Empfang", {"<Name>, ich bin mir sicher, dass Tyrande Whisperwind Euch persönlich für Eure Teilnahme an dieser Mission danken wollen wird.","Es mag auf den ersten Blick zwar nicht so aussehen, aber Ihr habt dazu beigetragen, etwas in Gang zu setzen, das unsere Position im Steinkrallengebirge erheblich stärken wird.","Falls Ihr Euch als unseren Verbündeten im Kampf gegen diese Gräueltaten betrachtet, sucht Tyrande im Tempel des Mondes in Darnassus auf."}, {"Sucht Tyrande Whisperwind in Darnassus auf."}},
[1082] = {"Neuigkeiten für Schildwache Thenysil", {"<Name>, Ihr habt Großes geleistet, indem Ihr uns hier geholfen habt.","Die Schildwachen von Ashenvale werden Euch reichlich belohnen, wenn Ihr ihnen mitteilt, dass Gaxim und ich noch am Leben sind. Sagt ihnen, dass wir beim Aufhalten der Venture Company und der Heilung des Landes gute Fortschritte machen.","Sucht Tara Thenysil in Astranaar auf. Sie wird sich freuen, dass Ihr mit guten Neuigkeiten kommt."}, {"Kehrt zu Schildwache Thenysil in Ashenvale zurück."}},
[1083] = {"Wütende Geister", {"Ich weiß nicht genau, was die Venture Company dem Tal angetan hat, aber es ist offensichtlich, dass es von den dortigen Kreaturen überwältigt wurde.","Sie steckten den Wald in Brand und benutzten ihre zerstörerischen Erfindungen, um so viel wie möglich des Tals zu zerstören.","In ihrer Dummheit haben sie jedoch auch noch etwas anderes entfesselt: brennende Geister, Elementare der Flammen, der Steine und der Wut.","Das Land wird Elune nicht mehr lächeln sehen, solange diese Kreaturen frei herumstreifen.","Bringt mir ihre glimmenden Überreste, um mir zu zeigen, dass Ihr sie dezimiert habt."}, {"Bringt Kaela Shadowspear im Steinkrallengebirge 8 glimmende Überreste der Feuerelementare des verbrannten Tals."}},
[1084] = {"Verletzte Urtume", {"Es scheint mir offensichtlich, dass die Beschützer des Tales - die Treants - irgendetwas damit zu tun hatten, dass die Venture Company zurückgeschlagen wurde. Leider wurden selbst diese einst so großen Wächter jetzt verderbt.","Gaxim glaubt, dass ihre Verderbnis irgendwie mit den Waffen zusammenhängt, die die Goblins während ihres Rückzuges benutzten.","Ich bat ihn, eine Probe von einem verwundeten Treant zu nehmen und sie zu untersuchen, um herauszufinden, ob das wirklich stimmt.","Bringt mir ein paar Späne, damit der Gnom mehr darüber herausfinden kann ..."}, {"Bringt Kaela Shadowspear im Steinkrallengebirge ein Stück eines zerfressenen Spans."}},
[1085] = {"Wache halten im Steinkrallengebirge", {"Die Lage hier im Steinkrallengebirge ist offensichtlich düster, wenn Nachtelfen und Gnome zusammenarbeiten.","Die Venture Company hat das Land verwüstet und ich habe einen Großteil meines Leben damit zugebracht, der Zerstörung ein Ende zu setzen.","Man sollte Gaxim nicht vorschnell beurteilen; er versteht die Notwendigkeit, warum wir zusammenarbeiten müssen.","Wenn Ihr uns helfen wollt, sprecht zunächst mit Gaxim.","Es tut mir Leid, dass ich Euch jetzt noch nicht mit guten Neuigkeiten zu Tara zurückschicken kann. Aber vielleicht schon bald."}, {"Sprecht mit Gaxim."}},
[1086] = {"Der Flugapparathafen", {"Das Toxin ist bereit. Jetzt muss es nur noch ausgeliefert werden. Ihr, <Name>, müsst es ausliefern.","Die Venture Company muss irgendwelche Mittel und Wege haben, Nachrichten und leichte Fracht zwischen ihrem Hauptquartier und dem Betrieb im Steinkrallengebirge hin- und herzuschicken. Es könnte ein kleiner Flugplatz sein - nicht groß genug für einen Zeppelin, aber vielleicht für einen Flugapparat. Ihr müsst die Gebiete, die von der Venture Company kontrolliert werden, durchsuchen und diesen Flugplatz finden, falls er existiert. Solltet Ihr ihn finden, platziert das Toxin dort.","Platziert es und lauft, was Ihr könnt."}, {"Bringt den Toxinvernebler zum Flugplatz der Venture Company und kehrt dann zu Apothekerin Zamah zurück."}},
[1087] = {"Cenarius' Vermächtnis", {"Ihr könnt Euch nicht vorstellen, wie groß mein Hass auf dieses elende Land ist. Solche Leute wie Cenarius und die Kaldoreidruiden auf dem Gipfel benutzen ihre lächerlichen Mächte, um den Magiefluss zu stoppen, sogar noch so weit von ihrem lächerlichen Besitz entfernt ... ihren geliebten Wäldern.","Ich wünschte, ich könnte nach Jaedenar zurückkehren und im Fluss der Magie baden, aber mein Meister erlaubt mir erst dorthin zurückzukehren, wenn meine Arbeit getan ist.","Zweifellos liebt Ihr die Nachtelfen und ihre Verbündeten genauso wenig wie ich. Helft mir, dann haben wir vielleicht beide etwas davon."}, {"Tötet für Brealyn Firehand in Sonnenfels 4 Söhne des Cenarius, 4 Töchter des Cenarius und 4 cenarische Botaniker."}},
[1088] = {"Ordanus", {"Sie dachten, dass man den Fluss der Magie zum Erliegen bringen könnte. Sie haben sich geirrt. Sie dachten, dass sie Ordanus verstecken könnten, aber ich will seinen Kopf!","Die Magie gewährt mir Durchblick. Ja... da ist er. Ein Scheideweg... auf dem Wipfel eines großen Baumes in Ashenvale. Ein Name... Raynewood. Einer der ältesten Söhne des Waldgottes. Sie haben ihn aus dem Wipfel geschleust, bevor ich ihn finden konnte, aber ihm läuft langsam die Zeit davon.","Bringt ihn mir, dann teile ich ein großes Geschenk mit Euch."}, {"Bringt Braelyn Firehand in der Nähe von Sonnenfels Ordanus' Kopf."}},
[1089] = {"Der Höhlenbau", {"Die Druiden errichteten den Krallenbau auf dem Steinkrallengipfel, in dem sie viele mächtige Gegenstände aufbewahren.","Sie haben sich ein komplexes Schema ausgedacht, damit sie nicht in die falschen Hände gelangen.","Glücklicherweise konnte ich einem von ihnen... ein paar Details entlocken.","Versteckt in einer kleinen Laterne am Eingang des Baus werdet Ihr den Torhüterschlüssel finden. Damit könnt Ihr die Behälter öffnen, die über den ganzen Bau verteilt sind. Mit den Gegenständen aus diesen Behältern wiederum könnt Ihr ihren Hort öffnen, der sich in einem der zentralen Bereiche befindet."}, {"Begebt Euch zum Höhlenbau auf den Steinkrallengipfel. Benutzt den Torhüterschlüssel, um Euch die versteckten Gegenstände der Druiden zu besorgen. Benutzt diese Gegenstände, um den Hort des Krallenbaus zu öffnen."}},
[1090] = {"Gerenzos Befehle", {"Wisst Ihr, ich war hier einst die zweitwichtigste Person. Das hat sich alles geändert, als Gerenzo hier übernahm. Hat meinen Boss Ziz Fizziks gefeuert und mich zum Arbeiten nach unten in die Minen geschickt. Ziz sagte mir aber, dass ich hier bleiben und Informationen sammeln solle. Sein neuer Boss wird uns gut bezahlen, wenn wir rausfinden, was Gerenzo plant.","Egal, was ich weiß ist, dass Gerenzo befohlen hat, dass Riesenmengen von diesem gold-grünen Erz abgebaut werden sollen. Ich hol Euch eine Probe, damit Ihr sie Ziz bringen könnt, aber Ihr müsst Euch vor Patrouillen hüten, während ich es hole."}, {"Nehmt das rätselhafte Erz, nachdem Piznik es fertig abgebaut hat."}},
[1091] = {"Kaelas Update", {"<Name>, bringt Kaela diese Briefe. Ich bin mir sicher, dass sie das interessiert. Oder zumindest die Schildwachen.","Erzählt Ihr unbedingt, wie erfolgreich wir waren und dass wir es ohne ein bisschen Magie nie geschafft hätten."}, {"Bringt Kaela Shadowspear die Dokumente der Venture Company."}},
[1092] = {"Gerenzos Befehle", {"Ich glaube, ich sollte auch langsam darüber nachdenken, hier zu verschwinden. Es wird nicht lange dauern, bis die da oben erfahren, was hier gelaufen ist, und ich bin sicher, dass die keinen großen Wert darauf legen, mich wiederzusehen.","Ich schleich mich raus und treff mich mit Ziz. Hoffentlich kann er mir einen neuen Job besorgen!","Ich werde versuchen, hier ein bisschen Ordnung zu schaffen, aber Ihr solltet die Erzprobe sofort zu Ziz bringen. Er sagte mir, dass er irgendwo an der Westseite der Scherwindklippe sein Lager aufgeschlagen hat. Viel Glück."}, {"Bringt Ziz Fizziks im Steinkrallengebirge das unbekannte Erz."}},
[1093] = {"Superschnitter 6000", {"Schrecklich, was die Venture Company hier getan hat, was? Was haben die sich dabei gedacht? Eine Goblin-Firma, die einen Gnom anheuert!","Ein Glück für mich, dass Sputtervalve ein Genie erkennt, wenn es vor ihm steht. Hat mich eingestellt, gleich nachdem ich gefeuert wurde!","Sagte mir, sie wollten eine riesige Holzfällmaschine bauen, und Sput möchte, dass ich für ihn die Pläne dazu stehle. Leider wird Gerenzo - dieser verdammte Gnom - mich umlegen, sobald er mich sieht ... würdet Ihr vielleicht die Pläne für mich stehlen? Ihr solltet sie bei einem ihrer Maschinenführer finden."}, {"Besorgt für Ziz Fizziks im Steinkrallengebirge die Blaupausen für den Superschnitter 6000."}},
[1094] = {"Weitere Instruktionen", {"In diesem Umschlag befinden sich alle Informationen, die ich mir schnappen konnte, bevor sie mich vor die Tür setzten... Interne Dokumente der Venture Company, ein paar Handbücher, technisches Zeugs... Oh, und natürlich die Blaupausen für den Superschnitter 6000.","Sputtervalve hat sich bei Ratchet im Brachland niedergelassen. Sollte nicht schwer sein, ihn zu finden. Er ist ein kleiner, grüner Kerl."}, {"Bringt Sputtervalve im Brachland den versiegelten Umschlag."}},
[1095] = {"Weitere Instruktionen", {"Die Tatsache, dass die Venture Company diesen Gnom angestellt hat, hat in Undermine für einiges Aufsehen gesorgt. Die Handelsbarone finden es nicht gut, dass Razdunk mit Gerenzo gemeinsame Sache macht.","Riddlevox hat mich gebeten, mich um das Problem zu kümmern und ich bin mir sicher, dass einer oder zwei dieser Handelsbarone ihn dazu angewiesen haben. Natürlich hat mir der Direktor das nicht erzählt. Sagen wir einfach, ich hätte so eine Ahnung.","Wie dem auch sei, Gerenzo muss entfernt werden und es wäre eine passende Prüfung, das Fizzik erledigen zu lassen."}, {"Bringt Ziz Fizziks im Steinkrallengebirge die neuen Befehle."}},
[1096] = {"Gerenzo Wrenchwhistle", {"Ich habe Gerenzo einmal getroffen, und lasst mich Euch eines sagen, er ist nicht gerade eine Schönheit. Sein Fleisch sieht aus, als würde es faulen. Dazu hat er noch überall an seinem Körper Metallteile und Zeugs angeflanscht und sogar einige seiner Körperteile durch mechanische ersetzt.","Nordöstlich von hier, weit oberhalb des Felskesselsees in der Nähe des Wasserrades beaufsichtigt Gerenzo die gesamte Stätte. Ich könnte mich unmöglich bis da hinauf durchkämpfen und schon gar nicht mich mit Gerenzo anlegen ... Aber Ihr seht aus, als könntet Ihr es schaffen.","Wenn Ihr ihn für mich tötet, soll es Euer Schaden nicht sein!"}, {"Bringt Ziz Fizziks im Steinkrallengebirge Gerenzo Wrenchwhistles mechanischen Arm."}},
[1097] = {"Elmores Aufgabe", {"In Stormwind gibt es einen Zwergen-Waffenschmied namens Grimand Elmore, der mal angedeutet hat, dass er Hilfe bei einer Lieferung bräuchte. Ich glaube, er möchte ein Paket in seine Heimat im Norden schicken.","Ihr habt da ein paar schöne kräftige Beine! Falls Ihr also an ein wenig Beinarbeit interessiert seid, sprecht mit Grimand. Wir könnten Euch hier unten brauchen, aber wir müssen auch unsere Bande mit den Zwergen stärken.","Ihr findet Grimand Elmore im Waffenladen im Zwergendistrikt von Stormwind, im nordöstlichen Teil der Stadt."}, {"Sprecht mit Grimand Elmore."}},
[1098] = {"Todespirscher in Shadowfang", {"Als Vorbereitung auf einen Überfall auf Shadowfang wurden zwei Todespirscher in die Burg geschickt, um Informationen zu sammeln. Das war vor mehreren Tagen und sie sind noch nicht wieder zurück, aber wir brauchen ihren Bericht, wenn wir auf einen erfolgreichen Angriff hoffen wollen!","Das ist Eure Aufgabe, <Name>. Geht zu Burg Shadowfang im Süden und sucht die Todespirscher Adamant und Vincent. Findet sie und helft ihnen, falls es erforderlich ist.","Diese Mission ist nicht nur gefährlich, sondern auch lebenswichtig, <Name>. Versagt bitte nicht."}, {"Sucht die Todespirscher Adamant und Vincent."}},
[1100] = {"Lonebrows Tagebuch", {"Ihr öffnet langsam das verwitterte Tagebuch des Henrig Lonebrow..."}, {"Lest Henrig Lonebrows Tagebuch."}},
[1101] = {"Die Greisin des Krals", {"Armer Heralath! Und dieser Zwerg... Lonebrow. Was war er doch für eine mutige Seele.","So viel Heldenmut darf nicht umsonst gewesen sein. Diese Greisin... Charlga Razorflank... muss aufgehalten werden.","Sicher, es wird gefährlich werden, den Kral zu bespitzeln. Wir haben keine Zeit, um Darnassus zu informieren. <Name>, stellt eine Gruppe zusammen, um die Greisin zu erschlagen.","Bringt mir Razorflanks Medaillon zum Beweis ihres Ablebens."}, {"Bringt Falfindel Waywarder in Thalanaar Razorflanks Medaillon."}},
[1102] = {"Ein schreckliches Schicksal", {"Es war edel von unserem Anführer Cairne, unser Volk hier in Thunder Bluff zu vereinen.","Ich kann jenen jedoch nicht vergeben, die uns so leicht aus dem Land unserer Vorfahren vertrieben haben. Jahrhundertelang lebte mein Volk im südlichen Brachland. Das Land war uns heilig. Aber wir wurden von zahllosen Feinden vertrieben.","Ein schreckliches Schicksal erwartet die Greisin Charlga Razorflank, die im Kral von Razorfen eine abscheuliche Armee zusammenzieht.","Bringt mir Razorflanks Herz, damit ich in Frieden sterben kann, <Name>."}, {"Bringt Auld Stonespire in Thunder Bluff Razorflanks Herz."}},
[1103] = {"Ruf des Wassers", {"Seid gegrüßt, <Bruda/Schwesta>! Seid auch Ihr gekommen, um das Wasser hier zu läutern und Islen Euer Wissen zu beweisen?","Ich habe den verderbten Wassergeist gerade selbst bezwungen und bin grad dabei, die Armschienen un' verbleibenden Wassertropfen in die Kohlenpfanne zu geben.","Was'n das? Ahhh. Tiev versteht nur zu gut, wenn wat falsch läuft. Tja, Tiev kann Euch echt helfen, wenn Ihr den Wassergeist wieda seh'n wollt.","Bringt mir 'in Wassersapta von Islen als Beweis, dass Ihr bereit seid, un' ich bin bereit, meinen Teil beizutrag'n."}, {"Bringt Tiev Mordune im Silberwald ein Wassersapta, wenn Ihr nicht mit der schwachen Wassermanifestation gesprochen habt."}},
[1104] = {"Salziges Gift", {"Ich habe so viele Pläne für unseren Raketenwagen! So viele! Und ich muss sie alle ausprobieren!!!","Ich arbeite gerade an einem neuen Treibstoff; an einem, der wirklich, wirklich, wirklich heiß ist! Die Mixtur stimmt zwar noch nicht ganz, aber ich glaube, ich weiß, was noch fehlt...","Die Skorpide, die die schimmernde Ebene durchwandern, haben ein Gift mit Unmengen Salz darin. Es ist einzigartig! Und es ist genau das, was ich brauche!","Bringt mir das Gift und vertraut mir: unser Wagen wird fliegen!"}, {"Bringt Fizzle Brassbolts in der schimmernden Ebene 6 Einheiten salziges Skorpidgift."}},
[1105] = {"Gehärtete Schalen", {"Mein Bruder arbeitet an einem neuen Treibstoff für seinen Raketenwagen. Er ist brillant, ehrlich. So brillant, dass... Ich glaube, der neue Treibstoff, den er da entwickelt, ist stark genug, um sich durch das Chassis des Wagens zu fressen!","Ich würde gern die Treibstofftanks verstärken, bevor das passiert.","Die Schildkröten draußen auf der Ebene haben sehr starke Panzer. Ich möchte daraus neue Tanks für unseren Raketenwagen bauen. Tanks, die den neuen Treibstoff meines Bruders aushalten.","Ihr seht mir verantwortungsbewusst genug aus. Holt Ihr mir solche Panzer?"}, {"Bringt Wizzle Brassbolts in der schimmernden Ebene 9 gehärtete Schildkrötenpanzer."}},
[1106] = {"Martek der Verbannte", {"Ich entwickle einen neuen Motor, mit dem der Wagen so schnell wie der Wind wird! Schnell genug, um zu fliegen, ich weiß es! Ich muss nur ein paar Kolben herstellen, die sehr, sehr große Belastungen aushalten. All meine bisherigen Versuche sind fehlgeschlagen, weil ich die Kolben einfach nicht robust genug herstellen kann!","Aber es gibt jemanden, der wissen könnte, wie es geht.","Sein Name ist Martek der Verbannte. Er ist ein großartiger Schmied und weiß mehr über Metall als alle anderen. Bringt ihm diesen Brief, ich weiß, dass er helfen kann!","Aber er lebt weit, weit weg - in Azeroth, im Ödland, in einem Lager, zusammen mit einem Goblin!","Sucht ihn!"}, {"Bringt Martek dem Verbannten im Ödland Fizzles Brief."}},
[1107] = {"Verkrustete Schwanzflossen", {"Unser Raketenwagen ist das schnellste, was es auf der Ebene gibt! So schnell, dass... ich fürchte, die Reibung reißt ihn auseinander!","Um das zu verhindern, muss ich einen Stoff entwickeln, der die beweglichen Teile des Wagens überzieht. Er muss hart sein, aber nicht spröde, und er muss resistent gegen Salzkorrosion sein, sonst wird der Wagen, während er sich in der schimmernden Ebene befindet, zerfressen.","Habt Ihr schon mal vom finsteren Riff gehört, <Name>? Es liegt vor der Küste des Schlingendorntals. Dort leben Salzschuppenmurlocs, deren Schwanzflossen unabdingbare Zutaten zu meinem Stoff sind."}, {"Bringt Fizzle Brassbolts in der schimmernden Ebene 10 verkrustete Schwanzflossen."}},
[1108] = {"Indurium", {"Ich bin nicht hier draußen, weil ich mich vor Leuten verstecken will. Das Ödland ist reich an Metall, <Name>, wenn man den Mut hat, es sich zu holen.","Das Metall heißt Indurium, und wie das Schicksal so spielt, soll es angeblich hohen Widerstand gegen Hitze und Belastung bieten. Das könnte genau das Richtige für Fizzles Wagen sein.","Aber wir sollten sichergehen. Bringt mir eine Indurium-Probe, dann kann ich seine Eigenschaften testen.","Echtes Induriumerz liegt tief drinnen in Uldaman, aber die Steingrufttroggs des Ödlandes tragen manchmal Späne davon mit sich herum. Bringt mir diese Späne."}, {"Bringt Martek dem Verbannten im Ödland 10 Induriumspäne."}},
[1109] = {"Go, Go, Guano!", {"Wie Ihr seht, <Name>, befassen wir uns mit einigen sehr... interessanten Experimenten in der Königlichen Apothekervereinigung.","Als Meister hier ist es meine Aufgabe, die anspruchsvollsten unserer alchimistischen Versuche zu überwachen. So viel Arbeit und nur so wenig Zeit!","Ihr seht weit gereist aus, <Klasse>. Vielleicht könnt Ihr mir helfen.","Ich brauche eine rare Substanz. Es ist dieses widerliche Zeug, das von einer raren Fledermausart stammt, die man nur in einem Dreckloch namens Kral von Razorfen findet. Bringt mir den Guano der Kral-Fledermäuse, damit wir mit unserer Arbeit beginnen können..."}, {"Bringt dem Apothekermeister Faranell in Undercity 1 Häufchen Kral-Guano."}},
[1110] = {"Raketenwagenteile", {"Hi-hi, bei den vielen Rennen, die hier stattfinden, ist es nicht verwunderlich, wenn dabei ein paar Unglücksfälle passieren. Wenn man sich in der schimmernden Ebene umschaut, sieht man ständig Spuren vergangener Unfälle - überall liegen Teile von Raketenwagen herum.","Und diese Teile bedeuten für die Gnome und Goblins bares Geld. Sie suchen ständig nach weiteren neumodischen Apparaten, um sie an ihre Autos anzubauen.","Also zieht los und bringt mir die Teile. Bringt mir einen Haufen und ich werde Euch gut bezahlen."}, {"Bringt 30 Raketenwagenteile zu Kravel Koalbeard in der schimmernden Ebene."}},
[1111] = {"Werftmeister Dizzywig", {"Mein Job ist es, den Leuten zu beschaffen, was sie möchten, und diese Gnome da drüben möchten Teile bestellen. Ihre Teile wurden vor einer Woche nach Ratchet geschickt, aber es war mir nicht möglich, dorthin zu reisen.","Also, seid Ihr bereit, mir die Kleinarbeit abzunehmen? Geht nach Ratchet und gebt diese Teile-Bestellung an Werftmeister Dizzywig - er wird wahrscheinlich bei den Docks sein.","Wenn Ihr das macht, habe ich weitere Aufträge für Euch.","Aufträge, bei denen eine Menge Profit rausspringen kann ..."}, {"Bringt Kravels Teile-Bestellung zu Werftmeister Dizzywig in Ratchet."}},
[1112] = {"Teile für Kravel", {"Hier ist Kravels Paket, <Name>. Und wenn Ihr es zustellt, sagt ihm bitte... wenn er weitere spezielle Lieferungen möchte, dann muss er bezahlen!"}, {"Bringt Kravels Teile zu Kravel Koalbeard in der schimmernden Ebene."}},
[1113] = {"Herzen des Eifers", {"Warum ich eine rare Art von Guano benötige, fragt Ihr Euch? Also bei diesem Experiment möchte ich wissen, wie zwei diametral entgegengesetzte chemische Substanzen aufeinander wirken, besonders wenn beide mit einem höchst toxischen Agens kombiniert werden, um die ... garstigsten Resultate zu erzielen.","In der Nordostspitze von Tirisfal liegt das Scharlachrote Kloster. Dort versammeln sich die wahnsinnigen Jünger des Scharlachroten Kreuzzuges. Tötet sie und bringt mir ihre übereifrigen Herzen.","Unser Gebräu wird äußerst tückisch sein!"}, {"Apothekermeister Faranell in Undercity möchte 20 Herzen des Eifers."}},
[1114] = {"Lieferung für die Gnome", {"Jetzt, wo ich habe, was ich brauche, gebt diese Teile Fizzle Brassbolts. Er wartet schon lange darauf - ich bin sicher, das kleine Teil, das ich behalten habe, wird er nicht vermissen...","Und ich würde ihn auch nicht darauf hinweisen. Es war klein und wertlos.","Glaubt mir."}, {"Bringt die empfindlichen Autoteile zu Fizzle Brassbolts."}},
[1115] = {"Der Gerüchteverbreiter", {"Jetzt, da wir diesen Dienst für die Gnome geleistet haben, wird es Zeit, den Goblins einen Gefallen zu tun!","Ich habe gehört, dass der Goblin-Rennfahrer Nazz Steamboil diesen Namen nicht immer getragen hat. Ich frage mich, weshalb er ihn änderte. Ob es wohl ein Geheimnis ist und... wie viel ihm dieses Geheimnis wert ist.","Wenn jemand die Wahrheit kennt, dann Krazek aus Booty Bay an der Südspitze des Schlingendorntals. Sprecht mit Krazek und fragt ihn nach unserem Goblin-Rennfahrer.","Wenn Ihr nützliche Informationen aus ihm herausbekommt, springt vielleicht Profit für uns raus."}, {"Sprecht mit Krazek in Booty Bay."}},
[1116] = {"Traumstaub im Sumpf", {"Es ist ein großer Gefallen, diese Informationen über Nazz zu beschaffen, <Name>. Ja, ein großer Gefallen. Aber Ihr habt den weiten Weg von der schimmernden Ebene auf Euch genommen... seid Ihr bereit, noch etwas weiter zu reisen?","Ihr könntet doch etwas für mich besorgen, während ich Kontakt mit meinen Informanten aufnehme, hm?","Hmm... was könnte es denn sein? Wie wäre es mit Traumstaub von den Großdrachenwelpen und Wyrmkin aus den Sümpfen des Elends? Ja, ich hätte gern etwas Traumstaub für meinen Schnupftabakbeutel!","Bringt ihn mir, <Name>, und ich gebe Euch die gewünschten Informationen."}, {"Bringt 10 Körnchen Traumstaub zu Krazek in Booty Bay."}},
[1117] = {"Gerüchte für Kravel", {"Ich musste einige Gefälligkeiten erweisen, eine rare Flasche Elfenwein herschenken und mir selbst einiges zusammenreimen! Aber ich habe die Informationen, die Ihr sucht.","Hier, ich habe einen Brief mit einigen sehr interessanten Infos über Euren Freund Nazz Steamboil verfasst. Macht damit, was Ihr wollt - für mich wäre es zu gefährlich, ihn zu benutzen.","Sagt nur keinem, woher Ihr das habt!"}, {"Bringt die Goblin-Gerüchte zu Kravel Koalbeard in der schimmernden Ebene."}},
[1118] = {"Zurück nach Booty Bay", {"<Name>, Ihr seid ein <Klasse>, dem ich trauen kann. Ihr werdet den Auftrag erledigen und die Klappe halten.","Ich habe einen neuen Plan geschmiedet. Er ist riskant, aber wenn er funktioniert, könnten wir beide gut absahnen! Und ich glaube, Ihr seid der Richtige, um das durchzuziehen.","Als Erstes müsst Ihr nach Booty Bay zurückkehren. Nun, ich bitte um Verzeihung, <Name>. Ich weiß, meine letzte Aufgabe führte Euch dorthin. Aber sprecht diesmal mit Crank Fizzlebub.","Gebt ihm diesen Brief. Und wenn er um Unterstützung bittet, dann tut, was Ihr könnt, um ihm zu helfen. Es wird sich lohnen."}, {"Bringt Kravels Plan zu Crank Fizzlebub in Booty Bay."}},
[1119] = {"Zanzils Mixtur und Fools Starkbier", {"Ich habe herausgefunden, wie man aus Zanzils Mixtur mit Starkbier ein mächtiges Getränk macht. Fools Starkbier. Und ich glaube, genau das braucht Kravel. Probiert es einfach mal aus und lasst mich wissen, wie es wirkt.","Und behaltet das Zeug nicht zu lange unbenutzt, <Name>. Zanzils Mixtur hat eine seltsame Wirkung auf die Hefe des Starkbiers und regt sie heftig an. Sie bleibt nicht ewig frisch."}, {"Bringt Fools Starkbier zu Kravel in der schimmernden Ebene zurück."}},
[1120] = {"Die Gnome betrunken machen", {"Wir müssen Fools Starkbier an einem aus der Boxencrew ausprobieren. Wenn Ihr es an den Gnomen erproben wollt, hier - nehmt das Starkbier und gebt es dem Gnomen-Boxenboss.","Und wartet nicht zu lange. Das Starkbier wird mit der Zeit schlecht."}, {"Bringt Fools Starkbier zum Gnomen-Boxenboss."}},
[1121] = {"Die Goblins betrunken machen", {"Wir müssen Fools Starkbier an einem aus der Boxencrew ausprobieren. Wenn Ihr es an den Goblins erproben wollt, hier - nehmt das Starkbier und gebt es dem Goblin-Boxenboss.","Und wartet nicht zu lange. Das Starkbier wird mit der Zeit schlecht."}, {"Bringt Fools Starkbier zum Goblin-Boxenboss."}},
[1122] = {"Meldet Euch bei Fizzlebub", {"Fools Starkbier hat astrein gewirkt! Ich habe beim nächsten Rennen gesetzt... hoffen wir, das Team, das Ihr betrunken gemacht habt, hat sein Auto nummeriert!","Bleibt nur noch eine Aufgabe für Euch - bringt diese Notiz zu Crank Fizzlebub in Booty Bay. Er wird von dem erfolgreichen Test wissen wollen.","Hoffentlich bekommen wir mehr von Fools Starkbier für künftige Rennen!"}, {"Bringt den Bericht über Fools Starkbier zu Crank Fizzlebub in Booty Bay."}},
[1123] = {"Rabine Saturna", {"Für diese Aufgabe sucht Majordomus Rabine Saturna für Bewahrer Remulos in Moonglade Unterstützung bei der Erforschung des riesigen Ödlandes weit im Westen von Tanaris und Un'Goro. Er lebt in Nighthaven, dem Hauptort von Moonglade.","Vorher möchte ich Euch jedoch noch von der Wichtigkeit Eurer Anwesenheit in Kenntnis setzen. Es gibt zunehmend Spannungen zwischen den Nachtelfen von Darnassus und dem Zirkel des Cenarius in Moonglade. Lasst Euch nicht von völkischer Engstirnigkeit beirren und verlasst Euch auf die Weisheit Eurer Vorfahren, wenn Ihr dort mit dem Zirkel zu tun habt, <Name>. Viel Glück."}, {"Sprecht mit Rabine Saturna im Dorf Nighthaven in Moonglade. Moonglade liegt zwischen dem Teufelswald und Winterspring, und Ihr erreicht es über den Pfad, der aus der Holzschlundfeste herausführt."}},
[1124] = {"Einöde", {"Westlich des Un'Goro Kraters entlang des nordwestlichen Grats liegt ein raues Ödland - Silithus. Nur wenige wissen, dass sich hier vor Urzeiten ein Krieg zwischen den Nachtelfen und einer bösartigen, außerirdischen Macht abgespielt hat. Das Böse wurde versiegelt, aber jetzt befürchten wir, dass Kräfte am Werk sind, um diesen... unaussprechlichen Horror wieder zu entfesseln.","Sprecht mit Layo Starstrike, einem unserer Agenten in Silithus. Sucht nach ihm in Heldenwacht, der letzten Ruhestätte der Gefallenen des Ödlandes. Gebt Ihm diesen Brief, damit erwerbt Ihr sein Vertrauen."}, {"Sprecht in der Nähe des Friedhofs Heldenwacht in Silithus mit Layo Starstrike und zeigt ihm Rabines Brief."}},
[1125] = {"Die Geister vom Südwindposten", {"Südwestlich von uns liegt ein tragischer Schauplatz - die Ruinen vom Südwindposten. Dort sollten wir mit unserer Arbeit beginnen, <Name>.","Nach allem, was wir wissen, war dieses Dorf einst ein Stützpunkt der Nachtelfen für Operationen in Silithus. In der Zwischenzeit wurde es längst von den Silithidschwärmen überrannt, die die Wüsten durchziehen. Ich vermag mir den Horror kaum vorzustellen, als die Zerstörung des Dorfs begann...","Die gequälten Geister gefallener Druiden und Schildwachen wandern ziellos durch die Ruinen. Erforscht diesen Ort und befreit die Seelen dieser unglücklichen Kreaturen, wo immer es erforderlich ist."}, {"Befreit die Geister von 8 gequälten Druiden und 8 gequälten Schildwachen beim Südwindposten und kehrt dann zu Layo Starstrike auf den Friedhof Heldenwacht in Silithus zurück."}},
[1126] = {"Schwarmbau im Turm", {"Wir müssen uns um den Schwarmbau beim Südwindposten kümmern, und ich glaube, ich weiß auch schon, wie wir an ihn herankommen. Meiner Erfahrung nach reagieren die Schwarmbauten der Silithiden empfindlich auf ihre Umgebung. Das erklärt auch ihre Angriffe, als sie die Todesschreie der Geister hörten.","Im Turm sind die Silithiden am stärksten - dort, wo massenweise Wespen herumschwirren. Besteigt den Turm und scheucht den Schwarmbau auf. Meldet Euch zurück, sobald Ihr etwas erfahren habt, aber seid vorsichtig! Wenn Ihr irgendetwas Hilfreiches entdeckt, bringt es zu mir."}, {"Besteigt den Turm beim Südwindposten und findet eine Möglichkeit, den Schwarmbau der Silithiden aufzuscheuchen. Bringt alles Ungewöhnliche, das Ihr dabei entdeckt, zu Layo Starstrike auf den Friedhof Heldenwacht in Silithus."}},
[1127] = {"Fools Starkbier", {"Ihr möchtet also etwas von Fools Starkbier, hm? Denkt Ihr daran, wieder die Rennen zu besuchen...?","Gut, bringt mir mehr von Zanzils Mixtur und ich braue noch etwas mehr Starkbier für Euch."}, {"Bringt 12 von Zanzils Mixturen zu Crank Fizzlebub in Booty Bay."}},
[1130] = {"Melor schickt Kunde", {"<Name>, Melor Stonehoof hat von Euren Taten gehört und Euch einen Weg geebnet.","Wenn Ihr ihn einschlagen möchtet, dann geht nach Thunder Bluff und sprecht mit Melor. Ihr findet ihn auf der Anhöhe der Jäger."}, {"Sprecht mit Melor Stonehoof in Thunder Bluff."}},
[1131] = {"Stahlbiss", {"Manchmal jagen wir wegen Essen. Manchmal jagen wir wegen der Ehre. Und manchmal jagen wir, um der Lehren der Erdenmutter teilhaftig zu werden.","Doch die Jagd, auf die ich Euch jetzt schicke, hat damit nichts zu tun. Es geht darum, eine Kreatur zu töten, die nicht unter dem Segen der Erdenmutter steht.","Die Hyäne Stahlbiss streift südlich des Brachlandes durch Tausend Nadeln. Sie ist eine Tyrannin, die andere Hyänen anlockt und Furcht und Blutvergießen unter den friedlicheren Wildtieren verbreitet.","Sucht Stahlbiss, <Name>. Sucht und besiegt sie."}, {"Bringt Stahlbiss' Rippe zu Melor Stonehoof in Thunder Bluff."}},
[1132] = {"Fiora Longears", {"Ach, wieder auf See zu sein! Den Kuss des Windes zu spüren und mich von den Wellen wiegen zu lassen wie vor langer Zeit von meiner gesegneten Mutter!","Oh, hätte ich doch nur Euer Glück, <Klasse>, denn ich sehe das Meer in Eurer Zukunft!","Meine Aufgabe ist es, eifrige Seelen nach Kalimdor zu bringen, in das Land der unbegrenzten Möglichkeiten! Wenn Ihr Euer Glück in Übersee machen wollt, dann reist von hier mit dem Schiff in den wunderschönen Hafen Theramore. Sprecht mit meiner Partnerin, der Elfin Fiora Longears.","Sie wird Euer Abenteuer in Kalimdor beginnen lassen!"}, {"Sprecht mit Fiora Longears in Theramore."}},
[1133] = {"Reise nach Astranaar", {"Wenn Ihr gerade vom Boot von Menethil kommt, dann solltet Ihr als Erstes nach... Astranaar gehen. Ich bin sicher, ein eifriges Mitglied der Allianz kann dort viel Gutes tun. Sprecht mit Shindrell Swiftfire und bietet Eure Dienste an.","Aber erwähnt nicht, wer Euch geschickt hat. Shindrell kennt mich nicht..."}, {"Sprecht mit Shindrell Swiftfire in Astranaar."}},
[1134] = {"Prachtschwingen des Steinkrallengebirges", {"Wir führen keinen offenen Krieg mit der Horde, aber der Frieden ist brüchig. Ihre Agenten gehen häufig gegen uns vor, aber stets heimlich.","Einer dieser Feldzüge wurde uns durch geheime Informanten in Theramore enthüllt, die behaupten, dass die Orcs Gift der Prachtschwingen beim Mirkfallonsee im Steinkrallengebirge im Süden sammeln. Dann benutzen Orc-Auftragsmörder das Gift bei ihren geheimen Feldzügen gegen die Allianz!","Wenn das stimmt, dann müssen wir sie aufhalten, <Name>. Wir müssen ihnen den Zugang zu dem Gift versperren."}, {"Bringt 12 Prachtschwingengiftbeutel zu Shindrell Swiftfire in Astranaar."}},
[1135] = {"Steilhanggift", {"Wie Ihr sicher wisst, werden Flügeldrachen oft von Orcs als Reittiere gezähmt. Weniger bekannt indessen ist, dass das Gift eines Flügeldrachen in manchen Kreisen höher geschätzt wird als die Bestie selbst. Falls Ihr also keine Angst vor einer kleinen Flügeldrachenjagd habt... habe ich einen Job für Euch.","Es gibt einen Ort im Land Tausend Nadeln, er liegt westlich von hier, welcher als der Steilhang bekannt ist. Die dort lebenden Steilhangflügeldrachen haben ein starkes Gift. Stark und kostbar."}, {"Bringt 10 Steilhanggiftbeutel zu Fiora Longears in Theramore."}},
[1136] = {"Frostrachen", {"<Name>, jetzt ist es Zeit, weit entfernte Länder zu bereisen. Ihr seid bereit.","Ihr jagt jetzt Frostrachen, einen Yeti im Alteracgebirge. Er ist schwer zu erkennen, da sein weißes Fell mit den Schneefeldern seiner Bergheimat verschmilzt. Man bekommt ihn kaum je zu sehen.","Aber ich zeige Euch, wie man eines solchen Geschöpfes habhaft wird. Hier, nehmt diese Rolle. Ich habe darauf vermerkt, wie man Frostrachen anlocken kann.","Geht jetzt, <Name>. Eure Reise ist lang und viele Herausforderungen harren Eurer. Erst wenn Ihr mit Frostrachens Mähne zurückkehrt, ist Eure Aufgabe abgeschlossen."}, {"Bringt Frostrachens Mähne zu Melor Stonehoof in Thunder Bluff."}},
[1137] = {"Neuigkeiten für Fizzle", {"Indurium besitzt einen erstaunlichen Hitzewiderstand! Ich bin sicher, es taugt für Fizzles Kolben. Aber er braucht echtes Indurium, nicht nur die Späne.","Das Erz muss tief in den Minen von Uldaman abgebaut werden, an der Nordgrenze des Ödlandes. Das ist die wahre Quelle von Indurium.","Bestellt Fizzle Brassbolts Grüße von mir und sagt ihm, was ich Euch gesagt habe.","Angenehme Reise, <Name>. Ich wäre nicht überrascht, wenn wir uns wiedersehen - wenn Fizzle von dem Indurium erfährt, schickt er Euch wahrscheinlich nach Uldaman, um es zu holen!"}, {"Sprecht mit Fizzle Brassbolts in der schimmernden Ebene."}},
[1138] = {"Meeresfrüchte", {"Ich liebe Krebse. Krebse sind die Früchte des Meeres! Man kann sie backen, grillen, kochen und braten. Man kann sie in der Pfanne braten, frittieren und kurzbraten. Es gibt Krebssalat, Krebssuppe, Krebseintopf, Pfefferkrebs, Zitronenkrebs, Peitscherwurzelkrebs und Ironforgekrebs à la Surprise. Das ... das war's eigentlich.","Ich angle hier gerade und daher kann ich mir keine feinen Krebsbrocken besorgen. Mann kann sie aus Riffkriechern und verkrusteten Gezeitenkriechern kratzen. Man kann viel mit ihnen anstellen. Man kann sie backen, grillen, kochen ..."}, {"Sammelt 6 feine Krebsbrocken für Gubber Blump in Auberdine."}},
[1139] = {"Die verlorene Tafel des Willens", {"Wir haben den Schattenschmiedeklan ausgebremst, indem wir ihn von der Blackrockspitze abgeschnitten haben, doch Hammertoes Mission wurde noch nicht erfüllt. Sein Tod darf nicht umsonst gewesen sein. Er träumte davon, die Schrifttafel des Willens zu finden: eine Steintafel, graviert mit Magie durch die Titanen selbst, die helfen, ihren Schöpfungen Leben einzuhauchen und ihnen damit einen eigenen Willen ermöglichen. So jedenfalls heißt es in den Legenden.","Dennoch müssen wir die Tafel bergen, ehe der Schattenschmiedeklan sie finden kann.","Kehrt nach Uldaman zurück, sucht die Tafel und kommt unverzüglich wieder her."}, {"Sucht die Tafel des Willens und bringt sie zu Berater Belgrum in Ironforge."}},
[1140] = {"Der Turm von Althalaxx", {"Díe Schrift der Orcs ist kaum mehr als das Gekritzel eines Kindes. Viele meines Ordens spotteten, als ich ihre Sprache lernen wollte, aber ich fand es hilfreich, meine Feinde besser zu verstehen.","Ilkruds Gekritzel zufolge scheint Athrikus immer noch zwei Seelenedelsteine zu besitzen, und die werden in Nightrun und Satyrnaar aufbewahrt, Satyrgebiete im nordöstlichen Ashenvale.","Seid vorsichtig da draußen, <Name>, die bluttriefenden Tempel der Satyrn können selbst für abgebrühte <Klasse> wie Euch beängstigend sein."}, {"Befreit die Hochgeborenenseelen in Night Run und Satyrnaar und kehrt zu Delgren dem Läuterer in Maestras Posten zurück."}},
[1141] = {"Die Familie und die Angel", {"Ich liebe das Angeln. Die Blump-Familie ist berühmt fürs Angeln. Mein Name ist Gubber Blump; ich angle.","Es gibt eine Fischart, die ich hier in der Gegend gern fange. Sie heißt Dunkelküstenbarsch. Früher fuhr ich mit einem Boot hinaus, um Barsche zu angeln, aber seitdem die gemeinen Murlocs gekommen sind, fahren keine Boote mehr raus. Ich wette, dass sie auch alle Barsche aufgefressen haben.","Sagt, wollt Ihr mir helfen, Dunkelküstenbarsche zu fangen? Ich würde Euch eine echte Blump-Familienangel geben, wenn Ihr es tun würdet! Es ist eine wirklich gute Angel, um damit Fische zu fangen."}, {"Fangt 6 Dunkelküstenbarsche für Gubber Blump in Auberdine."}},
[1142] = {"Die Sterblichkeit schwindet", {"Ich habe nicht mehr lange zu leben... so... so... schwach.","... der t-t-tapfere Zwerg Lonebrow hat jemanden geschickt, Falfindel zu w-w-warnen...","Aber bevor ich... s-s-sterbe...","... meine Frau Treshala... ihr Anhänger des Bundes mit mir... gestohlen... von einem der n-n-niederträchtigen Angreifer...","B-B-Bitte sucht ihn... und bringt ihn Treshala in Darnassus zurück...","... mit der Nachricht meines einsamen... Todes."}, {"Sucht und bringt Treshalas Anhänger zu Treshala Fallowbrook in Darnassus."}},
[1143] = {"Der Turm von Althalaxx", {"Verstehe. Also wenn das stimmt, was Ihr sagt, dann ist jetzt die Zeit gekommen, endlich zuzuschlagen. Ich glaube, Athrikus residiert in der Turmspitze. Aber seid vorsichtig, er ist von vielen mächtigen Hexenmeistern umgeben, die tödlicher sind als Eure bisherigen Gegner."}, {"Tötet Athrikus Narassin und bringt seinen Kopf zu Balthule Shadowstrike nahe des Turms von Althalaxx."}},
[1144] = {"Willix der Importeur", {"Endlich ist Hilfe eingetroffen!","Ihr habt keine Ahnung, wie lang ich hier gefangen gehalten wurde. Ich kann mich nicht erinnern, wann ich zum letzten Mal draußen war.","Wisst Ihr, ich bin Unternehmer. Immer am abkupfern, wie man so sagt. Ich habe Wind davon bekommen, dass Mebok Mizzyrix gut für Rotlaubknolle aus diesem Schweinepferch hier bezahlt. Ich dachte mir, das würde ein leichtes Spiel werden. Wie Ihr sehen könnt, habe ich mich geirrt!","Wie auch immer, wenn Ihr so freundlich wärt, mir hier raus zu helfen, sollte es Euer Schaden nicht sein."}, {"Führt Willix den Importeur aus dem Kral von Razorfen hinaus."}},
[1145] = {"Der Schwarm wächst", {"Viele Reisende, die durch Crossroads kamen, halfen mir, Eier vom Feld der Riesen zu sammeln, und viele können Geschichten erzählen, wie tödlich diese Kreaturen sind. Ich habe genug gehört, um diese Insekten als größere Bedrohung einzustufen als meine Meister zuerst dachten.","Ich brauche einen Kurier, der nach Orgrimmar reist und sich mit Belgrom Rockmaul trifft. Das dürfte keine Kleinigkeit werden und es sollte nur jemand gehen, der die Aufgabe ernst nimmt.","Belgrom ist für gewöhnlich vor der Halle der Kriegerhelden zu finden."}, {"Stellt Korrans versiegelte Notiz Belgrom Rockmaul in Orgrimmar zu."}},
[1146] = {"Der Schwarm wächst", {"Korran ist also zu einer ähnlichen Schlussfolgerung hinsichtlich dieser Insekten gekommen - sie scheinen doch eine größere Bedrohung zu sein, als wir dachten.","Vor ein paar Tagen kehrte ein Jäger halbtot aus Tausend Nadeln zurück. Er gehörte zu einem Stoßtrupp, der geschickt wurde, um ein Ausgrabungsteam der Zwerge von der schimmernden Ebene zu verjagen. Während des Angriffs kamen noch mehr von diesen Insekten aus einer Höhle an der Grabungsstätte und hätten die Gruppe fast getötet.","Ihr Anführer war Moktar Krin und sie suchten Zuflucht an der Grenze der schimmernden Ebene. Bringt ihm diese Notiz."}, {"Stellt Belgroms versiegelte Notiz Moktar Krin in Tausend Nadeln zu."}},
[1147] = {"Der Schwarm wächst", {"Hmm. Offenkundig hält Belgrom die Monster für eine Bedrohung. Er möchte sie in die Tiefen zurückgedrängt haben, aus denen sie gekommen sind.","Die Zwerge zu töten, die von der Forscherliga geschickt wurden, genügte wohl nicht - jetzt muss ich eine Gruppe halbtoter Krieger in einen neuen Kampf führen. Aber selbst können wir das nicht; nicht unter diesen Bedingungen. Die Grabungsstätte liegt südlich von hier. Es sollten keine Zwerge mehr in der Gegend sein. Habt Ihr Lust, Klinge oder Stab in die Hand zu nehmen und uns zu unterstützen?"}, {"Tötet 5 Silithidsucher, 5 Silithidschwarmdrohnen sowie 5 Silithideindringlinge und kehrt zu Moktar Krin in Tausend Nadeln zurück."}},
[1148] = {"Teile des Schwarms", {"Ihr untersucht den Knochenpanzer gründlich. Als Ihr ihn in den Händen dreht, stellt Ihr fest, dass die Chitinplatten derart verflochten sind, dass er dadurch unglaublich hart wird. So etwas habt Ihr noch nie gesehen. Ihr untersucht den Rest des Kadavers und kommt zu dem Schluss, dass diese Kreaturen sehr kräftig und hochentwickelt sind.","Als ihr einen weiteren Blick auf den Knochenpanzer werft, wird Euch klar, dass Korran diese Kreaturen gewiss gern studieren würde. Er könnte dadurch herausfinden, was diese Kreaturen sind und woher sie kommen."}, {"Bringt 1 Silithidenherz, 5 Silithidenkrallen und 3 intakte Silithidenknochenpanzer zu Korran in Crossroads."}},
[1149] = {"Test des Glaubens", {"Ihr habt mich also gesucht, oder führt Euch das Schicksal zu mir? Einerlei. Ihr seid jetzt hier, und so wird Eure Reise erneut beginnen.","Das Sein besteht aus vielen Aspekten: Geist, Körper, Verstand. Einige davon kann man anhand der Traditionen einer Kultur bewerten. Von anderen können wir nur hoffen, dass sie in uns selbst existieren. Wenn Ihr einverstanden seid, möchte ich einige der Aspekte in Euch testen.","Wenn Ihr besteht, werdet Ihr stärker werden. Wenn Ihr scheitert, werdet Ihr die Weite des Nether begreifen.","Als Erstes ein Test des Glaubens."}, {"Wenn Ihr festen Glaubens seid, dann springt von den Planken über Tausend Nadeln."}},
[1150] = {"Test der Belastbarkeit", {"Ich sehe mit Freuden, dass Euer Mut im Lichte Eurer bisherigen Taten unerschütterlich bleibt. Da Euer Glaube stark ist, wird es Zeit, Euren Körper zu testen.","Als Erstes ein Test der Belastbarkeit. Geht mit Freunden zum Bau der Wildfedern westlich vom Freiwindposten. Steigt in die Tiefen ihres Unterschlupfes hinab und zerstört ihre Nahrungsvorräte. Damit solltet Ihr deren Zorn angestachelt haben. Wenn Ihr lange genug überlebt, wird Grenka Blutkreischer Euch persönlich heimsuchen. Tötet sie und bringt mir ihre Klaue, um den Test abzuschließen."}, {"Bringt Grenkas Klaue zu Dorn Plainstalker in Tausend Nadeln."}},
[1151] = {"Test der Kraft", {"Zu Zeiten der Alten Götter bewohnten Elementare einen Großteil dieser Welt. Sie beteten die Alten Götter an, und die Götter fesselten die Elementare durch ihre Macht an diese Welt. Als die Alten Götter verbannt wurden, verblassten die Elementare in unserer Welt und kehren jetzt auf Geheiß mächtiger Magier zurück.","Aber es gibt Ausnahmen. Einige Elementar waren mächtig genug, aus eigenen Stücken hier zu bleiben. Solch ein Elementar ist Rok'Alim.","Sucht Rok'Alim in Tausend Nadeln und tötet ihn, um Eure Kraft zu beweisen."}, {"Bringt Fragmente von Rok'Alim zu Dorn Plainstalker in Tausend Nadeln."}},
[1152] = {"Test der Lehre", {"Eure Zeit bei mir neigt sich dem Ende zu. Ich bin wissend, aber den Test der Lehre kann ich Euch nicht stellen. Es gilt, Lektionen zu erlernen und Orte zu besuchen, die in der Zuständigkeit anderer liegen.","Wenn Ihr bereit seid, dann sucht Braug Dimspirit auf - er ist ein höchst weiser Schamane. Er wird Euch weiter testen, also achtet auf seine Worte, <Name>.","Wenn Ihr bereit seid, dann sucht den Tunnel, der das Steinkrallengebirge mit Ashenvale verbindet. Dort in der Nähe lebt Braug. Und seid vorsichtig, <Name>, die Nachtelfen versuchen vielleicht, Euren Test zu behindern."}, {"Sucht Braug Dimspirit in der Nähe des Eingangs zum Steinkrallenpfad im Steinkrallengebirge."}},
[1153] = {"Eine neue Erzprobe", {"<Klasse>, Ihr habt mir auch früher schon geholfen. Der Kriegshäuptling ist mit meinen bisherigen Leistungen zufrieden und bittet mich, mit neuer Entschlossenheit fortzufahren. Meine Ehre gebietet, dass ich den Kriegshäuptling wissen lasse, was Ihr alles vollbracht habt, doch zuvor möchte ich Euch abermals um Hilfe bitten.","Jüngst kamen Reisende aus dem Süden von einem Ort namens Tausend Nadeln. Dort töteten sie einen Kobold, der eine außergewöhnliche Erzprobe bei sich hatte, wollten sie aber nicht verkaufen. Ich möchte eine Probe dieses Erzes untersuchen."}, {"Sucht eine unbearbeitete Erzprobe bei einem Buddler oder Feldmesser der Bruchschnauzen und bringt sie zu Tatternack Steelforge im Camp Taurajo im Brachland."}},
[1154] = {"Test der Lehre", {"Ihr habt Dorns Tests bestanden und wollt jetzt Eure nächste Prüfung absolvieren. Sehr gut. Bisher sind alle Eure Herausforderungen kindisch gewesen. Es ist leicht, Euren Körper zu testen - bei Eurem Verstand sieht das schon anders aus.","Ich werde es mit einfachen Worten erklären: sucht das Buch, das ich Euch nenne, lest es und beantwortet meine Fragen nach seinem Inhalt.","Was für ein Buch, werdet Ihr fragen? Das 'Vermächtnis der Aspekte'. Man findet es in den Tiefen des Dor'danil-Grabhügels im südlichen Ashenvale, wo es von den toten Druiden und deren Tötern bewacht wird."}, {"Sucht das 'Vermächtnis der Aspekte' und bringt es zu Braug Dimspirit in der Nähe des Eingangs zum Steinkrallenpfad im Steinkrallengebirge zurück."}},
[1159] = {"Test der Lehre", {"Gut gemacht, <Name>. Ihr habt den ersten Teil des Tests der Lehren bestanden, aber es bleibt noch eine Frage zu beantworten. Aber es ist nicht an mir, diese Frage zu stellen.","Besucht Parqual Fintallas in Lordaeron. Er war ein bedeutender Historiker der Menschen, bevor die Seuche ihn erwischte. Jetzt sucht er immer noch Erleuchtung, freilich einer ganz anderen Art.","Er wird eine weitere Forderung an Euch stellen, bevor er Euch etwas fragt, aber Ihr habt Euch bisher meisterhaft geschlagen - ich bin sicher, Ihr werdet dieses Hindernis überwinden."}, {"Sucht Parqual Fintallas in Undercity."}},
[1160] = {"Test der Lehre", {"Ich sage Euch, was Ihr suchen müsst und werde Euch sogar meine Frage stellen, aber nicht, weil Ihr Euch beweisen wollt. Wissen ist weit wichtiger als Kraft. Auch wenn andere Euch in ihren Fähigkeiten überlegen sein mögen, Euer Wissen über die Welt ist wertvoller.","Geht zu dem Kloster im Norden Tirisfals. In der dortigen Bibliothek steht ein Buch mit dem Titel Die Anfänge der Bedrohung durch die Untoten. Es wurde von den jetzt verderbten Paladinen benutzt, um sie in meiner Art des Kampfes auszubilden.","Bringt es mir."}, {"Sucht Die Anfänge der Bedrohung durch die Untoten und bringt es zu Parqual Fintallas in Undercity."}},
[1164] = {"Von Dieben stehlen", {"Die Hure! Das Flittchen! Kenata lebt noch; ihre Familie blüht und gedeiht, während ich leide. Verlassene, wahrhaftig!","Meine Familie wurde Opfer der Seuche. Unser Besitz ebenfalls dahin, während des Kriegs geplündert und niedergebrannt. Und nach alledem haben sie und ihre lausigen Kinder die Stirn, das einzig Kostbare zu stehlen, das ich noch hatte.","Mir sind die Sachen, die sie gestohlen haben, längst einerlei. Ich möchte ihre Köpfe!","Tötet sie für mich! Geht zum Bauernhof der Dabyries in Arathi, nordöstlich der Zuflucht."}, {"Bringt Kenata, Fardel und Marcel Dabyries Köpfe zu Genavie Callow in Undercity."}},
[1166] = {"Oberanführer Mok'Morokks Sorgen", {"Verbeugen Euch vor Oberanführer Mok'Morokk!","Rexxar mich machen zu mächtigem Anführer von Stonemaulklan!","Ich gut Anführer. Ich Klan sicher machen.","Große Drachen kommen und machen Feuer in alte Heimat. Ich klug Oger. Finden neu Heimat für Klan hier.","Alte Heimat so schnell verlassen, ich Sachen vergessen. Winzig <Volk>, gehen mein Sachen holen. Gehen nach Süden und suchen mein lecker Grog, mein Schnupftabak und mein Geldkassette. Ich Euch mögen, wenn Ihr das tun. Ihr in Flammenbau und Stonemaul Ruinen schauen... sie dort sein, ich sicher!"}, {"Oberanführer Mok'Morokk in Brackenwall möchte, dass Ihr seinen Grog, seinen Schnupftabak und seine Geldkassette aus dem Dustwallowsumpf holt."}},
[1167] = {"Der Turm von Althalaxx", {"Bevor Ihr Athrikus konfrontiert, wäre es vermutlich klug, Balthule zu konsultieren. Zweifellos ist er wachsam in seinem Wachturm gewesen und könnte Informationen über jüngste Entwicklungen haben."}, {"Sprecht mit Balthule Shadowstrike beim Turm von Althalaxx."}},
[1168] = {"Armee des schwarzen Großdrachen", {"Mok'Morokk wurde von Rexxar zum Oberanführer gemacht. Ihm liegt mehr an Macht als an den Ogern.","Schwarze Großdrachen töten viele Oger. Töten Thargs Frau. Mok'Morokk sagt uns, wir müssen fliehen. Lässt uns hierher kommen.","Oger sollten kämpfen. Oger nicht gut im Weglaufen. Mok'Morokk schert sich nur um Mok'Morokk.","Für mich Stonemaul Ruinen Heimat. Mir gefallen im Süden. Dieser Ort nicht Heimat.","Helft Ogern, Armee der schwarzen Großdrachen zu bekämpfen. Ich wollen Rache. Ich wollen nach Hause."}, {"Tharg in Brackenwall möchte, dass Ihr 15 Späher der Feuermähnen, 10 Aschenschweife der Feuermähnen und 5 Bannschuppen der Feuermähnen tötet."}},
[1169] = {"Die Brut identifizieren", {"Während der törichte Narr Mok'Morokk seine Macht genießt und Tharg mit den Dämonen des Kummers und der Rache ringt und um die Führerschaft des Klans buhlt, scheine ich der Einzige zu sein, der die Ursache der Aggression in unserer alten Heimat herausfinden möchte.","Warum der verwirrte Blick, <Name>? Erwartet Ihr, dass ich wie ein tumber Grobian spreche, nur weil ich ein Oger bin?","Doch zurück zum Geschäft, bringt mir die Herzen und Zungen der Welpen und Jungtiere. Ich werde die Wurzel dieses Übels finden..."}, {"Draz'Zilb in Brackenwall hätte gern, dass Ihr ihm 15 sengende Zungen und 15 sengende Herzen bringt."}},
[1170] = {"Die Brut von Onyxia", {"Das Dorf Stonemaul wurde von der Brut von Onyxia überrannt. Aber warum sollte die Tochter des Schwarzen-Großdrache-Lords Deathwing über unser Land herfallen?","Das ist sehr beunruhigend. Gewiss wurde Onyxia aus einem anderen Grund hergeschickt, als eine kleine Schar von Ogern zu belagern.","Verständigt unverzüglich Mok'Morokk! Es muss gehandelt werden."}, {"Sprecht mit Oberanführer Mok'Morokk in Brackenwall."}},
[1171] = {"Die Brut von Onyxia", {"Ihr lasst mich jetzt in Ruhe. Sagt Draz'Zilb, wir bleiben hier. Keine schwarzen Großdrachen hier."}, {"Sprecht mit Draz'Zilb in Brackenwall."}},
[1172] = {"Die Brut von Onyxia", {"Onyxias Brut wurde über das Drachendüster verstreut. Es dürfen auf keinen Fall weitere Welpen schlüpfen.","Begebt Euch eilig zum Drachensumpf in den Marschen von Dustwallow. Gewiss hat sie dort einen Hort angelegt. Sucht die Eier der bösen Großdrachen und vernichtet sie.","Wir können das Dorf Stonemaul nie zurückerobern, wenn das Umland eine Brutstätte bleibt.","Was Mok'Morokk angeht ... Für den Jammerlappen habe ich andere Pläne."}, {"Draz'Zilb in Brackenwall möchte, dass Ihr 5 Eier von Onyxia zerstört."}},
[1173] = {"Fordert Oberanführer Mok'Morokk heraus", {"Ihr wagt es, Oberanführer Mok'Morokk herauszufordern?","Ha!","Ich zerschmettern einen Winzling von <Volk>!","Ihr mir sagen, wenn bereit für ordentliche Tracht Prügel."}, {"Besiegt Mok'Morokk und bringt die Nachricht zu Draz'Zilb in Brackenwall."}},
[1175] = {"Eine Straßenunebenheit", {"Nach dem schweren Zusammenstoß des Red-Thunder-Rennwagen der Goblins und einem Basilisken hat Kronkrider mich angeheuert, um die Strecke zu warten. Keine Ahnung, was die Tiere in der Ebene fressen, dass sie so groß werden, aber wir müssen sie dezimieren, damit sie nicht auf die Fahrbahn laufen, wenn ein Rennen stattfindet.","Ich sag Euch was. Wenn ihr ein wenig Profit machen wollt, zieht los und entfernt die Basilisken aus der Gegend."}, {"Tötet 10 Salzsteinbasilisken, 10 Salzsteinkristallhäute und 6 Salzsteinstarrer für Rennsteckenleiter Zherin in der schimmernden Ebene."}},
[1176] = {"Gewicht reduzieren", {"Wenn es um Rennen geht, zählt jede Kleinigkeit! Ob es sich nun um ein leises Quietschen der Kraftübertragung, eine geringfügige Schwäche der Stoßdämpfer oder ein leichteres Gewicht der Karosserie handelt, man muss alles berücksichtigen.","Wir benutzen Geierknochen für kleinere Modifikationen am Chassis unserer Rennwagen, die das perfekte Gleichgewicht zwischen Stärke und Gewicht haben. Und sie sind, was noch besser ist, leicht zu beschaffen, da es in der Salzwüste von Geiern wimmelt.","Bringt mir ein paar und ich zahle einen guten Preis dafür."}, {"Beschafft 10 hohle Geierknochen für Pozzik in der schimmernden Ebene."}},
[1177] = {"Hunger!", {"Mud großer Oger.","Ich hungrig.","Geht Fische hauen. Bringt Mud Essen.","Ihr füttert Mud oder Mud frisst Euch."}, {"Mudcrush Durtfeet im nördlichen Dustwallow möchte 12 Schlammflossenköpfe."}},
[1178] = {"Goblin-Sponsoring", {"Unser Rennfahrer-Team ist der Stolz aller Goblins. Ihr wärt überrascht, welche Leute unser Team auf die eine oder andere Weise unterstützen.","Kennt Ihr Gazlowe? Er treibt auf der ganzen Welt Ersatzteile für uns auf. Er kam sogar mal her, um an einer unserer Aufhängungen zu arbeiten!","Vor ein paar Wochen wollte er uns den Prototyp einer neuen Treibstoffeinspritzpumpe schicken, aber sie ist noch nicht eingetroffen. Sonst ist er immer zuverlässig ... Würde es Euch vielleicht etwas ausmachen, nach Ratchet zu gehen und ihn danach zu fragen? Ich will ja nicht drängen, aber die Gnome holen langsam auf!"}, {"Sprecht mit Gazlowe in Ratchet."}},
[1179] = {"Die Brüder Brassbolt", {"Schon von der schimmernden Ebene gehört, ja? Das ist eine Salzwüste drüben in Kalimdor, direkt südlich vom Canyon der Tausend Nadeln. Der ideale Austragungsort für Rennen, weil es da so flach ist!","Da draußen sind zwei Gnomenbrüder, Freunde von mir, die ihren neuen Raketenwagen erproben, und soweit ich gehört habe, ist er schnell! Hey, so schnell, dass die Fahrer immer verunglücken. Und Gehirnerschütterungen bekommen.","Darum baten sie mich um ein paar Helme für Belagerungsmaschinen, aber ich kann hier nicht weg, meine Pflicht fesselt mich an diesen Ort. Ich kann die Reise also nicht machen!","Könnt Ihr es?"}, {"Bringt die Kiste mit Schutzhelmen zu Wizzle Brassbolts in der schimmernden Ebene."}},
[1180] = {"Goblin-Sponsoring", {"Zurück zum Grund Eures Hierseins. Ich weiß, ich habe Pozzik versprochen, dass ich ihm eine neue Treibstoffeinspritzpumpe schicke, aber ich habe sie selbst noch nicht bekommen. Revilgaz drüben in Booty Bay sagte, er würde sie herschicken, aber irgendwie scheint da was schief gelaufen zu sein.","Wenn das stimmt, können wir wenig dagegen tun. Aber wir können wenigstens herausfinden, wann sie von Booty Bay abgeschickt wurde.","Fragt drüben bei Werftmeister Lozgil nach, er müsste die Frachtbriefe überprüfen können."}, {"Sprecht mit Werftmeister Lozgil in Booty Bay."}},
[1181] = {"Goblin-Sponsoring", {"Ich fürchte, ich kann Euch nicht weiterhelfen, <Name>. Wenn Ihr der Sache weiter nachgehen möchtet, würde ich vorschlagen, Ihr geht zum Baron persönlich. Wenn jemand über die Geschäfte von Revilgaz Bescheid weiß, dann Revilgaz selbst.","Ihr findet ihn in der Taverne jenseits des Hafens, Obergeschoss."}, {"Sprecht mit Baron Revilgaz in Booty Bay."}},
[1182] = {"Goblin-Sponsoring", {"Wisst Ihr, die Schredder der Venture Company draußen am Nazferitisee (im Norden des Schlingendorntals) wurden mit einer neuartigen Treibstoffeinspritzpumpe ausgerüstet. Ich dachte mir, Pozzik könnte Kapital daraus schlagen, wenn er einige Einzelheiten erfahren könnte. Leider scheint Cozzle, der dortige Großknecht, etwas schlauer zu sein als ich ihm zugestehen wollte.","Aber Ihr seht aus, als könntet Ihr es schaffen, die Blaupausen zu finden. Ich weiß, dass er sie in seinem Haus aufbewahrt, aber mit Sicherheit verschlossen und verriegelt."}, {"Bringt die Treibstoffreglerblaupausen zu Baron Revilgaz in Booty Bay."}},
[1183] = {"Goblin-Sponsoring", {"Ihr solltet zum Aufbruch bereit sein. Bestellt Pozzik schöne Grüße und sagt ihm, wenn er etwas braucht, soll er keine Hemmungen haben, zu fragen. Ich warte auf die baldige Nachricht, dass er den Streckenrekord gebrochen hat!"}, {"Bringt die Blaupausen der Treibstoffeinspritzpumpe zu Pozzik in der schimmernden Ebene."}},
[1184] = {"Teile des Schwarms", {"Geht zurück zu Belgrom in Orgrimmar und sagt ihm, dass Moktar in Tausend Nadeln ganz in seinem Sinne handelt. Sagt ihm auch, dass meine Studien voranschreiten und ich hoffe, dass ich bald mehr gelernt habe. Das sollte ihn freuen."}, {"Kehrt zu Belgrom Rockmaul in Orgrimmar zurück."}},
[1185] = {"Unter dem Chitin war...", nil, nil},
[1186] = {"Der achtzehnte Rennfahrer", {"Ich werde damit beschäftigt sein, diese neue Treibstoffeinspritzpumpe in unsere Rennwagen einzubauen, aber wenn Ihr dem Team noch weiter helfen möchtet, sprecht mit Razzeric. Er ist unser Fahrer - unser achtzehnter Fahrer, um genau zu sein -, und er nimmt selbst gern Veränderungen an den Rennwagen vor.","Aber er ist ein wenig daneben, bemüht Euch daher, nicht zu überrascht zu wirken."}, {"Sprecht mit Razzeric in der schimmernden Ebene."}},
[1187] = {"Razzerics Basteln", {"Pozzik ist ein toller Mechaniker, das weiß ich, aber er hat Angst davor, einfach was mit viel Power einzubauen und dann loszudüsen. Aber ich nicht.","Wenn Pozzik mit einem Rennwagen fertig ist, nehme ich mir die Zeit und baue eigene Veränderungen ein - Ihr wisst schon, die Steuerung lockern, Teile des Rahmens entfernen und ihm mehr Saft geben!","Ich habe einen Zephyriumnachbrenner von Shreev im Gizmorium in Gadgetzan bestellt, aber offenbar musste der Zeppelin, mit dem er geschickt wurde, in den Marschen von Dustwallow notlanden! Ich muss das Ding haben, <Name>!"}, {"Bergt den Zephyriumnachbrenner für Razzeric in der schimmernden Ebene."}},
[1188] = {"Sicherheit zuerst", {"Doch wartet, was ist das? Shreev hat die Sicherung aktiviert gelassen. Das reduziert die Menge Energie, die ich aus dem Nachbrenner rausholen kann. Ich sagte ihm, ich wollte sie deaktiviert haben! Das ist nicht gut.","Ihr müsst das ins Gadgetzan-Gizmorium in der Tanariswüste bringen und es von Shreev entfernen lassen."}, {"Bringt den Zephyriumnachbrenner zu Shreev in Gadgetzan."}},
[1189] = {"Sicherheit zuerst", {"So, ich habe getan, was Razzeric wollte. Sagt ihm nur, er soll vorsichtig sein, wenn er das zündet. Wenn er den kleinsten Fehler macht, fährt er nicht los, sondern ist nur noch ein Brandfleck auf der Rennstrecke.","Wäre doch ein Jammer, wenn das passiert. Wer weiß, ob unser neunzehnter Fahrer genauso gut wäre?"}, {"Bringt den umgebauten Zephyriumnachbrenner zu Razzeric in der schimmernden Ebene."}},
[1190] = {"Tempo halten", {"Die Brassbolts-Brüder haben einen neuen Baustoff entdeckt, das ist die einzig logische Erklärung. Ihre Rennwagen fahren schneller als vorher, aber Design und alle anderen Daten ihrer Probefahrten sind noch dieselben.","Wir wissen, dass Rizzle Brassbolts sich fast ununterbrochen mit den Plänen seiner neuen Entdeckung befasst hat.","Zamek kann ein Ablenkungsmanöver inszenieren, damit Ihr diese Pläne von ihnen stehlen könnt."}, {"Lasst Zamek ein Ablenkungsmanöver inszenieren und stehlt dann Rizzles Pläne für Pozzik in der schimmernden Ebene."}},
[1191] = {"Zameks Ablenkungsmanöver", nil, nil},
[1192] = {"Induriumerz", nil, nil},
[1193] = {"Die beschädigte Falle", nil, nil},
[1194] = {"Rizzles Baupläne", {"Rizzles Baupläne beschreiben den Vorgang der Veredelung von Induriumerz. Man kann dem Gekritzel entnehmen, dass Induriumerz an der Ausgrabungsstätte Uldaman der Zwerge im Ödland zu finden ist.","Die anderen Notizen behandeln fast ausschließlich seinen Hitzewiderstand.","Als Briefbeschwerer der Pläne dient ein Klumpen, der wie unbearbeitetes Induriumerz aussieht."}, {"Bringt die Induriumerzprobe zu Pozzik in der schimmernden Ebene."}},
[1195] = {"Die heilige Flamme", {"Die Galakzentauren von Tausend Nadeln beschützen ein Artefakt aus der Zeit der Erschaffung der Zentauren.","Wir würden es gern bergen, benötigen aber eine Phiole Wasser aus einem der Mondbrunnen der Nachtelfen.","Um dieses Wasser zu beschaffen, müsst Ihr zuerst eine Phiole besorgen, wie sie die Dryaden beim Raynewood im Herzen von Ashenvale besitzen. Am Westufer des Falfarren, südlich der Hauptstraße, befindet sich ein Mondbrunnen, in dem Ihr die Phiole füllen könnt."}, {"Bringt eine gefüllte geätzte Phiole zu Zangen Stonehoof in Thunder Bluff."}},
[1196] = {"Die heilige Flamme", {"Bringt die gefüllte Phiole zu Rau Cliffrunner beim Freiwindposten im Südosten von Tausend Nadeln. Ihr gelangt nach Tausend Nadeln, wenn Ihr den großen Aufzug am Südrand des Brachlandes benutzt.","Ich wäre nicht überrascht, wenn Ihr uns weiter behilflich sein könntet."}, {"Bringt die gefüllte geätzte Phiole zu Rau Cliffrunner im Freiwindposten."}},
[1197] = {"Die heilige Flamme", {"Wenn Ihr das Wasser benutzt, das Ihr mitgebracht habt, könnt Ihr vorübergehend die Flamme löschen, die das Zentaurenrelikt schützt. Aber der Effekt ist nicht von Dauer, daher müsst Ihr rasch handeln.","Ihr findet die Spalthufklippe, die Festung der Zentauren, nördlich von hier. Die Flamme wird in der Spalthufhöhle bewahrt, einem schwer bewachten Teil der Klippe.","Aber seid vorsichtig, die Zentauren der Galak sind dort in großer Zahl zugegen und dürften etwas, das ihnen so teuer ist, nicht freiwillig hergeben."}, {"Holt den gespaltenen Huf für Rau Cliffrunner im Freiwindposten."}},
[1198] = {"Auf der Suche nach Thaelrid", {"Heutzutage kursieren viele Gerüchte über die Vereinigung der Twilight-Hammer und Naga in der Blackfathom-Tiefe an den Küsten von Ashenvale.","Meine Rolle hier bei der Argentumdämmerung besteht darin, so viele Informationen wie möglich zu sammeln, damit wir wissen, wann und wo Hilfe erforderlich ist.","Es ist jetzt Wochen her, seit ich einen Späher losschickte, um die Aktivitäten in der Blackfathom-Tiefe auszuspähen. Argentumwache Thaelrid hat sich noch nicht zurückgemeldet.","Meine anderen Späher sind mit dringenden Angelegenheiten beschäftigt.","Sucht und unterstützt Thaelrid für mich, <Name>."}, {"Sucht Argentumwache Thaelrid in der Blackfathom-Tiefe auf."}},
[1199] = {"Die Twilight fallen", {"Mit der Macht der uralten Rituale der chaotischen Alten Götter versuchen die Mitglieder der Twilight-Hammer, ihre dunklen Schutzherren zurückzuholen und Tod und Schrecken in der Welt zu verbreiten.","Wir glauben, dass die Twilight-Hammer sich mit den Naga verbündet haben und sich in der Blackfathom-Tiefe an den Küsten von Ashenvale zusammenrotten.","Welch schändliche Pläne in den Tiefen des alten Tempels auch immer geschmiedet werden, wir müssen sie vereiteln.","Führt Krieg gegen die Hammer und bringt Beweise Eurer Taten mit. Eure Bemühungen sollen reich belohnt werden."}, {"Bringt 10 Twilight-Anhänger zu Argentumwache Manados in Darnassus"}},
[1200] = {"Schurkerei in Blackfathom", {"Die Kraft hat mich verlassen. Ich benötige Eure Hilfe!","Diese Stätte war einst ein großer Tempel von Elune. Doch das Unglück führte zum Untergang, als die Verderbnis eines Alten Gottes unter der Erde emporwallte und den heiligen Mondbrunnen besudelte.","Aku'Mai, die Dienerin der Alten Götter, erhob sich aus den Wassern.","Die Kultisten der Twilight-Hammer haben sich mit den Naga verschworen, um diese Ländereien zu besetzen. Die Kultisten opfern unter Führung von Kelris Unschuldige für Aku'Mai, um Macht zu erlangen.","Bitte, erschlagt Kelris und bringt Selgorm in Darnassus seinen Kopf, <Name>."}, {"Bringt den Kopf des Twilight-Lords Kelris zu Dämmerungsbehüter Selgorm in Darnassus."}},
[1201] = {"Theramores Spione", {"Die Horde und die Allianz wahren einen brüchigen Waffenstillstand. Beide Seiten liefern sich Scharmützel und heimliche Angriffe. Wir müssen dafür sorgen, dass die Horde stets die Oberhand behält!","Jetzt haben wir die Chance, genau das zu tun.","Spitzel der Allianz haben Stellungen östlich des Dorfs Brackenwall bezogen. Sie spionieren das Dorf aus und melden unsere Bewegungen Theramore, was wir nicht dulden können.","Jagt die Spitzel und schaltet sie aus. Schickt eine deutliche Botschaft an ihre Vorgesetzten, dass wir die Anwesenheit ihrer Spione nicht dulden!"}, {"Tötet 9 Spitzel von Theramore und kehrt dann zu Nazeer Bloodpike in Brackenwall zurück."}},
[1202] = {"Die Docks von Theramore", {"<Name>, zwei unserer Spione wurden ausgeschickt, um Dokumente von einem Schiff bei den Docks von Theramore zu stehlen. Sie bekamen die Papiere, wurden aber entdeckt und waren gezwungen, die Dokumente ins Meer zu werfen.","Später konnte ein Spion entkommen, die Beute aber nicht bergen. Er informierte mich über sein Scheitern und jetzt gebe ich Euch die Aufgabe.","Die Dokumente sind in einer Schließkiste irgendwo im Wasser unter den Docks von Theramore. Sucht diese Dokumente, <Name>, und bringt sie mir."}, {"Bringt die Dokumente des Kapitäns zu Nazeer Bloodpike in Brackenwall."}},
[1203] = {"Jarl braucht eine Klinge", {"Ich habe zu schneiden. Ich kann Euch nicht sagen, was ich schneiden werde, aber... Ich brauche eine Klinge. Eine ganz bestimmte Klinge...","Manche Leute sprechen von einem Mondstahlbreitschwert. Ich nenne es eine Schwingklinge.","Helft Ihr mir? Vielleicht findet Ihr sie in der Stadt, vielleicht sprecht Ihr mit einem Schmied und er macht sie eigens.","Aber schlaft nicht noch darüber. Was ich schneide, das hält nicht ewig..."}, {"Bringt ein Mondstahlbreitschwert zu Jarl in den Marschen von Dustwallow."}},
[1204] = {"Schlammpanzersuppe mit Käfern", {"Ich arbeite an einem Gericht namens \"Schlammpanzersuppe mit Käfern\". Die wichtigsten Zutaten fehlen mir leider noch immer... aber ich glaube nicht, dass ich einen Fußmarsch durch die Dustwallow-Marschen durchhalten könnte.","Nun denn, <Name>, könntet Ihr für mich auf die Jagd gehen? Als Erstes brauche ich die Zungen von Schlammpanzerschildkröten. Ihr findet die Schlammpanzer entlang der Küste, hauptsächlich im Bereich der Bucht.","Aber ich will nicht jede beliebige Zunge! Ich habe gehört, dass die Zungen von einigen Schlammpanzerschildkröten gespalten sind...","Man sagt, diese gespaltenen Zungen hätten einen charakteristischen Geschmack, und den möchte ich in meinem Gericht..."}, {"Bringt Morgan Stern in Theramore 8 gespaltene Schlammpanzerzungen."}},
[1205] = {"Schlammwühler", {"Schlammwühler ist ein uralter Krokilisk in den Marschen von Dustwallow. Und seine Zeit zum Sterben ist gekommen.","Alte Knochen knirschen, wenn er seinen gewaltigen Leib durch den Sumpf schleppt, und auch wenn er sich noch mit der Kraft und dem Tempo der Jugend bewegt, martert sein alter Körper den großen innewohnenden Geist, ein Geist, dessen Flamme nicht erlischt.","Heutzutage jedoch leidet er unablässig qualvolle Schmerzen. Ihr müsst das Leben dieser edlen Kreatur beenden, <Name>. Ihr müsst Schlammwühler Frieden geben."}, {"Bringt Schlammwühlers Zahn zu Melor in Thunder Bluff."}},
[1206] = {"Jarl braucht Augen", {"Augensaft ist ein guter Saft zum Trinken. Leider haben die meisten Tierwesen nur zwei Augen.","Aber Spinnen haben eine Menge Augen. Jede Menge. Und Graunebelaugensaft ist lecker. Samtig und salzig! Unzerplatzte Spinnenaugen... Ich schmecke sie schon förmlich.","Graunebelspinnen leben westlich von hier in den Graunebelhöhlen. Glaubt Ihr, die Spinnen wurden nach der Höhle benannt... oder ist es umgekehrt?"}, {"Bringt 40 unzerplatzte Graunebelaugen zu \"Sumpfauge\" Jarl im Swamplight-Anwesen."}},
[1218] = {"Feine Gewürze", {"Ich koche gern. Leckere Sumpfküche. Frittierte grüne Froschschenkel und dergleichen.","Und zum Kochen brauch ich Gewürze. Feine Gewürze. Dann mache ich einen Eintopf und topfe ihn ein und schlabber ihn aus und geb Euch auch was ab.","Mögt Ihr Eintopf?"}, {"Bringt 3 feine Gewürze zu \"Sumpfauge\" Jarl in den Dustwallow-Marschen."}},
[1219] = {"Der Orc-Bericht", {"Beim Durchsuchen der lockeren Erde wird die abgetrennte Hand eines Orcs gefunden. Die Hand hält eine aus Knochen geschnitzte Röhre umklammert. Und in der Röhre befindet sich ein fleckiges Pergament.","Das Pergament ist in der Sprache der Orcs verfasst... und sieht wichtig aus."}, {"Bringt den Bericht des Spions zu einem Lieutenant in Theramore."}},
[1220] = {"Captain Mumm", {"Captain Mumm sollte den Orcbericht sehen, den Ihr gefunden habt, <Name>. Er kennt die Sprache der Orcs besser als die meisten. Und er sollte wissen, dass sich Spione der Horde bei Theramore aufhalten!","Der Captain befindet sich in der Kaserne auf der nordöstlichen Seite der Insel Theramore."}, {"Bringt den Bericht des Spions zu Captain Garran Mumm."}},
[1221] = {"Blaulaubknollen", {"Blaulaubknollen gelten auf der ganzen Welt als Delikatesse! Aber sie sind rar, sehr rar. Man findet sie nur hier im Brachland, tief in der Erde des Krals von Razorfen.","Und selbst dann sind sie fast nicht zu finden, wenn man nicht weiß, wo man suchen muss! Darum habe ich diese Schnüffelnasenziesel abgerichtet, damit sie sie für mich aufstöbern. Sie haben unheimlich empfindliche Nasen und riechen eine Knolle aus fünfzig Schritt Entfernung.","Es wird nicht einfach sein, aber wenn Ihr mir ein paar Knollen bringt, bezahle ich Euch gut dafür."}, {"Schnappt Euch eine Kiste mit Löchern.","Schnappt Euch einen Schnüffelnasenleitstecken.","Schnappt Euch das Handbuch für Schnüffelnasenbesitzer und lest es.","Benutzt im Kral von Razorfen die Kiste mit Löchern, um ein Schnüffelnasenziesel zu beschwören, und benutzt den Leitstecken bei dem Ziesel, damit es nach Knollen sucht.","Bringt 6 Blaulaubknollen, den Schnüffelnasenleitstecken und die Kiste mit Löchern zu Mebok Mizzyrix in Ratchet."}},
[1222] = {"Stinkis Flucht", {"Klasse, endlich etwas, das mich nicht essen möchte!","Ich brauche Hilfe mit den Tierwesen hier, und Ihr kommt gerade recht, <Jungchen/Lady>.","Der Koch Morgan Stern arbeitet an einem neuen Rezept und hat mich hierher geschickt, um Sumpfbohnenblätter zu sammeln. Ich glaube, er findet sie lecker.","Wie auch immer, während ich nach einer Sumpfbohnenpflanze suchte ... Wurde ich umzingelt! Könnt Ihr mir hier raus helfen und auf mich aufpassen, während ich nach den Sumpfbohnen suche?"}, {"Begleitet \"Stinki\" Ignatz und sprecht dann mit Morgan Stern in Theramore."}},
[1238] = {"Der verlorene Bericht", {"Beim Durchsuchen der lockeren Erde wird die abgetrennte Hand eines Orcs gefunden. Die Hand hält eine aus Knochen geschnitzte Röhre umklammert. Und in der Röhre befindet sich ein fleckiges Pergament.","Das Pergament ist ein Bericht von einem Orc, der die Stadt Theramore über Wochen ausspionierte.","Als Ihr das Pergament überflogen habt, ist klar, dass es zum Orc-Spionagemeister in Brackenwall gebracht werden muss."}, {"Bringt den Bericht des Spions zu Nazeer Bloodpike in Brackenwall."}},
[1239] = {"Der abgetrennte Kopf", {"Ausgiebiges Graben fördert den Kopf eines männlichen Orcs zu Tage, der am Hals abgesägt worden war. Seine Lippen sind zu einer höhnischen Grimasse verzerrt, seine dunklen Augen blicken trotzig."}, {"Bringt den Kopf des aufsässigen Orcs zu Nazeer Bloodpike in Brackenwall."}},
[1240] = {"Der Troll-Hexendoktor", {"Wir müssen die Umstände von Margs Tod erfahren! Und das können wir nur, <Name>, durch die Hexenmagie der Trolle.","Bringt Margs Kopf zu Kin'weelay im Schlingendorntal im Basislager von Grom'gol.","Kin'weelay ist der mächtigste Hexendoktor der Darkspeartrolle. Wenn jemand mit Marg Nighteyes Geist sprechen kann, dann er."}, {"Bringt den Kopf des aufsässigen Orcs zu Kin'weelay im Schlingendorntal im Basislager von Grom'gol."}},
[1241] = {"Der vermisste Diplomat", {"Kürzlich wurde ein Diplomat nach Theramore geschickt, um sich mit Jaina Proudmoore zu treffen. Dieser Diplomat kam nie an.","Ich glaube, die Defias sind in diese Verschwörung verwickelt, weiß aber nicht, wie. Das Verschwinden des Diplomaten wurde noch nicht publik, aber lange kann das nicht mehr so bleiben.","Agenten des Königs durchkämmen die Stadt bereits nach Hinweisen, aber ich habe eigene Verbindungsleute, die ich gern einschalten würde.","Im Tal der Helden lebt ein alter Freund von mir namens Jorgen. Sucht ihn, gebt ihm diese Notiz und folgt seinen Anweisungen."}, {"Sucht Jorgen in der Stadt Stormwind."}},
[1242] = {"Der vermisste Diplomat", {"Bringt diese Notiz zu Elling Trias. Sagt sonst nichts zu ihm. Seid nur höflich und wartet auf seine Antwort. Und keine Bange, er wird antworten.","Ich sage Euch das nur noch einmal, <Name>: nehmt diese Sache ernst und haltet die Klappe.","Ihr findet Trias in seinem Käseladen, Trias' Käse, im Handelsdistrikt. Viel Glück."}, {"Bringt Jorgens versiegelte Notiz zu Elling Trias in der Stadt Stormwind."}},
[1243] = {"Der vermisste Diplomat", {"Jorgen, alter Pickel! Aha, so soll es also sein, hm?","Na gut, damit kann ich leben. Ich hoffe, er findet den wirbelnden Nether umso früher!","So wird es ablaufen, <Name>. Ich werde meine Verbindungsleute hier kontaktieren, aber Ihr werdet den Großteil der Beinarbeit erledigen, damit ich ein wenig spare.","Geht nach Dunkelhain im Dämmerwald und sucht Behüter Backus - er patrouilliert meist auf der Nordstraße vor der Stadt. Sagt ihm nur, dass Ihr für mich die Aktivitäten der Defias untersucht, dann wird er Euch mit allen verfügbaren Informationen weiterhelfen."}, {"Gebt Behüter Backus im Dämmerwald die versiegelte Notiz."}},
[1244] = {"Der vermisste Diplomat", {"Defiasaktivität? Es gibt immer etwas Aktivität; selbst wenn sie unter sich bleiben, betrachten wir sie als Bedrohung.","Aber jetzt, wo ich darüber nachdenke, kam es jüngst zu einer seltsamen Beobachtung. Es muss so zwei Wochen her sein, da hatten sich einige ihrer Agenten in Addles Siedlung versammelt. Dem Bericht glaubte ich zu entnehmen, dass etwas Großes vor sich ging.","Warum seht Ihr da nicht zuerst nach und bringt mir alles, was Ihr findet?","Der Bauernhof liegt südlich der Straße von Westfall direkt am Rand des Dämmerwalds."}, {"Sucht die Liste der Defias und kehrt zu Behüter Backus im Dämmerwald zurück."}},
[1245] = {"Der vermisste Diplomat", {"Boah, das sieht zu komplex aus, um Euch nur auf eine falsche Fährte zu locken. Ich sollte das nicht einmal ansehen. Sprecht mit Trias - er kann es bestimmt verstehen. Und lasst Euch durch NICHTS aufhalten. Dies ist größer als ich. Vielleicht sogar größer als er, aber wir werden sehen.","Und tut mir einen Gefallen, <Name>, sagt keinem, dass ich Euch geholfen habe. Wenn meine Vorgesetzten erfahren, dass ich davon wusste und sie nicht informiert habe, hängen sie mich wahrscheinlich."}, {"Bringt die Liste der Defias zu Elling Trias in Stormwind."}},
[1246] = {"Der vermisste Diplomat", {"Ja, jetzt, wo ich darüber nachdenke... Dashel... Dashel irgendwas... Wie war sein Name? FIST! Dashel Stonefist. In manchen Kreisen nennen sie ihn Fist - wahrscheinlich, weil er ein Raufbold ist.","Ihr könnt ihn normalerweise in der Altstadt finden - direkt mitten im Zentrum. Redet mit ihm. Wenn es sein muss, dann benutzt ein Schwert oder etwas Schweres und Stumpfes, um ihn zum Reden zu bringen. Und zögert nicht. Wenn er so tief drinsteckt, wie diese Papiere andeuten, wird er Euch freiwillig keine Informationen geben."}, {"Sprecht mit Dashel Stonefist in Stormwind."}},
[1247] = {"Der vermisste Diplomat", {"Ja, also... hm... Vielleicht weiß ich was über dieses Treffen in Addles Siedlung. Das war, glaube ich, vor ein paar Wochen. Ich hatte wirklich nichts damit zu tun. Meine Rolle war vorbei, als Plan A schief ging... verdammte Abenteurer.","Der Kerl, den wir für Plan B hatten, kam aus Menethil; ein Typ namens Slim. Mehr weiß ich nicht über ihren Ersatzplan."}, {"Sprecht mit Elling Trias in Stormwind."}},
[1248] = {"Der vermisste Diplomat", {"Ein Verbindungsmann von mir verbringt seinen Ruhestand in der Hafenstadt Menethil im Sumpfland. Er sollte Euch auf Euer Wort hin helfen, wenn Ihr ihm sagt, dass Ihr für mich arbeitet. Sein Name ist Mikhail. Bevor er Stormwind vor ein paar Jahren verließ, erwähnte er, dass er Schankkellner werden wollte.","Sprecht mit ihm, sucht diesen Slim und findet heraus, ob es noch etwas über diesen entführten Diplomaten zu wissen gibt. Wir müssen herausfinden, wohin sie ihn gebracht haben, oder wenigstens, wer er ist. In der Liste steht nichts von ihren Plänen nach seiner Entführung."}, {"Sprecht mit Mikhail im Sumpfland."}},
[1249] = {"Der vermisste Diplomat", {"Ihr sagt, Elling hat Euch geschickt? Dann soll Euch meine Gastfreundschaft zehnfach zuteil werden. Wenn Ihr etwas braucht, fragt nur. Ich tue mein Bestes, Euch zu unterstützen.","Hmm, Slim sagt Ihr? Bei dem Namen klingelt nichts, aber ich werde nach ihm Ausschau halten.","Schaut jetzt nicht hin, <Name>, aber der Herr dort am Ausgang sieht herüber. Er folgt unserer Unterhaltung etwas zu intensiv, wenn Ihr versteht, was ich meine. Ich an Eurer Stelle würde tun, was ich kann, um ihn zu schnappen, bevor er zur Tür flieht."}, {"Schnappt Tapoke Jahn bevor er entkommt und kehrt dann zu Mikhail in Menethil zurück."}},
[1250] = {"Der vermisste Diplomat", {"Die Defias zahlten mir nicht genug, um für diese Sache zu sterben. Sie entführten den König. Ja, den König von Stormwind, keinen kleinen Diplomaten. Er war auf dem Weg nach Theramore, um sich mit Jaina Proudmoore zu treffen - offenbar eine große Sache und streng geheim. Aber es sind nicht nur die Defias darin verwickelt. Sie arbeiten mit jemandem zusammen. Ich glaube, sie wussten, dass das ihre Kräfte übersteigt.","Meine Aufgabe war es, ihren Mann an Bord des königlichen Schiffes zu bringen, als es im Hafen lag. Der Typ hieß Hendel. Mehr weiß ich nicht. Ich schwöre es!"}, {"Sprecht mit Mikhail im Sumpfland."}},
[1251] = {"Der Schwarze Schild", {"Ein Eisenschild, vom Feuer geschwärzt, das in dem Gasthaus wütete, ist an dem verfallenen Kamin befestigt, einer der wenigen erhaltenen Teile des Gebäudes, das einmal das Gasthaus Zur süßen Ruh war.","Der Schild lässt sich von den Mauersteinen des Kamins ablösen."}, {"Bringt den geschwärzten Eisenschild zu Krog in Brackenwall."}},
[1252] = {"Lieutenant Paval Reethe", {"Unter dem Geröll sticht Euch ein Funkeln ins Auge. Kaum sind Schmutz und Asche von dem Objekt gewischt, kommt das Emblem eines goldenen Ankers auf einer weißen Emailleoberfläche zum Vorschein: das Zeichen von Theramore.","Unter dem Anker ist der Name \"Lieutenant Paval Reethe\" eingeprägt."}, {"Bringt Reethes Abzeichen zu Captain Garran Mumm in Theramore."}},
[1253] = {"Der Schwarze Schild", {"Ein Eisenschild, vom Feuer geschwärzt, das in dem Gasthaus wütete, ist an dem verfallenen Kamin befestigt, einer der wenigen erhaltenen Teile des Gebäudes, das einmal das Gasthaus Zur süßen Ruh war.","Der Schild lässt sich von den Mauersteinen des Kamins ablösen."}, {"Bringt den geschwärzten Eisenschild zu Captain Garran Mumm in Theramore."}},
[1258] = {"...und Käfer", {"Eine weitere Komponente meines neuen Rezepts ist das gepanzerte Bein eines Riesenkrebses. Und die Krebse, die sie in Dustwallow haben - Matschpanzer nennen sie sie -, taugen einfach nichts! Ich fürchte, die einzigen akzeptablen Krebse leben in den Sümpfen des Elends.","Die Schale des Krebsbeins ist das wichtigste Element der Präsentation des Gerichts, daher brauche ich makellose, unverletzte Beine. Ein ausgewachsener Krebs hat möglicherweise nur ein oder zwei Beine, die in Frage kommen, aber ich brauche viel mehr für mein Gericht.","Also, <Name> ... seid Ihr bereit für eine Reise?"}, {"Bringt 12 makellose Kriecherbeine zu Morgan Stern in Theramore."}},
[1259] = {"Lieutenant Paval Reethe", {"Ich kenne den Namen Lieutenant Paval Reethe nicht. Am besten erkundigt Ihr Euch bei Adjudant Tesoran. Er hat alle Aufzeichnungen über die Wachen von Theramore, vom Personal bis hin zur Ausrüstung.","Er sollte Euch nach einem Blick in seine Bücher sagen können, wer dieser Reethe ist."}, {"Findet bei Adjudant Tesoran in Theramore mehr über Reethe heraus."}},
[1260] = {"Morgan Stern", nil, nil},
[1261] = {"Marg spricht", {"Verfluchter Matschpanzer! Man kann Marg nicht töten! Ah, aber ich bin tot. Also muss er mich getötet haben...","Aber meine Mission! Meine Mission! Ihr, <Name>, müsst sie beenden.","In der Nähe von Theramore sah ich eine Gruppe Soldaten der Allianz am Ufer kampieren. Ein Soldat trug einen Anhänger... einen Anhänger mit mehr Edelsteinen, als ihn sich ein Soldat leisten könnte!","In jener Nacht schlich ich in ihr Lager und stahl den Anhänger, aber nach meiner Rückkehr zum Stützpunkt tötete mich ein Matschpanzerkrebsmann!","<Name>, Ihr müsst diesen Matschpanzer finden und den Anhänger retten!"}, {"Bringt den juwelenbesetzten Anhänger zu Nazeer in Brackenwall."}},
[1262] = {"Bei Zor melden", {"Wie seltsam, dass sich der Anhänger, den Marg stahl, in den Händen eines gemeinen Soldaten befand. Vielleicht hatte sich sein Besitzer verkleidet, oder die Soldaten selbst hatten ihn gestohlen!","Was auch immer, dies ist der Anhänger eines Adligen und wir müssen seinen wahren Besitzer finden, wenn wir das Rätsel lösen wollen.","Bringt den Anhänger zu Zor Lonetree in Orgrimmar. Er ist mit der Politik der Allianz vertraut und wird wissen, ob dieser Anhänger etwas mit der Geschichte der Adligen von Stormwind zu tun hat."}, {"Bringt den Edelstein-Anhänger zu Zor Lonetree in Orgrimmar."}},
[1264] = {"Der vermisste Diplomat", {"Am Anfang jedes Monats werden die Marines von Kul Tiras bezahlt und verlassen Theramore meist mit anderen Zielen. Viele kommen hierher, um Frauen aufzureißen, zu zechen und ihren Spaß zu haben. Eine dieser Gruppen betrachte ich als Stammgäste der Taverne, und ich glaube, dieser Hendel ist dabei.","Kommandant Samaul in Theramore könnte Euch helfen, ihn zu finden, wenn Hendel noch hier stationiert ist. Er ist Proudmoore treu ergeben, aber wenn Ihr Ellings Namen erwähnt, dürfte er Euch helfen... diskret."}, {"Sucht Kommandant Samaul in den Marschen von Dustwallow."}},
[1265] = {"Der vermisste Diplomat", {"Hendel? Ja, ich kenne den Mann. Aber wozu braucht Ihr einen meiner Soldaten?","Hat Elling Euch geschickt, ihn zu finden? Hmm, das gefällt mir ganz und gar nicht. Elling hätte Euch nicht zuerst zu mir geschickt, wenn Euer Anliegen nicht beunruhigend wäre. Ich verstehe, wenn Ihr mir nicht sagen könnt, worum es geht, aber denkt daran: Ich muss dieses Land beschützen. Wenn Ihr vorhabt, die Ruhe zu stören, solltet Ihr mich so viel wie möglich wissen lassen.","Ich glaube, Hendel war im ersten Turm außerhalb von Theramore stationiert. Seid vorsichtig."}, {"Sucht die Späherwacht nach Spuren des Gefreiten Hendel ab."}},
[1266] = {"Der vermisste Diplomat", {"Der Gefreite Hendel hält sich westlich von hier auf. Im Augenblick ist er auf Wache und beschützt einige von Theramores Spitzeln, während sie ausruhen. Sie behalten Brackenwall, den Stützpunkt der Horde, im Auge und suchen nach allem, das unsere Grenzen bedrohen könnte.","Ihr müsstet ihn problemlos finden können; sucht einfach nach Zelten am Straßenrand.","Ich muss mich um andere Dinge kümmern, stoße aber bestimmt bald zu Euch. Ihr könnt mich immer im Turm von Lady Proudmoore in Theramore finden, falls Ihr Probleme habt."}, {"Sucht den Gefreiten Hendel in den Marschen von Dustwallow."}},
[1267] = {"Der vermisste Diplomat", nil, nil},
[1268] = {"Verdächtige Hufabdrücke", {"Reihen von Hufspuren führen von den noch rauchenden Überresten des Gasthauses Zur süßen Ruh weg. Wenigstens einige Abdrücke sind klar erkennbar, weitere Reihen sind im weichen Schlamm des Sumpfs kaum zu sehen."}, {"Berichtet Krog in Brackenwall von den verdächtigen Abdrücken beim Gasthaus Zur süßen Ruh."}},
[1269] = {"Lieutenant Paval Reethe", {"Unter dem Geröll sticht Euch ein Funkeln ins Auge. Kaum sind Schmutz und Asche von dem Objekt gewischt, kommt das Emblem eines goldenen Ankers auf einer weißen Emailleoberfläche zum Vorschein: das Zeichen von Theramore.","Unter dem Anker ist der Name \"Lieutenant Paval Reethe\" eingeprägt."}, {"Bringt Reethes Abzeichen zu Krog in Brackenwall."}},
[1270] = {"Stinkis Flucht", {"Klasse, endlich etwas, das mich nicht essen will!!!","Ich brauche Hilfe mit den Tierwesen hier, und Ihr kommt gerade recht, <Jungchen/Mädel>.","Ein Goblin aus Ratchet namens Mebok Mizzyrix braucht ein paar Sumpfbohnenblätter für einen Trank oder so... Mir ist ganz ehrlich einerlei, warum - er hat gut bezahlt und ich kenne diesen Sumpf, also bin ich hier.","Zu dumm, dass ich umzingelt wurde, bevor ich die Sumpfbohnen fand! Könnt Ihr mir hier raushelfen und auf mich aufpassen, während ich nach Meboks Pflanze suche?"}, {"Begleitet \"Stinki\" Ignatz und sprecht dann mit Mebok Mizzyrix in Ratchet."}},
[1271] = {"Festmahl im Blauen Eremiten", nil, nil},
[1273] = {"Befragt Reethe", {"Es hat lange gedauert, aber ich fand Reethe. Für einen Menschen hatte er sich gut versteckt.","Ogron hatte Angst, nach der langen Zeit im Sumpf könnte er verrückt sein. Ihr kommt mit mir, denn wir brauchen Antworten von ihm."}, {"Geht mit Ogron und sprecht mit Reethe, dann kehrt zu Krog in Brackenwall zurück."}},
[1274] = {"Der vermisste Diplomat", {"Pardon, <werter Herr/holde Dame>. Bischof DeLavey bat mich, Abenteurer zu suchen, die ihm in einer delikaten Angelegenheit helfen könnten.","Wenn Ihr so gut sein könntet, heimlich zur Burg Stormwind zu reiten und mit ihm zu sprechen, sobald es Euch genehm ist - er wäre Euch sicherlich für Eure Hilfe dankbar. Und nochmals, bitte seid diskret. Es ist eine wichtige Angelegenheit."}, {"Sucht Bischof DeLavey in der Burg Stormwind."}},
[1275] = {"Erforschung der Verderbnis", {"Törichte Blutelfen spielen mit Dämonenmagie. Wurden sie nicht Zeugen, was mit den Naga und den Satyrn von Kalimdor geschehen ist?","Ich fürchte, die Blutelfen werden auf ähnliche Weise deformiert. Azeroth darf kein weiteres Volk abscheulicher Monster hervorbringen.","Ich möchte die Hirnstämme der Satyrn und Naga untersuchen, damit ich daraus folgern kann, welches Schicksal die Elfen erwartet. In der Blackfathom-Tiefe, an der Küste von Ashenvale, werdet Ihr sowohl Satyrn als auch Naga finden.","Es ist eine lange Reise, <Name>."}, {"Gershala Nightwhisper in Auberdine möchte 8 verderbte Hirnstämme."}},
[1276] = {"Der Schwarze Schild", {"Ah, jetzt erinnere ich mich! Mosarn ist ein angesehener Schmied in Thunder Bluff. Ich wusste, ich habe den Namen schon einmal gehört.","Als Dank belohnte mich der irdene Ring mit einem von Mosarn hergestellten Satz Rüstungen. Sehr gute Arbeit.","Vielleicht kann er Euch mehr über den Schild sagen, den er anfertigte.","Es ist nicht viel, aber da wir aus Reethe nichts Nützliches herausbekamen, haben wir nichts anderes."}, {"Bringt den geschwärzten Eisenschild zu Mosarn in Thunder Bluff."}},
[1282] = {"Sie nennen ihn den lächelnden Jim", {"Ich kann nicht anders, der alte Jim da tut mir Leid. Er war nicht immer so. Hatte eine Zeitlang ein Gasthaus im Sumpf, wunderbares Lokal.","Hört zu, ich will wirklich nicht darüber sprechen. Besprecht es mit Captain Mumm, wenn es sein muss."}, {"Sprecht mit Captain Garran Mumm in Theramore."}},
[1284] = {"Verdächtige Hufabdrücke", {"Reihen von Hufspuren führen von den noch rauchenden Überresten des Gasthauses Zur süßen Ruh weg. Wenigstens einige Abdrücke sind klar erkennbar, weitere Reihen sind im weichen Schlamm des Sumpfs kaum zu sehen."}, {"Berichtet Captain Garran Mumm in Theramore von den verdächtigen Abdrücken beim Gasthaus Zur süßen Ruh."}},
[1285] = {"Daelins Männer", {"Reddon... Redpath... Oh, da haben wir ihn! Lieutenant Paval Reethe.","Trat den Wachen von Theramore zusammen mit den meisten Rekruten bei, die mit Lady Proudmoore über das Meer kamen. Meldete sich mit sechzehn Jahren zu den Marines in Kul Tiras. Herausragende Dienste, Beförderungen...","Ist als vermisst eingetragen."}, {"Kehrt mit den Informationen über Reethe zu Captain Garran Mumm in Theramore zurück."}},
[1286] = {"Die Deserteure", {"Es war ein ständiger Kampf, in den Marschen präsent zu bleiben, aber durch die Kraft unserer Entschlossenheit konnten wir mehrere Wachtürme in diesem Gebiet zur Verteidigung halten.","Die aktuellsten Informationen und Späherberichte, die ich bekam, deuten darauf hin, dass eine Gruppe Deserteure sich in unserem alten Turm bei der verlassenen Wacht, südöstlich des Gasthauses, eingenistet hat.","Anführer der Deserteure ist ein Mann namens Jacken. Seht zu, ob Ihr Informationen über ihn bekommt.","Seid gewarnt, <Name>. Er könnte sich als harte Nuss erweisen."}, {"Sammelt Informationen über Reethe von Balos Jacken und den Deserteuren in den Marschen von Dustwallow."}},
[1287] = {"Die Deserteure", {"Reethe? Dieser Feigling?","Wir warfen ihn aus dem Lager hinaus, weil er dauernd die Vorräte der Oger in Brackenwall plündern wollte. So viele sind wir nicht, dass wir es mit diesen hirnlosen Schlägern aufnehmen könnten.","Er hat das Fass zum Überlaufen gebracht, daher haben wir ihn sich selbst überlassen.","Also ich hab Euch geholfen ... Warum helft Ihr jetzt nicht uns und vergesst, dass Ihr uns gefunden habt?"}, {"Sprecht mit Captain Garran Mumm in Theramore über Reethe."}},
[1301] = {"James Hyal", {"Bevor ich hier im Blauen Eremiten arbeitete, wurde ich von einem der besten Küchenchefs und Gastwirte ausgebildet, die ich kenne. Sein Name war James Hyal. Alles, was ich besitze und im Leben erreicht habe, verdanke ich ihm.","Ich frage mich, was er wohl so treibt... Sein Bruder Vincent und er wollten in einer Taverne im Hafen von Menethil arbeiten, aber ich habe lange nichts von ihm gehört.","Wenn Ihr jemals in diese Gegend kommen solltet, könntet Ihr ihm dann Grüße von mir bestellen?"}, {"Sprecht mit Vincent Hyal im Hafen von Menethil."}},
[1302] = {"James Hyal", {"Mein Bruder James fuhr mit Lady Proudmoore über das Meer. Als Letztes hörte ich, dass die Kämpfe dort drüben beigelegt waren und James irgendwo auf dem Kontinent ein Gasthaus eröffnete. Wo immer er sein mag, ich bin sicher, seine Geschäfte blühen. Er war ein verdammt guter Koch.","Aber ich habe eine Weile nichts mehr von ihm gehört - zu sehr mit seinem Gasthaus beschäftigt, denke ich. Der Amtmann in Theramore könnte Euch vielleicht zu ihm führen."}, {"Sprecht mit Amtmann Lendry in Theramore."}},
[1318] = {"Die offene Rechnung der Gordok", {"Nun Ihr de' König sein, vielleicht Ihr kriegen zurück wertvolles Artefakt der Gordok von gruseligen Elf, der sich selbst nennen Prinz! König vor Euch hat versagt, so wie alle Könige seit Tortheldrin - de' gruselige Elf - es gestohlen hat, von damaligen Gordokkönig!","Artefakt heißen Stulpen der Gordokmacht. Alte Zeit Geschichten sagen, nur wahre König de' Gordok kann sie zurückholen. Alte Zeit Geschichten sagen auch, der gruselige Elf ist immer noch hier - Ihr finden es und es werden Eure mächtigen Stulpen, für immer! Yar, ich zeigen!"}, {"Findet die Stulpen der Gordokmacht und bringt sie zu Captain Kromcrush in Düsterbruch.","Kromcrush zufolge sagen die \"Alte Zeit Geschichten\", dass Tortheldrin - ein \"gruseliger\" Elf, der sich selbst als Prinz bezeichnet- sie einem der Gordokkönige gestohlen hat."}},
[1319] = {"Der Schwarze Schild", {"Ich kann Euch leider nicht weiterhelfen, <Name>, schicke Euch aber zu jemandem, der es kann. Caz Twosprocket, einer unserer besten Schmiede, arbeitet in der Schmiede.","Bringt ihm den Schild und hört Euch an, was er zu sagen hat."}, {"Zeigt den geschwärzten Eisenschild Caz Twosprocket in Theramore."}},
[1320] = {"Der Schwarze Schild", {"Ah, das hätte ich fast übersehen... Der Ledergurt an der Rückseite muss verbrannt sein, aber er war offenbar für eine große Person gemacht. Ich schätze, für einen Tauren.","Mehr kann ich Euch nicht sagen. Sagt Captain Mumm, es tut mir Leid, dass ich nicht mehr helfen konnte."}, {"Sprecht mit Captain Garran Mumm in Theramore."}},
[1321] = {"Der Schwarze Schild", {"Hmm... Ich sehe nichts Ungewöhnliches an diesem Schild. Do'gol, der Schmied, sollte ihn sich ansehen. Vielleicht kann er uns mehr sagen, aber große Hoffnungen würde ich mir nicht machen..."}, {"Zeigt den geschwärzten Eisenschild Do'gol in Brackenwall."}},
[1322] = {"Der Schwarze Schild", {"Hmmm, hier am Rand des Schildes steht etwas geschrieben, aber durch die Brandspuren kann man es kaum lesen.","Ich brauche Säure, um ihn zu reinigen. Das Gift der Dunkelzahnspinnen im Sumpf würde helfen. Bringt mir ihre Giftsäcke und ich reinige diesen Schild."}, {"Bringt 6 säurehaltige Giftbeutel zu Do'gol in Brackenwall."}},
[1323] = {"Der Schwarze Schild", {"Oh, ich glaube, ich habe zu viel Säure auf den Schild geschüttet. Lasst mich ein wenig rubbeln...","Hm, schwer zu lesen, sieht aber wie der Name Mosarn aus. Nie von ihm gehört. Vielleicht weiß Krog etwas. Krog kennt viele Leute."}, {"Sprecht mit Krog in Brackenwall."}},
[1324] = {"Der vermisste Diplomat", {"Was?! Ihr kommt hierher und werft mir vor, ich mache mit den Defias gemeinsame Sache und glaubt, ich höre mir diese Beleidigungen an? Etwas sagt mir, dass Ihr das schon lange verdient habt."}, {"Besiegt den Gefreiten Hendel in den Marschen von Dustwallow."}},
[1338] = {"Stormpikes Bestellung", {"Es gibt einen Rüstungsschmied bei den Zwergen, dessen Arbeit ich sehr bewundere. Sein Name ist Furen Longbeard und seine Fertigkeit unübertroffen. Ich brauche einen neuen Schild und muss einen von ihm haben!","Das Problem ist... Furen lebt weit im Süden im Zwergendistrikt von Stormwind. Im Menschenland! Ich kann mir nicht vorstellen, warum er dort lebt, so weit weg von Ironforge. Die Menschen müssen ihn fürstlich bezahlen, damit er dort bleibt!","Wenn Ihr reisen wollt und nichts gegen das Geldverdienen habt, würdet Ihr Furen dann meine Bitte nach einem Schild überbringen?"}, {"Bringt Stormpikes Bitte zu Furen Longbeard in Stormwind."}},
[1339] = {"Gebirgsjäger Stormpikes Aufgabe", {"Ich hörte, Gebirgsjäger Stormpike sucht nach einem Läufer. Jemand, der eine Reise für ihn unternimmt. Wie wäre es? Seid Ihr ein <Volk>, der für den Job geeignet ist?","Wenn ja, findet Ihr Stormpike auf der Spitze des nördlichen Wachturms."}, {"Sprecht mit Gebirgsjäger Stormpike."}},
[1358] = {"Probe für Helbrim", {"Ein Kollege in Kalimdor, Apotheker Helbrim, untersucht die Auswirkungen von Lordaerons Toxinen auf die Wildtiere von Kalimdor. Er wäre an einer Probe der Wolfherzen interessiert, die Renferrel mir geschickt hat.","Helbrim lebt im Brachland in einem Dorf, das Crossroads heißt. Um ihn zu erreichen, müsst Ihr per Zeppelin zur Orc-Hauptstadt Orgrimmar reisen und dann zu Fuß Richtung Süden nach Durotar. Geht durch das Brachland nach Westen und Ihr erreicht bald den Ort Crossroads."}, {"Bringt die Wolfherzprobe zu Apotheker Helbrim ins Brachland."}},
[1359] = {"Zinges Lieferung", {"Die Wolfherzen, die Ihr beschafft habt, verfügen über ein Merkmal, das es in ähnlichen Proben nicht gibt. Sie verfügen über eine Giftigkeit, die für genau die Wölfe tödlich sein müsste, in denen die Herzen schlagen!","Es ist unglaublich und wir müssen das weiter untersuchen. Ich habe einige Proben der gewonnenen Herzen aufbewahrt. Bringt sie zu meiner Kollegin, Apothekerin Zinge. Ihr findet sie in unserem Hauptquartier in Undercity."}, {"Bringt die Wolfherzproben zu Apothekerin Zinge nach Undercity."}},
[1360] = {"Wiederbeschaffte Schätze", {"Ich hatte selbst eine gute Arbeitsstelle als Buddler an der Uldaman-Ausgrabungsstätte im Ödland... das heißt, bis diese verfluchten Troggs auftauchten. Ich konnte gerade noch mit heiler Haut entkommen, das war alles. In dem Chaos habe ich meinen wertvollsten Besitz zurückgelassen. Natürlich ist er in meiner Truhe sicher vor den Troggs, aber die befindet sich mitten unter ihnen.","Ich wäre in Eurer Schuld, sowohl in Ehre als auch in Münze, wenn Ihr sie für mich besorgen könntet! Eine Truhe mit meinem Namen darauf befindet sich am Ende der nördlichen Bankenhalle. Dorthin müsst Ihr gelangen."}, {"Holt Krom Stoutarms wertvollen Besitz aus seiner Truhe in der nördlichen Bankenhalle von Uldaman und bringt den Schatz zu ihm nach Ironforge."}},
[1361] = {"Regthar Deathgate", {"Ein Krieger der Horde, Regthar Deathgate, rekrutiert Agenten für einen Einsatz im Land Desolace. Sprecht mit ihm, wenn Ihr Euch freiwillig melden wollt. Er lebt im Brachland in einer Reihe von Bunkern westlich von Crossroads.","Und schärft Eure Waffen. Desolace ist ein vom Krieg zerrissener Ort."}, {"Sprecht mit Regthar Deathgate."}},
[1362] = {"Die Kolkar von Desolace", {"Die Kolkarzentauren leben in Desolace. Ihre Truppen sind in das Brachland vorgestoßen. Das ist bekannt. Agenten der Horde kümmern sich dort um sie.","Aber so lange die Zentauren in Desolace so stark sind, werden sie immer eine Bedrohung sein. Mit der Zeit schaffen wir uns diese Bedrohung vom Hals.","Sprecht mit Felgur Twocuts beim Geistwandlerposten in Desolace. Seine Aufgabe war es, Informationen über die Zentauren zu sammeln. Er weiß bestimmt, wie man am besten mit den Kolkar von Desolace fertig wird."}, {"Sprecht mit Felgur Twocuts beim Geistwandlerposten in Desolace."}},
[1363] = {"Mazens Befehl", {"Als Verwalter der Arbeit hier in der Bibliothek der Akademie der Arkanen Künste und Wissenschaften liegt es in meiner Verantwortung, dafür zu sorgen, dass alle magischen Folianten und Rollen stets erfasst sind. Wer weiß, was Schlimmes geschehen würde, fielen einige dieser verzauberten Texte in die falschen Hände?","Dabei fällt mir ein ... mein Assistent Dellis machte letzte Woche einen schrecklichen Fehler. In der Bibliothek ist er recht tüchtig, aber inkompetent, wenn er die Tore von Stormwind hinter sich lässt. Seid ein Held und helft dem alten Dellis, ja?"}, {"Sprecht mit Akolyt Dellis in Stormwind."}},
[1364] = {"Mazens Befehl", {"Meister Mazens Anweisungen waren ganz klar: Bringt einen sehr wichtigen Essay zu Behüter Mahar Ba in der Burg Nethergarde.","Aber ich bin noch nie in den verwüsteten Landen gewesen. Die Reise war beängstigend!","Als ich an die Sümpfe des Elends kam, bog ich falsch ab und war plötzlich von grässlichen pflanzenartigen Sumpfwesen umzingelt. Sie schienen mich angreifen zu wollen, also lief ich weg. Doch in meiner Hast ließ ich den Essay fallen. Ich bin sicher, eine der Bestien hat ihn genommen.","Könntet Ihr bitte versuchen, ihn zu holen und zu Mahar Ba zu bringen?"}, {"Rettet Khadgars 'Abhandlungen über dimensionale Konvergenz' und bringt sie zu Behüter Mahar Ba in der Burg Nethergarde."}},
[1365] = {"Khan Dez'hepah", {"Die Kolkar sind nicht die einzigen Zentauren von Desolace. Viele Klans ziehen durch diese trockenen Ebenen, bekämpfen einander und sterben. Und es ist gut, dass sie sich untereinander bekämpfen, denn sie sind stark und brutal. Vereinten sich die Zentauren gegen uns, wären sie ein schrecklicher Gegner.","Wir müssen sie erledigen, bevor sie sich zusammentun.","Euer erster Schritt ist, den Kolkarklan zu schwächen. Macht das, indem Ihr den Anführer Khan Dez'hepah besiegt.","Das Dorf Kolkar liegt nordöstlich von hier. Geht, und möge Euch das Glück hold sein."}, {"Bringt Khan Dez'hepahs Kopf zu Felgur Twocuts in Desolace."}},
[1366] = {"Zentaurenkopfgeld", {"Da Ihr die Zentaurenklans jetzt eingeschüchtert habt, wird es Zeit, sie bluten zu lassen. Wenn Ihr durch Desolace reist, jagt Zentauren. Mir ist gleich, welchen Klan Ihr jagt. Sammelt nur Ohren. Bergeweise Ohren.","Wenn Ihr ihre Zahl dezimiert, schwächt Ihr ihre Entschlossenheit und macht alle Gedanken an Expansion zunichte.","Gute Jagd, <Name>."}, {"Bringt 15 Zentaurenohren zu Felgur Twocuts in Desolace."}},
[1367] = {"Allianz mit den Magram", {"Um die Zentauren zu schlagen, müssen wir ihren Hass gegen sie wenden. Wir verbünden uns mit einem Klan und lernen durch ihn, wie wir alle Klans besiegen können.","Wenn Ihr Euch mit den Magram verbünden wollt, dann tötet ihre Gegner, die Gelkis. Das wird Euch den Respekt der Magram einbringen. Wenn Ihr genug verdient habt, dann trefft Euch mit dem Sprecher der Magram Warug am Rand des Dorfes Magram im Südosten von Desolace."}, {"Baut Euch einen freundlichen Ruf bei den Magram auf und sprecht dann mit Warug."}},
[1368] = {"Allianz mit den Gelkis", {"Um die Zentauren zu schlagen, müssen wir ihren Hass gegen sie wenden. Wir verbünden uns mit einem Klan und lernen durch ihn, wie wir alle Klans besiegen können.","Wenn Ihr Euch mit den Gelkis verbünden wollt, dann tötet ihre Gegner, die Magram. Wenn Ihr Euch den Respekt der Gelkis verdient habt, dann trefft Euch mit der Schamanin der Gelkis Uthek der Weisen am Rand des Dorfes Gelkis im Südwesten von Desolace."}, {"Baut Euch einen Ruf als freundliche Person bei den Gelkis auf und sprecht dann mit Uthek der Weisen."}},
[1369] = {"Zerbrochene Tränen", {"Die Gelkis glauben, dass unsere Zentaurenmutter Theradras uns beschützt. Sie sind dumm. Niemand beschützt die Zentauren. Die Magram brauchen keinen Schutz! Wir sind die Stärksten!","Zeigt den Gelkis, dass sie dumm sind. Geht zu der Höhle in ihrem Dorf. Sie horten Kristalle in dieser Höhle, die Tränen von Theradras. Zerstört die Tränen und bringt mir die zerbrochenen Stücke!"}, {"Wahrt Euren Ruf bei den Magram und bringt 3 zerbrochene Tränen zu Warug im Dorf Magram."}},
[1370] = {"Diebstahl von Vorräten", {"Wenn wir nicht im Krieg sind, jagen wir wegen Fleisch. Fleisch gibt uns Kraft und volle Bäuche kämpfen besser als leere.","So ist es bei den Magram. Sie lagern extra Fleisch ein, damit sie zwischen den Jagdausflügen länger kämpfen können. Wenn man ihr Dorf im Osten überfällt und ihre Fleischvorräte raubt, wird ihre Kraft schwinden."}, {"Bringt 6 Taschen primitiv getrocknetes Fleisch zu Uthek der Weisen im Dorf Gelkis."}},
[1371] = {"Gizmo für Warug", {"Vor langer Zeit kam ein kleiner Gnom nach Desolace. Er hatte Pferde und einen Karren, und auf dem Karren viele seltsame Dinge. Komische Dinge. Dinge, die in verschiedenen Farben brannten. Dinge, die sich bewegten und doch nicht lebten. Ich tötete den kleinen Gnom, aber vorher ... brachte er mich zum Lachen.","Ich behielt mein liebstes Ding von dem Wagen. Es war ein Mensch aus Holz, der mir zuwinkte, bis ich ihn schlug und zerbrach.","Ich will einen Neuen. Besorgt mir einen Neuen!"}, {"Wahrt Euren Ruf bei den Magram und bringt eine hochentwickelte Zielattrappe zu Warug im Dorf Magram in Desolace."}},
[1372] = {"Nichts als die Wahrheit", {"Auf Befehl von Varimathras wurde unsere Expeditionsstreitmacht ausgeschickt, um das untere Azeroth zu vermessen.","Beim Spähen bemerkte ich einen Spitzel der Menschen, der den Bettlerschlupfwinkel ausspionierte. Ehe ich ihn festnehmen konnte, schnappten ihn einige unserer ... Orcbrüder ... und brachten ihn nach Stonard, ihrer Festung in den Sümpfen des Elends.","Ich möchte unseren Orcverbündeten ... helfen. Sie sahen unser Lager hier nicht, dennoch wollte ich sie unterstützen.","Ich arbeitete hier mit Faustin, um ein ... Wahrheitsserum für unseren Freund in Stonard zu entwickeln."}, {"Sprecht mit Apotheker Faustin im Bettlerschlupfwinkel."}},
[1373] = {"Ongeku", {"Ihr müsst noch mehr beweisen und Eure nächste Aufgabe führt Euch weit weg, daher werden die Gelkis die Magram vorerst ohne Euch bekämpfen.","Die Erde flüstert jenen, die zuhören, viele Geheimnisse zu. Ein Geheimnis, von dem ich höre, betrifft einen roten Kristall. Er heißt Draenethyst und ist neu auf unserer Erde. Er stammt von einem Ort, wo Berge fliegen und Dämonen herrschen.","Bringt mir einen Splitter Draenethyst; er macht meine Magie stark! Der Splitter befindet sich an einem Ort namens Sümpfe des Elends und ist im Besitz von Ongeku, dem Verirrten."}, {"Wahrt Euren Ruf bei den Gelkis und bringt einen Draenethystsplitter zu Uthek der Weisen im Dorf Gelkis in Desolace."}},
[1374] = {"Khan Jehn", {"Die Erde spricht zu mir und sagt, dass die Magram schwach werden. Jetzt ist es Zeit, ihren Khan zu töten!","Bringt mir den Kopf des Magramkahns Jehn!"}, {"Bringt Khan Jehns Kopf zu Uthek der Weisen im Dorf Gelkis in Desolace."}},
[1375] = {"Khan Shaka", {"Ihr helft den Magram und ich mag Euch. Tötet jetzt den Khan der Gelkis! Er ist Khan Shaka und ich will seinen Kopf! Ich möchte seinen Kopf ansehen und ihn auslachen!"}, {"Wahrt Euren Ruf bei den Magram und bringt den Kopf von Khan Shaka zu Warug im Dorf Magram in Desolace."}},
[1380] = {"Khan Hratha", {"<Name>, Ihr habt uns gegen die Magram geholfen. Jetzt helft uns dabei, die Maraudine zu schlagen! Tötet den Anführer ihres Klans, Khan Hratha!","Um ihn herauszufordern, blast das Kriegshorn der Maraudine. Es befindet sich auf einem hohen Steilhang im Tal der Speere.","Doch um das Horn zu blasen, müsst Ihr erst ein Kriegshornmundstück erwerben. Diese Teile sind selten und sind ausschließlich im Besitz der vertrauenswürdigsten Maraudinezentauren.","Nachdem das Horn geblasen wurde, werden Zentauren kommen. Tötet sie, bis Hratha auftaucht.","Khan Hratha besitzt ein Stück eines... Schlüssels. Bringt es mir..."}, {"Wahrt Euren Ruf bei den Gelkis und bringt das Schlüsselfragment der Maraudine zu Uthek der Weisen im Dorf Gelkis."}},
[1381] = {"Khan Hratha", {"Der Khan der Gelkis ist tot, und jetzt werden die Maraudine fallen! Tötet Hratha, den Khan der Maraudine!","Um ihn herauszufordern, blast das Horn auf dem hohen Steilhang im Tal der Speere. Doch um das Horn zu blasen, braucht Ihr erst ein Kriegshornmundstück. Um ein Mundstück zu erwerben, müsst Ihr Zentauren der Maraudine töten. Tötet sie so lange, bis Ihr ein Mundstück findet. Dann blast das Horn.","Tut dies, und Zentauren der Maraudine werden zum Turm kommen. Tötet sie, bis Khan Hratha auftaucht. Khan Hratha besitzt ein Stück eines Schlüssels. Gebt mir dieses Stück..."}, {"Wahrt Euren Ruf bei den Magram und bringt das Schlüsselfragment der Maraudine zu Warug im Dorf Magram in Desolace."}},
[1382] = {"Seltsame Allianz", {"Wir können von Glück reden, dass die Zentauren sich untereinander bekämpfen, denn als vereinigte Kraft könnte sie nichts aufhalten. Ja, das kommt uns zu Gute und wir sollten unseren Vorteil ausbauen.","Einer der Zentaurenklans, der Gelkisklan, führt einen brutalen Krieg gegen den Magramklan. Wenn wir die Magram angreifen, können wir vielleicht eine Allianz mit den Gelkis schmieden.","Sprecht mit Uthek der Weisen im Randbezirk der Gelkislager im Südwesten von Desolace. Durch sie könnten wir eine Allianz mit den Gelkis schmieden."}, {"Baut eine freundliche Beziehung zu den Gelkis auf und sprecht dann mit Uthek der Weisen."}},
[1383] = {"Nichts als die Wahrheit", {"Mir schwebt genau das richtige Serum vor. Es wird die Wahrheit genau so behandeln, wie die Wahrheit behandelt werden sollte.","Für dieses Gebräu brauche ich mehrere Schattenpantherherzen aus dem Sumpf. Außerdem brauche ich den verzauberten Fungus des Morastlords, der dort haust. Ich bin sicher, jemand, der so tüchtig ist wie Ihr, <Name>, kann ihn überreden, sich von einigen zu trennen.","Die schwierigste Aufgabe wird sein, einen Tiefschreitertumor aus dem fernen Desolace zu beschaffen. Nur sehr selten werden die Riesen dort krank, sodass ein Tumor entsteht.","Und jetzt fort mit Euch!"}, {"Apotheker Faustin im Bettlerschlupfwinkel braucht 5 Schattenpantherherzen, Morastlordfungus und einen Tiefschreitertumor."}},
[1384] = {"Überfall auf die Kolkar", {"Die Kolkarzentauren glauben, ihre Magie sei stark. Die wissen nicht, was Magie ist! Nie und nimmer würden die richtige Magie überhaupt erkennen!","Ihr werdet ihnen zeigen, dass ihre Magie schwach ist. Geht zu den Kolkar im Nordosten und tötet sie. Nehmt ihre Glücksbringer. Bringt mir die Glücksbringer und ich werfe sie ins Feuer und lache!"}, {"Wahrt Euren Ruf bei den Gelkis und bringt 10 kunstlose Glücksbringer zu Uthek der Weisen im Dorf Gelkis in Desolace."}},
[1385] = {"Brutale Politik", {"Desolace ist ein Land im Krieg. Die Zentauren führen Krieg untereinander und wir glauben, der Sieger wird marschieren und unsere Festungen bedrohen. Das können wir nicht dulden.","Wir müssen lernen, wie man die Zentauren schlägt, und dazu... müssen wir uns mit einem der Klans verbünden. Die Zentauren der Magram führen Krieg mit den Gelkis. Wenn Ihr gegen die Gelkis kämpft, dann erwägen die Magram vielleicht eine Allianz.","Erringt die Gunst der Magram und sprecht dann mit Warug im Dorf Magram im Südosten von Desolace."}, {"Baut Euch einen freundlichen Ruf bei den Magram auf und sprecht dann mit Warug."}},
[1386] = {"Angriff auf die Kolkar", {"Die Kolkar halten sich für stark, aber wir lachen über sie! Wir lachen und wir töten sie! Tötet auch Ihr sie. Zeigt mir, dass Ihr Kolkar genauso wie wir töten könnt. Geht nach Norden und tötet sie alle!"}, {"Haltet Euren Ruf bei den Magram aufrecht. Tötet 12 Zentauren der Kolkar, 12 Späher der Kolkar sowie 6 Raufer der Kolkar und kehrt dann zu Warug ins Magramdorf in Desolace zurück."}},
[1387] = {"Zentaurenkopfgeld", {"Viele Klans bilden das Volk der Zentauren. Jeder hat seinen Anführer und eine Agenda, aber alle Klans haben eines gemeinsam: Grausamkeit.","Wenn wir je Frieden finden wollen, müssen die Zentauren entfernt werden. Und ich übertrage Euch die Aufgabe, genau das zu tun.","Wenn Ihr durch Desolace zieht, jagt Zentauren und sammelt ihre Ohren. Bringt mir viele, viele Ohren.","Macht das im Dienst der Allianz, <Name>, und wir sind dem Ende des Krieges einen Schritt näher."}, {"Bringt 15 Zentaurenohren zu Corporal Melkins bei der Nijelspitze in Desolace."}},
[1388] = {"Nichts als die Wahrheit", {"Alles läuft genau nach Plan, <Name>. Ich möchte, dass Ihr dieses Wahrheitsserum dem Todespirscher Zraedus gebt. Er wird Euch zeigen, wie man es richtig... anwendet.","Ha-ha!"}, {"Gebt Faustins Wahrheitsserum dem Todespirscher Zraedus im Bettlerschlupfwinkel."}},
[1389] = {"Draenethystkristalle", {"Die Verirrten, die im Fallow Heiligtum leben, sehen aus wie wir, aber sie sind Monster... die üblen Unbilden auf unserem Trek nach Azeroth haben sie wahnsinnig gemacht. Einst waren sie unsere Brüder, doch nun leben sie im Fallow Heiligtum im Osten und fangen oder töten alle, die sich in die Nähe wagen.","Und sie haben uns etwas Heiliges gestohlen - Draenethystkristalle.","Uns fehlt die Kraft, das Fallow Heiligtum zu betreten und unseren gestohlenen Draenethyst von ihren Hütten und Lagerfeuern zu holen, aber ohne diese Kristalle... könnten auch wir vom rechten Weg abkommen."}, {"Bringt 6 Draenethystkristalle zu Magtoor im sicheren Hafen in den Sümpfen des Elends."}},
[1391] = {"Nichts als die Wahrheit", {"Wir müssen mit einer gewissen Diskretion vorgehen. Immerhin werdet Ihr nicht da reinspazieren und den Narren überzeugen, Faustins Gebräu zu trinken, richtig?","Aber wenn es eines gibt, zu dem die Menschen nicht nein sagen können, dann ist es Fusel. Ich nehme Faustins Serum und mische es in dieses Bier. Der arme Kerl wird garantiert keine Hemmungen haben, wenn es darum geht, ein Gebräu zu kippen.","Geht hastig nach Stonard in den Sümpfen des Elends, <Name>. Und schweigt schön stille über unsere kleine Mission ..."}, {"Bringt Zraedus' Bräu zu Spitzel Marksen in Stonard."}},
[1392] = {"Noboru der Knüppel", {"Noboru verseuchte die Draenei des sicheren Hafens seit Monaten; sein Knüppel ist ein Symbol der Brutalität, die sie sich von den Verirrten gefallen lassen mussten."}, {"Bringt Noborus Knüppel zum Anführer des sicheren Hafens in den Sümpfen des Elends."}},
[1393] = {"Galens Flucht", {"Bitte helft! Diese Kreaturen werden mich fressen!","Helft mir hier raus und ich gebe Euch einen großen Schatz! Er ist in meinem Lager im Sumpf im Norden von Stonard, aber in einer Geldkassette eingeschlossen, die man mit einem Trick öffnet, den nur ich beherrsche.","Wenn ihr mir zur Flucht verhelft, zeige ich ihn Euch."}, {"Eskortiert Galen, bis er außer Gefahr ist, sucht dann sein Lager in den Sümpfen des Elends und öffnet seine Geldkassette."}},
[1394] = {"Letzte Überfahrt", {"Ihr habt Eure Sache gut gemacht, <Name>. Ihr habt meinen Test und die Tests vor meinem bestanden. Kehrt zu Dorn in Tausend Nadeln zurück. Er wird die Veränderung in Euch spüren und sehen, dass Euer Verstand, Körper und Geist stark genug sind, dass Ihr für Eure Bemühungen belohnt werden solltet.","Bleibt dem Gleichgewicht treu, das Ihr während all dieser Prüfungen gezeigt habt, <Name>. Wenn Ihr das tut, wird Dorn Euch irgendwann noch einmal prüfen."}, {"Sprecht mit Dorn Plainstalker in Tausend Nadeln."}},
[1395] = {"Vorräte für Nethergarde", {"Diese Kiste mit Kraftsteinen muss in die Burg Nethergarde in den verwüsteten Landen. Sie werden ihre Macht nicht lange halten, daher können wir sie nicht mit der nächsten Karawane aufgeben.","Ihr müsst sie nehmen, und zwar schnell.","Bringt sie zu Rüstmeister Lungertz in der Burg Nethergarde. Um die Burg zu erreichen, müsst Ihr nach Osten über den Gebirgspass der Totenwinde, dann nach Osten durch die Sümpfe des Elends und dann nach Süden durch die verwüsteten Lande.","Seid vorsichtig, <Name>. Ihr müsst durch gefährliche Länder und dürft doch nicht verzagen."}, {"Bringt die Kiste mit den Kraftsteinen zu Rüstmeister Lungertz, bevor die Kraft verbraucht ist."}},
[1396] = {"Lauernde Tierwelt", {"Meine Karawane sollte zur östlichen Küste ziehen, aber wir erreichten unser Ziel nicht. Verirrte haben uns angegriffen, die Wachen getötet und die Vorräte verstreut.","Ich allein habe ihren Angriff überlebt. Ich habe geschworen, die verbliebenen Vorräte zu schützen, aber wilde Kreaturen schleichen in der Nähe herum. Bitte helft mir, mein Lager gegen diese Bestien zu verteidigen!"}, {"Tötet 8 junge Sägezahnkrokilisken, 10 Trauerspinner sowie 10 Sumpfjaguare und kehrt dann zu Behüter Biggs in den Sümpfen des Elends zurück."}},
[1398] = {"Treibholz", {"Ich stehe tief in Eurer Schuld, <Name>. Ein ehrenhaftes Herz schlägt in Eurer Brust. Und jetzt habe ich eine andere Aufgabe, für die ich demütig um Eure Mithilfe bitte.","Wenn ich Nethergarde erreichen soll, dann muss ich diesen Wagen reparieren. Und dafür ... brauche ich Holz. Starkes, trockenes Holz - nicht das schimmelige, krumme Holz der Sumpfbäume.","<Name>, geht nach Osten zur Küste und sucht nach in der Sonne getrocknetem Treibholz. Bringt es mir."}, {"Bringt 8 Stücke sonnengetrocknetes Treibholz zu Behüter Biggs in den Sümpfen des Elends."}},
[1418] = {"Neeka Bloodscar", {"Das Ödland ist unwirtlich und liegt nahe am Land des Loch Modan, das von der Allianz beherrscht wird, daher wird die Kommunikation zwischen Stonard und den Ödland-Außenposten von Kargath oft unterbrochen.","Beweist Euch, <Name>. Reist ins Ödland und sprecht mit Neeka Bloodscar, der obersten Späherin des Kargath-Außenpostens. Sprecht mit ihr und bringt mir ihren Bericht."}, {"Sprecht mit Neeka Bloodscar."}},
[1419] = {"Kojoten-Diebe", {"Habt Ihr viele Tage im Ödland verbracht, <Klasse>? Habt Ihr sein Herz gespürt? Habt Ihr den Bestien getrotzt, die durch die staubigen Ebenen schleichen? Dann wird es aber höchste Zeit.","Klippenkojoten sind zähe Burschen. Zäh und tollkühn genug, dass sie sich nach Kargath schleichen und unsere Essensvorräte stehlen. Helft mit, den Außenposten von diesen Dieben zu befreien!","Jagt Klippenkojoten, <Name>. Jagt sie und bringt mir ihre Kieferknochen. Bringt mir genügend und ich glaube Euch vielleicht, wenn Ihr sagt, dass Ihr das Ödland kennt."}, {"Bringt 30 Kojotenkieferknochen zu Neeka Bloodscar in Kargath."}},
[1420] = {"Bericht für Helgrum", {"Ich schrieb einen neuen Bericht für Helgrum in Stonard. Bringt ihn zu ihm.","Und wenn Ihr Euch als Kundschafter betrachtet, kommt nicht vom Weg ab. Oder fallt einem Feind in die Hände."}, {"Bringt Neekas Bericht zu Helgrum dem Hurtigen in Stonard."}},
[1421] = {"Die verlorene Karawane", {"Ich wurde während des Angriffs der Verirrten betäubt, aber bevor mir schwarz vor Augen wurde, sah ich einen unserer Wagen, der zum Fallow Heiligtum gezerrt wurde, zum Dorf der Verirrten im Norden.","Eine Karawanentruhe mit wertvollen Reagenzien war in diesem Wagen, Reagenzien, die unabdingbar für die Studien der Hexer der Burg Nethergarde sind. Sucht die Truhe und holt diese Reagenzien, <Name>. Holt sie und bringt sie mir."}, {"Sucht die Karawanentruhe im Fallow Heiligtum, beschafft Euch die Reagenzien der Hexer und bringt sie zu Behüter Biggs."}},
[1422] = {"Gefahr aus dem Meer", {"Warum unterhaltet Ihr Euch nicht mit Katar, während ich an Eurer Waffe arbeite? Er erwähnte, dass er Hilfe bei Patrouillen am Strand braucht, und mir wäre es lieber, Ihr würdet Euren Geist und Eure Waffen trainieren, anstatt Euch mit den anderen Tunichtguten in diesem Lager zu betrinken."}, {"Sprecht mit Katar in den Sümpfen des Elends."}},
[1423] = {"Die verlorenen Vorräte", {"Dieses Fass ist klein, aber stabil. Seine Ritzen sind mit einem gelben, duftenden Wachs versiegelt. Auf der Unterseite des Fasses stehen die Worte:","RÜSTMEISTER LUNGERTZ","BURG NETHERGARDE"}, {"Bringt die verlorenen Vorräte zu Rüstmeister Lungertz."}},
[1424] = {"Tränenteich", {"Der Kriegshäuptling schickt uns, die seltsamen Vorkommnisse im Tempel von Atal'Hakkar zu untersuchen.","Die Trolle des Stammes der Atal'ai sind ein Stamm dunkler Zauberkundiger, die dafür bekannt sind, verbotene Magie auszuüben. Ihr könnt Euch vorstellen, wie erstaunt wir waren, als wir hier eintrafen und feststellten, dass der Tempel versunken war.","Die grünen Großdrachen hüten nun die Stelle und spielen sich als Beschützer von Azeroth auf.","Wir müssen herausfinden, was geschehen ist. Forscht rund um den Tränenteich und sammelt Artefakte der Atal'ai. Damit kann ich dieses Rätsel lösen."}, {"Fel'Zerul in Stonard möchte, dass Ihr 10 Artefakte der Atal'ai sammelt."}},
[1425] = {"Überbringen der Lieferung", {"Während ich diesen Wagen repariere, <Name>, habe ich eine letzte Aufgabe für Euch.","Ich habe die wichtigsten Vorräte eingepackt, die ich vor den Angriffen der Oger retten konnte. Bringt sie zur Burg Nethergarde. Die Burg schmiegt sich an die nördlichen Hügel der verwüsteten Lande und wacht über das Dunkle Portal.","Folgt der Straße nach Osten, um Nethergard zu erreichen. Geht an der Gabelung nach Süden in die verwüsteten Lande und bei der nächsten Gabelung nach links. Folgt der Straße, <Name>, und seid wachsam."}, {"Bringt die Ladung für Nethergarde zu Rüstmeister Lungertz in der Burg Nethergarde."}},
[1426] = {"Gefahr aus dem Meer", {"Ich mag weder das Meer noch die Kreaturen, die ich aus seiner Tiefe kommen sah. Es ist eine mächtige, aber blinde Herrin; launisch und brutal. Es wäre mir ein Vergnügen, die Fischmenschen zu Hunderten zu töten, wenn es in meiner Macht stünde. Eine kleine Freude ist es mir wenigstens, sie durch die Hände all derer sterben zu sehen, die den Willen der Horde erfüllen.","Und damit sind wir bei Euch, <Klasse> . Wie viele könnt Ihr besiegen? Würdet Ihr Euch mit dem Mächtigsten ihrer Art messen?"}, {"Tötet 10 Marschenmurlocs, 10 Marschentintenspucker und 10 Marschenfleischfresser und kehrt dann zu Katar in den Sümpfen des Elends zurück."}},
[1427] = {"Gefahr aus dem Meer", {"Sprecht wieder mit Tok'Kar, bevor Ihr Euch ausruht. Während Ihr fort wart, hat er, glaube ich, ein paar Waffen vollendet. Für Eure Hilfe würde er Euch eine davon anbieten. Ihr tätet gut daran, ihre Kraft an den Fischmenschen zu erproben, wenn Ihr bereit seid ... aber nur, um mich weiterhin zu erfreuen."}, {"Sprecht mit Tok'Kar in den Sümpfen des Elends."}},
[1428] = {"Fortwährende Gefahr", {"Freut mich zu sehen, dass Euer Hass auf die Fischmenschen mit meinem wächst.","Sucht mehr von ihnen am Strand entlang, und wenn Ihr genug vom Gefecht habt, kommt zu mir zurück. Dann sprechen wir über eine Belohnung für Eure Ausdauer."}, {"Tötet 10 Marschentintenspucker, 10 Marschenfleischfresser und 10 Marschenorakel und kehrt dann zu Katar in den Sümpfen des Elends zurück."}},
[1429] = {"Der Verbannte der Atal'ai", {"Während Ihr den Gefahren des Tränenteichs getrotzt habt, kehrte einer meiner Späher mit einer in Blut und auf getrocknetem Fleisch geschriebenen Rolle zurück. Es scheint sich um ein Urteil gegen einen der Priester des Stammes der Atal'ai zu handeln.","Der Priester wurde zu etwas Schlimmerem als dem Tod bei den Atal'ai verurteilt.","Er wurde ins Hinterland zu den Trollen der Witherbark verbannt.","Ich bündle diese Artefakte. Ihr müsst den Verbannten suchen und ihren wahren Verwendungszweck herausfinden. Kommt wieder zu mir, wenn Eure Reise beendet ist."}, {"Bringt das Artefaktbündel der Atal'ai zu dem Verbannten der Atal'ai ins Hinterland."}},
[1430] = {"Frischfleisch", {"Bei meinen Kunden besteht eine enorme Nachfrage nach dem saftigen Geschmack von Kriecherfleisch; besonders nach dem Fleisch der monströsen Kriecher.","Wenn Ihr vorhabt, zur Küste zu reisen, könnte es sich lohnen, wenn Ihr mir einige ihrer Beine bringt, damit ich sie nach Orgrimmar transportieren lassen kann.","Die Küste liegt östlich von hier. Ihr solltet keine Probleme haben, die Kreaturen zu finden, die an den Stränden und im Meer umherhuschen."}, {"Bringt 10 monströse Kriecherbeine zu Dar in den Sümpfen des Elends."}},
[1431] = {"Allianz-Beziehungen", {"Seid still, <Name>, und lasst mich Euch etwas Wichtiges über unser Volk sagen, das nicht auf der Hand liegt.","Wir sind ein stolzes Volk und der Krieg gegen die Menschen hat selbst Thralls große Geduld auf die Probe gestellt. Wisst Ihr, unser Kriegshäuptling ist weise und umsichtig und sein Volk kommt stets an erster Stelle. Letztendlich ist er ein Diplomat und hat in seiner Zeit bei den Menschen viel von ihrer Art gelernt.","Wenn die Geschichte, die ich begonnen habe, Euer Interesse weckt, dann geht zu Keldran beim westlichen Ausgang der Stadt. Dort steht seine Hütte."}, {"Sprecht mit Keldran in Orgrimmar."}},
[1432] = {"Allianz-Beziehungen", {"Wenn die Allianz immer tiefer nach Kalimdor vorstößt, könnten falsche Schlussfolgerungen über mögliche Bündnisse der Horde mit der Brennenden Legion gezogen werden. Dazu darf es der Kriegshäuptling nicht kommen lassen. Und mit seinem Segen übernehme ich die Aufsicht darüber, dass alle Missverständnisse ausgeräumt werden.","Ich habe Agenten in der Gegend von Desolace am Geistwandlerposten nördlich des großen Kodofriedhofs. Wenn Ihr vom verbrannten Tal im Steinkrallengebirge aus südwärts geht, solltet Ihr dort vorbeikommen. Wenn Ihr eintrefft, sucht Takata Steelblade auf."}, {"Sprecht mit Takata Steelblade in Desolace."}},
[1433] = {"Allianz-Beziehungen", {"Die Burning Blade haben eigene Ziele verfolgt; doch wisst, dass sie bis ins Mark böse sind und ihr Geist verderbt wurde. Sie zu vernichten ist die einzige Möglichkeit, ihnen Einhalt zu gebieten.","Keldran schickte Maurin mit mir, weil er die Klinge und die Arglist ihrer Hexenmeister kennt.","Er leitet unsere Verschwörungen gegen die Klinge, während ich mich um die anderen dämonischen Bedrohungen in Desolace kümmere.","Sprecht mit ihm und er wird Euch in seine Pläne einweihen."}, {"Sprecht mit Maurin Bonesplitter in Desolace."}},
[1434] = {"Beschmutzt durch Satyrn", {"Eine Dämonenplage geschieht nicht ohne andere Einflüsse - etwas anderes ermöglicht dem Bösen, Fuß zu fassen.","Es mag mit einem schwachen Geist beginnen, einer unnötigen Gier nach Macht, möglicherweise Rachedurst oder der Verlockung nach höchster Gerechtigkeit. Wenn der Schmerz uns übermannt, dann nutzt uns das Böse aus - das Böse wie die Satyrn.","Bande wurden zwischen ihnen und den Burning Blade geknüpft und Thrall hätte sie getötet. Sucht sie nordöstlich von hier und kommt zu mir zurück, wenn Ihr sie zurückgeschlagen habt."}, {"Besiegt 7 Schurken der Zornsäer, 7 Teufelsanbeter der Zornsäer, 7 Verräter der Zornsäer sowie 7 Höllenrufer der Zornsäer und kehrt zu Takata Steelblade in Desolace zurück."}},
[1435] = {"Brennender Geist", {"Für diese abscheulichen Kreaturen, die ihrem Lehen aus so egoistischen Gründen den Rücken kehren, ist der Tod höchst wünschenswert. Doch wenn man es mit denen zu tun hat, die sich der Dunkelheit verschrieben haben, reicht der Tod allein nicht aus.","Nehmt diesen Edelstein. Wenn einer der Burning Blade dem Tode nahe ist, aktiviert seine Magie. Der Zauber wird den verderbten Geist aus seinem Opfer herausziehen und einen damit erfüllten Edelstein hinterlassen.","Bringt mir diese erfüllten Edelsteine, damit ich sie angemessen entsorgen kann.","Geht jetzt, und möge Euch der Tod meiden."}, {"Bringt den brennenden Edelstein und 15 erfüllte brennende Edelsteine nach Desolace zu Maurin Bonesplitter."}},
[1436] = {"Allianz-Beziehungen", {"Der Kampf läuft gut, <Name>. Vielleicht lässt uns der Kriegshäuptling, bevor die Jahreszeiten wechseln, in unsere Heimat zurückkehren, wenn der Kampf gegen die Dämonen und ihre Unterlinge weiterhin so gute Fortschritte macht.","Kehrt zu Keldran zurück und lasst ihn wissen, was wir getan haben. Ich bin sicher, er belohnt Euch für Eure Taten hier. Seine Sache ist ihm wichtig und es wird ihn mit Stolz erfüllen, wenn er weiß, dass der Kriegshäuptling seine Sorgen respektiert."}, {"Sprecht mit Keldran in Orgrimmar."}},
[1437] = {"Vahlarriels Suche", {"Ich bin Vahlarriel, <Name>. Ich kam her, um mich nach dem Befinden zweier vermisster Adliger einer Kaufmannsfamilie aus Darnassus zu erkundigen - Haus Malem. Der Vater des Hauses fürchtet das Schlimmste. Ich soll tun, was ich kann, um seine vermissten Kinder zu finden. Ich habe noch nicht mit der Suche nach Tyranis und Dalinda begonnen, hoffe aber, ich werde mich nicht lange hier aufhalten.","Zuletzt hörte man, dass sie vor wenigen Wochen durch das Steinkrallengebirge gekommen sein sollen.","Falls Ihr ihren Wagen findet, sucht mich bitte unverzüglich auf."}, {"Sucht und findet den Wagen von Tyranis und Dalinda Malem in Desolace."}},
[1438] = {"Vahlarriels Suche", {"Sucht Dalinda und Tyranis, wenn Ihr könnt, und berichtet mir von Euren Funden. Beginnt Eure Suche in dieser Festung. Ich setze mich sofort mit meinen Vorgesetzten in Darnassus in Verbindung und informiere sie über die Situation.","<Name>, ich möchte Euch noch etwas wissen lassen. Meine Vorgesetzten haben Besorgnis wegen Tyranis und seinen Motiven für diese Reise geäußert. Es ist ihnen wichtiger, dass Dalinda gefunden und wohlbehalten zurückgebracht wird. Tyranis soll ... gefesselt werden, ehe man ihn nach Hause bringt."}, {"Sucht Dalinda Malem in Desolace."}},
[1439] = {"Suche nach Tyranis", {"Wir wurden hierher gebracht, nachdem die Orcs alle unsere Wachen und Diener abgeschlachtet hatten. Oh, was haben sie ihnen Schreckliches angetan! Ich dachte, sie würden uns auf der Stelle töten, doch dann wurde deutlich, dass sie andere Pläne hatten.","Tyranis versuchte, mit ihnen zu reden. Ich glaube, er wollte sie mit seinem arkanen Wissen beeindrucken ... Ich bin nicht sicher, warum.","Bitte, ich kann hier nicht weg, bevor ich weiß, was meinem Bruder zugestoßen ist. Sucht ihn und kommt zu mir, wenn Ihr sein Schicksal kennt."}, {"Sucht Tyranis Malem in Desolace."}},
[1440] = {"Rückkehr nach Vahlarriel", {"Also ist die Zeit gekommen? Ich weiß Eure Bemühungen zu schätzen, und wenn auch die Situation dafür nicht geeignet ist, lasst mich Euch jetzt für alles danken, was Ihr für mich und meine Familie getan habt. Wenn Ihr bereit seid, hier hält mich nichts mehr - wir können aufbrechen, falls es Euch passend erscheint."}, {"Begleitet Dalinda Malem aus der Festung Thunder Axe und kehrt zu Vahlarriel Demonslayer nach Desolace zurück."}},
[1442] = {"Suche nach dem Kor-Edelstein", {"Seid gegrüßt, <Name>. Was kann ich für einen tun, der dem heiligen Weg folgt?","Ah ja, Stilwell, der Schmied... das war vor langer Zeit. Ich erinnere mich gut an ihn, doch ich habe seinen Namen schon eine ganze Weile nicht mehr gehört.","Er schien mir nie ein habgieriger Mann zu sein, daher kann ich nur davon ausgehen, dass sein Wunsch nach einem Kor-Edelstein edle Motive hat.","Wenn Ihr wirklich so einen Edelstein möchtet, dann müsst Ihr in den Nordwesten von Ashenvale. Dort findet Ihr die Blackfathom-Tiefe mit einigen alten Ruinen. Die Naga, die diese Edelsteine noch immer benutzen, sind dort unten. Geht nicht allein, <Klasse>."}, {"Bringt einen Kor-Edelstein zu Thundris Windwirker in Dunkelküste."}},
[1444] = {"Rückkehr zu Fel'Zerul", {"Jammal'an der Prophet verbannte mich aus dem Tempel von Atal'Hakkar. Einst empfand ich den Hohepriester als würdigen Anführer.","Sein unerschütterlicher Glaube an seine Prophezeiung wird den Untergang der Atal'ai herbeiführen. Wenn es nicht schon geschehen ist.","Als ich meiner Besorgnis Ausdruck verlieh, hieß man mich einen Ungläubigen und verstieß mich aus dem Tempel.","Die Artefakte, die Ihr zu mir brachtet, sind harmlos. Aber wenn Jammal'an die richtige Fetischverzauberung für das Beschwörungsritual gefunden hat, droht der Welt große Gefahr..."}, {"Kehrt zu Fel'Zerul in Stonard zurück."}},
[1445] = {"Der Tempel von Atal'Hakkar", {"Also sprach er von Jammal'ans Prophezeiung?","Der spirituelle Anführer der Atal'ai glaubt, wenn Hakkar aus dem Nether nach Azeroth zurückkehrt, wird der Gott dem Stamm der Atal'ai Unsterblichkeit gewähren.","Törichte Arglist, um den Jüngsten Tag schneller herbeizuführen, wenn Ihr mich fragt.","Doch Ihr sagt, der Verbannte sprach von verzauberten Fetischen? Das erfüllt mich mit großer Sorge. Wenn sie wahrhaftig der Schlüssel zu dem Ritual der Atal'ai sind, das dieses Durcheinander verursachte, müssen wir die Mächte verstehen, die in ihnen wohnen.","Begebt Euch in den Tempel und werdet dieser Fetische habhaft!"}, {"Sammelt 20 Fetische von Hakkar und bringt sie zu Fel'Zerul in Stonard."}},
[1446] = {"Jammal'an der Prophet", {"Meine Gefährten aus Witherbark mögen keine Fremden in ihrer Nähe.","Ich? Ich habe keine Heimat mehr. Mein Volk, der große Stamm der Atal'ai aus den Sümpfen des Elends, hat mich verbannt.","Jammal'an, unser spiritueller Anführer, hatte eine, wie wir uns ausdrücken, Prophezeiung. Er glaubt, die Beschwörung des Gottes Hakkar bringt den Atal'ai Unsterblichkeit.","Ich jedoch drängte auf Vorsicht. Wenn die Prophezeiung nun nichts weiter als Manipulation war?","Ich will Rache für meine Verbannung. Bringt mir Jammal'ans Kopf. Vielleicht ist mein Volk dann frei."}, {"Der Verbannte der Atal'ai im Hinterland möchte den Kopf von Jammal'an."}},
[1447] = {"Der vermisste Diplomat", {"Ja, <Klasse>, Ihr seid offenkundig nicht einer der Klügsten Eurer Art, der je nach Stormwind kam, aber einer der Dümmsten, wenn Ihr nicht wieder fortgeht.","Habt Ihr meine Freunde getroffen?"}, {"Besiegt Dashel Stonefist in Stormwind."}},
[1448] = {"Auf der Suche nach dem Tempel", {"Es gab eine Zeit, da war ich jung und voller Kraft, wie Ihr, <Klasse>.","Doch jetzt bin ich alt und vermag die Welt nicht mehr zu erforschen wie einst. Ach, was habe ich nicht alles gesehen!","Aber ein Geheimnis entzog sich mir stets. Als meinen letzten Auftrag schickte mich die Forscherliga auf die Suche nach dem Tempel von Atal'Hakkar. Er sollte Gerüchten zufolge in den Sümpfen des Elends liegen.","Meines gebrechlichen Zustands wegen reiste ich auf einem Greif durch die Lüfte, fand den Tempel aber nie.","Helft Ihr einem alten Zwerg? Vielleicht habt Ihr zu Fuß mehr Glück."}, {"Brohann Caskbelly in Stormwind möchte, dass Ihr den Tempel von Atal'Hakkar sucht."}},
[1449] = {"Ins Hinterland", {"Wie ich schon früher sagte, versuchte ich selbst, den Tempel aus der Luft zu finden. Ganz allein war ich freilich nicht.","Ich reiste zu dem berühmten Greifenhorst bei Aerie Peak im Hinterland. Dort versicherte ich mich der Hilfe eines ihrer besten Piloten. Der Pilot fiel von seinem Reittier in den Sumpf ...","Den Namen des armen Burschen habe ich im Alter vergessen. Der Meister des Horsts kennt ihn zweifellos noch.","Begebt Euch dorthin und sucht nach Falstad Wildhammer.","Wir müssen herausfinden, was dem Piloten zugestoßen ist."}, {"Sprecht mit dem obersten Than, Falstad Wildhammer, in Aerie Peak im Hinterland."}},
[1450] = {"Greifenmeister Talonaxe", {"Ihr seid also nur ein <besserer Botenjunge/besseres Botenmädchen> für einen alten Tattergreis der Forscherliga, hm?","Hört mir gut zu, <Klasse>. Ich habe keine Ahnung, mit wem Euer alter Kumpel da unten in Azeroth rumgeflogen ist. Seh ich etwa wie der Greifenmeister Talonaxe aus? Ja?","Wenn Ihr Euch für die Flugpläne interessiert, warum fragt Ihr dann nicht den Greifenmeister? Wenn Ihr noch länger untätig hier herumhängt, werde ich Euch richtige Arbeit geben.","Und jetzt trollt Euch."}, {"Sprecht mit Greifenmeister Talonaxe im Horst bei Aerie Peak."}},
[1451] = {"Rhapsody-Shindigger", {"Rhapsody fand Gefallen am Schnaps. Versteht mich nicht falsch - alle Zwerge hier im Horst wissen ein gutes Bier zur passenden Stunde zu schätzen.","Aber der alte Shindigger brauchte den Fusel wie ein Greif den Himmel. Und das war echt ein Jammer. Niemand konnte so fliegen wie Rhapsody.","Die ersten Probleme gab es, als er mit Eurem Freund Brohann von dieser Mission zurückkehrte. Danach ging es rapide bergab. Musste ihn rauswerfen.","Zuletzt hörte ich, er habe östlich von hier eine Brauerei eröffnet ..."}, {"Sucht Rhapsody Shindigger im Hinterland."}},
[1452] = {"Rhapsodys Kalimdor-Cocktail", {"Hicks!","Dieser irre Narr Brohann schickt Euch also? Wir haben ein tolles Abenteuer zusammen erlebt... Hicks!","Wenn ich Euch erzählen soll, was da unten im Sumpf passiert ist, brauch ich was Ordentliches zu trinken. Das war echt kein schöner Anblick. Hicks!","Ich sag Euch was... bringt mir, was ich brauche, um einen Kalimdor-Cocktail zu mixen, dann erzähl ich Euch davon.","Man braucht dazu den Muskelmagen eines Roc aus Tanaris... etwas Groddoc- und Eisenfellleber aus Feralas...","Ein schöner kräftiger Kalimdor-Cocktail... dann kann ich plaudern!"}, {"Rhapsody Shindigger im Hinterland möchte, dass Ihr ihm 3 Muskelmägen eines Roc, 3 Groddoc-Lebern und 3 Eisenfelllebern bringt."}},
[1453] = {"Die Geschäfte der Wiederholer in Desolace", {"Es gibt Arbeit in Desolace, <Name>. Das ist ein grässlicher Ort, beschmutzt von Zentauren und anderer Unbill. Aber wenn Ihr eine Woche ehrlicher Arbeit sucht, dann geht dorthin, besucht Kreldig Ungor und sagt ihm, ich hab Euch geschickt.","Ihr findet ihn an der Nijelspitze - dem gemütlichen kleinen Lager in den Bergen des nördlichen Desolace. Haltet Euch einfach ostwärts, wenn Ihr vom Steinkrallengebirge kommt. Man übersieht es leicht, also seid wachsam."}, {"Sprecht mit Kreldig Ungor in Desolace."}},
[1454] = {"Karnitols Schiffbruch", {"Mein erster Auftrag betrifft den Magier Karnitol - eine Art Edelmann in Stormwind. Offenbar wurde sein Schiff vor der Küste im Nordwesten von Meeresriesen angegriffen. Er und seine Gefolgsleute versuchten, eine seiner wertvolleren Truhen an Land zu bringen, aber als die Meeresriesen in Schwärmen angriffen, beschloss er, sie aufzugeben und sich und seine Gefolgsleute in Sicherheit zu teleportieren.","Er möchte, dass wir die Truhe samt ihrem Inhalt finden und zu ihm nach Stormwind bringen."}, {"Sucht Karnitols Truhe in Desolace."}},
[1455] = {"Karnitols Schiffbruch", {"Ihr überlegt eine Weile, was die Kiste aufgebrochen haben könnte, und bemerkt an den Seiten und am Deckel lange, tiefe Kratzer, die belegen, dass sich etwas anderes als ein Mensch oder Riese dafür interessierte. Solche Spuren scheinen nicht von einem Dolch oder Schwert verursacht worden zu sein.","Ihr werft einen Blick in die Truhe und stellt fest, dass sie leer ist.","Da Ihr nicht wisst, was Ihr sonst tun sollt, klappt Ihr die Truhe zu und rüstet zum Aufbruch. Kreldig wird enttäuscht sein."}, {"Kehrt zu Kreldig Ungor in Desolace zurück."}},
[1456] = {"Karnitols Schiffbruch", {"Faszinierend.","Vielleicht besteht ja doch noch Hoffnung.","An derselben Küste, aber viel weiter nördlich, nahe der Insel Ranazjar, liegen Ruinen der Nachtelfen auf dem Grund des Meeres. Ein Stamm von Naga, die Zackenkämme, sind dort zu Hause. Das dürfte unsere beste Chance sein.","Wenn ein Kundschaftertrupp sich so weit in den Süden wagte, müssen sie sehr mächtig sein. Wenn Ihr Kanitols Habseligkeiten finden wollt, würde ich mit den mächtigeren Naga anfangen: den Gezeitenjägern oder den Meerhexen."}, {"Sucht Karnitols Ranzen und bringt ihn zu Kreldig Ungor in Desolace zurück."}},
[1457] = {"Karnitols Schiffbruch", {"Bringt Karnitols Sachen zu Roetten in Ironforge, er wird Euch die gute Arbeit wohl vergelten.","Haha, wenn Ihr nicht aufpasst, wird er so zufrieden sein, dass er Euch bittet, als Festangestellter bei uns zu bleiben.","Wieder einmal gut gemacht, <Name>.","Seid auf dem Rückweg vorsichtig und lasst Euch nicht zu viel Zeit - Karnitol ist nicht der Geduldigste, aber dennoch höchst großzügig."}, {"Bringt Karnitols Ranzen zu Roetten Stonehammer in Ironforge."}},
[1458] = {"Reagenzien für die Wiederholer AG", {"Zu meinen Pflichten hier gehört, Reagenzien für Leftwitch zu beschaffen, einen Gnomen-Magier in Kharanos. Roetten glaubt, das sei ein Kinderspiel, da wir ohnehin schon hier draußen sind. Ihm ist es gleich, dass wir auch so schon unterbesetzt sind.","Die ersten paar Gegenstände auf seiner Liste sollten leicht zu beschaffen sein, und für alle, die mir helfen, springt eine satte Belohnung raus.","Ich brauche einige Hörner und Klauen von den Satyrn der Zornsäer östlich von hier. Bringt sie mir, dann nehmen wir uns den Rest der Liste vor."}, {"Bringt 10 Zornsäerklauen und 10 Zornsäerhörner zu Kreldig Ungor in Desolace."}},
[1459] = {"Reagenzien für die Wiederholer AG", {"Als Nächstes steht auf Leftwitchs Liste... mal sehen... hmm. Oh, da haben wir es.","Recht einfach, aber mit sehr expliziten Anweisungen. Er braucht Gift von den Skorpashi und Bälge von Kodos. Im Kleingedruckten steht, beide müssen von bestimmten Kreaturen sein: Das Gift soll von irgendeinem Skorpashi stammen und der Balg muss von einem gealterten Kodo sein, aber keinem sterbenden oder uralten.","Magier, ich sage Euch, was die so alles wollen... abartig."}, {"Bringt 7 Phiolen Skorpashigift und 3 Bälge von gealterten Kodos zu Kreldig Ungor in Desolace."}},
[1462] = {"Erdsapta", nil, nil},
[1463] = {"Erdsapta", nil, nil},
[1464] = {"Feuersapta", nil, nil},
[1465] = {"Vahlarriels Suche", {"Als Ihr Eure Suche nach dem Wagen fortsetzt, findet Ihr nicht nur einen Anhänger, sondern auch Fußspuren, die in Richtung Süden führen. In der Ferne seht Ihr Rauch von einem großen Fort aufsteigen. Der Rauch stammt von einem Lagerfeuer, nicht von brennenden Gebäuden. Euer Instinkt sagt Euch, dass die Malems, wenn sie noch leben, in diese Richtung gebracht wurden.","Ihr seid der Ansicht, dass Vahlarriel an der Nijelspitze nicht nur den Anhänger sehen sollte, den Ihr gefunden habt, sondern auch von den Lebenszeichen im Süden wissen sollte."}, {"Kehrt mit dem Malem-Anhänger zu Vahlarriel Demonslayer in Desolace zurück."}},
[1466] = {"Reagenzien für die Wiederholer AG", {"Das ist der letzte Gegenstand und ganz ehrlich, er ist nicht schön. Ich gehe auch davon aus, dass Ihr dafür etwas länger braucht als für die anderen Gegenstände.","Doch genug der Warnungen, fangen wir an. Leftwitch sagt, er braucht Folgendes: zehn Gehirne, entweder von Manafressern, Magierjägern oder Leyjägern, zehn Netherflügel von einer Nethermaid, -Schwester oder -Zauberhexerin. Und... zehn Phiolen Verdammniswächterblut von einem Captain oder Lord der Verdammniswächter.","Ganz einfach, hm? Tja... ich hätte Verständnis dafür, wenn Ihr dies ausschlagen wollt."}, {"Bringt 10 Teufelshundgehirne, 10 Netherflügel und 10 Phiolen Verdammniswächterblut zu Kreldig Ungor in Desolace."}},
[1467] = {"Reagenzien für die Wiederholer AG.", {"Wenn Ihr Eure Bezahlung wollt, müsst Ihr nach Ironforge zurück und persönlich mit Roetten sprechen. Er dürfte froh sein, wenn er Leftwitchs Päckchen vom Hals hat. Gebt ihm das, es müsste genügen, damit Ihr Euren Anteil bekommt.","Keine Bange, ich werde trotzdem ein gutes Wort für Euch einlegen, wenn ich zurück bin. Ich sollte nicht mehr lange in Desolace bleiben müssen."}, {"Bringt Leftwitchs Päckchen zu Roetten Stonehammer in Ironforge zurück."}},
[1468] = {"Die Kinderwoche", {"Ihr wollt uns tatsächlich hier im Waisenhaus zur Hand gehen? Seid gesegnet, Freund. Eure Unterstützung in der Kinderwoche ist für uns von unschätzbarem Wert.","Nehmt diese Pfeife. Sie kann sieben Tage lang verwendet werden - über die ganze Dauer der Kinderwoche hinweg. Durch Benutzung der Pfeife, könnt Ihr mit dem Waisenkind sprechen, auf das Ihr aufpasst. Verwendet sie erneut, um das Kind wieder zu entlassen.","Das Kind ist schon ganz aufgeregt und freut sich darauf Euch kennenzulernen. Worauf wartet Ihr noch <Name>, benutzt die Pfeife und trefft Euren Schützling."}, {"Benutzt die Pfeife des menschlichen Waisenkindes um während der Kinderwoche mit Eurem Schützling zu sprechen."}},
[1469] = {"Rhapsodys Geschichte", {"Vor dem Flug trank ich ein ganzes Fass Starkbier. Hicks!","Und was für ein Flug das war. Ich sauste herum und Brohann folgte mir. Aber ich hab keinen Tempel nicht gesehen. Hicks!","Jedenfalls sah ich keinen Tempel, bis mir schwindlig wurde und ich von meinem Reittier fiel. Ich fiel kopfüber ins Wasser und wurde in die Tiefe gesogen. Dort sah ich mich von einer Schar unheimlicher Trolle umringt. Ich schwamm, was das Zeug hielt! Und schaffte es sogar mit diesem Trollsouvenir da raus. Hier, bringt es Brohann und lasst ihn wissen, dass der Tempel tatsächlich da unten ist. Hicks!"}, {"Bringt das Schrifttafelfragment der Atal'ai zu Brohann Caskbelly in Stormwind."}},
[1470] = {"Der zerrissene Schleier", {"Seid gegrüßt, Kind. Ich sehe, dass Ihr noch unerfahren in den Dingen der Hexenmeister seid, dass sich die Macht noch Eurem Griff entzieht. Doch ich spüre Euren Eifer zu lernen.","Und Ihr werdet lernen.","Durch Ignoranz kann man keine Stärke finden. Wissen ist unsere größte Macht. Indem wir es anwenden, können wir die chaotische Magie und die Wesen der Leere kontrollieren und unserem Willen beugen.","Ich zeige Euch, wie Ihr einen Diener an Euren Willen bindet, doch zuerst müsst Ihr mir drei Schädel der klappernden Skelette nahe der verlassenen Schmiede bringen."}, {"Bringt Venya Marthand 3 Schädel eines klappernden Skeletts."}},
[1471] = {"Die Bindung", {"Gewiss habt Ihr den Kreis der Beschwörung unter uns bemerkt. Nur dort könnt Ihr einen ungebundenen Leerwandler herbeirufen.","Mit diesen Runen könnt Ihr ihn herbeirufen, dann müsst Ihr ihn durch die Kraft von Waffen und Magie beherrschen.","Wenn er unterworfen wurde, wird er Euch ergeben sein. Kommt dann zu mir und ich werde den Beschwörungszauber erklären, damit er Euch in jeder Hinsicht zu Willen ist."}, {"Beschwört mit Hilfe der Runen der Beschwörung einen Leerwandler, unterwerft ihn und bringt die Runen der Beschwörung dann zu Carendin Halger im Tempel der Verdammten zurück."}},
[1472] = {"Verschlinger der Seelen", {"Ein Sukkubus ist ein Verschlinger von Seelen, Zerstörer von Herzen, Versucher der Menschen. Eine durch und durch böse und engstirnige Kreatur.","Ohne Anreiz kann er nicht in unsere Welt gebracht werden.","Doch wir wenden seine eigene Kraft gegen ihn an. Den Herzen guter, reiner oder liebender Männer kann der Sukkubus nicht widerstehen und tritt in unsere Welt herüber. Dann könnt Ihr ihn beherrschen und zu Eurem Eigen machen.","Sprecht mit meinem Assistenten Godrick Farsan im Tempel. Er hilft Euch bei der Suche nach einem Köder."}, {"Sprecht mit Godrick Farsan im Tempel der Verdammten."}},
[1473] = {"Kreatur der Leere", {"Alle scheinen sich einig, dass Ihr nunmehr bereit seid zu lernen, wie man einen Leerwandler beschwört.","Ein aus dem jenseitigen Chaos erschaffenes Wesen - wenn Ihr eines beschwört, es aber nicht kontrollieren könnt, zerreißt es Euch.","Egalin beschwor den ersten Leerwandler und hielt in seinem Zauberfoliant fest, wie es bewerkstelligt wird.","Leider wurde er uns von einem Agenten des scharlachroten Kreuzzugs gestohlen, der ihn in seinem Stützpunkt in einem Turm im Westen versteckte. Bringt ihn mir und ich lehre Euch das Wissen."}, {"Beschafft Egalins Zauberfolianten und bringt ihn zu Carendin Halgar in den Tempel der Verdammten."}},
[1474] = {"Die Bindung", {"Benutzt diese Herzen und wirkt einen Zauber am Beschwörungskreis, um eine Tür zu öffnen, durch die der Sukkubus kommen kann. Eure Mächte sind jetzt größer und Ihr werdet sie alle brauchen, um den Sukkubus zu besiegen.","Hütet Euch vor seinen Bezauberungen, <Name>, manch starker Geist wurde schon seinem Willen unterworfen. Wenn er überwunden wurde, kehrt der Sukkubus in die Leere zurück, dann lehre ich Euch den Zauber, um ihn nach Eurem Gutdünken zurückzurufen."}, {"Beschwört mit Hilfe der reinen Herzen einen Sukkubus, unterwerft ihn und bringt die reinen Herzen dann zu Carendin Halger im Tempel der Verdammten zurück."}},
[1475] = {"Im Tempel von Atal'Hakkar", {"Der Legende zufolge ist der Tempel von Atal'Hakkar ein heiliger Schrein, der dem alten Gott Hakkar dem Seelenschinder geweiht ist. Der Tempel, der von einem tückischen Trollstamm namens Atal'ai unterhalten wird, soll angeblich uneinnehmbar sein.","Da der Tempel jetzt im Tränenteich versunken ist, dürfte es noch schwieriger sein, Artefakte zu bergen. Aber genau das müsst Ihr tun, <Name>.","Helft mir dabei, indem Ihr die intakten Schrifttafeln der Atal'ai sammelt. Bringt sie mir und ich schicke der Forschergilde einen Helden zurück. Wir werden uns den Ruhm teilen."}, {"Sammelt 10 Schrifttafeln der Atal'ai für Brohann Caskbelly in Stormwind."}},
[1476] = {"Herzen der Reinen", {"Männer mit Ehre und Güte? Existiert so ein Mann?","Viele Recherchen waren erforderlich, doch durch Geschichten, die ich hörte, konnte ich zwei Männer finden, die genügen sollten.","Der Erste ist Dalin Forgewright, der sich der Fürsorge für die Flüchtlinge von Lordaeron verschrieben hat, die von ihresgleichen am Greymane-Wall verstoßen werden.","Der andere ist Comar Villard, ein Mann, der seiner verlorenen Liebsten treu blieb und im Sumpfland nach Spuren seiner Frau sucht.","Bringt ihre Herzen zu Carendin. Doch zuvor möchte ich Euch Eure Opfer zeigen."}, {"Bringt die Herzen von Dalin Forgewright und Comar Villard zu Carendin Halgar im Tempel der Verdammten."}},
[1477] = {"Überaus wichtige Vorräte", {"Unsere Magier führen Forschungen in der Burg Nethergarde in den verwüsteten Landen durch, die entscheidend für unser Verständnis der dunklen Portale sind. Sie benötigen fast ununterbrochen Nachschub, doch unsere Nachschubwege nach Nethergarde sind dürftig.","Ihr müsst helfen sicherzustellen, dass der Nachschub nach Nethergarde gelangt. Meldet Euch bei Wachhauptmann Sorigal in Dunkelhain und tut, was er verlangt. Diese Aufgabe führt Euch in Feindesland, <Name>. Macht Euch bereit."}, {"Sprecht mit Wachhauptmann Sorigal."}},
[1478] = {"Halgars Beschwörung", {"Ah, <Name>! Carendin Halgar ließ mich soeben wissen, dass er Euch für weitere Ausbildung sehen möchte.","Ihr solltet stolz auf Euch sein, <Name> Es kommt nicht jeden Tag vor, dass Meister Halgar persönlich einen jungen Hexenmeister unterrichtet.","Ihr findet ihn im Magieviertel von Undercity nahe des Tempels der Verdammten."}, {"Sprecht mit Carendin Halgar in Undercity."}},
[1479] = {"Der Baum der Ewigen", {"Ich habe Geschichten von den Nachtelfen gehört, darüber, dass sie ihre Bank in einen Baum gebaut haben, der aber wie ein Bär aussieht. Das ist so cool! Nachtelfen sind niedlich, wie sie die Natur lieben und so, aber Bären sind der Hammer!","Würdet Ihr bitte mit mir den Bärenbaum in Darnassus angucken gehen? Es gibt ein Boot, das uns ganz in die Nähe dorthin bringt und es wird auch bestimmt nicht lange dauern. Bitte, bitte, bitte!"}, {"Begleitet Euren Schützling zur Bank in Darnassus. Die dortige Bank ist in einen Baum gebaut, welcher als der Baum der Ewigen bekannt ist."}},
[1480] = {"Der Verderber", {"Die zerrissene Haut riecht verfault und ist erstaunlich geschmeidig für etwas, das so trocken und zerbrechlich wirkt.","Als Ihr sie aufrollt, könnt Ihr deutlich die roten Zeichen einer Art Sprache auf der einen Seite erkennen. Am unteren Ende der Haut befindet sich ein sehr deutliches Symbol, das Ihr für eine Art Unterschrift haltet.","Vielleicht könnte Maurin Euch mehr darüber erzählen?"}, {"Bringt die geschundene Dämonenhaut nach Desolace zu Maurin Bonesplitter."}},
[1481] = {"Der Verderber", {"Ich denke, mit den entsprechenden Reagenzien kann ich diesen Dämonenlord finden.","Wenn er wirklich so mächtig ist, wie ich vermute, brauche ich gesammelte Gegenstände von tödlichen Kreaturen. Doch Ihr handelt im Namen des Kriegshäuptlings, daher werdet Ihr die Aufgabe gewiss übernehmen.","Als Erstes muss ich jeden Schutzzauber brechen, den er gegen Wahrsagen hat. Daher brauche ich den Skalp eines Satyrs... aber nicht den eines beliebigen Satyrs. Der Skalp muss von einem Schattenpirscher der Zornsäer stammen.","Sucht in den antiken Ruinen nordöstlich von Desolace nach ihnen."}, {"Bringt Maurin Bonesplitter in Desolace einen Schattenpirscherskalp."}},
[1482] = {"Der Verderber", {"Ein Orakelkristall wäre genau richtig. Die Naga stellen sie her, damit ihre Orakel sie benutzen können.","Nordwestlich von hier liegt ein großes Korallenriff vor der Küste, das die Naga als ihre Heimat nutzen, da es so nahe am Ufer liegt. Dort könnt Ihr ihre Orakel finden. Die Zackenkammnaga sind tödlich und reagieren ungnädig auf Euer Eindringen, seid daher vorsichtig und eilt Euch.","Wenn Ihr zurückkehrt, suchen wir diesen Dämonenlord Azrethoc."}, {"Bringt Maurin Bonesplitter in Desolace einen Orakelkristall."}},
[1483] = {"Ziz Fizziks", {"Die Venture Company stahl viele meiner besten Goblin-Denker, aber von Zeit zu Zeit bekommen wir Gelegenheit, einige zurückzustehlen.","Vor einiger Zeit stellte die Filiale der Venture Company im Steinkrallengebirge Gerenzo Wrenchwhistle ein... einen Gnom! Ihr anderer Ingenieur, Ziz Fizziks, war so erschüttert davon, dass er mich anflehte, ihm eine neue Stelle beim Tüftlerverband zu besorgen. Ich gab ihm einige Aufgaben, aber möglicherweise braucht er dabei Hilfe.","Vielleicht könntet Ihr mal nach dem Rechten sehen? Er ist am Westrand der Scherwindklippe stationiert."}, {"Sprecht mit Ziz Fizziks bei der Scherwindklippe."}},
[1484] = {"Der Verderber", {"Diese Kreatur ist weitaus gefährlicher als ich dachte. Zweifellos stellt Takata in Frage, dass ich so einer Kreatur gewachsen bin, und es würde ihn in seiner Ehre kränken, wenn er nicht wüsste, was gerade passierte.","Sprecht mit dem Schwertkämpfer, sagt ihm, Ihr seid bereit, den Dämon zu erschlagen, wenn er es erlaubt, aber bedrängt ihn nicht zu sehr. Es ist besser, wenn er denkt, dass es seine Idee war."}, {"Sprecht mit Takata Steelblade in Desolace."}},
[1485] = {"Üble Familiare", {"Die Hexenmeister, die vor uns kamen, weckten das Misstrauen der Horde. Man toleriert uns zwar, aber man liebt uns nicht.","Das ließ die Hexenmeister freilich nicht näher zusammenrücken, da viele in Abgeschiedenheit leben und ihre Magie allein erforschen. Ich will Euch nicht auf den einen oder anderen Weg führen, sondern nur helfen, reifer zu werden.","Bevor ich Euch die Kunst des Beschwörens lehre, bringt mir sechs Köpfe der schändlichen Familiare aus der Höhle im Nordosten."}, {"Bringt 6 Köpfe schändlicher Familiare zu Ruzan."}},
[1486] = {"Deviatbälge", {"Als Naralex tiefer in seinen Alptraum vorstieß, stieg eine seltsame Schar von Bestien unter dem Brachland in die Höhlen des Wehklagens vor.","Diese Deviatkreaturen haben seltsame, überirdische Eigenschaften. Sie sind zwar von Natur aus böse, doch bin ich der Meinung, dass ihre Existenz hier in Kalimdor auch etwas Gutes haben kann. Ich glaube, ihre Bälge sind besonders gut für die Lederverarbeitung geeignet.","Wenn Ihr Euch der Aufgabe gewachsen fühlt, steigt in die Höhlen unter uns hinab und holt einige Deviatschuppen."}, {"Nalpak in den Höhlen des Wehklagens möchte 20 Deviatbälge."}},
[1487] = {"Ausrottung der Deviat", {"Naralex hatte ein edles Ziel.","Unser großer Anführer trachtete danach, in den Smaragdgrünen Traum zu gelangen und dabei zu helfen, aus diesem kargen Land wieder den üppigen Wald zu machen, der hier einst wuchs. Aber etwas ist furchtbar schief gegangen.","Naralex' Traum wurde zum Alptraum. Verderbte Kreaturen siedelten sich in den Höhlen an.","Während einige Jünger von Naralex versuchen, unseren Meister zu wecken, ist es mein Trachten, die Höhlen von diesen bösen Bestien zu befreien.","Wagt Euch in die Höhlen, <Name>, und löscht die Deviatbrut aus."}, {"Ebru in den Höhlen des Wehklagens möchte, dass Ihr 7 Deviatverheerer, 7 Deviatvipern, 7 Deviatschlurfer und 7 Deviatschreckensfange tötet."}},
[1488] = {"Der Verderber", {"Wiewohl ich nichts über diesen Lord Azrethoc weiß, stimme ich mit Maurin überein, er muss aufgehalten werden. Besonders wenn er versucht, Einfluss über die Burning Blade zu bekommen, um seine Ziele voranzutreiben.","Ihr habt schon gezeigt, dass Ihr den Kriegshäuptling unterstützen möchtet. Ich verlasse mich darauf, dass Ihr eine Truppe zusammenstellt, die den Dämonenlord und seinen Hexenmeisterdiener besiegt.","Wenn Ihr bereit seid, geht direkt nach Süden und kehrt zu mir zurück, wenn die Bedrohung beseitigt wurde."}, {"Besiegt Lord Azrethoc und Jugkar Grim'rod. Kehrt dann zu Takata Steelblade in Desolace zurück."}},
[1489] = {"Hamuul Runetotem", {"Die Schalen, die Ihr mir gebracht habt, haben eine Besudelung, eine, die ich nie zuvor gesehen habe. Ich bin mir sicher, dass das Wasser der Oasen der Grund dafür ist. Ich spüre die Verwirrung, die sie über das Land bringen. Es ist, als würde das Land träumen und nicht erwachen können.","Die Höhlen des Wehklagens müssen die Quelle dieser Besudelung sein. Doch ehe Ihr dorthin geht, solltet Ihr mit dem Druidenältesten Hamuul Runetotem sprechen. Ich ließ ihn über die Vorfälle im Brachland informieren. Er ist weise und kann Euch Rat geben.","Ihr findet ihn in Thunder Bluff auf der Anhöhe der Ältesten."}, {"Sprecht mit Hamuul Runetotem."}},
[1490] = {"Nara Wildmane", {"Ich betrat das Land der Träume und träumte von einem Druidenkult, der in den Höhlen des Wehklagens haust. Ich sah, dass ihre Ziele einst edel waren, sie jedoch vom rechten Weg abkamen. Sie sind jetzt Diener der Besudelung, die das Brachland befallen hat.","Ich bin alt und meine Kraft verlässt mich. Ich konnte nicht lange an diesem Traumort verweilen und weiß wenig mehr von den besudelten Druiden. Aber Nara ist jung und starken Willens. Sie blieb in dem Traum zurück, als ich floh.","Sprecht mit Nara. Sie ist in jenem Zelt da drüben."}, {"Sprecht mit Nara Wildmane."}},
[1491] = {"Klugheitstränke", {"Nun gut, die Raptorenhörner haben nicht funktioniert. Aber das macht nichts... nicht alles klappt beim ersten Mal! Außerdem habe ich wohl etwas gefunden, das in meinen Klugheitstränken noch besser funktioniert!","Es gibt eine Substanz, die Klageessenz genannt wird. Ich glaube, sie ist die Ursache für die seltsamen Pflanzen und Tiere im Brachland. Bringt mir etwas davon, dann erprobe ich es in meinem Trank!","Ich hörte, die Essenz reichert sich in Ektoplasmakreaturen in den Höhlen des Wehklagens im Westen an. Jagt diese Kreaturen und sammelt die Essenz."}, {"Bringt 6 Portionen Klageessenz zu Mebok Mizzyrix in Ratchet."}},
[1492] = {"Werftmeister Dizzywig", {"Ich muss eine Kiste mit Tränken, Reagenzien und anderen Gegenständen zu meinen Geschäftspartnern in der alten Welt schaffen. Ich habe kaum Zeit, sie selbst zum Dock von Ratchet zu bringen, und die letzte Karawane ist schon aufgebrochen.","Könntet Ihr sie bitte freundlichst zu Werftmeister Dizzywig bringen? Er wird dafür sorgen, dass sie an Bord des nächsten Schiffes zur Schwarzmeerbucht kommt."}, {"Bringt die gesicherte Kiste zu Werftmeister Dizzywig in Ratchet."}},
[1498] = {"Weg der Verteidigung", {"Als Krieger ist eine unserer wichtigsten Aufgaben... der Schutz. Wir sind der Schild der Horde und sorgen für die Sicherheit unserer schwächeren Brüder. Wenn Ihr Euch bei uns einreihen möchtet, müsst Ihr mir Eure Tauglichkeit beweisen.","Geht zum Donnergrat in Durotar, wo die Blitzechsen und Donnerechsen hausen. Jagt die Bestien! Zeigt mir, dass Ihr härter als sie seid! Trotzt ihren Angriffen und bringt mir einen Stapel ihrer versengten Schuppen als Beweis.","Gelingt Euch das, unterrichte ich Euch."}, {"Bringt 5 versengte Schuppen zu Uzzek im Farwatch-Posten im Brachland."}},
[1499] = {"Üble Familiare", {"Zureetha hat Novizen wie Euch geschickt, damit sie helfen, die Zahl der Kreaturen in der Höhle einzudämmen, in deren Nähe Ihr gegen die schändlichen Familiare gekämpft habt.","Ich finde, es wäre gut für Euch, wenn Ihr Erfahrung sammelt, indem Ihr ihr helft. Erwähnt unbedingt, dass Ihr mitgeholfen habt, das Wichtel-Problem zu lösen, womöglich hat sie eine Belohnung für Euch."}, {"Sprecht mit Zureetha Fargaze."}},
[1501] = {"Kreatur der Leere", {"So wenige Hexenmeister sind übrig... Wir riskieren viel, doch die Risiken sind gerechtfertigt. Ihr müsst bald Reife in Eurer Fähigkeit erlangen, wenn Ihr der Horde und dem Kriegshäuptling nützlich sein wollt.","Es ist Zeit, dass Ihr einen neuen Beschwörungszauber lernt.","Als Erstes müsst Ihr die Schrifttafel der Verga wiederbeschaffen, die von Hexenmeistern der Burning Blade gestohlen wurde und nun in ihrem Unterschlupf in der Knochenhöhle verwahrt wird. Mit der Tafel zeige ich Euch die Runen, mit denen ein Leerwandler beschworen wird."}, {"Holt die Schrifttafel der Verga für Gan'rul Bloodeye in Orgrimmar."}},
[1502] = {"Thun'grim Firegaze", {"Es ist wichtig, dass ein Krieger eine hochwertige Waffe hat. Und wenn es einen Orc gibt, der hochwertige Waffen herstellen kann, dann ist das Thun'grim Firegaze. Er ist ein Einsiedler, der auf den Hügeln östlich von Crossroads haust.","Wenn Ihr eine Waffe sucht, Krieger, dann solltet Ihr Euch an Thun'grim wenden. Um ihn zu finden reist Ihr zuerst nach Crossroads im Westen und wendet Euch dann nach Osten in die Berge. Sein Lager liegt hoch auf einem Berggipfel."}, {"Sprecht mit Thun'grim Firegaze im Brachland."}},
[1503] = {"Geschmiedeter Stahl", {"Ich gebe Euch eine Waffe, <Name>, doch vorher müsst Ihr mir helfen. Vor einigen Nächten kamen die Grimmhauer von Nordwesten in mein Lager. Sie wollten nicht mit mir kämpfen, wissen aber, wie schlecht es um mein Augenlicht bestellt ist. Einige lenkten mich durch Schnauben ab, während die anderen mich bestahlen. Diese Feiglinge!","Sie nahmen eine Eisentruhe mit, in der sich Schmiedestahlbarren befanden. Wenn Ihr sie wiederbeschaffen könnt, bekommt Ihr die Waffe, die Ihr sucht.","Sucht im Stacheleberlager nach der Eisentruhe und bringt die Schmiedestahlbarren zurück."}, {"Bringt die Schmiedestahlbarren zu Thun'grim Firegaze im Brachland."}},
[1504] = {"Die Bindung", {"Nun studiert die Schrifttafel der Verga, denn sie enthält die Glyphen, die Ihr für die Beschwörung braucht. Bringt sie zu dem Kreis in Neeru Fireblades Zelt auf der anderen Seite der Höhle. Dort könnt Ihr den Leerwandler herbeirufen.","Ihr müsst ihn mit allen körperlichen und magischen Mitteln besiegen, die Euch zur Verfügung stehen. Wenn das vollendet ist, kommt mit den Glyphen der Beschwörung zu mir zurück und ich führe Euch vor, wie Ihr die Kreatur an Euren Willen bindet."}, {"Beschwört mit Hilfe der Glyphen der Beschwörung einen Leerwandler, unterwerft ihn und bringt dann die Glyphen der Beschwörung zu Gan'rul Bloodeye in Orgrimmar zurück."}},
[1505] = {"Veteran Uzzek", {"Die Horde ehrt ihre Krieger, da ihre Kraft außer Frage steht. Doch die wahrhaft großen Helden sind auch große Schüler.","Sucht nach Uzzek. Er ist ein Veteran zahlreicher Kämpfe und unterrichtet jetzt junge Krieger in der Gefechtskunst. Ihr findet ihn südlich des Farwatch-Posten, der Orc-Garnison an der Grenze von Brachland und Durotar im Osten."}, {"Sprecht mit Uzzek im Farwatch-Posten."}},
[1506] = {"Gan'ruls Beschwörung", {"Ihr da. <Name>, richtig?","Ich hoffe, Ihr seid nicht zu sehr beschäftigt, denn Gan'rul möchte Euch unverzüglich sprechen. Ihr findet ihn mit den Hexenmeisterältesten in der Kluft der Schatten.","Meinen früheren Erfahrungen mit Gan'rul nach zu urteilen, bedeutet Eure Einbestellung entweder etwas sehr Gutes oder etwas sehr Schlechtes."}, {"Sprecht mit Gan'rul Bloodeye in Orgrimmar."}},
[1507] = {"Verschlinger der Seelen", {"Eure Zeit ist gekommen, mehr über die Beschwörungsmagien zu lernen. Ihr werdet lernen, einen Sukkubus zu beschwören, eine Kreatur mit unglaublichen geistigen Mächten. Er kann sich schwache Geister zu Diensten machen... doch seid unbesorgt! Ich bin sicher, Ihr seid der Herausforderung gewachsen.","Ein Sukkubus kann nur beschworen werden, wenn man ihn mit einem Gegenstand ködert, der große Hingabe und Liebe bedeutet. Ich überlasse es Euch, so ein Objekt zu finden.","Ein Vorschlag? Sprecht mit Cazul... Es scheint wenig zu geben, das der alte Hexenmeister nicht weiß."}, {"Sprecht mit Cazul in der Kluft der Schatten."}},
[1508] = {"Der blinde Cazul", {"Hmmm... Aber natürlich seid Ihr nicht gekommen, um deshalb mit mir zu sprechen.","Wenn Gan'rul Euch zu mir geschickt hat, dann gewiss, weil Ihr einen Sukkubus beschwören sollt und einen Gegenstand braucht, um ihn anzulocken.","Hmm... Wenn Ihr Euch von einem Blinden führen lassen möchtet, <Name>, dann hört zu. In den Hängen von Orgrimmar, westlich des Himmelsturms im Tal der Stärke, lebt Zankaja. Sie wartet darauf, dass ihr Lebensgefährte zurückkehrt.","Geht zu ihr."}, {"Sprecht mit Zankaja in Orgrimmar."}},
[1509] = {"Neuigkeiten von Dogran", {"Dogran, mein Gefährte, wurde jüngst zum Dienst nach Crossroads geschickt. Es war eine Beförderung und er war sehr stolz.","Ich war natürlich traurig, ließ es ihn aber nicht wissen. Und auch besorgt. Das Brachland ist gefährlicher als Orgrimmar, daher fürchtete ich das Schlimmste.","Seit einer Woche sollte er nun schon zu Hause sein und ich habe kein Wort von ihm gehört...","Vielleicht könntet Ihr nach Crossroads gehen und nachfragen, ob es Neuigkeiten gibt? Er arbeitete mit Gazrog, der noch dort stationiert sein sollte."}, {"Sprecht mit Gazrog in Crossroads."}},
[1510] = {"Neuigkeiten von Dogran", {"Er war im Camp Taurajo stationiert, wo wir versuchten, die Stacheleber auszurotten. Er führte ein paar Grunzer zu einem ihrer Lager, wo er schwer verwundet wurde. Die anderen schafften es zurück und versuchten, ihn zu retten. Er war in einem schlimmen Zustand, seine Wunden eiterten, daher wollten sie ihn nicht mitnehmen, ohne ihn erst zu versorgen.","Sie schickten eine Nachricht hierher und baten um ein Heilgebräu von den Trollen der Malaka'jin im Steinkrallengebirge. Wir schickten Leute, um eines zu holen, aber diese sind noch nicht zurückgekehrt."}, {"Sprecht mit Ken'zigla am Malaka'jin."}},
[1511] = {"Ken'ziglas Trunk", {"Ich dachte mir, dass ein anderer kommen würde, um den Trunk zu holen, daher habe ich ihn dennoch vorbereitet.","Da habt Ihr ihn, <Name>. Aber denkt daran, Ihr solltet ihm nur die Hälfte geben! Dadurch bekommt er genug Kraft, sich zu bewegen, ohne dass er verblutet. Wartet eine Nacht und gebt ihm dann die andere Hälfte.","Wie auch immer, Ihr solltet dies zu Logmar im Camp Taurajo bringen. Nach allem, was er mir gesagt hat, sollte er wissen, wo dieser Dogran ist."}, {"Bringt Ken'ziglas Trunk zu Grunzer Logmar im Camp Taurajo."}},
[1512] = {"Die Liebesgabe", {"Zankaja ist jung und wunderschön. An einen Invaliden gekettet zu sein... Ich dulde nicht, dass ihr diese Ungerechtigkeit widerfährt.","Lasst mich allein, <Name>. Und habt Dank. Vielleicht beende ich mein Leben im ruhmreichen Kampf statt als leblose Hülle.","Und bitte erzählt meiner Gefährtin nichts davon... Ich möchte nicht, dass sie sich Schuld auflädt."}, {"Bringt den schmutzverkrusteten Anhänger zu Gan'rul Bloodeye in Orgrimmar."}},
[1513] = {"Die Bindung", {"Bringt dies zum Beschwörungskreis in Neerus Zelt, <Name>. Beschwört einen schändlichen Sukkubus und zeigt ihm Eure Überlegenheit. Ich glaube an die Kraft Eurer Magie. Aber seid wie stets wachsam, denn Stolz und Selbstüberschätzung wurden manchem viel versprechenden Hexenmeister zum Verhängnis.","Wenn Ihr ihn besiegt habt, bringt den Anhänger zu mir, damit ich Zankaja die Neuigkeit überbringen kann."}, {"Beschwört mit Hilfe von Dograns Anhänger einen Sukkubus, unterwerft ihn und bringt dann Dograns Anhänger zu Gan'rul Bloodeye in Orgrimmar zurück."}},
[1514] = {"Verderbte Windblüte", nil, nil},
[1515] = {"Dograns Gefangenschaft", {"Ihr habt es bis hierher geschafft, wenn Ihr es bis zu Ende durchstehen möchtet, werde ich Euch nicht aufhalten. Aber ich kann keine Leute erübrigen, Ihr seid also auf Euch allein gestellt.","Dogran wird in einem Lager der Stacheleber nördlich von hier festgehalten. Sucht nach zwei nebeneinander stehenden Hütten. Er wird schwer bewacht... Das heißt, wenn sie ihn noch nicht getötet haben oder er seinen Verletzungen erlegen ist.","Viel Glück."}, {"Bringt Ken'ziglas Trunk zu Dogran im Brachland."}},
[1516] = {"Ruf der Erde", {"Die Zeit ist gekommen, junger <Klasse>. Ihr seid stark geworden und Euer Geist ist ausdauernd wie die Erde. Dass Ihr dem Pfad des Schamanen folgt, zeigt, dass Ihr Weisheit besitzt, sogar bevor Ihr Euch selbst beweist.","Das Element der Erde wird über Euer Schicksal wachen und Teil von Euch werden, wenn Ihr bereit seid. Doch Ihr müsst vor der Erde selbst bestehen. Wenn Ihr bereit seid, werdet Ihr Dinge sehen, von denen nur Schamanen wissen.","Sucht die Teufelspirscher im Norden und beschafft Euch zwei ihrer Hufe. Kehrt dann zu mir zurück und wir sprechen weiter über Eure Zukunft."}, {"Bringt 2 Teufelspirscherhufe zu Canaga Earthcaller im Tal der Prüfungen."}},
[1517] = {"Ruf der Erde", {"Ein Sapta ist ein Getränk, mit dem unsere Seelen an die Elemente gebunden werden. Mit Magie gestärkt lässt dieser Trank die Willensstarken die Elemente sehen, wie es sonst niemand kann. Euer Geist wird mit dem Element verbunden, für das das Sapta geschaffen wurde. Nur wenige auserwählte Schamanen kennen das Rezept.","Das Sapta wird stets an einem heiligen Ort getrunken, niemals anderswo ... vergesst das nicht.","Besucht den Geisterfelsen südwestlich von hier ... er befindet sich am Ende des verborgenen Pfades. Trinkt Euer Sapta dort und ... tja, Ihr werdet schon sehen."}, {"Sucht den Geisterfelsen und trinkt das Erdsapta."}},
[1518] = {"Ruf der Erde", {"Nehmt diesen Rohquarz von mir und bringt ihn als Beweis für unsere Begegnung zu Canaga Earthcaller. Er wird ein Totem für Euch anfertigen, dessen Herzstück dieser Kiesel sein soll. So winzig er scheinen mag, Ihr werdet feststellen, dass es nicht auf die Größe ankommt und selbst die kleinsten Dinge viele anderen überdauern können.","Habt Geduld, <Name>. Bleibt stark und sucht Weisheit. Dies verlangt die Erde von Euch. Am Ende solltet Ihr eins mit der Erde werden... respektiert sie so, wie sie Euch respektiert."}, {"Bringt den Rohquarz zu Canaga Earthcaller im Tal der Prüfungen."}},
[1519] = {"Ruf der Erde", {"Die Zeit ist gekommen, <Klasse>. Euer Körper ist ausgereift und stark geworden und Euer Geist ist ausdauernd wie die Erde. Ich sehe es Euch an... Ihr besitzt bereits Weisheit.","Die Erde leitet unser Volk und spricht zu uns... und bald auch zu Euch. Aber Ihr müsst geprüft werden. Wenn Ihr bereit seid, werdet Ihr Dinge sehen, von denen nur unsere Art weiß.","Sucht die Schamanen der Borstennacken in ihrer Klamm im Osten auf und beschafft Euch eine ihrer Salben, die sie für ihre Rituale verwenden. Kehrt erst zu mir zurück, wenn Ihr genügend davon für Euren eigenen Trank habt."}, {"Bringt 2 Dosen Ritualsalbe zu der Seherin Ravenfeather im Camp Narache."}},
[1520] = {"Ruf der Erde", {"Ein Sapta ist ein Getränk, mit dem unsere Seelen an die Elemente gebunden werden. Mit Magie gestärkt lässt dieses Elixier die, die offenen Geistes sind, die Elemente sehen, wie sie zuvor noch von niemandem gesehen wurden. Euer Geist wird ewig an das Element gebunden, für das das Sapta geschaffen wurde. Nur wenige Auserwählte unserer Art kennen das Geheimnis seiner Herstellung.","Das Sapta wird stets an einem heiligen Ort getrunken und niemals anderswo.","Sucht den Schamanenschrein östlich von hier und trinkt von dem Sapta."}, {"Sucht den Kodofels und trinkt das Erdsapta."}},
[1521] = {"Ruf der Erde", {"Nehmt diesen Rohquarz von mir und bringt ihn als Beweis für unsere Begegnung zu der Seherin Ravenfeather. Sie wird ein Totem für Euch anfertigen, dessen Herzstück dieser Kiesel sein soll. So winzig er scheinen mag, Ihr werdet feststellen, dass es nicht auf die Größe ankommt und selbst die kleinsten Dinge Berge überdauern können.","Habt Geduld, <Name>. Bleibt stark und sucht Weisheit. Dies verlangt die Erde von Euch. Am Ende solltet Ihr eins mit der Erde werden... respektiert sie so, wie sie Euch respektiert."}, {"Bringt den Rohquarz zu der Seherin Ravenfeather im Camp Narache."}},
[1522] = {"Ruf des Feuers", {"Feuer sei mit Euch, Mann. Ich sehe, Ihr seid schon bewandert in den Wegen der Erde und scheint mir bereit, mit Kranal zu sprechen.","Kranal lebt nördlich von Crossroads im Brachland. Er kennt sich mit den Elementen aus und kann Euch was übers Feuer beibringen ... wenn Ihr denkt, dass Ihr bereit seid.","Er ist leicht zu finden, geht nur nicht zu weit. Wenn Ihr zu weit geht, seid Ihr plötzlich im Elfenland."}, {"Sucht Kranal Fiss im Brachland."}},
[1523] = {"Ruf des Feuers", {"Es ist lange her, seit Ihr zuletzt geprüft wurdet, <Name>. Bislang habt Ihr bei Euren Studien Geduld bewiesen, doch die Zeit ist gekommen, da Ihr mehr lernen müsst. Die Zeit ist gekommen, mehr über Zerstörung zu lernen... und über Chaos. Ihr könnt das Element des Feuers verstehen lernen, wenn Ihr bereit seid, dafür zu bezahlen.","Sucht nach Kranal Fiss im Brachland. Er lebt in einer kleinen Siedlung nördlich von Crossroads, aber gebt Acht, wenn Ihr das Land der Nachtelfen erreicht, seid Ihr zu weit gegangen."}, {"Sucht Kranal Fiss im Brachland."}},
[1524] = {"Ruf des Feuers", {"Ihr seid mächtig genug, das Totem des Feuers zu tragen, doch bedeutet das nicht, dass Ihr bereit seid.","Am Südstromfluss in Durotar, vor dem großen Meer, liegt ein verborgener Pfad zu einem der höchsten Gipfel des Landes. Sucht einen Weg nach oben. Dann werdet Ihr einen unserer heiligen Schreine finden, den Telf Joolam bewacht ... den Schrein der schlafenden Flamme.","Bringt ihm diese Fackel ... sie zeigt, dass Ihr versucht, das Feuer zu bändigen. Bringt sie mir zurück, wenn Ihr sie an der Kohlenpfanne des Schreins entzündet habt, und ich sorge dafür, dass Ihr das Totem des Feuers bekommt."}, {"Bringt die Fackel der schlafenden Flamme zu Telf Joolam in Durotar."}},
[1525] = {"Ruf des Feuers", {"Aber Ihr seid doch nicht zum Geschichtsunterricht hergekommen, oder? Ihr seid wegen der ewigen Flamme und einem Feuertotem gekommen.","Zunächst einmal müsst Ihr ein Sapta anfertigen und Euch selbst an das Feuer binden.","Ich kann das Feuersapta für Euch herstellen, wenn Ihr mir zwei Gegenstände bringt. Der Erste ist Feuerteer, ein einfacher Gegenstand, den normalerweise die Zauberer der Grimmhauer im östlichen Brachland mit sich führen.","Der zweite Gegenstand dürfte gefährlicher zu bekommen sein: ein Reagenzienbeutel von einem Kultisten der Burning Blade. Ihr findet sie in einer Höhle über den Klammen nordöstlich von Klingenhügel."}, {"Bringt 1 Feuerteerstück und 1 Reagenzienbeutel zu Telf Joolam in Durotar."}},
[1526] = {"Ruf des Feuers", {"Das Feuersapta ist fertig. Die Zeit ist gekommen, dass Ihr Euch der Flamme stellt. Benutzt das Sapta am Schrein und geht zum Berggipfel, um Euch dort der Manifestation zu stellen. Er wird Euch erst angreifen, wenn Ihr bereit seid, wird aber wissen, dass Ihr ihn sehen könnt.","Schlagt ihn schnellstens nieder, <Name>, denn seine Macht wächst, je länger Ihr braucht, um ihn zu besiegen. Wenn er bezwungen wurde, legt seine Glut in die Kohlenpfanne und entzündet die Fackel."}, {"Besiegt die geringe Feuermanifestation und legt Glut in die Kohlenpfanne auf dem Schrein der ewigen Flamme."}},
[1527] = {"Ruf des Feuers", {"Mit der Fackel fest in der Hand wendet Ihr Euch von der Kohlenpfanne ab und lasst den Blick über Durotar schweifen. Ihr spürt, wie Aufregung Euren Geist erfüllt, als der Wind anschwillt und die Flamme heftig flackern lässt.","Auf der Suche nach Kranal Fiss betretet Ihr den Pfad zurück ins Brachland."}, {"Bringt die Fackel der ewigen Flamme zu Kranal Fiss im Brachland."}},
[1528] = {"Ruf des Wassers", {"Wasser sei in Eurer Zukunft, Mann. Ich sehe, Ihr seid schon bewandert in den Wegen des Feuers und scheint mir bereit, mit Islen zu sprechen.","Islen lebt an der Küste des Brachlandes. Wenn Ihr am Ufer des Südstroms nach Süden geht, könnt Ihr sie gar nicht verfehlen ... achtet auf den großen Hai! Sie kennt sich mit den Elementen aus und kann Euch etwas übers Wasser beibringen ... wenn Ihr denkt, dass Ihr bereit seid.","Sie ist leicht zu finden, geht nur nicht zu weit nach Süden ... sie ist gleich hinter Ratchet."}, {"Sucht Islen Waterseer im Brachland."}},
[1529] = {"Ruf des Wassers", {"Es ist lange her, seit Ihr zuletzt geprüft wurdet, <Name>. Bislang habt Ihr bei Euren Studien Geduld bewiesen, doch die Zeit ist gekommen, da Ihr mehr lernen müsst. Die Zeit ist gekommen, mehr über die Reinheit des Wassers zu lernen.","Sucht nach Islen Waterseer im Brachland. Sie befindet sich an der Ostküste, südlich von Ratchet und dem Südstrom. Ihr findet sie in ihrer Fischerhütte. Viel Glück, <Name>."}, {"Sucht Islen Waterseer im Brachland."}},
[1530] = {"Ruf des Wassers", {"Wenn Ihr eine Lektion über Wasser lernen müsst, dann diese: Wasser bedeutet Wiedergeburt.","Seine Macht fließt, erodiert und reinigt alle Arten von Dingen. Seine Fähigkeit zu heilen und zu regenerieren ist unvergleichlich, jedoch nur, wenn es rein ist.","Wenn Ihr begreift, dass verunreinigtes Wasser alles verheeren kann, was es berührt, werdet Ihr schätzen lernen, wie lebenswichtig dieser Rohstoff ist.","Wenn Ihr das Wassersapta möchtet, sucht Brine tief im südlichen Brachland. Ihr Heim wird unablässig von den dortigen Stachelebern heimgesucht."}, {"Sucht Brine im südlichen Brachland."}},
[1531] = {"Ruf der Luft", {"Macht Euch auf den Weg, Mann. Ihr seid bereit, Euch des Lufttotems würdig zu erweisen, aber zuerst müsst Ihr Prate Cloudseer in Tausend Nadeln finden. Wenn Ihr sie mit ausreichend Respekt behandelt, wird sie bereit sein, Euch zu sagen, wie Ihr das Lufttotem bekommen könnt.","Geht durch das Brachland nach Süden und weiter nach Osten, wenn Ihr Tausend Nadeln erreicht habt. Ihr werdet sie verborgen in einem Erdloch mit ihrem Liebsten finden - haha, sagt ihr nicht, dass ich schlecht über sie geredet habe - das mag sie nicht sonderlich."}, {"Sucht Prate Cloudseer in Tausend Nadeln."}},
[1532] = {"Ruf der Luft", {"Die Winde von Kalimdor wehen Euch entgegen und Ihr sollt zu den Mächtigsten unserer <Brüder/Schwestern> gehören. Ich sehe mit Stolz, dass Ihr so weit gekommen seid und so eine viel versprechende Zukunft habt.","Das Luftelement ist schwer zu kontrollieren, doch ich vertraue darauf, dass Eure Fertigkeiten alle Hindernisse überwinden, die Euch den Weg versperren.","Geht zum äußersten Südrand des Brachlandes und betretet Tausend Nadeln. Dort, weit im Osten, werdet Ihr Prate Cloudseer finden. Sie wird Euch mehr beibringen."}, {"Sucht Prate Cloudseer in Tausend Nadeln."}},
[1534] = {"Ruf des Wassers", {"Nehmt diesen Wasserschlauch und begebt Euch tief in die dichten Wälder, die die Nachtelfen Ashenvale nennen. Sie liegen weit nördlich des Brachlandes.","Wenn Ihr den Wald betreten habt, reist nach Westen, an den Bergen entlang, die das Steinkrallengebirge und Ashenvale trennen. Jenseits des Mystralsees findet Ihr einen heiligen Ort: die Sternenstaubruinen. In der Mitte des Sees findet Ihr einen kleinen Brunnen.","Bringt mir eine Probe des Brunnenwassers. Das ist die letzte Komponente, die Ihr braucht, ehe ich Euch zu Islen zurückschicke."}, {"Füllt den leeren blauen Wasserschlauch bei den Sternenstaubruinen in Ashenvale und kehrt zu Brine im Brachland zurück."}},
[1535] = {"Ruf des Wassers", {"Um Wissen zu erlangen und das Sapta von mir zu bekommen, dürft Ihr nur nach den reinsten Formen des Wassers suchen. Doch achtet auf Eure Umgebung, wenn Ihr die Proben für mich beschafft. Hütet Euch vor Kreaturen, die auf das Wasser angewiesen sind, das sie beschützen. Seht, wie es Wälder und Wüsten beeinflusst. Beobachtet, wie die Substanz, die das Leben erhält, Kreaturen dazu zwingt, Leben zu nehmen.","Wir beginnen mit einer einfachen Aufgabe: unterhalb meiner Hütte befindet sich ein Tümpel mit der kostbaren Flüssigkeit. Füllt diesen Schlauch damit und kehrt zu mir zurück."}, {"Füllt den leeren braunen Wasserschlauch im Wasserloch unterhalb von Brines Hütte und kehrt damit zu Brine im Brachland zurück."}},
[1536] = {"Ruf des Wassers", {"Vor langer Zeit folgte ich einigen Gefährten ins Vorgebirge von Hillsbrad. Wir verteidigten uns gegen einige aufmüpfige Menschen, die dort beheimatet sind, und kamen in eine kleine Stadt, die die Verlassenen übernommen hatten: Tarrens Mühle.","Dort fand ich etwas höchst Eigentümliches: In der Mitte der Stadt befand sich ein kleiner Brunnen mit frischem Quellwasser. Ich bin nicht sicher, ob die gebeutelten Bewohner der Stadt seinen Wert überhaupt kennen... er ist eine kleine heilige Insel in einem unheiligen Meer.","Bringt mir eine Probe dieses Wassers in diesem Wasserschlauch."}, {"Füllt den leeren roten Wasserschlauch am Brunnen in Tarrens Mühle und kehrt zu Brine im Brachland zurück."}},
[1558] = {"Der Steinwerkdamm", {"In der Nähe von Eisenschmiede, wo all die Zwerge und Gnome leben, gibt es einen Ort namens Loch Modan und die Zwerge haben da einen grooooßen Staudamm gebaut. Ich habe gehört, dass ist eines von den größten Dingen, die je gebaut wurden. Wow!","Könnt Ihr mir den Damm in Loch Modan zeigen, bitte?! Ich glaube der heißt Stein... äh... Steinwerkdamm? Er ist riesig, so viel ist klar. Ich würde so gerne auf den großen Wasserfall heruntersehen. Ich verspreche, mich auch nicht zu weit vorzulehnen."}, {"Begleitet Euren Schützling zum Steinwerkdamm in Loch Modan. Geht mit ihm zur Mitte des Damms, damit er auf den riesigen Wasserfall heruntersehen kann."}},
[1559] = {"Blitzstrahlbombenanleitung", nil, nil},
[1560] = {"Toogas Bitte", {"Ich habe mich vollkommen verirrt!","Torta, meine Frau, schickte mich Essen holen, aber ich muss irgendwo falsch abgebogen sein.","Würdet Ihr so freundlich sein, mich zu Torta zurückzuführen? Sie wartet südlich von Steamwheedle auf mich."}, {"Zeigt Tooga den Weg zurück zu seiner Frau Torta."}},
[1578] = {"Nachschub für die Front", {"Die Gebirgsjäger vom Loch Modan kämpfen gegen Troggs und sind damit schwer beschäftigt! Sie benötigen konstanten Nachschub an neuen Rüstungen und Waffen, wenn wir diese Troggs je bezwingen wollen.","Darum ist mein Kollege Thorvald hier und versucht, die Gebirgsjäger gut ausgerüstet zu halten. Wenn Ihr einige Kupferäxte und Kettengürtel aus Kupfer herstellen und sie Thorvald bringen könntet, wäre er Euch sehr verbunden. Vielleicht weiht er Euch sogar in sein Wissen ein.","Thorvald ist im südlichem Wachturm des Loch Modan."}, {"Bringt 6 Kupferäxte und 6 Kupferne Kettengürtel zu Thorvald im südlichen Wachturm des Loch Modan."}},
[1579] = {"Klemm-Muffen", {"Ich kam nach Auberdine wegen der Ruhe. Ich möchte meine Erfindungen machen, wo es ruhig ist, denn nach einer durchzechten Nacht ertrage ich den Lärm von Ironforge nicht!","Daher nahm ich ein Schiff nach Auberdine, trank aber unterwegs ein bisschen viel. Ich ließ meine Kiste mit Klemm-Muffen über Bord fallen! Ich brauche meine Klemmmuffen!","Ich weiß nicht genau, wo ich sie fallen ließ, aber vielleicht habt Ihr ja Glück, wenn Ihr im Meer danach fischt..."}, {"Bringt 8 Klemm-Muffen zu Wizbang Cranktoggle in Auberdine."}},
[1580] = {"Elektropeller", {"Wir wissen, dass Ihr Glück habt, aber... wie viel Glück genau, <Name>?","Eines Nachts schlenderte ich durch die Straßen von Dunkelküste. Ich suchte nach einem geeigneten Fluss oder See, um meine neuen propellerbetriebenen explodierenden Entenattrappen auszuprobieren. Ich muss wohl etwas beschwipst gewesen sein, denn Stunden später wachte ich in Auberdine auf. Mein Haar war versengt und meine Tasche mit den Elektropellern war weg! Sie müssen mir ins Wasser gefallen sein!","Ich brauche diese Elektropeller! Vielleicht könnt Ihr sie aus einem See oder Bach in Dunkelküste fischen..."}, {"Bringt 12 Elektropeller zu Wizbang Cranktoggle in Auberdine."}},
[1581] = {"Elixiere für die Bladeleafs", {"Unser Kräuter- und Alchimie-Laden floriert, aber mein Mann verbringt so viel Zeit damit, Tränke zu mischen, dass wir nie Zeit füreinander haben.","Könnt Ihr uns helfen?","Wenn Ihr mir einen Vorrat an vorgefertigten Elixieren bringt, würde ich mit Freuden mit Euch tauschen. Ich besitze gewisse rare Kräuter, die Euch nützlich sein könnten."}, {"Bringt 6 Elixiere Löwenkraft und 2 Elixiere der schwachen Verteidigung zu Syral Bladeleaf in Dolanaar."}},
[1582] = {"Mondscheinweste", {"Wir in Darnassus, besonders wir Handwerker, sind sehr beschäftigt. Junge Abenteurer brennen darauf, sich in der Wildnis zu beweisen, und benötigen unablässig Nachschub.","Wenn Ihr mir helft, meine Bestellungen an Lederwaren zu erfüllen, kann ich Euch vielleicht eine hochentwickelte Technik der Lederverarbeitung beibringen."}, {"Bringt 1 geprägten Lederhandschuh, 5 geprägte Lederstiefel und 5 geprägte Lederumhänge zu Lotherias in Darnassus."}},
[1598] = {"Der gestohlene Foliant", {"Ihr da! <Name>, richtig?","Ich hoffe, Ihr seid sicher, dass Ihr ein Hexenmeister werden möchtet, denn wenn Ihr nicht allein besteht, werdet Ihr gewiss untergehen.","Ich werde Euch unterrichten, muss aber zuerst sicher sein, dass Ihr stark genug seid.","Ein Novize, der an der Abtei studierte, lief weg und schloss sich der Bruderschaft der Defias an. Warum mich das kümmert? Weil er einige wichtige Bücher gestohlen hat.","Eines davon war ein Buch, das ich selbst wollte, \"Die Macht der Leere\". Stehlt es für mich, sie haben es in ihrem Zelt in den Weinbergen versteckt."}, {"Holt \"Die Macht der Leere\" für Drusilla La Salle zurück."}},
[1599] = {"Anfänge", {"Ihr müsst der neue Hexenmeister sein, über den alle tuscheln. Jemand muss Euch sehr mögen, wenn man mich bittet, meine Forschungen aufzuschieben und Eure Ausbildung zu übernehmen.","Keine Bange, ich mache Euch keinen Vorwurf.","Der einfachste Beschwörungszauber, den Ihr lernen werdet, ist der für einen Wichtel. Doch bevor ich ihn Euch beibringe, müsst Ihr beweisen, dass Ihr die notwendigen magischen und körperlichen Fähigkeiten besitzt.","Bringt mir drei Federglücksbringer von den Novizen der Frostmane aus der Höhle im Südwesten."}, {"Bringt 3 Federglücksbringer zu Alamar Grimm in Anvilmar."}},
[1618] = {"Die Aufrüstung im Rotkammgebirge", {"Wir Zwerge sind nicht die Einzigen, die kämpfen. Unsere menschlichen Verbündeten haben auch alle Hände voll zu tun!","Das Rotkammgebirge wird immer wieder von den Blackrockorcs angegriffen und der Vorrat an Ausrüstung ist stets knapp. Ich bin sicher, wenn Ihr ihnen helft, wird der ansässige Schmied, Verner Osgood, Euch den einen oder anderen Trick beibringen."}, {"Tormus Deepforge möchte, dass Ihr 4 runenverzierte Kupfergürtel und 4 schwere Kupferschlägel zu Verner Osgood ins Rotkammgebirge bringt."}},
[1638] = {"Die Ausbildung eines Kriegers", {"Ihr habt Euch als fähiger Krieger erwiesen, <Name>. Aber Ihr seid noch jung und müsst noch viel lernen.","Sprecht mit Harry Burlguard... er ist meist in der Taverne Zum pfeifenden Schwein in der Altstadt von Stormwind. Harry ist ein alter Veteran und guter Lehrer. Er kann Euch einige Kampftechniken zeigen, die Euer Leben verlängern."}, {"Sprecht mit Harry Burlguard."}},
[1639] = {"Bartleby der Trunkenbold", {"Ja, ich kann Euch unterrichten. Aber vorher will ich sehen, welche Kampftechniken Ihr schon beherrscht.","Seht Ihr den Betrunkenen am Ende der Bar? Bartleby? Nehmt ihm seinen Krug weg.","Ha, und wenn Ihr Bartleby kennt, dann wisst Ihr, dass er ihn nicht freiwillig rausrückt."}, {"Sprecht mit Bartleby."}},
[1640] = {"Besiegt Bartleby", {"Ihr wollt was??? Ihr seid verrückt!","Wenn Ihr diesen Krug wollt, müsst Ihr ihn mir aus den kalten, toten Händen brechen..."}, {"Besiegt Bartleby und redet dann mit ihm."}},
[1641] = {"Der Foliant der Offenbarung", nil, nil},
[1642] = {"Der Foliant der Offenbarung", {"Ein Paladin muss in allen Dingen das Licht widerspiegeln, das uns Kraft gibt. Wenn einer unserer Art nach dem Göttlichen strebt, bedeutet das nicht, dass er ein Gott sein möchte... wir trachten nur danach, in allem nur Gutes zu tun.","Auch wenn Ihr in diesen schweren Zeiten berufen seid, das Böse zu zerschmettern, dürft Ihr nicht vergessen, dass die Unterstützung anderer Euch wahrhaft von den Bürgern von Azeroth unterscheidet. Mitgefühl, Geduld, Tapferkeit... das alles bedeutet einem Paladin so viel wie Kraft im Kampf.","Bedenket das und vergesst es niemals."}, {"Sprecht mit Duthorian Rall in Stormwind."}},
[1643] = {"Der Foliant der Offenbarung", {"Eure Aufgabe, <Name>, besteht darin, jemanden hier in Stormwind zu finden, der der Hilfe bedarf. Jemanden mit reinem Herzen und edlen Motiven. Vielleicht jemanden, der anderen hilft. Vielleicht jemanden, der einen Führer braucht. Was auch immer, die Person muss edle Absichten haben und auf Eure Güte angewiesen sein.","Macht das und kommt zu mir zurück, wenn Ihr fertig seid. Dann sprechen wir weiter über Euren Weg und was es bedeutet, ein Paladin des Lichts zu sein."}, {"Sucht im Handelsdistrikt von Stormwind nach Stephanie Turner."}},
[1644] = {"Der Foliant der Offenbarung", {"Die meisten der guten Seelen haben keine Familien mehr aufgrund der Angriffe der Geißel im letzten Jahrzehnt.","Mein Mann John sammelt die Materialien, die ich brauche, um Hemden und Hosen für die Kinder zu machen, aber nicht einmal dafür hatte ich Zeit. Ich war zu sehr damit beschäftigt, ihnen Essen auf ihre Teller und ein Dach über ihre Köpfe zu geben.","Wie war das? Ihr wärt bereit, mir zu helfen? Oh, dem Licht sei Dank und Euch ebenfalls, <Name>.","Wenn Ihr mir etwas Leinen bringen könntet, damit ich den Kindern Kleidung nähen kann, wäre mir das eine große Hilfe."}, {"Bringt 10 Stück Leinenstoff zu Stephanie Turner in Stormwind."}},
[1645] = {"Der Foliant der Offenbarung", nil, nil},
[1646] = {"Der Foliant der Offenbarung", {"Ein Paladin muss in allen Dingen das Licht widerspiegeln, das uns Kraft gibt. Wenn einer unserer Art nach dem Göttlichen strebt, bedeutet das nicht, dass er ein Gott sein möchte... wir trachten nur danach, in allem nur Gutes zu tun.","Auch wenn Ihr in diesen schweren Zeiten berufen seid, das Böse zu zerschmettern, dürft Ihr nicht vergessen, dass die Unterstützung anderer Euch wahrhaft von den Bürgern von Azeroth unterscheidet. Mitgefühl, Geduld, Tapferkeit... das alles bedeutet einem Paladin so viel wie Kraft im Kampf.","Bedenket das und vergesst es niemals."}, {"Sprecht mit Tiza Battleforge in Ironforge."}},
[1647] = {"Der Foliant der Offenbarung", {"Eure Aufgabe, <Name>, ist es, jemanden hier in Ironforge zu finden, der der Hilfe bedarf. Jemand mit reinem Herzen und edlen Motiven. Vielleicht jemanden, der anderen hilft. Vielleicht jemanden, der einen Führer braucht. Was auch immer, die Person muss edle Absichten haben und auf Eure Güte angewiesen sein.","Macht das und kommt zu mir zurück, wenn Ihr fertig seid. Dann sprechen wir weiter über Euren Weg und was es bedeutet, ein Paladin des Lichts zu sein. Ich warte hier im Tempel auf Eure Rückkehr. Auch andere Paladine brauchen meine Unterstützung."}, {"Sucht im äußeren Ring von Ironforge nach John Turner."}},
[1648] = {"Der Foliant der Offenbarung", {"Meine Frau und ich führen das Waisenhaus in Stormwind, das überwiegend Kindern vorbehalten ist, die im letzten Jahrzehnt ihre Eltern bei Angriffen der Geißel verloren haben. Ich komme etwa einmal im Monat nach Ironforge und versuche, bei den Bürgern hier mehr Vorräte zu sammeln und mit den verschiedenen Gruppen zu reden, ob sie irgendwie helfen können.","Dieser Ausflug war schrecklich langwierig und nicht sehr ergiebig.","Könnte ich doch nur noch etwas mehr Leinen bekommen, damit meine Frau Kleidungsstücke nähen kann, dann könnte ich endlich eine Ladung nach Hause schicken."}, {"Bringt 10 Stück Leinenstoff zu John Turner in Ironforge."}},
[1649] = {"Der Foliant der Ehre", {"Ehre, und alle Tugenden, die damit einhergehen, sind wie rare Mineralien: man muss sie ihres Wertes wegen bewundern, wenn man sie findet, aber man muss sich auch die Zeit nehmen, sie aufzuheben... zu veredeln. Mischt man sie richtig, entsteht eine starke Legierung, die manchmal selbst die heftigsten Hiebe nicht zerbrechen können.","Diese Kraft, Euer Glauben, Eure Tapferkeit, müssen geprüft werden. Ihr müsst lernen, niemals den Glauben zu verlieren und erkennen, dass Ihr mit der Macht des Lichts Dinge überwinden könnt, vor denen andere klein beigeben müssen."}, {"Sprecht mit Duthorian Rall in Stormwind."}},
[1650] = {"Der Foliant der Ehre", {"Gut! Da Ihr nicht zögert, seid Ihr gewiss bereit und brennt darauf, Euch zu beweisen. So sei es.","Am äußersten Ende von Longshore in Westfall steht ein kleines Haus über dem Ozean. Dort lebt ein Paar, die Stilwells. Jordan ist ein unglaublicher Schmied und für einige Zeit nach Ironforge gegangen, um sich mit den Zwergen zu treffen. Dadurch ist seine Frau Daphne allein und schutzlos.","Es wird berichtet, dass die Defias fast das gesamte Gebiet übernommen haben. Gut möglich, dass Daphne Schutz vor den Bösewichtern braucht. Geht dorthin."}, {"Sucht Daphne Stilwell in Westfall."}},
[1651] = {"Der Foliant der Ehre", {"Ich wette, die kommen bald wieder, also macht Euch bereit.","Ich habe eine Schusswaffe im Haus. Ich hole sie und helfe Euch gern, aber Ihr werdet weitgehend auf Euch gestellt sein.","Ich habe keine Ahnung, wie viele sie schicken, aber es werden sicher mehr sein als die paar beim letzten Mal.","Bittet das Licht um jeden erdenklichen Segen, denn ... habt Ihr das gehört? Ich glaube, sie kommen.","Wir beziehen Position beim Haus!"}, {"Verteidigt Daphne Stilwell vor dem Angriff der Defias.","Keiner Eurer Geister darf aus seinen irdischen Fesseln befreit werden, wenn Ihr erfolgreich sein wollt.","Sprecht erneut mit Daphne Stilwell, wenn Ihr erfolgreich wart."}},
[1652] = {"Der Foliant der Ehre", {"Kehrt bitte zu Duthorian zurück und lasst ihn wissen, dass ich hier vorerst sicher bin. Dafür muss ich Euch und der Kirche danken.","Er wird eine Nachricht nach Ironforge schicken und meinen Jordan wissen lassen, dass er keinen Grund zur Furcht hat.","Oh, ich vermisse meinen Mann so sehr. Ich kann es kaum erwarten, bis er wieder da ist."}, {"Sprecht mit Duthorian Rall in Stormwind."}},
[1653] = {"Die Prüfung der Rechtschaffenheit", {"Ich zweifle nicht daran, dass Jordan Euch persönlich danken möchte, und da ich einen Boten brauche, der ihn wissen lässt, dass es seiner Frau gut geht, scheint Ihr mir die perfekte Wahl zu sein.","Jordan sollte ein Paladin des Lichts sein, bevor die Liebe zum Beruf seines Vaters ihn einen anderen Weg einschlagen ließ - er ist innig mit unserer Lebensweise vertraut und wird zu unseren Getreuesten gerechnet.","Er müsste noch in Ironforge sein - tagsüber arbeitet er gern vor den Toren, wohl wegen der kühlen Bergluft."}, {"Sprecht mit Jordan Stilwell in Ironforge."}},
[1654] = {"Die Prüfung der Rechtschaffenheit", {"Ich würde Euch gern eine Belohnung geben, wenn Ihr bereit seid, eine anzunehmen. Missversteht mich nicht, meine Großzügigkeit hängt davon ab, wie hilfreich Ihr sein könnt, aber ich denke, wir werden beide von einer derartigen Allianz profitieren.","Ich werde Euch eine Waffe schmieden, die Eurer Leistung würdig ist, ein Symbol der Paladinschaft und des Lichts, doch zuvor müsst Ihr die Gegenstände sammeln, die ich zu seiner Herstellung brauche. Ich habe schon angefangen, mir Notizen zu den Gegenständen zu machen, und brauche nur jemanden, der tapfer und geschickt genug ist, sie zu beschaffen. Was sagt Ihr dazu, <Klasse>?"}, {"Sucht mit Jordans Waffennotizen etwas Weißsteineichenholz, Bailors aufbereitete Erzlieferung, Jordans Schmiedehammer und einen Kor-Edelstein und bringt alles zusammen zu Jordan Stilwell in Ironforge."}},
[1655] = {"Bailors Erzlieferung", {"Ach, Ihr habt mich erschreckt, <Mann/Frau>! Seht Ihr nicht, dass ich nachdenke?","Wie war das? Jordans Lieferung... für Ironforge. Ahhh, ja, ich weiß, wovon Ihr sprecht. Oger der Mo'grosh haben die Erzlieferung meines alten Freundes gestohlen. Die Karawane war auf dem Weg vom Sumpfland hierher und geriet in einen Hinterhalt. Die Wache, die den Angriff überlebte, starb vor wenigen Tagen - der Heiler kam nicht schnell genug hin.","Wenn Ihr die Lieferung wollt, müsst Ihr über den Loch nach Nordosten. Dort muss sie irgendwo sein."}, {"Bringt Jordans Erzlieferung zu Bailor Stonehand am Loch Modan."}},
[1656] = {"Eine unvollendete Aufgabe", {"<Name>, bitte, ich flehe Euch an, könnt Ihr mir helfen? Ich habe meine Aufgaben hier auf der Mesa noch nicht beendet - ich bin mitten in meinem Ritual der Kraft - und mein Vater bat mich, diese Felle im Gasthaus von Bloodhoof abzuliefern.","Ich bin längst nicht erholt genug, mein Ritual und diese Aufgabe für meinen Vater zu beenden. Würdet Ihr an meiner statt gehen?","Das Gasthaus ist angenehm und eine perfekte Oase der Ruhe auf langen Reisen. Ich bin sicher, Ihr stimmt mir zu, wenn Ihr es seht."}, {"Bringt das Bündel Felle zu Gastwirt Kauth in Bloodhoof."}},
[1657] = {"Die Verpestung von Southshore", {"Feiert die Schlotternächte mit Stil... bereitet unseren Feinden in Southshore Kummer!","Nehmt eine dieser speziell angefertigten Stinkbomben. Sie sind mit einem derartig furchtbaren Schrecken gefüllt, dessen Geruch kein Mensch - oder anderes Wesen mit schwachem Gemüt - ertragen kann. Ihr müsst die Stinkbombe direkt ins Herz von Southshore werfen, damit sie richtig wirken kann. Seid also auf eine Auseinandersetzung mit der Allianz gefasst!","Solltet Ihr diese Aufgabe bewältigt haben, kehrt zu mir zurück... es ist ein Beitrag zu unserer Befreiung!"}, {"Werft drei Stinkbomben der Verlassenen ins Herz von Southshore in Hillsbrad und kehrt zu Ruferin der Dunkelheit Yanka beim Fest des Weidenmanns in Tirisfal zurück."}},
[1658] = {"Bespitzelt das Fest des Weidenmanns", {"Ich brauche mutige Freiwillige, die nach Tirisfal ziehen und das Fest des Weidenmanns auskundschaften. Ich möchte nicht lügen - diese Mission ist äußerst gefährlich. Die Wachen des Fests werden besonders bösartig sein. Trotzdem, wir müssen in Erfahrung bringen wie groß das Fest dieses Jahr ist und wieviel Schaden die Verlassenen dieses Mal während der Schlotternächte anrichten.","Solltet Ihr dabei sein, dann geht nach Tirisfal und kundschaftet das Fest aus. Kehrt mit den Informationen zu mir zurück und ich werde für eine angemessene Belohnung sorgen."}, {"Macht das Fest des Weidenmanns der Verlassenen in Tirisfal ausfindig. Kehrt zu Sergeant Hartman in Southshore zurück, sobald Ihr Eure Mission erfüllt habt."}},
[1661] = {"Der Foliant des Adels", {"Ich grüße Euch erneut, <Name>. Ich bin stolzer als jemals zuvor, Euch <Bruder/Schwester> nennen zu können - Euer Engagement ist ein Tribut an uns alle.","Die Zeit ist für mich gekommen, Euch ein wunderbares Geschenk zu übergeben. Wisset aber, dass ich es Euch fürs Erste zwar frei gebe, später jedoch, falls die Zeit es erlaubt, wird dies eine viel größere Herausforderung für alle Paladine sein, die sich dazu entscheiden, meinen Auftrag anzunehmen.","Sprecht erneut mit mir und ich werde Euch einen großen Segen gewähren."}, {"Sprecht mit Duthorian Rall in Stormwind."}},
[1665] = {"Bartlebys Becher", {"Okay, hier ist mein Becher. Wartet! Lasst mich zuerst austrinken... da haben wir es."}, {"Bringt Bartlebys Becher zu Burlguard."}},
[1666] = {"Marshal Haggard", {"Ihr habt einige Kampftechniken gelernt, braucht aber noch eine anständige Waffe. Ich wette, Marshal Haggard könnte Euch helfen, wenn Ihr mit ihm sprecht.","Marshal Haggard lebt in einem Anwesen im Wald von Elwynn, beim Holzfällerlager des Osttals. Er ist jetzt im Ruhestand, hat aber in seiner Laufbahn eine Menge Ausrüstung gesammelt.","Lasst ihn wissen, dass ich Euch geschickt habe, und mit etwas Glück hat er eine Waffe für Euch übrig."}, {"Sprecht mit Marshal Haggard."}},
[1667] = {"Stumpfzahn Jack", {"Burlguard hat Euch geschickt, nicht? Ja, ich habe Waffen übrig, aber zuerst habe ich eine Aufgabe für Euch...","Vor einer Weile überlistete mich ein Bösewicht der Defias, Stumpfzahn Jack. Es war dunkel und er trug ein Stormwindwams, daher dachte ich, er sei Marshal Dughan. Er sagte, er braucht meinen alten Marshalstern aus Stormwind und ich gab ihm diesen... dann lachte er und lief weg, ehe ich ihn packen konnte!","Bringt mir meinen Stern! Stumpfzahn Jack hat ihn wahrscheinlich in seinem Lager im Süden versteckt."}, {"Holt Marshal Haggards Abzeichen aus Stumpfzahn Jacks Lager und kehrt zu Marshal Haggard zurück."}},
[1678] = {"Vejrek", {"Wenn Ihr es in den Reihen der Krieger zu etwas bringen wollt, müsst Ihr zäh sein. Ich kann Euch neue Kampftechniken beibringen, aber zuvor müsst Ihr mir beweisen, dass Ihr gegen richtige Feinde bestehen könnt.","Vejrek ist der härteste Troll, den Ihr in dieser Gegend findet. Ihn müsst Ihr besiegen. Er hat eine Hütte im Vorgebirge südlich der Frostmanehöhle.","Bringt mir Vejreks Kopf, dann weiß ich, dass Ihr Mumm habt."}, {"Bringt Vejreks Kopf zu Muren Stormpike in Ironforge."}},
[1679] = {"Muren Stormpike", {"Hallo, Krieger! Ihr seid viel versprechend, aber dennoch müsst Ihr ein paar Tricks lernen, und Muren Stormpike ist genau der Zwerg, der sie Euch beibringen kann.","Ihr findet Muren in der Halle der Waffen im Militärviertel von Ironforge. Sprecht mit ihm, wenn Ihr bereit seid zu lernen, was es heißt, ein Krieger zu sein."}, {"Sprecht mit Muren Stormpike."}},
[1680] = {"Tormus Deepforge", {"Jetzt, da Ihr einige Kampftechniken gelernt habt, wird es Zeit, Euch eine anständige Waffe zu beschaffen. Sprecht mit Tormus Deepforge. Er kann Euch eine Waffe machen, die Euch sicher gefallen wird.","Ihr findet Tormus hier in Ironforge bei der großen Schmiede."}, {"Sprecht mit Tormus Deepforge."}},
[1681] = {"Ironbands Truppenlager", {"Mein Mentor Sturgy Ironband hatte ein Lager südlich des Helmsbedsees im östlichen Dun Morogh. Dort lehrte er die Schmiedekunst und er war der Beste.","Aber eines Tages brachte ihm einer seiner Schüler eine Ladung mit seltsamem Erz. Meister Ironband nannte es Umbralerz und konnte es kaum erwarten, damit zu arbeiten. Doch kurz nach der Lieferung griff eine Bande Dunkeleisenzwerge sein Lager an. Sie töteten alle!","Begebt Euch zu Ironbands Lager, <Name>, und rächt den Tod meines Meisters. Und bringt mir die Ladung Umbralerz, die all das auslöste."}, {"Bringt eine Ladung Umbralerz zu Tormus Deepforge in Ironforge."}},
[1682] = {"Waffen aus grauem Eisen", nil, nil},
[1683] = {"Vorlus Elendshuf", {"Bevor ich Euch unterrichten kann, müsst Ihr mir Eure Entschlossenheit beweisen.","Der Satyr Vorlus Elendshuf hat einen unserer entlegenen Mondbrunnen südöstlich der Grabhügel von Ban'ethil gefunden. Nun planscht er darin und verseucht das reine Wasser mit seinem Schmutz.","Sucht Vorlus beim Mondbrunnen und vernichtet ihn. Bringt mir sein Horn als Beweis, dann beginne ich Eure Fortgeschrittenen-Ausbildung zum Krieger.","Der Weg zum Mondbrunnen ist gut verborgen, <Name>, doch wenn Ihr konzentriert und wachsam bleibt, werdet Ihr ihn finden."}, {"Bringt das Horn von Vorlus zu Elanaria in Darnassus."}},
[1684] = {"Elanaria", {"Seid gegrüßt, Krieger. Eure Fertigkeiten wachsen, doch Eurer Beruf verlangt mehr, als Ihr für möglich haltet. Um weiter zu kommen müsst Ihr bald einen Lehrer finden.","Die Kriegerin Elanaria weilt in Darnassus auf der Terrasse der Krieger. Sie kann Euch unterweisen."}, {"Sprecht mit Elanaria."}},
[1685] = {"Gakins Beschwörung", {"Läuft es nicht immer so? Ich habe den ganzen Tag nach Euch gesucht und konnte Euch nicht finden, und jetzt, da ich es nicht erwarte, kommt Ihr einfach zu mir spaziert.","Ich habe genug Zeit mit der Suche nach Euch vergeudet, daher möchte ich gleich zur Sache kommen. Meister Gakin hat Euch zu weiterer Ausbildung gerufen. Mir scheint, Ihr solltet ihn so rasch wie möglich aufsuchen.","Ihr findet ihn im Keller der Taverne Zum geschlachteten Lamm. Übrigens solltet Ihr diesen Treffpunkt nicht an die große Glocke hängen. Muss ja nicht jeder wissen."}, {"Meldet Euch bei Gakin dem Dunkelbinder im Magierviertel von Stormwind."}},
[1686] = {"Eluras Schemen", {"Ihr habt viel gelernt, <Name>. Jetzt fertigen wir Euch eine Waffe an. Die Waffe eines Kriegers.","Dazu müsst Ihr nach Dunkelküste reisen.","Vor langer Zeit zerschellte ein Schiff auf den Felsen beim Leuchtturm von Auberdine. Viele Kisten Eluniterz sanken auf den Meeresgrund.","Als Erstes müsst Ihr den Wächter des Elunits besiegen, Eluras Schemen. Sie war einst Kapitän des Schiffes und verfluchte sich selbst dazu, für alle Zeiten in der Tiefe zu verweilen und ihre Schiffsladung zu schützen. Besiegt sie, nehmt Ihr das Medaillon ab und sammelt die verlorenen Elunitkisten ein."}, {"Bringt 8 Ladungen Eluniterz und das Medaillon von Elura zu Elanaria in Darnassus."}},
[1687] = {"Der gruselige Leuchtturm", {"Abends erzählen uns die Matronen manchmal Gruselgeschichten. Die sind spitze! Einmal haben sie uns von einem Geist erzählt, der in einem Leuchtturm in Westfall spukt. Er soll einmal ein Piratenkapitän gewesen sein, dessen Schiff ihm von anderen Piraten weggenommen wurde. Wow!","Können wir uns bitte den Leuchtturm in Westfall anschauen? Ich will nachgucken, ob der Geist des Kapitäns noch immer dort ist! Ich bleibe auch immer nah bei Euch. Ich renn' auch bestimmt nicht weg oder so was."}, {"Besucht mit Eurem Schützling den Leuchtturm an der Küste von Westfall."}},
[1688] = {"Surena Caledon", {"Ich hatte eine Studentin namens Surena Caledon. Sie stand da, wo Ihr jetzt steht, und brannte darauf, die Hexenmeistermagie zu lernen, und ihr Talent war nicht von schlechten Eltern. Mehr als das, sie war jung und eine wahre Augenweide. Hätte ich sie doch nur damals schon durchschaut, die falsche Schlange!","Das diebische Weib brannte mit einem der Defias durch, Erlan Drudgemoor. Ihr Verlust ist unbedeutend, aber ich schenkte ihr einen Blutsteinhalsschmuck, den ich wiederhaben muss.","Ihr Leben hat keine Bedeutung mehr für mich. Ihr findet sie bei Brackwells Kürbisbeet. Bringt wieder, was mir gehört."}, {"Beschafft Surenas Halsschmuck für Gakin den Dunkelbinder in Stormwind."}},
[1689] = {"Die Bindung", {"Ihr habt meinen Halsschmuck zurückgebracht und Surena bezwungen, und damit gezeigt, dass Ihr mindestens ebenso begabt seid wie sie es war.","Nichtsdestoweniger kann ich Euch versichern, dass Ihr dringend Ausbildung benötigt. Ihr meistert den Fluss der Magie, ja, aber zu einem Hexenmeister gehört viel mehr als das.","Ich zeige Euch, wie man einen Leerwandler beherrscht, aber bevor ich das kann, müsst Ihr einen am Beschwörungskreis herbeirufen und mit allen Euch zur Verfügung stehenden Waffen unterwerfen. Benutzt die Magie dieses Halsschmuckes unten am Beschwörungskreis."}, {"Beschwört mit Hilfe des Blutsteinhalsschmucks einen Leerwandler, unterwerft ihn und bringt dann den Blutsteinhalsschmuck zu Gakin dem Dunkelbinder in Stormwind zurück."}},
[1690] = {"Wüstenläufergerechtigkeit", {"Ihr da! Für Euch ist es an der Zeit, ein großer Helfer der Gadgetzan Water Company zu sein.","Die Wüstenläufernomaden draußen in der Tanariswüste haben fast alle Wasserbrunnen erobert! Ohne Zugang zu den Brunnen wird unser Wasservorrat im Handumdrehen schwinden. Wenn Ihr Euch unsere Dankbarkeit verdienen wollt, dann helft uns, diese Nomaden der Gerechtigkeit zuzuführen!","Begebt Euch von hier Richtung Osten und schaltet zehn Banditen und Diebe der Wüstenläufer aus und meldet Euch dann umgehend wieder bei mir. Gerechtigkeit wartet auf keinen Mann ... keinen Goblin ... ach, legt einfach los!"}, {"Erledigt 10 Banditen und Diebe der Wüstenläufer östlich von Gadgetzan und meldet Euch dann wieder bei Chefingenieur Bilgewhizzle."}},
[1691] = {"Noch mehr Wüstenläufergerechtigkeit", {"Wir müssen uns jetzt die stärkeren Ränge der Wüstenläufernomaden vornehmen. Ihr habt bisher gute Arbeit geleistet, aber wenn wir diese Wasserbrunnen zurückerobern wollen, dann wäre es gut, wenn uns dabei so wenig Nomaden wie möglich gegenüberstünden!","Dieses Mal müsst Ihr ihre Schurken, Auftragsmörder und Schattenmagier erledigen. Ihr werdet sie östlich und südöstlich von hier finden.","Ich werde Euch nichts vormachen - das ist eine schlimme Bande. Tut dies für uns, dann sind wir auf dem besten Weg, uns das zurückzuholen, was rechtmäßig uns gehört!"}, {"Erledigt 10 Schattenmagier der Wüstenläufer, 8 Schurken der Wüstenläufer und 6 Auftragsmörder der Wüstenläufer für Chefingenieur Bilgewhizzle in Gadgetzan."}},
[1692] = {"Schmied Mathiel", {"Ich habe das Elunit, das Ihr gesammelt habt, in eine Kiste gelegt. Bringt sie zum Schmied Mathiel, er wird eine Elunitwaffe für Euch anfertigen.","Mathiel ist im Süden, an der anderen Seite dieses Gebäudes zu finden."}, {"Bringt das Behältnis mit Elunit zu Schmied Mathiel."}},
[1693] = {"Waffen aus Elunit", nil, nil},
[1698] = {"Yorus Gerstenbräu", {"Yorus Gerstenbräu hat eine Herausforderung für alle tüchtigen Krieger der Allianz verkündet. Er wartet im Gasthaus von Seenhain im Rotkammgebirge und verspricht jedem, der seine Prüfung besteht, eine hohe Belohnung."}, {"Sprecht mit Yorus Gerstenbräu."}},
[1699] = {"Der Rethban-Spießrutenlauf", {"Ich nenne meine Herausforderung den Rethbanspießrutenlauf. Um die Prüfung zu bestehen, müsst Ihr lediglich die Rethbanhöhlen betreten, bis zur ersten Gabelung gelangen und hierher zurückkehren. Ganz einfach! Aber es gibt da ein paar Hürden...","Ihr müsst binnen einer Stunde zurück sein.","Ganze Heerscharen von Rotkammgnollen versperren den Weg.","Vor dem Start müsst Ihr einen Schluck des Stammgetränks meiner Familie zu Euch nehmen: Gerstenbräu!","Was sagt Ihr dazu? Die Rethbanhöhlen liegen im Vorgebirge nördlich von Seenhain. Seid Ihr bereit, am Rethbanspießrutenlauf teilzunehmen?"}, {"Betretet die Rethbanhöhlen, geht bis zur ersten Gabelung und kehrt innerhalb der zulässigen Zeit zu Yorus Gerstenbräu zurück.","Ihr dürft nicht sterben und Euren Geist befreien."}},
[1700] = {"Grimand Elmore", {"Ich fertigte eine Kopie meiner Notizen über die Techniken an, die ich lernte, während ich Eure Rüstung herstellte. Sie muss zu Grimand Elmore geschickt werden. Er ist hier im Zwergendistrikt, im Waffenladen.","Und duckt Euch, wenn Ihr ihm die gebt. Er bildet sich ungeheuer viel auf seine Fertigkeiten ein. Es wird ihm nicht gefallen, etwas von mir zu lernen ..."}, {"Bringt Furens Notizen zu Grimand Elmore."}},
[1701] = {"Feuergehärteter Panzer", {"Ich gab Euch einen guten Schild, aber dabei muss es nicht bleiben. Ich kenne das Geheimnis, wie man extrem harte Panzerrüstung herstellt. Es ist nicht leicht und erfordert sehr rare Materialien, aber wenn Ihr bereit seid, die Kleinarbeit zu machen, fertige ich ein Teil für Euch an.","Was sagt Ihr dazu, <Name>? Wenn Ihr Euch der Herausforderung gewachsen fühlt, gebe ich Euch eine Liste, was Ihr beschaffen müsst und wo Ihr es bekommt."}, {"Sammelt die Materialien, die Furen Longbeard benötigt, und bringt sie zu ihm nach Stormwind."}},
[1702] = {"Der Schildschmied", {"Ihr habt echt gezeigt, aus welchem Holz Ihr geschnitzt seid, als Ihr am Rethbanspießrutenlauf teilgenommen habt, <Name>. Und ich stehe zu meinem Wort.","Aber sie ist in Stormwind.","Sprecht mit meinem Freund Furen Longbeard im Zwergendistrikt von Stormwind. Er ist der beste Schildschmied, den Ihr je gesehen habt, und er schuldet mir einen großen Gefallen.","Hier, bringt Furen dieses Fässlein mit Gerstenbräu und bittet ihn um einen Schild. Er wird Euch einen schönen geben."}, {"Bringt das Fässlein mit Gerstenbräu zu Furen Longbeard."}},
[1703] = {"Mathiel", {"Ich fertigte eine Kopie meiner Notizen über die Techniken an, die ich lernte, während ich Eure Rüstung herstellte. Sie muss zum Schmied Mathiel geschickt werden. Er lebt in Darnassus. Das ist weit weg, aber ich bin sicher, er wird dankbar sein, wenn er erfährt, was ich gelernt habe."}, {"Bringt Furens Notizen zu Mathiel."}},
[1704] = {"Klockmort Spannerspan", {"Ich fertigte eine Kopie meiner Notizen über die Techniken an, die ich lernte, während ich Eure Rüstung herstellte, und brauche jemanden, der sie zu Klockmort Spannerspan bringt. Er ist ein Gnomen-Schmied in Ironforge, und wenn er herausfindet, was ich herausgefunden habe ... Ich wette, er reißt sich vor Aufregung selbst den Bart aus."}, {"Bringt Furens Notizen zu Klockmort Spannerspan."}},
[1705] = {"Brennendes Blut", {"Ich gebe nur ungern zu, dass Furen tatsächlich etwas gefunden haben könnte. Aber um ganz sicher zu sein, muss ich seine Techniken an einem neuen Rüstungsteil erproben. Wenn Ihr mir mit den Materialien helft, <Name>, könnt Ihr die Rüstung haben.","Um die Rüstung herzustellen benötige ich Proben von brennendem Blut und brennendem Felsen.","Die findet man meines Wissens nur im Dämmerwald. Die Worgs der Nachtheuler in der Mine von Rolands Verdammnis haben brennendes Blut, und Gutspill, ihr Anführer, wird den Fels haben.","Möge das Schicksal Euch Schnelligkeit gewähren <Name>."}, {"Bringt 20 Phiolen brennendes Blut und 1 brennenden Fels zu Grimand Elmore in Stormwind."}},
[1706] = {"Grimands Rüstung", nil, nil},
[1707] = {"Wasserbeutelkopfgeld", {"Seid gegrüßt, <Name>, mein Name ist Luglunket, und wir haben einen wichtigen Auftrag für Euch, wenn Ihr bereit seid! Hussah!","Nichts würde mir als altgedientem Leitungshahnmaschinisten mehr gefallen, als Euch jeden Wasserbeutel eines Wüstenläufers abzunehmen, den Ihr draußen in der Wüste findet. Das ist ja ohnehin unser Wasser - die Nomaden haben es einfach nur gestohlen! Die Gadgetzan Water Company bietet Euch ein offizielles Care-Paket für jeweils fünf, die Ihr abliefert. Am wichtigsten ist aber, dass Ihr Gadgetzan selbst helft!"}, {"Bringt Leitungshahnmaschinist Luglunket in Gadgetzan 5 Wasserbeutel eines Wüstenläufers."}},
[1708] = {"Eisenkoralle", {"Ich möchte Furen Longbeards neue Techniken an einer eigenen Rüstung erproben. Und weil Ihr Furen geholfen habt und er mir, sollt Ihr die Rüstung bekommen, wenn sie fertig ist!","Um das Teil zu beenden, brauche ich eine spezielle Koralle, Sengkoralle. Sie wächst nur an einem Ort - an der Küste südlich des Hafens von Menethil im Sumpfland.","Bringt mir genügend Sengkoralle und ich mache mich gleich an die Arbeit. Und beeilt Euch, <Name>. Ich brenne so sehr darauf, endlich anzufangen, dass meine Zehen jucken!"}, {"Bringt 20 Ladungen Sengkoralle zu Klockmort Spannerspan in Ironforge."}},
[1709] = {"Klockmorts Kreation", nil, nil},
[1710] = {"Sonnenverbrannte Schalen", {"Mein Kollege, Furen Longbeard, hat eine Entdeckung gemacht, die ich schnellstens ausprobieren möchte. Ich möchte sie an einer neuen Rüstung erproben, an der ich forsche, denn ich glaube, Furens Techniken werden grandiose Ergebnisse erzielen.","Werdet Ihr mir helfen? Wenn Ihr sammelt, was ich zur Herstellung der Rüstung brauche, fertige ich für Euch auch eine an.","Ich brauche sonnenverbrannte Eierschalen der Steilhangflügeldrachen, die am Steilhang in Tausend Nadeln hausen. Sucht unter den Nestern nach den Schalen und kommt zu mir zurück, wenn Ihr eine ausreichende Menge habt."}, {"Bringt 20 sonnenverbrannte Schalen zu Mathiel in Darnassus."}},
[1711] = {"Mathiels Rüstung", nil, nil},
[1712] = {"Cyclonian", {"Ich beobachte diesen Ort seit Ewigkeiten. Ich beobachte, damit ich eines Tages den großen Cyclonian fallen sehen kann. Doch ich fand noch keinen Helden, der den Windlord besiegen konnte.","Vielleicht seid Ihr dieser Held.","Beweist es mir. Sammelt die Gegenstände, die ich brauche, um Cyclonian zu beschwören, dann weiß ich, dass Ihr ein großer Krieger seid. Vielleicht könnt Ihr ihn besiegen und sein Wirbelherz bekommen.","Wenn ja, übertrage ich die Macht des Herzens in eine Waffe und schenke Euch diese Waffe.","Hier ein Pergament mit den Gegenständen, die ich benötige."}, {"Bringt die Gegenstände auf Bath'rahs Pergament zu Bath'rah dem Windbehüter in Alterac."}},
[1713] = {"Die Beschwörung", {"Ich bin bereit, das Ritual zur Beschwörung Cyclonians durchzuführen. Wenn er beschworen ist, müsst Ihr ihn besiegen und sein Wirbelherz nehmen. Ich werde es benutzen, um eine Waffe mit Cyclonians Macht zu stärken.","<Name>, seid Ihr bereit, dem Windlord zu trotzen?"}, {"Bringt das Wirbelherz zu Bath'rah dem Windbehüter."}},
[1714] = {"Essenz der Verbannung", nil, nil},
[1715] = {"Zum Geschlachteten Lamm", {"Hmph. Früher wurden alle Hexenmeister in Khaz Modan unter der Aufsicht der Meister von Gnomeregan ausgebildet, doch jetzt, wo wir gezwungen wurden, in dieses Rattenloch in Ironforge zu ziehen und Magni beschloss, uns genau zu beobachten, sind wir gezwungen, unsere Novizen zur Ausbildung nach Stormwind zu schicken.","Zum Glück ist Gakin kein Tölpel, daher werdet Ihr lernen, was Ihr wissen müsst. Dennoch bringt es mein Blut zum Kochen!","Wie auch immer, Ihr findet Gakin im Keller des Geschlachteten Lamms in Stormwind."}, {"Meldet Euch bei Gakin dem Dunkelbinder im Magierviertel von Stormwind."}},
[1716] = {"Verschlinger der Seelen", {"Ein Sukkubus ist ein Verschlinger der Seelen, Zerstörer der Herzen, Bezwinger des Geistes. Er kennt nur das Böse.","Dies ist die Kreatur, die Ihr beschwören und beherrschen lernt, doch ohne Anreiz kann sie nicht in unsere Welt gebracht werden.","Mit einem Symbol der Liebe als Köder wird er gewiss auf Euren Ruf reagieren. Dann könnt Ihr ihn besiegen und zu Eurem Werkzeug machen.","Es wird allerdings schwer sein, etwas dieser Art zu finden. Ihr solltet mit dem Seher Takar sprechen. Ihr findet ihn im Brachland von Kalimdor nahe dem Camp Taurajo."}, {"Sprecht mit Takar dem Seher im Brachland."}},
[1717] = {"Gakins Beschwörung", {"<Name>! Ich hätte nicht erwartet, Euch in der Gegend zu sehen, wurde aber angewiesen, nach Euch Ausschau zu halten.","Höchste Zeit, dass Eure Ausbildung fortgesetzt wird. Beim nächsten Besuch in Stormwind solltet Ihr bei Gakin reinschauen. Er wird Euch erwarten."}, {"Meldet Euch bei Gakin dem Dunkelbinder im Magierviertel von Stormwind."}},
[1718] = {"Der Inselbewohner", {"Euer Ruf als Krieger wächst, <Name>. Jetzt ist es Zeit, Euch mit Euresgleichen zu messen und festzustellen, was Ihr könnt.","Vor der Küste des Brachlandes, südlich von Ratchet, liegt eine Insel. Sie heißt Prügel-Eiland und ist ein Treffpunkt für Krieger.","Geht dorthin und kämpft.","Sprecht mit Klannoc Macleod, dem obersten Krieger der Insel. Wenn er Euch für würdig erachtet, wird Klannoc Euch weiter auf dem Pfad des Kriegers führen.","Um die Prügel-Eiland zu erreichen, müsst Ihr nach den Sandbankuntiefen entlang der Händlerküste suchen."}, {"Sprecht mit Klannoc Macleod."}},
[1719] = {"Der Ringkampf", {"Wenn Ihr von mir lernen wollt, müsst Ihr zuerst den Ringkampf absolvieren. Das ist eine Herausforderung hier auf dem Prügel-Eiland, an der wir aufstrebende Krieger teilnehmen lassen, die sich für gut genug halten. Wenn Ihr gewinnt, dann weiß ich, dass Ihr es tatsächlich seid.","Geht als Erstes mitten in diese Menge dort und stellt Euch auf den Rost. Dadurch weiß Twiggy Flathead, dass Ihr bereit seid, und er schickt Euch Herausforderer.","Wenn Ihr alle Herausforderer besiegt habt, ruft er Big Will. Tötet Big Will und kehrt innerhalb der von uns gesetzten Frist zu mir zurück."}, {"Tötet Big Will und sprecht mit Klannoc Macleod auf dem Prügel-Eiland."}},
[1738] = {"Herzholz", {"Als Vorbereitung auf Eure Ankunft nutzte ich mein zweites Gesicht, um über die Welt und hinter die Nebel der Zeit zu schauen. Ich habe herausgefunden, wonach Ihr sucht.","Im Krieg der Urtume fielen zwei Liebende gemeinsam im Kampf. Wo sie ihren letzten Seufzer taten, wuchs ein als Herzholz bekannter Baum als Symbol ihrer unsterblichen Liebe.","Er sollte genügen, um einen Sukkubus anzulocken. Ihr findet ihn in den Ruinen von Ordil'Aran im nordwestlichen Teil des Waldes von Ashenvale."}, {"Holt das Herzholz aus Ashenvale und bringt es zu Gakin dem Dunkelbinder im Magierviertel von Stormwind."}},
[1739] = {"Die Bindung", {"Mit dem Herzholzkern könnt Ihr einen Zauber am Beschwörungskreis unten in der Gruft wirken und ein Portal öffnen, durch das der Sukkubus kommen kann.","Seid vorsichtig, <Name>, Sukkubi haben schon den stärksten Geist unterjocht.","Wenn Ihr ihn bezwungen und in die Leere zurückgeschickt habt, kommt wieder zu mir, dann zeige ich Euch, wie Ihr ihn rufen und kontrollieren könnt."}, {"Beschwört mit Hilfe des Herzholzkerns einen Sukkubus, unterwerft ihn und bringt dann den Herzholzkern zu Gakin dem Dunkelbinder im geschlachteten Lamm zurück."}},
[1740] = {"Die Kugel von Soran'ruk", {"Wenn Dämonen der Legion in Ungnade fallen, wird ihr Geist in Kugeln gefangen und an ihre Magier und Hexenmeister verteilt.","Viele wurden von ihren Bediensteten in Azeroth getragen. Selbst als Bruchstücke besitzen sie die Macht der darin gefangenen Dämonen.","Die Kugel von Soran'ruk wurde in zwei Teile zerbrochen. Eines davon wurde abermals zerbrochen und von den Akolyten der Twilight-Hammer in der Blackfathom-Tiefe benutzt. Das andere, größere Teil besaß ein Hexer in der Burg Shadowfang. Sucht sie, dann kann ich sie zusammenfügen..."}, {"Sucht 3 Soran'ruk-Fragmente und 1 großes Soran'ruk-Fragment und bringt sie zu Doan Karhan im Brachland."}},
[1758] = {"Foliant der Kabale", {"Mehr als ein einfacher Beschwörungskreis wird erforderlich sein, um einen Teufelsjäger als Herausforderer für Euch herzubringen.","Meine drei Akolyten und ich helfen, die magischen Kreise zu erschaffen. Ihr benötigt eine Kopie des Folianten der Kabale, der seit geraumer Zeit als verschollen gilt. Er wurde von der ersten Gruppe Hexenmeister geschrieben, die ohne Unterstützung einen Teufelsjäger beschwörten.","Vielleicht kann Krom Stoutarm Euch dabei helfen. Heute ist er ein fahrender Hexenmeister, hortet alte Schriften und hängt in der Bibliothek von Ironforge herum."}, {"Sprecht mit Krom Stoutarm in Ironforge."}},
[1778] = {"Der Foliant der Offenbarung", {"Ich werde bald Pläne für meine Heimkehr machen. Hoffentlich geht es Stephanie gut in Stormwind. Ich kann es kaum erwarten, sie wiederzusehen. Auf bald, <Klasse>."}, {"Sprecht mit Tiza Battleforge in Ironforge."}},
[1779] = {"Der Foliant der Offenbarung", {"Ich möchte, dass Ihr das nehmt, <Name>. Es heißt Symbol des Lebens.","Ihr habt bewiesen, dass Ihr barmherzig und geduldig seid. Jetzt sollt Ihr mir zeigen, wie versiert Ihr geworden seid, was Gefecht und Macht angeht.","Mein Mann Muiredon kehrte jüngst von einer Begegnung mit Dunkeleisenzwergen zurück und kam gerade noch mit dem Leben davon - er ist da drüben und einer unserer Priester heilt schon seine Wunden.","Nehmt das Symbol und redet mit ihm. Wenn Ihr ihm helfen könnt, dann sprechen wir uns bald wieder. Möge das Licht auf Euch scheinen."}, {"Nehmt das Symbol des Lebens und sprecht mit Muiredon Battleforge in Ironforge."}},
[1780] = {"Der Foliant der Offenbarung", {"Ich bringe das noch heute zu den Kindern. Bitte bleibt unversehrt, <Klasse>. Es kommt nicht oft vor, dass <Männer/Frauen> Eures Charakters in der Stadt auftauchen, doch Ihr habt bewiesen, dass die Paladine in Stormwind die Bedürftigen nicht im Stich lassen.","Lebt wohl."}, {"Sprecht mit Duthorian Rall in Stormwind."}},
[1781] = {"Der Foliant der Offenbarung", {"Nehmt das, <Name>. Es heißt Symbol des Lebens.","Ihr habt bewiesen, dass Ihr barmherzig und geduldig seid. Jetzt ist die Zeit gekommen, um festzustellen, ob Ihr stark genug seid für die größte Gabe des Lichts.","Mein Freund Gazin kehrte jüngst von einer Begegnung mit Defias in Elwynn nahe Heldenwache zurück - er kam gerade so mit dem Leben davon. Er ist in der Nähe des Altars, wo unsere Priester seine Wunden heilen.","Nehmt das Symbol und sprecht mit ihm. Wenn Ihr ihm helfen könnt, dann sprechen wir uns bald wieder. Möget Ihr Euch im Schein des Lichts sonnen."}, {"Bringt das Symbol des Lebens zu Gazin Tenorm in Stormwind."}},
[1782] = {"Furens Rüstung", nil, nil},
[1783] = {"Der Foliant der Offenbarung", {"Er spionierte einigen Dunkeleisenzwergen nach, die sich südlich des Helmsbedsees eingebunkert hatten. Wir wussten, dass sie nichts Gutes im Schilde führten, daher dachten Narm und ich, wir sollten der Sache nachgehen - wir sahen, wie ihr Anführer seinen Männern Befehle gab.","Narm und ich wollten einige töten, aber sie waren stärker als wir dachten und es wurden immer mehr. Narm sagte mir, ich solle Verstärkung holen, und hier bin ich.","Narm braucht Hilfe und ich werde erst ruhen, wenn er sie bekommt.","Könnt Ihr helfen, <Name>?"}, {"Nehmt das Symbol des Lebens und erweckt Narm Faulk in Dun Morogh wieder zum Leben."}},
[1784] = {"Der Foliant der Offenbarung", {"Bevor ich im Kampf fiel, hatten Muiredon und ich fast einen der Spione der Dunkeleisenzwerge in der Nähe dieses kleinen Hauses besiegt. Wir wurden Zeugen, wie ihr Captain ihnen Befehle weitergab, und wollten sie töten und die Befehle an uns nehmen.","Einige im Rat von Ironforge glauben nicht, dass sich Dunkeleisenzwerge in Dun Morogh aufhalten. Bekämen wir eines dieser Manuskripte, hätten wir nicht nur den Beweis für ihre Tätigkeiten, sondern möglicherweise auch eine Vorstellung dessen, was sie planen.","Beschafft eines dieser Manuskripte und bringt es zu Muiredon, <Name>."}, {"Beschafft ein Dunkeleisenmanuskript von den Dunkeleisenzwergen beim Helmsbedsee und kehrt zu Muiredon Battleforge in Ironforge zurück."}},
[1785] = {"Der Foliant der Offenbarung", {"Ich sehe, meine Frau steht schon da drüben und platzt vor Stolz. Schleppt Euch besser mal hier rüber, aber pronto. Könnte mir denken, dass sie Euch was zu sagen hat.","'s Licht soll mit Euch sein, <Klasse>. Wenn die Zeit reif ist, such ich nach Euch auf den Schlachtfeldern, wenn es gegen die Geißel und die Dunkeleisenzwerge geht."}, {"Sprecht mit Tiza Battleforge in Ironforge."}},
[1786] = {"Der Foliant der Offenbarung", {"Henze und ich schafften es nach Heldenwache, einer Insel inmitten des Steinhügelsees im nordöstlichen Elwynn. Wir hörten, dass Defias unsere Grenzen unterwandert hatten und wollten einen Beweis für die Berater des Königs.","Als wir eine Bande dieses Abschaums sahen, die Dokumente durchlasen, griffen wir an. Leider wurden wir recht schnell durch ihre Magier überwältigt.","Henze wehrte sie ab, derweil ich entkam ... der elende Narr muss immer den Helden spielen.","Er ist jetzt da draußen, der gute Henze. Könnt Ihr uns helfen, <Name>?"}, {"Nehmt das Symbol des Lebens und erweckt Henze Faulk in Elwynn wieder zum Leben."}},
[1787] = {"Der Foliant der Offenbarung", {"Wir müssen uns später ausgiebiger über das Symbol unterhalten. Wichtig ist jetzt, dass wir diese Papiere bekommen, die die Defias studierten. Wenn wir sie in die Finger bekommen, können wir beweisen, dass die Defias Elwynn unterwandert haben und hoffentlich Einblick in ihre weiteren Pläne erhalten.","Momentan bin ich nicht in der Verfassung zu kämpfen, <Name>. Glaubt Ihr, Ihr seid stark genug, den Defias hier auf der Insel diese Papiere abzunehmen?","Wenn Ihr sie beschaffen könnt, bringt sie Gazin und lasst ihn wissen, dass ich wohlauf bin."}, {"Beschafft ein Manuskript der Defias von den abtrünnigen Hexern der Defias in Elwynn und kehrt zu Gazin Tenorm in Stormwind zurück."}},
[1788] = {"Der Foliant der Offenbarung", {"Ich bringe das Manuskript zu den Beratern des Königs, nachdem Duthorian einen Blick darauf geworfen hat. In der Zwischenzeit solltet Ihr mit Duthorian sprechen. Er wird Euch ganz sicher auf die Schulter klopfen wollen!","Nochmals danke, <Name>. Ich hoffe, wir sehen uns wieder. Ihr seid ein Verbündeter, den ich voller Stolz meinen Freund nenne."}, {"Sprecht mit Duthorian Rall in Stormwind."}},
[1789] = {"Das Symbol des Lebens", nil, nil},
[1790] = {"Das Symbol des Lebens", nil, nil},
[1791] = {"Der Windbehüter", {"Jetzt, da Ihr Eure Berserkerhaltung gelernt habt, wollt Ihr bestimmt auch eine hübsche Waffe dazu.","Sprecht mit Bath'rah dem Windbehüter. Das ist ein Troll-Einsiedler, der zwischen dem Alteracgebirge und den Westlichen Pestländern lebt. Er ist ein geschickter Waffenmacher und mächtiger Schamane.","Um Bath'rah zu finden, solltet Ihr zu dem Fluss gehen, der an der Ostflanke von Tarrens Mühle entlangfließt. Folgt ihm nach Norden - Bath'rah hat sein Lager nahe den Ruinen am Ostufer des Flusses aufgeschlagen."}, {"Sprecht mit Bath'rah dem Windbehüter."}},
[1792] = {"Wirbelwaffe", nil, nil},
[1793] = {"Der Foliant der Ehre", nil, nil},
[1794] = {"Der Foliant der Ehre", nil, nil},
[1795] = {"Die Bindung", {"Wenn Ihr hinreichend vorbereitet seid, sollten wir beginnen. Lasst mich Euch den Vorgang erklären.","Ich beginne damit, dass ich den Standard-Beschwörungskreis erstelle. Wenn das geschehen ist, werden meine Akolyten mit den Kanalisierungsruten den größeren Kreis erstellen.","Ist das abgeschlossen, lest Ihr die Zauberformel aus dem Folianten der Kabale und der Teufelsjäger erscheint. Seid vorsichtig, <Name>. Ein Teufelsjäger ist ein mächtiger Widersacher. Ich möchte nicht, dass er Euch einen Kopf kürzer macht."}, {"Beschwört mit Hilfe des 'Folianten der Kabale' einen Teufelsjäger, unterwerft ihn und bringt den 'Folianten der Kabale' zu Strahad Farsan in Ratchet zurück."}},
[1796] = {"Komponenten für die verzauberte Goldblutrobe", {"Der erste Gegenstand, bei dessen Erschaffung ich Euch helfen werde, ist eine Robe. Ihr werdet die Gegenstände für mich sammeln und wir werden sie zusammensetzen. Vorausgesetzt Ihr seid erfolgreich, werdet Ihr danach den ersten von vielen Gegenständen haben, die Euch mächtiger machen und Euch bei Eurem Kampf helfen werden.","Für den Anfang müsst Ihr in den Besitz der Roben von Arcana kommen. Ein Schneider kann sie für Euch machen oder vielleicht könnt Ihr eine von jemandem kaufen... am meisten würde es mich beeindrucken, wenn Ihr sie selbst herstellen könntet... aber wie auch immer, bringt mir die Roben und dann machen wir weiter."}, {"Bringt die Roben von Arcana zu Menara Voidrender im Brachland."}},
[1798] = {"Die Suche nach Strahad", {"Es ist Zeit, dass Ihr die Beschwörung eines Teufelsjägers lernt, <Name>. Ich habe nicht die erforderlichen Reagenzien und Gegenstände, um Euch hier zu unterrichten, daher müsst Ihr einen anderen aus unserem inneren Kreis aufsuchen, nämlich Strahad Farsan.","Er hat sich im Goblin-Hafen von Ratchet niedergelassen. Ich werde ihn von Eurer Ankunft in Kenntnis setzen lassen."}, {"Sprecht mit Strahad Farsan im Brachland."}},
[1799] = {"Fragmente der Kugel von Orahil", {"Gut, dann können wir beginnen.","Ihr müsst als Erstes mit meinen Akolyten im Inneren sprechen. Wählt einen ihrer Wege und folgt ihm - sie werden Euch alles erklären.","Als Zweites müsst Ihr nach Desolace reisen und eine Höllenkugel von einem der Hexenmeister von Mannorocs Koven holen. Diese Kugel werdet Ihr mit der Hilfe einer Magierin namens Tabetha tief in den südlichen Marschen von Dustwallow reinigen. Sie wird Euch aufgrund unserer gemeinsamen Vergangenheit helfen.","Kommt erst zurück, wenn Ihr beide Dinge erledigt habt, um die ich Euch gebeten habe."}, {"Sprecht mit Menaras Akolyten im Inneren des Turms über Ratchet und wählt einen ihrer Wege, um ihm zu folgen.","Bringt danach eine Höllenkugel zu Tabetha in den Marschen von Dustwallow."}},
[1800] = {"Der Thronsaal von Lordaeron", {"Wenn wir brav sind und die Hausarbeiten ordentlich machen, erzählen uns die Waisenmatronen oft Geschichten von großen Schlachten und darüber, wie Lordaeron fiel und wie der böse Arthas seinen eigenen Vater umbrachte, nur um König zu werden.","Er hatte keine Ehre, <Name>! Wenn ich erst einmal groß und ein mächtiger Krieger der Horde bin, wird mein Schwert durch die Ehre geleitet werden; niemals durch das Böse!","Würdet Ihr mit mir den Thronsaal besuchen, bitte? Er befindet sich kurz vor dem Abgang, über den man nach Unterstadt heruntergeht."}, {"Besucht mit Eurem Schützling den alten Thronsaal in Lordaeron. Er befindet sich kurz vor dem Abgang nach Unterstadt."}},
[1801] = {"Foliant der Kabale", {"Mehr als ein einfacher Beschwörungskreis wird erforderlich sein, um einen Teufelsjäger als Herausforderer für Euch herzubringen.","Meine drei Akolyten und ich helfen, die magischen Kreise zu erschaffen. Ihr benötigt eine Kopie des Folianten der Kabale, der seit geraumer Zeit als verschollen gilt. Er wurde von der ersten Gruppe Hexenmeister geschrieben, die ohne Unterstützung einen Teufelsjäger beschwörten.","Vielleicht kann Jorah Annison Euch dabei helfen. Heute arbeitet er als Kartograf in Undercity, weiß aber dennoch mehr über alte Schriften als ich."}, {"Sprecht mit Jorah Annison in Undercity."}},
[1802] = {"Foliant der Kabale", {"Er ist verschollen, aber man glaubt zu wissen, wo man ihn finden könnte.","Nur eine Kopie wurde jemals angefertigt, und die wurde einst beschädigt, eine Vielzahl von Seiten herausgerissen. Man munkelt, dass er irgendwie den Zentauren von Tausend Nadeln in die Hände gefallen sein soll.","Der Rest ging an der Küste westlich von Southshore verloren, als er per Schiff zum Hafen von Menethil hätte transportiert werden sollen. Er kam nie an.","Als Gegenleistung für dieses Wissen verlange ich nur, dass Ihr ihn findet und mir zur Ansicht bringt."}, {"Beschafft den schimmeligen Folianten und das zerlumpte Manuskript für Krom Stoutarm in Ironforge."}},
[1803] = {"Foliant der Kabale", {"Ah! Ich sehe, Ihr seid ein Hexenmeister, der meinen Geschmack teilt. Den Folianten der Kabale zu beschaffen ist ein würdiges Ziel. Habt Ihr gewusst, dass er sich einst innerhalb der Mauern von Lordaeron befand? Aber er wurde beschädigt, fast die Hälfte der Seiten herausgerissen. Wer würde so eine abscheuliche Tat begehen?","Diese Hälfte ist verschollen, doch man munkelt, sie soll den Zentauren von Tausend Nadeln in die Hände gefallen sein.","Der Rest des Textes sollte nach Ironforge gebracht werden, ging aber während der Überfahrt an der Westküste von Southshore verloren."}, {"Beschafft den schimmeligen Folianten und das zerlumpte Manuskript für Jorah Annison in Undercity."}},
[1804] = {"Foliant der Kabale", {"Ach ja, es ist recht schwer, ihn aus der Hand zu geben. Aber Ihr habt ihn gefunden und natürlich Wichtigeres damit zu tun. Vergesst nur nicht, Strahad zu sagen, dass ich eine Kopie haben möchte.","Während Ihr fort wart, hat er eine Nachricht für Euch geschickt. Er sagte mir, damit seine Akolyten bei der Beschwörung helfen können, müsst Ihr drei Kanalisierungsruten beschaffen.","Er weiß vielleicht nicht, wo man die findet, aber ich! Sie wurden zuletzt von den Orcs des Dragonmawklans im Sumpfland verwendet. Ich würde mich mal mit ihren Zauberern in Verbindung setzen!"}, {"Bringt den rekonstruierten Folianten und 3 Kanalisierungsruten zu Strahad Farsan in Ratchet."}},
[1805] = {"Foliant der Kabale", {"Das ist einer der interessantesten Texte, die ich in den letzten Jahren gelesen habe. Die Auswahl hier ist nicht sonderlich groß - zum Großteil sind es Bücher über Geographie - und ich habe sie schon alle gelesen. Hoffentlich schickt mir Strahad eine Kopie dieses Folianten, wenn er die Chance dazu hat.","Da wir gerade von ihm sprechen: während Ihr fort wart, habe ich eine Nachricht von ihm erhalten. Für Eure Aufgabe müsst Ihr drei Ruten des Kanalisierens beschaffen.","Weil Ihr mir diesen Text gezeigt habt, weihe ich Euch in ein Geheimnis ein: Ihr findet die Ruten bei den Orcs des Dragonmawklans im Sumpfland."}, {"Bringt den rekonstruierten Folianten und 3 Ruten des Kanalisierens zu Strahad Farsan in Ratchet."}},
[1806] = {"Die Prüfung der Rechtschaffenheit", {"Warum gehen wir nicht rüber zur Schmiede und fangen damit an? Ich treffe schon seit einiger Zeit Vorbereitungen für Eure Ankunft, daher dürfte es nicht allzu lange dauern.","Wenn wir fertig sind, werdet Ihr viel besser gerüstet sein, das Licht zu verteidigen, als bisher, das schwöre ich Euch.","Ich will ehrlich sein, <Name>, wenn ich die Ergebnisse Eurer Taten sehe, erfüllt mich ein gewisser Neid. Doch das Wissen, dass ich auf meine bedeutende Weise zu Euren Abenteuern beitragen kann, erfüllt mich mit Freude."}, {"Wartet, bis Jordan Stilwell eine Waffe für Euch geschmiedet hat."}},
[1818] = {"Gespräch mit Dillinger", {"Todeswache Dillinger sagte mir, er habe einen Job für jemanden wie Euch. Wenn Ihr bereit seid, den Streitkräften der dunklen Fürstin Euren Wert zu beweisen, dann meldet Euch umgehend bei Dillinger.","Todeswache Dillingers Posten liegt am Westende von Brill, nahe beim Friedhof."}, {"Sprecht mit Todeswache Dillinger in Brill."}},
[1819] = {"Ulag der Spalter", {"Als die Geißel durch Tirisfal zog, konnten wir einen ihrer Angehörigen einfangen, um ihn zu studieren. Er hatte einen freien Geist und nannte sich Ulag der Spalter. Doch obwohl er einen freien Willen besaß, stand er noch immer unter dem Bann des Lichkönigs.","Wir hofften, wir könnten den Einfluss des Königs auf Ulag brechen, doch es gelang uns nicht. Jetzt ist die Zeit gekommen, ihn zu befreien.","Befreit und tötet ihn.","Aktiviert den Schädelstein-Auslöser beim Mausoleum im Norden. Damit wird das magische Siegel entfernt, das ihn bindet.","Und dann... seid bereit, <Name>."}, {"Tötet Ulag den Spalter und sprecht dann mit Todeswache Dillinger."}},
[1820] = {"Gespräch mit Coleman", {"Ihr habt Euren Wert für die dunkle Fürstin bewiesen, vielleicht könnt Ihr ihn nun an einem ihrer Untertanen beweisen.","Coleman Farthing wartet auf einen Krieger, der bereit ist, sein Instrument der Rache gegen die Feinde der Verlassenen zu sein.","Ihr findet ihn in der Taverne Zur Galgenschlinge im Osten von Brill."}, {"Sprecht mit Coleman Farthing."}},
[1821] = {"Agamand-Erbstücke", {"Zu Lebzeiten war die Familie Agamand mein Arbeitgeber und sie war die Ursache für meinen Tod. Es ist nur recht und billig, dass die Agamands jetzt hirnlose Diener des Lichkönigs sind.","Und jetzt möchte ich ihnen den letzten Rest Ehre nehmen. Geht in ihre Familiengruft und stehlt ihnen die Familienerbstücke: ein Schwert, eine Axt, einen Dolch und einen Streitkolben. Bringt sie mir!","Die Gruft der Familie Agamand liegt am nördlichen Ende von Agamands Mühlen. Der Ort wird durch lauernde Angehörige der Geißel und tote Agamands bewacht."}, {"Bringt Coleman Farthing die Axt, das Schwert, den Streitkolben und den Dolch der Familie Agamand."}},
[1822] = {"Erbstückwaffe", nil, nil},
[1823] = {"Gespräch mit Ruga", {"Ihr seid ein Krieger mit Fertigkeiten und wachsendem Ansehen. Vielleicht seid Ihr bereit für Ruga Ragetotems Prüfung. Wenn ja, findet Ihr Ruga im Camp Taurajo im Brachland.","Er wartet auf Krieger, die seinem Ruf folgen."}, {"Sprecht mit Ruga Ragetotem."}},
[1824] = {"Prüfung auf dem Feld der Riesen", {"Um diese Prüfung zu bestehen, müsst Ihr nach Süden zum Feld der Riesen reisen. Dort findet Ihr einen Schwarm krabbelnder Insektenwesen. Sie sind neu im Brachland und mir gefällt nicht, was sie ankündigen.","Bringt mir einen ihrer noch zuckenden Fühler und vergeudet keine Zeit, <Name>, denn abgetrennte Fühler zucken nicht lange. Bringt mir die Fühler in der genannten Zeit und Ihr werdet die Prüfung bestehen."}, {"Bringt 5 zuckende Fühler zu Ruga Ragetotem im Camp Taurajo."}},
[1825] = {"Gespräch mit Thun'grim", {"Thun'grim Firegaze, ein berühmter Orc-Schmied, hat von Euren fortgesetzten Heldentaten im Dienst der Horde gehört. Er möchte eine Rüstung für Euch anfertigen und bittet, dass Ihr umgehend mit ihm sprecht.","Thun'grims Lager liegt östlich von Crossroads im Brachland."}, {"Sprecht mit Thun'grim Firegaze im Brachland."}},
[1838] = {"Brutale Rüstung", {"Ich habe Geschichten über Eure Kraft und Tapferkeit gehört und möchte eine Rüstung für Euch anfertigen, eine Unterstützung für Euch, während die Geschichten über Eure Ruhmestaten wachsen.","Aber die Herstellung dieser Rüstung wird eine Aufgabe für sich sein, <Name>. Ich werde Eure Hilfe benötigen und die Hilfe der anderen Schmiede der Horde.","Hier, nehmt diese Rolle. In vielen Nächten habe ich mit fast blinden Augen aufgeschrieben, was ich für die Rüstung benötige. Bringt mir die Gegenstände auf dieser Rolle und ich fertige das erste Teil der Rüstung für Euch an."}, {"Bringt Thun'grim Firegaze 15 rauchige Eisenblöcke, 10 pulverisierte Azurite, 10 Eisenbarren und 1 Phiole Phlogiston."}},
[1839] = {"Ula'elek und die brutalen Stulpen", {"Der Trollschmied Ula'elek wird die brutalen Stulpen für Euch anfertigen. Er wohnt mit seinen Darkspearbrüdern in Durotar in Sen'jin und erwartet Eure Ankunft."}, {"Sprecht mit Ula'elek in Durotar."}},
[1840] = {"Orm Stonehoof und der brutale Helm", {"Orm Stonehoof wird ob seiner Fertigkeiten hoch geachtet. Er wird den brutalen Helm für Euch machen.","Sucht ihn in Thunder Bluff beim Teich nahe dem großen, zentralen Totem der Stadt."}, {"Sprecht mit Orm Stonehoof in Thunder Bluff."}},
[1841] = {"Velora Nitely und die brutalen Beinschützer", {"Es heißt, vor ihrem Tod hätte Velora Nitely zu den besten Rüstungsschmieden von Lordaeron gehört. Offenbar konnte der Tod ihrem Talent nichts anhaben. Sprecht mit Velora - sie wird die brutalen Beinschützer für Euch machen.","Velora geht ihrem Gewerbe in Undercity unterhalb von Lordaeron nach, im Handwerksviertel."}, {"Sprecht in Undercity mit Velora Nitely."}},
[1842] = {"Satyrhufe", {"Ich kann Euch ein Paar brutale Stulpen machen, denen große Magie innewohnen wird. Doch vorher müsst Ihr diese Magie sammeln und mir bringen.","Die Satyrn halten sich im nordöstlichen Teil von Ashenvale auf wegen der Macht, die dem Wald innewohnt. Sie nähren sich an seiner Reinheit, saugen sie ab und verderben sie. An den geschwollenen, ungespaltenen Hufen erkennt man, welche Satyrn die meiste Magie Ashenvales aufgenommen haben.","Diese Hufe besitzen große Macht. Ich brauche sie, um Eure Stulpen anzufertigen."}, {"Bringt 7 ungespaltene Satyrhufe zu Ula'elek in Sen'jin in Durotar."}},
[1843] = {"Brutale Stulpen", nil, nil},
[1844] = {"Schimärenhorn", {"Ich mache Euch den Helm, doch vorher müsst Ihr mir zeigen, dass Ihr würdig seid.","Eingebettet in die nordwestlichen Ausläufer des Steinkrallengebirges liegt das verbrannte Tal. Das war einst ein fruchtbarer Ort, ist aber heute besudelt und voll Asche. Die Kreaturen, die dort noch hausen, sind wahnsinnig vor Schmerz und Verzweiflung.","An diesen Ort schicke ich Euch, <Name>.","Ihr müsst zum verbrannten Tal reisen und eine Schimärenmatriarchin suchen. Tötet sie, erlöst sie von ihrem Elend. Bringt mir ihr galvanisiertes Horn als Beweis, dann mache ich den Helm für Euch."}, {"Bringt ein galvanisiertes Horn zu Orm Stonehoof in Thunder Bluff."}},
[1845] = {"Brutaler Helm", nil, nil},
[1846] = {"Schienbeinknochen der Dragonmaw", {"Um brutale Beinschützer zu machen, brauche ich eine Stütze aus stabilem Schienbeinknochen. Die Orcs des Dragonmawklans im Sumpfland haben hübsche, dicke Schienbeinknochen. Die brauche ich.","Aber sie müssen stabil sein. Prüft die Schienbeinknochen unbedingt, wenn Ihr sie einsammelt. Wenn Ihr sie mit bloßen Händen zerbrechen könnt, sind sie nicht stabil genug für mich."}, {"Bringt 8 stabile Schienbeinknochen eines Dragonmaw zu Velora Nitely in Undercity."}},
[1847] = {"Brutale Beinschützer", nil, nil},
[1848] = {"Brutale Halsberge", nil, nil},
[1858] = {"Die Shattered-Hand", {"Ich überlasse es Euch, Tazans Ranzen zu öffnen und mir seinen Inhalt zu bringen. Der Schlossmechanismus ist komplex, daher wäre es wohl das Beste, wenn Ihr Euch den Schlüssel beschafft.","Da Ihr ihn bei Tazans Leichnam nicht gefunden habt, kann ich nur annehmen, dass er ihn jemandem in Obhut gegeben hat. Ah, ja, ganz recht! Er hatte einen Assistenten namens Gamon. Ein Trunkenbold, vermutlich findet Ihr ihn in der Taverne. Vielleicht könnt Ihr ihm den Schlüssel heimlich abnehmen."}, {"Stehlt Tazans Schlüssel, öffnet seinen Ranzen damit und bringt den Inhalt zu Therzok in der Kluft der Schatten in Orgrimmar."}},
[1859] = {"Therzok", {"Sprecht leiser, <Name>. Ja, ich kenne Euren Namen. Mir wurde gesagt, ich soll nach einem Orc Ausschau halten, auf den Eure Beschreibung passt.","Soweit wir gehört haben, könnt Ihr im Gefecht bestehen und habt nichts gegen ein wenig Heimlichtuerei von Zeit zu Zeit einzuwenden.","Habe ich Recht?","Wie auch immer, Ihr solltet Therzok in Orgrimmar aufsuchen. Er hat ein Angebot, das Ihr Euch anhören solltet."}, {"Sprecht mit Therzok in der Kluft der Schatten in Orgrimmar."}},
[1860] = {"Gespräch mit Jennea", {"Wir Magier haben eine Aufgabe, die erledigt werden muss. Sie ist für die Sicherheit von Stormwind und dem Wald von Elwynn von entscheidender Bedeutung.","Jennea Cannon kennt die Einzelheiten. Sie studiert im Magiersanktum im Magierviertel von Stormwind. Ihr solltet Euch eiligst zu ihr begeben."}, {"Sprecht in Stormwind mit Jennea Cannon."}},
[1861] = {"Spiegelsee", {"Diese Welt ist gewaltigen mystischen Kämpfen zum Opfer gefallen, die magische Ströme aus ihren einst stabilen Kanälen rissen. Wir haben Mühe, die Auswirkungen dieser Veränderungen herauszufinden. Einige von uns fürchten das Schlimmste.","Aber davon werde ich Euch später mehr erzählen. Im Augenblick müsst Ihr Informationen für uns sammeln.","Hier, nehmt dieses Fläschchen. Geht zum Wasserfall des Spiegelsees, südwestlich der Tore von Stormwind, und holt dort eine Probe des Wassers. Bringt sie mir, damit ich die magische Besudelung messen kann."}, {"Bringt eine Probe des Spiegelsee-Wassers zu Jennea Cannon in Stormwind."}},
[1878] = {"Wasserbeutelkopfgeld", nil, nil},
[1879] = {"Gespräch mit Bink", {"<Name>! Bink, meine Gnomen-Kollegin in Ironforge, hat sich gemeldet. Sie hat eine Aufgabe zu erledigen und will sich dabei nur auf einen Magier verlassen! Seid Ihr <ein Magier/eine Magierin>, <Name>?","Wenn ja, sprecht mit Bink. Sie verbringt ihre Zeit in Ironforge in der Halle der Mysterien im mystischen Krieg."}, {"Sprecht mit Bink in Ironforge."}},
[1880] = {"Magitastischer Gizmonitor", {"Wir Magier machen mehr als nur mit den Fingern schnippen und hübsche Funken regnen lassen. Wir kontrollieren die Magie, die Grundsubstanz unserer Welt! Das erfordert große Umsicht und Entschlossenheit. Und Wissen... viel, viel Wissen!","Eines meiner Geräte, der magitastische Gizmonitor, sammelt und speichert dieses Wissen in riesigen Mengen! Aber ich verlor ihn! Als wir aus unserer Heimat Gnomeregan flohen, ließ ich ihn in einer Truhe in einer unserer Hütten vor der Stadt, und jetzt wimmelt es dort von Lepragnomen!","Bitte, <Name>, findet meinen Gizmonitor wieder!"}, {"Bringt Bink ihren magitastischen Gizmonitor."}},
[1881] = {"Gespräch mit Anastasia", {"<Name>, Anastasia Hartwell hat eine Aufgabe für jemanden mit Euren Talenten. Sprecht mit ihr im Magieviertel von Undercity und bereitet Euch auf den ersten Auftrag der Magier der Verlassenen vor."}, {"Sprecht mit Anastasia Hartwell."}},
[1882] = {"Balnirs Bauernhof", {"Die Pestländer im Osten sind entartet. Der Lichkönig hat sie ersonnen, und jetzt breitet sich ihre Magie im Erdboden aus und verdirbt und tötet alles Leben. Die zerstörerischen Kräfte der Seuche haben zwar eine gewisse Bezauberung, doch wir, die Magier der Verlassenen, sind der Meinung, sie ist ungehörig und muss ausgemerzt werden.","Doch zuerst müssen wir herausfinden, wie weit sie sich nach Tirisfal ausgebreitet hat. Geht zu Balnirs Bauernhof im Osten von Tirisfal. Im Garten blühen Blumen - Löwenmäulchen. Sammelt sie und kehrt zurück."}, {"Bringt Balnir-Löwenmäulchen zu Anastasia Hartwell im Magierviertel von Undercity."}},
[1883] = {"Gespräch mit Un'thuwa", {"Un'thuwa, Stammesmagier in Sen'jin, spürt eine Störung in den magischen Strömen von Durotar und bittet um Unterstützung. Sprecht mit Un'thuwa und tut, was Ihr könnt, um ihm zu helfen.","Gelingt Euch das, werden Euch zu gegebener Zeit weitere Geheimnisse enthüllt."}, {"Sprecht mit Un'thuwa."}},
[1884] = {"Jujuhaufen", {"Der Hexenmeister Zalazane von den Echoinseln hat den normalen Fluss der Magie in Durotar gestört. Das vollbringt er mit besudelten Fetischen, Jujuhaufen, die er auf der Echohauptinsel in seinen Hütten aufgeschichtet hat.","Diese Haufen haben unser Ökosystem verdorben und Hexenmeistern und Höllenkreaturen, die in den dunklen Winkeln des Landes hausen, Macht gegeben.","Ihr müsst diese Jujuhaufen zerstören, <Name>, bevor der Fluss der Magie für immer verändert ist."}, {"Zerstört 4 Jujuhaufen und kehrt dann zu Un'thuwa in Sen'jin zurück."}},
[1885] = {"Mennet Carkad", {"<Name>? Nicht ganz das, was ich erwartet hatte, wenn man bedenkt, dass Carkad selbst Interesse an Euch gezeigt hat. Aber das Aussehen kann täuschen, nicht?","So oder so, Ihr solltet ihn schnellstmöglich aufsuchen. Ihr könnt Euch nicht vorstellen, was aus den Letzten geworden ist, die seinem Ruf nicht Folge leisteten."}, {"Sprecht mit Mennet Carkad im Schurkenviertel von Undercity."}},
[1886] = {"Die Todespirscher", {"Wenn Ihr Euch den Todespirschern anschließen möchtet, der Elitewache von Lord Varimathras, müsst Ihr Euren Wert beweisen.","Ich will Euch nichts vormachen, <Name>, diese Aufgabe könnte Euren Tod bedeuten.","Ich habe ein paar Angelegenheiten, die geregelt werden müssen. Ich benötige die persönlichen Habseligkeiten von Astor Hadren, einem Boten, der zwischen Silberwald und Undercity pendelt. Die Beschaffungsmethode überlasse ich ganz Euch.","Je weniger Ihr über diese Angelegenheit Bescheid wisst, desto besser. Wenn Ihr Erfolg habt, sage ich Euch mehr."}, {"Beschafft Astors Empfehlungsschreiben und bringt es zu Mennet Carkad im Schurkenviertel."}},
[1898] = {"Die Todespirscher", {"Das Empfehlungsschreiben sollte Andron überzeugen, dass Ihr der Bote seid, auf den er gewartet hat. Ich kenne Andron, er ist nicht so helle im Kopf, dass er Verdacht schöpfen würde.","Lord Varimathras vermutet, dass Andron einer anderen Gruppe Unterstützung zuteil werden ließ. Er möchte wissen, was das für eine Organisation ist, und braucht einen Beweis.","Ich möchte, dass Ihr mit Andron sprecht und diese Information bekommt. Ihr findet ihn in seinem Geschäft nahe des Apothekariums."}, {"Sprecht mit Andron Gant in Undercity."}},
[1899] = {"Die Todespirscher", {"Ihr findet das Geschäftsbuch mit den Informationen, die Eure Auftraggeber suchen, auf dem Bücherregal hinter mir. Überbringt es mit meinen besten Wünschen und sagt ihnen, dass ich es bevorzugen würde, wenn sie Euch schicken, sollte unsere Übereinkunft ihnen so lieb und teuer sein, dass sie sie fortsetzen möchten."}, {"Bringt Astors Geschäftsbuch zu Mennet Carkad im Schurkenviertel von Undercity."}},
[1918] = {"Das beschmutzte Element", {"Der erschlagene Elementar hat eine intensiv glühende Kugel aus Wasser zurückgelassen. Der Inhalt dieser Kugel ist bestenfalls als abstoßend zu bezeichnen. Es ist eine sichere Annahme, dass, was auch immer das Wasser des Mystralsees beschmutzt hat, höchstwahrscheinlich im Wasser dieser Kugel enthalten ist.","Es ist nicht gerade weit bekannt, wer sich für solch einen Fund interessieren könnte. Vielleicht weiß die Person etwas darüber, die Euch ursprünglich zum See hinausgeschickt hatte - Mastok Wrilehiss im Splintertreeposten."}, {"Bringt die beschmutzte Wasserkugel zu Mastok Wrilehiss im Splintertreeposten in Ashenvale."}},
[1919] = {"Meldung bei Jennea", {"Euer Wissen über das Handwerk wächst, <Name>. Seid Ihr jetzt bereit, die wahren Lektionen zu beginnen? Seid Ihr bereit, Euren Platz in dem geheimen Krieg einzunehmen, den wir führen?","Wenn ja, sprecht mit Jennea Cannon im Magiersanktum im Magierviertel von Stormwind. Sie hat eine Aufgabe für Euch, die Euer erstes Fenster in die Welt des Unsichtbaren öffnen wird."}, {"Sprecht in Stormwind mit Jennea Cannon."}},
[1920] = {"Untersuchungen im Blauen Eremiten", {"Es wurde von einer Störung in der Taverne 'Zum blauen Eremiten' im Magierviertel berichtet. Jetzt wandern seltsame unsichtbare Kreaturen durch die Taverne. Ich fürchte, dort hat sich ein Manariss gebildet.","Ich möchte, dass Ihr die Taverne untersucht und diese Kreaturen fangt. Benutzt die Inkantation der Manifestation, um die Kreaturen in der Taverne sichtbar zu machen, überwältigt sie und fangt sie dann mit einem Verwahrungskasten ein. Wenn Ihr genug habt, kommt mit den gefüllten Kästen, den leeren Kästen und der Anrufung zu mir zurück."}, {"Nehmt Euch die Inkantation der Manifestation und die Truhe mit Verwahrungskästen vom Tisch, hinter Jennea Cannon. Bringt 3 gefüllte Verwahrungskästen zu Jennea im Magiersanktum."}},
[1921] = {"Materialsammlung", {"Ihr habt Euren Magierkollegen gut gedient, jetzt wird es Zeit, die Hexerrobe anzuziehen. Wynne Larson kann Eure Robe nähen, aber Ihr müsst ihr die erforderlichen Materialien bringen.","Eure Robe wird aus Leinen bestehen, das leicht zu beschaffen ist, aber Ihr müsst auch aufgeladene Rissedelsteine finden, die nur in der Silberbachmine vom Loch Modan zu finden sind, wo die Minenarbeiterliga der Zwerge sie in Kisten zurückgelassen hat.","Beschafft das Leinen und die Edelsteine und bringt sie zu Wynne Larson in das Konfektionsgeschäft Larson im Magierviertel."}, {"Bringt 10 Leinentücher und 6 aufgeladene Rissedelsteine zu Wynne Larson in Stormwind."}},
[1938] = {"Urs Abhandlung über Schattenmagie", {"Vor langer Zeit studierten drei Magier in diesem Turm, bis einer den dunklen Künsten verfiel. Man bedrängte ihn, von dieser bösen Magie zu lassen, aber er konnte nicht widerstehen. Und so wurde er verbannt.","Das neue Domizil des gefallenen Magiers ist der Turm von Ilgalar im Rotkammgebirge. Er hat ein mächtiges Buch dorthin mitgenommen -'Urs Abhandlung über Schattenmagie'.","Wir, die Magier von Stormwind, möchten dieses Buch zurück. Begebt Euch in den Turm von Ilgalar, sucht den Folianten in seiner Bibliothek und bringt ihn mir."}, {"Bringt 'Urs Abhandlung über Schattenmagie' zu Zaubermeister Andromath in Stormwind."}},
[1939] = {"Zaubermeister Andromath", {"Der Zaubermeister Andromath wünscht Euch zu sprechen, <Name>. Er ist oben im Magiersanktum im Magierviertel von Stormwind.","Es wäre klug, mit ihm zu sprechen, denn Andromath ist einer der mächtigsten Magier, die nicht in Dalaran eingesperrt sind. Ein Rat von ihm ist in jedem Fall ein guter Rat."}, {"Sprecht mit Zaubermeister Andromath."}},
[1940] = {"Makellose Spinnenseide", {"Eure Treue ist unerschütterlich, <Name>. Ich glaube, es ist an der Zeit, dass Ihr ein besseres Kleidungsstück bekommt, das einem geschickten Magier von Eurem Rang angemessener ist. Wenn Ihr die erforderlichen Materialien sammeln könnt, werde ich veranlassen, dass die Robe angefertigt wird.","Ihr müsst in den Dämmerwald und Aaskrabblerspinnen jagen. Extrahiert ihre Seide, aber nur die feinste und makelloseste. Wenn Ihr genug davon habt, bringt sie zu Wynne Larson ins Konfektionsgeschäft Larson hier im Magierviertel. Sie wird Eure Robe anfertigen."}, {"Bringt 8 Rollen makelloser Spinnenseide zu Wynne Larson in Stormwind."}},
[1941] = {"Managewirkte Robe", nil, nil},
[1942] = {"Astralknotengewand", nil, nil},
[1943] = {"Gespräch mit Deino", {"Die Trollmagierin Deino bedarf Eurer Unterstützung, <Name>. Sie residiert in Orgrimmar im Dunkeldornen-Lodge."}, {"Sprecht mit Deino."}},
[1944] = {"Wasser von Xavian", {"Die legendäre Stadt Xavian liegt tief im östlichen Ashenvale. Die Geschichten über ihr Wissen aus längst vergangener Zeit sind den Gebildeten wohl bekannt. Man sagt, die Wasser von Xavian bergen enorme magische Kräfte.","Bringt mir eine Probe dieses Wassers direkt von der Quelle.","Nehmt dieses Fläschchen und reist nach Xavian. Füllt Wasser am höchsten Wasserfall ab und kehrt zu mir zurück. Aber seid vorsichtig in der uralten Stadt - heute hausen Satyrn in den Ruinen, die gar nicht mögen, wenn sich Magier in ihre Angelegenheiten einmischen."}, {"Bringt die Probe des Wassers von Xavian zu Deino in Orgrimmar."}},
[1945] = {"Lachende Schwestern", {"Als Dank für Eure Dienste habe ich veranlasst, dass Euch neue Roben angefertigt werden, sofern Ihr die erforderlichen Materialien beschaffen könnt...","Für diese Roben müsst Ihr nach Ashenvale zurückkehren und Locken vom Haar der lachenden Schwestern beschaffen - Dryaden, die sich in der Nähe des Raynewoodes tummeln.","Wenn Ihr das Haar habt, bringt es zu Kil'hala, einem Trollschneider in Crossroads im Brachland. Er wird Euch erwarten und Eure Roben zuschneiden."}, {"Bringt 12 Haarlocken der lachenden Schwestern zu Kil'hala in Crossroads."}},
[1946] = {"Netherspitzengewand", nil, nil},
[1947] = {"Reise in die Marschen", {"Jetzt ist es an der Zeit, Euch Euren Magierzauberstab zu verdienen. Um diese Aufgabe zu beginnen, solltet Ihr mit der Menschen-Einsiedlerin Tabetha in den Marschen von Dustwallow sprechen. Ihr findet ihre Hütte tief in den Marschen, westlich von Theramore.","Sprecht mit ihr, denn sie verfügt über umfangreiches Wissen. Tabethas Hütte befindet sich westlich von Theramore und direkt nördlich der Stonemaul Ruinen."}, {"Sprecht mit Tabetha."}},
[1948] = {"Gegenstände der Macht", {"Es ist nicht leicht, einen Magierzauberstab anzufertigen. Es sind rare Substanzen erforderlich und ein Gefäß muss gemacht werden, das große Mengen magischer Energie speichern kann. Ihr müsst diese Sachen sammeln und mir bringen.","Ich habe auf dieses Pergament geschrieben, was Ihr braucht und wie Ihr es bekommt. Bringt mir exakt das, was auf der Liste steht und folgt meinen schriftlichen Anweisungen auf den Buchstaben genau, denn wenn Ihr das nicht macht, geht bei der Herstellung Eures Zauberstabes vielleicht etwas ... schief."}, {"Bringt 1 Stück Jade und den vom Blitz aufgeladenen Dornbusch zu Tabetha in den Marschen von Dustwallow."}},
[1949] = {"Verborgene Geheimnisse", {"Um Euren Zauberstab herzustellen, muss ich einige wichtige Stellen in einem bestimmten Buch lesen. Dieses Buch wurde von dem Magus Tirth geschrieben und heißt 'Rituale der Macht'.","Ich besitze kein Exemplar dieses Buchs und es ist in keiner der großen Bibliotheken der Welt zu finden. Daher müsst Ihr mit Tirth sprechen und es von ihm bekommen.","Viel Glück. Tirth war ein großer Magier und Gelehrter, aber heute verbringt er seine Zeit bei den Gnomen- und Goblinrennen auf der schimmernden Ebene und trinkt und spielt."}, {"Sprecht mit Magus Tirth in der schimmernden Ebene."}},
[1950] = {"Der Knüller schlechthin", {"Ich kann Euch mit dem Buch helfen, doch vorher müsst Ihr mir helfen!","Der Foliant, den Ihr sucht, befindet sich in meiner magisch versiegelten Geldkassette. Um sie zu öffnen, muss ein bestimmter Satz gesprochen werden, aber ich kann mich nicht mehr an ihn erinnern! Normalerweise merkt sich mein Assistent so etwas, aber der ist unauffindbar.","Wenn Ihr mein Buch möchtet, sucht meinen Lehrling! Fragt auf der Rennbahn - wenn Ihr Glück habt, hat ihn jemand gesehen. Schleppt ihn entweder hierher oder entlockt ihm wenigstens meinen magischen Satz, damit wir die Kassette aufbekommen!"}, {"Bringt den Satz für Tirths Geldkassette in Erfahrung und kehrt zu Tirth zurück."}},
[1951] = {"Rituale der Macht", {"Hm, wenn ich so darüber nachdenke, ich habe mein Buch nicht! Wisst Ihr, ich hatte Spielschulden, und um sie zu bezahlen, verkaufte ich das letzte Exemplar von 'Rituale der Macht' an ein Mitglied der Kleriker...","Ein Mitglied der Scharlachroten Bruderschaft. Er muss es zur Bibliothek im Scharlachroten Kloster in Tirisfal gebracht haben!","Wenn Ihr das Buch wollt, müsst Ihr in das Kloster gehen und es suchen. Und entschuldigt den ganzen Ärger. Ich bin neuerdings nicht mehr ich selbst..."}, {"Bringt das Buch 'Rituale der Macht' zu Tabetha in den Marschen von Dustwallow."}},
[1952] = {"Der Zauberstab des Magiers", {"Ich werde jetzt die Zauberstäbe machen."}, {"Sprecht nach ihrem Ritual mit Tabetha."}},
[1953] = {"Rückkehr in die Marschen", {"<Name>, es wird Zeit, dass Ihr mit Tabetha sprecht... der Einsiedlerin in den Marschen von Dustwallow. Sie wird Euch in der Herstellung einer mächtigen Magiergerätschaft unterweisen.","Tabethas Hütte liegt tief in den Marschen von Dustwallow, westlich von Theramore."}, {"Sprecht mit Tabetha."}},
[1954] = {"Die Höllenkugel", {"Als ersten Schritt müsst Ihr eine Kugel voll Macht beschaffen. Die Hexenmeister der Burning Blade haben hübsche, hochwertige Kugeln... zu dumm, dass sie Dämonen darin eingebunden haben! Aber das können wir ändern, ja.","Geht nach Desolace und nehmt einem Beschwörer der Burning Blade eine Höllenkugel ab. Beschwörer findet Ihr bei Mannorocs Koven.","Kommt zu mir zurück, wenn Ihr die Kugel habt."}, {"Bringt eine Höllenkugel zu Tabetha in den Marschen von Dustwallow."}},
[1955] = {"Die Austreibung", {"Jetzt, da wir eine Beschwörerkugel haben, müssen wir den Dämon darin austreiben. Ich kann es machen. Das ist der einfache Teil.","Ihr habt die schwere Aufgabe. Ihr müsst den Dämon töten, wenn er draußen ist.","Und Ihr müsst Euch beeilen. Wenn Ihr zu lange braucht, springt er in die Kugel zurück und wir müssen von vorn anfangen!","Also macht Euch bereit. Bereitet Eure stärksten Zauber vor, habt Eure Tränke parat und holt einen Freund, wenn Ihr einen habt. Denn das hier wird nicht angenehm."}, {"Tötet den Dämon der Kugel und sprecht dann mit Tabetha."}},
[1956] = {"Macht in Uldaman", {"Jetzt, nachdem der Dämon entfernt wurde, müssen wir eine Kraftquelle finden. Leider sind die besten Kraftquellen meistens die ältesten und die, an die man am schwersten rankommt. Ist schon lustig, wie einem das Schicksal manchmal mitspielt, wie?","Also müsst Ihr nach Uldaman und dort die Obsidianschildwache besiegen. Der Kerl ist groß, er ist zäh und seine Kraftquelle hat ganz schön Pep.","Ich wünsche Euch viel Glück, <Name>. Falls Ihr kein Glück haben solltet, tja... dann war es schön, Euch gekannt zu haben."}, {"Beschafft Euch eine Obsidiankraftquelle und bringt sie in die Marschen von Dustwallow zu Tabetha."}},
[1957] = {"Manawogen", {"Wir sind fast fertig, aber dieser letzte Schritt ist der gefährlichste, <Name>.","Ich habe die leere Beschwörerkugel und bin bereit, sie zu ermächtigen. Das machen wir, indem wir die Energie eines Manarisses anzapfen. Ich wirke einen Zauber, der vorübergehend einen Riss auftut, aus dem Kreaturen, Manawogen, herauskommen. Ihr müsst die Manawogen vernichten, damit ihre Macht freigesetzt wird, die ich dann einsammle.","Zerschlagt genug Manawogen, bevor sich der Riss schließt, und ich versiegle ihre gesammelte Energie in Eurer Kugel."}, {"Vernichtet 12 Manawogen innerhalb des Zeitlimits."}},
[1958] = {"Himmlische Macht", nil, nil},
[1959] = {"Meldung bei Anastasia", {"Die Magier von Undercity haben eine magische Störung in der Stadt bemerkt und die Magier der Horde um Untersuchung gebeten.","Anastasia Hartwell kann Euch mehr sagen. Sprecht mit Ihr im Magieviertel in Undercity."}, {"Sprecht mit Anastasia Hartwell."}},
[1960] = {"Untersuchungen im Alchimistenladen", {"Algernons Alchimistenladen im Apothekarium ist das Zentrum einer seltsamen Störung. Unsichtbare, aus reiner Magie bestehende Kreaturen halten sich in dem Laden auf - dafür muss es einen Grund geben, und den müssen wir herausfinden!","Untersucht die Störung und fangt diese Kreaturen. Nehmt Euch von dort hinten eine Truhe mit Kästen und eine Rolle. Benutzt im Laden die Rolle, um die Kreaturen sichtbar zu machen, überwältigt sie mit Euren Zaubern und fangt sie dann mit einem Kasten ein. Kommt mit den gefüllten Kästen, den leeren Kästen und der Anrufung zu mir zurück."}, {"Besorgt eine Inkantation der Manifestation und eine Truhe mit Verwahrungskästen von Anastasia Hartwell. Bringt 3 gefüllte Verwahrungskästen, die Truhe mit Verwahrungskästen sowie die Inkantation der Manifestation zu Anastasia in Undercity."}},
[1961] = {"Materialsammlung", {"Jetzt ist es Zeit, Eure erste Magierrobe anzulegen, <Name>. Sie ist ein Zeichen Eures Ranges und ein Zeugnis für Euren Wert.","Um die Robe zu bekommen, müsst Ihr Manaedelsteine von den Magiern von Dalaran im Silberwald sammeln. Ihre Überzeugungen ähneln den unseren, daher ist es ein wenig geschmacklos, sie zu töten, aber wenn sie keine Kraft haben, uns Widerstand zu leisten, verdienen sie es nicht, zu leben.","Bringt die Edelsteine und das Leinen zu Josef Gregorian in der Schneiderei im Magieviertel."}, {"Bringt 10 Leinentücher und 6 Manaedelsteine aus Dalaran zu Josef Gregorian."}},
[1962] = {"Roben des Zauberfeuers", nil, nil},
[1963] = {"Die Shattered-Hand", {"Die Methoden des Kriegshäuptlings sind zu zimperlich. Er wagt nicht, die Macht der Horde zu entfesseln. Einst waren wir stärker, allesamt mächtige Eroberer. Diesen Stolz müssen wir zurückerlangen, und der Klan der Shattered-Hand wird dies bewerkstelligen.","Wir sammeln Kraft aus den Schatten und schlagen im Verborgenen gegen exponierte Feinde zu. Eine Aufgabe müsst Ihr für mich erledigen, um Euren Wert zu beweisen.","Der Troll Tazan treibt Handel mit den Piraten bei Ratchet. Er hat Informationen, die ich haben muss. Tötet ihn und bringt mir seinen Ranzen."}, {"Tötet Tazan und bringt seinen Ranzen zu Therzok in der Kluft der Schatten in Orgrimmar."}},
[1978] = {"Die Todespirscher", {"Das Syndikat? Darauf wäre ich nie gekommen. Offenbar sind die Lords von Alterac in den Jahren seit ihrem Verrat tief gesunken.","Lord Varimathras wird es mit Freuden hören, und da wir diese Entdeckung weitgehend Euch verdanken, ist es nur recht und billig, dass Ihr ihm die Nachricht überbringt.","Ah, und wie konnte ich das vergessen?","Willkommen bei den Todespirschern."}, {"Bringt Androns Geschäftsbuch zu Varimathras in der Halle der dunklen Fürstin in Undercity."}},
[1998] = {"Fenwick Thatros", {"Lord Varimathras hat mir soeben in einer Botschaft über eine Gefahr für die Verlassenen berichtet, die unverzüglich ausgeschaltet werden muss. Sein Name ist Fenwick Thatros. Lord Varimathras glaubt, dass er einer der Anführer der Untoten sei, die sich im Silberwald angesiedelt haben.","Mir kam sofort Euer Name in den Sinn, <Name>. Ich habe Vertrauen in Eure Fähigkeiten und glaube, Ihr werdet diese Mission fehlerfrei abschließen.","Die letzte Meldung von den Todespirschern im Silberwald deutet darauf hin, dass Thatros nahe der verfallenen Fähre am Lordameresee zu finden ist."}, {"Tötet Fenwick Thatros und bringt seinen Kopf zu Mennet Carkad im Schurkenviertel von Undercity."}},
[1999] = {"Werkzeuge des Handwerks", {"Die Hexer der Kirin Tor bei Ambermill haben wichtige Dokumente eingelagert, die den aktuellen Zustand von Dalaran betreffen. Das wissen wir durch die Aussagen eines unserer Gefangenen. Lord Varimathras hat die Aufgabe, diese zu beschaffen, den Todespirschern übertragen.","Das ist eine Arbeit für Euch, <Name>. Ihr braucht allerdings etwas Ausrüstung und Kenntnisse im Schlossknacken. Sprecht mit Estelle Gendry, unserer Lageristin, und bittet sie um ein Werkzeugset, bevor Ihr aufbrecht."}, {"Sucht den Statusbericht von Dalaran und bringt ihn zu Mennet Carkad im Schurkenviertel von Undercity."}},
[2038] = {"Bingles' verschwundene Werkzeuge", {"Als wäre es nicht schlimm genug gewesen, in den Loch zu stürzen, haben mich auch noch Troggs von der nahe gelegenen Insel angegriffen und mir mein Werkzeug und meinen speziellen Sprengstoff \"Superspreng\" aus der Blastenheimer-Sprengstoffstube gestohlen. Ohne Gyrokopter und Sprengstoff hat die Bewegung keine Chance!","Beschafft mir meine Werkzeuge wieder und findet mein Superspreng aus der Sprengstoffstube, <Name>. Für Gnomeregan!"}, {"Sucht Bingles' Werkzeuge und bringt sie ihm zurück. Es sind:","Bingles' Schraubenschlüssel, Bingles' Schraubendreher, Bingles' Hammer und Bingles' Sprengstofftube."}},
[2039] = {"Sucht Bingles", {"Wir haben den Kontakt mit Bingles verloren! Er wurde zu einer Aufklärungsmission über Gnomeregan geschickt und hat sich seit mehr als einer Woche nicht mehr gemeldet. Ich weiß, er hat sich selbst in die Luft gejagt.","Zuletzt sah man ihn über dem Loch fliegen. Vielleicht könnt Ihr dies untersuchen, <Name>?"}, {"Sucht Bingles Blastenheimer am Loch Modan."}},
[2040] = {"Unterirdischer Angriff", {"Gnomeregan ist diesen elendigen Troggs in die Hände gefallen! Die Situation ist ernst, aber vielleicht könnt Ihr helfen, <Name>.","Tief in den Todesminen gibt es einen funktionierenden Goblinschredder. Sucht diesen Schredder und bringt die intakte Energieversorgung mit. Mit der Energieversorgung des Schredders können wir unseren gyrobohrmatischen Exkavationatoren die erforderliche Energie zuführen, um durch die felsigen, unterirdischen Grenzen von Gnomeregan zu brechen und einem Gnomenangriff den Weg zu ebnen!"}, {"Holt das Gnoam-Sprecklesprocket aus den Todesminen und bringt es Shoni der Schtillen in Stormwind."}},
[2041] = {"Sprecht mit Shoni", {"Vielleicht... Vielleicht könnt Ihr uns im Kampf um Gnomeregan unterstützen.","In Stormwind findet Ihr den Kommandant unserer Untergrund-Kampftruppe, Shoni die Schtille. Shoni braucht Hilfe für ihre gyrobohrmatischen Exkavationatoren.","Ihr findet sie wahrscheinlich bei den Zwergen in Stormwind.","Viel Glück, <Name>."}, {"Sprecht mit Shoni der Schtillen in Stormwind."}},
[2078] = {"Gyromasts Rache", {"Landratte, Ihr müsst meinen Ersten Maat finden.","Nehmt diesen Schlüssel, steckt ihn in sein' Gulliver und schafft ihn her, dasser seim Käpt’n dient.","Diese Murlocs und ihr Häuptling werden nich so froh sein, wenn der Erste Maat sie zu fassen kriegt!"}, {"Sucht Gelkaks Ersten Maat, den Dreschonator 4100, und führt ihn zu Gelkak zurück."}},
[2098] = {"Gyromasts Rettung", {"Das Piratenleben ist eines der Schwersten, Landratte. Es dreht sich nicht nur um Plündern und Saufen! Wehe, mein Schiff sank vor der Küste! Schuld daran sind diese dreisten Dreschadons.","Ich muss mein' Ersten Maat funktionstüchtig wiederham!","Als ich an Land gespült wurde, zerbrach der Schlüssel für meinen Ersten Maat an den Felsen und wurde von den Biesterchen von Dunkelküste geraubt. Aus den Augenwinkeln sah ich tobende Kriecher, riesige Waldschreiter und Murlocs, die das Wrack durchstöberten..."}, {"Sucht die drei Stücke von Gelkaks Schlüssel und bringt sie Gelkak Gyromast."}},
[2118] = {"Verseuchtes Land", {"Eine Erkrankung breitet sich in Dunkelküste aus und befällt Körper und Geist aller, die damit in Kontakt kommen.","Die Distelbären wurden am schwersten von der Seuche getroffen. Die einst edlen Tiere sind jetzt ein Instrument der Zerstörung - tollwütig und rasend. Ich habe möglicherweise ein Heilmittel für dieses Leiden.","Geht mit dieser Falle in den Wald und legt sie auf den Boden. Jeder tollwütige Distelbär, der das Licht kreuzt, wird für kurze Zeit friedfertig. Wenn der Bär erst einmal friedfertig ist, wird er Euch folgen. Führt ihn hierher, <Name>."}, {"Fangt einen lebenden tollwütigen Distelbären und bringt ihn zu Tharnariun."}},
[2138] = {"Ausrottung der Infizierten", {"Wie ich befürchtete, das Heilmittel wirkt nicht.","Es stimmt mich traurig, Geschöpfe der Natur zum Tode zu verurteilen, aber die kranken und besudelten Distelbären müssen ausgerottet werden.","Kehrt in den Wald zurück und vernichtet 20 tollwütige Distelbären, <Name>. Das wird die Seuche sicher nicht stoppen, aber den Schaden, den die Tiere unserem Wald und seinen Bewohnern zufügen, zumindest für kurze Zeit verringern."}, {"Befreit den Wald von 20 tollwütigen Distelbären und kehrt dann zu Tharnariun Treetender in Dunkelküste zurück."}},
[2139] = {"Tharnariuns Hoffnung", {"Das Überleben unseres Volkes und unserer Länder ist mit bedauerlichen Kosten verbunden, <Name>.","Ihr habt das Blut der Diener der Natur vergossen; viele kranke Tiere habt Ihr erledigt. Das Volk von Auberdine steht in Eurer Schuld, denn die Qualen des Waldes sind gelindert - wenn auch nur vorübergehend.","Ich habe eine letzte Aufgabe für Euch, doch seid gewarnt, Ihr werdet wieder töten müssen. Dies wird hoffentlich das letzte Mal sein, dass ich Euch rufen muss, <Name>.","In einer Höhle im Nordosten, nahe Bashal'Aran, haust die Höhlenmutter. Tötet sie."}, {"Sucht und tötet die Höhlenmutter."}},
[2158] = {"Ruhe und Entspannung", {"Jeder Abenteurer sollte ausruhen, wenn ihn die Erschöpfung überkommt - und es gibt keinen besseren Platz für Ruhe und Entspannung als das Gasthaus Zur Höhle des Löwen!","Gastwirt Farley, der Inhaber der 'Höhle des Löwen', ist mein bester Freund. Wenn Ihr ihm sagt, dass ich Euch geschickt habe, dann gewährt er Euch vielleicht einen Spezialrabatt auf Essen und Trinken.","Das Gasthaus Zur Höhle des Löwen findet Ihr, wenn Ihr auf der Straße nach Süden reist - Ihr könnt es nicht verfehlen!"}, {"Sprecht im Gasthaus Zur Höhle des Löwen mit Gastwirt Farley."}},
[2159] = {"Dolanaar-Lieferung", {"Seid mir gegrüßt, <Klasse>. Könnt Ihr mir helfen? Ich habe ein Päckchen Kräuter, das ich in die Stadt Dolanaar liefern muss. Aber ich habe noch etwas mit den Druiden von Shadowglen zu regeln und kann nicht weg.","Könntet Ihr das Päckchen für mich zustellen? Es muss zu Gastwirt Keldamyr im Gasthaus von Dolanaar. Das liegt an der Straße nach Süden."}, {"Bringt die Dolanaar-Lieferung zu Gastwirt Keldamyr."}},
[2160] = {"Vorräte für Tannok", {"He! Ihr seht wie ein beherzter Abenteurer aus. Wenn Ihr vorhabt, den Pass zu überqueren, könntet Ihr da wohl ein Päckchen ins Gasthaus von Kharanos bringen?","Ihr hattet doch vor, im Gasthaus abzusteigen, nicht? Wenn Ihr den Pass bezwungen habt, werdet Ihr Euch dort ganz bestimmt etwas ausruhen wollen.","Wie auch immer, bringt das zu Tannok Frosthammer, dem Gehilfen des Gastwirts. Ich selbst kann nicht über den Pass und es dauert Tage, bis wieder eine Gebirgsjägereskorte vorbeikommt!"}, {"Bringt die Kiste mit Gasthausvorräten zu Tannok Frosthammer in Kharanos."}},
[2161] = {"Die Bürde eines Peons", {"Entschuldigung? Könntet Ihr mir helfen?","Ich habe hier eine Ladung Essen. Ich bin die Straße entlanggelaufen und habe Skorpiden und Spinnen und Schlimmerem getrotzt! Ich habe das Essen ins Tal der Prüfungen gebracht, weil ich dachte, sie könnten es brauchen und weil ich gern behilflich bin. Aber sie brauchen kein Essen! Jetzt muss ich es nach Klingenhügel zurückbringen und fürchte mich vor den ganzen Bestien unterwegs...","Könnt Ihr es für mich hinbringen? Ich bin nur ein Peon, Ihr aber seid <ein Held/eine Heldin>. Ihr fürchtet nichts!","Bringt es zu Gastwirt Grosk im Gasthaus von Klingenhügel. Danke!"}, {"Bringt Ukors Bürde zu Gastwirt Grosk in Klingenhügel."}},
[2178] = {"Schreitereintopf", {"Riecht Ihr dieses köstliche Aroma? Das ist mein Geheimrezept, <Name> - frischer Schreitereintopf. Köstlich!","Vielleicht möchtet Ihr das Rezept ja wissen?","Bevor ich Euch das Rezept verrate, müsst Ihr beweisen, dass Ihr den Wildtieren gewachsen seid.","Ich erwarte nicht, dass Ihr loszieht und eines zähmt und darauf zu mir zurückgeritten kommt, aber wenn Ihr einige Wildtiere töten könnt und mir etwas von ihrem saftigen Fleisch bringt, verrate ich Euch vielleicht das Schreiterrezept."}, {"Bringt 5 Stücke Schreiterfleisch zu Alanndarian Nightsonng in Auberdine."}},
[2198] = {"Die zerrissene Halskette", {"Ihr habt eine befleckte Halskette gefunden, doch unter der Stumpfheit des Metalls lässt sich noch die feine und magische Handarbeit erkennen. Die Halskette ist beschädigt, in ihrer Fassung scheinen drei Edelsteine zu fehlen. Auf der Unterseite der Halskette kann man vage den Stempel des Stadtsiegels von Ironforge erkennen. Unter dem Siegel befinden sich drei winzige eingravierte Buchstaben: \"TdK\".","Wenn Ihr etwas über die Halskette in Erfahrung bringen wollt, müsst Ihr herausfinden, woher sie stammt. Vielleicht sind das Siegel und die Initialen ein Anfang ..."}, {"Sucht nach dem Erschaffer der zerrissenen Halskette, um etwas über ihren möglichen Wert zu erfahren."}},
[2199] = {"Lehren haben ihren Preis", {"Ich genieße als Juwelier hier in Ironforge ein hohes Ansehen! Oder sagen wir besser: Ich genoss es einmal. Dank der gemeinen Tricks eines Konkurrenten mache ich gerade eine schwere Zeit durch. In dieser Branche bedeutet ein schlechter Ruf das finanzielle Desaster. Und so muss ich feststellen, dass mein guter, wenn auch momentan leicht beschmutzter Name keinen roten Heller mehr wert ist.","Ich möchte, dass Ihr mir fünf Silberbarren beschafft, die ich für einen Auftrag brauche - den ersten seit einer Ewigkeit.","Bringt mir die fünf Silberbarren, dann beantworte ich Eure Fragen über die Halskette."}, {"Bringt fünf Silberbarren zu Talvash del Kissel in Ironforge."}},
[2200] = {"Rückkehr nach Uldaman", {"Einer meiner Konkurrenten brachte das Gerücht in Umlauf, meine Halskette habe den Paladin verflucht. Und natürlich kauft einem Juwelier niemand verfluchte Gegenstände ab.","Ich schlag Euch etwas vor, <Name>: Ihr findet die Edelsteine und ich repariere die Halskette - aber nur, wenn Ihr herumerzählt, dass ich KEINE verfluchten Gegenstände herstelle!","Fangt am besten an, in Uldaman nach Hinweisen zu suchen; vielleicht liegt der Paladin dort begraben, von ihm wissen wir, dass er die Kette als Letzter hatte! Wenn Ihr die Edelsteine gefunden habt, solltet Ihr dies hier benutzen; dadurch könnt Ihr mit mir in Ironforge Kontakt aufnehmen."}, {"Sucht in Uldaman nach Hinweisen auf den momentanen Zustand von Talvashs Halskette. Der getötete Paladin, den Talvash erwähnte, hatte die Kette zuletzt."}},
[2201] = {"Suche nach den Edelsteinen", {"Das Tagebuch des Paladins liefert Euch die vielleicht einzigen Hinweise darauf, wo sich die Edelsteine aus der Halskette befinden könnten. Wenn der Paladin alles korrekt aufgezeichnet hat, dann müsst Ihr Euch um die Zwerge der Schattenschmiede und die Troggs kümmern, die um die Vorherrschaft über die Grabungsstätte konkurrieren.","Mit diesem Wissen bewaffnet, könnt Ihr Eure Suche nach den Edelsteinen jetzt ernsthaft beginnen. Sobald Ihr Euch alle Edelsteine angeeignet habt, solltet Ihr gemäß seinen Anweisungen Talvash in Ironforge mit Hilfe der Wahrsagephiole informieren"}, {"Findet den Rubin, den Saphir und den Topas, die in ganz Uldaman verstreut sind. Wenn Ihr sie habt, wendet Euch aus der Ferne an Talvash del Kissel, indem Ihr die Wahrsagephiole nutzt, die er Euch zuvor gegeben hat.","Aus dem Tagebuch, wisst Ihr...","* Der Rubin wurde in einer Bastion der Zwerge der Schattenschmiede versteckt.","* Der Topas steckt in einer Urne in einem der Trogg-Gebiete, in der Nähe einiger Zwerge der Allianz.","* Der Saphir wurde von Grimlok, dem Anführer der Troggs, mitgenommen."}},
[2202] = {"Reagenzsuche in Uldaman", {"Ich habe von einer neu entdeckten Pflanze gehört, den magenta Funguskappen. Sie wachsen in Büscheln tief drinnen in der Uldaman-Grabungsstätte; vielleicht kann man sie auch anderswo finden, aber bisher ist das der einzige mir bekannte Ort, an dem sie gedeihen. Ich möchte ihren potenziellen Nutzen für die Alchimie erforschen und dafür brauche ich Euch.","Findet die magenta Funguskappen-Büschel und bringt mir ein Dutzend Kappen. Aber seid gewarnt - die Pflanzen stoßen vielleicht Giftsporen aus, wenn man sie anstößt.","Wenn Ihr das für mich tut, dann braue ich Euch einen Schwung von meinem berühmten Regenerierungselixier!"}, {"Bringt 12 magenta Funguskappen nach Kargath zu Jarkal Mossmeld."}},
[2203] = {"Reagenzien-Suche im Ödland II", {"Ihr seid mir eine große Hilfe gewesen, <Klasse>. Ich würde Euch ja das Rezept des Gebräus geben, das Ihr vorhin gekostet habt... doch zuvor muss ich Euch um einen gefährlichen Gefallen bitten. Hört meine Bitte.","Diese Schließkassette enthält drei leere thaumaturgische Gefäße; sie sind mit einer Aura der Einstimmung magieerfüllt, die einem versengten Behütergroßdrachen das Blut aussaugt. Wenn Ihr sie gegen die Bestie benutzt, wird sie höchst erbost reagieren, also seid gewarnt. Bringt sie mir, wenn Ihr alle drei gefüllt habt.","Gelingt Euch das, gehört das Rezept Euch."}, {"Benutzt die leeren thaumaturgischen Gefäße bei versengten Wächtergroßdrachen im Ödland. Wenn Ihr sie gefüllt habt, bringt sie zu Jarkal Mossmeld in Kargath."}},
[2204] = {"Restaurierung der Halskette", {"Ihr müsst etwas \"Energie\" für die Halskette finden, und wenn ich es mir recht überlege, dann ist Uldaman sogar der beste Ort dafür. Ich vermute mal, da drinnen laufen eine Menge Konstrukte rum, hab ich Recht?Gut, dann sucht Euch das größte und böseste Konstrukt, das Ihr finden könnt, macht es fertig und schnappt Euch seine Kraftquelle! Ich bin mir ziemlich sicher, dass ich die Kraftquelle mit der Halskette verbinden kann.","Ohne die Kraftquelle wird es unmöglich sein, die Halskette zu reparieren! Kommt wieder zu mir nach Ironforge zurück, wenn Ihr die Kraftquelle habt, dann repariere ich die Halskette für Euch."}, {"Besorgt Euch eine Kraftquelle vom mächtigsten Konstrukt, das Ihr in Uldaman finden könnt, und liefert sie bei Talvash del Kissel in Ironforge ab."}},
[2205] = {"Aufsuchen des SI: 7", {"Das Abzeichen? Oh, das ist gar nichts, Dumpfbacke - jedenfalls nichts, mit dem Ihr fertig werden könntet. Wenn ich Euch so ansehe, würde ich sagen, dass Mathias Euch wahrscheinlich statt für einen anständigen Schurken für einen Käselieferanten halten wird. Aber wie dem auch sei, ich habe eine Aufgabe zugewiesen bekommen und ich habe versprochen, sie zu erledigen.","Da wir gerade davon sprechen, bringt Mathias dieses Päckchen, dann gibt er Euch vielleicht eine kleine Aufgabe oder auch zwei.","Zischt ab."}, {"Liefert Keryn Sylvius' Päckchen an Meister Mathias Shaw in der Kaserne von Stormwind."}},
[2206] = {"Schneller Eingriff", {"Unsere Agenten vor Ort melden starke Aktivitäten der Defias südlich von Goldhain. Die Berichte deuten an, dass die Defias Jerods Anlegestelle erobert haben und ihn nun benutzen, um große Mengen Hehlerware vom Rotkammgebirge nach Westfall zu schmuggeln.","Ihr müsst das Dockhaus bespitzeln und mir den Lieferzeitplan beschaffen, <Name>. Ihr findet ihn wahrscheinlich tief in den Taschen des Dockmeisters.","... und, <Name>, solltet Ihr entdeckt werden, wird der SI:7 strikt leugnen, dass er Euch kennt."}, {"Sucht den Dockmeister der Defias und beschafft den Lieferzeitplan für Meister Mathias Shaw."}},
[2218] = {"Straße der Rettung", {"Eine weitere verirrte Seele, die nach Führung sucht, hm? Ich nehme an, Ihr wollt ein Ziel finden - Euer Ziel. Das habe ich schon tausend Mal gesehen, ein Held, der Arbeit sucht.","Aber heute ist Euer Glückstag. Hogral hat ein Heilmittel für Eure Leiden.","Ihr macht Folgendes: Schlagt diesen Weg nach Ironforge ein und begebt Euch zum düsteren Viertel. Wenn Ihr dort seid, sucht die Diebesgilde und sprecht mit Hulfdan Blackbeard. Er wird Euch den Weg zur Rettung weisen."}, {"Sprecht mit Hulfdan Blackbeard in Ironforge."}},
[2238] = {"Einfache List", {"Ihr seht's ja, der König ist dabei, im Steinbruch nach Erz und Edelsteinen graben zu lassen. Mehr Reichtümer, als ein Zwerg normalerweise in zehn Leben zu sehen bekommen würde.","Der Verborgene Kreis hat auch Werte, <Name>. Wertsachen! Versteht Ihr, was ich meine?","Wir finden, der König sollte uns was von der Beute abgeben, aber der alte Stinkebart soll nicht erfahren, dass er was abgibt - ich hoffe, Ihr könnt mir folgen.","Ihr müsst mit Onin MacHammar drüben bei Gnomeregan reden und 'nen Bericht zurückbringen. Der listige Zwerg hat sich bei den Lepragnomen versteckt. Seid vorsichtig."}, {"Sucht Onin MacHammar bei Gnomeregan."}},
[2239] = {"Onins Bericht", {"Der Steinbruch? Ja, ich sehe fast jeden Tag diese monströsen Dampfpanzer hier vorbeirollen.","Meine Kontakte im Osten sagen mir, dass der Panzer in Ironforge startet, im Gol'Bolar-Steinbruch seine Ladung abholt, zum Auftanken in Kharanos hält und dann aus einem unerfindlichen Grund den langen Weg um Gnomeregan herum fährt, ehe er seine Ladung in Ironforge ablädt.","Es steht alles in dem Bericht, <Volk>. Bringt ihn zu Hulfdan, damit er den nächsten Schritt planen kann."}, {"Bringt Onins Bericht zu Hulfdan in Ironforge."}},
[2240] = {"Die geheime Kammer", {"Jetzt zur anstehenden Aufgabe:","Es gibt eine geheime Kammer, die Kammer von Khaz'mul, die erforscht werden muss. Das Problem dabei ist, dass die Kammer hinter einer riesigen verschlossenen Tür liegt! Glücklicherweise weiß ich, wie man sie öffnet.","Ich habe alles in dem Tagebuch hier auf dem Tisch beschrieben. Meine Leute und ich sitzen hier zurzeit fest ... also müsst Ihr die Kammer von Khaz'mul für uns finden, <Name>.","Lest mein Tagebuch, erforscht die Kammer und meldet Euch dann bei Ausgrabungsleiter Stormpike in der Forscherliga in Ironforge."}, {"Lest Baelogs Tagebuch, erforscht die geheime Kammer und erstattet dann Ausgrabungsleiter Stormpike Bericht."}},
[2241] = {"Der Apfel fällt", {"Sie sagen, ich bin 'liebeskrank', was immer das heißen mag.","Klar, ich denke ununterbrochen an Syurna. Klar, mein Haus ist voll von Bildern und Zeichnungen von Syurna. Klar, es kommt vor, dass ich tagelang nicht esse, schlafe oder trinke, während ich über die Liebe lamentiere, die uns nicht zuteil wurde. Ist das so falsch?","Jetzt will sie mich nicht sehen! MICH! Ich habe versucht, ihr Nachrichten zu schicken, aber sie redet mit keinem, es sei denn, er wäre ein Schurke.","Könntet Ihr diese Blume zu ihr bringen? Und vergesst nicht, ihr zu sagen, dass sie von Jannok ist."}, {"Bringt Jannoks Rose zu Syurna in Darnassus."}},
[2242] = {"Das Schicksal ruft", {"Wir glauben, dass es ein Wesen gibt, das uns verstehen helfen könnte, was mit den Wäldern von Teldrassil geschieht. Leider hatte es verheerende Folgen, als wir versuchten, dieses Wesen zur Mitarbeit zu überreden.","Er versteckt sich auf einem Ast am Rand der Lichtung des Orakels. Alle, die sich dem Satyr bisher genähert haben, sei es mit Gewalt oder Diplomatie, fanden ein böses Ende. Vielleicht ist Verstohlenheit die Lösung, <Name>.","Findet heraus, was Sethir der Uralte in seinen Rucksäcken hat und berichtet mir davon."}, {"Sucht Sethir den Uralten und bringt alle Hinweise, die Ihr finden könnt, zu Syurna."}},
[2258] = {"Reagenzien-Suche im Ödland", {"<Name>, ich könnte Eure Hilfe gebrauchen, wenn Ihr Euch dazu in der Lage fühlt. Ich bin ein angesehener Alchimist und benötige Eure Unterstützung, um einige Reagenzien zu beschaffen, die im Ödland zu finden sind.","Ich brauche folgende Gegenstände, um meine Vorräte aufzufüllen: 5 Bussard-Muskelmägen, 10 Klippenkojotenfangzähne und 5 Felselementarsplitter. Natürlich könnt Ihr diese Reagenzien direkt von den lieben Tieren selbst bekommen; aber wenn Ihr keine Kämpfernatur seid, dann sucht Euch besser Freunde, die kämpfen können.","Bringt mir diese Gegenstände, dann springen ein paar Münzen für Euch dabei raus. Was sagt Ihr?"}, {"Bringt Jarkal Mossmeld in Kargath im Ödland 5 Bussard-Mägen, 10 Klippenkojoten-Fangzähne und 5 Felselementarsplitter."}},
[2259] = {"Erion Shadewhisper", {"Keine Bange, <Name>, ich schicke Euch nicht zu Syurna. Erion verlangt nach Eurer Anwesenheit in der Enklave des Cenarius. Beeilung!"}, {"Kontaktiert Erion Shadewhisper in Darnassus."}},
[2260] = {"Erions Befehl", {"Die Kunde von Euren Taten ist in aller Munde, <Name>. Ihr seid so berühmt, dass der Geheimdienst von Stormwind nach Euch persönlich gefragt hat.","Die Reise ist anstrengend und nicht ungefährlich, aber die Gelegenheit einer Ausbildung beim SI:7 sollte man nicht in den Wind schlagen.","Eine neue Welt erwartet Euch, <Name>. Begebt Euch in die Altstadt von Stormwind und sprecht mit Renzik \"Das Messer\" im Hauptquartier des SI:7."}, {"Reist zur Stadt Stormwind und meldet Euch bei Renzik \"Das Messer\"."}},
[2278] = {"Die Platinscheiben", {"Die Kernstücke dieser Schatzkammer sind vier riesige Platinscheiben, von denen jede mit zahlreichen Siegelzeichen unbekannter Herkunft bedeckt ist. Als Ihr sie berührt, beginnt Eure Hand zu kribbeln. Unmittelbar, nachdem Ihr die Scheiben berührt habt, erscheint das Gesicht eines großen Steinbeobachters.","\"Willkommen. Ich bin ein Hüter des Wissens. Die Schöpfungssynthese der Irdenen wurde hier in den Scheiben von Norgannon für diese Welt aufgezeichnet und aufbewahrt.\"","Ihr habt eine Fülle an bislang unbekanntem Wissen über die Welt entdeckt!"}, {"Sprecht mit dem Steinbehüter und findet heraus, welche uralten Lehren er aufbewahrt. Sobald Ihr alles erfahren habt, was er weiß, aktiviert die Scheiben von Norgannon."}},
[2279] = {"Die Platinscheiben", {"Als Ihr die Scheiben von Norgannon ein weiteres Mal berührt, kribbelt Eure Hand erneut. Jetzt aber ist aus dem Inneren der Scheiben ein seltsames, knirschendes Geräusch zu hören.","Plötzlich ist Euer Rucksack schwerer als noch eine Sekunde vorher. In seinem Inneren findet Ihr eine Miniaturausgabe der Scheiben!","Zweifellos wäre ein solcher Gegenstand von unschätzbarem Wert für jeden, der sich für die Frühgeschichte von Azeroth interessiert. Die Forscherliga in Ironforge beispielsweise ist eine solche Gruppe und sie ist in der ganzen Allianz bekannt - vielleicht können die etwas mit diesem Fund anfangen."}, {"Bringt die Miniaturausgabe der Scheiben von Norgannon zur Forscherliga nach Ironforge."}},
[2280] = {"Die Platinscheiben", {"Als Ihr die Scheiben von Norgannon ein weiteres Mal berührt, kribbelt Eure Hand erneut. Jetzt aber ist aus dem Inneren der Scheiben ein seltsames, knirschendes Geräusch zu hören.","Plötzlich ist Euer Rucksack schwerer als noch eine Sekunde vorher. In seinem Inneren findet Ihr eine Miniaturausgabe der Scheiben!","Zweifellos wäre ein solcher Gegenstand von unschätzbarem Wert für jeden, der sich für die Frühgeschichte von Azeroth interessiert. Die Weisen von Thunder Bluff beispielsweise sind solche Historiker und sie sind in der ganzen Horde wohl bekannt - vielleicht können die etwas mit diesem Fund anfangen."}, {"Bringt die Miniaturausgabe der Scheiben von Norgannon zu einem der Weisen von Thunder Bluff."}},
[2281] = {"Rendezvous im Rotkammgebirge", {"Euch jetzt in den Einsatz zu schicken wäre Selbstmord. Renzik braucht nicht noch einen toten Schurken. Aber keine Sorge, <Name>... Renzik wird Euch schon Beine machen.","Geht ins Rotkammgebirge und sprecht mit einem Mitglied des SI:7 namens Lucius. Ihr werdet ihn auf den Docks von Seenhain finden, wo er mit den anderen halbseidenen Taugenichtsen herumhängt."}, {"Geht nach Seenhain im Rotkammgebirge und sprecht mit Lucius."}},
[2282] = {"Althers Mühle", {"Wir bilden hier schon seit Jahren Diebe aus! Schließlich wird nicht jeder Langfinger mit der Anmut und Finesse des alten Lucius geboren.","Oben an der Straße findet Ihr Althers Mühle. Ich habe dort oben eine Truhe mit wichtigem Inhalt. Holt sie für mich! Bringt mir diese Marke und"}, {"Öffnet Lucius' Schließkassette, nehmt die Marke der Diebeskunst heraus und bringt sie zu Lucius nach Seenhain."}},
[2283] = {"Wiederbeschaffung der Halskette", {"Wir haben Arbeit für Euch, falls Ihr den Mut habt, Euch daranzuwagen.","Tief im Ödland befindet sich Uldaman, eine Grabungsstätte. Wir haben eine Geschichte gehört von so einem dummen Paladin, der kürzlich dort gestorben ist. Das würde uns ja alles nicht interessieren, aber er hatte eine sehr wertvolle Halskette bei sich. Angeblich sind drei große Edelsteine dabei: ein Rubin, ein Saphir und ein Topas.","Kurz gesagt: Wir wollen die Kette haben und wir bezahlen GUTES SILBER, um sie zu kriegen. Vielleicht ist sie beschädigt, aber das tut nichts zur Sache. Wir reparieren sie, wenn Kratzer daran sein sollten."}, {"Sucht in der Grabungsstätte von Uldaman nach einer wertvollen Halskette und bringt sie nach Orgrimmar zu Dran Droffers. Die Halskette ist vielleicht beschädigt."}},
[2284] = {"Wiederbeschaffung der Halskette, Teil 2", {"Genauso sicher, wie Malton ein Idiot ist, sind diese Edelsteine noch irgendwo in Uldaman. Ihr werdet wohl dorthin zurückgehen müssen und nach ihnen suchen.","Ich hab's ja schon mal gesagt: Wenn unsere Informationen stimmen, dann gehören zu der Halskette ein Rubin, ein Saphir und ein Topas. Findet sie und fügt sie in die Halskette ein.","Aber wo die Edelsteine sind - wer weiß das schon? Vielleicht habt Ihr ja Glück und findet die Leiche dieses Paladins. Wenn er noch nicht völlig ausgeplündert worden ist, dann hat er sie ja vielleicht sogar bei sich."}, {"Sucht in den Tiefen von Uldaman nach einem Hinweis auf den Verbleib der Edelsteine."}},
[2298] = {"Königliche Erpressung", {"Wir war'n also grade dabei, Phase III zu planen, da platzen 'n paar von Magnis Holzköpfen hier rein, schwafeln was von 'ner Art Razzia und verhaften ein paar vom Verborgenen Kreis.","So springt keiner mit dem alten Blackbeard um! Die Diskussion wurde immer hitziger und irgendwann fiel Euer Name!","Mein Rat? Nehmt den ersten Greif aus Ironforge raus und geht nach Stormwind. Geht in die Altstadt und sprecht dort mit einem alten Freund von mir, der Renzik \"Das Messer\" genannt wird."}, {"Reist nach Stormwind und sucht Renzik \"Das Messer\" auf."}},
[2299] = {"Auf Hulfdan!", {"Wo seid Ihr gewesen, <Name>? Hulfdan sucht schon überall nach Euch. Wir dachten schon, der König hätte Euch in Eisen legen lassen.","Geht so schnell wie möglich nach Ironforge und sprecht mit Hulfdan!"}, {"Reist nach Ironforge und sprecht mit Hulfdan Blackbeard."}},
[2300] = {"Der SI:7", {"Renzik hat nach Euch gesucht, Dumpfbacke.","Ich an Eurer Stelle würde Mathias' Stellvertreter nicht warten lassen."}, {"Reist nach Stormwind und sucht Renzik \"Das Messer\" auf."}},
[2318] = {"Übersetzung des Tagebuchs", {"Ihr befindet Euch in einer Art Sackgasse, denn Ihr wisst, dass Ihr das Buch erst in eine Sprache übersetzen lassen müsst, die Ihr versteht. Es gibt etliche Leute auf der Welt, die das machen könnten - der nächste Ort, wo sich vielleicht jemand finden ließe, der dazu in der Lage ist, wäre vermutlich der Kargath-Außenposten im Ödland. Vielleicht wäre das ein guter Ort, um anzufangen."}, {"Sucht jemanden, der das Tagebuch des Paladins übersetzen kann. Der nächstgelegene Ort, wo Ihr so jemanden finden könntet, ist Kargath im Ödland."}},
[2338] = {"Übersetzung des Tagebuchs", {"Droffers und Sohn Bergungen' sind nicht schlau genug, die Magie der Halskette wiederherzustellen, aber ich kann es. Ich muss nur die Halskette studieren, damit ich lerne, wie ich selbst solche Ketten herstellen kann. Und dazu benötige ich Eure Halskette. Leiht sie mir, und im Gegenzug für die Zeit, in der ich die Kette studieren kann, werde ich das Tagebuch für Euch übersetzen. Ich sollte genug Zeit haben, alles Nötige zu erfahren, während Ihr nach den Edelsteinen sucht.","Es ist ein einfacher Handel und niemand wird etwas davon erfahren. Also, was sagt Ihr?"}, {"Leiht Jarkal die Halskette. Im Gegenzug wird er das Tagebuch für Euch übersetzen."}},
[2339] = {"Findet die Edelsteine und die Kraftquelle", {"Hier ist das Tagebuch, übersetzt, sodass Ihr es lesen könnt. Kurz gesagt steht darin, dass Zwerge den Rubin versteckt haben, Troggs versteckten den Topas und ein Trogg namens Grimlok hat den Saphir.","Es ist so, Ihr braucht eine Kraftquelle, um die Halskette wieder funktionstüchtig zu bekommen. Dran Droffers weiß das nicht. Um eine Kraftquelle zu finden, würde ich Folgendes tun: Begebt Euch so tief wie möglich nach Uldaman hinein und tötet das stärkste Konstrukt, das Ihr finden könnt. Ich zweifle nicht daran, dass das stärkste Konstrukt dort auch dasjenige ist, das die Kraftquelle hat.","Wenn Ihr alles zusammenhabt, dann kommt wieder hierher!"}, {"Beschafft in Uldaman alle drei Edelsteine sowie eine Kraftquelle für die Halskette und bringt sie anschließend zu Jarkal Mossmeld nach Kargath. Jarkal glaubt, dass sich eine Kraftquelle vielleicht im stärksten Konstrukt in Uldaman findet.","Aus dem Tagebuch, wisst Ihr...","* Der Rubin wurde in einer Bastion der Zwerge der Schattenschmiede versteckt.","* Der Topas steckt in einer Urne in einem der Trogg-Gebiete, in der Nähe einiger Zwerge der Allianz.","* Der Saphir wurde von Grimlok, dem Anführer der Troggs, mitgenommen."}},
[2340] = {"Ablieferung der Edelsteine", {"Jede Halskettenfassung und jeder Edelstein wird funktionieren, solange sie aus demselben Material bestehen. Doch es ist die Kraftquelle, die der Halskette die Magie wieder zurückgeben wird. Hoffe ich zumindest.","Ich werde Euch gern die Edelsteine zur Verfügung stellen, die Ihr für Eure Version der Halskette benötigt, wenn die Kraftquelle einwandfrei funktioniert.","Begebt Euch nach Orgrimmar und haltet Euren Teil der Abmachung mit 'Droffers und Sohn Bergungen' ein. Haltet lediglich den Mund über unser Geschäft, und wenn Ihr zurückkommt, habe ich vielleicht schon eine wundervolle Überraschung für Euch."}, {"Liefert die Halskette und die geborgenen Edelsteine bei Dran Droffers in Orgrimmar ab."}},
[2341] = {"Wiederbeschaffung der Halskette, Teil 3", {"Als Dran und Malton Droffers anfangen, sich um ihr neues Bergegut zu zanken, ist die Hälfte des Geschäftes bereits erledigt. Jetzt steht noch eine Reise ins Ödland an, um dort die Sache zum Abschluss zu bringen, denn Jarkal Mossmeld aus Kargath hat eine besondere Belohnung für Euch in petto - oder vielleicht auch nicht."}, {"Besucht Jarkal Mossmeld in Kargath, um festzustellen, ob er Erfolg gehabt hat."}},
[2342] = {"Wiederbeschaffte Schätze", {"Die Fledermausgehege mit diesem Besen auszukehren, das ist keine Aufgabe, die eines Garretts würdig wäre! Aber ich habe mit diesem verfluchten Job ein wenig Geld verdient und ich bin bereit, es für Euch auszugeben, wenn ich damit nur meine Situation verbessern kann.","Die Lieblingswaffe der Familie Garrett liegt weggeschlossen in unserer Truhe in der südlichen Bankenhalle von Uldaman. Das rührt noch von den Zeiten her, da wir Garretts dort als Buddler schufteten. Ohne sie werde ich für alle Zeiten hier festsitzen und im wahrsten Sinne des Wortes diesen höllischen Fledermäusen hinterherkehren.","Beschafft den Schatz meiner Familie wieder und bringt ihn mir! Helft mir dabei, mein Geburtsrecht einzufordern!"}, {"Holt Patrick Garretts Familienschatz aus der Truhe der Familie in der südlichen Bankenhalle von Uldaman und bringt diesen zu ihm nach Undercity."}},
[2358] = {"Hörner von Nez'ra", {"Vor vielen Jahren wurden die Hörner von Nez'ra aus dem Rabenholdtanwesen gestohlen. Über Ozeane und Kontinente hinweg habe ich nach ihnen gesucht, nur um schließlich zu erfahren, dass diese unbedeutenden Bestien sie die ganze Zeit über im Besitz hatten.","Erschlagt jeden Schwarzfelsorc, auf den Ihr trefft, bis Ihr die Hörner von Nez'ra wiedererlangt habt. Solltet Ihr die Hörner in Euren Besitz bekommen, dann sucht mich in Seenhain auf.","Wenn Ihr dies für mich tut, soll Euer Name in Rabenholdt Bekanntheit erlangen, <Name>."}, {"Bringt die Hörner von Nez'ra zu Arantir nach Seenhain."}},
[2359] = {"Klavens Turm", {"Ich überwache diesen Turm jetzt schon seit Wochen, <Name>. Kürzlich kam eine größere Lieferung aus dem Dämmerwald an und mit dieser Lieferung kamen auch diese Wachen.","Was sie sind? Das weiß ich genauso wenig wie Ihr. Eines kann ich Euch aber sagen: Menschen sind das nicht.","Wie dem auch sei, bei der Lieferung war eine Truhe dabei und ich vermute mal, das ist es, was diese Monstrositäten bewachen. Die Kreatur mit den Stacheln hat einen Schlüssel, den wir brauchen.","Beschafft Euch den Schlüssel, dringt in den Turm ein und stellt fest, was in dieser Truhe ist. Berichtet dann Mathias, was Ihr herausgefunden habt."}, {"Stehlt den Turmschlüssel der Defias, brecht in den Turm der Defias ein und stellt fest, was sich in der Dämmerwaldtruhe befindet. Bringt Mathias Shaw in Stormwind dann alle Informationen über das, was Ihr herausgefunden habt. Lest Kearnens Tagebuch, um neue Erkenntnisse über den Turm zu erlangen."}},
[2360] = {"Mathias und die Defias", {"Keinen Moment zu früh, <Name>. Spähtrupps des SI:7 berichten von ungewöhnlich intensiven Aktivitäten der Defias in Westfall.","Es sieht so aus, als hätten die Defias einen verlassenen Wachturm der Allianz südlich der Späherkuppe übernommen und verwendeten diesen als Operationsbasis, um Güter von der Lieferroute ins Rotkammgebirge hereinzuschmuggeln.","Begebt Euch nach Westfall und kontaktiert Agentin Kearnen. Sie wird Euch Bericht erstatten. Sie hält sich in der Nähe des Turms versteckt; dort findet Ihr sie.","... und, <Name>, solltet Ihr entdeckt werden, wird der SI:7 strikt leugnen, dass er Euch kennt."}, {"Reist nach Westfall und sucht Agentin Kearnen. Sie wird Euch Bericht erstatten."}},
[2361] = {"Restaurierung der Halskette", nil, nil},
[2378] = {"Suche nach der Shattered-Hand", {"Ihr habt den Schattenpirschern gut gedient, <Name>, aber vielleicht ist es an der Zeit, dass Ihr Euch nach draußen wagt. Zwar habt Ihr schon gezeigt, dass Ihr in der Kunst der Verstohlenheit und der Täuschung bewandert seid, doch Eure Ausbildung ist noch lange nicht beendet.","Die Schattenpirscher haben Verbündete bei der Shattered-Hand. Sucht jemanden auf, der nichts dagegen hat, uns zu helfen: Shenthul in Orgrimmar."}, {"Reist nach Orgrimmar und sprecht an der Kluft der Schatten mit Shenthul."}},
[2379] = {"Zando'zan", {"Entschlossenheit allein reicht nicht aus, um die Hindernisse zu überwinden, die vor uns liegen.","Sprecht mit Zando'zan, um Eure Ausbildung als <Agent/Agentin> der Shattered-Hand zu beginnen.","Erst wenn Ihr die Kunst der Verstohlenheit und die Diebeskunst gemeistert habt, könnt Ihr den nächsten Schritt auf Eurem persönlichen Weg des Schicksals gehen."}, {"Sprecht an der Kluft der Schatten mit Zando'zan."}},
[2380] = {"Nach Orgrimmar!", {"Noch seid Ihr <kein Meister/keine Meisterin> der Verstohlenheit und der Täuschung, doch das kann und wird sich alles ändern. Ganz offensichtlich habt Ihr die da oben mit Euren dürftigen Fähigkeiten beeindruckt, denn der große Shenthul selbst hat nach Euch gefragt.","Reist auf der Straße nördlich von hier nach Orgrimmar und sprecht in der Kluft der Schatten mit Shenthul."}, {"Sprecht an der Kluft der Schatten in Orgrimmar mit Shenthul."}},
[2381] = {"Das Ausplündern der Plünderer", {"Ich will Euch keine Angst einjagen, <Name>, aber die letzten fünf Diebe, die ich ausgesandt habe, um den Schatz der Südmeerfreibeuter zu stehlen, sind in Stücken zu mir zurückgekommen.","An der Küste im Süden findet Ihr die Tide Razor, das Schiff der Südmeerfreibeuter. Unten im Laderaum findet Ihr das Gesuchte: den Schatz der Südmeerfreibeuter. Auf dem zweiten Deck des Schiffes findet Ihr die Piratenhöhle, dort bilden sie ihre Diebe aus.","Geht an Bord dieses Schiffes, stehlt den Schatz und bringt ihn mir. Nutzt die Piratenhöhle, wenn Ihr Eure Diebeskunst üben wollt, aber seid vorsichtig!"}, {"Bringt den Schatz der Südmeerfreibeuter zu Wrenix dem Jämmerlichen in Ratchet. Vergesst nicht, einen R.C.V.K. und Diebeswerkzeug von Wrennixs Elektrodingsda-Apparat zu bringen. Ihr werdet diese Dinge brauchen, um Eure Mission zu beenden.","Wenn Ihr von ungewöhnlich feindlichen Papageien angegriffen werdet, benutzt Euren R.C.V.K.!"}},
[2382] = {"Wrenix aus Ratchet", {"Zando'zan hat es gesehen! Ja, der Tag wird kommen! Thralls Tod... der Dolch in seinem Rücken, das Gift, das durch seine Adern fließt... Zando'zan hat schon zu viel gesagt.","<Name>! Zando'zan hat eine Aufgabe für <Name>! <Name> muss nach Ratchet reisen, im Osten von Crossroads, und mit Wrenix dem Jämmerlichen sprechen. Los!"}, {"Reist nach Ratchet und sprecht mit Wrenix dem Jämmerlichen."}},
[2383] = {"Einfaches Pergament", {"Ah, während Ihr fort wart, kam ein Pergament für Euch, <Name>.","Lest es, wenn Ihr Zeit habt. Wenn ich mich nicht irre, kam es vom Kriegerlehrer Frang. Er würde gern mit Euch sprechen, wenn Ihr bereit seid. Ihr findet ihn außerhalb des Höhlenbaus, wo er sich in den Schatten verbirgt."}, {"Lest das einfache Pergament und sprecht mit Frang im Tal der Prüfungen."}},
[2398] = {"Die verschollenen Zwerge", {"Die Grabungsstätte von Uldaman war nicht immer so überlaufen. Wir Zwerge von Ironforge kontrollierten früher das Gebiet, aber... das war, bevor die Troggs kamen. Sie kamen so schnell und waren so zahlreich, dass nicht einmal die Macht der Zwerge sie aufhalten konnte.","Um ehrlich zu sein, kam ihr Angriff so plötzlich, dass ich fürchte, dass vielleicht einige unserer Leute hinter den feindlichen Linien festsitzen.","Mein Freund Baelog hatte eine kleine Gruppe Zwerge tief hinunter in die Grabungsstätte geführt, kurz bevor die Troggs auftauchten. Wenn er und seine Gefährten noch leben, dann möchte ich, dass Ihr sie findet."}, {"Sucht in Uldaman nach Baelog."}},
[2399] = {"Die sprießenden Wedel", nil, nil},
[2418] = {"Kraftsteine", {"Vor einiger Zeit hatte ich einen kleinen Zwischenfall mit den Zwergen der Schattenschmiede. Sie brachten mich in die Festung Angor, um dort zwei Arten von seltsamen Steinen zu untersuchen, wie sie in Uldaman vorkommen. Ich habe entdeckt, dass die Steine langlebige und stabile Kraftquellen darstellen, wenn sie richtig behandelt werden. Es ist schon erstaunlich, was man alles in dieser alten Grabungsstätte finden kann!","Jedenfalls warfen sie mich aus der Festung Angor raus, nachdem ich ihnen das Geheimnis der Steine verraten hatte, und begannen, in Uldaman nach weiteren Steinen zu graben.","Ich will diese Steine für mich! Bringt mir beide Arten von Steinen und ich bezahle Euch gut dafür!"}, {"Bringt Rigglefuzz im Ödland 8 Kraftsteine aus Dentrium und 8 Kraftsteine aus An'Alleum."}},
[2438] = {"Der smaragdgrüne Traumfänger", {"Einst erhielt ich von Gaerolas Talvethen, dem Bewacher der Druiden im Grabhügel von Ban’ethil, einen smaragdgrünen Traumfänger. Dieses mächtige Amulett ist in der Lage, Energie aus dem Smaragdgrünen Traum abzuziehen und damit denen, die es tragen, Glück zu bringen.","Leider habe ich den Traumfänger nicht mehr aus meiner Kommode in Starbreeze holen können. Starbreeze war einst ein ruhiger Ort, aber jetzt ist er durch die Furbolgs, die sich dort aufhalten, der Verderbnis anheim gefallen.","Wärt Ihr vielleicht bereit, mir meinen Traumfänger wiederzubeschaffen, <Klasse>?"}, {"Bringt den smaragdgrünen Traumfänger zu Tallonkai Swiftroot in Dolanaar."}},
[2439] = {"Die Platinscheiben", {"Dieser Gegenstand ist ein unglaublicher Fund. Die alten Titanen oder 'Schöpfer', wie sie auf diesen Scheiben genannt werden, sind die Verbindung zu unserer verzauberten Erschaffung, wenn das hierin gespeicherte Wissen korrekt ist! Wir werden uns so eingehend wie möglich mit diesen Scheiben beschäftigen.","Was Euch betrifft, <Klasse>: Bringt diesen Gutschein zu Dinita Stonemantle in dem Gewölbe. Für ihn erhaltet Ihr etwas, das meiner Meinung nach eines solch unglaublichen Fundes würdig ist.","Kehrt umgehend zu mir zurück, wenn Ihr fertig seid - es gibt noch viel mehr für die Forscherliga zu tun!"}, {"Bringt Euren Belohnungsgutschein zu Dinita Stonemantle im Gewölbe von Ironforge."}},
[2440] = {"Die Platinscheiben", {"Ich werde all meine Energien auf diese Scheiben fokussieren; wenn die Zeit gekommen ist, werden sie mir ihre Funktionsweise schon verraten.","Was Euch betrifft, <Name>, nun, Ihr sollt dafür belohnt werden, dass Ihr mir diese Sache kundgetan habt. Bringt den Belohnungsgutschein zu Bena Winterhoof, der Alchimielehrerin in Thunder Bluff. Sie wird Euch einen speziellen Gegenstand geben, der Euch bestimmt gefällt, und außerdem erhaltet Ihr von ihr eine Auswahl an Tränken aus meinem persönlichen Vorrat.","Kehrt bald zu mir zurück ... ich spüre, dass wir in Bezug auf diesen Fund noch viel mehr zu tun haben."}, {"Bringt den Belohnungsgutschein zu Bena Winterhoof nach Thunder Bluff."}},
[2458] = {"Undercover", {"Die Venture Company... Vielleicht habt Ihr von den Leuten schon gehört?","Ein Jahr lang haben wir daran gearbeitet, einen Agenten in ihre Reihen einzuschleusen. Endlich hatten wir - dank harter Arbeit und präziser Planung - Erfolg.","Vor kurzem haben wir jetzt gehört, dass die Venture Company irgendetwas Großes plant. Begebt Euch zum Turm der Venture Company nördlich des Schlickermoores und kontaktiert dort unseren Undercover-Agenten, Zuchtmeister Fizzule.","Sobald Ihr den Turm gefunden habt, verwendet diese Leuchtfeuerwaffe und grüßt ihn mit dem Gruß der Shattered-Hand."}, {"Reist zum Turm der Venture Company nördlich des Schlickermoores und kontaktiert dort den Agenten der Shattered-Hand, Zuchtmeister Fizzule.","Sobald der Turm in Sichtweite ist, verwendet die Leuchtfeuerwaffe, um Zuchtmeister Fizzule Eure Ankunft anzuzeigen. Feuert ZWEI Leuchtfeuergeschosse ab, nähert Euch dann Zuchtmeister Fizzule und zeigt den Gruß der Shattered-Hand.","Es ist wichtig, dass Ihr diese Aktionen in der richtigen Reihenfolge durchführt, da Fizzule jede Person, die er nicht erkennt, angreift und eliminiert."}},
[2459] = {"Ferocitas der Traumfresser", {"Der Smaragd... Er ist weg! Mein Traumfänger ist beschädigt!","Eine Gruppe von Knarzklauenmystikern lebt im Norden von Starbreeze. Ich habe gehört, dass ihr Anführer, Ferocitas der Traumfresser, seit einiger Zeit eine Halskette trägt, die des Nachts grün leuchtet. Jetzt, wo ich meinen Traumfänger sehe, bin ich mir sicher, dass er meinen Smaragd gestohlen hat... Er würde nie verstehen, dass dessen Macht völlig nutzlos für ihn ist.","Findet das verlorene Juwel, <Name>. Und wenn Ihr schon mal dort seid, erledigt auch gleich einige dieser verderbten Mystiker."}, {"Tallonkai Swiftroot aus Dolanaar möchte, dass Ihr 7 Mystiker der Knarzklauen tötet und das verlorene Juwel findet."}},
[2460] = {"Der Shattered-Gruß", {"Als ein Schurke der Shattered-Hand müsst Ihr unbedingt den Shattered-Gruß erlernen. Dies ist die einzige Methode, mit der man ein anderes Mitglied der Hand zweifelsfrei identifizieren kann.","Schaut zu, lernt, wiederholt."}, {"Führt Shenthul gegenüber den Shattered-Gruß aus."}},
[2478] = {"Mission: möglich, aber nicht sehr wahrscheinlich", {"Ihr müsst den Turmschlüssel von Großknecht Silixiz stehlen, <Name>. Sobald sich der Schlüssel in Eurem Besitz befindet, haltet ihn gut fest - ich könnte ihn später brauchen.","Vergesst nicht, das Gift hat alles in diesem Turm geschwächt. Wendet Eure Fähigkeiten an, um unter seinen Bewohnern Chaos und Zerstörung zu verbreiten. Erschlagt sie alle - zeigt keine Gnade!","Sobald sich dort drinnen nichts mehr rührt, knackt Ihr das Schloss von Gallywix' Schließkassette und stehlt den Vorrat an Zanzils Mixtur, der sich darin befindet. Bringt Shenthul in Orgrimmar sowohl Gallywix' Kopf als auch die Mixtur."}, {"Stehlt Silixiz' Turmschlüssel von Großknecht Silixiz. Tötet dann 2 mutierte Drohnen der Venture Co., 2 Patrouillen der Venture Co. und 2 Ausgucker der Venture Co.","Eliminiert den obersten Großknecht Gallywix und nehmt seinen Kopf. Knackt dann das Schloss von Gallywix' Schließkassette und stehlt den Vorrat an Zanzils veränderter Mixtur. Bringt den Vorrat und Gallywix' Kopf zu Shentul nach Orgrimmar; er wird Euch weitere Anweisungen geben."}},
[2479] = {"Hinotts Hilfe", {"Bringt eine Probe der Mixtur zu Serge Hinott nach Tarrens Mühle.","Wenn Euch überhaupt irgendjemand heilen kann, dann ist das Serge. Falls Ihr lange genug lebt, kann Serge Euch in der Kunst der Herstellung und Anwendung von Giften unterweisen."}, {"Reist nach Tarrens Mühle im Vorgebirge von Hillsbrad und liefert die Probe von Zanzils Mixtur bei Serge Hinott ab.","Um nach Tarrens Mühle zu kommen, müsst Ihr den Zeppelin nach Undercity nehmen und dann auf der Straße nach Süden durch den Silberwald in Richtung Hillsbrad reisen. Folgt den Schildern!"}},
[2480] = {"Hinotts Hilfe", {"Noch nie habe ich gesehen, dass ein Gift so wundervolle Auswirkungen hatte! Zanzil, sagt Ihr? Nie von ihm gehört. Aber ich muss ihm zur Kreation dieses Gebräus gratulieren.","Tja, ich soll Euch also hiervon heilen und Euch dann beibringen, wie man das Gift anwendet?","Gebt mir einen Moment Zeit, damit ich das Toxin untersuchen kann, <Name>."}, {"Wartet auf Serge Hinott, um das Heilmittel fertig zu stellen."}},
[2498] = {"Rückkehr zu Denalan", {"Ich habe gerade erfahren, dass Denalan am Al'Amethsee herausgefunden hat, was der Grund für die Tumore sein könnte, von denen die Bäumlinge befallen sind. Bitte sprecht mit ihm und sagt ihm, dass ich Euch geschickt habe.","Sputet Euch, <Name>; er braucht unsere Hilfe."}, {"Rellian Greenspyre möchte, dass Ihr am Al'Amethsee mit Denalan sprecht."}},
[2499] = {"Grimmeiche", {"In einer Höhle am Südufer des Sees überträgt ein Bäumling namens Grimmeiche die Verderbnis auf alle Wesen, die am Al'Amethsee leben.","Ich habe mich nicht allzu nah herangewagt, aber selbst aus der Entfernung ist eines ganz klar: Grimmeiche wird von dem größten Tumor vergiftet, den ich je gesehen habe. Ich würde sogar sagen, er ist nicht nur groß, er ist riesengroß.","Ihr habt schon viel getan, um mir zu helfen, <Name>, und doch möchte ich Euch um einen weiteren Gefallen bitten. Spürt Grimmeiche auf und holt den Tumor. Entfernt diese Quelle der Verderbnis aus meiner Heimat!"}, {"Denalan vom Al'Amethsee möchte, dass Ihr den riesengroßen Tumor von Grimmeiche einsammelt."}},
[2500] = {"Reagenzien-Suche im Ödland", {"<Klasse>, ich möchte Eure Dienste in Anspruch nehmen. Ich bin ein angesehener Alchimist und benötige Eure Unterstützung, um einige Reagenzien zu beschaffen, die im Ödland zu finden sind.","Ich brauche folgende Gegenstände, um meine Vorräte aufzufüllen: 5 Bussard-Muskelmägen, 10 Klippenkojotenfangzähne und 5 Felselementarsplitter. Natürlich könnt Ihr Euch diese Reagenzien von den Viechern selbst holen; aber wenn Ihr keine Kämpfernatur seid, dann sucht Euch besser Freunde, die kämpfen können.","Bringt mir diese Gegenstände, dann springen ein paar Münzen für Euch dabei heraus.","Also, was sagt Ihr?"}, {"Beschafft die Reagenzien, die Ghak Healtouch aus dem Ödland braucht, und bringt sie ihm nach Thelsamar."}},
[2501] = {"Reagenzien-Suche im Ödland II", {"Ihr seid mir eine große Hilfe gewesen, <Name>. Ich würde Euch gern das Rezept des Gebräus geben, das Ihr vorhin gekostet habt... doch zuvor muss ich Euch um einen recht gefährlichen Gefallen bitten.","Diese Schließkassette enthält drei leere thaumaturgische Gefäße; sie sind mit einer Einstellungs-Aura magieerfüllt, die einem versengten Wächtergroßdrachen das Blut aussaugt. Wenn Ihr ein solches Gefäß gegen die Bestie einsetzt, wird sie höchst erbost reagieren, also seid vorsichtig. Bringt sie mir, wenn Ihr alle drei gefüllt habt.","Gelingt Euch das, gehört das Rezept Euch."}, {"Benutzt die leeren thaumaturgischen Gefäße bei versengten Wächtergroßdrachen im Ödland. Wenn Ihr sie gefüllt habt, bringt sie zu Ghak Healtouch in Thelsamar."}},
[2518] = {"Tränen des Mondes", {"Einst wurde Lady Sathrah von Elune geliebt. Grazil und rein spann die Spinne ihre silbernen Fäden im Mondlicht, wo sie den Abendnebel einfingen. Der silbrige Tau hatte starke Heilkräfte und wurde darum hier im Tempel aufbewahrt.","Doch in jüngster Zeit ist Sathrah dem Wahnsinn verfallen. Auch die ihr nachfolgenden Generationen sind nun gefährdet.","Findet Lady Sathrah, <Name>, und beendet ihr Leiden. Sie hält sich an der nördlichen Grenze von Teldrassil auf, in der Nähe des Wellspringsees. Sammelt ihre silbrigen Spinndrüsen und bringt sie mir."}, {"Priesterin A'moora vom Tempel des Mondes in Darnassus möchte, dass Ihr ihr Lady Sathrahs silbrige Spinndrüsen bringt."}},
[2519] = {"Der Tempel des Mondes", {"Ein einstmals großes Wesen des Waldes benötigt Hilfe, <Volk>.","Eine schwierige Aufgabe muss getan werden.","Bitte sucht Priesterin A'moora auf: Sie wird Euch alles erklären. Ihr findet sie südöstlich von hier im Tempel des Mondes."}, {"Schwester Aquinne möchte, dass Ihr im Tempel des Mondes mit Priesterin A'moora sprecht."}},
[2520] = {"Sathrahs Opferung", {"Jetzt müsst Ihr die Aufgabe vollenden, die Euch auferlegt worden ist. Nehmt die Spinndrüsen und bringt Elune Lady Sathrahs Opfer dar. Verschafft der großen Spinne die ewige Ruhe.","Begebt Euch zum zentralen Brunnen in diesem Tempel. Dort könnt Ihr das Opfer niederlegen. Die heiligen Wasser werden die Verderbnis, durch die Sathrah in den Wahnsinn getrieben wurde, läutern und säubern.","Kommt zu mir zurück, sobald Ihr dies erledigt habt."}, {"Priesterin A'moora möchte, dass Ihr Lady Sathrahs silbrige Spinndrüsen am Brunnen innerhalb des Tempels ablegt und dann zu ihr zurückkehrt."}},
[2521] = {"Ein Dienst für Kum'isha", {"<Volk>, kann Euer winziges Gehirn überhaupt verstehen, wie selten eine völlig fehlerlose Draenethystsphäre ist? Viele Draenei haben ihr ganzes Leben damit verbracht, auch nur einen einzigen solchen Kristall zu finden, und keiner, der es versuchte, konnte sein Vorhaben je ausführen. Alle sind eines schrecklichen Todes gestorben.","Und nun steht Ihr vor Kum'isha, wissend, was Kum'isha wünscht, und Ihr möchtet dennoch behilflich sein? Nun gut, <Volk>... Solltet Ihr auf Euren Reisen durch die Lande irgendwann auf eine fehlerlose Draenethystsphäre stoßen, dann bringt sie mir, und ich belohne Euch mit Reichtümern, wie Ihr sie Euch niemals erträumen könnt."}, {"Falls Ihr auf Euren Reisen durch die verwüsteten Lande je auf eine fehlerlose Draenethystsphäre stoßen solltet, bringt sie zu Kum'isha dem Sammler.","Doch seid gewarnt. Der Edelstein ist einer der rarsten Kristalle von ganz Azeroth. Jedes Wesen in diesem Land könnte eine fehlerlose Draenethystsphäre bei sich haben.","Ihr erhaltet eine Belohnung für jede fehlerlose Draenethystsphäre, die Ihr beschaffen könnt."}},
[2522] = {"Kum'ishas Bemühungen", {"Obwohl die Herbeizauberung des Portals fehlgeschlagen ist, hoffe ich doch weiterhin, dass ich mit meinem nächsten Versuch Erfolg haben werde.","Die Hoffnung der Draenei ist ebenso groß wie Eure Gier nach Tand, <Volk>. Begebt Euch wieder hinaus in dieses verfluchte Land und beschafft mir eine weitere fehlerlose Draenethystsphäre.","Natürlich liegt es ganz allein an Euch, ob Ihr diese Aufgabe annehmen wollt oder nicht... <grinst>."}, {"Findet eine fehlerlose Draenethystsphäre und bringt sie Kum'isha dem Sammler."}},
[2523] = {"Verderbte Liedblume", nil, nil},
[2541] = {"Der schlafende Druide", {"Die hier lebenden Schamanen der Knarzklauen haben einen Weg entdeckt, wie man den Geist eines schlafenden Druiden von seinem Körper trennen kann. Die Furbolgs haben meinen physischen Körper reanimiert und lassen ihn jeden angreifen, der versucht, den Grabhügel von Ban'ethil zu erforschen. Ich selbst bin im Smaragdgrünen Traum gefangen und kann diesem Treiben keinen Einhalt gebieten.","Ihr müsst mir helfen. Alle Knarzklauenschamanen tragen einen seltsamen Glücksbringer bei sich, mit dem dieses Ritual durchgeführt wird, und ich würde einen solchen Glücksbringer gern untersuchen. Bitte, <Name>, bringt mir einen."}, {"Bringt einen Voodooglücksbringer des Schamanen zu Oben Zornklaue in den Grabhügel von Ban'ethil."}},
[2561] = {"Druide der Klaue", {"Nachdem ich den Glücksbringer untersucht habe, weiß ich jetzt, was zu tun ist, <Name>. Bitte nehmt ihn, und tut das, worum ich Euch bitte.","Ihr müsst die tiefsten Tiefen des Grabhügels von Ban'ethil erforschen. Dort findet Ihr meinen seelenlosen Körper. Und obwohl es mich sehr schmerzt, Euch das jetzt zu sagen, sehe ich keine andere Möglichkeit, wie ich mich der Kontrolle der Knarzklauen entziehen kann.","Damit ich mich von ihnen befreien kann, müsst Ihr meinen physischen Körper töten. Sobald das getan ist, wendet den Voodooglücksbringer auf meinen gefallenen Körper an. Und wenn Ihr diese Aufgabe erfüllt habt, kommt zu mir zurück."}, {"Oben Zornklaue möchte, dass Ihr seinen seelenlosen Körper tötet und dann den Voodooglücksbringer anwendet."}},
[2581] = {"Kicherfangunterkiefer", {"Es ist eine wohl bekannte Tatsache, dass Hyänen unglaubliche Kraft in ihrem Unterkiefer haben, aber drei Zoll Thoriumstahl durchbeißen? Unmöglich, völlig unwahrscheinlich, und dennoch habe ich es mit meinen eigenen Augen gesehen.","Wagt Euch hinaus in die verwüsteten Lande und bringt mir drei Kicherfangunterkiefer. Und während Ihr nach den Kicherfanghyänen sucht, haltet auch nach Ebern und Skorpoks Ausschau, denn für die erste Zubereitung der Inhaltsstoffprobe benötige ich auch zwei feuerverbrannte Eberlungen und eine Skorpokzange."}, {"Bringt Blutmagier Drazial 3 Kicherfangunterkiefer, 2 feuerverbrannte Eberlungen und 1 Skorpokzange."}},
[2582] = {"Wut der Zeiten", {"Ah, <Name>, willkommen. Es scheint, als habe es Euch gefallen, so von Kraft erfüllt und handlungsfähig zu sein, wie es durch die Wirkung der Kicherfangdroge möglich wurde. Doch seid gewarnt: Zwar zeigt sich unmittelbar nach der Einnahme des Präparates ein gigantischer Stärkeschub, doch auf lange Sicht gesehen könnte das Mittel nachteilige Auswirkungen auf Euren Geist und Eure Intelligenz haben.","Und jetzt, wo Ihr dies wisst - braucht Ihr noch mehr? Falls ja, dann bringt mir drei weitere Kicherfangunterkiefer, zwei feuerverbrannte Eberlungen und eine Skorpokzange."}, {"Bringt Blutmagier Drazial 3 Kicherfangunterkiefer, 2 feuerverbrannte Eberlungen und 1 Skorpokzange."}},
[2583] = {"Lebenskraft des Ebers", {"Hier in den verwüsteten Landen leben zwei Arten von Ebern: der Aschmähneneber und der Hölleneber.","Es scheint, als mache die Ausdauer dieser Wildtiere sie so gut wie völlig immun gegen Schaden jeglicher Art, da die anderen Tiere zu große Schwierigkeiten haben, sie zu töten, und daher in Ruhe lassen.","Wir müssen weitere Analysen durchführen, <Name>. Wenn Ihr mir drei feuerverbrannte Eberlungen, zwei Skorpokzangen und ein Basiliskengehirn bringt, dann werde ich aus den Überresten der Inhaltsstoffprobe ein himmlisches Elixier kreieren. Und Ihr seid mein erstes Versuchsobjekt!"}, {"Bringt Blutmagier Drazial 3 feuerverbrannte Eberlungen, 2 Skorpokzangen und 1 Basiliskengehirn."}},
[2584] = {"Geist des Ebers", {"Schon als ich das Elixier zum ersten Mal einsetzte, konnte ich sehen, dass eine oder zwei Anwendungen Euer Verlangen danach nicht stillen würden, <Name>. Selbst jetzt bleibt mir die Gier in Euren Augen nicht verborgen. Das Gebräu hat suchterzeugende Eigenschaften, denen, so scheint es, noch nicht einmal jemand mit äußerst starkem Willen widerstehen kann.","So sei es denn! Solltet Ihr mehr von dem Lungensaft-Cocktail benötigen, dann wagt Euch wieder hinaus ins Ödland und besorgt mir drei feuerverbrannte Eberlungen, zwei Skorpokzangen und ein Basiliskengehirn."}, {"Bringt Blutmagier Drazial 3 feuerverbrannte Eberlungen, 2 Skorpokzangen und 1 Basiliskengehirn."}},
[2585] = {"Der Entscheidungsschlag", {"Habt Ihr jemals gesehen, wie sie kämpfen, <Name>? Die Skorpoks, meine ich. Habt Ihr jemals die Anmut und Gewandtheit ihrer Bewegungen gesehen? Gesehen, wie entschlossen sie zuschlagen?","Wenn wir nur irgendwie diese Beweglichkeit einfangen und für uns nutzbar machen könnten, dann würden wir so reich werden, wie... ähem, dann wäre das ein unschätzbarer Segen für die wissenschaftliche Gemeinschaft, meine ich natürlich.","Helft mir, <Name>, dann lasse ich Euch auch von der fertigen Inhaltsstoffprobe kosten.","Um eine reaktionsstabile Probe herzustellen, benötige ich drei Skorpokzangen, zwei Geiermuskelmägen und eine feuerverbrannte Eberlunge."}, {"Bringt Blutmagier Drazial 3 Skorpokzangen, 2 Geiermuskelmägen und 1 feuerverbrannte Eberlunge."}},
[2586] = {"Salz der Skorpok", {"Ihr lebt noch! Das sind wahrlich gute Neuigkeiten. Ich vermute mal, Ihr seid wieder da, weil Ihr mehr von der Skorpokinhaltsstoffprobe möchtet?","Tja, <Name>, dann mal ab ins Ödland mit Euch, und kommt erst zurück, wenn Ihr drei Skorpokzangen, zwei Geiermuskelmägen und eine feuerverbrannte Eberlunge eingesammelt habt."}, {"Bringt Blutmagier Drazial 3 Skorpokzangen, 2 Geiermuskelmägen und 1 feuerverbrannte Eberlunge."}},
[2601] = {"Der Biss des Basilisken", {"In der Einöde wimmelt es nur so von Rotsteinbasilisken, <Name>. Diese Basilisken haben sich an ihre Umgebung angepasst und sind in ihr gediehen, mehr als jede andere wilde Tierart in diesem Gebiet.","Ich habe sie schon oft von weitem beobachtet, wenn sie jagen und ihren täglichen Aktivitäten nachgehen: Diese Wildtiere zeigen Denkprozesse, die komplexer sind als die mancher Humanoiden!","Ich muss ihre Gehirne studieren!","Bringt mir zehn ihrer Gehirne, <Name>. Doch für das Präparat benötige ich auch zwei Geiermuskelmägen. Diese bilden die Grundlage der Lösung."}, {"Bringt Blutmagierin Lynnore 10 Basiliskengehirne und 2 Geiermuskelmägen."}},
[2602] = {"Unfehlbare Gedanken", {"Eure fokussierte Konzentration hat so schnell nachgelassen? Dann muss unbedingt weitergeforscht werden!","Ach ja, und der Preis ist noch immer derselbe, <Name>. Wagt Euch in die verwüsteten Lande hinaus und kommt mit zehn Rotsteingehirnen und zwei Geiermuskelmägen wieder."}, {"Bringt Blutmagierin Lynnore 10 Basiliskengehirne und 2 Geiermuskelmägen."}},
[2603] = {"Lebensenergie des Geiers", {"Hier in den verwüsteten Landen besitzt der Tod tatsächlich Flügel, <Volk>. Ich habe gesehen, wie die schwarzen Töter und die Knochenpflücker sich am Kadaver so manch eines gefallenen Forschers gütlich taten - und manchmal fraßen sie sogar die übelsten, von Seuchen zerfressenen Leichen auf. Da ist es schon ein Wunder, wie ihre inneren Organe die Pestilenz überwinden, die in diesen Landen wütet.","Helft mir bei meinen Studien, <Name>! Ich brauche Muskelmägen! Aber ich benötige auch zwei Kicherfangunterkiefer. Diese stellen die Grundlage für das Gebräu dar.","Für Eure Anstrengungen erhaltet Ihr eine doppelte Belohnung."}, {"Bringt Blutmagierin Lynnore 10 Geiermuskelmägen und 2 Kicherfangunterkiefer."}},
[2604] = {"Geistige Beherrschung", {"Wie ich sehe, ist Euer inneres Feuer erloschen. Was meint Ihr, hat das geistige Hochgefühl etwa eine Stunde angedauert?","Falls Ihr mehr von der Muskelmagen-Kaumasse benötigt, dann benötige ich mehr Muskelmägen. Zurück in die Einöde mit Euch, <Name>. Kommt erst wieder, wenn Ihr zehn Muskelmägen und zwei Unterkiefer beschafft habt."}, {"Bringt Blutmagierin Lynnore 10 Geiermuskelmägen und 2 Kicherfangunterkiefer."}},
[2605] = {"Der durstige Goblin", {"Ich hab da so eine Idee. Ich habe von einigen seltsamen Kreaturen namens 'Tausammler' gehört, die im Südwesten leben. Angeblich können sie in speziellen Drüsen Wasser speichern.","Ich glaube, wenn ich eine von diesen Drüsen bekäme, dann könnte ich ein Getränk kreieren, das meinen schrecklichen Durst löschen würde. Könntet Ihr mir vielleicht helfen, <Name>?","Sucht bei den Kakteen, <Name>: Dort findet Ihr die Tausammler."}, {"Beschafft Euch eine volle Taudrüse und bringt sie nach Gadgetzan zu Marin Noggenfogger."}},
[2606] = {"Dem guten Geschmacke", {"Bäh! Igitt! Das Zeug schmeckt ja eklig! So viel dazu...","Bitte, was meint Ihr? Ja, ich weiß schon, Ihr findet, ich sollte nicht so schnell die Flinte ins Korn werfen. Na dann.","Ähm, da fällt mir was ein! Das ist genial! Bringt diesen Tau meiner Freundin Sprinkle - sie ist eine tolle Küchenchefin. Sagt ihr, was wir vorhaben, und dass wir das Zeug etwas aufpeppen wollen.","Ich meine, wenn es schon so fies schmeckt, dann sollte es wenigstens einen kleinen... Kick haben. Und davon kann ich nichts bemerken..."}, {"Marin Noggenfogger möchte, dass Ihr in Gadgetzan mit Sprinkle sprecht."}},
[2607] = {"Die Berührung von Zanzil", {"Es scheint, als hättet Ihr mehr Informationen bekommen, als selbst ich erwartet hätte. Es ist schon seltsam, wie das Schicksal manchmal spielt, <Name>.","Ein Heilmittel? Hmm... Geht hinunter in den Keller und sprecht mit Doc Mixilpixil: Vielleicht kann er Euch helfen."}, {"Sprecht im Keller der Kaserne mit Doc Mixilpixil."}},
[2608] = {"Die Berührung von Zanzil", {"Meine Güte, Ihr seht ja aus wie der Tod. Nein, falsch. Ich habe den Tod schon gesehen - Ihr seht schlimmer aus. Ihr seht aus wie das, was der Tod bei seiner letzten Vorsorgeuntersuchung ausgespuckt hat.","Ha! Habt Ihr das gehört, Noarm? Schreibt das auf!","<hustet> Ähem, ja, damit ich Euch helfen kann, muss ich Euch Blut abnehmen. Und keine Sorge, es tut nur die ersten... fünfzig Male oder so weh... Wenn Ihr bereit für die Behandlung seid, <Name>, solltet Ihr Euch bitte /hinlegen."}, {"Ihr solltet Euch /hinlegen, damit Doc Mixilpixil Euch untersuchen kann. Denn wie soll er ein Heilmittel finden, wenn er die Ursache nicht finden kann?"}},
[2609] = {"Die Berührung von Zanzil", {"Wir sind einstimmig zu folgendem Schluss gekommen, <Name>... Ihr habt eine 'Itis'.","Zum Glück kann ich die 'Itis' heilen.","Und das brauche ich dafür: ein Bündel einfache Wildblumen, eine verbleite Phiole, eine Bronzeröhre und schließlich und endlich noch eine Rolle blassgelben Seidenfaden. Die alte Emma hat in 'Das feinste Band' eine Rolle davon für mich hinterlassen. Ihr findet den Laden an einem der Kanäle.","Beeilt Euch jetzt, <Name>, und vermeidet es möglichst, jemanden anzufassen, bis Ihr geheilt seid. Dr. Montgomery meint übrigens, Ihr könntet eine Dusche vertragen - das hat er gesagt, nicht ich."}, {"Bringt Doc Mixilpixil 1 Bündel einfache Wildblumen, 1 verbleite Phiole, 1 Bronzeröhre und 1 Rolle blassgelben Seidenfaden. So eine 'Itis' heilt sich nicht von selbst, <junger Herr/junge Dame>."}},
[2621] = {"Der Entehrte", {"Bedauern. Das ist alles, was mir noch geblieben ist. Eine einzige Fehlentscheidung hatte gleich mehrere schreckliche Konsequenzen. Hätte ich doch nur auf meine Befehle gewartet. Vielleicht hätte darin etwas über die Schrecken gestanden, die uns in jenem verfluchten Land erwarteten! Vielleicht hätten sie uns zu einer anderen Abteilung beordert... Letztendlich war es meine Schuld. Es war alles meine Schuld.","Und so stehe ich hier und warte auf meine Befehle. Ich muss es wissen. Ich muss diese Befehle haben.","Zentralenkommandant Ruag lebt in Stonard. Findet ihn und bringt in Erfahrung, was aus den Befehlen geworden ist."}, {"Sprecht in Stonard mit Zentralen-Kommandant Ruag."}},
[2622] = {"Die fehlenden Befehle", {"Die Befehle? Jetzt sagt mir doch mal, was Ihr mit diesen Befehlen wollt!","Was wollt Ihr damit andeuten, <Name>? Natürlich habe ich die Befehle weitergegeben! Die Soldaten, denen ich die Befehle mitgab, wurden von Murlocs überfallen und auf brutalste Weise misshandelt. Nur ein Einziger von ihnen hat überlebt... wenn man das überleben nennen kann. Er kann kaum sprechen, kann nicht mehr laufen und muss durch einen Schlauch ernährt werden. Wenn Ihr wissen wollt, was mit diesen Befehlen passiert ist, dann sprecht mit Bengor. Er treibt sich hier mit Sicherheit irgendwo herum... und, <Name>, seid vorsichtig, in was Ihr Eure Nase steckt. Nicht, dass sie Euch abgebissen wird!"}, {"Sprecht mit Bengor."}},
[2623] = {"Der Sumpfredner", {"Befehle... vom Zentral-Kommandanten ausgegeben. Haben Befehle... genommen. Auf dem Weg in die verwüsteten Lande... überfallen...","Murlocs... Viele Murlocs... Einer... hieß Sumpfredner. Nahm mir Befehle ab, als Speer in meinen Schädel fuhr. Osten... Höhle...","<Bengor verliert das Bewusstsein.>"}, {"Beschafft die Befehle des Kriegshäuptlings wieder und gebt sie dem gefallenen Helden der Horde zurück."}},
[2641] = {"Sprinkles Geheimzutat", {"Als ich noch ein kleines Mädchen war, hat meine Schwester immer gekocht. Es war grauenhaft! Manche ihrer 'Gerichte' konnte ich kaum runterwürgen - bis ich irgendwann meine 'Geheimzutat' entdeckt habe! Egal, zu was man sie hinzufügt... im Nu schmeckt alles toll!","Um was es sich dabei handelt, wollt Ihr wissen? Es ist ein Pilz namens violetter Tragan. Wenn Ihr wollt, dass ich dieses fürchterlich schmeckende Gebräu in etwas Genießbares verwandle, dann begebt Euch ins Hinterland und sucht dort nach einem kleinen See.","Und vergesst nicht: Violette Tragane findet man nur unter Wasser."}, {"Sprinkle in Gadgetzan möchte, dass Ihr einen violetten Tragan sucht und ihr bringt."}},
[2661] = {"Lieferung für Marin", {"Hier habt Ihr das violette Pulver, <Name>. Bringt es zu Marin und sagt ihm, er schuldet mir was!"}, {"Sprinkle möchte, dass Ihr das violette Pulver nach Gadgetzan zu Marin Noggenfogger bringt."}},
[2662] = {"Noggenfogger-Elixier", {"So, jetzt mische ich das Pulver unter den Tau.","Sprecht mich in einer Minute noch mal an... wenn ich das fabelhafte Noggenfogger-Elixier perfektioniert habe!"}, {"Marin Noggenfogger möchte, dass Ihr ihn nochmals ansprecht, wenn er sein Elixier fertig gestellt hat."}},
[2681] = {"Die Steine, die uns binden", {"Allein wird es Euch nicht gelingen, die Streitmacht von Razelikh und seinen Untergebenen zu überwinden.","Sammelt eine Gruppe von geeigneten Abenteurern um Euch und befreit meine Männer aus der ewigen Qual! Achtzehn Steine müsst Ihr zerschmettern.","Kämpft hart und sterbt ehrenvoll, <Name>."}, {"Befreit 9 Diener von Razelikh, 3 Diener von Sevine, 3 Diener von Allistarj und 3 Diener von Grol. Kehrt zum gefallenen Helden zurück, sobald Ihr die Aufgabe erfüllt habt. Ihr müsst nahe bei den Steinen bleiben, sonst habt Ihr keinen Erfolg."}},
[2701] = {"Helden der Vergangenheit", {"Bitte, nehmt Euch ein Relikt der Vergangenheit. Wir haben für den Gegenstand keine Verwendung mehr.","Möge es Euch in Eurem Kampf gegen die Legion gute Dienste leisten."}, {"Öffnet die Truhe und nehmt Euch Eure Belohnung."}},
[2702] = {"Helden der Vergangenheit", {"Mit Eurem Edelmut und Eurer Heldenhaftigkeit erweist Ihr mir Ehre, <Name>.","Und auch ihnen habt Ihr Ehre erwiesen..."}, {"Sprecht mit Corporal Thund Splithoof."}},
[2721] = {"Kirith", {"Ein Kapitel Eurer Abenteuer mag jetzt beendet sein, doch ein weiteres Kapitel beginnt gerade, <Name>.","Einer meiner Männer ist noch immer verschollen, irgendwo in den verwüsteten Landen verschwunden. Ich spreche natürlich von Lieutenant Kirith.","Wie ich vorhin schon sagte, wurden wir beide von Allistarj gefangen genommen und gefoltert, aber ich fürchte, dass der arme Kirith nicht durchhielt und schwach wurde. Aus meinem Käfig heraus konnte ich die Experimente hören: Die gequälten Schreie hallten in der ganzen Höhle wider. Das war das Letzte, was ich von Kirith gesehen oder gehört habe."}, {"Findet heraus, was aus Lieutenant Kirith geworden ist."}},
[2741] = {"Der Super-Ei-O-Mat", nil, nil},
[2742] = {"Rin'ji steckt in der Falle!", {"He, <Volk>!","Bitte, helft Rin'ji flüchten. Ich weiß, Ihr seid <Volk>, mögt Rin'jis Volk nicht, aber Rin'ji bittet und bettelt! Muss hier raus!","Hab auch was Schönes für <Volk>, wenn <Volk> nett ist und Rin'ji hier raushilft...","Rin'ji hat was Geheimes bei den Aussichtsklippen versteckt."}, {"Eskortiert Rin'ji aus der Quel'Danil-Hütte heraus und findet dann das, was er bei den Aussichtsklippen im Osten versteckt hat."}},
[2743] = {"Der Mantel der Dunkelheit", {"Lasst uns nicht um den heißen Brei herumreden, <Volk>; ich habe nicht unbegrenzt Zeit.","Ihr müsst das Dreigestirn der Macht vernichten, das den Dämonenlord Razelikh beschützt. Doch das wird nicht ganz so einfach werden wie das Befreien einer verdammten Seele.","<Kirith grinst.>","Ihr müsst wissen, dass jeder von Razelikhs Untergebenen ein Drittel eines von Razelikh geschaffenen Amuletts um den Hals trägt. Und genau dieses Amulett braucht Ihr, um den Erzdämon zu beschwören.","Geht zu Trebor und sagt ihm, was benötigt wird, um Razelikh zu beschwören. Er kann Euch weitere Anweisungen geben."}, {"Kehrt mit Euren neuen Erkenntnissen zum gefallenen Helden der Horde zurück."}},
[2744] = {"Der Dämonenjäger", {"Das Dreigestirn wird von Razelikh beschützt.","Als Lohn für ihre Dienste hat Razelikh jeden von ihnen mit schier unbegrenzter Macht in den verwüsteten Landen ausgestattet. Die Teile des Amuletts sind auf die Lebenskraft ihrer jeweiligen Besitzer eingestellt und verleihen diesen Unsterblichkeit. Solange die sich in den verwüsteten Landen aufhalten, können sie nicht getötet werden, und auch ihre Lebensspanne ist bis zum Ende aller Tage ausgedehnt.","Doch einen gibt es, der Euch vielleicht weiterhelfen kann, <Name>. Reist nach Azshara und sucht dort nach dem Dämonenjäger Loramus Thalipedes. Ihr findet ihn vermutlich auf den Inseln."}, {"Sprecht mit Loramus Thalipedes in Azshara."}},
[2745] = {"Eindringen ins Schloss", {"Hmm, eine interessante Geschichte und eine, die man nicht auf die leichte Schulter nehmen sollte. Ja, ich kann Euch helfen, aber wenn Ihr das wirklich bis zum Ende durchziehen wollt, dann müsst Ihr andere Saiten aufziehen, die... nicht ganz legal sind.","Lescovar und Marzon müssen erledigt werden, und zwar schnell. Glaubt Ihr, Ihr seid zu so was fähig?","Die Entscheidung liegt bei Euch. Falls ja, dann geht so schnell wie möglich ins Schloss und redet dort mit einem Freund von mir, einem Gnom namens Tyrion. Er beobachtet Lescovar jetzt schon seit ein paar Wochen. Er sollte Euch dabei helfen können, die Sache zu Ende zu bringen."}, {"Sprecht in Schloss Stormwind mit Tyrion."}},
[2746] = {"Nicht unwichtige Gegenstände", {"Trias will also alles ein bisschen beschleunigen, wie? Na gut, können wir zwar machen, aber ich bin noch nicht bereit. Und damit eines mal klar ist: Ich hab den anstrengenderen Job, aber Ihr seid <derjenige, der/diejenige> die; sich die Hände schmutzig macht, verstanden? Gut.","Also, Ihr müsst mir ein paar Sachen besorgen, bevor ich meinen Plan in die Tat umsetzen kann. Ich hab schon die perfekte Verkleidung, um den Spionagebot an den Wachen vorbeizubekommen.","Bringt mir etwas Seide und ein paar Äpfel. Wo Ihr die Seide herkriegt, ist Eure Sache, aber auf Claras kleinem Hof draußen vor der Stadt gibt es Äpfel in genau der richtigen Größe."}, {"Bringt 3 Stück Seidenstoff und 2 frische Äpfel von Clara nach Stormwind zu Tyrion."}},
[2747] = {"Ein außergewöhnliches Ei", nil, nil},
[2748] = {"Ein gutes Ei", nil, nil},
[2749] = {"Ein gewöhnliches Ei", nil, nil},
[2750] = {"Ein schlechtes Ei", nil, nil},
[2751] = {"Barbarische Waffenschmiedekunst", {"Zwanzig Generationen von Omoshs haben diesen Hammer bereits in Händen gehalten, <Name>. Hunderttausend Tonnen Metall haben wir geformt. Doch leider, leider endet alles mit Orokk, dem Fehlschlag der Omosh: ohne Verwandte und nicht in der Lage, sich fortzupflanzen.","Ich kann nicht zulassen, dass die geheimen Rezepturen der Omosh mit mir sterben! Jemand muss das Werk fortführen, ganz gleich, ob derjenige ein Omosh ist oder nicht!","Beweist Orokk, dass Ihr fähig seid, <Name>. Bringt Orokk die folgenden Dinge: zwei gemusterte bronzene Armschienen, zwei bronzene Großschwerter und zwei scharfe Klauen. Bringt sie Orokk, dann machen wir weiter."}, {"Bringt 2 gemusterte bronzene Armschienen, 2 bronzene Großschwerter und 2 scharfe Klauen zu Orokk Omosh in Orgrimmar."}},
[2752] = {"Über Eisenschulterstücke", {"Orokk sieht den Eifer in Euren Augen, <Name>. Wie ein Schwamm saugt Ihr das Wissen der Omoshs auf.","Drei Lektionen werde ich Euch lehren.","Kehrt zum Amboss zurück, schwitzt in der Hitze der Schmiede und stellt für Orokk vier bronzene Streitäxte und vier bronzene Kriegshämmer her. Erst dann könnt Ihr etwas über Eisenschulterstücke lernen."}, {"Bringt 4 bronzene Streitäxte und 4 bronzene Kriegshämmer zu Orokk Omosh in Orgrimmar."}},
[2753] = {"In den Boden getrampelt", {"Nur zwei Lektionen noch hat Orokk für Euch, <Name>. Eure Muskeln schmerzen, ich weiß, doch macht weiter - für die Omoshs!","Bringt Orokk vier grüne Eisenhelme, vier grüne Eisenarmschienen und zwei grüne Eisengamaschen - zur Belohnung werdet Ihr lernen und Eure Feinde werden leiden!"}, {"Bringt 4 grüne Eisenhelme, 4 grüne Eisenarmschienen und 2 grüne Eisengamaschen zu Orokk Omosh in Orgrimmar."}},
[2754] = {"Hörner der Raserei", {"Jetzt lehrt Orokk Euch die letzte Lektion, <Name>. Zurück zum heiligen Amboss und der heiligen Schmiede mit Euch! Bringt Orokk zwei robuste Eisenschlägel, zwei bronzene Stiefel mit Versilberung und zwei bronzene Stulpen mit Versilberung.","Seht die Furcht in den Augen der Feinde, wenn Ihr Euch nähert!"}, {"Bringt 2 robuste Eisenschlägel, 2 bronzene Stiefel mit Versilberung und 2 bronzene Stulpen mit Versilberung zu Orokk Omosh in Orgrimmar."}},
[2755] = {"Freude der Omoshs", {"Orokk weint nicht, <Name>. Orokk hat Ruß ins Auge bekommen.","<Orokk wischt eine Träne weg.>","Noch ein letztes Familiengeheimnis der Omosh wird Orokk Euch beibringen. Dank <Name> wird Orokk glücklich sterben.","Lernt den Freudentanz der Omoshs... und dann lernt Ihr Eisenfaust!"}, {"Seht zu und lernt den Freudentanz der Omoshs."}},
[2756] = {"Die alten Traditionen", {"Früher, zur Zeit der Legenden, zerquetschte Aturk die Schädel seiner Feinde und zermahlte die Knochen zu einem feinen Pulver, das er zu Fasern verarbeitete. Diese Tage sind lange vergangen, <Volk>. Der Kriegshäuptling verlangt Frieden unter den Klans. Alte Feinde werden nur noch misstrauisch beäugt!","Es ist unglaublich, ich weiß, doch es ist nun einmal Thralls Wille.","Aber genug gejammert! Ihr seid zum Lernen hier, und Ihr werdet etwas lernen! Bringt mir vier Stahlbrustplatten und vier Stahlhelme, dann bekommt Ihr den Geschmack des Krieges zu kosten!"}, {"Bringt 4 Stahlbrustplatten und 4 Stahlplattenhelme zu Aturk dem Amboss in Orgrimmar."}},
[2757] = {"Auf nach Booty Bay!", {"Hört mal, wenn Ihr hier herumhängt, lernt Ihr nix.","Ich kenn da so 'n Typen, der 'n Typen kennt. Der treibt sich bei der Schmiede in Booty Bay rum. Wenn Ihr ihm die Insignie hier zeigt, dann erzählt er Euch alles über Mithril.","Also, was sagt Ihr? Überlasst diesen Babykram den Kleinkindern, <Name> - für Euch ist es Zeit weiterzuziehen.","Falls Ihr Interesse haben solltet, der Typ heißt McGavan. Zeigt ihm das hier, dann wird er sehen, dass es Euch ernst ist."}, {"Sprecht mit McGavan in Booty Bay."}},
[2758] = {"Die Herkunft der Schmiedekunst", {"Als ich etwa so alt war wie Ihr, nahm mich ein Zwerg namens Galvan unter seine Fittiche und bildete mich in der Kunst des Rüstungsschmiedens aus. Bevor er sich nach Booty Bay aufmachte, nahm er mir das Versprechen ab, dass ich all denjenigen ein Lehrer sein würde, die den Wunsch hätten und entschlossen genug wären, selbst eine Schmiedelaufbahn einschlagen zu wollen. Und hier bin ich nun - all die Jahre später.","Also, es ist jetzt Zeit für Euch, ein bisschen was zu lernen. Stellt mir sechs goldene Schuppenarmschienen her, dann zeige ich Euch, was echte Schmiedekunst ist, <Name>."}, {"Bringt 6 goldene Schuppenarmschienen zu Hank dem Hammer in Stormwind."}},
[2759] = {"Auf der Suche nach Galvan", {"Obwohl Galvan Booty Bay schon vor vielen Jahren verlassen hat, leben noch immer Mitglieder des Ordens in der Stadt und hoffen, dass sie eines Tages jemanden finden werden, der der Lehre von Galvan würdig ist.","Nehmt diese Insignie und zeigt sie McGavan in Booty Bay.","Solltet Ihr es tatsächlich bis dorthin schaffen, dann weiß er, dass Ihr würdig seid. Ich glaube an Euch, <Name>!"}, {"Sprecht in Booty Bay mit McGavan."}},
[2760] = {"Der Mithrilorden", {"Die meisten unserer Rekruten schaffen es nicht bis Booty Bay, <Name>. Der Glanz der Insignie reicht normalerweise aus, um selbst im zuverlässigsten Schmied die Beutegier ausbrechen zu lassen. Narren, alle zusammen, denn die Insignie ist nichts als Talmi im Vergleich zum Wert der Gegenstände, die Ihr zu sehen bekommen werdet!","Findet Galvan den Alten und zeigt ihm diese Anstecknadel. Er lebt weit im Norden, zwischen Zul'Gurub und dem Ogerhügel der Mosh'Ogg."}, {"Sprecht mit Galvan dem Alten im Schlingendorntal."}},
[2761] = {"Hier verhütten, da verhütten", {"Viele Schmiede glauben, der Bergbau und die Schmiedekunst seien zwei völlig getrennte, voneinander unabhängige Dinge, aber wie kann es Schmiedekunst geben ohne Bergbau? Genau, das ist völlig unmöglich! Und daher muss der vollendete Schmied auch ein tüchtiger Minenarbeiter sein.","Zeigt Galvan, dass Ihr fähig seid, <Name>. Bringt mir vierzig Barren Eisen und vierzig Barren Mithril. Dafür sagt Galvan Euch dann alles über verschnörkelte Mithrilhosen!"}, {"Bringt Galvan dem Alten im Schlingendorntal 40 Mithrilbarren und 40 Eisenbarren."}},
[2762] = {"Der große silberne Betrüger", {"Es heißt, dass Echtsilber noch stärker und haltbarer sei als Mithril. Galvan widerspricht dieser Einschätzung der Mineralien zwar nicht, aber Echtsilber trägt seinen Namen zu Unrecht. Echtsilber ist nicht echt! Mithril ist ein wesentlich edleres Mineral! HA! Aber wie dem auch sei ... für diese Lektion müsst Ihr den silbernen Betrüger finden, <Name>.","Bringt mir vierzig Barren Mithril und fünf Barren Echtsilber. Im Gegenzug werdet Ihr in das Geheimnis der verschnörkelten Mithrilhandschuhe eingeführt, und, was noch wichtiger ist, Ihr werdet lernen, dass Echtsilber lügt!"}, {"Bringt Galvan dem Alten im Schlingendorntal 40 Mithrilbarren und 5 Echtsilberbarren."}},
[2763] = {"Die Kunst der Magieerfüllung", {"Der Abbau von wertvollen Edelsteinen ist für unsere Zunft von äußerster Wichtigkeit, <Name>. Und nur wenige Edelsteine sind wertvoller als der Citrin. Die magischen Eigenschaften, mit denen wir unsere Waren ausstatten, werden deutlich verstärkt, wenn Citrine im Spiel sind.","In dieser Lektion verstärken wir vierzig Barren Mithril mit vier Citrinen. So bekommt Ihr ein besseres Verständnis für die magische Edelsteinkunde und lernt außerdem, wie man verschnörkelte Mithrilschultern herstellt."}, {"Bringt Galvan dem Alten im Schlingendorntal 40 Mithrilbarren und 4 Citrine."}},
[2764] = {"Galvans bester Schüler", {"Wenn Ihr bereit seid, Eure Ausbildung bei Galvan abzuschließen, dann solltet Ihr nach Gadgetzan reisen.","Dort findet Ihr meinen besten Schüler, Trenton Lighthammer. Er kann Euch viel über verschnörkeltes Mithril beibringen. Ansonsten bleibt hier und lernt weiter!","Ach ja, Gadetzan liegt natürlich in Tanaris!"}, {"Sprecht in Gadgetzan mit Trenton Lighthammer."}},
[2765] = {"Schmiedkunstexperte!", {"Schon als Ihr das erste Mal zu mir kamt, wusste ich, dass Ihr Euch als einer meiner besten Schüler herausstellen würdet! Ohne zu zögern trenntet Ihr Euch von einem nicht recht mächtigen Gegenstand und gabt ihn McGavan. Ihr tatet dies, nur damit Ihr mich treffen konntet. Solch blindes Vertrauen und Hingabe an unser Handwerk soll nicht unbelohnt bleiben.","Ihr seid zu einem wahren Experten der Schmiedekunst geworden, <Name>. Nun erntet den Lohn dafür!"}, {"Fordert Eure Belohnung von Galvan dem Alten ein!"}},
[2766] = {"Findet OOX-22/FE!", {"Ihr habt ein merkwürdiges Gerät aus Metall entdeckt, das die Form eines Eis hat. Als Ihr einen der Knöpfe berührt, erwacht das Ei zu Leben und verwandelt sich in eine Art Gnom-Roboter-Apparat! Aus dem Inneren des Roboteis erklingt eine rasselnde Stimme.","\"Mein Name ist Oglethorpe Obnoticus und mein Leitroboter ist abgestürzt! Ich werde Euch belohnen, wenn Ihr ihn aufspürt. Bringt dem Roboter bitte diesen Signalgeber!\"","\"Ich habe jetzt die Koordinaten des Roboters für Euch berechnet. Er ist in der Nähe des Wildschrammtals abgestürzt!\""}, {"Bringt das Notrufsignal zu Oglethorpes Leitroboter in einer Höhle in der Nähe des Wildschrammtals."}},
[2767] = {"Die Rettung von OOX-22/FE", {"Aus dem Roboter ertönt erneut die blecherne Stimme des Gnoms:","\"Ich muss OOX-22/FE an einen offenen, sicheren Ort schaffen, wo er die langwierige Startprozedur beginnen kann. Er hat eine eingebaute Tarnvorrichtung, aber ich brauche etwas Zeit, um sie von hier aus wieder zum Funktionieren zu bringen. Eskortiert den Roboter von seinem momentanen Standort zu, ach, sagen wir mal, dem Dock an der Vergessenen Küste. Das sollte der perfekte Ort für mein Vorhaben sein, und bis Ihr dort seid, sollte ich auch alles vorbereitet haben und online gehen können.\"","\"Eskortiert ihn sicher bis zum Dock und kommt dann zu mir nach Booty Bay! Oglethorpe Obnoticus - Ende!\""}, {"Eskortiert OOX-22/FE zum Dock an der Vergessenen Küste und meldet Euch dann bei Oglethorpe Obnoticus in Booty Bay."}},
[2768] = {"Wünschel-mato-Rute", {"Sergeant Bly hat mich bestohlen! Er sagte, er wolle sie sich nur ausleihen, aber er hat meine geliebte Wünschel-mato-Rute gestohlen! Wo soll ich denn bitte ohne diese Rute wissen, wo ich neue Wasserlöcher graben muss?","Findet Bly und bringt mir meine Rute! Ich habe gehört, er hätte seine Bande von zwielichtigen Abenteurern auf eine Schatzsuche nach Zul'Farrak im Westen geführt. Ich möchte wetten, dort findet Ihr ihn.","Hoffen wir, dass die Trolle ihn schon erledigt haben, denn falls Ihr Euch wegen der Rute mit ihm anlegen müsst, dann steht Euch ein schwerer Kampf bevor."}, {"Bringt die Wünschel-mato-Rute nach Gadgetzan zu Chefingenieur Bilgewhizzle."}},
[2769] = {"Die Brüder Brassbolt", {"Seid Ihr den Gebrüdern Brassbolt schon mal begegnet? Sie sind drüben in der schimmernden Ebene, in Kalimdor, um genau zu sein, und treten dort in Raketenwagenrennen gegen die Goblins an. Normalerweise gewinnen sie, aber das ist keine große Überraschung - der Wagen der Goblins fliegt so oft in die Luft, dass er es kaum jemals über die Ziellinie schafft!","Aber trotzdem suchen die Brassbolt-Brüder ständig nach neuen Möglichkeiten, ihren Wagen noch schneller zu machen, und Wizzle hat mir gesagt, dass er für sein nächstes Experiment noch eine sehr seltene Komponente benötigt.","Wahrscheinlich kommt er an dieses Ersatzteil nicht allein ran. Sprecht mit ihm, dann erfahrt Ihr mehr darüber."}, {"Sprecht mit Wizzle Brassbolts in der schimmernden Ebene."}},
[2770] = {"Gahz'rilla", {"Tief im Inneren von Zul'Farrak, der Stadt der Sandtrolle in Tanaris, liegt ein hochheiliger Teich. An diesem Teich beschwören die Trolle eine gewaltige Bestie, Gahz'rilla! Der ist so wild, dass sogar seine Schuppen vor Energie knistern. Und genau diese Energie will ich in meinem Wagen einsetzen.","Bringt mir die energiegeladene Schuppe von Gahz'rilla!","Wie man Gahz'rilla allerdings beschwört, das verraten die Trolle niemandem. Wenn Ihr ihm begegnen wollt, müsst Ihr daher erst den Trollen das Geheimnis entreißen."}, {"Bringt Wizzle Brassbolts in der schimmernden Ebene Gahz'rillas energiegeladene Schuppe."}},
[2771] = {"Ein schlauer Kopf auf den Schultern", {"Es scheint, als habe der Meister dies von Anfang an geplant. Ich habe nicht gelernt, solche Gegenstände herzustellen, wie Ihr es könnt, und Ihr habt nicht gelernt, solche Gegenstände herzustellen, wie ich es kann.","Der Meister ist klug, <Name>. Wir werden uns gegenseitig etwas beibringen!","Zeigt mir, wie man eine Mithrilhelmkappe und verschnörkelte Mithrilschultern herstellt, dann zeige ich Euch, wie man einen verschnörkelten Mithrilhelm macht."}, {"Bringt Trenton Lighthammer 2 Mithrilhelmkappen und 1 verschnörkelte Mithrilschulter."}},
[2772] = {"Die Welt zu Euren Füßen", {"Bei der Talmeisterschaft der Schmiedekunst letztes Jahr mussten alle, die zusahen, erleben, wie mir so ein feiger Hund von der Gießerei der Venture Company die Beine unter dem Körper wegzog. Aber den Grund, warum ich weitermachen und die Meisterschaft am Ende gewinnen konnte, den wusste niemand. Genau, verschnörkelte Mithrilstiefel!","Aber für die Meisterschaft dieses Jahr muss ich lernen, wie man verschnörkelte Mithrilhosen fertigt, denn ich muss gegen einen Schredder der Venture Co. antreten! Bringt mir bei, wie man die Hosen anfertigt, und lernt, was es mit den Stiefeln auf sich hat!"}, {"Bringt Trenton Lighthammer 2 schwere Mithrilstiefel und 1 verschnörkelte Mithrilhose."}},
[2773] = {"Mithril-Kid", {"Hier in der Gegend bin ich als Mithril-Kid bekannt. Ich beliefere Abenteurer aus aller Welt mit verschnörkeltem Mithril.","<Trenton flüstert.>","Die wissen aber nicht, dass ich bisher noch keine verschnörkelten Mithrilhandschuhe herstellen kann. Psst! Das bleibt unser kleines Geheimnis, ja?","Bringt mir bei, wie man verschnörkelte Mithrilhandschuhe fertigt, <Name>, dann zeige ich Euch im Gegenzug den Höhepunkt meiner Ausbildung unter Galvan: die verschnörkelte Mithrilbrustplatte."}, {"Bringt Trenton Lighthammer 2 schwere Mithrilbrustplatten und 1 verschnörkelten Mithrilhandschuh."}},
[2781] = {"GESUCHT: Kalif Skorpidstich", {"GESUCHT: Kalif Skorpidstich!","Die Gadgetzan Water Company aus Gadgetzan in Tanaris ist bereit, eine hohe Belohnung für den Kopf von Kalif Skorpidstich, dem Anführer der Wüstenläufernomaden, zu zahlen. Seine Verbrechen belaufen sich auf:","1. Ermordung von Angestellten der Gadgetzan Water Company!","2. Stehlen von Rohstoffen der Gadgetzan Water Company!","3. Besetzen von Eigentum der Gadgetzan Water Company!","Bringt Beweise für Kalif Skorpidstichs Untergang zu Chefingenieur Bilgewhizzle und Ihr werdet umgehend belohnt!"}, {"Bringt Chefingenieur Bilgewhizzle in Gadgetzan den Kopf von Kalif Skorpidstich."}},
[2782] = {"Rin'jis Geheimnis", {"Die Tafel ist mit Schriftzeichen der Witherbarktrolle bedeckt. Rin’jis Geheimnis ist wirklich ein Mysterium...","Gut möglich, dass Ihr hier etwas ziemlich Wertvolles in Händen haltet. Andererseits könnte ebenso gut das Gegenteil der Fall sein. Ein Troll der Witherbark in Not würde wahrscheinlich alles tun, um einen Ausweg aus seinen Problemen zu finden..."}, {"Oran Snakewrithe, eine bekannte Gelehrte auf dem Gebiet der Witherbarktrolle, könnte die Tafel vielleicht übersetzen. Normalerweise hält sie sich im Magierviertel von Undercity auf."}},
[2783] = {"Kleinliches Gezänk", {"Wir bekämpfen den falschen Feind, <Name>.","Die wahre Macht in diesem Land wird zahlen- und kräftemäßig immer stärker, während wir uns mit den Orcs über verbrannte Erde zanken.","Sollen wir wirklich in diesem Land einen Krieg gegen die Horde führen, nur um eines Tages von den Armeen der Brennenden Legion hinweggefegt zu werden? Es ist sinnlos.","Begrabt Eure Streitigkeiten, <Name>, und sprecht mit dem gefallenen Helden der Horde. Er kennt die Wahrheit über die verwüsteten Lande."}, {"Sprecht mit dem gefallenen Helden der Horde im Grenzgebiet zwischen den verwüsteten Landen und dem Sumpf."}},
[2784] = {"In Ungnade fallen", {"Hört meine Geschichte, <Volk>."}, {"Lauscht der Geschichte des gefallenen Helden der Horde."}},
[2801] = {"Eine Geschichte voller Leid", {"Bevor wir fortfahren können, müsst Ihr meine Geschichte hören."}, {"Lauscht der Geschichte des gefallenen Helden der Horde."}},
[2821] = {"Das Zeichen der Qualität", {"Meine Einstellung zur Lederverarbeitung verlangt, dass die Arbeit von bestmöglicher Qualität zu sein hat - ohne Kompromisse. Jeder, der behauptet, Qualität sei unwichtig, wird auch versuchen, Euch ein Küstengrundstück in Dun Morogh zu verkaufen!","Auf meiner jüngsten Suche nach hochwertigem Material fand ich dicke Yetibälge. Die Yeti sind in Feralas zuhause und viele findet man im Wildschrammtal. Ich hätte gern welche, und wenn Ihr mir zehn davon bringt, dürft Ihr Euch ein beliebiges Stück meiner Lederwaren aussuchen. Haltet Ihr das für angemessen? Dann sind wir im Geschäft."}, {"Bringt Pratt McGrubben in der Festung Feathermoon 10 dicke Yetibälge."}},
[2822] = {"Das Zeichen der Qualität", {"Ich bin stolz auf meine Lederarbeiten und ich glaube, das sieht man ihnen an; Qualität sollte niemals zweitrangig sein. Jeder, der behauptet, Qualität sei unwichtig, wird Euch wahrscheinlich auch glauben machen wollen, dass die Erde keine Geheimnisse enthält!","Auf meiner jüngsten Suche nach hochwertigem Material habe ich dicke Yetibälge entdeckt. Die Yetis sind in Feralas beheimatet und man findet sie häufig westlich des Wildschrammtals. Ihr dürft Euch ein beliebiges Stück meiner Lederwaren aussuchen für einen Zehnerstapel Yetibälge. Wenn das für Euch akzeptabel ist, dann sind wir im Geschäft!"}, {"Bringt Jangdor Swiftstrider im Camp Mojache 10 dicke Yetibälge."}},
[2841] = {"Maschinenkriege", {"Vor zwei Jahren noch hat man sich jedesmal darüber lustig gemacht, wenn die Rede auf Gnomentechnologie kam. Diese Zeiten haben sich definitiv geändert. Jetzt haben sie eine neue Maschine gebaut, die besser ist als all unsere Schreddermodelle zusammen. Ich brauche die Blaupausen dafür, <Name>!","Dummerweise sind die Pläne in einem Safe bei Robogenieur Thermaplugg sicher verwahrt. Was die Sache noch erschwert: Nur Robogenieur Thermaplugg kennt die Kombination zu diesem Safe.","Tut, was Ihr tun müsst, um diese Kombination zu erlangen, <Name>. Dann öffnet den Safe und bringt mir diese Blaupausen!"}, {"Besorgt die Maschinenblaupausen und Thermapluggs Safekombination aus Gnomeregan und bringt sie zu Nogg nach Orgrimmar."}},
[2842] = {"Chefingenieur Scooty", {"In dem Moment, als wir durch Kernobee von der neuen Gnomen-Maschine hörten, haben Scooty und ich mit der Arbeit am Transpolyporter 6000 begonnen: dem schnellsten und verlässlichsten Weg nach Gnomeregan.","Endlich ist er bereit, Passagiere aufzunehmen, <Name>. Wenn Ihr es einmal versuchen wollt, geht nach Booty Bay und redet mit Scooty am Windreiterhorst. Um nach Booty Bay zu gelangen, nehmt das Boot von Ratchet aus.","Natürlich könnt Ihr auch versuchen, Euch durch die von Zwergen verseuchten Berge von Dun Morogh nach Gnomeregan durchzuschlagen!"}, {"Sprecht mit Scooty in Booty Bay."}},
[2843] = {"Gnomer-weeeeg!", {"Solange Ihr den Transponder bei Euch habt, müsst Ihr lediglich auf diese Plattform hier treten und Ihr werdet nach Gnomeregan teleportiert.","Ihr werdet vielleicht ein paar ungewöhnliche Nebenwirkungen erleben, aber keine davon sollte dauerhaft sein... zumindest nicht mehr.","Ich hätte fast den wichtigsten Teil vergessen - die Rückkehr. Wenn Ihr wieder zurückkommen wollt, tretet einfach auf die Transpolyporter-Plattform in Gnomeregan!","Jetzt lasst uns diesen Transponder auf Euer Gewicht und Eure Größe einstellen."}, {"Wartet, bis Scooty den Goblin-Transponder kalibriert hat."}},
[2844] = {"Der Riesenwächter", {"Ich bin so unfähig, <Name>. Ich sollt' auf meine kleine Freundin, Shay, aufpassen, aber irgendwie ist sie mir verlor'n gegangen. Ich kann nicht genau sagen, was passiert ist, ehrlich, aber als ich sie das letzte Mal gesehen habe, lief’n wir durch die Ruinen direkt nördlich von hier.","Alles war wunderbar, aber dann hab ich mich umgedreht, un’ ... sie war weg.","Und jetz' hab ich keine Ahnung, was ich tun soll. Habt Ihr 'ne Idee, <Name>? Ich mache mir furchtbare Sorgen.","Hey, wie wär’s, wenn Ihr nach Shay suchen würdet? Eure Beine sind viel schneller als meine."}, {"Findet das verlorene Nachtelfen-Mädchen."}},
[2845] = {"Streunende Shay", {"Glaubt Ihr, Ihr könnt mir helfen, meinen Weg zurück zu meinem Freund, Felsbeißer, zu finden? Er sollte auf mich aufpassen... Aber ich neige dazu, gelegentlich davonzuschlendern. Es gibt immer so viele Dinge zu sehen und ich lasse mich dann ablenken.","Es gibt allerdings etwas, was immer meine Aufmerksamkeit erregt.","In dieser Truhe findet Ihr eine Glocke, die mir sehr teuer ist. Ich liebe einfach ihren Ton. Ihr habt mein Wort: Wenn ich davonschlendere, müsst Ihr lediglich damit klingeln und ich komme angelaufen."}, {"Nehmt Shays Glocke aus der Truhe.","Begleitet Shay Leafrunner zu Felsbeißers Lager."}},
[2846] = {"Tiara der Tiefen", {"Vor langer Zeit besaß ich ein wunderschönes Schmuckstück, die Tiara der Tiefen. Diese war nicht nur schön, sondern enthielt auch große Macht für diejenigen, die damit umzugehen wussten.","Als nun die Kunde von der Tiara die Wasserbeschwörerin Velratha erreichte, musste sie sie haben. Sie schickte Handlanger in mein Haus und die stahlen sie, während ich weg war. Diese Diebe!","Ich will meine Tiara wiederhaben! Geht nach Zul'Farrak, der Trollstadt in Tanaris, findet Velratha und ringt ihr die Tiara ab. Bringt sie mir zurück und Ihr steht in meiner Gunst."}, {"Bringt die Tiara der Tiefen zu Tabetha in den Marschen von Dustwallow."}},
[2847] = {"Wildlederrüstung", {"<Name>, Ihr werdet ziemlich gut als Lederer! Ich kenne da einige Muster, an denen Ihr vielleicht interessiert seid... Wildlederrüstung! Wildleder zieht Kraft aus den mächtigen und chaotischen Eigenschaften von Wildranken, die man in Gegenden mit üppiger Vegetation auf einigen der Kreaturen dort findet. Die daraus gefertigte Rüstung ist garantiert das Beste, was jemand Eures Geschicks herstellen kann.","Ich werde es Euch beibringen, aber Ihr werdet dafür eine Weile für mich arbeiten. Das Lehrgeld beträgt nur 10 Stücke dickes Leder. Danach reden wir über Details."}, {"Bringt Pratt McGrubben in der Festung Feathermoon 10 Stücke dickes Leder."}},
[2848] = {"Wildlederschultern", {"Wildlederschultern verstärken den Körper des Trägers unglaublich gut und bieten dabei erstklassigen Schutz für die Oberarme und die Schultern. Wie bei allen Wildlederrüstungen wird auch hier eine mächtige, allerdings zufällige Verbesserung erzeugt, wenn die Wildranke eingearbeitet wird.","Für dieses Muster werde ich 6 dicke Rüstungssets und eine Wildranke benötigen. Das sollte für Euch kein allzu schwer zu erfüllender Auftrag sein."}, {"Bringt Pratt McGrubben in der Festung Feathermoon 6 dicke Rüstungssets und eine Wildranke."}},
[2849] = {"Wildlederweste", {"Wildlederwesten sind die Grundlage aller Wildlederrüstungssets. Das Gerüst der Weste verbindet sich mit der Wildranke, um ein Produkt zu erzeugen, das sowohl maximalen Schutz für den Torso bietet als auch der mächtigen Magie erlaubt, sich in dem Gewand auszubreiten.","Für dieses Muster werde ich 2 Schildkrötenschuppenbrustplatten, 2 Paar Schildkrötenschuppenhandschuhe und eine Wildranke benötigen. Das ist ein großer Auftrag, aber sicher keiner, bei dessen Erfüllung Ihr Schwierigkeiten haben werdet."}, {"Bringt Pratt McGrubben in der Festung Feathermoon 2 Schildkrötenschuppenbrustplatten, 2 Paar Schildkrötenschuppenhandschuhe und eine Wildranke."}},
[2850] = {"Wildlederhelm", {"Ein Wildlederhelm bietet dem Träger verbesserten Kopfschutz, ohne dabei an der Bequemlichkeit zu sparen. Wie bei allen Wildlederrüstungen erzeugt die eingearbeitete Wildranke eine mächtige, aber zufällige Verbesserung der Struktur des Helms. Lederträger werden dieses Stück auf jeden Fall haben wollen!","Für dieses Muster brauche ich 2 Tuniken des Nachtschleichers, 2 Stirnbänder des Nachtschleichers und eine Wildranke. Erfüllt diesen Auftrag und das Muster gehört Euch!"}, {"Bringt Pratt McGrubben in der Festung Feathermoon 2 Tuniken des Nachtschleichers, 2 Stirnbänder des Nachtschleichers und eine Wildranke."}},
[2851] = {"Wildlederstiefel", {"Ihr habt schon eine ganze Menge gelernt, <Name>; dennoch gibt es noch mehr für Euch zu lernen. Als Nächstes sind Wildlederstiefel an der Reihe, der Grundstein eines jeden Sets.","Diese Stiefel werden so gefertigt, dass sie den Fuß in Bequemlichkeit hüllen, während sie ihn gleichzeitig gegen die Gefahren des Abenteuerlebens wappnen. Die weichen Sohlen sind ein Segen für diejenigen, die bei ihrer Fußbekleidung auf leise Bewegung Wert legen.","Für dieses Muster müsst Ihr mir 2 Hosen des Nachtschleichers und 2 Stiefel des Nachtschleichers besorgen. Ich werde außerdem zwei Wildranken benötigen, nicht nur eine. Wenn Ihr all das beisammen habt, gebt mir Bescheid."}, {"Bringt Pratt McGrubben in der Festung Feathermoon 2 Hosen des Nachtschleichers, 2 Stiefel des Nachtschleichers und 2 Wildranken."}},
[2852] = {"Wildledergamaschen", {"Jetzt, nachdem Ihr die Grundlagen beherrscht, seid Ihr bereit, Euch komplexeren Dingen zuzuwenden, <Name>. Wildledergamaschen gehören zur robustesten und stärksten Lederausrüstung überhaupt. Bauch, Hüften, Schenkel und Solarplexus werden ungeheuer gut geschützt. Und wenn dann noch die Verstärkung hinzukommt, die die Wildranke bewirkt, werden Euch die Abenteurer, die diese Rüstung besitzen, jedes Mal danken, wenn sie Euch sehen.","Für dieses Muster müsst Ihr mir 2 Schildkrötenschuppenhelme, 2 Schildkrötenschuppenarmschienen und 2 Wildranken besorgen."}, {"Bringt Pratt McGrubben in der Festung Feathermoon 2 Schildkrötenschuppenhelme, 2 Schildkrötenschuppenarmschienen und 2 Wildranken."}},
[2853] = {"Meister des Wildleders", {"Nun, <Name>, Ihr gebt dem Wort Sorgfalt eine neue Bedeutung. Es steht nur noch ein Wildlederrüstungsmuster aus, denn Ihr habt alle anderen gemeistert. Ich werde nicht von Euch verlangen, Euch unnötig für dieses Muster abzuplagen. Ihr habt es Euch bereits mehr als verdient... Aber ich kann es Euch nicht selbst geben. Mein Lehrer, derjenige, der mich in dieses Handwerk einführte, muss Euch belohnen.","Bringt diesen Brief zu Telonis. Er lehrt Lederverarbeitung in Darnassus. Zeigt ihm den Brief und lasst Euch von ihm als Gleichgestellten anerkennen, so wie ich das auch tue."}, {"Gebt Telonis, dem Lederverarbeitungsmeister von Darnassus, Pratts Brief."}},
[2854] = {"Wildlederrüstung", {"<Name>, Euer Ruf als Lederer eilt Euch voraus. Ich besitze Wissen über die Lederverarbeitung, das zu erwerben weise von Euch wäre... Wildlederrüstung! Wildleder zieht Kraft aus den mächtigen und chaotischen Eigenschaften von Wildranken, die man in Gegenden mit üppiger Vegetation auf einigen der Kreaturen dort findet. Die daraus gefertigte Rüstung ist garantiert das Beste, was ein Handwerker Eures Geschicks herstellen kann.","Ich werde es Euch beibringen, aber Ihr werdet dafür eine Weile für mich arbeiten. Das Lehrgeld beträgt nur 10 Stücke dickes Leder. Danach reden wir über Details."}, {"Bringt Jangdor Swiftstrider im Camp Mojache 10 Stücke dickes Leder."}},
[2855] = {"Wildlederschultern", {"Wildlederschultern verstärken den Körper des Trägers unglaublich gut und bieten dabei besten Schutz für Oberarme und Schultern. Wie bei allen Wildlederrüstungen wird auch hier eine zufällige, aber magieerfüllte Verbesserung erzeugt, wenn die Wildranke eingearbeitet wird.","Für dieses Muster werde ich 6 dicke Rüstungssets und eine Wildranke benötigen. Das sollte für Euch kein allzu schwer zu erfüllender Auftrag sein."}, {"Bringt Jangdor Swiftstrider im Camp Mojache 6 dicke Rüstungssets und eine Wildranke."}},
[2856] = {"Wildlederweste", {"Wildlederwesten sind die Grundlage jeglicher Wildlederrüstungssets. Die Struktur der Weste und das Wildrankengeflecht darin erzeugen ein Produkt, das sowohl maximalen Schutz für den Torso bietet, als auch der mächtigen Magie erlaubt, sich in dem Gewand auszubreiten.","Für dieses Muster werde ich 2 Schildkrötenschuppenbrustplatten, 2 Paar Schildkrötenschuppenhandschuhe und eine Wildranke benötigen. Dies ist keine einfache Lektion, aber eine, für die Ihr sicherlich das nötige Geschick habt."}, {"Bringt Jangdor Swiftstrider im Camp Mojache 2 Schildkrötenschuppenbrustplatten, 2 Schildkrötenschuppenhandschuhe und eine Wildranke."}},
[2857] = {"Wildlederhelm", {"Ein Wildlederhelm bietet dem Träger verbesserten Kopfschutz, ohne dabei an der Bequemlichkeit zu sparen. Wie bei allen Wildlederrüstungen erzeugt die eingearbeitete Wildranke eine mächtige, aber zufällige magische Verstärkung der Struktur des Helms. Lederträger werden dieses Stück auf jeden Fall haben wollen!","Für dieses Muster brauche ich 2 Tuniken des Nachtschleichers, 2 Stirnbänder des Nachtschleichers und eine Wildranke. Schließt diese Lektion erfolgreich ab und das Muster gehört Euch!"}, {"Bringt Jangdor Swiftstrider im Camp Mojache 2 Tuniken des Nachtschleichers, 2 Stirnbänder des Nachtschleichers und eine Wildranke."}},
[2858] = {"Wildlederstiefel", {"Euer Wissen über die Herstellung von Wildlederrüstungen wächst, <Name>, aber trotzdem habt Ihr noch einiges zu lernen.","Wildlederstiefel sind die Grundlage eines jeden Sets. Diese Stiefel werden so gefertigt, dass sie den Fuß in Bequemlichkeit hüllen, während sie ihn gleichzeitig gegen die Gefahren des Abenteurerlebens wappnen. Die weichen Sohlen sind ein Segen für diejenigen, die bei ihrer Fußbekleidung auf leise Bewegung Wert legen.","Für dieses Muster müsst Ihr mir 2 Hosen des Nachtschleichers und 2 Stiefel des Nachtschleichers herstellen. Ich werde außerdem zwei Wildranken benötigen, nicht nur eine. Wenn Ihr diese Dinge habt, werden wir weitermachen."}, {"Bringt Jangdor Swiftstrider im Camp Mojache 2 Hosen des Nachtschleichers, 2 Stiefel des Nachtschleichers und 2 Wildranken."}},
[2859] = {"Wildledergamaschen", {"Ihr seid so weit, komplexere Lektionen zu meistern, <Name>. Wildledergamaschen gehören zur robustesten und stärksten Lederausrüstung überhaupt. Bauch, Hüften, Schenkel und Solarplexus werden ungeheuer gut geschützt. Und wenn dann noch die Verstärkung hinzukommt, die die Wildranke bewirkt, werden Euch die Abenteurer, die diese Rüstung besitzen, jedes Mal danken, wenn sie Euch sehen.","Um Euch dieses Muster zu geben, benötige ich 2 Schildkrötenschuppenhelme, 2 Schildkrötenschuppenarmschienen und 2 Wildranken."}, {"Bringt Jangdor Swiftstrider im Camp Mojache 2 Schildkrötenschuppenhelme, 2 Schildkrötenschuppenarmschienen und 2 Wildranken."}},
[2860] = {"Meister des Wildleders", {"<Name>, Ihr habt alles erlernt, was ich Euch lehren kann. Es gibt allerdings noch ein letztes Wildlederrüstungsmuster. Ich werde nicht von Euch verlangen, Euch unnötig dafür abzuplagen. Ihr habt es Euch bereits mehr als verdient. Jedoch kann ich dieses Muster nicht einfach mit Euch teilen.","Mein Lehrer, derjenige, der mich in dieses Handwerk einführte, muss Euch belohnen. Bringt diese Notiz zu Una. Sie lehrt Lederverarbeitung in Thunder Bluff. Zeigt ihr den Brief und lasst Euch von ihr als Gleichgestellten anerkennen, so wie ich das auch tue."}, {"Gebt Una, der Lederverarbeitungsmeisterin von Thunder Bluff, Jangdors Schreiben."}},
[2861] = {"Tabethas Aufgabe", {"Gerüchte besagen, dass Tabetha, eine Magierkollegin, die tief in den Marschen von Dustwallow lebt, ein Hühnchen mit einem gewissen Sandtroll in Zul’Farrak zu rupfen hat. Wenn Ihr mit Tabetha sprecht, kann sie Euch sicher mehr dazu sagen.","Und nehmt ein paar Freunde mit, <Name>. Falls Tabetha Euch nach Zul’Farrak schickt, wollt Ihr sicher nicht allein hingehen.","Ihr findet Tabethas Hütte westlich von Theramore und direkt nördlich der Stonemaul Ruinen."}, {"Sprecht mit Tabetha in den Marschen von Dustwallow."}},
[2862] = {"Krieg gegen die Waldpfoten", {"Die Gnolle... sie sind mehr als nur ein Ärgernis. Sie sind eine konstante Bedrohung für die Existenz dieses gesamten Lagers! Es ist unwichtig, ob sie Eingeborene des Feralas sind oder nicht. Wenn wir uns nicht gegen sie wappnen, werden wir uns eines Tages aus dem gesamten Gebiet vertrieben sehen.","Die Gnoll-Angriffe auf das Lager wurden in letzter Zeit heftiger. Wir brauchen Eure Hilfe dabei, ihre Anzahl zu reduzieren. Bringt mir zehn ihrer Mähnen und ich werde Euch stattlich für Eure Mühe entlohnen.","Los!"}, {"Bringt Hadoken Swiftstrider im Camp Mojache 10 Mähnen eines Waldpfotengnolls."}},
[2863] = {"Schlag gegen die Alphas", {"Der Plan besteht darin, verschiedene Anführer der Gnolle - Alphas, wie sie sich selbst nennen - in schneller Folge auszuschalten. Wenn wir einen solch schnellen und präzisen Schlag gegen sie erzielen können, würde es in ihren Rängen zu Chaos führen. Vielleicht würde es dann endlich auch in ihren dicken, von Flöhen zerbissenen Köpfen klar, dass wir sie vernichten können und werden!","Ich möchte, dass Ihr der Ausführende meines Willens seid. Eliminiert innerhalb einer Stunde fünf Alphas, wenn das klappen soll, <Name>, sonst bleiben wir in dieser Pattsituation!"}, {"Tötet 5 Alphas der Waldpfoten und kehrt innerhalb einer Stunde zu Hadoken Swiftstrider zurück."}},
[2864] = {"Tran'rek", {"Mein Vetter Tran’rek ist in Schwierigkeiten. Er versprach mir vor einer Woche eine Fuhre Skarabäuspanzerschalen und hat bisher nicht geliefert. Er ist mein Vetter, also würde ich es wirklich hassen, wenn ich jemanden nach Gadgetzan schicken müsste, um ihm etwas zu brechen.","Könnt Ihr für mich mit Tran'rek sprechen? Lasst ihn wissen, dass er diese Lieferung zusammenbekommen muss, bevor ich irgendetwas Unüberlegtes mache.","Gadgetzan liegt im nördlichen Tanaris, in Kalimdor."}, {"Sprecht mit Tran’rek in Gadgetzan."}},
[2865] = {"Skarabäuspanzerschalen", {"Die Skarabäen von Tanaris haben sehr harte Panzerschalen! Hart genug, um sie als Baumaterial für eine Menge Dinge zu verwenden. So viele Dinge!","Tatsächlich sind diese Panzer so nützlich... dass die Skarabäen beinahe bis zur Ausrottung gejagt wurden.","Ich weiß, wo es noch mehr Skarabäen gibt, und wenn Ihr mir versprecht, mir Ihre Panzerschalen zu bringen, sage ich Euch, wo sie sind. Versprochen?","O.k., die Skarabäen haben eine Kolonie in Zul’Farrak. Ich schätze, die Trolle jagen sie nicht wegen ihrer Panzerschalen.","Aber Ihr könnt das tun! Geht nach Zul’Farrak und besorgt mir unbeschädigte Skarabäuspanzerschalen!"}, {"Bringt Tran’rek in Gadgetzan 5 unbeschädigte Skarabäuspanzerschalen."}},
[2866] = {"Die Ruinen von Solarsal", {"Südlich von uns befinden sich die Ruinen von Solarsal. Dies ist eine Gegend aus längst vergangener Zeit. Wir haben die Ruinen ursprünglich erforscht und nichts Besonderes darin gefunden. Daher sind wir friedlich wieder abgezogen.","Vor kurzem haben die Hasskammnaga die Ruinen in alarmierender Anzahl besetzt. Zuerst hielten wir das für die Vorbereitungen auf einen Angriff. Obwohl diese Bedrohung immer noch gegeben ist, glauben wir, dass dort etwas anderes vorgeht.","Geht dorthin, erforscht die Ruinen und achtet auf alles, was Euch ungewöhnlich vorkommt. Viel Glück, <Name>."}, {"Erforscht die Ruinen von Solarsal und findet etwas über die Anwesenheit der Naga dort heraus."}},
[2867] = {"Kehrt zur Festung Feathermoon zurück", {"Mit der Entdeckung des seltsamen Pavillons im Herzen der Ruinen von Solarsal ist es an der Zeit, zur Festung Feathermoon zurückzukehren und General Shandris Feathermoon zu berichten, was hier entdeckt wurde."}, {"Kehrt zu Shandris Feathermoon in der Festung Feathermoon zurück und berichtet von Euren Entdeckungen."}},
[2869] = {"Gegen die Hasskämme", {"Es ist derzeit die beste Handlungsweise, davon auszugehen, dass die Naga, was auch immer sie vorhaben, langfristig versuchen werden, die Insel für sich zu beanspruchen. Wir müssen aktive Schritte unternehmen, um diese Bedrohung auszuschalten, bevor es zu spät ist.","Bringt mir 10 Schuppen der Hasskämme in gutem Zustand. Es dürfte offensichtlich sein, wie man an diese herankommt. Ihr werdet Euch an den Naga das erste Mal Eure Sporen verdienen und wir haben dann eine geschwächte Nagapräsenz auf der Sardor-Insel - beides sind Aussichten, die uns zum Vorteil gereichen."}, {"Bringt Latronicus Moonspear in der Festung Feathermoon 10 Nagaschuppen der Hasskämme."}},
[2870] = {"Gegen Lord Shalzaru", {"<Name>, unsere neuesten Spähberichte haben uns einen guten Einblick in die Kommandokette der Naga erlaubt. In ihren Truppen gibt es viele Adlige und die sind es, die den Ton angeben.","Um genau zu sein, wir haben einen der grausameren Adligen als Lord Shalzaru identifiziert. Späher berichten, dass er höchstpersönlich bei einer kürzlich durchgeführten Nagaausgrabung in einer Höhle südlich der eigentlichen Ruinen, jenseits des Wassers, auf der Insel des Schreckens, ein seltsames Relikt gefunden hat.","Macht dem ein Ende, indem Ihr ihm ein Ende macht, und bringt mir dieses Relikt, damit wir es untersuchen können."}, {"Vernichtet Lord Shalzaru und bringt dann Latronicus Moonspear in der Festung Feathermoon das geheimnisvolle Relikt, das er entdeckt hat."}},
[2871] = {"Überbringung des Relikts", {"Wenn Ihr so nett wärt, das Relikt zu meiner Frau Vestia zu bringen, wüsste ich das zu schätzen. Sie ist nicht nur eine gute Priesterin für unsere Gemeinde, sondern auch eine der versiertesten Gelehrten, die es gibt. Ich könnte wahrscheinlich nicht einmal dann herausfinden, wo an diesem Ding oben oder unten ist, wenn mein Leben davon abhinge. Meine Frau wiederum ... Also wenn irgendwer herausfinden kann, was das ist, dann sie ... Und wir müssen es wissen!","Sie befindet sich gleich um die Ecke, ohne Zweifel am Studieren. Danke, <Name>."}, {"Bringt das geheimnisvolle Relikt zu Vestia Moonspear in der Festung Feathermoon."}},
[2872] = {"Stoleys Schuld", {"Mein alter Kumpel Stoley schuldet mir eine ganz bestimmte Flasche Rum. Er schmeißt die Bar in Steamwheedle in Tanaris, Kalimdor, und er versprach mir, dass der Rum dort stärker ist als alles, was man in Booty Bay finden kann. Er ist so davon überzeugt, dass er sagt, wenn ich diesen Rum probiere und ihm nicht zustimme, würde er das Geld, das er mir schuldet, verdoppeln!","Nun, ich glaube, er ist nicht mehr so zuversichtlich wie damals, als wir zuletzt miteinander geredet haben, und hat jetzt wohl Angst, mir diesen Rum zu geben. Ich lasse eine Wette aber nicht auf sich beruhen, also wie wäre es, wenn Ihr für mich mit ihm redet?"}, {"Sprecht mit Stoley in Steamwheedle."}},
[2873] = {"Stoleys Lieferung", {"Ich schulde \"Seewolf” MacKinley in Booty Bay etwas Rum, um eine Wette zu begleichen, aber mir ist der Rum ausgegangen! Kapitän Cuergo hat meine letzte Lieferung gestohlen und lagert sie in dieser ummauerten Piratenstadt im Süden.","Wenn Ihr meine Rumlieferung finden und zurückbringen könnt, dann kann ich MacKinley beweisen, dass Kalimdors Drinks die aus Booty Bay um Längen schlagen."}, {"Bringt Stoleys Lieferung zu Stoley nach Steamwheedle."}},
[2874] = {"Lieferung für MacKinley", {"Danke nochmals für Eure Hilfe, <Name>. Hier habt Ihr eine Flasche Steamwheedle-Bilge-Rum. Bringt sie zu MacKinley und er wird endlich seine verrückten Ansichten über Drinks aus Booty Bay über Bord werfen können."}, {"Bringt Stoleys Flasche zu \"Seewolf” MacKinley in Booty Bay."}},
[2875] = {"GESUCHT: Andre Feuerbart", {"Der niederträchtige Pirat Andre Feuerbart wird aufgrund seiner gegen Steamwheedle und Gadgetzan gerichteten Verbrechen gesucht.","Durch Erlass der Ratsregierung ist hiermit jeder, der dies liest, zu tödlicher Gewaltanwendung bei der Festnahme von Andre und seinen Spießgesellen autorisiert.","Des Weiteren gilt durch zusätzlichen Erlass, dass, sollte Andre verstorben oder anderweitig während seiner Anhörung unpässlich sein, sein Kopf als Ersatz gilt und die Aushändigung dieses Kopfes an Sicherheitschef Bilgewhizzle als vertragsbindend angesehen wird."}, {"Bringt Sicherheitschef Bilgewhizzle in Steamwheedle Andres Kopf."}},
[2876] = {"Schiffzeitpläne", {"Auf dieser Schriftrolle sind die Zeitpläne für Lieferungen zwischen Steamwheedle und Booty Bay verzeichnet. Sowohl vergangene als auch zukünftige Lieferungen sind aufgeführt sowie Schiffsnamen. Große rote Kreuze befinden sich neben einigen der Schiffsreisen und Anmerkungen wie \"Große Beute\" oder \"Harter Kampf\" neben anderen.","Jemand in Steamwheedle wird dieses Dokument sicher sehen wollen."}, {"Berichtet den Behörden in Steamwheedle von den Schiffzeitplänen."}},
[2877] = {"Skulk Rock-Aufräumen", {"Wir Wildhammers haben die Vergangenheit besser in Erinnerung als unsere Brüder in Ironforge. Wenn Ihr Euch hier beliebt machen wollt, dann müsst Ihr Euch schon Eure Hände für uns schmutzig machen!","Die Horde hat ihre Stellungen im Hinterland bei Skulk Rock aufgegeben. Jetzt wimmelt es dort von üblen Schlämmen und Brühschlammern. Wir haben Pläne für dieses Gebiet, aber zuerst muss uns eine Aufräummannschaft dort eine Bresche hineinschlagen.","Und hier kommt Ihr ins Spiel, Sonnenschein! Geht da runter und erledigt 10 grüne Schlämme und 10 jadegrüne Brühschlammer und meldet Euch dann wieder bei mir. Zieht los!"}, {"Tötet 10 grüne Schlämme sowie 10 jadegrüne Brühschlammer und meldet Euch dann wieder bei Fraggar Thundermantle in Aerie Peak."}},
[2878] = {"Verderbte Liedblume", nil, nil},
[2879] = {"Der Stock von Equinex", {"Es sieht so aus, als könnte dieser Stock der Stock von Equinex sein. Der Stock von Equinex ist in Wirklichkeit ein Schlüssel. Mit ihm kann man den Equinex-Monolithen öffnen, der immer noch in den nördlichen Ruinen von Rabenwind steht.","Findet die vier Feuer, die noch immer in den Ruinen brennen: Samha, Imbel, Byltan und Lahassa. Beschafft Euch deren Essenz. Und dann benutzt diese Essenz, während Ihr vor dem Equinex-Monolithen steht, um den Stock zu aktivieren. Wenn dies wirklich der Stock von Equinex ist, dann könnt Ihr den Monolithen öffnen und ein hochheiliges Artefakt daraus bergen."}, {"Aktiviert Troyas Stock und findet den Equinex-Monolithen."}},
[2880] = {"Kopfgeld für Trollhalsketten", {"Seid Euch darüber im Klaren, <Volk> - wir sind NICHT Teil der Allianz! Die Wildhammer sind unabhängig. Um den Wert einer Person zu beweisen, verlassen sie sich lieber auf Taten und hören nicht auf das Geschwätz derer, die mit ihrem Gequassel versuchen, ihre Untätigkeit zu überspielen.","Wenn Ihr hier Freunde sucht, beweist uns erst Euren Wert! Die üblen Trolle, die die Hänge des Hinterlands überrennen, tragen manchmal Stammeshalsketten. Bringt mir 5 davon und wir werden das als einen Schritt in die richtige Richtung für Euch verbuchen."}, {"Bringt Fraggar Thundermantle in Aerie Peak 5 Troll-Stammeshalsketten."}},
[2881] = {"Kopfgeld für Trollhalsketten", nil, nil},
[2882] = {"Cuergos Gold", {"Diese Karte zeigt ein Stück Strand an einer Südküste. Unter der Karte stehen elegant geschriebene Worte:","Südlich von Wheedle","Dürft Ihr drauf hoffen","Findet Flagge, Mast und Knochen","Grabt dort, wenn Ihr tapfer sein wollt","Grabt und erbt Cuergos Gold!","Einen Schlüssel werdet Ihr brauchen, einen Schlüssel werdet Ihr sehen","Die Mannen dort woll'n als Bezahlung Euer Blut Euch nehm'n."}, {"Findet Cuergos Gold!"}},
[2902] = {"Waldpfotennachforschung", {"Gnolle sind zwar nicht die Hellsten, aber sie werden bestimmt die Strategie ihrer jüngsten Aktivitäten irgendwo dokumentiert haben. Wenn wir diese Pläne finden würden, hätten wir eine bessere Vorstellung davon, was sie so aufbringt.","Ich möchte, dass Ihr zu den Waldpfoten zurückgeht und nach allem sucht, was Ähnlichkeit mit ihren Schlachtplänen haben könnte. Irgendwas muss es geben, ihre Angriffe waren einfach zu gut koordiniert. Findet diese Schlachtpläne und kommt zu mir zurück, sobald Ihr sie habt."}, {"Findet die Gnoll-Schlachtpläne irgendwo in den Gnoll-Lagern südlich vom Camp Mojache."}},
[2903] = {"Die Schlachtpläne", {"Jetzt, nachdem die Schlachtpläne der Gnolle entdeckt wurden, bleibt nur noch, die Pläne zu Hadoken Swiftstrider ins Camp Mojache zurückzubringen. Vielleicht kann er oder irgendjemand dort besser darauf schließen, was die Gnolle vorhaben."}, {"Bringt die Schlachtpläne der Waldpfoten zu Hadoken Swiftstrider ins Camp Mojache."}},
[2904] = {"Eine schöne Bescherung", {"<Kernobee stöhn.t> Was... Was ist passiert?","Das Letzte, an das ich mich erinnere, ist, wie ich von einer Gruppe Dunkeleisenzwerge gefangen, halb tot geprügelt und dann hier zu diesen schmutzigen Troggs geworfen wurde.","Holt mich hier raus, <Name>!","So ist der Plan: Bringt mich zum Ausgang; Wenn wir es lebend hier herausschaffen, geht nach Booty Bay und lasst Scooty wissen, dass ich nach Ratchet gegangen bin, um Tinkerwiz zu warnen, dass die Dunkeleisenzwerge in die Herstellung dieser Supermaschine verwickelt sind.","Gehen wir!"}, {"Begleitet Kernobee zur Uhrwerkgasse und meldet Euch dann wieder bei Scooty in Booty Bay."}},
[2922] = {"Rettet Techbots Hirn!", {"Als unsere geliebte Stadt Gnomeregan fiel, muss ein verirrter Funke mein armes Geschöpf, Techbot, völlig durcheinander gebracht haben. Früher half er mir und meinen Kollegen mit seinen zahllosen Dingsdas und seiner endlosen Speicherkapazität, aber sein positronisches Gehirn wurde polarisiert. Es ist jetzt negatronisch... und Techbots Verhalten wurde umgekehrt. Er wütet jetzt in ganz Gnomeregan!","Doch vielleicht kann man ihn noch retten. Wenn Ihr seinen Speicherkern auffinden und zu mir bringen könnt, kann ich die Ursache der Polarisierung möglicherweise finden und meinen armen Techbot reparieren."}, {"Bringt Techbots Speicherkern zu Tüftlermeister Overspark nach Ironforge."}},
[2923] = {"Tüftlermeister Overspark", {"Tüftlermeister Overspark ist ob des Schicksals seiner mechanischen Schöpfung, Techbot, ziemlich bestürzt. Ich erinnere mich an Techbot als ein freundliches und hilfsbereites Werkzeug der Gnome, aber Overspark erzählte mir, er hätte eine Fehlfunktion.","Wirklich traurig. Aber mit etwas Glück kann er vielleicht repariert werden!","Sprecht mit Tüftlermeister Overspark in Tüftlerstadt, Ironforge. Er würde sich über Eure Hilfe freuen."}, {"Sprecht mit Tüftlermeister Overspark in Ironforge."}},
[2924] = {"Grundlegende Artifixe", {"Ein beinahe universell einsetzbares mechanisches Bauteil - das grundlegende Artifix - war den Tüftlern und Schmieden von Gnomeregan von großem Nutzen. Doch während der panischen Flucht aus unserer Stadt hat niemand daran gedacht, einige von diesen Geräten mitzunehmen! Und ich brauche eine ganze Menge davon für eine Arbeit, die ich in Kürze in Angriff nehmen werde.","Bitte, geht nach Gnomeregan und bringt mir einige grundlegende Artifixe. Ihr werdet sie in den tieferen Bereichen unserer Stadt finden, in \"Künstliche Extrapolatoren\" genannten Behältern.","Danke, <Name>, und bitte beeilt Euch!"}, {"Bringt Klockmort Spannerspan in Ironforge 12 grundlegende Artifixe."}},
[2925] = {"Klockmorts Grundlagen", {"Ein Gnomen-Freund, Klockmort Spannerspan, hat mich aus Ironforge kontaktiert. Er teilt mir mit, dass er Hilfe bei einer gefährlichen, aber unverzichtbaren Mission nach Gnomeregan braucht, der einst so großen Gnomen-Stadt, die jetzt verseucht und voller verrückter Gnome und ihrer Gerätschaften ist.","Ich muss hier bleiben, aber mir blutet das Herz, wenn ich meinem Freund nicht helfen kann. Wenn Ihr an meiner Stelle gehen könnt, dann stünde ich tief in Eurer Schuld.","Ihr könnt Klockmort in Tüftlerstadt in Ironforge finden."}, {"Sprecht mit Klockmort Spannerspan in Ironforge."}},
[2926] = {"Gnogaine", {"Seit dem Unfall redet Gnoarn nur noch davon, Gnomeregan vom Troggbefall zu befreien und ein Heilmittel für die Erkrankung, die die Stadt verseucht, zu finden, <Name>.","Zum Glück sind Razzle und ich gerade dabei, ein solches Heilmittel zu entwickeln... oder vielleicht auch ein Rezept für wirklich starken Rum. Genau wissen wir das aber erst, wenn wir es ausprobiert haben! Logischerweise benötigen wir zum Test unserer Theorie ein paar Dinge, vor allem Ablagerungen von den bestrahlten Troggs in Gnomeregan. Füllt diese Phiole mit dem grünen Leuchten und bringt sie zurück!"}, {"Sammelt mit der leeren bleiernen Sammelphiole radioaktive Ablagerungen bestrahlter Eindringlinge oder Plünderer. Sobald sie voll ist, bringt Ihr sie zu Ozzie Togglevolt nach Kharanos zurück."}},
[2927] = {"Der Tag danach", {"Ihr glaubt doch wohl nicht, dass ich schon immer so kahlköpfig war, oder? Nein, <werter Herr/holde Dame>! Das Strahlungsleck in Gnomeregan rief diesen tragischen Haarverlust hervor. Vor jenem Tag hatte ich den einzigen Gnomen-Afro in ganz Azeroth.","<Gnoarn schüttelt drohend die Faust.>","Verdammt seien diese verdammten dreckigen Troggs, verdammt seien sie alle!","Vielleicht ist es noch nicht zu spät, <Name>. Wir können sie vielleicht immer noch retten - die Stadt, meine ich! Sprecht mit Ozzie Togglevolt in Kharanos. Er und sein Partner Razzle arbeiten an einer Lösung."}, {"Sprecht mit Ozzie Togglevolt in Kharanos."}},
[2928] = {"Gyrobohrmatische Exkavation", {"Es ist schrecklich, <Name>. Wir werden nicht genug Teile haben, um unsere Flotte aus gyrobohrmatischen Exkavationatoren nach Dun Morogh zu bringen.","Wir brauchen Ersatzteile, <Name>. Und welch besseren Ort als Gnomeregan könnte es geben, um Ersatzteile für unsere Fahrzeuge zu besorgen? Es heißt, dass Bots und Mechs dort herumlaufen wie Vieh auf der Weide.","Ihr müsst für mich nach Gnomeregan gehen und diese Roboter auseinander nehmen. Bringt mir ihre Innereien. Zwei Dutzend sollten ausreichen."}, {"Bringt 24 robomechanische Innereien zu Shoni nach Stormwind."}},
[2929] = {"Der große Verrat", {"Ich habe Thermaplugg vertraut, <Volk>. Ich hätte nie vermutet, dass er mich und das ganze Gnomen-Volk verraten würde. Und wofür? Macht? Reichtum? Alles Dinge, die er mit der Zeit auch so erlangt hätte. Jetzt sind wir aus unserer Heimat verdrängt worden und dieser Wahnsinnige ist dafür verantwortlich. Der König des Nichts ...","Wir werden Gnomeregan zurückerobern, <Name>. Wir werden nicht ruhen, bis die Stadt wieder unter unserer Kontrolle steht. Wenn Ihr Euch unserem Kampf anschließen wollt, habe ich eine einfach Bitte an Euch: Tötet den Verräter. Vernichtet Robogenieur Thermaplugg."}, {"Reist nach Gnomeregan und tötet Robogenieur Thermaplugg. Kehrt zu Hochtüftler Mekkatorque zurück, wenn der Auftrag ausgeführt ist."}},
[2930] = {"Datenrettung", {"Als wir aus Gnomeregan flohen, haben wir so viele Daten zurückgelassen! Lebenswichtige Informationen!","Ich brauche die Daten auf einer Prismalochkarte aus einem Matrix-Prägographen 3005-D, einem Hochsicherheitsterminal, tief in Gnomeregan. Ihr müsst auf dieses Terminal zugreifen, aber dafür braucht Ihr die Zugangsberechtigung ... und um die zu erhalten, müsst Ihr erst Zugang zu den Terminals niedrigerer Sicherheitsstufen - Modelle A, B und C - haben. DAFÜR wiederum braucht Ihr Sicherheitslochkarten, angefangen mit einer weißen ... Und wir haben alle Lochkarten in der Stadt vergessen.","Das ist eine Katastrophe! Könnt Ihr helfen?"}, {"Bringt Mechanikermeister Castpipe in Ironforge eine Prismalochkarte."}},
[2931] = {"Castpipes Auftrag", {"Mechanikermeister Castpipe in Ironforge sucht Abenteurer! Er organisiert Teams, um Gnomeregan auf einer Datensicherungsmission höchster Sicherheitsstufe und Wichtigkeit zu betreten.","Castpipe mag zwar nicht wie ich ein Mitglied der Erleuchteten Vereinigung für Arkanologie, Alchimie und Ingenieurwissenschaften sein, aber er ist dennoch ein kluger Gnom, und wenn er es für wichtig hält, dann besteht nach meinen Berechnungen eine vierundachtzigprozentige Wahrscheinlichkeit, dass es wirklich wichtig ist.","Das ist eine gute Chance!"}, {"Sprecht mit Mechanikermeister Castpipe in Ironforge."}},
[2932] = {"Grausige Botschaft", {"Die Trolle der Witherbark waren einst Verbündete der Horde, aber sie haben Thrall hintergangen... und das muss bestraft werden!","Hier, nehmt meine Pike und geht weit gen Norden ins Hinterland. Ihr werdet es nordöstlich von Tarrens Mühle vorfinden, einem Dorf, das von unseren Verbündeten, den Verlassenen, kontrolliert wird.","Wenn Ihr ins Hinterland kommt, jagt Trolle der Witherbark. Sammelt ihre Schädel und rammt meine Pike in einem ihrer kleineren Dörfer in den Boden: in Hiri'watha oder Zun'watha.","Zeigt ihnen, dass ihre heimtückischen Taten Konsequenzen nach sich ziehen!"}, {"Sammelt Schädel der Witherbark und hängt sie an Nimboyas Pike. Platziert Nimboyas behängte Pike in einem der Witherbarkdörfer im Hinterland und kehrt dann zu Nimboya ins Schlingendorntal zurück."}},
[2933] = {"Giftflaschen", {"Diese Flaschen enthalten ein übles grünes Gift.","Wenn man die Anzahl der Flaschen in diesem Dorf bedenkt, müssen die Witherbarks ihn für sehr wichtig halten. Vielleicht will einer der Apotheker im nahe gelegenen Tarrens Mühle eine Probe davon."}, {"Bringt eine Giftflasche zu einem Apotheker nach Tarrens Mühle."}},
[2934] = {"Unbeschädigter Giftbeutel", {"Ich benötige eine frische Probe von dem Gift, den Ihr mir gebracht habt. Die Spinnen, von denen er gesammelt wurde, dürften im Hinterland leben. Jagt sie, bis Ihr einen unbeschädigten Giftbeutel findet, und bringt ihn mir. Ihr findet viele Spinnen nahe der Trollstadt Shadra'Alor.","Und trödelt nicht rum, <Name>, denn der Giftbeutel muss frisch sein, wenn Ihr ihn abliefert."}, {"Bringt Apotheker Lydon in Tarrens Mühle einen unbeschädigten Giftbeutel."}},
[2935] = {"Konsultiert Meister Gadrin", {"Das Gift der Brutwachen war nicht so potent wie das Gift, den Ihr mir ursprünglich gebracht habt. Er muss von einer anderen Spinne gewesen sein.","Ich will eine frische Probe dieses Gifts. Um herauszufinden, woher er kam, müsst Ihr Euch mit einem Troll unterhalten, der die Witherbark gut kennt - Meister Gadrin im Dorf Sen’jin. Reist nach Durotar und sprecht mit Gadrin. Wenn wir Glück haben, weiß er, woher die Witherbarks ihr Gift bekommen."}, {"Sprecht mit Meister Gadrin in Sen'jin."}},
[2936] = {"Der Spinnengott", {"Die Witherbarks verehren einen Spinnengott und von ihm bekommen sie ihr wirkungsvolles Gift. Wenn Ihr also ihr Gift wollt, müsst Ihr ihren Gott beschwören!","Um das zu tun, müsst Ihr seinen wahren Namen wissen und dieser Name steht auf einer Tafel, die in Tanaris, in der Trollstadt von Zul’Farrak, in einer Gruft vergraben ist, die von Theka dem Märtyrer bewacht wird.","Besiegt Theka und findet und lest diese Tafel, um den Namen des Spinnengottes herauszufinden. Wenn Ihr ihn kennt, kehrt zu mir zurück."}, {"Lest von der Schrifttafel des Theka, um den Namen des Spinnengottes der Witherbark zu erfahren, und kehrt dann zu Meister Gadrin zurück."}},
[2937] = {"Die Beschwörung von Shadra", {"Nehmt dieses Pergament und verlest es am Altar von Shadra'Alor tief in der Welt der Witherbark im Hinterland. Dies wird Shadra, den Spinnengott, beschwören."}, {"Besiegt Shadra am Altar von Shadra’Alor und bringt sein Gift zu Apotheker Lydon nach Tarrens Mühle."}},
[2938] = {"Gift für Undercity", {"Das Gift, das Ihr vom Spinnengott der Witherbarks beschafft habt, ist ein großer Fund! Ich konnte mit nur einem Tropfen von dem Zeug fast zwei Dutzend Hasen töten! Na gut, einige dieser Hasen waren sehr jung und klein genug, um auf eine Handfläche zu passen, und daher war ihre Widerstandskraft gegen Toxine bestimmt niedrig, aber man kann nicht leugnen, dass dieses Gift mächtig ist!","Ich würde gern eine Probe zu meinem Vorgesetzten, Apotheker Faranell, in das Apothekarium von Undercity entsenden. Ich bin sicher, er würde es gern selbst testen."}, {"Bringt Faranells Päckchen zu Apothekermeister Faranell nach Undercity."}},
[2939] = {"Auf der Suche nach Wissen", {"Auch wenn die Ruinen von Feralas ziemlich gefährlich sein können, so erzählen sie doch viel darüber, was hier passiert ist...","Als ich vor einigen Tagen das Geröll südlich von hier durchstöberte, fand ich etwas, das wie ein gewöhnlicher Stock aussah. Allerdings werde ich das Gefühl nicht los, dass es mehr damit auf sich hat. Angelas und ich haben über unseren Büchern hier gebrütet, aber wir konnten nichts darüber herausfinden.","Ich habe aber eine Kollegin in Darnassus, die uns vielleicht erzählen kann, was das ist, <Name>. Warum sprecht Ihr nicht mit ihr und seht, ob sie uns helfen kann?"}, {"Sprecht mit Daryn Lightwind im Dorf Rut'theran."}},
[2940] = {"Feralas: Ein historischer Abriss", {"Es sieht so aus, als sei dieses Buch seit langer Zeit von niemandem mehr geöffnet worden. Sein Einband ist ziemlich abgenutzt und seine Seiten sind vergilbt, aber nach einer kurzen Untersuchung stellt Ihr fest, dass es genau das sein könnte, was Troyas sucht.","Ihr steckt es ein, aber dann fällt Euch ein, dass Ihr vielleicht vorher fragen solltet, bevor Ihr es ausleiht."}, {"Fragt Daryn Lightwind, ob Ihr Euch ihr Buch ausleihen dürft."}},
[2941] = {"Der Borger", {"Ich habe in meinem Leben viele Dinge studiert und meine jüngste Leidenschaft sind die Schnappkiefer, die den Strand im Hinterland bevölkern. Es gibt einen bestimmten, den ich gern sehen würde, einen Riesenschnappkiefer namens Gammerita. Ich würde gern selbst hingehen, aber meine Forschungen halten mich hier. Ein Bild von Gammerita wäre wohl die nächstbessere Lösung.","Bringt diesen Brief zu Curgle Cranklehop nach Tanaris. Sie hat etwas für mich erfunden, das Bilder einfangen kann. Ich glaube, sie nannte es \"Schnappschuss\" ..."}, {"Bringt den Brief zu Curgle Cranklehop nach Tanaris."}},
[2942] = {"Der Morgenstein", {"In dem Moment, in dem das Artefakt vom Monolithen getrennt wird, fühlt Ihr, wie die Energie im Stock von Equinex sich zu verflüchtigen beginnt."}, {"Bringt den funkelnden Stein und den Stock von Equinex zu Troyas Moonbreeze in die Festung Feathermoon."}},
[2943] = {"Rückkehr zu Troyas", {"Hier, <Name>. Bitte, passt auf mein Buch auf.","Nun aber schnell. Troyas wartet bestimmt schon ganz ungeduldig auf Eure Rückkehr."}, {"Liefert das Buch bei Troyas Moonbreeze in der Festung Feathermoon ab."}},
[2944] = {"Der Superschnapper FX", {"Werft erst mal einen Blick auf meine neue Erfindung, <Name>. Visiert einfach an, wovon Ihr ein Bild haben möchtet, und drückt dann auf den Knopf.","Was war das für eine Kreatur aus dem Hinterland, die Ihr erwähnt habt? Gammerita? Tja, viel Erfolg bei der Suche nach ihr. Ich bin sicher, Daryn wird mit dem Schnappschuss, den Ihr ihr zurückbringt, sehr zufrieden sein.","Wie auch immer, hier habt Ihr den Superschnapper. Viel Spaß!"}, {"Benutzt den Superschnapper FX, um einen Schnappschuss von Gammerita zu machen, und kehrt dann zu Daryn Lightwind nach Rut'theran zurück."}},
[2945] = {"Schmutzverkrusteter Ring", {"Ihr habt bei einer der Leichen der Dunkeleisenzwerge etwas entdeckt, das wie ein Ring aussieht. Es ist jedoch schwer zu sagen, was für ein Ring das ist. Das Ding ist derart mit Dreck und Verkrustungen überzogen, dass man es unmöglich an einen Finger stecken kann. Ihr glaubt, unter den Verkrustungen Edelmetall schimmern und vielleicht sogar einen Edelstein glitzern zu sehen.","Wenn Ihr diesen Ring verwenden wollt, müsst Ihr nach etwas suchen, das die Verkrustungen entfernt."}, {"Findet einen Weg, den schmutzverkrusteten Ring zu säubern."}},
[2946] = {"Mal sehen, was passiert...", {"Wir möchten, dass Ihr die Mininaturscheiben, die Ihr gefunden habt, zu den versiegelten Toren von Uldum bringt. Wir... wir sind uns nicht ganz sicher, was dann passieren wird.","Es klingt ein bisschen riskant, sicher, das geben wir auch zu. Es könnte allerdings auch sein, dass überhaupt nichts passiert. Vielleicht müsst Ihr mit den Scheiben irgendetwas anstellen. Vielleicht öffnen sich die Türen, wenn Ihr die Scheiben bei Euch habt. Versucht alles Mögliche und seid offen für alles, was passieren könnte.","Wenn Ihr herausgefunden habt, was die Scheiben bewirken, kehrt nach Ironforge zurück. Es wird einiges zu untersuchen geben, wenn Ihr das erledigt habt!"}, {"Bringt die Miniaturplatinscheiben zu den Toren von Uldum in Tanaris und sucht nach Hinweisen auf die Verbindung zwischen beidem."}},
[2947] = {"Die Rückkehr des Rings", {"Ihr öffnet die Schachtel, die der Funkelmat 5200 ausgegeben hat, und entdeckt, was Ihr vermutet hattet - einen glänzenden Ring! Beim Anstecken des Rings fühlt Ihr magische Macht durch Euren Körper fließen.","Auf der Innenseite des Rings befindet sich eine kleine, aber dennoch klar erkennbare Gravur des Siegels von Ironforge. Daneben sind drei kleine Initialen eingraviert: \"TdK\". Dies könnten entweder die Initialen des ursprünglichen Besitzers oder die des Herstellers des Rings sein. Den Ring behalten ... oder ihn dieser Person zurückgeben? Entscheidungen über Entscheidungen ..."}, {"Ihr könnt den Ring entweder behalten oder die Person finden, die für die Prägung und Gravuren auf der Innenseite des Rings verantwortlich ist."}},
[2948] = {"Gnomen-Verbesserungen", {"Hört mir zu, <Name>. Dieser Ring gehört Euch, das ist schon richtig... aber ich kann Euch einen besseren machen! Das Design ist, wie ich bereits sagte, wirklich einfach. Ich bin heutzutage wesentlich geschickter und für die läppische Summe von dreißig Silbermünzen werde ich ihn für Euch aufpeppen! Na ja, eigentlich etwas mehr als dreißig...","Ich bin derzeit in einer misslichen Lage. Mein ganzer Materialvorrat ist aufgebraucht. Wenn Ihr mir einen Barren Silber und einen Moosachat bringt, verziere ich Euch den Ring damit und peppe ihn richtig schön auf!!","Vertraut mir!"}, {"Bringt Talvash del Kissel in Ironforge den glänzenden Goldring, einen Silberbarren, einen Moosachat und 30 Silbermünzen."}},
[2949] = {"Die Rückkehr des Rings", {"Ihr öffnet die Schachtel, die der Funkelmat 5200 ausgegeben hat, und entdeckt einen glänzenden Ring! Beim Anstecken des Rings fühlt Ihr magische Macht durch Euren Körper fließen.","Auf der Innenseite des Rings befindet sich eine kleine, aber dennoch klar erkennbare Gravur des Siegels von Orgrimmar. Daneben sind Buchstaben eingraviert, aber selbst nach der Säuberung sind nur die ersten drei zu erkennen: 'NOG...'","Dies könnten entweder die Initialen des ursprünglichen Besitzers oder die des Herstellers des Rings sein. Den Ring behalten ... oder 'NOG' suchen - Entscheidungen, Entscheidungen ..."}, {"Ihr könnt den Ring entweder behalten oder die Person finden, die für die Prägung und Gravuren auf der Innenseite des Rings verantwortlich ist."}},
[2950] = {"Noggs Ringerneuerung", {"Nein, nein, nein - ich behaupte nicht plötzlich, der Ring sei meiner. Normalerweise mache ich so etwas nicht, aber Eure Information über die Dunkeleisenzwerge war sehr nützlich. Ich werde diesen Ring für Euch so verbessern, dass selbst der, der dieses einfache kleine Ding hergestellt hat, es nicht hätte besser machen können. Wahrscheinlich war es irgendein Gnom ...","Wenn Ihr mir einen Barren Silber, einen Moosachat und dreißig Silbermünzen bringt, verziere ich den Ring damit derart, dass seine Magie noch verstärkt wird. Interessiert?"}, {"Bringt Nogg in Orgrimmar den glänzenden Goldring, einen Silberbarren, einen Moosachat und 30 Silbermünzen."}},
[2951] = {"Der Funkelmat 5200!", {"Dieser Apparat ist etwas ganz Besonderes unter den Gnomen, er bietet dem Wahnsinn außerhalb dieses Raums tapfer die Stirn. Eine kleine Plakette auf dem Gerät besagt: \"Der Funkelmat 5200\", gefolgt von einem kurzen Text:","\"Schmutz und Verkrustungen ruinieren Euer Funkelzeug? Der Funkelmat 5200 definiert das Wort Glanz neu! Gebt den Gegenstand hinein, den Ihr gesäubert haben wollt, und werft drei Silbermünzen in den Münzschlitz. Lasst den Funkelmat 5200 einen Moment arbeiten - und voilà! Eure Wertgegenstände werden sauber und glänzend herauskommen!\""}, {"Gebt einen schmutzverkrusteten Gegenstand in den Funkelmat 5200 und stellt sicher, dass Ihr drei Silbermünzen habt, um die Maschine zu aktivieren."}},
[2952] = {"Der Funkelmat 5200!", nil, nil},
[2953] = {"Mehr Spaß mit dem Funkelmaten", nil, nil},
[2954] = {"Der Steinbehüter", {"Willkommen - Ich bin der Wächter des Eingangs. Ich werde Euch den Zugang zum Areal von Uldum nur gewähren, wenn Ihr die Platten von Uldum in Euren Scheibensatz integriert habt.","Das Erscheinen des Steinbehüters von Norgannon muss die Reaktion sein, die man sich davon erhoffte, als man Euch mit den Platinscheiben in die Tanariswüste schickte. Wie Euch aufgetragen, werdet Ihr die Geschichte, die hinter dem Steinbehüter steckt, herausfinden müssen, bevor Ihr weitermachen könnt."}, {"Findet den Zweck des Steinbehüters von Norgannon heraus und interagiert dann mit dem Uldum-Sockel in der Tanariswüste."}},
[2962] = {"Das einzige Heilmittel ist mehr grünes Leuchten", {"Eure Probe war nicht nur von unzureichender Menge, sondern auch noch verunreinigt.","Was wir brauchen, ist mehr von dem grünen Leuchten. Die Troggs sind offensichtlich nicht stark genug verstrahlt, um unseren Ansprüchen zu genügen. Ihr werdet nach Gnomeregan gehen und etwas der radioaktiven Ablagerung direkt von der Quelle besorgen müssen. Ich spreche von den bestrahlten Brühschleimern, Lauerern und Schrecken. Seid gewarnt, diese Art von Ablagerung ist schwer abzufüllen. Es könnte Euch mehrere Versuche mit der Phiole kosten, bis Ihr eine geeignete Probe bergen könnt."}, {"Reist nach Gnomeregan und bringt etwas von der hoch konzentrierten radioaktiven Ablagerung zurück. Seid gewarnt, die Ablagerung ist instabil und wird ziemlich schnell zerfallen.","Ozzie wird außerdem Eure schwere bleierne Phiole benötigen, nachdem die Aufgabe erledigt ist."}},
[2963] = {"Omen von Uldum", {"Seid mir erneut gegrüßt, <Name>! Seit Eurer Entdeckung in Uldaman ist die Forscherliga voller Aufregung. Die Liga hat daran gearbeitet, die Geheimnisse der Miniaturplatinscheiben zu enträtseln, die Ihr gefunden habt. Euer treffliches Erscheinen hier in der Halle erlaubt es mir, Euch mitzuteilen, dass wir ziemlich wahrscheinlich eine Spur haben!","Historiker Karnik hat einige wichtige Hinweise auf den wahren Ursprung des Tals der Behüter in der Tanariswüste gefunden. Er möchte so schnell wie möglich mit Euch sprechen!"}, {"Sprecht mit Historiker Karnik in der Halle der Forscher in Ironforge."}},
[2964] = {"Eine zukünftige Aufgabe", {"Nun, <Name>... Wir scheinen derzeit in einer temporären Sackgasse zu stecken. Wir wissen, was wir brauchen - die Platten von Uldum -, aber wir haben keinen Schimmer, wo diese sein könnten. Wir müssen eine Menge Nachforschungen anstellen, um einen guten Einsatzplan zusammenzustellen. Ich habe ein paar Ideen, wo man mit der Suche anfangen könnte. Ich hege große Hoffnungen, dass wir Euch bald bitten werden, loszuziehen und sie zu bergen.","Der Hochforscher muss hiervon in Kenntnis gesetzt werden, <Name>. Zweifellos wird er Euch ebenfalls persönlich dafür danken wollen, dass Ihr der Liga wieder einmal geholfen habt."}, {"Sprecht mit Hochforscher Magellas in Ironforge."}},
[2965] = {"Omen von Uldum", {"Ah, <Name>, es ist vortrefflich, dass Ihr erneut zu mir gekommen seid. Eure Funde auf der Uldaman-Grabungsstätte haben uns Gelehrten sicherlich einigen Gesprächsstoff geliefert. Ich unterhielt mich also gerade mit einer der Druiden, Nara Wildmane, als mir dämmerte, dass ihre früheren Erfahrungen als Abenteurer von großer Wichtigkeit für unseren nächsten Schritt sein könnten.","Ich möchte, dass Ihr mit ihr über ihre Reisen ins Tal der Behüter in der Tanariswüste sprecht. Sie erwartet Euch auf der Anhöhe der Ältesten."}, {"Sprecht mit Nara Wildmane auf der Anhöhe der Ältesten in Thunder Bluff."}},
[2966] = {"Mal sehen, was passiert...", {"Sage Truthseeker und ich würden Euch bitten, die Miniaturscheiben, die Ihr gefunden habt, zu den versiegelten Toren von Uldum zu bringen. Wir... wir sind uns nicht ganz sicher, was dann passieren wird.","Das klingt ein wenig riskant, sicher, das geben wir auch zu. Es könnte allerdings auch gar nichts passieren. Vielleicht müsst Ihr die Scheiben mit etwas benutzen. Vielleicht öffnen sich die Türen, wenn Ihr die Scheiben bei Euch habt. Versucht alles Mögliche und seid bereit für alles, was passieren könnte.","Findet alles heraus, was Ihr könnt, und kehrt dann zu mir zurück. Wir werden dann viel zu bereden haben."}, {"Bringt die Miniaturplatinscheiben zu den Toren von Uldum in Tanaris und sucht nach Hinweisen auf die Verbindung zwischen beidem."}},
[2967] = {"Rückkehr nach Thunder Bluff", {"Ihr greift erneut auf den Sockel zu und die Platinscheiben erscheinen plötzlich wieder in Eurem Rucksack.","Nachdem Ihr das Wissen des Steinbehüters verinnerlicht habt und die Scheiben wieder in Eurem Besitz sind, ist es jetzt an der Zeit, nach Thunder Bluff zurückzukehren und Nara Wildmane von Eurer Entdeckung in Kenntnis zu setzen."}, {"Sprecht mit Nara Wildmane in Thunder Bluff."}},
[2968] = {"Eine zukünftige Aufgabe", {"Nun, <Name>... Wir scheinen unglücklicherweise in einer Sackgasse zu stecken. Wir wissen, was wir brauchen - die Platten von Uldum -, aber wir haben keinen Schimmer, wo diese sein könnten. Wir werden Nachforschungen darüber anstellen und dann einen soliden Einsatzplan entwickeln. Ich habe ein paar Ideen, wo man mit der Suche anfangen könnte; Ich hege große Hoffnungen, dass wir Euch bald bitten werden, Euch wieder unseren Bemühungen um die Entdeckung der Wahrheit anzuschließen.","Sage Truthseeker muss hiervon in Kenntnis gesetzt werden, <Name>; zweifellos wird er Euch ebenfalls persönlich dafür danken wollen, dass Ihr noch einmal dem Ruf gefolgt seid."}, {"Sprecht mit Sage Truthseeker in Thunder Bluff."}},
[2969] = {"Freiheit für alle Kreaturen", {"Der dreckige Grimmtotemklan fängt Feendrachen ein, <Name>. Jer’kai und ich haben keine Ahnung, was sie mit ihnen vorhaben, aber es ist bestimmt nichts Gutes.","Wir sind nicht stark genug, um uns durch das Lager zu kämpfen und die gefangenen Drachen zu befreien, aber mit Eurer Hilfe...","Wenn Ihr willens seid und ein paar starke Freunde habt, dann befreit mit diesem Schlüssel die Kreaturen aus ihrem Käfig und stellt sicher, dass sie mit ihrem Leben davonkommen. Ich werde die ganze Zeit an Eurer Seite sein. Jer'kai wird hier bleiben."}, {"Befreit mit dem Bambuskäfigschlüssel die Grimmlingflitzer aus dem Pferch im Grimmtotemlager. Kindal Moonweaver sowie mindestens 6 Grimmlingflitzer müssen überleben.","Ihr habt 6 Minuten, bis Kindal beschließt, dass es Zeit zum Rückzug und Eure Aufgabe gescheitert ist."}},
[2970] = {"Gerechtigkeit üben", {"Wie sie Euch ja schon mitgeteilt hat, tragen Kindal und ich den Kampf jetzt zu den Grimmtotem. Gemeinsam sollten wir in der Lage sein, einige der Tauren ohne große Gefahr für uns selbst auszuschalten, aber wir würden etwas Hilfe sehr begrüßen, wenn Ihr dazu bereit wärt.","Ihr habt das Lager unterhalb von uns ja schon gesehen, aber es gibt mehr davon im Osten. Verwendet Eure eigenen Taktiken, um den Tauren so viel Schaden wie möglich zuzufügen. Wir können uns alle wieder hier treffen, nachdem sie Kindals Pfeile und Eure Waffen geschmeckt haben!"}, {"Tötet 12 Naturalisten der Grimmtotem, 10 Räuber der Grimmtotem sowie 6 Schamanen der Grimmtotem und kehrt dann zu Jer’kai Moonweaver nach Feralas zurück."}},
[2972] = {"Gerechtigkeit üben", {"Hier draußen in der Wildnis haben Kindal und ich nur geringe Mittel, um Euch für Eure Hilfe zu entlohnen, aber wir wollen nicht, dass Ihr Euch nicht wertgeschätzt fühlt. Nehmt meinen Siegelring. Solltet Ihr Euch jemals in Teldrassil wiederfinden, geht zum Tempel des Mondes im Herzen von Darnassus. Dort werdet Ihr meine Hohepriesterin vorfinden, Tyrande Whisperwind. Der Ring wird sie wissen lassen, dass es uns gut geht, und Eure Geschichte untermauern.","Wir beide danken Euch, <Name>."}, {"Bringt Jer’kais Siegelring zu Hohepriesterin Tyrande Whisperwind nach Darnassus."}},
[2973] = {"Schimmer für einen neuen Umhang", {"Grüße, <Klasse>. Ich bin mit meinen Gefährten auf einer Mission für unsere Meister aus Orgrimmar hinunter nach Feralas gereist.","Meine Lebensgefährtin Korrin liebt neue Kleider und daher habe ich vor, ihr einen neuen Umhang zu fertigen, der mit den hauchzarten Flügeln von Feendrachen geschmückt ist. Seid Ihr vielleicht in der Lage, mir zu helfen? Grimmlingflitzer mit prachtvollen Flügeln, wie ich sie will, gibt es westlich vom Camp Mojache."}, {"Bringt 10 regenbogenfarbene Grimmlingflitzerflügel zu Krueg Skullsplitter nach Camp Mojache."}},
[2974] = {"Eine grausige Entdeckung", {"Was!? Ihr seid in dieser Gegend auf Tauren getroffen? Ihr macht Witze! Wie sahen sie aus?","Der Grimmtotemklan! Ihr habt einige von denen gefunden? Belgrom, einer unserer Meister, interessiert sich für diese Sippe... Er hat sogar ein Kopfgeld auf sie ausgesetzt.","Ihr müsst wieder zurückgehen, <Klasse>. Geht zurück und bringt mir ihre Hörner... haufenweise, wenn Ihr könnt. Mit den Grimmtotem ist nicht zu spaßen und man sollte sie nicht unterschätzen. Geht zurück und erschlagt sie; lasst die Flüsse in Feralas rot werden von ihrem dunklen Blut."}, {"Bringt 20 Grimmtotemhörner zu Krueg Skullsplitter nach Camp Mojache."}},
[2975] = {"Die Oger von Feralas", {"Der Einfluss des Ogerstamms der Gordunni sind in diesem Gebiet kaum zu übersehen. Schaut Euch mal um, <Name>. Seine bloße Anwesenheit zeichnet das Land. Wir müssen die Gordunni zurückdrängen, bevor sie sich weiter nach Feralas hinein ausbreiten können.","Folgt dem Fluss nach Norden und Ihr werdet ihren Außenposten finden. Verringert ihre Zahl, <Name>. Wir müssen ihnen klar machen, dass ihre Art hier nicht willkommen ist."}, {"Rok Orhan im Camp Mojache will, dass Ihr 10 Oger der Gordunni, 10 Magier der Gordunni und 5 Schläger der Gordunni tötet."}},
[2976] = {"Eine grausige Entdeckung", {"Was?!","Hm, ich hab wohl wirklich ein Kopfgeld erwähnt, nicht wahr? Gut, das wird mich lehren, meinen Mund nicht zu weit aufzureißen, oder?","Ihr habt die Arbeit gemacht, also ist es wohl nur fair, wenn Ihr den größten Lohn einheimst. Nehmt die Hörner, ich lasse sie für Euch in eine Kiste packen. Findet Belgrom Rockmaul im Tal der Ehre. Er wird wissen wollen, was Ihr gesehen habt, und Euch für Eure harte Arbeit belohnen.","Macht es gut, <Klasse>, und danke noch einmal für alles, was Ihr getan habt."}, {"Bringt die Kiste mit Grimmtotemhörnern zu Belgrom Rockmaul nach Orgrimmar."}},
[2977] = {"Rückkehr nach Ironforge", {"Ihr greift erneut auf den Sockel zu und die Platinscheiben erscheinen plötzlich wieder in Eurem Rucksack.","Nachdem Ihr das Wissen des Steinbehüters verinnerlicht habt und die Scheiben wieder in Eurem Besitz sind, ist es jetzt an der Zeit, nach Ironforge zurückzukehren und die Forscherliga von Eurer Entdeckung in Kenntnis zu setzen."}, {"Sprecht mit Historiker Karnik in Ironforge."}},
[2978] = {"Die Gordunnischriftrolle", {"Ihr hebt die verschmutzte Schriftrolle auf und bemerkt, dass sie eine Art Versammlung oder Zermonie darstellt. Rok Orhan wird sich für diese Schriftrolle, und was sie für die Gordunni bedeutet, interessieren."}, {"Bringt die Gordunnischriftrolle zu Rok Orhan ins Camp Mojache."}},
[2979] = {"Dunkle Zeremonie", {"Dies scheinen Pläne für eine Art dunkle Zeremonie zu sein, <Name>. Soweit ich das erkennen kann, wird dieses Ritual von den Magierlords der Gordunni ausgeführt.","Findet einen dieser Magierlords und sammelt eine Kugel von ihm.","Die Kugel eines Magiers enthält große Macht und wird uns dabei helfen, mehr über diese Zeremonie und die Magie, die die Gordunni beherrschen, herauszufinden."}, {"Rok Orhan im Camp Mojache will, dass Ihr eine Gordunnikugel findet."}},
[2980] = {"Die Oger von Feralas", {"Jetzt, da Ihr bewiesen habt, dass Ihr ein wahrer Kämpfer seid, vertraue ich darauf, dass Ihr den Mut besitzt, mit der vorliegenden Aufgabe weiterzumachen.","Reist in die obere Wildnis südwestlich von hier. Dort gibt es einige verlassene und zerstörte Gebäude auf dem Land, das einst den Nachtelfen gehörte. Sucht nach diesen Ruinen, und wenn Ihr verwelkte Bäume und entweihtes Gelände seht, dann werdet Ihr die Gordunni finden. Drängt sie zurück.","Diese Oger sind viel stärker als diejenigen, denen Ihr bisher entgegengetreten seid, <Name>. Seht Euch vor."}, {"Rok Orhan im Camp Mojache will, dass Ihr 10 Schamanen der Gordunni, 10 Hexenmeister der Gordunni und 5 Raufer der Gordunni tötet."}},
[2981] = {"Eine Bedrohung in Feralas", {"Feralas ist der ideale Ort für jemanden mit Eurer Erfahrung, <Klasse>, der sich selbst beweisen will. Ein Stamm von Ogern, die sich Gordunni nennen, ist die vorherrschende Militärmacht dort, und ihre Zahl wächst. Sie müssen aufgehalten werden, bevor sie unsere Anwesenheit in Feralas bedrohen.","Eine mir bekannte Kriegerin mit Namen Rok Orhan lebt im Camp Mojache in Feralas; geht zu ihr. Sie wird Euch mehr über die Situation erzählen, wenn Ihr dort ankommt."}, {"Belgrom Rockmaul will, dass Ihr mit Rok Orhan im Camp Mojache sprecht."}},
[2982] = {"Die obere Wildnis", {"Obwohl es gefährlich ist, haben Troyas und ich die Ruinen erforscht, die in den Wäldern von Feralas zu finden sind.","In jüngster Zeit ist Troyas jedoch oft allein in die obere Wildnis gegangen und ich mache mir Sorgen um ihn. Die Oger der Gordunni haben dieses Gebiet besetzt und sie reagieren nicht sehr freundlich auf jemanden, der ihr Territorium betritt.","Ich hoffte, dass Ihr vielleicht zu den Ruinen im Südosten reisen und einige der Oger beseitigen würdet. Macht es ein wenig sicherer für meinen Troyas und ich werde Euch sehr dankbar sein."}, {"Angelas Moonbreeze will, dass Ihr 8 Hexenmeister der Gordunni, 8 Schamanen der Gordunni und 8 Schläger der Gordunni tötet."}},
[2983] = {"Ruf des Feuers", {"Es ist eine Weile her, seit Ihr zuletzt geprüft wurdet, <Name>. Ihr wart in Euren Studien bisher sehr geduldig, doch es ist an der Zeit, dass Ihr etwas über größere Mächte erlernt... Es ist an der Zeit, dass Ihr wahre Macht erfahrt. Das Element des Feuers ist Euer, wenn Ihr bereit seid, dafür zu bezahlen.","Sucht nach Kranal Fiss im Brachland. Er lebt in einer kleinen Siedlung nördlich von Crossroads, aber gebt Acht... wenn Ihr das Land der Nachtelfen erreicht, seid Ihr zu weit gegangen."}, {"Sucht Kranal Fiss im Brachland."}},
[2984] = {"Ruf des Feuers", {"Es ist lange her, seit Ihr zuletzt geprüft wurdet, <Name>. Bislang habt Ihr bei Euren Studien Geduld bewiesen, doch die Zeit ist gekommen, da Ihr mehr lernen müsst. Die Zeit ist gekommen, mehr über Zerstörung zu lernen... und über Chaos. Ihr könnt das Element des Feuers verstehen lernen, wenn Ihr bereit seid, dafür zu bezahlen.","Sucht nach Kranal Fiss im Brachland. Er lebt in einer kleinen Siedlung nördlich von Crossroads, aber gebt Acht, wenn Ihr das Land der Nachtelfen erreicht, seid Ihr zu weit gegangen."}, {"Sucht Kranal Fiss im Brachland."}},
[2985] = {"Ruf des Wassers", {"Ihr seht aus, als wäret Ihr bereit für die nächste Prüfung, <Name>. Ihr wart bislang geduldig in Euren Studien, doch die Zeit ist gekommen, da Ihr mehr erlernen müsst. Die Zeit ist gekommen, mehr über die Reinheit des Wassers zu erlernen.","Sucht nach Islen Waterseer im Brachland. Sie befindet sich an der Ostküste, südlich von Ratchet und dem Südstrom. Ihr findet sie in ihrer Angelhütte. Viel Glück, <Name>."}, {"Sucht Islen Waterseer im Brachland."}},
[2986] = {"Ruf des Wassers", {"Es ist lange her, seit Ihr zuletzt geprüft wurdet, <Name>. Bislang habt Ihr bei Euren Studien Geduld bewiesen, doch die Zeit ist gekommen, da Ihr mehr lernen müsst. Die Zeit ist gekommen, mehr über die Reinheit des Wassers zu lernen.","Sucht nach Islen Waterseer im Brachland. Sie befindet sich an der Ostküste, südlich von Ratchet und dem Südstrom. Ihr findet sie in ihrer Fischerhütte. Viel Glück, <Name>."}, {"Sucht Islen Waterseer im Brachland."}},
[2987] = {"Gordunnikobalt", {"Ich bin gerade erst hier in Feralas angekommen und habe gehört, dass die Oger der Gordunni, die die Wildnis hier bewohnen, einige interessante Mineralien besitzen. Keiner weiß, was die so vorhaben, aber sie passen höllisch gut auf dieses Zeug auf! Sie lassen es nicht einfach herumliegen. Oh nein, sie vergraben es, damit Leute wie ich es nicht in die Finger bekommen.","Wenn Ihr wollt, gebe ich Euch meine Schaufel. Geht zum Außenposten der Gordunni nördlich von hier und grabt mir etwas Gordunnikobalt aus. Haltet nach blauem Glühen Ausschau und fangt dann dort an zu graben!"}, {"Orwin Gizzmick im Camp Mojache will 12 Proben Gordunnikobalt."}},
[2988] = {"Käfige der Witherbark", {"Der Sohn unseres mächtigsten Greifen ist verschwunden! Sturmschwinges Erbe, Scharfschnabel, ging vor ein paar Tagen bei der Jagd mit seinen Eltern verloren und wir glauben, dass die Trolle ihn geschnappt haben.","Ihr müsst uns helfen! Durchsucht die Käfige in den beiden Dörfern der Witherbarktrolle, um herauszufinden, ob Scharfschnabel dort ist."}, {"Durchsucht die Käfige in den beiden Dörfern der Witherbark und kehrt dann zu Greifenmeister Talonaxe zurück."}},
[2989] = {"Der Altar von Zul", {"Scharfschnabel war nicht bei den Witherbarktrollen. Vielleicht haben ihn die Vilebranchtrolle geholt. Geht zum Altar von Zul am Westrand des Territoriums der Vilebranch und sucht nach dem Greifenjungen."}, {"Überprüft die Oberfläche des Altars von Zul und kehrt dann zu Greifenmeister Talonaxe zurück."}},
[2990] = {"Thadius Grimshade", {"Bringt diese Feder zum Rutengänger Thadius Grimshade. Er verließ vor Jahren Aerie Peak, um sich dunklerem Wissen zuzuwenden, aber in seinem Herzen ist er immer noch ein Wildhammer!","Und vielleicht können uns seine Talente dabei helfen, Scharfschnabel zu finden.","Diese Feder stammt aus dem Nest, in dem das Greifenjunge schläft. Wenn Ihr sie Thadius gebt, kann er mit etwas Glück seine Rutengangmagie verwenden, um Scharfschnabel zu finden.","Viel Glück, <Name>. Ihr werdet Thadius in den verwüsteten Landen finden, im Magierturm der Burg Nethergarde."}, {"Bringt Scharfschnabels Feder zu Thadius Grimshade in die verwüsteten Lande."}},
[2991] = {"Nekrums Medaillon", {"Ich werde herausfinden, wo Euer Greif ist, aber erst müsst Ihr etwas für mich tun. Es gibt da einen Troll in Zul'Farrak, Nekrum der Ausweider, der ein Medaillon besitzt, das ich wirklich gern studieren würde. Bringt mir das Medaillon und ich werde meine Kräfte dafür verwenden, Scharfschnabel zu finden.","Zul’Farrak ist jenseits des Ozeans, im Westen, in der Wüste von Tanaris."}, {"Bringt Thadius Grimshade in den verwüsteten Landen Nekrums Medaillon."}},
[2992] = {"Der Rutengang", {"Lasst mir etwas Platz, während ich ein Rutengangritual durchführe. Ich werde die Verbindung zwischen der Feder, die Ihr mir gebracht habt, und ihrem Besitzer herstellen und dann selbst dieser Verbindung zu Scharfschnabel folgen.","Zumindest... wenn alles nach Plan verläuft. Wenn nicht, nun... dann schließt Ihr besser die Augen."}, {"Wartet, bis Grimshade das Rutengangritual durchgeführt hat."}},
[2993] = {"Rückkehr ins Hinterland", {"Scharfschnabel wird in einem Käfig hoch oben auf einer Klippe am Gipfel des Jintha’Alor im Hinterland gefangen gehalten. Der Schlüssel zu diesem Käfig ist im Besitz der Hohepriesterin der Vilebranchtrolle, der üblen Priesterin Hexx.","Da. Ich habe meinen Teil erfüllt. Ich wünsche Euch Glück, <Name>."}, {"Sprecht mit Greifenmeister Talonaxe."}},
[2994] = {"Scharfschnabels Rettung", {"Wir müssen Scharfschnabel retten!","Kämpft Euch bis zum Gipfel von Jintha’Alor im Herzen des Vilebranchterritoriums vor, holt Euch den Schlüssel zu Scharfschnabels Käfig von der üblen Priesterin Hexx und dann sucht und befreit Scharfschnabel."}, {"Kämpft Euch bis zum Gipfel von Jintha’Alor durch, tötet die üble Priesterin Hexx, um an den Schlüssel zu Scharfschnabels Käfig zu kommen, und rettet Scharfschnabel."}},
[2995] = {"Nachrichtenwege", {"Die Quel'Danil-Hütte ist ein Zentrum der Kommunikation für die Hochelfen des Hinterlands. Ihre Botschafter sind damit beschäftigt, Beziehungen zu anderen Völkern zu knüpfen, mit denen sie ihr Wissen über die Trolle der Witherbark teilen, die das Gebiet im südlichen Hinterland bewohnen. Alle Aufzeichnungen betreffs dieses Nachrichtenaustauschs werden in der Hütte aufbewahrt.","Wir können es nicht brauchen, wenn die Hochtalelfen Allianzen mit anderen schließen, <Name>.","Vernichtet die Hochtaldokumente. Wir müssen ihre Nachrichtenwege niederbrennen... im wahrsten Sinne des Wortes."}, {"Oran Snakewrithe in Undercity will, dass Ihr ins Hinterland reist und die Hochtalaufzeichnungen, Hochtalberichte und Hochtalnotizen verbrennt."}},
[2996] = {"Die Suche nach Strahad", {"Ich habe Euch in der Vergangenheit viele Fertigkeiten beigebracht, aber es ist an der Zeit, dass Euch ein anderer lehrt. Er lebt in Ratchet und sein Name ist Strahad. Er ist ein Mensch, aber lasst Euch deswegen nicht dazu hinreißen, ihn für schwach zu halten. Er mag klein sein, aber er ist auch mächtig. Er kann Euch mächtigere Beschwörungen lehren - ich fühle, dass Ihr dazu bereit seid. Geht nun zu ihm. Er ist in Ratchet... Über der Hafenstadt gleich bei seinem Turm."}, {"Sprecht mit Strahad Farsan in Ratchet."}},
[2997] = {"Foliant der Offenbarung", {"Hallo, <Name>. Ich weiß, Ihr seid damit beschäftigt, den Leuten von Dun Morogh zu helfen, aber wenn Ihr etwas Zeit habt, könntet Ihr Euch auf den Weg nach Ironforge hinauf machen, um mit einer unserer am meisten Respektierten zu reden: Tiza Battleforge. Zu ihr kommen die jüngeren Paladine, wenn es an der Zeit für sie ist, mehr über das Licht und darüber zu erfahren, was von einem erwartet wird.","Ihr könnt sie oben in der Halle der Mysterien finden. Falls Ihr noch nie dort wart, das ist im Mystikerviertel. Viel Glück, <Name>."}, {"Sprecht mit Tiza Battleforge in Ironforge."}},
[2998] = {"Foliant der Offenbarung", {"Seid gegrüßt, <Bruder/Schwester>. Duthorian Rall, einer unserer einflussreichsten Paladine, hat kürzlich von Euch gesprochen. Ich habe mitbekommen, wie er darüber sprach, dass es an der Zeit sei, dass Ihr ein wenig getestet würdet... Dabei fielen auch einige Komplimente darüber, wie gut Ihr bisher abgeschnitten habt.","Wenn ich Ihr wäre, würde ich nach Stormwind gehen und mit ihm sprechen, sobald Ihr Zeit habt. Ich bin sicher, er ist in der Kathedrale des Lichts - dort verbringt er einen Großteil seiner Zeit."}, {"Sprecht mit Duthorian Rall in Stormwind."}},
[2999] = {"Foliant der Offenbarung", {"Hallo, <Name>. Ich weiß, Ihr seid damit beschäftigt, den Leuten von Dun Morogh zu helfen, aber wenn Ihr etwas Zeit habt, könntet Ihr nach oben gehen, um mit einer unserer am meisten Respektierten zu reden: Tiza Battleforge. Zu ihr kommen die jüngeren Paladine, wenn es an der Zeit für sie ist, mehr über das Licht und darüber zu erfahren, was von einem erwartet wird."}, {"Sprecht mit Tiza Battleforge in Ironforge."}},
[3000] = {"Foliant der Offenbarung", {"Hallo, <Name>. Ich weiß, Ihr seid damit beschäftigt, den Völkern Azeroths zu helfen, aber wenn Ihr etwas Zeit habt, solltet Ihr nach Ironforge zurückkehren, um mit einem der am meisten respektierten Paladine Eurer Heimat zu reden: Tiza Battleforge. Zu ihr kommen die jüngeren Zwergen-Paladine, wenn es an der Zeit für sie ist, mehr über das Licht und darüber zu erfahren, was von uns allen erwartet wird.","Ihr findet sie im Obergeschoss der Halle der Mysterien, die sich im Mystikerviertel befindet."}, {"Sprecht mit Tiza Battleforge in Ironforge."}},
[3001] = {"Die Suche nach Strahad", {"Ich habe Euch in der Vergangenheit viele Fertigkeiten beigebracht, aber es ist an der Zeit, dass Euch ein anderer lehrt. Er lebt in Ratchet und sein Name ist Strahad. Er ist ein Mensch, aber lasst Euch deswegen nicht dazu hinreißen, ihn für schwach zu halten oder als Euren Feind anzusehen - zumindest vorerst. Er ist mächtig und sollte respektiert werden. Er kann Euch große Beschwörungen lehren, Beschwörungen, für die Ihr, glaube ich, bereit seid. Geht nun zu ihm.","Er ist in Ratchet... Über der Hafenstadt, gleich bei dem Turm dort."}, {"Findet Strahad Farsan in Ratchet."}},
[3002] = {"Die Gordunnikugel", {"Obwohl ich meinen Feind genau kenne, bin ich ein Krieger, <Name>. Ich kann Euch nicht sagen, über welche Fähigkeiten diese Kugel verfügt.","Ich kenne einen Magier in Orgrimmar, der uns mehr über die Kugel sagen können wird. Bringt sie zu ihm."}, {"Bringt die Gordunnikugel zu Uthel’nay in die Dunkeldornen-Lodge in Orgrimmar."}},
[3022] = {"Vorsichtig behandeln", {"<Name>, ich sehe deutlich, dass Ihr Euch liebend gern meiner noblen Sache zuwenden möchtet. Ja, natürlich könnt Ihr etwas für mich tun!","Bringt diese Kiste mit Eiern zu Erelas Ambersky. Das ist ein Druide, der sich um diese Eier kümmert. Er bebrütet sie und bildet dann die jungen Hippogryphe aus. Ihr findet ihn in Teldrassil, in einem kleinen Dorf im Süden namens Rut'theran."}, {"Bringt die sorgfältig verpackte Kiste zu Erelas Ambersky in das Dorf Rut'theran."}},
[3042] = {"Trollaushärter", {"Die Trolle der Sandfury von Zul'Farrak stellen ein Aushärtungsmittel aus Sandstein her, und ich kann dies verwenden, um die von mir hergestellten Waren auszuhärten, ja, so ist es! Von Kennern wird es hoch gepriesen; wenn Ihr mir also einen guten Vorrat dieses Härters bringt, werde ich Euch reich belohnen.","Zul'Farrak liegt nordwestlich von Gadgetzan. Viel Glück."}, {"Bringt 20 Phiolen Trollaushärter zu Trenton Lighthammer in Gadgetzan."}},
[3062] = {"Dunkles Herz", {"Edana Hasskralle ist die Königin der Nordschwingen, die in den Ruinen im Westen zu Hause sind. Sie ist das vollkommen Böse; ich habe gehört, dass sie ein Herz aus Kristall hat. Sie ist bar jeglicher Emotionen außer dem Hass, den sie für alle anderen fühlt. Ihr müsst Euch Edana stellen und mir ihr dunkles Herz zurückbringen.","Ich habe von einem Horn gehört, das die Harpyien tragen - wenn es bei den Hasskrallensteinen, im nördlichen Teil der Ruinen, geblasen wird, wird die Königin beschworen.","Stellt Euch ihre Überraschung vor, wenn es nicht die ihren sind, die auf sie warten, sondern ihr Tod."}, {"Talo Thornhoof im Camp Mojache will das dunkle Herz von Edana Hasskralle."}},
[3063] = {"Rache an den Nordschwingen", {"Zu meiner Zeit war ich ein erfahrener Jäger. Es gab keine Kreatur, die ich fürchtete. Bis...","Ich war draußen im Wald, um nach einem heftigen Kampf meine Klinge zu schärfen, als ich in einen Hinterhalt geriet. Harpyien umzingelten mich, und ich schaffte es kaum, mich in Sicherheit zu bringen.","Selbst heute noch kann ich das Echo ihrer Schreie vernehmen...","Danach war ich nicht mehr derselbe. Aber der Hass kocht in meinem Inneren - ich will Rache, auch noch nach all diesen Jahren.","Die Harpyien der Nordschwingen leben westlich von hier. <Name>, rächt mich."}, {"Talo Thornhoof möchte, dass Ihr 4 Harpyien der Nordschwingen, 4 Wildfedern der Nordschwingen, 4 Windruferinnen der Nordschwingen und 4 Töter der Nordschwingen erschlagt."}},
[3065] = {"Einfache Schrifttafel", {"Ah, während Ihr fort wart, kam ein Pergament für Euch, <Name>.","Lest es, wenn Ihr Zeit habt. Wenn ich mich nicht irre, kam es vom Kriegerlehrer Frang. Er würde gern mit Euch sprechen, wenn Ihr bereit seid. Ihr findet ihn außerhalb des Höhlenbaus, wo er sich in den Schatten verbirgt."}, {"Lest die einfache Schrifttafel und sprecht mit Frang im Tal der Prüfungen."}},
