[3093] = {"Mit Runen beschriftete Notiz", {"Gerade eben fragte eine Botin nach Euch, <Name>. Ich glaube, sie wurde von der Schamanenlehrerin Meela geschickt. Wenn diese Nachricht von Meela ist, würde ich sie schnellstens lesen."}, {"Lest die mit Runen beschriftete Nachricht und sprecht mit Meela Dawnstrider im Camp Narache."}},
[3094] = {"Tiefgrüne Notiz", {"Gerade eben fragte eine Botin nach Euch, <Name>. Ich glaube, sie wurde vom Druidenlehrer Gart geschickt. Wenn diese Nachricht von Gart ist, würde ich sie schnellstens lesen."}, {"Lest die tiefgrüne Notiz und sprecht mit Gart Mistrunner im Camp Narache."}},
[3095] = {"Einfache Rolle", {"Ach ja, während Ihr unterwegs wart, um Euch um die hirnlose Geißel zu kümmern, kam diese Schriftrolle für Euch an. Ich würde meinen, dass es sich um etwas Wichtiges handelt, denn sie scheint das Siegel des Kriegerlehrers Dannal zu tragen. Ich würde mir die Zeit nehmen, sie zu lesen, bevor ich wieder losziehe."}, {"Lest die einfache Schriftrolle und sprecht mit Dannal Stern in Deathknell."}},
[3096] = {"Verschlüsselte Rolle", {"Ach ja, während Ihr unterwegs wart, um Euch um die hirnlose Geißel zu kümmern, kam diese Schriftrolle für Euch an. Ich würde meinen, dass es sich um etwas Wichtiges handelt, denn sie scheint das Siegel des Schurkenlehrers David Trias zu tragen. Ich würde mir die Zeit nehmen, sie zu lesen, bevor ich wieder losziehe."}, {"Lest die verschlüsselte Schriftrolle und sprecht mit David Trias in Deathknell."}},
[3097] = {"Geheiligte Rolle", {"Ach ja, während Ihr unterwegs wart, um Euch um die hirnlose Geißel zu kümmern, kam diese Schriftrolle für Euch an. Ich würde meinen, dass es sich um etwas Wichtiges handelt, denn sie scheint das Siegel des Priesterlehrers Duesten zu tragen. Ich würde mir die Zeit nehmen, sie zu lesen, bevor ich wieder losziehe."}, {"Lest die geheiligte Schriftrolle und sprecht mit dem dunklen Kleriker Duesten in der Kirche von Deathknell."}},
[3098] = {"Glyphen-Rolle", {"Ach ja, während Ihr unterwegs wart, um Euch um die hirnlose Geißel zu kümmern, kam diese Schriftrolle für Euch an. Ich würde meinen, dass es sich um etwas Wichtiges handelt, denn sie scheint das Siegel der Magielehrerin Isabella zu tragen. Ich würde mir die Zeit nehmen, sie zu lesen, bevor ich wieder losziehe."}, {"Lest die mit Glyphen versehene Schriftrolle und sprecht mit Isabella in der Kirche von Deathknell."}},
[3099] = {"Besudelte Rolle", {"Ach ja, während Ihr unterwegs wart, um Euch um die hirnlose Geißel zu kümmern, kam diese Schriftrolle für Euch an. Ich würde meinen, dass es sich um etwas Wichtiges handelt, denn sie scheint das Siegel des Hexenmeisterlehrers Maximillion zu tragen. Ich würde mir die Zeit nehmen, sie zu lesen, bevor ich wieder losziehe."}, {"Lest die besudelte Schriftrolle und sprecht mit Maximillion in Deathknell."}},
[3100] = {"Einfacher Brief", {"Ich wurde darum gebeten, Euch hierauf aufmerksam zu machen, sobald Ihr von den Koboldlagern zurückgekehrt seid, <Name>. Es scheint sich um einen versiegelten Brief mit den Insignien von Llane, unserem örtlichen Kriegerlehrer, zu handeln. Ich würde nicht zögern, ihn zu lesen, bevor Ihr irgendwelchen anderen Geschäften hier in der Abtei nachgeht."}, {"Lest den einfachen Brief und sprecht mit Llane Beshere in der Abtei von Nordhain."}},
[3101] = {"Geweihter Brief", {"Ich wurde darum gebeten, Euch hierauf aufmerksam zu machen, sobald Ihr von den Koboldlagern zurückgekehrt seid, <Name>. Es scheint sich um einen versiegelten Brief mit den Insignien von Bruder Sammuel, unserem örtlichen Paladinlehrer, zu handeln. Ich würde nicht zögern, ihn zu lesen, bevor Ihr irgendwelchen anderen Geschäften hier in der Abtei nachgeht."}, {"Lest den geweihten Brief und sprecht mit Bruder Sammuel in der Abtei von Nordhain."}},
[3102] = {"Verschlüsselter Brief", {"Ich wurde darum gebeten, Euch hierauf aufmerksam zu machen, sobald Ihr von den Koboldlagern zurückgekehrt seid, <Name>. Es scheint sich um einen versiegelten Brief mit den Insignien von Jorik, einem unserer örtlichen Lehrer, zu handeln. Ich würde nicht zögern, ihn zu lesen, bevor Ihr irgendwelchen anderen Geschäften hier in der Abtei nachgeht."}, {"Lest den verschlüsselten Brief und sprecht mit Jorik Kerridan im Stall hinter der Abtei von Nordhain."}},
[3103] = {"Geheiligter Brief", {"Ich wurde darum gebeten, Euch hierauf aufmerksam zu machen, sobald Ihr von den Koboldlagern zurückgekehrt seid, <Name>. Es scheint sich um einen versiegelten Brief mit den Insignien der Priesterin Anetta, unserer örtlichen Priesterlehrerin, zu handeln. Ich würde nicht zögern, ihn zu lesen, bevor Ihr irgendwelchen anderen Geschäften hier in der Abtei nachgeht."}, {"Lest den geheiligten Brief und sprecht mit Priesterin Anetta in der Abtei von Nordhain."}},
[3104] = {"Glyphen-Brief", {"Ich wurde darum gebeten, Euch hierauf aufmerksam zu machen, sobald Ihr von den Koboldlagern zurückgekehrt seid, <Name>. Es scheint sich um einen versiegelten Brief mit den Insignien von Kheldon, unserem örtlichen Magierlehrer, zu handeln. Ich würde nicht zögern, ihn zu lesen, bevor Ihr irgendwelchen anderen Geschäften hier in der Abtei nachgeht."}, {"Lest den mit Glyphen versehenen Brief und sprecht mit Kheldon Bremen in der Abtei von Nordhain."}},
[3105] = {"Besudelter Brief", {"Ich wurde darum gebeten, Euch hierauf aufmerksam zu machen, sobald Ihr von den Koboldlagern zurückgekehrt seid, <Name>. Es scheint sich um einen versiegelten Brief mit den Insignien von Drusilla, einer unserer örtlichen Lehrerinnen, zu handeln. Ich würde nicht zögern, ihn zu lesen, bevor Ihr irgendwelchen anderen Geschäften hier in der Abtei nachgeht."}, {"Lest den besudelten Brief und sprecht mit Drusilla La Salle nahe der Abtei von Nordhain."}},
[3106] = {"Einfache Rune", {"Während Ihr unterwegs wart, um mir zu helfen, wurde mir diese Rune für Euch gegeben. Nehmt Euch die Zeit, sie zu lesen, wenn Ihr die Gelegenheit dazu habt. Ich glaube, sie kommt vom Kriegerlehrer Thran. Werft einen Blick darauf und sucht ihn dann in Anvilmar auf, sobald Ihr die Gelegenheit dazu habt."}, {"Lest die einfache Rune und sprecht mit Thran Khorman im Coldridgetal."}},
