[6942] = {"Ruf der Lüfte - Slidores Luftflotte", nil, nil},
[6943] = {"Ruf der Lüfte - Ichmans Luftflotte", nil, nil},
[6961] = {"Altvater Winter ist hier!", {"Ich wünsche ein wunderbares Winterhauchfest! Vergesst nicht, Altvater Winter zu begrüßen! Wenn Ihr dieses Jahr brav wart, werdet Ihr am Tag des Winterhauchfestes wunderbare Leckereien erhalten. Denkt daran, ihm zu sagen, was Ihr Euch dieses Jahr wünscht!","Achtet außerdem darauf, Euch mit Leckereien für die einzudecken, die in diesem Jahr brav waren. Wir haben ein großes Angebot zur Auswahl. Frisch von unserem Hof auf Euren Tisch: beste Kokelwälder Vollwertkost!"}, {"Sprecht mit Altvater Winter. Er befindet sich in der Nähe des Verkaufsbereichs von Kokelwälder in Orgrimmar."}},
[6962] = {"Leckereien für Altvater Winter", {"He, könnt Ihr Altvater Winter einen Gefallen tun, ähm, <Kleiner/Kleines>?","Ich will ja nicht überflüssigerweise über mich selbst in der dritten Person sprechen, aber Altvater Winter arbeitet schon den ganzen Tag hier und könnte selbst eine kleine Leckerei brauchen. Ein paar Lebkuchen und etwas eiskalte Milch, um sie runterzuspülen, wären genau das Richtige. Glaubt Ihr, Ihr könntet dem Altvater eine Freunde machen und das für mich besorgen?"}, {"Bringt 5 Lebkuchen und etwas eiskalte Milch zu Altvater Winter in Orgrimmar."}},
[6963] = {"Gestohlene Winterhauch-Leckereien", {"Hört zu... ich will Euch ja den Zauber der Feiertage nicht verderben, aber Kokelwälder könnte etwas Hilfe brauchen... von der Art, für die Ihr Abenteurer bekannt seid.","Uns ist eine wichtige Lieferung mit Leckereien abhanden gekommen, mit der wir die Vorräte für diese Feiertage aufstocken wollten, und jetzt ist auch noch der Agent von Kokelwälder verschwunden, den wir losgeschickt haben, um sie zu suchen! Also - die letzten Informationen über die Lieferung stammen aus dem Alteracgebirge, und dort wollte unser Mann danach suchen.","Seht nach, ob sowohl Mann als auch Material dort zu finden sind, und zwar gesund und munter."}, {"Sucht den Ermittler von Kokelwälder, der im Alteracgebirge vermisst wird."}},
[6964] = {"Der Anlass für die Feiertage", {"Gaben, Leckereien, Geschenke, pah!","Diese Goblins beschmutzen die wahre Bedeutung dieser Feiertage - des Winterhauchfests. Dies ist eine Zeit der Erneuerung. Das Land schlummert unter einer Schneedecke, die Altvater Winter gebracht hat, und das ist NICHT der Wichtigtuer da drüben in dem roten Aufzug!","Falls Ihr erfahren wollt, worum es bei dem Fest wirklich geht, dann fragt Sagorne Crestrider. Es dürfte ihn bestimmt überraschen, dass sich jemand für Geschichte statt für Geschenke interessiert."}, {"Sprecht mit Sagorne Crestrider im Tal der Weisheit von Orgrimmar über das Winterhauchfest."}},
[6981] = {"Der leuchtende Splitter", {"Der Splitter ist von einem glänzenden Grün, und obwohl er massiv ist, scheint etwas wie Rauch knapp unterhalb der Oberfläche herumzuwirbeln. Ihr fühlt den Zwang, in die Tiefen des Kristalls zu schauen, aber gleichzeitig fühlt Ihr auch, dass etwas sehr Beunruhigendes von ihm ausgeht.","Vielleicht solltet Ihr in Ratchet nach jemandem suchen, der Euch mehr über diesen seltsamen Splitter erzählen kann."}, {"Begebt Euch nach Ratchet, um jemanden zu finden, der Euch mehr über den leuchtenden Splitter sagen kann.","Liefert dann den Splitter ab, wie Ihr angewiesen wurdet."}},
[6982] = {"Coldtooth-Vorräte", {"Die Coldtooth-Mine ist ein Vorratslager für die Horde. Ich vertrage das Essen nicht, das sie ihren Truppen reichen und der Zustand ihrer Waffen und Rüstungen ist grauenhaft! Aber... in Zeiten wie diesen, in denen Vorräte so wichtig sind, können wir wirklich alles verwenden, was sie haben.","Betretet die Coldtooth-Mine und bringt mir ihre Vorräte. Dies sollte der Allianz helfen sich weiterhin gut im Kampf zu bewähren und die Soldaten der Horde schwächen.","Viel Erfolg, <Name>. Die Mine ist im entfernten Süden, tief im Territorium der Horde."}, {"Bringt 5 Coldtooth-Vorräte zum Rüstmeister der Allianz in Dun Baldar."}},
[6983] = {"So was Gemeines...", {"Eine K-K-Kreatur namens \"Der monströse Griesgram\" hat u-u-unsere Lieferung mit L-L-Leckereien gestohlen, <Name>. Seid a-a-aber vorsichtig! Er hat m-m-mich in dieses S-S-Schneeschlamassel verwandelt!","Ihr w-w-werdet Hilfe brauchen, wenn Ihr ihm g-g-gegenübertretet. Ich habe gesehen, w-w-wie er dort herumzieht, wo die Y-Y-Yetis sind. Er ist s-s-sicherlich irgendwo h-h-hier! F-F-Findet die Leckereien, <Name>, bringt sie zu K-k-k-kokelwälder in Orgrimmar z-z-zurück und s-s-sagt ihnen, dass ich a-a-aufgetaut werden muss!"}, {"Sucht die gestohlenen Leckereien und bringt sie zu Kaymard Copperpinch nach Orgrimmar. Zuletzt befanden sie sich im Besitz des monströsen Griesgrams, der sich in den Schneegebieten der Arathi-Berge befindet."}},
[6984] = {"Ein Dankeschön von Kokelwälder!", {"Wir von Kokelwälder sind dankbar für die Wiederbeschaffung der gestohlenen Leckereien, <Name>. Dafür möchten wir Euch ein besonderes Geschenk anbieten... präsentiert von niemand Geringerem als Altvater Winter persönlich!","Bitte, sprecht mit Altvater Winter und er wird Euch Euer Geschenk zum Winterhauchfest von uns hier bei Kokelwälder überreichen. Frisch von unserem Hof auf Euren Tisch - beste Kokelwälder Vollwertkost... dank Euch natürlich!"}, {"Sprecht mit Altvater Winter in Orgrimmar."}},
[6985] = {"Irondeep-Vorräte", {"Die Zwerge von Stormpike benutzen die Irondeep-Mine als ein Vorratslager. Ich möchte, dass Ihr diese Mine überfallt und mir ihre Vorräte bringt. Dies sollte die Bemühungen der Stormpikes im Alteractal behindern und uns mit der nötigen Ausrüstung versorgen!","Seid vorsichtig, <Name>. Die Irondeep-Mine liegt tief im Territorium der Allianz, und die Kontrolle über die Mine ändert sich so oft wie neuer Schnee in Alterac fällt."}, {"Bringt 10 Irondeep-Vorräte zum Rüstmeister der Horde in die Burg Frostwolf."}},
[7001] = {"Verwaiste Ställe", {"Der Krieg hier im Tal hat viele Opfer gefordert. Unsere Streitkräfte müssen dringend wieder aufgebaut werden!","Wenn wir es schaffen unsere Ställe wieder zu füllen, können unsere glorreichen Wolfsreiter wieder in die Schlacht reiten.","Nehmt diesen Maulkorb und begebt Euch in die nahegelegenen Wälder. Sucht in der Umgebung nach herumstreifenden Frostwölfen und fangt sie mit dem Maulkorb ein. Wenn Ihr einen Wolf gezähmt habt, bringt ihn zu mir und ich werde ihn in unseren Ställen unterbringen.","Sobald wir wieder über ausreichend Wölfe verfügen, werden die Wolfsreiter ihre Frontpatrouillen fortsetzen."}, {"Findet einen Frostwolf im Alteractal. Wenn Ihr Euch in der Nähe eines Frostwolfs befindet, benutzt den Frostwolfmaulkorb um das Tier einzufangen. Nach erfolgreicher Zähmung, folgt Euch der Frostwolf bis zum Stallmeister zurück. Sprecht mit dem Stallmeister um für Euren Fang belohnt zu werden."}},
[7002] = {"Widderledernes Zaumzeug", nil, nil},
[7003] = {"Geschrumpfte Riesen", {"Ich muss etwas gegen die Riesen unternehmen, wenn ich hier draußen bleibe! Sie sind sicherlich nicht gut fürs Geschäft, oder nicht?","Ich habe mich an einer Variante von Gnomischen Schrumpfstrahlen versucht, benutze jetzt aber überlegene GOBLIN Technologien für die Herstellung! Im Zuge von Bemühungen, Unannehmlichkeiten wie meinen Tod beispielsweise zu vermeiden, möchte ich Euch bitten es einmal an diesen Riesen in Feralas zu testen. Ihr solltet nach dem Prozess Schrumpf-Überreste auf ihnen finden, bringt mir bitte etwas davon!"}, {"Benutzt Zorbins Super-Schrumpfer um jegliche Art von Riesen in Feralas in eine handlichere Form zu schrumpfen. Bringt Zorbin Fandazzle 15 Schrumpf-Überreste von diesen Riesen. Ihr findet Ihn bei den Docks, an der vergessenen Küste in Feralas."}},
[7021] = {"Altvater Winter ist hier!", {"Ich wünsche ein wunderbares Winterhauchfest! Ihr müsst unbedingt Altvater Winter begrüßen, der sich derzeit in Orgrimmar befindet! Wenn Ihr dieses Jahr brav wart, werdet Ihr am Tag des Winterhauchfestes wunderbare Leckereien erhalten. Denkt daran, ihm zu sagen, was Ihr Euch dieses Jahr wünscht!","Achtet außerdem darauf, Euch mit Leckereien für die einzudecken, die in diesem Jahr brav waren. Wir haben ein großes Angebot zur Auswahl. Frisch von unserem Hof auf Euren Tisch: beste Kokelwälder Vollwertkost!"}, {"Sprecht mit Altvater Winter. Er befindet sich in der Nähe des Verkaufsbereichs von Kokelwälder in Orgrimmar."}},
[7022] = {"Altvater Winter ist hier!", {"Ich wünsche ein wunderbares Winterhauchfest! Vergesst nicht, Altvater Winter zu begrüßen! Wenn Ihr dieses Jahr brav wart, werdet Ihr am Tag des Winterhauchfestes wunderbare Leckereien erhalten. Denkt daran, ihm zu sagen, was Ihr Euch dieses Jahr wünscht!","Achtet außerdem darauf, Euch mit Leckereien für die einzudecken, die in diesem Jahr brav waren. Wir haben ein großes Angebot zur Auswahl. Frisch von unserem Hof auf Euren Tisch: beste Kokelwälder Vollwertkost!"}, {"Sprecht mit Altvater Winter. Er befindet sich in der Nähe des Verkaufsbereichs von Kokelwälder in Ironforge."}},
[7023] = {"Altvater Winter ist hier!", {"Ich wünsche ein wunderbares Winterhauchfest! Ihr müsst unbedingt Altvater Winter begrüßen, der sich derzeit in Ironforge befindet! Wenn Ihr dieses Jahr brav wart, werdet Ihr am Tag des Winterhauchfestes wunderbare Leckereien erhalten. Denkt daran, ihm zu sagen, was Ihr Euch dieses Jahr wünscht!","Achtet außerdem darauf, Euch mit Leckereien für die einzudecken, die in diesem Jahr brav waren. Wir haben ein großes Angebot zur Auswahl. Frisch von unserem Hof auf Euren Tisch: beste Kokelwälder Vollwertkost!"}, {"Sprecht mit Altvater Winter. Er befindet sich in der Nähe des Verkaufsbereichs von Kokelwälder in Ironforge."}},
[7024] = {"Altvater Winter ist hier!", {"Ich wünsche ein wunderbares Winterhauchfest! Ihr müsst unbedingt Altvater Winter begrüßen, der sich derzeit in Orgrimmar befindet! Wenn Ihr dieses Jahr brav wart, werdet Ihr am Tag des Winterhauchfestes wunderbare Leckereien erhalten. Denkt daran, ihm zu sagen, was Ihr Euch dieses Jahr wünscht!","Achtet außerdem darauf, Euch mit Leckereien für die einzudecken, die in diesem Jahr brav waren. Wir haben ein großes Angebot zur Auswahl. Frisch von unserem Hof auf Euren Tisch: beste Kokelwälder Vollwertkost!"}, {"Sprecht mit Altvater Winter. Er befindet sich in der Nähe des Verkaufsbereichs von Kokelwälder in Orgrimmar."}},
[7025] = {"Leckereien für Altvater Winter", {"He, könnt Ihr Altvater Winter einen Gefallen tun, ähm, <Kleiner/Kleines>?","Ich will ja nicht überflüssigerweise über mich selbst in der dritten Person sprechen, aber Altvater Winter arbeitet schon den ganzen Tag hier und könnte selbst eine kleine Leckerei brauchen. Ein paar Lebkuchen und etwas eiskalte Milch, um sie runterzuspülen, wären genau das Richtige. Glaubt Ihr, Ihr könntet dem Altvater eine Freunde machen und das für mich besorgen?"}, {"Bringt 5 Lebkuchen und etwas eiskalte Milch zu Altvater Winter in Ironforge."}},
[7026] = {"Widderzaumzeug", nil, nil},
[7027] = {"Verwaiste Ställe", {"Seht Euch nur unsere Ställe an, <Name>! Völlig leer! Die Stormpikegarde hat keine Widder mehr, die sie reiten könnte, die Kavallerie ist praktisch nicht mehr existent.","Ihr müsst uns helfen, Soldat. Nehmt dieses Zähmungshalsband und begebt Euch in die Wildnis dieses Tals. Sucht in der Umgebung nach herumstreifenden Alteracwiddern und fangt sie mit dem Zähmungshalsband ein. Wenn Ihr einen Widder eingefangen habt, bringt ihn zu mir und ich werde ihn in unseren Ställen unterbringen. Sobald wir wieder über ausreichend Widder verfügen, werde ich den Kommandanten der Widderreiter informieren.","Mit Eurer Hilfe wird die Kavallerie neu entstehen!"}, {"Findet einen Alteracwidder im Alteractal. Wenn Ihr Euch in der Nähe eines Alteracwidders befindet, benutzt das Stormpike Zähmungshalsband um das Tier einzufangen. Nach erfolgreicher Zähmung, folgt Euch der Alteracwidder bis zum Stallmeister zurück. Sprecht mit dem Stallmeister um für Euren Fang belohnt zu werden."}},
[7028] = {"Dunkles Böses", {"Die Kristallhöhlen von Terramok liegen im Westen, tief in Maraudon... Das ist ein uralter Ort mit einer langen Geschichte. Und ich will einiges davon in die Hände bekommen, <Klasse>. Relikte alter Götter liegen hinter den riesigen Türen verborgen.","Wenn Ihr bereit seid, dem dunklen Bösen zu trotzen, das sich in Maraudon findet, wird es Euer Schade bestimmt nicht sein. Sammelt die theradrischen Kristallschnitzereien ein, die Ihr aufstöbern könnt, und bringt sie mir.","Sobald ich die Schnitzereien habe, kann ich sie... studieren. Diese Mächte haben schon zu lange geruht..."}, {"Sammelt 25 theradrische Kristallschnitzereien für Willow in Desolace."}},
[7029] = {"Schlangenzunges Verderbnis", {"Ein Satyr, der unter dem Namen Schlangenzunge bekannt ist, hat die Höhlen von Maraudon verderbt und so das Wachstum vieler Pflanzen im Inneren behindert.","Nehmt diese Phiole, <Name>. Füllt sie am orangefarbenen Kristallteich, den Ihr in den Höhlen findet. Die Beschichtung in der Phiole wird den Toxinen entgegenwirken und sie in einen Heilstoff verwandeln.","Sucht die Schlangenstrunkranken in den orangefarbenen Höhlen und wendet die geläuterte Flüssigkeit auf sie an. Die Pflanze wird damit von der Verderbnis gereinigt, doch Ihr müsst wissen, dass Schlangenzunge sie immer noch kontrolliert. Er kann sie gegen Euch einsetzen..."}, {"Füllt die beschichtete himmelblaue Phiole am orangefarbenen Kristallteich in Maraudon.","Benutzt die gefüllte himmelblaue Phiole mit den Schlangenstrunkranken, damit der verderbte Noxxious-Spross herausgezwungen wird.","Heilt 8 Pflanzen, indem Ihr diesen Noxxious-Spross tötet und kehrt dann zu Vark Battlescar in Shadowprey zurück."}},
[7041] = {"Schlangenzunges Verderbnis", {"Ein Satyr, der unter dem Namen Schlangenzunge bekannt ist, hat die Höhlen von Maraudon verderbt und so das Wachstum vieler Pflanzen im Inneren behindert.","Nehmt diese Phiole, <Name>. Füllt sie am verderbten orangefarbenen Kristallteich, den Ihr in den Höhlen findet. Die Beschichtung in der Phiole wird den Toxinen entgegenwirken und sie in einen Heilstoff verwandeln.","Sucht die Schlangenstrunkranken in den orangefarbenen Höhlen und wendet die geläuterte Flüssigkeit auf sie an. Die Pflanze wird damit von der Verderbnis gereinigt, doch Ihr müsst wissen, dass Schlangenzunge sie immer noch kontrolliert. Er kann sie gegen Euch einsetzen..."}, {"Füllt die beschichtete himmelblaue Phiole am orangefarbenen Kristallteich in Maraudon.","Benutzt die gefüllte himmelblaue Phiole mit den Schlangenstrunkranken, damit der verderbte Noxxious-Spross herausgezwungen wird.","Heilt 8 Pflanzen, indem Ihr diesen Noxxious-Spross tötet und kehrt dann zu Talendria an der Nijelspitze zurück."}},
[7042] = {"Gestohlene Winterhauch-Leckereien", {"Hört zu... ich will Euch ja den Zauber der Feiertage nicht verderben, aber Kokelwälder könnte etwas Hilfe brauchen - von der Art, für die Ihr Abenteurer bekannt seid.","Uns ist eine wichtige Lieferung mit Leckereien abhanden gekommen, mit der wir die Vorräte für diese Feiertage aufstocken wollten, und jetzt ist auch noch der Agent von Kokelwälder verschwunden, den wir losgeschickt haben, um sie zu suchen! Also - die letzten Informationen über die Lieferung stammen aus dem Alteracgebirge, und dort wollte unser Mann danach suchen.","Seht nach, ob sowohl Mann als auch Material dort zu finden sind, und zwar gesund und munter."}, {"Sucht den Ermittler von Kokelwälder, der im Alteracgebirge vermisst wird."}},
[7043] = {"So was Gemeines...", {"Eine K-K-Kreatur namens \"Der monströse Griesgram\" hat u-u-unsere Lieferung mit L-L-Leckereien gestohlen, <Name>. Seid a-a-aber vorsichtig! Er hat m-m-mich in dieses S-S-Schneeschlamassel verwandelt!","Ihr w-w-werdet Hilfe brauchen, wenn Ihr ihm g-g-gegenübertretet. Ich habe gesehen, w-w-wie er dort herumzieht, wo die Y-Y-Yetis sind. Er ist s-s-sicherlich irgendwo h-h-hier! F-F-Findet die Leckereien, <Name>, bringt sie zu Kokelwälder in Ironforge z-z-zurück und s-s-sagt ihnen, dass ich a-a-aufgetaut werden muss!"}, {"Sucht die gestohlenen Leckereien und bringt sie zu Wulmort Jinglepocket nach Ironforge. Zuletzt befanden sie sich im Besitz des monströsen Griesgrams, der sich in den Schneegebieten der Arathi-Berge befindet."}},
[7044] = {"Legenden von Maraudon", {"Ein dunkler Satyr namens Lord Schlangenzunge hat sein Böses in diesen verschlungenen Höhlen verbreitet und die Gedanken aller im Inneren vergiftet. Er residiert noch jenseits der lila Kristalle.","Schlangenzunge hat des Weiteren ein lebendes Symbol seiner Verderbnis namens Noxxion geschaffen, das sich hinter den orangefarbenen Kristallen befindet. Zusammen haben sie die beiden Teile des Szepters meines Bruders gestohlen. Celebras, mein Bruder ... wandert blind im Inneren umher, verflucht durch die Verderbnis.","Ihr müsst ihm helfen! Findet die Stücke und sprecht mit meinem Bruder ... irgendwie."}, {"Beschafft die beiden Teile des Szepters von Celebras: den Celebriangriff und den Celebriandiamanten.","Findet einen Weg, um mit Celebras zu sprechen."}},
[7045] = {"Ein Dankeschön von Kokelwälder!", {"Wir von Kokelwälder sind dankbar für die Wiederbeschaffung der gestohlenen Leckereien, <Name>. Dafür möchten wir Euch ein besonderes Geschenk anbieten... präsentiert von niemand Geringerem als Altvater Winter persönlich!","Bitte, sprecht mit Altvater Winter und er wird Euch Euer Geschenk zum Winterhauchfest von uns hier bei Kokelwälder überreichen. Frisch von unserem Hof auf Euren Tisch: beste Kokelwälder Vollwertkost... dank Euch natürlich!"}, {"Sprecht mit Altvater Winter in Eisenschmiede."}},
[7046] = {"Das Szepter von Celebras", {"Mein Szepter war einst eine Quelle der Hoffnung für mich. Seine Macht ermöglichte mir die Freiheit, schnell durch diese Höhlen zu reisen. Ich hatte gehofft, dem Geist meines Onkels Frieden bringen zu können... Das ist eine Aufgabe, die ich nun an Euch übergebe.","Damit die beiden Teile wieder eins werden können, benötige ich Eure Hilfe bei der Durchführung des Rituals. Folgt mir bitte und hört auf meine Anweisungen, während ich die Energie kanalisiere, die erforderlich ist, um den Griff wieder mit dem Diamanten zu vereinigen.","Zusammen werden wir das Szepter von Celebras wieder erschaffen!"}, {"Helft Celebras dem Erlösten, während er das Szepter von Celebras herstellt.","Sprecht mit ihm, nachdem das Ritual vollendet ist."}},
[7061] = {"Das Winterhauchfest", {"Hier - wenn Ihr daran interessiert seid, mehr über das Winterhauchfest zu erfahren, dann lest dieses Buch. Ich glaube zwar, dass unser Verständnis der Legende zutreffend ist, aber als jemand, der Legenden studiert, weiß ich durchaus eine Sammlung aller Informationen in einer einzelnen Quelle zu schätzen.","Wenn Ihr fertig seid, bringt das Buch zu Cairne Bloodhoof in Thunder Bluff. Ich weiß mit Bestimmtheit, dass es ihm nicht gleichgültig ist, wenn auch andere sich für die Lehre unseres Volkes interessieren."}, {"Ihr könnt gern das Buch 'Das Winterhauchfest' lesen, um mehr über diesen Feiertag zu erfahren. Wenn Ihr das Buch gelesen habt, bringt es zu Cairne Bloodhoof in Thunder Bluff."}},
[7062] = {"Der Anlass für die Feiertage", {"Gaben, Leckereien, Geschenke, pah!","Diese Goblins beschmutzen die wahre Bedeutung dieser Feiertage - des Winterhauchfests. Dies ist eine Zeit der Erneuerung. Das Land schlummert unter einer Schneedecke, die Altvater Winter gebracht hat, und das ist NICHT der Wichtigtuer da drüben in dem roten Aufzug!","Falls Ihr erfahren wollt, worum es bei dem Fest wirklich geht, dann befragt den Historiker Karnik in der Forscherliga. Es dürfte ihn bestimmt überraschen, dass sich jemand für Geschichte statt für Geschenke interessiert."}, {"Sprecht mit dem Historiker Karnik in der Forscherliga von Ironforge über das Winterhauchfest."}},
[7063] = {"Das Winterhauchfest", {"Hier - wenn Ihr daran interessiert seid, mehr über das Winterhauchfest zu erfahren, dann lest dieses Buch. Ich glaube zwar, dass unser Verständnis der Legende zutreffend ist, aber als Gelehrter, der Legenden studiert, weiß ich durchaus eine Sammlung aller Informationen in einer einzelnen Quelle zu schätzen.","Wenn Ihr fertig seid, dann bringt das Buch zu König Magni Bronzebeard. Ich weiß mit Bestimmtheit, dass es ihn sehr interessieren wird, wenn auch andere Interesse für die alten Legenden an den Tag legen."}, {"Ihr könnt gern das Buch 'Das Winterhauchfest' lesen, um mehr über diesen Feiertag zu erfahren. Wenn Ihr das Buch gelesen habt, dann bringt es zu König Magni Bronzebeard nach Ironforge."}},
[7064] = {"Verderbnis von Erde und Samenkorn", {"Tief in Maraudon lebt eine böse Kreatur des Chaos - Prinzessin Theradras ist eine Naturgewalt der Erde, die mit den alten Göttern verwandt ist. Vor langer Zeit gingen sie und Zaetar, der erstgeborene Sohn von Cenarius, eine Beziehung ein. Aus ihrer gemeinsamen Zeit entsprangen Wesen, die unter den Menschen von Kalimdor als Zentauren bekannt wurden. Diese undankbaren Kreaturen töteten Zaetar und bewachen nun seine Überreste. Meine Aufgabe besteht darin, jemanden zu finden, der mächtig genug ist, um Theradras zu erschlagen, damit wir Zaetars Überreste holen können, bevor wir in das Steinkrallengebirge zurückkehren."}, {"Tötet Prinzessin Theradras und kehrt zu Selendra in der Nähe von Shadowprey in Desolace zurück."}},
[7065] = {"Verderbnis von Erde und Samenkorn", {"In den Tiefen von Maraudon lebt eine Kreatur des Chaos und des Bösen, <Name>. Prinzessin Theradras ist eine Naturgewalt der Erde, die mit den alten Göttern verwandt ist. Vor langer Zeit stattete Zaetar, der erstgeborene Sohn von Cenarius, ihr einen Besuch ab. Die Nachkommen ihrer ... Beziehung sind jene Kreaturen, die unter dem Namen Zentauren bekannt sind. Diese undankbaren Kreaturen töteten Zaetar und Theradras bewacht nun seine Überreste. Meine Aufgabe besteht darin, jemanden zu finden, der mächtig genug ist, um Theradras zu erschlagen, damit wir Zaetars Überreste holen können, bevor wir in das Steinkrallengebirge zurückkehren."}, {"Erschlagt Prinzessin Theradras und kehrt zum Bewahrer Marandis an der Nijelspitze in Desolace zurück."}},
[7066] = {"Samenkorn des Lebens", {"Diese missratenen Zentauren - meine Söhne und Töchter - brauchen ihren Vater.","Seht Euch um - dieser Ort hallt wider von Hoffnung und all jenem, was mein Vater Cenarius predigt. All das entsprang aus mir! Und ich kann ihnen das nicht nehmen... nicht einmal ob ihrer Verbrechen gegen mich.","Eines jedoch möchte ich von Euch erbitten, damit mein Bruder Ruhe findet. Nehmt dies... es ist das erste Samenkorn, das aus dem Leben entstand, das meine Überreste nährten. Sagt ihm, dass mein Geist hier bleibt und dass ich weiterlebe. Er wird es sicherlich verstehen."}, {"Sucht Remulos in Moonglade auf und gebt ihm das Samenkorn des Lebens."}},
[7067] = {"Die Anweisungen des Pariahs", {"Obwohl ich als Ketzer verbannt wurde, bleibe ich meinem Glauben treu - das allein treibt mich voran. Aber damit mein Schicksal sich erfüllt, benötige ich die Hilfe eines Außenstehenden... jemanden, der keinem Zentaurenstamm angehört.","Für diese Aufgabe muss jemand die heiligen Hallen von Maraudon betreten - das ist ein Tempel, den mein Volk schützt, und nur die Gläubigsten dürfen ihn betreten.","Wenn Ihr einwilligt, mir zu helfen, dann müsst Ihr nur nicken. Ich werde Euch ein Pergament mit meinen Anweisungen geben. Lest es durch und denkt über meine Worte nach."}, {"Lest die Anweisungen des Pariahs. Beschafft Euch danach das Amulett der Vereinigung von Maraudon und bringt es dem Zentaurenpariah im südlichen Desolace."}},
[7068] = {"Schattensplitter", {"Man findet starke Magie am heiligen Ort der Zentauren... dem heiligen Ort von Maraudon. Die Desolace-Stämme der Zentauren schützen diesen Ort mit ihrem Leben, aber sie können uns nicht davon abhalten, das zu nehmen, was wir brauchen.","Begebt Euch zu diesem Maraudon-Ort und sucht nach den Felskreaturen, die aus Schattenstein bestehen. Bringt mir die lila Kristalle und Uthel'nay wird Euch Eure Mühe vergelten.","Der Ort ist gefährlich, daher solltet Ihr einige Freunde mitnehmen."}, {"Sammelt 10 Schattensplitter aus Maraudon und bringt sie zu Uthel'nay nach Orgrimmar."}},
[7070] = {"Schattensplitter", {"Ich habe kürzlich begonnen, verschiedene Kristalle und ihre arkanen Eigenschaften zu testen. Lady Proudmoore hat mir die Erlaubnis erteilt, Reisende, die nach Desolace ziehen, zu bitten, einen Ort zu suchen, der Maraudon genannt wird. In dem zerfallenen Tempel dort gibt es große Elementare, die aus lila Steinen bestehen. Ich habe sie Schattensplitter getauft.","Falls Ihr beschließt, diese Richtung einzuschlagen, und dort einige dieser Kristalle findet, wäre ich sehr dankbar. Ich würde Euch sogar ein Schmückstück daraus herstellen, wenn Ihr dies wünscht."}, {"Sammelt 10 Schattensplitter in Maraudon und bringt sie zu Erzmagier Tervosh in den Marschen von Dustwallow."}},
[7081] = {"Die Friedhöfe im Alteractal", {"Die Friedhöfe im Alteractal sind äußerst wertvolle Gebiete. Beide Seiten, die Horde und die Allianz, kämpfen verbissen um diese geheiligten Stätten. <Name>, seid Ihr bereit die Kämpfe zu unterstützen?","Ich möchte, dass Ihr einen gegnerischen Friedhof besetzt. Sucht einen Friedhof mit einem Hordenbanner in seiner Mitte und reißt es heraus! Wenn Ihr das schafft, ist meine Aufgabe an Euch erfüllt...","Solltet Ihr es schaffen, den Friedhof für einige Minuten zu halten, werden unsere Verstärkungstruppen eintreffen, um Euch bei der Verteidigung zu unterstützen."}, {"Erobert einen Friedhof und kehrt zu Sergeant Durgen Stormpike im Alteracgebirge zurück."}},
[7082] = {"Die Friedhöfe von Alterac", {"Die Frontlinien in diesem Tal sind in ständiger Bewegung. Die Gebietsgrenzen bewegen sich hin und her, während wir unseren Feinden in tödlichen Schlachten gegenüber treten. Doch um die Friedhöfe, <Name>, wird am grimmigsten gekämpft.","Ich möchte, dass Ihr einen von der Allianz besetzten Friedhof angreift! Sucht einen Friedhof mit einem Allianzbanner in seiner Mitte und reißt es aus dem Boden.","Wenn Ihr das schafft, ist meine Aufgabe an Euch erfüllt...","Solltet Ihr es außerdem schaffen, den Friedhof für einige Minuten zu halten, wird dieser uns gehören und unsere Truppen werden kommen, um Euch bei der Verteidigung dieser wichtigen Position zu unterstützen."}, {"Erobert einen Friedhof und kehrt zu Corporal Teeka Bloodsnarl im Alteracgebirge zurück."}},
[7101] = {"Türme und Bunker", {"Die Türme und Bunker in diesem Tal sind wichtige Stellungen und daher müssen wir sie möglichst alle erobern und halten! Genug geredet... Erobert einen vom Feind kontrollierten Turm oder Bunker.","Betretet dafür einen mit einem Allianzbanner. Im Inneren befindet sich ein weiteres Banner. Zieht das Banner heraus und ein weißes Hordenbanner wird an seiner Stelle erscheinen.","Damit habt Ihr meinen Auftrag erfüllt. Wenn Ihr das eroberte Gebäude lange genug haltet, ohne dass die Allianz es zurückgewinnt, gehört es uns!"}, {"Erobert einen gegnerischen Turm und kehrt anschließend zu Corporal Teeka Bloodsnarl in Frostwolf zurück."}},
[7102] = {"Türme und Bunker", {"Die Türme und Bunker in diesem Tal sind Schlüsselverteidigungspositionen und daher sollten wir soviele wie möglich davon unter Kontrolle halten! Geht und erobert einen vom Feind kontrollierten Turm oder Bunker.","Betretet dafür einen mit einem Hordebanner. Im Inneren befindet sich ein weiteres Banner. Zieht das Banner heraus und ein weißes Allianzbanner wird an seiner Stelle erscheinen.","Damit habt Ihr meinen Auftrag erfüllt. Wenn Ihr das eroberte Gebäude lange genug haltet, ohne dass die Horde es zurückgewinnt, gehört es uns!"}, {"Zerstört das Banner im Inneren eines gegnerischen Bunkers oder Turms. Kehrt anschließend zu Sergeant Durgen Stormpike im Alteracgebirge zurück."}},
[7121] = {"Der Rüstmeister", {"<Name>, unser Rüstmeister hat mich angewiesen, die neuen Rekruten direkt zu ihm zu schicken. Er benötigt dringend Vorräte aus Kisten, die in den Minen von Alterac eingelagert sind und aller Wahrscheinlichkeit nach wird er Euch entsenden, um sie zu beschaffen.","Folgt einfach der Straße. Ihr findet den Rüstmeister im Westen."}, {"Sprecht mit dem Rüstmeister der Stormpike."}},
[7122] = {"Mine besetzen", {"In den Minen des Alteractals ist noch weit mehr zu finden als bloßes Erz. Sowohl die Frostwolf, als auch die Stormpike, haben große Mengen ihrer Ausrüstung in der Irondeep- und der Coldtooth-Mine untergebracht... was eigentlich keine schlechte Idee ist, solange wir die Kontrolle über sie behalten!","Besetzt eine Mine und kehrt zu mir zurück! Um eine Mine zu erobern, betretet eine vom Feind kontrollierte Mine und tötet den dortigen Anführer. Nachdem Ihr das getan habt, gehört die Mine uns und wir werden Truppen zu ihrem Schutz entsenden!"}, {"Erobert eine Mine, die die Stormpike noch nicht kontrollieren, und kehrt dann zu Sergeant Durgen Stormpike im Alteracgebirge zurück."}},
[7123] = {"Sprecht mit unserem Rüstmeister", {"<Name>, der Frostwolf Rüstmeister sucht nach Lehrlingen für Aufgaben, die in die Minen des Tals von Alterac führen. Sprecht mit ihm; er wird Euch mehr berichten.","Folgt der Strasse westlich in den Bunker, dann nehmt den westlichen Ausgang und Ihr findet unseren Rüstmeister."}, {"Sprecht mit dem Rüstmeister der Frostwolf."}},
[7124] = {"Eine Mine besetzen", {"So wie Kämpfe im Gebiet des Alteractals wüten, so wüten sie auch durch die Coldtooth- und Irondeep-Minen. Beide bedeuten enorme Ressourcen an Mineralien und beide werden als Lager genutzt; daher ist es kein Wunder, dass sie so heiß begehrt sind!","Helft uns in der Schlacht, <Name>! Erobert für die Horde eine Mine! Begebt Euch zu einer Mine, die noch nicht von uns kontrolliert wird und tötet den dortigen Anführer! Sobald Ihr das geschafft habt, schicken wir Truppen aus, um die Mine zu bewachen!"}, {"Erobert eine Mine und kehrt zu Corporal Teeka Bloodsnarl im Alteracgebirge zurück."}},
[7141] = {"Die Schlacht um Alterac", {"Die Forscherliga glaubt, dass es im Alteractal einige Artefakte von Interesse gibt. Allerdings werden uns die Frostwolforcs wohl kaum danach forschen lassen. Wir können es der Horde jedoch nicht erlauben, unseren Forschungsbemühungen im Weg zu stehen! Vernichet den General der Frostwolf, Drek'Thar. Mit seinem Tod werden die Orcs ihren Vorteil verlieren.","Drek'Thar hält sich in der Burg Frostwolf auf, im Süden des Alteractals. Bezwingt ihn und kehrt dann zu mir zurück."}, {"Betretet das Alteractal, bezwingt den Hordegeneral Drek'thar und kehrt dann zu Ausgrabungsleiter Stonehewer im Alteracgebirge zurück."}},
[7142] = {"Die Schlacht um Alterac", {"Die Stormpike-Zwerge und ihre Verbündeten sind in das Alteractal eingefallen und wir müssen sie vertreiben! Um die Schlacht um Alterac gewinnen zu können, müssen wir ihren General töten, Vanndar Stormpike!","Ihr werdet Vanndar in Dun Baldar, im Norden des Alteractals finden.","Tötet ihn, <Name>, und kehrt dann zu mir zurück."}, {"Betretet das Alteractal und bezwingt den Zwergengeneral, Vanndar Stormpike. Kehrt anschließend zu Voggah Deathgrip im Alteracgebirge zurück."}},
[7161] = {"Flaggenjagd", {"Seid gegrüßt, <Klasse>. Es scheint eine Ewigkeit her, seit wir zuletzt dem Ruf des Krieges gefolgt sind. Die alten Gefühle erwachen nur langsam. Doch sind sie erst einmal erwacht, beginnt das Blut erneut zu kochen. Die Kampfeslust kehrt zurück...","Alle Neuankömmlinge im Tal müssen sich zuerst dem Ritus des Kampfes unterziehen.","Tief in der nahegelegenen Höhle der Wildpfoten findet Ihr ein Banner der Frostwolf. Zieht los und holt es für uns zurück. Bringt es zu mir und ich gewähre Euch das Abzeichen eines Frostwolfinitiaten."}, {"Begebt euch in die Höhle der Wildpfoten südöstlich vom Hauptstützpunkt und findet das Banner der Frostwolf. Bringt anschließend Kriegsmeister Laggrond das Banner."}},
[7162] = {"Flaggenjagd", {"Ihr seid genau zur rechten Zeit eingetroffen, <Klasse>. Diese Wilden der Frostwolf wollen einfach nicht aufgeben! Wir müssen ihnen die Macht der Stormpike vor Augen führen! Am Ende müssen sie sich unserer Überlegenheit beugen.","Doch bevor Ihr Euch auf das Schlachtfeld begebt, solltet Ihr den Reihen der Stormpike beitreten.","Südwestlich von Dun Baldar befinden sich die Icewinghöhlen. Tief in ihrem Inneren werdet Ihr ein Banner der Stormpike finden. Begebt Euch dorthin und holt das Banner zurück. Bringt es anschließend zu mir und ich werde Euch mit dem Abzeichen eines Stormpikeinitianten auszeichnen."}, {"Begebt euch in die Icewinghöhle südwestlich von Dun Baldar und findet das Banner der Stormpike. Bringt anschließend Lieutenant Haggerdin im Alteracgebirge das Banner."}},
