[8770] = {"Ziel: Verteidiger des Ashischwarms", {"Die Anwesenheit der Silithiden in diesem Gebiet stellt eine immer stärker werdende Bedrohung für unsere Operationen dar. Ihr wurdet ausgewählt, um an einem Angriff auf den Bau des Ashischwarms teilzunehmen. Eure Hauptziele sind die Verteidiger des Ashischwarms.","Erstattet bei Kommandant Mar'alith Bericht, sobald Ihr Eure Aufgabe beendet habt."}, {"Tötet 30 Verteidiger des Ashischwarms und erstattet Bericht bei Kommandant Mar'alith auf Burg Cenarius in Silithus. Ihr benötigt auch Kampfeinsatz I, um diese Aufgabe abschließen zu können."}},
[8771] = {"Ziel: Sandpirscher des Ashischwarms", {"Die Anwesenheit der Silithiden in diesem Gebiet stellt eine immer stärker werdende Bedrohung für unsere Operationen dar. Ihr wurdet ausgewählt, um an einem Angriff auf den Bau des Ashischwarms teilzunehmen. Eure Hauptziele sind die Sandpirscher des Ashischwarms.","Erstattet bei Kommandant Mar'alith Bericht, sobald Ihr Eure Aufgabe beendet habt."}, {"Tötet 30 Sandpirscher des Ashischwarms und erstattet Bericht bei Kommandant Mar'alith auf Burg Cenarius in Silithus. Ihr benötigt auch Kampfeinsatz II, um diese Aufgabe abschließen zu können."}},
[8772] = {"Ziel: Wegbehüter des Zoraschwarms", {"Es wurde Verstärkung für den Angriff auf den Bau des Zoraschwarms angefordert. Man hat Euch ausgewählt, um die Wegbehüter des Zoraschwarms anzugreifen. Erstattet bei Kommandant Mar'alith Bericht, sobald Ihr Eure Aufgabe beendet habt."}, {"Tötet 30 Wegbehüter des Zoraschwarms und erstattet anschließend Bericht bei Kommandant Mar'alith auf Burg Cenarius in Silithus. Ihr benötigt auch Kampfeinsatz VI, um diese Aufgabe abschließen zu können."}},
[8773] = {"Ziel: Häscher des Zoraschwarms", {"Es wurde Verstärkung für den Angriff auf den Bau des Zoraschwarms angefordert. Man hat Euch ausgewählt, um die Häscher des Zoraschwarms anzugreifen. Erstattet bei Kommandant Mar'alith Bericht, sobald Ihr Eure Aufgabe beendet habt."}, {"Tötet 30 Häscher des Zoraschwarms und erstattet Bericht bei Kommandant Mar'alith auf Burg Cenarius in Silithus. Ihr benötigt auch Kampfeinsatz IV, um diese Aufgabe abschließen zu können."}},
[8774] = {"Ziel: Wegelagerer des Regalschwarms", {"Die Anwesenheit der Silithiden in diesem Gebiet stellt, je weiter die Kriegsvorbereitungen voranschreiten, eine immer stärker werdende Bedrohung für unsere Operationen dar. Ihr wurdet ausgewählt, um an einem Angriff auf den Bau des Regalschwarms teilzunehmen. Eure Hauptziele sind die Wegelagerer des Regalschwarms.","Erstattet bei Kommandant Mar'alith Bericht, sobald Ihr Eure Aufgabe beendet habt."}, {"Tötet 30 Wegelagerer des Regalschwarms und erstattet anschließend Bericht bei Kommandant Mar'alith auf Burg Cenarius in Silithus. Ihr benötigt auch Kampfeinsatz VIII, um diese Aufgabe abschließen zu können."}},
[8775] = {"Ziel: Feuerspucker des Regalschwarms", {"Die Anwesenheit der Silithiden in diesem Gebiet stellt eine immer stärker werdende Bedrohung für unsere Operationen dar. Ihr wurdet ausgewählt, um an einem Angriff auf den Bau des Regalschwarms teilzunehmen. Eure Hauptziele sind die Feuerspucker des Regalschwarms.","Erstattet bei Kommandant Mar'alith Bericht, sobald Ihr Eure Aufgabe beendet habt."}, {"Tötet 30 Feuerspucker des Regalschwarms und erstattet anschließend Bericht bei Kommandant Mar'alith auf Burg Cenarius in Silithus. Ihr benötigt auch Kampfeinsatz IX, um diese Aufgabe abschließen zu können."}},
[8776] = {"Ziel: Sklavenjäger des Regalschwarms", {"Die Anwesenheit der Silithiden in diesem Gebiet stellt, je weiter die Kriegsvorbereitungen voranschreiten, eine immer stärker werdende Bedrohung für unsere Operationen dar. Ihr wurdet ausgewählt, um an einem Angriff auf den Bau des Regalschwarms teilzunehmen. Eure Hauptziele sind die Sklavenjäger des Regalschwarms.","Erstattet bei Kommandant Mar'alith Bericht, sobald Ihr Eure Aufgabe beendet habt."}, {"Tötet 30 Sklavenjäger des Regalschwarms und erstattet anschließend Bericht bei Kommandant Mar'alith auf Burg Cenarius in Silithus. Ihr benötigt auch Kampfeinsatz X, um diese Aufgabe abschließen zu können."}},
[8777] = {"Ziel: Einbuddler des Regalschwarms", {"Die Anwesenheit der Silithiden in diesem Gebiet stellt eine immer stärker werdende Bedrohung für unsere Operationen dar. Ihr wurdet ausgewählt, um an einem Angriff auf den Bau des Regalschwarms teilzunehmen. Eure Hauptziele sind die Einbuddler des Regalschwarms.","Erstattet bei Kommandant Mar'alith Bericht, sobald Ihr Eure Aufgabe beendet habt."}, {"Tötet 30 Einbuddler des Regalschwarms und erstattet anschließend Bericht bei Kommandant Mar'alith auf Burg Cenarius in Silithus. Ihr benötigt auch Kampfeinsatz XI, um diese Aufgabe abschließen zu können."}},
[8778] = {"Die Ironforgebrigade braucht Sprengstoff!", {"Uns liegt eine Anforderung der Ironforgebrigade vor, die eine Menge… na sagen wir, recht explosive Komponenten umfasst. Beschafft diese Komponenten und bringt sie zu einem Kerl namens Nozzlespring, einem Mitglied der Ironforgebrigade."}, {"Bringt Arkanist Nozzlespring nahe des Zoraschwarmbaus in Silithus 6 Öle des Feuerbrandes, 5 Einheiten Goblin-Raketentreibstoff und 10 Einheiten dichtes Sprengpulver. Ihr benötigt auch Logistikeinsatz IV, um diese Aufgabe abschließen zu können."}},
[8779] = {"Magische Materialien", {"Geologin Larksbanes Studien haben sich für unsere Operationen in Silithus als sehr wertvoll erwiesen. Vor kurzem hat sie eine Ladung magischer Materialien angefordert, die sie für ihre weiteren Forschungen dringend benötigt.","Beschafft die notwendigen Materialien und liefert sie direkt an sie aus, damit Geologin Larksbane ihre Forschungen ungestört fortsetzen kann."}, {"Bringt 1 großen glänzenden Splitter, 1 großen strahlenden Splitter und einen gewaltigen Smaragd zu Geologin Larksbane auf Burg Cenarius in Silithus. Ihr benötigt auch Logistikeinsatz V, um diese Aufgabe abschließen zu können."}},
[8780] = {"Rüstungssets für den Feldeinsatz", {"Der Ironforgebrigade gehen im Feld die Rüstungssets aus. Schnappt Euch eine Ladung und bringt sie zu Janela Stouthammer am Posten der Ironforgebrigade beim Zoraschwarmbau."}, {"Bringt 8 unverwüstliche Rüstungssets und 8 schwere Rüstungsset zu Janela Stouthammer am Posten der Ironforgebrigade beim Zoraschwarmbau in Silithus. Ihr benötigt auch Logistikeinsatz VII, um diese Aufgabe abschließen zu können."}},
[8781] = {"Waffen für den Feldeinsatz", {"Die Ironforgebrigade hat sich in den Kämpfen von Silithus als ein würdiger Verbündeter erwiesen. Unglücklicherweise hat sie auf dem Weg hierher die meisten ihrer Vorräte eingebüßt. Beschafft eine Ladung Waffen und bringt sie zu Janela Stouthammer nahe des Baus des Zoraschwarms."}, {"Bringt 2 Mondstahlbreitschwerter zu Janela Stouthammer am Posten der Ironforgebrigade beim Zoraschwarmbau. Ihr benötigt auch Logistikeinsatz VI, um diese Aufgabe abschließen zu können."}},
[8782] = {"Uniformnachschub", {"Die letzte Ladung neuer Rekruten hat uns einen Engpass an Uniformen beschert. Beschafft uns eine Ladung Schneiderwaren, damit wir mehr Uniformen herstellen können. Bringt alles zur Windruferin Proudhorn."}, {"Bringt 1 Einheit Mondstoff, 2 Runenstoffballen und 1 Einheit Eisenweberseide zu Windruferin Proudhorn auf Burg Cenarius in Silithus. Ihr benötigt auch Logistikeinsatz VIII, um diese Aufgabe abschließen zu können."}},
[8783] = {"Besondere Materialien", {"Vargus, der Schmied von Burg Cenarius, hat verzauberte Materialien zur Herstellung von stärkeren Waffen angefordert. Beschafft die benötigten Materialien und bringt sie direkt zu Vargus, um den hohen Ausrüstungsstand unserer Truppen auf Burg Cenarius aufrechtzuerhalten."}, {"Bringt Vargus auf Burg Cenarius in Silithus 2 verzauberte Thoriumbarren und 2 Einheiten verzaubertes Leder. Ihr benötigt auch Logistikeinsatz IX, um diese Aufgabe abschließen zu können."}},
[8784] = {"Geheimnisse der Qiraji", {"Ihr habt ein uraltes Artefakt der Qiraji entdeckt! Vielleicht interessiert sich einer der Drachen am Eingang des Tempels für dieses Relikt."}, {"Bringt das uralte Qirajiartefakt zu den Drachen, die sich nahe des Tempeleingangs versteckt halten."}},
[8785] = {"Die Orgrimmarlegion braucht Mojo!", {"Uns liegt eine Anforderung der Orgrimmarlegion vor, die eine Menge recht ungewöhnliche Materialien umfasst. Aber in Anbetracht ihrer jüngsten Leistungen in Silithus, möchten wir dieser Anfrage so schnell wie möglich nachkommen. Beschafft die notwendigen Materialien und bringt sie einer Trollfrau namens Shai."}, {"Bringt Schattenpriesterin Shai nahe des Regalschwarmbaus in Silithus 6 Einheiten mächtiges Mojo, 6 Fläschchen großes Mojo und 8 Öle des Feuerbrandes. Ihr benötigt auch Logistikeinsatz IV, um diese Aufgabe abschließen zu können."}},
[8786] = {"Waffen für den Feldeinsatz", {"Die Orgrimmarlegion hatte bereits vor einiger Zeit zusätzliche Waffen angefordert, die wir bisher noch nicht liefern konnten. Beschafft die notwendigen Waffen und bringt sie zu Merok Longstride außerhalb des Regalschwarmbaus."}, {"Bring 3 massive Eisenäxte zu Merok Longstride im Lager der Orgrimmarlegion außerhalb des Regalschwarmbaus. Ihr benötigt auch Logistikeinsatz VI, um diese Aufgabe abschließen zu können."}},
[8787] = {"Rüstungssets für den Feldeinsatz", {"Die Orgrimmarlegion ist zu unserer Unterstützung in Silithus eingetroffen. Unglücklicherweise hat sie auf dem Weg hierher die meisten ihrer Rüsstungssets eingebüßt. Beschafft eine Ladung und bringt sie zu Merok Longstride im Lager der Orgrimmarlegion."}, {"Bringt 8 unverwüstliche Rüstungssets und 8 schwere Rüstungsset zu Merok Longstride nahe des Regalschwarmbaus. Ihr benötigt auch Logistikeinsatz VII, um diese Aufgabe abschließen zu können."}},
[8788] = {"Ein leicht geschütteltes Geschenk", nil, nil},
[8789] = {"Imperiale Qirajiwaffen", {"Wir haben hier nicht ein Jahrtausend in völliger Untätigkeit verbracht, <Name>. Wir sahen mit eigenen Augen, wie sich diese Biester an ihren eigenen Artgenossen gütlich taten, nachdem diese ihre Nützlichkeit für den Schwarm verloren hatten. Wir lauschten, als sie ihre Pläne schmiedeten, diese Welt in Schutt und Asche zu legen. Und wir lernten, wie sich Verrat und Missgunst in ihren Reihen ausbreiten.","Sie haben Schwächen und wir wissen nun, wie wir diese zu unserem Vorteil nutzen können.","Findet und bringt mir ihre Zeremonienwaffen. Zusammen, mit dem durch ihren Gott erschaffenen Element, werden wir daraus mächtige Instrumente der Zerstörung erschaffen."}, {"Arygos im Tempel von Ahn'Qiraj wird Euch mit Elementium erfüllte Ausrüstung herstellen, wenn Ihr ihm imperiale Qirajiwaffen und 3 Einheiten Elementiumerz bringt."}},
[8790] = {"Imperiale Qirajiinsignien", {"Arygos hat die Theorie aufgestellt, dass das Element, welches während des Konflikts zwischen den uralten Göttern und ihren Anhängern entstanden ist, unter den richtigen Bedingungen für sie tödlich ist. Ich spreche vom Elementium, <Sterblicher/Sterbliche>.","Findet und bringt mir imperiale Qirajiinsignien sowie das giftige Element, dann werde ich Euch Waffen von größter Macht erschaffen!"}, {"Merithra des Traums im Tempel von Ahn'Qiraj wird Euch mit Elementium erfüllte Ausrüstung herstellen. Bringt ihr dazu imperiale Qirajiinsignien und 3 Einheiten Elementiumerz."}},
[8791] = {"Der Untergang von Ossirian", {"Eine schreckliche Bedrohung hat ihr Ende gefunden. Der grausame Ossirian existiert nicht mehr.","Die Bewohner Kalimdors werden aufatmen, wenn sie von diesem glorreichen Sieg erfahren. Nehmt Ossirians Kopf und bringt ihn zu Kommandant Mar'alith auf Burg Cenarius."}, {"Bringt den Kopf von Ossirian dem Narbenlosen zu Kommandant Mar'alith auf Burg Cenarius in Silithus."}},
[8792] = {"Die Horde braucht Euch!", {"Hallo, schön das Ihr Euch entschieden habt, mir zuzuhören, was ich zu sagen habe. Die Horde braucht jede Hilfe, die sie kriegen kann, um sich auf den Krieg in Ahn'Qiraj vorzubereiten. Die Horde braucht Euch! Während wir uns unterhalten, haben die offiziellen Sammler bereits damit begonnen, kriegstaugliche Materialien aller Art zu sammeln. Doch ohne Eure Mithilfe sind all diese Mühen vergebens, <Name>! Wir brauchen Eure Unterstützung!","Am besten unterhaltet Ihr Euch mit dem verantwortlichen Offizier, Kriegsherr Gorchuk. Können wir auf Euch zählen, <Klasse>? Werdet Ihr uns bei den Kriegsvorbereitungen zur Seite stehen?"}, {"Sprecht mit Kriegsherr Gorchuk im Tal der Geister in Orgrimmar."}},
