[8798] = {"Euer eigener Yeti", nil, nil},
[8799] = {"Der Held des Tages", {"Eigentlich darf ich Euch das gar nicht sagen, aber da Ihr ja der Held des Tages seid, mach ich mal 'ne Ausnahme.","Um diese Jahreszeit gibt es überall frische Tannenzweige, aber die Zweige bleiben nicht lange frisch. Wir haben eine Maschine, die Tannenzweige konserviert - die bleiben dann für immer frisch! Wir benutzen sie um die Festtage etwas aufzupeppen... und unsere Profite im Sommer zu steigern!","Ich lasse Euch den Konservierer benutzen, aber nur, wenn Ihr ein Meisterkoch seid. Ihr werdet etwas Tiefsteinsalz und etwa zwei, drei, ach, sagen wir einfach fünf Goldstücke brauchen. Damit solltet Ihr ein nettes Bündel konservieren können, das Ihr behalten könnt."}, {"Bringt 1 Tiefsteinsalz und 5 Goldstücke zum Tannenzweigkonservierer. Ihr werdet 5 konservierte Tannenzweige erhalten."}},
[8800] = {"Schlachtrüstung des Cenarius", {"Zwischen dem Erledigen der Euch im Krieg zugeteilten Aufträge, solltet Ihr ab und an mit Vargus dem Schmied sprechen. Zeigt ihm Eure erworbenen Abzeichen und er wird Euch im Gegenzug mit der Eurem Einsatz entsprechenden Ausrüstung versorgen."}, {"Sprecht mit Vargus auf Burg Cenarius in Silithus."}},
[8801] = {"C'Thuns Vermächnis", {"Die Mauern von Ahn'Qiraji erbeben. Eine finstere Macht, älter noch als die Welt, wurde vernichtet.","Als Ihr auf die Überreste der gigantischen Scheußlichkeit hinab blickt, scheint Euer Herz fast zu gefrieren. Selbst im Tod, könnt Ihr das böse Erbe C'Thuns um Euch herum spüren.","Ihr habt das Unmögliche vollbracht.","Eines seiner Augen, scheint Euch noch immer anzustarren. Ihr nehmt es auf und verstaut es in Eurem Gepäck.","Bringt den Drachen im nächsten Raum das Auge von C'Thun."}, {"Bringt Caelastrasz im Tempel von Ahn'Qiraj das Auge von C'Thun."}},
[8802] = {"Der Retter von Kalimdor", {"Begebt Euch zu den Höhlen der Zeit, <Lord/Lady> <Name>. Anachronos erwartet Eure Rückkehr. Übergebt ihm das Auge von C'Thun. Sicherlich wird er es der Sammlung seines Meisters hinzufügen wollen."}, {"Bringt Anachronos in den Höhlen der Zeit das Auge von C'Thun."}},
[8803] = {"Ein Festtagsgeschenk", nil, nil},
[8804] = {"Wüstenüberlebenspaket", {"Seitdem ständig neue Rekruten auf Burg Cenarius eintreffen, stehen wir vor dem Problem, sie in der Wüste am Leben zu erhalten. Der ständige Wasserentzug und die zahlreichen giftigen Kreaturen in dieser Gegend sind eine ernstzunehmende Gefahr für die unerfahren Soldaten. Sammelt alles, was notwendig ist, um ein Wüstenüberlebenspaket zusammenzustellen und bringt die erforderlichen Vorräte zu Calandrath auf Burg Cenarius."}, {"Bringt 4 Kugeln des Wassers, 4 mächtige Gegengifte und 4 geräucherte Wüstenknödel zu Calandrath in der Taverne von Burg Cenarius in Silithus. Ihr benötigt auch Logistikeinsatz I, um diese Aufgabe abschließen zu können."}},
[8805] = {"Stiefel für die Wache", {"Die berittene Einheit der Wache von Burg Cenarius hat kaum noch Vorräte an verschnörkelten Mithrilstiefeln. Beschafft Euch eine Ladung und bringt sie sofort zu Captain Vish Kozus auf Burg Cenarius."}, {"Bringt 3 Paar verschnörkelte Mithrilstiefel zu Vish Kozus, Captain der Wache von Burg Cenarius in Silithus. Ihr benötigt auch Logistikeinsatz II, um diese Aufgabe abschließen zu können."}},
[8806] = {"Schleifsteine für die Wache", {"Die Wache von Burg Cenarius hat kaum noch Vorräte an Schleifsteinen. Beschafft Euch eine Ladung und bringt sie sofort zu Captain Vish Kozus auf Burg Cenarius."}, {"Bringt 10 verdichtete Schleifsteine, 10 robuste Schleifsteine und 10 schwere Schleifsteine zu Vish Kozus, Captain der Wache von Burg Cenarius in Silithus. Ihr benötigt auch Logistikeinsatz III, um diese Aufgabe abschließen zu können."}},
[8807] = {"Magische Materialien", {"Geologin Larksbanes Studien haben sich für unsere Operationen in Silithus als sehr wertvoll erwiesen. Vor kurzem hat sie eine Ladung magischer Materialien angefordert, die sie für ihre weiteren Forschungen dringend benötigt.","Beschafft die notwendigen Materialien und liefert sie direkt an sie aus, damit Geologin Larksbane ihre Forschungen ungestört fortsetzen kann."}, {"Bringt 1 großen glänzenden Splitter, 1 großen strahlenden Splitter und 1 gewaltigen Smaragd zu Geologin Larksbane auf Burg Cenarius in Silithus. Ihr benötigt auch Logistikeinsatz V, um diese Aufgabe abschließen zu können."}},
[8808] = {"Uniformnachschub", {"Die letzte Ladung neuer Rekruten hat uns einen Engpass an Uniformen beschert. Beschafft uns eine Ladung Schneiderwaren, damit wir mehr Uniformen herstellen können. Bringt alles zur Windruferin Proudhorn."}, {"Bringt 1 Einheit Mondstoff, 2 Runenstoffballen und 1 Einheit Eisenweberseide zu Windruferin Proudhorn auf Burg Cenarius in Silithus. Ihr benötigt auch Logistikeinsatz VIII, um diese Aufgabe abschließen zu können."}},
[8809] = {"Besondere Materialien", {"Vargus, der Schmied von Burg Cenarius, hat verzauberte Materialien zur Herstellung von stärkeren Waffen angefordert. Beschafft die benötigten Materialien und bringt sie direkt zu Vargus, um den hohen Ausrüstungsstand unserer Truppen auf Burg Cenarius aufrechtzuerhalten."}, {"Bringt Vargus auf Burg Cenarius in Silithus 2 verzauberte Thoriumbarren und 2 Einheiten verzaubertes Leder. Ihr benötigt auch Logistikeinsatz IX, um diese Aufgabe abschließen zu können."}},
[8810] = {"Verbände für den Feldeinsatz", {"Nach den neusten Verlusten, die wir hinnehmen mussten, gehen unsere Vorräte an Verbänden für das Schlachtfeld gefährlich zur Neige. Besorgt Euch eine Ladung an Vorräten für Erste Hilfe und bringt sie zu Windruferin Proudhorn auf Burg Cenarius."}, {"Bringt 30 schwere Runenstoffverbände, 30 schwere Seidenverbände und 30 schwere Magiestoffverbände zu Windruferin Proudhorn auf Burg Cenarius in Silithus. Ihr benötigt auch Logistikeinsatz X, um diese Aufgabe abschließen zu können."}},
