LangQuestLookup["frFR"] = {
[2] = {"La griffe de Serres-tranchantes", {"Le grand hippogryphe Serres-tranchantes a été tué, et la griffe arrachée à son cadavre témoigne de votre victoire. Senani Thunderheart, au poste de Bois-brisé, sera sans doute intéressée de voir ce trophée qui est la preuve de votre exploit."}, {"Apporter la griffe de Serres-tranchantes à Senani Thunderheart, au poste de Bois-brisé, en Ashenvale."}},
[5] = {"L'estomac qui gargouille", {"Je suis resté caché dans cette ville fantôme depuis des semaines.","Apportez-moi de quoi festoyer et je vous payerai généreusement.","J'ai entendu parler d'un certain chef Grual qui fait de succulents gâteaux au crabe brun, à la taverne du Corbeau écarlate à Darkshire..."}, {"Parler au chef Grual."}},
[6] = {"La prime pour Garrick Padfoot", {"Garrick Padfoot – un brigand qui harcèle les fermiers et les marchands de la région depuis des semaines – a été vu dans une cabane près des vignes qui se trouvent à l'est de l'abbaye, juste après le pont. Rapportez-moi la tête de ce mécréant, et vous empocherez la récompense !"}, {"Tuer Garrick Padfoot et rapporter sa tête à l'Adjoint Willem de l'Abbaye du Northshire."}},
[7] = {"Nettoyer le camp des kobolds", {"Votre première tâche est un nettoyage, <nom>. Un clan de kobolds a envahi les bois se trouvant au nord. Allez là-bas et combattez cette vermine. Affaiblissez-la assez, pour qu'un jour nous puissions la repousser hors du Northshire."}, {"Tuer 10 Vermines kobold, puis revenir voir le maréchal McBride."}},
[8] = {"Un marché de voleur", {"Hé ! Voudriez-vous faire une faveur à un homme qui a eu plus que sa part de combats contre des zombies et des araignées ?","J'ai ici une lettre qui doit être livrée à Brill... à une aubergiste du nom de Renée quelque chose. Enfin c'est pas important son nom de famille.","Bref, c'est un joli petit coin, plein de personnes victimes de la peste qui essayent de trouver leur voie en ce bas monde. De plus, c’est un bon endroit pour se reposer. Vous devriez aller voir... D'autant que je vous payerai bien."}, {"Livrer une Lettre à Renée dans les Clairières de Tirisfal."}},
[9] = {"Les champs de mort", {"Regardez ce qu'il s'est passé ici ! Ces terres étaient autrefois habitées par de braves fermiers. Mais ces maudits voleurs les ont tous chassés. Pas moi, pour sûr ! Mais il semble que quelques Gardiens des moissons ont pris possession des champs.","Si vous êtes d'accord, j'aimerais que vous y alliez et que vous en détruisiez une vingtaine. Revenez quand vous aurez fini pour recevoir votre dû. Une fois que vous aurez débarrassé ceux de mon champ, occupez-vous des champs alentours."}, {"Le Fermier Saldean veut que vous tuiez 20 Gardiens des moissons."}},
[10] = {"La rédemption de Scrimshank", {"Oh, c'est terrible ! J'ai envoyé mon premier assistant, le géomètre novice Scrimshank, dans le désert au sud-est d'ici. C'est un endroit appelé le Gouffre béant. Et il est porté disparu. Dans son dernier rapport, il faisait allusion à ces étranges insectes près de là où il était. Serait-il possible qu'ils aient maintenant infesté deux grandes régions du désert ?","J'ai besoin que vous alliez retrouver Scrimshank dans le Gouffre béant ! Ou, au pire, que vous retrouviez son équipement topographique. Je crains que ce soit tout ce qu'il reste de lui."}, {"Découvrir ce qu'est devenu le géomètre Scrimshank, et rapporter son équipement topographique au grand géomètre Fizzledowser à Gadgetzan."}},
[11] = {"Prime pour les gnolls Rivepatte", {"Des gnolls, des animaux brutaux qui n'ont rien à faire ici, ont été aperçus à la lisière de la forêt d'Elwynn. C'est un très grand groupe, bien trop nombreux pour que nous nous en occupions seuls. Ils ont envahi les bois au sud de la tour de garde. Un autre groupe à envahi les zones autour du lac du Cairn à l'est.","L'armée de Stormwind engage quiconque pourrait en tuer. Apportez-moi des Brassards peints de gnolls pour me prouver vos actions."}, {"Apporter 8 Brassards peints de gnolls à l'Adjoint Rainer à la Caserne."}},
[12] = {"La milice du peuple", {"La Milice du peuple n'a qu'un seul objectif : défendre la marche de l'Ouest et ramener le calme dans les environs. Malheureusement, le prix de la paix est souvent le sang.","Un de mes éclaireurs rapporte qu'une bande de Trappeurs défias sème le trouble dans le coin. Ils auraient été aperçus près de la Mine de Jangolode, au nord-ouest, ainsi que dans la Ferme des Molsen et la Ferme de potirons des Furlbrow. Si vous voulez rejoindre nos rangs, tuez 15 Trappeurs défias et 15 Contrebandiers défias, puis revenez me voir."}, {"Tuer 15 Trappeurs défias et 15 Contrebandiers défias pour Gryan Stoutmantle, puis retourner le voir sur la Colline des sentinelles."}},
[13] = {"La milice du peuple", {"Une bande de pilleurs défias a été vue en train de saccager la carrière de la Côte de l'or ainsi que Ruisselune et la ferme des Alexston. La Milice du peuple n'acceptera pas ce genre de comportement. Déployez-vous immédiatement, <nom>, et faites en sorte que l'on sache que la Lumière est présente dans la marche de l'Ouest.","La carrière est près de la côte, à l'ouest de la tour. Comme étape de votre instruction, je veux que vous tuiez 15 Pilleurs défias et 15 Pillards défias."}, {"Tuer 15 Pilleurs défias et 15 Pillards défias pour Gryan Stoutmantle, puis retourner le voir sur la Colline des sentinelles."}},
[14] = {"La milice du peuple", {"Quelques Défias nous ont échappé. Mon éclaireur le plus fiable fait état de pillages défias dans les campagnes, au sud de la marche de l'Ouest. Nous pensons qu'ils se cachent dans les collines de la Dague, fomentant leur prochain coup. Détruisez ces misérables au nom de la Milice du peuple."}, {"Tuer 15 Malandrins défias, 5 Eclaireurs défias et 5 Truands défias pour Gryan Stoutmantle, puis retourner le voir sur la Colline des sentinelles."}},
[15] = {"Enquête sur la crête aux échos", {"<nom>, mes éclaireurs me rapportent que l'infestation kobold est d'une plus grande envergure que nous le pensions. Un groupe d'ouvriers kobolds a établi son campement près de la mine de la Crête aux échos au nord.","Allez à la mine et faites en sorte qu'il n'y en ait plus. Nous savons qu'il y en a au moins 10. Tuez-les, voyez s'il y en a davantage, et revenez me présenter votre rapport."}, {"Tuer 10 Ouvriers kobolds, puis revenir faire son rapport au maréchal McBride."}},
[16] = {"Procurer de l'eau à Gerard", nil, nil},
[17] = {"Rechercher les composants à Uldaman", {"On a découvert récemment une nouvelle plante : les champignons magenta. Ils poussent en grappes, au plus profond des excavations d'Uldaman ; il en existe peut-être ailleurs, mais je ne connais que cet endroit. Je veux étudier leur utilité éventuelle en alchimie, et c'est là que vous entrez en scène.","Trouvez les Champignons magenta et rapportez-m'en une douzaine. Attention, les grappes peuvent projeter des spores vénéneux quand on les manipule !","Rendez-moi ce service et je sortirai pour vous un petit Elixir revigorant de derrière les fagots."}, {"Apporter 12 Champignons magenta à Ghak Healtouch à Thelsamar."}},
[18] = {"La confrérie des voleurs", {"Un groupe de voleurs traîne depuis peu autour de Northshire. Ils se nomment eux-mêmes la Confrérie défias, et ont été aperçus de l'autre côté de la rivière à l'est.","Je ne sais pas ce qu'ils préparent, mais il est certain que ce n'est rien de bon ! Apportez-moi les Masques qu'ils portent, et je vous récompenserai d'une arme."}, {"Apporter 12 Masques rouges à l’Adjoint Willem à l’extérieur de l’Abbaye de Northshire."}},
[19] = {"Tharil'zun", {"Les renégats et les estafettes Blackrock tendent des embuscades entre ici et le donjon de Guet-de-pierre. Le chef des estafettes est un orc du nom de Tharil'zun – nous voulons qu'il meure. Rapportez-moi la tête de Tharil'zun !"}, {"Rapporter la Tête de Tharil'zun au maréchal Marris, dans les Carmines."}},
[20] = {"La menace des Blackrock", {"Les orcs Blackrock ont voyagé depuis les Steppes ardentes et ont pris possession du donjon de Guet-de-pierre, depuis le nord. De cette base, ils patrouillent dans toutes les zones au nord de Lakeshire et forcent les citoyens à rester en ville, parqués comme du bétail.","Aidez à libérer Lakeshire de cette oppression. Tuez ces orcs Blackrock et rapportez-moi 10 haches détériorées comme preuve de votre réussite.","Bien que les orcs Blackrock nous menacent au nord, nous avons des informations disant qu’il y aurait aussi des orcs au sud-est des Carmines. Si vous en trouvez là-bas, tuez-les."}, {"Rapporter 10 Haches détériorées au maréchal Marris de Lakeshire."}},
[21] = {"La bataille de la Crête aux échos", {"Vos dernières investigations ont prouvé que la mine de la Crête aux échos devait être nettoyée. Retournez-y et chassez les kobolds.","Ne perdez pas de temps, <nom>. Plus les kobolds seront tranquilles dans la mine, plus ils renforceront leurs positions dans le Northshire."}, {"Tuer 12 Travailleurs kobold, puis revenir voir le maréchal McBride, à l’Abbaye de Northshire."}},
[22] = {"La tourte au foie de Broche-tripes", {"Les oignons sont pelés et l'ail émincé. Le romarin est pilé. La pâte est cuite et le fenouil haché. La sauce finit de mijoter. Il ne me manque que 8 foies de Broche-tripes pour réussir ma fameuse tourte à la viande !"}, {"Salma Saldean a besoin de 8 Foies de Broche-tripes pour faire une Tourte au foie de Broche-tripes."}},
[23] = {"La patte d'Ursangous", {"La patte du puissant ours Ursangous représente votre trophée pour cette partie de la Chasse d'Ashenvale. Sur la patte figure un petit emblème en forme de croissant de lune, dont la signification reste mystérieuse.","La Patte doit être apportée à Senani Thunderheart, au Poste de Bois-brisé, comme preuve que vous avez abattu la puissante créature et terminé cette partie de la Chasse."}, {"Apporter la Patte d'Ursangous à Senani Thunderheart, au Poste de Bois-brisé, à Ashenvale."}},
[24] = {"La tête de Shadumbra", {"Le rapide et redoutable Shadumbra gît, immobile et silencieux, maintenant que vous l'avez vaincu. La tête du sabre-de-nuit est un trophée de première classe, parfaitement à même de satisfaire les conditions de la Chasse d'Ashenvale. Senani Thunderheart, au Poste de Bois-brisé, aimerait voir un tel trophée pour pouvoir valider cette partie de la Chasse d'Ashenvale."}, {"Apporter la Tête de Shadumbra à Senani Thunderheart, au Poste de Bois-brisé, dans Ashenvale."}},
[25] = {"Impasse aux Serres-Rocheuses", {"Si vous voulez vous rendre utile, pourquoi n'iriez-vous pas essayer de régler le problème près de la Perce des Serres ?","Les convois entre Ashenvale et les Serres-Rocheuses sont au point mort. En effet, c'est comme si le lac avait pris vie et attaquait tout ce qui s'approche à proximité... c'est à dire quiconque s'aventure sur la Perce. Au lieu de simplement massacrer ces élémentaires d'eau, il serait vraiment utile que quelqu'un aille reconnaître le kiosque du lac Mystral ; celui qui offre une si belle vue sur l'avant-poste de l'Alliance à cet endroit.","Alors, voulez-vous vous rendre utile ?"}, {"Tuer 12 Elémentaires d’eau corrompus au lac Mystral, à l’est de la Perce des Serres, et au sud-ouest du poste de Bois-brisé.","Faire une reconnaissance sur le kiosque du lac Mystral ; celui qui a vue sur l’avant-poste de l’Alliance.","Retourner voir Mastok Wrilehiss, au poste de Bois-brisé, dans Ashenvale."}},
[26] = {"Une leçon à apprendre", {"<nom>, j'ai suivi votre progression avec beaucoup de plaisir. Vous êtes assurément en train de devenir <un véritable gardien et protecteur/une véritable gardienne et protectrice> de la nature.","Le temps est venu pour vous d'en apprendre plus à propos de l'une des nombreuses formes animales que nous pouvons revêtir et dont nous gratifie Cénarius. Un aspect qui vous permettra de maîtriser l'eau. Pour cela, vous devez voyager jusqu'au village de Havrenuit, à Reflet-de-Lune, et parler à Dendrite Starblaze, du Cercle cénarien. Il vous guidera plus avant."}, {"Parler à Dendrite Starblaze, au village de Havrenuit, à Reflet-de-Lune."}},
[27] = {"Une leçon à apprendre", {"<nom>, j'ai suivi votre progression avec beaucoup de plaisir. Vous êtes assurément en train de devenir <un véritable gardien et protecteur/une véritable gardienne et protectrice> de la nature.","Le temps est venu pour vous d'en apprendre plus à propos de l'une des nombreuses formes animales que nous pouvons revêtir et dont nous gratifie Cénarius. Un aspect qui vous permettra de maîtriser l'eau. Pour cela, vous devez voyager jusqu'au village de Havrenuit, à Reflet-de-Lune, et parler à Dendrite Starblaze, du Cercle cénarien. Il vous guidera plus avant."}, {"Parler à Dendrite Starblaze, au village de Havrenuit, à Reflet-de-Lune."}},
[28] = {"L'épreuve du lac", {"Pour la première épreuve, vous devrez aller fouiller les profondeurs du lac Elune'ara, et trouver un Colifichet du sanctuaire. Attention, il se corrompt rapidement ; aussi, ne tardez pas à l'apporter au sanctuaire de Remulos. Utilisez le Colifichet sur le sanctuaire puis parlez à Tajarri ; elle est l'un des gardiens les plus fiables de Reflet-de-Lune.","Vous devez réussir cette quête avant l'expiration du temps imparti, et vous serez peut-être en concurrence avec d'autres druides, également à la recherche de colifichets. Il vous faudra user de ruse et de dextérité pour réussir dans cette mission.","Bonne chance, <nom>."}, {"Trouver un Colifichet du sanctuaire dans le Lac Elune'ara, et l’apporter au sanctuaire de Remulos, au nord-ouest de Reflet-de-Lune. Une fois sur place, utiliser le Colifichet du sanctuaire.","Vous devez parler à Tajarri, au sanctuaire, afin de terminer l’épreuve."}},
[29] = {"L'épreuve du lac", {"Pour la première épreuve, vous devrez aller fouiller les profondeurs du lac Elune'ara, et trouver un Colifichet du sanctuaire. Attention, il se corrompt rapidement ; aussi, ne tardez pas à l'apporter au sanctuaire de Remulos. Utilisez le Colifichet sur le sanctuaire puis parlez à Tajarri ; elle est l'un des gardiens les plus fiables de Reflet-de-Lune.","Vous devez réussir cette quête avant l'expiration du temps imparti, et vous serez peut-être en concurrence avec d'autres druides, également à la recherche de colifichets. Il vous faudra user de ruse et de dextérité pour réussir dans cette mission.","Bonne chance, <nom>."}, {"Trouver un Colifichet du sanctuaire dans le Lac Elune'ara, et l’apporter au sanctuaire de Remulos, au nord-ouest de Reflet-de-Lune. Une fois sur place, utiliser le Colifichet du sanctuaire.","Vous devez parler à Tajarri, au sanctuaire, afin de terminer l’épreuve."}},
[30] = {"L'épreuve du lion de mer", {"Pour la seconde épreuve, vous aurez besoin des deux moitiés qui constitueront le Pendentif du lion de mer. L'une des moitiés emprunte au lion de mer son agilité à glisser à travers les eaux. L'autre moitié emprunte à l'animal son incroyable endurance. Les druides doivent revêtir ces deux aspects pour pouvoir adopter leur forme aquatique.","Allez parler aux résidents de Reflet-de-Lune pour apprendre où les deux parties se trouvent, puis rapportez-les ici, afin de pouvoir les joindre ensemble. Quand vous aurez formé le pendentif, apportez-le à Dendrite Starblaze."}, {"Trouver le Demi-pendentif d'agilité aquatique et le Demi-pendentif d'endurance aquatique. Parler aux habitants de Reflet-de-Lune pour obtenir des indices quant à l’endroit où ces objets se trouvent.","Reformer le Pendentif du lion de mer à partir des deux moitiés. Vous devez vous trouver à proximité du sanctuaire de Remulos pour faire cela.","Apporter le Pendentif reformé à Dendrite Starblaze, au village de Havrenuit, à Reflet-de-Lune."}},
[31] = {"Forme aquatique", {"Vous avez terminé les leçons nécessaires, enseignées à tous les jeunes druides avant qu'ils ne puissent adopter un aspect aquatique. Retournez voir votre Maître, Turak Runetotem, à Thunder Bluff. Présentez-lui votre pendentif et montrez-lui que vous êtes en mesure d'apprendre ce qu'il est prêt à vous enseigner. Il veillera à parachever votre entraînement, vous permettant de revêtir une forme aquatique.","Au revoir, jeune druide. Nous nous reparlerons plus tard."}, {"Retourner à Thunder Bluff et montrer le Pendentif du lion de mer à Turak Runetotem."}},
[32] = {"L'ascension des silithides", {"Voici votre rapport. Je vous demande de l'apporter de toute urgence à quelqu'un qui a de l'expérience dans ce domaine.","Je connais un troll érudit du nom de Zilzibin Drumlore. Il a passé beaucoup de temps à étudier les formes de vie exceptionnelles de l'écologie d'Azeroth ; de plus, c'est un historien ! Si vous voulez rencontrer quelqu'un capable de comprendre la nature de ce danger et comment l'arrêter, apportez-lui ce rapport. Nous allons avoir besoin de toute l'aide disponible.","Zil réside dans une maison sur le second tiers de la Herse, à Orgrimmar."}, {"Apporter le rapport à Zilzibin Drumlore à Orgrimmar."}},
[33] = {"Les loups derrière la frontière", {"J'ai horreur de ces maudits loups des bois ! Mais j'aime beaucoup en déguster des steaks… Apportez-moi de la viande de loup coriace et je vous donnerai en contrepartie quelque chose que vous devriez trouver utile.","On peut trouver de la viande de loup coriace en chassant les loups des bois et les jeunes loups qui parcourent la campagne de Northshire."}, {"Apporter 8 pièces de Viande de loup coriace à Eagan Peltskinner, à l’extérieur de l’Abbaye de Northshire."}},
[34] = {"Un indésirable", {"Mes jardins ont encore été saccagés. Et je connais le responsable. C'est ce satané sanglier, celui que le maréchal Marris appelle Fouailleventre.","Je ne vais pas pouvoir replanter mes jonquilles avant la prochaine saison maintenant. Ruinée ! Je suis complètement ruinée à cause de ce sanglier ! Mettez un terme à cette nuisance ! Montrez-moi ses défenses et je serai heureuse de vous récompenser. Il semble qu'il passe son temps dans les champs au sud-ouest de Lakeshire, mais il vagabonde de temps en temps jusque dans mon jardin."}, {"Martie Jainrose de Lakeshire veut que vous tuiez Fouailleventre. Ramenez-lui ses défenses comme preuve."}},
[35] = {"Inquiétudes supplémentaires", {"Si vous voulez savoir si les rumeurs concernant les murlocs sont vraies, allez donc au pont, à l'est d’Elwynn et parlez au garde Thomas. Il y est en poste depuis une semaine, il saura de quoi il retourne.","Ramenez-moi son compte rendu."}, {"Le maréchal Dughan veut que vous parliez au garde Thomas."}},
[36] = {"Le ragoût de la marche de l'Ouest", {"Je n'avais jamais pensé un jour quitter ma ferme. Mais les champs grouillent de voleurs, et cela devient vraiment trop dangereux pour nous. Dès que la charrette du Fermier Furlbrow sera réparée, nous partirons.","Vous pourriez peut-être me faire une faveur? Laissez-moi écrire la recette du ragoût de la marche de l'Ouest. S'il vous plaît, apportez-la à Salma Saldean à la ferme de l'autre côté, là-bas. La ferme des Saldean est un peu plus loin sur la route, juste après l'embranchement."}, {"Verna Furlbrow veut que vous apportiez la Recette du ragoût de la marche de l'Ouest à Salma Saldean."}},
[37] = {"Trouver les gardes disparus", {"Il y a quelques jours, j'ai envoyé deux gardes, Rolf et Malakai, patrouiller le long de la rivière, et ils ne sont toujours pas revenus. Afin de finaliser mon rapport, je dois absolument savoir ce qu'il est advenu de ces hommes.","Allez vers le nord, le long de la rivière, et trouvez les gardes... ou ce qu'il en reste."}, {"Le garde Thomas veut que vous alliez vers le nord, le long de la rivière, à la recherche de deux gardes disparus, Rolf et Malakai."}},
[38] = {"Le ragoût de la marche de l'Ouest", {"Aidez-moi à faire du ragoût de la marche de l'Ouest. Ramenez les ingrédients suivants :","3 morceaux de Viande filandreuse de vautour ;","3 Groins de Broche-tripes ;","3 Yeux de murloc ;","3 Okras."}, {"Salma Saldean veut 3 morceaux de Viande filandreuse de vautour, 3 Groins de Broche-tripes, 3 Yeux de murloc et 3 Okras."}},
[39] = {"Remettre le rapport de Thomas", {"Allez annoncer au maréchal Dughan la mort de Malakai et de Rolf, et prévenez-le que les murlocs à l'est d'Elwynn ne pourront pas être contenus par les troupes présentes actuellement.","Je sais que nous n’avons pas beaucoup de troupes disponibles, mais Dughan trouvera une solution."}, {"Avertir le maréchal Dughan de Goldshire."}},
[40] = {"Péril en la rivière", {"<nom>, il y a une nouvelle menace dans la forêt d'Elwynn ! Les murlocs remontent le cours est de l'Elwynn, effrayant les poissons et attaquant des personnes innocentes !","J'ai averti le maréchal Dughan, mais il est plus préoccupé par les gnolls et les bandits. Il n'est pas convaincu que les murlocs représentent un réel danger.","S'il vous plaît, <nom>, parlez à Dughan et persuadez-le d'envoyer des renforts à l'est !"}, {"Remy \"Deux fois\" veut que vous parliez au maréchal Dughan à Goldshire."}},
[45] = {"La destinée de Rolf", {"En fouillant un peu plus dans la zone, vous tombez sur des traces de pas palmés qui mènent vers l'est, le long de la rive du lac du Cairn. Légèrement plus loin, vous apercevez un village murloc.","Peut-être que le destin de Rolf s'est arrêté ici..."}, {"Fouiller le village des murlocs pour trouver Rolf ou des traces de sa mort."}},
[46] = {"Des primes pour les murlocs", {"L'armée de Stormwind a mis à prix la tête des rôdeurs et des fourrageurs murlocs qui se trouvent à Elwynn. Tuez-les, découpez leurs ailerons et ramenez-les-moi, l'armée de Stormwind vous récompensera.","Les murlocs ont construit un village au lac du Cairn au nord d'ici, et un autre au sud, là où la rivière se sépare."}, {"Ramener 8 Ailerons de murloc au garde Thomas au Pont est d'Elwynn."}},
[47] = {"L'échange de poussière d'or", {"Par ici les kobolds transportent quelquefois de la poussière d'or sur eux. Cela peut m'être très utile : rapportez-m'en et je vous en donnerai le meilleur prix de la ville ! Le meilleur prix de la ville !","Vous trouverez des kobolds dans la mine de Fargodeep, au sud, et autour de la mine de Jasperlode au nord-est."}, {"Rapporter 10 Poussières d'or à Remy \"Deux fois\" à Goldshire. La Poussière d'or se récupère sur les kobolds dans la forêt d'Elwynn."}},
[48] = {"Doux whisky", {"Dans la campagne du Sud, là où la vigne pousse, en rampant et se tordant","Se cache un puits où l'eau coule profondément","Aussi pure que la Sœur sacrée de la Lumière, elle est","Apportez-moi un peu de cette eau sacrée."}, {"Rapporter de l'Eau de source sacrée à Grimbooze Thunderbrew."}},
[49] = {"Doux whisky", {"Le whisky, de ma vie est le plus ancien amour","Comme les étoiles qui durent pour toujours","Rapportez-moi, pour que mon amour revienne majestueux tel un aigle","Un sac de chacune de ces céréales : orge, maïs et seigle."}, {"Grimbooze Thunderbrew veut un Sac d'orge, un Sac de seigle et un Sac de maïs."}},
[50] = {"Doux whisky", {"Je mélangerai et mélangerai, jusqu'à de la pulpe obtenir","Pour que ce soit notre petit cache sans faiblir","En métal solide, il faut que cela soit fait","Pour me permettre de finir cette chanson, du vrai-argent il faut me rapporter."}, {"Grimbooze Thunderbrew veut du Vrai-argent."}},
[51] = {"Doux whisky", {"Pas encore filtré, mais déjà distillé","Comme une terre inconnue attendant d'être trouvée","Puis pour faire un tas de charbon il faut être actif","Livrez-moi une branche de sycomore, ceci est votre objectif."}, {"Apporter une Branche de sycomore à Grimbooze Thunderbrew."}},
[52] = {"Protéger la frontière", {"Salutations, <nom>. Les animaux sauvages deviennent de plus en plus agressifs à mesure que l'on s'éloigne de Goldshire, et le camp de bûcherons du Val d'est subit de perpétuelles attaques de la part de loups et d'ours !","Nous pourrions avoir besoin de votre aide."}, {"Tuer 8 Rôdeurs et 5 Jeunes ours des forêts, et revenir voir le garde Thomas au pont, à l'est d’Elwynn."}},
[53] = {"Doux whisky", {"Douce eau aussi translucide que des larmes d'enfant","Une merveilleuse et profonde couleur ambre prend avec le temps","Mais avant de nous saouler","Un fût de chêne doit être fabriqué."}, {"Apporter un Fagot de chêne à Grimbooze Thunderbrew."}},
[54] = {"Rapport pour Goldshire", {"<nom>, vous êtes un <classe> indubitablement impliqué dans la sûreté de Northshire. Vous avez maintenant en charge la protection de la zone entourant la forêt d'Elwynn.","Si vous acceptez cette responsabilité, j'ai préparé tous les documents qui doivent être apportés au maréchal Dughan à Goldshire. Goldshire se situe le long de la route sud, au-delà de la frontière."}, {"Apporter les documents du maréchal McBride au maréchal Dughan de Goldshire."}},
[55] = {"Morbent Fel", {"Morbent Fel se cache dans son repaire, la maison perchée sur la colline à l'est, celle qui domine le cimetière de Colline-aux-Corbeaux. Son temps sur cette terre touche à sa fin...","Utilisez le Fléau de Morbent contre lui. Il réduira à néant ses magies protectrices.","Tuez-le. Tuez-le et libérez-nous de sa cruauté. Soyez le bras armé de ma vengeance et <le héros/l'héroïne> du bois de la Pénombre !"}, {"Utiliser le Fléau de Morbent sur Morbent Fel, tuer Morbent Fel, puis rapporter le Fléau de Morbent à Sven, dans son camp."}},
[56] = {"Les Veilleurs", {"En tant que commandant des Veilleurs, et de par mon serment, il est de mon devoir de protéger les citoyens de Darkshire. Stormwind a rappelé les gardes qui étaient en faction ici, nous laissant nous débrouiller par nous-mêmes.","Ce serait un acte noble de votre part si vous nous aidiez, car nous en avons terriblement besoin ! Aidez les Veilleurs. Aventurez-vous dans les Jardins paisibles, juste au sud de Darkshire, et tuez 8 guerriers squelettes, ainsi que 6 mages squelettes. Les morts-vivants doivent être contrecarrés !"}, {"Le commandant Althea Ebonlocke de Darkshire veut que vous tuiez 8 Guerriers squelettes et 6 Mages squelettes."}},
[57] = {"Les Veilleurs", {"<nom>, on vient juste de me rapporter que la forêt était envahie de morts-vivants ! Si vous êtes d'accord pour le faire, voyagez jusqu'au cimetière de Colline-aux-Corbeaux et tuez 15 de ces infâmes démons squelettes et 15 squelettes infernaux. Mes informateurs me disent que des squelettes ont aussi été repérés au sud de Darkshire, autour et dans le cimetière des Jardins paisibles. Les morts-vivants doivent être repoussés hors du bois de la Pénombre !"}, {"Le commandant de Darkshire, Althea Ebonlocke, veut que vous tuiez 15 Démons squelettes et 15 Squelettes infernaux."}},
[58] = {"Les Veilleurs", {"La situation reste préoccupante à Colline-aux-Corbeaux. La sûreté de Darkshire est entre vos mains, <nom>. Je n'ai pas assez de Veilleurs pour protéger la ville. Nous avons encore besoin de vous. Allez à Colline-aux-Corbeaux pour débarrasser le mausolée est de 20 répandeurs de peste !"}, {"Le commandant de Darkshire, Althea Ebonlocke, veut que vous tuiez 20 Répandeurs de peste dans le mausolée est de Colline-aux-Corbeaux."}},
[59] = {"Les armures de cuir et d'étoffe", {"Pour récompenser votre perspicacité et votre bravoure, j'ai ici un Bon pour une pièce d'armure de Stormwind. Allez trouver Sara Timberlain, au Camp de bûcherons du Val d'est. Donnez-lui ce bon et elle vous fabriquera une armure. Une fois que vous l'aurez, <nom>, utilisez-la pour défendre Elwynn.","Le Camp de bûcherons du Val d'est est au-delà du poste de garde de Thomas, vers l'est."}, {"Donner le Bon pour une pièce d'armure de Stormwind à Sara Timberlain."}},
[60] = {"Les bougies de kobolds", {"Bonjour, <cher monsieur/ma dame> ! Auriez-vous une seconde ?","Mon frère et moi sommes apothicaires à Stormwind, et je suis ici pour acheter des grandes bougies, afin d'utiliser leur cire. Pourriez-vous m'aider ?","Les kobolds produisent ces grandes bougies, et j'ai entendu dire qu’ils infesteraient les mines d'Elwynn... La mine de Fargodeep au sud et la celle de Jasperlode à l'est. Je vous suggère d'aller me chercher ces bougies là où elles se trouvent."}, {"Rapporter 8 Grandes bougies à William Pestle de Goldshire."}},
[61] = {"Une cargaison pour Stormwind", {"Mon frère Morgan attend ma cargaison de bougies à Stormwind. Je n'ai pas le temps de faire le voyage moi-même, mais si vous livrez la cargaison à ma place, il vous payera bien.","J'ai empaqueté les bougies, et vous pouvez trouver Morgan à notre magasin, les Apothicaires Pestle, dans le Quartier commerçant de Stormwind."}, {"Livrer la Cargaison de William à Morgan Pestle dans le Quartier commerçant de Stormwind."}},
[62] = {"La mine de Fargodeep", {"Le Northshire n'est pas la seule région à avoir des problèmes avec ses mines ! J'ai des rapports disant que la mine de Fargodeep à Elwynn est aussi infestée de kobolds.","Allez explorer la mine afin de confirmer ces rapports, puis revenez me voir. Elle se situe au sud de Goldshire, entre les propriétés des Stonefield et des Maclure."}, {"Explorer la mine de Fargodeep, puis revenir voir le maréchal Dughan de Goldshire."}},
[63] = {"L'appel de l'eau", {"Le long de la côte de la forêt des Pins argentés, à l'ouest du Sépulcre, vous trouverez un autel qui a été corrompu. Les esprits n'y parlent plus aux gens. Vous devez résoudre le problème en purifiant l'autel.","Prenez ces bracelets élémentaires et la potion de Brine, placez-les sur le brasero de l’autel. Ce rituel devrait suffire à invoquer un esprit pur à qui vous pourrez parler.","Ramenez-moi une preuve de votre réussite, et je vous récompenserai avec un totem."}, {"Vaincre la Manifestation d'eau corrompue et placer les Bracelets de manifestation corrompue avec le reste d'eau pure sur le brasero d'Everfount dans la forêt des Pins argentés."}},
[64] = {"Le cadeau oublié", {"C'était horrible ! Verna m'a réveillé quand elle a entendu du grabuge dans les champs. Ils étaient envahis par des vandales. Nous nous sommes enfuis à toutes jambes, du coup j'ai oublié ma montre sur place. Le père de Verna me l'avait offerte pour notre mariage et je suis malade de savoir qu'elle est entre les mains de ces voleurs. J'ai laissé ma montre dans la penderie à la ferme. Cherchez le champ de citrouilles à l'ouest – vous ne pouvez pas le manquer. Si vous me la ramenez, je saurais être reconnaissant !"}, {"Le fermier Furlbrow désire que vous retrouviez sa Montre qui se trouve dans la penderie de sa ferme près du champ de citrouilles à l'ouest."}},
[65] = {"La Confrérie défias", nil, nil},
[66] = {"La légende de Stalvan", {"La nuit dernière, j'ai eu un mauvais pressentiment concernant ma petite-fille Alyssa. Elle serait en grand danger. J'ai tiré les cartes et la lame de la Faucheuse est sortie.","Après une longue et sombre transe, j’ai obtenu un indice. Un nom est apparu : Stalvan.","Allez voir un greffier de la mairie et trouvez plus de détails sur ce personnage. J'ai peur pour tout le monde…"}, {"Aller voir le Greffier Daltry à la mairie de Darkshire."}},
[67] = {"La légende de Stalvan", {"Stalvan, hein ? Laissez-moi vérifier dans les registres de la ville.","Stalvan. Stalvan…Voyons voir. Ah ! nous y voilà. J'ai la trace d'un certain M. Stalvan Mistmantle. Sa dernière adresse connue est l'école de Ruisselune. Enfin, mon registre date un peu !","Faites-moi une faveur, si vous allez à Ruisselune, renseignez-vous sur cette personne. Que je puisse mettre à jour mon registre."}, {"Voyager jusqu'à l'école de Ruisselune et rapporter des informations récentes sur Stalvan au Greffier Daltry."}},
[68] = {"La légende de Stalvan", {"Vous trouvez une lettre poussiéreuse dans la vieille caisse."}, {"Rapporter la lettre poussiéreuse au greffier Daltry."}},
[69] = {"La légende de Stalvan", {"Oh non ! J'ai dû manquer ça la première fois. Dans le registre, juste en dessous de l’adresse notée pour Stalvan, il y en a une autre partiellement effacée. Il semble qu’il s’agisse de l'auberge de la Fierté du lion à Goldshire. J'aimerais vérifier cela, <nom>."}, {"Aller à l'Auberge de la Fierté du lion pour apprendre si quelqu'un a entendu parler de Stalvan."}},
[70] = {"La légende de Stalvan", {"Oui, effectivement, je me rappelle de Stalvan !","Il y a quelques années, lors d'une nuit d'orage, un messager est venu, à la recherche d'un abri pour la nuit. Vers minuit, l'homme a dévalé les escaliers en hurlant, le visage terrifié. Il était toujours en pyjama quand il a disparu en courant sous la pluie torrentielle.","Dans sa précipitation, il a oublié ses lettres dans le coffre à l'étage. Il n'est jamais revenu. Une des lettres était adressée au quartier du Canal à Stormwind. Prenez-la si vous le voulez."}, {"Retrouver la Vieille lettre de Stalvan dans le coffre et la remettre au quartier du Canal à Stormwind."}},
[71] = {"Faire son rapport au garde Thomas", {"Maintenant que vous avez les deux médaillons, apportez-les au Garde Thomas au Pont est, il saura peut-être comment ces hommes ont été assassinés."}, {"Rapporter les Médaillons de Rolf et Malakai au garde Thomas au pont est d’Elwynn."}},
[72] = {"La légende de Stalvan", {"Mon père fut le gardien de cette propriété bien avant que je ne le sois. Il a dû tout nettoyer après le massacre.","Les derniers fonds des Flintridge ont fondu comme neige au soleil. Le reste des affaires de la famille va partir aux enchères. La faute à ces vautours des impôts. J'imagine que vous êtes <impatient/impatiente> de voir ce qu'il y a, vous pouvez regarder dans le tas de vieilleries. Si ça se trouve, quelque chose vous intéressera."}, {"Rechercher un indice dans le tas d'affaires."}},
[74] = {"La légende de Stalvan", {"Dans la caisse vous trouvez diverses choses : quelques objets moisis, un portrait de famille, quelques trophées de chasse et de vieux livres. Dans le fond, sous un vase en céramique, vous découvrez une Page de journal déchirée."}, {"La Page de journal déchirée que vous avez découverte est votre deuxième indice."}},
[75] = {"La légende de Stalvan", {"Quand j'ai emménagé ici, il y avait une liasse de lettres dans le coffre en haut. Je les ai examinées en arrivant, mais j'ai abandonné quand mes yeux ont commencé à perdre leur acuité.","Faites une faveur à un vieil homme aveugle, et allez voir dans le coffre à l'étage si vous trouvez quelque chose qui puisse vous aider à en savoir plus sur cet homme, Stalvan. Je suis presque sûr qu'il y a une vieille page de journal qui peut avoir un intérêt pour vous. Ramenez-la-moi et je vous aiderai autant que possible."}, {"Le maréchal Haggard veut que vous alliez chercher la Vieille page de journal dans le coffre à l'étage."}},
[76] = {"La mine de Jasperlode", {"Merci, nous savons désormais que la mine de Fargodeep est infestée de kobolds. Nous avons besoin maintenant d'un éclaireur pour explorer la mine la plus éloignée, celle de Jasperlode.","Explorez-la et confirmez la présence de kobolds. Pour parvenir à la mine, allez vers l'est le long de la route, jusqu'à la tour d'Azora. De là prenez vers le nord et vous tomberez sur la mine dans les collines."}, {"Explorer la Mine de Jasperlode, puis faites votre rapport au maréchal Dughan de Goldshire."}},
[77] = {"Une situation délicate", {"Je possède l'emballage d'origine nécessaire pour conditionner la Houle que Dran Droffers recherche. Je vais vous aider à l'emballer pour le père de Malton, à l'humeur si caustique... contre deux bouteilles de la susnommée Houle. Les bouteilles se trouvent dans une caisse, dans une base abandonnée de la Horde, près du rocher de l'Affût.","La tâche sera ardue : d'immondes gelées et même des limons infestent les ruines. On dit que ces créatures seraient commandées par une sorte de limon en chef. Quoiqu'il en soit, procurez-vous la Houle et revenez me voir ici pour la conditionner."}, {"Récupérer 10 bouteilles de Houle au miel des Hinterlands dans des caisses se trouvant aux environs de la base abandonnée de la Horde, au rocher de l'Affût."}},
[78] = {"La légende de Stalvan", {"Je connais quelqu’un qui peut peut-être vous aider. Quand je commandais les gardes de Stormwind, nous avions l'habitude d'aller boire des coups à la taverne du Corbeau écarlate à Darkshire. L'aubergiste, Smitts, connaît nombre d'histoires sur la région. Montrez-lui cette page et voyez ce qu'il peut vous en dire."}, {"Prendre la Vieille page de journal et la montrer à Smitts l'aubergiste."}},
[79] = {"La légende de Stalvan", {"Je m'intéresse à l'histoire de ce personnage, Stalvan, depuis des années. Le jour où ces nobles de passage furent massacrés, il y a quelques années, je suis venu avec Haggard pour enquêter. J'ai trouvé ces pages boueuses, mais je n'ai jamais pu faire le lien avec l'écriture de cet homme fou dans les bois. Ce que vous m'apportez là, prouve de toute évidence qu'il est coupable. Portez immédiatement cela au commandant Ebonlocke et racontez-lui ce que vous avez découvert !"}, {"Porter les pages au commandant Ebonlocke."}},
[80] = {"La légende de Stalvan", {"J'avais déjà l'œil sur ce sale type, Stalvan. Mais si les écritures concordent, alors cela prouve sans l'ombre d'un doute qu'il est coupable.","Avec toutes ces journées à tenir le registre de la ville, le Greffier Daltry est devenu un expert dans l'identification des écritures. Montrez-lui ces pages pour vérifier si les écritures concordent avec la signature présente sur le registre."}, {"Montrer au Greffier Daltry la Page de journal maculée de sang."}},
[81] = {"La livraison de Houle", {"J'ai pris la peine de mettre à l'abri dans le tonneau, en plus de la houle emballée de Dran, les bouteilles supplémentaires qu'il veut faucher pour son propre compte. Je n'aime pas beaucoup Dran, mais il n'y a rien de pire que de la houle éventée. Ne vous donnez pas la peine de me remercier.","Allez maintenant livrer le baril à Dran Droffers, à sa boutique d'Orgrimmar. Et passez le bonjour à Malton ! Dites-lui que s'il cherche d'excellentes boissons et de sympathiques compagnons de beuverie, nous serions honorés de recevoir sa visite."}, {"Livrer le tonneau à Dran Droffers à Orgrimmar."}},
[82] = {"Enquête au Repaire nuisible", {"Très bien, maintenant ces rapports à propos des insectes... Ce que nous savons, c'est qu'ils drainent l'eau du réservoir qui se trouvait à l'emplacement du Repaire nuisible. Ce que nous ne savons PAS... Tout le reste.","Essayons tout d'abord de savoir qui est notre ennemi ! Je veux que vous alliez au Repaire nuisible et rameniez des bouts de ces bestioles. Récupérez cinq morceaux de ces Centipaars et portez-les à l'alchimiste Pestlezugg pour qu'il voit de quoi il retourne. Avec lui sur l'affaire, nous le saurons vite !"}, {"Apporter cinq morceaux de Centipaars du Repaire nuisible à l'Alchimiste Pestlezugg à Gadgetzan."}},
[83] = {"Articles rouges en lin", {"Les Défias du Northshire portent de simples masques, mais ceux d'Elwynn portent des masques en lin, une belle matière avec laquelle je pourrais faire de très beaux articles.","Rapportez-moi des masques rouges en lin et je les utiliserai pour vous confectionner quelque chose.","Les membres de la Confrérie défias ont des campements disséminés à travers tout Elwynn."}, {"Apporter 6 Masques rouge en lin à Sara Timberlain au Camp de bûcherons du Val d'est."}},
[84] = {"Revenir voir Billy", {"Voilà. Quand vous donnerez cette tourte à Billy, dites-lui de s’étouffer avec !"}, {"Porter la Tourte de panse de porc à Billy Maclure aux Vignes des Maclure."}},
[85] = {"Le collier perdu", {"J'ai perdu mon collier, et je pense que c'est ce garnement de Billy Maclure qui l'a pris ! Généralement il vagabonde dans les Vignes des Maclure, à l'est d'ici.","Rapportez-moi mon collier, et vous réchaufferez le cœur d'une pauvre veuve."}, {"Parler à Billy Maclure."}},
[86] = {"Une tourte pour Billy", {"Si je mangeais une tourte, je pourrais peut-être vous dire qui a ce collier. Vous savez, je pense que cette vieille dame du nom de Bernice, dans l'autre ferme, fait de formidables tourtes de panse de porc...","Si vous lui apportiez quelques morceaux d’un des sangliers qui traînent autour de notre ferme, et si vous lui expliquiez toute l’histoire, alors elle vous préparerait une tourte."}, {"Apporter 4 morceaux de Viande de sanglier à Tatie Bernice Stonefield à la ferme des Stonefield."}},
[87] = {"Dent-d'or", {"Je jouais près de la mine de Fargodeep, et je pense que j'ai fait tomb... Euuuuh... Je veux dire que j'ai vu le collier de la vieille dame. Ne me demandez pas comment il est arrivé là... Ce n'est pas moi qui l'ai pris !","Quoi qu'il en soit, j'ai vu cet énorme kobold avec une dent en or le prendre et s'enfuir vers la mine. Allez là-bas, trouvez le kobold et vous trouverez le collier, c'est juré !"}, {"Rapporter le Collier de Bernice à \"Tatie\" Bernice Stonefield à la ferme des Stonefield."}},
[88] = {"Princesse doit mourir !", {"Les Brackwell ont un cochon qui a gagné des tas de prix, Princesse. C'est une ENORME truie, que je soupçonne de dévaster mon potager !","Avant qu'elle ne s’intéresse à mes champs... La bête doit mourir ! Rapportez-moi son collier pour preuve de son exécution, et je vous récompenserai pour votre travail !","Princesse dîne généralement du côté du Champ de potirons des Brackwell, à l'est, après la ferme des Maclure. Chopez-la avant qu'elle commence à avoir faim, et ne revienne ici !"}, {"Tuer Princesse, prendre son collier, puis le porter à Maman Stonefield à la ferme des Stonefield."}},
[89] = {"Le pont sur le lac", {"Lors de la dernière invasion des orcs, nous avons été obligés de fondre tout notre fer pour fabriquer des balles, des armures et des épées. Nous avons envoyé une demande de ravitaillement à Stormwind, mais une bande de gnolls des Carmines a détourné la caravane puis s'est enfuie dans les collines au-delà de Lakeshire.","Nous allons bientôt être à court de matériel pour reconstruire ce pont. Si vous pouviez me rapporter 5 clous en fer et 5 rivets en fer, <nom>, je ferais en sorte que l'on vous récompense.","Allez, au travail !"}, {"Apporter 5 Clous en fer et 5 Vis en fer au contremaître Oslow à Lakeshire."}},
[90] = {"Kébab de loup assaisonné", {"La spécialité de la maison est le kébab de loup assaisonné. Bien sûr, je peux vous en faire ! Mais j'ai besoin de différents ingrédients. Apportez-moi 10 Steaks de loup ainsi qu'un peu d'Herbes aromatiques de Stormwind. Il y a tellement de loups dans la forêt que cela ne devrait pas être un problème pour trouver la viande. Pour ce qui est des herbes, allez donc voir Felicia Gump, la fleuriste du quartier du canal à Stormwind."}, {"Récupérer 10 Steaks de loup et des Herbes aromatiques de Stormwind, puis retourner voir le chef Grual à Darkshire."}},
[91] = {"La loi de Solomon", {"Un ennemi de Stormwind s'est installé dans les collines à l’est des Carmines. D’infâmes voleurs utilisent les Arcanes pour répandre la terreur et la misère sur notre ville et ses habitants. Derrière cela, il y a une personne, Morganth ; c'est lui qui dirige cette entreprise maléfique.","Morganth contrôle maintenant le clan des gnolls Sombrepoil, qu’il utilise pour ses exactions. De par la loi de Lakeshire, aider un ennemi du royaume est passible de la peine de mort.","Ces infâmes gnolls doivent être tués !","Rapportez-moi 10 pendentifs sombrepoil, et vous serez <récompensé/récompensée>."}, {"Apporter 10 Pendentifs sombrepoil au Bailli Conacher à l'Hôtel de ville de Lakeshire."}},
[92] = {"Goulash des Carmines", {"Rien ne me ferait plus plaisir que de vous faire du goulash des Carmines. Mais il y a un petit problème. Lors la dernière attaque des orcs, j'ai dû nourrir la totalité des troupes de Stormwind. Mon garde-manger est complètement vide. Mais si vous me ramenez les ingrédients, je me ferai un plaisir de vous en préparer.","Il me faut seulement 5 morceaux de Viande de condor coriace, 5 Groins de grands Broche-tripes et 5 portions de Chair croustillante d'araignée."}, {"Le chef Breanna de Lakeshire veut 5 morceaux de Viande de condor coriace, 5 Groins de grands Broche-tripes et 5 portions de Chair croustillante d'araignée."}},
[93] = {"Gâteaux au crabe brun", {"Je vais vous confier un petit secret : mon gâteau au \"crabe\" brun est en réalité fait de pattes d'araignée ! C'est vrai que cela semble un peu répugnant, mais leur goût est merveilleux ! Apportez-moi des Pattes d'araignées fondantes et je vous en préparerai quelques-uns.","Vous en trouverez sur les araignées Tisse-venin qui nichent au nord, entre les collines et la rivière."}, {"Récupérer 6 Pattes d'araignée fondantes et les porter au chef Grual de Darkshire."}},
[94] = {"L'œil attentif", {"Mon adversaire, Morganth, est un puissant mage vicieux qui vit dans la Tour d'Ilgalar dans les Carmines. J'ai élaboré un moyen pour l'espionner, l'Œil d'Azora, et je peux maintenant l'observer de loin.","Mais pour connaître ses pensées, j'ai besoin d'assistance.","Si vous le voulez, portez mon Glyphe d'Azora à la Tour d'Ilgalar. Placez-le sur la Statue de lion près de la tour.","Lorsque cela sera fait, j'aurai une vision des plans futurs de Morganth."}, {"Placer le Glyphe d'Azora sur la Statue de lion près de la Tour d'Ilgalar dans les Carmines."}},
[95] = {"La vengeance de Sven", {"Je travaillais dans une ferme, au sud-est... Des cavaliers noirs du défilé de Deuillevent sont venus dans ma ferme et ont massacré toute ma famille alors que j'étais absent !","Quand je suis rentré, j'ai vu une silhouette indistincte rôder près de ma grange, en train d'enterrer quelque chose. Il s'est enfui avant que j'aie pu l'attraper, et je n'ai pas pu m'attarder, car je suis parti aussitôt à sa poursuite. Du coup, je n'ai pas vu ce qu'il avait caché.","Si vous pouviez découvrir ce qu'a enterré le cavalier noir, je vous en serai reconnaissant. L'endroit exact est derrière la vieille souche d'arbre près de la grange."}, {"Aller à la ferme de Sven et trouver ce qui a été enterré derrière la vieille souche d'arbre."}},
[96] = {"L'appel de l'eau", {"Prendre l'éclat. Prendre l'éclat et donner la vie.","Donner la vie et comprendre. Comprendre que l'eau, c'est la vie... La corruption détruit la vie.","Le pouvoir de l'eau protège et donne la vie."}, {"Apporter l'Éclat d'eau à Islen Waterseer dans les Tarides."}},
[97] = {"La légende de Stalvan", {"Prévenez immédiatement le commandant Ebonlocke que les écritures concordent, <nom> ! Ses soupçons étaient fondés !"}, {"Prévenir le commandant Ebonlocke."}},
[98] = {"La légende de Stalvan", {"Stalvan Mistmantle est un dépravé. De nombreuses victimes ont perdu la vie entre ses mains. Il est sans aucun doute coupable d'un nombre incalculable de crimes et de méfaits. Et maintenant ce dément menace Darkshire. Seule la Lumière sait quels actes abjects il prépare. Allez jusqu'à sa chaumière au nord de la ville, <classe>, et exécutez Stalvan.","Lorsque ce sera fait, allez voir Madame Eva et montrez-lui son anneau familial. Après tout, c'est elle qui a eu la prémonition qui nous a conduits jusqu'à cette terrible découverte. Mais Darkshire est désormais plus sûr grâce à elle."}, {"Tuer Stalvan Mistmantle et montrer son anneau familial à Madame Eva."}},
[99] = {"La folie d'Arugal", {"Mon mépris pour ce malheureux fou d'Arugal augmente à mesure que je déchiffre son enchantement. J'ai presque fini mes recherches pour y parvenir.","Ma compréhension sera totale quand j'aurai compris ce qui cause l'étrange comportement des habitants de Bois-du-Bûcher. Le jour, les paysans sont des gens normaux. Mais la nuit venue ils se transforment en worgens Ragelune.","Je dois puiser l'énergie des chaînes qu'a enchantées Arugal. Rapportez-moi 6 Chaînes enchantées de Bois-du-Bûcher, <nom>."}, {"Rapporter 6 Chaînes enchantées de Bois-du-Bûcher à Dalar Dawnweaver au Sépulcre."}},
[100] = {"L'appel de l'eau", {"À la fin de votre incantation une explosion lumineuse se produit. Le grésillement des gouttes dans les flammes s'est arrêté, et vos sens reviennent à mesure que les Esprits corrompus autour de vous sont détruits."}, {"Parler à la Manifestation d'eau inférieure dans la forêt des Pins argentés."}},
[101] = {"Le Totem d'affliction", {"À mesure que la corruption mystique rampe à travers la forêt, la nécessité d’une protection devient indispensable, <nom>. Les vents m'ont murmuré qu'un grand danger vous guette dans un avenir proche.","Si vous voulez vous garder de ce danger, noble <classe>, apportez-moi 10 Crocs de goule, 10 Doigts de Squelette et 5 Fioles de venin d'araignée. J’enchanterai alors un Totem d'affliction qui blessera ceux qui tenteront de vous agresser."}, {"Apporter 10 Crocs de goule, 10 Doigts de Squelette et 5 Fioles de venin d'araignée à Madame Eva à Darkshire."}},
[102] = {"Patrouilles dans la marche de l'Ouest", {"Stormwind nous a abandonnés. Un vent de corruption souffle sur la marche de l'Ouest. C'est ma terre natale et je n'abandonnerai pas les gens qui ont choisi de rester ici. Nous, les anciens fermiers, nous résisterons.","Votre mission, si vous l'acceptez, sera de patrouiller dans les prairies de la marche. Débusquez et tuez les infâmes gnolls qui apparemment sont de connivence avec les voleurs des Mortemines. Apportez-moi 8 Pattes de gnolls et je vous récompenserai pour votre courage."}, {"Rapportez 8 Pattes de gnolls au capitaine Danuvin à la Colline des sentinelles."}},
[103] = {"Le gardien de la flamme", {"Je me souviens de cette terrible nuit, au cours de laquelle la famille du gardien de phare a été tuée. J'ai contemplé, impuissant, l’attaque du Vieux Troubloeil. Mais bon, ce qui est fait est fait. Ma préoccupation maintenant est la sécurité des marins et des navires qui croisent sur la Grande mer, car la zone est très dangereuse près des récifs le long de la côte. Vu qu'il n'y a plus personne pour s'occuper du phare, la tâche m'incombe à présent de surveiller sa flamme.","Aidez-moi à garder la flamme allumée, apportez-moi 5 Flasques d'huile provenant des monstres des moissons."}, {"Apporter 5 Flasques d'huile au capitaine Grayson, au phare de l'Ouest"}},
[104] = {"Menace sur la côte", {"Quand ma vie a pris fin sur ces rochers, je ne savais pas que je pouvais espérer quelque chose après la mort. Cette nuit-là, le phare était éteint parce que le Vieux Troubloeil avait fait fuir la famille qui s'en occupait. Mais les gens sont revenus et ont rallumé la flamme. Alors le Vieux Troubloeil a contraint ces faibles d'esprit de murlocs à attaquer le phare. Cette fois-ci, la famille n'a pas eu le temps de fuir, et elle a été massacrée par ces ignobles créatures, sans que personne ne puisse intervenir.","Tuez le Vieux Troubloeil si vous le rencontrez le long de la plage, et rapportez-moi une de ses écailles, je vous récompenserai."}, {"Rapporter une Ecaille du Vieux Troubloeil au capitaine Grayson au phare de l'Ouest."}},
[105] = {"Hélas, Andorhal", {"Hélas, le temps est venu d'attaquer Andorhal et d'en finir avec la liche qui la contrôle !","Araj l'Invocateur, la liche, règne sur les ruines de la cité. Il est gardé par de nombreuses créatures du Fléau qui encerclent les restes du centre de la ville, et sa garde personnelle, qui lui est directement attachée, est redoutable. Vous aurez besoin de plusieurs alliés pour avoir la moindre chance de le vaincre.","Exécutez Araj, <nom>, et apportez-moi un éclat de son phylactère comme preuve !"}, {"Apporter l’Eclat phylactère d'Araj au Grand exécuteur Derrington à la Barricade, dans les Maleterres de l’ouest."}},
[106] = {"Jeunes amoureux", {"Je suis maudite ! Mon cœur appartient à Tommy Joe Stonefield, mais nos familles sont ennemies. Du coup, je ne peux pas le voir, et pourtant je languis d’admirer son magnifique visage !","S'il vous plaît, portez-lui cette lettre de ma part. Généralement, il va à la rivière à l'ouest de la ferme des Stonefield, qui se trouve à l'ouest d'ici."}, {"Donner la Lettre d'amour de Maybell à Tommy Joe Stonefield."}},
[107] = {"Note pour William", {"Je suis sûre que William Pestle a une potion qui permettrait de réunir ces deux amoureux !","Tenez, prenez cette note et portez-la à William. Il habite à l’auberge de la Fierté du lion à Goldshire."}, {"Porter la Note de Mamie Stonefield à William Pestle."}},
[109] = {"Aller voir Gryan Stoutmantle", {"J'ai l'impression que vous êtes rompu au combat, <classe>. Si vous ne l'avez pas déjà fait, vous devriez aller voir Gryan Stoutmantle. Il dirige la Milice du peuple, dont le but est de défendre les fermes de la marche de l'Ouest. Je suis sûr qu'il aurait besoin de vous. Il se trouve généralement à la Tour de pierre sur la Colline des sentinelles, près de la route au centre de la marche de l'Ouest."}, {"Parler à Gryan Stoutmantle. Il se trouve à la Tour de pierre sur la Colline des sentinelles, près la route au centre de la marche de l'Ouest."}},
[110] = {"Analyse des morceaux d'insectes", {"Je vais passer un peu de temps à examiner l'équipement d'analyse de Scrimshank... et remettre de l'ordre dans ses effets personnels. J'ai le sentiment que ces insectes vont nous faire subir bien d'autres pertes.","Pourriez-vous aller voir si l'Alchimiste Pestlezugg a analysé les morceaux d'insectes que nous lui avons envoyés. S'ils sont prêts, rapportez-les moi."}, {"Aller voir à Gadgetzan si l'Alchimiste Pestlezugg a terminé d'analyser les morceaux d'insectes."}},
[111] = {"Discuter avec Mamie", {"S'il vous plaît, <nom>, allez parler à Mamie. Si quelqu’un peut trouver une solution à notre problème, c'est bien elle.","Elle habite dans notre ferme à l'est d'ici."}, {"Parler à Mamie Stonefield."}},
[112] = {"À la recherche du varech", {"Je peux préparer une potion d'invisibilité pour Maybell, qui lui permettra de s'échapper du vignoble des Maclure et de rejoindre Tommy Joe. Mais pour préparer cette potion, j'ai besoin de varech cristallin.","Cette plante pousse habituellement dans l'océan, mais parfois les murlocs en récoltent. Il faut aller voir si les murlocs près du lac de Cristal n'en auraient pas en ce moment. Le lac de Cristal est à l'est de Goldshire."}, {"Apporter 4 Feuilles de Varech cristallin à William Pestle de Goldshire."}},
[113] = {"Analyse des morceaux d'insectes", {"Le grand géomètre Fizzledowser doit être informé immédiatement. En fait... toute personne sachant lire doit voir ce rapport. Nous sommes confrontés à une menace très sérieuse et très grave non seulement pour Tanaris et Gadgetzan, mais si on ne s'en occupe pas, pour tout le Sud de Kalimdor. Oups, et même pour tout Azeroth ; cette menace n'a donné aucun signe de déclin.","Demandez à Fizzledowser de faire des copies du rapport pour que vous le distribuiez le plus largement possible. Tout le monde doit être informé de cette menace !"}, {"Porter le rapport au grand géomètre Fizzledowser à Gadgetzan. Obtenir une copie de ce rapport, comme l'alchimiste Pestlezugg l'a demandé."}},
[114] = {"La fuite", {"Prenez cette potion d'invisibilité pour la jeune Maybell. Les effets devraient être suffisamment longs pour qu'elle puisse rendre visite à Tommy Joe."}, {"Porter la Potion d'invisibilité à Maybell Maclure."}},
[115] = {"Magie des Ténèbres", {"Les orcs Blackrock ont engagé des exhalombres pour les aider dans leurs attaques contre les Carmines ; ils ont emporté des orbes de minuit. Ces orbes ont infligé de graves dégâts aux défenses des Carmines et il est impératif que nous ne soyons plus confrontés à leur puissance.","Retrouvez et rapportez-moi les orbes de minuit, après avoir tué les exhalombres Blackrock qui les possèdent. Je détruirai ces orbes ; le monde ne se portera que mieux sans eux !"}, {"Apporter 3 Orbes de minuit au maréchal Marris à Lakeshire."}},
[116] = {"Régime sec", {"Je suis dans une situation désespérée, <nom>. Mes réserves sont presque à sec. La dernière cargaison de boisson aurait dû arriver il y a bien longtemps. L'invasion des orcs a été un enfer.","Peut-être pouvez-vous travailler pour moi ?","J'ai besoin que vous récupériez un tonneau de Thunderbrew de chez Grimbooze Thunderbrew dans les collines de la marche de l'Ouest, un tonneau de merlot de Stormwind, une bouteille de tord-boyaux de Darkshire et une outre de rhum doux de Goldshire. Rapportez-les-moi et je ferai en sorte que vous soyez <récompensé/récompensée>."}, {"Le tavernier Daniels de Lakeshire a besoin d'un Tonneau de Thunderbrew, d'un Tonneau de merlot, d'une Bouteille de tord-boyaux et d'une Outre de rhum doux."}},
[117] = {"La Tonnebière", {"Allez-donc, <mon ami/mon amie>, sans tarder hâtez-vous !","Pour que notre breuvage, fidèle à mes dosages,","Tienne plus de la bière que d’un mauvais ragoût,","Il me manque du houblon pour en faire le brassage."}, {"Apporter 5 Pieds de Houblon à Grimbooze Thunderbrew pour qu'il prépare son breuvage spécial."}},
[118] = {"Le prix des fers", {"J'ai utilisé tellement de métal pour équiper les Gardes de Stormwind que je n'en ai même plus pour ferrer les chevaux !","Apportez cette note à Argus le forgeron de Goldshire. Elle explique mon problème et lui demande une livraison de fers à cheval."}, {"Porter la Note de Verner à Argus le forgeron de Goldshire."}},
[119] = {"Le retour chez Verner", {"D'accord, je lui envoie 50 fers à cheval…","Les voilà, tout empaquetés et prêts à emporter ! Dites à Verner que c’est un plaisir de faire affaire, et faites en sorte qu'il trouve la facture accrochée à la caisse !"}, {"Retourner chez Verner Osgood dans les Carmines et lui donner la caisse de Fers à cheval."}},
[120] = {"Un message pour Stormwind", {"Nous vivons un moment crucial, <classe>. Le bourg est assiégé. Sans renforts, nous allons succomber. Le rapport dont je vous charge est de la plus haute importance. Remettez-le au général Marcus Jonathan de Stormwind le plus rapidement possible. Dès que vous le lui aurez remis, revenez avec sa réponse, qu'elle soit bonne ou mauvaise.","Maintenant hâtez-vous !"}, {"Le magistrat Solomon vous a remis un rapport qui doit être transmis au général Marcus Jonathan de Stormwind. Il exige que vous reveniez vers lui aussitôt que vous aurez donné le message."}},
[121] = {"Un message pour Stormwind", {"Pendant que j'attends la réponse du roi, je veux que vous portiez cette lettre au Magistrat Solomon.","Rompez, <classe> !"}, {"Porter la réponse du général Marcus Jonathan au magistrat Solomon, à Lakeshire."}},
[122] = {"Les écailles de dragonnet", {"J'ai besoin d'écailles ventrales de dragonnet noir pour payer les fers qu'Argus m'a envoyés de Goldshire. Si vous pouviez m'en trouver six, je réglerais ma dette... et il m'en resterait suffisamment pour vous façonner quelque chose.","Les dragonnets noirs errent souvent au sud de Lakeshire, mais ils aiment aussi se balader. Vous devrez vous embusquer et les chasser quand vous les verrez."}, {"Rassembler 6 Ecailles ventrales de dragonnet noir et les apporter à Verner Osgood dans les Carmines."}},
[123] = {"Le collecteur", {"Cette note est un tableau des heures et des jours auxquels une personne – décrite comme le \"Collecteur\" – doit recevoir une livraison d'or en provenance des mines de la forêt d'Elwynn.","D'après le tableau, il semblerait que le Collecteur habite près du Champ de potirons des Brackwell, à l'est d'Elwynn.","C'est une information importante. Vous devriez en faire part au maréchal Dughan de Goldshire."}, {"Aller voir le maréchal Dughan, à Goldshire, et lui donner le Plan de route du Collecteur."}},
[124] = {"Le hurlement des gnolls", {"Comme si les attaques des orcs ne suffisaient pas ! Maintenant j'ai des brutes et des mystiques gnolls qui rôdent au nord de mon écurie et qui massacrent mes chevaux dès qu'ils s'éloignent...","Si vous pouviez nous débarrasser de ces gnolls, mes chevaux et moi vous en serions très reconnaissants."}, {"Tuer 10 Brutes des Carmines et 8 Mystiques des Carmines, puis retourner chez Verner Osgood."}},
[125] = {"Les outils perdus", {"J'apprécierais vraiment votre aide, <nom>. La ville est assiégée, l'approvisionnement devient difficile. On devait me livrer des outils par chariot depuis Goldshire, mais le pont a sauté. Nous avons alors mis les outils dans un bateau, mais les orcs l'ont touché avec une catapulte. Évidemment, ma boîte d'outils a coulé tout droit au fond du lac.","Récupérez-la, <nom>, et je ferai en sorte que vous ne le regrettiez pas."}, {"Le contremaître Oslow de Lakeshire veut que vous récupériez sa Boîte d'outils au fond du lac Placide."}},
[126] = {"Les collines aux hurlements", {"<nom>, les gnolls sont toujours là. Je peux entendre leurs hurlements quand ils chassent dans les collines de Lakeshire. Et l'un d'eux hurle plus fort que les autres : Couineur, leur chef.","Tuez Couineur et dispersez sa meute ! Rapportez-moi sa patte comme preuve.","Mais faites attention ! Ce gnoll n'est pas leur chef pour rien ! Ce n'est pas un bâtard !"}, {"Rapporter la patte de Couineur à Verner Osgood à Lakeshire."}},
[127] = {"Criée aux poissons", {"Le lac Placide est connu pour son poisson-lune. C'est un poisson qui se vend bien et j'ai justement besoin d'argent !","Rapportez-m'en 10 ; je paierai bien. Si vous ne savez pas pêcher, peut-être pourrez-vous en voler aux murlocs !"}, {"Rapporter 10 Poissons-lunes au Chef des docks Baren à Lakeshire."}},
[128] = {"La prime Blackrock", {"Le clan Blackrock doit être démantelé. L'Ennemi a envahi nos terres. Ces montagnes sont cependant sous le règne souverain du roi de Stormwind. Nos sources nous indiquent que les chefs de chaque unité du clan sont appelés des champions. Vous les trouverez dans leur campement au nord, juste à l'ouest de la route menant aux Steppes Ardentes.","Le magistrat Solomon veut que ces brutes abjectes soient tuées. Tuez 15 Champions Blackrock et revenez me voir."}, {"Tuez 15 Champions Blackrock et le garde Howe de Lakeshire vous récompensera."}},
[129] = {"Un repas gratuit", {"Vous pouvez me faire une faveur ? J'ai préparé un déjeuner pour le Garde Parker, mais il est en patrouille... C'est un homme grand et fort, un Garde de Stormwind, qui peut se défendre, mais pour une citadine comme moi, c'est beaucoup trop dangereux dehors.","Si vous lui apportez ce repas à ma place, revenez me voir, je vous offre un déjeuner gratuit ! Parker patrouille le long de la route qui mène au bois de la Pénombre."}, {"Apporter son déjeuner au Garde Parker. Il patrouille sur la route qui mène au Darkshire."}},
[130] = {"Une visite chez l'herboriste", {"Puisque vous retournez voir Darcy, pouvez-vous lui apporter des fleurs de ma part ? Vous en trouverez chez l'herboriste, quand vous serez de retour au Lakeshire, à l'extrémité ouest de la ville.","Mais quand vous prendrez les fleurs, ne dites pas à l'herboriste, Martie, qui les achète, ni pour qui elles sont..."}, {"Parler à Martie Jainrose, l'Herboriste des Carmines."}},
[131] = {"Bouquet de jonquilles", {"Voilà votre bouquet. J'ai choisi des jonquilles pour vous. Ce sont mes fleurs préférées !"}, {"Donner à Darcy le bouquet de jonquilles."}},
[132] = {"La Confrérie défias", {"Ce que je vais vous dire peut me coûter la vie. Le gang défias est en train de préparer un gros coup. Il paraît qu'ils se sont acoquinés avec des gnolls, des kobolds et même des gobelins.","Apportez ce message à Stoutmantle. Il donne tous les détails de ce que je sais à ce sujet."}, {"Apporter la Note de Wiley à Gryan Stoutmantle, dans la marche de l'Ouest"}},
[133] = {"L'épouvantail à goules", {"Je vis tellement loin de la sûreté de la ville que c'est un miracle que les morts-vivants ne m'aient pas dévoré ! En fait, juste hier, un groupe de Mâcheurs est venu rôder près de ma maison !","Pour les éloigner, je voudrais faire une sorte d’\"épouvantail\" à goules. Il me faut des côtes de goules. On peut se les procurer sur les Dévoreurs, les Mâcheurs, les Mangeurs de cervelle, les Pourris et les Répandeurs de peste.","Trouvez-moi des côtes et je vous donnerais une récompense."}, {"Trouver 7 Côtes de goule. Les apporter à Abercrombie dans sa cabane."}},
[134] = {"Les ogres voleurs", {"Il y a quelques semaines, je cueillais des herbes assez loin de chez moi et j'ai été attaqué par une bande d'ogres ! Je me suis enfui, en laissant derrière moi une caisse pleine d’herbes et de précieux outils.","Les ogres m'ont pourchassé un moment, puis ils sont retournés à leur tertre, au sud du bois de la Pénombre. Je suis sûr que ma caisse est là-bas, pas très loin du tertre des ogres.","Je vous en prie, <nom>, récupérez cette caisse pour moi. Elle me fait gravement défaut."}, {"Rapporter sa caisse à Abercrombie."}},
[135] = {"La Confrérie défias", {"Je me demande de quoi parlait Wiley quand il a mentionné les Maçons. C'était peut-être un lapsus ? Les Défias pourraient-ils être reliés aux Maçons ? Le seul qui pourrait le savoir à coup sûr, c'est Mathias Shaw, le chef du SI:7. Montrez-lui la note de Wiley et voyez s'il a quelque chose à tirer de ce mystère qui s'épaissit. Si vous avez du mal à trouver Shaw, cherchez du côté de la caserne de la vieille ville."}, {"Apporter la Note de Wiley à Mathias Shaw à Stormwind."}},
[136] = {"Le trésor caché du capitaine Sanders", {"Si vous lisez ceci, c'est que le vieux capitaine Sanders repose sous six pieds de flotte. Mon trésor est à vous. Il vous suffit de suivre le jeu de piste.","D'abord, trouvez mon coffre. Il doit être à moitié enterré dans le sable, depuis le temps, sur la côte de la marche de l'Ouest, à côté de l'épave. Il y a des tas d'épaves sur la côte, mais une seule ancre rouillée. Qui trouve cette ancre trouve mon coffre ! Et avec le coffre, l'indice suivant !"}, {"Trouver le Coffre du capitaine Sanders. Le fouiller pour trouver l'indice suivant."}},
[138] = {"Le trésor caché du capitaine Sanders", {"L'indice que vous trouvez est le suivant : « Bon travail, matelot ! Maintenant il va falloir cingler vers l'est. À l'est, vers le haut des falaises, à l'est vers la route. Cherchez les ruines des vieilles cheminées, au bord de la route. Là, vous trouverez un vieux tonneau qui contient l'indice suivant. »"}, {"Trouver le Tonneau à côté des cheminées en ruine. Le fouiller pour trouver l'indice suivant."}},
[139] = {"Le trésor caché du capitaine Sanders", {"En fouillant le tonneau, vous découvrez un autre parchemin. Il y est écrit : « À partir de ce tonneau, tournez-vous vers le nord. Marchez droit comme une bande de cadets qui va au réfectoire, jusqu'à ce que vous voyiez la cruche vide, à côté du moulin à vent solitaire, sur les falaises. Si vous farfouillez autour de cette cruche, vous pourrez bien trouver ce que vous cherchez. »"}, {"Chercher la Cruche vide à côté du moulin à vent pour trouver le prochain indice."}},
[140] = {"Le trésor caché du capitaine Sanders", {"Bien vu ! Il y a un autre indice au fond de la cruche. L'encre a coulé par endroits et le papier sent le whisky, mais vous arrivez à reconstituer une partie du texte. « Maintenant que vous avez trouvé la vieille cruche de whisky, vous êtes presque au trésor ! Tournez-vous vers l'ouest et descendez vers le rivage. Ne vous arrêtez pas au bord de l'eau, continuez ! Nagez vers l'ouest jusqu'à l'île où se trouve mon coffre au trésor ! »"}, {"Localiser le Coffre au trésor du capitaine Sanders et prendre votre récompense."}},
[141] = {"La Confrérie défias", {"La Guilde des Maçons est dirigée par un homme du nom d'Edwin VanCleef. C'est lui qui a rebâti Stormwind après sa destruction par les orcs au cours de la Première Guerre. Apparemment, VanCleef et ses équipes n'ont pas apprécié la façon dont ils ont été traités par le roi à la fin de la reconstruction. Ça pourrait expliquer une ou deux petites choses.","J'ai rédigé un rapport détaillé pour ton maître, dans la marche de l'Ouest. Apporte-le-lui tout de suite !"}, {"Apporter le Rapport de Shaw à Gryan Stoutmantle, dans la marche de l'Ouest."}},
[142] = {"La Confrérie défias", {"Nous devons débusquer les Défias de leur cache, <nom>. Mes éclaireurs ont rendu compte qu'un Messager Défias a été vu sur les routes entre Ruisselune, la carrière de la côte de l'Or et la mine de Jangolode. Je veux que vous le capturiez. S'il résiste tuez-le et apportez-moi tout ce que vous trouverez sur lui."}, {"Traquer le Messager défias dans la marche de l'Ouest et apporter les documents qu'il transporte à Stoutmantle."}},
[143] = {"Un message pour la marche de l'Ouest", {"Il y a quelque chose d'étrange dans le comportement de l'armée de Stormwind. Elle devrait se trouver là en force à l'heure qu'il est. De toute façon, le temps est un luxe que nous ne pouvons pas nous offrir. Je ne resterai pas là à regarder couler le sang du peuple de Lakeshire sans essayer de trouver de l'aide. J'ai entendu dire qu'une milice populaire s'était formée dans la marche de l'Ouest.","Apportez cette demande d'aide à leur chef, Gryan Stoutmantle. Ses hommes pourront peut-être nous apporter leur soutien en attendant que le roi envoie ses réserves."}, {"Le Magistrat Solomon veut que vous portiez sa Demande d'aide à Gryan Stoutmantle dans la marche de l'Ouest."}},
[144] = {"Un message pour la marche de l'Ouest", {"La demande du Magistrat Solomon me peine plus que je ne saurais le dire. Visiblement, il ignore que la guerre a éclaté dans la marche de l'Ouest, sinon il saurait qu'aucune aide n'est à attendre de la Milice. Si Stormwind ne nous avait pas abandonnés elle aussi, nous n'aurions pas eu besoin de la Milice.","Apportez cette réponse à votre maître dans les Carmines, <classe>. Et dites-lui que mon cœur est lourd à la pensée de la mort de tant de braves."}, {"Retourner voir le Magistrat Solomon avec la Réponse de Stoutmantle."}},
[145] = {"Un message pour Darkshire", {"Je sollicite votre aide une nouvelle fois. J'ai écrit au seigneur Ebonlocke pour lui demander de m'envoyer ses gardes les plus expérimentés, les Veilleurs, afin de renforcer les défenses de Lakeshire. Il est primordial que ce message lui soit remis rapidement. Vous devez faire preuve de la plus grande vigilance. Ebonlocke est le maire de Darkshire, au cœur du bois de la Pénombre. Ne vous écartez pas des chemins."}, {"Le magistrat Solomon veut que vous apportiez une lettre au seigneur Ebonlocke de Darkshire."}},
[146] = {"Un message pour Darkshire", {"Si je ne connaissais pas personnellement le Magistrat Solomon, je penserais qu'il a perdu la tête ! Envoyer les Veilleurs au Lakeshire simplement parce que Stormwind lui refuse son aide ? Nous avons dû créer le corps des Veilleurs justement parce que Stormwind nous avait abandonnés au moment où cette horrible magie a commencé à se répandre. Si j'envoie mes Veilleurs, notre région sera la proie du Mal avant même que les soldats n’aient atteint la lisière de la forêt.","Portez cette lettre en réponse à votre maître au Lakeshire."}, {"Retourner voir le Magistrat Solomon avec la Lettre en réponse du Seigneur Ebonlocke."}},
[147] = {"Chasse à l'homme", {"Si le \"Collecteur\" prend de l'or dans nos mines, il le vole au royaume ! Traînez-le devant la justice et apportez-moi l'anneau dont parlait le Plan de route que vous m'avez donné. Cela pourrait bien donner une indication sur l'employeur du Collecteur...","Ce plan de route indique que le Collecteur se cache près du Champ de potirons des Brackwell. Cherchez-le vers là-bas."}, {"Trouver et tuer le \"Collecteur\" puis rapporter son Anneau au maréchal Dughan."}},
[148] = {"Fournitures en provenance de Darkshire", {"Oh ! Comment doit faire un pauvre vieillard ? J'ai besoin d'aller chercher des fournitures à Darkshire, mais le village est si loin et je suis si vieux, si faible... Je n'y arriverai jamais.","Pouvez-vous aller à Darkshire et me rapporter des mèches de cheveux fantômes ? Vous les obtiendrez chez Madame Eva, qui habite la place de Darkshire."}, {"S’adresser à Madame Eva."}},
[149] = {"Mèche de cheveux fantômes", {"Il y a un pauvre fantôme triste, Marie l'Aveugle, qui hante une vieille ferme, dans les collines au sud de Darkshire.","Prenez ce Peigne spectral et demandez-lui de peigner ses cheveux. Ensuite, rapportez-moi le peigne et j'en retirerai les cheveux nécessaires pour fabriquer la mèche.","Faites attention, car des créatures des ténèbres sont terrées autour de la maison..."}, {"Apporter le Peigne spectral à Marie l'Aveugle."}},
[150] = {"Les braconniers murlocs", {"Les murlocs sont en train de devenir gênants. De plus en plus de pêcheurs jettent l'ancre au large des rives du lac Placide et remontent un problème dans leurs filets ! Nous devons nous débarrasser d'eux avant qu'ils aient dévoré jusqu'au dernier poisson du lac !","Partez à la chasse aux murlocs ! Rapportez-moi huit ailerons et je vous offrirai quelque chose..."}, {"Apporter 8 Ailerons de murloc au Chef des docks Baren à Lakeshire"}},
[151] = {"Pauvre Vieille Blanchy !", {"Cette pauvre Blanchy ! Après tout ce que nous lui avons fait subir, elle est très fatiguée. Je l'ai nourrie avant que nous quittions la ferme, mais nous ne nous attendions pas à ce que le chariot nous trahisse. Si vous pouviez lui apporter quelques poignées d'avoine des champs, vous auriez ma reconnaissance.","Je parie que vous pourriez en trouver un peu près des fermes de la marche de l'Ouest, si vous parvenez à éviter ces horribles machines qui ont pris possession des lieux. Vous verrez de nombreuses fermes, au sud-ouest."}, {"Verna Furlbrow de la marche de l'Ouest veut que vous lui apportiez 8 Poignées d'avoine."}},
[152] = {"La côte n'est pas sûre", {"Vous avez probablement remarqué toutes les épaves le long de la côte. La Grande Mer est une maîtresse cruelle, en vérité. La côte de la marche de l'Ouest doit être nettoyée, afin de permettre aux marins de trouver leur chemin jusqu'à nos plages en toute sécurité. Les murlocs sont déjà assez dangereux !","Tuez 7 Chasse-marées, 7 Guerriers, 7 Oracles et 7 Cours-la-côte et vous recevrez une récompense."}, {"Tuer 7 Chasse-marées, 7 Guerriers, 7 Oracles et 7 Cours-la-côte, puis retourner voir le capitaine Grayson au Phare de l'Ouest."}},
[153] = {"Les masques en cuir rouge", {"Les Défias bougent en permanence. Ça fait un moment que je suis leurs mouvements. J'ai remarqué que nombre des membres du gang portaient un masque en cuir rouge.","Apportez-moi 15 de ces masques et vous recevrez une récompense."}, {"Apporter 15 Masques en cuir rouge à l'éclaireur Galiaan à la Colline des sentinelles."}},
[154] = {"Rendre le peigne", {"NON !","Prenez-le, reprenez ce peigne, éloignez-le de moi !","Je suis un monstre et personne ne peut me trouver le moindre charme !"}, {"Rapporter le Peigne spectral à Madame Eva à Darkshire."}},
[155] = {"La Confrérie défias", {"Ainsi Stoutmantle envoie quelqu'un d'aussi famélique pour me protéger ? Il faudra bien que ça aille. Tout de même, il vaudrait mieux s'entourer de quelques alliés...","Vous connaissez le plan, n'est-ce pas, <nom> ? Vous surveillez mes arrières et je vous mène à la cache des Défias. Vous feriez mieux de ne pas trop vous éloigner. Les Défias veulent ma tête à présent. S'ils me voient avec vous, ils essaieront de me tuer.","Prévenez-moi quand vous aurez rassemblé quelques amis et que vous serez prêts à partir."}, {"Escortez le Traître défias jusqu'à la cache secrète de la Confrérie défias. Lorsque vous saurez où se cachent VanCleef et ses hommes, rapportez l'information à Gryan Stoutmantle."}},
[156] = {"Cueillette de Racines pourries", {"Si vous m'apportez des racines pourries, je pourrai vous préparer une tournée de suc de zombie.","Les racines pourries poussent dans des endroits étranges. J'ai entendu dire qu'on pouvait les trouver à l'intérieur des crânes des Démons squelettes et des Squelettes infernaux... Cela dit, je ne me suis jamais approché pour vérifier !","Le cimetière de Colline-aux-Corbeaux et celui des Jardins paisibles sont tous les deux pleins de ces squelettes. Vous pourriez nous en débarrasser tout en cherchant les racines pourries. Darkshire vous en serait infiniment reconnaissant."}, {"Rapporter 8 Racines pourries à Smitts l'aubergiste de Darkshire."}},
[157] = {"Apporter la mèche", {"Voici votre mèche, <nom>. Elle a des milliers d'usages. Elle permet d'attacher les esprits maléfiques, de redonner la vie aux objets inanimés, que sais-je encore ?"}, {"Apporter la Mèche de cheveux fantômes à Abercrombie, dans sa cabane, au nord du cimetière de Colline-aux-Corbeaux."}},
[158] = {"Suc de zombie", {"J'ai besoin d'une bouteille d'une liqueur peu ordinaire : du Suc de zombie. Smitts, l'aubergiste de la taverne du Corbeau écarlate à Darkshire, doit pouvoir en fournir.","Et dépêchez-vous, <nom>. Les nuits commencent à fraîchir et j'ai besoin de ce breuvage pour réchauffer le sang dans mes veines."}, {"S’adresser à Smitts l'aubergiste."}},
[159] = {"Livrer le Suc", {"Voici votre Suc de zombie…","… Et faites attention avec ce truc. Il est assez fort pour réveiller les morts !"}, {"Apporter le Suc de Zombie à Abercrombie, dans sa cabane."}},
[160] = {"Une lettre pour le Maire", {"Apportez cette lettre à Ello Ebonlocke, le Maire de Darkshire.","Nous sommes de vieux amis… Et quand il lira ça et comprendra ce que vous avez fait pour moi, je suis sûr qu'il sera tout aussi heureux que moi ! Ha ha !"}, {"Apporter la Lettre d'Abercrombie à Ello Ebonlocke, Maire de Darkshire."}},
[161] = {"Une sombre menace se dessine", {"Si mes soupçons sont justifiés, c'est une espèce de poudre explosive. Il faut la faire analyser par un artificier pour savoir à quoi nous avons à faire exactement. Ashlan Stonesmirk a été affecté au régiment qui garde Dun Modr et le viaduc de Thandol. Vous allez devoir faire un périlleux voyage, <nom>.","Traversez la Porte d'Algaz, suivez la route jusqu'aux Paluns et trouvez Stonesmirk à Dun Modr. Et suivez mon conseil : ne vous éloignez pas de la route et ne vous arrêtez pour rien au monde !"}, {"L'Ingénieur en chef Hinderweir veut que vous apportiez cette étrange poudre à Stonesmirk l'Artificier à Dun Modr."}},
[162] = {"L'ascension des silithides", {"Je vous presse d'apporter ce rapport à quelqu'un qui a les connaissances nécessaires pour l'étudier.","Je connais une elfe de la nuit érudite, Gracina Spiritmight. Elle a passé beaucoup de temps à étudier les formes de vie exceptionnelles de l'écologie d'Azeroth et elle est historienne de surcroît ! Si vous cherchez quelqu'un qui pourrait comprendre la nature de cette menace, et surtout qui serait capable de trouver une solution, alors confiez-lui ce rapport. Nous aurons besoin de son aide.","La dernière fois que j'ai entendu parler d'elle, elle vivait au Temple de la Lune, à Darnassus."}, {"Apporter le Rapport d'analyse des insectes à Gracina Spiritmight à Darnassus."}},
[163] = {"Colline-aux-Corbeaux", {"Il y a quelque chose d'étrange à Colline-aux-Corbeaux, <nom>. Calor jure que quelqu'un (ou quelque chose) hante les bâtiments là-bas. Deux nuits de suite, il a remarqué que les ombres bougeaient et la nuit d'avant, il dit qu'il a vu de la lumière à l'une des fenêtres.","Si, comme vous le prétendez, vous vous intéressez vraiment à la sécurité du bois de la Pénombre, trouvez le monstre qui hante Colline-aux-Corbeaux."}, {"Découvrir ce qui hante Colline-aux-Corbeaux."}},
[164] = {"Livraison à Sven", {"Connaissez-vous Sven ? Voilà un homme fidèle à la Lumière ! Lui et ses fidèles vivent dans un camp à la lisière nord-ouest du bois de la Pénombre.","Malheureusement, ils sont si peu nombreux qu'ils ne peuvent même pas dépêcher l'un des leurs pour aller en ville chercher du ravitaillement. Voudriez-vous lui apporter ceci ?"}, {"Ravitailler Sven."}},
[165] = {"L'ermite", {"Il y avait un vieil homme qui venait régulièrement en ville faire des courses, mais cela fait un moment que je ne l'ai pas vu. Il vit dans une cabane, au-dessus du cimetière de Colline-aux-Corbeaux, si je me souviens bien. Vous devriez aller voir ce qui ne va pas."}, {"Aller voir le vieil homme dans sa cabane près du cimetière de Colline-aux-Corbeaux."}},
[166] = {"La Confrérie défias", {"Il vous reste une dernière mission à accomplir. Il va falloir éliminer Edwin VanCleef. Bien que cela me chagrine de condamner un homme à mort, c'est pour le bien du peuple de la marche de l'Ouest que VanCleef doit mourir. Apportez-moi sa tête lorsque l'acte sera accompli."}, {"Tuer Edwin VanCleef et apporter sa Tête à Gryan Stoutmantle."}},
[167] = {"Adieu mon frère…", {"Nous étions au plus profond d'une mine dans la marche de l'Ouest, cachés derrière un baraquement à Ruisselune. Impossible de savoir d'où viennent ces voleurs puants... Bref, la mine s'est effondrée sur nous. Je m'en suis sorti mais les autres... Enfin... On n'en a plus entendu parler.","Vous aurez peut-être plus de chance. Si vous arrivez à avoir des nouvelles de mon frère, je vous en serai reconnaissant. Il portait toujours son insigne de la Ligue des Explorateurs. Si vous ne le trouvez pas, mais que vous me rapportez cet insigne, cela me rendrait un peu de paix."}, {"Apporter l'Insigne de la Ligue des Explorateurs du contremaître Crispechardon à Wilder Crispechardon à Stormwind."}},
[168] = {"À la recherche de Cartes du Syndicat des Mineurs", {"Nombre de mes amis ont péri ce jour terrible de l'effondrement de la mine. Si vos pas vous conduisent dans cette partie de la mine de Ruisselune, voyez s’il existe des indices qui permettraient de les identifier. Si vous trouvez leurs Cartes du Syndicat des Mineurs, rapportez-les-moi et je préviendrai leurs familles."}, {"Récupérer 4 Cartes du Syndicat des mineurs et les apporter à Wilder Crispechardon à Stormwind."}},
[169] = {"On recherche : Gath'Ilzogg", {"On recherche ! Gath'Ilzogg, responsable des attaques Blackrock sur le donjon de Guet-de-pierre et des raids sur la ville de Lakeshire.","Gath'Ilzogg est très dangereux. On dit qu'il prépare ses prochains crimes contre les humains depuis le Donjon récemment conquis. L'invasion de ces orcs doit être stoppée à tout prix ! Une forte récompense est offerte au cœur vaillant qui apportera la tête de Gath'Ilzogg au magistrat Solomon."}, {"Tuer Gath'Ilzogg et apporter sa Tête au magistrat Solomon de Lakeshire pour toucher la récompense."}},
[170] = {"Une nouvelle menace", {"J'espère que vous êtes là pour nous donner un coup de main, <classe>. Après la dernière attaque des troggs, nous avons besoin de toute l'aide possible !","Je sais que ces canailles pullulent dans toutes les régions et on dirait que les Frigères ne font pas exception. On les a repérés au sud-est, partout dans les collines et à côté du lac Gelé.","Et ce n'est pas tout ! Il y a à peine quelques nuits, ils ont attaqué et détruit notre camp, à l'ouest !","Nous sommes un peu à court de personnel, ici, <mon gars/jeune fille>, et nous avons besoin de force et de bravoure pour renvoyer les troggs d'où ils viennent."}, {"Balir Frosthammer veut que vous tuiez 6 Troggs Mâcheroc et 6 Troggs Mâcheroc forcenés."}},
[171] = {"Un surveillant de l'Alliance", {"Vous avez été très <gentil/gentille> avec moi, <nom>. Je n'aurais jamais pensé que je m'amuserais autant pendant la Semaine des enfants. Merci.","J’aimerais que tous les orphelins puissent avoir la chance de faire ce que nous avons fait. Pensez-vous que nous pourrions retourner voir Foltiff Tremblhoupette pour obtenir un râtelier d'épées en mousse ? Je pense qu'il est encore en train de pousser ce chariot dans les Canaux à l'extérieur du quartier commerçant. Comme ça, tout le monde à l'orphelinat pourra aussi faire quelque chose de marrant !","Après, il sera temps pour moi de rentrer."}, {"Achetez un Râtelier d’épées en mousse à Foltiff Tremblhoupette. Puis ramenez votre protégé à l’orphelinat de Hurlevent, en parlant à la directrice de l’orphelinat, Rossignol, dans le Quartier de la Cathédrale."}},
[172] = {"La Semaine des enfants", {"Vous voulez aider l'orphelinat ? Soyez-en <remercié/remerciée> mon <ami/amie>. Votre aide durant la Semaine des enfants nous sera précieuse.","Prenez ce sifflet. Il ne restera pas disponible très longtemps : en fait, pas plus longtemps que ne dure la Semaine des enfants. Quand vous l'utiliserez, vous pourrez parler à l'enfant dont vous avez accepté de vous occuper. Si vous l'utilisez une seconde fois, il renverra l'enfant.","Je vous en prie, <nom>, faites vite la connaissance de votre protégé ! Je suis sûre que l'enfant est très impatient de vous rencontrer."}, {"Utiliser le sifflet d'orphelin orc pour parler à l'enfant que vous protégez durant la Semaine des enfants."}},
[173] = {"Worgens dans les bois", {"Les ténèbres s'étendent inexorablement sur le bois de la Pénombre. La quête de maître Carevin est l'expulsion du mal et de l'hérésie. Nos efforts protègent le peuple de Darkshire.","Vous sentez-vous digne de nous rejoindre ?","Je pensais comme vous, autrefois. Mais la suffisance des Veilleurs était pénible et j'ai rejoint maître Carevin. Si vous voulez prouver votre valeur, les mots ne suffiront pas.","Testez votre habileté contre les tisseurs d'ombre Plaie-de-nuit, des worgens qui rôdent dans le Verger pourissant et dans le bosquet de Clairbois (clair, mon oeil !)."}, {"Tuer 6 worgens Tisseurs d'ombre Plaie-de-nuit pour Calor de Darkshire."}},
[174] = {"Les yeux dans les étoiles", {"Mon ami Cog, un gnome, a promis de m'aider à construire un outil qui permet de voir les étoiles. Il semblerait qu'il existe une vérité, au-delà de la magie, qui explique les étranges changements survenus dans la forêt.","Apportez-moi un tube en bronze fabriqué par un ingénieur habile. C'est la première étape, si j'en crois le plan de Cog."}, {"Viktori Prism'Antras de Darkshire veut que vous lui apportiez un Tube en bronze."}},
[175] = {"Les yeux dans les étoiles", {"Selon le plan de Cog, il faut maintenant que je trouve un moyen de réfléchir la lumière. Je me souviens d'une femme du nom de Marie, bien connue par ici car elle avait l'habitude de transporter un miroir. Vous pourriez peut-être chercher Marie et voir si elle nous prêterait une surface réfléchissante pour notre entreprise ? Je crois qu'elle habitait au sud du bois de la Pénombre..."}, {"Trouver Marie et la persuader de prêter une surface réfléchissante pour le projet de Viktori."}},
[176] = {"On recherche : \"Lardeur\"", {"On recherche : Lardeur.","Un énorme gnoll, Lardeur, rôde dans les bois en quête de proies, au sud-ouest d'Elwynn. Nul n'a réussi à le capturer.","L'armée de Stormwind offre une généreuse récompense à celui qui le tuera et qui apportera une preuve de sa mort au maréchal Dughan, de Goldshire."}, {"Liquider le gnoll Lardeur et apporter sa Grande griffe au maréchal Dughan."}},
[177] = {"Les yeux dans les étoiles", {"Le miroir était l'un de mes plus précieux trésors jusqu'à... l'accident. Après cela, je l'ai jeté derrière la grange. Il y avait une goule folle qui traînait souvent par là-bas. Elle l'a peut-être récupéré. La rumeur dit qu'elle vit dans le cimetière des Jardins paisibles."}, {"Prendre le miroir de Marie l'Aveugle à la Goule folle et l’apporter à Viktori de Darkshire."}},
[178] = {"Theocritus récupère son Pendentif", {"<Vous entendez une voix désincarnée...>","Ne soyez pas <effrayé/effrayée>. Je suis Theocritus, grand mage de la tour d'Azora de la forêt d'Elwynn. Le pendentif que vous avez en main est un moyen de communication entre les gnolls Sombrepoil et leur maître Morganth.","Après des mois de recherches, j'ai cru que moi aussi je pourrais communiquer grâce à ces pendentifs. Si vous pouvez entendre ce message, c'est que mon sortilège est un succès.","Apportez-moi ce pendentif et je vous récompenserai."}, {"Apporter l'Ancien pendentif sombrepoil à Theocritus le Mage."}},
[179] = {"Tailleur chez les nains", {"Qu'est-ce que c'est ? On dirait que vous avez besoin de quelque chose pour tenir chaud aux mains, non ?","Je vais vous dire ce qui vous serait utile : une paire de jolis gants bien chauds. Et comme je suis une âme charitable, je vous les fournirai. À une condition, cependant.","J'ai besoin de viande de loup. Arrangement équitable, n'est-ce pas ? Vous m'apportez de la viande et je m'assure qu'on ne sera pas obligé de vous amputer d’un ou deux doigts gelés. Qu'est-ce que vous en dites ?"}, {"Sten Stoutarm voudrait 8 Morceaux de Viande de loup coriace."}},
[180] = {"On recherche : lieutenant Fangore", {"On recherche : Lieutenant Fangore.","Connu comme chef du clan des gnolls Sombrepoil, sous les ordres de Morganth, ennemi de la commune et du royaume. Sur mandat du magistrat Solomon, ce traître doit mourir. Apportez sa patte au magistrat comme preuve de sa mort."}, {"Tuer le lieutenant Fangore et rapporter sa patte au magistrat Solomon de Lakeshire."}},
[181] = {"Les yeux dans les étoiles", {"Il ne manque plus qu'un seul objet pour terminer cet appareil. Le plan de Cog mentionne une lentille ou quelque chose comme ça. La seule lentille qui soit assez large pour l'appareil risque d'être très difficile à acquérir.","Un ogre du nom de Zzarc'Vul habite dans la colline au sud du bois de la Pénombre. Si vous le pouvez, rapportez-moi son monocle et je l'utiliserai comme lentille !"}, {"Localiser Zzarc'Vul dans le tertre des ogres au sud du bois de la Pénombre et rapporter son Monocle à Viktori de Darkshire."}},
[182] = {"La caverne des trolls", {"Mon frère Senir et moi avons voyagé dans tous les coins de Dun Morogh pour évaluer la menace posée par les trolls. Le Sénat est déjà débordé par les ennuis que causent les troggs, il n'a pas besoin de ce problème supplémentaire.","De ce que j'ai vu, les trolls sont bien installés dans la vallée des Frigères, principalement dans les grottes du sud. Je ne pense pas qu'il soit nécessaire de recourir à l'armée. Quelques hommes robustes devraient suffire.","Vous aimeriez peut-être vous joindre à cette campagne ? J'ai le pouvoir de vous dédommager pour votre aide."}, {"Grelin Whitebeard aimerait que vous tuiez 14 Jeunes trolls Frostmane."}},
[183] = {"Chasseur de sangliers", {"Rien de tel qu'une journée de chasse au sanglier, pas vrai ?","Oui, eh bien ici, dans la vallée des Frigères, il y en a tellement que ça gâche tout plaisir. Pas besoin de les pousser à attaquer ! Ils sont furieux et prêts à charger sans qu'on les aide ! En fait, depuis quelque temps, il y a tellement de sangliers dans la vallée des Frigères que chasseur devient un métier à haut risque.","Pour faire court, si vous pouviez m'aider à tuer quelques bêtes, j'apprécierais le geste."}, {"Talin Keeneye aimerait que vous tuiez 12 Petits sangliers des rochers."}},
[184] = {"Titre de propriété de Furlbrow", {"Voici un titre de propriété concernant une ferme de la marche de l'Ouest, signé par un certain Théodore Furlbrow et par sa femme, Verna. Au dos, il y a quelques mots hâtivement écrits :","\"Nous avons fait pression sur Furlbrow et avons récupéré son titre pensant que ça pourrait nous être utile au cas où nous voudrions nous installer.","Ils ne nous embêteront pas. La dernière fois que je les ai vus, ils étaient sur la route de la marche, avec un chariot cassé.\"","Les Furlbrow aimeraient sans doute récupérer leur titre..."}, {"Rapporter son titre de propriété au Fermier Furlbrow."}},
[185] = {"Maîtrise de la chasse au tigre", {"Lorsque j'étais encore petite fille, mon père m'a tout appris de la chasse au tigre.","Les jeunes félins sont des proies plus faciles. C'est pourquoi vous commencerez par eux. On peut trouver de jeunes tigres à côté du camp de l'expédition.","Faites la preuve de vos capacités en traquant et en chassant quelques-unes de ces bêtes."}, {"Ajeck Rouack de l'Expédition Nesingwary veut que vous chassiez 10 Jeunes tigres de Strangleronce."}},
[186] = {"Maîtrise de la chasse au tigre", {"Vous apprenez vite, <classe>. Passons aux choses sérieuses à présent. Tuez 10 Tigres de Strangleronce, cette fois. Des adultes !"}, {"Ajeck Rouack de l'Expédition Nesingwary veut que vous chassiez 10 Tigres de Strangleronce."}},
[187] = {"Maîtrise de la chasse au tigre", {"Bien... Nous allons relever la barre, et voir dans quel bois on vous a taillé. Prouvez-moi que vous êtes en mesure de tuer 10 Anciens tigres de Strangleronce. Si vous y arrivez, vous serez digne d'affronter la dernière épreuve avant d'être nommé maître-chasseur de tigres en présence de maître Nesingwary.","Les Anciens tigres ne sont pas seulement les plus difficiles à débusquer, ils sont aussi les plus féroces."}, {"Ajeck Rouack de l'Expédition Nesingwary veut que vous chassiez 10 Anciens tigres de Strangleronce."}},
[188] = {"Maîtrise de la chasse au tigre", {"Voici votre dernier défi. Cela fait des semaines que nous traquons un tigre particulièrement malin. Nous l'appelons Sin'Dall. Essayez de réussir ce qu'aucun <race> n'a réussi avant vous : débusquer et tuer Sin'Dall. Apportez-moi sa patte comme preuve.","Ça ne va pas être une tâche facile..."}, {"Ajeck Rouack de l'Expédition Nesingwary veut que vous tuiez Sin’Dall et que vous rapportiez sa Patte."}},
[189] = {"Oreilles de Bloodscalp", {"Saletés de trolls ! Ils massacrent tous nos agents dès qu'ils mettent un pied dans la jungle ! J'ai perdu beaucoup de mes meilleurs hommes dans de simples escarmouches ! Les pertes deviennent insupportables ! Comment vais-je pouvoir expliquer cela au baron Revilgaz ?","Il faut une action décisive... Pense, Kebok, réfléchis... Tu n'as pas gagné tes galons et ton poste en rêvassant... J'y suis ! Vous... <nom>, c'est ça ? Vous allez m'aider, n'est-ce pas ? Il y a de l'argent à gagner.","Voilà le marché : vous me rapportez les oreilles de 15 trolls Bloodscalp et vous recevrez une récompense."}, {"Obtenir 15 Oreilles de trolls Bloodscalp et les apporter à Kebok, à Baie-du-Butin."}},
[190] = {"Maîtrise de la chasse à la panthère", {"Si vous voulez faire partie de la chasse avec ce groupe de cracks rassemblé par Hemet, il va falloir vous en montrer digne ! On va tester vos capacités de chasseur de panthères... En commençant par le plus facile, soyez tranquille. Voyons si vous êtes capable de tuer 10 jeunes panthères. Pour débuter.","Les débusquer représente seulement la moitié de la difficulté..."}, {"Le sire S. J. Erlgadin de l'Expédition Nesingwary veut que vous tuiez 10 Jeunes panthères."}},
[191] = {"Maîtrise de la chasse à la panthère", {"Bon. On va pouvoir passer au cran supérieur. Si vous voulez montrer que vous êtes digne de vous mêler aux chasseurs de grands fauves, vous devrez tuer 10 panthères.","Les adultes sont plus retorses. Elles ne sont pas aussi faciles à chasser que les jeunes."}, {"Le sire S. J. Erlgadin de l'Expédition Nesingwary veut que vous tuiez 10 Panthères."}},
[192] = {"Maîtrise de la chasse à la panthère", {"Maintenant, passons aux choses sérieuses. Un vrai chasseur doit être capable de chasser une panthère Ombregueule, le fauve le plus dangereux de Strangleronce. Si vous voulez nous prouver votre valeur, tuez 10 d'entre elles.","Si vous les trouvez difficiles à débusquer, attendez de vous trouver face-à-face avec l'une d'elles..."}, {"Le sire S. J. Erlgadin de l'Expédition Nesingwary veut que vous tuiez 10 panthères Ombregueule."}},
[193] = {"Maîtrise de la chasse à la panthère", {"Vous avez presque atteint le niveau de maître-chasseur de panthères. Il y en a une, du nom de Bhag'thera qui rôde dans la jungle. Jusqu'à présent, elle a réussi à échapper à toutes nos battues. Voyons si votre habileté est suffisante pour régler son compte à Bhag'thera.","Apportez-moi un Croc de Bhag'thera et vous gagnerez mon respect !"}, {"Le sire S. J. Erlgadin de l'Expédition Nesingwary veut que vous lui rapportiez un Croc de Bhag'thera."}},
[194] = {"Contrôler la population des raptors", {"Vous vous imaginez être un as parmi les chasseurs ? On peut vérifier ça ! Entrez dans cette jungle et tuez 10 raptors de Strangleronce. On verra bien...","Eh non, je ne vous aiderai pas à découvrir comment on les débusque ! La traque fait la moitié du défi !"}, {"Hemet Nesingwary veut que vous tuiez 10 Raptors de Strangleronce."}},
[195] = {"Contrôler la population des raptors", {"Commençons le défi à présent et voyons si vous êtes à la hauteur. Allez dans la jungle et tuez 10 de ces maudits raptors flagellants. Montrez-moi ce dont vous êtes capable !"}, {"Hemet Nesingwary veut que vous tuiez 10 Raptors flagellants."}},
[196] = {"Contrôler la population des raptors", {"Voyons comment vous vous en sortez avec ce nouveau défi. Il existe une espèce de raptor pas loin d'ici, une race assez méfiante de nature. Nous les appelons les Traqueurs de la jungle. Ils sont largement plus coriaces à chasser et à tuer que les proies sur lesquelles vous avez tenté votre chance jusqu'à présent. Tuez 10 Traqueurs de la jungle et je vous indiquerai le meilleur terrain de chasse."}, {"Hemet Nesingwary veut que vous tuiez 10 Traqueurs de la jungle."}},
[197] = {"Contrôler la population des raptors", {"Puisque vous avez montré que vous êtes une telle terreur dans la jungle, laissez-moi vous parler d'un terrible raptor que même moi je n'ai pas réussi à vaincre. Ajeck, que vous voyez là, appelle ce raptor rusé Téthis.","Si vous voulez prouver que vous maîtrisez l'art de la chasse, ramenez-moi sa griffe. C’est un exploit que personne n'a accompli jusqu’à présent."}, {"Hemet Nesingwary veut que vous tuiez Téthis, un raptor farouche et dangereux de Strangleronce."}},
[198] = {"Les marchandises du soldat Thorsen", {"Hum ! Oui, il y a quelque chose que vous pourriez faire. Le lieutenant Doren et ses partisans, dans le nord, ont besoin d'être réapprovisionnés.","Ils n'ont encore rien reçu, et avec les attaques des trolls, j'aurais besoin de quelqu'un pour le faire. Le campement de Doren se situe au nord-ouest de la route, à l'entrée de Strangleronce depuis le bois de la Pénombre. Donnez les provisions au soldat Thorsen ; s'il n'est pas dans le campement, c'est qu'il patrouille plus au sud du camp.","Attention, si vous échouez, vous devrez me rembourser."}, {"Remettre les marchandises des gobelins au soldat Thorsen."}},
[199] = {"Une sombre menace se dessine", {"Vous devez prendre un échantillon de l'étrange poudre odorante."}, {"Retourner voir l'ingénieur en chef Hinderweir et lui montrer l'indice que vous avez découvert."}},
[200] = {"Herod la Paperasse", {"Si Kurzen connaît le secret de Thorsen, alors cela veut dire qu'il y a un espion parmi nous. Nous devons trouver comment le secret a été éventé !","Le contact de Thorsen au camp de Kurzen est Herod la Paperasse. C’est l'officier en logistique de Kurzen et il sait tout ce qui se passe dans l'enceinte de Kurzen. Si vous pouvez mettre la main sur les notes et les registres de Herod, nous comprendrions peut-être comment Thorsen a été découvert.","Lorsque nous étions du côté de Kurzen, le bureau de la Paperasse se trouvait au dernier étage de la seule maison à étages du camp. Ses registres s'y trouvent probablement encore."}, {"Trouver les registres de Herod la Paperasse."}},
[201] = {"L'enquête au camp", {"On m'a rapporté qu'un groupe de chasseurs a établi campement au bord de la rivière, au-dessus de celui de la KapitalRisk, sur le lac Nazfériti. En temps normal, je n'y prêterais pas attention. Mais, aussi improbable que cela puisse paraître, j'ai entendu dire que Hemet Nesingwary était l'un de ces chasseurs ? Il semble avoir beaucoup d'affinités, si vous voyez ce que je veux dire, et l'on n'est jamais trop prudent.","Pourquoi n'iriez-vous pas y faire un tour, <nom>, et voir de quoi il retourne ?"}, {"Trouver le camp des chasseurs et communiquer sa position à Krazek à Baie-du-Butin."}},
[202] = {"Le colonel Kurzen", {"Vous avez montré votre valeur à maintes reprises, et vos ressources semblent même concurrencer celles de ce Kurzen en personne. Il se peut que vous représentiez le salut pour notre bande de rebelles… si vous pouvez vous mesurer à Kurzen et le vaincre !","Il contrôle ses hommes depuis les profondeurs de la Réserve. Vous devez y pénétrer, déborder ses défenseurs, puis combattre Kurzen et ses officiers.","Adieu, <nom>. Mes espoirs et la destinée de mes hommes sont entre vos mains."}, {"Tuer 6 Troupes d'élite, 4 Lieutenants et rapporter la tête de Kurzen au lieutenant Doren au camp rebelle."}},
[203] = {"La seconde rébellion", {"Nous combattons désespérément le colonel Kurzen. Non seulement c’est un habile tacticien, mais nous nous battons à 1 contre 10. Pour survivre, nous devons retourner contre lui ses propres tactiques sournoises.","Investissez son camp, à l'est, et tuez ses hommes. Le plus possible. Et ressortez avant que les renforts ne vous attrapent."}, {"Tuer 15 Guerriers de la jungle.","Retourner voir le sergent Yohwa au camp rebelle."}},
[204] = {"Mauvaise médecine", {"Si nous voulons avoir la moindre chance contre Kurzen, nous devons neutraliser ses services de soins. Il a rassemblé un nombre important de Guérisseurs qui soignent ses troupes avec des remèdes tropicaux et empoisonnent les nôtres avec des extraits de fougère vénéneuse.","Nous avons besoin de ces remèdes pour soigner nos gens, ainsi que d'un échantillon de ces extraits végétaux afin de l'étudier et d’en tirer nos propres médicaments. Fouillez la base de Kurzen et ramenez ces produits. Ses Guérisseurs auront les remèdes sur eux, quant aux extraits, ils sont probablement stockés dans une caisse d'approvisionnement quelconque."}, {"Apporter 7 Remèdes tropicaux et 1 Extrait de fougère vénéneuse au sergentYohwa au Camp rebelle."}},
[205] = {"Sorcellerie troll", {"Tandis que j'étudie ces légendes que vous avez apportées, rassemblez pour moi des instruments de la magie maléfique des trolls. Entrez dans le territoire des trolls Skullsplitter, une tribu mineure du Sud lointain, et obtenez de leurs Sorciers-docteurs et de leurs Mystiques certains Fétiches."}, {"Apporter 4 Fétiches Skullsplitter à frère Nimetz au camp rebelle."}},
[206] = {"Mai'Zoth", {"Kurzen est peut-être mort, mais… ce qui l'a conduit vers le Mal existe toujours ! Mes recherches m'ont appris ceci : un ogre mage du nom de Mai'Zoth vit dans les monts orientaux de Strangleronce. On dit qu'il utilise une magie contrôlant l'esprit, et je suis sûr qu'il a perverti Kurzen.","Si l'on permet à Mai'Zoth de vivre, alors ses maléfices peuvent en influencer d'autres, et nous verrons peut-être surgir un deuxième Kurzen.","Tuez-le, et apportez-moi sa Vue de l'Esprit, l'artefact qu'il utilise pour corrompre les gens."}, {"Apporter la Vue de l'Esprit au frère Nimetz au camp rebelle."}},
[207] = {"Le mystère de Kurzen", {"Le colonel Kurzen était jadis un homme grand et noble. Je dois découvrir la cause de sa déchéance ! Il est peut-être sous l'emprise de la magie des trolls. Pour étayer cette hypothèse, il me faut étudier certaines de leurs légendes.","Quatre tablettes sont cachées dans les ruines des trolls de Strangleronce. Je dois en prendre connaissance ! Recopiez-les et ramenez vos transcriptions.","La première tablette est dans les Ruines de Bal'lal, la deuxième dans le Récif infâme, et vous trouverez les troisième et quatrième dans les ruines de Zul'Kunda."}, {"Trouver les 4 tablettes et rapporter ces légendes au frère Nimetz."}},
[208] = {"La chasse au gros gibier", {"<nom>, vos talents à la chasse sont dignes de notre compagnie. Des champs de bataille de Lordaeron aux jungles les plus denses, j'ai rarement été témoin de prouesses telles que les vôtres.","Vous avez tué le féroce Sin'Dall, l'insaisissable Bag'thera et le perfide Téthis. Mais le trophée ultime reste la tête du roi Bangalash. Ce tigre blanc est la raison de ma présence ici. Il occupe mes pensées depuis longtemps déjà. Tuez le roi Bangalash et vous n'aurez personne à envier."}, {"Hemet Nesingwary veut que vous lui rameniez la tête du roi Bangalash, le grand tigre blanc."}},
[209] = {"Les défenses Skullsplitter", {"Il est difficile et onéreux de tuer assez de tigres pour fournir l'exportation de leurs crocs vers les îles des Mers du sud. Par chance, nous avons mis au point une technique pour fabriquer des faux indétectables.","Etonnamment, la matière idéale pour créer ces faux crocs, ce sont des défenses de troll Skullsplitter.","Et avant de vous mettre à râler, sachez que ce que le client ignore ne peut pas le déranger, hein ?","Apportez-moi beaucoup de ces défenses, afin que l'on puisse fournir la livraison mensuelle !"}, {"Acquérir 18 Défenses de trolls Skullsplitter et les apporter à Kebok."}},
[210] = {"La cuisine de Krazeck", {"Presque toute la cuisine se fait au camp. Et c'était beaucoup de travail, jusqu'à ce que j'achète une marmite à un marchand gobelin de Baie-du-Butin. Cette marmite est une pure merveille, le temps qu'elle m'a fait gagner est inimaginable… malheureusement elle est cassée !","Pourriez-vous la lui ramener afin qu'il la répare ? Son nom est Krazek."}, {"Apporter la Cocotte ébréchée de chez Krazeck à Krazeck le gobelin."}},
[211] = {"Hélas, Andorhal", {"Hélas, le temps est venu d'attaquer Andorhal et d'en finir avec la liche qui la contrôle !","Araj l'Invocateur, la liche, règne sur les ruines de la cité. Il est gardé par de nombreuses créatures du Fléau qui encerclent les restes du centre de la ville, et sa garde du corps personnelle, qui lui est directement attachée, est redoutable. Vous aurez besoin de plusieurs alliés pour avoir la moindre chance de le vaincre.","Exécutez Araj, <nom>, et apportez-moi un éclat de son phylactère comme preuve !"}, {"Apporter l’Eclat phylactère d'Araj au commandant Ashlam Valorfist, au camp du Noroît, dans les Maleterres de l’ouest."}},
[212] = {"Un repas servi froid", {"L'un de mes plats les plus célèbres est le basilic frappé avec sa vinaigrette citronnée. Mais le problème est qu'il est impossible de garder la viande fraîche, et je dois servir ce plat ce soir à l'occasion de l'anniversaire d'un noble !","Aussi si vous pouviez m'apporter un cuissot provenant d'un basilic Oeil-de-glace fraîchement occis, et ce dans les plus brefs délais, alors je vous serais redevable d’un festin de roi.","Ces créatures vagabondent souvent dans les dunes au nord-ouest de Strangleronce, vous avez donc un long chemin à parcourir. Et faites vite !"}, {"Tuer un basilic Œil-de-glace, collecter un Cuissot de basilic glacé, et le rapporter à Angus Stern, au Solitaire bleu.","<Pour réussir cette quête vous devez la terminer sans libérer votre esprit.>"}},
[213] = {"Une ponction musclée", {"Cette fois, c'en est trop ! Ce n'était pas assez que ce Gelriz vire les nababs invités par les princes marchands, à présent il essaie de s'immiscer dans les affaires du plus célèbre des pirates !","Revilgaz ne les aura pas, et il m'a demandé de régler le problème à ma manière.","Ma manière ? Le vol ! Les géologues de la KapitalRisk qui campent près du lac Nazfériti sont très intéressés par ces étranges cristaux bleus qu'ils ont trouvés dans les mines. Rapportez-moi des échantillons de ces cristaux. Peu m'importe comment vous les obtenez."}, {"Retrouver 8 Cristaux réduits et les apporter à Kebok à Baie-du-Butin."}},
[214] = {"Les masques rouges en soie", {"Ce n'est un secret pour personne que le signe de reconnaissance du gang des Défias est un masque rouge. Mais nous avons appris que la qualité du tissu dont est fait le masque détermine le rang de celui qui le porte au sein du gang.","Cela étant dit, je veux que vous éliminiez autant de Défias haut placés que possible. Vous en trouverez à coup sûr dans la cachette de VanCleef. Lorsque vous en aurez terminé, ramenez-moi 10 masques rouges en soie pour me prouver leur mort, je vous remettrai alors une récompense. Bonne chance, <nom>."}, {"L'Eclaireur Riell de la tour de la Colline des sentinelles veut que vous lui rameniez 10 masques rouges en soie."}},
[215] = {"Les secrets de la jungle", {"Merci de m'avoir sauvé, <nom>. Je sais de quoi ça a l'air, mais… permettez-moi de m'expliquer.","J'ai été chargé par le lieutenant Doren de rejoindre les troupes de Kurzen, pour les espionner, et fournir des informations vitales aux renseignements rebelles. Vous avez vu ce que j'ai cru être ma chance de m'infiltrer dans la base de Kurzen. Je m'étais trompé et, encore une fois, merci de m'avoir sauvé.","Il s'agissait d'une mission secrète, aussi n'en parlez à personne. Mais si vous en parlez à Doren, il vous expliquera."}, {"Parler au lieutenant Doren au camp rebelle."}},
[216] = {"Entre un roc et un Crin-de-Chardon", {"Les furbolgs Crins-de-Chardon sont devenus un danger qui menace notre expansion ! Le village des Crins-de-Chardon, à l'est, nous bloque la route menant au poste de Bois-brisé. Par ce chemin, nous pourrions contourner le village d'Astranaar. D'un autre côté, il offrirait à l'Alliance une voie d'accès supplémentaire par où poursuivre son expansion.","<nom>, nous ne pouvons pas tolérer... que se forme ainsi un chemin facilitant leur campagne d'expansion !","Allez au village des Crins-de-Chardon et réduisez le nombre des furbolgs."}, {"Tuer 12 Vengeurs Crins-de-Chardon et 12 Chamans Crins-de-Chardon ; la plupart d’entre eux se trouvent à l’est de la grève de Zoram, dans le village des Crins-de-Chardon. Retourner voir Karang Amakkar, à l’avant-poste de Zoram'gar, à Ashenvale, une fois que vous aurez terminé."}},
[217] = {"À la défense des terres du roi", {"<nom>, nos éclaireurs ont identifié le chef des troggs qui a mené la récente incursion que nous avons subie. Il semble que ces bêtes du Loch Modan répondent aux ordres d'un certain Grawmug. Celui-ci est bien protégé. Deux de ses meilleurs gardes, Grinceur et Bagarreur, ne le quittent pas une seconde.","Si vous vous en sentez capable, <nom>, j'aimerais que vous acceptiez la mission visant à assassiner Gramwug et ses deux acolytes. Une fois la direction des troggs décapitée, nous aurons peut-être une chance de renvoyer ces monstres sous terre."}, {"Tuer le chef des troggs, Grawmug, et ses deux gardes, Grinceur et Bagarreur, puis rendre compte de la situation au capitaine Rugelfuss à la Tour de garde méridionale."}},
[218] = {"Le journal volé", {"Mon journal ! Ils l'ont emporté dans la caverne. Celui qui l'a pris ? C'était une grosse brute avec d'étranges inscriptions sur la peau et le visage. Je n'ai pas pu regarder plus précisément.","Vous avez déjà combattu des trolls, peut-être pourriez-vous tenter de me le rapporter ?"}, {"Grelin Whitebeard veut que vous tuiez Grik'nir le Froid et que vous lui rapportiez son journal."}},
[219] = {"Porté disparu", {"Enfin, des amis de l'Alliance !","Au secours… Je vous en prie…","La dernière chose dont je me souviens est que les orcs ont submergé nos forces. La plupart des hommes ont été tués. J'ai reçu un coup terrible à la tête et tout s'est assombri. Je suis à présent prisonnier dans cette caverne. Au secours, je suis gravement blessé !","J'aurais besoin d'aide pour revenir jusqu'à la ville... par pitié... vous êtes mon seul espoir..."}, {"Protéger le caporal Keeshan sur le chemin des Carmines."}},
[220] = {"L'appel de l'eau", {"Islen aura une nouvelle tâche à vous confier avant de vous récompenser d'un totem. La fiole d'eau que je vous ai donnée est nécessaire à l'accomplissement de cette tâche.","Partez à présent et trouvez-la. Nous nous reverrons dans le futur."}, {"Apporter la Fiole d'eau la plus pure à Islen Waterseer dans les Tarides."}},
[221] = {"Worgens dans les bois", {"Vous avez peut-être remarqué de grands worgens accompagnant les Tisseurs d'ombre dans les bois ? D'après ce que nous savons, ces Coureurs sombres renforcent considérablement les rangs de leurs alliés.","Lors de mes expéditions, j'ai également découvert qu'ils ont envahi le Verger pourrissant, au sud-ouest de la ville. Ces worgens sont plus résistants que ceux que vous avez combattus précédemment.","Restez donc sur vos gardes."}, {"Tuer 12 worgens Messagers Plaie-de-nuit pour Calor en Darkshire."}},
[222] = {"Worgens dans les bois", {"Vos victoires passées m'ont convaincu que vous êtes capable de vous mesurer aux plus forts des worgens infestant les bois.","Parmi ceux qui se sont établis en ces contrées, les Crocs noirs et les Contaminés se sont montrés les plus dangereux. On peut les trouver près de certaines cavernes et dans la mine, au sud.","Vous pourrez voir de loin la lumière des feux qu'ils font avec les os de leurs victimes..."}, {"Tuer 8 Crocs noirs Plaie-de-nuit et 8 Contaminés Plaie-de-nuit pour Calor en Darkshire."}},
[223] = {"Worgens dans les bois", {"Voilà, <nom>. Portez ce message à maître Carevin.","<Il sort promptement un morceau de parchemin décoloré et vous le tend.>","Encore d’autres comme vous, et nous pourrons vaincre les Veilleurs ! Peut-être alors serions-nous en mesure d’achever l'œuvre à laquelle si peu d'entre nous se sont attelés."}, {"Apporter la lettre de Calor à Jonathan Carevin."}},
[224] = {"À la défense des terres du roi", {"Nous devons protéger le Loch, <nom> ! Avec tant de soldats du roi combattant vaillamment sur des champs de bataille lointains, les forces nous manquent à l'intérieur. Les troggs nous envahissent depuis la plus petite brèche et constituent la menace principale qui pèse sur Ironforge. Ces répugnants mutants doivent être annihilés.","Nous avons besoin de vous, <brave aventurier/courageuse aventurière>, pour mener une expédition et amener le chaos dans leurs rangs. Tuez 10 Troggs et 10 Eclaireurs parmi les Brisepierre."}, {"Le Montagnard Cobbleflint de la Tour de garde méridionale veut que vous tuiez 10 Troggs et 10 Eclaireurs Brisepierre."}},
[225] = {"La tombe effritée", {"L'épitaphe, sur la tombe effritée, annonce simplement :","MORGAN LADIMORE","<Après un examen plus minutieux, vous remarquez que l'on a touché à la couche de terre sur la tombe, et qu'une bonne partie semble avoir été déplacée...>"}, {"Parler à Sirra Von'Indi à Darkshire."}},
[226] = {"Des loups aux trousses", {"Sven et moi avons de difficiles journées qui nous attendent, avec ce Nécromancien à l'est et tout le reste. Et ici, isolés comme nous le sommes, nous devons chasser pour nous procurer de la nourriture. Il semble que chaque fois que je rentre au campement avec un peu de viande sur moi, des loups sauvages affamés ou enragés me poursuivent en quête d'un morceau. Cela va sans dire que vivre ici est une tâche ardue !","Mais si vous pouviez nous débarrasser de ces loups, vous nous rendriez les choses un peu plus simples. Ils rôdent pour la plupart au nord et à l'est d'ici, près de la rivière."}, {"Tuer 12 Loups redoutables affamés et 8 Loups redoutables enragés, puis retourner auprès de Lars au camp de Sven à la frontière ouest du bois de la Pénombre."}},
[227] = {"Morgan Ladimore", {"<Il cherche quelques instants sur les étagères puis en sort un livre relié en cuir.>","Si vous voulez en savoir plus, vous devriez parler à Althea, c'est elle qui s'est occupée du… problème… avec lui ces derniers temps."}, {"Parler au commandant Althea Ebonlocke à Darkshire."}},
[228] = {"Mor'Ladim", {"D'après ce que m'ont rapporté mes Eclaireurs, Mor'Ladim erre du côté du bois de la Pénombre. On le voit suivre un étrange chemin sinuant à travers le cimetière. Nous l'avons enseveli après la maison sur la colline, vous voyez où c'est ?","Il n'y a rien que je puisse faire pour vous venir en aide, mais je vous souhaite bonne chance."}, {"Tuer Mor'Ladim, puis rapporter son crâne au commandant Althea Ebonlocke à Darkshire."}},
[229] = {"La fille qui survécut", {"J'aurais dû vous le dire plus tôt…","Comment puis-je m'exprimer…","Il se peut que Morgan ait cru que sa famille tout entière était morte, mais en fait sa fille Sarah est Gardien à présent. Elle a toujours été troublée par les… circonstances... entourant la mort de son père. Peut-être pourriez-vous lui donner des nouvelles."}, {"Parler au Gardien Sarah Ladimore à Darkshire."}},
[230] = {"Le camp de Sven", {"En creusant, vous trouvez un petit livre, plein de terre.","Il ne porte pas de titre, mais il contient des informations que Sven aimerait connaître."}, {"Apporter à Sven le Livre de la ferme de Sven."}},
[231] = {"L'amour d'une fille", {"Voilà, prenez ceci et posez-la sur sa tombe. Peut-être… je ne sais comment… comprendra-t-il que je vais bien, et que personne ne le considère responsable de ce qui est arrivé.","<Les larmes perlent à ses yeux à présent, tandis qu'elle enlève une bague de sa main droite, et la presse dans votre paume.>","Et... merci, <nom>."}, {"Déposer la bague de Sarah Ladimore sur la tombe de Morgan Ladimore."}},
[232] = {"Course pour l'Apothicaire Zinge", {"La Société royale des apothicaires a besoin de vous. Écoutez attentivement et cela sera bénéfique pour nous deux.","L'apothicaire Keever dans l'autre salle risque d'être... vexé. Mais ses recherches porteraient leurs fruits si elles étaient faites par un chercheur plus intelligent. Heureusement, je suis cette chercheuse. Je veux que vous apportiez ce bon de commande à Alessandro Luca, le propriétaire du bazar de la Lune bleue. Il vous donnera une trousse d'analyse. Quand vous l'aurez, rapportez-la moi et je vous expliquerai alors le détail de la quête."}, {"Apporter le bon de commande à Alessandro Luca dans Undercity."}},
[233] = {"La distribution du courrier de la Vallée des Frigères", {"Hum, je ne pense pas que vous seriez <prêt/prête> à me rendre un petit service, si ? Une pile de lettres est arrivée au tri aujourd'hui, mais je n'ai pas le temps de les distribuer.","Elles ont toutes pour destinataire un dénommé Talin Keeneye. Vous pouvez le trouver à l'ouest, sur la route. Il a établi son camp près du lac Gelé.","Qu'en dites-vous ?"}, {"Apporter la pile de lettres à Talin Keeneye."}},
[234] = {"La distribution du courrier de la Vallée des Frigères", {"Si je me souviens bien, le camp de Grelin se trouve plus bas sur la route, vers le sud-est. Il ne fait aucun doute qu'il serait ravi de recevoir ce courrier."}, {"Remettre la lettre à Grelin Whitebeard."}},
[235] = {"La Chasse d'Ashenvale", {"Votre attention ! Les terres sauvages d'Ashenvale vous attendent tous !","La Horde s'est établie dans les terres au nord des Tarides. Nos deux avant-postes : le Poste de Bois-brisé et le Poste de la Grève de Zoram s'efforcent bravement d'apporter la gloire à la Horde ! Ceux et celles qui veulent faire montre de leur courage devraient y trouver conseil. De plus, Senani Thunderheart, au Poste de Bois-brisé, directement au nord des Tarides, cherche en ce moment des candidats prêts à prendre part à une grande Chasse d'Ashenvale !"}, {"Parler à Senani Thunderheart, au Poste de Bois-brisé, à Ashenvale."}},
[237] = {"À la défense des terres du roi", {"Le Montagnard Cobbleflint n'a pas tari d’éloges à votre égard, <nom>. Aussi, je vais vous confier une mission de la plus haute importance. Nous devons maintenir la pression sur les forces d'invasion des troggs jusqu'à ce que les nains, nos frères, reviennent du front de l'Alliance.","Mettez-vous en route pour les collines méridionales et tuez 10 Brise-crâne et 10 Prophètes Brisepierre. Vos attaques nous feront gagner un temps précieux. Revenez me voir une fois votre mission menée à bien."}, {"Le Montagnard Gravelgaw de la Tour de garde méridionale veut que vous tuiez 10 Brise-crâne et 10 Prophètes Brisepierre, puis que vous retourniez le voir."}},
[238] = {"Course pour l'Apothicaire Zinge", {"La trousse est prête, <nom>. L'apothicaire Zinge souhaite sûrement l'avoir le plus vite possible. Apportez-la-lui sans tarder et dites-lui que j'ai mis cela sur le compte de la Société. Passez-lui également mes plus sincères salutations.","Eh bien, ne restez pas là. C'est pourtant simple d'être coursier, non ?"}, {"Apportez une Trousse d'analyse de terrain à l'Apothicaire Zinge à Undercity."}},
[239] = {"Au secours de la garnison du ruisseau de l'ouest !", {"La garnison basée sur notre frontière à l'ouest fait état d'une augmentation de l'activité des gnolls et des voleurs. Elle demande plus de soldats de Stormwind, mais nous ne pouvons nous passer d'aucun !","Si vous le pouvez, votre aide sera la bienvenue. Allez voir l’Adjoint Rainer à la garnison du ruisseau de l'ouest. Il vous dira de quoi il s'agit.","La garnison se trouve le long de la route, à l'ouest. Après avoir traversé le pont au-dessus du ruisseau, ce sera sur la droite."}, {"Se rendre à la garnison du ruisseau de l'ouest et parler à l’Adjoint Rainer."}},
[240] = {"Retour vers la Tremblotte", {"Et nous y voilà. Des gâteaux au \"crabe\" façon Grual."}, {"Retourner voir la Tremblotte avec des Gâteaux au crabe brun."}},
[243] = {"Sur le terrain", {"Vous allez vous rendre dans le désert de Tanaris, pour tester la consistance des tissus des basilics, des scorpides et des hyènes de la région. Récupérez des échantillons de tissus et placez-les dans la trousse d'analyse ; certains ne conviendront sans doute pas. Il me faut huit échantillons corrects pour chacun des trois types de créatures.","De plus, les gobelins de Gadgetzan ont une source d'énergie utilisable avec ce genre de trousse modifiée. Allez parler à l'ingénieur en chef Bilgewhizzle et récupérez-en pour la trousse.","Ne revenez me voir que si vous réussissez."}, {"Parler à l'Ingénieur en chef Bilgewhizzle à Gadgetzan afin d'obtenir une source d'énergie pour la trousse d'analyse."}},
[244] = {"Des gnolls encombrants", {"Je vous salue, <classe>. On m'a chargé de patrouiller sur ce bout de route. Bien qu'elle soit sûre pour l'heure, j'ai vu des campements de gnolls au nord et à l'est d'ici.","Lakeshire doit être mise au courant de ce rassemblement de forces ! Allez voir l’Adjoint Feldon et informez-le. Faites-le, et je suis sûr qu’il vous offrira la prime d'un éclaireur.","D'ordinaire, Feldon inspecte les lieux au sud du pont menant à Lakeshire."}, {"Informer l’Adjoint Feldon."}},
[245] = {"Les plaies à huit pattes", {"Je vous salue, <classe>. Peut-être avez-vous vu ces araignées qui ont envahi la frontière ouest ? Nous n'avons pas les forces nécessaires à une attaque, et le commandant n'a pas suffisamment d'hommes pour en envoyer si loin de Darkshire.","Cela me gêne de vous le demander, mais il semble que vous êtes de taille à vous en occuper. Je ne peux rien vous promettre de grande valeur, mais je vous récompenserai si vous parvenez à déloger ces horribles bestioles.","Oh, un petit conseil : évitez leur venin..."}, {"Tuer 15 Araignées Tisse-venin pygmées, puis rendre compte à la Sentinelle Dodds au bois de la Pénombre."}},
[246] = {"L'appréciation du danger", {"Nous ne pouvons nous passer de personne, la présence de chacun est vitale ici. Pourtant, quelque chose doit être fait à propos de ces gnolls dont vous m'avez parlé.","Partez à leur recherche, au sud des Carmines. Harcelez-les, tuez tous ceux que vous pouvez et revenez m'informer de leur nombre et du danger potentiel qu'ils représentent. Vous trouverez le camp des gnolls le long de la route sud des Carmines.","Faites-le pour l'armée de Stormwind, <nom>, et vous serez <récompensé/récompensée>."}, {"Tuer 10 Bâtards des Carmines et 6 Braconniers des Carmines, et aller faire un rapport à l’Adjoint Feldon à Lakeshire."}},
[247] = {"La chasse achevée", nil, nil},
[248] = {"En regardant plus loin", {"J'ai observé la Tour d'Ilgalar, et ai découvert que Morganth est entré en conflit avec les orcs au donjon de Guet-de-pierre. Je n’en connais pas la raison, mais vous pourriez m'aider à lever le voile sur ce mystère.","Prenez donc le Glyphe d'Azora, et rendez-vous au sommet de la Tour de garde restée intacte, près du donjon de Guet-de-Pierre.","Là, trouvez un objet dans lequel placer le glyphe. Un coffre, une jarre, qu'importe, pourvu que le glyphe ne puisse pas être détecté."}, {"Monter au sommet de la Tour de garde intacte près du Donjon de Guet-de-pierre, puis trouver dans quoi placer le Glyphe d'Azora."}},
[249] = {"Morganth", {"Après une longue surveillance, j'ai découvert les plans de Morganth.","Il recherche la faux d'Elune… et il se peut même qu'il l'ait déjà trouvée ! S'il se l'est appropriée, alors les Carmines et le royaume de Stormwind tomberont bientôt sous son joug.","Je ne suis pas prêt à l’affronter, mais j'ai peur qu'il faille s'en occuper dès à présent. Vous devez donc pénétrer dans la tour d'Ilgalar, et combattre Morganth.","Remportez la victoire, et apportez-moi son pendentif des ombres. Si vous réussissez, le royaume vous sera grandement redevable."}, {"Tuer Morganth, puis rapporter son Pendentif des ombres à Théocritus, à la Tour d'Azora dans la forêt d'Elwynn."}},
[250] = {"Une sombre menace se dessine", {"Grâce au Ciel, quelqu'un s'inquiète pour le barrage ! D'abord la destruction du viaduc de Thandol, ensuite le pillage de Dun Modr ! Je suis sûr que le barrage est la prochaine cible des Sombrefer. La plus grande partie de mon détachement de sécurité a été dépêché sur le front de l'Alliance, y compris mon meilleur inspecteur.","C'est pourquoi j'ai besoin de vous, <nom>. J'ai aperçu quelques Sapeurs Sombrefer près de la rampe est du barrage. Enquêtez sur place, et ramenez un indice."}, {"Enquêter près de la rampe est du barrage et rapporter à l'ingénieur en chef Hinderweir un indice qui permettrait de deviner les intentions des nains Sombrefer."}},
[251] = {"La traduction de la lettre d'Abercrombie", {"Apportez la lettre que vous m'avez donnée à Sirra Von'Indi. C'est un érudit en lettres anciennes. Si quelqu’un ici peut traduire ce message, c'est lui !"}, {"Apporter la lettre à Ello, à Sirra Von'Indi."}},
[252] = {"La traduction pour Ello", {"Bon sang ! Si ce qui est écrit là est vrai, alors ce qu'a fait Abercrombie est… incroyable.","Vous devez la transmettre à Ello.","Et faites vite, car je crains qu'un sort funeste n'avance déjà vers notre ville..."}, {"Remettre la lettre traduite au seigneur Ello Ebonlocke."}},
[253] = {"La fiancée de l'Embaumeur", {"Le nom de l’Embaumeur apparaît dans l'une des histoires que se racontent les enfants. C'était un alchimiste bon et généreux, rendu fou par la mort de sa femme, Eliza. Dans le but de la ressusciter, il utilisa la magie noire et plaça son propre cœur dans la poitrine de son épouse décédée.","Il réussit à faire revivre Eliza... mais celle-ci était à présent désormais avide de chair humaine, et l'Embaumeur dut se résoudre à l'enterrer.","Son cœur est son pouvoir. Trouvez la tombe d’Eliza, récupérez son cœur, et ramenez-le-moi pendant qu'il bat encore. Procédez ainsi et son pouvoir s'éteindra."}, {"Trouver la tombe d'Eliza, retrouver le cœur de l'Embaumeur, puis retourner voir Ello Ebonlocke."}},
[254] = {"En creusant la terre", nil, nil},
[255] = {"Mercenaires", {"Deux mois d'attente, et pour apprendre que seule une poignée d'hommes sera dépêchée ici, et qu'il lui faudra deux autres mois pour arriver ! Je comprends que notre ville risquerait d’être sans protection, mais le site de fouilles et le barrage – le barrage ! – sont également l’objet d’attaques.","Je n'ai pas le choix, je vais devoir embaucher des mercenaires.","Qu'en pensez-vous ? Vous semblez faire l'affaire, <classe>. Un groupe d'ogres s'est établi au nord-est du lac. Vous pourriez peut-être recruter d'autres mercenaires et nous débarrasser de la menace qui pèse sur notre ville?"}, {"Le magistrat Bluntnose de Thelsamar vous a <embauché/embauchée> pour tuer 4 Ogres, 4 Brutes et 4 Massacreurs Mo'grosh."}},
[256] = {"ON RECHERCHE : Chok'sul", {"Par décret du Magistrat de Thelsamar : Chok'sul, l'ogre que l'on croit être l'instigateur et le chef des attaques menées sur la ville de Thelsamar, sur le barrage de Formepierre et sur les excavations, est recherché mort, et mort seulement.","La récompense sera donnée à celui qui apportera la preuve de sa mort au Magistrat.","La dernière fois que Chok'sul a été vu, il se trouvait au camp des ogres au nord-est de Loch Modan."}, {"Tuer Chok'sul et rapporter sa tête au Magistrat Bluntnose de Thelsamar."}},
[257] = {"La fanfaronnade d'un chasseur", {"Vous croyez pouvoir vous mesurer à Daryl l'Intrépide, hein ? Si j'étais vous, je n’y croirais pas ! Bien sûr, vous êtes libre d'essayer. Voilà un défi qui devrait être au-dessus de vos capacités, alors ne culpabilisez pas trop si vous n'êtes pas à la hauteur.","Un groupe de Buses s'est établi ici à Loch Modan. Pourquoi ne pas essayer d'en désabuser certaines ? Voilà le marché : si vous parvenez à accomplir le défi en quinze minutes, je vous donne mon arc ou mon fusil.","<Il vous jauge dédaigneusement.>","Vous n'avez pas grand-chose à perdre, de toute façon..."}, {"Tuer 6 Busards des montagnes et retourner auprès de Daryl le Jeune au pavillon des pérégrins en moins de 15 minutes."}},
[258] = {"Le défi d'un chasseur", {"<nom>af, c'est ça ? Je vois que vous exultez pour avoir réussi le premier test, pas vrai ? Comme je vous l'ai dit, ce n'était pas grand-chose.","Vous devriez vous essayer à la chasse au sanglier. Croyez-moi, cela n'a rien à voir avec la chasse au sanglier de la Vallée des Frigères, alors prenez vos précautions. Vous n'aurez que douze minutes cette fois-ci.","Et ne soyez pas <déçu/déçue> en cas d’échec. Je vous donnerai ma chemise si vous réussissez !","À propos, je vous ai déjà raconté comment j'ai reçu ma célèbre cicatrice ? Non ? C'était il y a deux ans..."}, {"Tuer 5 Anciens sangliers des montagnes et retourner auprès de Daryl le Jeune au pavillon des pérégrins en 12 minutes."}},
[261] = {"Sur le chemin écarlate", {"Je serai franc. Nous sommes en guerre contre le Fléau. C'est un mal qui corrompt notre peuple et pollue nos terres. Il faut l'arrêter avant qu'il ne nous prenne de vitesse et n'entraîne notre monde dans l'obscurité. Nous, la Croisade écarlate, avons juré de combattre le Fléau de toutes nos forces et de toute notre âme.","Si vous êtes de notre bord, faites montre de votre courage et prouvez-nous votre allégeance en détruisant les Ravageurs morts-vivants de Désolace, dans la Vallée des ossements, plus au sud.","Faites cela et la Croisade vous portera en haute estime."}, {"Détruire 30 Ravageurs morts-vivants, puis retourner voir Frère Anton, à la Combe de Nijel."}},
[262] = {"L'obscure silhouette", {"Je dois savoir qui était ce rôdeur que j'ai vu dans les ombres, la nuit où ma famille a été tuée !","Je vous en prie, <nom>, vous avez montré que vous êtes <plein/pleine> de ressources, trouvez cet individu. Je dois apprendre les secrets qu'il détient !","Je n'ai aucun autre indice que vous ne connaissiez déjà, mais tenez, prenez le livre que vous avez trouvé à la ferme. Peut-être quelqu'un pourra-t-il faire un lien entre ce livre et son propriétaire."}, {"Montrer le livre que vous avez trouvé à la ferme de Sven aux habitants de Darkshire."}},
[263] = {"À la défense des terres du roi", {"Les Montagnards disent que vous êtes <courageux/courageuse> et <compétent/compétente>, <nom>. Nous avons besoin de gens tels que vous ici. Le problème avec les troggs ne s'arrange pas. Les réservistes ont été envoyés à la guerre et nous sommes seuls. Mais à présent que nous avons un <classe> expérimenté comme vous, <nom>, nous allons voir de quoi vous êtes capable.","Sortez et tuez 10 Chamans et 10 Briseurs d'Os Brisepierre. Nous allons voir si votre réputation est fondée, <tueur de/tueuse de> troggs."}, {"Le Montagnard Wallbang de la Tour de garde méridionale veut que vous tuiez 10 Chamans et 10 Briseurs d'Os Brisepierre."}},
[264] = {"Jusqu'à ce que la mort nous sépare", {"Ordure abjecte ! Me laisser pour cette maudite croisade. Parce que \"la Lumière est notre bien le plus précieux contre la menace des morts-vivants.\"","Et ses enfants alors ? Et MOI ? Nuit après nuit, je l'ai attendu… à toujours passer après son fichu devoir !","Eh bien ! Regardez ce que la \"justice\" apporte, <nom> ! Il est mort et je porte au cou ce qu'il essayait d'arrêter !","Portez ce bout de ferraille sur sa tombe au Sépulcre. Je ne veux plus rien avoir à faire avec ça... et avec lui !"}, {"Placer le pendentif de Clarice sur la tombe de Yuriv, dans la forêt des Pins argentés."}},
[265] = {"L'obscure recherche se poursuit", {"Je vous rends votre livre, <nom>, et vous souhaite bonne chance dans vos recherches. Si vous enquêtez auprès d'autres personnes en ville, elles seront peut-être en mesure de vous fournir d’autres indices.","En attendant, en voici un : bien que l'auteur de ce livre soit étranger à Darkshire, le livre lui-même a été acheté ici."}, {"Voir si d'autres habitants ont des indices concernant l'obscure silhouette."}},
[266] = {"Une enquête à l'auberge", {"Voici votre livre, et bonne chance dans votre quête.","Vous devriez aller jusqu'à l'auberge. Si celui que vous recherchez a passé quelque temps en ville, on l’a peut-être vu là-bas."}, {"Chercher à l'auberge des indices concernant l'obscure silhouette."}},
[267] = {"La menace trogg", {"<classe>, il se peut que vous ayez entendu parler de la menace que les troggs font peser sur les nains. Les réservistes ayant été appelés pour servir sur le front de l'Alliance, nous nous retrouvons avec bien peu de forces pour assurer la sécurité de nos terres. Mon régiment a pour mission de garder la porte, ici, et nous ne pouvons quitter notre poste, nous craignons trop une invasion.","Mais nous devons attaquer ces maudits troggs, qui se terrent dans les collines. Si vous vous en sentez capable, lancez un assaut contre eux. Ramenez-moi 8 Dents de pierre de trogg comme preuve de votre réussite."}, {"Apporter 8 Dents de pierre de troggs au capitaine Rugelfuss à la Tour de garde méridionale."}},
[268] = {"Retourner voir Sven", {"Le livre que vous avez trouvé n'était écrit qu'à moitié. J'ai dû le laisser à l'exploitation de Sven lorsque je me suis enfui jusqu'ici…","Tenez, prenez la version complète de mon journal et donnez-la à Sven. Il décrit de manière plus exhaustive ce qu'il s'y est déroulé.","Ce journal a été ma seule compagnie durant ces dernières semaines, mais je vous le donne volontiers si cela m'épargne d’affronter Sven et sa colère."}, {"Retourner voir Sven avec le journal complété de la Tremblotte."}},
[269] = {"À la recherche de la sagesse", {"Rendez-vous à la cité de Stormwind et parlez à l'évêque Farthing dans la cathédrale de la Lumière. Donnez-lui le journal de la Tremblotte et dites-lui que vous voulez combattre Morbent Fel.","Bien que Stormwind ait abandonné Darkshire, Farthing est un homme aimable et empli de sagesse. Il pourrait vous aider dans votre quête pour nous sauver.","Votre mission est périlleuse, mais hâtez-vous, <nom>. À chaque heure qui s'écoule, le bois de la Pénombre s'assombrit encore..."}, {"Donner le journal complété de la Tremblotte à l'évêque Farthing à Stormwind."}},
[270] = {"La Flotte perdue", {"Il y a des années, la IIIe flotte de Kul'Tiras a coulé le long des côtes des Paluns, une étendue marécageuse située au nord du royaume nain d'Ironforge. Un vaisseau avait à son bord un chargement de fer de sancteforge.","Les objets forgés dans ce métal précieux frappent avec la force de la sainte vérité. Si vous devez combattre Morbent Fel, vous aurez besoin d'une telle arme.","Allez au port de Menethil, et rencontrez mon confrère le nain Glorin Steelbrow. S'il existe une chance de retrouver ce précieux métal, il vous indiquera où le chercher."}, {"Aller au port de Menethil, dans les Paluns, et parler à Glorin Steelbrow."}},
[271] = {"La vengeance de Vyrin", {"Je suppose que vous ne seriez pas contre le fait de rabattre un peu le caquet de Monsieur le Brave ? Daryl a une certaine habileté, il nous le rabâche assez.","Il y a un an, un grand ours était dans le collimateur du pavillon des pérégrins, mais pas un chasseur ne réussissait à le tuer. Alors Daryl s'est dit qu'il allait s'en occuper. La seule chose qu'il y a gagnée, c'est cette vilaine cicatrice à l’œil.","Tuez cet ours, ça clouera le bec de Daryl, pour une fois.","Vous trouverez cet ours, le vieux Noiraud, près de sa tanière dans les collines, au-delà de Thelsamar."}, {"Tuer le Vieux Noiraud puis montrer le résultat à Daryl le Blanc-bec au pavillon des pérégrins."}},
[272] = {"L'épreuve du lion de mer", {"Pour la seconde épreuve, vous aurez besoin des deux moitiés qui constitueront le Pendentif du lion de mer. L'une des moitiés emprunte au lion de mer son agilité à glisser à travers les eaux. L'autre moitié emprunte à l'animal son incroyable endurance. Les druides doivent revêtir ces deux aspects pour pouvoir adopter leur forme aquatique.","Allez parler aux résidents de Reflet-de-Lune pour apprendre où les deux parties se trouvent, puis rapportez-les ici, afin de pouvoir les joindre ensemble. Quand vous aurez formé le pendentif, apportez-le à Dendrite Starblaze."}, {"Trouver le Demi-pendentif d'agilité aquatique et le Demi-pendentif d'endurance aquatique. Parler aux habitants de Reflet-de-Lune pour obtenir des indices quant à l’endroit où ces objets se trouvent.","Reformer le Pendentif du lion de mer à partir des deux moitiés. Vous devez vous trouver à proximité du sanctuaire de Remulos pour faire ceci.","Apporter le Pendentif reformé à Dendrite Starblaze, au village de Havrenuit, à Reflet-de-Lune."}},
[273] = {"Approvisionnement du site de fouilles", {"Cependant, il n'est pas trop tard, votre aide est la bienvenue pour prêter main-forte à Huldar. Il ne peut pas être très loin : aussi forts qu'ils soient, Miran et Saean ne peuvent pas se déplacer très vite avec les barils dont ils sont chargés.","Ils ont pris la route habituelle pour aller au site, celle qui suit la courbe du lac au sud de la ville.","Ça ne devrait pas poser de problème de les rattraper à moins qu'ils ne soient déjà arrivés."}, {"Aller voir Huldar."}},
[274] = {"Une sombre menace se dessine", {"Ce n'est pas un explosif ordinaire. Regardez ces petits cristaux et sentez cette odeur particulière ! C'est de la poudre d'hydroglycérine. Cette substance est sans danger quand elle n'est pas humidifiée ; autrement, elle pourrait faire sauter tout Ironforge en une fois.","La réaction en chaîne peut être enrayée en mêlant quatre composants : du venin de rôdeuse, du cristal Mo'Grosh pilé, une larme de crocilisque et ce colloïde de désamorçage. Allez prévenir Hinderweir au plus vite !"}, {"Sur ordre d'Ashlan Stonesmirk, retourner voir l'Ingénieur en chef Hinderweir pour l'informer de la nouvelle découverte."}},
[275] = {"Fièvre sur le pays", {"Je ne sais pas pourquoi, mais il y a une prolifération maligne dans les Paluns. Les Rampants des tourbières se manifestent. Ils sont malades et répandent leur fièvre partout. Pour circonscrire l’épidémie, ils doivent être dispersés et renvoyés dans les marécages.","Les Rampants des tourbières divaguent dans les voies navigables des Paluns."}, {"Tuer 12 Rampants des tourbières, puis retourner voir Rethiel, le Gouverneur vert, dans les Paluns."}},
[276] = {"Piétinements de pattes", {"Les gnolls Poils-moussus ont vécu paisiblement dans les Paluns pendant longtemps, mais aujourd’hui leur nombre augmente. Les buissons et les fleurs du marais ne leur suffisent plus, et pour alimenter leurs feux, leurs haches crasseuses coupent trop de bois.","L'équilibre naturel n'est plus respecté. Pour la survie des Paluns, le nombre de gnolls doit être réduit.","Tuez 15 Gnolls Poils-moussus et 10 Bâtards Poils-moussus. Chassez-les au sud, près des nains de la Porte d'Algaz. Lorsque vous aurez terminé, revenez me voir."}, {"Tuer 15 Gnolls Poils-moussus et 10 Bâtards Poils-moussus puis retourner voir Rethiel, le Gouverneur vert, dans les Paluns."}},
[277] = {"Feu interdit", {"Les Poils-moussus coupent les arbres des Paluns et les brûlent pour se chauffer. Je ne le supporte plus !","Allez leur prendre leurs silex bruts, les outils qu'ils utilisent pour faire du feu. A l'exception des simples gnolls et des bâtards, tous les Poils-moussus portent des silex."}, {"Rapporter à Rethiel, le Gouverneur vert, 9 Silex bruts."}},
[278] = {"Une sombre menace se dessine", {"Commencez à collecter les matériaux de désamorçage, <nom>. Le venin de rôdeuse peut être collecté sur les araignées de Loch Modan. Vous trouverez les larmes de crocilisque dans le Loch également. Mais le cristal Mo'Grosh sera plus difficile à obtenir. Les ogres dans le nord-est en exploitent dans leurs mines, mais c’est un minerai très rare.","Nous devons être prêts à enrayer l’attaque des Sombrefer ! Revenez quand vous aurez collecté les ingrédients requis."}, {"Collecter du Venin de rôdeuse, un Cristal Mo'grosh et une Larme de crocilisque pour l'Ingénieur en chef Hinderweir."}},
[279] = {"Griffes des profondeurs", {"Les murlocs sortent des eaux profondes et construisent leurs villages sur le littoral. Ils harcèlent nos pêcheurs et nos marchands, et il faut les arrêter.","L'un de ces murlocs, Enfourneur, tend des embuscades aux marchands le long de la route, avec d'autres Branchies-bleues, et va ensuite se réfugier dans les taudis voisins. Nos marchands sont en danger et nous sommes prêts à payer pour les protéger.","Enfourneur rôde le long du fleuve au nord d'ici. Trouvez-le, éliminez sa famille et apportez-moi sa tête."}, {"Tuer 12 murlocs Branchies-bleues.","Tuer Enfourneur et prendre sa tête.","L'apporter à Karl Boran au port de Menethil."}},
[280] = {"Une sombre menace se dessine", {"Tout à l'heure, j'ai découvert quelques insurgés Sombrefer qui nageaient vers le barrage avec un tonnelet. Aucun Montagnard n'était en mesure de les arrêter ! J'ai bien peur qu'ils n'aient placé de l'hydroglycérine à la base du barrage et ça pourrait exploser à tout moment ! Laissez-moi préparer une mixture de désamorçage. Allons-y.","Maintenant, j'ai besoin de vous pour prendre ce produit et le mélanger au contenu du tonnelet avant que ce ne soit trop tard ! Vite !"}, {"Sur ordre de l'Ingénieur en chef Hinderweir, nager jusqu'à la base du barrage pour localiser le tonnelet de poudre et y verser la mixture de désamorçage pour empêcher une explosion."}},
[281] = {"Récupération de marchandises", {"Il y a peu de temps, un navire marchand, le \"Princesse aveugle\" a été attaqué par les murlocs. Tout ce qui n’intéressait pas les créatures a été jeté par-dessus bord, à commencer par une statuette de très grande valeur de Terenas Menethil. Nous voulons retrouver cette statuette.","Recherchez des caisses, des barils et autres éléments de la cargaison perdue le long de la côte ou dans les taudis des murlocs. Retrouvez la Statuette de Menethil et rendez-la-moi."}, {"Rechercher les caisses et les barils dans les camps murlocs le long de la côte."}},
[282] = {"Les observations de Senir", {"Je projetais d'envoyer mon apprenti apporter mon rapport à mon frère Senir, mais je préférerais qu'il soit placé entre des mains plus... fiables. Enfin, si vous êtes d'accord, bien sûr !","Hum... Vous devrez prendre le tunnel pour arriver à Kharanos.","Allez voir le Montagnard Thalos avant de traverser le tunnel. Il est complètement infesté de troggs maintenant.","Retournez vers Anvilmar, puis continuez en prenant à l'est vers le tunnel, Thalos n'est pas loin."}, {"Aller voir le Montagnard Thalos."}},
[283] = {"Une sombre menace se dessine", {"Le tonnelet pétille légèrement."}, {"La Mixture de désamorçage semble avoir fonctionné. Retourner voir l'Ingénieur en chef Hinderweir pour lui annoncer la bonne nouvelle."}},
[284] = {"La recherche continue", {"Vous avez fouillé avec soin les taudis murlocs dans ce secteur et vous n'avez trouvé aucune trace de la Statuette de Menethil.","Il y a peut-être davantage d'indices dans les deux taudis qui se trouvent tout près de vous au nord et au nord-est."}, {"Rechercher la Statuette de Menethil."}},
[285] = {"À la recherche d'autres taudis", {"L'inspection minutieuse de ces quelques taudis murlocs n'a rien donné.","Mais le prochain indice sera le bon.","L'un des taudis qui se situent au nord doit cacher la Statuette de Menethil !"}, {"Continuer à fouiller les taudis des murlocs pour retrouver la Statuette de Menethil."}},
[286] = {"Rapporter la statuette", {"Une fois le baril sorti de la boue et ouvert, on y trouve une figurine argentée empaquetée avec soin dans la paille. Elle représente un jeune paladin aux cheveux longs, qui brandit un marteau.","Ce doit être la Statuette de Menethil ! Vous devez maintenant l’apporter à Karl Boran au Port de Menethil."}, {"Apporter la Statuette de Menethil à Karl Boran."}},
[287] = {"Le repaire des Frostmane", {"Je suis sur mon rapport et j'ai besoin de votre aide, <nom>. Je viens juste de trouver où se cachent les trolls, mais ils sont si nombreux que j'ai peur d'y aller.","Voici ce que j'attends de vous... Allez à la grotte, entrez, tuez quelques trolls et revenez ici.","Prenez la route au nord de Kharanos, puis quand vous arriverez au pont, longez la rivière gelée par l'ouest jusqu'au Lac glacial. Vous trouverez le village de Brassetout sur la rive ouest. Leur repaire est au sud-ouest du village."}, {"Explorer le repaire des Frostmane et tuer 5 Chasseurs de tête Frostmane pour Senir Whitebeard à Kharanos."}},
[288] = {"La IIIe flotte", {"J'étais autrefois le Premier Officier du « Maîtresse de l'onde », l'un des bateaux de la IIIe flotte de Kul Tiras. La Flotte perdue. Perdue parce qu'à son dernier voyage, elle a été attaquée par des dragons rouges. Des dragons conduits par des orcs !","Nous n'avions aucune chance contre le feu des dragons... Notre flotte a été brûlée et envoyée par le fond. Je suis le seul survivant de ces malheureux navires...","... Oh ça cogne dans ma tête ! Il me faudrait un coup à boire pour supporter cette gueule de bois et continuer mon histoire."}, {"Acheter une Cruche d'hydromel pour le Premier Officier Fitzsimmons."}},
[289] = {"L'équipage maudit", {"<Revigoré, Fitzsimmons continue son histoire...>","Je suis le seul survivant de l'infortunée IIIe flotte de Kul Tiras, détruite au large de la côte, vers le nord. Mais bien que je sois le seul membre d’équipage qui respire toujours, je ne suis pas le seul qui marche encore ! Car sur l'un de nos bateaux a été caché un trésor épouvantable, dont le pouvoir énorme maintient prisonnières les âmes de mes frères d'armes dans les épaves pourries de nos bateaux.","Mais si vous parveniez à libérer quelques-uns de mes frères, peut-être pourriez-vous briser la malédiction."}, {"Tuer 13 Marins maudits, 5 Soldats de marine maudits et le Premier Officier Snellig. Rapporter la Tabatière de Snellig au Premier Officier Fitzsimmons au port de Menethil."}},
[290] = {"Levée de la malédiction", {"Pour rompre la malédiction et libérer mes frères d'armes, la... chose... qui se trouve sur mon bateau doit être récupérée et nettoyée ! Je ne sais pas ce que c'est, mais je sais qu'elle est en lieu sûr dans le coffre-fort du bateau, dont seul le capitaine Halyndor avait la clé.","Mon vieux capitaine rôde toujours dans mes rêves. Je sais qu'il erre sur \"l'Intrépide\", mon bateau perdu. Obtenez de lui sa clé et utilisez-la pour ouvrir le coffre de \"l'Intrépide\".","Ensuite, quand vous aurez cet objet maudit, apportez-le au prêtre Glorin Steelbrow en ville."}, {"Obtenir du capitaine Halyndor la Clé du coffre-fort de \"l'Intrépide\".","Utiliser la clé pour ouvrir le Coffre verrouillé de l'Intrépide."}},
[291] = {"Les rapports", {"Excellent ! Apportez mon rapport au Sénateur Barin Redstone. C'est un type plutôt aigri, alors ne vous laissez pas impressionner. Il est à Ironforge, dans la salle où se tient la cour du roi Magni.","Vous ne connaissez pas la route pour Ironforge ? Prenez la route au nord de Kharanos, traversez le pont, suivez ensuite la route de l'est – il y a de grandes bannières le long de la route – jusqu'en haut de la montagne.","Cela dit, si vous pouviez éviter de raconter que vous m'avez aidé... Je ne veux pas qu'ils pensent que je ne bosse pas assez dur ici, vous comprenez ?"}, {"Livrer le rapport de Senir au Sénateur Barin Redstone à Ironforge."}},
[292] = {"L'Oeil de Paleth", {"En ouvrant le coffre, on trouve une sphère noircie, qui irradie une lumière à faire frémir. C'est le trésor dont Fitzsimmons a parlé : un Oeil maudit de Paleth.","Apportez l'Oeil maudit de Paleth au prêtre Glorin Steelbrow, un nain qui se trouve au port de Menethil. Demandez-lui comment lever la malédiction de l'Oeil."}, {"Apporter l'Oeil maudit de Paleth à Glorin Steelbrow."}},
[293] = {"Purifier l'Oeil", {"Je ne peux pas lever la malédiction de l'Oeil de Paleth. Ces Yeux ont été fabriqués par des prêtres humains, vous devez donc aller à Stormwind pour trouver quelqu'un qui pourra vous aider. Allez voir l'archevêque Benedictus, le maître de la cathédrale de la Lumière de Stormwind. Il vous aidera.","Je l'espère pour vous."}, {"Apporter l'Oeil maudit de Paleth à l'archevêque Benedictus, à la cathédrale de la Lumière de la ville de Stormwind."}},
[294] = {"La vengeance d'Ormer", {"La situation est grave, c'est certain. Les raptors ont été attirés par les ossements. Ces sales bêtes ont tué mes compagnons et nous ont coincés ici, Merrin, le pauvre Prospecteur et moi.","Aidez-nous à nettoyer les Paluns de ces raptors, <nom>. Il y a des raptors tachetés et des Hurleurs tachetés à l'ouest. Tuez-en 10 de chaque, si vous le pouvez.","Ce sera un bon début à la vengeance que je leur réserve."}, {"Sur ordre d'Ormer Ironbraid à l'Excavation de Whelgar, tuer 10 Hurleurs tachetés et 10 Raptors tachetés."}},
[295] = {"La vengeance d'Ormer", {"Il est vraiment temps de faire payer à ces raptors leur soif sanguinaire. Un peu plus bas, il y a d’innombrables Faucheurs tachetés et Tranchegueules tachetés. Tuez 10 Faucheurs et 10 Tranchegueules !"}, {"Tuer 10 Faucheurs tachetés et 10 Tranchegueules tachetés pour Ormer Ironbraid, puis aller le voir à l'excavation de Whelgar."}},
[296] = {"La vengeance d'Ormer", {"Lorsque vous étiez là-bas, j'ai remarqué qu'un de ces monstres se tenait à l'écart des autres. Il était plus grand et plus menaçant. Je parie que c'est lui qui a incité les autres à venir ici interrompre les fouilles.","J'ai une dernière chose à vous demander, <nom>. Faites en sorte que Grondecroc réponde de ses crimes à la justice. Et compte tenu de la gravité de ses crimes, la justice dans ce cas, c'est la mort ! Apportez-moi une de ses serres pour preuve de sa mort."}, {"Sur ordre d'Ormer Ironbraid, de l'Excavation de Whelgar, tuer Grondecroc et revenir avec l'une de ses serres une fois la tâche accomplie."}},
[297] = {"Réunir les idoles", {"Récemment, juste avant que les troggs ne débarquent sur le site, nous avons découvert un grand nombre d'étranges idoles taillées. Mais nous n'avons pas eu le temps de les étudier, car les troggs nous ont chassés des ruines juste après ! Et ces idoles ont un curieux effet sur les troggs : elles les rendent fous furieux !","Rapportez-moi 8 de ces idoles. Je veux les étudier et il ne faut pas qu'elles restent aux mains des troggs ! Vous pouvez trouver les idoles sur les troggs qui infestent le site."}, {"Apporter 8 Idoles de pierre sculptée à Magmar Fellhew."}},
[298] = {"Compte rendu d'avancement des fouilles", {"Je dois envoyer un compte rendu d'avancement à Thelsamar et une nouvelle demande pour avoir plus d'explosif ! Voici le rapport. Portez-le à Jern Hornhelm. C'est notre contact local à Thelsamar.","Ce n'est pas une tâche difficile, mais ça doit être fait."}, {"Apporter le Compte rendu d'avancement d'Ironband à Jern Hornhelm à Thelsamar."}},
[299] = {"Découverte du passé", {"Avant l'invasion, j'ai découvert une tablette appelée la Pierre de Goaz. Elle s'est brisée en quatre morceaux. Elle parle d'un \"plan divin\" et d'une \"prophétie damnée\".","Je crains que nous n'ayons pas beaucoup de temps. Je suis trop vieux pour aller affronter ces bêtes. Mais vous êtes solide.","Explorez le site de fouilles et cherchez les 4 fragments : Ados, Modr, Golm et Neru. Cherchez dans les artefacts anciens, ou bien là où le sol est meuble. Apportez-les, que je puisse résoudre le mystère !"}, {"À la demande du prospecteur Whelgar, parcourir l'Excavation à la recherche des 4 fragments de tablette : Ados, Modr, Golm et Neru."}},
[301] = {"Rapport à Ironforge", {"Nous aurons besoin d'une autorisation pour fournir à Ironband plus d'explosif. Vous devrez apporter son rapport au quartier général de la Ligue des Explorateurs à Ironforge.","Voici ce rapport. Donnez-le au Prospecteur Stormpike et mettez-vous à l'abri juste après ! Stormpike est impétueux et il ne sera pas ravi d'apprendre qu'Ironband ne progresse que lentement sur le site.","Vous trouverez Stormpike à l'Assemblée des Explorateurs."}, {"Porter le Compte rendu d'avancement d'Ironband au Prospecteur Stormpike."}},
[302] = {"De l'explosif pour Ironband", {"Si Ironband a encore besoin d'explosif, par Magni, alors il l'aura ! J'ai envoyé un messager à Thelsamar avec une autorisation pour Jern Hornhelm. Il préparera la poudre et du matériel à remettre à Ironband.","Encore une chose : je veux que vous assuriez personnellement la livraison pour que l'explosif arrive à bon port. Le dernier chargement n'y est pas arrivé, alors gardez l'esprit vif et les yeux ouverts."}, {"Parler à Jern Hornhelm à Thelsamar."}},
[303] = {"La guerre contre les Sombrefer", {"La guerre fait rage ici autant qu'à l'étranger. Tandis que nos valeureux nains donnent leur vie loin de chez eux au nom de l'Alliance, nos propres terres sont la proie des nains Sombrefer. Ils ont eu raison du viaduc de Thandol. Dun Modr est tombée peu après.","Nous avons besoin de vous pour reprendre des forces et repousser le front. Si vous pouvez tuer 15 nains Sombrefer, 5 Tunneliers, 5 Saboteurs et 5 Démolisseurs, nos réservistes pourront être là à temps pour conduire l'offensive.","Faites vite !"}, {"Sur ordre de Motley Garmason à Dun Modr, tuer 15 nains Sombrefer : 5 Tunneliers Sombrefer, 5 Saboteurs Sombrefer et 5 Démolisseurs Sombrefer."}},
[304] = {"Une tâche sinistre", {"Beaucoup de mes soldats sont morts à la bataille de Dun Modr. Mon propre frère a péri lors de la destruction du pont de l'ouest du viaduc de Thandol. Oui, les nains Sombrefer sont une race diabolique. D'après ce que Roggo a réussi à apprendre, le chef de la bande de nervis qui se trouve dans les Paluns est un démoniste du nom de Balgaras le Crasseux. Et c'est un véritable lâche. Il envoie ses soldats en première ligne, mais il reste invisible et il évite le danger.","Si vous parvenez à trouver cette racaille, à l'exécuter et à m'apporter son oreille, je vous récompenserai, <classe>."}, {"Tuer Balgaras le Crasseux et rapporter son oreille à Longbraid le Macabre à Dun Modr."}},
[305] = {"À la recherche de l'équipe de fouilles", {"Oh bon sang ! Vous devez être l'éclaireur que Longbraid a promis d'envoyer ! Vous ne pouvez pas imaginer mon inquiétude.","Ma femme, Merrin, m'envoie des lettres toutes les semaines depuis le mois dernier. Elle fait partie de l'équipe de fouilles de Whelgar. Mais il s'est écoulé presque deux semaines depuis sa dernière lettre. Il y a sûrement quelque chose qui ne va pas !","Longbraid a dit qu'il enverrait un éclaireur aux nouvelles si nous n'avions pas rapidement du nouveau. Je suppose que l'éclaireur, c'est vous, <nom>. Allez voir Merrin à l'Excavation, à l'est !"}, {"À la demande de Tarrel Rockweaver, aller jusqu'à l'Excavation et contacter Merrin Rockweaver."}},
[306] = {"À la recherche de l'équipe de fouilles", {"C'était horrible ! Nous avions découvert quelques artefacts rares et nous commencions à nous intéresser à ces grands ossements.","Avant d'avoir compris ce qui se passait, ces raptors marbrés ont déboulé sur le site. Il y a eu beaucoup de morts.","C'est Ormer – et avec quel courage ! – qui nous a sauvés, le Prospecteur et moi. Il nous a couverts pendant que nous courions nous mettre à l'abri. Maintenant nous sommes pris au piège. Et nous avons perdu le fruit de notre dur labeur.","Tenez, apportez cette lettre à mon cher Tarrel pour lui expliquer la situation."}, {"Retourner voir Tarrel Rockweaver avec la lettre de Merrin."}},
[307] = {"Bas les pattes !", {"La mine du Ru d'argent à l'est est épuisée depuis longtemps. Elle a été convertie en dépôt de stockage par la Ligue des Mineurs, mais aujourd’hui les kobolds s'y sont installés, et ils mettent leurs pattes sales sur nos bons vieux outils !","Nous chasserons bientôt cette vermine de là, mais avant d'envoyer des guerriers qui feront forcément un peu de casse, la Ligue veut que quelqu'un aille chercher son équipement. Ce ne sera pas facile, vous pourriez avoir besoin de l'aide d'alliés.","L'équipement de mineur est stocké dans les caisses de la Ligue des Mineurs un peu partout dans la mine. Bonne chance !"}, {"Aller à la Mine du Ru d'argent pour prendre 4 lots d'Equipement de mineur.","Retourner voir le Montagnard Stormpike."}},
[308] = {"Distraire Jarven", nil, nil},
[309] = {"Protection de la cargaison", {"Prêt à y aller, <nom> ?","D'abord, nous devons trouver de l'explosif pour Ironband. Je ne pourrai pas le porter seul, d'autant que ça peut être dangereux – et qui sait ce que les Sombrefer me réservent ?","Je me sentirais mieux si vous m'accompagniez."}, {"S'assurer que Miran et la cargaison parviennent au Site de fouilles puis en informer le Prospecteur Ironband."}},
[310] = {"Bières rivales", {"Je dois admettre que ces Thunderbrew font quelques bonnes boissons. Mais il faut qu’ils comprennent qu'ils ne sont pas les seuls brasseurs ! Vous pouvez peut-être m'aider à faire passer le message...","Tenez, prenez ce tonneau de Barleybrew Scalder. Glissez-vous dans le sous-sol de la distillerie Thunderbrew, à Kharanos, et échangez-le avec un de leurs tonneaux de Thunder Ale. Nous verrons bien si leurs clients préfèrent leur recette ou la mienne !","Bien sûr, si les tonneaux sont gardés, il faudra peut-être distraire le garde..."}, {"Aller au sous-sol de la distillerie Thunderbrew à Kharanos et remplacer un Tonneau de Thunder Ale par un Tonneau de Barleybrew Scalder."}},
[311] = {"Retour chez Marleth", {"Après avoir mis un tonneau de chez Marleth Scalder dans la cave de la Distillerie Thunderbrew, vous emportez un Tonneau de Thunder Ale pour prouver que vous l'avez vraiment fait..."}, {"Apporter le Tonneau de Thunder Ale à Marleth Barleybrew au Village de Brassetout."}},
[312] = {"La réserve volée de Tundra MacGrann", {"J'avais un mois de réserves de viande séchée et salée que j'avais stockées pour la saison froide. C'était hors d'atteinte des ours, dans une caisse fermée ! Mais pendant que j'étais à la chasse au daim, cette bête, le Vieux Barbeglace, est venue et repartie avec ma caisse de viande. Il ne sera pas capable de défoncer le thorium de la caisse. Mais je vais mourir de faim si je ne peux pas récupérer mes réserves de viande.","Vous semblez plus discret que moi. Peut-être parviendrez-vous à récupérer ma caisse ? Je n'arriverai jamais à entrer et sortir assez vite de cette caverne avec ma jambe qui se traîne."}, {"Récupérer la caisse de viande séchée de Tundra MacGrann dans la Caverne du Vieux Barbeglace."}},
[313] = {"L'antre Gris", {"La conduite d'une machine de siège n'est pas à la portée de n'importe qui. Il faut une poigne de fer et des nerfs d'acier... Par chance, j'ai les deux ! Et vous ? De quel bois êtes-vous fait ? Vous voulez le prouver ?","Bon... Je cherche comment rendre mon engin, le \"Moissonne-trolls\", un peu plus confortable. Et si vous alliez me chercher quelques peaux de Wendigo dans l'Antre Gris, à l'ouest de Kharanos ? On pourrait en tapisser le pont intérieur de ma machine !"}, {"Réunir 8 Peaux de Wendigo et les apporter au Pilote Stonegear."}},
[314] = {"Protection du troupeau", {"Nous avons entendu des cris au milieu de la nuit. Et ce matin, il manquait deux béliers dans le troupeau. C'est cette bête poltronne connue sous le nom de Vagash qui s'en est prise à notre gagne-pain. Vu que la grande partie de l'armée du roi Magni est loin d'ici, en train de se battre avec l'Alliance, il n'y a personne pour tenir Vagash à distance.","Vous avez peut-être assez de courage pour poursuivre la bête et la tuer. Apportez-moi un de ses crocs et je vous récompenserai. Vagash se cache juste au-dessus du ranch, mais soyez <prudent/prudente>, il est très dangereux."}, {"Sur demande de Rudra Amberstill, tuer Vagash et rapporter un de ses crocs à la ferme des Amberstill."}},
[315] = {"La bière parfaite", {"Je cherche à faire la bière parfaite. Je sais que je peux le faire : le brassage, j'ai ça dans le sang. Je dois juste trouver la bonne recette...","Les trolls Frostmane cultivent une plante, la chatoyante, en haut des collines à l'est. Ils l'utilisent dans les rituels de leur tribu. Nous les nains, nous ne lui avons pas trouvé d'usages, mais elle a un goût unique... Et je veux essayer !","Apportez-moi de la chatoyante qu'utilisent les Prophètes Frostmane, ou piquez-en dans les paniers à chatoyante des trolls."}, {"Apporter 6 Brins de chatoyante à Rejold Barleybrew au village de Brassetout."}},
[317] = {"Ravitailler le Crache-vapeur", {"Je me prépare à entamer une mission pour la brigade des Sapeurs. C'est une longue mission et j'ai besoin de ravitailler le Crache-vapeur avec l'équivalent d'un mois de provisions. Alors pendant que Steelgrill travaille sur mon réservoir, pouvez-vous aller chasser pour moi ?","J'ai besoin de fourrure pour nos lits et de viande pour nous nourrir. Vous pouvez rapporter de la viande de sanglier et de la fourrure d'ours... Vous trouverez l'un et l'autre dans les terrains enneigés au sud de l'Antre Gris."}, {"Réunir 4 morceaux de Viande de sanglier et 2 Fourrures d'ours épaisses et les livrer au pilote Bellowfiz au dépôt de Steelgrill."}},
[318] = {"L’Evershine", {"Rejold Barleybrew fait des expériences de brassage. Certaines ont bon goût, d'autres sont mauvaises et certaines... Eh bien, certaines mettraient par terre les plus vaillants des nains !","Ce brasseur habite à Brassetout, à l'ouest de Kharanos, et j'ai besoin de l'une de ses boissons : l'Evershine. Ça me tiendra au chaud quand je serai sur les routes glacées.","Et... ce liquide béni brûle si bien que je peux le jeter dans la chaudière du Crache-vapeur pour avoir un peu plus de puissance ! Cette petite tactique m'a servi plus d'une fois."}, {"Demander un tonneau d'Evershine à Rejold Barleybrew au village de Brassetout."}},
[319] = {"Une faveur pour un tonneau d’Evershine", {"J'ai un tonneau d'Evershine sous la main, mais... Pouvez-vous me faire une faveur ? Mon village aurait besoin d'un coup de main pour se débarrasser des animaux sauvages qui errent dans les neiges voisines. Parfois ils rôdent tout près.","Nous n'avons pas peur de ces oursons, de ces chats et de ces cochons – aucun nain digne de ce nom n'en aurait peur – mais si vous pouviez vous occuper de ces bestioles, nous aurions plus de temps pour travailler."}, {"Tuer 6 Ours Griffeglace, 8 Anciens sangliers des rochers et 8 Léopards des neiges, puis retourner voir Rejold Barleybrew à Brassetout."}},
[320] = {"Retour vers Bellowfiz", {"Voici votre tonneau d'Evershine, <nom>. Et merci encore pour la partie de chasse."}, {"Donner le Tonneau d'Evershine au Pilote Bellowfiz au Dépôt de Steelgrill."}},
[321] = {"Le fer de sancteforge", {"Les épaves qui se trouvent près du rivage sont les débris de la Flotte perdue de Kul Tiras, brûlée et coulée par les dragons rouges pendant la dernière Grande guerre.","La flotte venait de Lordaeron, mais il y avait aussi un destroyer elfique, \"l'Albatros\". Il transportait une cargaison de fer de sancteforge et avait rejoint la IIIe flotte pour profiter de sa protection. Ironie du sort, il a été coulé par les mêmes dragons...","Mais assez raconté d'histoires. L'épave de \"l'Albatros\" est au sud du port de Menethil. Si vous trouvez du fer de sancteforge là-bas, apportez-le moi."}, {"Fouiller l'épave de l'Albatros, dans les Paluns."}},
[322] = {"Béni soit ce bras", {"Il y a un nain, fabricant d'armes dans le quartier des nains de Stormwind, qui sait travailler le fer de sancteforge. Il s'appelle Grimand Elmore.","J'ai emballé le métal que vous avez trouvé dans une caisse. Apportez-le à Grimand et racontez-lui votre histoire. Je suis sûr qu'il est prêt à aider quiconque mène une quête comme la vôtre et il vous fabriquera une arme – une arme qui donnera du fil à retordre à Morbent Fel lui-même !"}, {"Apporter la Caisse de lingots de sancteforge à Grimand Elmore, à Stormwind."}},
[323] = {"Prouver votre valeur", {"Morbent Fel est un nécromancien, allié des Cavaliers noirs. Je pourrais vous envoyer combattre Morbent Fel, mais je dois d’abord tester votre force avant que vous ne luttiez contre un tel adversaire.","Vous avez déjà prouvé votre bravoure, mais si vous voulez vraiment affronter le nécromancien, vous devez d’abord montrer votre valeur contre ses sbires."}, {"Tuer 15 Ecumeurs squelettes, 3 Soigneurs squelettes et 3 Gardiens squelettes puis retourner voir Sven."}},
[324] = {"Les lingots perdus", {"Puisque vous n'avez trouvé qu'un seul lingot de sancteforge dans le coffre, il paraît évident que les murlocs ont volé le reste. Pourchassez les écumeurs murlocs près de l'épave de \"l'Albatros\" pour retrouver le fer de sancteforge volé."}, {"Collecter 5 Lingots de sancteforge puis retourner voir Glorin Steelbrow, dans les Paluns."}},
[325] = {"En armes et prêt", {"Voici l'arme pour combattre le démon dont vous avez parlé. Vous pouvez y aller, <nom>. Je vous souhaite bonne chance et j'attends avec impatience d'entendre raconter vos exploits !"}, {"Apporter le Fléau de Morbent à Sven, dans le bois de la Pénombre."}},
[328] = {"La clé cachée", {"Bien qu'il ne soit jamais fait mention du soldat Thorsen dans ces rapports, une petite clé en cuivre était cachée dans un des registres d'Herod.","Et en poussant plus loin l'étude de ces notes, on trouve au milieu de divers papiers des références à un \"coffre-fort sécurisé\". Il est quelque part dans la Réserve, l'ensemble de cavernes qui se trouve derrière la Base de Kurzen.","Si nous retrouvons ce coffre-fort et que la petite clé peut l'ouvrir, nous résoudrons peut-être le mystère de Thorsen."}, {"Utiliser la Petite Clé en cuivre pour ouvrir le Coffre-fort de Herod la Paperasse."}},
[329] = {"L'espion découvert !", {"Parmi les objets précieux contenus dans le coffre-fort, il y a une note griffonnée et couverte de boue. Bien qu'elle soit à peine lisible, on dirait un itinéraire pour le soldat Thorsen. Il y a des détails sur ses itinéraires de patrouille, les gens auxquels il parle, ce qu'il fait de son temps avec les rebelles. Elle n'est signée que d'une lettre : M.","Mais celui ou celle qui a écrit cette note doit être proche de Thorsen. Celui ou celle qui a écrit cette note est le traître du camp rebelle."}, {"Apporter la Note maculée de boue au lieutenant Doren, au camp rebelle."}},
[330] = {"Composition des patrouilles", {"La note que vous m'avez donnée est signée d'un \" M \"... Je ne sais pas ce que ça signifie.","Mais... La note dit aussi que cette personne a partagé les tours de patrouille de Thorsen la semaine dernière. Le traître doit être l'un de ceux qui ont patrouillé en même temps que Thorsen !","Allez voir le caporal Sethman. C'est lui qui gère la composition des patrouilles, il vous dira qui était avec Thorsen la semaine dernière."}, {"Parler au caporal Sethman."}},
[331] = {"Rapport au Lieutenant Doren", {"Le Lieutenant Doren a-t-il demandé que vous retourniez le voir avec cette information concernant les patrouilles ?"}, {"Parler au Lieutenant Doren."}},
[332] = {"Publicité pour la cave à vins Gallina", {"Si vous parlez à qui que ce soit de vin, vous apprendrez que nous, les Gallina, nous vendons le meilleur vin de Stormwind. Et nous ne sommes pas loin, dans le Quartier commerçant, le long des superbes canaux de la ville.","Tenez, prenez ce bon d’échange. Apportez-le à ma sœur Suzetta dans notre magasin, et elle vous offrira une bouteille de bienvenue de notre célèbre pinot noir. Vous ne le regretterez pas !"}, {"Aller à la Cave à vins des Gallina et apporter à Suzetta Gallina le Bon pour une bouteille de vin gratuite."}},
[333] = {"Harlan a besoin d'être approvisionné", {"Nous avons fait pas mal d'affaires dernièrement. On dirait que tout le monde achète des armures et des vêtements résistants. Comme s'ils s'attendaient à une saison froide et dure...","Mais ce sont là des soucis pour plus tard. Mon souci aujourd'hui, c'est que je suis à court de vêtements tricotés à vendre. J'ai besoin d'un autre lot de mon fournisseur.","Si vous pouviez en prendre livraison auprès de Rema Schneider, chez le tailleur et équipementier du canal, je vous en serais très reconnaissant."}, {"Aller à la Boutique de tailleur du canal et donner à Rema Schneider la Demande de tissu, pour Harlan Bagley."}},
[334] = {"Un paquet pour Thurman", {"Mon fils Thurman est apprenti chez les Couturiers Larson, dans le Quartier des mages. Il était pressé aujourd'hui et il a oublié ses ciseaux et ses aiguilles. Je sais qu'un grand <classe> comme vous a des tâches autrement plus importantes à accomplir, mais sans ses outils, Thurman ne peut pas faire son travail d'apprenti !","S'il vous plaît, <nom>, pouvez-vous rapporter à mon fils son matériel de couture ? Les Couturiers Larson tiennent l'un des deux magasins de prêt-à-porter du Quartier des mages, c'est le plus éloigné, près de la Tour du mage."}, {"Aller chez les Couturiers Larson, dans le quartier des mages de Stormwind, pour donner à Thurman Schneider son Matériel de couture."}},
[335] = {"Un noble brassage", {"Le seigneur Wishock sème le trouble à la Maison des Nobles. Il sait pour notre petit \"établissement\" et fait pression pour qu'une enquête soit ouverte. J’ai un plan pour « occuper » Wishock, mais je vais avoir besoin de votre aide.","Il y a une fleur qui pousse dans le jardin de Stalvan aux environs de Darkshire. On appelle cette petite fleur blanche la larme de Tilloa. J'aurai aussi besoin d'une racine de musquash, qu'on ne trouve qu'en bas des chutes du barrage de Formepierre dans les Paluns."}, {"Sur ordre de Zardeth de la Griffe noire, rapporter une Larme de Tilloa de Darkshire et une Racine de Musquash des Paluns."}},
[336] = {"Un noble brassage", {"Le seigneur Wishock est comme tous les hommes, il a ses faiblesses. Il se trouve que la sienne est un penchant prononcé pour la bouteille. Quelle sidérante hypocrisie. Ce pauvre imbécile passe ses journées à essayer de faire fermer notre petite « taverne », et ses nuits à l’étage à rallonger une ardoise qu’il ne payera jamais. Et bien nous allons voir comment Wishock apprécie la cuvée spéciale que je lui ai préparée : la bière brune de la Griffe noire. C’est du brutal, <nom>.","Apportez-lui et voyez comment il réagit. Probable que ce vieil idiot soit en train de traîner autour de la Chambre du Requérant."}, {"Apporter la Bière brune de la Griffe noire au seigneur Wishock dans la Chambre du requérant."}},
[337] = {"Un vieux livre d'histoire", {"Bien que la couverture de ce livre soit éraflée et couverte de boue, on arrive à lire le titre \"L'Histoire de Stormwind\" sur la tranche. En l’ouvrant, on voit clairement tamponné sur la première page le cachet de la Bibliothèque de Stormwind.","On dirait que quelqu'un a emprunté ce livre et ne l'a jamais rendu..."}, {"Rendre le livre « l'Histoire de Stormwind » à la Bibliothèque de Stormwind."}},
[338] = {"« Les vertes collines de Strangleronce »", {"Maître Nesingwary va être tellement furieux s'il découvre ça !","Il m'a fait confiance pour relire le manuscrit de son roman « Les vertes collines de Strangleronce ». Tout allait bien, mais une grande rafale de vent a soufflé sur le camp et a dispersé toutes les pages !","J'ai pu en ramasser la plupart, mais certaines sont toujours perdues dans la jungle. Retrouvez les pages qui manquent pour compléter les quatre chapitres.","Je vous aiderai à les remettre dans l'ordre. Rendez-moi les chapitres une fois que vous les aurez tous les quatre."}, {"Rassembler les pages manquantes du manuscrit des « Vertes collines de Strangleronce ». Une fois les quatre chapitres complétés, les rendre à Barnil."}},
[339] = {"Chapitre I", nil, nil},
[340] = {"Chapitre II", {"Le Chapitre II regorgeait de passionnants récits de chasse au tigre et à la panthère. Je me donnerais des coups pour avoir laissé les pages s'envoler !","J’ai récupéré toutes les pages à l'exception des pages 10, 11, 14 et 16."}, {"Rapporter les pages 10, 11, 14 et 16 des « Vertes collines de Strangleronce » de Nesingwary à Barnil Stonepot pour achever le Chapitre II."}},
[341] = {"Chapitre III", {"Le chapitre III est mon préféré. Il parle de la première fois où le sire Erlgadin et dame Rouack ont tué avec un fusil des nains. Et la fin du chapitre, quel suspense !","Je vois qu'il nous manque encore les pages 18, 20, 21 et 24 pour clore le chapitre. Ces pages sont quelque part par là..."}, {"Rapporter les pages 18, 20, 21 et 24 des « Vertes collines de Strangleronce » de Nesingwary à Barnil Stonepot pour achever le Chapitre III."}},
[342] = {"Chapitre IV", {"Ah Le Chapitre IV : le grand final ! Et quel final !","Il ne nous manque plus que trois pages, les 25, 26 et 27, pour compléter le Chapitre IV."}, {"Rapporter les pages 25, 26 et 27 des « Vertes collines de Strangleronce » de Nesingwary à Barnil Stonepot pour achever le Chapitre IV."}},
[343] = {"En parlant de fermeté", {"Je fais un discours sur la fermeté de l'Esprit et je voudrais citer des passages d'un livre sur la métallurgie : \"Les Tensions du fer\". Un exemplaire de ce livre doit se trouver à la Bibliothèque royale, dans le Donjon de Stormwind. Pouvez-vous le récupérer pour moi ? Si vous le faites, que la bénédiction de la Lumière soit sur vous !","La Bibliothèque royale est très grande, mais le responsable, Milton Sheaf, connaît chaque étagère comme sa poche. Parlez-lui, il cherchera le livre pour vous."}, {"Allez à la Bibliothèque royale dans le Donjon de Stormwind pour parler à Milton Sheaf. Il trouvera le livre sur la métallurgie dont Frère Kristoff a besoin pour son discours."}},
[344] = {"Frère Paxton", {"« Les Tensions du fer » ? Oui, je connais ce livre. Excellent ouvrage, écrit par le mineur nain Margulf Blaggon il y a quelques décennies. Il a passé l'essentiel de sa vie au cœur des montagnes de Khaz Modan, à creuser pour trouver du minerai. Chic camarade !","Mais où en étais-je ? Ah oui ! « les Tensions du fer » ! J'ai bien peur que le livre ait été envoyé à l'abbaye du Northshire. Le bibliothécaire de l'abbaye, le frère Paxton, voulait faire une copie de l’ouvrage pour sa propre collection.","Mais c'était il y a plusieurs mois. Je me demande pourquoi ça lui prend si longtemps..."}, {"Parler au frère Paxton, à l'Abbaye du Northshire."}},
[345] = {"Provisions d'encre", {"J'avais l'intention de finir ma copie des « Tensions du fer », mais...","Eh bien vous voyez, je fabrique ma propre encre pour les livres que je copie. C'est une recette personnelle : elle ne coule pas, ne tache pas et sèche très rapidement, voire trop rapidement ! Mon dernier lot a séché avant la fin de mon travail de copie et je n'ai pas eu la possibilité d'en refaire !","Pour en fabriquer davantage, j'ai besoin de minerai de rethban qui vient des Carmines. Le contremaître Oslow de Lakeshire est mon fournisseur habituel."}, {"Aller voir le contremaître Oslow de Lakeshire et lui demander du minerai de rethban."}},
[346] = {"Retourner voir Frère Kristoff", {"Maintenant que j'ai fini ma copie des « Tensions du fer », je vous rends l'original. Merci pour votre patience et si vous voyez Milton, dites-lui que je suis désolé pour ce long retard."}, {"Retourner voir Frère Kristoff sur la Place de la Cathédrale. Lui donner le livre « les Tensions du fer »."}},
[347] = {"Minerai de rethban", {"Le minerai de rethban a été découvert par Bart Rethban. Lorsqu’il est fondu, ce minerai est moins dur que le fer, mais il possède une essence que certains scribes utilisent comme encre. Il peut être trouvé dans les Cavernes de Rethban, plus au nord.","Nous en avons normalement en réserve, mais devant nous préparer aux attaques des orcs, nous n'avons pas eu le temps d'en collecter dernièrement. De plus, les gnolls des Carmines occupent maintenant les cavernes de Rethban et leurs manouvriers y exploitent le minerai eux-mêmes.","Si vous voulez en collecter, vous devrez le faire par vos propres moyens."}, {"Réunir 5 charges de Minerai de rethban et les apporter à Frère Paxton à la bibliothèque du Northshire."}},
[348] = {"La fièvre de Strangleronce", {"Malade... Si... Très m-m-malade...","... attrapé la fièvre de Strangleronce... V-v-virus mortel...","Seul remède... est... est... manger le cœur de M-m-m-m-mokk le Sauvage... Sauvage...","... Le Sorcier-docteur Unbagwa... Lui seul peut me s-s-sau...","... Allez chercher Unbagwa dans la caverne cachée au sud du cap...","... vite... mourant... mourant..."}, {"Chercher le Sorcier-docteur Unbagwa pour qu'il invoque Mokk le Sauvage. Apporter le Cœur de Mokk à Fizracket."}},
[349] = {"La fièvre de Strangleronce", nil, nil},
[350] = {"Le point de vue d'un vieil ami", {"Ce Marzon va contacter un Défias et il est lié à Lescovar...","En ayant tué VanCleef et Thredd, il va être difficile de traîner en justice Lescovar, vu que vos preuves ne valent rien sans leurs témoignages. Sans compter que Lescovar est un noble et qu'il a de nombreux appuis. Il est ainsi au-dessus des lois.","Vous avez besoin d'une autre aide. Je répugne à le faire, mais voici quelqu'un que je connais et qui peut nous aider. Vous trouverez Trias dans le Quartier commerçant de la cité."}, {"Parler à Elling Trias à Stormwind."}},
[351] = {"Trouver OOX-17/TN !", {"Vous avez découvert un étrange objet en forme d'oeuf fait de métal. En jouant avec les petit boutons présents sur le dessus, l'oeuf s'anime, à mesure qu'il s'ouvre, il se transforme en une espèce de bidule robot gnome ! Une voix sort du robot en grésillant.","\"Je suis Oglethorpe Obnoticus, et mon robot guidé a disparu ! Je vous récompenserais si vous le réparez ! Apportez cette balise au robot. Excellent, j'ai programmé les coordonnées du robot pour vous ! Il s'est écrasé près du Gouffre béant à Tanaris !\""}, {"Apporter la balise de détresse au robot guidé d'Oglethorpe."}},
[353] = {"Livraison pour Stormpike", {"Les Stormpike sont un clan nain respecté et connu pour ses goûts raffinés et subtils. Ce n'est donc pas surprenant que Gringer Stormpike, un montagnard d'Ironforge, m'ait demandé de lui fabriquer une arme.","L'arme est terminée, mais Gringer vit loin d'ici, près du Loch Modan. Si vous projetez de voyager vers le nord, pouvez-vous lui livrer ce paquet ?","Son dernier message disait qu'il se trouvait à la Tour de garde nord du Loch Modan."}, {"Livrer le Paquet au montagnard Stormpike au Loch Modan."}},
[354] = {"Morts dans la Famille", {"Les Agamand étaient la famille la plus prospère des Clairières de Tirisfal. Je travaillais dans leurs moulins... avant la Peste.","Quand le Fléau est apparu, les Agamand ont fortifié leur maison et convaincu leurs employés de rester et de les aider à se défendre. Nous étions des imbéciles, mais au moins nous étions des imbéciles loyaux.","Les Agamand, à cause de leur orgueil, nous ont condamnés. Et maintenant le Fléau a fait d'eux ses favoris !","Servez ceux qui ont été abandonnés en vainquant les Agamand qui sont tombés du côté de la Peste. Servez-moi en m'apportant leurs restes."}, {"Rapporter les restes de Gregor, de Nissa et de Thurman à Coleman Farthing, à Brill."}},
[355] = {"Parler à Sevren", {"Vous m'avez ramené les restes des Agamand et satisfait mon désir de vengeance. Mais les Moulins d'Agamand sont une menace pour tous les Réprouvés, pas seulement moi.","J'ai parlé au Magistrat Sevren de vos exploits contre les Agamand et il désire vous parler."}, {"Parler au magistrat Sevren à Brill."}},
[356] = {"Patrouille d'arrière-garde", {"Les défenseurs de la Barricade protègent Tirisfal des Maleterres et sont en alerte permanente. Mais parfois, le Fléau arrive à s'infiltrer.","Notre succès à la Barricade dépend d'un front uni. Nous ne pouvons nous permettre d'être attaqués par l'arrière ou de voir les lignes d'approvisionnement des défenseurs coupées.","Renforcez la Barricade. Patrouillez à l'est et détruisez toutes forces du Fléau que vous rencontrerez. Portez un effort particulier sur la ferme des Balnir. C'est devenu un havre pour les forces du Fléau qui maraudent dans le secteur."}, {"Tuer 8 Horreurs sanguinolentes et 8 Esprits vagabonds, puis revenez voir Linnea, à son camp."}},
[357] = {"Le Nom de la Liche", {"Une liche réside dans l'île au nord du lac Etincelant. Bien qu'elle soit une Réprouvée sauvage, elle croit que tous les autres morts-vivants sont des esclaves du Fléau et envoie ses serviteurs attaquer quiconque s'approche d'elle.","Mais parce que c'est une Réprouvée, la Reine la veut. Et si elle est douée dans les arts de la nécromancie, son savoir serait réellement utile à notre cause.","Pour connaître son identité et ses capacités, j'ai besoin d'étudier son livre de sorts. Infiltrez-vous dans son camp, prenez-le livre et ramenez le moi."}, {"Rapporter le livre de sorts de la Liche et le donner à Bethor Iceshard à Undercity."}},
[358] = {"Les Profanateurs", {"Dans le nord, les fosses communes, au sud-ouest de l'antre de Garren ont été construites pour accueillir les morts de Tirisfal lorsque la Peste frappa la première fois.","Les corps qui s'y trouvent ont pu reposer en paix jusqu'ici mais le Fléau a envoyé des gnolls Poil-putride prendre les corps afin de les utiliser pour accroître leurs armées. Cela ne doit pas être !","Votre mission comporte deux parties : tuer les Poils-putrides dans les fosses communes et dans l'Antre de Garren et récupérer sur eux l'ichor d'embaumement qui leur donne vie."}, {"Tuer des Profanateurs Poils-putrides et des Gnolls Poils-putrides.","Rapporter les Ichors d'embaumement au magistrat Sevren à Brill."}},
[359] = {"Mission des Réprouvés", {"J'ai besoin d'un rapport récent sur notre base de la Garde noire dans l'est.","Je dois savoir s'il y a encore des incursions des forces du Fléau au-delà de la Barricade et dans Tirisfal. Notre vigilance à la frontière des Maleterres doit être maintenue. Nous ne pouvons pas nous permettre que les forces du Fléau s'établissent ici !","Voyagez vers le sud en suivant la route, puis vers l'est à l'embranchement jusqu'au poste. Demandez la Garde noire Linnea. Elle a les informations dont j'ai besoin."}, {"Parler à la Garde noire Linnea."}},
[360] = {"Retourner voir le Magistrat", {"Maintenant, rapportez au Magistrat Sevren les informations que je vous ai données. Il en a besoin le plus vite possible, soyez rapide.","Et si vous voyez des troupes du Fléau sur votre chemin, abattez-les. Nous ne pouvons leur permettre de parcourir nos contrées sans opposition."}, {"Retourner voir le Magistrat Sevren à Brill."}},
[361] = {"Une lettre perdue", {"Ceci est une lettre écrite par Thurman, l'aîné des fils de Gregor Agamand. Elle est adressée à Yvette Farthing, bien qu'il semble qu'elle n'ait pas atteint sa destinatrice…"}, {"Trouver Yvette Farthing. Lui donner la lettre de Thurman Agamand."}},
[362] = {"Les Moulins Hantés", {"Devlin Agamand était le plus jeune des deux fils de la famille Agamand. Jamais frères ne furent aussi différents. Thurman était grand et aimable tandis que son plus jeune frère était faible et acerbe.","Lorsque les Moulins d'Agamand sont tombés suite à la Peste, je n'ai pas été surpris d'apprendre que Devlin avait rapidement succombé. Son bavardage maladif peut toujours être entendu près de la route qui mène aux Moulins d'Agamand.","Je collectionne les restes des Agamand. Je veux ce pauvre Devlin. Trouvez-le, détruisez-le et rapportez-moi ses os."}, {"Tuer Devlin Agamand et ramener ses restes à Coleman Farthing à Brill."}},
[363] = {"Réveil brutal", {"Il était temps de se réveiller. Nous nous apprêtions à vous jeter au feu avec les autres mais il semble que vous ayez réussi.","Je suis Mordo, le fossoyeur de la crypte du Glas. Et vous n'êtes plus l'esclave du roi-liche.","Allez parler au prêtre des ombres Sarvis dans la chapelle en bas de la colline, il vous en dira plus sur ce que vous devez savoir."}, {"Parler au prêtre des ombres Sarvis."}},
[364] = {"Les Déchus", {"Nous, les Réprouvés, sommes en guerre contre le roi-liche et les troupes du Fléau : leurs armées de morts-vivants animés par la nécromancie, des bêtes immondes venues du nord et des spectres tourmentés.","La partie nord du village a été submergée par les Déchus et ils doivent être détruits. Anéantissez-les, n'ayez aucune pitié, même s'ils étaient nos frères et soeurs par le passé. Les Déchus ne sont rien d'autre que les esclaves du roi-liche."}, {"Le prêtre des ombres Sarvis veut que vous tuiez 8 Zombies lunatiques et 8 Zombies décharnés."}},
[365] = {"Les Champs du Chagrin", {"Ça par exemple ! Vous ressemblez à un épouvantail <classe>. Si vous espérez faire vos preuves au service de la Dame noire, il faudra apprendre les voies des Réprouvés.","À l'ouest, vous trouverez une ferme. Les humains infestent cette terre comme la vermine un cadavre. Et pire, la Croisade écarlate patrouille près de leur tour. Donnez-leur une leçon et volez 10 de leurs précieux Potirons.","Une fois que vous les avez, apportez-les à l'Apothicaire Johann à Brill."}, {"Volez 10 Potirons dans la ferme à l'ouest, juste au nord du Glas, et apportez-les à l'Apothicaire Johaan à Brill."}},
[366] = {"Ramener le livre", {"Il y a longtemps, Gunther et moi, ainsi que Thule Ravenclaw qui est aujourd'hui Nécromancien du Fléau, étions amis et étudiants au Kirin Tor - une société de mages de Dalaran.","Gunther et moi sommes tombés, tués par la Peste, mais je pensais qu'il ne s'était pas relevé ou bien qu'il avait rejoint les rangs du roi-liche. Il est agréable de le savoir libre.","Il doit être des nôtres. J'ai mis un sort dans son livre qui, une fois qu'il le verra, lui montrera que je ne suis pas non plus sous la domination du roi-liche. Une fois qu'il saura cela, j'espère qu'il nous rejoindra."}, {"Ramener son livre de sorts à Gunther, dans sa retraite sur son île."}},
[367] = {"Une Peste nouvelle", {"Dame Sylvanas a fait appel à la Société royale des apothicaires. La Dame noire croit que notre savoir couplé à cette nouvelle magie nous donnera la clé de la défaite d'Arthas. Elle nous a mis au défi de créer une Peste nouvelle, une Peste plus puissante que celle qui a ravagé Azeroth. Cette nouvelle infection amènera la ruine des forces du Fléau.","Mes études montrent que le sang de bêtes en pourrait être la clé. Rapportez-moi 5 fioles de sang de sombredogues, que je puisse tester ma théorie."}, {"Collecter 5 fioles de Sang de sombredogues pour l'Apothicaire Johaan de la ville de Brill."}},
[368] = {"Une Peste nouvelle", {"Pendant que vous collectiez mes échantillons, mes expériences m'ont permis de réaliser que des ingrédients supplémentaires seraient nécessaires pour que cette nouvelle maladie se répande proprement. Empoisonner une victime sans défense est un jeu d'enfant. Infecter un monde entier est un peu plus compliqué.","J'aurais besoin de 5 écailles prélevées sur les murlocs Ailerons noirs des environs. Vous trouverez ces créatures le long de la côte du nord ou de l'ouest."}, {"L'Apothicaire Johaan de la ville de Brill a besoin de 5 Ecailles d'Aileron noir prises sur les murlocs des Clairières de Tirisfal."}},
[369] = {"Une Peste nouvelle", {"Tandis que votre quête vous occupait, j'ai découvert de vieux textes dans un de mes livres qui indique qu'une ancienne peste a balayé des milliers de victimes innocentes. Il a été compris plus tard que l'agent infectieux de l'épidémie avait été préservé grâce à du venin des araignées Tisse-nuit vicieuses.","Rapportez-moi un peu de ce venin pour compléter mes expériences. Je veux voir si le principe conservateur de ce venin agit aussi sur mes décoctions. Les rumeurs disent que les araignées peuvent être trouvées à l'est des clairières de Tirisfal."}, {"L'Apothicaire Johaan de la ville de Brill désire que vous lui rameniez 4 échantillons de Venin de Tisse-nuit vicieuse."}},
[370] = {"En Guerre avec la Croisade écarlate", {"Les documents stratégiques de l'Exécuteur Arren contiennent tous les détails dont nous avons besoin pour balayer l'infection humaine de ces terres. Selon nos informations, la Croisade écarlate a assigné le capitaine Perrine et une brigade à la tour en ruine au sud-ouest de Brill.","Tuez Perrine ainsi que 3 Zélotes et 3 Missionnaires et revenez me rendre compte."}, {"Tuer le capitaine Perrine ainsi que 3 Zélotes écarlates et 3 Missionnaires écarlates."}},
[371] = {"En Guerre avec la Croisade écarlate", {"Nous sommes toujours en guerre et la Croisade écarlate croît en puissance. Le rapport que l'Exécuteur Arren m'a envoyé indique que la Croisade lance ses raids depuis la tour en ruines au sud-est de Tirisfal, près de la ferme des Balnir. Elle est sous le commandement du capitaine Vachon.","Tuez Vachon et 5 Frères de la Croisade. Cela sera un coup dévastateur pour ces Croisés !"}, {"L'Exécuteur Zygand de Brill vous a chargé de liquider le capitaine Vachon et 5 Frères écarlates."}},
[372] = {"En Guerre avec la Croisade écarlate", {"La Croisade écarlate lance des raids depuis la tour en ruine au nord-est de Tirisfal, au-delà du Repos de Faol. Selon nos informations, elle est menée par le capitaine Melrache qui a la réputation d'être un officier impitoyable.","Je vous charge cette mission, dangereuse et spéciale. Abattez Melrache et ses deux gardes du corps au nom de la Dame noire."}, {"Assassiner le capitaine Melrach et ses deux gardes du corps pour l'Exécuteur Zygand de la ville de Brill."}},
[373] = {"La lettre non envoyée", {"En fouillant le corps d'Edwin VanCleef, vous trouvez parmi d'autres objets une lettre non envoyée. Elle est adressée à Baros Alexston, architecte de la cité de Stormwind, Hôtel de ville, place de la cathédrale.","Elle a l'air récemment écrite et scellée."}, {"Livrer la lettre pour l'Architecte de la Cité à Baros Alexston à Stormwind."}},
[374] = {"Preuves de la mort", {"L'Exécuteur Zygand m'a dit que vous exterminiez des membres de la Croisade écarlate. Les Clairières de Tirisfal en sont remplies.","Varimathras, lieutenant de la Dame noire, a pris personnellement en charge la mission d'exterminer toute la vermine humaine d'Azeroth. Ici, dans les Clairières de Tirisfal, nous faisons face à un genre particulièrement ennuyeux d'humains, la Croisade écarlate.","Allez de l'avant et lancez une offensive sur la Croisade, <classe>. Ramenez moi 10 Anneaux de la Croisade écarlate comme preuve de votre loyauté à Varimathras et à la Dame noire."}, {"Apporter 10 Anneaux de la Croisade écarlate au Garde noir Burgess à Brill."}},
[375] = {"Le Frisson de la mort", {"Il fait si froid maintenant. Le Fléau mort-vivant brûle dans mes veines comme un serpent de glace. Mon âme sera bientôt perdue. Mais aucun destin, aussi irrévocable soit-il, ne m'empêchera de servir la Dame noire. Lorsque l'appel est arrivé, j'ai cousu des sacs pour les morts de la puissante armée de Sylvanas.","Maintenant, mes mains tremblent à cause du froid. Si vous me ramenez 5 Peaux de chauve-souris du crépuscule et une Bobine de fil grossier, je pourrai me coudre une couverture. Aidez-moi, <nom>, que je puisse continuer à servir la cause."}, {"Rapporter 5 Peaux de chauve-souris du crépuscule et une Bobine de fil grossier à Gretchen Dedmar à Brill."}},
[376] = {"Les Maudits", {"Je soigne nos guerriers blessés, j'adapte leurs armures et vêtements, et j'assiste le prêtre des ombres Sarvis.","Apparemment, vous allez entrer à son service, vous aussi, pour chasser les Âmes Perdues. Si vous voulez rester en un morceau, ce dont je ne doute pas, je peux peut-être vous aider. Je manque de pattes et d’ailes ; si vous m’en apportez, je vous fournirai une armure. Vous trouverez de nombreux loups et chauves-souris au sud."}, {"La novice Elreth a besoin de 6 Pattes de charognard et de 6 Ailes de chauve-souris du crépuscule."}},
[377] = {"Crime et Châtiments", {"Non contente de négliger les habitants du bois de la Pénombre, la Maison des Nobles a décidé d'humilier le Conseil de Darkshire en décidant d'emprisonner Dextren Ward à Stormwind plutôt que de le décapiter ainsi que le Seigneur Ebonlocke l'avait décidé.","Ward a été pris en train de vendre des corps du cimetière à Morbent Fel. Ce crime est passible de la peine capitale. Mais Stormwind exige que Ward soit leur prisonnier.","Assassinez Ward dans la Prison de Stormwind et ramenez-moi sa main, je vous récompenserai pour cela."}, {"Le conseiller Millstipe de Darkshire désire que vous lui rameniez la main de Dextren Ward."}},
[378] = {"Fureur dans les Profondeurs", {"Vous avez prouvé votre loyauté au roi Magni, <classe>. Et votre haine de la vermine Sombrefer est aussi forte que la mienne.","Voici une tâche que je pourrais effectuer mais je suis obligé par une dette d'honneur de rester à Longbraid. Roggo a rassemblé un faisceau d'informations qui prouvent que Kam Deepfury était derrière l'attaque du viaduc de Thandol. Ce sont les plans de Deepfury qui ont amené la ruine de Longbraid.","Deepfury est détenu pour des raisons politiques dans la Prison de Stormwind. Je veux qu'il meure, <nom>. Pour Longbraid ! Ramenez-moi sa tête !"}, {"Rapportez la tête de Kam Deepfury à Motley Garmason à Dun Modr."}},
[379] = {"Etancher cette soif", {"Vous voulez une source d'énergie. Je peux vous aider. Savez-vous ce que je veux moi ? Je veux quelque chose à boire ! Rapportez-moi cinq outres de Bat-le-désert, et je vous donnerais le 4711-FTZ.","Maintenant... Devinez où vous pouvez trouver ces outres ? Pourquoi ne pas aller voir un de ces nomades Bat-le-désert, à l'est d'ici, pour lui en demander une ? Faites-moi savoir si vous y parvenez."}, {"Rapporter 5 Outres de Bat-le-désert à l'ingénieur en chef Bilgewhizzle à Gadgetzan."}},
[380] = {"Les grottes des Tisse-nuit", {"Un de nos plus gros problèmes est d'obtenir les ressources nécessaires à notre survie. L'or était déjà rare lorsque l'Alliance était au sommet de sa puissance.","Il y a une mine d'or au nord-ouest qui a été envahie par les araignées. Nous avons besoin de cet or, mais nous ne pouvons l'exploiter à cause de ces créatures. Je n'ai pas beaucoup de personnels à affecter à cette tâche, alors je vais devoir le faire par étapes.","Allez-y et voyez ce que vous pouvez faire, <nom>."}, {"L’Exécuteur Arren veut que vous tuiez 10 Jeunes Tisse-nuit et 8 Araignées Tisse-nuit."}},
[381] = {"La Croisade écarlate", {"Il vous plaira d’entendre que nous progressons dans la mine, et cela grâce à vous. Nous pouvons maintenant nous atteler à d’autres tâches.","Mes éclaireurs m’ont dit qu’un détachement de la Croisade écarlate avait établi un campement au sud-est. Cette Croisade écarlate est une organisation abjecte qui nous chasse et n’aura de cesse d'exterminer jusqu'au dernier mort-vivant, qu'il soit du Fléau ou non. Nous devons frapper les premiers !","Attention ce sont des adversaires zélés et dangereux."}, {"Apporter à l’Exécuteur Arren 12 Bracelets écarlates, que vous trouverez sur des Convertis écarlates et des Initiés écarlates."}},
[382] = {"Le messager rouge", {"Les rapports de mes supérieurs à Brill indiquent qu'un agent de la Croisade a été détaché du Monastère écarlate et envoyé ici, au Glas. Toujours selon les rapports, ce messager possède d'importantes informations sur lui. Je n'ai pas plus de détails, mais qui que soit ce messager, nous devons l'avoir !","Vous êtes <le meilleur/la meilleure> que je peux trouver, <nom>. Trouvez ce messager et ramenez-moi toutes les informations que vous trouverez sur lui."}, {"Tuer Meven Korgal, le messager, au camp de la Croisade, puis ramener toutes les informations trouvées à l'Exécuteur Arren."}},
[383] = {"Informations décisives", {"Ceci doit être rapporté à mon supérieur, l'Exécuteur Zygand à Brill. Avec ces informations, nous serons à même de porter un coup décisif à la Croisade écarlate. Vos services ont été utiles ici au Glas, mais j'ai besoin de quelqu'un pour porter ceci à Zygand et je pense qu'il sera à même de vous trouver une mission plus en relation avec vos capacités.","Prenez la route au nord du Glas. Une fois dans Tirisfal, vous arriverez à un croisement. Prenez la route vers l'est et continuez. Vous passerez devant le manoir du Foyer froid sur votre route."}, {"Livrer les documents de la Croisade écarlate à l'Exécuteur Zygand à Brill."}},
[384] = {"Côtes de sanglier à la bière", {"Il n'y a rien que mes clients apprécient plus qu'une bonne côte de sanglier à la bière ! Le seul problème est que le trappeur qui nous ravitaille s'est engagé dans l'armée du roi pour aller à la guerre sur le front de l'Alliance.","Peut-être pourriez-vous m'aider ? Si vous pouvez me procurer six côtes de sangliers des rochers et une bière au malt Rhapsody de la taverne, je vous donne ma recette familiale des côtes de sanglier à la bière accompagnée bien évidemment d'un échantillon gratuit ! Le secret est dans la bière, mais je ne vous en dis pas plus !"}, {"Ragnar Thunderbrew à Kharanos veut 6 Côtes de sangliers des rochers et une Chope de bière au malt Rhapsody."}},
[385] = {"Chasse aux crocilisques", {"Beaucoup de chasseurs viennent au Loch Modan pour chasser nos fameux crocilisques. Il y a toujours des marchands qui payent pour avoir ces peaux servant à la confection de vêtements ou d’armures ; sans parler de ceux qui apprécient leur viande.","Les crocilisques étant féroces et retranchés sur leur île, au milieu du Loch, nous manquons de peaux. Mais ne vous laissez pas effrayer : esquiver les mâchoires de ces bêtes est une expérience enrichissante.","Du moins cette fois..."}, {"Trouver 5 morceaux de Viande de cocilisque et 6 Peaux de crocilisque pour Marek Ironheart au pavillon des pérégrins."}},
[386] = {"Ce qui se passait ailleurs…", {"Targorr le Terrifiant a servi sous les ordres de Gath'llzogg en tant qu'Exécuteur suprême. Sa méthode était impitoyable, même selon les standards orcs. Les hommes qui combattaient vaillamment étaient torturés selon ses désirs. Maintenant il est enfermé dans la Prison de Stormwind, condamné à mort. Mais quelque chose ne va pas. Un de ces nobles passionnés de procédures bureaucratiques a suspendu l'exécution. Je suis sûr qu'il y a des magouilles là-dessous.","Mettez fin à la carrière de Targorr le Terrifiant, <nom>. Allez jusqu'à la Prison et décapitez-le avant que toutes ces arguties finissent par le libérer."}, {"Ramener la tête de Targorr le Terrifiant au Garde Berton à Lakeshire."}},
[387] = {"Écraser la rébellion", {"La révolte doit être étouffée ! J'ai besoin de votre aide. Si l'on apprend dehors que cette prison est en rébellion, je perds mon poste ! Mais le plus important en ce moment est de forcer les prisonniers à se soumettre, pour la sécurité de Stormwind.","La punition en cas d'émeute est la mort. Tuez quelques-uns de ces déviants ! Cela devrait ramener les choses sous contrôle."}, {"Tuer 10 Prisonniers défias, 8 Détenus défias et 8 Insurgés défias dans la Prison pour le Gardien Thelwater de Stormwind."}},
[388] = {"La couleur du Sang", {"L'amertume coule dans mes veines comme un poison glacé. Mon petit-fils, Mac, était un si bon garçon. À peine 14 ans. Lorsque l'armée est partie en direction du front, Max a pris les armes pour défendre la ville. Il était garde à la Prison lorsque les Défias ont été emprisonnés. Mac a réprimandé certains prisonniers qui déchiraient leurs couvertures pour en faire des foulards, symbole de leur gang. Pour cela, ils l'ont poignardé dans le dos.","Je veux qu'il soit vengé. Rapportez-moi 10 de leurs précieux foulards de laine. Ils sont de la couleur du sang."}, {"Rapporter 10 foulards en laine rouge à Nikova Raskol de Stormwind."}},
[389] = {"Bazil Thredd", {"VanCleef et moi étions membres de la Guilde des Maçons. Notre grand projet était de reconstruire Stormwind après la guerre.","Une fois notre tâche finie, nous avons été escroqués. Les nobles ont refusé de payer. Plusieurs Contremaîtres se virent proposer des emplois dans le gouvernement, mais VanCleef refusa par loyauté envers la guilde. Il mena une émeute et quitta la cité, jurant de se venger.","Son lieutenant, Bazil Thredd, doit en savoir plus sur ses plans. Il est détenu dans la prison de Stormwind."}, {"Parler au gardien Thelwater à la prison."}},
[391] = {"Les Emeutes de la Prison", {"Qu'une chose soit bien claire, <nom> : je n'ai pas confiance en vous. Mais la situation étant ce qu'elle est, ça ne change pas grand-chose.","Alors voilà comment on va faire : descendez là-bas et parlez à Thredd. Et si vous êtes vraiment de notre côté, tuez-le et ramenez-moi sa tête. Et s'il se trouve que vous êtes de son côté, et que vous ne revenez pas... Je vous retrouve et vous périrez comme un rat. Compris ?"}, {"Tuer Bazil Thredd et ramener sa tête au Gardien Thelwater à la Prison."}},
[392] = {"Un curieux visiteur", {"Pendant les premières années de son emprisonnement, Thredd n'a reçu aucun visiteur. Je pensais qu'il n'était plus utile à la Confrérie Défias, alors ils l'ont abandonné.","Quoi qu'il en soit, il y a quelques mois, tout a changé ; il a commencé à recevoir des visites régulières... Une à deux fois par semaine. C'était un type étrange, calme. J'avais des soupçons, mais tous ses papiers étaient en règle.","Le nom sur les papiers était Maelik, voici sa description. Je ne pense pas qu'il va revenir voir Thredd."}, {"Apporter la description du visiteur de Thredd à Baros Alexston à Stormwind."}},
[393] = {"Les Ombres du Passé", {"Je n'en ai aucune idée, <nom>. Il y a quelqu'un d'autre qui pourrait vous aider, et il se trouve qu'il me doit une faveur... bon, pas la peine de parler de ça. Vous trouverez maître Mathias Shaw, du SI:7, dans la caserne de la vieille ville. Shaw est impliqué dans la plupart des opérations louches de la cité.","Si quelqu'un a des renseignements, ce sera lui."}, {"Apporter la Description scellée du visiteur de Thredd au Maître Mathias Shaw à Stormwind."}},
[394] = {"La tête de la bête", {"Retrouvez Shaw dans la vieille ville. Faites-lui savoir que la menace Lescovar a été résolue grâce à vous. Je suis sûr qu'il sera fier d'avoir un nouveau héros parmi les citoyens de Stormwind.","Je pense qu'il vous enverra rencontrer le Prince ou un autre noble. Ne vous laissez pas faire, <nom>. Les nobles peuvent penser qu'ils règnent sur la ville autant qu'ils le veulent, il n'en reste pas moins que certaines choses échappent à leur contrôle."}, {"Parler à Mathias Shaw, à Stormwind."}},
[395] = {"La fin de la Confrérie", {"Ah, j'avais presque oublié. Alexston est passé pendant que vous n'étiez pas là, il désirait vous parler. Il est clair qu'il désirait rendre compte de l'activité de la Confrérie défias au roi.","Un petit conseil, <nom>. Si quelqu'un le demandait, je donnerais tous les détails, mais sans préciser que c'est votre main qui a tué Lescovar. Croyez-moi. Je suis... habitué... à ce genre de pratiques.","En ce qui concerne Trias, he bien, ne parlez de lui à personne... jamais... Si vous le pouvez."}, {"Parler à Baros Alexston à Stormwind."}},
[396] = {"Une audience avec le roi", {"J'ai préparé un rapport pour le roi. Je crois que vous devriez le lui remettre, en même temps qu'une explication de vos découvertes. N'ayez crainte, il vous récompensera sûrement pour vos actions.","Votre audience est prévue pour bientôt, vous devriez vous hâter, il y a du chemin jusqu'au donjon de Stormwind."}, {"Apporter le rapport de Baros Alexston sur la Confrérie Défias au Roi Varian Wrynn au Donjon de Stormwind."}},
[397] = {"Vous nous avez bien servi", {"J'ai entendu dire que le seigneur Wishock avait apprécié ce breuvage.","Vous nous avez bien servi, <nom>. Les démonistes de Stormwind seront capables de s'exercer sans être dérangés et ceci grâce à vous. Pour vos efforts, nous vous avons préparé un cadeau. Allez voir mon serviteur, Zggi et demandez-lui votre récompense."}, {"Aller demander votre récompense en parlant à Zggi, le serviteur de Zardeth de Black Claw."}},
[398] = {"On recherche : Œil-de-ver", {"On recherche : Œil-de-ver !","Bête affreuse, même d'après les critères des gnolls, Œil-de-ver a été condamné à mort pour ses crimes envers les Réprouvés. Ce criminel infâme volait les corps pour que ceux-ci soient utilisés par l'armée du roi-liche. La dernière apparition d'Œil-de-ver a eu lieu près de l'antre de Garren, au nord de Brill, lorsqu'il pillait les terres.","La ville de Brill offre une récompense à la personne capable d'exécuter la sentence. Les griffes de ce brigand doivent être rapportées à l'Exécuteur Zygand.","La pitié est pour les faibles."}, {"Tuer Œil-de-ver et rapporter ses griffes à l'Exécuteur Zygand pour toucher la récompense."}},
[399] = {"Débuts modestes", {"J'ai l'impression qu'une éternité s'est écoulée depuis que j'étais un jeune garçon travaillant à la ferme, dans la marche de l'Ouest. On dit que l'on ne peut jamais revenir en arrière et c'est vrai. C'est d'autant plus vrai dans mon cas que la maison de ma famille a été brûlée et détruite par des voleurs.","J'ai parlé avec mon père de ce qu'il était advenu de quelques-uns de mes biens, notamment de ma première boussole. Il n'a pas pu les sauver. Il m'a dit qu'ils devaient être cachés dans la ferme.","La ferme des Alexston se trouve à l'ouest de la Colline des sentinelles. Peut-être pourriez-vous y aller et rapporter mes biens ?"}, {"Aller à la maison de Baros Alexston dans la marche de l'Ouest pour chercher sa boussole et lui rapporter à la place de la Cathédrale de Stormwind."}},
[400] = {"Des outils pour Steelgrill", {"Beldin Steelgrill tient un magasin de mécanique. C’est notre meilleur technicien d'engins de siège ! Mais il n'est pas soigneux avec ses outils. Je vous jure qu'il casse ses clés plates aussi vite que nous les lui fournissons !","Nous venons juste de préparer sa dernière commande d'outils. Si vous la lui livrez, je suis sûr qu’il vous récompensera.","Son magasin, le dépôt de Steelgrill, est juste au nord-est de Kharanos. Et c'est le repaire de nombreux pilotes d'engins de siège vétérans. Quand vous y serez, gardez les oreilles ouvertes, il y aura peut-être des occasions à saisir."}, {"Rapporter les outils de SteelGrill à Beldin Steelgrill."}},
[401] = {"Attendre la fin des travaux de Sirra", {"J'ai presque fini de traduire cette note. Si vous revenez dans un moment, j'aurai fini."}, {"Attendre un moment puis parler de nouveau avec Sirra Von'Indi."}},
[403] = {"La garde du tonneau de Thunderbrew", nil, nil},
[404] = {"Une tâche putride", {"Le Fléau s'est infiltré dans les clairières de Tirisfal et a infesté l'ouest de Brill, près du vieux pont.","Allez là-bas et faites reculer les morts pourrissants et les cadavres décomposés que vous trouverez. Eparpillez leurs ossements et rapportez leurs griffes putrides et les gardes noirs vous récompenseront."}, {"Rapporter 7 Griffes putrides au Garde noir Dillinger à Brill."}},
[405] = {"La Liche prodigue", {"Bethor Iceshard, un puissant mage d'Undercity, dans le sud, m'a demandé de lui envoyer un agent efficace contre le Fléau. Vous, <nom>, serez cet agent.","Présentez ces ordres à Bethor. Il vous instruira du détail de votre mission. Je ne connais pas les détails de celle-ci mais je crois qu'il s'agit de recruter une liche. Et cela sera dangereux.","Alors soyez prêt, <classe>. Vous ne devez pas échouer.","Vous trouverez Bethor dans le quartier des mages d'Undercity."}, {"Présenter les ordres de Sevren à Bethor Iceshard à Undercity."}},
[407] = {"Les Champs du Chagrin", {"D'inoffensives citrouilles, n'est-ce pas ? A ce qu'il semble. Si nous voulons stopper l'avance d'Arthas du nord et l'infestation humaine venant du sud, nous devons commencer à comprendre l'étendue de notre potentiel de morts-vivants.","Avec un peu d'ingéniosité une simple citrouille va devenir un agent de la Dame noire. Cette citrouille, traitée avec ma dernière formule, sera une menace sérieuse.","Un autre Zélote écarlate a été capturé et est gardé prisonnier dans le cellier de la Taverne des pendus. Prenez cette citrouille et donnez-la-lui."}, {"Donner la citrouille enchantée au Zélote écarlate capturé qui est gardé dans le cellier de la taverne des Pendus."}},
[408] = {"La Crypte Familiale", {"Les moulins d'Agamand sont devenus un avant-poste du Fléau. Leur point fort est la crypte de la famille Agamand où leur chef local, un squelette nommé le capitaine Dargol a son quartier général. A l'aide de la nécromancie, il a relevé les ancêtres des Agamand et veut les utiliser contre nous.","Cela ne doit pas être et c'est vous qui allez stopper ceci. Allez jusqu'à la crypte de la famille Agamand et battez les ancêtres Agamand.","Et ramenez-moi le crâne du capitaine Dargol."}, {"Tuer 8 Spectres ancestraux et 8 Ancêtres pourrissants.","Tuer le capitaine Dargol et ramener son crâne au magistrat Sevren à Brill."}},
[409] = {"Prouver l'allégeance", {"Vous dites que vous n'êtes pas <lié/liée> à la volonté du roi-liche et l'enchantement que Bethor a mis dans mon livre corrobore ceci. Mais avant que je ne vous croie, je dois avoir des preuves que vous êtes <un ennemi/une ennemie> du Fléau.","Il y a un vieil et sanglant autel sur la côte sud de cette île. Il est utilisé par un puissant agent du Fléau, Lillith Nefara.","Prenez une Bougie de signal dans cette boîte là-bas. Allumez-là sur l'autel et Lillith se réveillera. Détruisez-la et je saurai que vous êtes l'ennemi du Fléau."}, {"Obtenir une Bougie de signal.","Invoquer Lillith Nefara et la tuer.","Retourner voir Gunther sur son île."}},
[410] = {"L'Ombre dormante", nil, nil},
[411] = {"Le Retour de la Liche Prodigue", {"Portez cette Gemme du néant à mon vieux compagnon Bethor. Cela nous permettra de communiquer… et j'ai tant de choses à lui dire."}, {"Rapporter la Gemme du Néant à Bethor Iceshard à Undercity."}},
[412] = {"Opération Recombobulation", {"Après des analyses approfondies de la situation, il semble que non seulement nous avons échoué à éradiquer les troggs mais que nous avons changé la majorité de la race gnome en horribles gnomes lépreux.","Ozzie et moi voulons renverser l'effet de cette abominable lèpre grâce à notre dernière invention : le Recombobulateur. Cette machine est pratiquement complète mais nous avons un grand besoin de Rouages de Rééquilibrage et d'Engrenages gyromécaniques. Demandez-en pour nous aux gnomes lépreux devant Gnomeregan."}, {"Porter 8 Rouages de Rééquilibrage et 8 Engrenages gyromécaniques à Razzle Sprysprocket à Kharanos."}},
[413] = {"La bière Shimmer", {"J'aimerais que mon frère Wellart essaye ma nouvelle bière Shimmer. C'est un montagnard dont la garnison est la tour de garde sud, une des deux tours au bord du Loch Modan.","Il n'est peut être pas un brasseur comme le reste des Barleybrew mais il aime boire une bonne bière et je sais qu'il appréciera celle-ci.","Pouvez-vous lui porter ce Tonneau de Bière Shimmer pour moi ?"}, {"Amener le Tonneau de Bière Shimmer au Montagnard Barleybrew."}},
[414] = {"Bière à Kadrell", {"J'ai encore bu un peu de cette bière Shimmer faite par mon frère, c'est vraiment une sacrée bière ! J'aimerais qu'un ami montagnard la goûte. Son nom est Kadrell et il patrouille en général sur la route qui passe par Thelsamar au Loch Modan.","Pour aller à Thelsamar, allez au sud-ouest à travers le tunnel puis allez au nord-est quand le chemin se sépare. Suivez cette route jusqu'à Thelsamar.","Donnez au montagnard Kadrell ce Tonneau de Shimmer Stout et voyez ce qu'il en pense !"}, {"Portez le Tonneau de Bière Shimmer au montagnard Kadrell."}},
[415] = {"La nouvelle cuvée de Rejold", {"Rejold Barleybrew du village de Brassetout vous demande.","Il veut vous parler d'une nouvelle boisson sur laquelle il travaille… Il parle d'un coup de main que vous pourriez lui donner. Vous pourriez faire demi-tour et voir ce qu'il a à dire.","Il pourrait y avoir quelques mousses gratuites pour vous..."}, {"Parler à Rejold Barleybrew."}},
[416] = {"Chasse au rat", {"Nous avons vraiment un problème de vermine à l'ouest. Les rats des tunnels kobolds sont venus dans les collines, ont creusé leurs terriers et contaminé notre terre avec leur pestilence !","Nous voulons les éliminer. Chassez-les et ramenez-moi leurs oreilles pour la récompense.","Vous trouverez les tunnels des kobolds et leurs terriers à l'ouest de Thelsamar, disséminés à flanc de montagne."}, {"Apporter 12 Oreilles de Rat des tunnels au Montagnard Kadrell à Thelsamar."}},
[417] = {"La revanche du pilote", {"Hildelve écrit dans son journal qu'il a été attaqué par Griffe galeuse, un \"gigantesque ours Griffeglace\". Il a repoussé la bête mais pas avant d'avoir été gravement blessé.","D'après l'état du corps de Hildevel, Griffe galeuse a dû revenir et le tuer.","Néanmoins, Hildevel a écrit dans son journal sa dernière volonté :","Vengez-moi ! Tuez Griffe galeuse !"}, {"Tuer Griffe galeuse.","Rapporter la griffe à laquelle il doit son nom et le Journal de Hildelve au pilote Hammerfoot."}},
[418] = {"Les boudins de Thelsamar", {"On n'a jamais manqué d'estomacs vides à Thelsamar, avec les gamins qui courent partout et les ouvriers qui reviennent de leur journée de travail après un dur labeur. Nos boudins sont réputés, vous les avez déjà essayés ?","Non ? Eh bien, par ici, il faut travailler pour mériter sa pitance et ce n'est pas parce que vous êtes bien <habillé/habillée> que l'on fera une exception pour vous, <classe>.","Je vais avoir besoin de viande d'ours, d'intestins de sanglier pour la cuisson et d'ichor d'araignée pour épicer le tout. Trouvez-moi cela et laissez la cuisine à Vidra !"}, {"Apporter 3 morceaux de Viande d’ours, 3 Tripes de sanglier et 3 Ichors d'araignée à Vidra Hearthstove à Thelsamar."}},
[419] = {"Le pilote perdu", {"Mon ami et camarade Mori Hildelve, qui est Sapeur, a disparu dans les collines. Nous étions en train de chercher un minerai rare qui est nécessaire pour une poudre explosive de grande qualité. Durant nos recherches, il a essayé de monter une pente avec son engin lorsque le moteur a explosé !","Convaincu que le minerai était dans ces montagnes, Hildelve m'a confié la garde des engins tandis qu'il poursuivait la prospection à pied.","Cela fait maintenant des jours de cela et j'ai entendu les grognements de bêtes dans la montagne la nuit. Mori est dur comme l'acier mais je suis inquiet.","S'il vous plaît, <nom>. Trouvez-le."}, {"Trouver le pilote Hildeve."}},
[420] = {"Les observations de Senir", {"Les troggs qui infestent le tunnel sont extrêmement hostiles et n'hésitent pas à attaquer tout intrus. Vous avez l'air suffisamment solide, vous devriez donc vous en tirer.","La route à suivre ? Si vous voulez parler à Senir, vous le trouverez à Kharanos.","Passez par le tunnel et une fois dehors à l'autre extrémité, suivez la route, elle vous conduira à Kharanos."}, {"Apporter le rapport de Grelin à Senir Whitebeard à Kharanos."}},
[421] = {"Prouver votre valeur", {"Dame Sylvanas a chargé Varimathras de la conquête des terres humaines et naines se trouvant vers le sud.","Mais Arugal, ci-devant charlatan de Dalaran et aujourd'hui bête maudite du donjon d'Ombrecroc, a tristement utilisé sa magie. Il a créé le chaos dans la forêt des Pins argentés qui est d'une grande importance stratégique.","J'ai besoin de quelqu'un de doué dans les arts du combat pour rétablir l'ordre. Prouvez-moi votre valeur en tuant 5 Poils-blancs Ragelune. Ces bêtes dégénérées se trouvent généralement près de la route, en bas de la colline."}, {"Tuer 5 Poils-blancs Ragelune pour Dalar Dawnweaver au Sépulcre."}},
[422] = {"La folie d'Arugal", {"Comment Arugal a-t-il pu entrer au Kirin Tor ? C'est au-delà de ma compréhension. Son savoir semblait aussi léger qu'une bulle de savon.","Ce qui importe maintenant, c'est de savoir quelle magie Arugal a utilisée afin que je puisse la retourner contre lui et rendre la forêt des Pins argentés saine pour la Dame noire.","Arugal a longtemps habité dans la ferme juste au nord du pont. Retrouvez-moi le sort appelé le Remède d'Arugal dans ses livres."}, {"Retrouver le Remède d'Arugal pour Dalar Dawnweaver au Sépulcre."}},
[423] = {"La folie d'Arugal", {"Après examen des travaux d'Arugal, mes pires hypothèses se sont confirmées. Le vieux fou n'était même pas bon à vider des pots de chambre à Dalaran. Alors, représenter le Kirin Tor dans ses heures les plus sombres...! Les fous !","Arugal a utilisé des reliques magiques pour renforcer ses faibles pouvoirs. J'ai besoin d'étudier ces objets avant toute chose. Allez au nord et tuez des Gloutons Ragelune et des Ténébreux Ragelune jusqu'à ce que vous ayez récolté suffisamment de leurs bracelets enchantés. J'en ai besoin pour mes recherches. Vous trouverez ces créatures infâmes au nord et à l'est."}, {"Rapportez 6 Bracelets de Gloutons et 6 Bracelets de Ténébreux à Dalar Dawnweaver au Sépulcre."}},
[424] = {"La folie d'Arugal", {"Découvrir les sombres secrets des enchantements d'Arugal va me prendre plus de temps que je ne le pensais.","Pendant ce temps, j'aimerais que vous vous occupiez d'un problème que nos Traqueurs noirs ont découvert. Il semble qu'Arugal ait laissé sa magie s'étendre jusqu'à la mine du gouffre d'Elem dans les collines du sud-est.","Cette mine serait d'une aide précieuse pour Varimathras.","Je veux que vous décapitiez le contremaître de cette mine, Grimson le Pâle. Avec sa mort, la mine sera nôtre."}, {"Tuer Grimson le Pâle et ramener sa tête à Dalar Dawnweaver au Sépulcre."}},
[425] = {"Ivar l'Infect", {"Mon frère Quinn et moi avions pour mission de reconnaître la forêt des Pins argentés et de rendre compte au Grand exécuteur Hadrec. Mais nous ne sommes pas allés loin. En passant par cette ferme, nous avons été attaqués par la goule nommée Ivar l'Infect.","Nous avons réussi à nous échapper mais Quinn a été grièvement blessé par Ivar. Il récupère en ce moment grâce à la potion que vous lui avez donnée mais Ivar pourrait revenir avant qu'il soit guéri.","Tuez Ivar de manière à donner le temps à Quinn de récupérer. Ivar se trouve généralement dans la grange, attendant que je baisse ma garde."}, {"Tuer Ivar l'Infect et ramener sa tête à Rane Yorick au lopin d'Ivar."}},
[426] = {"La chute des moulins", {"Les forces du Fléau essayent de construire une base aux moulins d'Agamand. Si elles réussissent, elles pourront lancer d'autres attaques sur Tirisfal.","Les ordres sont clairs. Les forces du Fléau positionnées là-bas doivent être détruites.","Allez aux moulins. Collectez des côtes ébréchées sur les soldats et les squelettes Cliquethorax, ainsi que des crânes noircis d'Ossomanciens Sombroeil.","Pour rejoindre les moulins, suivez la route ouest. Après avoir traversé le pont, prenez la route nord au premier croisement. Lorsque vous verrez les moulins, la bataille commencera."}, {"Rassemblez 5 Côtes ébréchées et 3 Crânes noircis et rapportez-les au garde noir Dillinger à Brill."}},
[427] = {"En guerre avec la Croisade écarlate", {"Les documents que l'Exécuteur Arren nous a apportés sont juste ce qui nous manquait pour notre bataille contre la Croisade écarlate. Nous savons maintenant où ils se trouvent dans les Clairières de Tirisfal.","Mais la Garde noire a d'autres soucis. L'armée du roi-liche s'accroît chaque nuit. Nous avons besoin de quelqu'un capable \"d'initiatives\" personnelles tel que vous pour s'occuper de la Croisade écarlate.","Prouvez-moi que vous êtes digne de servir la Dame noire ! Allez vers l'ouest jusqu'à la tour après la ferme des Solliden et tuez 10 guerriers écarlates."}, {"Tuer 10 Guerriers écarlates pour l'Exécuteur Zygand."}},
[428] = {"Traqueurs noirs égarés", {"Il y a quelques semaines, nos Traqueurs noirs, Rane et Quinn Yorick, ont été envoyés en mission de reconnaissance au nord des Pins argentés. Nul n'a plus entendu parler d'eux.","Ils ont peut-être été victimes du Fléau, mais si cela est vrai, j'aimerais en avoir la confirmation. Trouvez ces Traqueurs.","Leur premier objectif était d'explorer les fermes au nord de la forêt. Vous devriez commencer par là."}, {"Trouver les Traqueurs noirs Quinn et Rane Yorick."}},
[429] = {"Cœurs sauvages", {"Mon frère Quinn a été gravement blessé par la goule Ivar l'Infect et je doute qu'il puisse guérir sans l'aide de la magie. Bien que je ne sois pas spécialiste de ces choses, je sais que nos Apothicaires savent concocter des remèdes à partir de cœurs décolorés de worgs.","Recueillez quelques-uns de ces cœurs et portez-les à l'Apothicaire Renferrel au Sépulcre, au sud. Revenez ensuite avec la potion.","Vous trouverez des meutes de worgs entre ici et le verger de Malden, plus à l'est."}, {"Recueillir six Cœurs de worg et les apporter à l'Apothicaire Renferrel au Sépulcre."}},
[430] = {"Retour vers Quinn", {"Voici la potion qui guérira Quinn Yorick.","Maintenant, si vous voulez bien m'excuser, j'ai un important travail à terminer..."}, {"Apporter le remède à Quinn Yorick au lopin d'Ivar, au nord du Sépulcre."}},
[431] = {"Bougies de signal", nil, nil},
[432] = {"Ces satanés troggs !", {"Ces satanés troggs sont en train de démolir mon lieu de travail, que la Lumière les brûle ! Regardez-moi ça : un nouveau baril de poudre qui part en fumée !","Oh ! Si seulement je pouvais y aller moi-même et tordre, l'un après l'autre, leurs petits cous décharnés ! J'aimerais les tuer tous !","Tout ce stress, cette colère... Cela n'est pas bon pour ma santé. Et qu'ont-ils laissé d'intact ? Rien !","Je les massacrerais volontiers, mais je ne sais plus viser ! Allez donc dans la carrière et tuez quelques-uns de ces salopiauds ! Je vous paierai, mais faites-leur mal, détruisez-les !"}, {"Tuer 6 Brise-crânes Mâcheroc pour le contremaître Stonebrow à la carrière de Gol'Bolar."}},
[433] = {"Le serviteur public", {"En tant que membre du Sénat et de la Ligue des Explorateurs, j'ai la charge de m'occuper des troggs qui infestent nos terres. Ils ont dévasté la carrière de Gol'Bolar, sans raison apparente. Comme nous forions profondément, ils ont jailli, détruisant notre équipement et chassant les mineurs. Nous n'avons d'autre solution que d'en exterminer un maximum, reconstruire et retourner au travail.","Si vous m'aidez à nous débarrasser de ces troggs, je vous récompenserai en conséquence."}, {"Tuer 10 Briseurs d'os Mâcheroc pour le sénateur Mehr Stonehallow à la carrière de Gol'Bolar."}},
[434] = {"L'attaque !", {"Après avoir passé le déguisement à l'Espion, il entrera pour parler à Lescovar. Les gardes devraient le laisser passer sans problème.","Pendant que vous cherchiez le matériel pour moi, j'ai pris soin d'informer Marzon qu'il était temps de rencontrer à nouveau Lescovar. Je lui ai aussi glissé de fausses informations.","Lescovar congédiera ses gardes et attendra Marzon dans le jardin. Dès que vous entendrez parler des Defias, agissez. Vous devez les tuer tous les deux. C'est compris ?","Quand cela sera fait, allez parler à Trias."}, {"Rester dans le jardin jusqu'à ce que le seigneur Gregor Lescovar et Marzon Lame silencieuse aient eu leur conversation. Après, les tuer tous les deux puis retourner voir Elling Trias à Stormwind."}},
[435] = {"Une escorte pour Erland", {"Les loups ne partiront pas, et leurs yeux me fixent avec avidité dès que je fais un pas dehors. Ils doivent avoir une sacrée fringale pour penser que je suis comestible !","J'ai un rapport à remettre à mon équipier, le Traqueur noir Rane Yorick, mais il faudra d'abord se tailler une route à travers ces loups.","Escortez-moi ! C'est une véritable meute qui attend dehors ; si vous appeliez des amis en renfort, nous doublerions nos chances de réussite..."}, {"Escorter Erland jusqu'à Rane Yorick."}},
[436] = {"Les fouilles d'Ironband", {"Le Prospecteur Ironband dirige l’exploration de ruines à l'est du lac. Malgré notre approvisionnement, sa progression s'est ralentie dernièrement.","Ironband est un solide et honnête nain qui aime à faire du bon travail, c'est pourquoi je crains que des forces contraires soient à l'œuvre pour le ralentir.","Allez aux fouilles d'Ironband et parlez à Magmar Fellhew. C'est lui qui gère le site, et il saura sûrement pourquoi le rythme de travail a décru.","Pour rejoindre les fouilles, contournez la pointe sud du lac, puis marchez droit vers l'est."}, {"Parler à l'Explorateur Fellhew."}},
[437] = {"Le Champ des morts", {"Nos premiers rapports de reconnaissance dans la forêt des Pins argentés indiquent que les Poils-putrides ont une base dans une vieille ferme, le Champ des morts. Nous les avons attaqués à plusieurs reprises, mais à chaque fois ils reçoivent l'assistance de la banshee Cinglenuit dont la magie désorganise et disperse nos forces.","Nous voulons la voir détruite.","Allez dans le Champ des morts et attaquez les Poils-putrides. Si vous ne voyez pas la banshee, massacrez les Poils-putrides jusqu'à ce qu'elle apparaisse, puis tuez-la.","Son essence même deviendra de la poussière. Rapportez-moi cette poussière comme preuve de sa destruction"}, {"Tuer des gnolls Poils-putrides au Champ des morts.","Détruire Cinglenuit quand elle apparaît.","Rapporter son essence au Grand exécuteur Hadrec au Sépulcre."}},
[438] = {"Le bac délabré", {"<nom>, nous avons découvert une nouvelle infestation du Fléau : le bac délabré. Le bac se trouve à l'est du Sépulcre, et fourmille de patrouilles de Poils-putrides et autres morts-vivants.","Le Fléau a sans doute une bonne raison pour occuper la zone. Allez au bac délabré et trouvez pourquoi le Fléau est là-bas."}, {"Aller au bac délabré.","Chercher la raison de la présence du Fléau."}},
[439] = {"Un indice vers les Poils-putrides", {"De la pile de corps dépasse une main de femme, marquée par la peste, mais encore relativement fraîche.","Un doigt porte une bague. L'observation de cette bague révèle une inscription :","\"Pour Deliah\""}, {"Rapporter la Bague de Deliah au Grand exécuteur Hadrec au Sépulcre."}},
[440] = {"La bague ciselée", {"Si vous voulez retrouver le mari ou l'amant qui a donné cette bague à Deliah, parlez au Magistrat Sevren à Brill.","Mais je n'entretiendrais pas d'espoir à votre place : celui qui a offert la bague est certainement mort à ce jour. Et s'il est devenu mort-vivant, il a oublié Deliah depuis longtemps."}, {"Ramener la Bague de Deliah au Magistrat Sevren à Brill."}},
[441] = {"Raleigh et Undercity", {"Deliah avait un mari, Raleigh, qui maintenant habite à Undercity. Si la Deliah qui vivait à Brill est bien celle à qui appartient la bague, alors Raleigh le saura.","Raleigh est dans le quartier des Marchands à Undercity."}, {"Apporter la Bague de Deliah à Raleigh."}},
[442] = {"L'attaque de l'île de Fenris", {"Il est maintenant temps d'attaquer le bastion du Fléau dans les Pins argentés : l'île de Fenris.","Vous devez aller sur l'île et tuer Thule Ravenclaw. Apportez-moi sa tête !","Cette mission est aussi importante qu'elle est vitale, <nom>. Vous ne pouvez échouer."}, {"Rapporter la Tête de Thule au Grand exécuteur Hadrec au Sépulcre."}},
[443] = {"L'ichor de Poil-putride", {"Avant que nous lancions notre offensive contre l'île de Fenris, nous devons déterminer qui dirige les gnolls Poils-putrides. Nos Apothicaires pensent que leur origine est liée au fluide étrange qui coule dans leurs corps.","Allez sur l'île de Fenris, à l'est du Bac délabré, et collectez de lichor de Poil-putride sur les gnolls. Portez-le à l'apothicaire Renferrel, qui se trouve à l'extérieur du Sépulcre"}, {"Rapporter 8 bouteilles d'Ichor de Poil-putride à l'apothicaire Renferrel."}},
[444] = {"L'origine des Poils-putrides", {"J'ai réduit l'ichor que vous m'avez ramené et en ai séparé les principaux composants. Mes découvertes ne laissent aucun doute sur au moins un point : la magie des démonistes et des nécromanciens a été utilisée dans sa création.","Pendant que je pousserai mes recherches sur ce sujet, je veux que vous en apportiez un échantillon à Bethor Iceshard à Undercity. Bethor n'est pas un apothicaire, mais sa connaissance de la magie pourrait nous aider à comprendre le mystère des Poils-putrides.","Partez ! Vous trouverez Bethor dans le Quartier de la magie d'Undercity."}, {"Apporter l'Echantillon d'ichor à Bethor Iceshard à Undercity."}},
[445] = {"Livraison dans la forêt des Pins argentés", {"Le temps n'est pas un luxe que peuvent s'offrir ceux qui travaillent pour Dame Sylvanas, mais vous le savez certainement.","En tant que membre de la Société royale des apothicaires, il est de mon devoir de partager mon savoir avec mes confrères afin que nos efforts conjoints puissent un jour offrir à la Dame noire cette Peste nouvelle qu'elle désire si ardemment.","Portez ces découvertes à l'apothicaire Renferrel qui réside au Sépulcre dans la forêt des Pins argentés."}, {"Apporter les découvertes de l'Apothicaire Johaan à l'Apothicaire Renferrel dans la forêt des Pins argentés."}},
[446] = {"Thule Ravenclaw", {"Cet ichor a été créé par un vieux confrère : Thule Ravenclaw. C'était un jeune homme prometteur avant la Grande Guerre, mais quand le roi-liche vint au pouvoir, il trahit ses camarades en s'alliant au Fléau.","Je n'avais pas réalisé qu'il était encore en vie, ni qu'il menait les Poils-putrides contre nous !","Je connais beaucoup de ses sorts, et je peux aider à concocter une potion pour les contrer. Rapportez ce parchemin à l'apothicaire Renferrel. Il contient les incantations nécessaires à la potion."}, {"Porter le parchemin de Bethor à l'Apothicaire Renferrel au Sépulcre des Pins argentés."}},
[447] = {"Une recette mortelle", {"Les troupes d'Arthas sont en nombre, mais avec une Peste nouvelle nous pourrions éradiquer à la fois l'armée du Fléau et les humains.","Mes études ont prouvé que du sang d'araignée associé à une toxine présente dans le cœur des vieux ours devient un élixir mortel. Collectez ces échantillons sur les araignées du Grouillement, au nord-ouest, et sur les ours qui arpentent la forêt des Pins argentés. Livrez ensuite ces ingrédients au maître apothicaire Faranell de la Société royale des apothicaires, à Undercity."}, {"Se rendre au Grouillement et y collecter 6 Cœurs de vieil ours et 6 échantillons de Sang du Grouillement, puis les apporter au maître apothicaire Faranell de la Société royale des apothicaires, à Undercity."}},
[448] = {"Un rapport pour Hadrec", {"Avant que vous n'attaquiez l'île de Fenris, faites un rapport au Grand exécuteur Hadrec. Il aura sans doute les informations et l'équipement complémentaires dont vous aurez besoin."}, {"Faire un rapport au Grand exécuteur Hadrec au Sépulcre."}},
[449] = {"Le rapport des Traqueurs noirs", {"Bien que les blessures de Quinn soient en train de guérir, je veux que notre rapport parvienne au Grand exécuteur Hadrec le plus vite possible. Il contient des informations vitales pour maintenir l'occupation de la forêt des Pins argentés par les Réprouvés."}, {"Apporter le rapport du Traqueur noir au Grand exécuteur Hadrec au Sépulcre."}},
[450] = {"Une recette mortelle", {"Vous pouvez encore être utile à la Société royale des apothicaires, <nom>.","Quand Arugal a jeté sa misérable magie sur la forêt, il a maudit l'un de nos membres les plus prometteurs, l'apothicaire Berard. Devenu un serviteur décérébré d'Arugal, Berard ne nous sert plus à rien, mais ses découvertes pourraient être utiles. Il étudiait une ancienne peste qui a décimé toute la faune aquatique du lac de Lordamere, il y a longtemps.","Récupérez le Journal de Berard dans son bureau à l'auberge de Bois-du-Bûcher et rapportez-le à l'Apothicaire Renferrel."}, {"Récupérer le Journal de Berard à l’Auberge de Bois-du-Bûcher et le rapporter à l'apothicaire Renferrel au Sépulcre."}},
[451] = {"Une recette mortelle", {"D'après le journal de Berard, la mousse de Furtifs et de Rampants du lac pourrait intervenir dans la composition d'une Peste nouvelle. Ces bêtes vivent sur les îles du lac de Lordamere, à l'est.","Le journal évoque aussi une rarissime Tumeur durcie qu'il a découverte sur un murloc Aileron noir. Apparemment cette tumeur est tellement rare que Berard n'a pu en prélever qu'une.","Recueillez des échantillons de mousse ainsi que de la Tumeur durcie et rapportez-les au Maître Apothicaire Faranell à Undercity."}, {"Apporter 6 Echantillons de Mousse de Rampant du lac, 6 échantillons de Mousse de Furtif du lac et une Tumeur durcie au Maître Apothicaire Faranell à Undercity."}},
[452] = {"Embuscade à Bois-du-Bûcher", {"Vous avez fait vite, <nom> !","Les Conseillers de Bois-du-Bûcher vont revenir dans un instant. Arugal utilise les membres du Conseil comme autant de marionnettes pour garder la main mise sur le village. Mais je pense que sans le Conseil, les partisans d'Arugal se retourneraient contre leur maître.","Je ne peux faire cela toute seule, j'ai besoin de votre aide. Ensemble, nous dévasterons le Conseil au nom de Dame Sylvanas."}, {"Aider le Traqueur noir Faerleia à tuer les Membres du Conseil de Bois-du-Bûcher."}},
[453] = {"À la recherche de l'Obscure Silhouette", {"Bonne chance, <nom>. J'espère que vous retrouverez le bonhomme, et vivant si possible.","S'il a fui la sécurité de Darkshire, il pourrait être la proie des bêtes sauvages."}, {"Retrouver l'Obscure Silhouette. Indices :","Il n'est pas né à Darkshire.","C'est un individu nerveux et craintif.","Il a quitté Darkshire pour faire route vers l'ouest."}},
[454] = {"Après l'embuscade", {"Avant toute cette...histoire, nous étions en train de convoyer les barils de poudre. Mais maintenant nous devons répartir les tâches, Miran prenant les barils et moi veillant sur les autres.","Je n'aime pas l'idée que Miran transporte le reste de la cargaison tout seul. Pourriez-vous l'escorter quand il sera prêt à partir ?"}, {"Parler à Miran."}},
[455] = {"Le défi d'Algaz", {"Les orcs du clan Dragonmaw ont un refuge dans les Paluns. Mais voilà : ils se répandent maintenant dans Dun Algaz ! Nous ne pouvons les laisser s'installer là, sinon le Loch Modan sera coupé du port de Menethil, notre port et bastion dans les Paluns.","Traversez Dun Algaz jusqu'aux Paluns et tuez quelques orcs pour faire bonne mesure! Faites un rapport à Valstag Ironjaw au port de Menethil.","Pour rejoindre Menethil, passez par Dun Algaz, puis allez au nord en longeant la route, et enfin prenez à l'ouest à l'embranchement.","Nous comptons sur vous, <nom>. Vous ne pouvez échouer."}, {"Traverser Dun Algaz.","Tuer 8 Eclaireurs Dragonmaw et 6 Grunts Dragonmaw.","Faites votre rapport à Valstag Ironjaw au port de Menethil."}},
[456] = {"L'Equilibre de la nature", {"Salutations, <nom>. Je suis le Conservateur Ilthalaine. Mon rôle à Sombrevallon est de m'assurer que l'équilibre naturel est maintenu.","Les pluies printanières furent particulièrement abondantes cette année, et certains animaux de la forêt en ont profité pour prospérer quand d'autres ont dépéri.","Malheureusement la population des sabres-de-nuit et des sangliers des chardons s'est trop accrue. Sombrevallon ne peut produire plus d'une quantité donnée de nourriture pour les animaux. En route, jeune <classe>, réduisez la population des sabres-de-nuit et des sangliers des chardons afin de garantir l'harmonie de la nature."}, {"Tuer 7 Jeunes sabres-de-nuit et 4 Jeunes sangliers des chardons, puis retournez voir le Conservateur Ilthalaine."}},
[457] = {"L'Equilibre de la nature", {"Réguler les populations d'animaux à Sombrevallon est un bon début <nom>, mais il y a encore beaucoup à faire.","Les ressources forestières seront bientôt épuisées si nous ne réglons pas rapidement le problème. Tuer les créatures de la nature est parfois un mal nécessaire pour le bien de tous ceux qui partagent cette terre. Aventurez-vous dans la forêt et éliminez les sabres-de-nuit et les sangliers des chardons malades, au nom de l'équilibre naturel."}, {"Le Conservateur Ilthalaine voudrait que vous tuiez 7 Sabres-de-nuit galeux et 7 Sangliers des chardons."}},
[458] = {"La Protectrice sylvestre", {"Louée soit la Nature, vous êtes là, <classe> ! D'étranges rumeurs sont parvenues jusqu'à moi par l'entremise des esprits de la forêt.","La mystérieuse Protectrice sylvestre, Tarindrella, est rentrée à Sombrevallon. Cette dryade n’est pas venue dans les forêts de Kalimdor depuis des années. Il y a sûrement quelque chose qui cloche pour motiver son retour.","Cherchez Tarindrella et voyez ce qui l'occupe en ce moment. Une des Sentinelles dit l'avoir vue au sud-ouest d'Aldrassil."}, {"Chercher la dryade connue sous le nom de Tarindrella."}},
[459] = {"La Protectrice sylvestre", {"Quelque chose de malfaisant se développe dans les forêts de Teldrassil. Allez dans les collines où les paisibles furbolgs avaient habitude de vivre. Ils ont quitté leurs foyers et se regroupent maintenant, sous le nom de tribu des Pins-tordus.","Seule la contamination par les pernicieuses gangremousses a pu provoquer ce changement. Les grells et les grellides ont infesté la zone et menacent les habitants de Sombrevallon.","Attaquez ces grells et ces grellides, <nom>, et voyez s'ils sont tombés sous l’influence des Mousses corrompues."}, {"Collecter 8 Gangremousses et les rapporter à Tarindrella."}},
[460] = {"Reposer en… pièces", {"Vous n'avez pas l'air d'un Poil-putride. C'est déjà ça. J'avais perdu espoir...","Mon nom est Alaric, j'étais naguère le serviteur et garde du corps de Thule. Il a tenté d'utiliser sa magie pour me \"protéger\" du Fléau mort-vivant, mais en fait, son sort m’a donné une forme de mort-vivant. Après avoir créé les maudits Poil-putride, Thule m’a décapité et donné ma tête aux gnolls pour qu'ils jouent avec !","Je vous supplie de me ramener près de mon corps. Je suis sûr que les gnolls l'ont enterré ici, au donjon de Fenris, près des étables, dans une tombe anonyme."}, {"Apporter la tête d'Alaric jusqu’à sa tombe."}},
[461] = {"L’alcôve secrète", {"Je connais une alcôve secrète dans le donjon, qui contient un objet cher à Thule et ses anciens confrères. Il y a également quelque chose qui pourrait vous servir. Amenez-moi sur place !","L’alcôve se trouve dans la tour la plus au sud-ouest du donjon, derrière une étagère poussiéreuse.","Prenez mon corps, mais laissez mes bottes. De toute façon elles ne m'allaient pas..."}, {"Amener Alaric à l’alcôve secrète."}},
[463] = {"Le Gouverneur vert", {"Avez-vous entendu parler du Gouverneur vert ? Vous ne le cherchez pas, n'est-ce pas ? Je dirais que vous êtes <fou/folle> si c'est le cas, mais qui suis-je pour empêcher <un illuminé/une illuminée> de courir à sa mort ?","Si vous cherchez le monstre, je crois qu'il hante les marécages à l'est de la route où il y a l'embranchement pour Dun Modr. Il est tapi là-bas, parmi les crocilisques et de choses pires encore !","Et puis, laissez donc votre argent ici ; il ne vous servira à rien où vous allez. Vous ne voulez pas non plus que le Verdâtre s'ébrèche les dents sur votre or quand il vous coupera en deux ?"}, {"Trouver le Gouverneur vert."}},
[464] = {"Bannières de guerre", {"Le clan des orcs Dragonmaw fut pratiquement anéanti à la fin de la Deuxième Guerre après qu'ils eurent perdu le contrôle de leurs dragons rouges. Les dragons n'étaient pas contents de leurs anciens maîtres et ont sauvagement traqué les Dragonmaw. Les quelques survivants ont fui de Grim Batol dans les montagnes jusqu’à l'est de l'Excavation de Whelgar.","Maintenant ils ont repris des forces et campent au nord-est de Menethil.","Nous voulons que vous attaquiez leur campement. Vous rassemblerez et me rapporterez leurs bannières de guerre comme preuve de leur destruction."}, {"Rapporter 8 Bannières de guerre Dragonmaw au capitaine Stoutfist."}},
[465] = {"La manœuvre de Nek'rosh", {"Pendant que vous combattiez les Dragonmaw, nous avons percé à jour certains de leurs plans.","Nek'rosh a fait construire des catapultes et projette d'assiéger Menethil ! Et avec les combats à Dun Algaz, nous sommes coupés des renforts !","Vous devez détruire ces catapultes. Elles sont au campement des Dragonmaw.","Voici, prenez ce combustible. Taillez-vous une route à coups d'épée jusqu'aux catapultes, puis brûlez-les à l'aide du combustible.","Après, trouvez et tuez Nek'rosh. Rien ne nous plairait plus que d'avoir la tête de cette engeance Dragonmaw au bout d'une pique..."}, {"Détruire les Catapultes Dragonmaw."}},
[466] = {"À la recherche de l'incendicite", {"J'ai appris qu'il existait un minerai rare, l'incendicite, dans les Paluns, dans une grotte au nord-ouest de la Porte d'Algaz. C’est un produit assez volatil, il explose à la moindre mauvaise manipulation ! Mais je suis sûr que correctement extrait, on pourrait l'utiliser pour donner un sacré punch aux obus de canon !","Apportez-moi de l'incendicite et je trouverai bien quelqu'un pour l'expérimenter.","Ah ! Et pour extraire ce minerai vous devez avoir la Compétence de Minage. Si vous ne l'avez pas, alors... amenez un ami qui la connaisse !"}, {"Apporter 6 Chargements d’Incendicite au Pilote Stonegear à Dun Morogh."}},
[467] = {"La quête de Stonegear", {"Le pilote Stonegear adore ses engins de siège, comme il se doit ! Et il est toujours avide d'informations lui permettant d'apporter des améliorations techniques à ses chars !","Il a entendu parler d'un minerai rare et cherche quelqu'un pour aller en collecter pour lui. Si vous aimez marcher, allez trouver Stonegear au dépôt de Steelgrill à Dun Morogh, et voyez ce dont il a besoin."}, {"Parler au Pilote Stonegear."}},
[468] = {"Un rapport pour le Montagnard Rockgar", {"Le Montagnard Rockgar cherche des volontaires pour une mission dans Dun Algaz et les Paluns.","Si vous voulez mouiller vos bottes dans cette terre détrempée au nord, allez parler à Rockgar à Dun Algaz. Dun Algaz est plus au nord sur la route, après la Tour de garde."}, {"Parler au Montagnard Rockgar."}},
[469] = {"Livraison quotidienne", {"Bonjour !","Vous n’iriez pas vers Menethil, par hasard ? Sinon, vous pouvez faire halte ici, c'est un bon endroit pour faire des achats ou prendre une pinte de bière.","Mais si vous allez tout de même à Menethil, vous pourriez peut-être apporter ce lot de peaux de crocilisques à James Halloran, le tanneur ?","Je pourrais le faire moi-même, mais ma femme est d’une méchante humeur en ce moment, et j’aimerais autant éviter de la contrarier."}, {"Apporter le Lot de peaux de crocilisques à James Halloran, le tanneur, au port de Menethil."}},
[470] = {"En creusant dans la vase", {"A l'occasion de ma visite hebdomadaire au Tombeau d'Ironbeard, j'ai été attaquée par de redoutables Limons visqueux ! Naturellement, j'ai paniqué.","J'ai jeté mon sac sur l'un d'eux, qui l'a littéralement aspiré, ce qui m'a laissé le temps de m'enfuir.","La mauvaise nouvelle, c’est que j'ai absolument besoin du contenu de mon sac !","Et je ne sais pas sur quel Limon je l'ai jeté : ils se ressemblent tous ! Le Tombeau d'Ironbeard est au nord-est, près de Dun Modr. Merci mille fois !"}, {"L'un des Limons dans le Tombeau d'Ironbeard a le sac de Sidonie. Le retrouver et le lui rapporter au Port de Menethil."}},
[471] = {"Les devoirs de l'apprenti", {"J'ai envoyé mon apprenti dans les marécages pour chasser les fameux crocilisques géants des Paluns. Comme il ne revenait pas, je suis parti à sa recherche et l'ai retrouvé. Tout ce que je peux dire c'est que ce n'était pas... plaisant à regarder.","La rebouteuse dit qu'il va s'en sortir, mais entre-temps, j'ai toujours besoin de mes peaux !","... Si vous tuiez quelques-uns de ces monstres, je n'y ferais aucune objection ; après ce qu'ils ont fait... Vous trouverez les crocos au nord de la côte, derrière les taudis des murlocs."}, {"Collecter 6 Peaux de crocilisque géant, et les apporter à James Halloran, au port de Menethil."}},
[472] = {"La chute de Dun Modr", {"Dun Modr est tombé aux mains des nains Sombrefer !","Mes blessures sont sérieuses, <classe>. Mon régiment a été pratiquement décimé ! Ces brutes de Sombrefer nous ont attaqués avant que nous puissions nous regrouper après l'embuscade du viaduc de Thandol.","Notre chef, Longbraid, a sonné la retraite. Comme nous quittions la ville, j’ai reçu une hache dans le dos. Un voile noir s'est abattu devant mes yeux.","Je me suis réveillé ici, en sécurité à Menethil, mais j'ai peur pour mes compagnons. L'espoir ne m'a pas encore abandonné. Peut-être Longbraid est-il encore vivant ! <nom>, voyez si vous pouvez le retrouver, pas loin du village de Dun Modr !"}, {"Rechercher Longbraid dans les parages du village de Dun Modr."}},
[473] = {"Un rapport au capitaine Stoutfist", {"<nom>. Le clan des orcs Dragonmaw montre sa puissance bien au-delà d'Algaz. Le capitaine Stoutfist a demandé à parler à tous ceux qui sont prêts à s'engager contre ces orcs. Il est à l'intérieur du donjon, dans la salle de guerre."}, {"Parler au capitaine Stoutfist."}},
[474] = {"Vaincre Nek'rosh", {"Maintenant que les catapultes ont été détruites, trouvez et exécutez le Chef Nek'rosh."}, {"Tuer le Chef Nek'rosh","Rapporter la tête de Nek'rosh au capitaine Stoutfist."}},
[475] = {"Une brise étrange", {"Une brise étrange souffle à travers la forêt.","Gaerolas Talvethren est le Grand gardien des druides de la Serre qui hibernent dans le Refuge des saisons de Ban'ethil. Son devoir en tant que protecteur des Dormants est de veiller à leur sécurité, afin que le pacte avec Ysera ne soit pas rompu.","Mais je n'ai toujours pas de nouvelles de Gaerolas. Allez à l'est, au village de Brise-stellaire et revenez avec des nouvelles de Gaerolas, que je puisse calmer mes craintes en apprenant que mon vieux camarade est sain et sauf."}, {"Chercher Gaerolas Talvethren au village de Brise-stellaire."}},
[476] = {"La corruption des Pins-tordus", {"La tribu des Pins-tordus a été corrompue !","Les furbolgs, jadis paisibles, se sont retournés contre les protecteurs de la forêt. Ils m'ont tendu une embuscade tandis que je partais pour le Refuge des saisons de Ban'ethil, puis ils ont pillé le village de Brise-stellaire. Ursal le Mutileur, leur chef, utilise les pouvoirs malfaisants des gangremousses pour les rendre fous.","Je suis trop gravement blessé pour aller voir Athridas et lui communiquer ces graves nouvelles. Cette tâche vous incombe à présent, jeune <classe>. Nous n'avons plus qu'à espérer que les Pins-tordus ne sont pas encore arrivés jusqu'à Ban'ethil !"}, {"Retourner auprès d'Athridas Bearmantle à Dolanaar."}},
[477] = {"Frontières", {"Comme vous devez le savoir, Moulin-de-l'Ambre reste un bastion de l'opposition humaine. La ville est soutenue par les Sorciers de Dalaran qui l’approvisionnent.","Je ne connais pas leurs plans, mais s'ils portent autant d'intérêt à ce trou perdu, c'est que cela doit être de première importance.","Ils transportent quantité de caisses depuis Hillsbrad. Beaucoup de ces convois ont pour terminus un petit camp au nord du village de Bois-du-Bûcher. Récupérez le contenu de l'une de ces caisses et apportez-le moi."}, {"Récupérer le contenu de l’une des caisses des Sorciers de Dalaran. Vous trouverez Bois-du-Bûcher au sud."}},
[478] = {"Des cartes et des runes", {"<Vous retirez rapidement le Pendentif de la caisse et rabattez le couvercle.>"}, {"Apporter l'étrange pendentif au prêtre des ombres Allister au Sépulcre."}},
[479] = {"Enquête à Moulin-de-l'Ambre", {"Dalar essaie de localiser la source de la magie des sorciers. Pour l'heure, nous devons nous efforcer de ralentir leurs progrès...","Il ne fait aucun doute que les conjurateurs, les mages et les protecteurs portent ces pendentifs. Récupérez-en.","Suivez la route principale au sud, puis prenez à l'est à l'embranchement qui conduit à Moulin-de-l'Ambre."}, {"Récupérer 8 Pendentifs de Dalaran pour le prêtre des ombres Allister du Sépulcre."}},
[480] = {"Le Tisserand", {"Un archimage de grand pouvoir supervise la réactivation du nœud d'énergie Ley. Vu les progrès qu'ils ont faits, notre seule chance de les arrêter est de tuer cet archimage. Ce sera difficile, car il est certainement protégé par d'autres mages et gardiens.","Vous devez partir et le neutraliser au plus vite. Quand il sera mort, récupérez son Bâton et apportez-le-moi. En avant !"}, {"Tuer l'archimage de Dalaran, puis récupérer son Bâton pour le prêtre des ombres Allister du Sépulcre."}},
[481] = {"L'analyse de Dalar", {"Apporter le Pendentif à Dalar Dawnweaver. Peut-être pourra-t-il anticiper les plans des Sorciers."}, {"Apporter le Pendentif marqué de runes à Dalar Dawnweaver au Sépulcre."}},
[482] = {"Les intentions de Dalaran", {"Bien sûr ! Comment ai-je pu oublier ?","Moulin-de-l'Ambre était l'un des sites de prédilection pour le Kirin Tor, célèbre pour abriter des nœuds dormants d'énergie Ley. Les Sorciers doivent projeter de réactiver les nœuds et d'utiliser cette énergie pour quelque grande œuvre.","Nous ne pouvons laisser faire...","Ils vont avoir besoin de beaucoup de puissance pour activer les zones d'énergie Ley et nous pouvons enrayer le processus en volant leurs Pendentifs aux Sorciers.","Rapportez cette information à Allister, il décidera de la suite."}, {"Parler au prêtre des ombres Allister au Sépulcre."}},
[483] = {"Les Reliques d'éveil", {"Les Pins-tordus ont été vus ravageant le Refuge des saisons de Ban'ethil, à l'ouest.","Les druides dormants vont être pris dans le Rêve d'émeraude pour l'éternité, à moins que nous n'intervenions. Leur très délicate hibernation ne peut pas être interrompue sans les quatre Reliques d'éveil.","Rendez-vous au Refuge et récupérez le Talisman Ravenclaw, la Plume noire, le Saphir des cieux et la Rune de nidification. Les druides les gardent dans des coffres sacrés. Apportez-les-moi afin que je prépare le rituel d'éveil."}, {"Récupérer les Reliques d'éveil et les rapporter à Athridas Bearmantle à Dolanaar."}},
[484] = {"Les peaux de jeune crocilisque", {"Je suis James Halloran, le tanneur. Mes peaux de crocilisque traitées sont prisées dans le monde entier.","Vous connaissez Einar ? C’est l’un des chasseurs qui m'approvisionne en peaux.","Je suis en ce moment à court de ces peaux soyeuses qui proviennent des jeunes crocilisques.","Je vous paierais grassement si vous pouviez me rapporter quelques-unes de ces peaux. Vous trouverez les jeunes crocilisques dans les marécages, à l’extérieur de la ville."}, {"Collecter 4 Peaux de jeune crocilisque, pour James Halloran, au port de Menethil."}},
[485] = {"Trouver OOX-09/HL !", {"Vous avez découvert un étrange objet en forme d'oeuf fait de métal. En jouant avec les petit boutons présents sur le dessus, l'oeuf s'anime, à mesure qu'il s'ouvre, il se transforme en une espèce de bidule robot gnomique ! Une voix sort du robot en grésillant.","\"Je suis Oglethorpe Obnoticus, et mon robot guidé a disparu ! Je vous récompenserais si vous le ramenez ! Amenez cette balise au robot.\"","\"J'ai programmé les coordonnées du robot pour vous. Il s'est écrasé en haut de la rivière près du rocher de l'Affût !\""}, {"Apporter la balise de détresse jusqu'au robot guidé d'Oglethorpe en haut de la rivière dans les Hinterlands, près du rocher de l'Affût."}},
[486] = {"Ursal le Mutileur", {"Le temps est venu de sauver les druides de la Serre. Si nous échouons maintenant, <nom>, ils resteront à jamais captifs de leur sommeil.","Je pourrais préparer les Reliques d'éveil et pratiquer le rituel, mais mon travail ne peut prendre effet tant que le responsable de cette horrible situation est en vie. Le rituel ne sera achevé que lorsqu'il aura été terrassé.","C'est Ursal le Mutileur qui a perturbé notre cycle, c'est Ursal le Mutileur qui doit payer pour qu'ils puissent être libérés. Voyagez jusqu'au camp des Pins-tordus, au sud-ouest, et exécutez-le."}, {"Tuer Ursal le Mutileur et retourner voir Athridas Bearmantle à Dolanaar."}},
[487] = {"La route de Darnassus", {"La route de Darnassus doit être sécurisée. Des voyageurs qui cheminaient entre Dolanaar et Darnassus y ont été témoins d'attaques de furbolgs de la tribu Pins-tordus.","D'importantes informations et des convois commerciaux en provenance de Darnassus empruntent cette voie quotidiennement. Nous ne pouvons tolérer qu'une bande de brigands y terrorise les gens.","Au nom de la Forêt sacrée, prenez les armes, <classe>. Leur repaire est quelque part au-delà de ce point. Tuez 6 de ces Embusqués Pins-tordus et revenez faire votre rapport."}, {"Tuer 6 Embusqués Pins-tordus et retourner voir la Sentinelle Amara Nightwalker à l'extérieur de Dolanaar."}},
[488] = {"Les commandements de Zenn", {"Volontaire pour des missions, à ce que je vois. Heureusement, il n'y a pas un jour sans que je n'aie besoin d'un courageux <classe> prêt et armé pour répondre à mes demandes.","Voyez-vous, <nom>, je puis vous rendre riche et vous offrir ce dont vous n'osez rêver. Mais en échange vous devez me rapporter certains objets.","Mon travail dans la forêt requiert souvent certains... réactifs. Rapportez-moi 3 Crocs de sabre-de-nuit, 3 Plumes de hibou Strigid et 3 échantillons de Soie d'araignée Tissebois.","Et que tout cela reste entre nous, <nom>."}, {"Rapporter 3 Crocs de sabre-de-nuit, 3 Plumes de Hibou Strigid et 3 Echantillons de Soie d'araignée Tissebois à Zenn Foulhoof à l'extérieur de Dolanaar."}},
[489] = {"En quête de rédemption !", {"Le Conseil de la forêt a eu vent que vous aviez aidé Zenn Foulhoof. Ce satyre est un ennemi de la forêt. Un <race> devrait avoir mieux à faire que de la fragiliser en tuant ses créatures.","Vous devez vous racheter aux yeux du Conseil si vous désirez rester <un ami/une amie> de Teldrassil.","Donnez une bonne leçon à Foulhoof et vous serez <racheté/rachetée>. Les gangrenoix sont des graines malades qui tombent des arbres. De la fumée verte en sort.","Apportez-en à Foulhoof. Il croira qu’il s’agit d’un inoffensif casse-croûte."}, {"Collecter 3 Gangrenoix et les offrir à Zenn Foulhoof à l'extérieur de Dolanaar."}},
[491] = {"Une Baguette pour Bethor", {"Thule m'a raconté qu'il y a longtemps, lui et deux de ses confrères créèrent une Baguette magique, la fusion des trois Baguettes que les mages possédaient alors. Cette Baguette devint le symbole de leur amitié, et bien que Thule se mit peu à peu à la mépriser il ne put se résoudre à s'en séparer ou à la laisser aux deux autres. Alors il vola la Baguette et la cacha dans le donjon de Fenris.","Voilà. Apportez la Baguette à Bethor Iceshard, l'un des anciens amis de Thule. Je suis sûr qu’il appréciera."}, {"Apporter le Bâton tressé à Bethor Iceshard dans le Quartier de la magie à Undercity."}},
[492] = {"Une Peste nouvelle", {"Selon le Garde noir, un autre de ces Montagnards nains insensés vient juste d'être capturé. Le Garde noir aime utiliser la cave de la taverne des Pendus comme cellule, jusqu'à ce qu'on puisse \"correctement\" s'occuper des prisonniers.","Pourquoi n'iriez-vous pas voir comment le captif apprécie la boisson spéciale que j'ai concocté pour lui ? Il contient un soupçon du traitement que la Dame noire a réservé au reste d'Azeroth."}, {"Apporter la Boisson spéciale de Johaan au Montagnard captif."}},
[493] = {"Voyage jusqu’aux contreforts d'Hillsbrad", {"La Société royale des apothicaires est sous la constante pression de la Dame noire pour le développement de la Peste nouvelle. Nous travaillons sans relâche et avons fait de grands progrès. Nous pensons que le succès de notre entreprise serait plus rapide si nous partagions les informations au sein de la Société.","Pour cette raison, je veux que vous portiez mes dernières recherches à l'apothicaire Lydon à Moulin-de-Tarren, une petite ville nichée dans les contreforts d'Hillsbrad. Le voyage sera long.","Allez au sud sans quitter la route. Suivez de près les panneaux !"}, {"Livrer les découvertes de l'Apothicaire Renferrel à l'Apothicaire Lydon à Moulin-de-Tarren dans les contreforts d'Hillsbrad."}},
[494] = {"Prêts à frapper", {"Taisez-vous et écoutez, <classe>.","J'ai été envoyée ici pour surveiller la ville d'Hillsbrad. C'est une mission de reconnaissance. Il est impératif que vous envoyiez immédiatement des nouvelles à la Grande Exécutrice Darthalia à Moulin-de-Tarren. Dites-lui que moi, le Traqueur noir Lesh, je lui transmets le message suivant :","\"Le cri du corbeau retentit à l'ouest.\"","Suivez la route vers l'est et faites bien attention aux panneaux. Allons, vite, partez pour Moulin-de-Tarren, et en toute hâte !"}, {"Se rendre à Moulin-de-Tarren et délivrer le message du Traqueur noir Lesh à la Grande Exécutrice Darthalia."}},
[495] = {"La Couronne de Volonté", {"Mug'Thol et sa troupe de guerre ogre ont été envoyés à Alterac afin de récupérer la Couronne de Volonté pour dame Sylvanas. Malheureusement, au lieu de la rapporter, cet idiot a décidé de l'essayer... Je suppose que les ogres ne peuvent résister à ce qui brille.","Ses actes irresponsables sont de toute façon devenus un problème. La Couronne de Volonté lui a permis de résister à l'enchantement qui le liait au service de dame Sylvanas.","La Dame noire veut faire un exemple, allez à Moulin-de-Tarren à Hillsbrad et faites un rapport à Melisara, mon lieutenant."}, {"Faire un rapport à Melisara à Moulin-de-Tarren dans les Contreforts d'Hillsbrad."}},
[496] = {"L'élixir de souffrance", {"Ah ! Un autre jour misérable à Moulin-de-Tarren ! Tout cet air pur me rend fou, <nom>.","Plus vite nous répandrons l'épidémie parmi les humains, mieux ce sera. Je fais d'importantes recherches pour découvrir des agents mortels à utiliser dans mes décoctions, mais je n'ai pas eu le temps de les collecter tous.","Si vous voulez vous rendre utile, trouvez-moi 10 Langues d'ours gris et du très rare Ichor de Rampant. Vous trouverez ces ingrédients juste à l'extérieur de Moulin-de-Tarren."}, {"Apporter 10 Langues d'ours gris et de l'Ichor de Rampant à l'Apothicaire Lydon à Moulin-de-Tarren."}},
[498] = {"Sauvetage", {"Moi et d'autres guerriers avons été envoyés par le Chef de guerre pour récupérer un objet à Durnholde et enquêter sur ce prétendu Syndicat.","Nous sommes allés à Durnholde pour jeter un coup d'œil, mais nous avons été surpris par un grand nombre d'humains. Nous nous sommes bravement battus, mais ils étaient plus nombreux. Ils se sont emparés de nous, mais je suis parvenu à m'échapper.","Durnholde est au sud-est, de l'autre côté de la rivière. Drull et Tog'thar sont retenus là-bas, vous devez aller leur porter secours."}, {"Selon les ordres de Krusk à Moulin-de-Tarren, libérer Drull et Tog'thar du Donjon de Durnholde."}},
[499] = {"L'élixir de souffrance", {"Cela va prendre un petit moment d'associer ces ingrédients pour mettre au point ce qui s'appellera l'élixir de souffrance.","Nous y voilà ! Ah ! Rien de tel pour égayer une triste journée dans ces mornes contrées que d'insuffler un peu de souffrance dans le monde.","Voyons voir si notre petite potion produit l'effet désiré.","Donnez à mon assistant Umpi un avant-goût de ce que la Dame noire a prévu pour les humains et ceux du Fléau."}, {"Donner l'Elixir de souffrance à Umpi."}},
[500] = {"La prime Cassecrête", {"Les ogres Cassecrête ont installé un repaire dans les montagnes d'Alterac, près des ruines d'Alterac. Mes éclaireurs m'ont appris qu'ils occupaient également ces ruines.","Nous ne pouvons pas les laisser s'installer tranquillement là-bas. S'ils commencent à se sentir en sécurité là où ils sont, ils ne tarderont pas à faire mouvement dans les collines. Ils seront alors à nos portes !","Partez au nord dans les montagnes d'Alterac et chassez des ogres. Rapportez-moi leurs Articulations sales, je vous en donnerai une belle prime."}, {"Collecter 9 Articulations sales d'ogres Cassecrête dans les montagnes d'Alterac. Les apporter au maréchal Redpath à Southshore."}},
[501] = {"L'élixir de douleur", {"Oh ! <nom>… Les fleurs s’épanouissent à Hillsbrad et l'air est si pur et vif... Comme cela est triste et regrettable. Je regarde par ma fenêtre crasseuse et je me languis de voir notre Peste nouvelle se répandre et porter la mort à ce monde qui le mérite tant.","Bien sûr, je resterais bien à ruminer mes pensées morbides toute la journée, mais mon devoir envers la Dame noire ne peut attendre. Vous m'aiderez, n'est-ce pas ? Mes recherches m'ont permis de conclure que la plus mortelle des préparations s'obtient à partir du sang d'un lion des montagnes. Apportez m'en et vous verrez ce que je suis capable de faire."}, {"Apporter 10 Fioles de sang de lion des montagnes à l'Apothicaire Lydon à Moulin-de-Tarren."}},
[502] = {"L'élixir de douleur", {"Ah ! Rien de tel que l'odeur du sang frais dans les collines !","Maintenant, nous allons mélanger tout cela et commencer à nous amuser pour de bon ! Ce que j'ai créé à partir de ce sang, je l'appellerai l'élixir de douleur. Si mes calculs sont exacts, cette mixture pourrait bien être le début de la grande œuvre que je veux offrir à dame Sylvanas. Mais avant tout, nous avons besoin de faire un test.","Prenez cet élixir et allez à la ferme la plus au nord dans les champs d'Hillsbrad, au sud-ouest. Et voyons comment Stanley, le petit chien du fermier Ray, apprécie cette \"douceur\"."}, {"Donner l’Elixir de douleur à Stanley."}},
[503] = {"Gol'dir", {"D'après les documents que vous avez rapportés, Gol'dir est retenu dans une grande base du Syndicat, plus au nord, très certainement dans les monts Alterac. Il est captif depuis trop longtemps et doit être absolument libéré."}, {"Libérer Gol'dir et retourner voir Krusk à Moulin-de-Tarren."}},
[504] = {"Les Combattants Cassecrête", {"Maintenant que vous avez eu un avant-goût des ogres Cassecrête, je veux que vous leur rentriez dedans franchement...","Allez dans les ruines d'Alterac et débusquez des Combattants Cassecrête. Je veux que vous en fauchiez le plus possible : c'est la seule façon de faire comprendre à ces brutes qu'il faut garder leurs distances avec le territoire de l'Alliance."}, {"Tuer 15 Combattants Cassecrête, puis retourner voir le maréchal Redpath à Southshore."}},
[505] = {"Syndicat d'assassins", {"Je suis le nouveau Magistrat de Southshore, assigné ici suite à l'assassinat du précédent Magistrat. Les tueurs n’ont jamais été retrouvés, mais nos investigations nous ont permis de conclure qu'ils avaient été engagés par le Syndicat, un groupe de voleurs commandé par les nobles du royaume déchu d'Alterac.","Le Syndicat a un camp au nord de Moulin-de-Tarren, occupé par la Horde. D'après nos derniers indices les pistes convergent toutes vers ce camp. Exécutez les membres du Syndicat que vous trouverez là-bas."}, {"Tuer 12 Détrousseurs du Syndicat et 8 Larrons du Syndicat puis retourner voir le magistrat Henry Maleb à Southshore."}},
[506] = {"L'héritage de Blackmoore", {"Il y a quelques jours, j'ai reçu la visite du leader de ce Syndicat. Son nom est Aliden Perenolde et il se prend pour le seigneur de ce pays. Il m'a confié qu'il voulait utiliser les orcs qui s'insurgent contre lui comme esclaves, exactement comme le voulait son père et mentor, le méprisable Blackmoore.","Plus important : il est venu en compagnie de sa maîtresse, une petite femme nommée Elysa, qui portait autour du cou le pendentif que le Chef de guerre désire tant.","Partez maintenant, je me débrouillerai pour régler mes affaires."}, {"Transmettre l'information recueillie par Gol'dir à Krusk à Moulin-de-Tarren."}},
[507] = {"Seigneur Aliden Perenolde", {"Il est évident que Perenolde doit être tué pour que son influence néfaste disparaisse de la surface de ce monde. Son manoir se trouve au nord, sur les rives du lac Lordamere. Faites votre devoir, <nom>, et récupérez le pendentif pour le Chef de guerre."}, {"Tuer le seigneur Aliden Perenolde et interroger sa maîtresse, Elysa, au sujet du pendentif de Taretha."}},
[508] = {"Le cadeau de Taretha", {"Le Pendentif ? Bien sûr que vous pouvez l'avoir, prenez-le ! Je ne veux garder de lui aucun souvenir… Laissez-moi tranquille, je ne vous demande rien d'autre."}, {"Rapporter le Pendentif de Taretha à Krusk à Moulin-de-Tarren."}},
[509] = {"L'élixir d'agonie", {"À chaque jour son élixir. Si cela ne tenait qu'à moi, je laisserais bien ces humains pathétiques et le Fléau se combattre. Ceux-là semblent nés pour s'entre-tuer.","Mais la Dame noire veut accélérer le processus, alors pourquoi ne pas passer ces longues et morbides journées à répandre un peu de peste et de mort à travers le pays ?","Voici le plan, <nom> : vous irez à la ferme des Nethander, au sud de Moulin-de-Tarren et à l'est de la rivière et de Southshore. Collectez 6 Fleurs de fangemufle dans les champs et rapportez-les-moi que je puisse achever mon prochain élixir."}, {"Apporter 6 Fleurs de fangemufle à l'Apothicaire Lydon à Moulin-de-Tarren."}},
[510] = {"Documents d'annonce d'attaque", {"Parmi ces documents se trouve une liste des marchandises commandées. Elle contient des requêtes ordinaires (nourriture, armes, vêtements), mais aussi des demandes moins conventionnelles (masques, huile, poison).","Mais ce qu'il y a de plus remarquable est le caractère urgent de la commande. Ces marchandises étaient nécessaires à l'exécution de ce qui est décrit dans le document comme une \"mission importante contre Southshore\", et l'on en avait besoin rapidement."}, {"Apporter les Documents d'annonce d'attaque au Magistrat Maleb à Southshore."}},
[511] = {"Lettre codée", {"L'une des lettres, sur la table, est codée et ne peut être déchiffrée."}, {"Apporter la Lettre codée au Maître des traditions Dibbs, à Southshore."}},
[512] = {"Nobles morts", {"J'ai peur que les dirigeants du Syndicat ne veuillent à tout prix la ruine de Southshore. Nous devons porter un coup décisif à ses chefs avant que leurs plans n'aboutissent.","Partez pour les Ruines de Strahnbrad, dans les monts Alterac au nord. Nous pensons que le Syndicat a établi son quartier général dans cette région.","Nous savons que ce gang de brigands reçoit ses ordres d'anciens nobles d'Alterac. Il se peut qu'ils se camouflent parmi les agents ordinaires du Syndicat, mais vous saurez que vous avez tué un noble à la chevalière qu'il porte.","Apportez-moi ces chevalières."}, {"Apporter 7 Chevalières d'Alterac au Magistrat Maleb à Southshore."}},
[513] = {"L'élixir d'agonie", {"Alors que je me résignais à l'idée qu'un autre jour allait s'achever exempt de toute joie, vous m'apportez ces merveilleuses fleurs de fangemufle.","Je vais d'abord les réduire en une fine poudre. Nous aurons alors besoin que le Maître Apothicaire Faranell à Undercity lui applique son colloïde de virulence. Après, on devrait plutôt s'amuser, je crois...","Tenez, prenez cette Poudre de fangemufle et courez immédiatement voir maître Faranell, <nom>."}, {"Apporter la Poudre de fangemufle au Maître Apothicaire Faranell à Undercity."}},
[514] = {"Le maître des lettres", {"J'ai un collègue à Ironforge qui pourrait peut-être décoder cette lettre. Rendez-vous à la bibliothèque du Hall des Explorateurs à Ironforge et parlez au Prospecteur Stormpike. Dites-lui que je vous envoie et montrez-lui la lettre. S'il y a une personne au nord du pic Blackrock à pouvoir lire cela, c'est bien lui."}, {"Apporter la Lettre ingénieusement codée au Prospecteur Stormpike à Ironforge."}},
[515] = {"L'élixir d'agonie", {"Un composé à base de fangemufle ? Absolument merveilleux ! Pourquoi n'y ai-je pas pensé ? Laissez-moi appliquer de mon colloïde de virulence à cette diabolique mixture.","Afin d'activer les agents contaminateurs de ce brouet de fangemufle, Lydon aura besoin d'une potion de sang de troll forte, d'écailles de Daguéchine et d'yeux d'Ailerons déchirés de la côte méridionale. Il saura quoi faire une fois que vous aurez réuni tous les ingrédients. Ah ! j'ai tellement hâte d'apprendre ce que l'expérience va donner."}, {"Apporter le Brouet de fangemufle, une Potion de sang de troll forte, 5 Ecailles de Daguéchine et 5 Yeux d'Ailerons déchirés à l'Apothicaire Lydon à Moulin-de-Tarren."}},
[516] = {"Le Péril de Beren", {"J'ai reçu des rapports indiquant qu'un groupe de morts-vivants se cache pour préparer une attaque contre nous. Armés de cette information, nous pouvons retourner la situation en les attaquant les premiers et réduire leur plan minable à un simple souvenir cocasse.","Malheureusement, les informations dont nous disposons sont incomplètes, au mieux. On dit qu'ils se cachent dans un endroit appelé Péril de Beren. La localisation exacte est inconnue, mais nous savons que c'est une caverne dans les collines, près d'une enclave des sorciers de Dalaran.","J'ai confiance en vos ressources. Trouvez-les et tuez-les."}, {"Localiser le Péril de Beren, tuer 6 Manouvriers Ravenclaw et 6 Gardiens Ravenclaw puis retourner voir le prêtre des ombres Allister au Sépulcre."}},
[517] = {"L'élixir d'agonie", {"Ce nouvel élixir est enfin prêt à être testé. Mais pourquoi gâcher une si bonne et si contagieuse peste sur une pauvre grenouille ou un chien famélique ? Amusons-nous un peu, au nom de Dame Sylvanas.","Descendez à Dun Garok. Vous connaissez les casernes où ces sales petits nains s’agitent partout ? Volez un baril de leur Bière Shindigger pour moi. Une fois que j'aurai ce baril, nous pourrons commencer la fête, vous et moi, <nom>."}, {"Apporter un baril de Bière Shindigger à l'Apothicaire Lydon à Moulin-de-Tarren."}},
[518] = {"La Couronne de Volonté", {"Mug'Thol et ses ogres Cassecrête ont envahi les ruines d'Alterac. Il va être difficile de lui infliger une sérieuse punition sans d’abord entamer ses troupes.","Après les avoir réduites à la portion congrue, nous l’attaquerons.","Compris ? Bien. Allez-y à présent."}, {"Tuer 14 Mutilateurs Cassecrête pour Melisara à Moulin-de-Tarren."}},
[519] = {"La Couronne de Volonté", {"Targ, Fouilleboue et Glommus sont les lieutenants de Mug'Thol. Ils lui sont loyaux, bien que personne ne sache vraiment ce qui peut amener un ogre à la loyauté.","Apportez-moi leurs têtes, <nom>, et alors tout ce qu'il restera à faire sera de s'occuper de celle de Mug'Thol, de sa couronne, et du reste."}, {"Tuer Targ, Fouilleboue et Glommus, puis apporter leurs têtes à Melisara à Moulin-de-Tarren."}},
[520] = {"La Couronne de Volonté", {"À présent que les lieutenants de Mug'Thol sont morts, tout ce qu'il reste à faire est de tuer Mug'Thol et de récupérer la Couronne de Volonté. Je ne crois pas qu'il veuille la donner gentiment. Et encore moins à vous, si l'on considère la façon dont ses amis sont... partis.","Trouvez Mug'Thol, et présentez-moi sa tête et la Couronne. Le glas sonnera alors pour la résistance des Cassecrête."}, {"Tuer Mug'Thol et présenter sa tête et la Couronne de Volonté à Melisara, à Moulin-de-Tarren."}},
[521] = {"La Couronne de Volonté", {"Tout ce qu'il reste à faire est de donner la Couronne de Volonté à Sharlindra, à Undercity. Elle veillera à ce que la Couronne soit remise à Dame Sylvanas. Je vous déconseille de penser à la coiffer à vos propres fins, <nom>. Le sort de Mug'Thol est assez éloquent."}, {"Remettre la Couronne de Volonté à Sharlindra, à Undercity."}},
[522] = {"Contrat de l'assassin", {"Ceci est un contrat pour l'assassinat du Magistrat Henry Maleb. L'ordre a été scellé à la cire rouge, à l'aide d'une chevalière caractéristique."}, {"Apporter le Contrat de l'assassin au Magistrat Maleb à Southshore."}},
[523] = {"La mort du baron Vardus", {"Le contrat que vous avez trouvé est signé par le baron Vardus, un noble de l'ancien royaume d'Alterac, réputé pour son cœur de pierre et ses pratiques barbares. Je ne suis pas surpris que quelqu'un comme le baron soit derrière tout ça.","Trouvez cet homme. Trouvez-le, et amenez-le devant la justice. Il doit être avec l'élite du Syndicat. Peut-être aux Ruines de Strahnbrad, ou dans la vallée qui est au nord."}, {"Apporter la tête du baron Vardus au Magistrat Maleb à Southshore."}},
[524] = {"L'élixir d'agonie", {"A côté, les Gardes noirs détiennent quelques fermiers pris en otage. La Grande Exécutrice Darthalia suspectait qu'un des stratèges de Stormwind se cache parmi eux. Apparemment, le pauvre militaire a été retrouvé mort dans les Maleterres la nuit dernière. Darthalia, toujours aux petits soins pour moi, a décidé de me laisser faire ce que je voulais avec les fermiers.","Je pense qu'il est temps de leur préparer une petite fête. Comme au bon vieux temps !","Placez ce baril infecté sur le tapis, dans leur pièce. Tournée générale bière pestilentielle !"}, {"Placer le Baril infecté sur le tapis pour les fermiers détenus comme otages."}},
[525] = {"D'autres mystères", {"J'ai déchiffré la lettre, mais je ne sais pas quoi en faire. Vous l'avez trouvée chez des voleurs dans les collines d'Alterac ? C'est étrange…","Vous devriez la montrer au Magistrat de Southshore. Bien que cette lettre soit sibylline, Southshore est la ville la plus proche d'Alterac. Si un danger menace, alors il doit savoir."}, {"Apporter la Lettre décodée au Magistrat Maleb, à Southshore."}},
[526] = {"Les lingots de sancteforge", {"Vous avez peut-être découvert un lingot de sancteforge dans ce coffre, mais ce n'est pas suffisant pour forger quoi que ce soit de décent avec !","Vous devriez y retourner et en chercher davantage. Les murlocs, près de l'épave, ont peut-être des lingots sur eux."}, {"Collecter 5 Lingots de sancteforge, puis retourner voir Glorin Steelbrow, dans les Paluns."}},
[527] = {"La bataille d'Hillsbrad", {"Mettez-vous au garde-à-vous lorsque vous vous adressez à moi, <classe> ! Je suis sous le commandement direct de Varimathras. Nous devons neutraliser la prolifération des humains jusqu'à ce que nos apothicaires développent la Peste nouvelle.","Nos services de renseignements pensent que la ville d'Hillsbrad serait vulnérable à une attaque. Votre première mission consiste à perturber leur approvisionnement et le stockage de nourriture.","Partez à l'ouest. Faites une incursion dans les fermes les plus au nord des Champs d'Hillsbrad. Assurez-vous que leurs propriétaires, les fermiers Ray et Getz, soient exécutés."}, {"Tuer 6 Garçons de ferme, 6 Fermiers, le Fermier Ray et le Fermier Getz, puis retourner voir Darthalia à Moulin-de-Tarren."}},
[528] = {"La bataille d'Hillsbrad", {"La nouvelle de l'attaque sur les fermes situées au nord a semé la panique chez les gens de la ville. Merveilleux !","Nos Traqueurs noirs rapportent que les paysans fuient vers les champs au sud d'Hillsbrad. Ces imbéciles pensent qu'ils peuvent s’y cacher et ainsi échapper à leur sort.","Montrez-leur qu'ils ont tort, <nom>. Retournez aux Champs d'Hillsbrad et traquez les paysans qui s'y tapissent. Apportez-leur le chaos au nom de la Dame noire.","Rompez !"}, {"Tuer 15 Paysans d'Hillsbrad et faire votre rapport à Darthalia à Moulin-de-Tarren."}},
[529] = {"La bataille d'Hillsbrad", {"Les humains d'Hillsbrad ont fait montre d'une résistance inattendue. Le problème semble être que les gens de la ville sont équipés par le forgeron local. Nous devons couper cet approvisionnement.","Votre mission consiste à exécuter le forgeron Verringtan. Tuez également ses apprentis. Pendant que vous y serez, ramenez un chargement de fer prélevé dans leur stock. Cela se révélera pratique lorsque nous aurons achevé la conquête d'Hillsbrad et que nous entreprendrons de raser Southshore."}, {"Tuer le Forgeron Verringtan et 4 Apprentis Forgerons d'Hillsbrad.","Récupérer un Chargement de fer, puis retourner voir Darthalia à Moulin-de-Tarren."}},
[530] = {"La vengeance d'un mari", {"Valdred, autrefois un bon ami à moi, est le meurtrier de ma femme. Bien que je sois incapable de ressentir de la peine, j'aspire à la vengeance. Si vous tuez Valdred et me rapportez ses mains maudites, alors je vous récompenserai.","La dernière fois que j'ai entendu parler de lui, il se trouvait au Mur de Greymane, dans le sud des Pins argentés, essayant de fuir vers le royaume de Gilnéas."}, {"Tuer Valdred Moray.","Apporter ses mains à Raleigh Andrean à Undercity."}},
[531] = {"La vengeance de Vyrin", {"Par la Lumière ! N'apportez pas de choses comme ça dans le pavillon… c'est insalubre, insupportable, in… in… insalubre !","Sortez-moi ça de là, voulez-vous ? Personne ne veut voir votre fichu petit trophée, vraiment !"}, {"Amener la tête du vieux Noiraud à Vyrin Swiftwind au pavillon des pérégrins."}},
[532] = {"La bataille d'Hillsbrad", {"La ville d'Hillsbrad est en pleine confusion et nous pensons qu'elle va bientôt tomber. Nous devons cependant persister dans notre barbarie jusqu'à ce que ces chiens d'humains soient balayés des collines pour l'éternité.","Malgré leurs pertes écrasantes, les humains se rassemblent autour de leurs chefs et continuent à se battre. Nous devons éliminer leur chef, le maire Burnside, de même que le conseil municipal. Détruisez également leur très précieux document politique, la Proclamation d'Hillsbrad. Et pendant que vous y êtes, volez le Registre municipal."}, {"Tuer le Magistrat Burnside et 5 Conseillers d'Hillsbrad. Détruire la Proclamation d'Hillsbrad. Voler le Registre municipal d'Hillsbrad. Rendre compte à Darthalia à Moulin-de-Tarren."}},
[533] = {"Infiltration", {"Nous avons enfin des nouvelles et des rumeurs, des indices sur l'endroit où pourrait être Gol'dir. Les humains l'ont emmené à divers endroits, mais nos recherches aboutissent au grand camp du Syndicat au nord d'ici.","Allez-y, <nom>, et ramenez toutes les informations sur lesquelles vous pouvez mettre la main."}, {"Rapporter des informations sur la localisation de Gol'dir pour Krusk, à Moulin-de-Tarren."}},
[535] = {"Valik", {"Eurrgh… Blessé et mourant, me voilà en face de l'ennemi.","Vous me tuerez de toute façon, mais si vous m'apportez de la bière de Southshore... Vous autres ne buvez pas, n'est-ce pas ? Je vous dirai ce que vous voulez savoir."}, {"Apporter une Chope de bière de Southshore à Valik, au Camp du Syndicat."}},
[536] = {"Le long de la côte", {"Transféré de la garde de la cité de Stormwind à Southshore, une ville visiblement abandonnée… L'odeur du port jour et nuit.","Sans parler du vieux Redpath… Veut que je m'occupe des murlocs. Ha !","J'ai du mal à m'imaginer allant jusqu'à l'une des enclaves de ces grotesques poissons mutants qui grouillent le long du Rivage occidental. Si vous pouviez y aller et vous occuper d'eux, j'apprécierais."}, {"Tuer 10 Chasse-marées et 10 Oracles Aileron déchiré pour le lieutenant Farren Orinelle à Southshore."}},
[537] = {"Le conseil noir", {"La lettre que vous m'avez apportée a été écrite par un être qui se nomme lui-même Nagaz, un membre de la Veille d'Argus. Je ne sais pas ce qu'est cette Veille d'Argus, mais si elle est alliée au Syndicat, alors elle constitue une menace.","Je veux que vous trouviez Nagaz et les Mages de l'ombre d'Argus mentionnés dans cette lettre. Je veux que vous les trouviez et que vous les tuiez.","Cherchez dans les Hauteurs, là où le Syndicat est le plus fort.","Avec un peu de chance, vous y trouverez ces représentants de la Veille d'Argus."}, {"Tuer 4 Mages de l'ombre d'Argus.","Apporter la Tête de Nagaz au Magistrat Maleb à Southshore."}},
[538] = {"Southshore", {"<nom>. Si un long voyage ne vous dérange pas, alors j'ai une tâche à vous confier, pour aider à nouveau Stormwind à étoffer sa bibliothèque.","L'ancien royaume d'Alterac s'est effondré après la Grande Guerre. C'est malheureux que la ville soit à présent en ruine, mais il est encore plus tragique que de grands savoirs aient disparu ! Nous devons retrouver ces importantes collections de livres.","Southshore est la ville la plus proche des Ruines d'Alterac. Rendez-vous-y et parlez à l'Erudit Dibbs. Il peut vous guider vers les ruines, et tous les livres que vous pourriez trouver."}, {"Parler à l'Erudit Dibbs à Southshore."}},
[539] = {"La bataille d'Hillsbrad", {"Le Registre municipal d'Hillsbrad indique que la Mine d'Azureload est sous le contrôle de l'Alliance. De plus, c’est une source importante de minerai de fer destinée aux armureries de l'Alliance.","Même si nous avons porté un coup décisif à la ville d'Hillsbrad, l'Alliance soutient et protège toujours la Mine d'Azureload.","Elle se trouve au sud d'Hillsbrad. Allez-y et tuez le contremaître, un humain du nom de Bonds. Tuez également ses Mineurs. Cela devrait envoyer un message fort à l'Alliance."}, {"Tuer le Contremaître Bonds et 10 Mineurs d'Hillsbrad, puis rendre compte à Darthalia, à Moulin-de-Tarren."}},
[540] = {"L'entretien des connaissances", {"Ces ogres répugnants résident à présent dans les ruines d'Alterac, et je frémis à l'idée de ce qu'ils peuvent faire des précieux livres qui s'y trouvent.","Pénétrez dans les ruines d'Alterac et récupérez des tomes sur les ogres. Prenez tout ce que vous trouverez, mais cherchez également le livre suivant : « Le Bras de Gri'lek ». C’est un recueil d’anciennes légendes trolls que je dois lire. Les ruines d'Alterac sont le dernier endroit où se trouve une copie intacte de cet ouvrage."}, {"Apporter 5 Tomes retrouvés, ainsi que le Livre de cuir usé « Le Bras de Gri'lek » au Maître des traditions Dibbs, à Southshore."}},
[541] = {"La bataille d'Hillsbrad", {"Des nains aidant des humains ? Que ces affreuses petites bêtes soient maudites.","Nous allons tester la force de cette prétendue Alliance. Nous verrons à quel point ces cogneurs de chevilles sont déterminés à aider les humains une fois que nous serons passés. Nous savons précisément où se trouve leur forteresse dans ce territoire.","Ils ont établi une caserne dans la partie sud-est d'Hillsbrad, le long du Mur de Thoradin. La mission est d'attaquer cette caserne, baptisée Dun Garok, <classe>. Le capitaine Ironhill, leur chef, doit mourir."}, {"Aller à Dun Garok et tuer 10 Montagnards, 8 Fusiliers, 4 Prêtres et le Capitaine Ironhill, puis rendre compte à Darthalia, à Moulin-de-Tarren."}},
[542] = {"Les livres de Milton", {"J'ai besoin de temps pour étudier le livre « l'Arme de Gri'lek » que vous m'avez apporté, mais je suis sûr que Milton a hâte d'enrichir la collection des livres de Stormwind avec ceux que vous avez retrouvés aux Ruines d'Alterac.","Les voici, empaquetés et catalogués. Amenez-les à Milton Sheaf à Stormwind et je suis certain qu'il sera reconnaissant."}, {"Apporter les Tomes d'Alterac à Milton Sheaf à Stormwind."}},
[543] = {"La tiare de Perenolde", {"La Tiare de Perenolde est un ancien héritage des \"nobles\" de Perenolde, la famille régnante d'Alterac, avant que ce royaume ne tombe en ruine. On dit qu'elle a été réalisée par des maîtres bijoutiers à Ironforge, et qu'elle est ornée des émeraudes les plus pures.","Quand Alterac a été détruite, la Tiare fut perdue. Mais les rumeurs disent qu'un ogre-mage, Grel'borg l'Avare, l'a trouvée, et qu'il fouille à présent les Ruines d'Alterac à la recherche d'autres trésors.","Nous aimerions que vous récupériez la Tiare pour nous."}, {"Apporter la Tiare de Perenolde à Remington Ridgewell, à Stormwind."}},
[544] = {"L'invasion de la prison", {"Je suis venu à Moulin-de-Tarren pour effectuer des recherches, mais je dois à présent résoudre une crise. Voyez-vous, quatre Réprouvés se sont enfuis d'Undercity il y a quelques mois. Ils ont trahi leurs semblables, mais pire encore : ils ont volé la Dame noire.","Ces voleurs ont forcé un coffre et se sont emparés de quatre artefacts, des objets dont nos apothicaires ont besoin pour leurs études. Dame Sylvanas veut récupérer ces artefacts.","Les voleurs ont fui à Dalaran, et là-bas les sorciers les ont placés en quarantaine dans le Camp d'internement de Lordamere."}, {"Trouver les traîtres et récupérer les artefacts, puis retourner voir le magus Voidglare à Moulin-de-Tarren."}},
[545] = {"Les patrouilles de Dalaran", {"Les Sorciers de Dalaran ont construit un vaste dôme magique autour du cœur de leur cité. Certains croient qu'il s'agissait de se protéger des violences de cette époque, d'autres pensent qu'ils recherchaient simplement la solitude, pour étudier et comploter.","Je ne m'intéresse pas à la raison. En revanche, ce qui m'intéresse, c’est que la périphérie de la ville en ruine, autour du dôme, puisse abriter des trésors magiques.","Mais les Sorciers et leurs Esclaves élémentaires patrouillent ce secteur.","Chassez-les donc, et revenez lorsque leur nombre sera conséquemment réduit."}, {"Tuer 6 Invocateurs de Dalaran et 12 Esclaves élémentaires, puis retourner voir le magus Voidglare à Moulin-de-Tarren."}},
[546] = {"Souvenirs de morts", {"J'ai entendu dire que vous étiez <recruté/recrutée> par Darthalia pour mener l'offensive contre les humains d'Hillsbrad. Je vous envie tellement…","Tandis que vous vous amuserez – à tuer des humains, à piller, à terroriser les innocents – je resterai coincé ici, à Moulin-de-Tarren, à monter la garde et à me morfondre.","Peut-être prendrez-vous en pitié un vieux Garde noir comme moi ? Vous remarquez que je fais la collection de crânes. Et vous allez combattre des humains pendant quelque temps, je crois. Au gré de votre longue bataille, vous récolterez bien quelques crânes, apportez-moi-en 30 et je vous rendrai la pareille d'une belle façon."}, {"Le Garde noir Samsa du Moulin-de-Tarren veut 30 Crânes d'humains d'Hillsbrad."}},
[547] = {"Epée d'Humbert", {"La Grande Exécutrice Darthalia m'a envoyé surveiller Dun Garok de loin. J'ai établi mon camp juste au-delà de leur champ de vision. Mais ce matin, pour faire bouillir de l'eau, je suis allé chercher du bois.","Quand je suis revenu au camp, j'ai découvert que mon équipement avait disparu.","Apparamment, des éclaireurs nains de Dun Garok sont passés par là et repartis avec mon matériel. J'ai besoin de mon épée ! Si vous pouviez la retrouver et me la rendre, je serais tellement reconnaissant. Il y a tellement de nains par ici. Ça va être très difficile de retrouver le sale petit voleur qui l'a dérobée."}, {"Retrouver l'Epée du Garde noir Humbert et la lui retourner à Moulin-de-Tarren."}},
[549] = {"ON RECHERCHE : Personnel du Syndicat", {"Au nom de l'autorité de Dame Sylvanas, tous les membres de l'organisation connue sous le nom du Syndicat sont recherchés. Ceux conduits à la garde ou faits prisonniers seront sommairement exécutés. Nous savons qu’actuellement ils se rassemblent dans les ruines du Donjon de Durnholde, au sud-est de Moulin-de-Tarren.","Une récompense est offerte à quiconque apporte la preuve de ses actions à la Grande Exécutrice Darthalia."}, {"Tuer 10 Voleurs et 10 Gardiens du Syndicat, puis aller voir la Grande Exécutrice Darthalia à Moulin-de-Tarren pour votre récompense."}},
[550] = {"La bataille d'Hillsbrad", {"Comme vous avez combattu avec valeur à la bataille d'Hillsbrad, j'ai écrit cette recommandation exaltant vos prouesses au combat, pour que le haut commandement les apprécie.","Apportez cette Recommandation scellée à Varimathras à Undercity. Allez avec fierté, <classe>."}, {"Apporter la Recommandation scellée à Varimathras à Undercity."}},
[551] = {"Le Parchemin ensorcelé", {"Ce parchemin est sous l’effet d’un sort qui le rend indéchiffrable. Mais un mot peut être lu, en haut du papier :","Nagaz"}, {"Apporter le Parchemin ensorcelé à l'Erudit Dibbs à Southshore."}},
[552] = {"La vengeance d'Helcular", {"Maître Helcular avait presque achevé sa transformation en liche lorsqu'une bande d'humains furieux a découvert son repaire dans les collines et l'a tué. Pour rendre les choses encore pires, ils ont enterré son corps au cimetière de Southshore.","Un tel manque de respect envers un si grand sorcier n'est pas acceptable. Etant son apprenti, je veillerai à ce que la pluie de la vengeance tombe drue sur la ville de Southshore.","Avant tout, nous avons besoin du Bâtonnet d'Helcular. Les yétis ont atteint son repaire. L'une de ces bêtes l'a sans doute emporté."}, {"Reprendre le Bâtonnet d'Helcular aux yétis et la ramener au novice Thaivand à Moulin-de-Tarren."}},
[553] = {"La vengeance d'Helcular", {"Tenez, prenez le Bâtonnet d'Helcular. Vous en aurez besoin.","Helcular a fabriqué 3 bûchers cérémoniels comme composants de son sort le plus puissant. La Flamme d'Azel et la Flamme de Veraz brûlent dans l'une des cavernes des contreforts. La Flamme d'Uzel éclaire une cave située plus haut et plus au nord dans les montagnes.","Une fois le Bâtonnet d'Helcular chargé avec ces trois flammes, il faut l’enfoncer dans la tombe d'Helcular située à Southshore. La vengeance sera nôtre."}, {"Charger le Bâtonnet d'Helcular des pouvoirs de la Flamme d'Azel, de la Flamme de Veraz et de la Flamme d'Uzel.","Enfoncez le Bâtonnet chargé dans la tombe d'Helcular à Southshore."}},
[554] = {"Stormpike décode", {"Le Prospecteur Stormpike est un maître du décodage. Si vous lui amenez le parchemin que vous avez trouvé, alors, avec un peu de chance, il peut en saisir la signification. Stormpike étudie en ce moment aux quartiers généraux des Prospecteurs à Ironforge.","Et quand vous le verrez dites-lui que vous m'avez parlé. Je lui ai envoyé beaucoup de choses dernièrement."}, {"Apporter le Parchemin ensorcelé au Prospecteur Stormpike à Ironforge."}},
[555] = {"Bisque de tortue apaisante", {"S'il y a une chose que vous apprendrez à propos de Southshore, c'est que nous avons l'une des meilleures cuisines au nord de Stormwind !","Prenez ma recette secrète de bisque de tortue, par exemple. J'en ai connu qui ont fait le voyage depuis Darkshire juste pour en déguster une coupe. Mais il y a bien longtemps que je n’en ai pas fait. J'allais au-delà de Dalaran, jusqu'au lac de Lordamere, mais c'est trop dangereux à présent. Si vous m'apportiez de la Viande de tortue, venant du nord, et des Epices apaisantes, je vous mitonnerai un bon petit plat."}, {"Apporter 10 morceaux de Viande de tortue et des Epices apaisantes au Chef Jessen à Southshore."}},
[556] = {"Les Runes gravées", {"Un sérieux atout des sorciers de Dalaran est leur familiarité avec les élémentaires, ces créatures puissantes venant d'autres dimensions. Les sorciers utilisent des Runes gravées pour contrôler ces élémentaires. Le magus Voidglare aimerait étudier certaines de ces runes.","Pénétrez sur le territoire des sorciers de Dalaran, à l'ouest d'Hillsbrad, et trouvez-en.","Il se peut que vous les trouviez sur n'importe quel citoyen de Dalaran, mais je suggère que vous vous les procuriez au camp d'internement. C'est un endroit moins dangereux que Dalaran."}, {"Apporter 10 Runes gravées au Conservateur Bel'varil à Moulin-de-Tarren."}},
[557] = {"Les Bracelets de liaison", {"Le magus sera très occupé à étudier ces runes gravées que vous avez apportées, mais je sais que tôt ou tard il voudra des échantillons de leurs contreparties : des brassards de liaison. Ce sont des brassards qui, passés aux poignets des élémentaires, les lient au service des mages.","Chassez des élémentaires dans les ruines de Dalaran et récupérez leurs brassards de liaison de terre. Le magus en sera très satisfait."}, {"Apporter 4 Brassards de liaison de terre au Conservateur Bel'varil à Moulin-de-Tarren."}},
[558] = {"L'autographe de Jaina", {"On dit que dame Jaina Portvaillant, à Theramore, est l'une des plus grandes héroïnes de l'Alliance de tous les temps. Quand je serai grand, je veux aussi être un héros de l'Alliance !","Vous connaissez Jaina, <nom> ? Vous pouvez me faire une faveur, s'il vous plaît ? Vous pouvez m'avoir un autographe de dame Jaina ? Je sais pas comment lui demander, et c'est sans doute une personne bien trop occupée pour s'occuper de quelqu'un comme moi... Mais pas vous ! Vous êtes <un aventurier/une aventurière>, comme elle ! Je parie qu'elle vous donnerait facilement un autographe !","S'il vous plaît ?"}, {"Allez à Theramore pour tenter d'obtenir un autographe de dame Jaina Portvaillant pour votre protégé."}},
[559] = {"La preuve de Farren", {"Le croirez-vous, <nom> ? Redpath n'était pas satisfait, en dépit de tous les murlocs que nous avons tués. Ah ! ne soyez pas modeste, vous y avez participé. À présent il veut que je lui apporte la preuve de leur mort, en allant chercher les têtes refroidies de ces misérables murlocs, pour les soumettre à sa validation."}, {"Collecter 10 Têtes de murlocs sur les murlocs du Rivage occidental pour le lieutenant Farren Orinelle à Southshore."}},
[560] = {"La preuve de Farren", {"Vous savez, <nom>, je préférerais ne pas toucher à ces têtes… Puisque vous avez déjà les mains qui sentent le poisson, cela vous dérangerait-il de les livrer à Redpath pour moi ?"}, {"Livrer le Sac de têtes de murlocs au maréchal Redpath à Southshore."}},
[561] = {"La preuve de Farren", {"Veuillez informer le lieutenant Orinelle que j'aimerais qu'il concentre ses efforts sur les nagas qui infestent le Rivage oriental. Quand il aura obtenu des résultats, dites-lui qu'il vienne en personne m'en rendre compte."}, {"Parler au Lieutenant Farren Orinelle à Southshore."}},
[562] = {"Stormwind me voilà", {"Mon ancien mentor à la garde de la cité de Stormwind a envoyé un message me disant que le Haut Commandement de l'Alliance commence à s'inquiéter du problème posé par les nagas. D'après ce qu'il a entendu, il y a des chances de promotion, ou d'une bonne affectation, pour quiconque se montrera valeureux dans la résolution de ce problème.","Il y a juste quelque chose qui ne va pas... Je ne peux pas m'occuper des nagas, <nom>. Oh bien sûr, je pourrais disperser une querelle d'ivrognes, ou alpaguer un clochard vindicatif, mais les nagas c’est trop pour moi. Vous devez m'aider, <nom>."}, {"Tuer 10 Chasseurs du Rivage et 10 Sirènes Daguéchine pour le lieutenant Farren à Southshore."}},
[563] = {"Nouvelle affectation", {"Que mon rapport soit lu ne devrait pas poser de problème. Le nouveau capitaine de la Garde, le major Samuelson, était mon mentor lorsque j'étais posté à Stormwind. Cela devrait lui donner suffisamment d'arguments pour que j'y sois à nouveau transféré. Je vais avoir besoin de vous pour porter mon rapport. Cependant, je n'aimerais pas que ce cher Redpath comprenne."}, {"Remettre le Rapport de Farren au major Samuelson au Donjon de Stormwind."}},
[564] = {"Un problème très coûteux", {"Y'a rien qui m'hérisse plus que d'perdre ces beaux ch'vaux, là. Et croyez-moi, on en a perdu pas mal, entre ces sales morts-vivants, c'fichu gang du Syndicat, et toutes les aut' crapules bonnes à rien qui traînent dans l'secteur.","Mais j'jure sur la tombe d'mon vieux grand-père qu'y a pas d'danger plus grand pour les ch'vaux que ces lions d'montagne en haut à Alterac.","Ils s'ramènent toujours des collines et m'rendent la vie difficile !","Vous croyez être <un héros/une héroïne> ? Allez-y et faites quek'chose pour y r'médier."}, {"Darren Malvew de Southshore veut que vous tuiez 8 Lions des montagnes et 10 Lions des montagnes massifs."}},
[565] = {"La cape en fourrure de yéti de Bartolo", {"Moi, le Grand Bartolo, je confectionnerai pour vous ma fameuse cape en fourrure de yéti, célèbre dans le monde entier, et qui fait la renommée de l'Alliance !","Je vous mets au défi de parcourir le monde pour trouver meilleure cape. Oh ! attendez, qu'est-ce que vous dites, <petit/petite> <nom> ? \"Bartolo, il n'existe pas de meilleure cape !\" Oui, oui, je sais, j'ai entendu ça des milliers de fois déjà.","Bartolo demande une pelote de laine, une cape d'homme des collines confectionnée par un habile tanneur, du fil fin et bien sûr de la fourrure de yéti. Apportez-moi tout cela et vous serez <un client satisfait/une cliente satisfaite>."}, {"Apporter à Bartolo Ginsetti de Southshore une Pelote de laine, une Cape d'homme des collines, du Fil fin et 10 Touffes de fourrure de yéti."}},
[566] = {"ON RECHERCHE : baron Vardus", {"Par décret de la Grande Exécutrice Darthalia, l'humain connu sous le nom de baron Vardus est recherché pour crime contre les Réprouvés. Nous savons que Vardus est un membre haut placé de l'organisation dite le Syndicat.","Il a été vu pour la dernière fois dans les Hauteurs des montagnes d'Alterac, au nord de Moulin-de-Tarren.","Il doit être ramené mort. Apportez une preuve de son trépas à la Grande Exécutrice Darthalia pour obtenir votre récompense."}, {"Tuer le baron Vardus et apporter sa tête à la Grande Exécutrice Darthalia, au Moulin-de-Tarren."}},
[567] = {"Dangereux !", {"Dangereux !","Les humains d'Hillsbrad dont les noms suivent ont été déclarés dangereux et leurs têtes sont mises à prix par la Grande Exécutrice Darthalia :","Commis Horrace Whitesteed. Recherché pour le meurtre du Garde noir Toma.","Citoyen Wilkes. Recherché pour le meurtre de l'Apothicaire Eli.","Mineur Hackett. Recherché pour le meurtre du Traqueur noir Fry.","Kalaba la fermière. Recherchée pour détournement de marchandises à Undercity.","Chacun de ces ennemis se cache et sera difficile à débusquer. Une récompense sera remise à la preuve de leur mort."}, {"La Grande Exécutrice Darthalia de Moulin-de-Tarren offre une prime pour la mort du Commis Horrace Whitesteed, du Citoyen Wilkes, du Mineur Hackett et de Kalaba la fermière."}},
[568] = {"La défense de Grom'gol", {"Throm'ka, <classe> !","Votre arrivée à Grom'gol tombe à point nommé. Je commande ce campement camp ici dans la jungle, au nom du Chef de guerre. Je suis tenu sur l'honneur d'assurer la sécurité de tous les membres de la Horde. Notre mission consistant à maintenir une chaîne d'approvisionnement jusqu’à Stonard est entravée par certaines espèces locales.","Je vous charge de réduire la population des raptors autour de Grom'gol. Une fois que vous aurez accompli des progrès significatifs, revenez me voir pour que je vous assigne une nouvelle mission."}, {"Le commandant Aggro'gosh du campement Grom'gol veut que vous tuiez 15 Raptors flagellants."}},
[569] = {"La défense de Grom'gol", {"La première fois que nous sommes arrivés dans cette jungle, nous n'avions pas l'intention de déclarer la guerre aux tribus locales. Notre mission d’approvisionnement de Stonard est de la plus haute priorité. Mais des complications ont surgi. Un groupe d'ogres occupe les Ruines de Mizjah au sud-est de Grom'gol. Malgré nos avertissements, ils continuent à tendre des embuscades à nos caravanes.","Je vous ordonne de vous occuper de ces ogres peu coopératifs. Allez vers les ruines et délogez-les de la région. Au moins, cela enverra un message fort."}, {"Le commandant Aggro'gosh du campement Grom'gol veut que vous tuiez 10 Brutes et 5 Sorciers ogres."}},
[570] = {"Enchantement de Mok'thardin", {"Le pouvoir de la magie de la jungle doit être compris et acquis par la Horde. Mes rituels se sont montrés efficaces jusque-là. Je peux lancer des enchantements très puissants. Pour vous, brave <classe>, je vais réaliser un objet très spécial.","Ramenez-moi quelques griffes de panthère Ombregueule et un croc de tigresse. Mais pas n'importe quel croc. Il doit être dans un état parfait.","Ces bêtes vivent cachées dans la jungle, mais vous en trouverez pas loin, juste de l'autre côté de la rivière, depuis les ruines de Mizjah au sud-est."}, {"Le prophète Mok'thardin du campement Grom'gol veut que vous trouviez 8 Griffes de panthère Ombregueule et 1 Croc de tigresse en parfait état."}},
[571] = {"Enchantement de Mok'thardin", {"Ce dont j'ai besoin va être très difficile à obtenir. Pour tenir ensemble ces plumes, griffes et ce croc parfait, il me faut une longe magique. Seul un tendon de gorille fera l'affaire.","Vous ne pourrez en trouver un que sur un ancien gorille de Valbrume. On sait que ces animaux fourragent à l'est de Baie-du-Butin.","Le tendon doit être parfait. Il se peut que vous deviez en tuer plus d'un pour trouver exactement ce dont j'ai besoin, mais cela en vaudra la peine."}, {"Apporter un Tendon d'Ancien gorille de Valbrume au Prophète Mok'thardin à Grom'gol."}},
[572] = {"Enchantement de Mok'thardin", {"Les matériaux que vous avez collectés sur les félins de la vallée n'étaient que les premiers ingrédients d'un puissant objet enchanté. Ces babioles de la jungle combinées à mon rituel chamanique vous fourniront un outil très puissant, au service de la Horde et du Chef de guerre.","Mais pour l'heure vous devez rassembler pour moi quelques plumes de Traqueurs de la jungle. Ces créatures portent des généralement des plumes sur leurs bras. Vous trouverez un groupe de raptors au sud de Grom'gol, non loin de la grande arène."}, {"Amener 10 Plumes de Traqueurs de la jungle au Prophète Mok'thardin au campement Grom'gol."}},
[573] = {"Enchantement de Mok'thardin", {"Il ne manque plus qu'un dernier réactif pour compléter l'enchantement.","Au sud, vers la côte occidentale, loin au-delà des falaises, coule une source sacrée. Elle est à l'origine d'un conflit parmi les érudits mystiques et religieux. Chaque race d'Azeroth proclame que cette source a été rendue divine pour ses propres populations.","La dernière demande que je vous fais : apportez-moi de l'Eau de source sacrée. Mais attention. Les nagas ont envoyé des explorateurs qui progressent vers la source. Vous devrez également vous en occuper."}, {"Le Prophète Mok'thardin de Grom'gol a besoin d'Eau de source sacrée. Il veut que vous tuiez aussi 10 Explorateurs nagas."}},
[574] = {"Forces spéciales", {"Maintenant que de nombreux hommes de troupe sont morts, vous devez combattre ses forces hautement entraînées. Lorsqu'ils ne sont pas dans la nature à massacrer nos soldats rebelles, on peut les trouver au fond de la Base de Kurzen, dans un endroit qu'ils appellent la Réserve.","Je connais personnellement beaucoup des hommes de Kurzen, et sais qu'ils sont entraînés, loyaux et cruels. Ce n'est pas une tâche facile que je vous confie là, <nom>.","Après les avoir battus, rendez compte au Lieutenant Doren, mon officier commandant. Il voudra votre rapport en priorité."}, {"Tuer 10 Commandos et 6 Réducteurs de têtes de Kurzen. Rendre compte au Lieutenant Doren au Camp rebelle."}},
[575] = {"L'offre et la demande", {"“Le meilleur artisan du cuir des mers du Sud et des deux mondes, Ancien et Nouveau”. C’est comme ça qu’on m’appelle. Moi ! Drizzlik ! Le meilleur artisan… Hem. Bon.","Mes objets en cuir sont réputés dans le monde entier pour leur perfection – le vrai choix des connaisseurs !","Je viens de recevoir une commande du directeur Riddlevox du Syndicat des Artisans. Il veut une douzaine de ses bottes préférées, le modèle Excelsior (notre meilleure vente, soit dit en passant.)","Pour mettre en place la forme, j’ai besoin de peaux de crocilisques. Vous trouverez les meilleurs spécimens le long des rivières, dans le nord de Strangleronce."}, {"Apporter 2 Grandes peaux de crocilisque des rivières à Drizzlik, à Baie-du-Butin."}},
[576] = {"Gardez un œil dehors", {"Eh, qui va là ?","Argh, je ne vaux rien sans mon œil enchanté spécial. J'aurais pas dû perdre mes yeux à la guerre pour commencer ! Mais par chance, nous avons débarqué à Menethil et un sorcier de passage a enchanté un œil de verre spécial qui me permet de voir à nouveau.","La nuit dernière, je me suis retrouvé agressé par des boucaniers de la Voile sanglante alors que je rentrais. Ils ont pris mon œil, oh oui ! On dirait que ces types se sont vite adaptés à Baie-du-Butin.","Vous voulez aider un vieux loup de mer à récupérer son œil ?"}, {"Dizzy le Borgne de Baie-du-Butin veut que vous récupériez son œil chez les boucaniers de la Voile sanglante."}},
[577] = {"Un peu d'assemblage est nécessaire", {"Bien, la forme des bottes est finalisée et elles se présentent bien, mais les peaux que vous avez amenées ne suffiront pas pour l'intérieur.","\"Alors que faire, Drizzlik ?\"... La question vous brûle sûrement les lèvres, et n'est-ce pas heureux que vous parliez à Drizzlik justement, parce qu'il va vous dire ce que l'on va faire !","On ne pourra jamais vraiment voir l'intérieur, alors on va juste récupérer de la peau plus épaisse et moins onéreuse sur les crocos autour du lac Nazfériti pour ça."}, {"Apporter 5 Peaux de Gueule d'acier à Drizzlik, à Baie-du-Butin."}},
[578] = {"La Pierre des marées", {"Si un mage gobelin et des élémentaires d'eau hantent effectivement l'île, cela peut vouloir dire que la Pierre des marées peut être mienne !","Je veux que vous localisiez cette île hantée… Découvrez ce qui s'y passe. Oh ! et si vous voulez en savoir plus sur la Pierre des marées, donnez ce titre à la bibliothèque de Stormwind, ils devraient être capables de vous trouver une copie du livre dans lequel j'ai eu vent pour la première fois de cette légende.","Serait-ce possible que ce Gazban ait vraiment découvert la Pierre... ?"}, {"Localiser l'Ile hantée pour le baron Revilgaz de Baie-du-Butin."}},
[579] = {"La Bibliothèque de Stormwind", nil, nil},
[580] = {"La bibine perdue de Whiskey Slim", {"On s'hum'ctait l'gosier féroc'ment, au large des Hauts-Surplombs, c' bonnes vieilles Hinterlands dans l' lointain. Cru qu'les mers allaient tous nous bouffer.","L'vieux Slim en c'nait un bout en bravoure à 40°. L'cap'tain' 'vait une caiss' de porto Pupellyverbos réquis'tionnée à un corsair' s'rendan' ta Stormwind.","'Lors quand c't' orage prè' des Hinterlands s'est pointé, j'ai fait mon enquête. Une grosse vague a visité l'endroit et a invité les bouteilles à la gaude. L'capt'aine va m' faire tâter du fouet si vous m'aidez pas à les ram'ner."}, {"Whiskey Slim de Baie-du-Butin veut que vous lui rameniez les bouteilles de porto Pupellyverbos qu'il a perdues."}},
[581] = {"La chasse pour Yenniku", {"Un sort, c'est ça. Un sort lancé sur le plus jeune fils de mon chef, Yenniku. Comme la coutume l'exige chez notre peuple, on l'a laissé chez les trolls Gurubashi de Zul'Gurub, mais après que ma tribu, la tribu Darkspear, a quitté les lieux en compagnie de l'orc Thrall, nous n'avons plus revu ce pauvre Yenniku. On m'a envoyé à Strangleronce depuis la lointaine Orgrimmar, pour rechercher le fils de mon chef.","Et je crois que la tribu des Bloodscalp le détient.","Si vous vouliez m'aider, partez à la chasse aux Bloodscalp. Vous pouvez les trouver au nord. Prenez leurs défenses et revenez me voir."}, {"Amener 9 Défenses de Bloodscalp à Nimboya au campement Grom'gol."}},
[582] = {"La chasse aux têtes", {"Nous devons découvrir si Yenniku est mort.","Tuez les Chasseurs de têtes Bloodscalp et prenez leurs Têtes réduites. Vous devez en réunir beaucoup, énormément, pour que je puisse m'assurer que celle de Yenniku n'est pas parmi elles.","Et si Yenniku a été tué par les Bloodscalp, que l'esprit de leur chef Gan'zulah soit maudit pour les 600 ans à venir !","Partez, <nom>. Vous pourrez trouver de nombreux Chasseurs de têtes aux ruines de Zuuldaia et à celles de Zul'Kunda, le long de la côte au nord."}, {"Apporter 20 Têtes réduites à Nimboya au campement Grom'gol."}},
[583] = {"Bienvenue dans la jungle", {"Bienvenue à Strangleronce !","Peut-être n'êtes-vous pas au courant, mais ce nain, là, n'est autre qu'Hemet Nesingwary, le seul et l'unique, héros de guerre renommé de l'Alliance, et maître de la chasse au gros gibier. Il n'est pas homme à accueillir facilement les <étrangers/étrangères> mais vous semblez avoir vécu quelques péripéties déjà, <classe>.","Etant un vétéran de bien nombreuses batailles, Maître Nesingwary a un point faible pour ses collègues.","Allez lui parler. Il vous donnera peut-être quelques conseils de chasse."}, {"Parler à Hemet Nesingwary."}},
[584] = {"Les têtes du clan Bloodscalp", {"La tête de Yenniku n’était pas parmi celles que vous avez apportées, et je remercie le Grand Serpent pour cela. Mais nous n'avons pas progressé dans nos recherches pour le retrouver.","Nous devons connaître la vérité, et je crains que seuls le Chef du clan Bloodscalp et son Sorcier la connaissent. Ce sont les ennemis de la tribu Darkspear et ils n'avoueront rien d'eux-mêmes, aussi nous devons extraire la vérité de leurs lèvres mortes.","Apportez-moi les têtes de Gan'zulah et de Nezzliok le Redoutable, et placez-les dans ce chaudron.","Ils sont loin dans les profondeurs de Zul'Kunda. Apportez-moi leurs têtes... nous les ferons alors parler."}, {"Apporter la Tête de Gan'zulah et la Tête de Nezzliok dans le Chaudron bouillonnant au campement Grom'gol."}},
[585] = {"En parlant avec Nezzliok", {"<La tête de Nezzliok parle.>","C'est vrai. Je sais où l'enfant Darkspear a été emmené. Vous m'avez battu et avez capturé mon esprit, mais je ne dirai rien sans apaisement !","Les Bloodscalp sont en guerre contre les Skullsplitter. Pour obtenir mes faveurs, volez aux Skullsplitter 3 de leurs crânes trophées et apportez-les ici avec nous, pour que mon esprit ait des esclaves dans le Néant. Un crâne doit provenir de la collection de trophées de Balia'mah, un autre de celle de Ziata'jai, et le dernier de celle de Zul'Mamwe."}, {"Apporter un Trophée de Balia'mah, un de Ziata'jai et un de Zul'Mamwe dans le Chaudron bouillonnant au campement Grom'gol."}},
[586] = {"En parlant avec Gan'zulah", {"<La tête de Gan'zulah parle.>","Soyez maudit ! Et vos ancêtres aussi !","Seuls le sang et la revanche peuvent calmer ma rage, alors si tel est votre souhait… faites ceci :","Jetez-vous au coeur des Skullsplitter à l'est. Massacrez les plus féroces d'entre eux, et si vous survivez, mesurez-vous à leur chef, Ana'thek le Cruel. Ha ! Apportez-moi son armure brisée si vous le pouvez !","Puisse-t-il vous démembrer et laisser votre cadavre pourrir sur place."}, {"Tuer 8 Chasseurs, 6 Chasseurs de têtes, et 4 Berserkers du clan Skullsplitter.","Apporter l'Armure brisée d'Ana'thek au Chaudron bouillonnant au campement Grom'gol."}},
[587] = {"Faire un tabac", {"Ah ! La vie en mer. Je ne vais pas dire que c'est la bonne vie, mais c'est ma vie.","Lorsque j'arrive au port, j'aime me trouver une bonne bouteille de tafia et du tabac à priser de qualité. Des habitudes dégoûtantes, mais que serait un pirate sans ses vices, pas vrai ?","Alors vous pouvez imaginer mon dépit lorsque j'ai découvert que l'herboriste de la ville était parti et avait vendu sa marchandise. Dire que son chargement a été détourné par ces fichus Ecumeurs de la Voile sanglante.","Je parie, <classe>, que vous pourriez les convaincre de partager, non ?"}, {"Deeg de Baie-du-Butin veut que vous lui apportiez 15 portions de Tabac à priser."}},
[588] = {"Le sort de Yenniku", {"<Nezzliok parle.>","Vous nous avez apaisés moi et mon chef, aussi vais-je vous dire ce que vous souhaitez savoir…","Le jeune Yenniku est perdu pour son clan.","Il a été emmené par le sorcier-docteur, Zanzil le Paria. À présent Zanzil le contrôle, de corps et d'esprit, et seule la magie la plus puissante pourrait le libérer.","Une magie dont nul <classe> ne peut disposer.","Peut-être vos alliés Darkspear peuvent vous offrir un espoir de salut pour Yenniku. Mais ça, j'en doute."}, {"Parler à Kin'weelay."}},
[589] = {"Les Eclats vibrants", {"Les légendes parlent d'un bijou, caché loin au cœur de Strangleronce. Nous l'appelons la Vue de l'Esprit, et il détient de grands pouvoirs. Si vous trouvez ce bijou, alors, peut-être, nous pourrions ramener Yenniku des ténèbres.","Je vais parler aux esprits de la jungle pour localiser la Vue de l'Esprit. Tandis que je m'en occupe, allez dans la Mine aux cristaux, au sud-est d'ici, et récupérez des éclats vibrants bleus des basilics Gueule de fer. Ces éclats sont rares, mais nous aurons besoin de leur magie pour faire plier la Vue de l'Esprit à notre volonté."}, {"Apporter 3 Eclats vibrants bleus à Kin'weelay au Campement Grom'gol."}},
[590] = {"Un marché de voleur", {"Quoi ?","Quoi ?","Vous voulez de l'argent ? Je ne vous dois pas un sou.","Une lettre ? Quelle lettre ? Oh ! Vous voulez vous battre, alors ? Très bien, <mon pote/ma fille>… Allons-y ! Je vous dois de l'argent…"}, {"Battre Calvin Montague au Glas."}},
[591] = {"La Vue de l'Esprit", {"La Vue de l'Esprit est gardée par un mage ogre, à l'est d'ici et loin au cœur du tertre des ogres Mosh'Ogg. Son nom est Mai'zoth. Je le connais de l'époque où les Darkspear vivaient à Strangleronce. Il est vicieux, et sa magie est puissante.","Et s'il possède la Vue de l'Esprit, alors sa magie le sera encore plus.","Vous devez combattre Mai'Zoth et lui arracher la Vue de l'Esprit. C'est une tâche périlleuse, mais c'est le seul espoir que nous avons de sauver Yenniku."}, {"Apporter la Vue de l'Esprit à Kin'weelay au Campement Grom'gol."}},
[592] = {"Il faut sauver Yenniku", {"J'ai façonné la Vue de l'Esprit dans une Gemme d'âme. Cette gemme possède deux fonctions : vous devez d'abord l'utiliser pour étourdir Yenniku et, une fois qu'il est inconscient, placer la gemme sur sa tête afin d'emprisonner son âme.","Puis apportez la gemme à Nimboya.","Yenniku demeure en compagnie de Zanzil le Paria le long du Rivage de cristal du lointain cap Strangleronce."}, {"Apporter la Gemme d'âme remplie à Nimboya."}},
[593] = {"Une âme pour la Gemme", nil, nil},
[594] = {"Message dans une bouteille", {"La note dit, d'une écriture plutôt élégante :","A quiconque peut m'aider,","Je vous en prie, j'ai désespérément besoin d'aide. Un grand Crin-de-ciel me détient captive sur une île des mers du Sud.","Les étoiles, la nuit, me sont étrangères et sont obscurcies par les arbres de la jungle. Mais les jours les plus clairs, je distingue le Rivage cruel au nord-ouest, et ce qui semble être de grands vaisseaux au sud-ouest.","Je vous en supplie, mon possible sauveur, venez m'aider."}, {"Trouver la main qui a écrit ce message lancé à la mer dans une bouteille."}},
[595] = {"Les boucaniers de la Voile sanglante", {"Vous avez l'air de vouloir travailler, <mon pote/ma belle>. J'ignore pourquoi des <classe>s viendraient offrir leur aide aux Ecumeurs des Flots noirs, mais je ne vais pas la refuser.","Notre bateau vient juste d'arriver à Baie-du-Butin et on dit que nos rivaux, ces abjects boucaniers de la Voile sanglante, ont été aperçus partout au nord de Strangleronce.","Tous les gars sont trop occupés à boire et à courir les jupons ! J'ai besoin que quelqu'un aille voir, <nom>.","Allez à la crique au nord de Baie-du-Butin, le long de la Côte sauvage et voyez ce que vous pouvez trouver."}, {"Le Premier Officier Crazz veut que vous contrôliez la crique au nord de Baie-du-Butin."}},
[596] = {"Les Colliers d'os ensanglantés", {"J'ai été envoyé à Grom'gol pour aider Nimboya dans sa quête, mais j'ai aussi des raisons personnelles d'être là.","La tribu des Bloodscalp est l'ennemie de la nôtre. Collecter les trophées de nos ennemis nous donne du pouvoir sur eux. Vous pouvez aider notre tribu en chassant nos ennemis.","Ils vivent au nord. Massacrez des trolls Bloodscalp et prenez leurs Colliers d'os ensanglantés. Trouvez-en beaucoup, et grande sera votre récompense."}, {"Apporter 25 Colliers d'os ensanglantés à Kin'weelay au Campement Grom'gol."}},
[597] = {"Les boucaniers de la Voile sanglante", {"La carte montre Baie-du-Butin avec un \"X\" rouge dessiné précisément à l'endroit où vous vous trouvez.","Le texte gribouillé dit :","\"Vous devez préparer un camp à cet endroit et rester tranquilles. Attendez le signal du poste de commandement du sud avant d'attaquer. Si quiconque approche, tuez-le.\""}, {"Rendre compte de vos découvertes au Premier Officier Crazz à Baie-du-Butin."}},
[598] = {"Collier d'os brisés", {"À présent que vous avez collecté les trophées des Bloodscalp, j'ai une autre tâche pour vous. La tribu des trolls Skullsplitter vit au sud-est, et nous sommes en guerre contre eux depuis des années. Bien que les Darkspear ne vivent plus à Strangleronce, nous sommes toujours en conflit avec les Skullsplitter.","Aussi, chassez-les. Collectez leurs Colliers d'os brisés et revenez me voir. Aidez-nous dans notre guerre, et vous gagnerez l'amitié des Darkspear."}, {"Apporter 25 Colliers d'os brisés à Kin'weelay au Campement Grom'gol."}},
[599] = {"Les boucaniers de la Voile sanglante", {"Bon sang ! Ainsi les boucaniers de la Voile sanglante préparent une attaque contre Baie-du-Butin ? Eh bien ! Ces crapules ne manquent pas d'air. Mais Baie-du-Butin restera dirigée par les Ecumeurs des Flots noirs !","C'est plus grave que je ne le croyais. Allez dire ce que vous avez appris à l'amiral Seahorn immédiatement ! Ce vieux loup de mer est quelque part en ville..."}, {"Rendre compte de l'attaque que prépare la Voile sanglante à l'amiral Seahorn."}},
[600] = {"La mauvaise mine de la KapitalRisk", {"La KapitalRisk possède un réseau d'opérateurs à travers Strangleronce qui empêche les gobelins durs à la tâche, comme moi, de faire de l'or honnête ! S'il vous plaît, vous devez m'aider !","La KapitalRisk exploite une mine près de la mine aux cristaux, au nord. Ils ne peuvent pas entrer dans la mine à cause de nombreux basilics, mais ils peuvent toujours extraire des cristaux chantants des collines environnantes.","Dérobez leurs cristaux, et montrez-leur que la jungle ne leur appartient pas.","Et... euh... apportez-moi ces cristaux comme preuve !"}, {"Apporter 10 Cristaux bleus chantants à Crank Fizzlebub, à Baie-du-Butin."}},
[601] = {"Les élémentaires d'eau", {"Les élémentaires d'eau étaient sans nul doute invoqués par Gazban, le sorcier gobelin dont la rumeur dit qu'il a découvert la Pierre des marées. Comme il se peut que vous le sachiez, cette Pierre lance une malédiction sur son porteur, le faisant apparaître et disparaître...","Peut-être pouvons-nous trouver un moyen de neutraliser cette malédiction. Nous pouvons demander à un mage de Dalaran d'analyser le résidu magique des bracelets de l'un des élémentaires d'eau. J'ai besoin que vous retourniez sur l'île et que vous rapportiez quelques échantillons."}, {"Acquérir 6 Bracelets d'élémentaires d'eau pour le baron Revilgaz à Baie-du-Butin."}},
[602] = {"Analyse magique", {"D'après ce que j'ai entendu, les mages de Dalaran sont très occupés à reconstruire leur ville détruite, mais je suis sûr que vous en dénicherez un qui donnera un peu de son temps pour regarder les bracelets. S'il y a une chose que je sais à propos des mages, c'est qu'ils ne peuvent résister à une énigme magique.","Prenez le sac de bracelets d’élémentaire d'eau et voyez s'ils peuvent apporter quelque lumière sur la malédiction de la pierre des marées. Cherchez tout particulièrement à rencontrer l'archimage Ansirem Runeweaver. J'ai déjà eu affaire à lui par le passé, et grand est son savoir."}, {"Apporter les Bracelets d’élémentaire d'eau à l'archimage Ansirem Runeweaver à Dalaran."}},
[603] = {"La Clé d'Ansirem", {"Le mage qui analyse la Pierre amplifie son pouvoir à travers un point focal… Un autel d'un certain genre. Vous devriez pouvoir le mettre hors d'état en le surchargeant.","Et de fait, je sais ce qu'il vous faut : une dague ancienne, une relique des trolls. Il n'y a qu'un... léger problème.","Vous voyez, j'ai trouvé la dague tandis que j'effectuais des recherches à Strangleronce, mais je l'ai donnée à ma fille entêtée, Catelyn, qui est partie à Baie-du-Butin pour devenir pirate."}, {"Parler à Catelyn à Baie-du-Butin."}},
[604] = {"Les boucaniers de la Voile sanglante", {"Crazz parle en termes flatteurs de votre dévouement envers les Ecumeurs des Flots noirs. Je vais vous charger d'une mission très importante pour la flotte.","Infiltrez le prétendu poste de commande méridional que les boucaniers de la Voile sanglante ont établi sur le Rivage cruel. Faites une incursion dans le camp. Apportez-moi leurs cartes et leurs ordres écrits. Nous verrons exactement ce que cette vermine prépare."}, {"L'amiral Seahorn à Baie-du-Butin veut que vous tuiez 10 Bretteurs de la Voile sanglante et que vous rapportiez les Cartes de la Voile sanglante et les Ordres de la Voile sanglante."}},
[605] = {"Les éclats bleus chantants", {"Les cristaux chantants sont uniques à Strangleronce, et très précieux pour certains. Je peux transporter les cristaux, mais cette maudite KapitalRisk fait en sorte qu'un honnête entrepreneur comme moi ne puisse pas en collecter.","J'aimerais vous embaucher.","Les basilics, à Strangleronce, mangent les cristaux. C’est ce qui durcit leur peau, et parfois, on peut récolter sur eux du cristal de bonne qualité.","Vous pouvez en trouver sur n'importe quel basilic, mais les moins terribles se trouvent le long du rivage, plus au nord."}, {"Apporter 10 Eclats de cristal chantant à Crank Fizzlebub."}},
[606] = {"Effrayer Philippe le Chancelant", {"Si quelque chose m'énerve, c'est bien les gens qui ne paient pas leurs dettes de jeu. Et beaucoup me doivent de l'argent. Vous pourriez m'aider à le récupérer ?","D'abord, il y a Phillippe le Chancelant, en bas, près des docks. Il me doit beaucoup depuis la semaine dernière et je veux mon dû. Mais pour ça, il va falloir l'effrayer.","Sortez de la ville et chassez des Anciens gorilles de Valbrume. Trouvez de bons abats, et montrez-lui. Ne dites pas qu'ils proviennent d'animaux, laissez-le s'imaginer le pire."}, {"Apporter 5 Abats de Valbrume à Phillippe le Chancelant à Baie-du-Butin."}},
[607] = {"De retour chez MacKinley", {"Tenez, prenez cette bourse et donnez-la à Loup des Mers. Et dites-lui que je comptais le rembourser ! Je le jure !","J'étais juste un peu saoul et je me suis perdu et… vous savez comment sont les choses. Il comprendra, vous n'avez pas besoin de devenir <brutal/brutale>."}, {"Apporter le Paiement de Philippe le Chancelant à MacKinley \"Loup des mers\"."}},
[608] = {"Les boucaniers de la Voile sanglante", {"Maudit soit le toupet de Firallon ! Il ne pourrait même pas diriger une yole sur le lac de Cristal, et encore moins mener une flotte entière contre nous.","Les boucaniers de la Voile sanglante vont payer leur négligence. À présent que nous connaissons leur plan d'attaque, nous pouvons les contrer avant même qu'ils ne sachent ce qui les frappe.","Mais pourquoi envoyer la flotte entière faire le travail d'un courageux groupe d'aventuriers ? <nom>, dirigez certains de vos meilleurs camarades au mouillage de Firallon et assassinez les dirigeants de la Voile sanglante. Leur flotte coulera peu après."}, {"L'amiral Seahorn de Baie-du-Butin veut que vous tuiez le capitaine Stillwater, le capitaine Keelhaul et l'amiral Firallon."}},
[609] = {"Gages vaudou", {"Ma liste se réduit, mais certains me doivent encore de l'argent.","Après, il y a Maury \"Pied bot\" Wilkins, Jon-Jon le Corbeau et Chucky \"Dix pouces\".","Les dettes de ces chiens remontent à des mois et je pensais qu'ils avaient quitté la ville pour éviter de payer ! Plus tard, j'ai appris qu'ils étaient maudits et ensorcelés. À présent, ils courent la jungle. Mais je me fiche de leur sort, je veux récupérer ce qui m'appartient !","Ils se trouvent dans les ruines d'Aboraz et dans les ruines de Jubuwai, au nord-est. Trouvez-les, et ramenez-moi mon dû."}, {"Apporter le Fléau de Maury, la Longue-vue dorée de Jon-Jon, et le Grand anneau de Chucky à MacKinley \"Loup des mers\" à Baie-du-Butin."}},
[610] = {"Duncan \"le Beau garçon\"", {"Mon PÈRE vous envoie ? J'aurais cru qu'il m'aurait totalement oubliée.","Ce n'est pas vraiment sa faute si je suis partie, il ne pouvait simplement pas imaginer que je ne voudrais pas vivre une vie ennuyeuse, à lire et relire des livres moisis et à devenir un de ces vieux mages livides aux cheveux blancs !","Si ma dague vous intéresse, vous devrez la récupérer chez Duncan le \"Beau garçon\", l'un des boucaniers de la Voile sanglante. Plantez quelque chose entre ses épaules et je suis certaine qu'il y renoncera."}, {"Récupérer la Dague de Catelyn auprès de Duncan le \"Beau garçon\"."}},
[611] = {"La malédiction des marées", {"Ainsi vous recherchez un autel d'un certain genre ? Eh bien ! Je ne suis pas experte en ruines de trolls comme peut l'être mon père, mais j'ai entendu parler d'une cité troll engloutie près de cette île que vous cherchez. Peut-être devriez-vous essayer là-bas ?","Vous avez récupéré mon couteau, alors je suppose que vous pouvez l'emprunter... Mais je veux que vous me le rendiez quand vous aurez fini !"}, {"Détruire l’Autel des Marées avec la Lame de Catelyn, tuer Gazban et rapporter la Pierre des Marées et la Lame de Catelyn au baron Revilgaz."}},
[613] = {"Le pied-bot de Maury", {"Malédiction ! Ce malade de Maury a mis un verrou sur son pied-bot, et je ne peux pas l'ouvrir ! Son butin est à l'intérieur, et c'est ce qu'il me doit !","Nous devons trouver la clé pour ouvrir le verrou. Maury était connu pour jouer avec les ogres de Mosh'Ogg, sur leur colline au nord-est. Et il m'a dit un jour que la dernière fois qu'il était allé là-bas, il avait été coursé après avoir trop gagné. Peut-être que ces ogres ont sa clé.","Mais s'ils l'ont, il se peut que vous deviez en tuer beaucoup avant de trouver celui qui la détient..."}, {"Apporter la Clé de Maury à MacKinley \"Loup des mers\" à Baie-du-Butin."}},
[614] = {"Le coffre du capitaine", {"Dans le passé, je pilotais un navire d'ici jusqu'à Ratchet, en Kalimdor. J'étais un grand capitaine avec une solide réputation.","Jusqu'à ce que…","Ces maudits géants des mers m'apportent la ruine ! Ils ont détruit mon bateau, tué mon équipage, et m’ont abandonné dans un canot. Et l'un d’eux, Gorlash, a volé mon coffre de capitaine. Il a dit qu'il le voulait pour en faire une tabatière. Quel toupet !","Je sais que Gorlash sillonne la côte à l'est d'ici. Trouvez-le et récupérez mon coffre ! Faites-le, et vous gagnerez la gratitude d'un capitaine."}, {"Apporter le Coffre au capitaine Hecklebury Smotts à Baie-du-Butin."}},
[615] = {nil, nil, nil},
[616] = {"L'île hantée", {"Le baron Revilgaz m'a chargé de laisser traîner une oreille sur les rumeurs concernant les événements étranges ou surnaturels qui surviennent à Baie-du-Butin et à Strangleronce. Je ne donne généralement pas beaucoup de crédit aux histoires de fantômes, mais j'ai entendu des marins à la taverne parler d'une île hantée près de la côte. Des élémentaires d'eau et un fantôme de gobelin qui divague, qu'ils disent.","Si vous allez parler à Revilgaz, pourriez-vous lui faire part de cette histoire ?"}, {"Parler au baron Revilgaz, à Baie-du-Butin."}},
[617] = {"De derrière les fagots", {"Vous cherchez du boulot, <nom> ? J'en ai p'têt' si vous vous sentez <prêt/prête> à relever un p'tit défi.","J'ai un ami, nommé Groy, qui fait un malheur en vendant c'que les nagas appellent des roseaux akiris : c't'un genre de roseau marin qu'a d'la valeur. Je n'sais pas trop à quoi ça sert, mais on paye cher pour des lots d'ce truc.","Apportez-m'en dix tiges, et nous ferons des affaires. Les nagas ont été vus dernièrement le long de la plage, au nord."}, {"Apporter 10 Tas de roseaux akiris au corsaire Bloads à Baie-du-Butin."}},
[618] = {"Face à Negolash", {"Negolash est un goinfre.","Quand les géants ont détruit notre navire, il est venu directement dans ma cuisine. Il n'avait pas faim d'hommes d'équipage comme les autres géants. Negolash voulait du vin et mes ailes de busard grillées au barbecue. C'est ma spécialité, et j'ai dû me rendre jusqu'aux Terres ingrates pour avoir la recette !","Si vous voulez tendre un piège au géant, trouvez du vin et un monceau d'ailes de busard. Placez-les dans notre vieux canot, au sud-est, le long de la côte. Quand Negolash sentira cette bonne nourriture, il rappliquera, sans aucun doute !"}, {"Apporter 10 Ailes de busard grillées et 5 bouteilles de Vin de Junglevine dans le Canot de sauvetage du capitaine Smotts.","Tuer Negolash, et apporter le Coutelas de Smotts au capitaine Smotts, à Strangleronce."}},
[619] = {"Allécher Negolash", nil, nil},
[621] = {"Le secret de Zanzil", {"Zanzil le Paria demeure avec ses partisans dans les Ruines de Jubuwai et dans celles d'Aboraz, au nord-est de Baie-du-Butin. Il a été banni de sa tribu. Pourquoi ? Eh bien !…","Zanzil a le pouvoir de soumettre la volonté des autres, et ses chefs ont peur de lui. Il utilise un mélange de plantes tropicales pour supprimer la volonté et endurcir le corps de ses partisans.","Cette préparation intéresse beaucoup ceux qui voudraient contrôler les autres.","Apportez-moi des échantillons de ce mélange, et je leur trouverai une utilité."}, {"Apporter 12 Echantillons de Mixture de Zanzil à Crank Fizzlebub à Baie-du-Butin."}},
[622] = {"La cocotte du caporal Kaleb", {"Pendant que vous étiez <parti/partie> j'ai réparé la cocotte que vous m'aviez apportée. J'y ai même ajouté un autre sifflet à vapeur, et regardez : il y a un hachoir au fond pour réduire les os de pattes en bouillie ! Oui ! La Cocotte ébréchée de Krazek ravira tout cuisinier à 100% ! Je suis sûr que son propriétaire en sera satisfait."}, {"Apporter la Cocotte réparée de Krazek au caporal Kaleb, au Camp rebelle."}},
[623] = {"De derrière les fagots", {"<nom>, puisque vous êtes là : j'ai reçu un message de Groy pendant que vous étiez <parti/partie>. Il dit être satisfait de la dernière livraison, mais il a un client qui en désire plus, et tout de suite.","Je ne vais pas à Theramore avant une semaine, mais si vous faites le voyage, vous pourriez transporter la marchandise pour moi.","Un peu d'aventure vous tente ? Vous savez où se trouve Âprefange, n'est-ce pas ? Je ferais attention quand même, Theramore peut être dangereuse si vous n’avez pas le bon profil."}, {"Apporter un Fagot de Roseaux akiris au corsaire Groy à Theramore."}},
[624] = {"Devinette de Cortello", {"Il est un pont fleuri, beaucoup emprunté","À l'est de Deuillevent et au sud des Carmines","Dessous, quelque chose vous attend","Certains l'appellent allusion, d'autres indice","Il reposera là pour d'éternels lendemains","Prenant son temps dans le marais des Chagrins."}, {"Résoudre l'énigme."}},
[625] = {"Devinette de Cortello", {"Une terre lointaine en Kalimdor attend","Où le péril a brisé espoirs et destinées","À l'est des Tarides le temps est sévère","Ténèbres et dangers criblent le marécage d'Âprefange","Mais là est un indice pour ceux qui prennent le risque","Attendant d'être découvert dans une caverne au sud-ouest."}, {"Résoudre l'énigme."}},
[626] = {"Devinette de Cortello", {"A Lordaeron ! La grande terre de bouleversements","Où le soldat courageux fait de la mort son métier","Trouvez votre chemin à travers l'agitation","Vous trouverez le fin mot de la quête","Le long de la côte des Hinterlands, au pied des grandes chutes","Sous l'eau, c'est là que votre trésor vous appelle !"}, {"Résoudre l'énigme."}},
[627] = {"Un service pour Krazek", {"Il y a une caverne dans la montagne, dans la gorge des Sèches-moustaches, loin au nord dans les hautes-terres d'Arathi. Cette caverne abrite un minerai rare : la pierre de sang. Celle de cette caverne n'est pas de la meilleure qualité, mais elle a quand même une certaine valeur...","J'en aimerais un peu.","Je vais réparer votre cocotte, mais pendant que j'y travaille, allez chercher des fragments de pierre de sang. Apportez-m'en assez, et je vous donnerai un bonus.","Et si vous n'avez pas la capacité de l'extraire... trouvez quelqu'un qui le peut !"}, {"Apporter 4 chargements de Minerai de pierre de sang inférieur à Krazek à Baie-du-Butin."}},
[628] = {"L’Excelsior de chez Drizzlik", {"Maintenant que l'intérieur est terminé, tout ce qui reste à faire est la couche extérieure. Pour ça, je n'utilise que les meilleures peaux de vieux crocis. Elles sont plus souples et solides, avec de belles écailles ; toutes les qualités qui définissent la gamme Excelsior de Drizzlik.","Il est difficile de trouver des peaux parfaites, et celles qui me conviennent le mieux proviennent des vieux crocilisques marins. Les vieux ne sortent que pour défendre les autres crocis marins, alors il faudra en occire quelques-uns avant d’attirer un vieux."}, {"Apporter une Peau d'ancien crocilisque à Drizzlik, à Baie-du-Butin."}},
[629] = {"Le Récif infâme", {"Il y a longtemps, un grand tremblement de terre engloutit une ancienne cité troll sous les eaux de la Côte sauvage au nord-ouest. Nous appelons cet endroit le Récif infâme, car ce sont les murlocs qui à présent résident dans les ruines de la cité, attaquant tous ceux qui s'en approchent.","Une vieille tablette repose parmi ces ruines, racontant l’histoire du héros troll Gri'lek. Cette légende est sacrée pour la tribu Darkspear, et bien que la tablette soit trop lourde pour qu'on puisse la bouger, j'en veux un fragment pour le conserver dans notre nouvelle demeure, à Orgrimmar."}, {"Apporter un Fragment de tablette à Kin'weelay au Campement Grom'gol."}},
[630] = {"Message dans une bouteille", {"<nom>, je remets mon sort entre vos mains et entre celles de tous ceux que vous pourrez réunir pour me libérer. Le roi Mukla, le grand Crin-de-ciel, est mon ravisseur. Il commande aux bêtes de l'île et me retient contre ma volonté. Si vous pouviez le combattre et trouver la clé qui ouvre mes chaînes, je vous serais grandement redevable.","Je l'entends souvent dans la partie sud-est de l'île, mais je vous en prie, soyez <prudent/prudente>, il ne doit pas être abordé à la légère."}, {"Tuer le roi Mukla, trouver la clé et libérer la princesse Poobah."}},
[631] = {"Le viaduc de Thandol", {"Une explosion monstrueuse a grondé à travers les collines. C'tait horrible, j'vous l'dis !","De bons soldats sont morts en défendant le viaduc de Thandol. Longbraid dit que nous prendrons notre revanche sur les Sombrefer avant que tout cela ne soit fini.","Ici, il n’y a que des blessés, et je ne vois aucun renfort arriver à l'horizon. Longbraid a envoyé le vieux Rustlocke en reconnaissance au pont à l'est, pour voir si nous pouvions le sécuriser.","C'était il y a plusieurs jours. Avec Dun Modr en état de siège, nous n'avons pas les ressources d’aller le chercher.","Peut-être pourriez-vous le trouver..."}, {"Rhag Garmason veut que vous retrouviez le vieux Rustlocke."}},
[632] = {"Le viaduc de Thandol", {"Il semblerait que le pauvre vieux Rustlocke soit tombé dans une embuscade de Sombrefer. Vous remarquez un parchemin froissé dans son poing serré.","Apportez la triste nouvelle à Rhag Garmason et montrez-lui ce que vous avez trouvé."}, {"Rendre compte à Rhag Garmason et lui montrer le parchemin que vous avez trouvé sur le corps de Rustlocke."}},
[633] = {"Le viaduc de Thandol", {"Par le Ciel, non ! Pauvre Rustlocke ! C'est une tragédie !","Nous ne devons pas laisser sa mort impunie. Le parchemin qu’il tenait indique que Kam Deepfury, le cerveau derrière la première attaque, a fait entreposer des explosifs dans une cache, juste de l'autre côté du viaduc de Thandol, dans les hautes-terres d'Arathi.","Si ces explosifs atteignent le pont, notre principale ligne d'approvisionnement vers le nord sera très compromise. La cache doit être détruite, <nom>. Revenez me voir lorsque votre mission sera accomplie.","Vous êtes notre seul espoir."}, {"Détruire la cache d'explosifs."}},
[634] = {"L'appel à l'Alliance", {"J'aimerais vous demander un autre petit service, <classe>. Nous attendons les troupes de réserve d'Ironforge. Cependant, Roggo connaît l'existence d'un camp de l'Alliance, au nord sur les hautes-terres d'Arathi, au Refuge de l'Ornière.","Allez-y et cherchez le capitaine Nials. Demandez-lui si l'Alliance peut nous prêter quelques soldats pour nous aider à reprendre Dun Modr et le viaduc de Thandol."}, {"Chercher le capitaine Nials au Refuge de l'Ornière."}},
[635] = {"Le cristal dans les montagnes", {"L'éclat vibre tandis que vous le tenez…","Vous ressentez comme un appel, comme si quelqu'un essayait de communiquer, quelqu'un qui avait désespérément besoin de votre aide.","Une vision apparaît dans votre esprit. Une vision des monts Arathi, et d'un cristal brillant saillant de la terre, là-bas.","Vous vous sentez <attiré/attirée> par cet endroit."}, {"Trouver le cristal de votre vision."}},
[636] = {nil, nil, nil},
[637] = {"Lettre de Sully Balloo", {"L'enveloppe imbibée d'eau et déchirée se dissout et laisse dans vos mains quelques pages d'une lettre, écrite avec soin.","La lettre est adressée à Mme Sara Balloo d'Ironforge et commence par les mots : \"Ma chère Sara…\""}, {"Donner la Lettre de Sully Balloo à Sara Balloo."}},
[638] = {"Trollbane", {"Thoras Trollbane étant mort, et Stromgarde en ruine, Vol'jin a donné pour instructions que nous mettions tout en œuvre pour dérober l'épée légendaire Trol'kalar, qui est aux mains des humains, afin de l'utiliser dans nos futurs combats contre les trolls de Zul'Gurub.","Zengu, l'un de nos chasseurs aguerris, a été envoyé au Trépas-d'Orgrim, dans la partie nord-est des hautes-terres d'Arathi, pour veiller à son obtention."}, {"Aller au Trépas-d'Orgrim dans les hautes-terres d'Arathi et parler à Zengu."}},
[639] = {"Glyphe de Strom", {"Dans des temps anciens, les trolls se battaient contre l'Empire arathorien et notre plus vaillant adversaire était le seigneur Ignaeus de Strom, dit Trollbane. Il se lançait dans la bataille en brandissant Trol'kalar, ce qui dans leur langue voulait dire \"Tueuse de trolls\".","Mais aujourd’hui les fils de Trollbane sont affaiblis et leur forteresse en ruine.","L'épée est protégée par de nombreux glyphes qui ont été répartis entre les survivants de Stromgarde, et nous devons les retrouver.","Le premier a été pris par le Syndicat, l'un de leurs hommes à Stromgarde devrait l'avoir."}, {"Trouver le Glyphe de Strom pour Zengu au Trépas-d'Orgrim."}},
[640] = {"Le Glyphe qui fut brisé", {"La tombe de Thoras Trollbane est protégée par trois glyphes. Vous avez récupéré le premier auprès du Syndicat. Les deux autres seront plus difficiles à obtenir, car ils sont détenus par la milice fortement retranchée de Stromgarde.","Le Glyphe de Thoradin a été brisé en onze morceaux par le fils de Thoras, Galen, et dispersé entre les défenseurs de Stromgarde. Retrouvez les morceaux et apportez-les à Tor'gan, le chaman troll, il les assemblera pour reconstituer le Glyphe complet."}, {"Reprendre les 11 Fragments de Glyphe aux défenseurs de Stromgarde, puis les apporter à Tor'gan au Trépas-d'Orgrim."}},
[641] = {"Glyphe de Thoradin", {"Le Glyphe a retrouvé son intégrité et peut désormais permettre d’ouvrir le sceau sur la tombe de Trollbane. Apportez-le à Zengu, il ne fait pas de doute qu'il a d'autres tâches à vous confier."}, {"Apporter le Glyphe de Thoradin reconstitué à Zengu, au Trépas-d'Orgrim."}},
[642] = {"La Princesse emprisonnée", {"Mon nom est Myzrael. Je suis une Princesse de la terre et mes ravisseurs, les géants, m'ont emprisonnée loin sous les hautes-terres d'Arathi. Ces éclats de cristal sont le seul moyen dont je dispose pour communiquer avec le monde de la surface.","Par pitié, aidez-moi. Les alliés des géants, les kobolds Sèches-moustaches, possèdent un de ces éclats à la gorge des Sèches-moustaches, à l'est. Pour l'activer, vous devez collecter des Poussières de Myzrael sur les kobolds, puis les y appliquer.","Je vous en supplie, <nom>, venez à mon secours !"}, {"Collecter 12 Poussières de Myzrael, puis les répandre sur les Eclats iridescents, à la gorge des Sèches-moustaches."}},
[643] = {"Glyphe d'Arathor", {"Bien qu'on puisse dire qu'il contrôle tout juste Stromgarde, et certainement pas toutes les hautes-terres d'Arathi, le prince humain fait courir la campagne aux restes de sa cavalerie. Je ne comprends pas pourquoi, mais qui peut comprendre la façon dont pensent les humains, hein ?","Le plus important, c’est qu’il ait donné l'un des Glyphes au lieutenant Valorcall, commandant sa cavalerie. Trouvez-le et rapportez-moi son Glyphe."}, {"Retrouver le Glyphe d'Arathor et l'apporter à Zengu, au Trépas-d'Ogrim."}},
[644] = {"Glyphe de Trollbane", {"Le dernier Glyphe dont nous avons besoin pour ouvrir la tombe de Thoras Trollbane est détenu par son fils en personne. Il vit loin à l'intérieur de Stromgarde, dans une chapelle des quartiers contrôlés par la milice. Le prince Galen est bien protégé et n'est pas maladroit à la guerre non plus, aussi cette tâche pourrait s'avérer très ardue."}, {"Tuer le prince Galen Trollbane et apporter le Glyphe de Trollbane à Zengu, au Trépas-d'Ogrim."}},
[645] = {"Trol'kalar", {"Qu'est-ce donc ? Les quatre Glyphes une fois réunis, ils se sont rassemblés pour n'en former qu'un seul : le Glyphe d'Ignaeus. Celui-ci pourra sans aucun doute désactiver la garde placée sur la tombe de Trollbane.","Ses restes sont ensevelis dans le Sanctuaire derrière la chapelle de Stromgarde. Rapportez Trol'kalar."}, {"Retirer Trol'kalar de la tombe de Trollbane à Stromgarde."}},
[646] = {"Trol'kalar", {"La lumière s'éteint, doucement, et l'épée Trol'kalar, reposant dans un fourreau de pierre, est libérée."}, {"Apporter Trol'kalar à Zengu au Trépas-d'Orgrim."}},
[647] = {"Le tord-boyaux de MacKreel", {"J'ai bu pas mal de mon bon vieux tord-boyaux la nuit dernière ! Longbraid aurait ma tête s'il savait que je me suis endormi pendant mon tour de garde.","L’alcool a frappé fort. Je me sentais comme si une brigade entière de machines de siège m'était passée sur le crâne, avec tous ces grondements.","Bon sang, regardez l'heure ! J'ai promis au brasseur Bilger de Southshore que je lui paierai ma dette en lui envoyant du tord-boyaux.","Mais il ne reste que 15 minutes, après je serai en retard ! Apportez-lui ce lot, s'il vous plaît ! Et dépêchez-vous !"}, {"Apporter le Tord-boyaux de MacKreel au brasseur Bilger à Southshore."}},
[648] = {"À la rescousse de OOX-17/TN !", {"Le robot émet à nouveau la voix du gnome :","\"Je dois déplacer OOX-17/TN dans un endroit dégagé et sûr pour qu'il puisse entamer sa longue procédure de décollage. Il est équipé d'un camouflage, mais il me faut du temps pour le rendre opérationnel de mon côté. J'ai besoin que vous l'escortiez jusqu'au... disons jusqu'à PortGentepression ! Ce serait un endroit parfait, et assez long, pour que tout puisse se mettre en route !\"","Escortez-le jusque là-bas, et venez me parler à Baie-du-Butin ! Oglethorpe Obnoticus - terminé !\""}, {"Escorter OOX-17/TN jusqu'à Port Gentepression, puis rendre compte à Oglethorpe Obnoticus à Baie-du-Butin."}},
[649] = {"La récupération de Houle", {"J'ai un travail de récupération pour vous, et si vous êtes un amateur de Houle comme moi, vous l'apprécierez sûrement. La Houle !","Mais c'est une boisson, imbécile.","Il reste une réserve de Houle au miel des Hinterlands dans un avant-poste abandonné de la Horde, qui date de l'époque où il fonctionnait encore. On ne peut plus trouver d’aussi bonne Houle, aujourd'hui. L'un des hommes de paille de Malton est au courant et connaît la musique. Alors, vous allez vous rendre là-bas et m'apporter une caisse de Houle emballée, et deux bouteilles pour ma collection. Parlez à Malton, il vous donnera tous les détails nécessaires."}, {"Parler à Malton Droffers à Orgrimmar."}},
[650] = {"La récupération de Houle", {"L’elfe que vous devez consulter s'appelle Gilveradin Sunchaser. Il peut identifier n'importe quel vin ou cuvée, uniquement à l'odorat ! Il peut emballer la Houle dont nous avons besoin, et c'est lui que vous devrez voir. Nous avons besoin de six bouteilles pour la livraison, de deux pour papa, et d'autant de bouteilles que l'elfe en voudra... Et il en voudra, croyez-moi ! Lorsque tout sera prêt et que vous aurez deux bouteilles pour papa, revenez me voir.","Le camp de Gilveradin est proche d'un gîte de hauts-elfes dans les Hinterlands, et ils ne sont pas commodes. Croyez-moi."}, {"Parler à Gilveradin Sunchaser. Son camp se situe dans les Hinterlands."}},
[651] = {"Les pierres de lien", {"Quatre Pierres de lien sont dispersées à travers tout Arathi. Ces pierres me retiennent prisonnière sous la terre.","Pour me libérer vous devez ouvrir la plus puissante : la Pierre de lien intérieure. Et pour cela, vous devez rassembler les clés des trois autres.","Ces pierres sont sous la surveillance de gardiens, mais si vous agissez intelligemment, ou si vous avez de la chance, vous pouvez trouver un moyen de les bannir."}, {"Collecter la Clé ardente, la Clé armoriée et la Clé de tonnerre aux lieux suivants : la Pierre de lien occidentale, la Pierre de lien orientale et la Pierre de lien extérieure.","Les apporter à la Pierre de lien intérieure."}},
[652] = {"La Pierre cruciale", {"Ouvrir les Pierres de lien a relâché l'étreinte de mes fers, mais quelque chose me retient toujours captive. C'est la Pierre cruciale que vous voyez parmi ce cercle de pierres. Et sa clé est détenue par l'un de mes geôliers, le Gardien de pierres Fozruk.","Vous devez prendre cette clé, que les Gardiens de pierres appellent le Bâtonnet de l'ordre, à Fozruk, et l'utiliser pour ouvrir la Pierre cruciale.","<nom>, vous avez déjà tellement fait pour me venir en aide. Encore un peu... et ma gratitude fera trembler les fondations même de la terre !"}, {"Trouver et tuer Fozruk. Apporter le Bâtonnet de l'ordre à la Pierre cruciale dans les hautes-terres d'Arathi."}},
[653] = {"Les alliés de Myzrael", {"<Myzrael parle.>","Libre ! JE SUIS LIBRE ! Je suis libre de rassembler mes forces, à l’abri du regard de mes geôliers. Si je les affrontais maintenant ils me domineraient et m'emprisonneraient de nouveau.","Mais au moment voulu, je combattrai les géants, et ils regretteront la tutelle qu'ils m'ont imposée !","Vous êtes <un noble allié/une noble alliée>, <nom>. J'aurai encore besoin de votre aide quand le temps sera venu. Bientôt, il vous faudra m'invoquer à la surface.","Parlez à Gerrig Bonegrip. Il se trouve à Ironforge, dans une boutique de la caverne lugubre. Il sait comment on doit m'invoquer."}, {"Parler à Gerrig Bonegrip, dans la caverne lugubre d’Ironforge."}},
[654] = {"Les échantillons du désert de Tanaris", {"L'activation de la Source d'énergie fait briller et vibrer la Trousse d'analyse de terrain. Tout semble fonctionner comme prévu.","D'après l'ingénieur en chef Bilgewhizzle, vos disposez à présent de deux heures pour récupérer les données dont vous avez besoin dans le Désert de Tanaris. Si vous n'y parvenez pas en deux heures, toutes les données seront perdues et vous devrez lui acheter une autre Source d'énergie.","Allez le voir une fois votre tâche accomplie."}, {"Acquérir des spécimens acceptables pour 8 basilics, 8 hyènes et 8 scorpions. Rapporter la Trousse d'analyse de terrain à l'Ingénieur en chef Bilgewhizzle à Gadgetzan avant que la source d'énergie ne s'épuise."}},
[655] = {"Trépas-d'Orgrim", {"Je m'en souviens très bien, c'est comme si c'était hier... Tellement d'années sans espoir, attaché à cet horrible endroit par du bois et par du fer. Et puis enfin, enfin ! Il y eut le Chef de guerre. Orgrim Doomhammer dans son armure étincelante, son marteau de guerre tournoyant au-dessus de la tête.","Ce fut... trop terrible. Que le coup d'un misérable lâche terrasse notre plus grand héros. Il a chargé, sur son cheval, par-derrière, et sa lance l'a transpercé. Je suis tombé à genoux. Quels espoirs restaient-ils ? Quels espoirs restent-ils ?"}, {"Parler à Tor'gan au Trépas-d'Orgrim."}},
[656] = {"L'invocation de la Princesse", {"Vous devez battre Myzrael avant que sa force n'augmente. Sinon, une fois qu'elle aura rassemblé suffisamment de pouvoir, elle défiera ses ravisseurs ! Certains pensent pouvoir la contrôler, mais si elle peut l'être, alors pourquoi les géants l'ont-ils enchaînée ??","Logique stupide, pas vrai ?","Pour la défier, invoquez-la aux Eclats de Myzrael, où vous lui avez parlé la première fois. Battez-la et prenez ses Chaînes indicibles, puis attachez-les aux Eclats de Myzrael.","Agissez ainsi et elle sera de nouveau prise au piège."}, {"Aller aux Eclats de Myzrael, l'invoquer et la battre.","Prendre ses Chaînes indicibles et les attacher aux Eclats de Myzrael."}},
[657] = {"Les indices d'une Peste nouvelle ?", {"<Quae commence à consulter le dossier.>","C'est effectivement intéressant. Il semblerait que l'Apothicaire Jorell ait travaillé sur des expériences mortelles ici à Go'shek. Je ne suis pas certaine de ce que cela a à voir avec Hillsbrad, mais Phin n'apprécierait pas que nous laissions se poursuivre ces expériences.","Nous avons eu une altercation avec des orcs pendant que vous étiez <parti/partie>, et je ne suis pas en suffisamment bonne forme pour aider Kinelory. Si vous vous sentez d'attaque, elle va avoir besoin de protection pour aller à la ferme et voler toutes les recherches que Jorell n'aurait pas encore envoyées à Moulin-de-Tarren."}, {"Parler à Kinelory."}},
[658] = {"Les indices d'une Peste nouvelle ?", {"Ainsi, Phin vous a <envoyé/envoyée> prendre de nos nouvelles ? Ça ne m'étonne pas, il s'inquiète toujours trop.","Nous avons surveillé la ferme pendant plusieurs jours, et Kin prétend qu'elle a vu une messagère sortir de cette petite maison et se diriger vers le sud il y a peu.","Elle parle beaucoup, mais je fais toujours confiance à ses sens lorsque nous sommes dans des endroits dangereux.","Vous ne seriez pas <un Pisteur compétent/une Pisteuse compétente>, par hasard ? Nous pensons que la messagère se rendait à Moulin-de-Tarren. Si nous pouvions mettre la main sur les documents qu’elle emportait, cela pourrait nous renseigner sur ce qui se passe à Hillsbrad."}, {"Trouver la Messagère des Réprouvés et ramener ses documents scellés à Quae près de la Ferme des Go'shek."}},
[659] = {"Les indices d'une Peste nouvelle ?", {"Salut et bienvenue, <nom>. Je mène un groupe d'aventuriers enquêtant sur des faits étranges se produisant dans le coin.","Mon cousin à Hillsbrad a récemment abattu son chien Stanley parce que l'animal avait magiquement grossi jusqu'à une taille immense et attaquait sauvagement quiconque l'approchait.","Je pense que les morts-vivants sont impliqués, et nous cherchons des indices le prouvant.","Deux de mes compagnes, Quae et Kinelory, sont parties en direction de la ferme des Go'shek sur les hautes-terres d'Arathi, et ne sont toujours pas revenues. Vous pourriez les trouver pour moi ? Je commence à m'inquiéter."}, {"Trouver Quae et Kinelory près de la Ferme des Go'shek dans les hautes-terres d'Arathi."}},
[660] = {"Les indices d'une Peste nouvelle ?", {"Très bien, <nom>. Je suis prête si vous l'êtes.","On y va ?"}, {"Protéger Kinelory jusqu'à ce qu'elle rejoigne Quae."}},
[661] = {"Les indices d'une Peste nouvelle ?", {"J'ai déjà vu Kinelory dans un état pire après toute une nuit passée dans une taverne. Vous avez bien fait de la protéger.","Ce qu'elle rapporte de la ferme permettra à Phin de se faire une idée de ce qui se trame à Arathi. Nous allons rester un peu pour être sûr qu'il n'y aura pas de représailles suite à votre attaque. Voudriez-vous retourner à Southshore et parler à Phin ?"}, {"Retrouver Phin Odelic à Southshore."}},
[662] = {"Sauvetage en haute mer", {"O'Breen a conduit les bateaux dans la crique. Nous étions certains d'y trouver le trésor. Le « Noir messager » a accosté sans encombre, mais à mesure que la « Folie de la vierge » et « L'Esprit des Pins argentés » approchaient, le niveau de la mer a commencé à baisser. C'est la chose la plus étrange que j'aie jamais vue !","Les deux bateaux se sont échoués sur quelque chose... Une sorte de temple dans les récifs. Puis ils ont coulé à pic ; une vraie tragédie.","Nous voulons récupérer leurs cartes maritimes et leurs journaux de bord avant de partir. Nous ne voulons pas que les autres puissent les récupérer !"}, {"Le lieutenant Nilzlix veut que vous récupériez les Cartes et les Journaux de bord de la « Folie de la vierge » et de « L'Esprit des Pins argentés »."}},
[663] = {"Terre !", {"Lolo voit une minuscule personne ! Toujours Lolo faire le guet !","Bienvenue à la crique de Faldir, <nom>. Capitaine O'Breen a dit que nous resterions que quelques heures. Juste le temps de récupérer le trésor, puis nous revenir à Baie-du-Butin.","Mais Lolo pense que ça va durer plus longtemps. Notre groupe a perdu deux navires. Pauvres « Esprit des Pins argents » et « Folie de la vierge ». Au fond de la mer maintenant ils reposent !","Lolo propose de vous parler au capitaine O'Breen si vous voulez rester ici."}, {"Parler au capitaine O'Breen à la Crique de Faldir."}},
[664] = {"Chagrin noyé", {"Argh, quelle horrible vie ! Je devrais être mort sous l'eau, et pas assis ici près d'un feu, respirant de l'air frais.","L'équipage a tout fait pour sauver mon merveilleux « Folie de la Vierge ». Loué soit-il !","O'Breen a réussi à atteindre la rive, nous on n'a pas été aussi chanceux. La mer s'est carrément défilée sous nous, je le jure.","A un instant on naviguait, l'instant d'après on était échoué sur un récif. Et ces créatures ! Elles ont tué tous les survivants. Saleté de nagas ! Je me suis évanoui, puis je me suis réveillé ici.","Je veux que ces créatures meurent !"}, {"Le capitaine Steelgut de la crique de Faldir veut que vous tuiez 10 Ecumeurs Daguéchine et 3 Sorcières Daguéchine."}},
[665] = {"Le trésor englouti", {"Maintenant que nous sommes des Ecumeurs des Flots noirs accomplis, il est de notre devoir d'aider M. O'Breen à localiser le trésor perdu des elfes.","Dans ces eaux sombres, il est presque impossible de trouver les gemmes sans aide. Le docteur a conçu des lunettes spéciales qui pourraient nous être utiles. Il a besoin de les charger avec l’énergie magique de pierres enchantées qui se trouvent dans la grotte en haut de la colline.","Mais cette grotte est maudite ! Quand on s'en approche, on se fait attaquer. Venez et protégez-moi, alors je pourrai transférer l'énergie des pierres vers les lunettes."}, {"Escorter le professeur Phizzlethorpe à la grotte et revenir."}},
[666] = {"Le trésor englouti", {"Le trésor repose au fond de la mer depuis si longtemps que les gemmes se sont calcifiées en pierres. Mais le pouvoir contenu dans ces lunettes vous permettront de les localiser facilement.","L'ingéniosité gnome permet beaucoup !","Aussi, empruntez les Lunettes de chasse aux gemmes, <nom>, et voyez si vous pouvez récupérer une partie du trésor perdu pour le capitaine O'Breen.","J'y plongerais bien moi-même mais... euh... des affaires scientifiques importantes nécessitent ma présence sur un sol dur et sec... euh... voilà, c'est ça."}, {"Le docteur Draxlegauge, à la crique de Faldir, veut que vous rapportiez 10 Gemmes elfiques et que vous rapportiez ensuite les Lunettes de chasse aux gemmes."}},
[667] = {"La mort venue d'en-bas", {"Ça ne se présente pas bien. Il semble que les Daguéchine se regroupent pour une attaque.","Les nagas ont la réputation d'être impitoyables dans la bataille et ne cesseront d'attaquer tant que je n'aurai pas rencontré la mort.","Je ne considère pas ma vie plus précieuse que celles des membres de mon équipage, mais vous devez aider à ma sauvegarde. L'équipage ne pourra pas revenir à Baie-du-Butin seul et sans la connaissance que je possède des mers.","Les nagas viendront de la mer. Je veux que vous restiez près de moi ici en haut pour parer leur attaque. Servez-vous du canon et repoussez-les.","Êtes-vous <prêt/prête>, <nom> ?"}, {"Protéger Shakes O'Breen lors de l'attaque."}},
[668] = {"Le trésor englouti", {"Ne faites pas attendre le capitaine O'Breen. Il veut voir les gemmes. Après tout, c'est quand même pour ça qu'on est là !","Après ces quelques semaines passées avec les pirates, le professeur pense que la pire des choses serait qu'ils s'aperçoivent que nous nous approprions leur trésor.","Voilà, <nom>, portez ça à O'Breen."}, {"Apporter les Gemmes elfique au capitaine O'Breen."}},
[669] = {"Le trésor englouti", {"L'amiral Seahorn voudrait voir le plus rapidement possible ce que vous avez trouvé.","Mais comme vous pouvez le constater, nous sommes un peu coincés ici. Primo la marée est trop basse, deuzio ces damnées créatures ne nous quittent pas des yeux.","Vous rendriez un immense service aux Ecumeurs des Flots noirs si vous pouviez prévenir l'amiral Seahorn à Baie-du-Butin que nous avons découvert le trésor, et que nous sommes en train de le remonter. Prenez cet échantillon en guise de preuve."}, {"Prendre l'échantillon de Gemme elfique et l'apporter à l'amiral Seahorn à Baie-du-Butin."}},
[670] = {"Le trésor englouti", {"Dites, <nom>, vous semblez aimer l'aventure. Ma flotte est bloquée à Baie-du-Butin pour cause de ravitaillement.","Il y a des choses qui se trament dans le coin, et j'ai besoin d'envoyer une correspondance secrète à Shakes O'Breen, qui a jeté l’ancre près des côtes d'Arathi.","Puis-je vous faire confiance pour remettre en toute confidentialité cette missive ?"}, {"Remettre la Lettre de Seahorn à Shakes O'Breen dans les hautes-terres d'Arathi."}},
[671] = {"Magies immondes", {"J'ai senti quelque chose de bizarre dans l'air... une chose qui m'a fait frémir. J'ai ma petite idée sur ce que c'est. En essayant de trouver d'où me venait cette sensation, j'ai découvert dans l'ouest, au manoir de Nordclos, des traces résiduelles d'une invocation démoniaque. A mesure que j'approchais, mon sang frémissait et la rage montait en moi.","Sur le sol, j'ai trouvé une Amulette de Pierre de sang, qui est généralement utilisée pour les invocations démoniaques. Celle que j'ai trouvée était déchargée. Il m'en faut une autre pour l'examiner, mais je ne retournerai pas dans le manoir. Pouvez-vous m'en procurer ?"}, {"Apporter 10 Amulettes de Pierre de sang à Tor'gan au Trépas-d'Orgrim."}},
[672] = {"L'éveil de l'esprit", {"Nous avons essayé de l’installer confortablement, de le dérider, de lui raconter des histoires sur notre majestueuse cité d'Orgrimmar en Kalimdor, mais rien ne le fait réagir.","Les histoires de notre jeune Chef de guerre Thrall, qui porte l'armure et le marteau d'Orgrim mais, peut-être... Peut-être ! Une démonstration de la nouvelle magie chamanique lui donnera de l'espoir. Ça vaut la peine d'essayer !","Nous pourrions peut-être lui fabriquer un petit quelque chose, oui... Amenez-moi quelques Yeux de raptor, et je vais voir ce que je peux faire."}, {"Récupérer 10 Yeux de raptors des plateaux sur les Arpenteurs des hautes-terres et les Vandales des hautes-terres pour Tor'gan au Trépas-d'Orgrim."}},
[673] = {"Magies immondes", {"Je n'ai plus aucun doute sur le fait qu'un puissant démoniste réside dans l'enceinte de Stromgarde. Régulièrement, en utilisant une relique de magie des arcanes, ce démoniste a invoqué un grand nombre de démons. Nous devons tarir sa source d'énergie.","Trouvez le démoniste et tuez-le. Ramenez-moi sa source d'énergie, elle doit être sur lui, et je m'emploierai à la détruire. Nous verrons alors si le pouvoir de cette chose continue de souiller cette terre..."}, {"Trouver l’Orbe de Pierre de sang de Marez Cowl et l’apporter à Tor'gan au Trépas-d'Orgrim."}},
[674] = {"L'éveil de l'esprit", {"Tenez, donnez cette amulette à Gor'mul, et regardez si la force des raptors peut ranimer la flamme guerrière qui sommeille en lui."}, {"Donner l'Amulette de griffe de raptor à Gor'mul."}},
[675] = {"L'éveil de l'esprit", {"<Gor'mul jette l'amulette au sol.>","Tor'gan vous envoie, c'est ça ? Peuh ! Pourquoi est-il si miséricordieux avec moi. Ma propre miséricorde m'a été enlevée le jour où je n'ai pas pu mourir avec mon Chef de guerre au combat. Un mauvais coup du sort..."}, {"Parler à Tor'gan."}},
[676] = {"Menace sur le Trépas-d'Orgrim", {"Mauviettes... Tous autant qu'ils sont. Vous, <classe>, qu'est-ce que vous regardez comme ça ? Vous pensez être quelqu'un de spécial, hein ? Pourquoi ne le prouvez-vous pas ?","L'avant-poste de Trépas-d'Orgrim dans les hautes-terres d’Arathi est attaqué. À moins qu'on leur envoie des renforts rapidement, leur défaite est imminente.","Mais avant de vous précipiter là-bas, j'aimerais vous faire passer un test. Au nord-est de Stromgarde, dans Arathi, il y a une colline habitée par la tribu Rochepoing. Si vous pouvez vous occuper de ces ogres et de leurs massacreurs, allez ensuite faire votre rapport à Drum Fel, à l'avant-poste du Trépas-d'Orgrim."}, {"Tuer 8 Ogres Rochepoings et 10 Massacreurs Rochepoings, puis retrouver Drum Fel à l'avant-poste du Trépas-d'Ogrim sur les hautes-terres d'Arathi."}},
[677] = {"L'appel du combat", {"Les trolls Witherbark et les ogres Rochepoings d'Arathi se sont alliés pour nous éradiquer de cette terre, mais nous ne mourrons pas de leurs mains, ni des mains de personne. Nous allons les tuer et leur montrer que nous aussi possédons notre demeure ici.","Les trolls nous ont refusé leur aide et les ogres, si nous les laissions faire, se serviraient de nous comme nourriture et comme literie. Cela ne sera pas !","Nous commençons notre attaque sur les trolls Witherbark, au sud. Tuez-les en masse, et ne revenez que lorsque leur camp sera rempli de leur sang."}, {"Tuer 10 Lanceurs de haches Witherbark, 10 Chasseurs de tête et 8 Sorciers-docteurs, puis retourner voir Drum Fel à l'avant-poste du Trépas-d'Orgrim."}},
[678] = {"L'appel du combat", {"Votre force ne fait aucun doute, <nom>. Voyons ce que vous valez contre un ennemi digne de ce nom.","Les Rochepoings sont basés au sud-ouest d'ici, vers le pont des nains, et si je ne me trompe pas, les marteleurs et les magi qui s'y trouvent représenteront pour vous un grand défi.","Tuez-en un maximum et revenez me voir quand vous aurez fini. Et même si vous n'en revenez pas, je ferai l'éloge de vos exploits sur le champ de bataille."}, {"Tuer 10 Brutes Rochepoings et 4 Magi Rochepoings, puis retourner voir Drum Fel à l'avant-poste du Trépas-d'Orgrim."}},
[679] = {"L'appel du combat", {"La tête de la bête est prête à tomber... avec grand fracas, <nom>. Vous avez définitivement prouvé votre valeur au cours de cette dernière bataille. Les commanditaires de ces attaques sont installés dans les ruines de Stromgarde, symbole de la faiblesse humaine.","Trouvez et tuez les seigneurs Rochepoings et leurs laquais chamans, qui essayent de nous repousser hors de nos terres."}, {"Tuer 15 Chamans Rochepoings et 10 Seigneurs Rochepoings et revenir voir Drum Fel à l'avant-poste du Trépas-d'Orgrim."}},
[680] = {"La réelle menace", {"Vous avez bien servi mon mari, <classe>, mais il ne connaît pas la véritable cause des attaques contre le Trépas-d'Orgrim.","Or'Kalar, le valet de Mug’Thol à Stromgarde, déteste mon mari, et souhaite sa mort depuis qu'il a été vaincu par lui quelques années auparavant.","J'avais le pressentiment qu'Or'Kalar tendait un piège à mon mari. Je lui ai donc caché les indices comme quoi il était à l'origine de ces attaques.","Sinon mon mari aurait foncé tête baissée dans le piège… Je voudrais que vous tuiez cette créature et que vous me rameniez sa tête."}, {"Tuer Or'Kalar et apporter sa Tête à Korin Fel à l'avant-poste du Trépas-d'Orgrim."}},
[681] = {"Le manoir de Nordclos", {"Nous avons combattu pour Stromgarde, et nous avons perdu. Les ogres, le Syndicat et nous, avons divisé notre grande cité et chaque faction s'est battue contre les autres.","Mais nos forces s'épuisent tandis que celles de nos ennemis se renforcent.","Il nous faut l'aide de la ville de Southshore, mais l'approvisionnement reste difficile, car le Syndicat contrôle le territoire entre les deux cités.","Allez au camp du Syndicat situé au manoir de Nordclos et tuez les occupants. Peut-être qu'ensuite, plus de caravanes de ravitaillement arriveront à destination."}, {"Tuer 10 Malandrins du Syndicat et 6 Mercenaires du Syndicat.","Retourner ensuite alors le capitaine Nials au refuge de l'Ornière."}},
[682] = {"Les insignes de Stromgarde", {"Le Syndicat de Stromgarde est actuellement en guerre contre la milice. Pour inciter la populace à nous affronter aussi, ils ont mis à prix la tête de nos soldats, et donnent de l'or à tous ceux qui apportent un insigne de Stromgarde pris sur un soldat mort.","Nous voulons récupérer ces insignes. Nous ne pouvons tolérer que ces manants gardent des trophées de nos braves soldats tombés sur le champ de bataille !","Allez dans la zone contrôlée par le Syndicat et récupérez les insignes."}, {"Rapporter les Insignes de Stromgarde au capitaine Nials au Refuge de l'Ornière."}},
[683] = {"Requête de Sara Balloo", {"<nom>, j'ai une petite faveur à vous demander.","Prenez cette lettre et portez-la à notre grand monarque d'Ironforge, le roi Magni Bronzebeard. Ce n'est pas grand-chose pour quelqu'un comme vous qui a tant fait pour l'Alliance.","Me présenter devant le roi dans l'état d'esprit qui est le mien ne servirait pas notre cause. Vous le comprendrez sûrement."}, {"Remettre la lettre de Sara Balloo au roi Magni Bronzebeard à Ironforge."}},
[684] = {"On recherche : Marez Cowl", {"La tête de Marez Cowl a été mise à prix. La dernière fois que cette conseillère de haut niveau du Syndicat a été aperçue, c'était dans la zone de Stromgarde contrôlée par le Syndicat.","La prime peut être réclamée au capitaine Nials.","Attention: Marez est une démoniste rusée et très adroite, et il est de notoriété publique qu'elle fricote avec les démons. Débusquez-la avec une extrême prudence."}, {"Apporter la tête de Marez au capitaine Nials, au Refuge de l'Ornière."}},
[685] = {"On recherche : Falconcrest et Otto", {"La milice de Stromgarde a mis à prix les têtes du seigneur Falconcrest et de son garde du corps Otto. Falconcrest dirige les forces du Syndicat dans les hautes-terres d'Arathi, et sa mort sèmerait le chaos dans ces forces.","Son garde du corps Otto, bien qu'il ne constitue pas une cible stratégique, est un adversaire puissant et il a déjà tué nombre de nos défenseurs.","Il faut aller voir le capitaine Nials pour récolter les primes."}, {"Apporter les Têtes de Falconcrest et d'Otto au capitaine Nials au Refuge de l'Ornière."}},
[686] = {"Hommage royal", {"Je ne demande pas à ce que les nains comme Madame Balloo comprennent que nous faisons cette guerre pour leur bien. La perte de son mari ainsi que toutes les autres dans le combat au nom de l'Alliance pèsent lourd sur mes épaules.","Même si je ne peux pas le ramener du Néant distordu, je peux lui rendre hommage.","Je vais faire construire un mémorial en l'honneur de Sully Balloo, par le maître-maçon Marblesten.","Envoyez-lui un message, <classe>. C'est vous qui serez en charge de faire respecter cet ordre."}, {"Le roi Magni Bronzebeard veut que vous parliez avec le maître-maçon Marblesten."}},
[687] = {"Theldurin l’Egaré", {"Theldurin l'Egaré était un membre de notre ordre, le Marteau du crépuscule. Il a perdu l'esprit et a rejeté nos enseignements. Et avant de s'enfuir il nous a volé le Parchemin de Myzrael ! Nous n'avons pas pu le rattraper, mais des rumeurs font état de sa présence dans les Terres ingrates, complètement fou et baragouinant n'importe quoi.","Pour ramener la Dame, vous devez absolument retrouver le parchemin. Et pour cela vous devez retrouver Theldurin."}, {"Trouver Theldurin l'Egaré."}},
[688] = {"Les alliés de Myzrael", {"<Myzrael parle>","Libre ! JE SUIS LIBRE ! Je suis libre de rassembler mes forces, hors de portée de mes ravisseurs. Si je les rencontrais maintenant, ils seraient sûrement plus forts que moi et me captureraient à nouveau.","En temps voulu, j'affronterai les géants, et ils regretteront de m'avoir bannie !","Vous êtes d’une aide précieuse, <nom>. J'aurai encore besoin de vous. Quand je serai prête, j'aurai besoin de vous pour m'invoquer à la surface.","Parlez avec Zaruk au Trépas-d'Orgrim. Il sait comment m'invoquer."}, {"Parler à Zaruk au Trépas-d'Orgrim."}},
[689] = {"Hommage royal", {"Donc mon bon roi veut construire un monument en l'honneur du soldat Sully Balloo ? Et vous me dites que le pauvre homme est mort lors de la tragédie du viaduc de Thandol ?","N'y a-t-il jamais eu la paix en Azeroth ?","Quoi qu'il en soit, les ordres du roi sont prioritaires. Pour construire un monument grandiose et durable, j'ai besoin d'une pierre spéciale, d'une grande finesse. Allez sur la Colline de Darrow, dans Hillsbrad et rapportez-moi du Granit d'Alterac."}, {"Le Maître Maçon Marblesten d'Ironforge veut 5 Morceaux de Granit d'Alterac."}},
[690] = {"La demande de Malin", {"Bonjour, <nom>. Vous avez l'air d'être <un aventurier/une aventurière>, et j'ai un petit travail qui doit être fait.","L'archimage Trelane, mon ancien maître, a fui depuis peu Stromgarde dans les hautes-terres d'Arathi, et il m'a demandé de récupérer quelques objets qu'il n'avait pas pu emporter.","La cité assiégée est tombée, bien qu'il ait essayé de la défendre. J'ai déjà des agents sur les hautes-terres, mais ils ont besoin de renfort. Cela vous intéresse ?"}, {"Trouver Skuerto au Refuge de l'Ornière dans les hautes-terres d'Arathi."}},
[691] = {"Valoir son poids en or", {"L’archimage Malin nous a envoyés, Skuerto et moi, collecter des ingrédients. Maintenant que vous êtes là, je sais que je n’aurai pas de problèmes pour trouver ces objets.","J’ai besoin des objets suivants : des défenses de Witherbark, des bourses-médecine Witherbark et un couteau de chasseur d'ombre.","Pourriez-vous me rapporter ces objets ? Les Witherbark vivent au sud-est d’Arathi."}, {"Trouver 10 Défenses de Witherbark, 4 Bourses-médecine Witherbark et un Couteau de Chasseur d'ombre pour l'Apprenti Kryten au Refuge de l'Ornière."}},
[692] = {"Les fragments perdus", {"Le Parchemin de Myzrael... Le Parchemin de Myzrael ? Vous ne voulez tout de même pas ça ! Sa puissance peut faire revenir Myzrael à la surface du monde, et si vous le faites, on sera tous en danger !","Ou alors... il est peut-être déjà trop tard. Peut-être qu'elle est déjà libre. Peut-être ne fait-elle qu'attendre en rassemblant ses forces avant de les abattre sur nous. Si c'est le cas, alors vous avez BESOIN du parchemin !","... Quel dommage, il a été détruit. Un élémentaire enragé l'a volé et déchiré en morceaux.","Si vous pouvez rassembler les fragments, je pourrai, je pense, le reconstituer..."}, {"Apporter le Fragment de Parchemin déchiré, le Fragment de Parchemin fripé et le Fragment de Parchemin noirci à Theldurin l'Egaré."}},
[693] = {"La baguette au poing", {"Hé <nom> ! Désolé d'avoir été un peu bref, la dernière fois j'étais pris de court... euh, non ce n'est pas un jeu de mots.","J'ai découvert où le premier objet de Trelane a échoué : c'est Kor'gresh qui l'a au Hall Rochepoing. Pendant que j'y suis : il jette des éclairs de glace sur ses troupes pour qu'elles gardent leur position, ce qui prouve qu'il n'est pas loin de maîtriser tous les pouvoirs de l'objet.","Vous trouverez le Hall Rochepoing au sud d'ici puis à l'est du viaduc de Thandol. Trouvez cet ogre et ramenez-moi la baguette.","Pendant que vous serez en chemin, je vais voir ce que je peux faire pour Stromgarde."}, {"Trouver la Baguette d'invocation de Trelane et revenir voir Skuerto au Refuge de l'Ornière."}},
[694] = {"Les défenses de Trelane", {"Alors vous êtes d'attaque ?","Voici la prochaine étape : il y a des ogres tout autour de la Tour d'Arathor à Stromgarde. Nous devons activer les défenses de la tour si nous voulons avoir une chance contre eux et sauvegarder les objets. Pour cela, nous avons besoin de ces gemmes que les chamans Rochepoings apprécient particulièrement. Ils les ont récupérées à la tour, mais ils ne vont pas les garder très longtemps.","Récupérez une de ces gemmes, et portez-la à Kryten : il connaît le sort pour l'enchanter afin qu'elle serve de clef."}, {"Trouver une Agate d'azur et la porter à l'Apprenti Kryten au Refuge de l'Ornière."}},
[695] = {"Enchantement d'Apprenti", {"Et voilà, <nom>. Il suffit d'appuyer la gemme sur un des piédestaux près de la base de la tour, et ses défenses s'activeront. Utilisez-la pour rentrer dans la tour quand vous serez <prêt/prête> et allez chercher les autres objets de Trelane.","Bonne chance à vous ! Je vous attendrai au camp. Oh ! Parlez à Skuerto avant de partir. Il vous donnera tous les détails nécessaires."}, {"Parler à Skuerto."}},
[696] = {"Attaque sur la tour", {"Voici ce qu'il faut récupérer : le Phylactère de Trelane, l'Orbe de Trelane et l'Agate de braise de Trelane.","Les trois objets doivent se trouver dans un coffre dans la tour, vous ne devriez pas avoir de problème pour l'ouvrir ; faites juste attention aux ogres.","Les défenses de la tour ne s'occuperont pas d'eux ; vous devez avoir vos armes prêtes. Quand les ogres remarqueront que vous êtes là pour les voler, ils risquent de ne pas apprécier.","Soyez <prudent/prudente>, et bonne chance."}, {"Trouver le Phylactère de Trelane, l'Orbe de Trelane et l'Agathe d'ambre de Trelane. Les rapporter à Skuerto au Refuge de l'Ornière."}},
[697] = {"La demande de Malin", {"Apportez cette lettre à Stormwind et informez Malin que Kryten sera bientôt de retour. Il vous récompensera pour nous avoir aidés, <nom>, sans aucun doute.","Bon voyage."}, {"Porter la Lettre scellée à l'archimage Malin dans le quartier des mages de Stormwind."}},
[698] = {"Petit bonus du soir", {"Je rassemble des provisions pour notre avant-poste près de la plage à l'est. Tok'kar fait ce qu'il peut, mais entre les tours de garde et la cuisine pour ses soldats, ses journées sont déjà bien remplies.","Ils aiment cuisiner de la viande de Dents-de-scie quand ils le peuvent, mais je n'ai pas le temps de leur en trouver une autre cargaison. Cela vous dérangerait de m’aider ?","Les jeunes Dents-de-scie n'ont pas autant de goût que ceux plus âgés, alors laissez-les en paix un an ou deux pour qu'ils atteignent leur taille adulte."}, {"Apporter 8 Flancs de Dents-de-scie non préparés à Tok'Kar dans le marais des Chagrins."}},
[699] = {"Petit bonus du soir", {"En raison de la menace des murlocs le long des côtes, nous sommes stationnés ici pour repousser, ou au pire alerter s'ils sont trop nombreux, toutes les menaces sur Stonard. Nous n'avons pas vraiment le temps de respirer, donc si vous le souhaitez, vous pouvez rejoindre nos rangs pour nous aider.","Les Griffes de Mordeurs Dents-de-scie sont un excellent matériau pour fabriquer des armes. Rapportez-m'en quelques-unes et nous vous donnerons une de nos armes, de la viande fraîche et une bonne compagnie."}, {"Apporter 6 Griffes de Mordeurs Dents-de-scie à Tok'Kar dans le marais des Chagrins."}},
[700] = {"Hommage royal", {"Ah, Lordaeron fournit une si belle pierre. Il est si dommage que cette terre connaisse une telle confusion.","Mais la construction du Mémorial s'est très bien déroulée ! Je pense que cette nouvelle ravira le Roi.","À présent, si vous le voulez bien, veuillez informer Sa Majesté que le Mémorial est prêt, comme il l'a ordonné."}, {"Rendre compte au Roi Magni Bronzebeard."}},
[701] = {"La ruse du raptor", {"Avec quelques petites choses en plus, je serai capable de créer un objet qui donnera à Gor'mul la ruse du raptor ! Avec le pouvoir du raptor en lui, Gor'mul prendra conscience de notre puissance, et le guerrier qui sommeille en lui se réveillera !","J'ai besoin de cœurs de raptors pour créer l'objet et lancer le sort."}, {"Récupérer 12 Cœurs de raptor provenant de Charognards des hautes-terres pour Tor'gan."}},
[702] = {"La ruse du raptor", {"Prenez cet orbe et utilisez sa magie pour insuffler à Gor'mul l'essence des raptors. Cela va revigorer son esprit et lui redonner goût à la vie. Lorsqu'il sera comme avant, nous lui ferons un résumé de l'activité de la Horde, et il comprendra que pour nos incessantes batailles nous avons besoin d'un grand guerrier."}, {"Donner l'Orbe de Tor'gan à Gor'mul."}},
[703] = {"Ailes de busard grillées", {"Oh oui ! Mes ailes de busard grillées sont les meilleures ! La sauce est savoureuse et la viande est épicée à souhait. Son odeur attirerait les gens à des kilomètres à la ronde !","J'ai tout ce qu'il faut pour en préparer. Tout... excepté des ailes de busard !","Donc si vous en voulez, apportez-moi des ailes de busard. Apportez m'en assez, et je vous donnerai même la recette !"}, {"Apporter 4 Ailes de busard à Rigglefuzz."}},
[704] = {"Le destin d'Agmond", {"Le dernier rapport que j'ai reçu d'Agmond est inquiétant, d'autant plus qu'à présent il est mort ! Il dit que son équipe a découvert des \"urnes anciennes\" au site de fouilles en dehors d'Uldaman. J'ai peur que les urnes qu'il y a découvertes n'aient poussé les troggs à agir, de la même manière que lorsque j'ai découvert les idoles.","J'ai besoin que vous récupériez ces urnes afin que nous les étudions. Allez au site d'Uldaman, récupérez-les, et revenez me voir."}, {"Apporter 4 Urnes de pierre gravées au Prospecteur Ironband au Loch Modan."}},
[705] = {"Pêche aux perles", {"Les Terres ingrates sont inhospitalières. De dangereux prédateurs et de puissants charognards y pullulent. Effrayant, surtout pour un tout petit gobelin. Pour y survivre, il faut être astucieux !","Je sais comment fabriquer des bombes éclairantes. Je les utilise pour éloigner les bêtes sauvages. Mais je commence à manquer d'un des ingrédients : de la poudre de perles bleues. Apportez-en et je vous récompenserai.","Hé ! J'espère que vous avez de bonnes bottes. Les perles bleues proviennent de palourdes qui vivent au Récif infâme.","Ouaip, le Récif infâme de Strangleronce !"}, {"Apporter 9 Perles bleues à Rigglefuzz dans les Terres ingrates."}},
[706] = {"Enchantement flamboyant éclatant", {"En tant qu'apprenti de Feranor Steeltoe, j'ai appris à créer des armes dotées d'un enchantement flamboyant. La dernière fois que j'ai entendu parler de lui, il travaillait dans une forge à Lordaeron.","Je ne peux pas vous donner beaucoup de détails quant au processus, mais je sais qu'il requiert du sang d'un cœur de drake noir qui bat encore. Je n'ai pas grand-chose à faire en attendant les ordres d'Ironforge. Si vous me rapportez un tel cœur du ravin de Lethlor, je vous montrerai mes talents !"}, {"Récupérer un Cœur de drake noir pour Sigrun Ironhew."}},
[707] = {"Ironband a besoin de vous !", {"<nom>. J'ai reçu une demande du Prospecteur Ironband : il a besoin de quelqu'un pour un travail sur un de nos sites.","Je n'ai pas plus de détails, mais si vous allez voir Ironband, il vous en donnera."}, {"Parler au Prospecteur Ironband, sur le Site de fouilles de Loch Modan."}},
[708] = {"La boîte noire", {"La surface de cette boîte noire métallique est fortement grêlée, comme si elle avait été immergée dans de l'acide. Elle a dû rester longtemps dans l'estomac du Condor géant.","Alors que vous retournez la boîte, vous découvrez un symbole gravé à sa base. Sous le symbole, on peut lire :","\"Moteur n°19 - Brigade des sapeurs d'Ironforge\""}, {"Apporter la Boîte noire corrodée au Hall des armes, à Ironforge."}},
[709] = {"La solution à la malédiction", {"Oh dieux ! Une malédiction approche. Écoutez-moi ! Une malédiction !","Non, je ne peux pas en parler... je ne peux pas prononcer leur nom, ils pourraient entendre ! Mais si vous voulez savoir, oui si vous voulez en savoir plus sur eux... Alors vous en apprendrez peut-être assez pour les contrecarrer !","Peut-être.","Allez à Uldaman. Non ! Je l'ai dit ! Vous devez trouver... trouvez la tablette de... Ryun'eh. Oui, trouvez-la ! Et ne me faites plus prononcer ce nom !","Elle est au fond de cet endroit, dans un ancien coffre. C'est là qu'elle est cachée... dans un coffre construit bien avant n'importe quelle construction naine."}, {"Rapporter la Tablette de Ryun'eh à Theldurin l'Egaré."}},
[710] = {"Étude des éléments : la roche", {"Ici, dans les Terres ingrates, mes travaux sur mon dernier projet, des entraves pour élémentaires, sont bloqués.","Si j’aboutis, elles permettront aux mages d'invoquer des élémentaires très puissants. Pour les finaliser, j'ai besoin de tester certains de mes nouveaux sorts offensifs sur quelques bidules que j'ai sous la main. Il me faut donc de petits éclats de pierre.","Ces éclats peuvent facilement être récupérés sur les élémentaires de roche inférieurs, à l'ouest d'ici. Pouvez-vous m'en apporter ?"}, {"Trouver 10 Petits Eclats de Pierre et les porter à Lotwil Veriatus dans les Terres ingrates."}},
[711] = {"Étude des éléments : la roche", {"<nom>, vous avez déjà montré plus de capacités que tous mes apprentis réunis.","Les prochaines parties dont j'ai besoin nécessitent un peu plus de force. On trouve de larges plaques de pierre sur des élémentaires de roche, en étant prudent. Leurs corps sont plus forts et plus grands, et ainsi plus enclins à posséder des morceaux assez importants pour mes tests. Ils se trouvent encore plus à l'ouest d'ici, ou bien au sud-est le long des bords des montagnes.","Prenez votre temps, j'en aurai besoin pour tester ces plus petites parties que vous m'avez apportées."}, {"Apporter 3 Grands blocs de pierre à Lotwil Veriatus dans les Terres ingrates."}},
[712] = {"Étude des éléments : la roche", {"<nom>, il y a une dernière chose dont j'ai besoin en provenance des Terres ingrates : des brassards de liaison de pierre portés par des élémentaires de roche supérieurs. Selon mes calculs, ces brassards me suffiraient pour calibrer mes propres liaisons.","Mes divinations pour localiser des élémentaires de roche supérieurs m'ont permis de trouver un groupe au sud-ouest d'ici, à côté d'ogres si je me souviens bien.","Rapportez-moi 5 de leurs brassards, comme ça j'en aurai un peu plus que nécessaire. La fameuse légende comme quoi Servo gâche un peu trop de réactifs..."}, {"Apporter 5 Brassards de liaison de pierre à Lotwil Veriatus dans les Terres ingrates."}},
[713] = {"La tête froide l'emporte toujours", {"Laissez-moi vous apprendre quelque chose sur l'Huile glaciale : elle a la meilleure capacité de refroidissement pour les embrayages et les moteurs.","Ses propriétés sont sans égales, et en plus elle lubrifie à merveille et durablement. Et ce n'est pas tout : elle aide à maintenir les enchantements sur les objets.","J'ai besoin d'une fiole de cette huile pour augmenter la capacité de mes entraves, afin qu'elles soient assez puissantes pour protéger l’invocateur de la créature invoquée.","Bro'kin, un de mes amis gobelins, peut vous apprendre à en faire. Il se trouve en Alterac."}, {"Trouver de l'Huile glaciale et la porter à Lotwil Veriatus dans les Terres ingrates."}},
[714] = {"Un Gyro... quoi ?", {"<nom>, vous avez brillamment accompli votre travail.","<Lotwil est visiblement obnubilé par son travail.>","Si vous venez à Kharanos, n'hésitez pas à me rendre visite.","Oups ! Vite ! Rattrapez le... oui, ça là. Merci.","Maintenant, j'ai juste besoin d'une dernière petite chose. C'est un petit truc, mais c'est indispensable, j'en ai peur : un Gyrochronatome.","Si vous pouviez en récupérer un et me l'apporter, alors je pourrais finir ceci."}, {"Apporter un Gyrochronatome à Lotwil Veriatus dans les Terres ingrates."}},
[715] = {"Pierre liquide", {"Je pense que les objets que vous avez récupérés pour Lotwil sont le dernier de nos soucis, <nom>. En revanche, vous avez prouvé que vous étiez <expert/experte> en sciences, et ça, c'est sacré pour Lotwil, donc comment pourriez-vous m'aider ?","J'ai besoin d'une Potion d'invisibilité et d'une Potion de soins ; elles seraient rapidement prêtes s’il daignait arrêter ce qu'il est en train de faire.","En échange, je vous donnerai la recette d’une potion que j’ai conçue quand j'étais l'élève de Lotwil. Elle est basée sur les recherches qu'il fait dans les Terres ingrates."}, {"Apporter une Potion d'invisibilité inférieure et une Potion de soins à Lucien Tosselwrench dans les Terres ingrates."}},
[716] = {"Mieux est la pierre que le tissu", {"Je sais à quel point Lotwil peut faire preuve de mesquinerie, <nom>, aussi laissez-moi vous confectionner quelque chose en échange de tous vos efforts. Cela vous protégera peut-être même de l'atrocité imminente qu'est la nouvelle invention de Lotwil.","J'ai travaillé avec le surplus des pierres que vous avez trouvées pour lui, et je peux faire de bons brassards que vous pourriez porter. Des brassards enchantés, bien-sûr.","J'aurais juste besoin qu'un forgeron me fasse des brassards guillochés en bronze pour les terminer."}, {"Apporter des Brassards guillochés en bronze à Lucien Tosselwrench dans les Terres ingrates."}},
[717] = {"Tremblement de terre", {"Il est devenu évident que nous ne pouvons plus garantir que Cinglenoir et Hematus resteront prisonniers à Lethlor. Les dragons noirs qui se sont installés là-haut, et les ogres qui projettent de voler le Signe, tout cela nécessite des mesures draconiennes.","La prison des drakes est scellée grâce à trois Pierres runiques qui ne peuvent être retrouvées que par le Signe de la Terre. Ces Pierres runiques peuvent être utilisées pour briser le sceau qui les garde enfermés.","<nom>, faites en sorte qu'ils ne se réveillent jamais."}, {"Utiliser le Signe de la Terre pour activer les Piliers d'Améthyste, d'Opale et de Diamant, et obtenir les Pierres runiques.","Placer les Pierres runiques dans le Sceau de la Terre pour libérer Cinglenoir et Hematus.","Les tuer, puis rapporter les Liens de Cinglenoir, les Entraves de Hematus et le Signe de la Terre à Garek."}},
[718] = {"Mirages", {"Dans notre précipitation pour quitter le site de fouilles d'Uldaman, nous avons été obligés d'abandonner de nombreux chariots et caisses de fournitures le long du chemin. Les ogres locaux du Camp Kosh se sont précipités sur notre équipement comme des vautours sur une charogne. Ils ne sentent pas meilleur d'ailleurs. Que la charogne je veux dire !","Enfin bref, ils ont pris une caisse qui contenait toutes nos armes et notre... ben, c'est un peu embarrassant, nous n'avons pas pris de nourriture avec nous.","Nous sommes dans le pétrin maintenant, pourriez-vous nous aider ?"}, {"Retrouver la Caisse de fournitures pour Sigrun Ironhew."}},
[719] = {"Un nain et ses outils", {"Bonjour, <nom>. Faites attention par ici. Il y a des nains Sombrefer partout : ils ressemblaient à ceux du clan Ombreforge.","Ils viennent juste d'attaquer le site de fouilles où je travaillais... Ils ont tué pratiquement tout le monde, y compris mon chef, Hammertoe. J'ai réussi à m'enfuir, c’est pourquoi je suis encore en vie.","Le site se trouve au nord. Je projette de récupérer nos fournitures et plus particulièrement mon piolet porte-bonheur.","Pouvez-vous m'aider ? L'un de ces maudits Sombrefer doit avoir mon piolet."}, {"Trouver le Piolet de chance de Ryedol et le rapporter au Prospecteur Ryedol au sud du site de fouilles de Hammertoe."}},
[720] = {"Un signe d'espoir", {"<Des mots sont grossièrement écrits à la craie sur la carte.>","\"Ryedol,","Ils m'emmènent à Uldaman. Envoie de l'aide.","-Hammertoe\""}, {"Trouver le Prospecteur Ryedol et lui dire que Hammertoe Grez est en vie."}},
[721] = {"Un signe d'espoir", {"<nom>, l'avez-vous trouvé ? Avez-vous trouvé Hammertoe à Uldaman ?","Si ces sales Ombreforge le gardent pour le faire parler des anciens Titans, alors tout Ironforge court un grand danger.","Depuis longtemps maintenant, les Sombrefer sont à la recherche de magies pour créer des Golems très puissants. Hammertoe faisait la même chose, mais dans le but de les contrecarrer.","Ils le garderont probablement en vie tant qu'ils ne lui auront pas arraché toutes ses connaissances. Nous devons nous dépêcher de le sauver !"}, {"Trouver Hammertoe Grez à Uldaman."}},
[722] = {"L'amulette des secrets", {"Vous avez du courage… <toux>… pour venir jusqu'à Uldaman pour moi, <classe>.","Si vous êtes vraiment là pour aider… le vieux Hammertoe, alors… <toux>… vous devrez me trouver… une amulette.","Deepshadow l’a… Magregan Deepshadow. L'est dans l'coin... dans les ruines, c'est sûr. L'amulette les mènera... <toux>... droit aux parchemins si on n'l'arrête pas. Ça dépend de vous, <nom>. Je vous en prie... récupérez-la.","Les parchemins aideront sans doute les Sombrefer à créer des Golems encore plus puissants. Je l'sais, c'est tout. Je peux le sentir dans mes os."}, {"Trouver l'Amulette de Hammertoe et lui rapporter à Uldaman."}},
[723] = {"Espérance en la foi", {"<Hammertoe tient sa blessure et attend un moment pour reprendre son souffle avant de continuer.>","Trouvez Ryedol, <nom>. Portez-lui l'amulette. <toux toux> Il... <toux> saura quoi en faire. Il sait que je recherchais l'origine de notre peuple, et il... <toux>... sait que c'était ma mission que d'arrêter le clan Ombreforge. Ils ne doivent pas s'emparer de ces parchemins... <toux>"}, {"Porter l'Amulette de Hammertoe au Prospecteur Ryedol dans les Terres ingrates."}},
[724] = {"Espérance en la foi", {"D'accord, d'accord... Qui pourrait savoir quoi en faire ?","L'historien Karnik ! Bien sûr !","Il est à Ironforge. Lui pourra nous aider. Lui et Hammertoe travaillaient ensemble pour retrouver des artefacts perdus à Uldaman.","Je pense que le clan Ombreforge a trouvé deux d'entre elles et qu'il en sait plus qu’il n’y paraît.","Racontez à Karnik ce qui est arrivé et donnez-lui l'amulette. Il vous dira quoi faire ensuite."}, {"Apporter l'Amulette de Hammertoe à l'historien Karnik à Ironforge."}},
[725] = {"Avertir de la menace", {"<nom>, courrez trouver le Conseiller Belgrum aussi vite que vous le pourrez. Dites-lui ce que vous savez et demandez-lui ce que nous devons faire. Cette note lui donnera plus d’information.","Pendant ce temps, je vais essayer de contacter l'esprit de Hammertoe. Peut-être qu'il sait quelque chose qui pourrait nous aider à mettre un terme aux stupides activités des Ombreforge dans les Terres ingrates. Il faut le souhaiter."}, {"Trouver le Conseiller Belgrum et lui donner la Note."}},
[726] = {"Avertir de la menace", {"<Le Conseiller Belgrum achève de lire la note, et ses yeux s'élargissent un moment.>","Houla, très mauvaises nouvelles !","<nom>, Retournez voir l'Historien Karnik et dites-lui qu'il agit sous l'autorité du roi, et qu'il s'occupe de cette affaire rapidement et avec fermeté. Il doit mettre un terme aux activités des Ombreforge dans les Terres ingrates."}, {"Parler à l'Historien Karnik."}},
[727] = {"Aller à Ironforge chercher le « Sommaire de Yagyin »", {"Les tablettes en disent long. Mais pas assez ! Vraiment pas assez !","Il y a un livre, que Yagyin a écrit il y a des années. Les... gens que j'ai fuis... ils en ont toujours un exemplaire sous la main. Et ils l'étudient presque tous. Ce livre renferme un immense savoir. Un savoir qui peut nous permettre de nous protéger !","Allez voir Gerrig Bonegrip. Il se trouve dans une boutique à Ironforge, dans la caverne lugubre. Montrez-lui ce glyphe et il pensera que vous êtes un des leurs. Demandez-lui le \"Sommaire de Yagyin\"... et rapportez-le-moi !"}, {"Donner le Glyphe du Marteau à Gerrig Bonegrip, dans la caverne lugubre d’Ironforge."}},
[728] = {"Pour le « Sommaire de Yagyin »", {"Les tablettes en disent long. Mais pas assez ! Vraiment pas assez !","Il y a un livre, que Yagyin a écrit il y a des années. Les... gens que j'ai fuis... ils en ont toujours un exemplaire sous la main. Et ils l'étudient presque tous. Il comprend un immense savoir. Un savoir qui peut nous permettre de nous protéger !","Allez voir le Gardien Bel'dugur à Undercity, Lordaeron. Montrez-lui ce glyphe et il pensera que vous êtes un des leurs. Demandez-lui le « Sommaire de Yagyin »... et rapportez-le-moi !"}, {"Apporter le Glyphe du Marteau au Gardien Bel'dugur à Undercity."}},
[729] = {"Les absences du Prospecteur", {"Quel honneur cela a été quand Maître Greywhisker m'a affecté aux ordres du grand Prospecteur Remtravel. À l'Académie d'Ironforge tout le monde connaît les grandes découvertes de Remtravel.","Mais le Prospecteur est plutôt... hum... inconscient... de son environnement.","Nous avons découvert les traces d’une grande société. D'horribles Golems sont sortis du sol et ont envahi le site. Remtravel n'a pas semblé s'en apercevoir. J'ai fui à Auberdine pour demander de l'aide.","S'il vous plaît, allez vers le sud pour voir si le prospecteur va bien !"}, {"Aller vers le sud et vérifier l'état de santé du Prospecteur Remtravel."}},
[730] = {"Des problèmes à Sombrivage ?", {"Il est plaisant de voir qu'un <classe> s'intéresse à l'archéologie et aux grandes merveilles de notre monde.","Bien souvent, notre métier est considéré comme un simple passe-temps par nos amis de l'Alliance. Mais ce que la plupart d'entre eux ne comprennent pas, c'est que les récentes découvertes dans Khaz Modan ont prouvé que de grandes et mortelles puissances menacent tout Azeroth, de Lordaeron à Kalimdor.","Je suis vraiment inquiet pour le groupe que j'ai envoyé à Sombrivage. Je suis sans nouvelles de lui depuis des semaines.","Allez à Auberdine et cherchez des indices sur ce qu'il lui arrive."}, {"Aller à Auberdine et chercher des indices sur les nains de l'équipe de fouilles."}},
[731] = {"Les absences du Prospecteur", {"Alors, Hollee... Oh c'est vrai ! Vous n'êtes pas Hollee. Où est Hollee ?","Bah, ce n'est pas le problème ! Nous devons trouver ce pinceau à pierre... enfin je veux dire ce mystérieux fossile ! Nous devons le trouver. La Ligue des Explorateurs à Darnassus veut savoir sur quoi je travaille.","Dites-moi quand vous serez d'accord et nous partirons à la recherche de ce pinceau.... je veux dire de ce fossile ! Nous trouverons peut-être quelque chose d'intéressant à écrire à la Ligue.","Alors on y va ?"}, {"Protéger le Prospecteur Remtravel pendant qu'il recherche le mystérieux fossile, puis revenir voir l'Archéologue Hollee à Auberdine."}},
[732] = {"Tremblement de terre", {"Sigrun m'a expliqué que vous l'aviez beaucoup aidé. Vous pourrez peut-être aussi m'aider. Je suis envoyé par mon maître, Krasus de Kirin Tor, pour mettre fin aux problèmes dans le ravin de Lethlor.","Je ne peux pas divulguer la nature exacte de ma mission, ni la tâche que je dois accomplir, mais elle est de première importance.","Une bande d'ogres errants, qu'on a vu parfois près de Camp Boff, a volé un objet appelé le Signe de la Terre, que je dois récupérer. Si vous me le rapportez, je vous en dirai plus."}, {"Retrouver le Signe de la Terre pour Garek."}},
[733] = {"Demandes variées", {"Cela va devenir difficile de se rendre ici si je ne peux pas me procurer du matériel pour construire des défenses et les choses nécessaires. J'ai remarqué que les ogres de la région semblaient bien équipés en morceaux de métal. Peut-être que les légendes à propos d’engins de siège dans les Terres ingrates ne sont pas si loin de la vérité finalement...","Quoi qu'il en soit, le Prospecteur s'inquiète de l'état de notre petit camp, et j'aimerais commencer à y travailler dès que possible.","Le camp principal des ogres est situé dans la grotte de Crache-poussière, assez loin d'ici en direction de l'ouest."}, {"Collecter 7 Morceaux de métal pour Sigrun."}},
[734] = {"Grandes difficultés", {"Merveilleux, merveilleux.","<Lotwil lève les yeux vers vous un instant avant de reprendre son travail.>","Allez parler à Lucien, <nom>. J'ai besoin d'un rouage n° 5. Voulez-vous bien me l'apporter ?"}, {"Parler à Lucien Tosselwrench dans les Terres ingrates."}},
[735] = {"L'étoile, la main et le cœur", {"Le \"Sommaire de Yagyin\", hein ? Vous allez établir une nouvelle branche de notre ordre, quelque part ? Laissez tomber, je n'ai pas à le demander.","Je vais vous aider, j'en ai une copie en plus. On doit s'entraider, n'est-ce pas ? C'est comme cela que nous prospérons.","J'ai besoin de trois objets de puissance. Apportez-les-moi et je vous donnerai le livre.","Je ne vais pas vous donner les noms de vive voix, les murs ont des oreilles, mais je vais vous les écrire."}, {"Rapporter les Objets écrits sur le mot de Bonegrip à Gerrig Bonegrip dans Ironforge."}},
[736] = {"L'étoile, la main et le cœur", {"Vous avez besoin du « Sommaire de Yagyin » ? Vous projetez donc de réunir une nouvelle cellule ? Bien sûr que c'est le cas... et bien sûr que je vais vous aider, <frère/sœur>.","Mais vous devez m'aider aussi.","J'ai besoin de trois objets de puissance. Apportez-les-moi et je vous donnerai le livre.","Je ne peux pas prononcer leurs noms, mais je peux vous les écrire.","Apportez-moi les objets de la liste. Si vous pouvez me les obtenir, alors vous méritez de créer une nouvelle branche de notre ordre."}, {"Apporter les objets de la liste de Bel'dugur au Gardien Bel'dugur à Undercity."}},
[737] = {"Le savoir perdu", {"J'ai le livre pour vous, <nom>. Je n'ai pas besoin de vous prévenir de bien y faire attention. Et de le garder hors de portée de nos ennemis.","Ce livre contient de nombreuses vérités...","... Et la vérité peut vous consumer."}, {"Porter le « Sommaire de Yagyin » à Theldurin."}},
[738] = {"Trouver Agmond", {"Le Prospecteur Agmond avait la charge de nos sites de fouilles dans les Terres ingrates, au sud-ouest. Je dis \"avait\" car… en fait, cela fait des semaines que je n'ai pas entendu parler de lui. J'ai besoin que vous le trouviez pour me ramener son rapport.","Aux dernières nouvelles, il se trouverait au site de fouilles le plus au sud des Terres ingrates, un minuscule site au-delà de la Forteresse d'Angor. Si j'étais vous, je commencerais mes recherches là-bas."}, {"Trouver Agmond."}},
[739] = {"Murdaloc", {"Les traces d'Agmond partent de l'ouest. Les traînées de sang sur la pierre et sur le sable vous indiquent que le nain a rampé sur une certaine distance.","Et il est mort ici.","Un examen plus approfondi vous fait remarquer le mot \"Murdaloc\", tracé sur un tas de sable des Terres ingrates.","Tracé près de la main morte d'Agmond."}, {"Tuer l'assassin d'Agmond : Murdaloc.","Tuer 12 Briseurs d’os Cavepierre.","Et revenir voir le Prospecteur Ironband au Loch Modan."}},
[741] = {"Les absences du Prospecteur", {"Alors le prospecteur veut envoyer le mystérieux fossile à la Ligue des Explorateurs à Darnassus ? Quitter Sombrivage sans lui… ce n'est pas bien.","Voilà, <nom>, apportez le mystérieux fossile au Chef Archéologue Greywhisker à Darnassus."}, {"Apporter le Mystérieux fossile au Chef archéologue Greywhisker, à Darnassus."}},
[742] = {"La Chasse d'Ashenvale", {"Votre attention ! Les terres sauvages d'Ashenvale vous attendent tous !","La Horde s'est établie dans les terres au nord des Tarides. Nos deux avant-postes : le Poste de Bois-brisé et le Poste de la grève de Zoram s'efforcent bravement d'apporter la gloire à la Horde ! Ceux et celles qui veulent faire montre de leur courage devraient y trouver conseil. De plus, Senani Thunderheart, au Poste de Bois-brisé, directement au nord des Tarides, cherche en ce moment des candidats prêts à prendre part à une grande Chasse d'Ashenvale !"}, {"Parler à Senani Thunderheart, au Poste de Bois-brisé, à Ashenvale."}},
[743] = {"Le danger des Furies-des-vents", {"Merci de prendre le temps de me parler, <nom>. Je suis Ruul, guerrier et professeur.","Vous avez atteint l'âge où il vous faut vous préparer à de dures épreuves si vous comptez vous aventurer hors de Mulgore.","Si vous voulez éprouver votre force, commencez par aller chasser les harpies Furie-des-vents au sud-est. Elles nichent le long des crêtes des montagnes, loin de la route.","Elles sont l'un de nos ennemis naturels, ici à Mulgore. Les affronter éprouvera vos compétences."}, {"Apporter 8 Serres de Furie-des-vents à Ruul Eagletalon, au village Bloodhoof."}},
[744] = {"Préparation à la cérémonie", {"<nom>, mon frère doit bientôt se présenter devant le chef Bloodhoof et il est de mon devoir de lui faire une coiffe digne de ce nom.","Je voudrais vous demander une faveur, pendant que je finis de tanner ces lanières de cuir. Je n'ai, hélas, pas eu le temps de collecter suffisamment de plumes et je me demandais si vous pouviez en rassembler quelques-unes pour moi ?","Vous pouvez trouver des plumes de bonne taille sur les harpies qui vivent loin au nord et au nord-ouest de Thunder Bluff. Je n'ai besoin que de 6 plumes d'azur et de 6 plumes de bronze."}, {"Collecter 6 Plumes d'azur et 6 Plumes de bronze et les rapporter à Eyahn Eagletalon à Thunder Bluff."}},
[745] = {"Partager la terre", {"<nom>, il y a beaucoup de conflits qui ont dévasté cette terre. J'espère seulement que vous n'aurez pas à être le témoin du même nombre de batailles que je le fus, moi-même. Il y a pourtant une chose que je voudrais que vous fassiez ; cela vous montrera comment une menace, même minime, peut blesser ce pays, si elle n'est pas endiguée à temps.","Des crins-pâles gnolls ont colonisé le sud de Bloodhoof et une grotte à l'ouest. Ils ne répondent pas à nos tentatives de communication et massacrent les animaux sauvages de Mulgore.","Avec cette engeance, la diplomatie n'est, hélas, plus une solution satisfaisante."}, {"Tuer 10 Tanneurs crins-pâles, 8 Ecorcheurs crins-pâles et 5 Braconniers crins-pâles, puis retourner voir Baine Bloodhoof, au village Bloodhoof."}},
[746] = {"Les fouilles des nains", {"Il y a des nains qui préparent des fouilles au nord-ouest. Ils pensent que la terre abrite des secrets et ils ont raison. Mais creuser et souiller le pays n'est pas un bon chemin vers la sagesse.","Collectez les outils de forage des nains, fracassez-les et revenez avec les outils brisés.","Vous ne pourrez détruire les outils que dans une forge. Vous en trouverez une à Thunder Bluff, bien que n'importe quelle forge (même une forge dans le camp des nains) suffise à la tâche."}, {"Collecter 5 Piolets de prospecteur.","À la forge, brisez les Piolets du prospecteur pour obtenir des Outils cassés.","Rapporter 5 Outils cassés à Baine Bloodhoof, au village Bloodhoof."}},
[747] = {"La chasse commence", {"Vous avez un air prometteur, et vous allez avoir l'occasion de prouver votre valeur au sein de la tribu. Peut-être le jour est-il proche où l'on vous accueillera dans la grande ville de Thunder Bluff. Mais avant, vous devrez démontrer votre valeur à mon père, le Chef Hawkwind.","Ici, à la Mesa de Nuage rouge, nous sommes fiers de nos talents de chasseurs. Les taurens chassent par nécessité et par goût du sport. Notre stock de viande a diminué et nous avons besoin de plumes pour nos vêtements. Chassez les trotteurs des plaines du voisinage et faites vos preuves en réapprovisionnant le village."}, {"Grull Hawkwind, à Camp Narache voudrait que vous lui apportiez 7 Plumes de trotteurs des plaines et 7 morceaux de Viande de trotteur des plaines."}},
[748] = {"Eau empoisonnée", {"Des gobelins et leurs serviteurs ont pollué nos puits sacrés ! Nous ne pouvons tolérer cela.","Pour assainir les puits, je dois créer un totem de purification que vous devrez porter au puits, afin de pratiquer le rituel de purification.","D'abord nous allons fabriquer le totem de purification pour le puits Winterhoof. Pour ce faire, le pays doit offrir son aide. Chassez des loups des prairies pour leurs pattes et des trotteurs des plaines adultes pour leurs serres. Vous les trouverez au sud-ouest.","Revenez avec les pattes et les serres, <nom>."}, {"Apporter 6 Pattes de loup des prairies et 4 Serres de trotteur des plaines à Mull Thunderhorn à Bloodhoof."}},
[749] = {"La Caravane dévastée", {"Il y a quelques jours, nous avons intercepté une caravane de la KapitalRisk qui se déplaçait sous escorte sur les rives du lac Taureau-de-pierre. Nous avions déjà perdu deux des nôtres dans une bataille antérieure ; aussi avons-nous attaqué sans hésiter. La caravane fut dévastée.","Nous avons laissé leurs caisses de provisions sur place, pensant que nos torches détruiraient toute chose de valeur. Mais comme des sauveteurs de la KapitalRisk ont été repérés à côté des caisses brûlées, il se peut que nous nous soyons trompés.","Peut-être pourriez-vous aller sur place et examiner le contenu de ces caisses."}, {"Examiner le contenu d'une caisse de provisions à la caravane dévastée, pour Morin Cloudstalker."}},
[750] = {"La chasse continue", {"Un tauren compétent dans l'art de la chasse sait que ses proies ne sont pas de simples trophées. Les animaux des plaines nous fournissent ce dont nous avons besoin pour survivre. Vous ferez grosse impression sur les plus âgés si vous réussissez à revenir avec quelques-unes de ces précieuses Peaux de puma des montagnes. Vous pourrez trouver ces bêtes dans les collines plus au sud.","Nos enfants ont besoin de vêtements et nos tentes d'être raccommodées."}, {"Grull Hawkwind, à Camp Narache, voudrait que vous lui apportiez 10 Peaux de puma des montagnes."}},
[751] = {"La Caravane dévastée", {"L'ouverture de la caisse révèle un assortiment de minerais divers, soigneusement empaquetés. Ceci est une preuve supplémentaire de l'activité minière de la KapitalRisk dans Mulgore. Sur les minerais, vous récupérez des rouleaux de papier, étroitement ficelés.","La-haut, dans les collines, vous pouvez presque entendre les bruits des outils de forage qui fendent les roches et voir la fumée des gigantesques feux d'alimentation."}, {"Rapporter les Documents de la KapitalRisk à Morin Cloudstalker, à côté du village Bloodhoof."}},
[752] = {"Une humble contribution", {"Tous les membres de la tribu prennent part à l'harmonie naturelle de la vie. Nous vivons et travaillons tous ensemble.","Notre engagement les uns pour les autres implique une grande responsabilité envers autrui. Je voudrais vous demander une humble contribution.","Ma mère est partie ce matin pour tirer et rapporter de l'eau d'un puits qui est au sud-est de Narache. Le temps passe et elle n'est toujours pas rentrée. Peut-être pourriez-vous aller à sa rencontre ? Je suis, hélas, retenu ici par les affaires de la tribu."}, {"Partir chercher la mère du Chef Hawkwind, près du puits, au sud-est de Camp Narache."}},
[753] = {"Une humble contribution", {"J'en ai vu et parcouru du pays, dans ma vie. Ces jambes fatiguées n'ont plus la vigueur d'antan, mais je peux encore servir la tribu... en prenant le temps qu'il faut.","Vous avez l'air d'un de ces <classe>s enthousiastes. Voici de quoi tester votre jeunesse et votre vitalité : prenez une cruche d'eau et apportez-la à mon fils, le Chef, à Camp Narache.","Rappelez-vous que même la tâche la plus humble peut parfois vous attirer la reconnaissance des aînés."}, {"Prendre une Cruche d'eau du puits.","Apporter la cruche au Chef Hawkwind, à Camp Narache, au nord-ouest du puits."}},
[754] = {"Purification de Winterhoof", {"J'ai réalisé le totem pour purifier le puits Winterhoof. Il vous faut maintenant l'apporter au puits et y pratiquer le Rituel de purification. Vous trouverez le puits dans le sud-est, gardé par d'infects gobelins !","Ce sera tout sauf une mission facile mais vous devez réussir, si nous voulons un jour pouvoir boire de nouveau l'eau précieuse de ce puits.","Bonne chance à vous, <nom>."}, {"Utiliser le Totem de purification de Winterhoof au puits Winterhoof, puis retourner voir Mull Thunderhorn."}},
[755] = {"Les Rites de la Terre-mère", {"La volonté dont vous avez fait preuve en acceptant cet humble travail pour les taurens de Narache, ainsi que votre goût pour l'apprentissage révèlent la noblesse de votre caractère, <nom>. Je suis sûr qu'un jour vous serez <connu/connue> à Thunder Bluff comme l'un des <classe>s les plus valeureux.","Avant cela, vous serez mis à l'épreuve des trois Rites de la Terre-mère.","La première épreuve est le Rite de force. Allez voir le Prophète Graytongue et dites-lui que le Chef Hawkwind vous envoie.","Vous trouverez la demeure du Prophète directement au sud de Camp Narache."}, {"Aller voir le Prophète Graytongue qui vit dans les collines au sud de Camp Narache."}},
[756] = {"Le Totem de Thunderhorn", {"Pour créer le Totem de purification de Thunderhorn, vous devez collecter les griffes des prédateurs qui hantent les plaines de Mugore. Chassez les traqueurs des prairies et les pumas des basses plaines pour leurs griffes, puis revenez me voir.","Vous trouverez les traqueurs des prairies et les pumas des basses plaines à l'est et à l'ouest."}, {"Rapporter 6 Griffes de Traqueur et 6 Griffes de puma à Mull Thunderhorn."}},
[757] = {"Le Rite de force", {"Les rites de la Terre-mère sont un passage obligé pour tout jeune tauren qui se respecte, à Thunder Bluff.","La première épreuve est le rite de Force. Vous devrez y démontrer votre bravoure en tuant des ennemis de la tribu.","Les Dos-hirsute du ravin de Roncelame à l'est, empiètent sur nos terres tribales. Ils nous tendent des embuscades lors de nos parties de chasse et volent dans les villages, à la nuit tombée.","Prouvez votre valeur en massacrant ces vauriens et retournez voir le Chef, à Camp Narache, avec leurs ceintures pour prouver que vous les avez tués."}, {"Tuer des Dos-hirsute dans le Ravin de Roncelame et apporter 12 Ceintures de Dos-hirsute au chef Hawkwind au camp Narache."}},
[758] = {"Purification de Thunderhorn", {"Le totem est prêt, <nom>. Il vous faut encore pratiquer le Rituel de purification.","Allez au puits Thunderhorn, au nord du village et utilisez le Totem de purification de Thunderhorn pour le rituel. Revenez me voir quand cela sera fait.","Que les esprits soient avec vous."}, {"Utilisez le Totem de purification de Thunderhorn au puits Thunderhorn, puis retournez voir Mull."}},
[759] = {"Le Totem de Wildmane", {"Il ne reste qu'un puits à purifier, appelé puits Wildmane, d'après le clan du même nom. Pour nettoyer ce puits, nous aurons besoin des dents d'un redoutable prédateur : le loup alpha des prairies. Allez chasser ces loups au nord, puis revenez me voir lorsque vous en aurez fini.","Les alphas rôdent souvent autour de notre majestueuse cité de Thunder Bluff."}, {"Apporter 8 Dents de loup alpha des prairies à Mull Thunderhorn, au village Bloodhoof."}},
[760] = {"Purification de Wildmane", {"Votre purification des puits Winterhoof et Thunderhorn est objet de discussion et de fierté parmi les esprits, jeune <nom>. Le totem de Wildmane est construit et le devoir vous appelle, encore une fois.","Le puits Wildmane se trouve au nord de Thunder Bluff. Pratiquez-y le rituel ! Débarrassez le pays du poison des gobelins. Que l'eau claire coule de nouveau !"}, {"Utiliser le Totem de purification de Wildmane au puits Wildmane, puis retournez voir Mull Thunderhorn."}},
[761] = {"Chasse aux Rapaces", {"Le rapace est un oiseau rusé, difficile à trouver et à chasser, qui plus est. Une collection de plumes de rapace est signe d'intelligence et de détermination pour un chasseur.","Si vous êtes <prêt/prête> à entreprendre ce travail, pénétrez les plaines et chassez les rapaces.","Rapportez-moi leurs plumes et faites honneur à votre clan.","Vous trouverez les rapaces partout dans Mulgore, mais vos yeux doivent être affûtés pour les localiser et vos jambes, agiles pour les attraper."}, {"Apporter 8 Trophées de plumes de Rapace à Harken Windtotem, au village Bloodhoof."}},
[762] = {"Un ambassadeur du mal", {"L'Ambassadeur Infernus a été envoyé à la Forteresse d'Angor par Ragnaros lui-même, pour suivre les recherches des artefacts perdus des Ombreforge à Uldaman.","Cela a déjà coûté d'énormes pertes à notre peuple et la situation empire encore. Je n'ose imaginer combien d'artefacts ils pourraient rassembler et utiliser contre Ironforge.","Notre objectif est simple, <classe>. Retournez dans les Terres ingrates et tuez l'Ambassadeur Infernus. Une fois la menace éliminée, revenez voir le Conseiller Belgrum, avec les preuves de votre victoire.","Tout est entre vos mains, à présent."}, {"Tuer l'Ambassadeur Infernus, à la Forteresse d'Angor, et apporter la preuve de sa mort au Conseiller Belgrum à Ironforge."}},
[763] = {"Les Rites de la Terre-mère", {"Il vous faut encore subir deux épreuves pour achever les Rites de la Terre-mère. Il est temps pour vous d'élargir votre expérience, <nom>.","Voyagez jusqu'au Village Bloodhoof et trouvez le Chef, Baine Bloodhoof, fils de Cairne. Là, vous devrez continuer votre périple et gagner l'approbation des aînés de Thunder Bluff.","Portez ce totem à Baine ; il reconnaîtra mes gravures et vous aidera dans votre quête.","Suivez la route hors de Camp Narache et hâtez vous. Ne vagabondez pas de-ci de-là, vous vous perdriez."}, {"Apporter le Totem de Hawkwind à Baine Bloodhoof au village Bloodhoof. Suivre la route hors du camp Narache."}},
[764] = {"La KapitalRisk", {"Ceci explique l'accroissement des effectifs et des équipements de la KapitalRisk à Mulgore. Ces gobelins... leur nombre augmente bien trop vite. Comme on dit : quand on montre quelque chose à un gobelin, les bruits de la monnaie et du profit ne tardent pas à retentir.","Les avides petites créatures... malheureusement pour eux, nous, les taurens, ne permettrons pas à leur entreprise de se développer sur nos terres. Allez à leur mine, au nord de la caravane dévastée, et portez leur un message."}, {"Tuer 14 Travailleurs de la KapitalRisk et 6 Superviseurs de la KapitalRisk pour Morin Cloudstalker du village Bloodhoof."}},
[765] = {"Superviseur Fizsprocket", {"A en croire les documents que vous avez sauvés de la Caravane, la KapitalRisk a conçu plusieurs projets pour nous chasser de nos terres afin de pouvoir les piller et les exploiter en toute liberté.","Je suis épouvanté de voir à quelles extrémités ces odieuses créatures sont prêtes à en venir pour accomplir leurs desseins. Le cerveau qui est à l'origine de leurs plans doit être tué. Son nom est Superviseur Fizsprocket et vous le trouverez à la mine de la KapitalRisk, à l'est de la Caravane dévastée. Rapportez-moi son écritoire ; peut-être nous apprendra-t-il quelque chose."}, {"Tuer le Superviseur Fizsprocket et rapporter son Ecritoire à Morin Cloudstalker, au village Bloodhoof."}},
[766] = {"Mazzranache", {"Mon grand-père m'a conté sa bataille avec la créature nommée Mazzranache. Il m'a parlé de ses yeux rouges, démoniaques, de ses serres aiguisées comme des rasoirs et de sa morsure venimeuse.","J'ai eu la malchance de rencontrer la bête tandis que je cheminais à travers les plaines. Elle était exactement comme mon grand-père me l'avait décrite ; elle m'a attaqué et m'a profondément mordu à l'épaule. Son poison coule à présent dans mes veines et j'aurais besoin d'ingrédients rares pour réaliser un antidote : un coeur de loup, un fémur de puma, une écaille de trotteur des plaines et un gésier de rapace.","Hâtez-vous, le temps m'est compté."}, {"Apporter un Coeur de loup des plaines, un Fémur de puma des basses plaines, une Ecaille de trotteur des plaines et un Gésier de rapace à Maur Raincaller, au village Bloodhoof."}},
[767] = {"Le Rite de vision", {"Réussir le Rite de vision, un des rites de la Terre-mère, vous attirera le respect des aînés de Thunder Bluff.","Notre peuple a compris que la terre était pourvoyeuse de ressources. Pour faire l'expérience de la vision rituelle, vous devez parler au chef spirituel du village, Zarlman Deux-Lunes."}, {"Parler à Zarlman Deux-Lunes, au village Bloodhoof."}},
[768] = {"La chasse aux peaux", {"Salut, <mon/ma> jeune <ami/amie> ! Votre oeil brille d'une flamme farouche et je vois que vous avez hâte de retourner dans les plaines pour chasser. Puissiez-vous faire l'honneur de votre clan !","Je travaille les peaux de bêtes ; j'en fais des vêtements et des armures pour les gens de Thunder Bluff.","Si vous alliez chassez des bêtes et m'en rapportiez les peaux, je pourrais vous confectionner quelque chose."}, {"Apporter 12 pièces de Cuir léger à Veren Tallstrider, à Thunder Bluff."}},
[769] = {"Sac en peau de kodo", {"Les kodos de Mulgore sont puissants et robustes, qualités que nous respectons beaucoup. Si vous savez travailler le cuir et souhaitez apprendre à confectionner un sac en peau de kodo, apportez-moi les fournitures nécessaires.","Faites cela et je partagerai mes connaissances avec vous."}, {"Apporter 4 pièces de Cuir léger et 4 Bobines de fil grossier à Veren Tallstrider, à Thunder Bluff."}},
[770] = {"La Cape de démon balafrée", {"Cette peau déchiquetée de Hurleur fantomatique porte toujours la terrible blessure reçue lors de la bataille des loups contre la Légion ardente.","Peut-être quelqu'un au village Bloodhoof sait-il quelque chose au sujet de la malédiction du Hurleur fantomatique..."}, {"Trouver quelqu'un qui puisse vous en apprendre davantage au sujet du Hurleur fantomatique."}},
[771] = {"Le Rite de vision", {"Pour passer le Rite de vision, vous devez collecter les ingrédients nécessaires à la réalisation de l'Eau des Prophètes.","J'aurai besoin de Pierres de puits, qui se trouvent autour des puits, à Mulgore. Je les combinerai aux pommes de pin, qui tombent des arbres majestueux de notre pays.","Ces ingrédients sont porteurs de grands principes magiques ; quand vous en consommerez les décoctions devant le feu tribal, leurs essences vous donneront une vision. Suivez-la et vous accéderez à l'étape suivante de votre quête sacrée."}, {"Collecter 2 Pierres de puits et 2 Pommes de pin et les rapporter à Zarlman Deux-Lunes, au village Bloodhoof."}},
[772] = {"Le Rite de vision", {"Vous allez maintenant être <confronté/confrontée> à l'Eau des Prophètes.","Quand vous vous sentirez <prêt/prête>, consommez-la en face du feu tribal. Une fois bue l'eau sacrée, la vision se matérialisera rapidement devant le feu.","A ce moment-là, vous serez <seul/seule> avec votre destin..."}, {"Consommer l'Eau des Prophètes devant le feu tribal, au village Bloodhoof et suivre la vision quand elle apparaîtra."}},
[773] = {"Le Rite de sagesse", {"Pour gagner l'approbation des aînés de Thunder Bluff, vous devez désormais réussir le Rite de sagesse.","Maintenant que vous avez achevé le Rite de vision, les esprits ancestraux des Rochers rouges vous donneront la bénédiction des ancêtres. Seuls ceux qui ont bu l'Eau des Prophètes accèdent à cette bénediction.","Voyagez à l'est de Thunder Bluff, jusqu'aux Rochers rouges et cherchez l'Esprit ancestral, <nom>."}, {"Voyager aux Rochers rouges, à l'est de Thunder Bluff et parler à Esprit ancestral."}},
[775] = {"Voyage à Thunder Bluff", {"Vous allez maintenant être <révéré/révérée> dans notre grande ville. Chaque <race> qui passe avec succès les Rites de la Terre-mère a droit à cet honneur.","Pour votre quête finale, trouvez notre noble chef, Cairne Bloodhoof, qui demeure au sommet de la plus haute mesa de Thunder Bluff."}, {"Parler à Cairne Bloodhoof à Thunder Bluff."}},
[776] = {"Les Rites de la Terre-mère", {"Vous avez passé les rites de la Terre-mère et avez désormais votre place parmi nous, à Thunder Bluff.","Mais pour maintenir un prestige si durement gagné, vous devez continuer à prouver votre valeur auprès de votre peuple.","Nous sommes des chasseurs, <nom>. Plus bas, à Mulgore, vit une formidable bête kodo nommée Arra'chea. Prouvez votre habileté à la chasse ; traquez la bête et rapportez-moi la Corne d'Arra'chea."}, {"Apporter la Corne d'Arra'chea à Cairne Bloodhoof, à Thunder Bluff"}},
[777] = {"Grandes difficultés", {"Bon.","Je serai là-bas... à l'abri.","Rejoignez-moi, si vous le souhaitez."}, {"Apporter le Rouage N°5 à Lotwil Veriatus."}},
[778] = {"Ça promet d'être dur", {"Haha ! c'est fait ! Les entraves sont finies !","Il est temps, Lucien. Il est temps, Servo !","Êtes-vous <prêt/prête> à assister à la fusion parfaite de la science alchimique et des arcanes, <nom> ?","Je vais maintenant invoquer un puissant élémentaire. C'est un grand jour pour nous !"}, {"Vaincre le Gardien Fam'retor et rapporter les entraves des élémentaires de Lotwil à Lotwil Veriatus."}},
[779] = {"Sceau de la Terre", nil, nil},
[780] = {"Les sangliers de guerre apprivoisés", {"Les sangliers de guerre apprivoisés du ravin de Roncelame se répandent sur nos terrains de chasse. Ils sont entraînés au combat et rendus agressifs par les hurans Dos-hirsute contre lesquels nous sommes en guerre.","Partez ; massacrez ces infâmes créatures et revenez avec leurs groins et leurs flancs, que nous puissions en faire un bon ragoût pour les jeunes."}, {"Grull Hawkwind, à Camp Narache, voudrait que vous tuiez des sangliers de guerre, et que vous rapportiez 8 Groins de sanglier de guerre et 8 Flancs de sanglier de guerre."}},
[781] = {"L'attaque du Camp Narache", {"Après une analyse attentive de la carte, vous comprenez que vous avez découvert les plans du chef de guerre Dos-hirsute pour assiéger le camp Narache.","Le chef Hawkwind pourrait certainement tirer profit de ces informations !"}, {"Apporter les Plans d'attaque Dos-hirsute au chef Hawkwind, au camp Narache."}},
[782] = {"Alliances rompues", {"Durant l'alliance de la Deuxième guerre entre la Horde et le dragon Aile de mort, nous avons appris que deux de ses lieutenants, Cinglenoir et Hematus, ont été emprisonnés dans le ravin de Lethlor, loin à l'est. Nous devons réduire au silence nos anciens alliés pervertis afin de restaurer l'honneur de la Horde !","Nous avons engagé Tho'grun et sa bande de mercenaires ogres pour obtenir le Signe de la Terre, une clé nécessaire à l'ouverture de la prison des drakes. Mais Tho'grun nous a trahis, nous a submergés et s'en est emparé. Allez au Camp Boff et récupérez-le !"}, {"Tuer Boss Tho'grun et rapporter le Signe de la Terre à Gorn, à Kargath."}},
[783] = {"Menace intérieure", {"J'espère que vous avez le coeur bien accroché, jeune <classe>, car il y a du travail à faire à Northshire.","Et ce n'est pas du travail de ferme.","Les gardes de Stormwind ont beaucoup à faire afin de maintenir la paix en ces lieux, tandis que nombre d'entre nous sont en des terres lointaines et que tellement de dangers nous menacent. Aussi acceptons-nous l'aide de tous ceux qui souhaitent défendre leur demeure. Et leur alliance également.","Si votre volonté est de répondre à cet appel, allez parler à mon supérieur, le maréchal McBride. Il se trouve dans l'abbaye qui est derrière moi."}, {"Parler au maréchal McBride."}},
[784] = {"Vaincre les traîtres", {"Guidés par l'amiral Proudmoore, les humains de Kul Tiras prirent pied en Durotar, en violation du pacte signé des années auparavant entre le Chef de guerre et Jaina Proudmoore pour vaincre Archimonde.","L'attaque humaine fut repoussée et le donjon de Tiragarde tomba. Mais, récemment, les troupes de réserves de l'amiral, menées par le lieutenant Benedict, ont repris le donjon et menacent nos terres, une fois de plus. Ces humains ne montrent décidément aucun respect pour la diplomatie.","Démontrez votre bravoure et voyagez vers le sud, jusqu'au donjon de Tiragarde, pour éliminer ses envahisseurs humains."}, {"Tuer 10 Marins de Kul Tiras, 8 Soldats de marine de Kul Tiras et le Lieutenant Benedict, puis retourner voir Gar'Thok à Tranchecolline."}},
[786] = {"Déjouer l'agression des Kolkar", {"Parlez plus doucement, <classe>. Les centaures kolkar sont juste derrière la crête, à l'ouest, dans la combe des Kolkar.","La nuit dernière, alors qu'ils étaient en manœuvres, je me suis glissé dans leur village et j'ai découvert que ces tristes créatures envisageaient une attaque massive contre les trolls et les orcs de Durotar.","Nous ne pouvons leur permettre de mener leurs desseins à terme. Peut-être avez-vous les capacités requises pour vous infiltrer dans la combe des Kolkar et y détruire leurs plans d'attaque ?","Aux dernières nouvelles, ces plans ont été répartis entre trois de leurs chefs."}, {"Détruire 3 Jeux de Plans d'attaque dans la combe des Kolkar, pour Lar Prowltusk à l'extérieur du village de Sen'jin."}},
[787] = {"La nouvelle Horde", {"Throm'ka, <classe>. Je suis Eitrigg, chargé par Thrall de superviser l'entraînement des recrues.","La Horde n'est plus ce qu'elle était. Jadis, je la quittai, écoeuré par l'influence toujours croissante des sbires avides de la Légion ardente. Durant mon exil, je fus pris par un groupe d'humains, mais secouru ensuite par le Chef de guerre. Lorsqu'il me fit part de sa vision de la Horde, libre de toute influence démoniaque et guidée par les chamans, je réintégrai ses rangs.","Gornek aura d'autres instructions pour vous."}, {"Faites un rapport à Gornek, dans l'Antre"}},
[788] = {"La dent tranchante", {"La première chose à faire sera de tremper un peu ce corps trop tendre. Je pourrais vous envoyer dans les Tarides pour y chasser des kodos ; mais en toute honnêteté, vous nous êtes plus utile <vivant que mort/vivante que morte>.","En revanche, je pense que les sangliers tachetés seront des adversaires à votre hauteur. Vous les trouverez plus au nord."}, {"Tuer 10 Sangliers tachetés, puis retourner voir Gornek, dans l’Antre."}},
[789] = {"L'aiguillon du scorpide", {"De piètres novices comme de grands guerriers ont succombé au dard venimeux des scorpides. Vous trouverez un grand nombre de ces créatures au nord-ouest. Apportez moi dix de leurs queues comme preuve que vous les avez affrontées.","L'antidote à leur venin est préparé à partir de leur aiguillon. Nous conservons de grandes quantités de ce remède pour sauver de jeunes vies comme la vôtre...","Mais vous n'aurez certainement aucun besoin de ce contre-poison, n'est-ce pas ?"}, {"Apporter 10 Queues de scorpides ouvriers à Gornek, dans l’Antre."}},
[790] = {"Sarkoth", {"<classe> ! Je me voyais déjà mort ici, ignoré de tous. Alors que je chassais les scorpides de la vallée, je fus soudain confronté à un spécimen particulièrement puissant. Je parvins à lui porter un grand coup avant qu'il ne referme ses pinces autour de ma jambe.","Je fus, hélas, distrait par cette attaque et en oubliai le redoutable aiguillon, qui se ficha dans ma poitrine. Je vous en prie, tuez ce scorpide pour moi ! Mon honneur doit être lavé. J'ai combattu la créature sur le plateau, plus au sud."}, {"Tuer Sarkoth et rapporter sa pince à Hana'zua."}},
[791] = {"Assumer ses responsabilités", {"Mon âge me rend inapte au maniement des armes, mais j'ai appris à me rendre utile par d'autres moyens.","De ce poste d'observation, je guette les envahisseurs. Nos forces s'accroissant de jour en jour, je me trouve de moins en moins souvent à devoir donner l'alarme en soufflant dans le cor.","Aussi, pour passer le temps, je confectionne du matériel et aide de plus jeunes guerriers que moi à défendre leur pays.","Je peux fabriquer un sac pour vos affaires. Si cela vous intéresse, apportez-moi du canevas, un matériau qu'on trouve communément chez les humains et les centaures."}, {"Apporter 8 Morceaux de canevas à Furl Scornbrow, dans la la tour de guet de Tranchecolline."}},
[792] = {"Les vils quasits", {"Je suis sûre que la Vallée des épreuves vous enseignera beaucoup, jeune <classe>.","J'ai été envoyée dans la Vallée pour vous guider, mais j'y ai découvert une menace croissante.","Un groupe qui se fait appeler la Lame ardente a un convent, ici, dans la Vallée des épreuves. Ils se cachent dans une grotte, au nord-est, d'où leurs vils quasits partent se répandre sur les terres pour les ravager.","Comme première action contre la Lame ardente, je vous demande de vaincre ces quasits. Massacrez-les en nombre et, si vous y avez survécu, revenez me voir."}, {"Tuer 12 Vils quasits.","Retourner voir Zureetha Fargaze, à l’extérieur de l’Antre."}},
[793] = {"Alliances rompues", {"Le Sceau de la Terre est retenu par trois Pierres runiques, placées dans les Piliers de diamant, d'onyx, et d’améthyste dans le Ravin de Lethlor. Avec le Signe de la Terre, vous serez capable d'enlever les Pierres runiques et de déverrouiller le Sceau de la Terre.","Prudence, <nom>, les drakes noirs Cinglenoir et Hematus sont des ennemis à prendre au sérieux. Tuez-les et apportez-moi leurs liens.","Pour atteindre le ravin, allez dans la partie la plus à l'est des Terres ingrates. Attention, car le ravin abrite de nombreux drakes."}, {"Utiliser le Signe de la Terre pour activer les Piliers de diamant, d'opale et d'améthyste, et obtenir les Pierres runiques.","Placer les Pierres runiques dans le Sceau de la Terre pour libérer Cinglenoir et Hematus.","Les exécuter et rapporter les Liens de Cinglenoir, les Entraves d'Hematus et le Signe de la Terre à Gorn."}},
[794] = {"Le médaillon de la Lame ardente", {"Mes divinations me font voir qu'un objet de puissance se trouve loin à l'intérieur du convent de la Lame ardente, et qu'il est gardé par des bêtes et de la magie noire.","Vous devez trouver cet objet, le Médaillon de la Lame ardente et le prendre au convent.","Attention, il sera probablement entre les mains d'un agent de la Lame ardente autrement plus puissant que les quasits.","Partez, <nom>. Vous trouverez le Convent dans une caverne, au nord puis à l'ouest."}, {"Rapporter le Médaillon de la Lame ardente à Zureetha Fargaze, à l'extérieur de l'Antre."}},
[795] = {"Sceau de la Terre", nil, nil},
[804] = {"Sarkoth", {"Ce que vous avez fait pour moi remplit mon cœur de courage. Je ne peux pas me laisser mourir comme ça, je dois me battre !","Je ne puis, cependant, regagner l'Antre sans assistance. S'il vous plaît, <nom>, retournez à l'Antre et informez Gornek de ma situation. Peut-être saura-t-il m'aider."}, {"Informer Gornek, à l'Antre, de la situation critique dans laquelle se trouve Hana'zua."}},
[805] = {"Faire un rapport au Village de Sen'jin", {"Vos épreuves contre la Lame ardente sont finies… ici dans la Vallée. Mais je veux que vous rendiez compte de vos découvertes.","Allez au village troll de Sen'jin et cherchez Maître Gadrin. Le village est à l'est de la vallée, puis à droite à l'embranchement.","Parlez-lui de la Lame ardente, dites-lui que ses membres ont atteint la Vallée des épreuves. Apprenez de lui s'ils ont déjà atteint Sen'jin.","Allez, <nom>, et faites vite. J'ai peur que le mal découvert au convent de la Lame ardente ne présage un plus grand danger."}, {"Parler à Maître Gadrin au village de Sen'jin."}},
[806] = {"Noirs orages", {"Nous ne pouvons permettre à la Lame ardente de prendre pied en Durotar ! Nous devons les détruire avant que ce mal ne suppure !","J'ai mené mes propres enquêtes et j'ai découvert qu'un démoniste de la Lame ardente, le gobelin Fizzle Darkstorm, s'est établi dans les falaises du Tonnerre au nord-ouest. Là-bas, lui et ses sectateurs répandent le chaos.","Trouvez Fizzle, et éliminez-le. Apportez-moi alors sa griffe !"}, {"Apporter la Griffe de Fizzle à Orgnil Soulscar à Tranchecolline."}},
[808] = {"Crâne de Minshina", {"J'entends la voix de mon frère Minshina qui m'appelle dans mes rêves.","Il a été enlevé par Zalazane, le démoniste des îles de l'Écho, à l'est. Et il est mort.","Mais la mort ne l’a pas libéré. Car Zalazane a emprisonné son esprit dans son propre crâne. Dans mes rêves, je peux le voir parmi d'autres crânes, dans un cercle de puissance situé sur la plus grande des îles de l'Écho. Tant qu'il y reste, son esprit est condamné.","Je vous en prie, <nom>. Trouvez le cercle et le Crâne de Minshina. Rapportez-le.","Libérez-le !"}, {"Récupérer le Crâne de Minshina dans le Cercle de puissance, sur les îles de l'Écho.","Le rapporter à Maître Gadrin, au village de Sen'jin."}},
[809] = {"Ak'Zeloth", {"Depuis la dernière grande guerre où la Légion ardente fut défaite, j'ai recherché les sources de corruption démoniaque dans la société des orcs. Le collier que vous m'avez apporté confirme mes craintes.","Il appartient à la Lame ardente, un culte en possession d'un objet de grand pouvoir maléfique qu'on appelle la Graine démoniaque. Vous pouvez la trouver dans les Tarides, au sommet du pic de Brume-funeste. La Graine doit absolument être détruite !","Allez au Poste de garde extérieur, à l'ouest sur la frontière des Tarides et parlez avec mon assistant, Ak'Zeloth. Il vous donnera les prochaines directives."}, {"Parler à Ak'Zeloth, dans les Tarides."}},
[812] = {"Besoin d'un remède", {"<nom>... votre minutage est parfait. J'espère que je pourrai en dire autant de votre célérité.","J’ai manqué d'attention en combattant des Queues-venin aux alentours, et j'ai été piqué par l'un d'entre eux. Le poison m'affaiblit au moment même où je parle. A ce rythme, il me reste peut-être une heure à vivre. Mais je vais avoir besoin de vous...","Kor'ghan, à Orgrimmar, sait comment confectionner l'antidote. Trouvez-le... et faites vite, <nom>. Je ne vais pas pouvoir tenir beaucoup plus longtemps. Il devrait se trouver dans la Faille de l'ombre."}, {"Trouver Kor'ghan à Orgrimmar et obtenir l'Antidote du Queue-venin. L'apporter ensuite à Rhinag près de la frontière nord-ouest de Durotar."}},
[813] = {"La recherche de l'antidote", {"Les scorpides Queue-venin sont parmi les plus dangereux en Durotar. Rhinag a manqué de sagesse lorsqu'il a les a chassés sans emporter d'antidote, mais à présent, une remontrance est vraiment la dernière chose dont il a besoin.","Apportez-moi des Glandes à venin de n'importe quel Queue-venin, et je lui préparerai un antidote. Vous en trouverez près de l'entrée d'Orgrimmar.","Dépêchez-vous, <nom>. L'un d'entre nous a besoin de votre aide."}, {"Apporter 4 Glandes à venin de Queue-venin à Kor'ghan à Orgrimmar."}},
[815] = {"Des oeufs pour l'omelette", {"Bah ! J'allais presque oublier... faut qu'je ficelle le p'tit déjeuner","<nom>, bougez- vous l'train et allez m'trouver quelques oeufs de fouette-queue. M'en faut au moins trois si j'veux en avoir aussi pour le déjeuner de demain.","Le fouette-queue Griffesang peut être farouche et protège ses oeufs jusqu'à la mort, ça oui. Ils les enfouissent parfois bien trop profondément pour pouvoir les déterrer ; mais, bon, si vous fouinez un peu du côté des îles de l'Écho, au sud-est du village de Sen'jin, vous en trouverez d'accessibles à foison, partout sur ces îles."}, {"Rapporter 3 Oeufs de fouette-queue au cuisinier Torka, à Tranchecolline."}},
[816] = {"L'égaré", {"<nom>, pouvez-vous m'aider ? Mon fils, Kron, est parti chasser il y a quelques jours et il n'est toujours pas revenu. Il est allé à l'ouest, vers les Tarides, pour traquer des crocilisques, le long du fleuve Furie-du-Sud. Je crains le pire.","Aussi fort soit-il, sa fierté mal placée l'a toujours conduit dans les pires situations. Lorsque je lui ai dit que les crocilisques étaient puissants et brutaux, il s'est emporté et est parti.","Si vous ne le ramenez pas, découvrez au moins ce qu'il est devenu...Même si vous devez pour cela ouvrir le ventre de chaque crocilisque, le long des bancs de sable de la Furie-du-Sud."}, {"Trouver un indice du sort de Kron et en informer Misha Tor'kren, à la ferme, au nord-ouest de Tranchecolline."}},
[817] = {"Proie nécessaire", {"La plupart des peaux que nous utilisons proviennent des tigres de Durotar, <nom>. Couvertures, armures, tentes, nous avons beaucoup de raisons de chasser ces bêtes, et autant de les laisser prospérer.","Le temps est venu de cueillir la récolte, pour ainsi dire. Nos rangs grandissent et les besoins de mon peuple sont supérieurs à nos provisions. Il me faut plus de peaux pour subvenir à ces besoins.","Apportez-moi 4 Fourrures de tigres de Durotar, et je vous récompenserai. Vous pourrez les trouver sur les îles au sud d'ici."}, {"Apporter 4 Peaux de tigres de Durotar à Vel'rin Fang, au village de Sen'jin."}},
[818] = {"Un esprit parfaitement éduqué", {"Même si mes yeux sont faibles, j'y vois encore suffisamment clair. Je me repose souvent sur mes compétences en alchimie pour les magies qui autrefois m'étaient aisées. Mais je refuse de prendre <un apprenti/une apprentie>. Aucun orc ou troll assez digne ne s'est jamais présenté.","Et vous, en êtes-vous digne ? Bien sûr que vous en êtes digne... que vous croyez l'être.","J'ai besoin de certaines choses. Pouvez-vous les obtenir ?","Il me faut des yeux de makruras intacts, et des fioles de mucosités de clampants. De n'importe quels makruras ou clampants en Durotar. Nous nous reparlerons bientôt."}, {"Apporter 4 Yeux de makrura intacts et 8 Fioles de mucus de clampant à maître Vornal, au village de Sen'jin."}},
[819] = {"Tonneau vide de Chen", {"Un petit écriteau, placé sur l'un des bouts du tonneau, dit :","\"Chen Stormstout-","Que laper règne sur la terre comme au ciel, et que les spiritueux soient avec votre esprit.\""}, {"Trouver quelqu'un qui en sache davantage au sujet du Tonneau vide de Chen."}},
[821] = {"Tonneau vide de Chen", {"Voudriez-vous goûter à l'homonyme de Chen ? Ahhh ! La Stormstout est une puissante mixture. Chen a appris la recette à mon mentor, et mon mentor me l'a apprise. J'aurais besoin que vous me trouviez certaines choses, mais je vous assure : le choc en vaut la peine.","Apportez-moi 5 Canines de lion des savanes, de n’importe quel lion des savanes ; 5 Rognons de trotteur des plaines, de n'importe quel trotteur des plaines, et 1 Corne de lézard-tonnerre. Ça devrait parfaitement faire l'affaire.","Vous pouvez trouver ces ingrédients dans les Tarides."}, {"Apporter 5 Canines de lion des savanes, 5 Rognons de trotteur des plaines, et 1 Corne de lézard-tonnerre au brasseur Drohn à Ratchet."}},
[822] = {"Tonneau vide de Chen", {"Sacré caractère, cette Brune d’Orage, hein ?","J’ai une autre recette que j’ai apprise de Chen. Ça vous intéresse ? Ça ne sera pas long, et ça peut même vous être utile si vous comptez poursuivre vos aventures.","Apportez-moi cinq glandes d’éclair d’orageux, une glande salivaire de faucon-tonnerre et un foie de kodo. Vous trouverez tout ça dans les Tarides.","Comme je l'ai dit, ce breuvage décoiffe. C'est la tête de Trogg dont je vous ai parlé."}, {"Apporter 5 Glandes de corne-éclair, 1 Glande salivaire de faucon-tonnerre et 1 Foie de kodo au brasseur Drohn à Ratchet."}},
[823] = {"Un rapport à Orgnil", {"Nous autres au village de Sen'jin avons nos problèmes, et nous vous remercions pour votre aide. Mais les nouvelles que vous apportez à propos de la Lame ardente pourraient annoncer des ennuis pour tout le monde.","Un orc nommé Orgnil Soulscar est posté à Tranchecolline. Il protège Durotar de maux tels que la Lame ardente. Allez lui parler et informez-le de notre demande. Donnez-lui également des nouvelles de la Vallée des épreuves. Il voudra savoir ces choses.","Tranchecolline est au nord."}, {"Parler à Orgnil Soulscar à Tranchecolline."}},
[824] = {"Je'neu du Cercle terrestre", {"Le Cercle terrestre est un groupe de chamans dédié à l'étude et à la préservation des éléments. Ils en savent plus sur les dieux anciens fous, les vieilles légendes et les savoirs antiques que la plupart des savants de ce monde.","Heureusement pour vous, je connais quelqu'un du Cercle terrestre, ici, à Ashenvale. Allez à l'avant-poste de Zoram'gar, sur la côte de la grève de Zoram. Montrez à Je'neu Sancrea ce que nous avons trouvé. Si quelqu'un peut découvrir quelle est la signification de ce globe, c'est bien lui."}, {"Apporter le Globe d'eau souillée à Je'neu Sancrea, à l’Avant-poste de Zoram'gar, dans Ashenvale."}},
[825] = {"Des épaves", {"L'un de mes éclaireurs les plus observateurs me rapporte que les épaves de la flotte de Proudmoore sont toujours près de la côte de Durotar, juste à l'est du donjon de Tiragarde.","Ce n'est un secret pour personne que les humains, alliés à ces petites bêtes idiotes que sont les gnomes, possèdent des connaissances avancées en mécanique. Nous devons apprendre précisément les savoirs de tous nos ennemis potentiels. Et notre peuple bénéficiera de ces nouveaux savoirs, également.","Nagez à travers les épaves, <nom>, et trouvez pour moi les outils de l'Alliance."}, {"Gar'Thok de Tranchecolline veut que vous retrouviez 3 Outils gnomes parmi les épaves près de la côte."}},
[826] = {"Zalazane", {"Le sorcier-docteur Zalazane demeure sur les îles de l'Écho, à l'est. Ce sont les îles que nous appelions autrefois « maison ».","De là, il envoie ses trolls sur le continent et déchaîne ses maléfices sur notre peuple pour placer davantage des nôtres sous son emprise.","Il faut l'arrêter.","Battez Zalazane et ses serviteurs, d'anciens trolls Darkspear perdus pour notre cause. Apportez-moi sa tête, et je saurai que son règne maléfique est terminé."}, {"Battre Zalazane.","Tuer 8 Trolls vaudou et 8 Trolls maléficiés.","Apporter la Tête de Zalazane à Gadrin."}},
[827] = {"Rocher du crâne", {"<nom>. La Lame ardente a infesté la caverne appelée le Rocher du crâne, à l'est d'Orgrimmar. Ils y accomplissent des rituels abominables, et brûlent leur propre chair avec des Colliers brûlants.","En portant ces colliers, je pense que les sectateurs s'harmonisent avec la puissance démoniaque. Mais pour le confirmer, je dois en posséder une collection afin de les étudier.","Partez pour le Rocher du crâne et prenez les Colliers brûlants que vous trouverez sur les membres du culte. Apportez-les-moi, et je dévoilerai leurs mystères."}, {"Prendre les Colliers brûlants des sectateurs au Rocher du crâne.","Les apporter à Margoz à son camp."}},
[828] = {"Margoz", {"L'un de nos chamans, Margoz, en sait plus sur la corruption de la Lame ardente. Il parle d'une caverne appelée Rocher du crâne dans les montagnes, juste à l'extérieur d'Orgrimmar, qui abrite un important groupe de sectateurs.","Avant que vous ne partiez pour le Rocher du crâne, parlez à Margoz. C'est un sage et son conseil est précieux.","Il campe au nord-est, entre la côte et le Ravin asséché.","Suivez son avis, mais quoi qu'il dise, <nom>, je veux que vous écrasiez ce culte !"}, {"Parler à Margoz."}},
[829] = {"Neeru Fireblade", {"Les Colliers brûlants que vous m'avez apportés sont des instruments puissants et démoniaques. Deviner leur origine est cependant, je le crains, au-delà de mes capacités de chaman. Nous aurons besoin qu'un démoniste les étudie.","Apportez un Collier brûlant à Neeru Fireblade. Bien qu'il soit un redoutable démoniste, il professe qu'il utilise ses pouvoirs pour contrecarrer les démons, et proclame que ses recherches sur l'occulte sont bénignes. Que cela soit vrai ou pas, nous pouvons avoir besoin de son aide contre le culte démoniaque en Durotar. Vous trouverez Neeru à Orgrimmar, dans la Faille de l'ombre."}, {"Apporter un Spécimen de collier à Neeru Fireblade à Orgrimmar."}},
[830] = {"Les ordres de l'amiral", {"Vous ouvrez la vieille enveloppe décolorée et découvrez un document d'apparence officielle. Vous reconnaissez le sceau de l'amiral Proudmoore.","Ça a l'air important. Peut-être que Gar'Thok, le commandant de Tranchecolline, serait intéressé par cette information."}, {"Apporter les Ordres de l'amiral Proudmoore à Gar'Thok de Tranchecolline."}},
[831] = {"Les ordres de l'amiral", {"On ne peut pas faire confiance aux humains. Nous avons combattu à leurs côtés le cœur empli de lassitude, sachant qu'ils nous trahiraient un jour.","La mort de l'amiral Proudmoore n'a pas été suffisante pour clore cette longue tradition de tromperie. Cette vermine avait bien préparé ses plans avant qu'il ne rencontre la mort.","Son influence ne s'éteindra pas non plus avec Benedict. Qui sait combien de temps il reste avant que la prochaine vague d'hommes de Proudmoore ne déferle sur nos rivages ?","Nous devons apporter ces ordres à Nazgrel à Orgrimmar immédiatement ! On peut le trouver dans les logements de Thrall."}, {"Porter les Ordres de l'amiral Proudmoore à Nazgrel, dans les appartements de Thrall à Orgrimmar."}},
[832] = {"Ombres ardentes", {"<Des mots émanent du pendentif et dans votre esprit.>","Gazz'uz ?","Gazz'uz… au rapport !","On raconte que vous avez été découvert au rocher du crâne. Vous devez vous préparer à une attaque !","Gazz'uz, je vous ordonne de parler ! Parlez ou je m'assurerai que Neeru Fireblade apprenne votre présence, et il vous tombera dessus comme la misère sur le monde.","Ne testez pas vos capacités contre Neeru...","... Gazz'uz, vous êtes là ?"}, {"Apporter cet Oeil à Neeru Fireblade."}},
[833] = {"Cimetière sacré", {"Seuls les plus vaillants taurens sont enterrés aux Rochers rouges, notre terre funéraire sacrée. C'est un honneur accordé aux grands guerriers qui ont aidé à construire et à défendre Thunder Bluff et à ceux qui ont donné leur vie pour le bien de leur tribu et de leurs chefs.","Mais il apparaît qu'un infâme danger menace notre terre sacrée. Un groupe d'Intrus Dos-hirsute ravage le cimetière et je suis trop vieux et trop faible pour les repousser.","Ils doivent pourtant être chassés sans pitié, <nom>."}, {"Le Gardien du savoir Raintotem veut que vous tuiez 8 Intrus Dos-hirsute aux Rochers rouges."}},
[834] = {"Des vents dans le désert", {"Je suis Rezlak, l'un des gars de Gazlowe. Le patron m'a envoyé pour donner un coup de main aux orcs, ici en Durotar. Les choses se sont bien passées, à part pour les caravanes. Je n'arrive pas à assurer leur sécurité ! Ça rend mon boulot un peu plus difficile, vous savez ?","La dernière livraison – ils ont promis que je passerais ! – a été chipée par les harpies Terrevent au... c'était où déjà... au canyon de Tranchevent ?","Faut que je récupère ces marchandises ou je n'arriverai jamais à rien ! Suivez le grand canyon vers le sud, vous trouverez le ravin coupant exactement les parois à l'est et à l'ouest."}, {"Retrouver 5 Sacs de marchandises et les rendre à Rezlak près d'Orgrimmar."}},
[835] = {"Des lignes sécurisées", {"Gazlowe m'a dit une fois \"Rezlak, il y a une chose que tu dois savoir dans la vie : si tu veux qu'une chose soit faite de manière correcte, fais-la toi-même.\"","Je ne peux supporter la perte de nos caravanes, alors je vais m'occuper des choses en personne. Le Ravin asséché est quelque part dans la partie est du canyon de Tranchevent. Si nous y tuons toutes les harpies, les routes seront sûres.","Bien sûr, il y a autre chose que Gazlowe m'a enseigné, et c'est \"En payant bien, tout est possible.\" Alors, vous allez m'aider, <nom> ?"}, {"Tuer 12 Terrevent farouches et 8 Sorcières des tempêtes Terrevent pour Rezlak près du Ravin asséché."}},
[836] = {"À la rescousse de OOX-09/HL !", {"Le robot émet à nouveau la voix du gnome :","\"Je dois déplacer OOX-09/HL dans un endroit dégagé et sûr pour qu'il puisse entamer sa longue procédure de décollage. Il est équipé d'un camouflage, mais il me faut du temps pour le rendre opérationnel de mon côté. Il faut que vous l'escortiez jusqu'au... disons jusqu'au littoral qui s'étend au-delà des Haut-Surplombs l ! Ce serait un endroit parfait, et assez long, pour que tout puisse se mettre en route !\"","Escortez-le jusque là-bas, et venez me parler à Baie-du-Butin ! Oglethorpe Obnoticus, terminé.\""}, {"Escorter OOX-09/HL jusqu'au littoral au-delà des Haut-Surplombs, puis rendre compte à Oglethorpe Obnoticus à Baie-du-Butin."}},
[837] = {"Empiètement", {"Quand nous sommes arrivés, les hurans Tranchecrin possédaient la majeure partie de la terre, et étaient comme une épine dans notre flanc. Par des efforts constants, nous sommes parvenus à en chasser une grande partie, mais ils restent toujours bien fortifiés dans certains secteurs.","Cela a suffisamment duré, cependant. Pour notre propre sécurité, nous ne pouvons tolérer la présence des Tranchecrins sur notre territoire. On peut trouver leurs camps à l'ouest d'ici. Cherchez les roncières et vous les trouverez. Aujourd'hui, nous les chassons de Durotar, et demain, peut-être, de tout Kalimdor."}, {"Tuer 4 hurans Tranchecrin, 4 Eclaireurs Tranchecrin, 4 Lève-poussière Tranchecrin et 4 Gardes de guerre Tranchecrin pour Gar'Thok de Tranchecolline."}},
[838] = {"Scholomance", {"Sur l'île de Caer Darrow se trouve Scholomance - une école servant de forteresse au Fléau. Nos ennemis y résident, à l'abri, et libres de poursuivre des recherches qui ne font qu'accroître leur pouvoir. Une porte se dresse sur le chemin, et malgré tous nos efforts, elle ne peut être franchie sans que la bonne clé ne lui soit présentée.","Le Pharmacien Dithers en connaît beaucoup sur Scholomance, et a une idée quant à la façon d'obtenir cette clé. Vous m'avez prouvé que vous êtes capable d'atteindre des objectifs, aussi je vous confie cette tâche. Allez lui parler et entretenez-le à ce propos."}, {"Parler au Pharmacien Dithers à la Barricade, dans les Maleterres de l'ouest."}},
[840] = {"Sous les drapeaux de la Horde", {"Hum ! Vous avez l'air plutôt <fort/forte>.","Écoutez, mon bon ami Kargal a besoin de nouvelles recrues pour servir dans les Tarides. Je sais que vous voulez faire ce qui est bon pour la Horde. Eh bien, voici votre chance !","Apportez cette lettre de recrutement à Kargal et voyez s'il la signe..."}, {"Suivre la route de l'ouest, celle qui part de Tranchecolline, et qui arrive aux Tarides par un pont.","S'arrêter à l'avant-poste orc sur le pont.","Donner votre lettre de recrutement à Kargal Battlescar aux Tarides."}},
[841] = {"Une autre Source d'énergie ?", {"Laissez-moi deviner… vous n’avez pas accompli l'un de vos horribles exploits à temps. Non, attendez, encore mieux, vous l'avez laissée tomber dans le sable ! Bien, qu’importe la raison, j’ai d’autres sources d'énergie que je peux vous vendre. Cette fois, cependant, ça vous coûtera dix, OUI DIX Outres de Bat-le-désert !","Les temps sont durs par ici vous savez, <nom>, et les sources d'énergie ne poussent pas sur les arbres ! Hmm, mais si c'était le cas... peut-être que nous pourrions FABRIQUER un arbre à sources d'énergie..."}, {"Apporter 10 Outres de Bat-le-désert à l'ingénieur en chef Bilgewhizzle à Gadgetzan pour qu'il les échange contre une autre source d'énergie."}},
[842] = {"Conscription à la Croisée", {"Apportez cette lettre de recrutement signée à la Croisée.","Sergra Darkthorn, que sa peau soit maudite, est la responsable là-bas. C'est une affreuse chaman qui vous saoulera de paroles avant de vous laisser faire quoi que ce soit d'utile. Thrall est convaincu qu'elle est spéciale, mais je n'en suis pas si sûr.","Vous aurez à vous faire votre propre opinion, <morveux/morveuse>..."}, {"Suivre la route à l'ouest à partir du Poste de garde extérieur de Kargal.","A l'intersection en T, tourner à gauche et suivre la route vers le sud.","Trouver Sergra Darkthorn où les routes se croisent à l'intérieur de la Croisée."}},
[843] = {"La réclamation de Gann", {"Je foule ces chemins dans la solitude.","Les nains de Bael Modan n'ont aucun respect pour ma terre. D'horribles explosions creusent profondément le sol tandis que des machines éventrent les collines. La terre qui autrefois était le foyer et la nourrice de ma tribu est aujourd'hui grouillante de ces nains destructeurs.","Les tentatives diplomatiques pour arriver à un accord ont échoué. À présent l'heure de l'action radicale a sonné.","Repoussez par la force les nains de Bael Modan et apportez-moi le journal de leur chef, le Prospecteur Khazgorm."}, {"Gann Stonespire veut que vous tuiez 15 Excavateurs de Bael'dun et 5 Surveillants de Bael'dun.","Apporter le Journal de Khazgorm à Gann Stonespire."}},
[844] = {"La menace des trotteurs des plaines", {"Votre première proie sera une proie facile.","Les trotteurs des plaines, à l'est, attaquent constamment nos réserves de nourriture et sont devenus une véritable nuisance.","Abattez-les et revenez me voir avec leurs becs."}, {"Collecter 7 Becs de trotteurs des plaines et les apporter à Sergra Darkthorn à la Croisée."}},
[845] = {"Les zhévras", {"Les zhévras ne sont pas les bêtes les plus redoutables des Tarides, mais ils sont un peu plus dangereux que les trotteurs des plaines. Ne vous en faites pas, nous vous enverrons combattre des proies plus conséquentes en temps utile, mais vos dents ont encore besoin d'être aiguisées.","Chassez les zhévras au nord et au sud, et récupérez leurs sabots. Apportez-les-moi et peut-être que la prochaine fois nous vous indiquerons quelque chose de plus coriace."}, {"Tuer des Messagers zhévras et apporter 4 Sabots de zhévra à Sergra Darkthorn à la Croisée."}},
[846] = {"La revanche de Gann", {"Il est évident que le prospecteur dépendait des explosifs et des véhicules bruyants pour déchirer le sol. Nous pouvons nous attendre à ce qu'un nouveau contremaître le remplace bientôt.","J'aimerais faire goûter aux nains leur propre médecine. D'après ces plans, si nous combinons de la nitroglycérine avec de la pulpe de bois et du nitrate de sodium, nous pouvons créer une charge explosive. Les fusiliers, les soldats et les officiers du donjon possèdent ces ingrédients.","Récupérez ces objets et apportez-les-moi. Je confectionnerai une charge que les nains ne sont pas près d'oublier."}, {"Gann Stonespire veut que vous lui apportiez 6 Fioles de Nitroglycérine, 6 Paquets de Pulpe de bois, et 6 Echantillons de Nitrate de sodium."}},
[847] = {"La ruse du raptor", {"Ce… Ce pouvoir… Est-ce le pouvoir des chamans que Tor'gan a essayé de me montrer ? Je dois admettre que c'est intéressant. Dites à Tor'gan qu'il a réussi à me convaincre… pour l'instant. Je vais écouter ce qu'il a à dire."}, {"Parler à Tor'gan."}},
[848] = {"Spores fongiques", {"Les oasis des Tarides abritent un mystère. L'énergie de la vie coule dans leur eau, revigorant les plantes et les bêtes qui la boivent.","Revigorante et stimulante.","Un champignon pousse près de ces oasis. Ses spores détiennent des propriétés que nous, les Apothicaires de Lordaeron, trouvons utiles.","Apportez-moi ces spores et vous obtiendrez ma gratitude."}, {"Apporter 4 Spores fongiques à l'Apothicaire Helbrim à la Croisée."}},
[849] = {"La revanche de Gann", {"En suivant les plans de Khazgorm, la fabrication d’une charge explosive ne devrait pas poser de problème.","Ah ! Nous y voilà.","À présent, <nom>, je vais vous confier une tâche spéciale. Prenez ce Bâton explosif et utilisez-le pour faire sauter la machine volante à Bael Modan.","Non seulement cette machine est une partie cruciale de l’infrastructure du site, mais elle sert également de machine de guerre. Plus important encore, c'est un symbole de pouvoir et une insulte portée à la mémoire de ma tribu.","Faites ma fierté en concluant cette quête héroïque et vous serez <récompensé/récompensée>."}, {"Détruire la Machine volante à Bael Modan et retourner voir Gann Stonespire."}},
[850] = {"Les chefs des Kolkar", {"Les centaures nuisent aux taurens depuis des années. Et récemment, les centaures Kolkar des Tarides sont devenus un danger réel. D'habitude, ils étaient désorganisés, mais de nouveaux chefs ont émergé. Et les ont unis.","Afin de préserver nos possessions ici, ces chefs centaures doivent être éliminés.","Apportez-moi la tête de Barak Kodobane. Il campe près des Bassins oubliés au nord.","Battez-le, puis revenez me voir."}, {"Apporter la Tête de Barak à Regthar Deathgate, à l'ouest de la Croisée."}},
[851] = {"Verog le Derviche", {"Le centaure Verog le Derviche rôde dans les Tarides, et il va être difficile à trouver. Mais il est basé à la tente de commandement des centaures, à l'Oasis stagnante au sud-est. Il peut être possible de l'attirer.","Partez pour l'Oasis stagnante au sud-est et attaquez les centaures près de la tente de commandement. Ce sera dangereux, mais si vous tuez suffisamment de centaures, ils sonneront l'alarme. Et Verog viendra.","Apportez-moi sa tête et je la mettrai avec celle de Barak Kodobane."}, {"Apporter la Tête de Verog à Regthar Deathgate, à l'ouest de la Croisée."}},
[852] = {"Hezrul Marque-de-sang", {"Hezrul Marque-de-sang est le chef des centaures Kolkar dans les Tarides. Il est féroce, brutal et malin. L’éliminer sèmerait la confusion et briserait l’unité des Kolkar. Le danger qu'ils représentent serait ainsi largement diminué.","Aussi tuez-le. Tout comme pour Barak et Verog, apportez-moi sa tête.","Hezrul dirige son peuple depuis l'Oasis luxuriante, au sud."}, {"Apporter la Tête d'Hezrul à Regthar Deathgate, à l'ouest de la Croisée."}},
[853] = {"Apothicaire Zamah", {"J'ai fait une émulsion avec les spores que vous m'avez apportées. Je dois à présent l'envoyer à mon associée, l'Apothicaire Zamah. Si vous pouviez la lui apporter, elle pourra vous offrir quelque chose de nos magasins d'alchimie.","Elle est aux Bassins de la vision, une caverne qui se trouve sous les chamans de Thunder Bluff. Le chemin menant à cette caverne est bien caché, mais vous pourrez le trouver sur la Cime des Esprits.","Dépêchez-vous ! Cette émulsion ne restera pas puissante longtemps. Elle doit être livrée et utilisée au plus vite."}, {"Apporter les Spores fondues à l'Apothicaire Zamah à Thunder Bluff, avant que la limite de temps ne soit dépassée."}},
[854] = {"Un voyage à la Croisée", {"J'avais prédit votre arrivée, jeune <classe>. Bien que peut-être vous ne soyez plus si jeune.","Je crois qu'il est temps de vous aventurer plus profondément dans ces terres. Je sens que vous êtes <destiné/destinée> à de grandes choses.","C'est la Croisée que vous devez chercher à présent, dans la mesure où nos vies se sont mêlées à celles des orcs et qu'ils ont besoin de notre aide. Descendez cette route vers l'est et suivez-la vers le nord en direction de la Croisée. Trouvez Thork dans ses murs, il est béni par la Terre-mère."}, {"Parler à Thork, à la Croisée des Tarides."}},
[855] = {"Les bracelets de centaures", {"Les Kolkar représentent une menace pour les ambitions de la Horde dans les Tarides ; nous devons réduire leur nombre. De fait, j'ai une mission pour vous, en échange d'une récompense.","Allez sur les terres des Kolkar, tuez leurs guerriers et récupérez des bracelets de centaures. Revenez quand vous en aurez un tas ; la Horde vous récompensera largement."}, {"Apporter 15 Bracelets de centaures à Regthar Deathgate, à l'ouest de la Croisée."}},
[857] = {"La larme des lunes", {"La puissance était mienne ! Dans mes propres mains ! Et je veux la retrouver ! La retrouver, je vous dis !","Ces fichus Excavateurs l'ont découverte… Elle était tellement belle. Avoir trouvé la Larme des lunes, c'était la première grande découverte qu'on avait faite à Bael Modan.","Elle aurait dû être mienne ! Mienne je vous dis !","Mais le général Twinbraid devint jaloux de la puissance qu'elle me donnait. Il l'a enfermée à clé dans le donjon. L'a traitée de maudite, il l'a fait ! L'imbécile !","D'autres la voulaient et ils devaient être arrêtés ! Ce bâtard de Twinbraid m'a chassé.","Maintenant, je la veux à nouveau ! Je la veux je vous dis !"}, {"Feegly l’Exilé veut que vous retrouviez pour lui la Larme des lunes."}},
[858] = {"Contact", {"Ce n'est sûrement pas Sputtervalve qui vous a <envoyé/envoyée> ? Je suis coincé ici. Je suis entré sans réaliser que j'avais besoin d'une clé pour déverrouiller la colonne de mouvement du déchiqueteur. L'un des autres opérateurs m'a demandé si tout allait bien, et j'ai paniqué ! Au lieu de lui dire que je n'avais pas la clé, je lui ai dit qu'il y avait un genre de problème mécanique.","Nous devons sortir d'ici au plus vite ! Montez à la salle de contrôle au sommet du derrick, le superviseur devrait avoir une clé pour ce déchiqueteur.","Sortez-moi de là !"}, {"Récupérer la Clé de contact et l'apporter à Wizzlecrank."}},
[860] = {"Sergra Darkthorn", {"Si vous souhaitez suivre la voie du chasseur, alors votre voyage vous mène jusqu'aux Tarides. Ses créatures sont robustes et féroces. Vous y apprendrez beaucoup en les traquant, et votre volonté grandira.","Parlez à Sergra Darkthorn. Elle sera votre premier guide dans les Tarides.","Elle se trouve à la Croisée. Pour l'atteindre, allez à l'est en partant du village Bloodhoof et en vous rendant dans les Tarides, puis allez au nord lorsque le chemin se sépare. Et attention tandis que vous voyagez. Les Tarides réservent de grands dangers pour ceux qui manquent de préparation ou de prudence."}, {"Parler à Sergra Darkthorn, à la Croisée."}},
[861] = {"La voie du chasseur", {"Vous êtes avide d'explorations, ça se voit. J’avais aussi le désir d'explorer, autrefois…","D'explorer, et de chasser. Car chasser est le plus grand honneur d'un tauren.","Si vous désirez suivre cette voie, Melor Stonehoof peut vous y aider. Il est à Thunder Bluff, sur la Cime des chasseurs.","Pour lui prouver vos compétences et votre résolution, apportez-lui les griffes de Rôdeurs des basses plaines de Mulgore. Ils sont robustes et astucieux, une proie parfaite pour de jeunes <classe>s qui suivent la voie du chasseur."}, {"Apporter 4 Griffes de Rôdeurs des basses plaines à Melor Stonehoof à Thunder Bluff."}},
[862] = {"Brouet de rat", {"Les <classe>s comme vous doivent plutôt avoir faim ici dans les Tarides. Moi, je crève la dalle tout au long de la journée.","Je vais vous mitonner un chouette brouet. J'ai juste besoin de quelques rats fouisseurs.","Il semble que ces sales nains de Bael Modan soient submergés par le festin favori de tout un chacun. À présent descendez vers le sud, à Bael Modan, et apportez-moi quelques rats fouisseurs.","Vous ne pouvez pas vous prétendre <aventurier chevronné/aventurière chevronnée> si vous n'avez pas passé du temps à tuer des rats ! Ha !"}, {"Apporter 8 Rats fouisseurs à Boustiff' à l'est de la Croisée."}},
[863] = {"La fuite", {"Je suppose que j'apprendrai en le faisant… Ça ne devrait pas être trop difficile. Juste quelques boutons par-ci par-là, et un levier ou deux… Alors, êtes-vous <prêt/prête> à y aller ?"}, {"Protéger Wizzlecrank et le Déchiqueteur gobelin volé jusqu'à Sputtervalve à Ratchet."}},
[864] = {"De retour vers l'Apothicaire Zinge", {"\"Et voilà, la source d'énergie est enlevée et les données sont préservées. Bien, faites attention sur la route et tout ça. J'espère que cette horrible expérience s'est bien déroulée pour vous, blablabla…\"","Le gobelin se détourne pour s'occuper des demandes de la Compagnie des eaux de Gadgetzan. Vous mettez la trousse d'analyse verrouillée à l'abri en vue du voyage de retour vers Undercity. Là-bas, l'apothicaire Zinge vous attend, vous et la trousse d'analyse de terrain verrouillée."}, {"Apporter la Trousse d'analyse de terrain verrouillée à l'apothicaire Zinge à Undercity."}},
[865] = {"Les cornes de raptors", {"Les raptors des Tarides sont plus malins que les autres. Et je pense que cette intelligence se trouve dans leurs cornes ! Si tel est vraiment le cas, alors je pourrais réduire leurs cornes en poudre et les utiliser pour en faire une \"potion d'intelligence\". Je pourrais en tirer une fortune !!!","Et vous pouvez m'aider. Trouvez-moi des Cornes de raptors intactes sur des Faucheurs solécailles. Ils parcourent les Tarides septentrionales, et le nord des Tarides près de la frontière avec la forêt d'Ashenvale."}, {"Collecter 5 Cornes de raptors intactes prises sur des Faucheurs solécaille, et les apporter à Mebok Mizzyrix à Ratchet."}},
[866] = {"Les échantillons de racines", {"Les herbes qui poussent dans les Tarides ne sont pas tout à fait pareilles à celles des autres contrées. Leurs propriétés sont à peu près semblables, mais l'eau des oasis les transforme légèrement. Elles sont juste un peu différentes.","J'aimerais étudier ces différences... pour voir si nous pouvons les exploiter !","Si vous êtes <un herboriste compétent/une herboriste compétente>, cueillez les herbes que vous trouvez dans les Tarides. Apportez-moi ces échantillons que je puisse les comparer aux herbes des autres régions."}, {"Apporter 8 Echantillons de Racines à Mebok Mizzyrix, à Ratchet."}},
[867] = {"Les harpies pillardes", {"Ces maudites harpies massacrent nos caravanes d'approvisionnement depuis trop longtemps déjà. Il est temps que nous tranchions quelques-unes de leurs gorges et que nous réduisions leur nombre.","J'ai jeté des coups d'œil discrets à leurs nids et j’ai compris leur hiérarchie. L'attaque la plus dévastatrice devra détruire une bonne partie de leur chaîne inférieure de commandement, pour que nous puissions ensuite remonter jusqu'au sommet.","Nous allons commencer en douceur. Tuez des harpies et des Volplumes Ensorçaile au nord-ouest, et apportez-moi 8 Griffes d'Ensorçaile. Ce sera bien pour commencer..."}, {"Collecter 8 Griffes d'Ensorçaile.","Les apporter à Darsok Swiftdagger à la Croisée."}},
[868] = {"La chasse aux œufs", {"Hé <nom> ! On m'a fait venir à la Croisée pour surveiller la zone et noter ce qui s'y déroule, pour mes maîtres à Orgrimmar.","L'un de mes sujets d'étude est cette créature, ressemblant à un insecte, et que nous avons découverte au sud dans le Champ des géants. Nous savons peu de choses sur ces bêtes, alors je me fais un point d'honneur d'en découvrir plus. Elles semblent posséder quelque intelligence, plus que la plupart des simples animaux.","Prenez cette griffe d'excavation et récupérez quelques œufs dans les tertres où vivent ces bêtes. Mais attention, si elles sont alertées par quoi que ce soit, elles vous attaqueront."}, {"Apporter 12 Œufs de silithide et la Griffe d'excavation à Korran, à la Croisée."}},
[869] = {"Les raptors voleurs", {"Il y a peu, une cargaison d'argent a été volée à notre tour de garde. Cet argent était destiné à payer les gardes de la Croisée, et nous voulons le récupérer.","Le point étrange est que… nous avons arrêté un voleur la nuit même du vol. Et il s'agissait... d'un raptor ! Incroyable !","Je ne sais pas quels raptors seraient intéressés par cet argent. Mais qu’importe ! Je veux qu’ils soient tués afin qu'ils ne nous volent pas à nouveau !","Partez à leur recherche dans les Tarides et récupérez leurs têtes !"}, {"Apporter 12 Têtes de raptor à Gazrog, à la Croisée."}},
[870] = {"Les Bassins oubliés", {"Il y a longtemps de cela, les Tarides étaient un endroit luxuriant, fourmillant de vie. Mais la guerre et le chaos ont ravagé la terre, l'ont brûlée et finalement laissée telle une bogue desséchée. Ainsi se sont passées les choses, et cela chagrine mon cœur.","Mais ces dernières années, de nouvelles oasis se sont formées dans les Tarides, et la vie frémit à nouveau. Et loin sous le sol, nous les druides sentons une puissance se frayer un chemin vers la surface.","Nous devons en trouver la source. Partez pour les Bassins oubliés, au nord-ouest de la Croisée. Parcourez les eaux et cherchez la source de cette puissance, puis revenez me voir."}, {"Rendre compte de vos découvertes à Tonga Runetotem."}},
[871] = {"Stopper les attaques des hurans", {"Tous les hurans sont nos ennemis, <nom>. Certains ne représentent simplement qu'une nuisance plus importante que les autres.","La tribu Tranchecrin a attaqué nos lignes d'approvisionnement en provenance de Durotar, nous causant des soucis sans nombre. J'ai des éclaireurs qui recherchent le chef de ces expéditions, mais en attendant, toutes les pertes que vous pourriez leur infliger nous seraient d'une grande aide.","Commencez au nord-est, en direction de Durotar. Vous pouvez toujours repérer leurs tanières grâce aux ronces géantes qui sortent de la terre. Cherchez-les. Trouvez-les. Et tuez-les."}, {"Dans la tribu Tranchecrin, tuer 8 Sourciers, 8 Tisseuses d’épines et 3 Chasseurs, puis retourner voir Thork."}},
[872] = {"La perturbation cesse", {"L'un de mes éclaireurs a été témoin d'une attaque sur une caravane en provenance de Durotar, <nom>. Les coupables sont effectivement des hurans de la tribu Tranchecrin. L'un d’eux en particulier dirige les attaques : Kreenig Grondegroin. On l'a vu au nord-est d'ici, juste au sud de la route venant de Durotar. Pour régler le problème, Kreenig doit mourir.","Pour s'assurer que les attaques cessent, je suggérerais quand même que l'on fasse un exemple.","Tuez Kreenig et des membres de sa tribu, puis revenez me voir lorsque vous aurez sa Défense."}, {"Tuer 8 Géomanciennes Tranchecrin, 8 Défenseurs Tranchecrin et Kreenig Grondegroin.","Puis apporter la Défense de Kreenig Grondegroin à Thork à la Croisée."}},
[873] = {"Isha Awak", {"Le grand Isha Awak est le seigneur de ces eaux. Grande est sa force et solennelle est sa fierté. Les humains de la côte le craignent, car il en a occis beaucoup parmi eux.","Mais il ne me fait pas peur. Je suis reconnaissante de son existence. Il est un digne défi et une proie honorable.","Si vous êtes <prêt/prête> alors partez nager et cherchez Isha Awak, le Destin des profondeurs.","Son esprit demeure dans son cœur, et l'entendre battre, c'est voir votre destinée."}, {"Apporter le Cœur d'Isha Awak à Mahren Skyseer."}},
[874] = {"Mahren Skyseer", {"Vous avez chassé les bêtes des Tarides. Votre esprit est fort. Mais un chasseur doit toujours être prêt. Un véritable chasseur doit être capable de traquer une proie sur n'importe quelle piste. Des montagnes ou des marais ne sauraient le rebuter.","Et pas même la mer.","Allez trouver ma soeur, Mahren. Elle chasse les grandes bêtes maritimes le long de la côte. Ce sera votre professeur.","Mais soyez <prudent/prudente> tandis que vous la recherchez, car la côte des Tarides est détenue par des humains."}, {"Parler à Mahren Skyseer."}},
[875] = {"Lieutenants harpies", {"Ha ha ha ! <nom>. Vous en voulez encore ? Bien. J'aime entendre ça.","Cette fois-ci je veux que vous tuiez certaines des lieutenants harpies des Collines arides. Seules les Tueuses Ensorçaile peuvent être lieutenants, et vous pouvez les reconnaître à la bague qu'elles portent. C'est une vilaine bande, qui dirige beaucoup de subalternes dans le secteur. Tuer 6 d'entre elles portera un coup décisif à leur matriarcat.","Faites en sorte qu'elles meurent dans la douleur, <nom>. Nous voulons montrer à ces femelles qu'il n'est pas sage de choisir la Horde comme adversaire."}, {"Rassembler 6 Anneaux de lieutenant harpie sur des Tueuses Ensorçaile et les apporter à Darsok Swiftdagger à la Croisée."}},
[876] = {"Serena Plumesang", {"Vous vous en êtes tellement bien <sorti/sortie> avec les lieutenants et autres harpies subalternes que j'aimerais que vous fassiez une dernière chose pour moi.","Serena Plumesang est la sœur d'une harpie nommée Plumesang qui a été tuée par Rexxar il y a longtemps. Apparemment, ces attaques sur les caravanes de la Horde sont une vengeance pour la mort de sa sœur.","J'ai besoin que vous lui tranchiez la gorge et que vous me rapportiez sa tête. Je la placerai sur la prochaine caravane que nous enverrons... Histoire de donner aux harpies un sujet de réflexion."}, {"Tuer Serena Plumesang et apporter sa tête à Darsok Swiftdagger à la Croisée."}},
[877] = {"L'oasis stagnante", {"Tout comme celle que vous avez trouvée aux Bassins oubliés, il se peut qu'il y ait des fissures dans les autres oasis des Tarides. Si tel est le cas, alors peut-être que ces fissures sont la source de vie des oasis.","Nous devons tester cette hypothèse.","Tenez, prenez ces graines. Elles sont mortes et stériles, mais emportez-les à l'Oasis stagnante, au sud-est. S'il y a une fissure là-bas, alors placez-y les graines... et observez."}, {"Retourner voir Tonga Runetotem à la Croisée, après avoir enquêté à l'Oasis stagnante."}},
[878] = {"Tribus en guerre", {"Vous devez frapper mes ennemis <grogne>. Vous devez aider Mangletooth et les Tranchecrins en affaiblissant les Dos-hirsute Les deux tribus accordent beaucoup d'importance à la spiritualité et sont très liées à la terre et à ses pouvoirs <grogne>. Cela nous apporte de la force en tout, et pour la guerre en particulier.","Si vous voulez découvrir qui a causé à Thork <grogne> et à la Horde un tel supplice, alors aidez Mangletooth ! Attaquez les chefs spirituels des Dos-hirsute au nord. Affaiblissez-les afin que les Tranchecrins puissent les vaincre et régner sur les Tarides."}, {"Tuer 6 Sourciers Dos-hirsute, 12 Tisseurs d’épines Dos-hirsute et 12 Géomanciennes Dos-hirsute puis retourner auprès de Mangletooth au camp Taurajo dans les Tarides."}},
[879] = {"Une trahison de l'intérieur", {"Trois membres de ma tribu sont venus du Kraal pour diriger les raids contre la Horde, <nom> <grogne>. Ils sont brutaux et malins, et si vous parvenez à les vaincre, alors votre Croisée et même la plus grande partie des Tarides seront en paix... <grogne>.","Nak, Kuz et Lok Plaie-des-orcs sont ceux que vous recherchez. Ils se trouvent loin au sud des Tarides. L'un lance des sorts, l'autre est un pisteur, et leur chef, celui qu'on nomme Plaie-des-orcs <grogne> est un guerrier. Tuez-les, <nom>, tout comme ils m'ont tué."}, {"Tuer Nak, Kuz et Lok Plaie-des-orcs et apporter leurs crânes à Mangletooth au Camp Taurajo dans les Tarides."}},
[880] = {"Source de vie", {"Vos découvertes sont incroyables, <nom>. Ces oasis détiennent des propriétés qui doivent provenir d'une source extérieure. Ou peut-être intérieure.","Je veux savoir comment ces fissures affectent les bêtes qui boivent aux eaux des oasis.","Chassez les gueules d'acier des oasis à l'Oasis luxuriante et à l'Oasis stagnante. Apportez-moi leurs carapaces pour que je les examine."}, {"Apporter 8 Carapaces de Gueules d'acier altérées à Tonga Runetotem à la Croisée."}},
[881] = {"Echeyakee", {"Blanchebrume, Echeyakee dans la langue des taurens, est le roi des lions des savanes. Il chasse si furtivement qu’il est comme une brume légère et blanche sur la terre. Et il tue si vite que ses proies n'ont pas le temps d'avoir peur ni de souffrir.","Les taurens disent qu'il est à la fois la miséricorde et la mort.","Rendez-vous à son repaire, soufflez dans cette corne, et il viendra."}, {"Apporter la Peau d'Echeyakee à Sergra Darkthorn à la Croisée."}},
[882] = {"Ishamuhale", {"Ishamuhale, Lancecroc, est le plus redoutable des raptors solécaille. Il ne chasse pas pour se divertir, ni pour se nourrir. Il chasse par passion. Il tue car sa nature est de tuer.","Et vous allez apprendre de cette nature, <nom>, car votre voie suit à présent les traces griffues d'Ishamuhale.","Commencez la chasse. Tuez sa proie favorite, un zhévra, puis emmenez sa carcasse à l'arbre mort au nord-ouest de Ratchet. Ishamuhale sera attiré par l’odeur.","Soyez <prêt/prête> lorsqu'il arrivera."}, {"Apporter le Croc d'Ishamuhale à Jorn au Camp Taurajo."}},
[883] = {"Lakota'mani", {"Le Sabot de Lakota'mani est un symbole de la force du grand kodo."}, {"Apporter le Sabot de Lakota'mani à Jorn au Camp Taurajo."}},
[884] = {"Owatanka", {"Lorsqu'on le touche, des étincelles jaillissent de l'épine caudale d'Owatanka."}, {"Apporter l'Epine caudale d'Owatanka à Jorn au Camp Taurajo."}},
[885] = {"Washte Pawne", {"La Plume de Washte Pawne est douloureuse au toucher."}, {"Apporter la Plume de Washte Pawne à Jorn Skyseer au Camp Taurajo."}},
[886] = {"Les oasis des Tarides", {"Les druides de Thunder Bluff sentent une étrange puissance qui fuit dans les Tarides, à l'est de Mulgore.","Nous avons envoyé Tonga Runetotem pour enquêter sur la source de cette puissance, mais nous craignons qu'il ait besoin d’aide.","Voyagez jusqu'aux Tarides et parlez à Tonga. Il sera à la Croisée. Pour atteindre cet endroit, prenez la route à l'est en sortant du village Bloodhoof. Entrez dans les Tarides, puis tournez vers le nord lorsque la route se divise. Continuez dans cette direction et vous trouverez la Croisée.","Et ne quittez pas la route, <nom>, car les Tarides sont hostiles."}, {"Parler à Tonga Runetotem à la Croisée."}},
[887] = {"Les flibustiers des Mers du sud", {"J'aurais vraiment souhaité que le vice-amiral Grezzlik fasse un meilleur travail pour que nos navires marchands puissent sillonner les mers en toute sécurité. Avec tout l'or que les princes marchands ont versé, voir autant de pirates attaquer mes navires et voler mes marchandises me rend malade !","J'ai entendu dire que les Flibustiers des Mers du Sud ont établi un camp juste au sud d'ici. Ils ont été une source d'irritation constante et si Grezzlik ne s'en occupe pas, alors, vous, peut-être, pourriez m'en débarrasser."}, {"Tuer 12 Brigands et 6 Canonniers des Mers du Sud pour Gazlowe de Ratchet."}},
[888] = {"Course à la lentille", {"J'aimerais récupérer toutes mes marchandises. Hélas, je suis sûr que ce n'est pas possible. Mais il y a des choses que je dois absolument récupérer !","Comme vous pouvez le constater, mon observatoire est prêt à fonctionner, mais il manque la plus petite des deux lentilles. La première est arrivée de Durotar par caravane, mais la deuxième a été envoyée par bateau, et elle ne m'est jamais parvenue ! Je n'ai pas non plus reçu mes bottes de chez Drizzlik à Baie-du-Butin !","Partez pour le camp des pirates, et voyez si vous les trouvez."}, {"Retrouver la Cargaison de bottes et la Lentille de télescope pour Gazlowe de Ratchet."}},
[889] = {"L'esprit du vent", nil, nil},
[890] = {"La livraison perdue", {"J'ai attendu durant une éternité l'arrivée de mon dernier chargement de marchandises de Baie-du-Butin ! Je suis à peu près sûr qu'il a dû être volé par les Flibustiers, mais pour que je le sois complètement, pourriez-vous descendre du côté des docks et demander à Dizzwig si mes marchandises ont déjà été livrées à mon entrepôt sans que je le sache ?","Tenez, descendez mon Grand livre à Dizzwig et demandez-lui de vérifier à nouveau mes inventaires par rapport à ses registres."}, {"Apporter le Grand Livre de Gazlowe au Maître des quais Dizzywig."}},
[891] = {"Sous le feu de l'ennemi", {"J'ai encore perdu un navire contre ces humains trop zélés du Fort du Nord !","Nous devons nous occuper de ces bâtards. Leur paranoïa les met dans un état de totale frénésie. Ces gâchettes faciles, ces rustres insouciants canardent tout ce qui passe dans leur ligne de mire.","Partez vers le sud, le long de la Côte des marchands, vers le Fort, et persécutez-les. Apportez les médailles de leurs soldats comme preuve. Et pour l'amour de ma flotte marchande, tuez le capitaine Fairmount et ses canonniers trop enthousiastes."}, {"Le capitaine Thalo'thas Brightsun de Ratchet veut que vous collectiez 10 Médailles de Theramore et que vous tuiez le Capitaine Fairmount, le Canonnier Whessan et le Canonnier Smythe."}},
[892] = {"La livraison perdue", {"Tout a l'air correct selon mes registres, <nom>. Retournez-y et dites à Gazlowe que sa chance a tourné. Je ne serais pas surpris que les pirates aient pris ses marchandises.","Je ne peux rien faire d'autre pour lui. Oh !… Vous pourriez lui dire quand même que j'ai quelque chose venant d'Undermine à son nom, s'il peut faire un saut pour le chercher."}, {"Rapporter le Grand Livre de Gazlowe à Gazlowe à Ratchet."}},
[893] = {"Des armes de choix", {"Les hurans Tranchecrin, au sud, au-delà du Champ des géants, n'ont pas de forgerons compétents, que je sache. Mais ils ont apparemment commencé à construire des armes plus solides. J'aimerais mettre la main sur différents types de ces armes pour acquérir leurs techniques, si possible.","Si vous souhaitez œuvrer pour l'honneur de la Horde, apportez-moi des exemples de leurs armes. Obtenez une dague de leurs traqueurs, une baguette de leurs prophètes, et un bouclier de leurs frénétiques. Cela devrait parfaitement faire l'affaire."}, {"Apporter un Poignard de Tranchecrin, une Baguette Tranchecrin carbonisée et un Bouclier Tranchecrin à Tatternack Steelforge au camp Taurajo dans les Tarides."}},
[894] = {"Samophlange", {"La KapitalRisk a construit un petit centre de recherches loin au nord, au sud-ouest de la Videfange. Je ne sais pas vraiment ce qu'ils y font, mais j'ai quand même réussi à découvrir qu'ils font des expériences avec quelque chose qu'ils appellent \"Samophlange\".","À présent, que diable est le Samophlange ? Eh bien, quoi que ce soit, je veux l'examiner, et j'ai besoin que quelqu'un le prenne.","J'ai réussi à obtenir une copie de leur Manuel de la console de contrôle, vous devriez ainsi comprendre comment en désengager le Samophlange."}, {"Accéder à la Console de contrôle au Centre de recherches de la KapitalRisk."}},
[895] = {"ON RECHERCHE : baron de Longrivage", {"ON RECHERCHE !","Baron de Longrivage, capitaine de \"l'Insouciant\".","Le capitaine Longrivage de Gilnéas commande des navires pour le compte des Flibustiers des Mers du sud, il est recherché pour actes de piraterie. Ses restes doivent être identifiables !","Gazlowe"}, {"Apporter la tête du baron de Longrivage à Gazlowe à Ratchet."}},
[896] = {"La bonne fortune d'un mineur pauvre", {"Etant Maître des quais d'un port plein d'activité comme Ratchet, je prends toujours le pouls de l'information. Je sais tout des échanges de biens et d'argent entre ici et Baie-du-Butin.","La dernière nouvelle que j'ai entendue concerne la mine des Pierriers de la KapitalRisk, au nord-est de la Videfange. L'un des mineurs y a découvert une émeraude aussi grosse que votre poing. Je connais quelques acheteurs qui voudraient mettre la main sur un tel joyau, et je suis prêt à faire 50-50."}, {"Prendre l'Emeraude œil-de-chat à l'un des Surveillants ou l'un des Massacreurs de la KapitalRisk pour le Maître des quais Dizzywig à Ratchet."}},
[897] = {"Le Moissonneur", {"Cette étrange créature, qui ressemble à un insecte, n'a pas l'air de faire partie de l'ordre naturel, comme si elle n'appartenait simplement pas aux Tarides."}, {"Apporter la Tête du Moissonneur à Jorn Skyseer."}},
[898] = {"Hors du fort", {"Enfin ! Quelqu'un est venu me sauver !","Il est incroyable que vous ayez passé les gardes. Ces fanatiques de Theramore sont devenus fous. Ils ont coulé notre navire et m'ont emprisonné. Je suis le seul survivant. Ils ont dit que j'étais leur ennemi et un danger pour l'Alliance.","Moi ! Un danger pour leur parodie d'Alliance ? J'étais matelot sur un transport de gnôle entre Ratchet et Baie-du-Butin.","Mais assez parlé. Aidez-moi à retourner à Ratchet, voulez-vous ? Dites-moi lorsque vous serez <prêt/prête> et nous prendrons la fuite."}, {"Escorter en toute sécurité Gilthares Firebough jusqu'au capitaine Brightsun à Ratchet."}},
[899] = {"Consumé par la haine", {"Connaissez-vous l’affliction du doute, <classe> ? Sachez seulement que je suis là toute la journée à scruter l’horizon pour repérer ces monstres. J’ai tué chaque homme-sanglier que j’ai rencontré depuis, mais ma soif de sang est loin d’être étanchée. Peut-être qu’au lieu d’aller au nord, vers la Croisée, j’aurais dû me diriger sur Taurajo.","Vous, <classe>, vous pourriez m’aider.","Tuez-en autant que vous pourrez. Apportez-moi leurs défenses et nous boirons ensemble à leur destruction."}, {"Apporter 60 Défenses de huran Dos-hirsute à Mankrik, à la Croisée."}},
[900] = {"Samophlange", {"Les trois valves de contrôle sont à présent ouvertes. Les indications sur le panneau de contrôle vous amènent à penser qu'elles doivent être fermées pour que l'appareil puisse être arrêté."}, {"Fermer la Valve de contrôle du carburant, la Valve de réglage et la Valve de contrôle principale, puis utiliser à nouveau la console de contrôle."}},
[901] = {"Samophlange", {"Maintenant que l'appareil est désactivé, le panneau de contrôle peut s'ouvrir et on peut sortir le Samophlange de la console. À présent que tout est en place, il reste à trouver la clé qui déverrouillera la console."}, {"Obtenir la Clé de la console de l'artisan Sniggles, puis l'utiliser sur la console de contrôle."}},
[902] = {"Samophlange", {"Ouvrir la console de contrôle révèle des entrelacs de fils, de tubes et d'autres objets mécaniques étranges, dont la plupart ne semblent avoir aucune utilité. En les écartant et en fouillant au fond le la console, vous découvrez le Samophlange. Vous le retirez d'un coup sec de son réceptacle."}, {"Rapporter le Samophlange à Sputtervalve à Ratchet."}},
[903] = {"Les rôdeurs des Tarides", {"Il est temps à présent de courir après quelque chose qui mord vraiment. Les rôdeurs des Tarides sont une espèce résistante. Résistante, pleine de ressources, sournoise… et mortelle. Vous les trouverez parmi les herbes hautes. Ils sont nombreux au sud-ouest de la Croisée.","Soyez <prudent/prudente> tandis que vous les chassez, <nom>, ou vous ne serez plus <le prédateur/la prédatrice>, mais bien la proie."}, {"Collecter 7 Griffes de Rôdeur des savanes pour Sergra Darkthorn à la Croisée."}},
[905] = {"Les Faucheurs furieux", {"À présent, <nom>, la chasse devient plus difficile. Vous devez battre votre proie, et trouver le chemin jusqu'à son repaire.","Chassez les raptors solécaille au sud. Tuez-les et enlevez leurs plumes. Placez les plumes dans les nids des Faucheurs au sud-ouest de l'oasis stagnante. Montrez à leurs semblables que vous ne les craignez pas !"}, {"Tuer des raptors solécaille et récupérer leurs plumes. Placer ces plumes dans les 3 nids de Faucheurs solécaille, puis retourner voir Sergra Darkthorn à la Croisée."}},
[906] = {"Une trahison de l'intérieur", {"Apportez la tête de Lok à Thork à la Croisée, <nom> <grogne>. Il devrait savoir ce qu'il est advenu de ma tribu sans le moindre mot--ha, mais je ne m'attends pas à ce qu'il croie que Mangletooth y a <grogne> contribué. Mais il est certain qu'il vous récompensera pour vous être <occupé/occupée> de tels actes <reniflement>.","Je vais continuer à vous bénir du pouvoir d'Agammagan aussi longtemps que je resterai dans cette cage, <nom>. Jusque-là, adieu."}, {"Apporter le Crâne de Lok à Thork à la Croisée des Tarides."}},
[907] = {"Les lézards-tonnerre enragés", {"À présent, <nom>, vous devez combattre les grands lézards-tonnerre. Ces bêtes sont si imposantes que le sol tremble lorsqu'ils parcourent les plaines. Prenez garde lorsque vous les combattez, car un faux pas peut vous jeter directement sous leurs pattes.","Vous trouverez les lézards-tonnerre au sud des Tarides. Ils sont connus sous les noms de Groins-tempête, Orageux et Têtes-tonnerre. Revenez me voir lorsque vous les aurez vaincus."}, {"Apporter 3 doses de Sang de lézard-tonnerre à Jorn Skyseer, à Camp Taurajo."}},
[910] = {"Un tour sur les quais", {"J'ai entendu parler des grands bateaux qui naviguent entre Cabestan et Baie-du-Butin ! C'est génial de voyager sur l'océan et de faire de grandes batailles navales. Peut-être que je serai un puissant marin de la Horde quand je serai grand ! Ouais !","Vous voulez bien m'emmener voir les quais de Cabestan, s'il vous plaît ? Je vous promets que je sauterai pas dans l'eau et que je ne ferai pas de bêtise. Je veux juste voir les grands bateaux et sentir l'eau salée de la mer ! Ouais !"}, {"Emmenez votre protégé sur les quais de Cabestan, dans les Tarides."}},
[911] = {"L'accès vers la frontière", {"Ils disent qu'Orneval est la nouvelle frontière, et que le Rempart de Mor'shan dans les Tarides est à sa porte. Ça doit être génial d'être un pionnier, de trouver tout ce dont on a besoin dans la nature sauvage ! Ils disent que c'est là que la Horde et l'Alliance se battront le plus dans l'avenir, et je veux y être, <nom> !","Vous voulez bien m'emmener au Rempart de Mor'shan pour que je puisse voir à quoi ressemble la frontière, s'il vous plaît ? Je veux voir l'endroit où la Horde chasse ces sales elfes de la nuit puants !"}, {"Emmenez votre protégé au Rempart de Mor'shan dans la région nord des Tarides, juste à l'entrée d'Orneval."}},
[913] = {"Le cri du faucon-tonnerre", {"Le faucon-tonnerre, <nom>, est une bête féroce. Il est temps pour vous d'en affronter. Vous devez trouver où il rôde, puis m'apporter ses ailes comme preuve de votre chasse fructueuse.","Parvenez-y, et l'heure de notre séparation sonnera bientôt."}, {"Trouver et tuer un faucon-tonnerre, et rapporter ses ailes à Jorn Skyseer, au Camp Taurajo."}},
[914] = {"Les druides du Croc", {"Les druides des Cavernes des lamentations, les druides du Croc, sont une aberration. Ils appartenaient à un ordre de druides nobles voulant soigner les Tarides, mais ils tentent maintenant de plier cette terre à leurs fantasmes.","Ils ont quatre chefs, et chacun possède une gemme de rêve. Même à présent, leurs visages me hantent ! Vous devez les tuer et m'apporter les gemmes. Ces terres pourront peut-être ainsi connaître à nouveau la paix.","Allez, <nom>. Vous les trouverez, tapis, au fond des Cavernes des lamentations."}, {"Apporter les Gemmes de Cobrahn, d'Anacondra, de Pythas et de Serpentis à Nara Wildmane à Thunder Bluff."}},
[915] = {"Je veux une glaaaaace !", {"DE LA GLACE ! Oh, s'il vous plaaaaîîîîîît, je peux avoir de la glace ?! La glace à la fraise est ma glace préférée, et la meilleure glace à la fraise du monde, c'est celle d'Aguilor & Dagharn ! C'est ma glace préférée dans le monde entier !","J'en ai mangé il y a longtemps aux courses des Salines, mais il paraît qu'ils en vendent en ville ! S'il vous plaît ? S'il vous plaaaaîîîîîît ? Avec une deuxième boule sur le dessus ??!?!"}, {"Allez chercher de la glace à la fraise pour votre protégé. Le gamin semble préférer la marque de glace Aguilor & Dagharn."}},
[916] = {"Le venin des Tissebois", {"Je suis venu à Sombrevallon pour observer les araignées Tissebois qui vivent dans la grotte de Sombrefil. Elles sont cousines d'une variété bien plus petite. Je pense que l'arbre-monde a eu un profond effet sur elles, et j'aimerais des spécimens à étudier pour confirmer cette hypothèse.","D'abord, j'aimerais un peu de leur venin. Prélevez des glandes à venin de Tissebois sur les araignées à l'intérieur et autour de la grotte de Sombrefil, au nord. Je pourrai alors étudier les similitudes avec le venin de leurs cousines plus petites."}, {"Apporter 10 Glandes à venin Tissebois à Gilshalan Windwalker, à Aldrassil."}},
[917] = {"L'œuf d'araignée Tissebois", {"À présent que j'ai le venin de ces araignées, j'aimerais étudier des spécimens vivants. Malheureusement, capturer une araignée géante est plus que je ne peux vous demander, jeune <classe>. Et je ne pourrais pas moi non plus maîtriser une araignée géante.","Si vous pouvez trouver un œuf non encore éclos, alors il sera plus facile de posséder des spécimens, et je peux organiser les choses pour les enfermer avant que les araignées ne sortent.","Il doit y avoir un nid, au plus profond de la grotte de Sombrefil. S'il vous plaît, partez chercher un œuf dans ce nid et rapportez-le-moi."}, {"Apporter un Œuf d'araignée Tissebois à Gilshalan à Aldrassil."}},
[918] = {"Les graines de sylvain", {"Les sylvains de Teldrassil sont des élémentaires de la Nature. D'une certaine manière, ils reflètent l'ordre naturel des plantes et des animaux sur notre Grand Arbre.","Aussi est-ce inquiétant de voir les sylvains devenir furieux.","Je pense que cela a quelque chose à voir avec la terre. J'ai travaillé à différentes méthodes pour alimenter les plantes et aimerais les essayer sur des graines de sylvains. S'il vous plaît, pouvez-vous collecter des graines de sylvain autour du lac Al'Ameth et me les apporter ?"}, {"Apporter 8 Graines de sylvain à Denalan au lac Al'Ameth."}},
[919] = {"Les pousses de sylvain", {"De petits sylvains poussent près des eaux du lac Al'Ameth. J'ai bien peur que nous ne puissions plus rien faire pour ces jeunes sujets. Nous devons les arracher avant qu'ils ne grandissent trop et causent des problèmes.","Lorsque vous serez près du lac, arrachez les pousses de sylvains que vous verrez. Aidez-nous à garder une terre propre !"}, {"Apporter 12 Pousses de sylvain à Denalan au lac Al'Ameth."}},
[920] = {"Les appels de Tenaron", {"<nom>, n'est-ce pas ? J'ai entendu dire que Tenaron vous cherchait. Vous le trouverez à l'endroit le plus haut d'Aldrassil.","Il se peut que nous vivions longtemps encore, mais ce serait mieux de ne pas le faire attendre trop longtemps."}, {"Parler à Tenaron Stormgrip au sommet d'Aldrassil à Sombrevallon."}},
[921] = {"La couronne de la Terre", {"Il est temps pour vous de partir en quête de votre destinée, <nom>. Mais avant que vous ne soyez <prêt/prête> à vous aventurer au-delà de nos forêts enchantées, il y a beaucoup à apprendre de notre histoire récente.","Notre peuple a beaucoup changé depuis la Bataille du mont Hyjal. Nordrassil n'est plus que l'ombre pâle de ce qu'il était autrefois... Son pouvoir avait battu Archimonde et repoussé la Légion ardente...","Il y a une tâche que vous devez accomplir. Partez pour le Puits de lune au nord d'Aldrassil et apportez-moi une fiole de son eau."}, {"Remplir la Fiole de cristal et la rapporter à Tenaron Stormgrip au sommet d'Aldrassil."}},
[922] = {"Rellian Greenspyre", {"<nom>, pouvez-vous apporter l'une des graines que vous avez trouvées à mon ami, Rellian Greenspyre ? Il est druide à Darnassus, et la dernière fois que nous avons parlé il m'a révélé son intérêt concernant mes travaux sur les sylvains. Il avait quelques idées de son côté et il appréciera d'avoir une graine comme spécimen à étudier.","Merci, <nom>. Vous m'avez été d'un grand secours. J'espère qu'un jour, vous verrez les fruits de mon labeur.","Rellian parcourt souvent les chemins de l'Enclave cénarienne à Darnassus."}, {"Apporter une Graine de sylvain à Rellian Greenspyre à Darnassus."}},
[923] = {"Tumeurs", {"Une maladie décime les sylvains. Nous essayons d'en trouver la cause, mais tant que nous n’y parvenons pas nous devons arracher les sylvains contaminés, pour la sécurité de Teldrassil. Ceux qui poussent près du lac d'Aigue-vive, au nord de Teldrassil, sont les plus atteints. Nous devons les arracher !","Détruisez les sylvains que vous trouvez, et prélevez la Tumeur moussue qui se développe sur eux. Apportez-moi les tumeurs afin qu'elles soient brûlées."}, {"Apporter 5 Tumeurs moussues à Rellian Greenspyre à Darnassus."}},
[924] = {"La Graine démoniaque", {"La Graine démoniaque est un outil puissant au service de la Légion ardente. Elle repose dans une caverne du pic de Brume-funeste, au-dessus d'un diabolique autel de feu.","J'ai préparé des Pierres de puissance qui, lorsqu'elles seront placées près de la graine, couperont sa connexion avec la Légion.","Prenez une pierre sur la table et portez-la à l'autel de feu au sommet du pic de Brume-funeste. Voyagez à l'ouest, puis au nord lorsque la route se divise. Le pic sera sur votre gauche. L'escalade la plus sûre s'effectue par la face nord.","Et faites vite, le pouvoir de la pierre faiblit avec le temps."}, {"Prendre une Pierre de puissance imparfaite. L'apporter à l'autel de feu avant que le pouvoir de la pierre n'expire, puis revenir à Ak'Zeloth."}},
[925] = {"L'empreinte de Cairne", {"On dit que Cairne Sabot-de-Sang est l'un des plus grands héros de la Horde de tous les temps. Quand je serai grand, je veux être un héros de la Horde, moi aussi !","Vous connaissez Cairne, <nom> ? Vous pouvez me faire une faveur, s'il vous plaît ? Vous pouvez avoir son empreinte de sabot pour moi ? Je sais pas comment lui demander, et c'est sans doute une personne bien trop occupée pour s'occuper de quelqu'un comme moi... Mais pas vous ! Vous êtes <un aventurier/une aventurière>, comme lui ! Je parie qu'il vous donnerait facilement son empreinte de sabot !","S'il vous plaît ?"}, {"Allez aux Pitons-du-Tonnerre pour tenter d'obtenir l'empreinte de sabot de Cairne pour votre protégé."}},
[926] = {"Pierre de puissance imparfaite", nil, nil},
[927] = {"Le cœur moussu", {"Le cœur de Noiremousse le Fétide est couvert d'une mousse sombre et huileuse. En fait, ce qui permet d’identifier ce cœur, c’est sa pulsation, lente et rythmée.","Peut-être que Denalan voudra voir ce cœur ?"}, {"Apporter le Cœur moussu à Denalan au lac Al'Ameth."}},
[928] = {"La couronne de la Terre", {"Bien que je puisse vous entretenir encore longtemps des Puits de lune et de Teldrassil, je dois vous faire partir. Corithras Moonrage va vous attendre. J'ai versé l'eau de la fiole que vous m'avez apportée dans ce récipient que vous devez lui remettre.","Cherchez Corithras. Vous le trouverez au Puits de lune à Dolanaar. Suivez la route vers le sud à partir d'Aldrassil et vers Sombrevallon, et continuez à suivre les pavés ronds lorsque la route se dirige vers l'ouest.","Attention, restez toujours sur la route, <nom>. Il y a de dangereuses bêtes, le soir, dans les forêts."}, {"Apporter le Récipient à moitié plein à Corithras Moonrage à Dolanaar."}},
[929] = {"La couronne de la Terre", {"Tout d'abord, laissez-moi vous en dire plus à propos de la quête que vous devez accomplir. Les druides de Darnassus utilisent l'eau des Puits de lune de Teldrassil, et leurs Puits de lune doivent être remplis de temps en temps. En utilisant ces fioles spécialement conçues, vous pouvez collecter l'eau des Puits de lune.","Apportez ce récipient jusqu'au Puits de lune à l’extérieur du village de Brise-stellaire à l'est, et remplissez-le d'un peu de ses eaux. Puis revenez me voir. Lorsque vous aurez accompli votre tâche, je reprendrai l'histoire là où Tenaron s’est arrêté."}, {"Remplir la Fiole de jade et la rapporter à Corithras Moonrage à Dolanaar."}},
[930] = {"Le fruit luminescent", {"Pendu à cette plante, sous ses feuilles, se trouve un grand fruit rond.","Denalan voudra étudier ce fruit, c'est certain."}, {"Apporter le Fruit luminescent à Denalan au lac Al'Ameth."}},
[931] = {"Les racines flamboyantes", {"Les feuilles de la plante flamboient dans la lumière de la forêt, lui donnant une aura pulsante et sinueuse.","Vous pensez que Denalan en souhaiterait un spécimen."}, {"Apporter les Racines flamboyantes à Denalan au lac Al'Ameth."}},
[932] = {"Haine folle", {"Je vous avertis, <nom>, cette affaire doit rester entre nous. Les satyres nous embarrassent déjà suffisamment, et celui-ci est beaucoup trop proche.","Il s'appelle le seigneur Melenas. Il réside dans la caverne proche de Gangreroche où il a rassemblé de nombreux guerriers grells. Son cœur est aussi noir que la nuit, et il prépare quelque chose d'abominable.","Vous devez le trouver à l'intérieur de sa caverne proche juste au nord d'ici, et m'apporter sa tête."}, {"Tuer le seigneur Melenas et apporter sa tête à Tallonkai Swiftroot à Dolanaar."}},
[933] = {"La couronne de la Terre", {"Il y a un autre Puits de lune au sud-est de l'entrée vers Darnassus, sur les rives des bassins d'Arlithrien. Les Sentinelles ont des problèmes lors de leurs patrouilles dans ce secteur. Ils sont attaqués par les furbolgs du clan Pin-tordu, de plus en plus sauvages.","Soyez <prudent/prudente> quand vous chercherez le Puits, et gardez la main près de vos armes."}, {"Remplir la Fiole de tourmaline et la rapporter à Corithras Moonrage à Dolanaar."}},
[934] = {"La couronne de la Terre", {"Cependant, tout n'était pas parfait sur Teldrassil. Les plans soigneusement conçus de Staghelm pour le nouvel Arbre Monde s'étaient déroulés comme il l'avait espéré. Mais il y eut un petit problème, un problème sans doute à l'origine des nombreuses difficultés rencontrées sur Teldrassil.",". Mais je ne vais pas m'étendre maintenant sur ce sujet. Vous devez vous rendre au dernier puits de lune, au nord-ouest, dans la clairière de l'Oracle. Sous les branches de l'arbre-oracle se trouve le premier et le plus puissant de nos puits. Remplissez un flacon de son eau et revenez me voir."}, {"Remplir la Fiole d'améthyste et la rapporter à Corithras Moonrage à Dolanaar."}},
[935] = {"La couronne de la Terre", {"Sans la bénédiction d'Alexstrasza la Lieuse de vie et celle de Nozdormu l'Éternel, la croissance de Teldrassil ne fut pas parfaite. On rapporte que des bêtes étranges sortaient de ses racines et que des furbolgs enragés attaquaient des voyageurs.","Je ne peux qu'espérer que l'archidruide trouvera rapidement la solution. Je vais verser le contenu de toutes les fioles que vous m'avez amenées dans ce récipient, pour que vous l'apportiez à Darnassus.","Apportez-le à Fandral Staghelm, vous le trouverez dans le bosquet des druides."}, {"Apporter le Récipient plein à l'archidruide Fandral Staghelm à Darnassus."}},
[936] = {"L'aide à l'archidruide Runetotem", {"Pardonnez-moi, <classe>, mais j'aimerais prendre quelques instants de votre temps, si cela est possible.","Le Cercle cénarien de Thunder Bluff est très intéressé par des <aventuriers aguerris tels/aventurières aguerries telles> que vous pour lui prêter main-forte dans un projet crucial. Bien que je n’en connaisse pas les détails, je peux vous dire que Hamuul Runetotem lui-même mène ce projet.","Je vous en prie, si cela vous intéresse, allez lui parler en personne à la Cime des Anciens dans Thunder Bluff."}, {"Parler à l'archidruide Hamuul Runetotem à Thunder Bluff."}},
[937] = {"La clairière enchantée", {"On m'a envoyée ici avec un petit groupe de Sentinelles pour protéger l'arbre-oracle des harpies qui ont construit leurs nids tout autour de la clairière. Nous essayons de les repousser petit à petit.","Lorsque l'arbre-oracle a tenté d'envoyer un messager avec un rapport, il a été attaqué par un groupe de harpies et tué.","Si vous vous sentez suffisamment <préparé/préparée> pour cette tâche, allez à leurs nids, tuez-les et rapportez-moi leurs ceintures en guise de preuves de vos exploits."}, {"Acquérir 6 Ceintures plumesang et les apporter à la Sentinelle Arynia Cloudsbreak à la clairière de l'Oracle."}},
[938] = {"Brume", {"La fourrure du tigre à dents de sabre est d'un gris particulier, tranchant avec les couleurs de la forêt environnante. Tandis qu'elle repose là, vous remarquez qu'elle est gravement blessée, de profondes lacérations déchirent son dos et son ventre.","Elle lève la tête et vous regarde, ses yeux bleus brillant d'intelligence. Elle a l'air de souhaiter vous suivre."}, {"Escorter Brume vers la Sentinelle Arynia Cloudsbreak au puits de lune, près de l'arbre-oracle."}},
[939] = {"Flûte de Xavaric", {"Vous étudiez la simple flûte de bois que vous avez récupérée après avoir vaincu Xavaric.","Le bois noueux semble solide mais les spirales et les motifs de sa surface semblent se mouvoir quand vous tournez l’instrument entre vos mains. Soudain, vous êtes pris d’un sentiment d’appréhension...","Vous vous ébrouez, pensant que votre esprit vous joue des tours.","Il est possible de trouver auprès d'autres satyres des indices relatifs à ce que vous avez trouvé. Apportez vos découvertes au druide Eridan Bluewind qui vit dans la partie sud de Gangrebois."}, {"Apporter la Flûte de Xavaric et 5 échantillons de Lien maudit Jadefeu à Eridan Bluewind, dans le sud de Gangrebois."}},
[940] = {"Teldrassil", {"On entend murmurer la forêt qui loue vos grandes prouesses, <nom>. Les harpies une fois délogées de leurs nids, un grand calme s'est répandu sur la forêt, ses créatures se sentant à nouveau en sécurité. J'ai eu peur d'envoyer un autre messager à l'archidruide, mais je sais que vous, vous parviendrez à lui remettre un message.","Apportez-lui ceci et sachez que la forêt veillera à votre sécurité le long du chemin menant à la Forêt de pierre."}, {"Remettre le rapport de l'arbre-oracle à l'archidruide Fandral Staghelm à Darnassus."}},
[941] = {"Une belle plante", {"J'ai débarrassé le cœur de cette ignoble mousse, mais il reste contaminé…","Essayez de placer le cœur dans ce pot. Il est rempli d’un terreau spécial qui pourrait le nettoyer et le guérir."}, {"Planter le Cœur corrompu dans le Pot de Denalan."}},
[942] = {"Les absences du Prospecteur", {"Le fossile ressemble de manière frappante à un autre que j'ai vu une fois à Ironforge. Un Archéologue du nom de Flagongut l'avait apporté à notre conférence annuelle de la Ligue des Explorateurs. Il croyait que le fossile détenait un pouvoir que l'on aurait pu réveiller d'une certaine façon.","La dernière fois que j'ai entendu parler de lui, Flagongut s'était installé à la taverne de l'Eau-profonde, au Port de Menethil, attendant une escorte pour le mener à l'Excavation de Whelgar. Recherchez-le et montrez-lui le Fossile de Remtravel. Peut-être pourra-t-il révéler les secrets du passé.","Le bateau pour le Port de Menethil part d'Auberdine."}, {"Apporter le mystérieux Fossile à l'Archéologue Flagongut au Port de Menethil."}},
[943] = {"Les absences du Prospecteur", {"Le Prospecteur Whelgar détient à présent le fossile original, en son site des marécages. Quand j'ai découvert la Pierre de Relu, j'ai cru y voir la solution à l'énigme du fossile.","Alors que je taillais la route pour aller à la rencontre de Whelgar, j'ai subi une attaque de raptors et la relique fut perdue. J'ignore laquelle des ces créatures chamarrées a bien pu l'avaler, mais si vous pouvez la retrouver, je peux libérer le pouvoir de ces artefacts.","Apportez-moi donc la Pierre de Relu, et le fossile original que Whelgar possède."}, {"L'archéologue Flagongut au Port de Menethil veut que vous lui apportiez la Pierre de Relu et le Fossile de Flagongut."}},
[944] = {"Le Glaive du Maître", {"C'est étrange que des nagas soient là, à Sombrivage. Mais... la présence du Marteau du crépuscule l'est encore plus. Ce culte vénère de très anciens seigneurs de la terre. Des seigneurs vaincus il y a longtemps de cela.","Le Glaive du Maître, au sud, est un endroit où est tombé l'un de ces anciens seigneurs.","Allez-y et cherchez d'autres sectateurs du Marteau du crépuscule.","Prenez cette fiole de divination. Utilisez-la pour créer une coupe de divination après avoir enquêté au Glaive du Maître. Puis parlez-moi par l'intermédiaire de cette coupe."}, {"Trouver des informations, puis utiliser la Fiole de divination pour créer une Coupe de divination. Utiliser la coupe pour parler à Onu."}},
[945] = {"L'évasion de Therylune", {"Au secours !","Je me promenais autour du Glaive du Maître, et ces infâmes sectateurs m'ont entourée. Encore heureux que je sois douée pour me cacher !","Pouvez-vous m'aider à m'échapper ? Et j'aurais aussi besoin de dire à ma sœur Therysil que je vais bien. Elle est à Ashenvale au sud, au sanctuaire d'Aessina."}, {"Aider Therylune à s'échapper, puis dire à Therysil au Sanctuaire d'Aessina que sa sœur est saine et sauve."}},
[947] = {"La caverne aux champignons", {"Je prépare une potion qui nécessite des champignons rares, des champignons qui ne poussent que dans une certaine caverne. La caverne en question se trouve au-delà des Chutes de la Bondissante, à l'est et légèrement au nord le long des montagnes.","J'irais bien moi-même, mais le Bosquet des Anciens m'a conseillé de me tenir à l'écart de cet endroit. Nos vénérables alliés pressentent que cette caverne est l'antre d'une nouvelle créature maléfique, ici à Sombrivage.","S'il vous plaît, <nom>, ramassez ces champignons pour moi. Et pendant que vous y êtes, explorez la caverne pour confirmer le pressentiment des Anciens."}, {"Apporter 5 Pédoncules d'Amanite et 1 Amanite phalloïde à Barithras Moonshade à Auberdine."}},
[948] = {"Onu", {"Onu, un Ancien du savoir au Bosquet des Anciens, est au courant de votre voyage vers les Chutes de la Bondissante et souhaite vous parler. Le bosquet se trouve au sud, blotti près des montagnes.","Les Anciens sont patients et sages, <nom>. Mais si Onu souhaite votre conseil à propos de ce que vous avez vu aux chutes, alors j'ai peur que ce soit urgent."}, {"Parler à Onu au Bosquet des Anciens."}},
[949] = {"Le camp du Marteau du crépuscule", {"Nous ne connaissons pas le but du Marteau du crépuscule au Glaive du Maître.","Fouillez leur camp au Glaive… Essayez de trouver quelque chose qui puisse nous indiquer leurs intentions.","Car nous devons les connaître avant d'établir un plan."}, {"Trouver un indice au Camp du Marteau du crépuscule au Glaive du Maître."}},
[950] = {"La page qui rend fou", {"Consulter le Tome du crépuscule rend fou. Au premier regard, le texte commence à flotter et danser ; images et enluminures s'entremêlent pour créer des formes grotesques.","Vous arrivez tout juste à déchirer une page de ces gribouillages déments avant qu'un vertige ne vous submerge."}, {"Apporter les Gribouillages déments à Onu."}},
[951] = {"Les Reliques de Mathystra", {"Le parchemin que vous avez découvert au Glaive du Maître est la page d'un texte ancien… écrit par des elfes ! Il est souillé de la corruption du Marteau du crépuscule. Ainsi, le culte utilise l'ancienne magie des elfes pour ramener ses vieux maîtres dans notre monde.","Voyagez jusqu'aux ruines de Mathystra au nord-est, et cherchez d'anciennes reliques, dans l'herbe et parmi les pierres brisées. En les étudiant, il se peut que nous comprenions comment le Marteau du crépuscule compte exploiter la magie des elfes."}, {"Apporter 6 Reliques de Mathystra à Onu, au Bosquet des Anciens."}},
[952] = {"Bosquet des Anciens", {"Si vous avez suffisamment de temps pour servir de <messager/messagère> pour l'arbre-oracle, je pourrais vous demander de remettre un message au bosquet des Anciens, à Sombrivage, au sud d'Auberdine.","Vous aurez probablement à effectuer le voyage en hippogryphe, mais j'ai foi en vos compétences. Apportez ceci à Onu, l'Ancien du savoir. Il attend un mot de ma part, tout comme j'attendais un mot de la Clairière de l'Oracle."}, {"Livrer le message de Fandral à Onu, au bosquet des Anciens, à Sombrivage, au sud d’Auberdine."}},
[953] = {"La chute d'Ameth'Aran", {"Vous trouverez à l'est les ruines d'Ameth'Aran. Elles sont habitées par les Esprits sans repos des Bien-nés qui vivaient autrefois dans ces murs, quand les serviteurs d'Azshara y pratiquaient librement leur puissante magie.","On m'a envoyée pour explorer les ruines et trouver deux grandes tablettes, sur lesquels sont racontées les histoires d'Ameth'Aran et de sa chute. Alors que je lisais les runes, j'ai été surprise par les Esprits, et je me suis enfuie.","S'il vous plaît, si vous le pouvez, aventurez-vous dans les ruines et déchiffrez les tablettes pour moi."}, {"Étudier les tablettes qui parlent d'Ameth'Aran et de sa chute, puis retourner voir la Sentinelle Tysha Moonblade à Sombrivage."}},
[954] = {"Bashal'Aran", {"Les ruines de Bashal'Aran à l'est sont infestées de serviteurs démoniaques. Les fées et les satyres qui ont élu domicile dans la région se nourrissent des énergies magiques du secteur, et leurs pouvoirs augmentent avec le temps passé sur place.","Mais malgré cela, j'ai remarqué un sanctuaire dont ils ne s'approchent jamais. A l’ouest des ruines, au sommet d'un petit promontoire, filtre une étrange aura bleue... Il doit y avoir une explication à la répugnance des démons.","J'aimerais que vous enquêtiez à ce propos."}, {"Trouver la source de l’aura bleue dans les Ruines de Bashal'Aran."}},
[955] = {"Bashal'Aran", {"Si j'avais à raconter l'histoire de ma vie, il ne fait pas de doute qu'elle dépasserait les limites de votre patience. Disons que ma vie a été longue et douloureuse, et cette forme spectrale qui est la mienne à présent est peut-être le pire de tous ces tourments.","Je suis retenu ici par les pouvoirs de la magie. Bien que mes mots puissent sonner faux, je vous assure que je vous serais reconnaissant au-delà de tout si vous pouviez m’aider à identifier ce qui me retient prisonnier. Un sceau fait partie de ce dispositif, et en examinant les boucles d'oreilles des fées et des grells, je pourrais en trouver la trace."}, {"Acquérir 8 Boucles d'oreilles de grell pour Asterion à Bashal'Aran."}},
[956] = {"Bashal'Aran", {"Si les grells sont entrés en contact avec le sceau qui verrouille ma prison éternelle, je crois en connaître la cause. Il ne fait pas de doute que le sceau est en la possession du satyre qui les dirige.","Je ressens fortement que cela doit être vrai, <nom>. L'un des satyres le possède à présent. Si vous pouvez l'obtenir, je serais si proche de pouvoir passer les barreaux de ma prison que les larmes me monteraient aux yeux."}, {"Obtenir l'Ancien sceau de pierre de lune et l'apporter à Asterion à Bashal'Aran."}},
[957] = {"Bashal'Aran", {"C'est le savoir de l'un des plus puissants Bien-nés qui a créé le sceau fermant ma prison. À Ameth'Aran, dans les ruines au sud, jumelles de celles-ci, brûle une flamme antique, de couleur bleue. Dans cette flamme, ce sceau pourrait être détruit.","Soyez <prudent/prudente> dans les ruines, <nom>."}, {"Détruire l'Ancien sceau de pierre de lune à la flamme antique qui se trouve dans Ameth'Aran, puis retourner voir Asterion à Bashal'Aran."}},
[958] = {"Les outils des Bien-nés", {"<classe>. J'aurais quelque chose à vous demander.","Connaissez-vous les ruines d'Ameth'Aran ? Si ce n'est pas le cas, vous les trouverez à l’est de la route principale, à quelque distance au sud. Alors qu'elles abritaient de nombreux Bien-nés puissants, elles ne sont plus à présent que l’image de la destruction à laquelle leurs expériences ont abouti.","Les Sentinelles disent que les esprits des Bien-nés sont toujours là, et qu'ils manient toujours leurs instruments magiques. Nous devons nous emparer de ces reliques, afin de les détruire."}, {"Retrouver 7 Reliques de Bien-né pour Thundris Windweaver à Auberdine."}},
[959] = {"Fuite de porto aux docks", {"Oups !","J'ai eu une petite mésaventure, comme vous pouvez le voir ! La cargaison du matin venant de Baie-du-Butin était énorme. Enorme je dirais ! J'ai jamais vu autant de caisses. Bien, j'essayais d'expédier rapidement les choses alors j'ai commencé à décharger un peu plus que je ne pouvais soutenir. C'est à ce moment-là que tout s'est cassé la gueule.","Avant que je sache ce qu'il se passait, j'ai vu cette peste de Magglish le Dingue filer avec une bouteille de porto vieille de 99 ans. Je l'ai poursuivi jusqu'aux Cavernes des lamentations. Pas question d'y rentrer, quand même.","Peut-être êtes-vous suffisamment <courageux/courageuse> pour aller le retrouver ?"}, {"Le grutier Bigglefuzz de Ratchet veut que vous repreniez la Bouteille de porto vieille de 99 ans à Magglish le Dingue, qui se cache dans les Cavernes des lamentations."}},
[961] = {"Onu réfléchit", nil, nil},
[962] = {"Fleur de serpent", {"La Société royale des apothicaires, basée dans la grande ville d'Undercity de Lordaeron, m'a envoyée ici pour une mission très particulière, <nom>. Peut-être souhaitez-vous m'aider, et ainsi aider la Dame noire, dans nos efforts pour faire progresser les Réprouvés.","J'ai récemment étudié un spécimen rare de flore appelé Fleur de serpent. Je crois qu'en grande quantité cette herbe a un bon potentiel.","Malheureusement, elle ne pousse qu’au plus profond des Cavernes des lamentations, un réseau de cavernes situé dans les Tarides."}, {"L'Apothicaire Zamah à Thunder Bluff veut que vous récoltiez 10 Fleurs de serpent."}},
[963] = {"Un amour éternel", {"À la suite des batailles au Puits d'éternité, j'ai appris qu'Ameth'Aran avait été détruit, et son peuple tué, y compris mon bel amour Anaya.","Je n'aurais jamais cru, des milliers d'années plus tard, que des souvenirs d'Anaya hanteraient toujours mes rêves. Frappé de stupeur, j'ai erré dans les bois de Sombrivage, et j’ai retrouvé les ruines d'Ameth'Aran... Là j’ai vu l'esprit hanté de ma bien-aimée.","Elle doit être libérée, mais je n'ai pas le cœur de faire cela. Son esprit doit être détruit."}, {"Libérer l'esprit d'Anaya Dawnrunner et rapporter son pendentif à Cerellean Whiteclaw à Auberdine."}},
[964] = {"Fragments de squelette", {"La clé pour accéder à Scholomance s'appelle une Clé squelette. Elle doit être forgée à partir des restes d'un squelette - de plusieurs en fait - et doit être renforcée des métaux les plus robustes dans un moule particulier. Enfin, un sceau de pouvoir provenant d'une créature naturellement capable d'ouvrir le portail de Scholomance permettra à la clé d'avoir la fonction désirée.","Mais commençons par le commencement, <nom>. Nous allons avoir besoin de Fragments de squelette pour forger la clé. Les squelettes d'Andorhal devraient nous les fournir, mais ceux en dehors des murs devraient pouvoir faire l'affaire également."}, {"Apporter 15 Fragments de squelette au Pharmacien Dithers à la Barricade, dans les Maleterres de l'ouest."}},
[965] = {"La Tour d'Althalaxx", {"Salutations, jeune <race>. Je m'appelle Elissa Starbreeze, et il est de mon devoir de protéger Auberdine de tout mal.","À cette fin, j'ai envoyé Balthule Shadowstrike observer les événements étranges qui se produisent près de la Tour d'Althalaxx au nord-est.","Il aurait déjà dû revenir. J'ai peur qu'il ait rencontré un danger imprévu dans la forêt. J'apprécierais beaucoup si vous pouviez le trouver et vous assurer qu'il va bien."}, {"Trouver Balthule Shadowstrike près de la Tour d'Althalaxx à Sombrivage."}},
[966] = {"La Tour d'Althalaxx", {"Un groupe de démonistes s'est établi autour et à l'intérieur de la tour. Je serais retourné à Auberdine pour rendre compte à Elissa plus tôt, mais j'avais peur de rater quelque chose une fois parti.","Delgren suspectait qu'une telle compagnie puisse se rassembler à la tour, mais il n’en savait pas la cause.","Quelques morceaux de parchemins appartenant à ces démonistes ont atterri entre mes mains, mais j'en ai besoin d'autres pour compléter le puzzle. Je vous avertis cependant de ne pas vous aventurer dans la tour, les démonistes qui s'y trouvent sont beaucoup trop puissants !"}, {"Collecter 4 Parchemins passés pour Balthule Shadowstrike près de la Tour d'Althalaxx."}},
[967] = {"La Tour d'Althalaxx", {"Mon maître, Delgren le Purificateur, est un paladin qui a gracieusement offert son assistance pour défendre nos forêts contre les démons et les morts-vivants. Il m'a beaucoup appris sur le Feu sacré et sur l'art de la bataille.","Delgren doit être mis au courant des opérations du culte immédiatement.","Vous le trouverez loin au sud, au poste de Maestra dans la forêt d'Ashenvale. Faites vite, j'ai peur que la menace que représente la Rive noire n'augmente d'heure en heure."}, {"Remettre la Lettre de Balthule à Delgren le Purificateur dans la forêt d'Ashenvale."}},
[968] = {"Les puissances d'en-bas", {"Bien qu'affreusement usé et égratigné, le titre est encore lisible sur la couverture :","Les puissances d'en bas.","À l'intérieur du livre écorné se trouve une liste de noms imprononçables affublés d'horribles titres, des méthodes de culte, des sacrifices conseillés... dont beaucoup incluent des créatures humanoïdes, vivantes.","Au verso de la couverture, sont inscrits les mots suivants : \"Runes et malédictions de Bonegrip, la caverne lugubre, Ironforge. Propriétaire : Gerrig Bonegrip.\"","Si Bonegrip vend de tels livres, peut-être qu'il rachètera celui-ci."}, {"Apporter le Livre : Les puissances d'en bas à Gerrig Bonegrip, dans la caverne lugubre d'Ironforge."}},
[969] = {"Que la chance soit avec vous", {"Rien qu’à vous regarder, je vois que vous voulez un charme de chance. Mau'ari sait tout !","Là-bas, dans le sud du Berceau, vous trouverez un canyon où les géants collectent des éclats de glace. Vous pouvez toujours prélever vous-mêmes des éclats sur le canyon. Ils ont une puissante qualité de réflexion.","Rapportez 10 Eclats cognegivre, et mon charme de chance sera à vous."}, {"Collecter 10 Eclats cognegivre pour le sorcier-docteur Mau'ari à Long-guet."}},
[970] = {"La Tour d'Althalaxx", {"La lettre de Balthule est désespérée. Ce culte de la Rive noire m'irrite au plus haut point et doit être éradiqué. J'ai eu affaire à quelques vermines de la Rive noire au nord-est d'ici à Ordil'Aran. L'un d'entre eux possède une gemme d'âme, qui je crois détient le secret de la puissance de ce culte.","Apportez-la-moi, et je serai capable de déchiffrer les secrets qu'elle contient."}, {"Trouver une Gemme d'âme luminescente et la rapporter à Delgren le Purificateur au poste de Maestra."}},
[971] = {"La connaissance des profondeurs", {"Comme il se peut que vous le sachiez, je recueille le savoir. Le savoir ancien. Le savoir puissant. Le savoir qui ouvre les portes et le savoir qui réveille Ceux qui dorment.","Il court des rumeurs sur une pièce de savoir, le Manuscrit de Lorgalis, loin dans les Profondeurs de Brassenoire, dans Ashenvale. Cet endroit est la demeure d'elfes depuis longtemps disparus. Des elfes qui avaient réuni de grandes connaissances avant que leur cité ne tombe en ruines.","Fouillez les Profondeurs de Brassenoire à la recherche du manuscrit. Faites-le, et je ne l'oublierai pas. Même pas après... les derniers jours."}, {"Apporter le « Manuscrit de Lorgalis » à Gerrig Bonegrip à Ironforge."}},
[972] = {"Le sapta d'eau", nil, nil},
[973] = {"La Tour d'Althalaxx", {"Ilkrud Magthrull ? Oui, je le connais. Il s'agit d'un puissant démoniste orc qui s’est établi au Sanctuaire de Scarfeu, un endroit très maléfique dans le sud-ouest d'Ashenvale.","Il n'a pas été plus qu'un simple désagrément jusqu'à présent, et je n'ai pas eu affaire à lui, mais il semble que le temps soit venu."}, {"Apporter le Tome d'Ilkrud Magthrull à Delgren le Purificateur au Poste de Maestra."}},
[974] = {"À la recherche de la source", {"Les sources chaudes ne cesseront de m’étonner ; d’où vient cette chaleur ?","Vu à quel point les sources sont proches de la crête de la Fournaise, je me demande si l’influence du volcan n’est pas responsable. J’ai créé un thermomètre pour lire et enregistrer la température de différentes parties du volcan et trouver la plus chaude. La chaleur est exprimée en degrés Kraklenheit !","Voici, prenez-le et faites des mesures de température sur la crête, dès que vous voyez un point chaud !"}, {"Découvrir la zone la plus chaude de la crête de la Fournaise, pour Krakle, au cratère d'Un'Goro.","Quand vous trouvez une zone de chaleur, cliquez avec le bouton droit de la souris sur le thermomètre afin de prendre la température. Continuez vos recherches jusqu’à avoir trouvé la zone la plus chaude."}},
[975] = {"Cache de Mau'ari", {"Avant de vous donner ce charme, je vous expliquerai comment l’utiliser.","Si vous le portez sur vous, vous trouverez certains objets spéciaux sur les créatures que vous combattrez. Sans le charme, pas d’objets spéciaux, vous voyez ?","Ces objets s’appellent des E'ko. Si vous trouvez n'importe lequel de ces objets, apportez-le à la vieille Mau'ari, qui saura vous aider.","Attendez ici pendant que je prépare votre charme."}, {"Attendre un instant, puis reparler au sorcier-docteur Mau'ari pour recevoir le Charme de chance."}},
[976] = {"De l’approvisionnement pour Auberdine", {"Delgren m'a demandé d'apporter un message et du ravitaillement à la Sentinelle Starbreeze à Auberdine, mais je déteste partir seul. La forêt regorge de dangereux adversaires et je ne veux pas prendre le risque que le ravitaillement ou des informations capitales tombent entre de mauvaises mains.","Si vous et quelques autres pouvez m'accompagner en tant que gardes, je partirai tout de suite. Si vous nous protégez, aucun doute que ces marchandises arriveront à bon port."}, {"Parler à Delgren le Purificateur, au Poste de Maestra, après avoir escorté Feero à travers la Forêt d'Ashenvale."}},
[977] = {"Yéti ? Y es-tu ?", {"Maintenant que j’ai ce dont j’ai besoin pour construire les parties mécaniques mobiles, il ne me manque qu’une seule chose. Deux choses, en fait. Deux cornes !","Les patriarches et matriarches Chardon de glace sont ceux qui ont les plus grandes cornes. Ces yétis se trouvent dans les grottes, au sud-est. Il y en a un grand nombre là bas, vous n’aurez pas de problèmes à les trouver.","Rappelez-vous que je ne veux que les plus belles cornes, sans la moindre éraflure !"}, {"Collecter 2 Cornes de yéti en parfait état pour Umi Rumplesnicker, à Long-guet"}},
[978] = {"Les Indomptables lunaires", {"Les plumes d’Indomptables des Hinterlands contiennent des traces de magie. Toutefois, ces créatures ne semblent pas avoir de pouvoirs particuliers. La magie semble faire partie de leur essence.","Pendant mes études, j’ai entendu qu’une importante population d’Indomptables résidait dans les terres enneigées du Berceau-de-l'Hiver. Ces créatures auraient-elles les mêmes pouvoirs ? Aidez-moi à trouver les réponses à ces questions, <nom>. Allez au Berceau-de-l'Hiver et collectez des plumes qui traînent au sol, puis revenez me voir."}, {"Collecter 10 Plumes de Chouettard lunaire, dans le Berceau-de-l'Hiver, puis retourner voir Erelas Ambersky, à Rut'theran."}},
[979] = {"Trouver Ranshalla", {"Oui, oui, ces plumes semblent contenir le même enchantement que j'ai remarqué précédemment… Bien que je ne sache pas vraiment ce que nous pourrions découvrir, je vous conjure de continuer à chercher des réponses, <nom>.","L'une de mes collègues a voyagé récemment au Berceau-de-l'Hiver afin d'y observer les Indomptables, dans leur habitat naturel. Pourquoi ne pas essayer de la retrouver et voir ce qu'elle a pu découvrir..."}, {"Trouver Ranshalla dans l’est du Berceau-de-l'Hiver."}},
[980] = {"Les nouvelles sources", {"Il y a d’autres sources chaudes dans un endroit appelé le Berceau-de-l'Hiver, loin au nord. Ce qui est étrange, c’est qu’il n’y a pas de volcan à proximité ; intéressant n’est-ce pas ?","Si vous voulez en apprendre plus, vous devriez aller au Berceau-de-l'Hiver et parler à une de mes amies. Son nom est Donova Snowden, et elle réside aux environs de ces sources.","Merci de m’aider avec mon thermomètre et à plus tard !"}, {"Se rendre au Berceau-de-l'Hiver et parler à Donova Snowden."}},
[981] = {"La Tour d'Althalaxx", {"Vous méritez bien une récompense pour tout ce que vous avez accompli. Retournez voir Delgren et dites-lui qu'Athrikus Narassin gît à terre par vos soins et que le Culte de la Rive noire est vaincu et démantelé."}, {"Aller voir Delgren le Purificateur au Poste de Maestra."}},
[982] = {"Océan profond, mer immense", {"Au large de Sombrivage, plus au nord, il y a deux épaves de bateau : l’Aube argentée et le Voile de brume. Il y a quelque temps, ces deux bateaux sont tombés sur ces maudits murlocs, alors qu’ils faisaient voile à travers la vaste mer jusqu’à Auberdine. Ils reposent maintenant au fond des eaux, comme autant de trophées au crédit de ces démons.","Les deux capitaines n’ont pas survécu et leurs journaux de bord et effets personnels sont toujours dans leurs coffrets. Je voudrais que vous les récupériez ; cela serait symboliquement très important pour tous les équipages de cette contrée."}, {"Récupérer le Coffret de l’Aube argentée et le Coffret du Voile de brume, pour Gorbold Steelhand, à Auberdine. Ces deux objets se trouvent à bord des épaves des navires respectifs, au nord du village."}},
[983] = {"Bigobox 827", {"Hé ! J'ai besoin d'aide pour ma dernière invention, la bigobox. Elle permet de parler aux gens à distance !","Vous en avez peut-être déjà vu, ce sont des boîtes avec un levier dessus. Le seul problème, c'est qu'il faut une maintenance permanente.","Elles ont toutes un problème différent, mais j'ai eu une idée géniale. J'ai placé chacune d'elle près de la créature qui possède les éléments appropriés pour la réparer. En ce moment, la bigobox 827 est en panne. C'est juste au sud d'Auberdine, vraiment tout près. Il faut 6 pattes de clampants pour la réparer. Je vous payerai pour ça..."}, {"Réunir 6 Pattes de clampants et les mettre dans la bigobox 827."}},
[984] = {"Quelle est l'ampleur de la menace ?", {"Certains de mes frères ont été sauvés de justesse d'un furbolg corrompu à Teldrassil, et je me suis juré d'arrêter ces atrocités avant que d'autres ne soient blessés... ou pire.","J'ai déjà vu des prémices de corruption à Sombrivage, mais il faut que je cherche d'autres signes ailleurs. Je pense qu'il serait logique de poursuivre les investigations auprès des furbolgs. Pourriez-vous trouver un de leurs camps et me dire s’il y a des signes de corruption ?"}, {"Trouver un camp de furbolgs corrompus à Sombrivage et retourner voir Terenthis dans Auberdine."}},
[985] = {"Quelle est l'ampleur de la menace ?", {"Vous avez déjà prouvé vos qualités dans le domaine de la reconnaissance, <nom>. Avez-vous aussi les qualités nécessaires pour affronter l'adversaire ?","Les aventuriers ne préfèrent pas tous le combat direct à l'art de la ruse et de l'évasion.","Si vous pensez être à la hauteur de la tâche, sachez que la plus grande menace pour notre peuple est aujourd'hui le camp furbolg au sud d'Auberdine. Vous y trouverez des furbolgs de la tribu Noirbois. Tuez 8 Guides et 5 Messagers du vent et revenez me voir."}, {"Tuer 8 Guides et 5 Messagers du vent Noirbois puis retourner voir Terenthis à Auberdine."}},
[986] = {"Un maître perdu", {"<nom>, votre habileté m'a déjà été utile. Puis-je à nouveau vous demander d'aider Griffe-sinistre et son maître Volcor ? Je vais vous fabriquer une cape magique qui vous permettra de vous déplacer librement au milieu des créatures de Sombrivage pendant que vous le chercherez.","Pour faire cette cape, j'ai besoin de cinq belles peaux de géniteur ou de matriarche traquelune. Vous trouverez ces tigres au sud de la Sinueuse ou beaucoup plus au sud, non loin d'Ashenvale."}, {"Trouver 5 Belles peaux de traquelune et les apporter à Terenthis, à Auberdine."}},
[990] = {"Voyage vers Ashenvale", {"<nom>, ma maîtresse, Raene Wolfrunner, vous attend à la cité d'Astranaar dans Ashenvale. Peut-être que grâce à vous, nous pourrons réduire la corruption qui y règne.","En partant d'ici, vous devez prendre plein sud en restant près de la route ; vous trouverez sans problème votre chemin en suivant cette direction."}, {"Trouver Raene Wolfrunner dans Ashenvale."}},
[991] = {"Le nettoyage selon Raene", {"<nom>, l'un de mes vieux amis est aussi venu à Ashenvale pour aider les Sentinelles, mais il est déjà reparti.","Il cherchait un objet qui pourrait retarder les attaques des furbolgs contre notre peuple : un Bâtonnet créé par un sorcier diabolique, aujourd’hui mort.","Avant de partir, il a dit qu'il cherchait une gemme pour ce Bâtonnet.","Il pensait que la gemme était cachée dans le sanctuaire près du lac Falathim, au pied de la montagne à l'ouest. La gemme était là avant l’invasion.","Trouvez mon ami, je vous en prie, <nom>."}, {"Trouver Teronis dans Ashenvale."}},
[992] = {"Étude des eaux de Gadgetzan", {"Nous avons un poste temporaire vacant de géomètre novice à la Compagnie des Eaux de Gadgetzan, si ça vous intéresse !","Comme les nomades s'emparent de tous nos puits, nous devons exploiter tous les points d'eau libres ! Il y en a dans le désert, mais seulement pour ceux qui les étudient en premier.","Prenez cette fiole de sourcier et récoltez un échantillon d'eau de la mare près du Guet de Tristesable. Juste sous le nez des trolls. Rapportez-moi la fiole pleine quand vous aurez fini !"}, {"Utiliser la Fiole de sourcier vide à la mare près du Guet de Tristesable. Une fois l'échantillon récolté, remettre la Fiole de sourcier pleine au grand géomètre Fizzledowser à Gadgetzan."}},
[993] = {"Un maître perdu", {"La cape est terminée, <nom>. Nous devons trouver Volcor à présent. J'espère seulement que nous sommes encore dans les temps.","La magie de la cape, une fois invoquée, ne durera pas longtemps. Cinq minutes, peut-être. Ce sera à vous de choisir quand l'utiliser. Je suggèrerais d'attendre de trouver Griffe-sinistre.","Si ce qu'il m'a dit est vrai, vous trouverez son maître dans une caverne au sud d'ici. Griffe-sinistre vous attendra le long de la route. Faites-lui un signe de la main lorsque vous le verrez et il vous indiquera la direction de son maître."}, {"Trouver Volcor, à Sombrivage, et lui donner la Cape enchantée de traquelune."}},
[994] = {"Évasion par la force", {"Ça va, <nom>. Sortons de là. Nous nous fraierons un passage jusqu'à la route ; Griffe-sinistre devrait encore y être. Une fois là bas, nous pourrons nous séparer.","Terenthis va vouloir que vous lui racontiez immédiatement ce qui s'est passé, vous irez donc à Auberdine dès que nous serons sortis vivants d'ici."}, {"Protéger Volcor jusqu'à la route puis aller voir Terenthis à Auberdine."}},
[995] = {"Évasion par la ruse", {"D’accord... si vous le voulez, nous nous échapperons d’ici.","Rappelez vous, une fois que vous aurez quitté ce lieu, allez à Auberdine et rencontrez Terenthis. C’est là que je vais me rendre aussi, et j’y serai probablement avant vous."}, {"S'échapper de la grotte des furbolgs et aller voir Terenthis à Auberdine."}},
[996] = {"La fleur-de-vent corrompue", nil, nil},
[997] = {"La terre de Denalan", {"Vous allez vers le sud ? Au lac Al'Ameth ? Si c'est le cas, j'ai une tâche à vous confier...","Mon collègue Denalan campe le long du rivage oriental du lac, où il étudie la flore de Teldrassil. Il a demandé qu'on lui envoie un paquet de Terre rare de Darnassus, mais il y a eu du retard et le paquet vient seulement d'arriver à Dolanaar.","Pouvez-vous le lui apporter ?"}, {"Rapporter le paquet de Terre rare à Denalan au lac Al'Ameth."}},
[998] = {"La fleur-de-vent corrompue", nil, nil},
[1000] = {"La nouvelle frontière", {"Votre attention !","Le Cercle cénarien cherche à recruter ceux qui se sentent capables pour une mission d'exploration sur les nouveaux territoires de Kalimdor ! Des terres désolées, interdites, regorgeant de dangers, se trouvent à l'ouest de Tanaris et de Gadgetzan, et le Cercle a besoin d'honorables représentants pour agir en son nom.","Tous ceux et celles qui seraient intéressés devraient solliciter une audience auprès du Cercle cénarien. Parlez avec l'archidruide Hamuul Runetotem, sur la Cime des Anciens de Thunder Bluff, pour plus d'informations !"}, {"Parler à l’archidruide Hamuul Runetotem, sur la Cime des Anciens de Thunder Bluff. Evoquer l’appel du Cercle cénarien concernant l’exploration des frontières de Kalimdor."}},
[1001] = {"Bigobox 411", {"Une petite voix crépite à l'intérieur de la machine.","\"C'est Wizbang ! La prochaine Bigobox se trouve au nord d'Auberdine, sur la plage !","Cette Bigobox porte le numéro 411 et a besoin de 3 Yeux de batteur pour être réparée. Ils sont un peu au large de la côte ! Les Frappeurs de Sombrivage... *Hic*... Pardon...","Tout comme pour la dernière fois, quand vous donnerez les objets à la Bigobox, elle vous crachera votre récompense. *Hic*... Quoi ? Non, je vais bien !\""}, {"Rassembler 3 Yeux de batteur de Sombrivage au fond de la mer, près de la bigobox 411."}},
[1002] = {"Bigobox 323", {"La bigobox se tait un instant puis vous entendez Wizbang commencer à parler.","\"La-la bigobox suivante est la numéro 323. L'est au nord d'Auberdine mais... Où est la bière ? Hein ? Oh ! C'est sur-sur la route alo-lors ça ne devrait pas-pas être dif-difficile de la trou-trouver près du pont.\"","Wizbang marmonne quelque chose d'incohérent puis vous entendez un bruit de déglutition sonore.","\" Il f-f-faut des Crocs de traquelune... 6 cro-crocs.\""}, {"Trouver 6 Crocs de traquelune et les mettre dans la bigobox 323."}},
[1003] = {"Bigobox 525", {"En plus du bruit de fond et des sons écœurants qui viennent de la machine, vous entendez la voix hésitante de Wizbang appeler.","\"Au-autre au s-sud en bas de l-la route. Ne devrait pa-pas trop dur à trouver... juste au b-bord... près d'une clôture. Je p-pense que je l'ai ca-cachée dans un beu-beu... buisson. Hé hé hé !... Ouais, la bigobox 525 a be-besoin de s-scalps vieillis de vieux ou-ours des chardons. Garçon, une autre ! Vi-vieillis... Hé hé hé !...\"","Vous entendez déglutir tandis que la machine s'arrête. Puis soudain, elle éructe.","\"4 !\""}, {"Se procurer 4 Scalps vieillis sur de Vieux ours des chardons au sud d'Auberdine et les placer dans la Bigobox 525."}},
[1004] = {"La nouvelle frontière", {"Votre attention !","Le Cercle cénarien cherche à recruter ceux qui se sentent capables pour une mission d'exploration sur les nouveaux territoires de Kalimdor ! Des terres désolées, interdites, regorgeant de dangers, se trouvent à l'ouest de Tanaris et de Gadgetzan, et le Cercle a besoin d'honorables représentants pour agir en son nom.","Tous ceux et celles qui seraient intéressés devraient solliciter une audience auprès du Cercle cénarien. Parlez avec l'archidruide Hamuul Runetotem, sur la Cime des Anciens de Thunder Bluff, pour plus d'informations !"}, {"Parler à l’archidruide Hamuul Runetotem, sur la Cime des Anciens de Thunder Bluff. Evoquer l’appel du Cercle cénarien concernant l’exploration des frontières de Kalimdor."}},
[1007] = {"L'ancienne statuette", {"Je vais vous charger d'une mission, <nom>.","J'étais sur mon petit bateau, voguant sur les ruines englouties de Zoram, quand les nagas ont émergé des eaux et m'ont attaqué en me déchirant avec leurs griffes ! Je me suis enfui, en transportant tout ce que je pouvais pour le camp.","Et lorsque j'ai atteint le rivage, je me suis mis à courir... J'ai perdu ma précieuse cargaison.","Je vous en prie, <nom>, retrouvez l’endroit où je suis tombé dans cette embuscade et cherchez une ancienne statuette. C'est pour elle que j'ai bravé les dangers de la grève de Zoram."}, {"Apporter l'Ancienne Statuette à Talen, dans son camp près de la grève de Zoram."}},
[1008] = {"La grève de Zoram", {"Il y a un endroit sur notre côte nord-ouest, connu sous le nom de grève de Zoram.","C'est là que se trouve la cité perdue de Zoram, détruite pendant le Fractionnement et submergée par les eaux. Cela fait une éternité que les elfes de la nuit l'ont perdue. Perdue et presque oubliée.","Aujourd’hui, les nagas y sont retournés et nous ne savons pas pourquoi. Mais la raison importe peu : nous devons tuer ces démons et les rejeter dans les profondeurs !","Revenez me voir quand votre mission sera achevée."}, {"Apporter 20 Têtes d'Irequeue à Shindrell Swiftfire, à Astranaar."}},
[1009] = {"Ruuzel", {"J'ai peur que les secrets de l'ancienne statuette ne restent un mystère tant que vous n'aurez pas trouvé la clé qui ouvre cette vieille serrure.","Cette clé est un anneau, l'Anneau de Zoram. Il a appartenu aux dirigeants de cette cité avant que les ondes du Fractionnement ne l'aient noyée.","L'anneau a été perdu pendant des années, mais les nagas viennent de le retrouver.","Leur chef, Ruuzel, se trouve sur une île, sur le côté nord de Zoram. Je vous en prie, <nom>, trouvez-moi cet anneau !"}, {"Apporter l'Anneau de Zoram à Talen près de la grève de Zoram."}},
[1010] = {"Les Cheveux de Bathran", {"Il y avait une plante qui poussait autrefois dans les vieilles ruines du repaire de Bathran au nord. On l'appelait Cheveux de Bathran et elle était réputée guérir les maux de l'esprit.","Il y a un enfant malade dans le village et je crois qu'il s'agit d'autre chose que d'une simple maladie physique. Accepteriez-vous d'aller au repaire de Bathran pour y chercher des Cheveux de Bathran ? Il se peut que j'en ai besoin pour soigner cet enfant.","Le repaire de Bathran se trouve au nord du Poste de Maestra, juste au sud de la frontière de Sombrivage."}, {"Rapporter 5 Cheveux de Bathran à Orendil Broadleaf dans Ashenvale."}},
[1011] = {"Les maladies des Réprouvés", {"Les Réprouvés, le bras mort-vivant de la Horde, ont rampé jusqu'en Ashenvale, et rôdent près de nos ruines et de nos refuges des saisons. Nous pensons que leur objectif est de répandre la maladie sur les terres des elfes de la nuit. Nous devons nous en assurer !","Les Réprouvés ont un camp au sud d'ici, près du refuge des saisons de Dor'danil. Ils y conçoivent poisons et maladies et les placent dans des bouteilles. Frayez-vous un passage jusqu'à ce camp et volez une de ces bouteilles pleines. Revenez alors me voir. J'expérimenterai cette maladie et découvrirai à quelles fins elle a été conçue."}, {"Apporter une Bouteille des maladies à Kayneth Stillwind au Chant des Forêts."}},
[1012] = {"Les druides fous", {"Lorsque je me battais avec les poisons des Réprouvés, j'ai eu une vision. J'ai vu les druides du Refuge des saisons de Dor'danil empoisonnés... Assassinés par les morts-vivants ! Ils sont désormais des fantômes errants, séparés de leurs corps et se conduisant comme des fous.","Pour que les esprits des druides puissent retrouver la paix, vous devez entrer dans le Refuge des saisons de Dor'danil et éliminer leurs grands chefs : Taneel Darkwood, Uthil Mooncall et Mavoris Cloudsbreak.","Faites cela et revenez me voir."}, {"Tuer les druides : Taneel Darkwood, Uthil Mooncall, Mavoris Cloudsbreak ; puis retourner auprès de Kayneth Stillwind au Chant des Forêts."}},
[1013] = {"Le Livre d'Ur", {"<nom>, il y a un livre dans le donjon d'Ombrecroc, le « Livre d'Ur », qui ferait très bon effet dans ma collection. Ur était un grand mage de Dalaran avant l'arrivée du Fléau, les études qu'il a faites dans certains domaines ont beaucoup de valeur aux yeux de certains... parmi les Réprouvés.","Pénétrez dans le donjon d'Ombrecroc et trouvez ce livre. Apportez-le-moi et je ferai part de votre action à notre Dame noire."}, {"Apporter le \"Livre d'Ur\" au Gardien Bel'dugur à l'Apothicarium à Undercity."}},
[1014] = {"Arugal doit mourir", {"Arugal se trouve toujours dans le Donjon d'Ombrecroc. Nous ne pouvons pas faire des Pins argentés une forteresse stratégique pour la Dame noire tant qu'Arugal est vivant.","Je vais me débrouiller pour que sa magie soit annihilée, <nom>. Mais je vous laisse le soin de faire en sorte qu'Arugal rencontre la mort qu'il sert si bien.","Allez au Donjon d'Ombrecroc pour mettre fin une fois pour toutes aux charmes crasseux d'Arugal. Apportez-moi la tête de ce vil sorcier !"}, {"Tuer Arugal et apporter sa Tête à Dalar Dawnweaver au Sépulcre."}},
[1015] = {"La nouvelle frontière", {"Votre attention !","Le Cercle cénarien cherche à recruter ceux qui se sentent capables pour une mission d'exploration sur les nouveaux territoires de Kalimdor ! Des terres désolées, interdites, regorgeant de dangers, se trouvent à l'ouest de Tanaris et de Gadgetzan, et le Cercle a besoin d'honorables représentants pour agir en son nom.","Tous ceux et celles qui seraient intéressés devraient solliciter une audience auprès du Cercle cénarien. Parlez avec l'archidruide Fandral Staghelm, dans Darnassus, pour plus d'informations !"}, {"Parler à l’archidruide Fandral Staghelm, dans l'Enclave cénarienne, à Darnassus. Evoquer l’appel du Cercle cénarien concernant l’exploration des frontières de Kalimdor."}},
[1016] = {"Les bracelets d'élémentaire", {"Prenez ce Parchemin de divination, il pourra nous apprendre qui a créé ces élémentaires. Mais vous aurez besoin d'une chose qui leur appartienne et que l’on pourra utiliser comme réactif pour focaliser la magie du parchemin ; leurs bracelets, par exemple, devraient faire l’affaire.","Quelques bracelets intacts suffiront. Les autres bracelets ne serviront pas nos desseins mais pourront être revendus auprès d’un marchand. Une fois que vous aurez les bracelets intacts, essayez d’utiliser le Parchemin. Revenez avec le résultat de l'expérience et nous verrons quelle suite donner aux événements."}, {"Récolter 5 Bracelets élémentaires intacts et utiliser le Parchemin de divination sur eux. Ensuite, apporter le Parchemin de divination à la Sentinelle Velene Starstrike au Refuge de Vent-argent."}},
[1017] = {"Mage invocateur", {"Il n'y a qu'un seul mage mort-vivant dans les Tarides capable de souiller autant d'élémentaires d'eau. C'est aussi le seul suffisamment idiot pour le faire. Sarilus Foulborne est probablement assis au sommet du pic de Brume-funeste, en train de se moquer de moi.","<nom>, vous devez pénétrer en territoire ennemi et éliminer ce scélérat. J'en ai assez de sa stupidité et nous devons lui montrer ce qui arrive à ceux qui s'en prennent aux Sentinelles.","Tuez-le et apportez-moi sa tête. Mais soyez <prudent/prudente>, il se peut que la Horde vous surveille."}, {"Tuer Sarilus Foulborne et apporter sa Tête à la Sentinelle Velene Starstrike au Refuge de Vent-argent."}},
[1018] = {"La nouvelle frontière", {"Votre attention !","Le Cercle cénarien cherche à recruter ceux qui se sentent capables pour une mission d'exploration sur les nouveaux territoires de Kalimdor ! Des terres désolées, interdites, regorgeant de dangers, se trouvent à l'ouest de Tanaris et de Gadgetzan, et le Cercle a besoin d'honorables représentants pour agir en son nom.","Tous ceux et celles qui seraient intéressés devraient solliciter une audience auprès du Cercle cénarien. Parlez avec l'archidruide, Hamuul Runetotem, sur la Cime des Anciens de Thunder Bluff, pour plus d'informations !"}, {"Parler à l’archidruide Hamuul Runetotem, sur la Cime des Anciens de Thunder Bluff. Evoquer l’appel du Cercle cénarien concernant l’exploration des frontières de Kalimdor."}},
[1019] = {"La nouvelle frontière", {"Votre attention !","Le Cercle cénarien cherche à recruter ceux qui se sentent capables pour une mission d'exploration sur les nouveaux territoires de Kalimdor ! Des terres désolées, interdites, regorgeant de dangers, se trouvent à l'ouest de Tanaris et de Gadgetzan, et le Cercle a besoin d'honorables représentants pour agir en son nom.","Tous ceux et celles qui seraient intéressés devraient solliciter une audience auprès du Cercle cénarien. Parlez avec l'archidruide, Fandral Staghelm, dans Darnassus, pour plus d'informations !"}, {"Parler à l’archidruide Fandral Staghelm, dans l'Enclave cénarienne, à Darnassus. Evoquer l’appel du Cercle cénarien concernant l’exploration des frontières de Kalimdor."}},
[1020] = {"Jouvence d'Orendil", {"<nom>, j'ai mis au point un traitement qui, je pense, devrait guérir cette pauvre enfant. Apportez-le à Astranaar et donnez-le à son père, Pelturas Whitemoon.","Pour atteindre Astranaar, suivez la route par le sud puis par l'est."}, {"Apporter la Jouvence d'Orendil à Pelturas Whitemoon dans Astranaar."}},
[1021] = {"Vil satyre ! dryades en danger !", {"Écoutez, je ne sais pas qui vous êtes, mais j'ai besoin de votre aide. Mes sœurs sont allées reprendre la branche de Cénarius au satyre. Elles ont dit qu'elles seraient vite de retour, mais il y a deux jours entiers qu'elles sont parties !","<nom>, vous devez les trouver ! Elles cherchaient les ruines d'un temple des elfes de la nuit au nord-est de Xavian. Je ne sais que penser. Cherchez Anilia, c'est elle qui menait la recherche."}, {"Trouver Anilia dans Xavian."}},
[1022] = {"Le Val hurlant", {"Bien que nous ayons dépensé beaucoup d'énergie pour repousser les démons encore présents de Gangrebois jusqu'au nord, nous n'avons pas eu beaucoup de succès. Nous avons pu éviter toute présence démoniaque à Ashenvale.","Au nord, près de la frontière de Gangrebois, de mystérieux hommes-loups ont investi le sanctuaire en ruine de Mel'Thandris. Leurs cris sont si terrifiants qu'on a surnommé ce secteur le Val hurlant. Le « Tome de Mel'Thandris » qui est gardé dans le sanctuaire peut faire la lumière sur la raison de la venue de ces hommes-loups."}, {"Se rendre au Val hurlant et étudier le « Tome de Mel'Thandris », puis retourner voir la Sentinelle Melyria Frostshadow au Sanctuaire d'Aessina."}},
[1023] = {"Le nettoyage selon Raene", {"En fouillant les affaires de Teronis, vous trouvez un journal qui est encore en bon état.","Vous prenez le journal et laissez le cadavre de Teronis comme il est.","Il ne vous reste plus qu'à trouver la gemme que Teronis cherchait. Raene a dit qu'elle était dans le secteur. Peut-être que les murlocs l'ont prise..."}, {"Trouver la Gemme luminescente et retourner voir Raene Wolfrunner dans Astranaar avec le Journal de Teronis."}},
[1024] = {"Le nettoyage selon Raene", {"Vous devrez chercher par vous-même, mais d'après les notes de Teronis, il semble que la prochaine étape soit de trouver les autres morceaux du Bâtonnet que Dartol a créé. Je commencerais par me diriger vers le puits de lune à l'est du lac Iris. Suivez la route et prenez au nord lorsque vous atteindrez une petite zone de repos sur votre gauche.","Votre meilleur guide sera une dryade du nom de Shael'dryn. Cherchez-la.","Prenez la Gemme avec vous : Shael'dryn en aura besoin si elle doit vous aider à réparer le Bâtonnet."}, {"Trouver Shael'dryn, au Puits de lune, au nord-est."}},
[1025] = {"Une défense agressive", {"Avant que vous n'alliez au Puits de lune, j'ai une autre tâche à vous confier pour aider les habitants d'Ashenvale, si ça vous dit de tester vos compétences.","Le long de la route qui commence à l'est, avant le fleuve Falfarren, les furbolgs ont attaqué des voyageurs. Leurs esprits tordus se nourrissent de la peur qu'ils causent et seul le sang des innocents les rassasie.","Aidez les Sentinelles à protéger nos terres en rendant à ces monstres la monnaie de leur pièce.","Je crois que leurs camps sont au sud de la route... Faites preuve de prudence."}, {"Tuer 1 Protecteur, 2 Ursas, 10 Totemiques et 12 Guerriers de la tribu Vile-sylve, puis retourner voir Raene Wolfrunner, à Astranaar."}},
[1026] = {"Le nettoyage selon Raene", {"Je regrette que vous n'ayez pas de meilleures nouvelles.","Raene a raison, je sais ce qu'avait créé Dartol et je connais les tréants du secteur – du moins je les connaissais. Désormais, ils sont corrompus et leur nature a changé.","Votre mission est de rapporter le morceau suivant du bâtonnet, mais vous devez d’abord trouver la clé qui ouvre le coffre qui contient le morceau.","La Clé en bois est sur l'un des tréants au nord-est, près de Gangrebois. Une fois que vous l'aurez, cherchez une petite clairière à l'ouest de la route près de la lisière de Gangrebois... Le coffre devrait être caché par là."}, {"Trouver la Clé en bois et un morceau du Bâtonnet de Dartol avant de retourner voir Shael'dryn au Puits de lune."}},
[1027] = {"Le nettoyage selon Raene", {"Le dernier morceau du Bâtonnet a été donné aux druides de Dor'danil, qui en assurent la garde. Je n'ai aucune idée de l'endroit où ils l'ont caché. Je suis désolée de ne pas pouvoir mieux vous aider.","Tout ce que je peux vous dire, c'est de suivre la route qui mène aux Tarides. Les druides se sont réfugiés à l'est de cette route, mais ils ne vous seront pas très utiles : ils ont été tués récemment... C'est une chose à laquelle j'évite de penser.","Quand vous aurez trouvé le dernier morceau, apportez-le-moi et je vous aiderai à réparer le Bâtonnet."}, {"Trouver le dernier morceau du Bâtonnet de Dartol et retourner voir Shael'dryn au puits de lune."}},
[1028] = {"Le nettoyage selon Raene", {"Le Bâtonnet est terminé, <nom>. Le réparer, c'était facile... Ce qui va nous donner plus de mal, c'est d’atteindre l'Autel qui a le pouvoir de le recharger.","Avec de la concentration et des efforts, cela ne devrait pas être trop dur pour quelqu'un comme vous, qui a déjà tant accompli.","Partez vers le sud-est, il y a très peu de chemins, et ne perdez pas le nord de vue – et faites preuve de prudence. L'Autel est caché dans les montagnes qui entourent cette prairie.","Une fois arrivé, placez le Bâtonnet sur l'Autel... Le reste relève de la magie."}, {"Trouver l'Autel caché et recharger le Bâtonnet de Dartol."}},
[1029] = {"Le nettoyage selon Raene", {"Je vous suggère d'aller trouver Raene pour lui dire que le bâtonnet est terminé. Elle saura vous dire quelle est votre prochaine mission.","Bonne chance, <nom>."}, {"Retourner voir Raene Wolfrunner à Astranaar."}},
[1030] = {"Le nettoyage selon Raene", {"Hélas, je ne sais pas ce qu'il comptait faire ensuite.","Si ce qu'il a dit sur le pouvoir du bâtonnet est vrai, alors il devrait vous permettre de prendre une autre forme. Laquelle, je ne sais pas.","Peut-être qu'il voulait utiliser ces pouvoirs pour approcher les furbolgs infectés, mais même les créatures non corrompues l'auraient abattu s'il avait manqué de prudence. Transformé par le bâtonnet, Dartol aurait pu parler à un furbolg ayant échappé aux effets de la gangremousse.","Vous devriez commencer vos recherches au sud-est du lac Mystral."}, {"Trouver un furbolg non corrompu et lui parler en utilisant le pouvoir du Bâtonnet de transformation de Dartol."}},
[1031] = {"La branche de Cénarius", {"Anilia pose ses mains sur vos épaules et vous attire vers elle. Haletante, elle indique l'arche au nord, avant de fermer les yeux pour toujours. \" Il l'a pris... Geltharis...\" Au fond du cœur, vous comprenez que le dernier souhait d'Anilia est de voir la branche de Cénarius à nouveau entre les mains des dryades."}, {"Tuer Geltharis et rendre la Branche de Cénarius à Illiyana au Sanctuaire d'Aessina."}},
[1032] = {"Mort aux satyres !", {"Je n'arrive pas à croire qu'Anilia ne soit plus là. Nous avions l'habitude de jouer ensemble dans la forêt. Je me sens presque... triste.","Je sais ce qui pourrait me faire me sentir mieux. Si vous pouviez massacrer les satyres qui m'ont pris Anilia ! Rapportez-moi leurs cornes comme preuve de leur mort ; si vous faites cela, vous gagnerez mon éternelle gratitude."}, {"Apporter 16 Cornes de satyre à Illiyana, au Sanctuaire d'Aessina."}},
[1033] = {"Larme d'Elune", {"J'espérais que le remède d'Orendil sauverait Relara, mais je crains qu'il ne puisse que ralentir la progression de la maladie qui terrasse mon enfant. Nous devons trouver des médicaments plus efficaces ! Heureusement, Orendil m'a parlé d'un autre remède à essayer au cas où le premier échouerait.","La Larme d'Elune est une variété de pierre qu'on ne trouve qu'en un seul endroit : l'île du lac Iris, à l'est d'Astranaar. Orendil a dit que cette pierre soignerait Relara... Je vous en prie, <nom>, apportez-moi la Larme d'Elune et priez pour qu'elle guérisse mon enfant !"}, {"Apporter une Larme d'Elune à Pelturas Whitemoon à Astranaar."}},
[1034] = {"Les ruines de Chimétoile", {"La Larme d'Elune a un peu amélioré l'état de ma fille. Ses cauchemars ont diminué et je remercie la déesse pour sa bénédiction. Mais... la fièvre n'a pas baissé.","Orendil m'a parlé d'une poussière dépolie que l'on trouve dans les Ruines de Chimétoile. Peut-être qu'elle diminuerait la fièvre de Relara. Trouvez un peu de cette poussière, et rapportez-la-moi, je vous en prie.","Les Ruines de Chimétoile se trouvent au sud, <nom>. Puisse Elune vous faire revenir rapidement !"}, {"Rapporter 5 Poignées de Poussière d'étoile à Pelturas Whitemoon à Astranaar."}},
[1035] = {"Le lac Tombeciel", {"La fièvre de Relara est tombée, mais elle est toujours très faible. Je ne crois pas que sa force reviendra avec le temps. Il va falloir le pouvoir d'une pierre de lune.","Nous avions l'habitude d'en ramasser au lac Tombeciel au sud-est... jusqu'à ce que les Sombretaillis s'y installent.","Leur chef, un oracle, a ramassé toutes les pierres tombées de la lune et les conserve. Vous devez maintenant lui reprendre une pierre de lune.","Mais pour ça, je crains que vous ne soyez dans l'obligation de le tuer."}, {"Reprendre une Pierre de lune tombée à l'Oracle Sombretaillis puis l'apporter à Pelturas Whitemoon à Astranaar."}},
[1036] = {"En avant toutes, jeune mariole !", {"<nom>... Oui, j'ai entendu parler de vous ; vous n'êtes pas l'<ami/amie> de Baie-du-Butin, exactement comme nous, les boucaniers de la Voile sanglante. L’amiral Firallon recherche des volontaires vigoureux et enthousiastes comme vous pour une mission spéciale.","Si cela vous intéresse, trouvez la Voile cramoisie et parlez avec l'amiral, sous le pont. Le navire est ancré plus à l’est, au large de la côte Sauvage, à proximité du Courant et de La Chance de la demoiselle."}, {"Parlez à l’amiral Firallon, à bord de la Voile cramoisie, au large de la côte de la vallée de Strangleronce."}},
[1037] = {"Velinde Starsong", {"Velinde Starsong m'a précédé dans la forêt d'Ashenvale. Au début, elle semblait contrôler la situation dans Gangrebois, mais peu à peu ses résultats ont faibli. Un beau jour, elle a tout simplement disparu.","On m'a envoyé ici pour continuer sa mission. Cependant, j'ai bien peur de ne rien savoir d'elle. Peut-être que Thyn'tel Bladeweaver, l'un des commandants des Sentinelles, a du nouveau sur sa disparition, des détails que j’ignorerais.","Elle comprendra certainement l'importance de ce genre d'informations."}, {"Parler à Thyn'tel Bladeweaver à la Terrasse des guerriers de Darnassus."}},
[1038] = {"Les effets personnels de Velinde", {"Le « Tome de Mel'Thandris » vous a montré cela ? Je suppose qu'il n'y a pas grand mal à vous permettre d'examiner ses affaires. Cette clé ouvrira le coffre dans lequel nous avons stocké ses effets, dans le baraquement des Sentinelles. Elle a tenu un journal de sa mission, s'il y a quoi que ce soit à apprendre, ce sera là-dedans.","Je dois ajouter que les Sentinelles croient qu'elle avait des raisons personnelles de partir et s'attendent à ce qu'elle revienne à tout moment. La prêtresse a fait beaucoup dans le passé pour gagner notre confiance."}, {"Fouiller le coffre de Velinde pour trouver son Journal et le rendre ensuite avec la clé à Thyn'tel Bladeweaver à Darnassus."}},
[1039] = {"Le port des Tarides", {"Ratchet est le seul port des Tarides. L'hypothèse la plus probable est que Velinde a trouvé à Ratchet un navire de commerce pour la conduire à la crique des Flots noirs, en Azeroth. Nous avons des relations limitées avec les gobelins qui gèrent le port, mais le Maître des docks devrait avoir des informations sur les arrivées et les départs des passagers des bateaux.","Suivez la route au sud-est d'Ashenvale et vous arriverez dans les Tarides. Faites preuve de prudence, <nom>, les guerriers de la Horde patrouillent dans le secteur. Mais au port, vous serez en sûreté."}, {"Aller voir le Maître des docks Dizzywig à Ratchet."}},
[1040] = {"Passage à Baie-du-Butin", {"Oh oui ! Enfin trouvé. Z'auriez dû me dire qu'elle était passée il y a longtemps. Voyons.","Velinde Starsong. Passage réservé à Baie-du-Butin sur le « Balbuzard noir ». Je n'ai rien d'autre là-dedans, donc je suppose qu'il est arrivé à bon port.","Je ne peux pas faire grand'chose de plsu pour vous. Elle a cependant demandé comment voyager par voie de terre de l'autre côté et je lui ai indiqué Ruzzgot, un chef de caravane basé à Baie-du-Butin. Peut-être que cette Velinde a voyagé avec lui.","Maintenant dégagez. Je n'ai pas toute la journée à vous consacrer."}, {"Prendre un bateau pour Baie-du-Butin et aller voir le Caravanier Ruzzgot."}},
[1041] = {"La route de la caravane", {"S'avère que j'ai eu tort à propos de vous et que ça n'arrive pas tous les jours.","Je viens juste de me rappeler de cette Velinde que vous cherchez. Pas tous les jours qu'une prêtresse des elfes de la nuit veut voyager dans une vieille et sale – mais parfaitement adaptée à vos transports de marchandises ! – caravane comme la mienne.","Nous avons pris ensemble le chemin vers le nord, elle se dirigeait vers Darkshire. L'employé là-bas tient à jour toutes sortes d'informations. Pourrait savoir quelque chose d'utile.","Faites preuve de prudence dans la jungle, c'est un endroit mortel même dans les bons jours."}, {"Aller voir le greffier Daltry à Darkshire."}},
[1042] = {"La famille Carevin", {"Non, je n'ai reçu aucun rapport concernant une Velinde Starsong résidant à Darkshire... Quoique, si vous voulez mon avis, j'aie du mal à imaginer une prêtresse elfe de la nuit prenant une chambre à l'auberge, si vous voyez ce que je veux dire.","Pour ce qui est des hommes-loups que vous mentionnez, j'en ai entendu parler. Justement l'autre jour, Calor est rentré en ville avec une guirlande de leurs têtes. Il travaille avec la famille Carevin. Des chasseurs de démons, de morts-vivants et autres monstres. Allez voir Jonathan, c'est le chef de famille."}, {"Aller voir Jonathan Carevin à Darkshire."}},
[1043] = {"La Faux d'Elune", {"Votre histoire sonne juste... Je ne comprends pas bien vos motifs, mais si vous venez dans le bois de la Pénombre pour le débarrasser de quelques worgens, je ne vais plus chercher à comprendre.","Il y a une mine au sud, que les worgens ont envahie... Ils sont apparus de nulle part, mais d'après ce que nous savons, c'est là qu'on les a vus pour la première fois.","Faites votre boulot, mais si vous découvrez quoi que ce soit d'important, je vous demande de m'en faire part. Nous acceptons l'aide de tout le monde dans notre guerre contre le Mal."}, {"Chercher les signes de la Faux d'Elune puis retourner voir Jonathan Carevin à Darkshire."}},
[1044] = {"Réponses aux questions", {"Nous maîtriserons le problème des worgens, n'ayez aucune crainte là-dessus. Ce mal que votre amie a introduit dans nos bois sera contenu et je ne lui en veux pas pour ses actions. Des événements étranges surviennent en ce moment, <nom> et l'obscurité ne connaît aucun répit.","Je ne vous retiens pas plus. Je soupçonne que d'autres ont entendu parler de ce que vous avez trouvé dans les mines du bois de la Pénombre."}, {"Retourner vers Thyn'tel Bladeweaver à Darnassus."}},
[1045] = {"Le nettoyage selon Raene", {"Les elfes de la nuit sont aussi vieux que le temps, mais leur arrogance détruit leur monde même. Et maintenant elle détruit aussi nos vies. Mais on ne peut plus rien contrôler...","Je blâme les elfes pour beaucoup de choses, mais nous sommes toujours responsables de nos actions. Même sans la corruption, nous sommes capables de mauvaises choses.","Ran Dent-rouge est de ce genre de créatures démoniaques. La soif de pouvoir, la luxure... La corruption n'a fait qu'aggraver les choses. Je brûle de haine pour les elfes, mais je n'appelle pas la maladie sur leurs enfants, parce que nous devons maintenir l'ordre chez les nôtres."}, {"Tuer Ran Dent-rouge et 4 Gardes de Dent-rouge, puis retourner voir Krolg près du lac Mystral."}},
[1046] = {"Le nettoyage selon Raene", {"Retournez dans votre tribu pour raconter votre victoire.","Montrez-leur votre trophée et appréciez la gloire que vous venez de conquérir…"}, {"Apporter le Crâne de Ran Dent-rouge et le Bâtonnet de transformation de Dartol à Raene Wolfrunner à Astranaar."}},
[1047] = {"La nouvelle frontière", {"Votre attention !","Le Cercle cénarien cherche à recruter ceux qui se sentent capables pour une mission d'exploration sur les nouveaux territoires de Kalimdor ! Des terres désolées, interdites, regorgeant de dangers, se trouvent à l'ouest de Tanaris et de Gadgetzan, et le Cercle a besoin d'honorables représentants pour agir en son nom.","Tous ceux et celles qui seraient intéressés devraient solliciter une audience auprès du Cercle cénarien. Parlez avec l'archidruide Fandral Staghelm, dans Darnassus, pour plus d'informations !"}, {"Parler à l’archidruide Fandral Staghelm, dans l'Enclave cénarienne, à Darnassus. Evoquer l’appel du Cercle cénarien concernant l’exploration des frontières de Kalimdor."}},
[1048] = {"Au monastère écarlate", {"Pendant que la Dame noire s'occupe du problème urgent posé par l'avance du roi-liche au nord, je me préoccupe de ce qui se passe par ici.","Aucun doute, les parasites humains qui se nomment la Croisade écarlate sont l’épine à l'intérieur même de nos terres. Les Clairières de Tirisfal grouillent littéralement de ces petites fourmis rouges.","Allez au Monastère, <classe>, et faites disparaître le Grand Inquisiteur Whitemane et ses lieutenants les plus importants."}, {"Tuer le grand inquisiteur Whitemane, le commandant Mograine de la Croisade, le champion Herod et le maître-chien Loksey, et retourner faire son rapport à Varimathras à Undercity."}},
[1049] = {"Compendium des Déchus", {"Le Cercle terrestre avait des intentions pures envers nos frères tourmentés. Mais qui connaît les motivations de ceux qui chuchotent aux oreilles du Conseil des Anciens ?","Les Réprouvés auxquels nous nous sommes alliés ont un lourd passé de duperie. Notre pacte a été trop hâtif. Peut-être Cairne aurait-il été sage de tenir compte des avertissements d'Orgrimmar.","Le cœur du Monastère écarlate abrite le « Compendium des Déchus », gardé par des humains fanatiques et fous furieux. Ils tirent leur méthode de leur folie, mais leurs recherches pourraient s'avérer utiles."}, {"Trouver le « Compendium des Déchus » au Monastère dans les Clairières de Tirisfal et retourner voir le Sage Truthseeker à Thunder Bluff."}},
[1050] = {"Mythologie des Titans", {"L'avenir est inévitable ! Nous nous lançons vers lui comme les gouttes de pluie tombent dans la Grande mer.","Mais peut-être qu'en étudiant le passé, nous pourrons savoir à quoi nous attendre pour mieux nous préparer.","Avant que Lordaeron ne connaisse ces événements sinistres, il y avait un bastion de connaissance caché dans les Clairières de Tirisfal. La rumeur disait qu'un livre appelé « la Mythologie des Titans » avait été placé dans la bibliothèque du vieux Monastère.","Retrouvez-le et peut-être qu'ensemble nous pourrons découvrir les secrets du passé !"}, {"Reprendre « la Mythologie des Titans » au Monastère et le rapporter au Bibliothécaire Mae Paledust à Ironforge."}},
[1051] = {"La vengeance de Vorrel", {"Bien que ma volonté soit intacte, je ne peux plus servir la Dame noire. Je sens que mes jours sur Azeroth vont prendre fin. Ma force de vie vole en éclats.","C'est l'œuvre de l'Interrogateur Vishas ! Il m'a torturé pendant des semaines ! Il a pris mon anneau de mariage et l'a donné à sa femme. Le bâtard !","Vengeance... Je veux ma vengeance !","Ce pompeux imbécile s'est vanté de sa précieuse femme, Nancy, qui vit le long du lac de Lordamere. Traquez cette femme, assassinez-la et rendez mon anneau de mariage à ma femme Monika, à Moulin-de-Tarren.","Exaucez mes dernières volontés..."}, {"Rapporter l'anneau de mariage de Vorrel Sengutz à Monica Sengutz à Moulin-de-Tarren."}},
[1052] = {"Sur le chemin écarlate", {"Nous, les membres de la Croisade écarlate, nous revendiquons des forteresses de Hearthglen aux Clairières de Tirisfal. Nous sommes assez fiers des bastions de nettoyage que nous avons installés partout dans Lordaeron.","Vous avez fait vos preuves contre les morts-vivants de Désolace. Mais la vraie menace de cette peste se trouve à Lordaeron.","Faites route jusqu'à la ville de Southshore, dans les Royaumes de l'est. Cherchez-y un croisé nommé Raleigh le Dévot. Remettez-lui cette lettre de recommandation portant mon cachet et il vous donnera une place d'honneur dans notre monastère."}, {"Apporter la Lettre de recommandation du Frère Anton à Raleigh le Dévot à Southshore."}},
[1053] = {"Au nom de la Lumière", {"J'ai servi autrefois la Croisade écarlate avec honneur, fidélité et fierté. J'ai cru que sa cause était noble : débarrasser Azeroth des morts-vivants.","Mais lorsque je suis allé au Monastère, dans les Clairières de Tirisfal, je me suis rendu compte combien les frères perdaient le sens des réalités. Aujourd'hui, ils sont persuadés que tous ceux qui ne portent pas leur tabard sont des êtres tourmentés. Des hommes et des femmes innocents ont été torturés sous prétexte qu'ils étaient tourmentés.","La Croisade écarlate doit être écrasée au nom de la Lumière. C'est vous, <nom>, qui devez renverser ce régime fourvoyé."}, {"Tuer le grand inquisiteur Whitemane, le commandant Mograine de la Croisade, le champion Herod de la Croisade et le maître-chien Loksey, et retourner faire un rapport à Raleigh le Dévot à Southshore."}},
[1054] = {"Diminution de la menace", {"Les furbolgs Crins-de-Chardon sont au nord d'Astranaar. Mes éclaireurs m'ont appris que leur nombre a très fortement augmenté ces derniers mois. S'ils deviennent trop nombreux, ils pourraient bien trouver le courage d'attaquer Astranaar...","Tuez-en autant que possible, mais surtout apportez-moi le crâne de leur chef pour me prouver que vous les avez arrêtés... Au moins pour l'instant.","Je vais rester à Astranaar pour attendre de vos nouvelles. Qu'Elune soit avec vous !"}, {"Apporter le Crâne de Dal Griffesang à Raene Wolfrunner à Astranaar."}},
[1055] = {"Le nettoyage selon Raene", {"Le Bâtonnet, maintenant achevé, repose entre vos mains. Shael'dryn va vouloir apprendre votre réussite."}, {"Retourner voir Shael'dryn au puits de lune."}},
[1056] = {"Voyage au pic des Serres-Rocheuses", {"Les Esprits de la forêt m'ont dit que vous aviez du courage et que vous vouliez voyager.","Au sud, près du lac Mystral, il y a un tunnel nommé la Perce des Serres. Par ce tunnel, vous arriverez dans un secteur connu sous le nom de la Combe des Cisailles, dans les Serres-Rocheuses. Une fois là-bas, faites route vers le sud-ouest, devant le lac de la Combe et ensuite vers le nord, en prenant la montée jusqu'au pic des Serres-Rocheuses.","Le Gardien Albagorm vous y attend. Tenez compte de ce qu'il vous proposera, <nom>.","Le voyage sera périlleux."}, {"Voyager pour aller voir le Gardien Albagorm au pic des Serres-Rocheuses."}},
[1057] = {"À la reconquête du Val calciné", {"En tant que protecteur des esprits de la forêt, vous pouvez sans aucun doute deviner pourquoi je suis alarmé.","Vous avez voyagé à travers ces montagnes pour atteindre ce pic, et avez sûrement vu les changements terrifiants qu'a subi la terre. La cupidité a mené à la destruction de ces collines sacrées.","Le Val calciné, au sud, a été consumée par une avide armée mécanique. Pire encore, les Rougefuries qui ont empiété sur ce territoire nous en interdisent l'accès et nous empêchent d’y prospérer à nouveau.","Chassez ces affreuses harpies, <nom> !"}, {"Le Gardien Albagorm du pic des Serres-Rocheuses veut que vous tuiez 7 Harpies, 7 Embusqués, 7 Tueuses et 7 Volplumes Rougefuries."}},
[1058] = {"Magie de la forêt de Jin'Zil", {"Je suis surexcité !","Trois petites cages pour trois petits elfes ! Mais qu'allons-nous faire de nos trois calinouilleurs d'arbre ? Eh bien ! Faisons-leur goûter à… comment appellent-ils cela ?… La magie de la forêt !","J'aurais besoin de pas mal de Sève des Serres-Rocheuses. Prenez également des Moustaches crépusculaires à ces affreux félins. Cela nous amène aux Yeux de coursier : on ne peut jamais en avoir trop sous la main. Et surtout n'oublions pas : une précieuse Ecaille de dragon-faë !","Hâtez-vous de rejoindre le pic des Serres-Rocheuses <nom>, que je puisse concocter une recette magique de la forêt."}, {"Le sorcier-docteur Jin'Zil à Malaka'jin veut 5 doses de Sève des Serres-Rocheuses, 5 Moustaches crépusculaires, 30 Yeux de coursier et une Ecaille de dragon-faë."}},
[1059] = {"À la reconquête du Val calciné", {"Les Rougefuries ont été maîtrisées. Mais elles se regrouperont bientôt si nous n'agissons pas.","Loin au sud des Tarides se trouve une terre connue sous le nom de Mille pointes. Si vous voyagez à l'ouest des Mille pointes jusqu'à la lisière de la luxuriante forêt de Feralas, vous trouverez un druide nommé Falfindel Waywarder.","Ce grand et sage druide est capable d'invoquer de puissants esprits de la forêt qui pourraient aider à la reconquête du Val calciné.","Faites-lui connaître notre requête ! Partez à sa recherche. Maintenant !"}, {"Trouver Falfindel Waywarder à la frontière entre les Mille pointes et Feralas."}},
[1060] = {"Lettre à Jin'Zil", {"Vous avez tellement fait pour moi, <nom>. Vous me portez vraiment chance et j'aimerais que vous voyiez quelque chose.","Les harpies ne viennent pas des environs des Tarides. Elles viennent d'un endroit appelé les Serres-Rocheuses, loin au nord-ouest d'ici. Bien qu'il s'agisse probablement d'un endroit dangereux pour vous, j'ai besoin que cette lettre soit remise à l'un de mes vieux amis qui y réside, un sorcier-docteur du nom de Jin'Zil.","Il sera content d'apprendre que Serena Plumesang est morte, et je crois que vous trouverez les Serres-Rocheuses intéressantes."}, {"Remettre la lettre de Darsok à Jin'Zil dans sa caverne des Serres-Rocheuses."}},
[1061] = {"Les esprits des Serres-Rocheuses", {"Les esprits des Serres-Rocheuses sont courroucés, car les gobelins et leurs serviteurs pillent et brûlent cette région. Ainsi la Terre a appelé à grands cris, et ceux qui sont à son écoute, ont entendu.","Seereth Stonebreak, un chaman prometteur de la Horde, a été envoyée dans les Serres-Rocheuses. Elle témoigne d’une large déforestation perpétrée par les gobelins, et elle demande de l'aide.","Rencontrez Seereth dans les Serres-Rocheuses. Pour la trouver, suivez la route à l'ouest de la Croisée des Tarides. Elle s'est établie près du val de Grandbois."}, {"Parler à Seereth Stonebreak dans les Serres-Rocheuses."}},
[1062] = {"Les invasions gobelines", {"La KapitalRisk, dirigée par des gobelins, s'est installée dans les Serres-Rocheuses, où ils coupent les arbres et brûlent des secteurs entiers de la forêt. Les esprits de cette terre sont quasiment aveuglés par la douleur et par la haine. Nous devons l'arrêter !","Partez pour le nord-ouest, au-delà du Val de Grandbois et dans la combe des Cisailles. Vous trouverez les gobelins et leurs serviteurs à l'oeuvre. Montrez-leur que la Horde ne permettra pas l'exploitation des Serres-Rocheuses. Prouvez-le avec les arguments qu'ils comprennent le mieux...","La violence."}, {"Tuer 15 Déboiseurs de la KapitalRisk, puis retourner auprès de Seereth Stonebreak à la frontière des Serres-Rocheuses et des Tarides."}},
[1063] = {"L'Ancienne", {"Pour vraiment soigner les esprits des Serres-Rocheuses, nous devons consulter les anciens esprits des taurens.","Le seul chef dont j’ai entendu parler est Magatha. Elle est très vieille et ses connaissances sont grandes. Si quelqu’un peut canaliser une aide spirituelle des taurens, c'est bien elle.","Magatha réside à Thunder Bluff, la capitale des taurens, sur la Cime des Anciens, la cité au nord-est du promontoire. Parlez à Magatha, et racontez-lui les périls encourus par les Serres-Rocheuses."}, {"Parler à Magatha à Thunder Bluff."}},
[1064] = {"L'aide des Réprouvés", {"Les taurens ont un lien avec cette terre, et apprendre la mauvaise santé des Serres-Rocheuses me peine. Mais j'ai peur que pour soigner cette terre, nous devions d'abord éradiquer la maladie qui l’envahit.","N'est-ce pas heureux, alors, que les Réprouvés soient nos alliés ? Ils en savent beaucoup à propos des maladies. Je pense qu'ils peuvent nous aider, et en nous aidant ils consolideront la confiance qui existe entre nos deux peuples.","Parlez à l'Apothicaire Zamah. C'est une érudite et un émissaire des Réprouvés. Vous la trouverez aux Bassins de la vision sous la Cime des Esprits."}, {"Parler à l'Apothicaire Zamah aux Bassins de la vision."}},
[1065] = {"Un voyage vers Moulin-de-Tarren", {"Pour débarrasser les Serres-Rocheuses de la KapitalRisk, nous devons employer des mesures extrêmes. Extrêmes, mais sans effusion de sang.","Il se peut que l'un de mes confrères ait exactement l'outil dont nous avons besoin. Il se nomme Lydon, et réside dans la ville de Moulin-de-Tarren, dans les contreforts d'Hillsbrad, dans la terre lointaine de Lordaeron.","Pour atteindre Hillsbrad, voyagez par zeppelin vers notre capitale, Undercity. Puis allez au nord vers les Clairières de Tirisfal, au sud-ouest vers les Pins argentés, et enfin au sud vers Hillsbrad.","Cherchez Lydon et donnez-lui cette note. Elle explique ce que nous attendons de lui."}, {"Apporter la Note de Zamah à l'Apothicaire Lydon à Moulin-de-Tarren."}},
[1066] = {"Le sang des innocents", {"Je vais créer une toxine pour annihiler la violence de la KapitalRisk, et la rendre faible et docile. Cela devrait régler le problème de la Horde dans les Serres-Rocheuses, pas vrai ?","Mais pour créer la toxine, je vais avoir besoin de sang d'innocent, un ingrédient difficile à trouver.","Heureusement le Syndicat, un groupe de brigands humains, emploie des mages de l'ombre... Et ces mages stockent du sang d'innocent pour leurs propres desseins, peu recommandables.","Vous trouverez les mages de l'ombre du Syndicat au donjon de Durnholde, au sud-est."}, {"Apporter 5 Fioles de Sang d'innocent à l'apothicaire Lydon à Moulin-de-Tarren."}},
[1067] = {"Le retour à Thunder Bluff", {"Voici la toxine. Si vous l'utilisez contre la KapitalRisk, cela devrait produire des effets intéressants, et peut-être même contagieux. Je dois dire que c'est très excitant de voir mon travail appliqué sur le terrain !","Mais avant de l'utiliser, apportez-la à l'Apothicaire Zamah. Elle doit concevoir une méthode de diffusion particulière pour optimiser les effets de la toxine."}, {"Apporter la Toxine de Lydon à l'Apothicaire Zamah à Thunder Bluff."}},
[1068] = {"Les machines à déchiqueter", {"Il existe une relation entre la magie spirituelle des chamans et la magie naturelle des druides. C'est pourquoi nous, les chamans, nous pouvons entendre la complainte des esprits des Serres-Rocheuses. Beaucoup de leurs murmures m'échappent, mais un message – une vision – est clair...","J'ai eu la vision de deux machines à forme humaine, dans la Combe des cisailles, au nord-ouest du val de Grandbois, coupant les arbres à une vitesse incroyable. Ces bêtes de métal doivent être stoppées !","Dans ma vision, il y avait d'étranges symboles gravés sur les machines :","XT4 et XT9."}, {"Détruire les Déchiqueteurs XT4 et XT9, puis retourner voir Seereth Stonebreak à la frontière entre les Serres-Rocheuses et les Tarides."}},
[1069] = {"Omelette à la mousse profonde", {"L’omelette aux oeufs d'araignée est la nouvelle mode culinaire à Baie-du-Butin ! Le problème c'est qu'il n'y a pas le moindre oeuf dans toute la ville.","Une possibilité se profile !","À la combe des Cisailles et dans le canyon de Sishir, dans les Serres-Rocheuses, au nord-ouest, vit l'araignée des mousses profondes. Apportez-moi ses oeufs et je vous payerai un sacré paquet !","À la combe des Cisailles, vous trouverez les oeufs groupés sous les arbres qui restent.","En ce qui concerne le canyon de Sishir... eh bien, si vous avez un estomac fragile, vous feriez bien de vous en tenir à la combe des Cisailles pour collecter les oeufs..."}, {"Apporter 15 Œufs des mousses profondes à Mebok Mizzyrix à Ratchet."}},
[1070] = {"De garde dans les Serres-Rocheuses", {"Salutations, <nom>.","Votre apparence semble indiquer que vous avez envie de parcourir le monde. Êtes-vous <allé/allée> dans les Serres-Rocheuses ? C'est une région tourmentée par la KapitalRisk et par d'autres ennemis des elfes de la nuit.","Récemment, la sentinelle Wolfrunner y a envoyé mon amie Kaela Shadowspear enquêter sur place, et je n'ai toujours pas reçu de ses nouvelles. L'inquiétude me ronge, d'autant que l'Alliance a envoyé un mage gnome avec elle... Mauvais présage, pour sûr.","Pourriez-vous la retrouver et revenir m'informer ?"}, {"Retrouver Kaela Shadowspear qui surveille la vallée à l'entrée de la combe des Cisailles dans les Serres-Rocheuses."}},
[1071] = {"Du répit pour un gnome", {"Vous voyez, <nom>, je suis un membre de l'Assemblée illuminée d'arcanologie, d'alchimie et de sciences d'ingénierie, et c'est mon but… non, il est de mon DEVOIR de montrer aux elfes de la nuit que l’association de magie, de mixtures et de mécanique peut aider à sauver leurs forêts.","Mais c'est un combat acharné, et les gobelins semblent se reproduire comme des lapins. Je n'ai même pas eu le temps de construire mes dernières inventions !","J'ai besoin de temps pour planifier et organiser. Mais vous pouvez m’aider à retenir les gobelins à quai à la combe des Cisailles, à l'est."}, {"Tuer 10 Déboiseurs de la KapitalRisk et 10 Défricheurs de la KapitalRisk, puis retourner voir Gaxim Rustfizzle dans les Serres-Rocheuses."}},
[1072] = {"Un vieux collègue", {"L'appareil auquel je pense est à ce jour ma version la plus avancée. Mais nous aurons besoin d'une potion spéciale si nous voulons que ça marche. Je pense que nous pourrons avoir la bonne dose également puisque cette mission sera votre dernière.","Et pour ça, il nous faut un puissant explosif : le Nitromirglyceronium.","La seule personne qui puisse faire du NG-5 est un de mes vieux amis : Lomac Gearstrip. Cherchez-le à Brikabrok.","Demandez-lui de nous faire du NG-5, je vais travailler au placement de mes appareils en attendant."}, {"Parler à Lomac Gearstrip à Ironforge."}},
[1073] = {"Inaptitude + Produits chimiques = Rigolo", {"Ha ha ha ! Vous avez besoin de nitromirglyceronium pour empêcher les gobelins de détruire une forêt ?","Savoir si vous connaissez Gaxim ou non ne m'intéresse pas… Je vais vous donner le matos juste histoire de vous voir ou de les voir exploser en petits confettis.","Il y a un problème quand même : je n'en ai plus.","Mais je vais vous dire, vous m'apportez les bonnes potions, et je vous en fais tout de suite.","Le moins que vous puissiez faire c'est de me montrer que vous avez des talents d'alchimiste avant que je vous donne un échantillon de ma grande œuvre."}, {"Apporter 4 Potions de mana mineures et 2 Elixirs de Robustesse mineure à Lomac Gearstrip à Ironforge."}},
[1074] = {"Inaptitude + Produits chimiques = Rigolo", {"Voilà, <étranger/étrangère>. Mais faites attention avec ça quand même… C'est volatil.","Si vous êtes vraiment <un ami/une amie> de Gaxim, alors transmettez-lui mes salutations. Si vous ne l'êtes pas, alors amusez-vous bien à vous faire sauter la tronche."}, {"Rapporter le Nitromirglyceronium à Gaxim Rustfizzle, dans les Serres-Rocheuses."}},
[1075] = {"Un parchemin de Collin Mauren", {"OK, maintenant à propos de l’explosion à distance… Oh ! Je n'ai pas encore expliqué cette partie de mon plan ?","Bien, alors voici la version courte : avec un peu de magie, on peut déclencher l’explosion sans avoir besoin de vous dans les parages. Je suis en train de peaufiner un système qui vous permettra d’appuyer simplement sur un bouton pour que le NG-5 explose, à votre commandement.","Mais j'ai besoin du sort d'un ami à Stormwind. Il s'appelle Collin Mauren. Vous le trouverez dans le quartier des mages, là-bas. C'est un humain, alors ça sera difficile de le rater."}, {"Trouver Collin Mauren à Stormwind."}},
[1076] = {"Des diables dans la marche de l'Ouest", {"Ah ! Je pense que ça vient. Un sort de contrôle… Je crois que c'est l'effet qu'il recherche.","Je vais devoir créer un parchemin pour y inscrire le sort. Pour cela, j’ai besoin de débris de Diable de poussière.","Tandis que je rassemble le reste des ingrédients, pourquoi n'iriez-vous pas dans la marche de l'Ouest me trouver une de ces ignobles créatures ? Elles sont dangereuses, mais je suis sûr que vous êtes <prêt/prête> à tout pour participer à une expérimentation de Gaxim.","Je serai là quand vous reviendrez."}, {"Apporter des Débris de Diable de poussière à Collin Mauren à Stormwind."}},
[1077] = {"Livraison spéciale pour Gaxim", {"Voilà, <nom>. Ça devrait faire l'affaire.","Assurez Gaxim de ma considération la prochaine fois que vous le verrez. J'ai hâte d'entendre la suite de cette aventure.","Adieu."}, {"Apporter le Parchemin de contrôle à Gaxim Rustfizzle dans les Serres-Rocheuses."}},
[1078] = {"Des retrouvailles pour Mauren", {"Les voyageurs m'interrogent toujours au sujet des Serres-Rocheuses. C’est une région populaire pour qui cherche l'aventure, qu’il s’agisse de traquer des wyverns, des élémentaires, ou du personnel de la KapitalRisk.","Dans le Val calciné, loin à l’intérieur des montagnes, vivait autrefois une espèce de basilic dont les écailles, une fois plantées dans la poussière, produisaient un merveilleux réactif pour certains des sorts que j'ai créés.","Si ces basilics vivent toujours, j'aimerais avoir quelques-unes de leurs écailles.","Prenez votre temps, ce n'est pas urgent, mais je paye bien."}, {"Apporter 8 Ecailles de cristal à Collin Mauren à Stormwind."}},
[1079] = {"Opérations secrètes – Plan A", {"Ça y est, <nom>. Faites très attention ! Les dispositifs sont montés et prêts à fonctionner.","Prenez ces explicatifs. Ils résument tout ce qu'il faut savoir sur les détonateurs et les explosifs, et indiquent également où vous devez les placer.","Rappelez-vous, le succès de votre mission ne dépend pas de la somme de destruction que vous provoquerez. Le succès du plan A dépend du fait que vous rapportiez les plans d'ingénierie de la KapitalRisk de l'intérieur de la scierie au sud du lac de la Combe."}, {"Récupérer les Plans d'ingénierie de la KapitalRisk dans la scierie de la Combe des Cisailles."}},
[1080] = {"Opérations secrètes – Plan B", {"Le plan B est identique au plan A, <nom>.","Nous faisons diversion et nous volons leurs précieuses notes.","Le plus important est d'obtenir leurs notes… Rappelez-vous de ça.","Les explicatifs que je vous ai déjà fournis indiquent où placer les explosifs. Revenez simplement ici en vie et avec ces notes..."}, {"Récupérer les Notes de la KapitalRisk dans la mine des Cisailles à l'extrémité est de la Combe des Cisailles."}},
[1081] = {"Réception par Tyrande", {"<nom>, il est sûr que Tyrande Whisperwind en personne voudrait vous remercier de prendre part à cette mission.","Il n’y paraît peut-être pas, mais votre aide pourrait renforcer notre position dans les Serres-Rocheuses.","Si vous vous considérez comme notre <allié/alliée> face à ces atrocités, allez voir Tyrande au Temple de la Lune de Darnassus."}, {"Trouver Tyrande Whisperwind à Darnassus."}},
[1082] = {"Une mise à jour pour la Sentinelle Thenysil", {"<nom>, vous avez accompli un noble geste en nous offrant votre aide.","Les Sentinelles d'Ashenvale vous récompenseront si vous leur donnez des nouvelles de Gaxim et de moi. Dites-leur que nous sommes bien partis pour stopper la KapitalRisk et soigner la terre.","Trouvez Tara Thenysil à Astranaar ; elle sera ravie de vous voir revenir avec de bonnes nouvelles."}, {"Retourner voir la Sentinelle Tara Thenysil en Ashenvale."}},
[1083] = {"Esprits enragés", {"Je ne sais pas exactement comment la KapitalRisk s’est comportée vis-à-vis de la vallée, mais une chose est sûre : elle a été submergée par des créatures locales.","Ses gens ont mis le feu à la forêt et utilisé leurs engins de destruction pour ravager la vallée.","Mais dans leur ignorance ils ont libéré des esprits ardents et des élémentaires de flamme, de roche et de rage.","La Terre n'a aucune chance de voir le sourire d'Elune tant que ces créatures la fouleront librement.","Apportez-moi leurs Braises ardentes pour me montrer que vous avez réduit leur nombre."}, {"Apporter 8 Braises ardentes provenant de n'importe quel élémentaire de feu du Val calciné à Kaela Shadowspear, dans les Serres-Rocheuses."}},
[1084] = {"Les Anciens blessés", {"Il me semble évident que les protecteurs de la vallée, les tréants, ont quelque chose à voir avec le retrait de la KapitalRisk. Malheureusement, même ceux qui étaient autrefois de grands gardiens sont aujourd'hui corrompus.","Gaxim pense que leur corruption est d'une certaine manière liée aux armes utilisées par les gobelins pendant le combat.","Je lui ai dit que je collecterais un résidu sur un tréant blessé pour qu’il l'étudie et découvre l’origine du mal.","Apportez-moi un morceau de shrapnel pour que le gnome puisse m'en dire plus..."}, {"Apporter un morceau de Shrapnel abîmé à Kaela Shadowspear, dans les Serres-Rocheuses."}},
[1085] = {"De garde dans les Serres-Rocheuses", {"La situation ici dans les Serres-Rocheuses est suffisamment grave pour qu'une elfe de la nuit et un gnome travaillent ensemble.","La KapitalRisk a dévasté cette région et j'ai passé la majeure partie de mon temps à mettre un terme à cette destruction.","Ne jugez pas Gaxim trop vite, il comprend la nécessité de notre travail commun.","Si vous souhaitez nous aider, alors commencez par parler à Gaxim.","Je suis désolée de ne pas encore pouvoir vous renvoyer à Tara avec de bonnes nouvelles. Bientôt peut-être…"}, {"Parler à Gaxim."}},
[1086] = {"L'aéroport de la machine volante", {"La toxine est prête. Tout ce qu'il reste à faire est de la livrer. Vous devez vous en occuper, <nom>.","La KapitalRisk doit avoir un moyen d'envoyer des messages et de petites cargaisons entre ses quartiers généraux et ses sites d'opérations dans les Serres-Rocheuses. Il s'agit probablement d'un petit aéroport, pas suffisamment important pour un zeppelin, mais assez pour une machine volante. Cherchez dans les Serres-Rocheuses les secteurs sous contrôle de la KapitalRisk et trouvez cet aéroport, s'il existe. Placez-y alors la toxine.","Placez-la, et courez !"}, {"Apporter le Brouillard toxique à l'aéroport de la KapitalRisk, puis retourner voir l'Apothicaire Zamah."}},
[1087] = {"L'héritage de Cénarius", {"Vous ne pouvez pas imaginer la haine que je ressens pour cette terre misérable. Au pic, les semblables de Cénarius et les druides kaldorei emploient leurs pouvoirs minables pour étouffer le flot de magie, même jusqu'au sud de leurs possessions pathétiques... leurs forêts bien-aimées.","Je voudrais retourner à Jaedenar et exulter dans le flot de magie, mais mon maître ne le permettra pas tant que mon travail ici ne sera pas achevé.","Il ne fait aucun doute que vous aimez les elfes de la nuit et leurs alliés aussi peu que je les aime. Aidez-moi, et notre but commun pourra être atteint."}, {"Tuer 4 Fils de Cénarius, 4 Filles de Cénarius et 4 Botanistes cénariens pour Braelyn Firehand, près de la retraite de Roche-Soleil."}},
[1088] = {"Ordanus", {"Ils ont cru qu'ils pourraient arrêter le flot de magie. Ils se sont trompés. Ils ont cru pouvoir cacher Ordanus, mais j'aurai sa tête !","La magie m'accorde la vision véritable. Oui… il est là. Un croisement… Au sommet d'un grand arbre dans la forêt d'Ashenvale. Un nom... Raynewood. L'un des fils aînés du dieu de la Forêt. Ils l'ont fait disparaître du pic avant que je puisse le trouver, mais son temps est compté.","Apportez-le-moi et je vous offrirai un puissant cadeau."}, {"Apporter la Tête d'Ordanus à Braelyn Firehand, près de la Retraite de Roche-Soleil."}},
[1089] = {"L'Antre", {"Les druides ont construit l'Antre des Serres dans le pic des Serres-Rocheuses. Ils y conservent de nombreux objets puissants.","Ils ont conçu un plan compliqué pour que ces derniers ne tombent pas entre de mauvaises mains.","Par chance, j'ai réussi à… extraire quelques informations de l'un d'entre eux.","Cachée dans une petite lanterne, à l'entrée de l'Antre, vous trouverez la Clé du gardien ; elle vous permettra d'ouvrir des cachettes autour de l'Antre. Les objets extraits de ces cachettes donnent accès au trésor, situé dans l'un des secteurs centraux."}, {"Aller dans l’Antre, sur le Pic des Serres-Rocheuses. En utilisant la Clé du gardien, obtenir les objets cachés des druides. Utiliser ces objets pour ouvrir le Trésor de l'Antre des Serres."}},
[1090] = {"Les ordres de Gerenzo", {"Vous savez, avant j'étais la seconde personne la plus importante ici sur ce site. Tout a changé quand Gerenzo est arrivé. Il a viré mon patron, Ziz Fizziks, et m'a envoyé bosser dans les mines. Ziz m'a dit de rester ici et de rassembler des renseignements. Son nouveau patron nous paiera bien si nous pouvons découvrir les plans de Gerenzo.","Mais bon, ce que je sais c’est que Gerenzo a ordonné que des quantités importantes de ce minerai or-vert soient extraites. Je peux vous trouver un échantillon pour que vous l'apportiez à Ziz, mais faites attention aux patrouilles le temps que j’aille en chercher."}, {"Obtenir le minerai mystérieux de Piznik après qu'il l’aura extrait."}},
[1091] = {"Des nouvelles pour Kaela", {"<nom>, apportez ces notes à Kaela. Il est certain qu'elle voudra en savoir plus à ce propos. Ou du moins ce sera le cas des Sentinelles.","Dites-lui à quel point notre victoire a été éclatante, et à quel point nous n'aurions pu y parvenir sans l'aide d'un peu de magie."}, {"Apporter les Notes de la KapitalRisk à Kaela Shadowspear."}},
[1092] = {"Les ordres de Gerenzo", {"Je pense que je ferais mieux d’envisager de ficher le camp d'ici, aussi. Ça ne va pas prendre beaucoup de temps avant que la nouvelle de ce qui s'est passé ici se répande au-dessus, et je suis sûr qu'ils ne seraient pas ravis de me revoir.","Je vais dégager d’ici et aller voir Ziz. J'espère qu'il pourra me trouver du boulot !","Je vais essayer de nettoyer ce bordel, mais vous devez apporter l'échantillon de minerai à Ziz tout de suite. Il m'a dit qu'il établirait un camp quelque part du côté ouest de la Combe des Cisailles. Bonne chance."}, {"Remettre le Minerai non identifié à Ziz Fizziks dans les Serres-Rocheuses."}},
[1093] = {"Super-Découpeur 6000", {"C'est terrible ce que la KapitalRisk a fait ici, hein ? Mais à quoi donc pensaient-ils ? Une compagnie de gobelins qui embauche un gnome !","Heureusement pour moi, Sputtervalve reconnaît un génie lorsqu'il en voit un. M'a embauché juste après que je me suis fait virer !","Il me dit qu'ils construisent une énorme machine pour découper le bois, et Sputt veut que je vole les plans pour lui. Malheureusement, Gerenzo – maudit gnome – veut ma peau... Peut-être que vous pourriez voler les plans pour moi ? Vous devriez les trouver sur l'un de leurs opérateurs."}, {"Trouver le Plan du Super-Découpeur 6000 pour Ziz Fizziks dans les Serres-Rocheuses."}},
[1094] = {"D'autres instructions", {"Cette enveloppe contient toutes les informations que j'ai pu rassembler avant qu'ils me flanquent à la porte : des documents internes de la KapitalRisk, quelques manuels, des trucs techniques… Et bien entendu, le plan du Super-Découpeur 6000.","Sputtervalve a élu résidence à Ratchet dans les Tarides. Vous ne devriez pas avoir trop de mal à le trouver, c'est un type un peu vert."}, {"Remettre l'Enveloppe scellée à Sputtervalve dans les Tarides."}},
[1095] = {"D'autres instructions", {"L’embauche de ce gnome par la KapitalRisk a créé quelques remous à Undermine. Les princes marchands n'approuvent pas que Razdunk fasse des affaires avec ce Gerenzo.","Riddlevox m'a demandé de m'occuper du problème, et il est certain qu'il a reçu, d'un ou de plusieurs princes marchands, les mêmes instructions. Bien sûr, le directeur ne me l'a pas dit. Disons que j'ai simplement une intuition.","Quoi qu'il en soit, nous devons faire en sorte de supprimer Gerenzo, et ce serait le test parfait de voir Fizziks s'en occuper."}, {"Apporter les nouveaux ordres à Ziz Fizziks dans les Serres-Rocheuses."}},
[1096] = {"Gerenzo Wrenchwhistle", {"J'ai rencontré Gerenzo une fois, mais laissez-moi vous dire qu'il n'est pas très beau à voir. On dirait que sa chair est en train de pourrir, et il s'est fait greffer du métal et des accessoires sur le corps. Il a remplacé certains membres naturels par des membres mécaniques.","Au nord-est d'ici, loin au-dessus du lac de la Combe, près de la roue hydraulique, Gerenzo surveille le site entier.","Je ne pourrais pas monter là-haut, je ne survivrais pas aux combats. Sans parler de me charger de Gerenzo... Mais vous semblez être capable de réussir.","Si vous le tuez pour moi, je ferai en sorte que ça en vaille le coup !"}, {"Apporter le Bras mécanique de Gerenzo Wrenchwhistle à Ziz Fizziks dans les Serres-Rocheuses."}},
[1097] = {"La quête d'Elmore", {"Il y a un fabricant d'armes à Stormwind, un nain du nom de Grimand Elmore, qui nous a envoyé un mot disant qu'il avait besoin d'aide avec une livraison. Je crois qu'il veut qu'un paquet soit envoyé dans son pays d’origine, dans le nord.","Vous avez des jambes robustes ! Si vous êtes <intéressé/intéressée> par un peu de marche à pied, alors parlez à Grimand. Nous pourrions vous utiliser ici, mais nous devons également entretenir nos bonnes relations avec les nains.","Vous trouverez Grimand Elmore au magasin d'armes dans le quartier des nains de Stormwind, dans la partie nord-est de la ville."}, {"Parler à Grimand Elmore."}},
[1098] = {"Des Traqueurs noirs à Ombrecroc", {"En préparation d'un assaut sur Ombrecroc, deux Traqueurs noirs ont été envoyés au donjon pour réunir des renseignements. Plusieurs jours ont passé, et ils ne sont toujours pas revenus. Nous devons cependant prendre connaissance de leur rapport si nous voulons que notre attaque ait la moindre chance de réussir !","Aussi voici votre tâche, <nom>. Partez pour le donjon d'Ombrecroc au sud et trouvez les Traqueurs noirs Adamant et Vincent. Trouvez-les, et s'ils ont besoin d’aide, aidez-les !","Cette mission est aussi dangereuse qu'elle est critique, <nom>. N'échouez pas."}, {"Trouver les Traqueurs noirs Adamant et Vincent."}},
[1100] = {"Journal de Lonebrow", {"Vous ouvrez lentement le journal décoloré de Henrig Lonebrow…"}, {"Lire le Journal de Henrig Lonebrow."}},
[1101] = {"La mégère du Kraal", {"Pauvre Heralath ! Et ce nain... Lonebrow. Quel esprit courageux il avait.","Nous ne devons pas laisser leur vaillance périr pour rien. Cette mégère… Charlga Trancheflanc… doit être arrêtée.","Il ne fait pas de doute qu'infiltrer le Kraal sera périlleux. Nous n'avons pas le temps d'envoyer un mot à Darnassus. <nom>, rassemblez un groupe et tuez la mégère.","Apportez-moi le médaillon de Trancheflanc comme preuve de son décès."}, {"Apporter le Médaillon de Trancheflanc à Falfindel Waywarder à Thalanaar."}},
[1102] = {"Un destin funeste", {"Cairne est un noble chef qui a unifié notre peuple, ici, à Thunder Bluff.","Mais je ne peux pas pardonner aussi facilement à ceux qui nous ont éloignés de nos terres ancestrales. Mon peuple a habité les Tarides du sud pendant des décennies. Cette terre est sacrée pour nous. Mais nous en avons été chassés par de nombreux ennemis.","Un destin funeste attend la sinistre mégère, Charlga Trancheflanc, qui rameute une ignoble armée depuis le Kraal de Tranchebauge.","Apportez-moi le coeur de Trancheflanc et je pourrai mourir en paix, <nom>."}, {"Apporter le Cœur de Trancheflanc à Stonespire le Vieil, à Thunder Bluff."}},
[1103] = {"L'appel de l'eau", {"Bienvenue, <mon pote/m'dame> ! Vous êtes ici pour purifier l'eau et prouver votre compréhension de cet élément à Islen ? J'viens juste de vaincre l'esprit corrompu de l'eau, ouais, moi-même, et j'm'apprêtais à placer les bracelets et les gouttes d'eau restantes dans le brasero.","Qu'est-ce que c'est que ce machin ? Ahhh. Tiev comprend très bien quand quelque chose tourne mal. Eh bien, Tiev peut beaucoup vous aider si vous voulez voir de nouveau l'esprit pur de l'eau.","Apportez-moi un sapta d'eau d'Islen comme preuve de votre bonne préparation, et j'vous attendrai pour faire ma part du travail."}, {"Apporter un sapta d'eau à Tiev Mordune dans la forêt des Pins argentés, si vous n'arrivez pas à parler à la Manifestation d'eau mineure."}},
[1104] = {"Le venin acide des salines", {"J'ai tellement de modèles pour notre chariot explosif ! Tellement ! Et je dois tous les essayer !","Je travaille à un nouveau type de carburant. Un qui met vraiment vraiment vraiment le feu ! Je n'ai pas encore trouvé le mélange exact, mais je pense savoir ce dont j'ai besoin... Les scorpides qui vadrouillent dans les Salines ont un venin qui contient des tas et des tas d’acides. C'est tout à fait unique ! Et c'est exactement ce qu'il me faut !","Apportez-moi de ce venin et croyez-moi, notre chariot va voler !"}, {"Apportez 6 doses de Venin de scorpide acide à Fizzle Brassbolts, dans les Salines."}},
[1105] = {"Les carapaces durcies", {"Mon frère travaille sur un nouveau carburant pour son chariot explosif. Il est brillant, vraiment. Tellement brillant que… Je pense que ce nouveau carburant va être tellement corrosif qu’il risque de dissoudre le châssis.","Je veux renforcer ses réservoirs avant que ça n'arrive.","Les tortues des Salines ont une carapace très résistante. J'avais l'idée de les utiliser pour créer de nouveaux réservoirs sur notre chariot explosif. Des réservoirs capables de contenir le nouveau carburant de mon frère.","Vous avez l'air suffisamment responsable. Vous pourriez m'obtenir ces carapaces ?"}, {"Apporter 9 Carapaces de tortue durcies à Wizzle Brassbolts dans les Salines."}},
[1106] = {"Martek l'Exilé", {"Je suis en train de développer un nouveau moteur. Il rendra le chariot si rapide ! Suffisamment rapide pour voler, je le sais ! J'ai juste besoin de faire des pistons qui peuvent supporter de très très grandes pressions. Tous mes essais ont échoué, car je n'arrive pas à faire des pistons suffisamment forts !","Mais il y a quelqu'un qui pourrait m’aider.","Son nom est Martek l'Exilé. C'est un grand forgeron et il en connaît plus sur les métaux que n'importe qui d'autre. Apportez-lui cette lettre, je sais qu'il peut m'aider !","Mais il est très très loin. En Azeroth, dans les Terres ingrates, dans un camp, avec un gobelin !","Trouvez-le !"}, {"Apporter la Lettre de Fizzle à Martek l'Exilé dans les Terres ingrates."}},
[1107] = {"Les nageoires incrustées", {"Notre chariot explosif est ce qu'il y a de plus rapide sur les plateaux. Tellement rapide que… j'ai peur que les frottements ne le détruisent complètement.","Pour empêcher cela, je dois créer un composite pour enduire les parties mobiles du chariot. Il doit être dur, mais pas friable, et pouvoir résister à la corrosion du sel, ou le temps passé dans les Salines le grignotera petit à petit.","Avez-vous entendu parler du Récif infâme, <nom> ? C'est près de la côte de la vallée de Strangleronce. Des murlocs Selécaille y vivent, et leurs nageoires sont un élément crucial de mon composite."}, {"Apporter 10 Nageoires incrustées à Wizzle Brassbolts dans les Salines."}},
[1108] = {"Indurium", {"Je ne suis pas là simplement pour rester à l’écart des gens. Il y a une fortune en métal dans les Terres ingrates, <nom>, si vous avez les tripes pour aller la chercher.","Le métal s'appelle l'indurium, et une de ses qualités est sa très haute résistance à la chaleur et à la pression.","Ça pourrait faire l'affaire pour le chariot de Fizzle.","Mais nous devons nous en assurer. Trouvez-moi des échantillons d'indurium et je testerai ses propriétés.","Le véritable indurium se trouve dans les profondeurs d'Uldaman, mais les troggs Cavepierre des Terres ingrates en ont parfois des flocons sur eux. Apportez-moi ces flocons."}, {"Apporter 10 Flocons d'indurium à Martek l'Exilé dans les Terres ingrates."}},
[1109] = {"Corvée de guano", {"Comme vous pouvez le constater, <nom>, nous préparons de très… intéressantes expérimentations à la Société royale des apothicaires.","En tant que Maître, mon travail est de superviser les plus ambitieuses de nos tentatives alchimiques. Tellement de travail et si peu de temps !","Vous avez l'air d'avoir beaucoup voyagé, <classe>. Peut-être pourriez-vous m'aider.","J'ai besoin d'une substance rare. C'est un matériau infect, qui provient d'une espèce rare de chauves-souris que l'on trouve dans la fosse connue sous le nom de Kraal de Tranchebauge. Apportez-moi du guano de chauve-souris du kraal afin que nous puissions nous mettre au travail..."}, {"Apporter 1 tas de Guano du kraal au Maître Apothicaire Faranell à Undercity."}},
[1110] = {"Pièces de chariot explosif", {"Eh ! Avec toutes les courses qui ont lieu ici, il n'est pas surprenant que quelques incidents surviennent le long du chemin.","Si vous regardez tout autour des Salines, vous verrez des traces d’accidents. On trouve des pièces de chariots explosifs partout !","Et ces pièces valent de l'argent. Elles intéressent les gnomes et les gobelins. Ils cherchent toujours des pièces à monter sur leurs chariots.","Alors allez-y et trouvez-moi des pièces. Apportez-m'en un tas et je vous paierai bien."}, {"Apporter 30 Pièces de Chariot explosif à Kravel Koalbeard des Salines."}},
[1111] = {"Maître des quais Dizzywig", {"Mon boulot, c'est que les gens obtiennent ce dont ils ont besoin, et les gnomes par-là ont besoin qu'une commande soit passée. Leurs pièces sont arrivées à Ratchet il y a une semaine, mais je n'ai pas pu faire le voyage.","Alors, êtes-vous <prêt/prête> à marcher un peu ? Allez à Ratchet, et donnez cette commande de pièces au Maître des quais Dizzywig. Il se trouvera probablement sur les docks.","Faites cela pour moi, et j'aurai plus de boulots à vous confier.","Des boulots, mon <ami/amie> avec de sérieux bénéfices à la clé..."}, {"Apporter la Commande de Kravel au Maître des quais Dizzywig à Ratchet."}},
[1112] = {"Les pièces détachées de Kravel", {"Voici le paquet de Kravel, <nom>. Et lorsque vous lui remettrez, dites-lui que s'il veut plus de commandes spéciales, alors il doit payer l'addition !"}, {"Apporter les Pièces détachées de Kravel à Kravel Koalbeard dans les Salines."}},
[1113] = {"Des cœurs zélés", {"Vous pourriez vous demander pourquoi j'ai besoin d'un type rare de guano. Eh bien, pour cette expérience, je veux voir quels effets deux substances chimiques diamétralement opposées peuvent avoir l'une sur l'autre, et spécialement lorsqu'elles sont combinées à un agent extrêmement toxique pour assurer les résultats les plus... effrayants.","Dans les pics au nord-est des Clairières de Tirisfal, vous trouverez le Monastère écarlate. Là, les disciples fanatiques de la Croisade se réunissent. Tuez-les et apportez-moi leur coeurs zélés.","Notre mélange sera particulièrement diabolique !"}, {"Le maître apothicaire Faranell, à Undercity, veut 20 Cœurs zélés."}},
[1114] = {"Une livraison pour les gnomes", {"À présent que j'ai ce dont j'ai besoin, apportez ces pièces à Fizzle Brassbolts. Cela fait longtemps qu'il les attend. Je suis sûr que la pièce que j'ai prélevée sur sa commande ne lui manquera pas…","Et ça ne vaut même pas le coup de lui dire. C'est une toute petite pièce complètement inutile, de rien du tout. Minuscule...","Faites-moi confiance."}, {"Apporter les Pièces de chariot fragiles à Fizzle Brassbolts dans les Salines."}},
[1115] = {"Le marchand de rumeurs", {"Maintenant que nous avons rendu un service aux gnomes, il est temps de faire une faveur aux gobelins !","Je crois savoir que le pilote de course gobelin, Nazz Steamboil, n'a pas toujours porté ce nom. Je me demande pourquoi il en a changé. Je me demande s'il s'agit d'un secret, et je me demande s'il accorde de la valeur à ce secret.","Si quelqu’un connaît la vérité, alors c'est Krazek à Baie-du-Butin, sur la pointe sud de Strangleronce. Parlez à Krazek et interrogez-le sur notre pilote gobelin.","Si vous parvenez à en tirer une information utile, alors nous pourrions peut-être en tirer profit..."}, {"Parler à Krazek à Baie-du-Butin."}},
[1116] = {"De la Poussière de rêve dans les marécages", {"Vous obtenir des informations sur Nazz est une grande faveur que je vous fais, <nom>. Oui, une grande faveur ! Mais vous avez fait tout le chemin depuis les Salines pour en avoir… Voulez-vous voyager encore un peu ?","Pendant que je contacte mes sources, pourquoi ne chercheriez-vous pas quelque chose pour moi, hein ?","Hmm... Qu'est-ce que ce sera ?... Et que diriez-vous de Poussière de rêve ? On en trouve chez les dragonnets et les wyrmides du marais des Chagrins... Oui, j'aimerais de la Poussière de rêve pour ma blague à tabac !","Trouvez-m'en, <nom>, et je vous fournirai l'information que vous recherchez."}, {"Apporter 10 Grains de Poussière de rêve à Krazek à Baie-du-Butin. La Poussière de rêve se récupère sur les dragonnets du marais des Chagrins."}},
[1117] = {"Des rumeurs pour Kravel", {"J'ai dû utiliser une faveur qu’on me devait et abandonner une bouteille de vin elfique rare. J'ai même dû faire des déductions difficiles moi-même. Mais j'ai trouvé l'information que vous cherchez.","Tenez, j'ai écrit une lettre avec des passages très croustillants au sujet de notre ami, Nazz Steamboil. Faites-en ce que vous voulez, c'est trop dangereux pour que moi je l'utilise.","Mais ne dites à personne qui vous a informé !"}, {"Apporter les Rumeurs gobelines à Kravel Koalbeard dans les Salines."}},
[1118] = {"Retour à Baie-du-Butin", {"<nom>, vous êtes quelqu'un en qui je peux avoir confiance, pour faire le boulot, mais aussi pour la fermer tout en le faisant.","J'ai échafaudé un nouveau plan, <classe>. C'est risqué, mais si ça marche, on peut tous les deux se faire un paquet ! Et je pense que vous êtes la bonne personne pour ne pas se laisser abuser.","Pour commencer, vous devez retourner à Baie-du-Butin. Désolé, <cher/chère> <nom>. Je sais que ma dernière quête vous y a <amené/amenée>. Mais cette fois-ci, parlez à Crank Fizzlebub.","Donnez-lui cette lettre. Et s'il demande de l'aide, faites de votre mieux pour lui apporter. Cela en vaudra la peine."}, {"Apporter le Plan de Kravel à Crank Fizzlebub à Baie-du-Butin."}},
[1119] = {"La bière du sot", {"J'ai trouvé comment mélanger la Mixture de Zanzil avec de la bière pour créer une boisson qui décape. Une Bière du sot. Je pense que c’est exactement ce dont Kravel a besoin, mais essayez-la et dites-moi comment ça se passe.","Et <nom>, ne gardez pas trop longtemps ce truc sans l'utiliser. La Mixture de Zanzil a un effet bizarre sur les levures de la bière, elle les booste un max. Mais elle ne va pas rester fraîche éternellement."}, {"Rapporter la Bière du sot à Kravel, aux Salines."}},
[1120] = {"Saoulez les gnomes", {"Nous devons essayer cette bière du Sot sur l'une des équipes de mécanos. Si vous voulez l'essayer sur l'équipe des gnomes, alors tenez, prenez la bière et donnez-la au chef des mécaniciens gnomes.","Et n'attendez pas trop. Cette bière va se gâter après un temps."}, {"Apporter la bière du Sot au Chef-mécanicien gnome."}},
[1121] = {"Saoulez les gobelins", {"Nous devons essayer cette bière du Sot sur l'une des équipes de mécanos. Si vous voulez l'essayer sur l'équipe des gobelins, alors tenez, prenez la bière et donnez-la au chef des mécaniciens gobelins.","Et n'attendez pas trop. Cette bière va se gâter après un temps."}, {"Apporter la bière du sot au Chef-mécanicien gobelin."}},
[1122] = {"Le compte rendu à Fizzlebub", {"Cette Bière du sot a marché à merveille ! J'ai parié sur la course suivante… Vous pensez bien que l'équipe que vous avez éméchée a fait son numéro sur le chariot !","Il n'y a plus qu'une chose à faire : apportez cette note à Crank Fizzlebub à Baie-du-Butin. Il voudra connaître les résultats du test.","Et avec un peu de chance, nous aurons plus de cette Bière du sot pour les courses futures !"}, {"Apporter le Rapport sur la Bière du sot à Crank Fizzlebub à Baie-du-Butin."}},
[1123] = {"Rabine Saturna", {"Pour cette tâche, vous devrez assister et aider le majordome du Gardien Remulos, Rabine Saturna, à Reflet-de-Lune. Il vous faudra explorer les terres désolées qui se trouvent loin à l’ouest de Tanaris et d’Un'Goro. Il habite à Havrenuit, le plus grand village de Reflet-de-Lune.","Je voudrais que vous compreniez à quel point votre présence y est importante. En effet, les tensions ne cessent de croître entre les elfes de la nuit de Darnassus et le Cercle cénarien de Reflet-de-Lune. Essayez de vous départir de tout préjugé racial et appelez-en à la sagesse de vos ancêtres qui savaient comment traiter avec le Cercle cénarien, <nom>. Bonne chance."}, {"Parler à Rabine Saturna, au village de Havrenuit, à Reflet-de-Lune. Reflet-de-Lune se trouve entre Gangrebois et le Berceau-de-l'Hiver. Le chemin qui y mène traverse le Repaire des Grumegueules."}},
[1124] = {"La dévastation", {"A l’ouest du cratère d’Un'Goro, par la crête du nord-ouest, se trouvent d’arides terres désolées du nom de Silithus. Peu nombreux sont ceux qui savent qu’une guerre y eut lieu, opposant les elfes de la nuit à une force extérieure maléfique. Le mal put être contenu, mais maintenant nous pensons que des forces sont à l’ouvrage pour réanimer cette inimaginable... horreur.","Allez voir Layo Starstrike, l’un de nos agents dans Silithus ; vous le trouverez au cimetière du Repos des vaillants, là où reposent tous ceux qui sont tombés. Donnez-lui cette lettre, afin qu’il vous accorde sa confiance."}, {"Parler à Layo Starstrike, à proximité du Repos des vaillants de Silithus, et lui montrer la Lettre de Rabine."}},
[1125] = {"Les esprits de Sudevent", {"Au sud-ouest se trouve un endroit terrible : les ruines du village de Sudevent. C’est là que nous devons commencer à travailler, <nom>.","D’après ce que j’ai appris, ce village fut jadis une base des elfes de la nuit en Silithus. Il est maintenant envahi par des ruches de silithides. Je ne peux penser sans la plus grande horreur comment le village fut jadis emporté et submergé...","Les esprits torturés des druides et des Sentinelles hantent les ruines, sans but. Explorez cet endroit et libérez les âmes de ces malheureux."}, {"Libérer l’esprit de 8 Druides torturés et de 8 Sentinelles torturées, dans le village de Sudevent, puis revenir voir Layo Starstrike, au cimetière du Repos des Vaillants de Silithus."}},
[1126] = {"Une ruche dans la tour", {"La ruche doit être traitée au village de Sudevent, et je pense que je sais comment les atteindre. Mon expérience m’a appris que les ruches des silithides étaient extrêmement sensibles à leur environnement. Ce qui explique leurs attaques dès qu'ils entendent les gémissements des esprits.","Les silithides sont plus nombreux dans la tour ; celle qui est entourée d’un nuage de guêpes. Escaladez la tour et forcez la ruche à entrer en activité. Revenez faire votre rapport sur ce que vous avez appris, mais faites attention ! Si vous découvrez quoi que ce soit qui vous semble utile, rapportez-le-moi."}, {"Escalader la Tour du village de Sudevent, et trouver un moyen de mettre la ruche des silithides en activité. Toute chose inhabituelle doit être rapportée à Layo Starstrike, au cimetière du Repos des vaillants de Silithus."}},
[1127] = {"Bière du sot", {"Ainsi, vous voulez de la Bière du sot, hein ? Vous pensez retourner aux courses ?","Très bien, apportez-moi de la Mixture de Zanzil et je préparerai davantage de bière pour vous."}, {"Apporter 12 Mixtures de Zanzil à Crank Fizzlebub à Baie-du-Butin."}},
[1130] = {"Melor envoie un message", {"<nom>, Melor Stonehoof a entendu parler de vos exploits, et vous a préparé une voie à suivre.","Si vous souhaitez l'emprunter, alors partez pour Thunder Bluff et parlez à Melor. Vous le trouverez sur la Cime des chasseurs."}, {"Parler à Melor Stonehoof à Thunder Bluff."}},
[1131] = {"Broie-l'acier", {"Parfois, nous chassons pour vivre. Parfois, nous chassons pour l'honneur. Et parfois, nous chassons pour mériter le savoir de la Terre-mère.","Mais la chasse qui vous attend n'est pas de celles-là. Il s'agit de détruire une créature qui refuse les bénédictions de la Terre-mère.","Les Mille pointes, au sud des Tarides, sont le terrain de chasse de la hyène Broie-l'acier. C'est un tyran, qui attire à lui les autres hyènes, répand la terreur et massacre les bêtes plus paisibles.","Trouvez Broie-l'acier, <nom>. Trouvez-le et tuez-le."}, {"Apportez 1 Côte de Broie-l'acier à Melor Stonehoof à Thunder Bluff."}},
[1132] = {"Fiora Longears", {"Ah ! Pouvoir prendre la mer une fois encore ! Sentir le baiser du vent, être bercé par les vagues comme un enfant par sa mère !","J'aimerais avoir votre chance, cher <classe>, car je vois la mer dans votre avenir proche !","Il est de mon devoir de raconter aux âmes vaillantes la terre de Kalimdor, la terre de toutes les possibilités ! Si vous voulez chercher fortune sur la mer, prenez ici un bateau qui vous mènera au joli port de Theramore. Adressez-vous alors à mon amie, l'elfe Fiora Longears.","Elle vous aidera à commencer l'aventure de Kalimdor !"}, {"S'adresser à Fiora Longears à Theramore."}},
[1133] = {"Le voyage vers Astranaar", {"Si vous débarquez tout juste du bateau qui vient de Menethil, la première chose à faire est d'aller à Astranaar. Je suis sûr <qu'un/qu'une> fidèle membre de l'Alliance comme vous peut faire beaucoup de bien là-bas. Allez offrir vos services à Shindrell Switfire.","Inutile de mentionner le nom de la personne qui vous envoie, car Shindrell ne me connaît pas..."}, {"Parler à Shindrell Switfire à Astranaar."}},
[1134] = {"Les Ailes-fières des Serres-Rocheuses", {"Nous ne sommes pas en guerre ouverte avec la Horde, mais la paix est fragile. Leurs agents agissent clandestinement contre nous.","Leurs incursions nous ont été révélées par des sources secrètes à Theramore : il semble que des orcs collectent du venin des wyvernes Ailes-fières du lac Mirkfallon, dans le sud des Serres-Rocheuses. Un venin utilisé ensuite par leurs assassins lors de raids secrets contre l'Alliance !","S'il s'agit de la vérité, nous devons les stopper, <nom> ! Nous devons anéantir leur source d'approvisionnement en venin."}, {"Apporter 12 Glandes de venin d'Aile-fière à Shindrell Swiftfire à Astranaar."}},
[1135] = {"Le venin de Haut-perchoir", {"Comme vous le savez, les wyvernes sont souvent domestiquées par les orcs, qui les utilisent comme montures. Ce que l'on sait moins, c'est que, dans certains cercles, le venin de wyverne est plus recherché que la bête elle-même. Si vous n'avez pas peur de chasser la wyverne, j'ai un travail pour vous.","Il existe dans la région des Mille pointes, à l'ouest, un lieu nommé Haut-perchoir. Les wyvernes qui y nichent produisent un puissant venin. Très puissant et largement monnayable."}, {"Apporter 10 Glandes à venin de Haut-perchoir à Fiora Longears à Theramore."}},
[1136] = {"Gueule-glacée", {"<nom>, il est temps pour vous de voyager vers de lointaines contrées. Le moment est venu.","Vous devez partir à la poursuite de Gueule-glacée, un yéti des montagnes d'Alterac. Il est très difficile à trouver, car sa fourrure blanche se confond avec la neige de ses montagnes maternelles.","Je vais vous montrer comment attirer une telle créature à découvert. Prenez ce parchemin. Il vous indiquera la manière de débusquer Gueule-glacée.","Allez, maintenant, <nom>. Votre voyage sera long et plein d'embûches. Votre quête sera accomplie lorsque vous rapporterez la crinière de Gueule-glacée."}, {"Rapporter la Crinière de Gueule-glacée à Melor Stonehoof à Thunder Bluff."}},
[1137] = {"Des nouvelles pour Fizzle", {"L'Indurium est étonnamment résistant à la chaleur. Je suis certain que ce métal serait idéal pour les pistons de Fizzle. Mais il lui faudra du minerai d'Indurium naturel, pas uniquement quelques paillettes.","On ne trouve ce minerai que très profondément enfoui dans les terres d'Uldaman, en bordure septentrionale des Terres ingrates. C'est là que se trouve le véritable Indurium.","Envoyez mes amitiés à Fizzle Brassbolts et répétez-lui ce que je viens de vous dire.","Que votre voyage soit paisible, <nom>. Je ne serais pas étonné que nous soyons appelés à nous rencontrer de nouveau... Dès que Fizzle recevra mon message au sujet de l'Indurium, il vous enverra certainement à Uldaman pour en chercher !"}, {"Parler à Fizzle Brassbolts dans les Salines."}},
[1138] = {"Fruit de la mer", {"J’adore le crabe, ce fruit de mer par excellence ! Vous pouvez le faire griller, l’ébouillanter, le braiser, en faire des beignets frits, des acras, le déguster en salade ou le consommer en soupe ; voilà tout ce qu’on peut faire avec.","Je suis ici en train de pêcher en ce moment, et je ne peux donc pas me procurer les morceaux de belle chair de crabe dont j'ai besoin. Vous pouvez en trouver sur les clampants des récifs et sur les clampants des marées incrustés. Vous pouvez en faire plein de choses. Vous pouvez le faire griller, l’ébouillanter, le braiser..."}, {"Collecter 6 morceaux de Belle chair de crabe pour Gubber Blump, à Auberdine."}},
[1139] = {"La tablette de volonté", {"Nous avons ralenti le clan Ombreforge en le coupant du pic Blackrock, mais la quête d'Hammertoe n'est toujours pas accomplie. Sa mort ne doit pas rester vaine. Il voulait trouver la Tablette de Volonté : un morceau de pierre que les Titans avaient gravé de caractères magiques pour les aider à donner la vie à leurs créations, leur communiquer une volonté propre. C'est ce que dit la légende en tout cas.","Il nous faut retrouver cette tablette avant ceux du clan Ombreforge !","Repartez pour Uldaman, trouvez la Tablette et revenez ici immédiatement."}, {"Trouver la Tablette de Volonté et la rapporter au Conseiller Belgrum à Ironforge."}},
[1140] = {"La tour d'Althalaxx", {"L'écriture des orcs est à peine plus lisible que le gribouillage d'un enfant. J'ai essuyé bien des moqueries lorsque j'ai décidé d'apprendre leur langue, mais il est toujours utile de connaître l'ennemi.","Si j'en crois les griffonnages d'Ilkrud, il semble qu'Athrikus possède toujours deux gemmes d'âme captives, au défilé de la Nuit et à Satyrnaar, des régions tenues par les satyres au nord-est d'Ashenvale.","Faites attention, <nom>, les temples baignés de sang des satyres peuvent être effrayants, même pour des <classe>s aguerris !"}, {"Libérer les Âmes de Bien-nés captives au défilé de la Nuit et à Satyrnaar, puis retourner voir Delgren le Purificateur au poste de Maestra."}},
[1141] = {"La famille à la canne à pêche", {"J’adore pêcher. La famille Blump est connue comme une grande famille de pêcheurs. Je m’appelle Gubber Blump ; je pêche.","Il y a un poisson que j’aime attraper dans le coin, le mérou de Sombrivage. J’avais l’habitude d’aller en bateau pour pêcher Ie mérou, mais les bateaux ne sortent plus depuis que ces maudits murlocs sont arrivés. Je suppose qu’ils ont aussi dévoré tous les mérous.","Voulez-vous m’aider à attraper des mérous de Sombrivage ? Si vous m’aidez, je vous donnerai une authentique canne à pêche de la famille Blump ! C’est une excellente canne pour attraper le poisson."}, {"Attraper 6 Mérous de Sombrivage pour Gubber Blump, à Auberdine."}},
[1142] = {"Le déclin et la mort", {"Il ne me reste plus beaucoup de temps... Tellement... Tellement faible...","Le brave Lonebrow a été envoyé pour avertir Falfindel...","Mais avant que je ne m-m-meure...","... Ma femme, Treshala... Son pendentif de mariage avec moi... V-v-volé... par un de ces sauv-v-vages agresseurs...","Trouvez-le, j-j-je vous en prie... Et rapportez-le à Treshala, à Darnassus...","... Et racontez-lui ma mort... solitaire."}, {"Trouver et rapporter le Pendentif de Treshala à Treshala Fallowbrook à Darnassus."}},
[1143] = {"La tour d'Althalaxx", {"Je vois. Si ce que vous affirmez est vrai, alors c'est le moment de frapper. Je sais qu'Athrikus réside en haut de la tour. Faites attention, car il est entouré de nombreux démonistes, bien plus dangereux que ceux que vous avez déjà combattus."}, {"Tuer Athrikus Narassin et apporter sa Tête à Balthule Shadowstrike près de la Tour d'Althalaxx."}},
[1144] = {"Willix l’Importateur", {"Enfin de l'aide !","Vous n'avez pas idée du temps que j'ai passé prisonnier ici ! Je n'arrive plus à me rappeler quand j'ai vu le monde extérieur pour la dernière fois.","Vous voyez, je suis un entrepreneur. Prêt à tout en échange d'un sou, si vous voyez ce que je veux dire. J'avais entendu dire que Mizzyrix payait bien pour les Racines de Feuillerouge locales, de ce trou à rats ! Je pensais que ce serait de l'argent facile. Je me trompais et pas qu'un peu !","Enfin... Merci de m'avoir sorti de là, je vous le revaudrai..."}, {"Escorter Willix l'Importateur hors du Kraal de Tranchebauge."}},
[1145] = {"L'essaim s'agrandit", {"Bien des voyageurs qui sont passés par la Croisée m'ont aidé à ramasser des oeufs dans le Champ des géants, et nombreux sont ceux qui ont rapporté des récits effrayants sur les créatures. J'en ai assez entendu pour considérer ces insectes comme plus dangereux que ne l'avaient cru mes maîtres de prime abord.","J'ai besoin d'un courrier pour aller à Orgrimmar trouver Belgrom Rockmaul. Cela ne sera pas simple et celui qui ira devra prendre cette tâche au sérieux.","On trouve généralement Belgrom devant la maison des guerriers."}, {"Remettre la Note scellée de Korran à Belgrom Rockmaul à Orgrimmar."}},
[1146] = {"L'essaim s'agrandit", {"Ainsi, Korran est arrivé aux mêmes conclusions que nous sur ces insectes... On dirait qu'il s'agit d'une menace plus grande que nous ne le pensions au départ.","Il y a quelques jours, un chasseur est rentré à moitié mort des Mille pointes. Il accompagnait un groupe chargé de déloger une équipe de fouille naine hors des Salines. Une nuée de ces insectes a jailli d'une grotte pendant l'attaque et les a presque anéantis.","Ils étaient sous les ordres de Moktar Krin et ils se sont réfugiés à la lisière des Salines. Apportez-lui cette note."}, {"Apporter la Note scellée de Belgrom à Moktar Krin aux Mille pointes."}},
[1147] = {"L'essaim s'agrandit", {"Hum... On dirait que Belgrom pense que ces monstres sont une menace. Il veut que nous les repoussions jusque dans les profondeurs abjectes d'où ils sont sortis.","On dirait que tuer les nains de la Ligue des Explorateurs ne leur a pas suffi... Et maintenant il va falloir que j'envoie une section à moitié anéantie mener une nouvelle bataille. Nous ne pourrons pas réaliser cette mission par nous-mêmes. Pas tant que nous serons dans cet état. Le site de fouilles est là-bas vers le sud. Envie de manier l'épée et de nous donner un coup de main ?"}, {"Tuer 5 Chercheurs silithides, 5 Bourdons silithides et 5 Envahisseurs silithides, puis retourner voir Moktar Krin aux Mille pointes."}},
[1148] = {"Des parties de l'essaim", {"Vous inspectez la carapace avec soin. Comme vous la tournez, vous comprenez que le placage est entrecroisé de telle manière qu'elle lui donne une dureté incroyable. Cela ne ressemble à rien que vous connaissiez. Vous inspectez le reste du corps et vous réalisez que ces créatures sont très puissantes et très évoluées.","En regardant de nouveau la carapace, vous vous dites que Korran apprécierait sûrement d'étudier ces créatures d'un peu plus près. Cela l'aiderait à découvrir ce qu'elles sont et d'où elles viennent."}, {"Apporter 1 Coeur de silithide, 5 Serres de silithide et 3 Carapaces de silithide intactes à Korran, à la Croisée."}},
[1149] = {"L'épreuve de la Foi", {"Vous me cherchiez, ou peut-être est-ce le destin qui vous mène à moi ? Aucune importance. Vous voilà et un nouveau voyage commence pour vous.","Il y a différents aspects dans un être : l'esprit, le corps, l'âme. Certains peuvent être améliorés par les traditions d'une culture. Pour d'autres... Nous pouvons simplement espérer qu'elles existent en nous. Si vous êtes d'accord, je vais mettre à l'épreuve quelques-unes de vos qualités.","Si vous réussissez, vous y gagnerez en force. Si vous échouez, vous comprendrez l'immensité du Néant.","La première épreuve est l'Epreuve de la Foi."}, {"Si vous avez la Foi, sautez des planches qui dominent les Mille pointes."}},
[1150] = {"L’épreuve de l’Endurance", {"Il me plaît de constater que votre courage reste entier à la lumière de ce que vous avez déjà réalisé. Puisque votre foi est grande, il est temps de mettre les capacités de votre corps à l'épreuve. Allez avec des amis jusqu'à la tanière des Volplumes, à l'ouest du poste de Librevent. Plongez au cœur de leur tanière et détruisez leurs réserves de nourriture. Cela devrait provoquer leur colère, et si vous êtes capable de survivre assez longtemps à leur furie, Grenka Hurlesang elle-même s'attaquera à vous... Tuez-la et apportez-moi sa griffe en accomplissement de votre épreuve."}, {"Apporter la Griffe de Grenka au traqueur des plaines Dorn, aux Mille pointes."}},
[1151] = {"L'épreuve de la Force", {"Au temps des Dieux très anciens, les élémentaires peuplaient ce monde. Ils adoraient les Dieux très anciens, et le pouvoir de ces derniers maintenait les élémentaires attachés à ce monde. Lorsque les Dieux très anciens furent bannis, les élémentaires disparurent de notre monde et ils n’y reviennent que s'ils sont appelés par de puissants mages.","Il y a pourtant des exceptions. Certains élémentaires étaient suffisamment puissants pour rester. L'un d'eux est Rok'Alim.","Pour mettre votre force à l'épreuve, trouvez Rok'Alim dans les Mille pointes et tuez-le."}, {"Apporter des Fragments de Rok'Alim au traqueur des plaines Dorn dans les Mille pointes."}},
[1152] = {"L'épreuve de la Connaissance", {"Vous avez presque terminé votre apprentissage avec moi, mais l'Epreuve de la Connaissance n'est pas de mon ressort. Il y a des leçons que l'on apprend et des lieux que l'on visite pour lesquels il faut un autre guide que moi-même.","Si vous vous sentez apte, cherchez Braug Dimspirit... C'est un chaman d'une grande sagesse. Il vous mettra à l'épreuve, alors prêtez attention à ses paroles, <nom>.","Lorsque vous vous estimerez apte, trouvez le tunnel qui relie les Serres-Rocheuses et Ashenvale : Braug habite près de là. Mais soyez <prudent/prudente>, <nom>. Les elfes de la nuit pourraient vouloir vous empêcher d'accomplir votre épreuve."}, {"Trouver Braug Dimspirit près de l’entrée de la Perce des Serres dans les Serres-Rocheuses."}},
[1153] = {"Un nouveau minerai", {"Vous m'avez apporté une aide précieuse par le passé, <classe>. Le Chef de guerre apprécie, au moins jusque-là, ce que j'ai accompli, et me demande de continuer avec une nouvelle détermination. Mon honneur me commande de vous envoyer au Chef de guerre avec une recommandation pour tout ce que vous avez fait, mais avant je voudrais vous demander votre aide une nouvelle fois.","Des voyageurs, récemment revenus d'un endroit appelé les Mille pointes, ont tué un kobold porteur d'un échantillon d'un minerai inhabituel. Ils n'ont pas voulu le vendre. Je voudrais que vous me rapportiez un échantillon de ce minerai."}, {"Trouver un échantillon de minerai brut sur un Mineur ou un Géomètre Mufle-de-gravier et l’apporter à Tatternack Steelforge, à Camp Taurajo dans les Tarides."}},
[1154] = {"L'épreuve de la Connaissance", {"Vous avez réussi les épreuves de Dorn et vous cherchez à présent le défi suivant. Ce que vous avez réussi, un enfant aurait pu le réussir. Il est facile de mettre son corps à l'épreuve. Pour l'esprit, c'est tout autre chose.","Je vais m'expliquer simplement : trouvez le livre dont je vais vous parler, étudiez-le et répondez à mes questions sur son contenu.","Quel livre ? « L'Héritage des aspects ». On peut le trouver au pied de Dor'danil au sud d'Ashenvale, protégé par les druides morts et leurs assassins."}, {"Trouver l’Héritage des Aspects et l’apporter à Braug Dimspirit, à côté de l’entrée de la Perce des Serres, dans les Serres-Rocheuses."}},
[1159] = {"L'épreuve de la Connaissance", {"Bien joué, <nom>. Vous avez passé la première partie de l'Epreuve de la Connaissance, mais il reste une question à laquelle vous devez répondre. Cette question, je ne puis la poser.","Cherchez Parqual Fintallas dans la cité de Lordaeron. C’était un grand historien parmi les humains, avant d'être emporté par la Peste. Il continue sa quête du savoir, mais un savoir d'une nature différente.","Il exigera de vous une certaine tâche avant d’accepter de vous tester, mais vous avez superbement mené à bien les épreuves jusqu'ici... Je suis sûr que vous surmonterez cet obstacle."}, {"Trouver Parqual Fintallas à Undercity."}},
[1160] = {"L'épreuve de la Connaissance", {"Je vous dirai ce que vous devrez chercher, et je vous poserai même ma question, mais certainement pas parce que vous cherchez à vous prouver quelque chose à vous-même. La Connaissance est bien plus importante que la force. Même si d'autres vous surpassent en habileté, votre connaissance du monde vous sera précieuse.","Rendez-vous au Monastère au nord des Clairières de Tirisfal. Dans la bibliothèque, vous trouverez un livre intitulé « Les commencements de la menace des morts-vivants ». Il était utilisé par les paladins, désormais corrompus, pour leur entraînement contre ceux de ma race.","Apportez-le-moi."}, {"Trouver « Les commencements de la menace des morts-vivants » et le rapporter à Parqual Fintallas à Undercity."}},
[1164] = {"Le voleur volé", {"La traînée ! La truie ! Kenata vit toujours ; sa famille est riche et prospère tandis que je souffre, comme une Réprouvée que je suis !","Ma famille a disparu, emportée par la peste. Notre domaine a disparu, lui aussi, détruit et brûlé pendant la guerre. Et malgré cela, aidée de ses misérables enfants, elle a l'audace de voler le seul bien qui me reste.","Je n'attache plus d'importance aux choses qui m'ont été volées. Ce que je veux à présent c'est leurs têtes !","Tuez-les pour moi ! Rendez-vous à la ferme des Dabyrie en Arathi, au nord-est du Refuge de l'Ornière."}, {"Apporter la Tête de Kenata, Fardel et Marcel Dabyrie à Genavie Callow à Undercity."}},
[1166] = {"Les soucis du Seigneur Mok'Morokk", {"Inclinez-vous devant le seigneur Mok'Morokk !","Rexxar faire de moi le puissant chef du clan Cognepierre !","Moi bon chef. Moi garder clan en sûreté.","Grands dragons venir et faire le feu avec anciennes maisons. Mais moi ogre malin. Trouver nouveau foyer pour clan ici.","Quitté ancienne maison trop vite. Oublié truc. Minuscule <race>, aller trouver mes trucs. Aller sud et trouver mon grog délicieux, mon tabac à priser et mon coffre. Moi apprécier vous après ça. Chercher dans Antre des flammes et dans ruines Cognepierres... Mes trucs là-bas, moi sûr !"}, {"Le seigneur Mok'Morokk de Mur-de-Fougères veut que vous retrouviez son grog, son tabac à priser et son coffre dans le marécage d'Âprefange."}},
[1167] = {"La tour d'Althalaxx", {"Il serait sage de vous entretenir avec Balthule avant d'affronter Athrikus. Il a certainement observé la Tour avec une grande vigilance et il pourrait avoir des renseignements intéressants."}, {"S'entretenir avec Balthule Shadowstrike près de la Tour d'Althalaxx."}},
[1168] = {"L'armée des dragons noirs", {"Mok'Morokk fait seigneur par Rexxar. Lui faire attention plus pouvoir que ogres.","Dragons noirs tuer beaucoup ogres. Tuer femme de Tharg. Mok'Morokk dire s'enfuir. Venir ici.","Ogres devoir combattre. Ogres pas bons pour fuite. Mok'Morokk seulement intéresse Mok'Morokk.","Moi appeler maison \"ruines Cognepierres\". Moi mieux au sud. Ici pas maison.","Aider ogres à combattre armée des dragons noirs. Moi vouloir vengeance. Moi vouloir rentrer maison."}, {"Tharg de Mur-de-Fougères veut que vous tuiez 15 Eclaireurs Crins-de-feu, 10 Queues-cendrées Crins-de-feu et 5 Gardes Crins-de-feu."}},
[1169] = {"Identification de la portée", {"Voyez ce vieux fou de Mok'Morokk, qui se complaît dans son pouvoir et Tharg, qui combat les démons du désespoir et de la vengeance tout en espérant prendre la direction du clan. On dirait que je suis le seul qui cherche à connaître la source de l'agression qui a détruit notre ancien foyer.","Pourquoi ce regard ahuri, <nom> ? Vous vous attendiez à m'entendre parler comme un forban mal dégrossi parce que je suis un ogre ?","Bon ! Au travail, maintenant ! Apportez-moi des cœurs et des langues de dragonnets incendiaires et de jeunes incendiaires. Je pourrai alors chercher la source du mal."}, {"Draz'Zilb de Mur-de-Fougères souhaite que vous lui apportiez 15 Langues d'incendiaire et 15 Cœurs d'incendiaire."}},
[1170] = {"La portée d'Onyxia", {"Le village des Cognepierres a été envahi par la portée d'Onyxia. Mais pourquoi la fille d'Aile de mort, le seigneur du vol noir, est-elle descendue sur nos champs ?","C'est très troublant. Onyxia a sûrement un autre but que de faire le siège d'un petit village ogre.","Rendez immédiatement compte à Mok'Morokk ! Il faut agir !"}, {"Parler au seigneur Mok'Morokk au village de Mur-de-Fougères."}},
[1171] = {"La portée d'Onyxia", {"Vous me laisser maintenant. Aller dire à Draz'Zilb nous rester ici. Pas de dragon noir ici."}, {"Parler à Draz'Zilb à Mur-de-Fougères."}},
[1172] = {"La portée d'Onyxia", {"L'engeance d'Onyxia s'est répandue dans tout le Cloaque aux dragons. Il est impératif d'empêcher les dragonnets d'éclore.","Hâtez-vous de rejoindre la tourbière du Ver, au sud du marécage d'Âprefange. Il est sûr qu'elle a laissé ses œufs là-bas. Trouvez-les tous et détruisez-les.","Nous ne pourrons jamais rebâtir le village des Cognepierres si les environs servent de nid aux dragons !","Quant à Mok'Morokk, j'ai d'autres plans pour lui…"}, {"Draz’Zilb, de Mur-de-Fougères, veut que vous détruisiez 5 des Œufs d'Onyxia."}},
[1173] = {"Défier le Seigneur Mok'Morokk", {"Toi oser défier le Seigneur Mok'Morokk ?","Morbleu !","Moi écraser <race> comme un insecte !","Toi me dire quand vouloir bonne raclée."}, {"Défaire Mok'Morokk en combat singulier et rapporter la nouvelle à Draz’Zilb au village de Mur-de-Fougères."}},
[1175] = {"Les cahots de la piste", {"Après le gros accrochage entre le Tonnerre rouge gobelin et un basilic, Kronkrider m'a embauché pour surveiller la piste. Je ne sais pas ce que mangent les animaux d’ici pour grossir comme ça, mais il faut réduire leur nombre si nous voulons éviter qu'ils ne se baladent sur la piste pendant une course.","Je vais vous dire : si vous voulez gagner un peu d'argent sortez d'ici et faites dégager les basilics de la zone."}, {"Tuer 10 Basilics du sel gemme, 10 Peaux de cristal du sel gemme et 10 Contemplateurs du sel gemme pour le maître pisteur Zherin des Salines."}},
[1176] = {"Allégement de la structure", {"Quand on veut vraiment courir, chaque détail compte. Il faut tout prendre en compte depuis la plus petite chute de puissance jusqu'au plus léger défaut dans la tension des fils des bobines, en passant par un trop grand allégement du châssis…","Nous utilisons des os de vautour pour fabriquer une grande partie des structures mineures de nos voitures, qui sont des modèles d'équilibre entre légèreté et solidité. Cela pourrait être encore amélioré si nous avions plus de ces os ! Ils sont faciles à obtenir : les plateaux salés sont littéralement infestés de vautours.","Rapportez-moi des os de vautour et vous recevrez une récompense en échange."}, {"Se procurer 10 Os creux de vautour pour Pozzik dans les Salines."}},
[1177] = {"Faim !", {"Pil'bou grand ogre.","Moi faim.","Aller taper poisson. Apporter manger pour Pil'bou.","Vous nourrir Pil'bou sinon Pil'bou manger vous."}, {"Pilebou Piessale, dans le nord d'Âprefange, veut 12 Têtes d'Aileron boueux."}},
[1178] = {"Mécénat gobelin", {"Notre équipe de course est la fierté de tous les gobelins ! Vous seriez surpris si je vous disais les noms de tous ceux qui y participent, d'une manière ou d'une autre !","Vous connaissez Gazlowe ? Il voyage dans le monde entier pour nous rapporter nos pièces détachées. Il a même participé une fois à la construction d'un des engins !","Il y a quelques semaines, il a dit qu'il nous enverrait un nouveau prototype de régulateur de carburant, mais il n'est pas encore arrivé. D'habitude, il est plus rapide que ça... Peut-être accepteriez-vous d'aller à Ratchet pour le lui demander ? Je sais que j'exagère un peu en vous le demandant, mais les gnomes sont en train de nous prendre de vitesse !"}, {"Parler à Gazlowe à Ratchet."}},
[1179] = {"Les frères Brassbolts", {"Vous avez déjà entendu parler des Salines ? C'est une étendue plate et salée au-delà de Kalimdor, juste au sud du canyon des Mille pointes. C'est un endroit fabuleux pour les courses ; forcément, c'est complètement plat !","Une paire de gnomes, des frères, des amis à moi, sont là-bas pour essayer leur nouvelle voiture à réaction. D'après ce qu'on m'a dit, elle dépote ! Elle dépote tellement que les pilotes se crashent tous l'un après l'autre. Et finissent avec le crâne en miettes.","Alors, évidemment, ils m'ont demandé de fabriquer des casques, mais j'ai des contraintes ici ! Je ne peux pas faire le voyage !","Vous pouvez ?"}, {"Apporter la caisse de casques anti-accidents à Wizzle Brassbolts, dans les Salines."}},
[1180] = {"Mécénat gobelin", {"Bon, revenons à la raison de votre voyage. Je sais que j'ai promis à Pozzik un nouveau régulateur de carburant, mais je ne l'ai pas encore reçu. Revilgaz, à Baie-du-Butin, dit qu'il l'a envoyé par bateau, mais il s'est peut-être perdu en route.","Si c'est le cas, nous ne pouvons pas faire grand-chose. Mais nous pouvons tout de même vérifier qu'il est bien parti de Baie-du-Butin.","Vérifiez avec Maître Lozgil, sur les quais, là-bas, il devrait être en mesure de vérifier les registres de chargement."}, {"S'adresser à Maître Lozgil à Baie-du-Butin."}},
[1181] = {"Mécénat gobelin", {"Désolé, je ne vais pas être d'un grand secours, <nom> ! Si vous voulez d’autres informations, je vous suggère d'aller trouver le baron lui-même. S'il y a quelqu'un qui est en mesure de vous renseigner sur les affaires de Revilgaz, c'est Revilgaz lui-même.","Vous le trouverez à l’étage de la Taverne, de l'autre côté du port."}, {"Parler au baron Revilgaz à Baie-du-Butin."}},
[1182] = {"Mécénat gobelin", {"Vous voyez, les déchiqueteurs de la KapitalRisk du lac Nazfériti (au nord de Strangleronce) sont équipés d'un nouveau régulateur de carburant. Je pensais que Pozzik serait en mesure de l'adapter s'il avait quelques détails précis à son sujet. Malheureusement, j'ai sous-estimé l'intelligence de Cozzle, le Surveillant du chantier.","Peut-être aurez-vous plus de chance dans la recherche des plans. Je sais qu'il les garde chez lui, mais ils sont certainement sous clé."}, {"Apporter les Plans du régulateur de fuel au baron Revilgaz à Baie-du-Butin."}},
[1183] = {"Mécénat gobelin", {"Vous devriez être sur le départ. Transmettez mes salutations à Pozzik et dites-lui que s'il a besoin de quoi que ce soit, qu’il n'hésite pas à demander. J'attends les nouvelles de sa victoire avec impatience !"}, {"Apporter les Plans du Régulateur de carburant à Pozzik dans les Salines."}},
[1184] = {"Des parties de l'Essaim", {"Retournez voir Belgrom à Orgrimmar pour lui dire que Moktar fait tout ce qu'il peut aux Mille pointes. Dites-lui aussi que mes études progressent et que je pourrai lui donner des détails assez vite. Cela devrait le satisfaire pleinement."}, {"Retour à Orgrimmar pour voir Belgrom Rockmaul."}},
[1185] = {"Sous la chitine...", nil, nil},
[1186] = {"Le dix-huitième pilote", {"Je vais être très occupé avec l'intégration de ce nouveau Régulateur de carburant sur la structure de nos voitures de course, mais si vous voulez apporter une aide supplémentaire à l'équipe, adressez-vous à Razzeric. C'est notre pilote. En fait, c'est notre dix-huitième pilote... Et il aime bien apporter ses petites modifications personnelles à sa voiture.","Je vous préviens, il est un peu à côté de ses pompes alors tâchez de ne pas manifester de surprise quand vous lui parlerez !"}, {"S'adresser à Razzeric, dans les Salines."}},
[1187] = {"Les trucs de Razzeric", {"Pozzik est un grand mécanicien, je le sais, mais il tergiverse trop lorsqu'il s'agit de puissance et de se lancer dans l'action. Ce n'est pas mon cas.","Lorsque Pozzik en a fini avec une machine, je prends quelques instants pour apporter mes petites modifications. Vous savez ce que c'est, je rends la direction un peu plus souple, j'enlève des morceaux de carrosserie inutiles, et j'y mets un peu plus de patate...","J'ai commandé un lance-hydroglycérine à Shreev au Bidulium de Gadgetzan, mais apparemment le zeppelin qui le transportait s'est écrasé dans le marécage d'Âprefange ! Il faut que je le récupère, <nom> !"}, {"Récupérer le Lance-hydroglycérine pour Razzeric dans les Salines."}},
[1188] = {"La sécurité d'abord", {"Mais qu'est-ce que c'est que ça ? Shreev a laissé la sûreté enclenchée. Ça limite l'énergie que je peux en tirer à chaque fois. Je lui avais dit de l'enlever ! Ça ne va pas !","Vous devez rapporter ça au Bidulium de Gadgetzan dans le désert de Tanaris, et faire enlever ce truc par Shreev."}, {"Apporter le Lance-hydroglycérine à Shreev à Gadgetzan."}},
[1189] = {"La sécurité d'abord", {"Voilà ! J'ai fait selon les recommandations de Razzeric. Dites-lui simplement d'être prudent quand il installera ce truc. À la moindre erreur, il n'avancera pas : il explosera sur place.","Ce serait dommage, non ? Qui sait ce que vaudra notre dix-neuvième pilote !"}, {"Rapporter le Lance-hydroglycérine modifié à Razzeric, dans les Salines."}},
[1190] = {"Rester dans la course", {"Les frères Brassbolts ont utilisé un nouveau matériau de construction, c'est la seule explication plausible. Leurs voitures avancent plus rapidement qu'avant, mais leur aspect et toutes les données que nous avons recueillies sur leurs courses restent identiques.","Ce que nous savons, c'est que Rizzle Brassbolts est branché à temps plein sur les plans de sa nouvelle découverte.","Zamek peut créer une diversion pour vous, ainsi vous pourrez dérober ces plans."}, {"Utiliser Zamek pour faire diversion, dérober les plans de Rizzle et les apporter à Pozzik dans les Salines."}},
[1191] = {"La diversion de Zamek", nil, nil},
[1192] = {"Minerai d'indurium", nil, nil},
[1193] = {"Un piège cassé", nil, nil},
[1194] = {"Les schémas de Rizzle", {"Les schémas de Rizzle montrent le processus de raffinage de l'Indurium. D'après ses gribouillages, il apparaît que le minerai provient de la mine des nains d'Uldaman dans les Terres ingrates.","La plupart des autres notes évoquent ses propriétés de résistance à la chaleur.","Ce qui sert de presse-papiers pour les plans est vraisemblablement un gros morceau d'Indurium non raffiné."}, {"Apporter un Echantillon de Minerai d'Indurium à Pozzik dans les Salines."}},
[1195] = {"La flamme sacrée", {"Les centaures du clan Galak, dans les Mille pointes, protègent un artefact du temps de la création de leur espèce.","Nous aimerions le voir de plus près, mais il nous faut d’abord une fiole d'eau provenant d’un des puits de lune des elfes de la nuit.","Pour puiser l'eau, vous aurez besoin d'une des fioles que transportent les dryades près de la retraite de Raynewood, au cœur de la forêt d'Ashenvale. Il existe un puits de lune près de la rive ouest de la rivière Falfarren, au sud de la route principale. Vous pourrez y remplir la fiole."}, {"Rapporter une Fiole d'Eau forte remplie à Zangen Stonehoof à Thunder Bluff."}},
[1196] = {"La flamme sacrée", {"Apportez la Fiole remplie à Rau Cliffrunner au Poste de Librevent dans le sud-est des Mille pointes. Vous pouvez atteindre les Mille pointes en empruntant la Grande élévation à la limite sud des Tarides.","Je ne serais pas surpris que vous puissiez nous aider davantage."}, {"Remettre la Fiole d'Eau forte remplie à Rau Cliffrunner au Poste de Librevent."}},
[1197] = {"La flamme sacrée", {"En l'aspergeant avec l’eau que vous avez apportée, vous pourrez temporairement éteindre la flamme qui protège la relique des centaures. L'effet sera provisoire, vous devrez faire vite.","Vous trouverez la combe du Sabot fendu, la ville fortifiée des centaures, plus au nord. La flamme est gardée dans le bastion du Sabot fendu, une zone très solidement gardée.","Faites attention, les centaures du clan Galak sont très nombreux à cet endroit et ils combattront férocement pour protéger une chose aussi importante à leurs yeux."}, {"Retrouver le Sabot fendu pour Rau Cliffrunner du Poste de Librevent."}},
[1198] = {"À la recherche de Thaelrid", {"De nombreuses rumeurs circulent ces jours-ci sur l'union entre le Marteau du crépuscule et les nagas dans les Profondeurs de Brassenoire sur les côtes d'Ashenvale.","Mon rôle ici avec l'Aube d'argent est de collecter autant d'informations que possible, afin que nous sachions où et quand on a besoin d'aide.","Cela fait des semaines à présent que j'ai envoyé un éclaireur espionner les activités menées dans les Profondeurs de Brassenoire. Le Garde d’argent Thaelrid n'est toujours pas revenu.","Mes autres éclaireurs sont très pris par d'autres tâches pressantes.","Recherchez et venez en aide à Thaelrid pour moi, <nom>."}, {"Aller chercher le Garde d'argent Thaelrid dans les Profondeurs de Brassenoire."}},
[1199] = {"Le crépuscule descend", {"Les membres du Marteau du crépuscule, se servant des vieux rites des chaotiques Dieux très anciens, se battent pour les ramener en ce monde et pour y répandre la mort et la terreur.","Nous pensons que le Marteau du crépuscule s'est allié aux nagas et se regroupe en masse dans les Profondeurs de Brassenoire sur la côte d'Ashenvale.","Quels que soient les plans maléfiques qu'ils préparent dans les profondeurs du temple ancien, ils doivent être contrecarrés.","Lancez un assaut sur le Marteau et rapportez la preuve de vos actes. Vos efforts se verront récompensés de grandiose manière."}, {"Apporter 10 Pendentifs du Crépuscule au Garde d'argent Manados à Darnassus."}},
[1200] = {"L'infamie de Brassenoire", {"Mes forces me quittent. J'ai besoin de votre aide !","Il y a longtemps, ce site était un grand temple d'Elune. Mais la malchance l'a conduit à sa ruine, quand un Dieu très ancien, en contaminant la terre, a corrompu le Puits de lune sacré.","Aku'Mai, serviteur du Dieu très ancien, est sorti des eaux.","Les sectateurs du Marteau du crépuscule se sont alliés aux nagas pour occuper ces terres. Les sectateurs, conduits par Kelris, sacrifient des innocents à Aku'Mai pour augmenter son pouvoir.","Tuez Kelris et apportez sa tête à Selgorm, à Darnassus, je vous en prie, <nom>."}, {"Apporter la Tête du Seigneur du crépuscule Kelris au Veilleur de l'aube Selgorm, à Darnassus."}},
[1201] = {"Les espions de Theramore", {"La trêve entre la Horde et l'Alliance n'est pas toujours respectée. Des escarmouches et des actions secrètes sont menées par chacun des deux camps, et il est de notre devoir de nous assurer que la Horde garde l’avantage !","Nous avons maintenant l'occasion de démasquer les infiltrateurs de l'Alliance.","Ils sont en position à l'est du village de Mur-de-Fougères. Ils nous espionnent et rapportent nos mouvements à Theramore, ce que nous ne pouvons permettre.","Traquez-les et tuez-les. Envoyons un message clair à leurs chefs : nous ne supporterons pas leur présence sur notre sol !"}, {"Tuer 9 Infiltrateurs de Theramore, puis rendre compte à Nazeer Bloodpike à Mur-de-Fougères."}},
[1202] = {"Les docks de Theramore", {"<nom>, deux de nos espions ont été envoyés pour dérober des documents sur un bateau dans les docks de Theramore. Ils ont trouvé les papiers, mais ont été découverts et ils ont dû jeter les documents à la mer.","L'un des deux a réussi à s'échapper par la suite, mais il n’a pas pu récupérer le paquet. Il a rendu compte de son échec et maintenant je vous confie cette mission.","Les documents sont dans un coffret, quelque part dans les eaux des docks de Theramore. Trouvez ces documents, <nom>, et apportez-les-moi."}, {"Apporter les Documents du capitaine à Nazeer Bloodpike au village de Mur-de-Fougères."}},
[1203] = {"Jarl a besoin d'une épée", {"Je dois découper. Je ne peux pas vous dire quel type de découpage je vais faire, mais j'ai besoin d'une lame… Mais pas n'importe quelle lame !","Il y en a qui l'appellent Epée d'acier lunaire. Moi je l'appelle Epée à double tranchant.","Allez-vous m'aider ? Peut-être que vous la trouverez en ville, peut-être que vous pourrez la faire faire chez un forgeron.","Mais ne traînez pas. Ce que je dois découper n'attendra pas des années."}, {"Apporter une Epée en acier lunaire à Jarl, dans le marécage d'Âprefange."}},
[1204] = {"Soupe de Rochefange aux insectes", {"Je prépare un plat que je nomme \"Soupe et insectes rochefange\". Il me manque encore les ingrédients principaux… Eh bien, je pense que je ne ferais pas long feu à patauger tout seul dans le marécage d'Âprefange !","Aussi, <nom>, cela vous dérangerait-il de faire une petite partie de chasse pour moi ? D'abord, il me faut des langues de tortues Rochefange. Vous pouvez en trouver le long de la côte, près de la baie.","Mais il ne me faut pas n'importe quelles langues ! J'entends par là que certaines tortues ont la langue fourchue...","Il paraît que ces langues fourchues ont un goût particulier, et je veux cet ingrédient-là dans ma recette..."}, {"Apporter 8 Langues fourchues de Rochefange à Morgan Stern, à Theramore."}},
[1205] = {"Mortebourbe", {"Mortebourbe est un ancien crocilisque du marécage d'Âprefange. Son heure est arrivée.","Ses vieux os grincent quand il extrait son énorme corps du marais, et pourtant il évolue avec la force et la vitesse de ses jeunes années. Son corps fatigué torture le grand esprit qui l'habite, un esprit dont la flamme ne faiblit pas.","Pourtant, il vit maintenant marqué par une douleur cruelle et permanente. Vous devez mettre un terme à la vie de cette noble créature, <nom>. Vous devez conduire Mortebourbe à la paix."}, {"Apporter la Dent de Mortebourbe à Melor à Thunder Bluff."}},
[1206] = {"Jarl a besoin d’yeux", {"Le jus d’œil est bon à boire. Malheureusement la plupart des monstres n'ont que deux yeux.","Mais les araignées, elles, ont beaucoup d'yeux. Plus que beaucoup. Et le jus d'oeil de Sombrebrume est fameux. Doux et salé ! Des yeux d'araignée entiers... J'ai l'impression de les savourer.","Les araignées Sombrebrume se trouvent à l'ouest d'ici, dans la caverne de Sombrebrume. A votre avis, c'est cette grotte qui a donné son nom aux araignées, ou l'inverse ?"}, {"Apporter 40 Yeux de Sombrebrume à Jarl \"Œil-des-marais\", au Manoir des Flammeroles."}},
[1218] = {"Epices apaisantes", {"J'aime la cuisine. La bonne cuisine du marais. Les cuisses de grenouille verte grillées par exemple.","Et pour cuisiner j'ai besoin d'épices. Des épices apaisantes. Après je ferai un ragoût qui mijotera et qui mijotera et je le mangerai. Mais je vous en donnerai un peu aussi.","Vous aimez le ragoût ?"}, {"Apporter 3 Epices apaisantes à Jarl \"Œil-des-marais\", dans le marécage d'Âprefange."}},
[1219] = {"Le rapport orc", {"En remuant la poussière, on trouve la main coupée d'un orc. Une main qui agrippe encore un tube en os gravé. Le tube contient un parchemin taché.","Le parchemin est écrit en orc... Et ça semble important."}, {"Communiquer le Rapport de l'Espion à un Lieutenant de Theramore."}},
[1220] = {"Le capitaine Vimaire", {"Le capitaine Vimaire désire voir le rapport orc que vous avez trouvé, <nom>. C’est lui qui connaît le mieux la langue des orcs. Et il doit savoir qu'il y a des espions de la Horde près de Theramore !","Le capitaine est dans la Caserne, au nord-est de Theramore."}, {"Apporter le Rapport orc au capitaine Garran Vimaire."}},
[1221] = {"Racines de Feuillebleue", {"Les racines de feuillebleue sont une friandise universellement appréciée ! Mais elles sont rares, très rares. On ne peut les trouver qu'ici, dans les Tarides, profondément enterrées au kraal de Tranchebauge.","Et même ici, vous ne pourriez pas les trouver à moins de savoir comment les chercher ! C'est pourquoi j'ai entraîné ces Ecureuils Sniffetarin à les trouver. Ils ont un grand nez et peuvent sentir une racine à cinquante pas.","Cela ne sera pas facile, mais si vous me ramenez quelques racines, je vous paierai grassement."}, {"Prendre une Caisse percée.","Prendre un Bâton de commandement de Sniffetarin.","Lire le Manuel d'utilisateur de Sniffetarin.","Au Kraal de Tranchebauge, utiliser la Caisse pour invoquer un Ecureuil Sniffetarin et utiliser le Bâton de commandement pour ordonner à l'Ecureuil de chercher les racines.","Ramener 6 Racines de Feuillebleue, le Bâton de commandement de Sniffetarin et la Caisse percée à Mebok Mizzyrix, à Ratchet."}},
[1222] = {"Sauvetage du Puant", {"Génial, enfin quelque chose qui ne veut pas me dévorer !","J'ai besoin d'aide pour occuper ces monstres autour de moi et vous m'avez l'air de pouvoir le faire.","Le chef cuisinier Morgan Stern travaille sur une nouvelle recette et il m'a envoyé ici pour cueillir des feuilles de haricots des marais. Je pense qu'il les trouve savoureux.","Quoi qu'il en soit, pendant que je cherchais un plant de haricot, je me suis retrouvé cerné ! Pouvez-vous m'aider à sortir de là et surveiller les alentours pendant que je cherche des plants de haricots ?"}, {"Escorter Ignatz \"Puant\" puis discuter avec Morgan Stern à Theramore."}},
[1238] = {"Le rapport perdu", {"Au milieu de la poussière, une main d'orc, seule. Et dans cette main, un tube creusé dans de l'os. Dans le tube, un parchemin sale.","Ce parchemin est un rapport établi par un orc qui a passé des semaines à espionner la ville de Theramore.","Après examen du rapport, il est clair qu'il doit être apporté au Maître Espion, au village de Mur-de-Fougères."}, {"Apporter le Rapport de l'espion à Nazeer Bloodpike à Mur-de-Fougères."}},
[1239] = {"La tête coupée", {"Une fouille approfondie permet de découvrir une tête d'orc mâle. Les lèvres sont figées dans un rictus et les yeux noirs semblent vous fixer avec défi."}, {"Apporter la Tête d'orc défiant à Nazeer Bloodpike à Mur-de-Fougères."}},
[1240] = {"Le Sorcier-docteur troll", {"Nous devons connaître les faits concernant la mort de Marg ! Et il n'y a qu'une seule manière de savoir, <nom>. C'est d'utiliser la sorcellerie des trolls.","Apportez la tête de Marg à Kin'weelay dans la vallée de Strangleronce au Campement Grom'gol.","Kin'weelay est le plus puissant des Sorciers-docteurs des trolls Darkspear. Si quelqu'un peut parler à l'esprit de Marg Nighteye, c'est lui."}, {"Apporter la Tête d'orc défiant à Kin'weelay au Campement Grom'gol dans la vallée de Strangleronce."}},
[1241] = {"Un diplomate a disparu", {"Récemment, un diplomate a été envoyé à Theramore pour rencontrer Jaina Proudmoore. Ce diplomate n'est jamais arrivé.","Je pense que les Défias sont impliqués dans ce complot, mais je ne vois pas de quelle manière. La disparition du diplomate n'a pas encore attiré l'attention du public, mais cette situation ne durera pas.","Des agents du roi sont déjà en train de fouiller la ville à la recherche d'indices, mais j'aimerais consulter mes propres contacts.","Dans la Vallée des héros vit un vieil ami à moi qui s'appelle Jorgen. Trouvez-le, donnez-lui cette note et suivez ses instructions."}, {"Trouver Jorgen dans la cité de Stormwind."}},
[1242] = {"Un diplomate a disparu", {"Portez cette note à Elling Trias. Ne lui dites rien d'autre. Contentez-vous d'être <poli/polie> et attendez la réponse. Et ne vous inquiétez pas, il répondra.","Que ce soit bien clair, <nom> : c'est une affaire sérieuse, alors gardez votre clapet fermé.","Vous trouverez Trias dans son magasin de fromages, « Aux Fromages de Trias », dans le Quartier commerçant. Bonne chance !"}, {"Apporter la Note scellée de Jorgen à Elling Trias dans la cité de Stormwind."}},
[1243] = {"Un diplomate a disparu", {"Jorgen, vieux fils de… Alors c'est comme ça que ça va se passer, hein ?","Très bien, je peux m'en accommoder. J'espère qu'il trouvera le Néant distordu au plus tôt !","Voilà comment nous allons procéder, <nom>. Je commence à utiliser mes contacts ici, mais comme c'est vous qui faites le plus gros du travail de terrain, je vous prête un peu d'argent.","Allez à Darkshire, dans le bois de la Pénombre, et trouvez le gardien Backus. Il patrouille généralement sur la route nord juste en dehors de la ville. Dites-lui simplement que vous êtes là pour surveiller, pour moi, l'activité des Défias, et il vous aidera en vous donnant toutes les informations qu'il possède."}, {"Donner la Note scellée au Gardien Backus, au bois de la Pénombre."}},
[1244] = {"Un diplomate a disparu", {"Les activités des Défias ? Il y a toujours de l'activité, même s'ils ne se vantent pas que nous les considérions comme une menace.","Mais pendant que j'y pense, il y a eu récemment quelque chose de bizarre. Cela s'est passé il y a environ deux semaines : certains de leurs agents se sont réunis près de la ferme des Addle. D'après le rapport, j'en ai déduit que quelque chose d'important était en préparation.","Pourquoi ne commenceriez-vous pas par vérifier cela et me rapporter tout ce que vous trouverez ?","La ferme est au sud de la route qui vient de la marche de l'Ouest, juste à l'endroit où vous rentrez dans le bois de la Pénombre."}, {"Trouver le Registre des Défias et le ramener au Gardien Backus au bois de la Pénombre."}},
[1245] = {"Un diplomate a disparu", {"Ouais ! Cela semble bien trop compliqué pour quelque chose qui serait juste destiné à vous induire en erreur. Je ne devrais même pas être en train de regarder tout ça. Portez-le à Trias. Il sera capable de comprendre tout cela. Et ne laissez PERSONNE se mettre en travers de votre chemin. Ceci est trop gros pour moi. C'est peut-être même trop gros pour lui, mais il verra.","Et faites-moi une faveur, <nom>, ne dites à personne que je vous ai fourni de l'aide. Si mes supérieurs apprennent que je savais quelque chose sur cette affaire et que je ne les ai pas prévenus, ils me pendront."}, {"Ramener le Registre des Défias à Elling Trias à Stormwind."}},
[1246] = {"Un diplomate a disparu", {"Ouais, maintenant que j'y pense… Dashel… Dashel… quelque chose… Son nom c'était quoi déjà ? FIST ! Dashel Stonefist. Ils l'appelaient Fist dans certains milieux, probablement parce qu'il aimait se battre.","Vous le trouverez habituellement dans la vieille ville, pile au centre. Allez donc lui parler. Et n'hésitez pas à utiliser une épée ou quelque chose de bien lourd et contondant pour l’engager à discuter. N'y allez pas de main morte, s'il est aussi impliqué que ces papiers le laissent à penser, il n'est pas prêt à parler de son plein gré."}, {"Parler à Dashel Stonefist à Stormwind."}},
[1247] = {"Un diplomate a disparu", {"Alors ouais… Hum !… Il se pourrait que je sache quelque chose à propos de cette rencontre à la ferme des Addle. Ça s'est passé il y a quelques semaines je crois. Je n'y ai pas participé. J'avais terminé ma part du travail lorsque le plan A a échoué… Maudits aventuriers !","La taupe que nous avions pour le plan B venait de Menethil, un type appelé Slim. C'est tout ce que je sais sur leur plan de rechange."}, {"Parler à Elling Trias à Stormwind."}},
[1248] = {"Un diplomate a disparu", {"Un de mes contacts a pris sa retraite dans les Paluns, dans le port de la cité de Menethil pour être précis. Il sera d'accord pour vous aider si vous lui dites que vous travaillez pour moi. Son nom est Mikhail. Avant de quitter Stormwind, il y a quelques années, il disait qu'il voulait s’occuper d’un bar.","Parlez-lui, trouvez ce Slim et trouvez tout ce qui est possible sur ce diplomate disparu. Nous avons besoin de savoir où ils l'ont emmené ou au moins qui il est. Le registre ne dit rien concernant leurs projets après sa capture."}, {"Parler à Mikhail dans les Paluns."}},
[1249] = {"Un diplomate a disparu", {"Vous dites que c'est Elling qui vous envoie ? Eh bien l'hospitalité que je vous dois est encore plus grande. Quels que soient vos besoins, demandez. Je ferai de mon mieux pour vous aider.","Hmm, Slim vous dites ? Ça ne me dit rien, mais je garderai un oeil ouvert au cas où.","Ne vous retournez pas maintenant, <nom>, mais le gentilhomme là-bas près de la sortie regarde par ici. Il écoute notre conversation un peu trop attentivement, si vous voyez ce que je veux dire. Si j'étais vous, je l'intercepterais avant qu'il ne puisse atteindre la porte et disparaître."}, {"Soumettre Tapoke Jahn avant qu'il ne s'échappe et ensuite retourner voir Mikhail à Menethil."}},
[1250] = {"Un diplomate a disparu", {"Les Défias ne me payent pas suffisamment pour que je meure pour leur cause. Ils ont pris le roi. Ouais, le roi de Stormwind, pas un petit diplomate de rien du tout. Il était en route pour rencontrer Jaina Proudmoore. C'était un gros coup apparemment et un grand secret. Mais il n'y avait pas que les Défias qui y participaient. Ils travaillaient pour quelqu'un. Je pense qu'ils savaient que c'était quelque chose de trop gros pour eux.","Mon travail consistait à introduire l'un de leurs hommes sur le bateau du roi lorsqu'il était à quai. Le nom de ce type est Hendel. C'est tout ce que je sais. Je le jure !"}, {"Parler à Mikhail dans les Paluns."}},
[1251] = {"Le bouclier noir", {"Un bouclier en fer, noirci par le feu qui a fait rage, est fixé à la cheminée branlante, une des rares structures encore debout de ce qui a été l'auberge du Repos ombragé.","Le bouclier peut être détaché du mur en brique de la cheminée."}, {"Rapporter le Bouclier en fer noirci à Krog, à Mur-de-Fougères."}},
[1252] = {"Lieutenant Paval Reethe", {"Un éclat de lumière attire votre attention sur les gravats. Ecartant les débris et la poussière, vous trouvez un insigne émaillé représentant une ancre dorée sur fond blanc : le symbole de Theramore.","Sous l'ancre est gravé : \"Lieutenant Paval Reethe\"."}, {"Rapporter le badge de Reethe au capitaine Garran Vimaire à Theramore."}},
[1253] = {"Le Bouclier noir", {"Un bouclier en fer, noirci par le feu qui a fait rage, est fixé à la cheminée branlante, une des rares structures encore debout de ce qui a été l'auberge du Repos ombragé.","Le bouclier peut être détaché du mur en brique de la cheminée."}, {"Rapporter le Bouclier en fer noirci au capitaine Garran Vimaire à Theramore."}},
[1258] = {"... Et des bestioles", {"Un autre ingrédient de ma nouvelle recette est la patte de crabe géant. Et les crabes qu'ils ont à Âprefange, ceux qu'on appelle les Bourbecoques, ne conviennent pas ! J'ai bien peur que les seuls crabes valables ne vivent loin, dans le marais des Chagrins.","La carapace des pattes de crabe est un des éléments clés de la présentation de ce plat. Cela veut dire que j'ai besoin de pattes parfaites, sans trace ni éraflure. Un crabe adulte n'a généralement qu’une ou deux pattes de cette qualité et il m'en faut beaucoup pour mettre au point ce plat.","Alors, <nom>... est-ce qu'un voyage vous tente ?"}, {"Apporter 12 Pattes de Clampant en parfait état à Morgan Stern à Theramore."}},
[1259] = {"Le lieutenant Paval Reethe", {"Le nom du lieutenant Paval Reethe ne me dit rien. Votre meilleure chance est de vérifier auprès de l'adjudant Tesoran. Il conserve tous les dossiers de la garde de Theramore, du personnel jusqu'à l'équipement.","Il devrait être capable de vous dire qui est ce Reethe après un rapide coup d'oeil dans ses registres."}, {"Trouver plus de renseignements sur Paval Reethe auprès de l'adjudant Tesoran à Theramore."}},
[1260] = {"Morgan Stern", nil, nil},
[1261] = {"Marg parle", {"Maudit Bourbecoque ! Tu ne peux pas tuer Marg ! Ah, mais je suis mort. Alors il doit m'avoir tué...","Mais ma mission ! Ma mission ! Vous, <nom>, devez achever ma mission.","Alors que j'étais près de Theramore, j'ai espionné un groupe de soldats de l'Alliance qui campaient sur la plage. Un de ces soldats portait un pendentif... Un pendentif avec plus de pierres précieuses que dans les rêves d'un soldat !","Cette nuit-là, je me suis glissé dans leur camp et j'ai volé le pendentif, mais au retour à la base, un homme-crabe Bourbecoque m'a tué !","<nom>, vous devez le retrouver et récupérer le pendentif !"}, {"Apporter le Pendentif orné à Nazeer à Mur-de-Fougères."}},
[1262] = {"Rapport à Zor", {"Il est étrange que le pendentif que Marg a volé était en possession de simples soldats. Peut-être que son propriétaire était déguisé, ou que les soldats l’avaient eux-mêmes volé !","Quoi qu'il en soit, il s'agit d'un pendentif de noble et nous devons découvrir son véritable propriétaire si nous voulons percer ce mystère.","Apportez-le à Zor Lonetree à Orgrimmar. Il en connaît beaucoup sur les personnages politiques de l'Alliance et saura dire si ce pendentif est connu parmi les nobles de Stormwind."}, {"Apporter le Pendentif orné à Zor Lonetree à Orgrimmar."}},
[1264] = {"Un diplomate a disparu", {"Au début de chaque mois, les soldats de marine de Kul Tiras reçoivent leur paye et quittent généralement Theramore pour un autre endroit. Un grand nombre d'entre eux viennent ici pour les femmes, faire la bringue et passer un bon moment. L'un de ces groupes vient régulièrement à cette taverne et je crois que ce Hendel en fait partie.","Le commandant Samaul à Theramore devrait vous aider à le trouver si Hendel est toujours par ici. Il est loyal à Proudmoore, mais si vous citez négligemment le nom de Elling, il devrait vous aider... discrètement."}, {"Trouver le commandant Samaul, dans le marécage d'Âprefange."}},
[1265] = {"Un diplomate a disparu", {"Hendel ? Oui, je le connais. Mais pourquoi avez-vous besoin d'un de mes soldats ?","Elling vous a demandé de le trouver ? Je n'aime pas cela. Elling ne vous aurait pas <envoyé/envoyée> directement à moi si cela n'était pas pour des problèmes. Je comprends que vous ne puissiez pas me dire de quoi il s'agit, mais sachez ceci : j'ai pour mission de protéger ce pays. Si vous pensez créer des problèmes, alors je vous suggère de m'en informer.","Je crois que Hendel est cantonné à la première tour en dehors de Theramore."}, {"Chercher le soldat Hendel à la halte de la Vigie."}},
[1266] = {"Un diplomate a disparu", {"Le soldat Hendel se trouve à l'ouest d'ici. Il est en ce moment de garde et protège des infiltrateurs de Theramore tandis qu'ils se reposent. Ils ont tenu à l'œil la base de la Horde, Mur-de-Fougères, et tout ce qui pourrait menacer nos frontières.","Vous ne devriez rencontrer aucune difficulté pour le trouver. Cherchez simplement des tentes le long de la route.","Une autre affaire nécessite mon attention, mais je vous rejoindrai bientôt. Vous pourrez toujours me rencontrer dans la tour de dame Proudmoore si vous avez le moindre problème."}, {"Trouver le soldat Hendel dans le marécage d'Âprefange."}},
[1267] = {"Un diplomate a disparu", nil, nil},
[1268] = {"Des empreintes de sabots suspectes", {"Des lignes d'empreintes de sabots partent des ruines encore fumantes de l'auberge du Repos ombragé. Il y a au moins quelques traces visibles avec d'autres empreintes peu marquées dans la boue du marais."}, {"Faire votre rapport sur les empreintes suspectes de l'auberge du repos ombragé à Krog, au village de Mur-de-Fougères."}},
[1269] = {"Lieutenant Paval Reethe", {"Un éclat de lumière attire votre attention sur les gravats. Ecartant les débris et la poussière, vous trouvez un insigne émaillé représentant une ancre dorée sur fond blanc : le symbole de Theramore.","Sous l'ancre est gravé : \"Lieutenant Paval Reethe\"."}, {"Rapporter le Badge de Reethe à Krog à Mur-de-Fougères."}},
[1270] = {"Sauvetage du Puant", {"Enfin quelque chose qui ne semble pas vouloir me manger !","J'ai besoin d'aide pour repousser ces monstres et vous me semblez convenir.","Un Goblin de Ratchet, du nom de Mebok Mizzyrix, a besoin de feuilles de haricots des marais pour une potion ou je ne sais quoi... J'avoue que je me fiche complètement de la raison, son argent est bienvenu et je connais ce marais, donc me voilà.","Dommage que je me sois fait encercler avant d'avoir trouvé les haricots ! Pourriez-vous m'aider à sortir de là et surveiller mes arrières tandis que je cherche les plantes pour Mebok ?"}, {"Escorter Ignatz \"Puant\" puis discuter avec Mebok Mizzyrix à Ratchet."}},
[1271] = {"Fête au Solitaire Bleu", nil, nil},
[1273] = {"Questionner Reethe", {"Cela a pris du temps, mais j'ai trouvé Reethe. Il se cache bien pour un humain.","Ogron se demande s’il n'est pas devenu fou après tout ce temps passé dans le marais. Vous venez avec moi pour lui tirer les vers du nez."}, {"Aller avec Ogron pour parler à Reethe puis retourner voir Krog à Mur-de-Fougères."}},
[1274] = {"Un diplomate a disparu", {"Excusez-moi, <monsieur/madame>, l'évêque DeLavey m'a demandé de prendre contact avec des <aventuriers/aventurières> qui pourraient être capables de l'aider avec un problème délicat.","Si vous pouviez vous rendre discrètement au Donjon de Stormwind pour lui parler dès que cela vous est possible, il est certain qu'il apprécierait grandement votre secours. A nouveau, soyez <discret/discrète>. Il s'agit d'une affaire d'une certaine importance."}, {"Trouver l'évêque DeLavey au Donjon de Stormwind."}},
[1275] = {"Recherches sur la corruption", {"Ces imbéciles d'elfes de sang peinent avec la magie démoniaque. N'ont-ils pas vu ce qui est arrivé aux nagas et aux satyres de Kalimdor ?","Je crains que les elfes de sang ne rencontrent pareille déformation. Azeroth ne supporterait pas la naissance d'une autre race de monstres infects.","Je souhaite étudier les Souches de cerveaux des satyres et des nagas afin d'en déduire la voie que prennent les elfes. Vous trouverez des satyres et des nagas dans les Profondeurs de Brassenoire, sur la côte d'Ashenvale.","C'est un long voyage, <nom>."}, {"Gershala Nightwhisper d'Auberdine veut 8 Souches de cerveau corrompu."}},
[1276] = {"Le Bouclier noir", {"Je me souviens ! Mosarn est un forgeron respecté de Thunder Bluff. Je savais que j'avais entendu son nom quelque part !","En remerciement, le Cercle terrestre m'a offert un équipement complet forgé par Mosarn. Du très beau travail !","Peut-être pourrait-il vous en dire plus sur ce bouclier qu'il a forgé.","Ce n'est pas une grosse piste, mais c’est la seule et c’est Reethe qui l’a donnée."}, {"Montrer le Bouclier en fer noirci à Mosarn à Thunder Bluff."}},
[1282] = {"Ils l'appellent Jim le Souriant", {"Je ne peux pas aider tant que je m'apitoierai sur le vieux Jim là-bas. Il n'a pas toujours été comme cela. Il a tenu une auberge en dehors des marais pendant un moment, un endroit superbe.","Écoutez, je ne veux pas vraiment en parler. Allez voir le capitaine Vimaire si vous avez besoin de quelque chose."}, {"Discuter avec le capitaine Garran Vimaire à Theramore."}},
[1284] = {"Des empreintes de sabots suspectes", {"Des lignes d'empreintes de sabots partent des ruines encore fumantes de l'auberge du Repos ombragé. Il y a au moins quelques traces visibles avec d'autres empreintes peu marquées dans la boue du marais."}, {"Rapporter la présence des empreintes suspectes à l’auberge du Repos ombragé au capitaine Vimaire à Theramore."}},
[1285] = {"Les hommes de Daelin", {"Reddon… Redpath… Ah voilà ! Lieutenant Paval Reethe.","A rejoint la garde de Theramore en même temps que la plupart des recrues ayant fait la traversée avec dame Proudmoore. Engagé dans les soldats de marine de Kul Tiras à seize ans. Etats de service brillants, promu...","Porté disparu."}, {"Ramener l'information concernant Reethe au capitaine Vimaire à Theramore."}},
[1286] = {"Les déserteurs", {"Nous avons lutté pour maintenir notre présence dans les marais, et grâce à notre détermination, nous avons pu conserver plusieurs tours de garde défensives dans le secteur.","Les dernières informations et rapports d'éclaireurs que j'ai reçus indiquent qu'un groupe de déserteurs squatte une tour abandonnée à la Halte perdue, au sud-est de l'auberge.","Leur chef se nomme Jacken. Voyez si vous pouvez lui soutirer des informations.","Soyez sur vos gardes, <nom>. Il risque d'être réticent à collaborer."}, {"Rapporter l'information de Balos Jacken sur Reethe et les déserteurs dans le marécage d'Âprefange."}},
[1287] = {"Les déserteurs", {"Reethe ? Ce lâche ?","Nous l'avons éjecté du camp, car il insistait pour aller piller le ravitaillement des ogres de Mur-de-Fougères. Nous ne sommes pas suffisamment nombreux pour faire face à ces brutes sans cervelle.","Il a franchi trop de fois les limites, alors nous l'avons envoyé paître.","Bon je vous ai aidé... Alors aidez-moi à votre tour en oubliant que vous nous avez trouvés !"}, {"Discuter avec le capitaine Garran Vimaire à Theramore à propos de Reethe."}},
[1301] = {"James Hyal", {"Avant de travailler ici, au Solitaire bleu, j'ai été formé par le meilleur chef cuisinier et gérant d'auberge que je connaisse. Son nom était James Hyal. Tout ce que j'ai et tout ce que j'ai appris, je le lui dois.","Je me demande ce qu'il devient. Son frère Vincent et lui sont allés travailler dans une auberge du Port de Menethil, mais je suis sans nouvelles depuis longtemps.","Si vous passez dans le coin, donnez-lui le bonjour de ma part, d'accord ?"}, {"Discuter avec Vincent Hyal au Port de Menethil."}},
[1302] = {"James Hyal", {"Mon frère James a accompagné Dame Proudmoore lorsqu'elle a traversé les océans. Les dernières nouvelles disaient que le calme était revenu là-bas et que James avait monté une auberge quelque part sur le continent. Où qu'il soit, je suis sûr qu'il fait de bonnes affaires. C'est un excellent chef cuisinier !","Je suis sans nouvelles de lui depuis un bon moment. Trop de travail avec son auberge, je suppose. Mais le greffier à Theramore a peut-être plus de renseignements."}, {"Parler au greffier Lendry à Theramore."}},
[1318] = {"Affaires inachevées chez les Gordok", {"Maintenant qu’z’êtes le roi, p’têt' qu’vous pourrez récupérez l’objet précieux des Gordok que le sale elfe qui se dit Prince nous a pris. L’roi avant vous a pas réussi, et celui d’avant non plus, et aucun roi depuis que Tortheldrin – c’est le sale elfe – l’a volé à un vieux roi gordok d’il y a longtemps !","C’est le Gantelet de puissance Gordok. Les vieux, ils disent que seul le vrai roi des Gordok pourra le ramener. Et puis, les vieux, ils disent aussi que le sale elfe est toujours là – si vous le trouvez, vous pourrez porter le gantelet de puissance pour toujours ! Ouais ! Je vais vous montrer !"}, {"Trouvez le Gantelet de Puissance Gordok et rendez-le au capitaine Kromcrush à Hache-tripes.","Selon Kromcrush, les histoires des « vieux » racontent que Tortheldrin – un « vilain » elfe qui se fait appeler prince – l’a volé à l’un des rois gordok."}},
[1319] = {"Le Bouclier noir", {"Je suis désolé de ne pouvoir vous en dire plus, <nom>, mais je sais qui peut vous aider. Caz Twosprocket, un de nos meilleurs forgerons. Portez-lui le bouclier et voyez ce qu'il peut vous dire."}, {"Montrer le Bouclier en fer noirci à Caz Twosprocket à Theramore."}},
[1320] = {"Le Bouclier noir", {"Ah ! J'ai failli manquer ceci… Cette lanière de cuir au dos aurait dû brûler, et elle a été faite pour un grand gabarit. Je dirais même un tauren.","C'est tout ce que je peux dire. Dites au capitaine Vimaire que je suis désolé de n'avoir pas pu l’aider plus."}, {"Discuter avec le capitaine Garran Vimaire à Theramore."}},
[1321] = {"Le Bouclier noir", {"Hmmm… Je ne vois rien qui sorte de l'ordinaire à propos de ce bouclier. Demandons à Do'gol, le forgeron, de l'examiner, puis écoutons ce qu'il peut nous dire, mais n'espérons pas trop…"}, {"Montrer le Bouclier en fer noirci à Do'gol au village de Mur-de-Fougères."}},
[1322] = {"Le Bouclier noir", {"Hmmm ! Il y a quelque chose d'écrit sur le bord du bouclier, c'est dur à lire avec toutes ces traces de suie.","J'ai besoin d'acide pour le faire apparaître. Du venin d'araignée Sombrecroc des marais serait parfait. Rapportez-moi quelques glandes et je vous nettoie cela."}, {"Trouver 6 Sacs de Venin acide pour Do'gol de Mur-de-Fougères."}},
[1323] = {"Le Bouclier noir", {"Oh ! Je crois que j'ai mis trop d'acide sur le bouclier. Frottons un peu pour voir…","Hmm, difficile à lire, mais il me semble qu'il s'agit d'un nom : Mosarn. Jamais entendu parler de lui. Peut-être Krog le sait. Krog connaît tellement de monde."}, {"Parler à Krog au village de Mur-de-Fougères."}},
[1324] = {"Un diplomate a disparu", {"Quoi ? Vous venez m'accuser d'être allié aux Défias et vous pensez que je vais rester là tranquillement alors que vous êtes en train de m'insulter ? Quelque chose me dit que vous pensez à cela depuis longtemps."}, {"Battre le soldat Hendel dans le marécage d'Âprefange."}},
[1338] = {"La mission de Stormpike", {"Il y a un forgeron nain dont j'admire beaucoup le travail. Il s'appelle Furen Longbeard et ses compétences sont inégalables. J'ai besoin d'un nouveau bouclier et je veux l'un des siens !","Le problème… Furen est loin dans le sud, dans le quartier des nains de Stormwind. Chez les humains ! Je ne comprends pas pourquoi il vit là-bas, si loin d'Ironforge. Les humains doivent lui faire un pont d’or pour qu'il accepte de rester !","Si vous avez envie de voyager, et ne pensez pas à demander une récompense, alors pourquoi ne porteriez-vous pas ma commande de bouclier à Furen ?"}, {"Porter la commande de Stormpike à Furen Longbeard à Stormwind."}},
[1339] = {"La mission du Montagnard Stormpike", {"J'ai entendu dire que le montagnard Stormpike a besoin d'un coursier. Quelqu'un pour voyager à sa place. Qu'en pensez-vous, <nom> ? Êtes-vous capable de ce travail ?","Si c'est le cas, vous trouverez Stormpike au sommet de la tour de garde la plus au nord."}, {"Parler au Montagnard Stormpike."}},
[1358] = {"Un échantillon pour Helbrim", {"Un collègue de Kalimdor, l'apothicaire Helbrim, étudie les effets des toxines de Lordaeron sur les animaux de Kalimdor. Il serait très intéressé par un échantillon de coeurs de loups que Renferrel m'a envoyés.","Helbrim se trouve dans les Tarides, dans un village connu sous le nom de la Croisée. Pour le rejoindre, vous voyagerez par zeppelin jusqu'à Orgrimmar, la capitale des orcs, puis à pied vers le sud en Durotar. Allez à l'ouest, dans les Tarides, et vous trouverez rapidement la Croisée."}, {"Apporter l'échantillon de coeur de loup à l'Apothicaire Helbrim dans les Tarides."}},
[1359] = {"Une livraison pour Zinge", {"Les Coeurs de loups que vous avez là possèdent une qualité inouïe pour ces spécimens. Ils possèdent une toxicité telle qu'elle tuerait net les loups vivants !","C'est fascinant, il faut approfondir l'étude. J'ai gardé quelques échantillons de ces coeurs. Apportez-les à ma collègue, l'Apothicaire Zinge. Vous la trouverez dans notre quartier général à Undercity."}, {"Apporter les Echantillons de coeurs de loups à l'apothicaire Zinge à Undercity."}},
[1360] = {"Des Trésors à recouvrer", {"J'avais un bon travail de terrassier à l'excavation d'Uldaman dans les Terres ingrates… jusqu'à ce que ces sacrés troggs arrivent. J'ai à peine réussi à sauver ma peau, rien d'autre. Dans la panique, j'ai laissé mon plus précieux bien. C'est dans mon coffre donc à l'abris des troggs, mais au beau milieu d'eux.","Vous auriez toute ma gratitude, en honneur et en or, si vous pouviez me le ramener. Il y a un coffre portant mon nom au bout du hall commun nord. C'est là qu'il faut aller."}, {"Récupérer les biens de Krom Stoutarm dans son coffre dans le hall commun nord d'Uldaman et les lui rapporter à Ironforge."}},
[1361] = {"Regthar Deathgate", {"Un guerrier de la Horde, Regthar Deathgate, recrute des agents pour une mission dans les terres de Désolace. Allez lui parler si vous souhaitez vous porter volontaire. Il se trouve dans les Tarides, dans une série de fortins situés à l'ouest de la Croisée.","Et aiguisez vos armes. Désolace est ravagée par la guerre."}, {"Discuter avec Regthar Deathgate."}},
[1362] = {"Les Kolkar de Désolace", {"Les centaures Kolkar vivent en Désolace et leurs forces sont disséminées dans toutes les Tarides. Il est de notoriété publique que des agents de la Horde s'occupent d'eux.","Mais tant que les centaures seront puissants en Désolace, ils resteront une menace. Il est temps de nous en débarrasser.","Parlez à Felgur Twocuts au Poste de Rôdeur-fantôme en Désolace. Il y a été envoyé pour réunir des informations sur les centaures. Il saura comment faire au mieux avec les Kolkar de Désolace."}, {"Discuter avec Felgur Twocuts au poste de Rôdeur-fantôme en Désolace."}},
[1363] = {"Les ordres de Mazen", {"En tant que conservateur de la bibliothèque de l'Académie des arts et sciences des arcanes, il est de ma responsabilité de m'assurer que tous les livres magiques et les parchemins sont bien présents. Qui sait quelle folie pourrait survenir si l'un de ces textes enchantés tombait entre de mauvaises mains ?","Cela me fait penser : mon assistant, Dellis, a fait une terrible erreur la semaine dernière. Il est parfaitement à l'aise dans la bibliothèque, mais parfaitement incompétent dès qu'il franchit les portes de Stormwind. Soyez un exemple de ce temps et allez aider le vieux Dellis, voulez-vous ?"}, {"Parler à l'acolyte Dellis à Stormwind."}},
[1364] = {"Les ordres de Mazen", {"Les ordres de maître Mazen sont très clairs : porter d'importants essais au Gardien Mahar Ba au Rempart-du-Néant.","Mais je n'ai jamais été dans les Terres foudroyées avant ! C’est terrifiant !","Dans le marais des Chagrins, j'ai pris un mauvais chemin et j'ai fini cerné par d'horribles créatures à moitié végétales. Elles bougeaient comme si elles voulaient m'attaquer, alors j'ai fui. Mais dans ma hâte, j'ai laissé tomber les papiers. Un des monstres les a pris, j'en suis sûr.","Pourriez-vous les retrouver s'il vous plaît et les porter à Mahar Ba ?"}, {"Retrouver les Essais de Khadgar sur la convergence dimensionnelle et les porter au Gardien Mahar Ba au Rempart-du-Néant."}},
[1365] = {"Khan Dez'hepah", {"Les Kolkar ne sont pas les seuls centaures de Désolace. De nombreux clans parcourent ces plaines sèches, combattant et mourant. Il est heureux qu’ils se battent entre eux, car ils sont forts et brutaux. Si les centaures s'unissaient contre nous, ils seraient un adversaire redoutable.","Nous devons nous occuper d'eux avant qu'ils ne s'allient.","Votre première mission est de désorganiser le clan Kolkar. Vous y parviendrez en tuant son chef, le khan Dez'hepah.","Le village des Kolkar est au nord-est. Allez et que les dieux soient avec vous !"}, {"Rapporter la Tête du khan Dez'hepah à Felgur Twocuts en Désolace."}},
[1366] = {"La prime aux centaures", {"Maintenant que vous avez terrifié les clans centaures, il est temps de les saigner. Lorsque vous voyagerez à travers Désolace, chassez-les. Je me moque de savoir à quel clan ils appartiennent. Vous devez juste collectionner les oreilles. Je veux un monceau d'oreilles !","En diminuant leur nombre vous porterez un coup à leur résolution et sonnerez le glas de leurs envies d'expansion.","Bonne chasse, <nom>."}, {"Apporter 15 Oreilles de centaure à Felgur Twocuts, en Désolace."}},
[1367] = {"L'alliance avec les Magram", {"Pour battre les centaures, nous allons utiliser leur haine contre eux. Nous allons nous allier à l'un des clans, et par ce biais nous apprendrons comment battre les autres clans.","Si vous décidez de vous allier aux Magram, alors tuez leurs ennemis, les Gelkis. Vous gagnerez ainsi le respect des Magram. Lorsque cela sera fait, alors allez rencontrer Warug, le porte-parole des Magram, dans les faubourgs du village des Magram au sud-est de Désolace."}, {"Faire en sorte que les Magram soient Amicaux envers vous, puis aller parler à Warug."}},
[1368] = {"L'alliance avec les Gelkis", {"Pour battre les centaures, nous allons utiliser contre eux leur haine fratricide. Nous allons nous allier à l'un des clans, et par ce biais, nous apprendrons comment vaincre les autres clans.","Si vous décidez de vous allier aux Gelkis, alors tuez leurs pires ennemis, les Magram. Après avoir gagné le respect des Gelkis, allez rencontrer le chaman Uthek la Sage dans les environs du village des Gelkis, au sud-ouest de Désolace."}, {"Faire en sorte que les Gelkis deviennent Amicaux envers vous, puis aller parler à Uthek la Sage."}},
[1369] = {"Larmes brisées", {"Les Gelkis pensent que Theradras, la mère des centaures, nous protège. Ils sont idiots. Personne ne protège les centaures. Les centaures n'ont pas besoin de protection ! Les Magram n'ont pas besoin de protection ! Nous sommes les plus forts !","Montrez aux Gelkis qu'ils sont stupides. Allez dans la caverne de leur village. Ils y possèdent des cristaux, les Larmes de Theradras. Détruisez ces cristaux et rapportez-moi les morceaux."}, {"Maintenir votre réputation auprès des Magram et ramener 3 Larmes brisées à Warug au village des Magram."}},
[1370] = {"Voleur de viande", {"Lorsque nous ne sommes pas en guerre, nous chassons pour trouver de la viande. La viande nous donne la force et des ventres remplis se battent mieux que des ventres vides.","C'est la même chose chez les Magram. Ils gardent la viande en trop afin de se battre plus longtemps entre leurs chasses. Si vous attaquez leur village par l'est et prenez leurs réserves de viande, leur force va diminuer."}, {"Rapporter 6 Sacs de viande grossièrement séchée à Uthek la Sage au village des Gelkis."}},
[1371] = {"Un Bidule pour Warug", {"Il y a longtemps, un petit gnome est venu en Désolace. Il avait des chevaux et une carriole qui contenait des choses étranges. De drôles de choses. Des choses qui brûlaient de différentes couleurs. Des choses qui bougeaient, mais n'étaient pas vivantes. J'ai tué le petit gnome mais avant cela... Il m'a fait rire.","J'ai gardé ma chose préférée de sa carriole. C'était un humain en bois qui me faisait signe jusqu'à ce que je le tape et que je le casse.","J'en veux un nouveau !"}, {"Maintenir votre réputation auprès des Magram et rapporter un Leurre perfectionné à Warug au village des Magram en Désolace."}},
[1372] = {"La vérité, rien que la vérité", {"Notre force expéditionnaire a été envoyée sur les ordres de Varimathras pour surveiller le bas-Azeroth.","Pendant une reconnaissance, j'ai remarqué un infiltrateur humain qui espionnait le Repaire des mendiants. Avant que je ne puisse l'attraper, certains de nos... frères orcs l'ont capturé et emmené à Stonard, leur forteresse dans le marais des Chagrins.","Je veux… aider… nos alliés, les orcs. Ils n'ont pas vu notre camp, mais malgré cela, je veux les aider.","Travaillez avec Faustin pour développer… un sérum de vérité pour notre ami, à Stonard."}, {"Parler à l’Apothicaire Faustin, au Repaire des mendiants."}},
[1373] = {"Ongeku", {"Vous avez encore à prouver des choses et votre prochaine mission vous emmènera loin, car maintenant les Gelkis vont combattre les Magram sans vous.","La terre chuchote bien des secrets à ceux qui savent écouter. Un des secrets que j'ai appris est qu'il existe un cristal rouge appelé draenéthyste, nouvellement arrivé sur notre terre. Il vient d'un endroit où les montagnes volent et où les démons règnent.","Apportez-moi un éclat de draenéthyste et ma magie grandira ! Le cristal se cache dans un endroit appelé le marais des Chagrins, et appartient à un Perdu, Ongeku."}, {"Maintenir votre réputation auprès des Gelkis et rapporter un Eclat de draenéthyste à Uthek la Sage au village des Gelkis, en Désolace."}},
[1374] = {"Khan Jehn", {"La terre me parle et me dit que les Magram sont de plus en plus faibles. Il est maintenant temps de tuer leur khan !","Rapportez-moi la tête de Jehn, le khan des Magram !"}, {"Rapporter la Tête du khan Jehn à Uthek la Sage au village des Gelkis, en Désolace."}},
[1375] = {"Khan Shaka", {"Vous avez aidé les Magram et je vous apprécie. Maintenant tuez le khan des Gelkis ! Il s'appelle Shaka et je veux sa tête ! Je veux la regarder et me moquer d'elle !"}, {"Maintenir votre réputation parmi les Magram et rapporter la Tête du khan Shaka à Warug au village des Magram en Désolace."}},
[1380] = {"Khan Hratha", {"Vous nous avez aidés contre les Magram, <nom>. À présent, aidez-nous à battre le clan des Maraudine ! Tuez leur chef, le khan Hratha !","Pour le combattre, soufflez dans la corne de guerre maraudine. Elle se trouve au sommet d'un grand promontoire, dans la Vallée des lances.","Mais pour souffler dans la corne, vous devez d’abord obtenir une embouchure de corne de guerre. Elles sont rares, et portées uniquement par des Maraudine de confiance.","Quand vous aurez soufflé dans la corne, des centaures arriveront. Tuez-en jusqu'à ce que Hratha apparaisse.","Le khan Hratha porte un fragment de... clé. Apportez-le-moi..."}, {"Maintenir votre réputation auprès des Gelkis, et apporter le Fragment de clé des Maraudine à Uthek la Sage au village des Gelkis."}},
[1381] = {"Khan Hratha", {"Le khan des Gelkis est mort, et à présent le clan Maraudine va tomber ! Tuez le khan des Maraudine, Hratha.","Pour le combattre, soufflez dans la Corne sur le grand promontoire de la Vallée des lances. Mais pour y souffler, vous aurez besoin d'une Embouchure. Pour l'obtenir, tuez des centaures Maraudine. Tuez jusqu'à ce que vous la trouviez. Puis soufflez dans la corne.","Agissez ainsi et les centaures Maraudine viendront à la tour. Tuez-en jusqu'à ce que le khan Hratha apparaisse. Il détient un fragment de clé. Apportez-moi ce fragment..."}, {"Maintenir votre réputation auprès des Magram, et apporter le Fragment de clé des Maraudine à Warug au village des Magram en Désolace."}},
[1382] = {"Une étrange alliance", {"Nous avons de la chance que les clans de centaures se battent entre eux, car s'ils unissaient leurs forces, rien ne pourrait les arrêter. Oui, nous sommes chanceux, et nous devrions mettre à profit cet avantage.","L'un des clans de centaures, le clan Gelkis, mène une guerre acharnée au clan Magram. Si nous attaquons ce second clan, alors nous pourrions peut-être conclure une alliance avec les Gelkis.","Allez parler à Uthek la Sage, aux approches des campements gelkis, au sud-ouest de Désolace. Grâce à elle, vous pourrez peut-être conclure une alliance avec le clan Gelkis."}, {"Obtenir que les Gelkis se montrent Amicaux, puis parler à Uthek la Sage."}},
[1383] = {"La vérité, rien que la vérité", {"J'ai tout simplement en tête le bon sérum. Il s'occupera de la vérité exactement comme il convient. Pour cette préparation, je vais avoir besoin de beaucoup de cœurs de panthères des ombres, en provenance du marais. J'aurai également besoin d'un champignon de boueux enchanté, de la réserve personnelle du seigneur du bourbier local. Il est certain que quelqu'un d'aussi talentueux que vous, <nom>, peut le persuader de s'en séparer.","Le plus difficile sera de trouver une tumeur d'Arpentefonds dans la lointaine Désolace. Il est très rare que les géants de là-bas tombent malades et forment des tumeurs.","Allez-y à présent !"}, {"L'apothicaire Faustin, du repaire des mendiants, a besoin de 5 Cœurs de panthère des ombres, d'un Champignon de Boueux et d'une Tumeur d'Arpentefonds."}},
[1384] = {"Raid sur les Kolkar", {"Les Kolkar estiment que leur magie est puissante. Ils ne connaissent rien à la magie ! Ils ne reconnaîtraient même pas la magie si elle les frappait !","Vous allez leur montrer que leur magie est faible. Allez chez les centaures Kolkar au nord-est, et tuez-les. Détruisez leurs camps. Apportez-moi leurs charmes, que je les jette au feu et que je rie !"}, {"Maintenir votre réputation auprès des Gelkis, et apporter 10 Charmes bruts à Uthek la Sage dans le village des Gelkis, en Désolace."}},
[1385] = {"Une violente politique", {"Désolace est une terre en guerre. Les centaures se font la guerre les uns les autres. Nous pensons que les vainqueurs vont surgir et menacer nos forteresses, ce que nous ne pouvons permettre.","Nous devons apprendre comment battre les centaures, et pour cela nous devons nous allier à l'un des clans. Les centaures Magram sont en guerre contre les Gelkis. Si vous combattez les centaures Gelkis, alors les Magram pourraient envisager une alliance.","Gagnez les faveurs des Magram, puis parlez à Warug, au village des Magram, au sud-est de Désolace."}, {"Faire en sorte que les Magram soient Amicaux à votre égard, puis aller parler à Warug."}},
[1386] = {"Assaut sur les Kolkar", {"Les centaures Kolkar pensent qu'ils sont forts, mais nous rions de leur force ! Nous rions d'eux et nous les tuons ! Vous les tuez aussi. Montrez-moi que vous pouvez tuer des centaures Kolkar comme nous les tuons. Allez vers le nord et massacrez-les tous !"}, {"Maintenir votre réputation auprès des Magram, tuer 12 Centaures kolkar, 12 Eclaireurs kolkar, 6 Marteleurs kolkar, puis retourner voir Warug, dans le village des Magram, en Désolace."}},
[1387] = {"La prime aux centaures", {"Le peuple des centaures est divisé en nombreux clans. Chaque clan a son chef et ses propres buts, mais ils ont tous une « qualité » en commun : la cruauté.","Si nous voulons avoir une chance de construire la paix, alors les centaures doivent être éradiqués. C'est exactement la tâche que je vous confie.","Pendant que vous serez en Désolace, chassez des centaures et prenez leurs oreilles. Apportez-moi de nombreuses, très nombreuses oreilles.","Œuvrez au service de l'Alliance, <nom>, et nous nous rapprocherons de la fin de la guerre."}, {"Apporter 15 Oreilles de centaures au caporal Melkins, à la combe de Nijel, en Désolace."}},
[1388] = {"La vérité, rien que la vérité", {"Tout se déroule selon le plan, <nom>. Je veux que vous remettiez ce sérum de vérité au traqueur noir Zraedus et il vous montrera comment l'administrer… proprement.","Ha ! Ha !"}, {"Remettre le Sérum de vérité de Faustin au traqueur noir Zraedus au Repaire des mendiants."}},
[1389] = {"Les cristaux de draenéthyste", {"Les Perdus qui vivent dans le Sanctuaire des friches nous ressemblent physiquement, mais ce sont des monstres… rendus fous par les rigueurs de notre voyage vers Azeroth. Nos anciens frères attrapent et tuent quiconque s’approche du Sanctuaire des friches, à l'est.","Et ils nous ont volé quelque chose de sacré à nos yeux : des cristaux de draenéthyste.","Nous n'avons pas la force suffisante pour pénétrer dans le Sanctuaire des friches et reprendre nos draenéthystes volées dans leurs huttes et près des feux de camp. Mais sans ces cristaux... nous pourrions également perdre la voie, tout comme eux."}, {"Apporter 6 Cristaux de draenéthyste à Magtoor au Havre boueux du marais des Chagrins."}},
[1391] = {"La vérité, rien que la vérité", {"Nous devons procéder avec une certaine discrétion. Après tout, ce n'est pas comme si vous alliez danser la valse là-bas et convaincre un imbécile de boire la préparation de Faustin, pas vrai ?","Mais il y a une chose à laquelle les humains ne savent pas dire non, c'est l'alcool. Je prendrai le sérum de Faustin et le verserai dans de la bière. Je peux garantir que le pauvre type n’hésitera pas à la siffler d'un trait.","Hâtez-vous vers Stonard, dans le marais des Chagrins, <nom>.","Et faites en sorte que notre petite mission reste secrète et sans accroc..."}, {"Apporter la Bière de Zraedus à l'Infiltrateur Marksen à Stonard."}},
[1392] = {"Noboru le Gourdin", {"Noboru a harcelé les draeneï du Havre boueux pendant des mois, et son gourdin est le symbole de la brutalité qu’ils ont subi de la part de ceux qu'ils ont perdus."}, {"Apporter le Gourdin de Noboru au Chef du Havre boueux dans le marais des Chagrins."}},
[1393] = {"L'évasion de Galen", {"Au secours ! Ces créatures vont me manger !","Sortez-moi de là, et je vous donnerai un grand trésor ! Il est dans mon camp, dans le marais au nord de Stonard, mais il est enfermé dans un coffre qui ne peut être ouvert que grâce à un truc que moi seul connais.","Si vous m'aidez à m'en sortir, alors je vous l’enseignerai."}, {"Escorter Galen jusqu'à ce qu'il soit hors de danger, puis trouver son camp dans le marais des Chagrins et ouvrir son coffre."}},
[1394] = {"Le dernier voyage", {"Vous avez bien œuvré, <nom>. Vous avez réussi mon test, et les tests d’avant. Retournez voir Dom aux Mille pointes. Il ressentira le changement qui s'est opéré en vous, et constatera que votre cerveau, votre corps et votre esprit sont suffisamment forts pour que vous soyez <récompensé/récompensée> de vos efforts.","Restez fidèle à l'équilibre que vous avez montré durant ces épreuves, <nom>. Dorn vous testera à nouveau dans l'avenir si vous le faites."}, {"Parler au traqueur des plaines Dorn aux Mille pointes."}},
[1395] = {"Fournitures pour Rempart-du-Néant", {"Cette caisse de pierres de puissance doit être livrée au Rempart-du-Néant, dans les Terres foudroyées. Elles ne vont pas conserver leur pouvoir longtemps, aussi nous ne pouvons pas les envoyer par la prochaine caravane.","Vous devez les emporter, et vous devez les emporter rapidement.","Apportez-les à l'intendant Lungertz au Rempart-du-Néant. Pour atteindre le donjon, vous devez voyager vers l'est, à travers le défilé de Deuillevent, puis à l'est à travers le marais des Chagrins, et enfin vers le sud à travers les Terres foudroyées.","Faites attention, <nom>. Vous allez traverser des endroits dangereux, mais vous n'avez pas le droit d'échouer."}, {"Apporter la Caisse de Pierres de puissance à l'Intendant Lungertz, avant que le pouvoir des pierres n'expire."}},
[1396] = {"Des créatures sauvages rôdent", {"Ma caravane avait pour destination la côte est. Mais nous ne l'avons pas atteinte. Les Perdus nous ont attaqués, tuant les gardes et volant des marchandises.","Moi seul ai survécu à l'assaut. J'ai juré de protéger les marchandises restantes, mais des créatures sauvages rôdent dans les alentours. Je vous en prie, aidez-moi à défendre mon campement contre ces bêtes !"}, {"Tuer 8 Jeunes crocilisques Dents-de-scie, 10 Tisseuses des pleurs et 10 Jaguars du marécage, puis retourner voir le Gardien Biggs, dans le marais des Chagrins."}},
[1398] = {"Bois flotté", {"Je vous dois beaucoup, <nom>. Un cœur noble bat en vous. J'ai à présent une autre tâche pour laquelle je demanderai humblement votre aide.","Si je veux avoir la moindre chance d'atteindre Rempart-du-Néant, alors je dois réparer mon chariot. Et pour cela, j'ai besoin de bois. Du bois fort, du bois sec, pas de ce bois spongieux et noueux des arbres du marais.","<nom>, partez à l'est pour la côte et cherchez du bois qui a séché au soleil. Et apportez-le-moi."}, {"Apporter 8 Morceaux de Bois séché au soleil au Gardien Biggs dans le marais des Chagrins."}},
[1418] = {"Neeka Bloodscar", {"Les Terres ingrates sont une région difficile, en bordure du Loch Modan, un territoire aux mains de l'Alliance. Aussi, la liaison entre Stonard et Kargath, l'avant-poste des Terres ingrates, est-elle souvent interrompue.","Montrez votre valeur, <nom>. Partez pour les Terres ingrates et parlez à Neeka Bloodscar, Eclaireur principal de l'avant-poste de Kargath. Parlez-lui, puis envoyez-moi son rapport."}, {"Parler à Neeka Bloodscar."}},
[1419] = {"Les coyotes voleurs", {"Avez-vous passé beaucoup de temps dans les Terres ingrates, <classe> ? Avez-vous ressenti la chaleur ? Avez-vous affronté les bêtes qui rôdent dans les plaines poussiéreuses ? Il est temps que vous le fassiez.","Les coyotes des rochers sont une espèce robuste. Robuste, et suffisamment intrépide pour s'aventurer jusqu'à Kargath et chaparder de la nourriture. Aidez-nous à débarrasser l'avant-poste de ces pique-assiette !","Chassez les coyotes des rochers, <nom>. Chassez-les et apportez-moi leurs mâchoires. Apportez-en assez, et peut-être que je vous croirai lorsque vous dites que vous connaissez les Terres ingrates."}, {"Apporter 30 Mâchoires de coyotes à Neeka Bloodmark à Kargath."}},
[1420] = {"Le rapport à Helgrum", {"J'ai écrit un rapport tout frais pour Helgrum à Stonard. Remettez-le-lui.","Et si vous vous considérez comme une bonne estafette, alors ne vous perdez pas. Et ne tombez pas aux mains de l’ennemi."}, {"Apporter le Rapport de Neeka à Helgrum l'Agile à Stonard."}},
[1421] = {"Le chariot perdu", {"J'ai perdu connaissance durant l'attaque des Perdus, mais avant que tout ne s'obscurcisse, j'ai vu l'un de nos chariots en train d'être traîné vers le Sanctuaire des friches, le village des Perdus, au nord.","Un coffre plein d'ingrédients de valeur se trouvait dans ce chariot. Ces ingrédients sont d'un intérêt scientifique vital pour les sorciers du Rempart-du-Néant. Retrouvez ce coffre et mettez la main sur ces ingrédients, <nom>. Une fois que vous les aurez en votre possession, rapportez-les-moi."}, {"Trouver le Coffre de la Caravane, dans le Sanctuaire des friches, obtenir les Composants pour les sorciers, et les apporter au Gardien Biggs."}},
[1422] = {"Le danger venu des flots", {"Pendant que je commence à travailler sur votre arme, pourquoi n'iriez-vous pas parler à Katar ? Il a mentionné qu'il aurait besoin d'aide pour patrouiller le long de la plage, et je vous imagine mieux gardant vos esprits et vos armes en alerte que vous saoulant en compagnie des autres poux avec qui je partage ce camp."}, {"Parler à Katar, dans le marais des Chagrin."}},
[1423] = {"Les marchandises perdues", {"Ce tonneau, bien que petit, est robuste. Il est scellé avec une cire jaune à l’odeur âcre. On peut lire, tamponné au cul du tonneau :","INTENDANT LUNGERTZ","REMPART-DU-NEANT."}, {"Apporter les Marchandises perdues à l'Intendant Lungertz."}},
[1424] = {"Le bassin des larmes", {"Le chef de guerre nous a envoyés enquêter sur des événements étranges survenus au temple d'Atal'Hakkar.","Les trolls Atal'ai sont une tribu d'ensorceleurs noirs connus pour utiliser des magies interdites. Vous pouvez imaginer notre étonnement lorsque nous sommes arrivés pour découvrir que le temple avait été englouti.","À présent, les dragons verts gardent le site, et ils se disent protecteurs d'Azeroth.","Nous devons découvrir ce qui s'est passé. Explorez les lieux autour du Bassin des larmes et rassemblez des artefacts atal'ai. Ils me permettront de reconstituer une partie du puzzle."}, {"Fel’zerul à Stonard veut que vous rassembliez 10 Artefacts atal'ai."}},
[1425] = {"La remise du chargement", {"Pendant que je répare ce chariot, <nom>, j'ai une dernière tâche à vous confier.","J'ai empaqueté les marchandises les plus importantes rescapées des attaques des ogres. Apportez-les au Rempart-du-Néant. Le donjon est blotti dans les collines au nord des Terres foudroyées, pour surveiller la Porte des ténèbres.","Pour atteindre Rempart-du-Néant, suivez la route est. A l'embranchement, allez vers le sud dans les Terres foudroyées, puis tournez à gauche au second. Continuez à suivre la route, <nom>, et soyez <prudent/prudente>."}, {"Apporter la Cargaison pour Rempart-du-Néant à l'Intendant Lungertz au Rempart-du-Néant."}},
[1426] = {"Le danger venu des flots", {"Je n'ai aucun amour pour la mer, ni pour aucune des créatures que j'ai vu sortir de ses profondeurs. C'est une maîtresse puissante, mais aveugle. Insensible et brutale. Cela m'apporterait un grand plaisir de massacrer les hommes-poissons par centaines si c'était en mon pouvoir. Mais je ressens également du plaisir à les regarder mourir de la main de quiconque obéit à la volonté de la Horde.","Ce qui nous amène à vous, <classe>. Combien pouvez-vous en massacrer ? Voudriez-vous vous mesurer à leur plus puissante espèce ?"}, {"Tuer 10 Murlocs des marais, 10 Cracheurs des marais, 10 Dévoreurs des marais, puis retourner voir Katar dans le marais des Chagrins."}},
[1427] = {"Le danger venu des flots", {"Avant que vous ne vous reposiez, parlez à nouveau à Tok'Kar. Pendant que vous étiez <parti/partie>, il a terminé quelques armes. Pour récompenser l'aide que vous nous avez apportée, il devrait vous en offrir une. Vous devriez tester sa force contre les hommes-poissons quand vous vous sentirez <prêt/prête>... mais seulement pour me faire plaisir."}, {"Parler à Tok'Kar dans le marais des Chagrins."}},
[1428] = {"Le danger permanent", {"Il me plaît de voir que votre haine pour les hommes-poissons grandit en même temps que la mienne.","Trouvez-en d'autres, le long de la plage, et lorsque vous serez <fatigué/fatiguée> de vous battre, revenez me voir. Nous parlerons alors d'une récompense pour vos efforts assidus."}, {"Tuer 10 Cracheurs des marais, 10 Dévoreurs des marais, 10 Oracles des marais, puis retourner voir Katar dans le marais des Chagrins."}},
[1429] = {"L'Exilé atal'ai", {"Tandis que vous braviez les dangers du Bassin des larmes, l'un de mes éclaireurs a trouvé un vélin en peau séché, avec un texte écrit avec du sang. Cela semble être une sentence prononcée à l’encontre d’un prêtre de la tribu Atal'ai.","Le prêtre a été condamné à un sort pire que la mort, pour un Atal'ai.","Il a été exilé, obligé de vivre loin au nord, dans les Hinterlands, avec les trolls Witherbark.","Je vais faire un paquet de ces artefacts. Vous allez trouver l'exilé et lui demander d’identifier ces objets. Revenez me voir lorsque vous aurez achevé votre voyage."}, {"Apporter les Artefacts atal'ai à l'Exilé atal'ai dans les Hinterlands."}},
[1430] = {"Viande fraîche", {"Le goût succulent de la chair de clampant est très prisé parmi mes clients. Et particulièrement celui des clampants monstrueux.","Si vous comptez vous rendre sur la côte prochainement, je peux rentabiliser votre voyage : rapportez-moi quelques-unes de leurs pattes et je les expédierai à Orgrimmar.","La côte se trouve à l'est, et vous y trouverez facilement ces créatures, détalant de-ci de-là le long des plages et dans la mer."}, {"Apporter 10 Pattes de clampants monstrueux à Dar dans le marais des Chagrins."}},
[1431] = {"Les relations de l'Alliance", {"Tenez-vous tranquille, <classe>, et permettez-moi de partager avec vous quelque chose d'important pour notre peuple, mais loin d'être évident.","Nous sommes une race fière, et la guerre contre les humains a mis à rude épreuve même la grande patience de Thrall. Vous voyez, notre Chef de guerre est sage et prudent, et il continue à placer son peuple en premier. Il est, en fin de compte, un diplomate, et le temps qu'il a passé avec les humains lui a appris leur façon d'être.","Si l'histoire que je viens de commencer éveille votre intérêt, alors trouvez Keldran près de la sortie ouest de la ville. Sa masure s'y trouve."}, {"Parler à Keldran à Orgrimmar."}},
[1432] = {"Les relations de l'Alliance", {"Tandis que l'Alliance progresse plus profondément en Kalimdor, des conclusions hâtives pourraient être faites à propos d'hypothétiques accords entre la Horde et la Légion ardente. Le Chef de guerre ne peut le permettre. Et avec sa bénédiction, je prends le contrôle pour superviser leur anéantissement.","J'ai des agents dans le secteur de Désolace, au Poste de Rôdeur-fantôme, au nord du grand Cimetière des kodos. Allez au sud du Val Calciné, dans les Serres-Rocheuses, vous devriez tomber dessus. Une fois sur place, cherchez Takata Steelblade."}, {"Parler à Takata Steelblade en Désolace."}},
[1433] = {"Les relations de l'Alliance", {"Les sectateurs de la Lame ardente élaborent de nombreux plans, mais sachez qu'ils sont foncièrement maléfiques, que leur esprit est corrompu, et que la destruction est le seul moyen de mettre un terme à leurs objectifs.","Keldran a envoyé Maurin avec moi car il connaît la Lame et les méthodes des démonistes.","Il supervise nos actions contre la Lame, tandis que je m'occupe des autres dangers démoniaques ici en Désolace.","Parlez-lui, et il vous expliquera ses projets."}, {"Parler à Maurin Bonesplitter en Désolace."}},
[1434] = {"Souillé par les satyres", {"Une infestation démoniaque n'arrive pas sans une aide extérieure. Quelque chose permet au Mal de prendre pied.","Cela peut être un esprit affaibli, tenaillé par un désir vain de pouvoir. Peut-être est-ce une soif de vengeance, ou l'appel de la justice finale. Lorsque nous sommes assaillis par la douleur, le démon devient plus fort que nous. Un démon tel que les satyres.","Des liens ont été établis entre les satyres et la Lame ardente, et Thrall souhaiterait les voir massacrés. Cherchez-les au nord-est d'ici, et revenez me voir lorsque vous les aurez vaincus."}, {"Massacrer 7 Voleurs Fielfurie, 7 Liges de la corruption Fielfurie, 7 Traîtres Fielfurie et 7 Implorateurs de l’enfer Fielfurie, puis retourner voir Takata Steelblade en Désolace."}},
[1435] = {"Esprits consumés", {"La mort est très désirable pour ces dégoûtantes créatures qui quittent les ordres de leur seigneur pour des raisons si égoïstes. Mais la mort n'est pas suffisante quand on a affaire à ceux qui se sont donnés aux ténèbres.","Prenez cette gemme. Lorsque un membre de la Lame ardente approchera de la mort, activez sa magie. Le sort attirera l'esprit corrompu de sa victime, laisssant une gemme ardente canalisée.","Apportez-moi ces gemmes ardentes, que je m'en occupe comme il convient.","Partez à présent, et puissiez-vous échapper à la mort."}, {"Apporter une Gemme ardente et 15 Gemmes ardente canalisées à Maurin Bonesplitter en Désolace."}},
[1436] = {"Les relations de l'Alliance", {"La lutte se déroule bien, <nom>. Peut-être que le Chef de guerre nous permettra de rentrer dans nos foyers avant la fin de la saison si nous continuons à frapper de si grands coups dans notre bataille contre les démons et leurs servants.","Retournez voir Keldran et faites-lui savoir ce que nous avons accompli. Il est certain qu'il récompensera vos efforts ici. Sa cause est importante à ses yeux, et il sera fier de savoir que le Chef de guerre la respecte."}, {"Parler à Keldran à Orgrimmar."}},
[1437] = {"La recherche de Vahlarriel", {"Je me nomme Vahlarriel, <nom>. Je suis ici pour retrouver deux nobles disparus, issus d'une famille marchande de Darnassus : la famille Malem. Le père craint le pire, et je dois tout tenter pour retrouver ses enfants disparus. J'ai commencé mes recherches pour retrouver Tyranis ou Dalinda, mais j'ai bon espoir que je ne serai pas ici longtemps.","Aux dernières nouvelles, ils passaient par les Serres-Rocheuses il y a quelques semaines.","Si vous trouvez leur chariot, s'il vous plaît, veuillez revenir ici pour m'en informer."}, {"Rechercher le chariot de Tyranis et de Dalinda Malem en Désolace."}},
[1438] = {"La recherche de Vahlarriel", {"Trouvez Dalinda et Tyranis si vous le pouvez, et revenez m'informer de vos progrès. Commencez vos recherches dans cette forteresse. Je vais immédiatement contacter mes supérieurs à Darnassus et les informer de la situation.","<nom>, il y a quelque chose que je souhaite vous dire. Mes supérieurs ont montré quelque inquiétude à propos de Tyranis Malem et de ses raisons de venir ici. Le plus important pour eux est que Dalinda Malem soit retrouvée et ramenée en bonne santé. Nous devons... jauger Tyranis avant qu'il ne rentre chez lui."}, {"Trouver Dalinda Malem en Désolace."}},
[1439] = {"À la recherche de Tyranis", {"Nous avons été emmenés ici après le massacre de nos gardes et de nos serviteurs par les orcs. Oh ! Ils leur ont fait des choses si horribles ! J'ai cru qu'ils allaient également nous tuer, sur place, mais ils avaient visiblement d'autres plans.","Tyranis a essayé de leur parler. Je pense qu'il voulait les impressionner par sa connaissance des arcanes... Je ne suis pas sûre de savoir pourquoi.","Je vous en prie, je ne peux pas quitter les lieux tant que j'ignore ce qu'il est advenu de mon frère. Retrouvez-le, et venez me dire quel a été son sort."}, {"Trouver Tyranis Malem en Désolace."}},
[1440] = {"Le retour vers Vahlarriel", {"Ainsi, le temps est venu ? J'apprécie vos efforts, et si la situation ne se résout pas, alors permettez-moi de vous remercier dès à présent pour tout ce que vous avez fait pour moi et ma famille. Si vous êtes <prêt/prête>, rien ne me retient ici. Nous pouvons partir quand vous estimerez le bon moment arrivé."}, {"Escorter Dalinda Malem depuis la forteresse de Hache-Tonnerre et retourner voir Vahlarriel Demonslayer, en Désolace."}},
[1442] = {"À la recherche de la gemme de Kor", {"Salutations, <nom>. Que puis-je faire pour quelqu'un qui suit la voie sacrée ?","Ah oui ! Stilwell, le forgeron… d'il y a longtemps. Je me souviens bien de lui, mais cela fait quelque temps que je n'ai pas entendu parler de lui.","Il ne m'a jamais paru impudent, aussi je ne peux que supposer que son désir d'une gemme de Kor est noble.","Si vous souhaitez réellement l'une de ces gemmes, alors vous devez vous diriger vers le nord-ouest d'Ashenvale. Vous y trouverez les Profondeurs de Brassenoire à l'intérieur de ruines anciennes. Les nagas qui utilisent toujours ces gemmes se trouvent en bas. Ne partez pas <seul/seule>, <classe>."}, {"Apporter une Gemme de Kor à Thundris Windweaver à Sombrivage."}},
[1444] = {"De retour vers Fel’Zerul", {"Jammal'an le Prophète m'a condamné à être exilé du temple d'Atal'Hakkar. Je considérais autrefois que le grand prêtre était un chef digne.","Sa foi inflexible dans sa prophétie apportera la destruction aux Atal'ai. Si ce n'est pas déjà fait.","Lorsque j'ai exprimé des réserves, j'ai été traité d’infidèle et évincé du temple.","Les artefacts que vous avez apportés sont inoffensifs. Mais si Jammal'an a découvert le bon fétiche d'enchantement pour le rituel d'invocation, alors le monde court un grand péril..."}, {"Retourner voir Fel’Zerul à Stonard."}},
[1445] = {"Le temple d'Atal'Hakkar", {"Ainsi il a parlé de la prophétie de Jammal'an ?","Le chef spirituel des Atal'ai croit qu'une fois Hakkar revenu du Néant pour Azeroth, le dieu accordera l'immortalité à sa tribu.","Une excuse idiote pour provoquer une fin du monde prématurée, si vous voulez mon avis.","Mais vous dites que l'Exilé a parlé de fétiches enchantés ? Cela m'inquiète profondément. S'ils constituent la clé du rituel atal'ai et qu'ils sont à l'origine de ce chaos, nous devons comprendre leurs pouvoirs intrinsèques.","Aventurez-vous dans le temple, et emparez-vous des fétiches."}, {"Rassembler 20 Fétiches d'Hakkar et les apporter à Fel’Zerul à Stonard."}},
[1446] = {"Jammal'an le Prophète", {"Mes compagnons Witherbark n'apprécient pas beaucoup les étrangers qui rôdent près de chez eux.","Moi ? Je n'ai plus de maison. J'ai été exilé par mon peuple, la grande tribu Atal'ai du marais des Chagrins.","Notre chef spirituel, Jammal'an, avait ce qu'il appelait une prophétie. Il pense que l'invocation du dieu Hakkar apportera l'immortalité à la tribu Atal'ai.","Mais j'ai prôné la prudence. Et si cette prophétie n'était rien d'autre que de la manipulation ?","Je veux une revanche pour mon exil. Apportez-moi la tête de Jammal'an. Peut-être qu’ensuite mon peuple retrouvera son libre arbitre."}, {"L'Exilé atal'ai des Hinterlands veut la Tête de Jammal'an."}},
[1447] = {"Un diplomate a disparu", {"Ouais ! De toute évidence, vous n'êtes pas le <classe> le plus malin à venir à Stormwind, mais vous êtes sur le point d'être l'un des plus stupides à n'en jamais repartir.","Je vous ai présenté mes amis ?"}, {"Battre Dashel Stonefist à Stormwind."}},
[1448] = {"À la recherche du temple", {"Fut un temps, j'étais jeune et plein de vigueur comme vous, <classe>.","Mais à présent je suis vieux et incapable d'explorer le monde comme je le faisais autrefois. Ah, les choses que j'ai vues !","Mais il reste un mystère qui m'a toujours échappé. Pour ma dernière quête, la Ligue des Explorateurs m'a envoyé chercher le temple d'Atal'Hakkar. La rumeur veut qu'il se trouve dans le marais des Chagrins.","En raison de mon état fragile, j'ai parcouru les cieux à dos de gryphon, mais je ne l'ai jamais trouvé.","Pourriez-vous venir en aide à un vieux nain ? Peut-être aurez-vous plus de chance à pied."}, {"Brohann Caskbelly à Stormwind veut que vous recherchiez le temple d'Atal'Hakkar."}},
[1449] = {"Vers les Hinterlands", {"Comme j'en ai déjà parlé plus tôt, j'ai moi-même tenté une reconnaissance du temple depuis les airs. Mais en fait, je n'étais pas exactement seul.","J'ai voyagé sur un gryphon de la fameuse volière du Nid-de-l'Aigle dans les Hinterlands. Là, j’avais acheté les services d’un de leurs meilleurs pilotes. Mais il est tombé de sa monture, et directement dans le marais...","Mon vieil âge m'empêche de me souvenir du nom de ce pauvre type. Le maître de la volière devrait s'en souvenir, très certainement.","Partez-y et cherchez Falstad Wildhammer.","Nous devons découvrir ce qui est arrivé à ce pilote."}, {"Parler au haut-thane Falstad Wildhammer, au Nid-de-l'Aigle des Hinterlands."}},
[1450] = {"Le Maître des gryphons Talonaxe", {"Ainsi, vous êtes un des fameux coursiers au service des vieux schnocks de la Ligue des Explorateurs, hein ?","Bien, écoutez-moi, <classe>. Je ne sais pas avec qui votre vieux pote faisait des ronds dans le ciel d’Azeroth. Est-ce que j'ai la tête du Maître des gryphons Talonaxe ? Hein ?","Si vous êtes tellement <intéressé/intéressée> par les aléas du trafic aérien pourquoi n'allez-vous pas lui parler directement ? Si je vous prends à glandouiller dans le coin un peu plus longtemps, je vais vraiment vous faire transpirer.","À présent, bougez de là !"}, {"Parler au Maître des gryphons Talonaxe dans la Volière de Nid-de-l'Aigle"}},
[1451] = {"Rhapsody Shindigger", {"Rhapsody commençait vraiment à apprécier le goulot. Mais ne vous méprenez pas : tous les nains ici à la volière savent apprécier une bonne bière quand le bon moment arrive.","Mais le vieux Shindigger s'est habitué à la boisson comme le jour s'est habitué à la nuit. Quel dommage ! Personne ne savait voler comme lui.","Les premiers signes du problème sont apparus quand il est revenu en titubant de cette mission avec votre pote, Brohann. Tout s'est écroulé peu de temps après. Nous avons dû le laisser partir.","La dernière fois que j'ai entendu parler de lui, il avait monté un camp de brassage à l'est d'ici..."}, {"Chercher Rhapsody Shindigger dans les Hinterlands."}},
[1452] = {"Le Kocktail Kalimdor de Rhapsody", {"Hic !","Alors c'est ce vieux fou de Brohann qui vous envoie ? Bon sang ! On a eu une de ces aventures… Hic !","Si vous voulez que je vous raconte ce qui s'est passé au marais, je vais avoir besoin de quelque chose de costaud à boire. C'était pas beau à voir, vous comprenez ? Hic !","Je vais vous dire... Apportez-moi de quoi concocter un Kocktail Kalimdor et je vous en parlerai.","Je vais avoir besoin de gésiers de Roc de Tanaris... et voyons... de foies de Groddoc et de Ferpoil de Feralas...","Un bon Kocktail Kalimdor bien costaud... ça m'f'ra causer !"}, {"Rhapsody Shindigger dans les Hinterlands veut que vous lui rameniez 3 Gésiers de roc, 3 Foies de Groddoc et 3 Foies de Ferpoil."}},
[1453] = {"Les affaires de Récupérateurs SA en Désolace", {"Il y a beaucoup de travail à faire en Désolace, <nom>. C'est un endroit atroce, souillé par les centaures et autres monstruosités. Mais si vous voulez un bon petit travail honnête à la semaine, alors allez-y, parlez à Kreldig Ungor, et dites-lui que je vous envoie.","Vous le trouverez planqué à la Combe de Nijel, un petit camp plutôt douillet niché dans les montagnes dans la partie nord de Désolace. Allez simplement à l'est si vous venez des Serres-Rocheuses. C'est facile à rater, alors faites attention."}, {"Parler à Kreldig Ungor en Désolace."}},
[1454] = {"L'épave de Karnitol", {"Ma première commande concerne le mage Karnitol, une sorte de noble de Stormwind. Il semblerait que son bateau ait été attaqué par des géants des mers tandis qu'il naviguait au large de la côte, dans le nord-ouest de Désolace. Ses serviteurs ont essayé de ramener sur la rive l'un de ses coffres les plus précieux, mais lorsque les géants ont attaqué en masse, il a préféré abandonner le coffre et se téléporter, avec ses serviteurs, en sécurité.","Il veut que nous retrouvions le coffre et son contenu, et que nous le lui rapportions à Stormwind."}, {"Retrouver le Coffre de Karnitol en Désolace."}},
[1455] = {"L'épave de Karnitol", {"Vous prenez un moment pour comprendre comment le coffre a été ouvert, et vous remarquez, sur les côtés et le dessus du coffre, de longues et profondes entailles. Elles révèlent que quelque chose d'autre qu'un humain ou qu'un géant s'y est intéressé. Les marques ne semblent être celles d'une épée ou d'une dague.","Après avoir fouillé le coffre, vous constatez qu'il est complètement vide.","N'ayant plus rien d'autre à faire, vous refermez le coffre et vous préparez à partir. Kredlig va être déçu."}, {"Retourner voir Kreldig Ungor en Désolace."}},
[1456] = {"L'épave de Karnitol", {"Intriguant.","Bien, il se peut que tout espoir ne soit pas perdu.","Le long de cette même côte, mais plus au nord, près de l'île de Ranazjar, se trouvent des ruines sous-marines datant des elfes de la nuit. Une tribu de nagas, les Ondulame, s'y est établie ! Je pense que c'est ce notre meilleure chance.","Si un groupe de leurs éclaireurs a été envoyé aussi loin au sud, alors cette tribu doit être puissante. Pour retrouver les possessions de Karnitol, je m’attaquerais en priorité aux nagas les plus forts : les chasse-marées et les sorcières des mers."}, {"Retrouver la Sacoche de Karnitol et la rapporter à Kreldig Ungor en Désolace."}},
[1457] = {"L'épave de Karnitol", {"Rapportez les biens de Karnitol à Roetten à Ironforge, et il vous paiera pour ce travail bien accompli.","Ha ! Ha ! Si vous ne faites pas attention, il sera tellement satisfait de vous qu'il vous demandera de nous rejoindre à plein temps.","Mais à nouveau, bon travail, <nom>.","Soyez <prudent/prudente> sur le chemin du retour, et ne prenez pas tout votre temps ; Karnitol n'est pas le plus patient des hommes, mais il reste très généreux néanmoins."}, {"Apporter la Sacoche de Karnitol à Roetten Stonehammer à Ironforge."}},
[1458] = {"Des réactifs pour les Récupérateurs", {"L'une de mes tâches ici consiste à collecter des réactifs pour Leftwitch, un mage gnome de Kharanos. Roetten semble penser que c'est de l'argent facilement gagné puisque nous sommes déjà sur place. Il se soucie peu du fait que nous sommes si démunis.","Les premiers objets de sa liste devraient être assez faciles à trouver, et ce sera de l'argent gagné pour quiconque m'aidera.","J'ai besoin de cornes et de griffes des satyres Fielfurie qui vivent à l'est d'ici. Rapportez-m'en et nous poursuivrons la liste."}, {"Apporter 10 Griffes de Fielfurie et 10 Cornes de Fielfurie à Kreldig Ungor en Désolace."}},
[1459] = {"Des réactifs pour les Récupérateurs", {"Ensuite, sur la liste de Leftwitch, on trouve… on trouve… Oh !","Relativement facile, mais il faut décrypter ses directives. Il a besoin de venin de scorpashi et de peau de kodo. Mais il est précisé que le venin doit provenir de n'importe quel scorpashi, et que la peau doit être celle d'un vieux kodo, pas d'un kodo mourant ou d'un ancien kodo.","Les mages, je vous dis pas, les trucs qu'ils veulent parfois... c'est tout simplement bizarre."}, {"Apporter 7 Fioles de venin de scorpashi et 3 Peaux de vieux kodo à Kreldig Ungor, en Désolace."}},
[1462] = {"Le sapta de terre", nil, nil},
[1463] = {"Le sapta de terre", nil, nil},
[1464] = {"Le sapta de feu", nil, nil},
[1465] = {"La recherche de Vahlarriel", {"Tandis que vous continuez à fouiller le chariot, vous découvrez non seulement un pendentif mais également des traces de pas qui se dirigent vers le sud. Au loin, vous distinguez de la fumée s'élevant d'une grande forteresse. La fumée provient d'un feu entretenu, il ne s'agit pas de bâtiments qui brûlent, et votre instinct vous dit que les Malem, si tant est qu'ils soient toujours en vie, ont été emmenés dans cette direction.","Vous estimez que Vahlarriel, à la Combe de Nijel, devrait voir le pendentif et savoir qu'il se trouve des signes de vie au sud."}, {"Rapporter le Pendentif de Malem à Vahlarriel Demonslayer en Désolace."}},
[1466] = {"Des réactifs pour les Récupérateurs", {"Ce sont les derniers objets à rassembler. Cela vous prendra aussi un peu plus de temps que pour les précédents.","Bien, assez parlé, commençons. Leftwitch dit qu'il a besoin de : dix cervelles de mange-mana, de chasseur de mage ou de chasseur ley ; dix ailes du néant prélevées sur une vierge du néant, une soeur du néant ou une ensorceleuse du néant. Et... dix fioles de sang de garde funeste provenant d'un capitaine ou seigneur de la garde funeste.","C'est assez simple, non ? Non, sérieusement, si vous laissez tomber cette quête, je comprendrai."}, {"Apporter 10 Cervelles de gangrechiens, 10 Ailes du Néant et 10 Fioles de Sang de garde funeste à Kreldig Ungor, en Désolace."}},
[1467] = {"Des réactifs pour Récupérateurs SA", {"Pour être <payé/payée>, vous devez retourner à Ironforge et parler à Roetten en personne. Il aimera être débarrassé de la caisse de Leftwitch, de toute façon. Remettez-lui simplement ceci et ça devrait suffire pour avoir votre récompense.","Et ne vous en faites pas, je ferai passer un mot positif à votre sujet lorsque je reviendrai. Je ne devrais plus rester longtemps en Désolace."}, {"Rapporter la Caisse de Leftwitch à Roetten Stonehammer à Ironforge."}},
[1468] = {"La Semaine des enfants", {"Vous voulez aider l'orphelinat ? Soyez <béni, mon ami/bénie> mon amie;. Votre aide durant la Semaine des enfants sera inestimable.","Prenez ce sifflet. Vous ne pourrez vous en servir que durant sept jours, en fait, pendant toute la Semaine des enfants. Quand vous l'utiliserez, vous pourrez bavarder avec l'enfant que vous avez accepté de surveiller. Si vous l'utilisez une seconde fois, il renverra l'enfant.","Je vous en prie, <nom>, utilisez le sifflet pour faire la connaissance de votre protégé ! Je suis sûre que l'enfant est très impatient de vous rencontrer."}, {"Utilisez le Sifflet d'orphelin humain pour parler à l'enfant que vous protégerez pendant la Semaine des enfants."}},
[1469] = {"L'histoire de Rhapsody", {"Avant le vol, je me suis bu un plein tonneau de bière. Hic !","Et ça a été un de ces vols ! Volé de-ci de-là avec Brohann qui me suivait. Mais pas vu de temple en tout cas. Hic !","Je veux dire, j'ai pas vu de temple, jusqu'à ce que je sois trop parti et que je tombe de ma monture. J'ai plongé directement dans la flotte et j’ai été aspiré. Me suis trouvé entouré par un tas de trolls à la mine patibulaire. Vous pensez bien que j'ai nagé très vite pour partir ! J'ai quand même ramassé un souvenir troll. Tenez, apportez ça à Brohann et dites-lui que son temple est là-bas, en bas, de fait. Hic !"}, {"Apporter le Fragment de tablette atal'ai à Brohann Caskbelly à Stormwind."}},
[1470] = {"Lever le voile", {"Salutations, enfant. Je peux voir que vous êtes encore novice dans les arts du démoniste, le pouvoir échappant toujours à votre contrôle. Je peux aussi sentir votre désir d'apprendre.","Et vous apprendrez.","La force ne réside pas dans l'ignorance. La connaissance est notre pouvoir le plus puissant. Par l'application du savoir, nous pouvons contrôler les magies et les êtres chaotiques du vide, et les soumettre à notre volonté.","Je peux vous montrer comment soumettre un servant, mais d'abord vous devez m'apporter trois crânes pris sur les squelettes Cliquethorax qui rôdent près de la forge abandonnée."}, {"Apporter 3 Crânes de Cliquethorax à Venya Marthand."}},
[1471] = {"Le lien", {"Vous avez sûrement vu le cercle d'invocation en dessous. C'est le seul endroit d’où vous pourrez tirer un marcheur du Vide libre.","En utilisant ces runes, vous pourrez l'attirer ici. Vous devrez alors le dominer par la force des armes et de la magie. Lorsqu'il sera assujetti, il sera lié à vous. Venez alors me voir, et je vous expliquerai le sort d'invocation, pour que vous puissiez vous en servir, pour quelque objectif que ce soit."}, {"Utiliser les Runes d'invocation pour invoquer et soumettre un marcheur du Vide, puis rapporter les Runes d’invocation à Carendin Halgar, au Temple des Damnés."}},
[1472] = {"La dévoreuse d'âmes", {"Une succube est une dévoreuse d'âmes, une destructrice de cœurs et une tentatrice d'hommes. Une créature machiavélique à l’esprit très singulier.","Elle ne peut pas être amenée dans notre monde sans une impulsion.","Aussi nous utilisons sa propre force contre elle. Face à des cœurs d'hommes bons, d'hommes purs, d'hommes aimants, la succube est incapable de résister, et elle viendra jusqu'à notre monde. Alors vous pourrez la dominer, et l’utiliser.","Parlez à mon assistant, Godrick Farsan, à l'intérieur du temple. Il vous aidera à trouver un appât."}, {"Parler à Godrick Farsan au Temple des damnés."}},
[1473] = {"La créature du Vide", {"Il semble acquis que vous êtes <prêt/prête> à apprendre à invoquer un marcheur du Vide.","Un être créé par le chaos qui l'entoure. Si vous en invoquez un, mais que vous ne pouvez pas le contrôler, vous serez <annihilé/annihilée>.","Le premier marcheur du Vide a été invoqué par Egalin, et le procédé est expliqué dans son grimoire.","Malheureusement, il nous a été volé par un agent de la Croisade écarlate, qui l'a caché dans sa base, dans une tour de l'ouest. Rapportez-le-moi, et je vous transmettrai cette connaissance."}, {"Retrouver le Grimoire d'Egalin et l'apporter à Carendin Halgar au Temple des damnés."}},
[1474] = {"Le lien", {"Lancez un sort avec ces cœurs au cercle d'invocation. Vous ouvrirez ainsi le chemin à une succube. Vous avez de grands pouvoirs à présent, et vous en aurez besoin pour la soumettre.","Attention à ses charmes, <nom>, beaucoup d'esprits forts ont cédé devant eux. Une fois qu'elle sera battue, la succube retournera au Néant, et je vous apprendrai le sort pour l'invoquer à volonté."}, {"Utiliser les Coeurs purs pour invoquer et soumettre une succube, puis rapporter les Coeurs purs à Carendin Halgar, au Temple des Damnés."}},
[1475] = {"Dans le temple d'Atal'Hakkar", {"D'après la légende, le temple d'Atal'Hakkar est un lieu saint dédié au dieu ancien, Hakkar l'Ecorcheur d'âmes. Habité par une ignoble tribu de trolls, les Atal'ai, on dit qu'il est impossible d'y entrer.","Ce temple est à présent sous les eaux du Bassin des larmes, et récupérer les artefacts sera d'autant plus difficile. Mais c'est pourtant ce que j'ai besoin que vous fassiez, <nom>.","Aidez-moi, en récupérant les Tablettes atal'ai intactes. Apportez-les-moi, et je deviendrai un héros aux yeux de la Ligue des Explorateurs. Nous partagerons ensemble cette gloire."}, {"Rassembler 10 Tablettes atal'ai pour Brohann Caskbelly à Stormwind."}},
[1476] = {"Des cœurs purs", {"Des hommes d'honneur et de bonté ? Est-ce qu'un tel homme existe seulement ?","Cela a nécessité une recherche assez longue, mais grâce à quelques fragments d’histoires entendues par-ci par-là, je suis parvenu à trouver deux hommes qui devraient faire l'affaire.","Le premier se nomme Dalin Forgewright. Il a consacré sa vie aux réfugiés de Lordaeron, ceux qui sont rejetés par leurs pairs au mur de Greymane.","L'autre s'appelle Comar Villard, un homme qui reste fidèle à son premier amour, et qui court les Paluns à la recherche de sa femme.","Remettez leurs cœurs à Carendin. Mais auparavant, laissez-moi vous montrer votre gibier."}, {"Apporter les Cœurs de Dalin Forgewright et de Comar Villard à Carendin Halgar dans le Temple des Damnés."}},
[1477] = {"Fournitures vitales", {"Les mages conduisent des recherches au Rempart-du-Néant dans les Terres foudroyées, des recherches vitales à notre compréhension des Portes des ténèbres. Ils ont besoin presque constamment de marchandises, mais notre ligne d'approvisionnement vers Rempart-du-Néant est fragile.","Vous devez vous assurer que les provisions parviennent bien au Rempart-du-Néant. Allez voir le maître de guet Sorigal à Darkshire, et faites ce qu'il demande. Cette quête vous conduira dans des terres hostiles, <nom>. Soyez <prêt/prête>."}, {"Parler au Maître de guet Sorigal."}},
[1478] = {"Les invocations de Halgar", {"Ah, <nom> ! Je viens juste de recevoir un mot de Carendin Halgar disant qu'il voulait vous voir pour continuer l'entraînement.","Vous devriez être <fier/fière> de vous, <nom>. Ce n'est pas tous les jours que Maître Halgar daigne instruire personnellement un jeune démoniste.","Vous le trouverez dans le Quartier de la magie à Undercity, près du Temple des damnés."}, {"Parler à Carendin Halgar à Undercity."}},
[1479] = {"Le Rameau des Éternels", {"J'ai entendu parler d'histoire sur les elfes de la nuit, qui ont leur banque dans un arbre, mais qui ressemble à un ours ! C'est génial ! C'est chouette, il paraît que les elfes de la nuit aiment la nature et ce genre de choses. Et les ours, c'est trop terrible !","Vous voulez bien m'emmener voir l'arbre-ours à Darnassus, s'il vous plaît ? Y'a un bateau qui peut nous amener juste à côté, ça ne devrait pas prendre trop de temps. S'il vous plaaaaaaaaaaaît !"}, {"Conduire l'orphelin à la banque de Darnassus. La banque est creusée dans le tronc d'un arbre connu sous le nom du Rameau des Éternels."}},
[1480] = {"Le corrupteur", {"La peau déchirée sent le pourri. Malgré son aspect sec et fragile, elle est remarquablement souple.","En la déroulant, vous voyez clairement une sorte de langage écrit en rouge sur l'un des côtés. Un symbole est apposé en évidence en bas de la peau ; vous supposez qu’il s’agit d’une signature.","Peut-être Maurin pourrait-il vous en dire plus ?"}, {"Apporter une Peau de démon écorché à Maurin Bonesplitter en Désolace."}},
[1481] = {"Le corrupteur", {"Je pense pouvoir trouver ce démon si j'ai les réactifs adéquats.","S'il est effectivement aussi puissant que je le suspecte, je vais avoir besoin de certains objets disponibles sur des créatures mortelles. Mais vous agissez au nom du Chef de guerre, aussi je suis certain que vous accepterez ma quête.","D'abord, je vais devoir briser toutes les protections qu'il aurait contre les visions. Pour cela, j'aurais besoin d'un scalp de satyre. Mais pas de n'importe quel satyre. Il doit provenir d'un Traqueur des ténèbres Fielfurie.","Cherchez cette tribu dans les ruines anciennes au nord-est de Désolace."}, {"Apporter un Scalp de Traqueur des ténèbres à Maurin Bonesplitter en Désolace."}},
[1482] = {"Le corrupteur", {"Un cristal d'oracle ferait parfaitement l'affaire. Ils sont conçus par les nagas et utilisés par leurs oracles.","Au nord-ouest d'ici, le long de la côte, se trouve un vaste récif de corail que les nagas considèrent comme leur demeure, lorsqu'ils viennent près du rivage. Vous trouverez leurs oracles là-bas. Les Ondulames sont dangereux et ne réagiront pas amicalement à votre intrusion : soyez <prudent/prudente> et hâtez-vous.","Quand vous reviendrez, nous verrons à localiser le seigneur-démon Azrethoc."}, {"Apporter un Cristal d'oracle à Maurin Bonesplitter, en Désolace."}},
[1483] = {"Ziz Fizziks", {"La KapitalRisk a détourné un grand nombre de savants gobelins, mais de temps en temps, nous avons l’occasion de les leur reprendre.","Il y a quelque temps, l'équipe de la KapitalRisk des Serres-Rocheuses a embauché Gerenzo Wrenchwhistle. Un gnome ! Leur autre ingénieur, Ziz Fizziks, en a été tellement dégoûté qu'il a essayé de trouver un nouveau travail auprès du Syndicat des Artisans. Je lui ai confié quelques travaux à faire, mais il a peut-être besoin de renfort.","Peut-être que vous pourriez voir ça pour moi ? On peut le trouver à la lisière ouest de la combe des Cisailles."}, {"Parler à Ziz Fizziks dans la Combe des Cisailles."}},
[1484] = {"Le corrupteur", {"Cette créature est beaucoup plus dangereuse que je ne l'avais cru. Il est certain que Takata remet en cause mes capacités à battre une telle créature, et ce serait insulter son honneur que de ne pas l'informer de ce qui vient juste de se passer.","Parlez à l'homme d'épée, dites-lui que vous voulez tuer le démon s'il l’autorise, mais n'insistez pas trop. Ce serait mieux qu'il croit que c'est son idée."}, {"Parler à Takata Steelblade en Désolace."}},
[1485] = {"Les vils quasits", {"Ces démonistes qui sont passés avant nous ont engendré la méfiance parmi la Horde, et bien que nous soyons tolérés, nous ne sommes pas aimés.","Cela n'a pas poussé les démonistes à s’unir pour autant, et beaucoup choisissent de vivre reclus, marinant seuls dans leur magie. Je ne vais pas tenter de vous faire choisir un chemin ou l'autre, mais seulement vous aider pendant votre progression.","Avant que je vous apprenne l'art de l'Invocation, apportez-moi six têtes de vils quasits. Vous en trouverez dans la grotte au nord-est."}, {"Apporter 6 Têtes de vils quasits à Ruzan."}},
[1486] = {"Les peaux communes", {"Tandis que Naralex s'enfonçait toujours plus profondément dans son cauchemar, des bêtes étranges émergèrent de sous les Tarides, jusqu'aux Cavernes des lamentations.","Ces créatures perverties possèdent des propriétés étranges, comme venues d'un autre monde. Bien qu'elles soient maléfiques de nature, je pense qu’il y a des avantages à tirer de leur présence ici. Leur peau peut certainement être travaillée.","Si vous vous considérez à la hauteur, partez dans les cavernes, en dessous, et trouvez des écailles communes."}, {"Napalk dans les Cavernes des lamentations veut 20 Peaux communes."}},
[1487] = {"L'éradication des Déviants", {"Naralex poursuivait un noble but.","Notre grand chef aspirait à entrer dans le Rêve d'émeraude pour faire reverdir ces terres arides, qui étaient autrefois une forêt luxuriante. Mais quelque chose s'est mal passé.","Le rêve de Naralex s'est transformé en cauchemar et des créatures perverties ont commencé à envahir les cavernes.","Des Disciples de Naralex tentent de le réveiller, mais mon souci est de débarrasser les cavernes de ces bêtes maléfiques.","Bravez les cavernes, <nom>, et éradiquez cette vermine étrangère."}, {"Ebru, des Cavernes des lamentations, veut que vous tuiez 7 Ravageurs Déviant, 7 Vipères Déviant, 7 Glacials Déviant et 7 Crocs-d'effroi Déviant."}},
[1488] = {"Le corrupteur", {"Bien que je ne sache rien de ce seigneur Azrethoc, je suis d'accord avec Maurin. Il faut l'arrêter, surtout s'il tente de gagner du pouvoir au sein de la Lame ardente pour faire avancer ses objectifs.","Vous nous avez déjà exprimé votre souhait d’aider le Chef de guerre, et j'aimerais vous charger de réunir un groupe pour aller tuer ce seigneur-démon et son serviteur démoniste.","Lorsque vous serez <prêt/prête>, partez pour le sud, et revenez me voir lorsque ce danger sera éliminé."}, {"Tuer le seigneur Azrethoc et Jugkar Grim'rod puis retourner voir Takata Steelblade en Désolace."}},
[1489] = {"Hamuul Runetotem", {"Les carapaces que vous m'avez apportées portent une souillure que je n'ai jamais vue auparavant. Je pense que l'eau des oasis en est la cause, et je peux ressentir le désordre qu'ils causent à cette terre. C'est comme si la terre rêvait et ne pouvait s'éveiller.","Les Cavernes des lamentations doivent être à l’origine de cette souillure. Mais avant de vous y rendre, allez parler à Hamuul Runetotem, le druide ancien. Je lui ai envoyé un message décrivant les événements dans les Tarides. C'est un sage, et il peut vous donner de bons conseils.","Vous le trouverez à Thunder Bluff, sur la Cime des Anciens."}, {"Parler à Hamuul Runetotem."}},
[1490] = {"Nara Wildmane", {"J'ai pénétré sur la terre des rêves et j’ai vu un groupe de druides dans les Cavernes des lamentations. J'ai vu qu’autrefois leurs buts étaient nobles, mais qu'ils ont perdu le chemin depuis. Ils sont à présent serviteurs de la souillure qui sourd dans les Tarides.","Je ne suis plus jeune et les forces me manquent. Je ne pourrai pas rester plus longtemps sur cette terre des rêves pour en apprendre davantage sur ces druides souillés. Mais Nara est jeune et son esprit est féroce. Elle est restée à rêver tandis que je me retirais.","Parlez-lui. Elle est dans la tente, là-bas."}, {"Parler à Nara Wildmane."}},
[1491] = {"Les potions d'intelligence", {"Ainsi les cornes de raptors n'ont pas si bien marché que cela ! Mais ce n'est pas grave, personne ne devient riche au premier essai ! Et de plus, je pense avoir trouvé encore mieux pour mes potions d'intelligence !","Il existe une substance appelée essence de lamentation, qui est sûrement responsable de l’état de certaines plantes et bêtes des Tarides. Trouvez-en, et je l'essaierai dans mes boissons !","J'ai entendu dire que cette essence apparaît sur des créatures à l'état d'ectoplasme vivant dans les Cavernes des lamentations, à l'ouest. Chassez ces créatures et récupérez l'essence."}, {"Apporter 6 doses d'Essence de lamentation à Mebok Mizzyrix, à Ratchet."}},
[1492] = {"Maître des quais Dizzywig", {"J'ai besoin d'envoyer une caisse de potions, de réactifs et d’objets divers à mes associés dans l'Ancien Monde. J'ai en fait assez peu de temps et je ne peux pas vérifier moi-même qu'elle est bien embarquée aux docks de Ratchet. De plus, la dernière caravane est déjà partie.","Si vous pouvez, apportez-la au Maître des quais Dizzywig. Il veillera à ce qu'elle soit chargée sur le prochain bateau en partance pour la crique des Flots noirs."}, {"Apporter la Caisse sûre au Maître des quais Dizzywig à Ratchet."}},
[1498] = {"La voie de la défense", {"En tant que guerrier, l'une de nos tâches les plus cruciales est… la protection. Nous sommes le bouclier de la Horde, et nous gardons nos semblables les plus faibles à l'arrière. Si vous voulez rejoindre nos rangs, alors vous devez me prouver votre ardeur.","Partez pour les falaises du Tonnerre en Durotar, où vivent les foudroyants et les lézards-tonnerre. Chassez ces bêtes ! Montrez-moi que vous êtes plus <fort/forte> qu'eux ! Affrontez leurs assauts et apportez-moi un tas d'écailles roussies comme preuve.","Faites cela, et alors je deviendrai votre maître."}, {"Apporter 5 Ecailles roussies à Uzzek au Poste de garde extérieur dans les Tarides."}},
[1499] = {"Les vils quasits", {"Zureetha envoie des novices comme vous réduire le nombre des créatures de la caverne près de laquelle vous avez combattu les vils quasits.","Je pense qu'il serait bon pour vous de l'aider et ainsi de gagner de l'expérience. Dites-lui également que vous avez participé à la résolution du problème posé par ces diablotins, elle aura probablement une récompense pour vous."}, {"Parler à Zureetha Fargaze."}},
[1501] = {"La créature du Vide", {"Il reste si peu de démonistes… Nous risquons beaucoup, mais cela en vaut la peine. Vous devez rapidement atteindre la pleine possession de vos capacités pour être utile à la Horde et au Chef de guerre.","Il est temps pour vous d'apprendre un nouveau sort d'invocation.","D'abord, vous devez récupérer la Tablette de Verga, qui a été volée par des démonistes de la Lame ardente et qui est à présent détenue dans leur antre du Rocher du crâne à l'est de la ville. Une fois que j'aurai la tablette, je vous montrerai les runes nécessaires à l'invocation d'un marcheur du Vide."}, {"Récupérer la Tablette de Verga pour Gan'rul Bloodeye à Orgrimmar."}},
[1502] = {"Thun'grim Firegaze", {"Il est vital que l'arme d'un guerrier soit de qualité. Et s'il y a bien un orc capable de concevoir des armes de qualité, c’est Thun'grim Firegaze. C'est un ermite qui vit au sommet des collines à l'est de la Croisée.","Si vous cherchez une arme, <guerrier/guerrière>, alors cherchez Thun'grim. Pour le trouver, voyagez d'abord vers la Croisée, vers l'ouest. Puis tirez droit à l'est, dans les collines. Son camp est au sommet d'une d'entre elles."}, {"Parler à Thun'grim Firegaze dans les Tarides."}},
[1503] = {"Acier forgé", {"Je vais vous donner une arme, <nom>, mais d'abord vous devez me venir en aide. Les Tranchecrins du nord-ouest sont venus dans mon camp il y a plusieurs nuits. Ils ne venaient pas m’affronter, mais ils savaient que ma vue est mauvaise. Quelques-uns m'ont distrait, en grognant, tandis que les autres me dévalisaient ! Les lâches !","Ils ont emporté un coffre de fer contenant des barres d'acier forgé. Si vous pouvez les récupérer, alors je vous donnerai l'arme idéale.","Trouvez le coffre en fer dans le camp des hurans et rapportez-moi les barres d'acier forgé."}, {"Apporter les Barres d'Acier forgé à Thun'grim Firegaze dans les Tarides."}},
[1504] = {"Le lien", {"À présent, observez la Tablette de Verga, car elle contient les glyphes nécessaires à l'invocation. Apportez-les au cercle sous la tente de Neeru Fireblade de l'autre côté de la grotte. Vous serez alors capable d'invoquer un marcheur du Vide.","Vous devez le battre en utilisant tous les moyens physiques et magiques que vous avez à votre disposition. Une fois qu'il sera battu, revenez me voir avec les glyphes d'invocation et je vous apprendrai comment soumettre la créature."}, {"Utiliser les Glyphes d'invocation pour invoquer et soumettre un marcheur du Vide, puis rapporter les Glyphes d'invocation à Gan'rul Bloodeye, à Orgrimmar."}},
[1505] = {"Le vétéran Uzzek", {"La Horde honore ses guerriers, car leur force n'est pas remise en question. Mais les grands guerriers sont également de grands étudiants.","Cherchez Uzzek. C'est le vétéran de maintes batailles et il enseigne à présent l'art du combat à de jeunes guerriers. Vous le trouverez au sud du Poste de garde extérieur, la garnison orc à la frontière entre les Tarides et Durotar à l'est."}, {"Parler à Uzzek au Poste de garde extérieur."}},
[1506] = {"Les invocations de Gan'rul", {"Vous là ! <nom>, c'est ça ?","J'espère que votre programme n'est pas trop chargé, car Gan'rul veut vous voir tout de suite. Vous le trouverez avec les démonistes seniors à la Faille de l'ombre.","D'après mes souvenirs de Gan'rul, le fait qu'il vous appelle est soit une très bonne chose, soit une très mauvaise."}, {"Parler à Gan'rul Bloodeye à Orgrimmar."}},
[1507] = {"La dévoreuse d'âmes", {"Il est temps pour vous d'en apprendre plus sur les magies d'invocation. Vous allez apprendre à invoquer une succube, une créature dotée de pouvoirs mentaux incroyables. Elle peut apaiser les esprits faibles jusqu'à ce qu'ils tombent en son pouvoir. Mais ne craignez rien ! Je sais que vous êtes capable de relever le défi.","Une succube peut être invoquée que si elle est appâtée avec un objet qui représente une grande dévotion et un grand amour.","Une suggestion ? Parlez à Cazul... Il semble qu'il n'y ait pas grand-chose que le vieux démoniste ignore."}, {"Parlez à Cazul à la Faille de l'ombre."}},
[1508] = {"Cazul l'aveugle", {"Hmm… mais bien sûr, ce n'est pas la raison pour laquelle vous êtes <venu/venue> me parler.","Si Gan'rul vous envoie, ce doit être parce que vous devez invoquer une succube, et que vous avez besoin d'un objet pour la séduire.","Hmm… si vous pouvez faire confiance à un homme aveugle pour vous diriger, <nom>, alors écoutez. Zankaja vit à l'ouest de la tour des Cavaliers célestes, dans les hauteurs d'Orgrimmar. Elle attend le retour de son compagnon.","Allez la voir."}, {"Parler à Zankaja à Orgrimmar."}},
[1509] = {"Des nouvelles de Dogran", {"Mon compagnon, Dogran, a été récemment envoyé à la Croisée pour y accomplir son devoir. Il avait obtenu une promotion, et il était très fier.","Moi, j'étais triste, mais je n'en ai rien laissé paraître. Et j'étais inquiète, aussi. Les Tarides sont une région bien plus dangereuse qu'Orgrimmar, et je craignais le pire.","À présent, cela fait une semaine qu'il aurait dû renter, et je n'ai toujours reçu aucune nouvelle...","Peut-être pourriez-vous vous rendre à la Croisée et voir s'ils ont des nouvelles de lui ? Il travaillait avec Gazrog, qui devrait toujours y être assigné."}, {"Parler à Gazrog à la Croisée."}},
[1510] = {"Des nouvelles de Dogran", {"Il était en poste au Camp Taurajo, où nous essayions de nous débarrasser des hurans. Il a dirigé quelques grunts sur l'un de leurs campements, mais il a été alors grièvement blessé. Les autres sont revenus et ont essayé de lui porter secours. Mais il allait très mal, ses blessures saignaient et ils hésitaient à le déplacer avant de le panser correctement.","Ils ont envoyé un message ici, demandant un breuvage de soin des trolls de Malaka'jin, au pied des Serres-Rocheuses. Nous avons envoyé quelqu'un pour le chercher, mais personne n'est encore revenu."}, {"Parler à Ken'zigla, au village de Malaka'jin."}},
[1511] = {"Breuvage de Ken'zigla", {"Je pensais bien que quelqu'un d'autre viendrait prendre le breuvage, alors je l'ai préparé à toutes fins utiles.","Tenez, prenez, <nom>. N’oubliez pas : ne lui donnez que la moitié ! Cela lui donnera suffisamment de force pour qu'il puisse bouger sans perdre tout son sang. Laissez passer une nuit, puis donnez-lui l'autre moitié.","Voilà, vous feriez bien de l'apporter à Logmar au Camp Taurajo. D'après ce que m'a dit l'autre qui est passé avant, il devrait avoir une idée de l'endroit où Dogran se trouve."}, {"Apporter le Breuvage de Ken'zigla au grunt Logmar, à Camp Taurajo."}},
[1512] = {"Le cadeau de l'amour", {"Zankaja est jeune et belle. Etre enchaînée à un invalide… Je ne permettrai pas qu'elle souffre de cette injustice.","Laissez-moi, <nom>. Et merci. Je peux ainsi terminer ma vie dans une glorieuse bataille, et non comme un légume.","Et je vous en prie, je souhaiterais que vous ne disiez rien à ma compagne... Je ne veux pas quelle se sente coupable."}, {"Rapporter le Pendentif de poussière à Gan'rul Bloodeye à Orgrimmar."}},
[1513] = {"Le lien", {"Apportez cela au cercle d'invocation dans la tente de Neeru, <nom>. Invoquez une ignoble succube, et dominez-la. Comme toujours cependant, soyez <prudent/prudente>, car la fierté et une trop grande confiance en soi ont terrassé de nombreux démonistes prometteurs.","Une fois que vous l'aurez vaincue, venez me rendre le pendentif, pour que je puisse donner des nouvelles à Zankaja."}, {"Utiliser le Pendentif de Dogran pour invoquer et soumettre une succube, puis rapporter le Pendentif de Dogran à Gan'rul Bloodeye, à Orgrimmar."}},
[1514] = {"La fleur-de-vent corrompue", nil, nil},
[1515] = {"La captivité de Dogran", {"Vous êtes <arrivé/arrivée> jusque-là, alors si vous voulez voir la fin, je ne vais pas vous arrêter. Cependant, je ne peux pas vous fournir de forces supplémentaires, vous êtes <seul/seule>.","Dogran est prisonnier dans un camp de hurans au nord d'ici. Cherchez une paire de huttes. Il sera surveillé de près... À condition qu’ils ne l'aient pas encore tué et qu’il n’ait pas succombé à ses blessures.","Bonne chance."}, {"Apporter le Breuvage de Ken'zigla à Dogran dans les Tarides."}},
[1516] = {"L'appel de la terre", {"Le temps est venu, jeune <classe>. Vous êtes <devenu plus fort/devenue plus forte> et votre esprit est aussi endurant que la terre. Votre choix de suivre la voie du chaman montre votre sagesse, avant même de devoir faire vos preuves.","L'élément de la terre guidera votre destinée, et fera partie intégrante de votre être, si vous y êtes <prêt/prête>. Mais vous devez vous présenter devant la terre elle-même. Si vous êtes <prêt/prête>, vous verrez des choses dont seuls les chamans ont connaissance.","Cherchez les traqueurs corrompus au nord, et prenez-leur deux de leurs sabots. Revenez alors me voir, et nous parlerons de votre avenir."}, {"Apporter 2 Sabots de traqueur corrompu à Canaga Earthcaller dans la Vallée des épreuves."}},
[1517] = {"L'appel de la terre", {"Un sapta est une boisson qui lie notre esprit aux éléments. Imprégnée de magie, cette potion permet aux esprits forts de percevoir les éléments comme nul autre ne le peut. Votre esprit contactera l'élément pour lequel le sapta a été créé, que seuls de rares chamans peuvent confectionner.","Il faut toujours boire le sapta dans un endroit sacré, jamais ailleurs, ne l’oubliez pas.","Cherchez le rocher des esprits au sud-ouest d'ici, au bout du chemin secret. Buvez-y votre sapta et... et vous verrez."}, {"Trouver le Rocher des esprits et boire le Sapta de terre."}},
[1518] = {"L'appel de la terre", {"Prenez ce quartz brut et apportez-le à Canaga Earthcaller. Il attestera de notre rencontre. Il créera un totem pour vous, et ce caillou en sera le cœur. Aussi petit qu'il puisse paraître, vous comprendrez avec le temps que la taille importe peu, et que même la plus petite des choses peut survivre à bien d'autres.","Soyez <patient/patiente>, <nom>. Restez <fort/forte>. Et recherchez la sagesse. C'est ce que la terre attend de vous. À la fin, vous ne ferez plus qu'un avec elle. Respectez-la comme elle vous respecte."}, {"Apporter le Quartz brut à Canaga Earthcaller dans la Vallée des épreuves."}},
[1519] = {"L'appel de la terre", {"Il est temps à présent, jeune <classe>. Votre corps s’est aguerri et votre esprit est aussi endurant que la terre. Je le vois, vous possédez déjà la sagesse.","La terre guide notre peuple, et nous parle, de la même manière qu'elle vous parlera lorsque le temps sera venu. Mais vous devez être <mis à l'épreuve/mise à l'épreuve>. Si vous êtes <prêt/prête>, alors vous verrez des choses que nous seuls connaissons.","Partez à la recherche des chamans Dos-hirsute dans leur ravin à l'est, et procurez-vous le baume qu'ils utilisent lors de leurs rituels. Ne revenez me voir que lorsque vous en aurez suffisamment pour votre propre potion."}, {"Apporter 2 Applications de Baume rituel au Prophète Ravenfeather au Camp Narache."}},
[1520] = {"L'appel de la terre", {"Un sapta est une boisson qui lie notre esprit aux éléments. Imprégnée de magie, cette potion permet aux esprits ouverts de voir les éléments comme personne d’autre ne peut les voir. Votre esprit sera lié à jamais à l'élément pour lequel le sapta a été créé, que seuls de rares chamans peuvent confectionner.","Il faut toujours boire le sapta dans un endroit sacré, jamais ailleurs.","Cherchez l'autel du chaman à l'est d'ici et buvez-y le sapta."}, {"Trouver le Rocher des kodos et boire le Sapta de terre."}},
[1521] = {"L'appel de la terre", {"Prenez ce quartz brut et apportez-le à la prophétesse Ravenfeather. Il attestera de notre rencontre. Elle concevra un totem pour vous, et ce caillou en sera le cœur. Aussi petit qu'il puisse paraître, vous comprendrez avec le temps que la taille importe peu, et que même la plus petite des choses peut survivre aux montagnes.","Soyez <patient/patiente>, <nom>. Restez <fort/forte>. Et recherchez la sagesse. C'est ce que la terre attend de vous. À la fin, vous ne ferez plus qu'un avec elle. Respectez-la comme elle vous respecte."}, {"Apporter le Quartz brut à la prophétesse Ravenfeather au Camp Narache."}},
[1522] = {"L'appel du feu", {"Qu'le feu soit avec vous, <m'sieur/m'dame>. Je vois que vous êtes balèze déjà dans les arts de la terre, et j'pense que vous êtes <prêt/prête> à parler à Kranal.","Il vit au nord de la Croisée des Tarides. Il s'y connaît dans les éléments et peut vous apprendre les méthodes du feu... si vous pensez être <prêt/prête>.","L'est facile à trouver, n'allez pas trop loin, c'est tout, sinon vous vous retrouvez direct chez les elfes de la nuit."}, {"Trouver Kranal Fiss dans les Tarides."}},
[1523] = {"L'appel du feu", {"Cela fait longtemps que vous n'avez pas été <mis à l'épreuve/mise à l'épreuve>, <nom>. Vous avez montré de la patience jusque-là dans vos études, mais le temps est à présent venu d'apprendre la destruction et le chaos. L'élément du feu peut vous appartenir, si vous êtes <prêt/prête> à en payer le prix.","Recherchez Kranal Fiss dans les Tarides. Il vit dans une petite demeure au nord de la Croisée, mais prenez garde. Si vous atteignez les terres des elfes de la nuit, c'est que vous êtes <allé/allée> trop loin."}, {"Trouver Kranal Fiss dans les Tarides."}},
[1524] = {"L'appel du feu", {"Vous êtes suffisamment <puissant/puissante> pour manier le totem de feu, mais cela ne signifie pas que vous êtes <prêt/prête>.","Le long de la Furie-du-Sud en Durotar, avant la Grande Mer, se trouve un sentier caché menant aux hauteurs les plus élevées de cette terre. Trouvez le chemin menant au sommet et vous découvrirez l'un de nos autels sacrés, surveillé par Telf Joolam : l'Autel de la flamme dormante.","Apportez-lui cette torche, cela voudra dire que vous tentez d'apprivoiser le feu. Rapportez-la-moi lorsqu'elle sera enflammée au brasero de l’autel et je veillerai à ce que le totem de feu vous soit remis."}, {"Apporter la Torche de la flamme dormante à Telf Joolam en Durotar."}},
[1525] = {"L'appel du feu", {"Mais vous n'êtes pas là pour recevoir un cours d'histoire, n'est-ce pas ? Vous êtes <venu/venue> pour la flamme éternelle et le totem de feu.","La première étape consiste à créer un sapta et à vous lier à la flamme.","Je peux m'en occuper si vous m'apportez deux choses. La première est du goudron de feu, un objet simple à se procurer et généralement porté par les lanceurs de sorts Tranchecrin dans les Tarides de l'est.","Il sera plus dangereux de se procurer la seconde : une bourse de réactifs que vous trouverez sur l'un des sectateurs de la Lame ardente. Ils sont dans une caverne au-dessus des ravins, au nord-est de Tranchecolline."}, {"Apporter 1 Goudron de feu et 1 Bourse de réactifs à Telf Joolam en Durotar."}},
[1526] = {"L'appel du feu", {"Le sapta de feu est terminé. Il est temps pour vous de vous confronter à la flamme. Utilisez le sapta à l'autel et rendez-vous au sommet de la montagne pour affronter la manifestation. Elle n'attaquera pas tant que vous ne serez pas <prêt/prête>, mais elle sait que vous pouvez la voir.","Frappez bien, et frappez vite, <nom>, car sa puissance grandira si vous tardez à la tuer. Quand vous l'aurez vaincue, placez ses braises dans le brasero et allumez la torche."}, {"Vaincre la Manifestation de feu mineure, et placer sa Braise rougeoyante dans le brasero, en haut de l'Autel de la flamme éternelle."}},
[1527] = {"L'appel du feu", {"Tenant fermement la torche, vous vous détournez du brasero et embrassez Durotar du regard. Vous sentez la fièvre battre dans votre esprit tandis que le vent avive les flammes qui dansent furieusement.","Vous prenez le chemin du retour vers les Tarides, pour trouver Kranal Fiss."}, {"Apporter la Torche de la flamme éternelle à Kranal Fiss dans les Tarides."}},
[1528] = {"L'appel de l'eau", {"L'eau est dans votre avenir, <mon pote/m'dame>. Je vois qu'vous vous en sortez déjà bien avec les arts du feu, et je pense que vous êtes <prêt/prête> à parler à Islen.","Elle vit le long de la côte des Tarides. Si vous suivez le fleuve Furie-du-Sud en direction du sud, vous pouvez pas la rater – cherchez l'grand requin ! Elle maîtrise les arts des éléments et peut vous apprendre ceux de l'eau... si vous pensez être <prêt/prête>.","Elle est facile à trouver, n’allez pas trop loin, c'est tout. Elle se trouve juste après Ratchet."}, {"Trouver Islen Waterseer dans les Tarides."}},
[1529] = {"L'appel de l'eau", {"Cela fait longtemps que vous n'avez pas été <mis à l'épreuve/mise à l'épreuve>, <nom>. Vous avez montré de la patience jusque-là dans vos études, mais le temps est à présent veu pour vous d'apprendre la pureté de l'eau.","Cherchez Islen Waterseer dans les Tarides. Elle se trouve le long de la côte est, au sud du fleuve Furie-du-Sud et de Ratchet. Vous la trouverez dans une hutte de pêcheur. Bonne chance à vous, <nom>."}, {"Trouver Islen Waterseer dans les Tarides."}},
[1530] = {"L'appel de l'eau", {"S'il y a une leçon à apprendre de l'eau, la voici : l'eau est la renaissance.","Son pouvoir coule, érode et rend propre toute chose. Ses propriétés de guérison et de ressourcement sont incomparables, mais seulement lorsqu'elle est pure.","Lorsque vous comprendrez que l'eau, quand elle est polluée, peut dévaster tout ce qu'elle touche, vous apprécierez à quel point cette ressource est vitale.","Si vous souhaitez le sapta de l’eau, trouvez Brine loin dans les Tarides du sud. Sa maison est constamment la cible des hurans."}, {"Trouver Brine dans les Tarides du sud."}},
[1531] = {"L'appel de l'air", {"Y faut que vous y alliez, là. Préparez-vous à montrer qu'vous êtes digne de r'cevoir vot' totem d'air. Mais déjà, vous d'vez trouver Prate Cloudseer aux Mille pointes. Elle vous dira comment obtenir vot' totem d'air.","Passez au sud par les Tarides, puis aux Mille pointes, à l'est. Vous la trouverez cachée dans un trou, avec son p'tit copain. Haha, ne lui dites pas qu'je la salue pas, elle aimerait pas trop."}, {"Trouver Prate Cloudseer aux Mille pointes."}},
[1532] = {"L'appel de l'air", {"Les vents de Kalimdor se hâtent pour vous rencontrer et vous serez bientôt <compté/comptée> parmi nos plus puissants frères et sœurs. La fierté m'emplit de vous voir arriver à ce stade, et de savoir votre avenir si prometteur.","L'air est difficile à contrôler, mais j'ai foi en vos capacités à surmonter tous les obstacles.","Partez pour l'extrême sud des Tarides et entrez aux Mille pointes. C'est là, loin à l'est, que vous trouverez Prate Windseer. Elle vous enseignera."}, {"Trouver Prate Cloudseer aux Mille pointes."}},
[1534] = {"L'appel de l'eau", {"Prenez cette outre et enfoncez-vous au cœur de la forêt luxuriante que les elfes de la nuit appellent Ashenvale. C'est à l'extrême nord des Tarides.","Une fois dans la forêt, allez à l'ouest. Le long des montagnes qui séparent les Serres-Rocheuses d'Ashenvale, au-delà du lac Mystral, vous trouverez un lieu sacré : les ruines de Chimétoile. Une petite fontaine jaillit au centre du lac.","Apportez-moi un échantillon de l'eau de cette fontaine. Ce sera le dernier ingrédient que vous devez obtenir avant que je ne vous renvoie à Islen."}, {"Remplir l'Outre bleue vide aux Ruines de Chimétoile à Ashenvale et la rapporter à Brine dans les Tarides."}},
[1535] = {"L'appel de l'eau", {"Pour trouver la compréhension et obtenir le sapta, vous devez trouver les eaux les plus pures. Tandis que vous rassemblerez les échantillons pour moi, observez les lieux qui vous entourent. Soyez <attentif/attentive> aux créatures qui ont besoin d'eau, et qui la protègent. Voyez comment elle affecte les forêts et les déserts. Soyez témoin de la manière dont la substance même qui maintient la vie force les créatures à ôter la vie.","Nous commencerons par une tâche simple : en bas de ma hutte, vous trouverez un bassin de ce précieux liquide. Remplissez-en cette outre et revenez me voir."}, {"Remplir l'Outre marron vide au trou d’eau, derrière la hutte de Brine, puis rapportez-lui la, dans les Tarides."}},
[1536] = {"L'appel de l'eau", {"Il y a longtemps, j'ai suivi quelques compagnons jusqu'aux contreforts d'Hillsbrad. Nous nous sommes défendus contre des humains tenaces qui s'y sont établis et nous nous sommes retrouvés dans une petite ville occupée par les Réprouvés : Moulin-de-Tarren.","C'est là que j'ai découvert une chose des plus étranges : en pleine ville, un petit puits offrait une belle eau fraîche. Je ne crois même pas que ces pestiférés comprennent sa valeur. C'est une goutte de pureté au cœur d’une mer de malédiction.","Remplissez cette outre avec cette eau et rapportez-la-moi."}, {"Remplir l'Outre rouge vide au puits de Moulin-de-Tarren et retourner voir Brine dans les Tarides."}},
[1558] = {"Le barrage de Formepierre", {"Forgefer, c'est là où vivent tous les nains et les gnomes. Pas loin, il y a un endroit qui s'appelle le Loch Modan. Les nains y ont construit un ÉNORME barrage. Ils disent que c'est l'une des choses les plus grandes jamais construites. Ouah !","Vous pouvez m'emmener au Loch Modan pour voir le barrage, s'il vous plaît ? Je crois que c'est le barrage de Forme... de Formepierre. Il est énorme, c'est sûr. J'aimerais bien regarder par-dessus la grande chute d'eau. Je promets que je ne me pencherai pas trop au-dessus de la rambarde !"}, {"Conduire l'orphelin au barrage de Formepierre au Loch Modan. Vous devez le conduire au milieu du barrage pour qu'il puisse voir par-dessus l'immense chute d'eau."}},
[1559] = {"La recette de la bombe éclairante", nil, nil},
[1560] = {"La quête de Tooga", {"Je suis complètement perdu !","Ma femme, Torta, m'a envoyé chercher le dîner et j'ai dû prendre le mauvais chemin quelque part.","Auriez-vous l'amabilité de me guider vers Torta ? Elle m'attend au sud de Port Gentepression."}, {"Montrer à Tooga le chemin du retour vers sa femme, Torta."}},
[1578] = {"Rééquiper le front", {"Les montagnards de Loch Modan combattent les troggs, et cela les occupe beaucoup. Ils ont constamment besoin d'être approvisionnés en armures et en nouvelles armes.","C'est pourquoi mon collègue Thorvald est ici, pour maintenir l'équipement des montagnards en bon état. Aussi, si vous pouviez fabriquer des haches en cuivre et des ceintures en anneaux de cuivre, puis les apporter à Thorvald, il vous en serait reconnaissant. Il se peut même qu'il partage une partie de son savoir avec vous.","Thorvald est basé dans la tour de garde méridionale de Loch Modan."}, {"Apporter 6 Haches en cuivre et 6 Ceintures en anneaux de cuivre à Thorvald dans la Tour de garde méridionale du Loch Modan."}},
[1579] = {"Rouages électriques", {"Je suis venu à Auberdine parce que c'est calme. Je veux créer dans le calme. Ironforge est décidément trop bruyante après une nuit passée à boire !","Alors j'ai pris un bateau pour Auberdine et j’ai bu un petit peu trop pendant le voyage. J'ai laissé tomber ma boîte de rouages électriques par-dessus bord ! J'en ai besoin !","Je ne sais pas exactement où je l’ai laissée tomber, mais si vous pêchez quelque part dans l'océan, il se peut que vous ayez de la chance..."}, {"Apporter 8 Rouages électriques à Wizbang Cranktoggle à Auberdine."}},
[1580] = {"Pêche aux Electropellers", {"Nous savons que vous êtes <chanceux/chanceuse>, mais à quel point l'êtes-vous vraiment, <nom> ?","Une nuit, je marchais sur les routes de Sombrivage. J'essayais de trouver une bonne rivière ou un bon lac pour tester mon nouvel appeau à canard explosif et à propulsion automatique, et je pense que j'étais un peu... parti. Je me suis endormi et je me suis réveillé des heures plus tard à Auberdine. Mes cheveux étaient roussis et je ne trouvais plus mon sac d'Electropellers ! J'avais dû le laisser tomber dans l'eau !","Il me faut ces Electropellers ! Peut-être pourriez-vous les pêcher quelque part dans un lac ou dans un cours d'eau quelconque à Sombrivage..."}, {"Apporter 12 Electropellers à Wizbang Cranktoggle à Auberdine."}},
[1581] = {"Les élixirs pour les Bladeleaf", {"Notre magasin d'herbes et d'alchimie est un succès, mais mon mari passe tellement de temps à préparer ses potions qu'il ne nous en reste plus pour nous deux.","Pouvez-vous nous aider ?","Si vous m'apportiez une provision d'élixirs déjà faits, alors je serais heureuse de commercer avec vous. J'ai ici quelques herbes rares qui pourraient vous intéresser."}, {"Apporter 6 Elixirs de force du lion et 2 Elixirs de défense mineure à Syral Bladeleaf à Dolanaar."}},
[1582] = {"Veste de Moonglow", {"Nous autres à Darnassus sommes très occupés, et tout particulièrement les artisans. Les jeunes aventuriers souhaitant affronter les dangers de la nature ont constamment besoin d'équipement.","Si vous pouviez m'aider à honorer mes commandes de cuir, alors je pourrais peut-être vous apprendre une technique avancée d'artisanat du cuir."}, {"Apporter 1 Gant en cuir estampé, 5 Bottes en cuir estampé et 5 Capes en cuir estampé à Lotherias à Darnassus."}},
[1598] = {"Le Tome dérobé", {"Vous ici ! <nom>, c'est ça ?","Êtes-vous <sûr/sûre> de votre vocation de démoniste, car si vous ne pouvez pas maîtriser ce que vous invoquez, vous finirez <aspiré/aspirée> dans le Néant.","Je dois vous enseigner les arts du démoniste, mais je dois d'abord m'assurer que vous êtes suffisamment <fort/forte>.","Un novice qui étudiait à l'abbaye s'est enfui pour rejoindre la Confrérie défias. Pourquoi est-ce que cela m'intéresse ? Eh bien, il a volé certains livres importants.","L'un d'entre eux se trouve être un livre que je voulais pour mon usage personnel : « la Puissance du Vide ». Volez-le pour moi, il est caché dans sa tente dans les vignes."}, {"Retrouver la Puissance du Vide pour Drusilla La Salle."}},
[1599] = {"Les débuts", {"Vous devez être <le nouveau/la nouvelle> démoniste dont tout le monde parle. Quelqu'un doit vous apprécier pour qu’on m'arrache à mes recherches et me demande de vous enseigner.","Ne vous inquiétez pas, je vais vous consacrer du temps.","Le plus simple des sorts d'invocation est celui du diablotin. Mais avant que je vous le livre, vous devez prouver que vous avez les capacités physiques et mentales nécessaires.","Apportez-moi trois Charmes de plume que portent les novices Frostmane de la caverne au sud-ouest."}, {"Apporter 3 Charmes de plume à Alamar Grimm à Anvilmar."}},
[1618] = {"Le rééquipement des Carmines", {"Nous les nains ne sommes pas les seuls à nous battre. Nos alliés humains sont très occupés également.","Les Carmines subissent régulièrement des attaques des orcs Blackrock, et leur réserve d'équipement est constamment très basse. Si vous vouliez leur venir en aide, je suis sûr que leur forgeron permanent, Verner Osgood, vous apprendrait un ou deux trucs."}, {"Tormus Deepforge veut que vous apportiez 4 Ceintures runiques en cuivre et 4 Grands maillets en cuivre à Verner Osgood, dans les Carmines."}},
[1638] = {"L'entraînement du guerrier", {"Vous avez montré que vous êtes <un guerrier/une guerrière> capable, <nom>. Mais vous êtes encore jeune et avez beaucoup à apprendre.","Parlez à Harry Burlguard, il est généralement à la taverne du « Cochon siffleur », dans la vieille ville de Stormwind. Harry est un vétéran et un excellent Maître. Il pourrait vous montrer quelques passes d’armes qui vous aideraient à vivre plus vieux."}, {"Parler à Harry Burlguard."}},
[1639] = {"Bartleby le poivrot", {"Ouais, je peux vous enseigner ! Mais d'abord je veux voir ce que vous savez déjà.","Vous voyez ce poivrot là-bas au bout du bar ? Bartleby ? Je veux que vous lui preniez sa chope.","Ha ! Et si vous connaissez Bartleby, alors vous savez qu'il ne vous la donnera pas gentiment."}, {"Parler à Bartleby."}},
[1640] = {"Battre Bartleby", {"Vous voulez quoi ? Vous êtes <fou/folle> !","La seule façon pour vous d'obtenir cette chope serait de la prendre entre mes doigts froids et morts…"}, {"Battre Bartleby, puis lui parler."}},
[1641] = {"Le Tome de divinité", nil, nil},
[1642] = {"Le Tome de divinité", {"En toutes choses, un paladin doit réfléchir la Lumière qui nourrit notre force. Tendre vers la pureté, se battre pour l'un des nôtres, ne veut pas dire que nous nous battons pour une déité. Nous nous battons pour être bons dans toutes nos actions.","Bien que nous soyons appelés pour frapper le Mal en ces temps difficiles, vous ne devez jamais oublier que c'est en aidant les autres que vous vous démarquerez véritablement des citoyens d'Azeroth. La compassion, la patience, la bravoure, ces valeurs sont aussi importantes pour un paladin que la force dans la bataille.","Prenez-en conscience, et ne l'oubliez pas."}, {"Parler à Duthorian Rall à Stormwind."}},
[1643] = {"Le Tome de divinité", {"Votre tâche, à présent, <nom>, est de trouver quelqu'un ici, à Stormwind, qui a besoin de votre aide. Quelqu'un ayant un cœur et des motivations purs. Peut-être une personne dévouée aux autres. Peut-être quelqu'un ayant besoin d'un guide. En tout cas, cette personne doit être pétrie de bonnes intentions et doit vouloir compter sur votre gentillesse.","Agissez au mieux et revenez me voir après. Alors, nous discuterons de la voie du paladin et de la Lumière."}, {"Chercher Stephanie Turner dans le Quartier commerçant de Stormwind."}},
[1644] = {"Le Tome de divinité", {"Qu’ils soient bénis. La plupart ont perdu leur famille dans les attaques du Fléau, ces dernières années.","Mon mari, John, collecte des matériaux qui me servent à confectionner des chemises et des pantalons pour les enfants, ce que je n’ai malheureusement pas eu le temps de faire dernièrement.","Mais peut-être voudriez-vous m'aider ? Oh, merci mille fois, <nom>.","Si vous pouviez m’apporter de l’étoffe de lin pour que je puisse en faire des vêtements pour les enfants, je vous en serai reconnaissante."}, {"Apporter 10 morceaux d'étoffe de lin à Stephanie Turner à Stormwind."}},
[1645] = {"Le Tome de divinité", nil, nil},
[1646] = {"Le Tome de divinité", {"En toutes choses, un paladin doit réfléchir la Lumière qui nourrit notre force. Tendre vers la pureté, se battre pour l'un des nôtres, ne veut pas dire que nous nous battons pour une déité. Nous nous battons pour être bons dans toutes nos actions.","Bien que nous soyons appelés pour frapper le Mal en ces temps difficiles, vous ne devez jamais oublier que c'est en aidant les autres que vous vous démarquerez véritablement des citoyens d'Azeroth. La compassion, la patience, la bravoure, ces valeurs sont aussi importantes pour un paladin que la force dans la bataille.","Prenez-en conscience, et ne l'oubliez pas."}, {"Parler à Tiza Battleforge à Ironforge."}},
[1647] = {"Le Tome de divinité", {"Votre tâche, <nom>, consiste à trouver quelqu'un ici à Ironforge qui ait besoin d'aide. Quelqu'un ayant un cœur et des motivations purs. Peut-être une personne dévouée aux autres. Peut-être quelqu'un ayant besoin d'un guide. En tout cas, cette personne doit être pétrie de bonnes intentions et doit vouloir compter sur votre gentillesse.","Agissez au mieux et revenez me voir après. Alors, nous discuterons de la voie du paladin et de la Lumière. Je resterai ici, au temple, dans l'attente de votre retour. D'autres paladins auront besoin que je les guide, également."}, {"Chercher John Turner à la périphérie d'Ironforge."}},
[1648] = {"Le Tome de divinité", {"Ma femme et moi nous nous occupons d'un orphelinat à Stormwind, principalement pour des enfants qui ont perdu leurs parents au cours des attaques du Fléau cette dernière décennie. Je passe à Ironforge à peu près tous les mois afin de collecter des vivres offerts par des citoyens d’ici, et j’essaie de convaincre certaines associations de soutenir notre action vis-à-vis des enfants.","Ce voyage a été horriblement lent, et pas très productif.","Si je pouvais simplement trouver des pièces de lin pour que ma femme puisse coudre quelques vêtements, je pourrais enfin envoyer quelque chose à la maison."}, {"Apporter 10 morceaux d'étoffe de lin à John Turner à Ironforge."}},
[1649] = {"Le Tome de la bravoure", {"La bravoure et toutes les vertus qui vont avec sont comme les minerais rares : pour leur valeur vous devez les chérir, mais vous devez aussi prendre le temps de les entretenir, de les polir. Quand vous les associez judicieusement, vous obtenez un alliage résistant, qui peut résister même aux coups les plus violents.","Cette force, votre foi, votre bravoure doivent être éprouvées. Vous devez apprendre à ne jamais perdre la foi et comprendre qu'avec le pouvoir de la Lumière à vos côtés, vous pourrez surmonter plus d’obstacles que la plupart des gens."}, {"Parler à Duthorian Rall à Stormwind."}},
[1650] = {"Le Tome de la bravoure", {"Bien ! Votre engouement est le signe que vous êtes <prêt/prête> et montre votre enthousiasme à commencer le test. Ainsi soit-il !","A l'extrémité de Longrivage, dans la marche de l'Ouest, une petite maison donne sur l'océan. C’est la demeure du couple Stilwell. Jordan, un très bon forgeron, est parti à Ironforge voir les nains. Mais il a laissé Daphne, sa femme, seule et sans protection.","Des rapports disent que les Défias ont envahi cette zone : Daphne aura peut-être besoin de protection. Allez là-bas."}, {"Trouver Daphne Stilwell dans la marche de l'Ouest"}},
[1651] = {"Le Tome de la bravoure", {"Je suis sûre qu'ils seront là dans peu de temps maintenant, préparez-vous.","J'ai une arme à la maison. Je vais la chercher et je reviens vous donner un coup de main, mais vous allez devoir faire le plus gros du travail j'en ai peur.","Je n'ai aucune idée de leur nombre, mais pour sûr ils seront plus nombreux que la dernière fois.","Priez la Lumière de vous accorder ses faveurs parce que... Vous avez entendu ça ? Je pense qu'ils arrivent.","Nous allons nous replier près de la maison !"}, {"Défendez Daphne Stilwell contre les attaques des Défias.","Aucun de vous deux ne doit mourir, si vous voulez réussir la quête.","Quand vous aurez réussi, reparlez à Daphne Stilwell."}},
[1652] = {"Le Tome de la bravoure", {"S'il vous plaît, retournez voir Duthorian et dites-lui que je suis saine et sauve. Et c'est grâce à vous et à l'Eglise.","Je vais envoyer un mot à Jordan à Ironforge pour lui dire de ne pas s'inquiéter. Oh qu'il me manque ! J'ai hâte qu'il revienne."}, {"Parler à Duthorian Rall à Stormwind."}},
[1653] = {"Le test de droiture", {"Je pense que Jordan voudra vous remercier lui-même, et comme j'ai besoin de quelqu'un pour lui porter le message disant que sa femme est en sécurité, ce serait bien que vous le lui apportiez.","Jordan a été un paladin de la Lumière avant que son dévouement au métier de son père le fasse changer de voie. Il connaît nos coutumes et il est considéré parmi nos meilleurs fidèles.","Il doit être encore à Ironforge. Dans la journée, il aime travailler hors de l'enceinte de la ville, sans doute pour profiter du grand air de la montagne."}, {"Parler à Jordan Stilwell à Ironforge"}},
[1654] = {"Le test de droiture", {"J'aimerais vous récompenser, si vous voulez bien l'accepter ? Ne vous y trompez pas, ma générosité sera tributaire de votre contribution, mais je sens que cette association sera bénéfique pour nous deux.","Je vous fabriquerai une arme à la hauteur de vos actions, un symbole des paladins et de la Lumière, mais vous devez me procurer les composants dont j'ai besoin pour la forger. J'ai déjà fait la liste de ce qui est nécessaire, il manquait juste une personne de qualité comme vous pour me les procurer. Qu'en pensez-vous, <classe> ?"}, {"Utiliser les Notes sur les armes de Jordan, trouver du Bois de chêne de blanchepierre, la Cargaison de Minerai raffiné de Bailor, le Marteau de forge de Jordan et une Gemme de Kor, puis revenir voir Jordan Stilwell à Ironforge."}},
[1655] = {"Cargaison de minerai de Bailor", {"Ach, vous m'avez fait peur <homme/femme> ! Vous ne pouviez pas remarquer que j'étais perdu dans mes pensées ?","Qu'est-ce que c'est ? La cargaison de Jordan... pour Ironforge. Ahhh, ouais, j'vois de quoi vous parlez ! Les ogres Mo'grosh... z'ont volé la cargaison de minerai d'mon vieil ami. La caravane venant des Paluns a été attaquée. Le garde qui avait survécu est mort il y a quelques jours : le soigneur n'est pas arrivé à temps.","Si vous voulez la cargaison, faut que vous alliez au nord-est à travers le Loch. Elle est quelque part par là."}, {"Apporter la Cargaison de minerai de Jordan à Bailor Stonehand au Loch Modan."}},
[1656] = {"Une tâche inachevée", {"<nom>, s'il vous plaît, je vous en supplie, pouvez-vous m'aider ? Je n'ai pas fini ce que j'ai à faire ici sur le Plateau – je suis au milieu de mon rite de force – et mon père veut que je livre ces fourrures à l'auberge de Bloodhoof.","Je ne suis pas suffisamment reposée pour finir mon rite et faire ce que mon père m'a demandé. Cela vous embêterait-il d'y aller pour moi ?","L'auberge est agréable et c'est l'endroit parfait pour se reposer après un long voyage. Vous en conviendrez lorsque vous l'aurez vue."}, {"Livrer le Ballot de fourrures à l'aubergiste Kauth au village Bloodhoof."}},
[1657] = {"Empuantir Southshore", {"Nous allons célébrer la Sanssaint en grande pompe… en en faisant voir de toutes les couleurs à nos ennemis de Southshore !","Prenez l’une de ces boules puantes conçues spécialement pour l’occasion. Elle est remplie d’une substance nauséabonde dont aucun humain - ou aucune bête à l’esprit simple, d’ailleurs - ne peut supporter l’odeur. Il faut la jeter au beau milieu de Southshore pour qu’elle agisse correctement, donc attendez-vous à combattre l’Alliance !","Quand vous aurez accompli cette tâche… cet hommage à notre libération, revenez me voir !"}, {"Lancer trois Boules puantes des Réprouvés au milieu de Southshore en Hillsbrad, puis revenez voir la sombre imploratrice Yanka à la Fête de l’homme d’osier, aux Clairières de Tirisfal."}},
[1658] = {"Un intrus à la Fête de l'homme d'osier", {"J’ai besoin de personnes courageuses pour se rendre aux clairières de Tirisfal, et voir comment se déroule la Fête de l’homme d’osier. Je ne vais pas vous mentir, aller là-bas sera dangereux : les gardes de faction lors de la fête seront exceptionnellement brutaux. Cependant, nous devons absolument connaître l’ampleur des festivités, et la gravité des problèmes que les Réprouvés nous causeront durant la Sanssaint.","Si vous vous sentez d’attaque, dirigez-vous vers Tirisfal et espionnez les Réprouvés. Revenez me voir avec des informations, et je m’assurerai que vous recevrez une compensation convenable."}, {"Allez voir la Fête de l'homme d'osier des Réprouvés dans les clairières de Tirisfal. Revenez voir le sergent Hartman à Southshore quand ce sera fait."}},
[1661] = {"Le Tome de noblesse", {"Encore bonjour, <nom>. Je suis encore plus fier de vous appeler <frère/soeur> qu’avant ; votre dévouement est une bénédiction pour nous tous.","Le temps est venu de vous accorder une grande récompense. Sachez que je vous donne cela gratuitement, mais que plus tard, les défis qu’auront à accomplir les paladins qui voudront réaliser mes quêtes seront autrement plus exigeants.","Reparlez-moi plus tard et je vous accorderai une grande faveur."}, {"Parler à Duthorian Rall, à Stormwind."}},
[1665] = {"Tasse de Bartleby", {"Ok, voilà ma tasse. Attendez ! Laissez-moi finir avant... Ok, allez-y !"}, {"Apporter la Tasse de Bartleby à Burlguard."}},
[1666] = {"Le maréchal Haggard", {"Vous avez appris quelques passes d'armes, mais il faudrait utiliser une arme décente. Je parie que si vous parliez au maréchal Haggard il pourrait vous aider.","Le maréchal Haggard habite dans un manoir dans la forêt d'Elwynn, au Camp de bûcherons du Val d'est. Il est à la retraite, mais il a accumulé beaucoup d'équipements quand il était en activité.","Dites-lui que vous venez de ma part, et avec un peu de chance il aura une arme en trop à vous offrir."}, {"Parler au maréchal Haggard."}},
[1667] = {"Jack Dent-Morte", {"Burlguard vous a dit de passer ? Oui, j'ai des armes en trop ici, mais avant vous devez faire quelque chose pour moi...","Il y a quelque temps un scélérat défias appelé Jack Dent-Morte m'a escroqué. Il faisait sombre et il portait un tabard de Stormwind, j'ai cru que c'était le maréchal Dughan. Il m'a dit qu'il avait besoin de mon ancienne plaque de Maréchal de Stormwind et je lui ai donnée... Alors il a éclaté de rire et s'est enfui avant que je puisse l'attraper !","Rapportez-moi ma plaque ! Jack Dent-Morte a probablement un camp secret dans le sud."}, {"Retrouver la Plaque du maréchal Haggard au Camp de Jack Dent-Morte, et la rapporter au maréchal Haggard."}},
[1678] = {"Vejrek", {"Si vous voulez rejoindre les rangs des guerriers, vous avez intérêt à être robuste. Je peux vous apprendre de nouvelles passes d'armes, mais avant prouvez-moi que vous pouvez vous débrouiller contre des adversaires réels.","Vejrek est le plus coriace des trolls du coin ; c'est lui que vous devez affronter. Il a une hutte dans les collines au sud du repaire des Frostmane","Rapportez-moi sa tête et je saurai que vous avez du courage."}, {"Apporter la Tête de Vejrek à Muren Stormpike à Ironforge."}},
[1679] = {"Muren Stormpike", {"Bonjour guerrier ! Vous êtes <voué/vouée> à un grand avenir, mais il vous reste quelques petits trucs à apprendre et Muren Stormpike est le nain qu'il vous faut pour vous les enseigner.","Vous trouverez Muren dans le Hall des Armes à la Garde militaire d'Ironforge. Parlez-lui quand vous serez <prêt/prête> à apprendre ce qu'est un vrai guerrier."}, {"Parler à Muren Stormpike."}},
[1680] = {"Tormus Deepforge", {"Maintenant que vous avez appris de nouvelles passes, il est temps pour vous d'avoir une arme digne de ce nom. Parlez à Tormus Deepforge. Il peut vous fabriquer une arme dont vous me donnerez des nouvelles.","Vous trouverez Tormus ici, à Ironforge, à la Grande Forge."}, {"Parler à Tormus Deepforge."}},
[1681] = {"Base d'Ironband", {"Mon mentor, Sturgy Ironband, a une base au sud du lac du Lit d'Helm, à l'est de Dun Morogh. Il est maître forgeron, et c'est le meilleur.","Mais un jour, un de ses apprentis lui a apporté un étrange minerai. Le maître Ironband l'a baptisé \"minerai d'Umbral\" et s'est empressé de le travailler. Mais au moment où il était sur le point d’aboutir, une bande de nains Sombrefer a attaqué sa base. Ils ont tué tout le monde !","Allez à la Base d’Ironband, <nom>, vengez la mort du maître, et rapportez-moi une cargaison de Minerai d'Umbral."}, {"Rapporter du Minerai d'Umbral à Tormus Deepforge à Ironforge."}},
[1682] = {"Armes en fer gris", nil, nil},
[1683] = {"Vorlus Vilehoof", {"Avant de vous apprendre quoi que ce soit, vous devez me prouver votre résolution.","Le satyre Vorlus Vilehoof a découvert un de nos lointains Puits de lune, au sud-est du Refuge des saisons de Ban'ethil. Il est en train d’y barboter, souillant l'eau pure avec sa crasse.","Trouvez Vorlus au Puits de lune et détruisez-le. Ramenez-moi sa corne comme preuve, et je vous entraînerais.","Le chemin vers le Puits de lune est bien caché, <nom>, restez <concentré/concentrée> et alerte, et vous le trouverez."}, {"Rapporter la Corne de Vorlus Vilehoof à Elanaria à Darnassus."}},
[1684] = {"Elanaria", {"Bonjour, <guerrier/guerrière> ! Vos compétences progressent, mais il y a plus à savoir sur votre métier que vous ne le pensez. Pour continuer à progresser il vous faudra bientôt trouver un Maître.","La guerrière Elanaria réside à Darnassus, à la Terrasse des guerriers. Elle peut vous former."}, {"Parler à Elanaria."}},
[1685] = {"Convocation de Gakin", {"N'est-ce pas toujours ainsi ? Je vous ai <cherché/cherchée> partout aujourd'hui et je n'ai pas pu vous trouver, et maintenant que je n'ai plus besoin de vous, vous venez me voir. !","J'ai perdu assez de temps à vous chercher, je ne vais donc pas vous faire attendre plus longtemps. Maître Gakin vous convoque pour une autre leçon. Il me semble que vous devriez y aller immédiatement.","Vous le trouverez au sous-sol de « l'Agneau assassiné ». Au fait, pas un mot : je préférerai que l’endroit reste tranquille."}, {"Aller voir Gakin le Tisse-noir dans le quartier des mages de Stormwind."}},
[1686] = {"L'Ombre d'Elura", {"Vous avez appris beaucoup, <nom>. Maintenant nous allons vous fabriquer une arme. Une arme de <guerrier/guerrière>.","Pour cela, vous devez aller à Sombrivage.","Il y a bien longtemps, un bateau s'est brisé sur les rochers près du phare d'Auberdine, éparpillant ses caisses de minerai d'Elunite au fond de l'océan.","Tout d'abord, vous devez vaincre la gardienne de l'Elunite : l'Ombre d'Elura. C'était le capitaine du bateau, elle s'est condamnée elle-même à errer au fond de l'océan pour protéger la cargaison à jamais. Combattez-la, récupérez son médaillon, et remonter toutes les caisses de minerai."}, {"Apporter 8 Caisses de Minerai d'Elunite et le Médaillon d'Elura à Elanaria à Darnassus."}},
[1687] = {"Le phare hanté", {"Des fois, tard le soir, les gouvernantes de l'orphelinat nous racontent des histoires qui font peur. C'est GÉNIAL ! Il y en a une à propos d'un fantôme qui hante un phare dans la marche de l'Ouest. C'était un pirate à qui d'autres pirates ont pris son bateau. Waou !","Vous pourriez m'emmener au phare de la marche de l'Ouest ? Je veux voir le fantôme du capitaine ! Je resterai près de vous, c'est promis, je ne m'enfuirai pas ni rien..."}, {"Emmenez l'orphelin voir le phare sur la côte de la marche de l'Ouest."}},
[1688] = {"Surena Caledon", {"J'ai eu une étudiante du nom de Surena Caledon. Elle était là où vous êtes en ce moment, enthousiaste à apprendre la magie des démonistes, et talentueuse. En plus, elle était jeune et belle. Quand j'y repense, quelle traînée !","Cette voleuse s'est enfuie avec un Defias, Erlan Drudgemoor. Sa perte est négligeable, mais je lui avais offert une Echarpe de Pierre de sang que je veux absolument récupérer.","Sa vie m'importe peu maintenant. Vous la trouverez au Champ de Potirons des Brackwell. Rapportez-moi ce qui m'appartient."}, {"Retrouver l’Écharpe de Surena pour Gakin le Tisse-noir à Stormwind."}},
[1689] = {"Le lien", {"En retrouvant mon écharpe et en éliminant Surena, vous avez prouvé que vous étiez au moins aussi <talentueux/talentueuse> qu'elle.","Cela dit, vous avez terriblement besoin d'entraînement. Vous pouvez manier les flux magiques, bien sûr, mais être démoniste, c'est bien plus que cela.","Je vais vous montrer comment contrôler un marcheur du Vide, mais avant, vous devez en invoquer un dans le cercle d'invocation et le maîtriser avec les armes – physiques et magiques – dont vous disposez. Utilisez la magie que contient l'écharpe sur le cercle d'invocation en bas."}, {"Utiliser l’Echarpe de Pierre de sang pour invoquer et soumettre un marcheur du Vide, puis la rapporter à Gakin le Tisse-noir, à Stormwind."}},
[1690] = {"Châtier les Bat-le-désert", {"Vous là-bas ! Il est temps d'aider la Compagnie des Eaux de Gadgetzan.","Les nomades Bat-le-désert de Tanaris se sont emparés de presque tous les puits ! Si nous ne pouvons pas y avoir accès, notre réserve d'eau va s'assécher en un clin d'oeil. Si vous voulez vous faire bien voir, aidez-nous à faire la loi contre ces nomades !","Allez à l'est d'ici et abattez dix bandits et dix larrons Bat-le-désert, puis revenez immédiatement me faire votre rapport. La justice n'attend pour aucun homme... gobelin... bon, enfin bref, allez-y !"}, {"Abattre 10 Bandits et 10 Larrons Bat-le-désert à l'est de Gadgetzan, puis revenir au rapport auprès de l'ingénieur en chef Bilgewhizzle."}},
[1691] = {"Continuer à châtier les Bat-le-désert", {"Nous voulons que vous attaquiez les rangs les plus forts des nomades Bat-le-désert. Vous avez fait du bon travail jusque-là, mais pour reprendre ces puits, il faut affronter le moins possible de ces nomades !","Cette fois, il faut que vous éliminiez des voleurs, des assassins et des mages de l'ombre. Ils se trouvent à l'est et au sud-est d'ici.","Je ne vais pas vous mentir, ils sont coriaces. Faites cela pour nous, et nous pourrons bientôt reprendre ce qui est nôtre."}, {"Tuer 10 Mages de l'ombre Bat-le-désert, 8 Voleurs Bat-le-désert et 6 Assassins Bat-le-désert pour l'ingénieur en chef Bilgewhizzle à Gadgetzan."}},
[1692] = {"Forgeron Mathiel", {"J'ai mis l'Elunite que vous avez récupérée dans une caisse. Portez-la au Forgeron Mathiel, il fabriquera votre arme avec.","Mathiel est au sud, de l'autre côté de ce bâtiment."}, {"Apporter la Caisse d'Elunite au Forgeron Mathiel."}},
[1693] = {"Armes en Elunite", nil, nil},
[1698] = {"Yorus Barleybrew", {"Yorus Barleybrew a lancé un défi à tous les guerriers de l'Alliance. Il attend à l'Auberge de Lakeshire, dans les Carmines, et promet une grande récompense à celui qui passera l'épreuve."}, {"Parler à Yorus Barleybrew."}},
[1699] = {"Le défi de Rethban", {"J'ai appelé cette épreuve le \"défi de Rethban\". Pour passer l'épreuve, tout ce que vous avez à faire c'est d'entrer dans les Cavernes de Rethban, jusqu'à la première fourche. Facile ! Sauf qu'il y a certaines conditions...","Vous devez être de retour dans moins d'une heure.","Et il y a un tas de gnolls des Carmines sur le chemin.","Avant de commencer, il faut que vous preniez une lampée de ma boisson personnelle : du Barleybrew Scalder !","Alors qu'en dites-vous ? Les Cavernes de Rethban sont dans les collines, au nord de Lakeshire. Êtes-vous <prêt/prête> à relever le défi de Rethban ?"}, {"Entrer dans les Cavernes de Rethban jusqu'à la première fourche, et revenir voir Barleybrew dans le temps imparti.","Vous ne devez pas mourir."}},
[1700] = {"Grimand Elmore", {"J'ai pris des notes sur les techniques que j'ai mises au point en faisant votre armure, et je dois en faire parvenir une copie à Grimand Elmore. Il est ici dans le quartier des nains, au Magasin d'armes.","Et attention quand vous lui donnerez mes notes. Il est extrêmement fier de ses compétences, et il n'aimera pas l'idée que je puisse lui apprendre quelque chose..."}, {"Porter les Notes de Furen à Grimand Elmore."}},
[1701] = {"Une cotte de mailles en acier trempé", {"Alors je vous ai donné un bon bouclier, mais on ne va pas s'arrêter là. Je connais le secret pour fabriquer une Cotte de mailles extrêmement résistante. C'est difficile à faire et demande des matériaux très rares, mais si vous voulez faire le travail sur le terrain, alors je vous en fabriquerais une.","Qu’en dites-vous, <nom> ? Vous relevez le défi ? Je vais donc vous donner la liste des éléments dont j'ai besoin et où les trouver."}, {"Réunir les matériaux dont Furen Longbeard a besoin et lui apporter à Stormwind."}},
[1702] = {"Fabricant de bouclier", {"Vous avez montré vos talents en relevant le défi de Rethban, <nom>. Et je respecterai ma parole. Je vais vous donner votre récompense...","Sauf qu'elle est à Stormwind.","Parlez à mon ami, Furen Longbeard, dans le quartier des nains de Stormwind. C'est le meilleur fabriquant de boucliers que vous rencontrerez, et il me doit une faveur.","Tenez, apportez-lui ce tonneau de Barleybrew Scalder et demandez-lui un bouclier. Il vous en donnera un bon."}, {"Apporter le Tonneau de Scalder à Furen Longbeard."}},
[1703] = {"Mathiel", {"J'ai pris des notes sur les techniques que j'ai mises au point en faisant votre armure, et je dois en faire parvenir une copie au Forgeron Mathiel. Il vit à Darnassus, assez loin d'ici, mais je suis sûr qu'il sera reconnaissant de pouvoir apprendre ce que j'ai appris."}, {"Apporter les Notes de Furen à Mathiel."}},
[1704] = {"Klockmort Spannerspan", {"J'ai pris des notes sur les techniques que j'ai mises au point en faisant votre armure, et je dois en faire parvenir une copie à Klockmort Spannerspan. C'est un Forgeron gnome d’Ironforge et quand il lira mes découvertes, je parie qu'il s'arrachera la barbe."}, {"Apporter les Notes de Furen à Klockmort Spannerspan."}},
[1705] = {"Le sang ardent", {"Je déteste l'admettre, mais Furen a fait une découverte impressionnante. Mais pour être sûr je dois l'essayer moi-même sur une armure. Si vous me procurez le matériel, <nom>, alors vous pourrez en avoir une.","Pour faire cette armure, il me faut des échantillons de sang ardent et du roc ardent.","Le seul endroit où je sais en trouver est le bois de la Pénombre. Les worgens Plaie-de-nuit basés dans la mine du Destin de Roland ont du sang ardent, et leur chef, Videssac, devrait avoir le Roc.","Que la chance soit avec vous, <nom> !"}, {"Rapporter 20 Fioles de sang ardent et 1 Roc ardent à Grimand Elmore à Stormwind."}},
[1706] = {"L'armure de Grimand", nil, nil},
[1707] = {"Prime de la poche à eau", {"Salutations, <nom>. Mon nom est Luglunket, et nous avons un travail important à vous confier si vous vous sentez d'attaque ! Houzza !","Il n'y a rien qui me ferait plus plaisir, en tant qu'Opérateur du robinet expérimenté, que de récupérer les outres de Bat-le-désert que vous trouverez dans le désert. C'est notre eau de toute façon : les nomades l'ont juste volée ! La Compagnie des Eaux de Gadgetzan vous offre un paquet pour cinq outres que vous ramenez. Encore plus important : venir en aide à Gadgetzan, c'est l'aider à se venir en aide !"}, {"Apporter 5 Outres de Bat-le-désert à l'Opérateur de robinet Luglunket à Gadgetzan."}},
[1708] = {"Le corail de fer", {"Je veux essayer la nouvelle technique de Furen Longbeard sur une de mes pièces d'armure. Et parce que vous avez aidé Furen et qu'il m'a aidé, je vous offrirai l'armure que j’aurai fabriquée !","Pour faire la pièce, j'ai besoin d'un corail spécial, du corail torride. Il pousse à un seul endroit : le long des côtes au sud du port de Menethil dans les Paluns.","Apportez-moi suffisamment de corail torride et je pourrai fabriquer l'armure. Et dépêchez-vous, <nom>. Je suis impatient de commencer !"}, {"Apporter 20 Coraux torrides à Klockmort Spannerspan à Ironforge."}},
[1709] = {"La création de Klockmort", nil, nil},
[1710] = {"Coquilles desséchées au soleil", {"Mon collègue, Furen Longbeard, a fait une découverte que je suis impatient d'essayer. Je veux la tester sur une de mes nouvelles armures, car je pense que les techniques de Furen me permettraient d’améliorer la qualité de mes produits.","Voulez-vous m'aider ? Si vous me ramenez de quoi fabriquer l'armure, alors j'en ferai une pour vous.","J'ai besoin de coquilles d'œuf desséchées au soleil des wyvernes Aile-fière qui résident au Haut-perchoir, dans les Mille pointes. Cherchez les coquilles au pied de leurs nids, et revenez quand vous en aurez assez."}, {"Apporter 20 Coquilles desséchées au soleil à Mathiel à Darnassus."}},
[1711] = {"L'armure de Mathiel", nil, nil},
[1712] = {"Cyclonien", {"J'ai passé un temps considérable à observer ce site. J'observe… Je verrai peut-être tomber le Grand Cyclonien ! Mais je n'ai toujours pas trouvé un champion pour combattre le Seigneur des vents.","Peut-être est-ce vous ?","Prouvez-le moi. Rassemblez les éléments dont j'ai besoin pour invoquer un Cyclonien, et je saurai que vous êtes <un grand guerrier/une grande guerrière>. Peut-être pouvez-vous le battre et récupérer son cœur de tourbillon.","Si c'est le cas, alors je transmettrai le pouvoir du cœur dans une arme que je vous donnerai.","Voilà le parchemin qui liste les objets dont j'ai besoin."}, {"Apporter les objets du Parchemin de Bath'rah à Bath'rah la Vigie des vents à Alterac."}},
[1713] = {"L'invocation", {"Je suis prêt à faire mon rituel pour invoquer un Cyclonien. Quand il apparaîtra, vous devez l'abattre et lui prendre son Cœur de tourbillon. Je l'utiliserai pour faire une arme qui possédera les pouvoirs du Cyclonien.","<nom>, êtes-vous <prêt/prête> à affronter le Maître des vents ?"}, {"Rapporter le Cœur de tourbillon à Bath'rah la Vigie des vents."}},
[1714] = {"Essence d'exil", nil, nil},
[1715] = {"L'Agneau assassiné", {"Hum ! Dans le temps, tous les démonistes de Khaz Modan étaient entraînés par les maîtres de Gnomeregan, mais maintenant que nous avons été forcés de nous réfugier dans ce trou à rats qu'est Ironforge, et que Magni a décidé de nous surveiller, nous sommes forcés d'envoyer les novices étudier à Stormwind.","Heureusement, Gakin n'est pas un empoté, vous pourrez donc continuer à recevoir un enseignement, mais cela m'exaspère !","Enfin, vous trouverez Gakin au sous-sol de « l'Agneau assassiné » à Stormwind."}, {"Aller voir Gakin le Tisse-noir dans le quartier des mages de Stormwind."}},
[1716] = {"La dévoreuse d'âmes", {"Une succube est une dévoreuse d'âmes, une destructrice de cœurs et une dominatrice d'esprits. Elle ne connaît rien d'autre que le Mal.","C'est cette créature que vous allez apprendre à invoquer et à contrôler, mais on ne peut pas l'amener dans notre monde sans un appât.","Avec un symbole d'amour comme appât, elle répondra sûrement à votre appel. Alors vous pourrez la maîtriser, et en faire votre esclave.","Il va cependant être difficile de trouver quelque chose de cette nature. Vous devriez parler à Takar le Prophète. Vous le trouverez dans les Tarides de Kalimdor près du Camp Taurajo."}, {"Parler à Takar le Prophète dans les Tarides."}},
[1717] = {"Convocation de Gakin", {"<nom> ! Je ne pensais pas vous trouver ici, mais on m'a demandé de vous chercher.","Il est temps pour vous de reprendre l'entraînement, vous devez aller voir Gakin la prochaine fois que vous passerez à Stormwind. Il vous attend."}, {"Aller voir Gakin le Tisse-noir dans le quartier des mages de Stormwind."}},
[1718] = {"L'île mortelle", {"Votre renommée en tant que <guerrier/guerrière> s'accroît, <nom>. Il est temps de vous mesurer à l'un de vos pairs.","Il existe une île près des côtes des Tarides, au sud de Ratchet : l'île de la Dispute. Les guerriers s’y retrouvent.","S’y retrouvent et s’affrontent.","Parlez au chef des guerriers de l'île, Klannoc Macleod. S’il pense que vous le méritez, alors il vous guidera plus avant sur la voie du guerrier.","Pour atteindre l'île de la Dispute, cherchez des bancs de sable à faible profondeur le long de la Côte des marchands."}, {"Parler à Klannoc Macleod."}},
[1719] = {"La Dispute", {"Si vous voulez que je sois votre Maître, vous devez d'abord passer la Dispute. C'est une épreuve que nous, sur l'île de la Dispute, aimons faire passer aux guerriers qui pensent être forts, et si vous réussissez ce test alors nous saurons que vous l'êtes.","Pour commencer, marchez à travers cette foule là-bas, et allez jusqu'à l'âtre. Dites à Twiggy Flathead que vous êtes <prêt/prête>, il vous enverra des partenaires de combat.","Battez-les tous, et il appellera le Grand Will. Tuez le Grand Will, et revenez me voir."}, {"Tuer le Grand Will et parler à Klannoc Macleod sur l'île de la Dispute."}},
[1738] = {"Le cœur du bois", {"En prévision de votre arrivée, j'ai utilisé ma vision pour regarder à travers le monde et à travers le temps. J'ai trouvé ce que vous cherchez.","Lors de la guerre des Anciens, deux amoureux sont tombés ensemble lors d'une bataille. Là où ils sont morts, un arbre connu sous le nom de cœur du bois poussa, symbole de leur amour éternel.","Ce sera assez pour attirer une succube. Vous le trouverez dans les ruines d'Ordil'Aran, dans la partie nord-ouest de la forêt d'Ashenvale."}, {"Trouver le Cœur du bois en Ashenvale et le porter à Gakin le Tisse-noir dans le quartier des mages de Stormwind."}},
[1739] = {"Le lien", {"En utilisant le noyau du cœur du bois, vous serez en mesure de jeter le sort dans le cercle d'invocation qui se trouve dans la crypte en bas afin d'ouvrir un portail d'où la succube arrivera.","Attention, <nom>, les succubes arrivent à contrôler les esprits les plus forts.","Une fois que vous l'aurez vaincue et renvoyée dans le néant, revenez me voir pour que je vous apprenne comment l'appeler et la contrôler."}, {"Utiliser le Noyau du cœur du bois pour invoquer et soumettre une succube, puis rapporter le Noyau de cœur du bois à Gakin le Tisse-noir, à l'Agneau assassiné."}},
[1740] = {"L'Orbe de Soran'ruk", {"Quand les démons de la Légion sont tombés, leurs esprits furent emprisonnés dans des orbes et distribués à leurs mages et leurs démonistes.","Beaucoup furent apportés en Azeroth par leurs serviteurs. Même fragmentés, ils gardent les pouvoirs du démon emprisonné à l'intérieur.","L'orbe de Soran'ruk a été brisé en deux morceaux, l'un a été fragmenté pour être utilisé par les acolytes du Marteau du crépuscule dans les Profondeurs de Brassenoire. L'autre, le plus gros morceau, est en possession d'un sorcier dans le donjon d'Ombrecroc. Retrouvez les fragments et je pourrai réparer l'orbe."}, {"Trouver 3 Fragments de Soran'ruk et 1 Grand Fragment de Soran'ruk et les rapporter à Doan Karhan dans les Tarides."}},
[1758] = {"Tome de la Cabale", {"Il faudra plus qu'un simple cercle d'invocation pour attirer un chasseur corrompu et le défier.","Mes trois acolytes et moi-même allons vous aider à créer les cercles magiques. Vous aurez besoin d'un exemplaire du Tome de la Cabale, qui a été perdu depuis bien des années. L’ouvrage a été écrit par le premier groupe de démonistes pour invoquer seul un chasseur corrompu.","Krom Stoutarm peut peut-être vous aider. Ce démoniste nomade passe son temps à accumuler des textes anciens et traîne parfois dans la bibliothèque à Ironforge."}, {"Parler à Krom Stoutarm à Ironforge."}},
[1778] = {"Le Tome de divinité", {"Je vais devoir rentrer chez moi le plus rapidement possible. Heureusement, Stephanie fait de même pour rentrer à Stormwind. J'ai hâte de la revoir. A bientôt, <classe>."}, {"Parler à Tiza Battleforge à Ironforge."}},
[1779] = {"Le Tome de divinité", {"Je veux que vous preniez ceci, <nom>. Cela s'appelle le Symbole de vie.","Vous avez montré que vous étiez charitable et <patient/patiente>, maintenant je veux que vous me montriez votre pouvoir et vos talents au combat en tant qu'adepte.","Muiredon, mon mari, a rencontré récemment des nains Sombrefer, et il s'en est sorti de justesse. Il est à Ironforge, aux bons soins d’un de nos prêtres.","Prenez le Symbole et parlez-lui. Si vous pouvez l'aider, alors nous nous reparlerons bientôt. Que la Lumière soit avec vous !"}, {"Prendre le Symbole de vie et parler à Muiredon Battleforge à Ironforge."}},
[1780] = {"Le Tome de divinité", {"J'amènerai ça aux enfants dès aujourd'hui. Merci beaucoup, <classe>. Il est rare de nos jours qu'<un homme/une femme> de votre trempe se montre dans cette ville, mais vous avez prouvé que les paladins à Stormwind ne tourneront jamais le dos à ceux qui sont dans le besoin.","Adieu."}, {"Parler à Duthorian Rall à Stormwind."}},
[1781] = {"Le Tome de divinité", {"Je veux que vous preniez ceci, <nom>. Cela s'appelle un Symbole de vie.","Vous avez montré que vous étiez charitable et <patient/patiente>, et maintenant il est temps de savoir si vous avez la puissance nécessaire pour contrôler un des plus grands dons de la Lumière.","Mon ami Gazin a croisé récemment des Défias à Elwynn près de la Veillée des héros – il s'en est sorti de justesse. Il est près de l'autel en train de se faire soigner par un de nos prêtres.","Prenez le Symbole et parlez-lui. Si vous pouvez l'aider, alors nous nous reparlerons bientôt. Que votre chemin soit éclairé par la Lumière !"}, {"Porter le Symbole de vie à Gazin Tenorm à Stormwind."}},
[1782] = {"L'armure de Furen", nil, nil},
[1783] = {"Le Tome de divinité", {"J'espionnais des nains Sombrefer qui se terrent au sud du lac du Lit d'Helm. Nous savions qu'ils n'étaient pas là pour rien, alors Narm et moi nous nous sommes rapprochés – assez près pour voir leur chef donner des ordres à ses hommes.","Narm et moi nous avons tenté d'en tuer quelques-uns, mais ils se sont révélés plus résistants que nous le pensions, et beaucoup plus nombreux. Narm m'a dit de partir et de ramener du renfort, donc voilà pourquoi je suis là.","Narm a besoin d'aide, et je ne me reposerai pas tant qu'il n'en n'aura pas trouvé.","Allez-vous m'aider, <nom> ?"}, {"Prendre le Symbole de vie et ressusciter Narm Faulk dans Dun Morogh."}},
[1784] = {"Le Tome de divinité", {"Avant de tomber, Muiredon et moi avions presque vaincu un des espions Sombrefer près de cette petite maison. Nous avons vu le capitaine leur passer des ordres, et nous avons cherché à le tuer pour récupérer ces ordres.","Certaines personnes au conseil d'Ironforge ne croient pas à la présence des Sombrefer à Dun Morogh. En récupérant un de ces scripts, nous aurions non seulement la preuve de leur présence ici, mais nous aurions aussi une idée de ce qu'ils trament.","Récupérez un de ces scripts et rapportez-le à Muiredon, <nom>."}, {"Récupérer un Script des nains Sombrefer près du lac du Lit d'Helm et le rapporter à Muiredon Battleforge à Ironforge."}},
[1785] = {"Le Tome de divinité", {"J'peux déjà voir l'visage d'ma femme s'illuminer de fierté. Vous devriez bouger vot'postérieur tout de suite et aller la voir. J'pense qu'elle aura des choses à vous dire.","La Lumière soit avec vous, <classe> ! Je vous reverrai sur l'champ de bataille contre le Fléau et ces damnés Sombrefer quand le temps viendra."}, {"Parler à Tiza Battleforge à Ironforge."}},
[1786] = {"Le Tome de divinité", {"Henze et moi sommes allés à la Veillée des héros, une île au milieu du lac du Cairn au nord-est d'Elwynn. Nous avions entendu dire que des Défias avaient infiltré nos frontières et nous voulions le prouver aux conseillers du roi.","Nous en avons surpris en possession de documents, et nous les avons attaqués. Malheureusement, nous avons été rapidement submergés par des mages.","Henze les a retenus pendant que je m'enfuyais... C’était cinglé de sa part, mais il était brave.","Il est mort à présent. Pourriez-vous nous aider, <nom> ?"}, {"Apporter le Symbole de vie et ressusciter Henze Faulk à Elwynn."}},
[1787] = {"Le Tome de divinité", {"Nous aurons le temps de parler du Symbole plus tard. Ce qui importe c'est de récupérer les papiers que les Défias avaient sur eux. Si nous pouvons mettre la main dessus, nous serons en mesure de prouver que la Confrérie a infiltré Elwynn et nous aurons une idée de ce qu'elle trame.","Je ne suis pas assez remis pour combattre maintenant, <nom>. Pensez-vous pouvoir récupérer <seul/seule> ces papiers dans l'île ?","Si vous pouvez les récupérer, apportez-les à Gazin et dites-lui que je vais bien."}, {"Retrouver un Script Défias sur les Sorciers-voleurs défias d'Elwynn et le rapporter à Gazin Tenorm à Stormwind."}},
[1788] = {"Le Tome de divinité", {"Je vais porter le Script aux conseillers du roi après que Duthorian y aura jeté un coup d'oeil, ensuite allez parler à Duthorian. Il voudra sûrement vous féliciter !","Merci encore, <nom>. J'espère que l'on se reverra. Je suis fier de vous avoir comme <allié/alliée>... et comme <ami/amie>."}, {"Parler à Duthorian Rall à Stormwind."}},
[1789] = {"Le Symbole de vie", nil, nil},
[1790] = {"Le Symbole de vie", nil, nil},
[1791] = {"La Vigie des vents", {"À présent que vous avez appris la posture berserker, vous aurez besoin d'une belle arme avec laquelle l'utiliser.","Allez parler à Bath'rah la Vigie des vents, un troll ermite qui vit entre les montagnes d'Alterac et les Maleterres de l'est. C'est un fabricant d'armes très versé dans son métier et un puissant chaman.","Pour le trouver, allez à la rivière qui coule le long du côté est de Moulin-de-Tarren. Remontez-la vers le nord. Bath'rah campe près des ruines jonchant la rive est de la rivière."}, {"Parler à Bath'rah la Vigie des vents."}},
[1792] = {"Arme Tourbillon", nil, nil},
[1793] = {"Le Tome de la bravoure", nil, nil},
[1794] = {"Le Tome de la bravoure", nil, nil},
[1795] = {"Le lien", {"Si vous êtes suffisamment <préparé/préparée>, alors nous pouvons commencer. Laissez-moi vous expliquer la procédure.","Je vais commencer par créer un simple cercle d'invocation. Quand j'aurai fini, mes acolytes vont utiliser leurs bâtonnets de canalisation pour créer un cercle majeur.","Lisez alors l'incantation du Tome de la Cabale, et le chasseur corrompu apparaîtra. Faites attention, <nom>. Un chasseur corrompu est un ennemi puissant, et j'aimerais que vous gardiez la tête sur les épaules."}, {"Utiliser le Tome de la Cabale pour invoquer et soumettre un Chasseur corrompu, puis rapporter le Tome de la Cabale à Strahad Farsan, à Ratchet."}},
[1796] = {"Les composants de la robe sanglante dorée enchantée", {"Le premier objet que je vais vous aider à confectionner est une Robe. Vous rassemblerez ses composants pour moi, et nous les assemblerons ensemble. Après quoi, si tant est que vous aurez réussi, vous aurez les premiers des nombreux objets qui vous aideront dans vos luttes.","Mais pour commencer, vous devez avoir des Robes d'Arcana confectionnées. Un tailleur peut vous les faire, ou peut-être que vous pourriez en acheter à quelqu'un d'autre, mais cela m'impressionnerait que vous les confectionniez vous même. Quelle que soit la manière dont vous vous les procurerez, apportez-moi les robes, et nous commencerons."}, {"Apporter des Robes d’Arcana à Menara Voidrender dans les Tarides."}},
[1798] = {"À la recherche de Strahad", {"Il est temps pour vous d'apprendre à invoquer un chasseur corrompu, <nom>. Je n'ai pas ni composants ni les objets nécessaires ici pour vous l'enseigner, donc adressez-vous à Strahad Farsan, un membre de notre communauté.","Il réside dans le port gobelin de Ratchet. Je vais lui envoyer un message pour lui annoncer votre venue."}, {"Parler à Strahad Farsan dans les Tarides."}},
[1799] = {"Fragments de l'Orbe d'Orahil", {"Bien, alors commençons.","Allez d'abord parler à mes acolytes à l’intérieur et choisissez l’une des voies qu’ils vous proposeront de suivre... Ils vous en diront davantage eux-mêmes.","Ensuite, vous devrez voyager en Désolace et prendre un Orbe infernal sur l'un des démonistes du convent de Mannoroc. C’est cet orbe que vous purifierez avec l’aide d’un mage du nom de Tabetha, au sud du marécage d'Âprefange. Elle vous aidera en souvenir de notre histoire commune.","Ne revenez pas avant d’avoir accompli les deux tâches que j’ai requises auprès de vous."}, {"Parler aux acolytes de Menara dans la tour qui domine Ratchet et choisir de suivre l’une de leurs voies.","Ensuite, apporter un Orbe infernal à Tabetha, dans le marécage d'Âprefange."}},
[1800] = {"La salle du trône de Lordaeron", {"La gouvernante de l'orphelinat, à la maison, nous raconte de grandes histoires de bataille et de sagesse, si nous sommes gentils et que nous avons fait nos corvées. Une histoire raconte la chute de Lordaeron, et comment le maléfique Arthas a assassiné son père pour devenir le chef.","Il n'avait pas d'honneur, <nom> ! Quand je serai grand, je serai un puissant guerrier de la Horde, et ma lame sera guidée par l'honneur - jamais par le mal !","Vous voulez bien m'emmener voir la salle du trône, s'il vous plaît ? C'est juste avant l'endroit où il faut descendre pour aller à Fossoyeuse."}, {"Emmenez votre protégé à la vieille salle du trône de Lordaeron, qui se trouve juste avant l'endroit où il faut descendre pour aller à Fossoyeuse."}},
[1801] = {"Tome de la Cabale", {"Il va falloir plus qu'un simple cercle d'invocation pour appeler le chasseur corrompu que vous allez affronter.","Mes trois acolytes et moi allons vous assister pour créer les cercles magiques. Vous avez besoin d’un exemplaire du Tome de la Cabale, qui a été perdu depuis bien des années. Il a été écrit par le premier groupe de démonistes pour invoquer seul un chasseur corrompu.","Peut-être que Jorah Annison peut vous aider. En ce moment, il est employé comme cartographe à Undercity, mais il en connaît plus que quiconque sur les textes anciens."}, {"Parler à Jorah Annison à Undercity."}},
[1802] = {"Tome de la Cabale", {"Il a été perdu, mais j'ai une petite idée d’où il se trouve.","Il n’en existe qu’un seul exemplaire, en très mauvais état : déchiré à plusieurs endroits et en grande partie arraché de la reliure. La rumeur dit que cette partie serait entre les mains des centaures des Mille pointes.","L’autre partie a été perdue sur la côte ouest de Southshore, alors qu’elle était envoyée par bateau au port de Menethil. Elle n'est jamais arrivée à destination.","En échange de ces informations, tout ce que je demande c'est que vous le trouviez, et que vous me l'apportiez afin de le voir."}, {"Retrouver le Tome moisi et le Manuscrit en lambeaux pour Krom Stoutarm à Ironforge."}},
[1803] = {"Tome de la Cabale", {"Ah ! Je vois que vous avez les mêmes goûts de démoniste que moi. Vraiment, vouloir récupérer le Tome de la Cabale est une noble intention. Saviez-vous qu'il était autrefois à Lordaeron ? Mais il a été abîmé, presque la moitié des pages ont été arrachées. Qui a pu faire une chose pareille ?","Une rumeur circule à propos de cette moitié perdue : on dit qu’elle serait entre les mains des centaures des Mille pointes.","Le reste a été envoyé à Ironforge, mais a été perdu lors de son transport sur la côte ouest de Southshore."}, {"Retrouver le Tome moisi et le Manuscrit en lambeaux pour Jorah Annison à Undercity."}},
[1804] = {"Tome de la Cabale", {"Ah ! C'est difficile de le laisser partir. Mais vous l'avez trouvé et vous avez des choses importantes à faire avec. S’il vous plaît, dites bien à Strahad que j'en veux une copie !","Pendant que vous étiez <parti/partie>, il vous a envoyé un message. Les acolytes qui vont participer à l'invocation ont besoin de trois Bâtonnets de canalisation, vous devez les trouver.","Il ne sait peut-être pas où les chercher, mais moi si ! Les derniers à en avoir utilisé étaient des orcs du clan Dragonmaw dans les Paluns, leurs magiciens doivent en posséder…"}, {"Apporter le Tome recomposé et 3 Bâtonnet de canalisation à Strahad Farsan à Ratchet."}},
[1805] = {"Tome de la Cabale", {"C'est l'un des textes les plus intéressants que j'ai parcouru au cours de ces dernières années. Ici, il n'y a pas grand-chose, à part des livres de géographie, que j'ai déjà tous lus... Strahad m'en enverra peut-être une copie, quand il le pourra...","À propos, il vous fait dire que pour remplir votre mission, il vous faudra trois bâtonnets de canalisation.","Pour vous remercier de m'avoir laissé regarder ce texte, laissez-moi vous confier un secret : vous trouverez ces bâtonnets sur les orcs du clan Dragonmaw dans les Paluns."}, {"Apporter le Tome recomposé et 3 Bâtonnets de canalisation à Strahad Farsan à Ratchet."}},
[1806] = {"Le test de droiture", {"Pourquoi n'irions-nous pas à la forge pour commencer ? J'ai préparé votre arrivée depuis quelque temps déjà et tout devrait être prêt.","Après cela, vous serez mieux <préparé/préparée> que jamais pour défendre la Lumière, je vous le promets.","Je dois vous avouer, <nom>, que je suis un peu envieux de votre réussite. Mais sachez que de pouvoir participer à ma manière à cette réussite me remplit d'aise."}, {"Attendre que Jordan Stilwell finisse de forger votre arme."}},
[1818] = {"Parler à Dillinger", {"J'ai entendu dire que Dillinger le Garde noir avait un travail pour quelqu'un comme vous. Si vous voulez prouver votre valeur aux forces de la Dame noire, alors présentez-vous devant Dillinger immédiatement.","Le poste de Dillinger le Garde noir est à la frontière ouest de Brill, près du cimetière."}, {"Parler au Garde noir Dillinger à Brill."}},
[1819] = {"Ulag le Fendeur", {"Lorsque le Fléau s'est abattu sur les Clairières de Tirisfal, nous avons capturé l'un de ses serviteurs pour l'étudier. C'était un esprit libre, qui se nommait Ulag le Fendeur. Pourtant, malgré sa libre conscience, le roi-liche avait gardé son emprise sur lui.","Nous espérions trouver un moyen de le délivrer de cette influence, mais nous avons échoué. Il est temps de le libérer, maintenant...","Libérez-le et tuez-le.","En actionnant le déclencheur de la Pierre du Crâne devant le Mausolée qui se trouve au nord, vous briserez le sceau magique qui le lie.","Après... Il faudra frapper vite et fort, <nom> !"}, {"Tuer Ulag le Fendeur, puis parler au Garde noire Dillinger."}},
[1820] = {"Parler à Coleman", {"Vous avez prouvé votre valeur à la Dame noire, à présent vous pourrez peut-être la prouver à l'un de ses sujets.","Coleman Farthing cherche un guerrier pour être son bras vengeur contre les ennemis des Réprouvés.","Vous le trouverez à « la Taverne des Pendus » dans l'est de Brill."}, {"Parler à Coleman Farthing."}},
[1821] = {"L'héritage des Agamand", {"J'ai servi la famille Agamand tout le long de ma vie et ils ont été la cause de ma mort ! S'ils sont maintenant les esclaves du roi-liche, ce n'est que justice !","À présent, je dois m'approprier les derniers vestiges de leur honneur perdu. Dans leur crypte, je veux que vous voliez leur héritage familial : une épée, une hache, une dague et une masse, et que vous me les apportiez.","La Crypte de la famille Agamand se trouve à la lisière nord des Moulins d'Agamand, sous la protection d'un Fléau errant et des Agamands morts."}, {"Rapporter à Coleman Farthing la Hache de la famille Agamand, l'Epée de la famille Agamand, la Masse de la famille Agamand et la Dague de la famille Agamand."}},
[1822] = {"Une arme en héritage", nil, nil},
[1823] = {"Parler à Ruga", {"Vous êtes un guerrier compétant à la renommée croissante. Peut-être êtes-vous <prêt/prête> pour l'épreuve de Ruga Ragetotem ? Si c'est le cas, alors allez trouver Ruga au Camp Taurajo dans les Tarides.","Il cherche des guerriers qui accepteraient de passer son épreuve."}, {"Parler à Ruga Ragetotem."}},
[1824] = {"L'épreuve du Champ des géants", {"Pour passer l'épreuve, vous devez voyager vers le sud jusqu'au Champ des géants. Là-bas, vous trouverez un grand nombre d'insectes rampants. Ils sont récemment arrivés dans les Tarides et je pense que cela ne présage rien de bon.","Apportez-moi leurs antennes mobiles, et ne perdez pas de temps, <nom>. Ces antennes ne remuent pas longtemps. Apportez-les-moi dans le temps alloué, et vous aurez passé l'épreuve."}, {"Apporter 5 Antennes mobiles à Ruga Ragetotem au Camp Taurajo."}},
[1825] = {"Parler à Thun'grim", {"Thun'grim Firegaze, un forgeron orc de grande renommée, a entendu parler de vos exploits au service de la Horde. Il voudrait vous confectionner un équipement d'armure et vous propose d'aller en discuter immédiatement avec lui.","Thun'grim campe à l'est de la Croisée dans les Tarides."}, {"Parler à Thun'grim Firegaze dans les Tarides."}},
[1838] = {"Armure de brutalité", {"J'ai entendu parler de votre force et de votre courage, et je voudrais faire pour vous une armure complète qui vous aidera à mesure que vos exploits passeront à la postérité.","Mais faire une armure est une véritable quête en soi <nom>, et je vais avoir besoin de votre aide et de celle d'autres forgerons de la Horde.","Tenez, prenez ce parchemin. J'ai passé de nombreuses nuits à lutter avec mes yeux presque aveugles pour écrire tout ce dont j'ai besoin pour l'armure. Apportez-moi ce qui est noté sur ce parchemin et je créerai pour vous la première pièce de l’ensemble."}, {"Apporter à Thun'grim Firegaze 15 Lingots de Fer cendré, 10 Azurites en poudre, 10 Barres de Fer et une Fiole de Phlogiston."}},
[1839] = {"Ula'elek et les Gantelets de brutalité", {"Ula'elek, le forgeron troll, va façonner pour vous les gantelets de brutalité. Il demeure en Durotar, au Village de Sen'jin, avec ses frères Darkspear, et il attend votre arrivée."}, {"Parler à Ula'elek en Durotar."}},
[1840] = {"Orm Stonehoof et le Camail de brutalité", {"Orm Stonehoof est très respecté pour son habileté. Il va fabriquer pour vous le Camail de brutalité.","Cherchez-le à Thunder Bluff, près du bassin du grand totem central de la cité."}, {"Parler à Orm Stonehoof à Thunder Bluff."}},
[1841] = {"Velora Nitely et les Jambières de brutalité", {"On dit qu'avant de mourir, Velora Nitely était l'un des plus grands forgerons d'armures de Lordaeron. La mort, semble-t-il, n'a pas affecté ses talents. Allez voir Velora : elle fabriquera vos Jambières de brutalité.","Velora officie à Undercity de Lordaeron, dans le quartier des Marchands."}, {"Parler à Velora Nitely à Undercity."}},
[1842] = {"Les sabots de satyre", {"Je peux vous faire une paire de gantelets de brutalité, et ils seront porteurs d'une grande magie, mais pour cela, vous devez d'abord m'apporter cette magie.","Les satyres parcourent le nord-est d'Ashenvale à cause du pouvoir de la forêt. Ils se nourrissent de sa pureté, ils aspirent son innocence comme des sangsues et la corrompent. Ceux d'entre eux ont des sabots fendus et abîmés. Ils ont volé plus de magie à la forêt que les autres.","Ces sabots détiennent un grand pouvoir. J'en aurai besoin pour façonner vos gantelets."}, {"Rapporter 7 Sabots fendus de satyre à Ula'elek au village de Sen'jin en Durotar."}},
[1843] = {"Gantelets de brutalité", nil, nil},
[1844] = {"La corne de chimère", {"Je veux bien vous faire un casque, mais vous devez d'abord me montrer que vous en êtes digne.","Blottie sur les flancs nord-ouest des Serres-Rocheuses se trouve le Val calciné. C'était autrefois un endroit verdoyant, mais il est désormais souillé et étouffé sous la cendre. Les créatures qui y vivent encore en sont folles de douleur et de désespoir.","C'est là que je vous envoie, <nom>.","Vous devez aller au Val calciné et trouver une matriarche chimère. Tuez-la, libérez-la de sa misère. Apportez-moi sa corne galvanisée comme preuve et je vous ferai votre casque."}, {"Apporter une Corne galvanisée à Orm Stonehoof à Thunder Bluff."}},
[1845] = {"Camail de brutalité", nil, nil},
[1846] = {"Les tibias de Dragonmaw", {"Pour faire les Jambières de brutalité, j'ai besoin de quelques solides tibias. Les orcs Dragonmaw des Paluns ont de jolis tibias bien épais. Je vais en avoir besoin.","Mais ils doivent être vraiment solides. Prenez soin de tester les tibias que vous ramasserez. Si vous pouvez les casser à mains nues, c'est qu'ils ne sont pas assez solides pour que je puisse travailler avec."}, {"Apporter 8 Solides tibias de Dragonmaw à Velora Nitely, dans Undercity."}},
[1847] = {"Jambières de brutalité", nil, nil},
[1848] = {"Le haubert de brutalité", nil, nil},
[1858] = {"La Main brisée", {"Je vous le laisse pour ouvrir la sacoche de Tazan et me rapporter son contenu. Le mécanisme de verrouillage est complexe, ce serait sans doute plus facile si vous trouviez la clé.","Comme elle n'était pas sur le corps de Tazan, je ne peux que supposer qu'il a confié la clé à quelqu'un... Oh oui ! C'est ça ! Il avait un aide nommé Gamon. Un ivrogne, vous le trouverez probablement à la taverne. Peut-être que vous pourrez lui prendre la clé en douceur."}, {"Voler la Clé de Tazan puis l'utiliser pour ouvrir sa Sacoche et livrer son contenu à Therzok dans la Faille de l'ombre à Orgrimmar."}},
[1859] = {"Therzok", {"Parlez moins fort, <nom> ! Ouais, je sais qui vous êtes ! On m'a fait un topo.","D'après ce qu'on entend dire de vous, vous vous débrouillez au combat et vous employez la ruse à l'occasion.","J'ai raison ?","De toute façon, vous devriez aller voir Therzok à Orgrimmar. Il a quelque chose à vous proposer."}, {"Aller voir Therzok dans la Faille de l'ombre à Orgrimmar."}},
[1860] = {"Parler à Jennea", {"Nous, les mages, avons une tâche à accomplir. Une tâche essentielle pour la sécurité de Stormwind et de la forêt.","Jennea Cannon vous donnera plus de détails. Elle étudie dans le Sanctuaire du Sorcier, dans le quartier des mages de Stormwind. Hâtez-vous d'aller la voir."}, {"Parler à Jennea Cannon à Stormwind."}},
[1861] = {"Le lac Miroir", {"Les importantes luttes mystiques que le monde a vécues ont fait déborder les courants magiques de leurs canaux autrefois stables. Nous essayons de déterminer quels effets ces changements ont provoqué et certains d'entre nous craignent le pire !","Mais je vous en dirai davantage plus tard. Pour l'instant, nous avons besoin de vous pour recueillir des informations.","Tenez, prenez cette fiole. Allez au bas de la chute d'eau du lac Miroir, au sud-ouest des portes de Stormwind, et prélevez un échantillon de cette eau. Apportez-le-moi pour que je puisse vérifier si l'eau est contaminée par la magie."}, {"Apporter un Echantillon d'Eau du lac Miroir à Jennea Cannon à Stormwind."}},
[1878] = {"Prime de la poche à eau", nil, nil},
[1879] = {"Parler à Bink", {"<nom> ! Bink, ma collègue d'Ironforge, vous salue. Elle a une mission qu'elle ne veut confier qu'à un mage ! Avez-vous cette qualification, <nom> ?","Dans ce cas, adressez-vous à elle. Elle passe son temps dans le Hall des Mystères de la Garde mystique d'Ironforge."}, {"Parler à Bink à Ironforge."}},
[1880] = {"Le Bidulotron de youplala", nil, nil},
[1881] = {"Parler à Anastasia", {"<nom>, Anastasia Hartwell a une tâche à proposer à quelqu'un de votre valeur. Allez la voir dans le Quartier de la magie d'Undercity et préparez-vous à accomplir votre première tâche pour les mages des Réprouvés."}, {"Aller voir Anastasia Hartwell."}},
[1882] = {"La ferme des Balnir", {"Les Maleterres de l'est sont une aberration. Engendrées par le roi-liche, elles étendent leur magie dans le sol, corrompant et tuant tout ce qui vit. Bien que les forces destructrices de la corruption aient un certain charme, nous, les mages des Réprouvés, pensons que cette façon de faire est chaotique et doit être arrêtée.","Mais d'abord, nous devons mesurer leur étendue dans Tirisfal. Allez à la ferme des Balnir, à l'est de Tirisfal. Il y a des fleurs qui poussent dans le jardin, des gueules-de-loup. Cueillez-les et revenez me voir."}, {"Apporter des Gueules-de-loup du jardin des Balnir à Anastasia Hartwell dans le Quartier de la magie d'Undercity."}},
[1883] = {"Parler à Un'thuwa", {"Un'thuwa, le mage tribal du village de Sen'jin, a détecté une perturbation dans les courants magiques de Durotar et demande de l'aide. Allez voir Un'thuwa et faites ce que vous pouvez pour l'aider.","En retour, de nouveaux secrets vous seront révélés."}, {"Aller voir Un'thuwa."}},
[1884] = {"Les Tas de Ju-Ju", {"Le démoniste Zalazane des îles de l'Écho a perturbé le flux magique en Durotar. Il l'a fait par l’intermédiaire de fétiches infectés, des tas de Ju-ju, qu'il a placés dans ses huttes sur l'île principale d'Écho.","Ces tas perturbent la faune et la flore et accroissent le pouvoir des sorciers et des créatures infernales qui se cachent dans les coins sombres du pays.","Vous devez détruire les tas de Ju-ju, <nom>, avant que le flux de magie ne soit altéré pour toujours."}, {"Détruire 4 Tas de Ju-Ju, puis retourner voir Un'thuwa au village de Sen'jin."}},
[1885] = {"Mennet Carkad", {"<nom> ? Pas vraiment ce à quoi je m'attendais, compte tenu de l'intérêt que Carkad lui-même manifeste pour vous ! Comme quoi les apparences sont parfois trompeuses, non ?","Peu importe, vous devez aller le voir dès que possible. Autant que vous ne sachiez pas ce qui est arrivé au dernier qui a ignoré ses sommations."}, {"Aller voir Mennet Carkad dans le Quartier des voleurs d'Undercity."}},
[1886] = {"Les Traqueurs noirs", {"Si vous voulez rejoindre les Traqueurs noirs, la garde d'élite du Seigneur Varimathras, vous devez prouver votre valeur.","Je ne vais pas vous mentir, <nom>, cette tâche pourrait bien signifier votre mort.","Il y a certaines choses auxquelles il faut prêter attention. J'ai besoin des effets personnels d'Astor Hadren, un messager qui voyage entre les Pins argentés et Undercity. Je vous laisse le choix de la méthode.","Moins vous en saurez, mieux ça vaudra pour vous. Réussissez et je vous en dirai plus."}, {"Trouver la Lettre de recommandation d'Astor et l'apporter à Mennet Carkad dans le Quartier des voleurs."}},
[1898] = {"Les Traqueurs noirs", {"La lettre de recommandation devrait être suffisante pour convaincre Andron que vous êtes le messager qu'il attendait. J'ai déjà rencontré Andron, il n'est pas assez intelligent pour être soupçonneux.","Le Seigneur Varimathras suspecte Andron d'avoir fourni de l'aide à un autre groupe. Il exige de savoir lequel et en veut la preuve.","Je veux que vous alliez voir Andron pour obtenir cette information. Vous le trouverez à son magasin près de l'Apothicarium."}, {"Aller voir Andron Gant à Undercity."}},
[1899] = {"Les Traqueurs noirs", {"Vous trouverez les informations qui intéressent vos employeurs dans le grand livre sur l'étagère derrière moi. Apportez-leur avec mes respects et dites-leur que je préférerais que ce soit vous qu'ils envoient, si ce qu'ils trouvent leur semble avoir assez de valeur, pour continuer notre petit accord."}, {"Apporter le Grand Livre d'Astor à Mennet Carkad dans le Quartier des voleurs d'Undercity."}},
[1918] = {"Les éléments souillés", {"Sur l'élémentaire vaincu, vous trouvez un globe d'eau, formidablement lumineux, dont le contenu semble proprement répugnant. Apparemment, l'essence qui a souillé les eaux du lac Mystral est également présente dans les eaux du globe.","Ce genre de découverte n'est pas en mesure d'intéresser le commun des mortels, et sans doute la personne qui vous a demandé de vous rendre au lac, Mastok Wrilehiss, au poste de Bois-brisé, pourrait bien avoir une idée plus précise à ce sujet."}, {"Apporter le Globe d'eau souillée à Mastok Wrilehiss, au Poste de Bois-brisé, à Ashenvale."}},
[1919] = {"Rendre compte à Jennea", {"Vous connaissez de mieux en mieux le métier, jeune <nom>. Vous vous sentez d'attaque pour commencer vos vraies leçons maintenant ? Pour prendre part à la guerre secrète que nous menons ?","Si oui, allez voir Jennea Cannon au Sanctuaire du Sorcier, dans le quartier des mages de Stormwind. Elle vous confiera une tâche qui sera votre première fenêtre sur le monde invisible."}, {"Parler à Jennea Cannon à Stormwind."}},
[1920] = {"Trouver le Solitaire bleu", {"On nous a rapporté un incident qui est survenu à la taverne du « Solitaire bleu » dans le quartier des mages. Des créatures étranges et invisibles rôdent dans la taverne et je crains qu'une faille de mana ne se soit formée là-bas.","Il faut que vous y alliez pour neutraliser ces créatures. Sur place, utilisez l'incantation de manifestation pour les rendre visibles, puis capturez-les avec un coffre de retenue. Quand vous en aurez suffisamment, revenez me voir avec les coffres remplis, les coffres vides et l'incantation."}, {"Obtenir une Incantation de manifestation et une Caisse de coffres de retenue placée derrière Jennea Cannon. Apporter 3 Coffres de retenue remplis à Jennea Cannon au Sanctuaire du sorcier."}},
[1921] = {"Rassembler les matériaux", {"Vous avez bien servi les mages, et il est temps de vous attribuer votre robe de sorcier. Wynne Larson peut la réaliser pour vous, mais il faudra lui fournir les matériaux nécessaires.","La robe sera faite de lin, ce qui ne sera pas difficile à trouver, mais vous devrez aussi fournir des gemmes chargées de la faille. On ne peut les trouver que dans la mine du Ru d'argent à Loch Modan, dans les caisses laissés par la Ligue des mineurs nains.","Rassemblez le lin et les gemmes et apportez-les à Wynne Larson, chez les couturiers Larson, dans le quartier des mages."}, {"Apporter 10 unités de Lin et 6 Gemmes chargées de la faille à Wynne Larson à Stormwind."}},
[1938] = {"Traité d'Ur sur la Magie des Ombres", {"Autrefois, trois mages étudiaient dans cette tour, jusqu'à ce que l'un d'entre eux tombe dans les travers de la magie noire. On l'a pressé de se détacher de ses travaux maléfiques, mais il n'a pas pu résister. Il fut alors banni.","Le mage renégat a élu domicile dans la tour d'Ilgalar, dans les Carmines, et il a emporté avec lui un livre très puissant : le \"Traité d'Ur sur la Magie de l'Ombre\".","Nous, mages de Stormwind, voulons récupérer ce livre. Volez-le dans la tour d'Ilgalar et rapportez-le."}, {"Apporter le « Traité d'Ur sur la magie de l'Ombre » au Grand Sorcier Andromath à Stormwind."}},
[1939] = {"Grand sorcier Andromath", {"Le Grand Sorcier Andromath souhaite vous parler, <nom>. Il est en haut du Sanctuaire du Sorcier, dans le quartier des mages de Stormwind.","Il serait sage d'aller le voir, car Andromath est l'un des mages les plus puissants de Dalaran. Avec lui, le Conseil est en effet un Haut Conseil."}, {"Aller voir le Grand Sorcier Andromath."}},
[1940] = {"Soie d'araignée en parfait état", {"Vous avez servi sans faille, <nom>. Je pense qu'il est temps pour vous de revêtir un vêtement plus adapté à votre statut. Si vous allez chercher les matériaux nécessaires, nous ferons faire la robe qui convient.","Vous devez entrer dans le bois de la Pénombre et chasser les araignées qui s'y cachent. Prélevez leur soie, mais uniquement la plus fine et la plus pure. Quand vous en aurez suffisamment, apportez-la à Wynne Larson, des Couturiers Larson, dans le quartier des mages. Elle fera votre robe."}, {"Apporter 8 Soies d'araignée en parfait état à Wynne Larson à Stormwind."}},
[1941] = {"La robe de tisse-mana", nil, nil},
[1942] = {"L'Habit du noeud astral", nil, nil},
[1943] = {"Parler à Deino", {"La mage troll Deino a besoin de votre aide, <nom>. Elle réside à Orgrimmar, au pavillon de Buissombre."}, {"Aller voir Deino."}},
[1944] = {"Les Eaux de Xavian", {"La ville légendaire de Xavian est enfouie au plus profond des terres orientales d'Ashenvale et les initiés connaissent bien son histoire. Il est dit que les eaux de Xavian ont des pouvoirs magiques énormes.","Je voudrais un échantillon de ces eaux, pris à la source.","Prenez cette fiole et allez à Xavian. Recueillez de l'eau en bas de la chute la plus haute et rapportez-la-moi. Et prenez garde en vous déplaçant dans la cité ancienne : les satyres se sont installés dans les ruines et ils n'aiment pas que les mages viennent fourrer leur nez dans leurs affaires."}, {"Apporter l'Echantillon d'Eau de Xavian à Deino à Orgrimmar."}},
[1945] = {"Les sœurs rieuses", {"En remerciement de vos services, j'ai pris des dispositions pour vous faire confectionner de nouvelles robes, à condition que vous alliez chercher les matériaux nécessaires...","Pour ces robes, vous devez vous rendre à Ashenvale et prélever des boucles de cheveux sur les sœurs rieuses, les dryades qui se réunissent près de la retraite de Raynewood.","Une fois en possession de leurs cheveux, apportez-les à Kil'hala, un tailleur troll qui se trouve à la Croisée, dans les Tarides. Il vous attend et coudra vos robes."}, {"Apporter 12 Cheveux de sœurs rieuses à Kil'hala à la Croisée."}},
[1946] = {"L'Habit du néant", nil, nil},
[1947] = {"Voyager vers le Marais", {"Maintenant est venu le temps d'obtenir votre Baguette de mage. Tout d'abord, allez voir Tabetha, l'ermite humaine du marécage d'Âprefange. Sa cabane se trouve au fin fond du marais, à l'ouest de Theramore.","Adressez-vous à elle, car son savoir est immense. Vous trouverez sa cabane à l'ouest de Theramore, et juste au nord des ruines Cognepierres."}, {"Aller voir Tabetha."}},
[1948] = {"Les Objets de Puissance", {"La fabrication d'une Baguette de mage n'est pas aisée. Il faut des substances rares et il faut fabriquer un récipient pour recueillir de grandes quantités d'énergie magique. Vous devez me rapporter tout ça.","J'ai écrit sur ce parchemin ce dont vous aurez besoin et des instructions sur la façon de les obtenir. Apportez-moi exactement ce qui est sur cette liste et suivez mes instructions à la lettre, sinon ça pourrait mal tourner au moment de la fabrication de votre Baguette magique."}, {"Apporter un Jade et le Roncier de foudre à Tabetha, dans le marécage d'Âprefange."}},
[1949] = {"Des secrets cachés", {"Pour faire votre baguette magique, je dois lire des passages d'un certain livre. Ce livre, intitulé « les Rituels du pouvoir », a été écrit par le magus Tirth.","Je n'ai pas d’exemplaire de ce livre et aucune des grandes bibliothèques de ce monde n'en possède un. Vous devez donc aller voir Tirth et l’obtenir de lui.","Bonne chance ! Tirth était un grand mage et un grand savant, mais maintenant il passe tout son temps aux courses des gnomes et des gobelins des Salines, à boire et à parier."}, {"Parler au magus Tirth dans les Salines."}},
[1950] = {"Trouver le Scoop", {"Je peux vous aider pour le livre, mais d'abord vous devez m'aider !","L’ouvrage que vous cherchez est dans mon coffre-fort, fermé magiquement. Pour l'ouvrir, il faut prononcer une incantation spéciale et je ne me souviens plus de l'incantation ! D’habitude mon assistant m'aide à me rappeler de ces choses, mais je ne le trouve nulle part.","Si vous voulez mon livre, trouvez d'abord mon apprenti ! Demandez aux gens le long de la piste. Avec un peu de chance, quelqu'un l'aura vu. Ramenez-le ici, ou au moins qu'il vous donne l'incantation pour ouvrir le coffre !"}, {"Trouver l'Incantation pour ouvrir le coffre-fort de Tirth puis retourner voir Tirth."}},
[1951] = {"Rituels du Pouvoir", {"Hum... Maintenant que j'y pense, je n'ai plus ce livre ! Vous voyez, j'avais quelques dettes de jeu et pour les régler, j'ai vendu ma dernière copie des « Rituels du pouvoir » à un membre du clergé...","Un membre de la Croisade écarlate. Il a dû le mettre dans la bibliothèque du Monastère écarlate dans les Clairières de Tirisfal !","Si vous voulez ce livre, il va falloir que vous alliez le chercher au monastère. Et désolé pour tout ce tracas. Je ne suis plus moi-même ces derniers temps..."}, {"Apporter le livre des « Rituels du pouvoir » à Tabetha dans le marécage d'Âprefange."}},
[1952] = {"La baguette du mage", {"Je vais aller fabriquer les Baguettes magiques maintenant."}, {"Aller voir Tabetha après son rituel."}},
[1953] = {"Retourner dans le Marais", {"Il est temps pour vous de parler à l'ermite Tabetha, <nom>. Elle vous donnera des indications sur la fabrication de l'un des objets les plus importants pour les mages.","La cabane de Tabetha est loin dans le marécage d'Âprefange à l'ouest de Theramore."}, {"Aller voir Tabetha."}},
[1954] = {"L'orbe infernal", {"D'abord, il faut acquérir un orbe puissant. Les démonistes de la Lame ardente ont des orbes splendides, de qualité... Dommage qu'ils soient liés à des démons ! Mais ça peut s'arranger.","Rendez-vous en Désolace et prenez un orbe infernal à un Invocateur démoniste. Ils se retrouvent au convent de Mannoroc.","Lorsque vous aurez cet orbe, rapportez-le."}, {"Apporter un Orbe infernal à Tabetha dans le marécage d'Âprefange."}},
[1955] = {"L'Exorcisme", {"Maintenant que nous avons l'Orbe de l'Invocateur, nous devons exorciser le démon qu'elle contient. Je peux y arriver. C'est même la partie facile.","Vous, vous allez avoir à faire le plus dur : tuer le démon quand il va sortir.","Et il va falloir faire vite, sans ça il se réfugiera dans l'Orbe et il faudra tout recommencer !","Préparez-vous. Apportez vos sorts les plus puissants et gardez vos potions à portée de la main. Et amenez un ami, si vous en avez un. Parce que ça ne va pas être joli..."}, {"Tuer le démon de l'Orbe puis parler à Tabetha."}},
[1956] = {"L'Energie d'Uldaman", {"Le démon a été enlevé, et maintenant il nous faut une source d'énergie. Malheureusement, les plus puissantes sont les plus anciennes et les plus difficiles à acquérir ! Le destin n'est pas tendre avec nous, hein ?","Il va falloir que vous alliez à Uldaman pour y vaincre la sentinelle d'obsidienne. Il est grand, il est fort, et sa source d'énergie est pleine... d'énergie !","Je vous souhaite beaucoup de chance, <nom>. Et si vous n'avez pas cette chance, et bien... C'était un plaisir d'avoir pu faire votre connaissance."}, {"Récupérer une Source d'énergie en obsidienne et la rapporter à Tabetha dans le marécage d'Âprefange."}},
[1957] = {"Les éruptions de mana", {"Nous avons presque fini, mais la dernière étape est la plus périlleuse, <nom>.","J'ai l'orbe d'invocateur vide, prêt à être ensorcelé. Nous le ferons en canalisant l'énergie d'une faille de mana. Je jetterai un sort qui va en ouvrir une pour un court moment. Des créatures issues du mana en surgiront. Vous devrez combattre ces éruptions de mana pour extraire leur puissance, dont je m’emparerai.","Tuez suffisamment d'éruptions de mana avant que la faille ne se referme et je canaliserai toute leur énergie dans votre orbe."}, {"Tuer 12 Eruptions de mana dans la limite de temps imposée."}},
[1958] = {"L'Energie céleste", nil, nil},
[1959] = {"Rendre compte à Anastasia", {"Les mages d'Undercity ont noté une perturbation magique dans la ville et ont demandé à tous les mages de la Horde d’enquêter.","Anastasia Hartwell vous en dira plus. Allez la voir dans le quartier des mages de Undercity."}, {"Aller voir Anastasia Hartwell."}},
[1960] = {"Enquêter dans le magasin de l'Alchimiste", {"L'échoppe d'alchimiste d'Algernon, à l'Apothicarium, est l'objet de curieux troubles. Des créatures magiques, faites seulement de magie, la parcourent. Il doit bien y avoir une raison, et nous devons la découvrir !","Enquêtez à ce propos et capturez ces créatures. Prenez des coffres de stockage et un parchemin, derrière moi. Utilisez-le à l'échoppe pour rendre les créatures visibles, assujettissez-les à l'aide de vos sorts, puis capturez-les dans les coffres. Rapportez les coffres pleins, ceux qui sont vides et l'incantation."}, {"Obtenir une Incantation de Manifestation et une Caisse de Coffres de retenue placée derrière Anastasia Hartwell. Apporter 3 Coffres de retenue remplis, la Caisse de Coffres de retenue et l'Incantation de Manifestation à Anastasia Hartwell à Undercity."}},
[1961] = {"Rassembler les matériaux", {"Il est temps pour vous de porter vos premières robes de mage, <nom>. Elles sont le signe de votre statut et un témoignage de votre valeur.","Pour gagner ces robes, il vous faudra aller dans la forêt des Pins argentés et prendre des gemmes de mana aux mages de Dalaran. Leurs croyances sont semblables aux nôtres, il est donc assez désagréable de les assassiner, mais s'ils ne sont pas assez forts pour résister, alors ils ne méritent pas de vivre.","Apportez les gemmes et une longueur de lin à Josef Gregorian, à sa boutique de tailleur, dans le Quartier de la magie."}, {"Apporter 10 pièces de lin et 6 Gemmes de mana de Dalaran à Josef Gregorian."}},
[1962] = {"Robe du feu-sorcier", nil, nil},
[1963] = {"La Main brisée", {"Le Chef de guerre utilise des méthodes trop subtiles. Par prudence, il veut éviter de déchaîner le pouvoir de la Horde. Pourtant, nous avons été plus forts, nous avons été de fiers conquérants ! Nous devons retrouver notre gloire passée et la Main brisée va la reconquérir.","Nous tirons notre force de l'ombre et frappons nos ennemis en secret. Une tâche que vous devez accomplir pour prouver votre valeur.","Le troll Tazan est en affaires avec les pirates près de Ratchet. Il détient des informations dont j'ai besoin. Tuez-le et apportez-moi sa sacoche."}, {"Tuer Tazan et apporter sa Sacoche à Therzok, à la Faille de l'ombre à Orgrimmar."}},
[1978] = {"Les Traqueurs noirs", {"Le Syndicat ? Je ne m'en serais jamais douté. Il semble que ces messieurs d'Alterac soient tombés bien bas depuis leur trahison.","Le Seigneur Varimathras aura plaisir à apprendre ça et comme vous avez contribué à cette découverte, il convient que ce soit vous qui la lui annonciez.","Ah comment pouvais-je oublier !","Bienvenue chez les Traqueurs noirs…"}, {"Livrer le Grand Livre d'Andron à Varimathras au Hall de la Dame noire à Undercity."}},
[1998] = {"Fenwick Thatros", {"Le Seigneur Varimathras vient juste de m'envoyer une missive au sujet d'une menace concernant les Réprouvés, menace qui doit être immédiatement supprimée. Son nom est Fenwick Thatros, et le Seigneur Varimathras pense que c'est l'un des chefs morts-vivants qui ont pris pied dans la forêt des Pins argentés.","J'ai immédiatement pensé à vous, <nom>. J'ai assez confiance dans vos capacités pour penser que vous réussirez cette mission.","Les derniers rapports des Traqueurs noirs dans la forêt indiquent que Thatros doit se trouver près du vieux port abandonné sur Lordamere."}, {"Tuer Fenwick Thatros et rapporter sa Tête à Mennet Carkad dans le Quartier des voleurs d'Undercity."}},
[1999] = {"Les outils du métier", {"Les magiciens de Kirin Tor à Moulin-de-l'Ambre ont stocké d'importants documents se rapportant à l'état actuel de Dalaran. Nous tenons cela d'un de nos prisonniers. Le Seigneur Varimathras a ordonné de remettre ces documents aux Traqueurs noirs.","Cette tâche vous incombe, <nom>. Cependant, vous aurez besoin de certains équipements, et de la maîtrise de la technique du Crochetage. Allez voir Estelle Gendry, notre spécialiste, et demandez-lui un jeu d'outils avant de partir."}, {"Trouver le Rapport de situation de Dalaran et le remettre à Mennet Carkad dans le Quartier des voleurs d'Undercity."}},
[2038] = {"Les fournitures perdues de Bingles", {"Comme si le crash dans le Loch n'avait pas suffi, des troggs de l'île voisine m'ont attaqué et ont volé mes outils et mes capsuleurs soufflants Blastenheimer spéciaux ! Sans gyrocoptère et sans explosifs, le mouvement n'a aucune chance !","Rapportez-moi mes outils et mes capsuleurs soufflants, <nom>. Pour Gnomeregan !"}, {"Trouver et rapporter à Bingles son matériel :","La Clé de Bingles, le Tournevis de Bingles, le Marteau de Bingles et le Capsuleur soufflant de Bingles."}},
[2039] = {"Trouvez Bingles", {"Nous avons perdu contact avec Bingles ! On l'a envoyé en mission de reconnaissance sur Gnomeregan et ça fait une semaine qu'il n'a pas envoyé de rapport. Je sais juste qu'il est parti et s'est envolé.","La dernière fois qu'on a vu son gyrocoptère, c'était au-dessus du Loch. Peut-être que vous pourriez enquêter, <nom>."}, {"Trouver Bingles Blastenheimer, au Loch Modan."}},
[2040] = {"Assaut souterrain", {"Gnomeregan est tombé sous les contrôle de ces troggs maudits ! La situation est grave, mais vous pouvez peut-être nous aider, <nom>.","Au plus profond des mortemines, il y a un Déchiqueteur gobelin en état de fonctionnement. Trouvez-le et rapportez sa source d'énergie intacte. Elle permettra à nos Excavateurs gyrodrilmatiques de percer les murailles pour l'assaut !"}, {"Récupérer l'Indicateur Gnoam dans les mortemines et le ramener à Shoni la Silencieuse à Stormwind."}},
[2041] = {"Allez voir Shoni", {"Peut-être... Peut-être que vous pouvez nous aider dans la bataille de Gnomeregan.","Vous trouverez à Stormwind le commandant de notre équipe d'assaut souterraine, Shoni la Silencieuse. Shoni a besoin d'aide pour ses excavateurs gyrodrilmatiques.","Vous la trouverez probablement parmi les nains de Stormwind.","Bonne chance, <nom>."}, {"Aller voir Shoni la Silencieuse à Stormwind."}},
[2078] = {"La vengeance de Gyromast", {"Bouseux de terrien ! Tu dois me dégoter mon Premier Officier.","Prends c'te clé, mets-y dans le bon trou, et ramèn' le ici pour servir le cap'taine.","Ces murlocs et leur chef vont pas beaucoup aimer quand mon Premier Officier va leur mettre l'grappin dessus !"}, {"Trouver le Premier Officier de Gelkak, le Destructomembreur 4100, et le ramener à Gelkak."}},
[2098] = {"La recherche de Gyromast", {"La vie d'un pirate n'est pas facile, bouseux de terrien. C'est pas pillage et tafia à volonté ! Hélas ! Mon navire est à la baille, tout près du bord, à cause de ces saletés de battrodons.","J'ai b'soin que mon Premier Officier rapplique en ordre de marche !","Quand j'me suis ramassé sur le rivage, la clé de contact de mon Premier Officier est tombée sur les rochers et a été chapardée par des bestioles du cru. Du coin de l'oeil, j'ai vu des clampants enragés, des trotteurs des forêts géants et des murlocs fouiller l'épave..."}, {"Trouver et rapporter les 3 Morceaux de la clé de Gelkak à Gelkak Gyromast."}},
[2118] = {"Terres malades", {"Il y a une maladie qui s'étend à travers tout Sombrivage, contaminant les esprits et les organismes qu'elle touche.","Ce sont les ours des chardons qui ont été les plus touchés. Ces animaux, autrefois si nobles, sont devenus des instruments de destruction, enragés et frénétiques. J'ai peut-être trouvé un remède contre ce mal.","Apportez ce piège dans la forêt et placez-le au sol. N'importe quel ours des chardons enragé qui traversera cette lumière deviendra docile pour un court moment. Une fois que l'ours sera docile, il vous suivra. Ramenez-le ici, <nom>."}, {"Capturer vivant un Ours des chardons enragé, puis le ramener à Tharnariun."}},
[2138] = {"Nettoyage de l'infection", {"Comme je le craignais, le remède ne marche pas.","Cela m'attriste énormément de devoir ordonner la mort de certaines créatures de la nature, mais les ours des chardons malades et infectés doivent être éliminés.","Retournez dans la forêt et détruisez 20 ours des chardons enragés, <nom>. Cela ne suffira sans doute pas à éradiquer la contamination, mais ça permettra de limiter pendant quelque temps les dégâts que ces animaux causent à notre forêt et à ses habitants."}, {"Débarrasser la forêt de 20 Ours des chardons enragés et retourner voir Tharnariun Treetender, à Sombrivage."}},
[2139] = {"Espoir de Tharnariun", {"La survie de notre peuple et de nos terres a exigé des sacrifices, <nom>.","Hélas, vous avez dû verser le sang des serviteurs de la nature et bien des animaux sont tombés sous vos coups. Le peuple d'Auberdine vous doit beaucoup, et la souffrance de la forêt est apaisée, au moins pour le moment.","J'ai une dernière tâche à vous confier mais sachez qu'il vous faudra tuer de nouveau. J'espère de tout coeur que ce sera la dernière fois, <nom>.","À côté de Bashal'Aran, dans une caverne au nord-est d'ici, habite la Mère de la tanière. Faites-la disparaître."}, {"Trouver et tuer la Mère de la tanière."}},
[2158] = {"Repos et détente", {"Tous les aventuriers doivent prendre du repos s'ils veulent éviter l'épuisement... Et pour cela, le meilleur endroit, c'est bien l'Auberge de la Fierté du lion !","Mon meilleur ami, l'aubergiste Farley, tient cette auberge. Si vous venez de ma part, il vous fera sûrement des réductions sur le prix de la nourriture et de la boisson !","Suivez cette route vers le sud, vous ne pouvez pas la rater !"}, {"Parler à l'aubergiste Farley, à l'auberge de la Fierté du lion."}},
[2159] = {"Livraison à Dolanaar", {"Salutations, jeune <classe>. Pouvez-vous m'aider ? Je dois livrer ce paquet d'herbes dans la ville de Dolanaar, mais ne puis partir d'ici car j'ai une tâche à terminer avec les druides de Sombrevallon.","Pouvez-vous livrer ce paquet pour moi ? Il doit être remis à l'aubergiste Keldamyr, à l'auberge de Dolanaar. Elle se trouve sur cette route, au sud."}, {"Apporter la Livraison de Dolanaar à l'aubergiste Keldamyr."}},
[2160] = {"Des provisions pour Tannok", {"Hé ! Vous avez l'air d'être un cœur généreux. Si vous projetez de prendre le Passage, pourriez-vous apporter un paquet à l'auberge de Kharanos ?","Vous vouliez vous arrêter à l'auberge, non ? Si vous prenez le Passage, vous aurez certainement envie de vous y reposer.","Quoi qu'il en soit, apportez ceci à Tannok Frosthammer, l'assistant de l'aubergiste. Je ne peux pas passer le Passage moi-même et il faudra des jours avant qu'une escorte de Montagnards ne passe par ici !"}, {"Livrer la Caisse de ravitaillement à l'auberge de Tannok Frosthammer à Kharanos."}},
[2161] = {"Un Fardeau de Péon", {"Excusez-moi... Pouvez-vous m'aider ?","Vous voyez ce chargement ? J'ai suivi la route, j'ai bravé les scorpides, les araignées, que sais-je encore, tout ça pour apporter ces vivres à la vallée des Epreuves. Je pensais qu'ils en auraient besoin... Mais non, ils n'en ont pas voulu ! Maintenant, il ne me reste plus qu'à rapporter tout ça à Tranchecolline et ça me terrorise, à cause des bêtes...","Vous pourriez vous en charger pour moi ? Je ne suis qu'un malheureux péon mais vous... Vous n'avez peur de rien, vous avez l'habitude d'être héroïque !","Rapportez ce paquet à l'aubergiste Grosk à l'auberge de Tranchecolline. Merci !"}, {"Apporter le Fardeau d'Ukor à l'aubergiste Grosk à Tranchecolline."}},
[2178] = {"Brouet de trotteur", {"Sentez-vous cet arôme délicieux ? C'est ma recette secrète, <nom> : le brouet de trotteur frais. Délicieux !","La recette vous intéresse peut-être ?","Avant de vous l'apprendre, je veux voir comment vous vous débrouillez avec les bêtes.","Je ne m'attends pas à ce que vous sortiez en dompter un, mais si vous pouvez en abattre quelques-uns et rapporter leur succulente viande, ça suffira à me convaincre que je peux vous montrer comment cuisiner mon brouet de trotteur."}, {"Apporter 5 morceaux de Viande de trotteur à Alanndarian Nightsong, à Auberdine."}},
[2198] = {"Le Collier brisé", {"Vous avez trouvé un Collier terni mais sur lequel on devine encore des entrelacs compliqués et des signes magiques. Il a été brisé et on voit qu'il manque trois Gemmes. Sous le collier, on peut reconnaître le sceau de la ville d'Ironforge et, sous le sceau, trois toutes petites lettres : \"TdK\".","S'il y a quelque chose à tirer de ce Collier, il va falloir revenir à ses origines. Le sceau et les initiales sont déjà un début..."}, {"Chercher celui qui a fabriqué le Collier brisé pour découvrir sa valeur potentielle."}},
[2199] = {"Le Prix de la Connaissance", {"Je suis un joaillier de renom, ici, à Ironforge. Enfin, je l'étais... Je suis la victime d'un concurrent déloyal ! Dans ce métier, c'est la réputation qui fait tout... Et résultat, je me retrouve sans un sous, uniquement propriétaire d'un nom traîné dans la boue !","Je veux que vous me rapportiez cinq Barres d'argent pour terminer une commande qui m'a été faite - la première commande depuis des lustres !","Vous me rapportez ces barres d'argent et je répondrai à vos questions au sujet du Collier."}, {"Apporter 5 Barres d'Argent à Talvash del Kissel, à Ironforge."}},
[2200] = {"Retour à Uldaman", {"Un de mes concurrents a fait courir la rumeur que le collier avait maudit le paladin. Inutile de dire que personne ne vient acheter des bijoux maudits à un joaillier !","<nom>, je vous propose un marché : trouvez les gemmes et je réparerai le collier, à condition que vous affirmiez partout que je ne fabrique PAS de bijoux maudits !","Cherchez des indices à Uldaman ; voyez si le corps du paladin est là-bas ; après tout c'est lui qui en était le dernier propriétaire ! Quand vous aurez les gemmes, utilisez ceci pour me contacter, ici à Ironforge."}, {"Chercher des indices sur le Collier de Talvash à Uldaman. Le paladin dont il a parlé est la dernière personne à l'avoir porté."}},
[2201] = {"Trouver les Gemmes", {"Le journal du paladin est sans doute le seul indice dans Uldaman qui puisse vous conduire aux gemmes. Si ce qu'il prétend est vrai, il va falloir traiter avec les nains Ombreforge et les troggs qui combattent pour le contrôle de l'excavation.","La traque des gemmes doit commencer immédiatement. Lorsqu'elles seront récupérées, il faudra prendre les instructions de Talvash à Ironforge en utilisant la Fiole de Divination."}, {"Trouvez le rubis, le saphir et la topaze qui sont éparpillés à travers Uldaman. Une fois que vous les aurez, contactez Talvash del Kissel en utilisant la Fiole de divination qu’il vous a donnée précédemment.","C'est dans le journal, vous savez...","* Le rubis a été caché dans une zone barricadée des Ombreforge.","* La topaze a été dissimulée dans une urne, dans l’une des zones trogg, pas très loin de nains de Alliance.","* Le saphir est en possession du chef trogg, Grimlok."}},
[2202] = {"Rechercher les composants à Uldaman", {"On a découvert récemment une nouvelle plante : le champignon magenta. Ils poussent en grappes, au plus profond des excavations d'Uldaman ; il en existe peut-être ailleurs, mais je ne connais que cet endroit. Je veux étudier leur utilité éventuelle en alchimie, et c'est là que vous entrez en scène.","Trouvez les champignons magenta et rapportez-m'en une douzaine. Attention, les grappes peuvent projeter des spores vénéneux quand on les manipule !","Rendez-moi ce service et je sortirai pour vous un petit élixir revigorant de derrière les fagots."}, {"Apporter 12 Champignons magenta à Jarkal Mossmeld à Kargath."}},
[2203] = {"Les réactifs des Terres ingrates II", {"Votre aide m'a été précieuse, merci, <classe>. Je vous récompenserai en vous donnant la recette de l'Elixir que vous avez testé un peu plus tôt... mais je vais d'abord vous confier une mission dangereuse. Écoutez bien.","Ce coffret contient trois Récipients thaumaturgiques vides. Ils sont entourés d'une Aura d'harmonie qui attire le Sang de Gardien dragon brûlé. Attention ! Si vous n'en utilisez qu'un, la bête deviendra enragée. Lorsque vous aurez rempli les trois, rapportez-les-moi.","Vous aurez la recette de mon Elixir en échange."}, {"Utiliser les 3 Récipients thaumaturgiques vides sur des dragons gardiens des terres brûlées dans les Terres ingrates. Une fois remplis, les rapporter à Jarkal Mossmeld à Kargath."}},
[2204] = {"Réparer le Collier", {"Vous allez avoir besoin de \"pep's\" pour le collier, et l'endroit idéal pour en trouver, c'est Uldaman... Je suppose qu'il y a des tas de constructions qui se baladent par là-bas, non ? Il suffit de choisir la plus vile et la plus grande et de lui piquer sa Source d'énergie. Je suis presque sûr de pouvoir m'en servir pour le collier !","Sans source d'énergie, de toute façon, la réparation est impossible. Revenez-me voir à Ironforge quand vous l'aurez, et on verra ce qu'on peut faire !"}, {"Obtenir une Source d'énergie de la construction la plus puissante que vous pouvez trouver à Uldaman, puis la remettre à Talvash del Kissel à Ironforge."}},
[2205] = {"Chercher le SI:7", {"L'insigne ? Rien... Rien qui soit pour vous... Si j'en juge par votre apparence, j'ai peur que Mathias vous prenne pour le livreur de fromage, mon enfant, au lieu du malandrin attendu. Enfin, on m'a donné un travail et j'ai promis de livrer ce travail.","Tiens, à propos, vous apporterez ce paquet à Mathias, et peut-être qu'il vous donnera un ou deux petits boulots à accomplir.","Allez, du vent."}, {"Remettre le colis de Keryn Sylvius à maître Mathias Shaw à la Caserne de Stormwind."}},
[2206] = {"Effraction", {"Nos espions sur le terrain rapportent que les Défias sont très actifs dans le sud de Goldshire. Il semble qu'ils aient pris le Débarcadère de Jerod et qu'ils l'utilisent pour passer en contrebande de grandes quantités de produits entre les Carmines et la marche de l'Ouest.","Il faut que vous vous introduisiez dans les docks et que vous me rapportiez le programme d'embarquement des navires, <nom>. Vous le trouverez probablement au profond des poches de l'Intendant des docks.","... Et <nom>, si vous étiez découvert, sachez que le SI:7 nierait vous avoir jamais <connu/connue>."}, {"Trouver l'Intendant des docks défias et récupérer le programme d'embarquement, pour le compte de maître Mathias Shaw."}},
[2218] = {"La voie du salut", {"Une autre âme perdue en quête de conseil, hein ?Je suppose que vous cherchez une voie, votre voie. J'ai déjà vu ça mille fois, encore un héros désœuvré qui cherche du travail.","Eh bien, c'est aujourd'hui votre jour de chance, mon <ami/amie>. Hogral connaît la solution à votre tourment.","Voici ce qu'il vous faut faire : prenez cette route jusqu'à Ironforge et cherchez-y la caverne lugubre. Une fois sur place, localisez la guilde des voleurs et demandez entretien à Hulfdan Blackbeard. Il vous guidera vers votre salut."}, {"Parler à Hulfdan Blackbeard à Ironforge."}},
[2238] = {"Simple subterfuge", {"Vous voyez, le roi a épuisé les carrières en extrayant le minerai de fer et les gemmes. Cela fait plus de richesses que n'en pourrait amasser un nain en vivant dix vies.","Le Cercle secret a des valeurs, <nom>. Nous accordons de la valeur a ce qui a de la valeur. Vous comprenez ?","Nous pensons que le roi doit partager ce butin, mais nous ne voulons pas que \"Barbe-puante\" sache qu'il est en train de partager, pigé ?","Adressez-vous à Onin MacHammar à Gnomeregan et rendez moi compte. Il est habile, il se cache parmi les lépreux. Faites attention."}, {"Trouver Onin MacHammar à Gnomeregan."}},
[2239] = {"Rapport d'Onin", {"La carrière ? Ouaip, je vois ces monstrueux chars à vapeur qui passent près de là, presque tous les jours.","Mes contacts, à l'est, me disent que le char quitte Ironforge, charge sa cargaison à la carrière de Gol'Bolar, se ravitaille à Kharanos et après, pour une raison bizarre, emprunte le long chemin qui contourne Gnomeregan avant d'atteindre Ironforge où il décharge.","Tout est dans le rapport, <nom>. Portez-le à Hulfdan, qu'il puisse réfléchir à la suite du plan."}, {"Porter le rapport d'Onin à Hulfdan à Ironforge."}},
[2240] = {"La Chambre secrète", {"Bon... Pour la tâche en cours...","Il va falloir explorer une chambre secrète, la Chambre de Khaz'mul. Le problème, c'est qu'elle se trouve derrière une lourde porte fermée ! Il est heureux que je sache comment l'ouvrir !","J'ai tout écrit dans ce journal, là, sur la table. Pour l'instant nous sommes coincés ici. Trouvez la Chambre de Khaz'mul pour nous, <nom> !","Lisez mon journal, explorez la Chambre, puis rendez compte au Prospecteur Stormpike de la Ligue des Explorateurs à Ironforge."}, {"Lire le journal de Baelog, explorer la Chambre secrète, puis rendre compte au Prospecteur Stormpike."}},
[2241] = {"Pomme d'amour", {"Ils disent que je suis fleur bleue... quoi que cela puisse vouloir dire.","C'est vrai qu'à tout instant je pense à Syurna. C'est vrai, ma maison est tapissée de peintures et de dessins de Syurna. C'est vrai, je peux passer des jours sans manger, boire ou dormir en me languissant de l'amour que nous aurions pu connaître. Mais est-ce mal ?","Désormais, elle ne me verra plus ! MOI ! J'ai essayé de lui envoyer des messages, mais elle ne parlera même pas à quelqu’un qui n’est pas un voleur.","Pourriez-vous lui porter cette fleur ? Et n'oubliez pas de dire que c'est de la part de Jannok."}, {"Porter la fleur de Jannok à Syurna à Darnassus."}},
[2242] = {"L'appel du destin", {"Nous pensons qu'il existe un être qui pourrait nous aider à comprendre ce qui arrive aux forêts de Teldrassil. Malheureusement, toutes les tentatives pour amener la créature à coopérer se sont soldées par un désastre.","Il se cache sur une branche, sur le rebord de la clairière de l'Oracle. À ce jour, tous ceux qui ont approché le satyre, par la force ou la diplomatie, ont été anéantis. Le camouflage est peut-être la solution, <nom>.","Trouvez ce que Sethir l'Ancien cache dans ses poches et faites-moi un rapport."}, {"Trouver Sethir l’Ancien et rapporter tout indice découvert à Syurna."}},
[2258] = {"Les réactifs des Terres ingrates", {"<nom>, je pourrais avoir l'usage de vos capacités. Je suis un alchimiste de quelque renom et j'ai besoin de votre aide pour acquérir des composants que l'on ne peut trouver que dans les Terres ingrates.","J'ai besoin des objets suivants pour réapprovisionner mon stock : cinq Gésiers de busards, dix Crocs de coyote des rochers et cinq Fragments d'élémentaire de roche. A prendre sur les bêtes elles-mêmes, bien entendu... Si vous n'êtes pas du genre batailleur, trouvez quelqu'un qui le soit !","Trouvez-moi ces objets, et vous aurez quelques pièces en échange, ça vous va ?"}, {"Apporter 5 Gésiers de busard, 10 Crocs de coyote des rochers et 5 Fragments d'élémentaire de roche à Jarkal Mossmeld, à Kargath, dans les Terres ingrates."}},
[2259] = {"Erion Shadewhisper", {"Ne vous inquiétez pas, <nom>, je ne vous envoie pas voir Syurna. Erion veut que vous le rejoigniez dans l'Enclave de Cenarion. Faites vite !"}, {"Contacter Erion Shadewhisper à Darnassus."}},
[2260] = {"L'ordre d'Erion", {"Le Service de Renseignement de Stormwind (SI:7) a entendu parler de vos exploits, <nom>, et il veut vous recruter.","Le voyage est coûteux et périlleux, mais on ne rejette pas l'opportunité de s'entraîner pour le SI:7 !","Un nouveau monde s'ouvre à vous, <nom>. Rendez-vous dans la vieille ville de Stormwind et adressez-vous à Renzik \"le Kriss\" \" au QG du SI:7."}, {"Voyager vers Stormwind pour y rencontrer Renzik \"le Kriss\"."}},
[2278] = {"Les Disques de platine", {"Les pièces centrales de cette chambre au trésor sont quatre larges disques de platine ornés de sceaux d'origine inconnue. Lorsque vous les touchez, vous sentez un frémissement. Le visage d'un grand Gardien des pierres apparaît alors.","\"Salut à vous. Je suis le Gardien du Savoir. La synthèse des Créateurs de la Terre a été gravée et conservée dans les Disques de Norgannon.\"","Vous avez découvert une manne de savoir inconnu du monde entier !"}, {"Parler au Gardien des pierres pour connaître le Savoir qu'il défend. Une fois découvert ce qu'ils peuvent offrir, activer les Disques de Norgannon."}},
[2279] = {"Les Disques de platine", {"Votre main vibre de nouveau au contact des Disques de Norgannon. Cette fois, pourtant, c'est un étrange grincement qui se fait entendre.","Soudain, votre sac paraît plus lourd. Vous y trouvez une version miniature des Disques !","Voilà qui sera utile à tous ceux qui s'intéressent à l'histoire ancienne d'Azeroth. La Ligue des Explorateurs d'Ironforge est connue dans toute l'Alliance pour ses recherches sur le sujet ; ils devraient pouvoir faire bon usage de tout ceci."}, {"Apporter la version miniature des Disques de Norgannon à la Ligue des Explorateurs à Ironforge."}},
[2280] = {"Les Disques de platine", {"Votre main vibre de nouveau au contact des Disques de Norgannon. Cette fois, pourtant, c'est un étrange grincement qui émane des disques.","Soudain, votre sac paraît plus lourd. Vous y trouvez une version miniature des Disques !","Voilà qui sera utile à tous ceux qui s'intéressent à l'histoire ancienne d'Azeroth. Les Sages de Thunderbluff sont connus dans toute la Horde pour leurs recherches sur le sujet ; ils devraient pouvoir faire bon usage de tout ceci."}, {"Apporter la version miniature des Disques de Norgannon à l'un des Sages de Thunder Bluff."}},
[2281] = {"Le Rendez-vous des Carmines", {"Vous jeter maintenant sur le champ de bataille serait pur suicide. Renzik ne veut pas d'un autre cadavre de voleur dans son équipe. Mais pas d'inquiétude, <nom> : Renzik vous mettra à niveau.","Dirigez-vous vers les Carmines et discutez avec Lucius, un associé du SI:7. Vous le trouverez facilement, il traîne généralement autour des docks de Lakeshire avec d'autres bons à rien."}, {"S'aventurer vers Lakeshire dans les Carmines et parler à Lucius."}},
[2282] = {"La scierie d'Alther", {"J'entraîne des voleurs depuis des années ! Après tout, c'est pas n'importe quel vaurien qu'est né avec la grâce et la finesse du vieux Lucius.","Un peu plus loin, z'allez trouver la scierie d'Alther. J'y ai un coffre fermé qui contient que'q' chose d'important. Allez le récupérer pour moi. Ramenez-moi ce qui s'y trouve, et vous recevrez vot' Certificat de voleur !"}, {"Ouvrir le Coffret de Lucius, récupérer le Gage de voleur et le rapporter à Lucius à Lakeshire."}},
[2283] = {"Réparer le Collier", {"Nous avons du travail pour vous, si vous avez du cran...","Au plus profond de l'excavation d'Uldaman, dans les Terres ingrates, il paraît qu'il y a les restes d'un crétin de paladin qui est mort par là il y a pas longtemps. Ça nous intéresse pas, sauf qu'il avait un collier de prix avec lui. On dit qu'il y avait trois Gemmes dessus : un Rubis, un Saphir et une Topaze.","Bon alors voilà : on le paiera très cher. Il est peut-être abîmé mais on s'en moque. On le réparera."}, {"Rechercher dans l'excavation d'Uldaman un Collier de prix et le rapporter à Dran Droffers à Orgrimmar. Ce Collier peut être endommagé."}},
[2284] = {"Réparer le Collier, deuxième prise", {"Aussi vrai que Malton est un crétin, ces gemmes sont encore quêk part dans Uldaman. Va falloir y r'tourner.","Comme j'l'ai d'jà dit, si les informations que nous avons sont justes, il y a un rubis, un saphir et une topaze.","Où elles sont, comment je l'saurais ? Si vous avez du bol et qu'vous trouvez l'corps du paladin, et qu'il a pas encore été nettoyé, elles s'ront p'têt encore là."}, {"Trouver, dans les profondeurs d'Uldaman, des indices sur la localisation des Gemmes."}},
[2298] = {"Kingly Shakedown", {"Alors on était là, en train de comploter pour la phase III quand d'un coup, deux crétins de Magni se pointent en parlant de je ne sais quel coup, et d'arrêter ceux du Cercle secret.","Personne ne porte la main sur le vieux Blackbeard ! La discussion s'envenime, blabla, paf, et soudain on prononce vot' nom !","V'voulez un conseil ? Prenez le premier Griffon pour Stormwind. Une fois là-bas, allez voir Renzik \"le Kriss\" dans la vieille ville. C'est un vieil ami à moi."}, {"Aller à Stormwind et chercher Renzik \"le Kriss\"."}},
[2299] = {"Cap sur Hulfdan !", {"Où donc étiez-vous, <nom> ? Hulfdan vous cherche partout. On craignait que le roi ne vous ait fait mettre aux fers.","Allez à Ironforge dès que possible et parlez à Hulfdan !"}, {"Se rendre à Ironforge et parler à Hulfdan Blackbeard."}},
[2300] = {"SI:7", {"Renzik vous cherchait, l'ami.","Je ne ferais pas attendre le bras droit de Mathias à votre place."}, {"Aller à Stormwind et chercher Renzik \"le Kriss\"."}},
[2318] = {"La Traduction du Journal", {"Vous êtes dans l'impasse si vous n'arrivez pas à faire traduire le journal dans une langue que vous comprenez. Il doit bien y avoir des gens qui en sont capables, non ? L'endroit le plus proche où vous pourriez trouver un traducteur, c'est l'avant-poste de Kargath dans les Terres ingrates. C'est le mieux pour commencer !"}, {"Trouver quelqu'un qui puisse traduire le journal du paladin. L'endroit le plus proche est Kargath, dans les Terres ingrates."}},
[2338] = {"La Traduction du Journal", {"Ceux de la Récupération Droffers et fils sont trop bêtes pour restaurer la magie du Collier. Moi, je peux le faire. Je veux l'étudier pour apprendre à le confectionner moi-même. Pour ce faire, j'aurai besoin de votre collier. Laissez-le-moi et en échange je vous traduirai le journal. Le temps que vous trouviez les Gemmes, j'aurai le loisir d'apprendre ce qui m'intéresse.","Un marché simple et sans duperie... Alors, qu'en pensez-vous ?"}, {"Laisser Jarkal emprunter le Collier en échange de la traduction du Journal."}},
[2339] = {"Trouver les Gemmes et la Source d'énergie", {"Voici la traduction du journal, pour que vous puissiez le lire de votre côté.","Il est crucial de savoir que ce sont les nains qui ont caché le rubis, les troggs la topaze et que c'est un trogg nommé Grimlok qui a le saphir.","Bon. Ensuite, vous aurez besoin d'une source d'énergie pour que le Collier fonctionne de nouveau. Et ça, Dran Droffers ne le sait pas. Pour en trouver une, moi, j'irais au plus profond d'Uldaman et je détruirais les machines les plus puissantes. Elles doivent en disposer !","Quand vous aurez tout ça, revenez me voir."}, {"Récupérer, dans Uldaman, les trois gemmes et une source d’énergie pour le collier, puis les rapporter à Jarkal Mossmeld, à Kargath. Jarkal pense que vous pouvez trouver une source d’énergie sur la machine la plus puissante d’Uldaman.","C'est dans le journal, vous savez...","* Le rubis a été caché dans une zone barricadée des Ombreforge.","* La topaze a été dissimulée dans une urne, dans l’une des zones trogg, pas très loin de nains de Alliance.","* Le saphir est possession du chef trogg, Grimlok."}},
[2340] = {"Livrer les Gemmes", {"Un collier et des gemmes ordinaires marcheront aussi bien, du moment qu'ils sont faits du même matériau. Cette Source d'énergie, en revanche, c'est elle qui va donner sa magie au Collier... Enfin j'espère !","Je fournirai volontiers les gemmes nécessaires pour la confection de votre version du Collier, si toutefois cette Source d'énergie fonctionne.","Retournez à Orgrimmar, et terminez votre marché avec Droffers et Fils. Ne leur dites rien de notre rencontre et lorsque vous reviendrez, je pourrais bien avoir un cadeau pour vous."}, {"Apporter Les gemmes et le collier récupérés à Dran Droffers, à Orgrimmar."}},
[2341] = {"Réparer le collier, troisième prise", {"Pendant que Dran et Malton Droffers se disputent la prise, la moitié de l'affaire est maintenant close. Il vous reste à retourner dans les Terres ingrates pour finir tout ça. Jarkal Mossmeld, à Kargath, pourrait avoir une récompense spéciale à vous donner."}, {"Rendre visite à Jarkal Mossmeld à Kargath pour constater (ou non) son succès."}},
[2342] = {"Des Trésors à recouvrer", {"Balayer les enclos à chauve-souris avec ce balai, ce n'est pas digne d'un Garrett ! J'ai fait quelques économies avec ce boulot maudit, et je suis prêt à les dépenser pour améliorer mes conditions de travail.","L'arme familiale des Garrett est restée enfermée dans notre Coffre, dans le Hall commun sud d'Uldaman, où nous avons trimé comme mineurs. Sans elle, je suis coincé ici pour toujours, à balayer derrière ces immondes chauve-souris !","Rapportez-moi ce trésor de famille ! Aidez-moi à retrouver ma dignité !"}, {"Rapporter à Patrick Garrett, à Undercity, son Trésor de famille, qui se trouve dans le Coffre de famille dans le Hall commun sud d'Uldaman."}},
[2358] = {"Cornes de Nez'ra", {"Il y a bien des lustres, les Cornes de Nez'ra ont été volées au manoir de Ravenholdt. Je les ai cherchées par le monde entier, sur terre comme sur mer. Et tout ce temps, c’étaient ces bêtes immondes qui les avaient !","Exterminez tous les orcs rochenoires que vous croiserez jusqu'à ce que vous retrouviez les Cornes de Nez'ra.","Faites ceci pour moi, <nom>, et votre renommée atteindra Ravenholdt."}, {"Rapporter les Cornes de Nez'ra à Arantir, à Comté-du-Lac."}},
[2359] = {"La Tour de Klaven", {"Je suis là, à surveiller cette tour depuis des semaines, <nom>. Récemment, un chargement important est arrivé du bois de la Pénombre, protégé par ces gardes.","Que sont-ils ? Aucune idée. La seule chose que je puisse affirmer, c'est qu'ils ne sont pas humains.","Quoiqu'il en soit, dans cette cargaison repose un coffre, et je suppose que ces abominations sont commises à sa garde. Le gros avec les piques, là, possède une clé dont nous aurons besoin.","Récupérez cette clé, entrez dans la Tour et découvrez ce que contient ce coffre. Rapportez à Mathias le produit de votre recherche."}, {"Voler la Clé de la Tour défias, pénétrer dans la Tour et trouver ce que contient le Coffre du bois de la Pénombre. Rapporter les informations à Mathias Shaw à Stormwind. Lire le journal de Kearnen pour savoir ce qui se passe dans la Tour."}},
[2360] = {"Mathias et les Défias", {"Pas trop tôt, <nom>. Les éléments de reconnaissance du SI:7 nous apprennent les Défias sont inhabituellement actifs dans la marche de l'Ouest.","On dirait qu'ils ont pris une ancienne Tour de guet de l'Alliance abandonnée au sud-est de la Colline des sentinelles, et qu'ils s'en servent comme base d'opérations pour leurs trafics à partir de la route d'approvisionnement des Carmines.","Allez dans la marche et contactez l'Agent Kearnen. Elle est cachée à proximité de la tour.","... Oh, <nom>, si vous veniez à être <capturé/capturée>, le SI:7 nierait avoir eu connaissance, etc."}, {"Se rendre dans la marche de l'Ouest et trouver l'Agent Kearnen pour un débriefing."}},
[2361] = {"Réparer le Collier", nil, nil},
[2378] = {"Trouver la Main brisée", {"Vous avez bien servi les Traqueurs de ténèbres, <nom>, mais il est temps d'étendre votre expérience. Vous avez montré vos capacités en matière de ruse et de traîtrise, mais votre entraînement n'est pas terminé.","Les Traqueurs de ténèbres ont des alliés au sein de la Main brisée. Cherchez ceux qui viennent à notre aide : trouvez Shenthul à Orgrimmar."}, {"Voyager vers Orgrimmar pour y parler à Shenthul, dans la Faille de l'ombre."}},
[2379] = {"Zando'zan", {"Votre seule détermination ne suffira pas pour affronter les obstacles qui nous attendent.","Adressez-vous à Zando'zan pour commencer votre entraînement comme opérationnel de la Main brisée.","Lorsque vous maîtriserez le noble art du vol et de la tricherie vous pourrez faire un pas de plus vers votre destinée."}, {"Parler à Zando'zan à la Faille de l'ombre."}},
[2380] = {"Cap sur Orgrimmar !", {"Spécialiste de la forfaiture et de la tricherie, vous ne l'êtes pas, mais ça peut changer. Il semble que vous ayez impressionné les chefs avec vos maigres talents, puisque le grand Shenthul lui-même vous réclame.","Prenez la route vers le nord jusqu'à Orgrimmar et adressez-vous à Shenthul à la Faille de l'ombre."}, {"S'adresser à Shenthul à la Faille de l'ombre à Orgrimmar."}},
[2381] = {"Piller les Pilleurs", {"Sans vouloir vous effrayer, <nom>, les cinq derniers qui ont essayé de voler le trésor des Mers du sud ont été mis en pièces.","Le long de la côte, au sud, vous trouverez le \"Tranche-les-vagues\", le navire des Flibustiers des Mers du sud. Le trésor se trouve dans la cale. Au deuxième niveau se trouve la salle des pirates, une salle d'entraînement pour leurs voleurs.","Montez à bord, volez le trésor, et rapportez-le-moi. Utilisez la salle des pirates si vous voulez vous entraîner, mais attention : prudence !"}, {"Rapporter le Trésor des Mers du sud à Wrenix le Misérable à Ratchet. Ne pas oublier d’obtenir un B.G.A.C. et des Outils de voleur de l’Appareil bidulotronique de Wreni. Ces deux éléments sont nécessaires pour accomplir la mission.","En cas d’attaque par des perroquets particulièrement hostiles, utiliser le B.G.A.C.!"}},
[2382] = {"Wrenix de Ratchet", {"Zando'zan l'a vu ! Oui, le jour vient ! La mort de l'esclave... Le poignard dans le dos, le poison suintant de chaque veine... Zando'zan en a trop dit.","<nom> ! Zando'zan a une tâche pour <nom> ! <nom> doit aller à Ratchet, à l'est de la Croisée, pour rencontrer Wrenix le Misérable. Allez !"}, {"Aller jusqu'à Ratchet pour voir Wrenix le Misérable."}},
[2383] = {"Parchemin simple", {"Ah, pendant que vous n’étiez pas là un parchemin est arrivé pour vous, <nom>.","Lisez-le quand vous en aurez le temps. Si je ne me trompe pas, il provient du Maître des guerriers Frang. Il souhaiterait s’entretenir avec vous, quand vous jugerez le moment venu. Vous le trouverez à l’extérieur de l’Antre, dissimulé dans l’ombre."}, {"Lire le Simple parchemin, et parler à Frang, dans la Vallée des épreuves."}},
[2398] = {"Les nains perdus", {"Le site de fouilles d'Uldaman n'a pas toujours été mal fréquenté. Nous, les nains d'Ironforge, contrôlions le secteur... jusqu’à la venue des troggs. Ils sont arrivés si vite et si nombreux que même des nains n'ont pas pu les arrêter !","Leur attaque a été si soudaine que je crains que certains des nôtres soient pris au piège derrière les lignes ennemies.","Mon ami Baelog a conduit une petite troupe assez loin dans le site avant d'être repéré. S'ils sont vivants, je veux que vous les trouviez."}, {"Trouver Baelog dans Uldaman."}},
[2399] = {"Les racines ont poussé", nil, nil},
[2418] = {"Les pierres de puissance", {"Je viens d'avoir un problème avec les nains Ombreforge. Ils m'ont amené à la forteresse d'Angor pour étudier deux sortes de pierres étranges venant d'Uldaman. J'ai découvert que ces pierres, à condition d’être bien utilisées, étaient des sources d'énergie stables. C'est étonnant tout ce qu'on peut trouver sur ce vieux site !","Bref, après leur avoir appris le secret des pierres, ils m'ont chassé de la forteresse et ont commencé les fouilles.","Je veux ces pierres ! Apportez-moi ces deux sortes de pierres et je vous paierai bien !"}, {"Apporter 8 Pierres de puissance Dentrium et 8 Pierres de puissance An'Alleum à Rigglefuzz dans les Terres ingrates."}},
[2438] = {"Le Capturêve d'émeraude", {"Un jour, Gaerolas Talvethren, le Gardien des druides du Refuge des saisons de Ban'ethil, m'a donné un Capturêve d’émeraude. Cette puissante amulette est capable d'aspirer l'énergie du Rêve d'émeraude pour donner de la chance à ceux qui la portent.","Hélas, je n'ai pas pu la récupérer dans ma commode au village de Brise-stellaire... Autrefois, Brise-stellaire était un endroit tranquille, mais il a succombé à la corruption des furbolgs qui y résident.","Voudriez-vous récupérer mon Capturêve, <classe> ?"}, {"Apporter le Capturêve d'émeraude à Tallonkai Swiftroot à Dolanaar."}},
[2439] = {"Les Disques de platine", {"Cet objet est incroyable. Si ce qu'il dit est vrai, les anciens Titans, ou les Créateurs comme ces disques les nomment, sont le lien avec notre création enchantée ! Nous devons investir autant de temps que possible dans cette recherche.","Pour vous, héroïque <classe>, apportez ce bon à Dinita Stonemantle à la Banque. Il vous donnera une récompense à la hauteur de cette magnifique trouvaille !","Revenez directement me voir quand vous serez <prêt/prête>, il y a encore beaucoup à faire pour la Ligue !"}, {"Apporter le Bon de récompense à Dinita Stonemantle à la Chambre forte d'Ironforge."}},
[2440] = {"Les Disques de platine", {"Je vais concentrer mes travaux sur ces disques ; ils me révéleront bientôt leur raison d’être.","<nom>, je vais vous récompenser pour la diligence avec laquelle vous m'avez apporté ceci. Portez ce bon à Bena Winterhoof, le maître des Alchimistes de Thunder Bluff. Elle vous offrira un objet spécial que vous apprécierez sûrement et elle vous proposera un choix de breuvages magiques de ma réserve personnelle.","Revenez vite me voir, car je sens qu'il y a du pain sur la planche pour étudier cette trouvaille."}, {"Apporter le Bon de récompense à Bena Winterhoof à Thunder Bluff."}},
[2458] = {"Sous le couvert de la nuit", {"La KapitalRisk... Vous avez entendu parler d'eux ?","Depuis un an, nous essayons d'y introduire un de nos agents. Nous avons travaillé dur, mais prudemment, et nous avons réussi.","Nous venons d'apprendre que la KapitalRisk projette une opération de grande envergure. Allez au nord de la Videfange, à la tour de la KapitalRisk, et contactez notre agent, le sous-chef Fizzule.","Une fois sur place, utilisez ce lance-fusées, approchez Fizzule et faites-lui le salut de la Main brisée."}, {"Aller à la tour de la KapitalRisk, au nord de la Videfange, et contacter l'agent secret de la Main brisée, le sous-chef Fizzule.","Une fois la tour en vue, utiliser le lance-fusées pour se signaler au sous-chef Fizzule. Tirer DEUX fusées, puis s'approcher du sous-chef Fizzule et faire le Salut brisé (/salut).","Respecter l'ordre de ces actions, car Fizzule attaque et tue toute personne qu'il ne peut pas identifier."}},
[2459] = {"Ferocitas le Mangeur de rêves", {"L'émeraude... Elle a disparu ! Mon capturêve a été endommagé !","Une bande de Mystiques Pins-tordus rôde au nord de Brise-stellaire. J'ai entendu dire que leur chef, Ferocitas le Mangeur de rêves, porte un collier qui émet des lueurs vertes pendant la nuit. Vu l'état de mon capturêve, il est certain qu'il a volé mon émeraude... Il ne comprendra jamais que sa puissance lui est inutile.","Trouvez mon bijou disparu, <nom>. Et tant que vous y êtes, profitez-en pour nous débarrasser de quelques Mystiques corrompus."}, {"Sur ordre de Tallonkai Swiftroot de Dolanaar, tuer 7 Mystiques Pins-tordus et retrouver le Bijou disparu."}},
[2460] = {"Le Salut brisé", {"En tant que voleur de la Main brisée, il est impératif que vous appreniez le Salut brisé. C'est la seule façon d'identifier un autre membre de la Main.","Regardez, apprenez, répétez."}, {"Faire le Salut brisé à Shenthul."}},
[2478] = {"Mission : possible mais non probable", {"Vous devez voler la clé de la tour au contremaître Silixiz, <nom>. Une fois la clé en votre possession, gardez-la soigneusement, je pourrais en avoir besoin plus tard.","Profitez de l'état d'affaiblissement des habitants empoisonnés, et laissez donc votre tendance au chaos et à la destruction s'exprimer librement. Tuez-les tous ! Ne montrez aucune pitié !","Lorsque tous baigneront dans leur sang, ouvrez le coffre de Gallywix et volez la Mixture de Zanzil. Apportez la tête de Gallywix ainsi que la Mixture à Shenthul à Orgrimmar."}, {"Voler la Clé de la tour de Silixiz au contremaître Silixiz, puis tuer deux Pantins mutants de la KapitalRisk, deux Patrouilleurs de la KapitalRisk, et deux Sentinelles de la KapitalRisk.","Assassiner le grand contremaître Gallywix et prendre sa tête. Ouvrir le coffre de Gallywix et y voler la Mixture altérée de Zanzil. Apporter la Mixture et la tête de Gallywix à Shenthul à Orgrimmar, et attendre de nouvelles instructions."}},
[2479] = {"L'aide d'Hinott", {"Apportez un échantillon du mélange à Serge Hinott à Moulin-de-Tarren.","Si quelqu'un peut vous guérir, c'est bien Serge. Si vous vivez suffisamment longtemps, il vous montrera comment créer un poison et comment l'utiliser."}, {"Aller à Moulin-de-Tarren dans les contreforts d'Hillsbrad et livrer l'Échantillon de la mixture de Zanzil à Serge Hinott.","Pour rejoindre Moulin-de-Tarren, prenez le zeppelin à Undercity et suivez la route du sud, à travers les Pins argentés et vers Hillsbrad. Suivez les panneaux !"}},
[2480] = {"L'aide d'Hinott", {"Je n'ai jamais vu d’aussi merveilleux résultats pour un poison ! Zanzil, vous dites ? Jamais entendu parler de lui, mais je m'incline devant le mélange qu'il a créé.","Bon, vous voulez que je vous guérisse et que je vous apprenne ensuite à utiliser le poison ?","Laissez-moi un peu de temps pour examiner la toxine, <nom>."}, {"Attendre Serge Hinott pour achever le remède."}},
[2498] = {"Retour vers Denalan", {"Je viens de recevoir un mot de Denalan au lac Al'Ameth, disant qu'il a peut-être découvert la cause les tumeurs chez les sylvains. Allez le voir et dites-lui que je vous envoie.","Faites vite, <nom>, il a besoin de nous."}, {"Sur ordre de Rellian Greenspyre, aller voir Denalan au lac Al'Ameth."}},
[2499] = {"Froncechêne", {"Dans une caverne le long de la rive sud du lac, un sylvain nommé Froncechêne propage la corruption parmi les créatures qui vivent au lac Al'Ameth.","Je n'ai pas osé m'approcher, mais c’est visible à distance : il est parasité par la plus grande tumeur que j'ai jamais vue... On pourrait même dire gargantuesque.","<nom>, vous m’avez déjà été d'un grand secours, mais je vous demande encore cela : pourchassez-le et débarrassez-le de sa tumeur, éliminez cette source de contamination de chez moi."}, {"Sur ordre de Denalan du lac Al'Ameth, ôter la Tumeur gargantuesque de Froncechêne."}},
[2500] = {"Les réactifs des Terres ingrates", {"<classe>, vos services vont m’être utiles. Je suis alchimiste et j'ai besoin de votre aide pour trouver certains réactifs dans les Terres ingrates.","J'ai besoin de ceci pour ma réserve : 5 Gésiers de busard, 10 Crocs de coyote des rochers et 5 Fragments d'élémentaire de roche. Évidemment, vous pouvez trouver les réactifs sur les créatures elles-mêmes. Si vous n'êtes pas du genre battant, trouvez donc des amis qui le soient.","Apportez-moi tout ça et je vous récompenserai.","Qu'en dites-vous ?"}, {"Aller chercher les réactifs dont Ghak Healtouch a besoin dans les Terres ingrates, puis les lui rapporter à Thelsamar."}},
[2501] = {"Les réactifs des Terres ingrates II", {"Vous m'avez été des plus utiles, <nom>. Votre récompense sera la recette de la boisson que vous avez goûtée tout à l'heure... Mais j'ai une tâche très dangereuse pour vous.","Cette boîte fermée contient trois Récipients vides ; ils sont imprégnés d'un fluide magique qui drainera le sang d'un dragon gardien des terres brûlées. Je vous préviens : le fait de les utiliser irrite beaucoup les bêtes. Une fois que vous aurez rempli les trois, apportez-les-moi.","Ensuite la recette sera à vous !"}, {"Utiliser les Récipients thaumaturgiques vides sur des dragons gardiens des terres brûlées dans les Terres ingrates. Une fois remplis, les rapporter à Ghak Healtouch à Thelsamar."}},
[2518] = {"Les larmes de la Lune", {"Autrefois, dame Sathrah était aimée d'Elune. Gracieuse et pure, l'araignée tissait ses toiles d'argent au clair de lune pour recueillir la brume du soir. La rosée argentée avait de grands pouvoirs de guérison et on la conservait au temple.","Mais récemment, dame Sathrah a plongé dans la folie. Sa descendance à venir est également menacée.","Trouvez dame Sathrah, <nom>, et mettez fin à ses souffrances. Elle demeure le long des frontières, au nord de Teldrassil, près du lac d'Aigue-vive. Prenez ses filières d'argent et apportez-les-moi."}, {"Apporter les Filières d'argent de dame Sathrah à la prêtresse A'moora, au temple de la Lune, à Darnassus."}},
[2519] = {"Le Temple de la Lune", {"Une créature de la forêt, autrefois l'une des plus grandes, a besoin de votre aide, <nom>.","C'est une tâche difficile, mais qui doit être accomplie.","S'il vous plaît, allez voir la prêtresse A'moora, elle vous expliquera de quoi il s'agit. Elle est au sud-est, dans le Temple de la Lune."}, {"Sœur Aquinne veut que vous parliez à la prêtresse A'moora, au temple de la Lune."}},
[2520] = {"Sacrifice de Sathrah", {"Maintenant vous devez finir la tâche qui vous a été confiée. Prenez les Filières d'argent et offrez le sacrifice de dame Sathrah à Elune. Permettez à la Grande araignée de trouver le repos.","Dans le temple, trouvez la fontaine centrale. C'est là que vous déposerez le sacrifice. Les eaux saintes vont nettoyer et purifier la corruption qui a conduit Sathrah à la folie.","Quand vous aurez fini, revenez me voir."}, {"Sur ordre de la prêtresse A'moora, placer les Filières d'argent de dame Sathrah dans la fontaine du Temple, puis retourner la voir."}},
[2521] = {"Au service de Kum'isha", {"<nom>, votre cerveau étriqué peut-il comprendre la rareté d'une sphère de draenéthyste sans défaut ? Les draeneï ont consacré des vies entières à chercher un seul de ces cristaux et ils n'ont rencontré que des destins horribles.","Et vous voilà devant Kum'isha, sachant ce qu'il veut et souhaitant toujours l'aider ? Très bien, <nom>... Si en parcourant ces terres, vous trouvez une sphère de draenéthyste sans défaut, apportez-la-moi et je vous récompenserai au-delà de vos rêves les plus fous."}, {"Si vous trouvez une sphère de draenéthyste sans défaut au hasard des voyages dans les Terres foudroyées, apportez-la à Kum'isha le Collecteur.","Cette gemme, l'une des plus rares de tout Azeroth, peut être détenue par n'importe qui dans le pays.","Une récompense sera donnée pour chaque sphère de draenéthyste sans défaut trouvée et rapportée."}},
[2522] = {"Les efforts de Kum'isha", {"Bien que l'invocation de portail ait échoué, j'ai bon espoir que la prochaine tentative sera la bonne.","L'espoir des draeneï est aussi fort que votre soif de bibelots, <nom>. Retournez dans ces terres maudites et trouvez une autre sphère de draenéthyste sans défaut.","Bien sûr, vous êtes libre d'accepter ou non cette tâche *grand sourire*."}, {"Trouver une Sphère de draenéthyste sans défaut et l'apporter à Kum'isha le Collecteur"}},
[2523] = {"La fleur-de-chant corrompue", nil, nil},
[2541] = {"Le druide dormant", {"Les chamans Pins-tordus qui habitent cet endroit ont découvert comment séparer l'esprit d'un druide dormant de son corps physique. Les furbolgs ont ranimé ma forme physique et l'utilisent pour attaquer ceux qui essaient d'explorer le Refuge des saisons de Ban'ethil. Je suis impuissant, pris au piège dans le Rêve d'émeraude.","Vous devez m'aider. Les chamans Pins-tordus portent un charme étrange qu'ils utilisent pour exécuter ce rituel et je voudrais l'examiner. Je vous en prie, <nom>, il m’en faut un."}, {"Rapporter un Charme vaudou de chaman à Oben Grifferage dans le Refuge des saisons de Ban'ethil."}},
[2561] = {"Le druide de la Griffe", {"J'ai examiné ce charme, <nom>, et je vois maintenant ce qu'il faut faire. Prenez-le et suivez mes instructions.","Vous devez explorer les zones les plus reculées du Refuge des saisons de Ban'ethil pour trouver mon corps sans âme... Je regrette de devoir en arriver à cette extrémité, mais je ne vois pas d'autre moyen de me libérer du contrôle des Pins-tordus.","Vous devez tuer ma forme physique. Ensuite, utilisez le Charme vaudou sur mon corps. Lorsque vous aurez accompli cette tâche,revenez me voir."}, {"Sur ordre d'Oben Grifferage, tuer son Corps privé d'âme et utiliser ensuite le Charme vaudou."}},
[2581] = {"Joues de Raillecroc", {"Tout le monde sait que la mâchoire des hyènes possède une force énorme, mais jusqu’à quel point ? Au point de mordre dans trois pouces d'acier au thorium ? Impossible ! Improbable ! Et pourtant je l'ai vu de mes propres yeux.","Allez dans les Terres foudroyées et apportez-moi trois joues de Raillecroc. En cherchant les Raillecroc, trouvez aussi les sangliers et les scorpoks : la préparation initiale exige deux Poumons de sanglier éclatés et une Pince de scorpok."}, {"Apporter 3 Joues de Raillecroc, 2 Poumons de sanglier éclatés et 1 Pince de scorpok au Mage de sang Drazial."}},
[2582] = {"La colère des âges", {"Oh <nom> ! Bienvenue. On dirait que vous avez apprécié l'effet de ce mélange tiré des Raillecroc. Prenez garde, si l'effet immédiat est un fantastique regain de force, à long terme les effets pourraient être dangereux pour votre esprit et votre intelligence.","Sachant cela, en voulez-vous encore ? Dans ce cas, apportez-moi trois autres joues de Raillecroc, deux poumons de sanglier éclatés et une pince de scorpok."}, {"Apporter 3 Joues de Raillecroc, 2 Poumons de sanglier éclatés et 1 Pince de scorpok au Mage de sang Drazial."}},
[2583] = {"La vitalité du sanglier", {"Sur ces terres désolées vivent deux espèces de sanglier : le sanglier cendré et le sanglier infernal.","L'endurance de ces bêtes les rend presque invulnérables, et même les autres prédateurs les évitent.","Nous devons faire de nouvelles analyses, <nom>. Si vous m'apportez trois Poumons de sanglier éclatés, deux Pinces de scorpok et un Cerveau de basilic, je créerai un élixir divin. Vous serez mon premier cobaye !"}, {"Apporter 3 Poumons de sanglier éclatés, 2 Pinces de scorpok et 1 Cerveau de basilic au Mage de sang Drazial."}},
[2584] = {"L'esprit du sanglier", {"Quand je vous ai administré l'élixir, <nom>, je savais qu'une ou deux applications ne rassasieraient pas votre appétit. Et maintenant, je peux voir la faim dans vos yeux. Le mélange provoque une dépendance, à laquelle même les plus forts ne résistent pas.","Qu'il en soit ainsi ! Si vous voulez plus de Jus de poumon, retournez dans le désert et trouvez-moi trois poumons de sanglier éclatés, deux pinces de scorpok et un cerveau de basilic."}, {"Apporter 3 Poumons de sanglier éclatés, 2 Pinces de scorpok et 1 Cerveau de basilic au Mage de sang Drazial."}},
[2585] = {"Le coup décisif", {"Avez-vous observé les scorpoks au combat, <nom> ? Avez-vous vu la grâce et l'agilité avec lesquelles ils se déplacent ?","Si seulement nous pouvions isoler cette agilité et l'encapsuler, nous serions riches au-delà de... je veux dire... ce serait bien pour la communauté scientifique.","Aidez-moi, <nom> et je vous permettrai d'essayer le résultat.","Pour faire un échantillon stable, j'ai besoin de trois Pinces de scorpok, de deux Gésiers de vautour et d'un Poumon de sanglier éclaté."}, {"Apporter 3 Pinces de scorpok, 2 Gésiers de vautour et 1 Poumon de sanglier éclaté au mage de sang Drazial."}},
[2586] = {"Le sel de scorpok", {"Vous êtes en vie ! C'est une bonne nouvelle, vraiment. Je suppose que vous revenez pour avoir plus de préparation de scorpok ?","Très bien, <nom>, retournez dans le désert et ne revenez pas avant d'avoir prélevé trois pinces de scorpok, deux gésiers de vautour et un poumon de sanglier éclaté."}, {"Apporter 3 Pinces de scorpok, 2 Gésiers de vautour et 1 Poumon de sanglier éclaté au mage de sang Drazial."}},
[2601] = {"La morsure du basilic", {"Ces terres désolées sont envahies par les basilics Rougepierre, <nom>. Ils se sont adaptés et multipliés plus que n'importe quelle autre espèce.","J'ai observé leur technique de chasse et leur comportement : leur processus de pensée est plus complexe que celui de certains humanoïdes.","Je veux étudier leur intelligence !","Rapportez-moi dix cerveaux, <nom>. Cependant, le composé ne sera pas complet sans deux gésiers de vautour pour former la base de la solution."}, {"Apporter 10 Cerveaux de basilic et 2 Gésiers de vautour au Mage de sang Lynnore."}},
[2602] = {"Un esprit infaillible", {"Votre concentration est déjà retombée ? Il faut faire d'autres recherches !","Oh oui ! Le prix est le même, <nom>. Retournez dans les Terres foudroyées et revenez me voir avec dix cerveaux de basilic Rougepierre et deux gésiers de vautour."}, {"Apporter 10 Cerveaux de basilic et 2 Gésiers de vautour au Mage de sang Lynnore."}},
[2603] = {"La vigueur du vautour", {"La mort a réellement des ailes dans les Terres foudroyées, <race>. J'ai vu les Pourfendeurs noirs et les Racleur-d'os festoyer sur de nombreux cadavres d'explorateurs et déchiqueter même les carcasses les plus atteintes par la maladie. Je me demande comment leur système neutralise la peste qui sévit sur ces terres.","Aidez-moi dans mes études, <nom> ! J'ai besoin de gésiers ! Egalement de deux joues de Raillecroc pour la base du mélange.","La récompense de vos efforts sera double."}, {"Apporter 10 Gésiers de vautour et 2 Joues de Raillecroc au Mage de sang Lynnore."}},
[2604] = {"Domination spirituelle", {"Je vois que votre feu intérieur a diminué. Cela voudrait-il dire que l'euphorie n'a duré qu'une heure ?","Si vous voulez plus de Gomme de gésier, alors j'ai besoin de plus de gésiers. Retournez dans le désert, <nom>. Et ne revenez pas avant d'avoir récupéré dix gésiers et deux joues."}, {"Apporter 10 Gésiers de vautour et 2 Joues de Raillecroc au Mage de sang Lynnore."}},
[2605] = {"Le gobelin assoiffé", {"J'ai une idée… J'ai entendu parler de créatures étranges appelées collecteurs de rosée, vivant au sud-ouest... Elles sont capables de stocker de l'eau dans des glandes spéciales.","Je pense que si je pouvais me procurer l'une de ces glandes, je pourrais mettre au point une boisson qui étancherait ma terrible soif ! Pensez-vous que vous pourriez m'aider, <nom> ?","Allez au jardin des cactus, <nom>, c'est là que vous trouverez les collecteurs de rosée."}, {"Trouver une Glande de rosée remplie et l'apporter à Marin Noggenfogger à Gadgetzan."}},
[2606] = {"De bon goût", {"Beurk ! Cette substance a un goût épouvantable ! Trop pour mon idée...","Attendez, qu'est-ce que c'est que ça ? Oh, je sais ! Vous pensez que je dois continuer de croire en cette substance. Bon alors...","J'ai une idée brillante ! Apportez cette rosée à mon amie Sprinkle, c'est un excellent chef. Dites-lui où nous en sommes et ce que nous voulons faire.","Je veux dire, si c'est aussi mauvais, il y a sûrement quelque chose à en faire... C'est juste que moi, je ne le sens pas..."}, {"Sur ordre de Marin Noggenfogger, aller voir Sprinkle à Gadgetzan."}},
[2607] = {"Le toucher de Zanzil", {"On dirait que vous avez rapporté plus d'informations que je n'en attendais. Les événements prennent une tournure étrange, <nom>.","Un remède ? Hum... Rendez-vous à la Cave et allez voir le docteur Mixilpixil, il pourra peut-être vous aider."}, {"Aller voir le docteur Mixilpixil dans la Cave de la caserne."}},
[2608] = {"Le toucher de Zanzil", {"Oh Ciel ! Vous ressemblez à la mort. Je corrige : j'ai vu la mort, vous semblez en pire état. Vous ressemblez à ce que serait la mort à son dernier instant.","Ah ! Vous entendez ce Noarm ? Notez-le !","*soupir* Hum ! Oui, pour vous aider, je dois prendre un peu de votre sang. Ne vous inquiétez pas, ça ne fait mal que les… cinquante premières fois. Allongez-vous (/coucher) quand vous serez <prêt/prête> pour le traitement, <nom>."}, {"S'allonger (/coucher) pour être <examiné/examinée> par le docteur Mixilpixil. Comment peut-il trouver le remède s'il ne peut pas trouver la cause ?"}},
[2609] = {"Le toucher de Zanzil", {"Le diagnostic est unanime, <nom>. Vous êtes <atteint/atteinte> de “itis”.","Heureusement, j'ai un remède.","Voilà ce dont j'ai besoin : une brassée de fleurs sauvages simples, une fiole plombée, un tube en bronze et une bobine de fil de soie chartreuse. La vieille Emma en a laissé pour moi au \"Fil le plus fin”. Le magasin se trouve le long des canaux.","Dépêchez-vous <nom>, et ne touchez personne jusqu'à votre guérison. Oh ! Montgomery dit que vous devriez prendre une douche – c'est lui qui le dit, pas moi."}, {"Apporter au docteur Mixilpixil une Brassée de Fleurs sauvages simples, une Fiole plombée, un Tube en Bronze et une Bobine de Fil de soie chartreuse. Le itis ne guérit pas tout seul."}},
[2621] = {"Le déshonoré", {"Désolé. C'est tout ce que j'ai pour l'instant. Une mauvaise décision aux conséquences horribles. Si seulement j'avais attendu ces ordres. Peut-être détaillaient-ils les horreurs qui nous attendaient ! Peut-être devaient-ils nous guider autrement ? En fin de compte, c'est ma faute. Tout cela est ma faute.","Je suis là et j'attends les ordres. Je dois savoir. Je dois avoir ces ordres.","Le commandant d'ordonnance Ruag réside à Stonard. Trouvez-le et découvrez ce qui est arrivé aux ordres."}, {"Aller voir le commandant d'ordonnance Ruag dans Stonard."}},
[2622] = {"Les ordres disparus", {"Les ordres ? Que voulez-vous faire avec ?","Pourquoi vous impliquez-vous, <nom> ? Bien sûr que j'ai relayé ces ordres ! Les soldats auxquels j'ai envoyé ces ordres ont été pris en embuscade par les murlocs. Un seul s'en est sorti, si on peut dire. Il peut à peine parler, ne peut pas marcher et on l'alimente par un tube. Si vous voulez savoir où sont ces ordres, allez voir Bengor. Il est quelque part par là... et, <nom>, méfiez-vous des endroits où vous fourrez votre nez : il pourrait bien être mordu."}, {"Aller voir Bengor."}},
[2623] = {"Le Parleur des marécages", {"Le commandant d'ordonnance a donné des ordres. Nous avons pris... pris des ordres. Sur le chemin des Terres foudroyées, pris en embuscade.","Les murlocs... Beaucoup de murlocs... Un qui s'appelle le Parleur des marécages. À pris les ordres de mes mains avant de me frapper le crâne avec sa lance. À l'est... la caverne...","<Bengor trépasse.>"}, {"Retrouver les Ordres du Chef de guerre et les rendre au Héros déchu de la Horde."}},
[2641] = {"L'ingrédient secret de Sprinkle", {"Quand j'étais gosse, ma sœur faisait à manger. C'était horrible ! Mon estomac ne supportait pas sa cuisine, jusqu'à ce que je trouve ce que j'aime à appeler mon ingrédient \"secret\" ! Ajoutez-le à n'importe quoi et... ouah ! le goût devient génial !","De quoi je parle ? D'un champignon, le tragan violet. Si vous voulez que je vous aide à rendre buvable ce breuvage écœurant, allez aux Hinterlands et cherchez un petit lac.","Le tragan violet ne vit que sous l'eau."}, {"Collecter un Tragan violet et l'apporter à Sprinkle, à Gadgetzan."}},
[2661] = {"Livraison pour Marin", {"Voilà la Poudre violette, <nom>. Portez-la à Marin et dites-lui qu'il me doit une faveur !"}, {"Sur ordre de Sprinkle, apporter la Poudre violette à Marin Noggenfogger à Gadgetzan."}},
[2662] = {"Elixir Noggenfogger", {"Maintenant, il faut mélanger cette poudre avec la rosée.","Partez et revenez me voir un peu plus tard... Quand j'aurai perfectionné le fabuleux Élixir Noggenfogger !"}, {"Retourner voir Marin Noggenfogger une fois qu'il aura créé son élixir."}},
[2681] = {"Les pierres qui nous lient", {"Vous ne pourrez pas à vous <seul/seule> affronter les forces de Razelikh et de ses subordonnés.","Rassemblez un groupe d'aventuriers pour libérer mes hommes de leur torture éternelle ! Vous devez briser dix-huit pierres.","Battez-vous durement et mourez avec honneur, <nom>."}, {"Libérer 9 Serviteurs de Razelikh, 3 Serviteurs de Sevine, 3 Serviteurs d'Allistarj et 3 Serviteurs de Grol. Retourner voir le Héros déchu une fois la tâche terminée. Il faut rester à proximité des pierres ou le processus échouera."}},
[2701] = {"Les héros des Anciens", {"Je vous en prie, prenez cette Relique des Anciens. Nous n'en avons plus besoin.","Puisse-t-elle bien vous servir dans votre combat contre la Légion."}, {"Ouvrir le coffre et prendre la récompense."}},
[2702] = {"Les héros des Anciens", {"Votre noblesse et votre héroïsme m'ont honoré, <nom>.","Vous les avez honorés aussi…"}, {"Aller voir le caporal Thund Splithoof."}},
[2721] = {"Kirith", {"Un chapitre de vos aventures se termine, un autre commence, <nom>.","L'un de mes hommes manque toujours à l'appel, il est perdu quelque part dans ces terres. Je parle du lieutenant Kirith, bien sûr.","Comme je l'ai déjà dit, nous avons été tous deux capturés et torturés par Allistarj mais, pauvre Kirith, sa force a vacillé. Depuis ma cage, je pouvais entendre ce qu’il subissait, ses cris résonnaient dans toute la caverne. Je ne l’ai plus vu depuis."}, {"Découvrir ce qui est arrivé au Lieutenant Kirith."}},
[2741] = {"Le Super-Oeuf-O-Matic", nil, nil},
[2742] = {"Rin'ji est pris au piège !", {"Hé <race> !","S'il plaît à vous, aidez Rin'ji à s'évader. Moi sais que <race> pas aimer race de Rin'ji, mais Rin'ji demande pitié ! Rin'ji doit sortir ici !","Cadeau agréable pour <race> si <il/elle> aider Rin'ji à sortir...","Rin'ji cache quelque chose de secret sur les Hauts-Surplombs."}, {"Escorter Rin'ji hors du Gîte de Quel'Danil, puis découvrir son secret caché sur les Hauts-Surplombs, à l’est."}},
[2743] = {"Le couvert de l'obscurité", {"Faisons vite, <nom>, mon temps ici est compté.","Il faut détruire la triade de pouvoir qui protège le seigneur-démon Razelikh. Ce ne sera pas aussi facile que de libérer une âme damnée.","<Kirith sourit.>","Vous voyez, chacun des trois subalternes de Razelikh porte autour du cou le tiers d'une amulette créée par Razelikh. L'amulette dont vous aurez besoin pour appeler le démon.","Retournez voir Trebor et dites-lui comment appeler Razelikh. Il pourra vous aider à aller plus loin."}, {"Retourner voir le Héros déchu de la Horde avec ces découvertes."}},
[2744] = {"Le chasseur de démons", {"La triade est protégée par Razelikh.","En échange de leur servitude, Razelikh a accordé à chacun d'eux un pouvoir presque illimité dans les Terres foudroyées, ainsi qu'une amulette leur conférant l'immortalité. Tant qu'ils restent dans leurs terres, ils ne peuvent pas être tués.","Quelqu'un peut vous aider à poursuivre, <nom>. Allez à Azshara et trouvez le chasseur de démons, Loramus Thalipedes. Cherchez-le dans les îles."}, {"Aller voir Loramus Thalipedes à Azshara."}},
[2745] = {"En infiltrant le Château", {"Hum ! Une histoire intéressante à ne pas prendre à la légère. Ouais, je peux vous aider, mais ce que vous devez faire n'est pas vraiment une justice... légale, si vous allez jusqu'au bout.","On doit d’abord s'occuper de Lescovar et de Marzon, et vite. Pensez-vous en être capable ?","A vous de décider. Si oui, rendez-vous rapidement au château pour y rencontrer un vieil ami à moi, un gnome nommé Tyrion. Il observe Lescovar depuis maintenant quelques semaines. Il devrait pouvoir vous aider à en finir."}, {"Aller voir Tyrion au Château de Stormwind."}},
[2746] = {"Des objets d’une certaine importance", {"Trias veut accélérer les choses, hein ? Bien, nous pouvons le faire, mais je ne suis pas prêt. Je vais faire la partie difficile du boulot, mais c'est vous qui allez vous salir les mains, d’accord ? Bon.","Il faut que vous m'obteniez certaines choses pour mettre mon plan à exécution. J'ai le déguisement parfait pour que l'Espion passe ces gardes.","Apportez-moi de la soie et deux pommes. Pour la soie, débrouillez-vous, mais vous trouverez des pommes parfaites à la petite ferme de Clara à l'extérieur de la ville."}, {"Apporter 3 Etoffes de soie et 2 Pommes fraîches de Clara à Tyrion à Stormwind."}},
[2747] = {"Un œuf extraordinaire", nil, nil},
[2748] = {"Un œuf de premier choix", nil, nil},
[2749] = {"Un œuf ordinaire", nil, nil},
[2750] = {"Un mauvais œuf", nil, nil},
[2751] = {"Remparts barbares", {"Vingt générations d'Omosh ont tenu ce marteau, <nom>. Nous avons façonné cent mille tonnes de métal. Tragiquement, tout prend fin avec Orokk, l'échec des Omosh : sans famille et sans héritier.","Je ne peux pas laisser les savoir-faire secrets des Omosh mourir avec moi ! Quelqu'un doit recueillir cet héritage, qu’il soit ou non un Omosh !","Prouvez à Orokk que vous êtes celui-là, <nom>. Apportez deux brassards guillochés en bronze, deux glaives en bronze et deux griffes aiguisées. Apportez-les à Orokk et nous pourrons continuer."}, {"Apporter 2 Brassards guillochés en bronze, 2 Glaives en bronze et 2 Griffes aiguisées à Orokk Omosh à Orgrimmar."}},
[2752] = {"Épaulettes en fer", {"Orokk voit l'ardeur dans vos yeux, <nom>. Comme une éponge, vous absorbez la sagesse des leçons des Omosh.","Trois leçons encore !","Retournez à l'enclume, absorbez-vous dans la chaleur de la forge, créez pour Orokk quatre marteaux de guerre en bronze et quatre haches de guerre en bronze. Ensuite seulement vous apprendrez le secret des Epaulettes en fer."}, {"Apporter 4 Haches de guerre en bronze et 4 Marteaux de guerre en bronze à Orokk Omosh à Orgrimmar."}},
[2753] = {"Piétinés", {"Deux leçons encore vous enseignera Orokk, <nom>. Vos muscles font mal, je sais, mais continuez pour les Omosh !","Apportez à Orokk quatre heaumes en fer émeraude, quatre bracelets en fer émeraude et deux jambières en fer émeraude – pour votre apprentissage et la souffrance de vos ennemis !"}, {"Apporter 4 Heaumes en fer émeraude, 4 Bracelets en fer émeraude et 2 Jambières en fer émeraude à Orokk Omosh à Orgrimmar."}},
[2754] = {"Les cornes de Frénésie", {"Voici la dernière leçon d'Orokk, <nom>. A l'enclume sacrée et à la forge, vous devez retourner ! Apportez à Orokk deux solides maillets en fer, deux bottes en bronze argenté et deux gantelets en bronze argenté.","Regardez la crainte grandir dans leurs yeux à votre approche !"}, {"Apporter 2 Solides maillets en fer, 2 Bottes en bronze argenté et 2 Gantelets en bronze argenté à Orokk Omosh à Orgrimmar."}},
[2755] = {"Les joies des Omosh", {"Orokk ne pleure pas, <nom>. Il a une poussière dans l'œil.","<Orokk essuie une larme.>","Il y a un dernier secret de famille Omosh qu'Orokk va transmettre. Orokk mourra heureux, grâce à <nom>.","Apprenez la danse de joie des Omosh et vous apprendrez ensuite le secret des Poings de fer !"}, {"Regarder et apprendre la Danse de joie des Omosh."}},
[2756] = {"Les vieilles voies", {"Aux temps légendaires, Aturk aurait écrasé les crânes de ses ennemis et réduit leurs os en poudre comme combustible de lampe. Ces jours sont loin, <nom>. Le chef de guerre appelle à la paix parmi les clans. Les vieux ennemis ne sont plus observés que par des yeux fatigués !","C'est incroyable, je sais, mais telle est la volonté de Thrall.","Assez ! Vous êtes ici pour apprendre et vous allez apprendre ! Apportez-moi quatre cuirasses en acier et quatre heaumes en plaques d'acier et vous aurez un avant-goût de la guerre !"}, {"Apporter 4 Cuirasses en acier et 4 Heaumes en plaques d'acier à Aturk l'Enclume à Orgrimmar."}},
[2757] = {"Baie-du-Butin ou la mort !", {"Écoutez, <gamin/gamine> : vous n'apprendrez plus rien ici.","Je connais un type qui connaît un autre type. Il tourne autour de la forge à Baie-du-Butin. Montrez-lui cet insigne, il vous dira tout du mithril.","Alors, qu'en dites-vous ? Laissez cette matière aux bébés dans leurs langes, <nom>. Il est temps de progresser.","Si ça vous intéresse, ce gars s'appelle McGavan. Montrez-lui ce bibelot et il reconnaîtra votre engagement."}, {"Aller voir McGavan à Baie-du-Butin."}},
[2758] = {"Les origines de la forge", {"Quand j'avais votre âge, un nain du nom de Galvan m'a pris sous son aile et m'a tout appris du métier de Forgeron d'armures. Avant de partir pour Baie-du-Butin, il m'a fait promettre de former tous ceux qui auraient le désir et la détermination de devenir forgerons.","Bien, il est temps pour vous d'apprendre quelque chose. Faites-moi six brassards en écailles dorées et vous comprendrez comment on devient forgeron, <nom>."}, {"Apporter 6 Brassards en écailles dorées à Hank le Marteau à Stormwind."}},
[2759] = {"À la recherche de Galvan", {"Galvan a quitté Baie-du-Butin depuis longtemps, mais les membres de l'ordre sont restés en ville, espérant trouver un jour une autre personne digne de la tutelle de Galvan.","Portez cet insigne à McGavan à Baie-du-Butin.","Si vous avez réussi à aller aussi loin, il saura que vous êtes digne de lui. J'ai foi en vous, <nom> !"}, {"Aller voir McGavan à Baie-du-Butin."}},
[2760] = {"L'Ordre du mithril", {"La plupart de nos recrues ne vont jamais jusqu'à Baie-du-Butin, <nom>. L'éclat de l'insigne est suffisant pour déclencher la convoitise, même chez le plus loyal des forgerons. Les imbéciles ! Cet insigne est une goutte d'eau en comparaison de l'océan des richesses que vous verrez !","Allez trouver Galvan l'Ancien et montrez-lui cette épingle. Il habite loin au nord, entre Zul Gurub et le tertre des ogres Mosh'Ogg."}, {"Aller voir Galvan l'Ancien dans la vallée de Strangleronce."}},
[2761] = {"Ça fond, ça ne fond pas", {"Beaucoup de forgerons pensent qu'extraire le minerai et le travailler sont des tâches séparées, indépendantes, mais qui peut être forgeron sans être mineur ? C'est impossible ! Un forgeron accompli doit aussi être un mineur accompli.","Montrez donc à Galvan ce dont vous êtes capable, <nom>. Apportez-moi quarante barres de fer et quarante barres de mithril. En échange, Galvan vous apprendra comment réaliser des jambières ornées en mithril !"}, {"Apporter 40 Barres de mithril et 40 Barres de fer à Galvan l'Ancien à Strangleronce."}},
[2762] = {"Le vif-argent n’est pas de l'argent", {"On dit que le vrai-argent est plus solide et plus résistant que le mithril. Galvan n'est pas contre les comparaisons, mais contrairement à ce que son nom laisse croire, le vrai-argent n'est pas de l'argent ! Le mithril est un minerai beaucoup plus noble ! Il faut comprendre cela par soi-même, <nom>.","Apportez-moi quarante barres de mithril et cinq barres de vrai-argent. En échange, vous saurez le secret des Gants ornés en mithril et mieux, vous comprendrez que le vrai-argent ne tient pas ses promesses !"}, {"Apporter 40 Barres de mithril et 5 Barres de vrai-argent à Galvan l'Ancien à Strangleronce."}},
[2763] = {"L'art de l'imprégnation", {"Les pierres précieuses sont de la plus grande importance pour notre art, <nom>. Peu de gemmes sont plus précieuses que la citrine. Elle amplifie les propriétés magiques que nous insufflons à nos produits.","Pour cette leçon, nous allons renforcer quarante barres de mithril avec quatre citrines. Cela vous permettra de mieux comprendre la gemmologie magique et comment créer des Epaulières ornées en mithril."}, {"Apporter 40 Barres de mithril et 4 Citrines à Galvan l'Ancien à Strangleronce."}},
[2764] = {"Le meilleur disciple de Galvan", {"Si vous êtes sur le point de terminer votre formation avec Galvan, vous devez aller à Gadgetzan.","Là, vous trouverez mon meilleur élève : Trenton Lighthammer. Il a beaucoup à vous apprendre sur le mithril orné. Sinon restez ici et apprenez !","Oh bien sûr Gadgetzan est à Tanaris !"}, {"Parler à Trenton Lighthammer à Gadgetzan."}},
[2765] = {"Maître Forgeron !", {"J'ai su, dès que je vous ai vu, que vous deviendriez un de mes meilleurs disciples ! Vous n’avez pas hésité à donner une relique à McGavan. Dans le seul but de le rencontrer. Une telle foi et une telle dévotion pour l'art doivent être récompensées.","Vous avez obtenu une expertise dans l'art de la forge, <nom>. Cueillez le fruit de vos efforts !"}, {"Réclamez votre récompense à Galvan l'Ancien !"}},
[2766] = {"Trouvez OOX-22/FE !", {"Vous découvrez une sorte d'engin métallique en forme d'œuf. Il revient à la vie lorsque vous tripotez l'un de ses boutons, apparaissant comme un machin robotique gnome ! Une voix grésille.","\"Mon nom est Oglethorpe Obnoticus, et mon robot guidé s'est perdu ! Je vous récompenserai si vous le retrouvez. S'il vous plaît allez placer cette balise dans le robot.\"","\"J'ai calculé la position de mon robot égaré : il est dans une caverne près du val des Griffes farouches !\""}, {"Placer la balise de détresse dans le robot d'Oglethorpe qui se trouve dans une caverne près du val des Griffes farouches."}},
[2767] = {"Sauvez OOX-22/FE !", {"Le robot émet à nouveau la voix du gnome :","\"Je dois déplacer OOX-22/FE dans un endroit dégagé et sûr pour qu'il puisse entamer sa longue procédure de décollage. Il est équipé d'un camouflage, mais il me faut du temps pour le rendre opérationnel. J'ai besoin que vous escortiez le robot jusqu'à... disons la Côte oubliée ! Ce serait un endroit parfait et assez vaste pour que tout puisse se mettre en route !\"","Escortez-le jusque là-bas, et venez me parler à Baie-du-Butin ! Oglethorpe Obnoticus - terminé !\""}, {"Escorter OOX-22/FE au dock le long de la Côte oubliée, puis faire votre rapport à Oglethorpe Obnoticus à Baie-du-Butin."}},
[2768] = {"Le bâtonnet divino-matic", {"Le sergent Bly m'a volé ! Il a dit qu'il ne faisait que l'emprunter, mais il a volé mon bâtonnet divino-matic ! Sans lui, comment savoir où creuser de nouveaux puits ?","Trouvez Bly et reprenez mon bâtonnet ! J'ai entendu qu'il emmenait une bande d'aventuriers mal famés chasser les trésors à l'ouest, à Zul'Farrak. Je parie que vous le trouverez là-bas.","Espérons que les trolls se seront déjà occupés de lui, car si vous devez l’affronter pour reprendre le bâtonnet, le combat sera difficile."}, {"Rapporter le Bâtonnet divino-matic à l'Ingénieur en chef Bilgewhizzle, à Gadgetzan."}},
[2769] = {"Les frères Brassbolts", {"Avez-vous rencontré les frères Brassbolt ? Ils sont dans les Salines en Kalimdor. Ils font des courses de voitures à fusée contre les gobelins. Ils gagnent régulièrement. Il faut dire que les voitures des gobelins explosent si souvent qu'elles passent rarement la ligne d'arrivée !","Malgré cela, les frères Brassbolt cherchent toujours à avoir une voiture plus rapide. Wizzle m'a dit qu'il cherchait un composant très rare pour sa nouvelle expérience.","Il a besoin d'aide pour trouver ce composant. Allez l'aider."}, {"Parler à Wizzle Brassbolts dans les Salines."}},
[2770] = {"Gahz'rilla", {"Au coeur de Zul'Farrak, la cité des trolls des sables à Tanaris, il y a un bassin sacré. De ce bassin, les trolls invoquent une bête énorme ! Gahz'rilla ! Elle est tellement puissante que même ses écailles crépitent d'énergie. C'est cette énergie que je convoite pour ma voiture !","Rapportez-moi les écailles électrifiées de Gahz'rilla !","Mais l'invocation de Gahz'rilla est l’un des secrets les mieux gardés des trolls. Avant d’affronter le monstre, vous devez apprendre le secret des trolls."}, {"Rapporter les Ecailles électrifiées de Gahz'rilla à Wizzle Brassbolts dans les Salines."}},
[2771] = {"Une tête bien faite sur les épaules", {"Il semble que le maître ait planifié tout cela depuis le début. Je n'ai pas appris à créer le genre d'objet que vous savez créer, de même que vous ne savez pas créer ce que je sais faire.","Le maître est sage, <nom>. Chacun de nous doit apprendre de l'autre !","Montrez-moi comment faire un camail en mithril et des épaulières ornées en mithril et je vous montrerai comment faire un heaume orné en mithril."}, {"Rapporter deux Camails en mithril et une paire d'Epaulières ornées en mithril à Trenton Lighthammer."}},
[2772] = {"Le monde à vos pieds", {"Au dernier championnat intervallée des forgerons, tous les spectateurs ont vu un de ces chiens de la mine de la KapitalRisk m'attaquer la jambe. Mais ils ne savent pas ce qui m'a permis de continuer et de gagner ce championnat. Eh oui ! Des bottes ornées en mithril !","Je vais avoir besoin d'apprendre à faire des jambières ornées en mithril cette année, car la KapitalRisk participe encore au championnat ! Apprenez-moi comment faire les jambières et je vous apprendrai à faire les bottes !"}, {"Rapporter deux paires de Bottes en mithril lourd et des Jambières ornées en mithril à Trenton Lighthammer."}},
[2773] = {"Le gars du mithril", {"Dans cette région, on m'appelle le gars du mithril. Je fournis des équipements ornés en mithril aux aventuriers du monde entier.","<Trenton chuchote.>","Peu savent que je ne connais pas la technique des gants ornés en mithril. Mais chut ! Cela sera notre petit secret.","Enseignez-moi cette technique, <nom>, et en échange je vous montre le point culminant de mon apprentissage chez Galvan : la cuirasse ornée en mithril."}, {"Rapporter deux Cuirasses lourdes en mithril et une paire de Gants ornés en mithril à Trenton Lighthammer."}},
[2781] = {"ON RECHERCHE : Caliph Dard-de-scorpide", {"ON RECHERCHE : Caliph Dard-de-scorpide !","La Compagnie des Eaux de Gadgetzan de Tanaris paiera une forte récompense pour la tête de Caliph Dard-de-scorpide, chef des hors-la-loi Bat-le-désert. Ses crimes sont :","1) Assassinat d'employés de la Compagnie des Eaux de Gadgetzan ;","2) Vol des ressources de la Compagnie des Eaux de Gadgetzan ;","3) Vol des biens de la Compagnie des Eaux de Gadgetzan.","Rapportez les preuves de la mort de Caliph Dard-de-scorpide à l'ingénieur en chef Bilgewizzle pour toucher immédiatement votre récompense."}, {"Apporter la tête de Caliph Dard-de-scorpide à l'Ingénieur en chef Bilgewizzle à Gadgetzan."}},
[2782] = {"Secret de Rin'ji", {"La tablette est couverte de lettres gravées par des trolls Witherbark. Le secret de Rin'ji est réellement un mystère…","Il est possible que vous ayez quelque chose de réellement précieux entre les mains. Mais l'inverse est également possible. Un troll Witherbark en détresse ferait probablement n'importe quoi pour s'en sortir..."}, {"Oran Snakewrithe, une érudite réputée pour sa connaissance des us et coutumes des trolls Witherbark, pourrait peut-être déchiffrer la tablette. Elle habite le Quartier de la magie à Undercity."}},
[2783] = {"Querelles mesquines", {"Nous avons combattu le mauvais ennemi, <nom>.","La vraie puissance de cette région continue de croître alors que nous nous chamaillons avec les orcs sur cette terre aride.","Devons-nous continuer à nous battre contre la Horde, pour finir écrasés un jour par les armées de la Légion ardente ? C'est futile.","Mettez vos différences de côté, <nom>, parlez au Héros déchu de la Horde. Il sait ce qui se passe dans les Terres foudroyées."}, {"Parler au Héros déchu de la Horde à la frontière des Terres foudroyées et du marais."}},
[2784] = {"Perte d'honneur", {"Écoutez mon histoire, <nom>."}, {"Écouter le Héros déchu de la Horde raconter son histoire."}},
[2801] = {"Une histoire de chagrin", {"Avant que nous continuions, vous devez écouter mon histoire."}, {"Écouter le Héros déchu de la Horde raconter son histoire."}},
[2821] = {"La marque de la qualité", {"Ma philosophie, concernant le travail du cuir, c’est que mon travail doit être le meilleur possible : pas de compromis. Quelqu’un disant que la qualité ne compte pas essaye de vous escroquer !","Dans ma recherche de matières premières de qualité, rien ne vaut la peau de yéti épaisse. Elle provient des yétis de Feralas, et il y en a beaucoup au val des Griffes farouches. J'en ai besoin et j’échangerai l'un de mes objets en cuir contre un ballot de dix peaux. Si cela vous semble équitable, le marché est conclu."}, {"Rapporter 10 Peaux de yéti épaisses à Pratt McGrubben au bastion de Feathermoon."}},
[2822] = {"La marque de la qualité", {"Je suis fier de mon travail et je sais que cela se voit : la qualité ne doit jamais être dévalorisée. Quiconque dit que la qualité n’est pas primordiale ferait également croire que les abysses sont lumineux.","Dans ma recherche de matières premières de qualité, rien ne vaut la peau de yéti épaisse. Elle provient des yétis de Feralas, et il y en a beaucoup au val des Griffes farouches, à l'ouest d'ici. Je vous laisserai choisir un objet parmi mes créations contre un ballot de dix peaux. Si cela vous convient, nous faisons affaire !"}, {"Apporter 10 Peaux de yéti épaisses à Jangdor Swiftstrider au Camp Mojache."}},
[2841] = {"Guerre des plates-formes", {"Technologie gnome… Ce terme était autrefois brandi comme un slogan. Les temps ont changé. Ils ont désormais une nouvelle plate-forme, supérieure à tous nos modèles de Déchiqueteurs combinés. J'ai besoin des plans de cette plateforme, <nom> !","Malheureusement ceux-ci sont enfermés dans le coffre du mekgénieur Thermaplugg. Et pour compliquer les choses, la combinaison du coffre n'est connue que de lui seul.","Faites ce qu’il faut pour obtenir cette combinaison, <nom>. Puis ouvrez le coffre et ramenez-moi ces plans !"}, {"Obtenir la Combinaison du coffre de Thermaplugg, prendre les Plans de la plate-forme et les apporter à Nogg à Orgrimmar."}},
[2842] = {"L'ingénieur en chef Scooty", {"Dès que nous aurons des nouvelles de Kernobee à propos de la nouvelle plate-forme des gnomes, Scooty et moi commencerons à travailler sur le Transpolyporteur 6000 : le moyen le plus rapide et le plus sûr d’aller à Gnomeregan.","Il est enfin prêt à recevoir des passagers, <nom>. Si vous désirez l'essayer, allez à Baie-du-Butin et parlez avec Scooty au perchoir des Cavaliers célestes. Pour aller à Baie-du-Butin, prenez le bateau depuis Ratchet.","Vous pouvez aussi traverser les montagnes de Dun Morogh, mais elles sont infestées de nains…"}, {"Parler à Scooty à Baie-du-Butin."}},
[2843] = {"Gnomer-paaarti !", {"Aussi longtemps que vous êtes en possession du transpondeur des gobelins, tout ce que vous avez à faire est de traverser le portail et vous vous retrouverez à Gnomeregan.","Il y aura peut-être des effets secondaires bizarres, mais aucun ne devrait être permanent... plus maintenant.","J'oubliais de parler du plus important : le retour. Lorsque vous serez <prêt/prête> à revenir, allez au portail de multitéléportation de Gnomeregan !","Bon, il faut maintenant régler le transpondeur selon votre poids et votre taille."}, {"Attendre que Scooty calibre le transpondeur des gobelins."}},
[2844] = {"Le garde géant", {"Je suis vraiment bon à rien, <nom>. J'étais supposé surveiller mon amie Shay, et je l’ai perdue. Je ne suis pas sûr de ce qui s'est passé, mais la dernière fois que je l'ai vue, nous étions en train de marcher dans les ruines au nord d'ici.","Tout allait bien, mais lorsque je me suis retourné vers elle... elle avait disparu.","Maintenant je ne sais plus quoi faire. Vous avez une idée, <nom> ? Je suis affreusement inquiet.","Et si vous alliez la chercher ? Vos jambes vous permettent d'aller plus vite que moi."}, {"Retrouver la jeune elfe de la nuit perdue."}},
[2845] = {"Shay l'égarée", {"Pouvez-vous m'aider à retrouver mon chemin jusqu'à mon ami, Croque-roc ? Il était supposé me surveiller… Mais j'ai tendance à folâtrer de-ci de-là. Il y a tant de choses à voir et je me laisse facilement distraire.","Il y a toutefois une chose qui me ramène toujours.","Dans ce coffre, vous trouverez une cloche qui m'est précieuse. J'adore sa sonorité. Si je m'égare, vous n'avez qu'à la faire sonner, je reviendrai immédiatement."}, {"Prendre la Cloche de Shay dans le coffre.","Escorter Shay Leafrunner au Camp de Croque-roc."}},
[2846] = {"La tiare des abysses", {"Il y a longtemps, je possédais une splendide pièce de joaillerie, la tiare des abysses. Elle n'était pas seulement belle, elle détenait un grand pouvoir, pour ceux qui savaient l'utiliser.","Quand l'hydromancienne Velratha apprit son existence, elle l’a convoitée. Elle a envoyé des agents chez moi pour la voler pendant mon absence. Voleurs !","Je veux ma tiare ! Allez à Zul'Farrak, la cité des trolls à Tanaris, trouvez Velratha et reprenez-lui la tiare. Rapportez-la et vous gagnerez mes faveurs."}, {"Rapporter la Tiare des abysses à Tabetha au marécage d'Âprefange."}},
[2847] = {"L'armure en cuir sauvage", {"<nom>, vous avez acquis une bonne connaissance du travail du cuir ! Mais je sais des choses qui pourraient vous intéresser… L'armure en cuir sauvage ! L’intérêt du cuir sauvage tient aux propriétés chaotiques des sauvageonnes et de quelques autres créatures d'ici. L'armure que vous réaliserez sera ce qu’un artisan de votre compétence peut fabriquer de mieux.","Je vous apprendrai à la réaliser si vous acceptez de travailler pour moi pendant un moment. Le coût initial est juste de dix pièces de cuir épais. Après nous parlerons technique."}, {"Rapporter 10 Cuirs épais à Pratt McGrubben au bastion de Feathermoon."}},
[2848] = {"Les épaulières en cuir sauvage", {"Les épaulières en cuir sauvage protègent efficacement le haut des bras et les épaules. Comme l'ensemble des pièces d'armure en cuir sauvage, elles bénéficient d'une qualité supplémentaire, mais aléatoire, en raison de l'utilisation des sauvageonnes.","Pour ce patron, j’ai besoin de six renforts d'armure épais et d’une sauvageonne. Cela ne devrait pas vous poser de problèmes."}, {"Apporter 6 Renforts d'armure épais et 1 Sauvageonne à Pratt McGrubben au bastion de Feathermoon."}},
[2849] = {"Le gilet en cuir sauvage", {"Le gilet en cuir sauvage est la pièce maîtresse de toute armure en cuir sauvage. Il procure une protection maximale du torse tout en permettant à la puissante magie des sauvageonnes de se diffuser dans l’armure.","Pour ce modèle, je demande deux paires de gants en écailles de tortue, deux cuirasses en écailles de tortue et une sauvageonne. C'est une grosse commande, mais vous ne devriez pas avoir de problème à me la fournir."}, {"Rapporter 2 Cuirasses en écailles de tortue, 2 paires de Gants en écailles de tortue et 1 Sauvageonne à Pratt McGrubben au bastion de Feathermoon."}},
[2850] = {"Le casque en cuir sauvage", {"Un casque en cuir sauvage offre une protection optimale sans sacrifier au confort. Comme pour l'armure en cuir sauvage, les sauvageonnes lui apportent une amélioration puissante, mais aléatoire. Les porteurs d'armures de cuir raffolent de cet article !","Pour ce modèle, j'ai besoin de deux tuniques de la nuit, deux bandeaux de la nuit et d'une sauvageonne. Complétez cette commande et le patron est à vous !"}, {"Rapporter 2 Tuniques de la nuit, 2 Bandeaux de la nuit et 1 Sauvageonne à Pratt McGrubben au bastion de Feathermoon."}},
[2851] = {"Les bottes en cuir sauvage", {"Vous avez déjà beaucoup appris, <nom>. Mais ce n’est pas fini. Après viennent les bottes en cuir sauvage, la pierre angulaire de tout l'équipement.","Ces bottes protègent bien les pieds tout en étant très confortables. Les semelles souples sont une bénédiction pour ceux qui se déplacent discrètement.","Pour ces bottes, j'ai besoin de deux pantalons de la nuit, de deux paires de bottes de la nuit, ainsi que de deux sauvageonnes. Lorsque vous aurez tout cela, faites-le-moi savoir."}, {"Rapporter 2 Pantalons de la nuit, 2 paires de Bottes de la nuit et 2 Sauvageonnes à Pratt McGrubben au bastion de Feathermoon."}},
[2852] = {"Les jambières en cuir sauvage", {"Vous pouvez maintenant apprendre des techniques plus complexes, <nom>. Les jambières en cuir sauvage sont un des équipements en cuir les plus résistants et les plus puissants. La taille, les hanches et les parties vitales sont particulièrement bien protégées. Les aventuriers qui l'utiliseront apprécieront grandement cette protection combinant l'amélioration apportée par la sauvageonne.","Pour ce modèle, j'ai besoin de deux casques en écailles de tortue, de deux brassards en écailles de tortue et de deux sauvageonnes."}, {"Rapporter 2 Casques en écailles de tortue, 2 Brassards en écailles de tortue et 2 Sauvageonnes à Pratt McGrubben au bastion de Feathermoon."}},
[2853] = {"Maître du cuir sauvage", {"Eh bien, <nom> ! Vous êtes un modèle de rapidité. Vous connaissez tous les patrons de l'armure en cuir sauvage, sauf un. Je ne vous demanderai rien pour celui-là, vous le méritez… mais je ne peux pas vous l’apprendre moi-même. Mon maître est le seul qui puisse vous transmettre ce savoir.","Portez cette lettre à Telonis, il enseigne le travail du cuir à Darnassus. Montrez-lui la lettre et laissez-le vous reconnaître comme son égal, ainsi que je l'ai fait."}, {"Donner la Lettre de Pratt à Telonis, le Maître tanneur de Darnassus."}},
[2854] = {"L'armure en cuir sauvage", {"<nom>, la renommée de vos talents vous précède. Je connais quelque chose qui pourrait vous intéresser... l'armure en cuir sauvage ! Le cuir sauvage tire ses propriétés des sauvageonnes que l'on trouve dans la végétation luxuriante et sur quelques créatures. Cela ne devrait pas être compliqué pour quelqu'un de votre niveau.","Je vous enseignerai, mais en échange vous travaillerez pour moi pendant un moment. La première chose consiste à me rapporter dix pièces de cuir épais. Après nous discuterons technique."}, {"Rapporter 10 Cuirs épais à Jangdor Swiftstrider au Camp Mojache."}},
[2855] = {"Les épaulières en cuir sauvage", {"Les épaulières en cuir sauvage protègent efficacement le haut des bras et les épaules. Comme toutes les pièces d'armure en cuir sauvage, elles bénéficient d'une amélioration puissante, mais aléatoire, grâce aux sauvageonnes.","Pour ce modèle, je demande six renforts d'armure épais et une sauvageonne. Cela ne devrait pas vous poser trop de problèmes."}, {"Apporter 6 Renforts d'armure épais et 1 Sauvageonne à Jangdor Swiftstrider au Camp Mojache."}},
[2856] = {"Le gilet en cuir sauvage", {"Le gilet en cuir sauvage est la pièce maîtresse de toute armure en cuir sauvage. Il procure une protection maximale du torse tout en permettant à la puissante magie des sauvageonnes de se diffuser dans l’armure.","Pour ce patron, il me faut deux cuirasses et deux paires de gants en écailles de tortue, et une sauvageonne. Ce n'est pas une leçon facile, mais vous disposez clairement des talents pour la mener à bien."}, {"Rapporter 2 Cuirasses en écailles de tortue, 2 paires de Gants en écailles de tortue et une Sauvageonne à Jangdor Swiftstrider au Camp Mojache."}},
[2857] = {"Le casque en cuir sauvage", {"Un casque en cuir sauvage offre au porteur une protection optimale sur les parties vitales sans sacrifier au confort. Comme pour l'armure en cuir sauvage, les sauvageonnes lui confèrent une amélioration puissante et aléatoire. Les porteurs d'armure en cuir sauvage souhaiteront avoir cette pièce d'équipement !","Pour ce modèle, j'ai besoin de deux tuniques de la nuit, deux bandeaux de la nuit et d'une sauvageonne. Rapportez-les et le patron est à vous !"}, {"Rapporter 2 Tuniques de la nuit, 2 Bandeaux de la nuit et 1 Sauvageonne à Jangdor Swiftstrider au Camp Mojache."}},
[2858] = {"Les bottes en cuir sauvage", {"Vous avez déjà beaucoup appris, <nom>. Mais ce n’est pas tout. L'étape suivante concerne les bottes en cuir sauvage.","Elles protègent bien les pieds tout en restant confortables. Les semelles souples sont une bénédiction pour ceux qui aiment se déplacer discrètement.","Pour ces bottes, j'ai besoin de deux pantalons de la nuit et deux paires de bottes de la nuit, ainsi que de deux sauvageonnes. Lorsque vous aurez tout cela, nous continuerons."}, {"Rapporter 2 Pantalons de la nuit, 2 paires de Bottes de la nuit et 2 Sauvageonnes à Jangdor Swiftstrider au Camp Mojache."}},
[2859] = {"Les jambières en cuir sauvage", {"Vous pouvez maintenant apprendre des techniques plus complexes, <nom>. Les jambières en cuir sauvage sont un des équipements en cuir les plus résistants et les plus puissants. La taille, les hanches et les parties vitales sont particulièrement bien protégées. Les aventuriers qui l'utiliseront apprécieront grandement cette protection combinant l'amélioration apportée par la sauvageonne.","Pour ce modèle, j'ai besoin de deux casques en écailles de tortue, de deux brassards en écailles de tortue et de deux sauvageonnes."}, {"Rapporter 2 Casques en écailles de tortue, 2 Brassards en écailles de tortue et 2 Sauvageonnes à Jangdor Swiftstrider au Camp Mojache."}},
[2860] = {"Maître du cuir sauvage", {"<nom>, vous avez appris tout ce que je pouvais vous enseigner. Il reste néanmoins un dernier élément de l’armure en cuir sauvage à apprendre. Je ne vous demanderai rien pour celui-là, vous l'avez mérité. Mais je ne peux vous le donner.","Mon maître est la seule personne à pouvoir vous l’enseigner. Donnez cette lettre à Una, elle enseigne le travail du cuir à Thunder Bluff. Montrez-lui la lettre et laissez-la vous reconnaître comme son égal, ainsi que je l'ai fait."}, {"Donner la Lettre de Jangdor à Una, le Maître tanneur de Thunder Bluff."}},
[2861] = {"La mission de Tabetha", {"La rumeur dit que Tabetha, une collègue mage qui vit loin dans le marécage d'Âprefange, a un compte à régler avec un certain troll des sables à Zul'Farrak. Si vous parlez à Tabetha, elle pourra vous en dire davantage.","Et invitez quelques amis, <nom>. Si Tabetha vous envoie à Zul'Farrak, il ne faut pas y aller <tout seul/toute seule>.","Vous trouverez le cottage de Tabetha à l'ouest de Theramore, juste au nord des ruines Cognepierres."}, {"Parler à Tabetha dans le marécage d'Âprefange."}},
[2862] = {"La guerre avec les Griffebois", {"Les gnolls… Ils sont plus qu'une nuisance. Ils sont une menace constante pour ce campement ! Qu'ils soient natifs de Feralas ou non n'a aucune importance, si nous ne prenons pas bientôt des mesures appropriées contre eux, nous serons balayés de la région.","Les attaques contre le camp sont de pire en pire. Nous avons besoin de votre aide pour réduire leur nombre. Ramenez-moi dix crinières et je vous récompenserai de vos efforts.","Allez !"}, {"Rapporter 10 Crinières de gnolls Griffebois à Hadoken Swiftstrider au Camp Mojache."}},
[2863] = {"Frappe sur les Alphas", {"Le plan concerne divers chefs gnolls - les Alphas pour reprendre leur propre appellation - et leur élimination successive en un minimum de temps. Si nous pouvions exécuter ce genre d'éradication rapide et précise contre eux, cela amènerait le chaos dans leurs rangs. Et peut-être que le message passerait même à l'intérieur de leurs têtes épaisses et infestées de puces que nous pouvons et allons les détruire !","Je veux que vous soyez <le messager/la messagère> de ma volonté. Eliminez cinq de leurs Alphas en une heure pour cette mission, <nom>, ou nous resterons dans l'impasse."}, {"Tuer 5 Alphas Griffebois et retourner voir Hadoken Swiftstrider en moins d'une heure."}},
[2864] = {"Tran'rek", {"Mon cousin Tran'rek me pose un problème. Il m'a promis une cargaison de carapaces de Scarabées il y a quelques semaines, et je n'ai toujours rien reçu. C’est mon cousin, je n'aimerais donc pas le rappeler méchamment à l’ordre.","Pouvez-vous lui parler avant que je ne commette un acte regrettable ? Dites-lui que j'ai vraiment besoin de cette cargaison.","Gadgetzan est dans le nord de Tanaris, en Kalimdor."}, {"Parler à Tran'rek à Gadgetzan."}},
[2865] = {"Les carapaces de Scarabées", {"Les scarabées de Tanaris ont une carapace très dure ! Si dure qu’elle est utilisée dans de nombreux domaines.","En fait ces carapaces sont si utiles que les scarabées ont presque disparu !","Je sais où trouver des scarabées, mais vous devez me promettre de ramener leurs carapaces. Promis ?","OK, les scarabées ont une colonie à Zul'Farrak. Je pense que les trolls ne les chassent pas pour leurs carapaces.","Mais vous si ! Allez à Zul'Farrak et trouvez-moi ces carapaces intactes !"}, {"Rapporter 5 Carapaces de scarabée intactes à Tran'rek à Gadgetzan."}},
[2866] = {"Les ruines de Solarsal", {"Au sud se trouvent les ruines de Solarsal, un site bien antérieur à l'ère moderne. Nous avons exploré ces ruines et rien trouvé, aussi les avons-nous délaissées.","Très récemment, les nagas Hainecrête se sont installés en nombre dans les ruines. Au début, nous pensions à une attaque. Bien que cela soit toujours possible, nous croyons qu'il s'agit d'autre chose.","Allez là-bas, explorez les ruines et rendez compte de tout ce qui vous paraîtra inhabituel. Bonne chance, <nom>."}, {"Explorer les ruines de Solarsal et enquêter sur la présence des nagas dans le secteur."}},
[2867] = {"Retourner au bastion de Feathermoon", {"Après la découverte du belvédère étrange au cœur des ruines de Solarsal, il est temps de retourner au bastion de Feathermoon et faire votre rapport au général Shandris Feathermoon."}, {"Retourner voir Shandris Feathermoon au bastion de Feathermoon et lui rendre compte de vos découvertes."}},
[2869] = {"Contre les Hainecrête", {"La meilleure approche est de penser, indépendamment du projet réel des nagas, qu'ils tentent de prendre possession de l'île. Nous devons réagir énergiquement avant qu'il ne soit trop tard.","Rapportez-moi dix écailles de Hainecrête en bon état ; pas besoin de vous expliquer comment vous les procurer. Vous diminuerez leur puissance et affaiblirez leur présence sur l'île de Sardor, ce qui joue en notre faveur."}, {"Rapporter 10 Ecailles de naga Hainecrête à Latronicus Moonspear au bastion de Feathermoon."}},
[2870] = {"Contre le Seigneur Shalzaru", {"<nom>, de nouveaux rapports nous donnent une bonne idée de la chaîne de commandement des nagas. Parmi le tout-venant se trouve des individus de sang royal, et ce sont eux qui prennent toutes les décisions.","Nous avons identifié l'un de ces nobles, le seigneur Shalzaru. Nos agents disent qu'il a découvert une relique étrange au cours de fouilles récentes qu’il a menées dans une caverne au sud des ruines principales, sur l'île de l'Effroi.","Arrêtez les autres en neutralisant Shalzaru et rapportez-moi sa relique afin que nous puissions l'étudier."}, {"Eliminer le seigneur Shalzaru et rapporter la mystérieuse relique qu'il a découverte à Latronicus Moonspear au bastion de Feathermoon."}},
[2871] = {"Livraison de la relique", {"Si vous étiez assez aimable de porter la relique à ma femme Vestia, je vous en serai très reconnaissant. Elle n'est pas seulement une excellente prêtresse, elle fait également partie des meilleurs érudits que vous puissiez trouver. Je n'arrive pas à comprendre l’usage de cet objet. Si quelqu'un peut le savoir, c'est ma femme... et nous avons besoin de savoir de quoi il s'agit !","Elle est juste derrière le virage, sûrement en train d'étudier. Merci, <nom>."}, {"Porter la Relique mystérieuse à Vestia Moonspear au bastion de Feathermoon."}},
[2872] = {"Les dettes de Stoley", {"Mon vieux copain Stoley me doit une bouteille d’un rhum très spécial. Il tient un bar à Port Gentepression. C'est dans Tanaris, en Kalimdor. Il soutient que ce rhum est plus fort que tout ce que l'on peut trouver à Baie-du-Butin. Il y croit dur comme fer. À tel point que si je ne suis pas de son avis après avoir goûté cet alcool, je double ce qu'il me doit !","Je pense que depuis la dernière fois, il a perdu confiance, car il ne m’a toujours pas envoyé son fameux rhum à goûter. Un pari est un pari, je ne reviendrai pas dessus. Pourquoi n'iriez-vous pas lui parler un peu ?"}, {"Parler à Stoley, à Port Gentepression."}},
[2873] = {"Chargement de Stoley", {"Je dois du rhum à MacKinley \"Loup des mers\" pour payer une dette de jeu, mais je n'en ai plus ! Le capitaine Cuergo a volé ma dernière caisse et il l'a entreposée dans cette ville de pirates fortifiée, au sud.","Si vous pouviez rapatrier ma cargaison de rhum, alors je pourrais prouver à MacKinley que la gnôle de Kalimdor est autrement plus musclée que le petit-lait qu'ils boivent à Baie-du-Butin."}, {"Apporter le Chargement de Stoley à Port Gentepression et remettez-le à Stoley."}},
[2874] = {"Livraison à MacKinley", {"Merci de votre aide, <nom>. Voici une bouteille de rhum de cale Gentepression. Portez-la à MacKinley et il pourra remballer ses préjugés à propos des alcools de Baie-du-Butin."}, {"Porter la Bouteille de Stoley à MacKinley \"Loup des mers\" à Baie-du-Butin."}},
[2875] = {"ON RECHERCHE : André Barbe-en-feu", {"Le vil pirate André Barbe-en-feu est recherché pour crimes contre Gentepression et Gadgetzan.","Par décret du conseil gouvernemental, quiconque lit ceci est autorisé à utiliser la force, et à tuer si nécessaire, pour procéder à l'arrestation de André et de ses complices.","Par un autre décret, si André était décédé ou indisposé pendant son interrogatoire, sa tête servirait de preuve et la présentation de ladite tête au Chef de la sécurité Bilgewizzle constitue un contrat valable et inaliénable."}, {"Rapporter la tête d'André au Chef de la sécurité Bilgewhizzle à Port Gentepression."}},
[2876] = {"Horaires des bateaux", {"Ce parchemin répertorie les cargaisons envoyées entre Port Gentepression et Baie-du-Butin. La liste contient le nom des navires et des dates, passées ou à venir. Certaines lignes sont cochées d’un grand X. D'autres sont annotées avec \"Bon butin\" ou \"Dur combat\".","Quelqu'un à Port Gentepression apprécierait certainement de voir ce document."}, {"Rapporter le Planning des expéditions aux autorités de Port Gentepression."}},
[2877] = {"Nettoyer le rocher de l'Affût", {"Nous, les Wildhammer, nous souvenons du passé, mieux que nos frères d'Ironforge. Si vous désirez vous faire des amis, alors travaillez pour nous !","La Horde a abandonné ses positions dans les Hinterlands, autour du rocher de l'Affût qui est maintenant envahi par des Limaces et des Limons. Nous avons des plans pour ce secteur, mais nous avons besoin de quelqu'un pour faire du nettoyage.","C'est là que vous intervenez ! Allez là-bas et liquidez dix Limaces vertes et dix Limons de jade puis revenez me faire votre rapport ici. Exécution !"}, {"Tuer 10 Limaces vertes et 10 Limons de jade puis faire votre rapport à Fraggar Thundermantle au Nid-de-l'Aigle"}},
[2878] = {"La fleur-de-chant corrompue", nil, nil},
[2879] = {"Le Bâton d'Equinex", {"Il semble que ce bâton soit le Bâton d'Equinex. C'est une clé qui permet d'ouvrir le Monolithe d'Equinex qui est érigé dans les ruines de Vent-du-Corbeau, sur le continent, à l'ouest du Bosquet du rêve.","Localisez les quatre flammes qui brûlent toujours dans les ruines: Samha, Imbel, Byltan et Lahassa. Là, retrouvez leur essence et pendant que vous êtes près du Monolithe, utilisez cette essence pour dynamiser le Bâton d'Equinex. Vous pourrez alors ouvrir le Monolithe et prendre l'artefact sacré qui s'y trouve."}, {"Dynamiser le Bâton de Troyas et trouver le Monolithe d'Equinex."}},
[2880] = {"Chasse au collier troll", {"Comprenez bien ceci, <nom>. Nous ne faisons PAS partie de l'Alliance ! Les Wildhammer sont indépendants, ils prouvent leur valeur par l'action et leurs actes et non en bavardant, contrairement à ceux qui parlent afin de camoufler leur passivité !","Si vous cherchez des amis par ici, commencez par prouver votre valeur ! Ces idiots de trolls qui remplissent les Hinterlands portent parfois des colliers tribaux. Rapportez-en cinq et nous considérerons que vous avez fait un pas dans la bonne direction."}, {"Rapporter 5 Colliers tribaux trolls à Fraggar Thundermantle au Nid-de-l'Aigle"}},
[2881] = {"Chasse aux colliers trolls", nil, nil},
[2882] = {"L'or de Cuergo", {"Cette carte montre une plage orientée au sud. Sous la carte, des mots élégamment tracés…","Au sud de Gentepression, avec raison","Au sud de la maison","Trouvez un drapeau, un mât et des os","Creusez ici, si vous l'osez","Creusez et gagnez l'or de Cuergo !","Une clé il faut et une clé vous verrez","Les hommes d'en face il faudra tuer."}, {"Trouvez l'or de Cuergo !"}},
[2902] = {"Enquête chez les Griffebois", {"Les gnolls ne sont pas des lumières, mais ils ont certainement des documents concernant leurs récents agissements. Si nous trouvions des plans, alors nous aurions une meilleure idée de ce qui les motive.","Je veux que vous retourniez chez les Griffebois et que vous rapportiez tout ce qui ressemble à un plan de bataille. Ils doivent avoir quelque chose, leurs attaques sont trop coordonnées. Trouvez les plans de bataille et rapportez-les-moi."}, {"Trouver les Plans de bataille des gnolls dans les camps gnolls du sud du Camp Mojache."}},
[2903] = {"Les plans de bataille", {"Une fois trouvés les plans des gnolls, il reste à les porter à Hadoken Swiftstrider au Camp Mojache. Peut-être comprendra-t-il les projets des gnolls…"}, {"Rapporter les Plans de bataille des Griffebois à Hadoken Swiftstrider au Camp Mojache."}},
[2904] = {"Une jolie pagaille", {"<Kernobee grogne>Qu… Que s'est-il passé ?","La dernière chose dont je me souviens est d'avoir été capturé par un groupe de nains Sombrefer, d'avoir été battu comme plâtre et jeté ici avec ces troggs crasseux.","Sortez-moi de là, <nom> !","Voici le plan : vous me conduisez à la sortie, si nous survivons, vous allez à Baie-du-Butin prévenir Scooty que je suis allé à Ratchet pour informer Tinkerwiz de l'implication des Sombrefer dans la conception de cette super plate-forme.","Allons-y !"}, {"Escorter Kernobee jusqu’à la sortie de la Fuite du temps, puis faire un rapport à Scooty, à Baie-du-Butin."}},
[2922] = {"Sauver le cerveau de Techbot !", {"Lorsque notre cité bien-aimée de Gnomeregan est tombée, une étincelle a fait disjoncter ma pauvre création, le Techbot. Il nous aidait bien, moi et mes associés, avec ses nombreux gadgets et ses banques d'informations illimitées, mais son cerveau positronique a été polarisé. Techbot est maintenant négatronique et son comportement s'est inversé. Il erre, enragé, dans Gnomeregan !","Mais peut-être peut-il être sauvé. Si vous retrouviez sa mémoire principale et me la rapportiez, je pourrais découvrir la cause de la polarisation et réparer mon pauvre Techbot."}, {"Rapporter la Mémoire principale de Techbot au maître-artisan Overspark à Ironforge."}},
[2923] = {"Maître-artisan Overspark", {"Le maître-artisan Overspark est très contrarié par la perte de sa création mécanique, le Techbot. Je me souviens du Techbot comme d’une machine gentille et serviable, très utile aux gnomes, mais Overspark dit qu'il a eu des problèmes de fonctionnement.","Vraiment dommage, mais avec de la chance, cela pourrait être réparé !","Parlez avec maître Overspark, à Brikabrok, le quartier gnome d'Ironforge. Votre aide sera la bienvenue."}, {"Parler au maître-artisan Overspark à Ironforge."}},
[2924] = {"Les cerveaux mécaniques", {"Un composant pratiquement universel, le cerveau mécanique, est d'une grande utilité pour les artisans et les forgerons de Gnomeregan. Mais durant notre exode, personne n'a pensé à en emporter ! Et j'en ai besoin d'un grand nombre pour le travail que je vais bientôt accomplir.","S'il vous plaît, allez à Gnomeregan et rapportez-en. Vous les trouverez dans les profondeurs de notre cité, dans des conteneurs appelés des extrapolateurs artificiels.","Merci, <nom> et dépêchez-vous !"}, {"Rapporter 12 Cerveaux mécaniques à Klockmort Spannerspan à Ironforge."}},
[2925] = {"Les cerveaux mécaniques de Klockmort", {"Un ami gnome, Klockmort Spannerspan m'a écrit depuis Ironforge. Il me dit qu'il a besoin d'aide pour une mission dangereuse, mais vitale à Gnomeregan, l'ancienne et puissante cité gnome, aujourd’hui contaminée et envahie de gnomes fous et de leurs inventions.","Je dois rester ici, mais je dois aider mon ami. Si vous pouviez y aller à ma place, je serais votre débiteur.","Vous pouvez trouver Klockmort à Brikabrok, le quartier gnome d'Ironforge."}, {"Parler à Klockmort Spannerspan à Ironforge."}},
[2926] = {"Gnogaine", {"Depuis l'accident, Gnoarn discute et palabre sur le fait de nettoyer Gnomeregan de l'infestation des troggs et de trouver un remède à la maladie, <nom>.","Par chance, Razzle et moi sommes près de trouver quelque chose, un remède ou une recette de rhum particulièrement corsée, nous ne sommes pas sûrs… Nous le saurons après les tests ! Nous avons bien évidemment besoin de quelques ingrédients pour tester notre produit, notamment des retombées radioactives collectées sur des troggs irradiés de Gnomeregan. Remplissez cette fiole avec la Lueur verte et rapportez-la."}, {"Utiliser la Fiole de collecte plombée vide sur les Envahisseurs ou les Pilleurs irradiés pour recueillir des retombées radioactives. Une fois pleine, rapportez-la à Ozzie Togglevolt à Kharanos."}},
[2927] = {"Le jour d'après", {"Vous ne pensiez quand même pas que j’aie toujours été chauve ? Non <m’sieur/ma'am> ! C'est la fuite radioactive à Gnomeregan qui a causé cette tragique perte de cheveux. Avant cela, j'avais la seule coiffure afrognome de tout Azeroth.","<Gnoarn brandit le poing vers le ciel>","Maudits soient ces troggs crasseux, qu'ils soient tous maudits !","Peut-être qu'il n'est pas trop tard, <nom>. Nous pouvons peut-être encore la sauver, je parle de la ville ! Allez voir Ozzie Togglevolt à Kharanos. Lui et Razzle, son partenaire, travaillent à résoudre ce problème."}, {"Parler à Ozzie Togglevolt à Kharanos."}},
[2928] = {"Excavateurs gyrodrilmatiques", {"C'est affreux, <nom>. Nous n'avons pas suffisamment de pièces détachées pour notre flotte d'excavateurs gyrodrilmatiques à Dun Morogh.","Nous avons besoin de ces pièces, <nom>. Le meilleur endroit pour en trouver est Gnomeregan, mais j'ai entendu dire que les bots et les mechs grouillaient là-bas, comme les puces sur le dos d'un chien.","Vous devez aller à Gnomeregan et mettre en pièces quelques robots. Ramenez-moi des entrailles mécaniques. Deux douzaines devraient suffire."}, {"Rapporter 24 Entrailles mécaniques de robot à Shoni à Stormwind."}},
[2929] = {"La grande trahison", {"J'avais confiance en Thermaplugg, <nom>. Je ne pensais pas qu'il me trahirait, moi et tout le peuple gnome. Et pourquoi ? La puissance ? L'argent ? Toute chose qu'il aurait eu de toute façon. Maintenant nous avons été jetés hors de notre pays et ce malade est à sa tête. Le roi du vide...","Nous reprendrons Gnomeregan, <nom>. Nous n'arrêterons pas tant que la ville ne sera pas nôtre à nouveau. Si vous désirez vous joindre à nous, j'ai une mission simple à vous confier : tuer le traître. Détruisez le mekgénieur Thermaplugg."}, {"Aller à Gnomeregan et tuer le mekgénieur Thermaplugg. Puis retourner voir le Grand Bricoleur Mekkatorque."}},
[2930] = {"Sauvetage de données", {"Quand nous avons fui Gnomeregan, nous avons dû abandonner tant de données importantes !","J'ai besoin des données de la carte perforée prismatique d'une Matrice d'encodage 3005-D, dans Gnomeregan. C'est votre destination ; mais pour cela, vous devez d'abord accéder aux terminaux de modèles -A, -B et -C. Et ceci n'est possible qu'avec les cartes prismatiques, en commençant par les blanches... Et nous avons laissé toutes nos cartes dans la ville.","Pouvez-vous nous aider ?"}, {"Apporter une Carte perforée prismatique au Maître mécanicien Castpipe, à Ironforge."}},
[2931] = {"Du travail pour Castpipe", {"Le maître-mécanicien Castpipe à Ironforge a besoin d'aventuriers ! Il organise des groupes pour pénétrer à nouveau dans Gnomeregan afin d'effectuer une mission de récupération de première importance.","Il se peut que Castpipe ne soit pas membre des grandes Assemblées d'arcanologie, d'alchimie ou de sciences d'ingénieries comme je le suis, mais il reste un gnome intelligent. S'il considère que c'est important, alors mes calculs indiquent qu'il y a quatre-vingt pour cent de chance que ce soit véritablement important !","Il s'agit là des probabilités les plus optimistes en ce qui nous concerne !"}, {"Parler au Maître mécanicien Castpipe, à Ironforge."}},
[2932] = {"Sinistre message", {"Les trolls Witherbark étaient jadis des alliés de la Horde, mais ils ont trahi Thrall... ce qui ne peut être toléré !","Voici, prenez ma pique et allez dans les Hinterlands, loin au nord. Vous trouverez l'endroit en question au nord-est du Moulin-de-Tarren, un village tenu par nos alliés, les Réprouvés.","Quand vous entrerez sur les terres des Hinterlands, chassez les trolls Witherbark. Collectez leurs crânes, puis plantez ma pique dans le sol de l'un de leurs petits villages, Hiri'watha ou Zun'watha.","Montrez-leur que tout se paie en ce bas monde !"}, {"Collecter les Crânes de Witherbark, et les mettre en place sur la Pique de Nimboyae. Placer la Pique de Nimboya préparée dans l’un des Villages Witherbark, dans les Hinterlands, puis retourner voir Nimboya, à Strangleronce."}},
[2933] = {"Bouteilles de venin", {"Ces bouteilles contiennent un terrible venin.","Vu le nombre de bouteilles dans ce village, les Witherbark doivent projeter quelque chose d'important avec elles ; peut-être qu'un apothicaire, près du Moulin-de-Tarren, serait interéssé par l'étude d'un échantillon."}, {"Apporter une Bouteille de venin à un apothicaire, au Moulin-de-Tarren."}},
[2934] = {"Glande à venin intacte", {"Il me faut un échantillon frais du venin que vous m'avez apporté. Les araignées dont il provient hantent les Hinterlands. Chassez-les jusqu'à ce que vous trouviez une glande à venin intacte, puis rapportez-la-moi. Vous trouverez beaucoup d'araignées près de la ville des trolls de Shadra'Alor.","Ne tardez pas, <nom>, la glande à venin doit être fraîche lorsque vous l'apporterez."}, {"Rapporter une Glande à venin intacte à l'Apothicaire Lydon, au Moulin-de-Tarren."}},
[2935] = {"Consulter Maître Gadrin", {"Le venin des gardes du clan n'était pas le même que celui que vous m'avez rapporté. Ils proviennent sans doute d'araignées différentes.","Je veux un échantillon de venin frais. Pour savoir où en trouver, vous devez consulter un troll qui connaît bien les Witherbark : maître Gadrin, au village de Sen'jin. Allez en Durotar et parlez à Gadrin. Avec un peu de chance, il saura de quelle araignée les Witherbark tirent leur venin."}, {"Parler à Maître Gadrin au Village de Sen'jin."}},
[2936] = {"Le dieu-araignée", {"Les Witherbark adorent un dieu-araignée dont ils obtiennent ce venin extrêmement toxique. Ainsi, si vous voulez en collecter un peu, il vous faudra invoquer leur dieu !","Pour réussir l'invocation, vous devez connaître le vrai nom de cette divinité. Il est écrit sur une tablette, enterrée à Tanaris, dans la cité troll de Zul'Farrak. Dans une tombe gardée par Theka le Martyr, pour être plus exact.","Tuez Theka, trouvez et lisez la tablette pour connaître le véritable nom du dieu-araignée, puis revenez me voir."}, {"Lire la Tablette de Theka pour connaître le véritable nom du dieu-araignée des Witherbark, puis retourner voir Maître Gadrin."}},
[2937] = {"L'invocation de Shadra", {"Prenez ce parchemin et lisez-le devant l'autel de Shadra'Alor, loin à l'intérieur des terres des Witherbark, dans les Hinterlands. Shadra, le dieu-araignée sera invoqué et paraîtra."}, {"Terrasser Shadra, à l'autel de Shadra'Alor, puis rapporter son venin à l'Apothicaire Lydon, au Moulin-de-Tarren."}},
[2938] = {"Du venin pour Undercity", {"Le venin que vous avez récupéré sur le dieu araignée des Witherbark est une grande découverte ! J'ai pu tuer près de deux douzaines de lapins avec une seule goutte. Il est juste que certains de ces lapins étaient très jeunes et suffisamment petits pour tenir dans la paume de la main. Leur résistance aux toxines devait être très basse. Néanmoins, il s'agit là d'un puissant poison !","J'aimerais en envoyer un échantillon à mon supérieur, l'apothicaire Faranell à l'Apothicarium d'Undercity. Il ne fait aucun doute qu'il aimerait conduire ses propres expériences."}, {"Apporter le Colis de Faranell au maître apothicaire Faranell, à Undercity."}},
[2939] = {"En savoir plus", {"Bien que les Ruines de Feralas puissent être très dangereuses, elles ont beaucoup à raconter sur ce qui s'est passé ici.","Il y a quelques jours, tandis que je fouillais les décombres, j'ai découvert ce qui semble être une douve classique. Je ne peux cependant pas me défaire de l'idée que cela cache quelque chose... Angelas et moi avons fait des recherches dans nos livres, mais nous n’avons rien découvert la concernant.","Je connais une collègue à Darnassus qui pourrait peut-être nous dire de quoi il s'agit, <nom>. Pourquoi n'iriez-vous pas lui parler et voir si elle peut nous aider ?"}, {"Parler à Daryn Lightwind, au village de Rut'theran."}},
[2940] = {"Feralas : une histoire", {"Ce livre à l'air de ne pas avoir été ouvert depuis des années. Sa couverture est passée et ses pages jaunies ; mais en l'examinant de plus près, vous remarquez que c'est exactement ce que Troyas devait rechercher.","Vous le prenez, puis réalisez que vous devriez d'abord demander la permission avant de l'emprunter."}, {"Demander à Daryn Lightwind si vous pouvez emprunter son livre."}},
[2941] = {"Le prêt", {"J'ai étudié beaucoup de thèmes et de sujets, et ma dernière étude porte sur la créature appelée Gueule d'acier qui vit sur les plages, dans les Hinterlands. Il y a en particulier un spécimen que j'aimerais voir, nommé Gammerita. Mes recherches me retiennent ici, hélas. Je pense qu'une image de la bête suffirait à son étude.","Portez cette lettre à Curgle Cranklehop, à Tanaris. Elle a inventé une machine qui prend des images. Elle appelle cela : \"photographie\", je crois..."}, {"Apporter la lettre à Curgle Cranklehop, à Tanaris."}},
[2942] = {"La Pierre du matin", {"Lorsque vous retirez l'artefact du Monolithe, vous sentez que l'énergie du Bâton d'Equinex commence à décliner."}, {"Rapporter la Pierre étincelante et le Bâton d'Equinex à Troyas Moonbreeze, dans le bastion de Feathermoon."}},
[2943] = {"Troyas", {"Voilà, <nom>. Je vous en prie, prenez soin de mon livre.","Partez, à présent. Je suis sûre que Troyas attend votre retour avec impatience."}, {"Apporter le livre à Troyas Moonbreeze, dans le bastion de Feathermoon."}},
[2944] = {"Le Preneur d'images FX", {"Regardez ma dernière invention, <nom>. Tout ce que vous avez à faire est de viser puis d'appuyer sur ce bouton.","Quelle était la créature des Hinterlands que vous avez mentionnée ? Gammerita ? Eh bien, bonne chance. Je suis sûre que Daryn sera contente du cliché que vous lui en ferez.","Voici le Preneur d'images FX, faites-en bon usage et amusez-vous bien !"}, {"Utiliser le Preneur d'images FX pour prendre un cliché de Gammerita, puis retourner voir Daryn Lightwind à Rut'theran."}},
[2945] = {"Anneau sali", {"Vous avez découvert, sur le corps d'un des nains Sombrefer, ce qui semble être un anneau. La crasse le recouvre entièrement, rendant difficile son identification et impossible son port. Il vous semble pouvoir discerner, sous les résidus, comme le chatoiement d'un métal précieux et l'éclat d'une gemme.","Si vous voulez utiliser cet anneau, il vous faudra trouver un moyen de nettoyer la saleté qui le souille."}, {"Trouver un moyen de nettoyer l'Anneau sali."}},
[2946] = {"Questions sans réponses", {"Nous voulons que vous portiez les disques que vous avez trouvés, aux portes scellées d'Uldum. Nous... ne sommes pas sûrs de ce qui va se passer quand vous ferez cela.","Cela pourrait être risqué, aussi bien que parfaitement inefficace. Vous devrez utiliser les disques, d'une manière ou d'une autre... Peut-être les portes s'ouvriront-elles toutes seules à l'approche des disques... allez savoir. Essayez ce qui vous semblera bon et attendez-vous à tout.","Revenez ensuite à Ironforge. Du travail nous attend !"}, {"Porter les Disques de platine miniatures aux Portes d'Uldum, à Tanaris et chercher les indices de la connexion qui existe entre eux."}},
[2947] = {"Le retour de l'anneau", {"En ouvrant la boîte, vous trouvez ce à quoi vous vous attendiez : un anneau scintillant, comme flambant neuf ! Dès que vous l'enfilez, vous sentez une vague de puissance magique traverser votre corps.","Dans la face intérieure de l'anneau, vous voyez le poinçon d'Ironforge, accompagné de trois petites initiales gravées : \"TdK\". Ce peuvent être celles du précédent propriétaire de l'anneau ou celles de son artisan. Garder l'anneau... ou le rendre à qui il appartient. Difficiles décisions, en vérité..."}, {"Garder l'anneau ou retrouver la personne qui y a gravé les lettres."}},
[2948] = {"Amélioration gnomique", {"Écoutez, <nom>, cet anneau vous appartient mais je peux l'améliorer. Sa forme est très simple et j'ai beaucoup progressé depuis. Pour la modique somme de trente pièces d'argent, je lui donnerai de nouveaux atours... bon, un peu plus de trente pièces, car je suis un peu gêné financièrement, en ce moment.","Tous mes stocks de matériaux sont épuisés ; si vous m'apportez une barre d'argent et une agate mousse, je pourrai ajouter un treillis de perles et d'argent qui donnera à cet anneau un sacré coup de fouet !","Faites-moi confiance !"}, {"Apporter l’Anneau d'or brillant, une Barre d'argent, une Agate mousse et 30 pièces d’argent à Talvash del Kissel, à Ironforge."}},
[2949] = {"Le retour de l'anneau", {"En ouvrant la boîte produite par le Décapeur 5200, vous découvrez un anneau scintillant. Quand vous le passez à votre doigt, vous sentez une incroyable vague de puissance magique traverser votre corps.","Dans la face intérieure de l'anneau, vous pouvez voir le petit poinçon d'Orgrimmar à côté duquel les trois premières lettres gravées d'un mot sont visibles : \"NOG...\"","Ce sont peut-être les initiales de l'ancien propriétaire de l'anneau ou de son fabricant. Garder l'anneau... ou retrouver \"NOG\" - difficile décision, en vérité..."}, {"Garder l'anneau ou retrouver la personne qui y a gravé les lettres."}},
[2950] = {"Nogg améliore l'anneau", {"Non, non, non, je ne suis pas en train de dire que cet anneau est à moi. Cependant, votre information sur les Sombrefer fut très utile. J'améliorerai cet anneau pour vous, bien mieux que ne le pourrait celui qui a forgé cette pauvre petite chose... sans doute un gnome, d'ailleurs...","Si vous m'apportez une barre d'argent, une agate mousse et trente pièces d'argent, je pourrai ajouter un treillis de perles et d'argent qui donnera une ampleur toute nouvelle à la magie contenue dans cet anneau. Cela vous intéresse ?"}, {"Apporter l’Anneau d'or brillant, une Barre d'argent, une Agate mousse et 30 pièces d’argent à Nogg, à Orgrimmar."}},
[2951] = {"Le Décapeur 5200 !", {"L'étrange objet est là, au milieu des gnomes. Une petite plaque sur la machine indique : \"Décapeur 5200\", suivit d'un texte :","\"La crasse et les résidus ruinent vos objets ? Le Décapeur 5200 est l'outil qu'il vous faut pour restaurer toute la brillance et l'éclat qu'ils méritent. Veuillez placer l'objet à décaper dans la machine, ainsi que trois pièces d'argent dans la fente prévue à cet effet et attendez un petit moment : vos objets de valeur en sortiront comme neufs\""}, {"Insérer un Objet sali dans le Décapeur 5200, sans oublier de jeter trois pièces d'argent dans la fente, pour démarrer la machine."}},
[2952] = {"Le Décapeur 5200 !", nil, nil},
[2953] = {"Le Décapeur décape encore", nil, nil},
[2954] = {"Le Gardien des pierres", {"Bonjour, je garde l’entrée. Si vous n’avez pas les Plaques d'Uldum intégrées à votre jeu de disques je ne vous laisserai pas entrer dans le complexe d’Uldum.","L’apparition du Gardien des pierres de Norgannon est la réaction qui était espérée en vous envoyant dans le Désert de Tanaris avec les disques de platine. Vous devez maintenant découvrir l’histoire du Gardien des pierres avant de continuer."}, {"Apprendre le but du Gardien des pierres de Norgannon, puis interagir avec le Socle d'Uldum, dans le désert de Tanaris."}},
[2962] = {"Encore plus de Lueur verte !", {"Votre échantillon n'était pas seulement insuffisant, mais impur.","Nous avons besoin de plus de Lueur verte. Les troggs ne sont apparemment pas encore assez irradiés. Vous devrez aller à Gnomeregan et rapporter de la matière radioactive, prélevée à la source. Je parle bien sûr de toutes ces gelées, rôdeurs et autres horreurs irradiées qui errent là-bas. Attention, la collecte et le conditionnement de telles matières est difficile ; il vous faudra faire plusieurs tentatives avant qu'un échantillon valable soit prélevé."}, {"Voyager à Gnomeregan et rapporter de la Matière radioactive à haut potentiel. Attention, la matière radioactive est instable et se désactive rapidement.","Après le travail, conserver la Flasque plombée lourde, pour Ozzie."}},
[2963] = {"Prodiges d'Uldum", {"Ah, <nom> ! La nouvelle de votre découverte dans Uldaman a mis en joie la Ligue des explorateurs. La Ligue a beaucoup travaillé pour découvrir le secret des petits disques de platine que vous avez trouvés. Je puis vous dire que nous sommes au seuil de quelque chose d'important !","L'Historien Karnik a quelques hypothèses très intéressantes sur l'origine de la vallée des Guetteurs dans le désert de Tanaris. Il voudrait vous parler dès que possible."}, {"Parler à l'Historien Karnik dans le Hall des explorateurs, à Ironforge."}},
[2964] = {"Travail imminent", {"Bien, <nom>... Il semblerait que nous soyons dans l'impasse. Nous avons besoin des Plaques d'Uldum, mais nous ne savons pas où elles se trouvent. Nous devons réfléchir avant d'avoir un plan pertinent. J'ai bien quelques petites idées et j'espère que nous vous enverrons bientôt pour les récupérer.","Le Grand Explorateur doit être informé de tout cela, <nom> ; il est à parier qu'il veut aussi vous remercier pour l'aide que vous avez apportée à la Ligue."}, {"Parler au Grand Explorateur Magellas, à Ironforge."}},
[2965] = {"Prodiges d'Uldum", {"Ah, <nom>, vous voilà de nouveau. Vos révélations quant au site de fouilles d'Uldaman ont motivé bien des discussions parmi nous. J'en parlais justement avec mon ami, le druide Nara Wildmane et il m'apparut que son expérience passée pouvait orienter la suite de notre action.","Allez la voir et écoutez l'histoire de ses voyages dans la vallée des Guetteurs du désert de Tanaris. Elle vous attend à la Cime des Anciens."}, {"Aller à la Cime des Anciens, dans Thunder Bluff et parler à Nara Wildmane."}},
[2966] = {"Questions sans réponses", {"Le Sage Truthseeker et moi-même voulons que vous portiez les disques que vous avez découverts jusqu'aux portes scellées d'Uldum. Nous... ne sommes pas sûrs de ce qui va arriver quand vous serez là-bas.","Cela pourrait être risqué, aussi bien que parfaitement inefficace. Vous devrez utiliser les disques, d'une manière ou d'une autre... peut-être les portes s'ouvriront-elles toutes seules à l'approche des disques... allez savoir. Essayez ce qui vous semblera bon et attendez-vous à tout.","Apprenez ce que vous pourrez, puis revenez me voir ; nous aurons beaucoup à nous dire."}, {"Porter les Disques de platine miniatures aux Portes d'Uldum, à Tanaris et chercher les indices de la connexion qui existe entre eux."}},
[2967] = {"Le retour à Thunder Bluff", {"Vous accédez au socle, à nouveau ; les disques de platine réapparaissent une nouvelle fois dans votre sac à dos.","Avec ce que vous avez appris du Gardien des pierres et les disques en votre possession, vous pouvez à présent retourner à Thunder Bluff et informer Nara Wildmane de votre découverte."}, {"Parler à Nara Wildmane, dans Thunder Bluff."}},
[2968] = {"Travail imminent", {"Bien, <nom>... Il semble que nous soyons dans l'impasse. Nous avons besoin des Plaques d'Uldum, mais nous ne savons pas où elles se trouvent. Nous devons faire des recherches à ce sujet. J'ai bien quelques petites idées et j'espère que vous irez bientôt les récupérer. Nous vous demanderons bientôt de vous joindre à nous pour découvrir la vérité.","Le Sage Truthseeker doit être informé de cela, <nom> ; il voudra aussi vous remercier d'avoir, une fois de plus, répondu à l'appel."}, {"Parler au Sage Truthseeker, à Thunder Bluff."}},
[2969] = {"Liberté pour toutes les créatures", {"L'odieux clan du Totem sinistre capture des créatures féeriques, <nom>. Jer'kai et moi ne savons pas quels desseins ils poursuivent en faisant cela, mais ce n'est certainement rien de bon.","Nous ne sommes pas assez puissants pour combattre le Totem sinistre dans son camp et libérer les créatures, mais peut-être qu'avec votre aide...","Si vous êtes d'accord et avez quelques amis robustes qui voudraient se joindre à vous, allez libérer les créatures de leurs cages grâce à cette clé et assurez-vous qu'elles restent en vie. Je serai à vos côtés tout le temps ; Jer'kai, pour sa part, restera ici, au cas où..."}, {"A l’aide de la Clé de la cage en bambou, libérer les Fées fléchetteuses de l’enclos dans le Camp du Totem sinistre. Au moins 6 Fées fléchetteuses et Kindal Moonweaver doivent survivre.","Vous avez 6 minutes avant que Kindal décide qu’il est temps de battre en retraite et que vous échouiez dans votre quête."}},
[2970] = {"Justice de châtiment", {"Comme vous l'a déjà dit Kindal, nous allons désormais combattre le clan du Totem sinistre. Ensemble, nous devrions pouvoir affronter quelques taurens, mais nous apprécierions votre aide, si vous êtes d'accord.","Vous avez vu l'un des camps, en aval ; il y en a encore d'autres à l'est. Faites selon vos compétences et techniques mais infligez autant de dégâts possibles aux taurens. Quand ils auront goûté à la piqûre des flèches de Kindal et à vos propres armes, nous nous retrouverons tous ici, pour faire le point."}, {"Tuer 12 Naturalistes du Totem sinistre, 10 Ecumeurs du Totem sinistre et 6 Chamans du Totem sinistre avant de retourner voir Jer'kai Moonweaver, en Feralas."}},
[2972] = {"Justice de châtiment", {"Ici, dans les terres sauvages, Kindal et moi n’avons pas grand-chose à vous offrir, mais nous voulons récompenser votre aide. Prenez ma chevalière. Si un jour vous êtes dans les environs de Teldrassil, allez au Temple de la Lune au cœur de Darnassus. Là, vous trouverez ma Haute prêtresse Tyrande Whisperwind. L'anneau lui dira que nous allons bien et que votre histoire est vraie.","Nous vous remercions tous les deux, <nom>."}, {"Apporter la Chevalière de Jer'kai's à la Grande prêtresse Tyrande Whisperwind, à Darnassus."}},
[2973] = {"Une nouvelle cape", {"Ah, vous voici, <classe>. Lors d'une mission pour nos maîtres d'Orgrimmar, j'ai voyagé loin en direction de Feralas.","Mon amie Korrin adore les vêtements, j'ai donc l'intention de lui confectionner une nouvelle cape ornée des ailes légères des créatures féériques. M'aiderez-vous ? Les Fées fléchetteuses aux ailes sublimes se trouvent à l'ouest de Camp Mojache."}, {"Rapporter 10 Ailes iridescentes de Fées fléchetteuses à Krueg Skullsplitter à Camp Mojache."}},
[2974] = {"Une découverte sinistre", {"Quoi ! Vous avez rencontré des taurens dans le secteur ? Vous plaisantez ! A quoi ressemblaient-ils !?","Le clan Totem sinistre ! Vous en avez vus ? Belgrom, l'un de nos maîtres, s'intéresse à eux… Il offre même une prime à leur égard.","Vous devez repartir, <classe>. Retournez-y et apportez-moi leurs cornes. Des montagnes si vous pouvez. Ils ne doivent pas être traités à la légère ou sous-estimés. Retournez-y et massacrez-les. Que les rivières de Feralas rougissent de leur sombre sang."}, {"Apporter 20 Cornes de Totem sinistre à Krueg Skullsplitter à Camp Mojache."}},
[2975] = {"Les ogres de Feralas", {"La tribu des ogres Gordunni ne peut pas passer inaperçue, dans cette région ; regardez autour de vous : le pays est comme blessé par leur simple présence, <nom>. Nous devons les repousser avant qu'ils ne se répandent plus avant dans Feralas.","Suivez la rivière au nord, vous tomberez sur leur avant-poste. Portez la mort et la désolation parmi eux, <nom>. Nous devons leur montrer à quel point nous les trouvons indésirables."}, {"Tuer 10 Ogres gordunni, 10 Ogre-magi gordunni et 5 Brutes gordunni pour Rok Orhan, à Camp Mojache."}},
[2976] = {"Une découverte sinistre", {"Comment ?","Ah, j'ai parlé d'une récompense, n'est-ce pas ? Bon... ça m'apprendra à ouvrir la bouche !","Vous avez fait le travail et je suppose que je vous dois ce que je vous avais promis. Prenez les cornes, je les ai mises dans une caisse pour vous et portez-les à Belgrom Rockmaul, dans la Vallée de l'honneur. Il souhaite entendre votre rapport et vous récompenser.","Que les vents vous portent, <classe>, et merci encore pour tout."}, {"Apporter la Caisse de Cornes de Totem sinistre à Belgrom Rockmaul, Orgrimmar."}},
[2977] = {"Retour à Ironforge", {"Comme vous accédez de nouveau au socle, les disques de platine apparaissent une fois de plus dans votre sac à dos.","Avec ce que vous avez appris du Gardien des pierres et les disques de nouveau en votre possession, vous pouvez maintenant retourner à Ironforge et informer la Ligue des explorateurs de votre découverte."}, {"Parler à l'Historien Karnik, à Ironforge."}},
[2978] = {"Le parchemin Gordunni", {"En ramassant les parchemins salis, vous remarquez qu'ils représentent quelque cérémonie ou rassemblement. Rok Orhan aimerait sûrement jeter un oeil sur ce parchemin et savoir quel est son rapport avec les Gordunni."}, {"Apporter le Parchemin Gordunni à Rok Orhan, à Camp Mojache."}},
[2979] = {"Sombre cérémonie", {"On dirait bien des plans pour quelque sombre cérémonie, <nom>. D'après ce que je vois, cette assemblée est tenue par les seigneurs-mages Gordunni.","Trouvez l'un de ces seigneurs-mages et prenez-lui un orbe.","L'orbe d'un mage contient beaucoup de pouvoirs et nous aidera à en savoir plus sur cette cérémonie secrète des Gordunni."}, {"Rapporter un Orbe Gordunni à Rok Orhan, à Camp Mojache."}},
[2980] = {"Les ogres de Feralas", {"Vous avez prouvé que vous saviez vous battre et je sais que vous avez le courage de continuer la mission.","Allez dans les Plateaux sauvages, au sud-ouest. Vous y verrez des structures détruites et abandonnées sur les terres qui, jadis, appartenaient aux elfes de la nuit. Dirigez-vous sur ces ruines et quand vous traverserez des terres profanées aux arbres flétris, vous trouverez les Gordunni. Repoussez-les.","Ces ogres sont beaucoup plus forts et dangereux que ceux que vous avez précédemment combattus, <nom>. Soyez <prudent/prudente>."}, {"Tuer 10 Chamans Gordunni, 10 Démonistes Gordunni et 5 Marteleurs Gordunni pour Rok Orhan, à Camp Mojache."}},
[2981] = {"Menace sur Feralas", {"Feralas est un lieu parfait pour faire ses preuves, <classe>. Une tribu d'ogres appelées les Gordunni domine militairement l'endroit et leur nombre, sans cesse, s'accroît. Ils doivent être arrêtés avant de devenir une véritable menace pour Feralas.","Je connais une certaine Rok Orhan qui habite à Camp Mojache, dans Feralas ; allez la trouver, elle vous en dira plus sur la situation."}, {"Comme le veut Belgrom Rockmaul, aller parler à Rok Orhan, à Camp Mojache."}},
[2982] = {"Les Plateaux sauvages", {"Bien que ce soit risqué, Troyas et moi avons visité les ruines dans les forêts de Feralas.","Ces derniers temps, Troyas à pris l'habitude de se rendre seul sur les Plateaux sauvages, et je m'inquiète pour lui. Les ogres Gordunni contrôlent la zone et ils ne traitent pas avec douceur ceux qui s'aventurent dans leur domaine.","Accepteriez-vous d'aller dans les ruines, au sud-est, et de tuer quelques ogres afin de rendre la zone un peu plus sûre pour Troyas. Je vous en remercie par avance."}, {"Tuer 8 Démonistes Gordunni, 8 Chamans Gordunni et 8 Brutes Gordunni pour Angelas Moonbreeze."}},
[2983] = {"L'appel du feu", {"Cela fait quelque temps que vous n'avez pas été <mis à l'épreuve/mise à l'épreuve>, <nom>. Vous avez fait preuve de patience dans vos études jusque-là, mais il est temps pour vous d'apprendre de plus grands pouvoirs… il est temps d'apprendre le réel pouvoir. L'élément de feu vous appartient si vous êtes <prêt/prête> à payer pour cela.","Recherchez Kranal Fiss dans les Tarides. Il réside dans une petite demeure au nord de la Croisée, mais faites attention ; si vous atteignez les terres de ces maudits elfes, c'est que vous êtes <allé/allée> trop loin."}, {"Trouver Kranal Fiss dans les Tarides."}},
[2984] = {"L'appel du feu", {"Cela fait longtemps que vous n'avez pas été <mis à l'épreuve/mise à l'épreuve>, <nom>. Vous avez montré de la patience jusque-là dans vos études, mais le temps est à présent arrivé d'apprendre la destruction et le chaos. L'élément Feu peut vous appartenir, si vous êtes <prêt/prête> à en payer le prix.","Recherchez Kranal Fiss dans les Tarides. Il vit dans une petite demeure au nord de la Croisée, mais faites attention, si vous atteignez les terres des elfes de la nuit, c'est que vous êtes <allé/allée> trop loin."}, {"Trouver Kranal Fiss dans les Tarides."}},
[2985] = {"L'appel de l'eau", {"Vous êtes capable d'affronter la prochaine épreuve, <nom>. Vous avez su monter de la patience et avez progressé dans vos études, mais il est maintenant temps d'apprendre plus encore et de connaître la pureté de l'eau.","Cherchez Islen Waterseer, dans les Tarides. Elle vit le long de la côte est, au sud du fleuve Furie-du-Sud et de Ratchet. Vous la trouverez dans sa cabane de pêcheur. Bonne chance, <nom>."}, {"Trouver Islen Waterseer dans les Tarides."}},
[2986] = {"L'appel de l'eau", {"Cela fait longtemps que vous n'avez pas été <mis à l'épreuve/mise à l'épreuve>, <nom>. Vous avez montré de la patience jusque-là dans vos études, mais le temps est à présent arrivé pour vous d'apprendre la pureté de l'Eau.","Cherchez Islen Waterseer dans les Tarides. Elle se trouve le long de la côte est, au sud du fleuve Furie-du-Sud et de Ratchet. Vous la trouverez dans une hutte de pêcheur. Bonne chance à vous, <nom>."}, {"Trouver Islen Waterseer dans les Tarides."}},
[2987] = {"Cobalt Gordunni", {"Je viens juste d'arriver dans Feralas et j'ai appris que les ogres Gordunni qui y résident gardent d'intéressants minéraux. Impossible de savoir ce qu'ils projettent, mais ils protègent leurs stocks farouchement et les enterrent pour empêcher qu'un fou comme moi ne mette la main dessus.","Si vous êtes d'accord, voici ma pelle : allez dans l'avant-poste des Gordunni, plus au nord, et extrayez-moi un peu de cobalt Gordunni. Cherchez la lueur bleue, puis commencez à creuser !"}, {"Collecter 12 échantillons de Cobalt Gordunni pour Orwin Gizzmick, à Camp Mojache."}},
[2988] = {"Les cages Witherbark", {"Le fils de notre plus puissant gryphon est parti ! L'héritier d'Aile-vive, Bec-tranchant, a été perdu tandis qu'il chassait avec ses parents il y a quelques jours, et nous pensons que ce sont les trolls qui l'ont enlevé.","Vous devez nous aider. Passez en revue les cages dans les deux villages de trolls Witherbark pour savoir si Bec-tranchant s'y trouve."}, {"Inspecter les cages dans les deux villages Witherbark, puis retourner auprès du Maître des gryphons Talonaxe."}},
[2989] = {"L'autel de Zul", {"Bec-tranchant ne se trouvait pas avec les trolls Witherbark. Peut-être les trolls du clan Vilebranch l'ont-ils enlevé ? Partez pour l'autel de Zul sur le bord ouest du territoire Vilebranch et cherchez le jeune gryphon."}, {"Regarder sur le dessus de l'autel de Zul, puis retourner auprès du Maître des gryphons Talonaxe."}},
[2990] = {"Thadius Grimshade", {"Apportez cette plume à Thadius Grimshade, le devin. Il a quitté le Nid-de-l'Aigle il y a des années pour engranger un savoir plus sombre, mais dans son cœur il reste un Wildhammer !","Peut-être que ses talents peuvent nous aider à retrouver Bec-tranchant.","Cette plume provient du nid où dormait le jeune gryphon. Donnez-la à Thadius qui, avec un peu de chance, pourra utiliser ses talents divinatoires pour localiser Bec-tranchant.","Bonne chance, <nom>. Vous trouverez Thadius dans les Terres foudroyées, dans la tour des mages du Rempart-du-Néant."}, {"Apporter la Plume de Bec-tranchant à Thadius Grimshade, dans les Terres foudroyées."}},
[2991] = {"Médaillon de Nekrum", {"Si vous voulez que je localise votre gryphon, vous devrez faire quelque chose pour moi. Il y a un troll dans Zul'Farrak, Nekrum Mâchetripes, qui possède un médaillon que j'aimerais beaucoup étudier. Apportez-le-moi et j'utiliserai mes pouvoirs pour retrouver Bec-tranchant.","Zul'Farrak est de l'autre côté de l'océan, à l'est, dans le désert de Tanaris."}, {"Apporter le Médaillon de Nekrum à Thadius Grimshade, dans les Terres foudroyées."}},
[2992] = {"La Divination", {"Laissez-moi un peu de place pour me permettre de pratiquer le rituel de divination. Je vais connecter la plume à son propriétaire, ce qui me donnera accès à Bec-tranchant lui-même.","Si tout se déroule comme prévu, évidemment... Sinon, hé bien, vous feriez mieux de ne pas regarder."}, {"Attendre que Grimshade ait fini de pratiquer son rituel de divination."}},
[2993] = {"Retour dans les Hinterlands", {"Bec-tranchant est enfermé dans une cage, placée haute sur une falaise au sommet de Jintha'Alor dans les Hinterlands. La clé de cette cage est en possession de la grande prêtresse des trolls du clan Vilebranch, la vile prêtresse maléfique Maléficia.","Voilà, j'ai fait ma part. Je vous souhaite bonne chance dans votre quête, <nom>."}, {"Parler au Maître des gryphons Talonaxe."}},
[2994] = {"Sauver Bec-tranchant", {"Bec-tranchant doit être secouru !","Frayez-vous un chemin, à coups d'épée s'il le faut, jusqu'au sommet de Jinth'Alor, au coeur du territoire des Vilebranch et arrachez la clé de la cage à la vile prêtresse maléfique Maléficia. Enfin, retrouvez et libérez Bec-tranchant !"}, {"Combattre jusqu’au sommet de Jintha'Alor, tuer la vile prêtresse Maléficia pour obtenir la clé de la cage de Bec-tranchant, et sauver Bec-tranchant."}},
[2995] = {"Lignes de communication", {"Le gîte de Quel'Danil est un centre de communication pour les elfes des Hinterlands. Leurs ambassadeurs essaient d'entrer en contact avec les races partageant leur connaissance des trolls Witherbark, qui habitent dans le sud des Hinterlands. Tous les documents concernant les communications sont gardés dans le gîte.","Nous ne voulons pas que les gens du Haut-val tissent des alliances, <nom>.","Détruisez les documents du Haut-val. Nous devons brûler leurs lignes de communication... au sens propre comme au sens figuré."}, {"Voyager dans les Hinterlands et brûler les Registres du Haut-val, le Rapport du Haut-val et les Notes du Haut-val, pour Oran Snakewrithe, à Undercity"}},
[2996] = {"À la recherche de Strahad", {"Je vous ai beaucoup appris et maintenant je dois passer la main à un autre professeur. Il s'appelle Strahad et habite à Ratchet. C'est un humain, mais que cela ne vous incline pas à croire qu'il est faible. Il est de petite taille, c'est vrai, mais très puissant et pourra vous apprendre des invocations beaucoup plus importantes. Allez le voir, maintenant, vous le trouverez à Ratchet... A côté de la tour qui surplombe le port."}, {"Parler à Strahad Farsan, à Ratchet."}},
[2997] = {"Le Tome de divinité", {"Bonjour, <nom>. Je sais que vous êtes très <occupé/occupée> à aider les gens de Dun Morogh, mais si vous avez le temps, allez jusqu'à Ironforge pour parler à la vénérable Tiza Battleforge. Les plus jeunes de nos paladins vont la voir lorsqu'ils veulent en apprendre plus sur la Lumière et sur ce que l'on attend d'eux.","Vous la trouverez à l'étage du Hall des Mystères qui se trouve dans la Garde mystique. Bonne chance, <nom>."}, {"Parler à Tiza Battleforge à Ironforge."}},
[2998] = {"Le Tome de divinité", {"Salutations ! Duthorian Rall, l'un de nos paladins les plus influents, parlait de vous récemment. Je l'ai entendu dire qu'il était temps que vous soyez <mis/mise> à l'épreuve... Ainsi que des compliments vous concernant.","Je vous conseille de vous rendre à Stormwind et de lui parler. Je pense que vous le trouverez dans la cathédrale de la Lumière, où il passe le plus clair de son temps."}, {"Parler à Duthorian Rall à Stormwind."}},
[2999] = {"Le Tome de divinité", {"Bonjour, <nom>. Je sais que vous êtes très <occupé/occupée> à aider les gens de Dun Morogh, mais si vous avez le temps, allez jusqu'à Ironforge pour parler à la vénérable Tiza Battleforge. Les plus jeunes de nos paladins vont la voir lorsqu'ils veulent en apprendre plus sur la Lumière et sur leurs obligations."}, {"Parler à Tiza Battleforge, à Ironforge."}},
[3000] = {"Le Tome de divinité", {"Bonjour, <nom>. Je sais que vous êtes très <occupé/occupée> à aider les gens d'Azeroth, mais si vous avez le temps, retournez à Ironforge et parlez à l'une de nos paladins les plus respectés, Tiza Battleforge. Nos jeunes paladins nains vont la consulter quand ils veulent en apprendre plus au sujet de la Lumière et sur ce que l'on attend d'eux.","Vous la trouverez à l'étage du Hall des Mystères qui se trouve dans la Garde mystique."}, {"Parler à Tiza Battleforge à Ironforge."}},
[3001] = {"À la recherche de Strahad", {"Je vous ai appris des sorts, mais l'heure est venue pour d'autres leçons que je ne puis plus prodiguer. Votre nouveau professeur habite à Ratchet ; il s'appelle Strahad. C'est un humain, mais ne vous fiez pas à cela : il n'est ni faible ni hostile et ses pouvoirs sont grands. Il pourra vous enseigner les grandes invocations que vous avez maintenant les moyens d'apprendre.","Vous le trouverez à Ratchet... à côté de la tour qui surplombe le port."}, {"Trouver Strahad Farsan, à Ratchet."}},
[3002] = {"L'Orbe Gordunni", {"Je connais bien mon ennemi, <nom>, mais je ne suis qu'un guerrier. Je ne peux pas vous dire à quoi peut servir cet orbe.","Mais je connais un mage à Orgrimmar qui saura nous en dire plus à ce sujet. Montrez-lui l'objet."}, {"Apporter l'Orbe Gordunni à Uthel'nay, au pavillon de Buissombre, à Orgrimmar."}},
[3022] = {"À manipuler avec soin", {"<nom>, je vois que vous aspirez à embrasser ma louable cause. Bien entendu, vous pouvez faire quelque chose pour moi !","Apportez cette caisse d'oeufs à Erelas Ambersky. Ce druide les met en couveuse puis entraîne les jeunes hippogryphes. Vous le trouverez dans Teldrassil, dans un petit village appelé Rut'theran."}, {"Apporter la Caisse soigneusement empaquetée à Erelas Ambersky, au village de Rut'theran."}},
[3042] = {"Agent durcissant troll", {"Les trolls Sandfury de Zul'Farrak fabriquent un agent durcissant à partir du grès, que j'utilise aussi pour tremper mes créations. Cette matière est très prisée, et si vous m'en rapportez une bonne quantité, vous serez largement <rétribué/rétribuée>.","Zul'Farrak est au nord-ouest de Gadgetzan. Bonne chance."}, {"Apporter 20 Fioles d'agent durcissant troll à Trenton Lighthammer, dans Gadgetzan."}},
[3062] = {"Coeur noir", {"Edana Serrehaine est la reine des harpies Ruissenord, qui vivent dans les ruines, à l'ouest. Cette créature est le mal à l'état brut ; il paraît que son coeur est de cristal froid, vide de toute émotion sauf de haine pure. Je vous demande de me rapporter le coeur d'Edana, après l'avoir tuée.","Un cor est en possession des harpies ; et s'il est utilisé au lieu-dit des Pierres de Serrehaine, dans la partie nord des ruines, la reine est invoquée.","Imaginez sa surprise quand elle se rendra compte que ce n'est pas sa chère famille, mais sa mort qui l'a appelée."}, {"Rapporter le Coeur noir d'Edana Serrehaine à Talo Thornhoof, à Camp Mojache."}},
[3063] = {"Se venger des Ruissenord", {"Jadis, j'étais un chasseur de renom et ne craignais aucune espèce de créature. Jusqu'à ce que...","Un jour que j'aiguisais mon épée dans la forêt, après une rude bataille, des harpies m'ont encerclé et attaqué. Je ne sais comment je réussis à en sortir vivant.","Encore maintenant, je peux entendre leurs affreux cris de crécelle...","Depuis cette embuscade, je ne suis plus le même tauren ; j'ai peur et mon âme est emplie de haine.","Les harpies Ruissenord vivent à l'ouest. <nom>, soyez le bras armé de ma colère, vengez-moi !"}, {"Tuer 4 Harpies Ruissenord, 4 Volplumes Ruissenord, 4 Imploratrices célestes Ruissenord et 4 Pourfendeuses Ruissenord pour Talo Thornhoof."}},
[3065] = {"Tablette simple", {"Ah, pendant que vous n’étiez pas là un parchemin est arrivé pour vous, <nom>.","Lisez-le quand vous en aurez le temps. Si je ne me trompe pas, il provient du Maître des guerriers Frang. Il souhaiterait s’entretenir avec vous, quand vous jugerez le moment venu. Vous le trouverez à l’extérieur de l’Antre, dissimulé dans l’ombre."}, {"Lire la Tablette simple, et parler à Frang, dans la Vallée des épreuves."}},
[3082] = {"Tablette gravée", {"Ah, pendant votre absence, on a apporté une tablette pour vous, <nom>.","Lisez-la, dès que vous aurez un moment. Si je ne me trompe pas, elle provient du Maître des chasseurs, Jen'shan. Elle souhaiterait s'entretenir avec vous, quand vous jugerez le moment venu."}, {"Parler à Jen'shan dans la Vallée des épreuves"}},
[3083] = {"Tablette codée", {"Ah, pendant votre absence, on a apporté une tablette pour vous, <nom>.","Lisez-la, dès que vous aurez un moment. Si je ne me trompe pas, elle provient du Maître des voleurs, Rwag. Il souhaiterait s'entretenir avec vous, quand vous jugerez le moment venu."}, {"Lire la Tablette codée, et parler à Rwag, dans la Vallée des épreuves."}},
[3084] = {"Tablette runique", {"Ah, pendant votre absence, on a apporté une tablette pour vous, <nom>.","Lisez-la, dès que vous aurez un moment. Si je ne me trompe pas, elle provient du Maître des chamans, Shikrik. Elle souhaiterait s'entretenir avec vous, quand vous jugerez le moment venu."}, {"Parler à Shikrik dans la Vallée des épreuves."}},
[3085] = {"Tablette bénie", {"Ah, pendant votre absence, on a apporté une tablette pour vous, <nom>.","Lisez-la, dès que vous aurez un moment. Si je ne me trompe pas, elle provient du Maître des prêtres, Ken'jai. Il souhaiterait s'entretenir avec vous, quand vous jugerez le moment venu."}, {"Lire la Tablette bénie, et parler à Ken'jai, à l’extérieur de l’Antre, dans la Vallée des épreuves."}},
[3086] = {"Tablette glyphée", {"Ah, pendant votre absence, on a apporté une tablette pour vous, <nom>.","Lisez-la, dès que vous aurez un moment. Si je ne me trompe pas, elle provient du Maître des mages, Mai'ah. Elle souhaiterait s'entretenir avec vous, quand vous jugerez le moment venu."}, {"Lire la Tablette glyphée, et parler à Mai'ah, près de l’entrée de l’Antre, dans la Vallée des épreuves."}},
[3087] = {"Parchemin gravé", {"Ah, pendant que vous n’étiez pas là un parchemin est arrivé pour vous, <nom>.","Lisez-le quand vous en aurez le temps. Si je ne me trompe pas, il provient du Maître des chasseurs, Jen'shan. Elle souhaiterait s’entretenir avec vous, quand vous jugerez le moment venu."}, {"Lire le Parchemin gravé, et parler à Jen'shan, dans la Vallée des épreuves."}},
[3088] = {"Parchemin codé", {"Ah, pendant que vous n’étiez pas là un parchemin est arrivé pour vous, <nom>.","Lisez-le quand vous en aurez le temps. Si je ne me trompe pas, il provient du Maître des voleurs, Rwag. Il souhaiterait s’entretenir avec vous, quand vous jugerez le moment venu."}, {"Lire le Parchemin codé, et parler à Rwag, dans la Vallée des épreuves."}},
[3089] = {"Parchemin runique", {"Ah, pendant que vous n’étiez pas là un parchemin est arrivé pour vous, <nom>.","Lisez-le quand vous en aurez le temps. Si je ne me trompe pas, il provient du Maître des chamans, Shikrik. Elle souhaiterait s’entretenir avec vous, quand vous jugerez le moment venu."}, {"Lire le Parchemin runique, et parler à Shikrik, dans la Vallée des épreuves."}},
[3090] = {"Parchemin contaminé", {"Ah, pendant que vous n’étiez pas là un parchemin est arrivé pour vous, <nom>.","J'évite normalement de traiter avec des démonistes, si c'est possible, mais je pense que vous devriez le lire, quand vous en aurez le temps. Si je ne me trompe pas, il provient du maître qui vous a été récemment assigné, Nartok. Il souhaiterait s’entretenir avec vous, quand vous jugerez le moment venu."}, {"Lire le Parchemin contaminé, et parler à Nartok, à l’intérieur de l’Antre, dans la Vallée des épreuves."}},
[3091] = {"Note simple", {"Il y a quelques instants, une messagère était à votre recherche, <nom>. Je crois qu'elle était envoyée par le Maître des guerriers, Harutt. Si ce message est bien de Harutt, je ne tarderais pas à le lire, à votre place."}, {"Lire la Note simple, et parler à Harutt Thunderhorn, à Camp Narache."}},
[3092] = {"Note gravée", {"Il y a quelques instants, une messagère était à votre recherche, <nom>. Je crois qu'elle était envoyée par le Maître des chasseurs, Lanka. Si ce message est bien de Lanka, je ne tarderais pas à le lire, à votre place."}, {"Lire la Note gravée, et parler à Lanka Farshot, sur la Mesa de Nuage rouge."}},
[3093] = {"Note runique", {"Il y a quelques instants, une messagère était à votre recherche, <nom>. Je crois qu'elle était envoyée par le Maître des chamans, Meela. Si ce message est bien de Meela, je ne tarderais pas à le lire, à votre place."}, {"Lire la Note runique, et parler à Meela Dawnstrider, à Camp Narache."}},
[3094] = {"Note verdoyante", {"Il y a quelques instants, une messagère était à votre recherche, <nom>. Je crois qu'elle était envoyée par le Maître des druides, Gart. Si ce message est bien de Gart, je ne tarderais pas à le lire, à votre place."}, {"Lire la Note verdoyante, et parler à Gart Mistrunner, à Camp Narache."}},
[3095] = {"Parchemin simple", {"Ah, pendant que vous vous occupiez du Fléau, ce parchemin est arrivé pour vous. Je suppose qu'il est de quelque importance, car il porte le sceau du Maître des guerriers, Dannal. À votre place, je prendrais le temps de le lire avant de repartir."}, {"Lire le Parchemin simple, et parler à Dannal Stern, au Glas."}},
[3096] = {"Parchemin codé", {"Ah, pendant que vous vous occupiez du Fléau, ce parchemin est arrivé pour vous. Je suppose qu'il est de quelque importance, car il porte le sceau du Maître des voleurs, David Trias. À votre place, je prendrais le temps de le lire avant de repartir."}, {"Lire le Parchemin codé, et parler à David Trias, au Glas."}},
[3097] = {"Parchemin béni", {"Ah, pendant que vous vous occupiez du Fléau, ce parchemin est arrivé pour vous. Je suppose qu'il est de quelque importance, car il porte le sceau du Maître des prêtres, Duesten. À votre place, je prendrais le temps de le lire avant de repartir."}, {"Lire le Parchemin béni, et parler au sombre clerc Duesten, dans l’église du Glas."}},
[3098] = {"Parchemin glyphé", {"Ah, pendant que vous vous occupiez du Fléau, ce parchemin est arrivé pour vous. Je suppose qu'il est de quelque importance, car il porte le sceau du Maître des mages, Isabella. À votre place, je prendrais le temps de le lire avant de repartir."}, {"Lire le Parchemin glyphé, et parler à Isabella, dans l’église du Glas."}},
[3099] = {"Rouleau contaminé", {"Ah, pendant que vous vous occupiez du Fléau, ce parchemin est arrivé pour vous. Je suppose qu'il est de quelque importance, car il porte le sceau du Maître des démonistes, Maximillion. À votre place, je prendrais le temps de le lire avant de repartir."}, {"Lire le Rouleau contaminé, et parler à Maximillion, au Glas."}},
[3100] = {"Lettre simple", {"On m'a demandé de vous remettre cela dès que vous seriez de retour des camps des kobolds, <nom>. Il s'agit d'une lettre portant le sceau de Llane, notre Maître des guerriers local. À votre place, je n'hésiterais pas à la lire avant de faire quoi que ce soit d'autre, ici, dans l'Abbaye."}, {"Lire la Lettre simple et parler à Llane Beshere, à l’Abbaye de Northshire."}},
[3101] = {"Lettre consacrée", {"On m'a demandé de vous remettre cela dès que vous seriez de retour des camps des kobolds, <nom>. Il s'agit d'une lettre portant le sceau de frère Sammuel, notre Maître des paladins local. À votre place, je n'hésiterais pas à la lire avant de faire quoi que ce soit d'autre, ici, dans l'abbaye."}, {"Lire la Lettre consacrée, et parler à Frère Sammuel, à l’Abbaye de Northshire."}},
[3102] = {"Lettre codée", {"On m'a demandé de vous remettre cela dès que vous seriez de retour des camps des kobolds, <nom>. Il s'agit d'une lettre portant le sceau de Jorik, l'un de nos Maîtres locaux. À votre place, je n'hésiterais pas à la lire avant de faire quoi que ce soit d'autre, ici, dans l'Abbaye."}, {"Lire la Lettre codée, et parler à Jorik Kerridan, dans l’écurie, derrière l’Abbaye de Northshire."}},
[3103] = {"Lettre bénie", {"On m'a demandé de vous remettre cela dès que vous seriez de retour des camps des kobolds, <nom>. Il s'agit d'une lettre portant le sceau de la prêtresse Anetta, notre Maître des prêtres local. À votre place, je n'hésiterais pas à la lire avant de faire quoi que ce soit d'autre, ici, dans l'abbaye."}, {"Lire la Lettre bénie, et parler à la prêtresse Anetta, à l’abbaye de Northshire."}},
[3104] = {"Lettre glyphée", {"On m'a demandé de vous remettre cela dès que vous seriez de retour des camps des kobolds, <nom>. Il s'agit d'une lettre portant le sceau de Kheldon, notre Maître des mages local. À votre place, je n'hésiterais pas à la lire avant de faire quoi que ce soit d'autre, ici, dans l'Abbaye."}, {"Lire la Lettre glyphée, et parler à Kheldon Bremen, à l’intérieur de l’Abbaye de Northshire."}},
[3105] = {"Lettre contaminée", {"On m'a demandé de vous remettre cela dès que vous seriez de retour des camps des kobolds, <nom>. Il s'agit d'une lettre portant le sceau de Druisilla, l'un de nos Maîtres locaux. À votre place, je n'hésiterais pas à la lire avant de faire quoi que ce soit d'autre, ici, dans l'Abbaye."}, {"Lire la Lettre contaminée, et parler à Drusilla La Salle, à proximité de l’Abbaye de Northshire."}},
[3106] = {"Rune simple", {"Pendant que vous m'aidiez, on m'a donné cette rune pour vous. Prenez le temps de la lire. Je pense qu'elle provient du Maître des guerriers, Thran. Jetez-y un coup d'oeil et allez le retrouver, à l'intérieur d'Anvilmar, quand vous en aurez le temps."}, {"Lire la Rune simple, et parler à Thran Khorman, dans la Vallée des Frigères."}},
[3107] = {"Rune consacrée", {"Pendant que vous m'aidiez, on m'a donné cette rune pour vous. Prenez le temps de la lire. Je pense qu'elle provient du Maître des paladins, Bromos. Jetez-y un coup d'oeil et allez le retrouver, à l'intérieur d'Anvilmar, quand vous en aurez le temps."}, {"Lire la Rune consacrée, et parler à Bromos Grummner, dans la Vallée des Frigères."}},
[3108] = {"Rune gravée", {"Pendant que vous m'aidiez, on m'a donné cette rune pour vous. Prenez le temps de la lire. Je pense qu'elle provient du Maître des chasseurs, Thorgas. Jetez-y un coup d'oeil et allez le retrouver, à l'intérieur d'Anvilmar, quand vous en aurez le temps."}, {"Lire la Rune gravée, et parler à Thorgas Grimson, dans la Vallée des Frigères."}},
[3109] = {"Rune codée", {"Pendant que vous m'aidiez, on m'a donné cette rune pour vous. Prenez le temps de la lire. Je pense qu'elle provient du Maître des voleurs, Solm. Jetez-y un coup d'oeil et allez le retrouver, à l'intérieur d'Anvilmar, quand vous en aurez le temps."}, {"Lire la Rune codée, et parler à Solm Hargrin, dans la Vallée des Frigères."}},
[3110] = {"Rune bénie", {"Pendant que vous m'aidiez, on m'a donné cette rune pour vous. Prenez le temps de la lire. Je pense qu'elle provient du Maître des prêtres, Branstock. Jetez-y un coup d'oeil et allez le retrouver, à l'intérieur d'Anvilmar, quand vous en aurez le temps."}, {"Lire la Rune bénie, et parler à Branstock Khalder, à Anvilmar."}},
[3112] = {"Note de service simple", {"Pendant que vous m'aidiez, on m'a donné cette note de service pour vous. Prenez le temps de la lire. Je pense qu'elle provient du Maître des guerriers, Thran. Jetez-y un coup d'oeil et allez le retrouver, à l'intérieur d'Anvilmar, quand vous en aurez le temps."}, {"Lire la Note de service simple, et parler à Thran Khorman, dans la Vallée des Frigères."}},
[3113] = {"Note de service codée", {"Pendant que vous m'aidiez, on m'a donné cette note de service pour vous. Prenez le temps de la lire. Je pense qu'elle provient du Maître des voleurs, Solm. Jetez-y un coup d'oeil et allez le retrouver, à l'intérieur d'Anvilmar, quand vous en aurez le temps."}, {"Lire la Note de service codée, et parler à Solm Hargrin, dans la Vallée des Frigères."}},
[3114] = {"Note de service glyphée", {"Pendant que vous m'aidiez, on m'a donné cette note de service pour vous. Prenez le temps de la lire. Je pense qu'elle provient du Maître des mages, Marryk. Jetez-y un coup d'oeil et allez le retrouver, à l'intérieur d'Anvilmar, quand vous en aurez le temps."}, {"Lire la Note de service glyphée, et parler à Marryk Nurribit, dans Anvilmar, au-dessus de la Vallée des Frigères."}},
[3115] = {"Note de service contaminée", {"Pendant que vous m'aidiez, on m'a donné cette note de service pour vous. Prenez le temps de la lire. Je pense qu'elle provient du Maître des démonistes, Alamar. Jetez-y un coup d'oeil et allez le retrouver, à l'intérieur d'Anvilmar, quand vous en aurez le temps.","Et faites attention, <nom>, on n'apprécie pas trop votre espèce, sur ces terres."}, {"Lire la Note de service contaminée, et parler à Alamar Grimm, dans Anvilmar, au-dessus de la Vallée des Frigères."}},
[3116] = {"Sceau simple", {"Ce sceau m'a été donné par un messager de notre Maître des guerriers, Alyissia. Elle souhaiterait vous parler, dès que vous aurez un moment à lui accorder. Lisez-le et rapportez-le-lui."}, {"Lire le Sceau simple, et parler à Alyissia, à Sombrevallon."}},
[3117] = {"Sceau gravé", {"Ce sceau m'a été donné par un messager de notre Maître des chasseurs, Ayanna. Elle souhaiterait vous parler, dès que vous aurez un moment à lui accorder. Lisez-le et rapportez-le-lui."}, {"Lire le Sceau gravé, et parler à Ayanna Everstride, au sommet d'Aldrassil, dans Sombrevallon."}},
[3118] = {"Sceau codé", {"Ce sceau m'a été donné par un messager de notre Maître des voleurs, Frahun. Il souhaiterait vous parler, dès que vous aurez un moment à lui accorder. Lisez-le et rapportez-le-lui."}, {"Lire le Sceau codé et parler à Frahun Shadewhisper, à Sombrevallon."}},
[3119] = {"Sceau béni", {"Ce sceau m'a été donné par un messager de notre maître des prêtres, Shanda. Elle souhaiterait vous parler, dès que vous aurez un moment à lui accorder. Lisez-le et rapportez-le-lui."}, {"Lire le Sceau béni, et parler à Shanda, à Aldrassil."}},
[3120] = {"Sceau verdoyant", {"Ce sceau m'a été donné par un messager de notre maître des druides, Mardant. Il souhaiterait vous parler, dès que vous aurez un moment à lui accorder. Lisez-le et rapportez-le-lui."}, {"Lire le Sceau verdoyant, et parler à Mardant Strongoak, dans l'arbre Aldrassil de Sombrevallon."}},
[3121] = {"Une Requête étrange", {"Neeru Fireblade à Orgrimmar a ce qu'il me faut pour travailler ici ! Il a préparé un mélange dont j'ai besoin pour... Enfin vous verrez.","Mettez-vous en chemin pour Orgrimmar. Tenez, prenez ceci. C'est une tête réduite que j'ai promise à Neeru en échange des herbes. C'est la tête d'un pauvre nain qui a croisé ma route dans la forêt. Je suis certain que Neeru appréciera.","Vous trouverez Neeru dans la Faille de l'ombre."}, {"Porter la tête réduite à Neeru Fireblade à Orgrimmar."}},
[3122] = {"Retourner voir le sorcier-docteur Uzer'i", {"Voici les herbes, <nom>. Portez-les au sorcier-docteur Uzer'i.","Dites-lui que je suis très content de la tête réduite."}, {"Livrer une Poche d'herbes de Neeru au Sorcier docteur Uzer'i, en Feralas."}},
[3123] = {"Test du récipient", {"Il va s'agir d'un test, qui mettra à l'épreuve le rituel que j'ai accompli comme vos capacités. Prenez ce récipient, <nom>. Avec cela, vous pourrez réduire la taille des créatures et les y capturer.","Partez pour les Hinterlands et cherchez des bêtes connues sous le nom d'Indomptables. C’étaient autrefois les animaux de compagnie de la déesse des elfes de la nuit, Elune. Vos cibles sont les Chouettards, des Indomptables vicieux, primitifs et sauvages, <nom>. Tuez-en 10, puis utilisez le Récipient de muisek pour les faire rétrécir et les capturer avant que leurs esprits ne s'échappent."}, {"Partez pour les Hinterlands et localisez les Indomptables. Tuez-en 10, puis utilisez le Récipient de muisek pour faire rétrécir et capturer les Indomptables vaincus.","Ramenez 10 Indomptables miniaturisés et le Récipient d'Indomptables miniaturisés au sorcier-docteur Uzer'i, en Feralas."}},
[3124] = {"Hippogryphe miniaturisé", {"Puisque vous avez montré que vous êtes à la fois digne de confiance et <doué/douée> pour utiliser le Vaisseau de muisek, je souhaite à présent que vous capturiez une autre créature.","Les hippogryphes ont été les compagnons loyaux des elfes de la nuit durant plusieurs années. Leur esprit est résolu et ferme.","Votre tâche consiste à tuer 10 hippogryphes Aigreplume et de rapidement les faire rétrécir et les capturer avec le vaisseau, afin que leur muisek soit préservé. Vous trouverez les hippogryphes dans les Plateaux sauvages au sud d'ici."}, {"Tuer 10 Hippogryphes Aigreplume de n'importe quel type, puis utiliser le Vaisseau de muisek pour les faire rétrécir et les capturer."}},
[3125] = {"Dragon féérique miniaturisé", {"À présent, <nom>, je souhaite que vous capturiez une créature fragile en apparence, mais dont l'esprit puissant nous intéresse. Je veux parler des dragons féeriques. Ils ont été les alliés des elfes de la nuit. Nous devons en capturer afin que la Horde puisse également bénéficier de cette force spirituelle.","Voici le Vaisseau, <nom>. Tuez 8 Fées fléchetteuses ou Petits dragons féeriques, et capturez leur muisek. On peut en trouver à l'ouest d'ici."}, {"Tuer 8 Fées fléchetteuses ou Petits dragons féeriques. Utiliser le Vaisseau de muisek pour faire rétrécir et capturer les dragons féeriques vaincus."}},
[3126] = {"Tréant miniaturisé", {"Les marcheurs des bois errants que l'on peut voir vagabonder sous les arbres sont nos prochains objectifs, <nom>. Pour combattre la Horde, les elfes de la nuit ont enrôlé des tréants comme alliés. Ils ont beau être lents, leur rôle de protecteurs n'est pas négligeable.","Nous pourrons bientôt utiliser cette puissance à notre profit.","Trouvez trois Marcheurs des bois errants. Tuez-les, puis utilisez le Vaisseau de muisek pour les réduire et les capturer."}, {"Tuer 3 Marcheurs des bois errants. Utiliser le Vaisseau de muisek pour faire rétrécir et pour capturer les tréants vaincus."}},
[3127] = {"Géant des montagnes miniaturisé", {"La dernière chose que je vous demande est de partir vers le nord-ouest pour trouver l'ennemi final.","Les géants des montagnes ont quitté leurs montagnes pour aider les elfes de la nuit quand la Légion ardente est apparue. Vous les reconnaîtrez à leur stature massive ainsi qu’aux vibrations du sol à mesure que vous avancerez dans leur contrée rocailleuse.","J'ai à cœur de maîtriser une puissance d'une telle taille. Tuez 7 géants des montagnes puis utilisez le Récipient pour capturer leur puissant muisek."}, {"Tuer 7 Marcheurs des terres ou Géants des falaises. Utiliser le Récipient de muisek pour faire rétrécir et pour capturer les Géants des montagnes."}},
[3128] = {"Matériaux Naturels", {"Je vais avoir besoin d'autres choses pour finir ce que j'ai commencé... Vous devrez me rendre ce service avec d'autres choses que je vous donnerai à faire.","J'aurai besoin de bûches fendues de tréant de Feralas. Du minerai incrusté, que l'on peut trouver sur les géants des montagnes ou les dragons féeriques, à l'ouest. Aussi, trouvez-moi des tendons résistants des chouettards des Hinterlands ou des hippogryphes de Feralas. Enfin, il me faudrait une grande quantité de fragments métalliques de chacune des créatures mentionnées."}, {"Apporter 2 Bûches fendues, 6 Minerais incrustés, 20 Morceaux de tendons résistants et 40 Fragments métalliques au sorcier-docteur Uzer'i au Camp Mojache."}},
[3129] = {"Armes de l'Esprit", {"Imprégner les armes de l'esprit des miniatures est un processus difficile. Maintenant que nous avons les matériaux nécessaires et les miniatures des créatures que vous avez capturées, je peux commencer. Attendez ; je reviens dans un moment."}, {"Le sorcier-docteur Uzer'i veut que vous patientez tandis qu'il prépare les armes."}},
[3130] = {"Contre les Hainecrête", {"À présent, je vais vous diriger vers mon second, Latronicus. Il a gardé un oeil attentif sur les forces des nagas jusqu'alors. J'estimerai la situation générale ici selon les nagas. Non pas que je veuille envoyer une force compacte les combattre, laissant ainsi la forteresse ouverte à une attaque directe de la Horde, ou pire...","Mais passons, Latronicus attend. Vous avez fait du bon travail jusque là, <nom>. Continuez comme ça.","Vous pouvez disposer."}, {"Se présenter devant Latronicus Moonspear au bastion de Feathermoon."}},
[3141] = {"Loramus", {"Donc vous souhaitez vous battre contre des démons ? Vous feriez bien d'écouter mon histoire."}, {"Écouter l'histoire de Loramus Thalipedes."}},
[3161] = {"Gahz'ridian", {"<nom>, je suis un scientifique mais au fond de moi je suis aussi un chasseur de trésor !","Il y a longtemps, quand les trolls occupaient ces terres, ils ont créé de nombreux ornements à l'image de Gahz'rilla, une hydre qu'ils adorent comme divinité. Ces ornements sont sculptés à partir d'une matière que j'appelle \"Gahz'ridian,\" d'après le nom du dieu hydre.","J'ai en déjà trouvé un peu moi-même, mais j'aimerais en avoir plus à étudier ; peut-être pourrez-vous m'en trouver ?","J'ai même inventé un détecteur qui rend la recherche de Gahz'ridian un jeu d'enfant ! Il vous suffit de le porter et la recherche peut commencer !"}, {"Marvon Rivetseeker à Tanaris veut que vous collectiez 30 Ornements Gahz'ridian."}},
[3181] = {"La corne de la bête", {"La corne gigantesque est couverte d'égratignures et de fissures. Un objet de métal inhabituel s'est logé sous la surface dure.","Après de plus amples observations, vous remarquez qu'il s'agit d'une lame de hache brisée. Sur le métal sont gravés les mots suivants :","Propriété de la Montagnarde Pebblebitty.","Quand vous essayez de déloger la lame, elle s'effrite en mille morceaux.","Oups !"}, {"Porter la Corne Gigantesque de Margol à la Montagnarde Pebblebitty."}},
[3182] = {"Titre de propriété", {"Et vous pensez que je vais avaler une histoire pareille ? Le fer de ma hache se serait brisé et serait coincé dans la véritable corne, s'il s'agissait bien d'elle.","Écoutez, apportez ce faux grossier au conservateur en chef Thorius à Ironforge. S'il est capable de valider vos dires, vous recevrez un certificat de haut fait. Rapportez-moi cette preuve et je vous remettrai la clé de la gorge des Vents brûlants.","Allez-y !"}, {"Porter la Corne gigantesque de Margol au conservateur en chef Thorius à Ironforge."}},
[3201] = {"Enfin !", {"Je n'arrive pas à croire que Pebblebitty ait pu douter de l'authenticité de la corne. Entre vous et moi, je pense qu'elle est seulement jalouse. Je sais qu'elle tenait vraiment à tuer elle-même cette bête, à cause de \"l'incident\" et tout.","Donc vous avez besoin d'un document comme preuve ? Après la contribution que vous venez de faire au musée, ce ne sera pas un problème.","<Le conservateur en chef Thorius commence à remplir un grand document>","Et voilà ! Vous pouvez présenter ceci à Pebblebitty."}, {"Apporter le document de preuve à la Montagnarde Pebblebitty."}},
[3221] = {"Parler à Renferrel", {"Tandis que vous étiez en train d'aider nos Traqueurs noirs, l'apothicaire Renferrel vous a fait appeler. Il n'a rien précisé mais a dit qu'il voulait vous parler à propos des coeurs de loup que vous lui aviez donnés."}, {"Parler à l'apothicaire Renferrel au Sépulcre."}},
[3261] = {"Jorn Skyseer", {"Nos routes se séparent, <nom>. Pour en apprendre plus, vous devrez aller au Camp Taurajo, vers le sud et parler à Jorn Skyseer. Il continuera de vous guider."}, {"Parler à Jorn Skyseer, à Camp Taurajo."}},
[3281] = {"Argent volé", {"Vous avez peut-être tué les raptors, mais l'argent qu'ils ont volé doit être récupéré ! Des rapports indiquent que les raptors ont un important site de nidification au sud de Ratchet. Cet endroit est connu sous le nom de Terres des raptors. Il est probable qu'ils y aient apporté notre argent volé.","Allez dans ce repaire de raptors et cherchez-le. Quand vous l'aurez trouvé, rapportez-le-moi."}, {"Rapportez l'Argent volé à Gazrog à la Croisée."}},
[3301] = {"Mura Runetotem", {"Ma camarade de clan, Mura Runetotem, est partie pour les Pins argentés afin d'aider nos alliés morts-vivants. C'est une forêt qui dépérit doucement, et elle avait l'espoir d'y insuffler la vie à nouveau. Peut-être que la vie insolite que nous trouvons ici dans les Tarides l'aiderait à réaliser ses souhaits pour les Pins argentés ?","Prenez avec vous une des carapaces que vous avez apportées, et donnez-la à Mura Runetotem aux Pins argentés. Elle séjourne au Sépulcre. Avec un peu de chance, l'étude de cette carapace l’aidera à trouver une solution pour guérir cette forêt à l'agonie."}, {"Parler à Mura Runetotem au Sépulcre."}},
[3321] = {"Est-ce que ceci est à vous ?", {"Vous avez beaucoup fait pour l'Ordre, <nom>. Le total dévouement nécessaire pour parvenir jusqu'ici aurait rebuté toute personne ordinaire. Il ne fait aucun doute dans mon esprit, ni dans celui de Galvan, que vous faites partie des nôtres.","C'est la raison pour laquelle j'estime que vous méritez une petite récompense. Ce n'est pas grand chose en fait, un simple colifichet. Il se peut que vous soyez <familier/familière> avec ses propriétés, car je crois que c'est ce qui vous a <amené/amenée> à nous en premier lieu."}, {"Attendre que Trenton ait terminé son travail."}},
[3341] = {"L'anéantissement", {"Il se trouve dans les Tarides un horrible fouillis de ronces entremêlées, les Souilles de Tranchebauge. Bien que nos premières observations à cet endroit n'aient révélé que peu de dangers, nos découvertes récentes sont beaucoup plus alarmantes…","Les hurans des Souilles de Tranchebauge se sont alliés au Fléau. Une liche du nom de Amnennar, dite le Porte-froid, les contrôle à présent et utilise le pouvoir de son imposante connaissance pour diriger chacun de leurs mouvements.","Amnennar est liée à Ner'zhul par la télépathie ; nous devons trancher ce lien, <nom>. Il faut éliminer le Porte-froid."}, {"Andrew Brownell veut que vous tuiez Amnennar le Porte-froid et que vous lui rameniez son crâne."}},
[3361] = {"Les malheurs d'un réfugié", {"Nous avons chassé les troggs de Gnomeregan, mais c'est devenu catastrophique depuis ! Maintenant notre maison est complètement irradiée et nous, les gnomes, nous avons été dispersés dans tout Dun Morogh.","Dans ma hâte de fuir les radiations, j'ai perdu toutes mes affaires personnelles et mes outils. Ce sont les trolls qui les ont pris. Ils ont volé mon coffre, ma boîte et mon seau de boulons ! Ils les ont emportés dans leur camp au sud-ouest d'Anvilmar.","Je ne suis pas un aventurier… Pourriez-vous retrouver mes affaires et me les rapporter ici, s'il vous plaît ?"}, {"Apporter la Boîte de Felix, le Coffre de Felix et le Seau de boulons de Felix à Felix Whindlebolt, à Anvilmar."}},
[3362] = {"La vallée des Chardonniers", {"Avez-vous déjà vu la vallée des Chardonniers ? C’est un endroit dangereux, <nom>.","Il y a d’étranges bêtes là-bas, imprévisibles ! Je crois qu’on les appelle les Chardonniers noueux et les Chardonniers taille-racine...","Je veux accéder à la vallée, mais avec tous ces Chardonniers dans les parages c’est bien trop risqué. Si vous vouliez bien aller en tuer quelques-uns, je pourrais envisager d’y retourner. Qu’en pensez-vous, <nom> ? M’aiderez-vous ?"}, {"Tran'rek, à Gadgetzan, voudrait que vous tuiez 8 Chardonniers noueux et 8 Chardonniers taille-racines."}},
[3363] = {"La fleur-de-chant corrompue", nil, nil},
[3364] = {"Une livraison de thé brûlant", {"Ah ! Je devais apporter un Thé brûlant à Durnan Furcutter, à Anvilmar, mais je dois d’abord en porter un à Grelin. Je n'arriverai jamais à Anvilmar avant qu'il ne refroidisse !","Vous m’avez l'air rapide. Vous pouvez le faire. Cette boisson ne restera chaude que 5 minutes et Durnan n'a pas commandé un Thé \"glacé\", alors partez tout de suite. Anvilmar est un village dans la montagne, au nord-est.","Merci, <nom>, et n'oubliez pas de me rapporter ma tasse !"}, {"Apporter un Thé brûlant à Durnan Furcutter, dans Anvilmar, dans les cinq minutes imparties avant qu'il ne refroidisse !"}},
[3365] = {"Rapporter la Tasse", {"Ah ! Vous êtes <arrivé/arrivée> à point nommé ! Rien de tel qu'un bon thé chaud, non, BRULANT, par une froide journée d'hiver pour réchauffer les méandres de votre esprit.","Tenez, <nom>, soyez assez aimable pour rapporter cette tasse vide à Nori pour moi, voulez-vous ?"}, {"Apporter la Chope de Nori à Nori Pridedrift."}},
[3367] = {"Les pierres de Suntara", {"<Dorius gémit.>","Je suis grièvement blessé, <nom>. Je me sens… la tête toute légère… tout… tournoie… je saigne… à l'intérieur.","Je dois retourner à Ironforge. Aidez-moi, <nom>."}, {"Aider Dorius à retourner à Ironforge."}},
[3368] = {"Les pierres de Suntara", {"Mon frère,","Si vous lisez cette lettre, c'est que je n'existe plus. Comment vous est-elle parvenue, je l'ignore. Mais remerciez et récompensez ceux qui ont fait en sorte que vous l'ayez reçue.","À propos des pierres : elles existent, les Sombrefer les ont en leur possession. Leur chef, Lathoric le Noir, utilise le pouvoir de ces pierres pour asservir mon équipe d'archéologues. Ils ont été emmenés au Chaudron. Leurs esprits sont à présent asservis et ils travaillent nuit et jour pour achever une chose qui répond au nom d'Obsidion.","Secourez-les. Il s'agit là de la dernière volonté d'un homme mort.","-Dorius"}, {"Apporter la Lettre roussie à Thorius à Ironforge. Il se trouve dans le Hall des Explorateurs."}},
[3369] = {"Au coeur des cauchemars", {"Cet éclat détient de grands secrets. Il s'agit de l'essence pure du Rêve d'émeraude. Ce que je vois dans cet éclat n'a rien d'un rêve cependant. On pourrait même appeler cela une vision de cauchemar.","Mon but n'est pas de vous alarmer, <nom>, mais je crois qu'il serait mieux pour vous de remettre cet éclat à un grand druide, et ce le plus tôt possible.","La personne que vous devriez rencontrer à la Cime des Anciens se nomme Hamuul Runetotem. Partez pour Thunder Bluff. Je ne peux pas vous en dire plus."}, {"Apporter l'Eclat de cauchemar à Hamuul Runetotem à la Cime des Anciens."}},
[3370] = {"Au coeur des cauchemars", {"Cet éclat détient de grands secrets. Il s'agit de l'essence pure du Rêve d'émeraude. Ce que je vois dans cet éclat n'a rien d'un rêve cependant. On pourrait même appeler ce genre de vision un cauchemar.","Mon souhait n'est pas de vous alarmer, <nom>, mais je crois qu'il serait mieux pour vous de remettre cet éclat aux mains d'un grand druide, et ce dès que vous le pouvez.","La personne que vous devez trouver au Cercle cénarien se nomme Mathrengyl Bearwalker. Partez pour Darnassus. Je ne peux vous en dire plus."}, {"Apporter l'Eclat de cauchemar à Mathrengyl Bearwalker à Darnassus."}},
[3371] = {"La justice des nains", {"Il y a, à présent, un creux vide de sens, là où se trouvait autrefois mon coeur, <nom>. Je ne me remettrai pas de la perte de Dorius. Je dois faire en sorte que sa dernière volonté soit accomplie. Justice doit être rendue contre ceux qui ont commis ce meurtre de sang-froid.","Retournez à la Gorge des Vents brûlants et respectez sa dernière volonté : libérez ses hommes."}, {"Retourner à la Gorge des Vents brûlants et trouver l'Equipe d'archéologues de Dorius."}},
[3372] = {"La libération", {"Libérez-les, <nom>. Leurs corps sont morts, mais la puissance de Suntara les oblige à achever leur travail.","L'Autel de Suntara est la clé pour les libérer. Allez au nord, dans les collines au-dessus de la caverne… Le Marteau du crépuscule y garde une relique. Emparez-vous-en et placez-la sur l'Autel de Suntara.","<L'archéologue mourant s'effondre.>"}, {"Trouver l'Artefact mystérieux gardé par le Marteau du crépuscule au nord."}},
[3373] = {"L'Essence d'Eranikus", {"Alors que vous touchez la gemme, une voix grave résonne dans votre esprit.","\"<Mortel/Mortelle>, considérez ma requête. Je suis Eranikus des dragons verts, gardien de ce temple maudit. J'avais pour charge de m'assurer que les trolls n'amènent plus en ces lieux leurs dieux abominables. Mais j'ai échoué. Alors que j'imprègne cette gemme d'une partie de mon essence, je sens que mon étreinte sur le Rêve s'évanouit vers le néant. Je vous en prie, placez cette gemme dans le Réceptacle d'essence de mon antre, et que notre magie en ces lieux purge l'infection de mon esprit et me permette de reposer.\""}, {"Placer l'Essence d'Eranikus dans le Réceptacle d'essence situé dans son antre dans le Temple englouti."}},
[3374] = {"L'Essence d'Eranikus", {"Vous activez la Pierre de vœu et Itharius parle directement à votre esprit à travers elle.","\"En acceptant la charge contenue dans cette Pierre de vœu, vous vous engagez à soutenir ma cause et celle des dragons d'Ysera, qui est de racheter l'esprit corrompu d'Eranikus. Le premier sacrifice, parmi les nombreux que vous et moi ferons sans aucun doute, va être de m'abandonner la gemme qui a enchaîné ce qu'il reste de volonté dans l'esprit d'Eranikus.\"","\"Faites-le, <nom>, et nous œuvrerons de concert au rachat d'une âme digne de rédemption.\""}, {"Apporter la Pierre de voeu des dragons d'Ysera et l'Essence d'Eranikus enchaînée à Itharius au marais des Chagrins. C'est là que vous choisirez d'aider ou non les dragons d'Ysera."}},
[3375] = {"La fiole de remplacement", {"Hum, vous avez besoin d'une autre Fiole de divination ? C'est une plaisanterie, n'est ce pas ? Qu'avez-vous fait de la première ?! Beaucoup de travail nous attend, <nom>. Ces fioles ne poussent pas sur les arbres. En fait, j'aimerais bien que ce soit le cas, car je m'y rendrais tout simplement et en cueillerais une, puisqu'il ne m'en reste plus !!","Pour que je puisse vous la préparer, j'ai besoin des choses suivantes : une Potion de mana, un morceau de Charbon, et du Nectar sucré. Ne me demandez pas pourquoi, amenez-moi simplement tout ceci et je vous en ferai une autre."}, {"Apporter une Potion de mana, du Charbon, et du Nectar sucré à Talvash del Kissel à Ironforge pour remplacer la Fiole de divination de Talvash."}},
[3376] = {"La fin de Brochaigu !", {"<nom>, j'ai entendu parler de vous. Peut-être saurez-vous nous venir en aide là où d'autres ont échoué.","Nous, taurens, avons fait de cette terre notre foyer. Mais cela n'a pas été sans pertes. Les hurans Dos-hirsute, du ravin de Roncelame, menés par leur chef Brochaigu Mantépine, nous accablent sans cesse. Je vous demande de me rapporter la tête de leur chef !","Vous pourrez le trouver dans le ravin, à l'est, dans leur village de fortune."}, {"Apporter la Tête du chef Brochaigu Mantépine à Brave Windfeather à la mesa de Nuage rouge."}},
[3377] = {"Prière à Elune", {"Me viendrez-vous en aide, <mortel/mortelle> ? Écoutez mon récit."}, {"Écouter le récit de Zamael."}},
[3378] = {"Prière à Elune", {"Me viendrez-vous en aide, <mortel/mortelle> ? Trouverez-vous la Prière à Elune ?","Comme je l'ai dit, cette prière a été gravée sur une tablette en or. Elle doit se trouver sur l'une de ces crapules vénérant des idoles.","Si vous parvenez à retrouver ma prière, apportez-la à Astarii Starseeker à Darnassus. Il importe peu que ma foi ait été ravivée après les innombrables actions malveillantes que j'ai infligées à ce monde et à ses habitants, mais ma prière doit être entendue. La dernière chose que je demande est l'absolution."}, {"Trouver la Prière à Elune et l'apporter à Astarii Starseeker à Darnassus."}},
[3379] = {"Les tisseuses d'ombre", {"Les araignées de la gorge des Vents brûlants sont une source inestimable de Soie des ténèbres. Comme vous le savez, la Soie des ténèbres est très recherchée par les artisans de notre métier.","Malheureusement, un groupe de braconniers a récemment empiété sur ces terres, et massacre en masse les araignées sans se soucier des effets à long terme de leurs actions. Aussi, la première chose que je vous demanderai sera assez simple : trouvez-les, et... tuez-les.","Commencez vos recherches au nord.","Si vous avez besoin d'assistance, parlez avec Raze."}, {"Tuer le groupe de braconniers Ombresoie qui rôde dans la gorge des Vents brûlants. Nilith a de bonnes raisons de croire qu'ils doivent être exterminés tous les cinq. Retourner voir Nilith dans la gorge des Vents brûlants lorsque vous aurez terminé."}},
[3380] = {"Le Temple englouti", {"Il existe en ce monde des esprits qui ne connaissent pas le repos, <nom>. Il existe des esprits viciés par leur solitude, liés à un mal antique…","J'ai entendu parler d'un endroit se trouvant profondément sous la surface de la terre, banni en ce lieu par des puissances plus grandes que je n'aie jamais rencontrées.","Si vous souhaitez en apprendre davantage sur cet endroit, le Temple englouti, partez à la recherche d'un gobelin nommé Marvon Rivetseeker. Il a étudié de nombreuses parties du monde, et aux dernières nouvelles, il a quitté sa demeure à Ratchet pour étudier les ruines des trolls à Tanaris."}, {"Trouver Marvon Rivetseeker à Tanaris."}},
[3382] = {"Un équipage sous le feu ennemi", {"Nous étions près de la côte et cherchions un endroit sûr où jeter l'ancre. Le soleil ne s'était pas couché depuis plus d'une heure lorsqu'ils ont attaqué… Ces maudits nagas !","Je ne sais pas ce qu'ils cherchaient, mais il est clair qu'ils ne l'ont pas trouvé. Ils sont déjà revenus une fois. Deux de mes éclaireurs sont de garde à l'entrée de la vallée pour nous avertir lorsque la prochaine attaque aura lieu.","Cela me désole que vous arriviez à ce moment, <nom>, mais je n'y peux rien.","Êtes-vous <prêt/prête> pour le combat de votre vie ?"}, {"Défendre la capitaine Vanessa Beltis, son équipage et les passagers de “l’Horizon” contre l'attaque des nagas. La capitaine Vanessa Beltis doit survivre."}},
[3385] = {"Le Sous-marché", {"Ils se sont donnés le nom de Sous-marché : des commerçants sans vergogne qui vendront, intimideront, commerceront, ou feront à peu près n'importe quoi contre la bonne somme d'argent.","Mais les choses empirent, <nom>. Ils ont établi une base d'opérations ici dans la gorge des Vents brûlants. Kovic dirige les opérations et entretient des liens étroits avec les nains Sombrefer dans la région.","Nous devons frapper fort et envoyer un message clair à leurs dirigeants. Tuez Kovic, ainsi que son agent Clonk. Apportez-moi sa Sacoche du marchand lorsque vous en aurez terminé.","Partez pour le Chaudron à présent !"}, {"Tuer le Maître d'artisanat Kovic et son agent Clonk. Récupérer sur son corps la Sacoche du marchand et l'apporter à Nilith."}},
[3402] = {"Le Sous-marché", {"Je veux que vous informiez personnellement Vizzklick à Gadgetzan de la défaite de Kovic. Il sera si heureux d'apprendre sa mort qu'il vous remettra sûrement une récompense."}, {"Parler à Vizzklick à Gadgetzan."}},
[3421] = {"Le voyage de retour", nil, nil},
[3441] = {"Châtiment divin", {"Écoutez mon récit, <aventurier/aventurière>."}, {"Écouter le récit de Kalaran Windblade."}},
[3442] = {"La flamme parfaite", {"Vous avez pris la bonne décision, <nom>.","Nous devons frapper à l'endroit où nous ferons le plus de mal : aux quatre tours de garde.","Lorsque les quatre tours seront en feu, nous pourrons lancer notre offensive avant même qu'ils aient le temps de réagir.","Vous allez avoir besoin du bon outil pour incendier les tours. Et pour cet outil, nous devons trouver quelques ingrédients.","Apportez-moi quatre Cœurs des flammes, et quatre globes d'Huile de golem, et je ferai naître la torche de flamme parfaite.","Vous devriez prendre tout d'abord pour cibles les golems et les élémentaires de ces terres."}, {"Réunir 4 Cœurs des flammes et 4 globes d'Huile de golem puis rapporter ces objets à Kalaran Windblade."}},
[3443] = {"Forger le manche", {"Pour porter la flamme parfaite, nous aurons besoin d'un manche et d'une enveloppe. Vous devez tout d'abord trouver un manche convenable.","Les sous-chefs et les esclavagistes Sombrefer du Chaudron se servent d'armes en thorium enchanté, un alliage extrêmement solide. Si vous m'apportez huit dagues plaquées en thorium, je pourrai les fondre et utiliser le métal pour fabriquer le manche."}, {"Apporter 8 Dagues plaquées en thorium à Kalaran Windblade."}},
[3444] = {"Le Cercle de pierres", {"J'ai pénétré une fois dans les premières salles du Temple englouti. Le mal de ces lieux oppresse quiconque y entre. Je n'ai réussi à trouver la sortie qu'à grand-peine, ne progressant que difficilement à travers le limon qui recouvre à présent le sol. Je ne me souviens que d'avoir trébuché dans une grande pièce circulaire, remplie de statues de serpents...","J'ai récemment découvert une pierre ronde qui portait le même symbole de serpent gravé sur sa face. Je l'ai faite envelopper et envoyer à mon atelier à Ratchet. Récupérez-la, et il se peut que je puisse vous en dire davantage."}, {"Récupérer le Cercle de pierres à l'Atelier de Marvon Rivetseeker à Ratchet."}},
[3445] = {"Le Temple englouti", {"J'ai entendu parler d'un endroit se trouvant profondément sous la surface de la terre, banni en ce lieu par de plus grandes puissances que je n'aie jamais rencontrées.","Si vous souhaitez en apprendre davantage sur cet endroit, le Temple englouti, partez à la recherche d'un gobelin nommé Marvon Rivetseeker. Il a étudié de nombreuses régions du monde, et aux dernières nouvelles, a quitté sa demeure à Ratchet pour étudier les ruines des trolls à Tanaris.","Je vous souhaite bonne chance dans vos recherches, cela fait longtemps que personne ne l'a vu."}, {"Trouver Marvon Rivetseeker à Tanaris."}},
[3446] = {"Dans les profondeurs", {"J'ai trouvé à l'intérieur du temple une pièce circulaire avec six stèles, chacune étant une statue de serpent. La curiosité m’amena à pousser l'une d'entre elles, simplement pour voir ce qui allait se passer. C'était un piège, et l'explosion m'a presque emmené jusqu’aux portes de la mort. Alors que je reposais là, sur le bord, je regardais en bas pour m’apercevoir qu'il y avait une autre statue dans la partie inférieure de la pièce : un autel si l'on peut dire... Hakkar, je crois qu'il s'appelait comme ça.","Je pense que cet autel est la clé du mystère : vous devez apporter ce symbole à cet autel du Temple englouti."}, {"Trouver l'Autel d'Hakkar dans le Temple englouti du marais des Chagrins."}},
[3447] = {"Le secret du cercle", {"Ma mémoire est brumeuse, mais je vais vous raconter ce dont je me souviens. Je crois que le cercle de statues situé dans le Temple englouti détient, malgré les dangers qui s'y cachent, un secret permettant de découvrir un trésor encore plus important.","Je pouvais activer une série de lumières depuis l'autel principal. Il est possible que si vous parveniez à comprendre la signification de ces lumières, vous puissiez découvrir les mystères de cette pièce.","Je vous mets quand même en garde, <nom>. De puissants maléfices protègent ces lieux. Il est probable que quoi que ce soit qui ait de la valeur soit gardé avec ferveur."}, {"Voyager jusqu'au Temple englouti et découvrir le secret du cercle de statues."}},
[3448] = {"La transmission du fardeau", {"On m'a récemment donné la responsabilité d'étudier Azshara, un endroit autrefois magnifique que les elfes considéraient comme leur demeure. C'était à l'époque où ils pratiquaient la magie. Cet endroit est à présent plus dangereux que beau, mais cela ne nous empêche pas de retourner chaque pierre qui s'y trouve pour découvrir des réponses sur notre passé.","Un de mes compagnons du nom de Tymor m'a proposé son aide pour étudier ce lieu, car il s'intéresse particulièrement à de tels sujets.","Il possède une petite demeure en ville, à droite du Hall des mystères."}, {"Parler au mage Tymor à Ironforge."}},
[3449] = {"Les runes des arcanes", {"Certains elfes vénéraient autrefois la magie tout autant qu'Elune. Des mages, parmi les plus puissants, conçurent des runes et des tablettes citant les règles des arcanes et des aperçus de leurs découvertes. Ils les laissèrent dans des lieux publics pour que chacun puisse les méditer.","J'aimerais beaucoup obtenir des reproductions de ces runes des ruines d'Eldarath. J'ai ici pour vous une trousse qui vous permettra d'effectuer ces reproductions, mais il vous appartiendra de trouver ces runes. Faites attention quand même, la Ligue des Explorateurs n'est pas la seule à s'intéresser à Eldarath."}, {"À l'aide de la Trousse de dessin, effectuer des reproductions de la Rune de Beth'Amara, de la Rune de Jin'yael, de la Rune de Markri, de la Rune de Sael'hai, puis se diriger vers la petite île près de la Péninsule méridionale d'Azshara et signaler au Pilote Xiggs Fuselighter de venir les prendre."}},
[3450] = {"Une collecte facile", {"Je veux que les reproductions soient apportées à ma tour, mais il n'est pas nécessaire que vous fassiez tout le chemin jusque là-bas. Je vais simplement les faire transporter par les airs depuis Azshara.","Xiggs Fuselighter me doit un service ou deux et je serai bientôt dans le coin. On l'a embauché pour surveiller la côte. Allez lui parler avant de partir. Il pourra vous donner un lance-fusée pour que vous puissiez lui envoyer un signal. Lorsque vous serez en possession des reproductions, dirigez-vous vers l'endroit qu'il vous indiquera et donnez-les lui pour qu'il puisse les emmener en toute sécurité jusqu'à ma tour. Il devrait être au hangar du Quartier militaire."}, {"Parler à Xiggs Fuselighter à Ironforge."}},
[3451] = {"Signal de la récupération", {"Ah, ainsi Tymor vous envoie, hein ? Oui, je lui dois un service ou deux. Qu'est-ce que c'est ? Eh bien, quel diable est-ce là... Oui, je peux lui donner un coup de main avec ça. Vous aurez besoin d'un lance-fusée et d'un lieu de rendez-vous… c'est simple.","Nous avons construit un site d'atterrissage sur une petite île au large de la péninsule au sud d'Azshara. Prenez ce lance-fusée, et utilisez-le là-bas. Je verrai votre signal et viendrai récupérer la marchandise... quoi que ce soit."}, {"Parler à Xiggs Fuselighter à Ironforge et en obtenir un Lance-fusée standard."}},
[3452] = {"L'enveloppe de la flamme", {"Nous avons besoin d'une dernière pièce pour former la torche.","Vous trouverez un grand campement du Marteau du crépuscule dans les montagnes au nord-ouest. La rumeur dit qu'ils portent des symboles et des idoles afin de rendre hommage à leur dieu. Il se peut que ces symboles et ces idoles portent en eux suffisamment d'enchantement pour former une enveloppe magique. Apportez un seul Symbole de Ragnaros et je tenterai d'en extraire la magie pour créer l'enveloppe de la flamme."}, {"Trouver et apporter un Symbole de Ragnaros à Kalaran Windblade."}},
[3453] = {"La Torche de vindicte", {"Laissez-moi un moment afin que je combine les différentes parties et que j'y applique les enchantements nécessaires."}, {"Attendre que Kalaran Windblade termine la Torche de vindicte."}},
[3454] = {"La Torche de vindicte", {"La torche doit à présent être liée à <son/sa> propriétaire. Prenez-la, <nom> !"}, {"Prendre la Torche de vindicte."}},
[3461] = {"Le retour vers Tymor", {"Tymor ne m'a rien donné avec lequel je puisse vous payer, aussi je suggère que vous abordiez le sujet directement avec lui lorsque vous retournerez à Ironforge. Il est certain qu'il occupe toujours la même place dans la Garde mystique. Frappez simplement à sa porte et assurez-vous qu'il vous rétribue correctement. Cela n'aura rien d'un pique-nique... c'est sûr...","Merci encore pour l'aide que vous lui avez apportée cependant, et bonne chance.","À propos, je n'ai plus besoin de la trousse de dessin. Pourquoi ne la rapporteriez-vous pas à Tymor lorsque vous irez le voir ?"}, {"Rapporter la Trousse de dessin du mage Tymor au mage Tymor, à Ironforge."}},
[3462] = {"L'écuyer Maltrake", {"Adressez-vous à mon représentant, l'écuyer Maltrake. Il vous en apprendra davantage sur l'utilisation de la Torche de vindicte."}, {"Parler à l'écuyer Maltrake."}},
[3463] = {"Allumer le feu !", {"Écoutez bien, <nom>. Bien que les quatre tours ne pourront jamais être détruites, vous pouvez vous servir de la Torche de châtiment afin d'y mettre le feu pour une longue période. Un incendie qu'aucun élément ou qu'aucune force ne pourra éteindre.","Tant qu'elles seront en flammes, aucun Garde ou aucune Sentinelle ne pourra y résider. Ainsi, leur première ligne de défense n'existera simplement plus.","Vous devez pénétrer dans chaque tour et utiliser la Torche de vindicte à l'intérieur des fortifications. Méfiez-vous des Sentinelles d'élite Sombrefer, <nom>."}, {"Mettre le feu aux Tours de garde nord, sud, est, et ouest en utilisant la Torche de vindicte à l'intérieur de chaque tour."}},
[3481] = {"Bibelots...", {"Le coffre a l'air fermé."}, {"Ouvrir le coffre."}},
[3483] = {"Signal de la récupération", nil, nil},
[3501] = {"Tout se compte en grandes quantités", {"Même si vous ne pourrez jamais trouver une sphère de draenéthyste sans défaut, vous découvrirez sans doute divers fragments imparfaits de draenéthyste en explorant ces terres.","Je doute que Kum'isha puisse les utiliser pour rentrer à la maison mais ils peuvent avoir une autre utilité pour les draeneï.","Apportez tous les fragments imparfaits de draenéthyste que vous pourrez et je vous récompenserai."}, {"Apporter des Fragments imparfaits de draenéthyste à Kum'isha et toucher une récompense pour chaque fragment rapporté."}},
[3502] = {"Au rebut pour les draeneï", {"Eh bien, après cette démonstration d'avidité vorace, j'ai la nausée.","Cela dit, je ne vais pas m'étendre trop longtemps sur votre penchant.","Pour chaque fragment imparfait de draenéthyste que vous m'apporterez, je vous récompenserai avec le même genre d'affaires bonnes pour le rebut. Mon tas d'affaires va diminuer tandis que le vôtre va devenir de plus en plus lourd. Sur Draenor, nous appelons cela une \"victoire sur toute la ligne\"."}, {"Apporter des Fragments imparfaits de draenéthyste à Kum'isha. N'importe quel monstres des Terres foudroyées peut détenir un fragment."}},
[3503] = {"Réunion avec le Maître", nil, nil},
[3504] = {"Trahison", {"Une traîtresse, une voleuse ! C'est ce qu'elle sera toujours !","J'ai fait une grave erreur, <nom>. J'ai fait confiance à un elfe de sang. Pire, à UNE elfe de sang !","Elle prétendait qu'Azshara renfermait de grandes richesses... Je n'avais qu'à envoyer quelques guerriers avec elle pour la protéger et j'en serais récompensé. Elle les a tous tués !","Je réclame vengeance, <nom>, mais je ne peux pas envoyer quelqu'un d'autre. Allez voir Ag'tor à Valormok, au nord du campement des Haldarr. Il vous en dira plus."}, {"Apporter la Lettre Scellée à Ag'tor Bloodfist dans Azshara."}},
[3505] = {"Trahison", {"J'ai réfléchi.","Nous commençons notre croisade contre ses ouvriers au sud des falaises de la péninsule du nord d'Azshara. Tuez-les avec une colère sanglante, qu'ils sachent le prix de la trahison.","Ne vous occupez pas d'elle pour l'instant : elle passe son temps dans Azshara avec ses gardes du corps. Une fois ses ouvriers morts, il faudra trouver son campement personnel et faire une reconnaissance du terrain avant de l'attaquer. Nous trouverons peut-être quelque chose à utiliser pour la provoquer."}, {"Tuer 10 Vengeurs elfes de sang et 10 Géomètres elfes de sang avant de rechercher le campement du magus Rimtori. Une fois le camp trouvé, chercher quelque chose d'important pour elle et qui peut être utilisé pour l'obliger à se battre."}},
[3506] = {"Trahison", {"Quelques minutes de réflexion vous donnent des idées, dont l'une vous fait sourire : le fait de détruire les cristaux suffirait-il à provoquer l'elfe de sang responsable du meurtre de tant d'orcs et de la trahison de Belgrom ?","Vous continuez à déchiffrer le texte et les images du livre et vous vous rendez compte que l'un des cristaux est lié au créateur du cercle ; les autres sont liés à quelque chose de beaucoup plus sinistre.","Vous fermez le livre et commencez à chercher quel cristal détruire."}, {"Détruire le Cristal de focalisation des arcanes lié au magus Rimtori et l'assassiner ensuite. Puis apporter la tête du magus Rimtori à Ag'tor Bloodfist dans Azshara."}},
[3507] = {"Trahison", {"Retournez voir Belgrom dans Orgrimmar. Il voudra voir sa tête de ses yeux ! Nous reviendrons bientôt.","Ah, c'est en effet une bonne journée. Merci pour votre aide, <nom>."}, {"Aller voir Belgrom Rockmaul dans Orgrimmar."}},
[3508] = {"Briser l'Amulette", {"Vous êtes téméraire, <nom>. C'est une qualité admirable de votre espèce, le fait de foncer aveuglément dans la bataille alors qu'il n'y a aucune chance de succès, ou si peu. Je vous aiderai, ne serait-ce que pour voir tomber un seul des sbires de Razelikh.","Briser la tutelle de Razelikh sur ses subalternes va prendre du temps et nécessitera l’utilisation d'artefacts rares.","Ne bougez plus, je dois commencer le rituel de Lumière. L'information que j'en tirerai m'aidera à façonner des armes de grand pouvoir."}, {"Attendre que Loramus ait achevé le rituel de Lumière."}},
[3509] = {"Le nom de la bête", {"Il n'y a qu'une façon de nuire au Seigneur démon et à ses sbires. Il faut forger une arme qui portera son véritable nom.","Si nous parvenons à forger une telle arme, le démon et toute sa clique seront vulnérables.","Il y a dans Azshara un autre seigneur, le seigneur Arkkoroc. Vous devez le rechercher, mais attention, ses sbires n'hésiteront pas à vous éliminer s'ils vous découvrent.","Vous aurez certainement un prix à payer pour cette information."}, {"Aller voir le seigneur Arkkoroc dans Azshara. Commencer la recherche au bord de l'eau pour trouver le temple d'Arkkoran."}},
[3510] = {"Le nom de la bête", {"Elle tue mes enfants, <mortel/mortelle>. Celle qui se proclame souveraine de ces océans. Une fausse déesse, rien de plus. Écrasez-la et apportez-moi chacune de ses têtes.","En échange, vous obtiendrez le nom que vous cherchez et autre chose, d'importance égale.","Elle rôde sur la couvée... Elle se nomme Hetaera.","<Le seigneur Arkkoroc désigne le sud-ouest.>","Allez-y !"}, {"Assassiner Hetaera et apporter la Tête ensanglantée d'Hetaera, la Tête battue d'Hetaera et la Tête meurtrie d'Hetaera au seigneur Arkkoroc."}},
[3511] = {"Le nom de la bête", {"Comme promis, je vais vous révéler le nom du seigneur-démon : Razelikh le Souilleur. Mais tout d'abord, je dois préparer quelque chose de la plus grande importance pour votre voyage.","L'arme que vous cherchez à confectionner nécessite une trempe particulière. Laissez-moi quelques instants pour vider le sang de ces têtes.","<Le seigneur Arkkoroc presse les têtes entre ses gigantesques poings, exprimant ainsi tout le sang.>","Apportez la trempe à Loramus, ainsi que le nom de la bête : Rakh'likh."}, {"Rapporter le durcisseur à Loramus Thalipedes."}},
[3512] = {"Par les mots d'Eranikus", {"<nom>, nous devons d'abord inventer une méthode pour parler à Eranikus par l'intermédiaire de la gemme. Il se trouve entre le sommeil et la mort, dans un enfer de cauchemars. Même si l'essence que vous m'avez donnée est infectée, nous avons une chance de l'atteindre.","Vous devez aller jusqu'aux terres glacées de l'est du Berceau-de-l'Hiver et chercher un de nos alliés mortels, l'implorateur d'esprits Umbranse. Il parle aux morts. Quoiqu'Eranikus ne soit pas vraiment mort, il pourra peut-être nous aider."}, {"Se rendre dans la partie orientale du Berceau-de-l'Hiver et aller voir Umbranse l'implorateur d'esprit."}},
[3513] = {"Le Parchemin runique", {"Ce parchemin à l'air très officiel est fermement entouré d'un ruban argenté. Vous pensez que ça intéresserait sûrement quelqu'un de le voir, mais qui ?","Il y a un avant-poste au nord de la frontière d'Ashenvale et si l'information sur ce rouleau concerne les elfes de la nuit, c'est le bon endroit pour l'apporter."}, {"Apporter le Parchemin runique à la tour de garde Nord dans les Tarides."}},
[3514] = {"Présence de la Horde", {"Ce parchemin vient de la dryade rusée nommée Rynthariel, Maître des clés de l'antre des Serres, au pic des Serres-Rocheuses.","Elle ne pourra plus contacter la personne sur qui vous l'avez pris, hein ? Mais nous devons renforcer notre position. La Horde ne tolérera pas qu’on lui dispute ces lieux.","<nom>, partez pour l'antre des Serres. Tuez Rynthariel, et placez un symbole que tous pourront voir et comprendre. Prenez le drapeau de la Horde, <nom>. Hissez-le dans l'antre des Serres, qu'ils n'aient pas la témérité d'envoyer un autre messager."}, {"Tuer le chef de l'Antre des Serres, Rynthariel le Maître des clés."}},
[3517] = {"L'art du vol", {"J'admets avoir profité de ma situation dans Azshara, <nom>. La punition de Belgrom a eu de bons côtés.","J'y ai fait des recherches et vous seriez <étonné/étonnée> de voir combien il y a d'objets de valeur ici.","Je vous en dirai plus sur mes affaires et vous ferai participer aux profits, si vous m'aidez à les récupérer.","J'ai besoin de tablettes disposées partout dans les ruines d'Eldarath, à l'est d'ici. Les quatre portent le nom de sorciers bien-nés. Trouvez-les pour moi et nous discuterons."}, {"Trouver la Tablette de Beth'Amara, la Tablette de Jin'yael, la Tablette de Markri et la Tablette de Sael'hai puis retourner voir Jediga dans Azshara."}},
[3518] = {"Livraison à Magatha", {"La première tablette va à un tauren femelle... Elle s'appelle Magatha. Elle se trouve parmi les anciens dans Thunder Bluff et elle est l'un des partisans de l'alliance entre les taurens et les Réprouvés. Plus que n'importe lequel des anciens, elle sent que les taurens et leurs connaissances peuvent aider les Réprouvés à redevenir des hommes.","Apportez-lui la Tablette de Beth'Amara. La tablette est peut être le lien dont elle a besoin pour aider les Réprouvés... ou du moins c'est ce qu'elle dit."}, {"Apporter la Tablette de Beth'Amara à Magatha dans Thunder Bluff."}},
[3519] = {"Un ami dans le besoin", {"Ahhh... J'ai été mordu par une araignée nommée Githyiss la Vile en explorant sa caverne, tout près d'ici. Une grande quantité de poison m'a été injectée ; vous devez m'aider.","Prévenez Dirania Silvershine. Elle sera capable de me secourir.","Vite... J'ai des vertiges de plus en plus alarmants..."}, {"Parler à Dirania Silvershine, à Sombrevallon."}},
[3520] = {"Esprits de Hurleurs", {"Je cherche les esprits des hurleurs des vallées de Feralas. Ils sont cousins d'un dieu fort ancien et leurs esprits lui sont liés.","Prenez ce roncier magique et allez en Feralas pour y trouver les hurleurs des vallées, au sud et au centre. Tuez-les, puis agitez ma baguette au-dessus d'eux. Cela exhortera leur esprit à se révéler. Touchez les esprits et prenez leur essence.","Revenez me voir avec ma baguette et les esprits et je vous en dirai plus de ce dieu ancien dont je vous ai parlé."}, {"Capturer l’Esprit de 3 Hurleurs en Feralas, puis retourner voir Yeh'kinya à Port Gentepression."}},
[3521] = {"Antidote d'Iverron", {"Nous allons pouvoir aider Iverron, je connais un antidote qui devrait combattre le poison. Il me faut cependant quelques ingrédients.","Je vais avoir besoin de Champignons jacinthe. Vous pouvez en trouver sous les arbres, ou chez les grells au sud ; ils ont l'air de les aimer. J'aurai aussi besoin de Lys pétales de lune, qui ne poussent qu'autour des points d'eau.","Le dernier ingrédient sera plus difficile à trouver. Il me faut de l'ichor de ces mêmes araignées sylvestres qui ont empoisonné Iverron."}, {"Trouver 7 Champignons jacinthe, 4 Lys pétales de lune et 1 Ichor d'araignée Tissebois pour Dirania Silveshine dans Sombrevallon."}},
[3522] = {"Antidote d'Iverron", {"L'antidote est prêt, <nom>. Faites en sorte qu'Iverron le boive.","Vous devez savoir une chose : l'antidote ne reste efficace que pendant 5 minutes. Vous devez le lui apporter à temps.","Que la vitesse soit avec vous, <nom>."}, {"Apporter l’Antidote d'Iverron à Iverron avant que la limite de temps ne soit atteinte. Iverron se trouve près de la grotte, au nord."}},
[3523] = {"Le Fléau des Souilles", {"Remarquable ! Mon nom est Belnistrasz et je vous remercie de m'avoir secouru. Je me voyais déjà mort... ou pire, transformé en repas pour cette vermine infestée par le Fléau. Oui, j'ai dit le Fléau à dessein : ici, ils ont négocié une sorte d'accord avec les hurans et ce cauchemar en est le résultat.","Écoutez, plutôt que de fuir, voulez-vous m'aider à en finir ? C'est probablement dangereux, mais vous semblez être du genre à ne pas vous inquiéter pour si peu."}, {"Pour aider Belnistrasz, lui parler de nouveau et lui rendre la Pierre de voeu qu’il vous avait donnée."}},
[3524] = {"Echouage", {"De majestueuses créatures marines se jettent sur le littoral de Sombrivage, où elles restent échouées jusqu’à leur mort. Dernièrement, le nombre de ces bêtes n’a cessé d’augmenter. C’est le temple de la Lune qui m’envoie pour enquêter, mais la présence des murlocs, sur le littoral, a freiné mes recherches.","Il y a une créature géante échouée au sud d’Auberdine, et beaucoup de ces maudits murlocs Grisebrume aux alentours. Pourriez-vous aller récupérer des os sur la carcasse de cette créature ? Ils nous serviront pour nos études."}, {"Récupérer des Os de créature marine sur la créature échouée, au sud d’Auberdine, puis les apporter à Gwennyth Bly'Leggonde, à Auberdine."}},
[3525] = {"L'extinction de l'idole", {"Près de l’entrée, il y a une idole qui sert de four aux hurans, ces bêtes... ils consument ceux qu’ils enlèvent dans les Tarides. Je peux faire en sorte qu’ils ne puissent plus jamais s’en servir, mais j’ai besoin de votre aide. Voici mon plan : escortez-moi jusqu’à l’idole, quand nous arriverons, je commencerai le rituel. Pendant ce temps, je ne pourrai pas communiquer avec vous et encore moins me défendre. Protégez-moi pendant que je détruis l’idole. S’il parviennent à m’abattre, tout est fini.","Êtes-vous avec moi ?"}, {"Escorter Belnistrasz jusqu'à l'Idole des hurans dans les Souilles de Tranchebauge.","Protéger Belnistrasz tandis qu'il exécute le rituel pour arrêter l'idole."}},
[3526] = {"Ingénierie des gobelins", {"La meilleure ingénierie est représentée par deux disciplines : celle des gnomes et celle des gobelins. L'Ingénierie des gobelins est clairement le choix de tous les ingénieurs brillants. Apprendre à vaincre le monde par l'utilisation judicieuse d'explosifs et d'incroyables mécanismes !","<nom>, lisez ce manuel. Si vous voulez en apprendre plus, apportez-le à Nixx Sprocketspring à Gadgetzan. Mais attention : l’adhésion à cette discipline est définitive et exclusive, alors soyez <sûr/sûre> de faire le bon choix !"}, {"Pour en apprendre davantage sur l'Ingénierie gobeline, apporter le Manuel d'Ingénierie à Nixx Sprocketspring dans Gadgetzan."}},
[3527] = {"La prophétie de Mosh'aru", {"La prophétie ancienne de Mosh'aru explique comment contenir l'essence du dieu Hakkar. Elle a été écrite sur deux tablettes, apportées à la cité troll de Zul'Farrak, à l'ouest de Gadgetzan.","Apportez-moi les Tablettes Mosh'aru !","La première tablette est détenue par le troll Theka le Martyr, mort depuis longtemps. On raconte que ses bourreaux ont été changés en scarabées et qu'ils parcourent à présent son autel.","La seconde tablette est détenue par l'Hydromancienne Velratha, près du bassin sacré de Gahz'rilla.","Une fois que vous aurez les tablettes, apportez-les-moi."}, {"Apporter les Première et Deuxième tablettes Mosh'Aru à Yeh'kinya, à Tanaris."}},
[3528] = {"Le dieu Hakkar", {"Maintenant il faut capturer l'essence de l'avatar d'Hakkar.","Apportez l'Oeuf d'Hakkar au Sanctuaire du dieu déchu dans le Temple englouti d'Azeroth. Invoquez le pouvoir de l'œuf pour réveiller le dieu mort.","Il enverra ses sbires contre vous. Combattez-les et prenez leur sang Hakkari, puis versez-le sur les flammes éternelles qui retiennent l'esprit d'Hakkar. Une fois les flammes éteintes, l'avatar d'Hakkar entrera dans notre monde.","Combattez-le et placez son essence dans l'œuf d'Hakkar."}, {"Apporter l'Oeuf Rempli d'Hakkar à Yeh'kinya dans Tanaris."}},
[3541] = {"Livraison pour Jes'rimon", {"J'ai un copain dans Orgrimmar. Il réside dans la rue principale. Il est difficile à trouver si vous ne savez pas où regarder, mais allez voir dans les bâtiments les plus hauts et vous devriez sans peine tomber sur lui.","Faites-moi une faveur : n'allez pas répéter à tout Kalimdor qu'il est là : ce serait mauvais pour lui comme pour moi.","Il nous donnera un prix plus qu'honnête pour la Tablette de Jin'yael. Je ne sais pas pourquoi, mais vous auriez dû voir comme il était excité quand je lui en ai parlé."}, {"Apporter la Tablette de Jin'yael à Jes'rimon dans Orgrimmar."}},
[3542] = {"Livraison à Andron Gant", {"Andron Gant est mon contact suivant. Il se terre habituellement dans Undercity, tout près de l'Apothicarium.","Quand il est venu me voir, il était plus loquace qu'à l'ordinaire et il m'a dit pourquoi il voulait la Tablette. Apparemment, des mages d'Undercity veulent étudier certains des charmes mystérieux que les Bien-nés utilisaient. Ils pensent que cette tablette les y aidera. Markri était expert en invocation de créatures, surtout des élémentaires.","Apportez la Tablette de Markri et revenez me voir pour votre récompense."}, {"Apporter la Tablette de Markri à Andron Gant dans Undercity."}},
[3561] = {"Livraison à l'archimage Xylem", {"Cette dernière tablette est destinée à quelqu'un de très dangereux, <nom>. Croyez-moi, je lui fais encore moins confiance qu'aux autres.","L'archimage Xylem habite une tour en haut des collines qui entourent Azshara. Il est humain, mais il n'agit comme aucun autre humain. La puissance se dégage de tout son être et je n'ose lui demander ce qu'il veut faire de la tablette.","Son garde du corps Sanath doit m'aider à parvenir à la tour lorsque je serai prêt à le rencontrer. Allez chercher Sanath, au nord."}, {"Apporter la Tablette de Sael'hai à l'archimage Xylem dans Azshara."}},
[3562] = {"Le Paiement de Magatha à Jediga", {"Portez cette note à Jediga. Dites-lui que son paiement l'attendra à son retour. Et dites-lui que son travail est satisfaisant.","Faites vite, <nom>. Dites à votre Maître que je l'attendrai ici."}, {"Apporter la Note de Magatha à Jediga dans Azshara."}},
[3563] = {"Le Paiement de Jes'rimon à Jediga", {"Portez cette lettre à Jediga. Faites-lui savoir que notre accord a été rentable et que son travail me satisfait.","Dites-lui de revenir vite à Orgrimmar et que Jes'rimon prendra bien soin d'elle.","Dites-lui tout cela et j'aurai peut-être besoin de vos services dans le futur, <nom>."}, {"Apporter la Note de Jes'rimon à Jediga dans Azshara."}},
[3564] = {"Le Paiement d'Andron à Jediga", {"Ici... Portez... <hic> cela à Jediga. Bonne chance !"}, {"Apporter la Note d'Andron à Jediga dans Azshara."}},
[3565] = {"Le Paiement de Xylem à Jediga", {"Qu'est-ce que c'est ? Oh oui, votre paiement, bien sûr !","Prenez cela et repartez. Je suis sûr que Jediga attend avec une grande, grande patience votre retour."}, {"Apporter la Note de Xylem à Jediga dans Azshara."}},
[3566] = {"Debout, Obsidion !", {"C'était... C'était... C'était un piège.","Oubliez... moi.","Tuez ce bâtard… Tuez-le pour nous… pour Thorius."}, {"Assassiner Lathoric le Noir et Obsidion, puis retourner voir Thorius, à Ironforge, avec la Tête de Lathoric le Noir et le Coeur d'Obsidion."}},
[3567] = {"Au sommet", nil, nil},
[3568] = {"Corruption Suintante", {"Regardez cette taurène derrière moi, celle qui a l'air de se trouver mal. Elle était dans Azshara récemment. Elle prétend avoir été blessée par des nagas en traversant les ruines d'Eldarath.","Après les avoir repoussés, elle s'est reposée sur la plage et a bu dans l'un des étangs de marée. Maintenant elle est malade.","Je veux découvrir ce qu'elle a bu. Prenez cette boîte, avec quelques fioles vides que j'ai étiquetées. Remplissez chacune d'elle dans un étang différent et revenez me voir."}, {"Avant de retourner voir le Chimiste Cuely, remplir les 4 Fioles vides aux étangs des marées, le long de la côte des Ruines d'Eldarath, à Azshara."}},
[3569] = {"Corruption Suintante", {"Donnez-le à Thersa. Peut-être que ça va guérir sa... Ou peut-être pas. Nous ne le saurons que lorsqu'elle aura bu."}, {"Donner l’élixir de Cuely à Thersa Windsong, à Undercity."}},
[3570] = {"Corruption Suintante", nil, nil},
[3601] = {"Kim'jael évidemment !", {"Kim'jael, effectivement ! Savez-vous ce que Kim'jael signifie chez les elfes de sang, <nom> ? Petit rat... ou quelque chose comme ça. Quels imbéciles !","Ils m'ont embauché pour aider à reconnaître le secteur et pour chercher des genres de trésors perdus. Je me suis évidemment précipité lorsque j'ai entendu le mot \"trésor\" et le montant qu'ils allaient me payer, mais ils m'ont bien entendu trahi et ont essayé de me tuer... MOI !","C'est pour vous dire, on ne peut jamais faire confiance à un elfe de sang.","Maintenant, le problème, c'est qu'ils sont en possession de mon équipement et que je ne peux pas le récupérer. Vous voulez m'aider ?"}, {"Fouiller le Camp de base thalassien pour trouver la Boussole, la Lunette, le Poulet farci et le Wizzlegoober de Kim'Jael, puis les lui rapporter à Azshara."}},
[3602] = {"Azsharite", {"Les anciens géants des falaises de la région du sud d'Azshara se nourrissent de la roche et des constructions locales. Leurs excréments forment un cristal très malléable et extrêmement solide appelé azsharite.","On le trouve dans le Cercle du sud, mais on en récupère peu en raison du tempérament extrêmement violent des géants.","Vous devez aller au sud et récupérer suffisamment d'azsharite pour créer le corps de l'arme. Mes gangrechiens vous aideront à trouver le cristal."}, {"Trouver 20 Azsharites cristallisées au sud d'Azshara. Utiliser éventuellement le gangrechien pour ‘renifler’ l'emplacement où sont intelligemment cachés les dépôts.","Retourner voir Loramus une fois votre tâche accomplie."}},
[3621] = {"La Formation de Felbane", {"Les armes sont prêtes à être forgées. Vous devez apporter l'azsharite à quelqu'un qui saura en exploiter l'énergie.","Il y a un forgeron dans la Vallée de Strangleronce. Galvan l'Ancien, le chef de l'Ordre du Mithril, pourra fabriquer ces armes. Vous trouverez son campement entre Zul'Gurub et le tertre des ogres Mosh'Ogg.","Puissent votre détermination et votre foi vous porter dans cette épreuve. Si vous échouez, sachez que votre dieu vous regarde en souriant, <mortel/mortelle>."}, {"Apporter la cargaison à Galvan l'Ancien dans la vallée de Strangleronce."}},
[3625] = {"Armes Fel en Azsharite enchantée", {"<nom>, la création de chaque arme va prendre un peu de temps.","De plus, vous ne pourrez utiliser le pouvoir du sort Corruption qu'une fois toutes les trois minutes. Ses propriétés d'affaiblissement contre Razelikh le Souilleur et ses sbires peuvent varier dans la durée. C'est pourquoi vous devez utiliser le sort Corruption seulement au moment judicieux !","Donnez-moi une minute pour terminer votre arme, <nom>."}, {"Attendre que Galvan ait fini de forger les armes."}},
[3626] = {"Retour aux Terres foudroyées", {"L'heure de la rédemption est proche, <nom>. Vous devez retourner aux Terres foudroyées avec votre nouvel équipement et parler au Héros déchu de la Horde pour recevoir votre objectif final."}, {"Retourner voir le Héros déchu de la Horde."}},
[3627] = {"Réunir les morceaux de l'Amulette brisée.", {"Pour atteindre le Souilleur, vous devez d'abord détruire la triade de pouvoir et récupérer les morceaux d'amulette. Quand vous aurez récupéré les trois morceaux, revenez me voir et je vous donnerai de nouvelles instructions.","Allez, <nom> ! Aux Terres foudroyées ! Sevine, Allistarj et Grol doivent mourir !"}, {"Assassiner Grol le Destructeur, dame Sevine et l'archimage Allistarj. Prendre sur leurs cadavres l'Amulette de Grol, l'Amulette de Sevine et l'Amulette d'Allistarj.","UTILISER les armes Fel en Azsharite enchantée pour affaiblir la triade (ils ne peuvent pas être tués autrement), mais les utiliser sagement..."}},
[3628] = {"Vous êtes Rakh'likh, démon", {"Razelikh se terre sur la plus haute montagne des Terres foudroyées. Le seul moyen d'y accéder est d'utiliser les Runes de téléportation près de la montagne. Tant que vous tenez l'amulette, vous pouvez vous y transporter. Une fois là-bas, vous devez neutraliser la bête. L'amulette est votre atout, <nom>. Utilisez-la sur l'Autel du Souilleur.","Si vous arrivez à le vaincre, prenez sa corne. Elle sera signe d'espoir pour ceux qui la verront, la preuve que les héros existent dans ce monde déchiré par la guerre."}, {"Détruire Razelikh le Souilleur et apporter la Corne brisée du Souilleur au Héros déchu de la Horde. Rendre aussi l'Amulette du Souilleur au Héros Déchu pour qu'il puisse la détruire, afin de l'empêcher de tomber dans de mauvaises mains."}},
[3629] = {"Ingénierie des gobelins", {"L'ingénierie pratiquée au niveau d'expert connaît deux disciplines : celle des gnomes et celle des gobelins. Je ne mentionne l'Ingénierie des gobelins que parce que je suis un Maître juste. Ils aiment faire des choses qui explosent et se soucient rarement des conséquences.","<nom>, lisez ce manuel. Si vous voulez apprendre comment vous faire sauter, apportez-le à Nixx Sprocketspring dans Gadgetzan. Mais le choix de cette discipline est définitif et exclut l'autre, alors soyez <sûr/sûre> de votre choix !"}, {"Pour en apprendre davantage sur l'Ingénierie gobeline, apporter le Manuel d'Ingénierie à Nixx Sprocketspring dans Gadgetzan."}},
[3630] = {"Ingénierie des gnomes", {"L'ingénierie pratiquée à un niveau d'expert connaît deux disciplines : celle des gnomes et celle des gobelins. L'Ingénierie gnome est de loin supérieure ! Elle consiste à construire de merveilleux dispositifs qui rendent la vie meilleure !","<nom>, lisez ce manuel. Si vous voulez en apprendre plus sur l'Ingénierie gnome, apportez ce manuel au maître-artisan Overspark dans Ironforge. Mais le choix de cette discipline est définitif et exclut l'autre, alors soyez <sûr/sûre> de votre choix !"}, {"Pour en apprendre davantage sur l'ingénierie gnome, apporter le Manuel d'Ingénierie au maître-artisan Overspark, à Ironforge."}},
[3631] = {"Invoquer un Palefroi corrompu", {"Hum. À mon avis, vous n’avez ni la force ni les compétences requises, mais cela ne me regarde pas puisque Strahad pense autrement, et que c’est lui qui décide... cette fois.","Il voudrait vous parler le plus vite possible. Vous le trouverez, avec ses acolytes, dans la petite tour qui domine Ratchet, dans les Tarides.","Ne traînez pas."}, {"Parler à Strahad Farsan, dans les Tarides."}},
[3632] = {"Ingénierie des gnomes", {"L'ingénierie pratiquée à un niveau d'expert connaît deux disciplines : celle des gnomes et celle des gobelins. L'Ingénierie gnome est de loin supérieure ! Elle consiste à construire de merveilleux dispositifs qui rendent la vie meilleure !","<nom>, lisez ce manuel. Si vous voulez en apprendre plus sur l'Ingénierie gnome, apportez ce manuel au maître-artisan Overspark dans Ironforge. Mais le choix de cette discipline est définitif et exclut l'autre, alors soyez <sûr/sûre> de votre choix !"}, {"Pour en apprendre davantage sur l'ingénierie gnome, apporter le Manuel d'Ingénierie au maître-artisan Overspark, à Ironforge."}},
[3633] = {"Ingénierie des gobelins", {"La meilleure ingénierie est représentée par deux disciplines : celle des gnomes et celle des gobelins. L'Ingénierie des gobelins est clairement le choix de tous les ingénieurs brillants. Apprendre à vaincre le monde par l'utilisation judicieuse d'explosifs et d'incroyables mécanismes !","<nom>, lisez ce manuel. Si vous voulez en apprendre plus, apportez-le à Nixx Sprocketspring à Gadgetzan. Mais attention : l’adhésion à cette discipline est définitive et exclusive, alors soyez <sûr/sûre> de faire le bon choix !"}, {"Pour en apprendre davantage sur l'ingénierie gobeline, apporter le Manuel d'Ingénierie à Nixx Sprocketspring, à Gadgetzan."}},
[3634] = {"Ingénierie des gnomes", {"L'ingénierie au niveau d'expert connaît deux disciplines : celle des gnomes et celle des gobelins. Bien que ce soit du gâchis à mon sens, vous devriez vous spécialiser dans celle des gnomes. Ils fabriquent des objets et des gadgets pour soit-disant changer le monde, mais ça fonctionne rarement.","<nom>, lisez ce manuel. Si ça vous intéresse, apportez-le au maître-artisan Overspark dans Ironforge. Mais le choix de cette discipline est définitif et exclut l'autre, alors soyez <sûr/sûre> de votre choix !"}, {"Pour en apprendre davantage sur l'ingénierie gnome, apporter le Manuel d'Ingénierie au maître-artisan Overspark, à Ironforge."}},
[3635] = {"Ingénierie des gnomes", {"L'ingénierie au niveau d'expert enseigne deux disciplines : celle des gnomes et celle des gobelins. Bien que ce soit du gâchis à mon sens, vous devriez connaître celle des gnomes. Ils fabriquent des objets et des gadgets pour prétendument changer le monde, mais ils fonctionnent rarement.","<nom>, lisez ce manuel. Si ça vous intéresse, apportez-le à Oglethorpe Obnoticus à Baie-du-Butin. Mais attention : l’adhésion à cette discipline est définitive et exclusive, alors soyez <sûr/sûre> de faire le bon choix !"}, {"Pour en apprendre davantage sur l'Ingénierie gnome, apporter le Manuel d'Ingénierie à Oglethorpe Obnoticus à Baie-du-Butin."}},
[3636] = {"Apportez la Lumière", {"Le Fléau est une menace persistante pour l'Alliance, vous le savez fort bien. Récemment, nous avons découvert quelque chose, <nom>.","Dans les Tarides, il y a un amas de ronces, les Souilles de Tranchebauge. C'était autrefois la demeure de hurans normaux, mais maintenant un pacte y a été fait avec le Fléau, pour créer des créatures beaucoup plus mauvaises... Elles sont gouvernés par la liche Amnennar, dite le Porte-froid.","Soyez le porte-parole de la Lumière, <nom> ; mettez fin à cette mauvaise union."}, {"Tuer Amnennar le Porte-froid, aux Souilles de Tranchebauge, pour l’Archevêque Benedictus."}},
[3637] = {"Ingénierie des gnomes", {"L'ingénierie au niveau d'expert enseigne deux disciplines : celle des gnomes et celle des gobelins. Bien que ce soit du gâchis à mon sens, vous devriez connaître celle des gnomes. Ils fabriquent des objets et des gadgets pour prétendument changer le monde, mais ils fonctionnent rarement.","<nom>, lisez ce manuel. Si ça vous intéresse, apportez-le à Oglethorpe Obnoticus à Baie-du-Butin. Mais attention : l’adhésion à cette discipline est définitive et exclusive, alors soyez <sûr/sûre> de faire le bon choix !"}, {"Pour en apprendre davantage sur l'Ingénierie gnome, apporter le Manuel d'Ingénierie à Oglethorpe Obnoticus à Baie-du-Butin."}},
[3638] = {"Le Vœu du secret", {"L'ingénierie des gobelins traite de l'utilisation pratique des explosifs ! Nos schémas nous permettent de faire des bombes et des armes puissantes ; c’est le thème du manuel que vous lisez.","Depuis toujours, ce manuel est lié par un Vœu du secret couvrant tous nos schémas. Une fois que vous serez Ingénieur gobelin, vous ne pourrez plus accéder à l'Ingénierie des gnomes. Si vous voulez toujours devenir Ingénieur gobelin, signez ce document vous engageant au secret et revenez me voir. Réfléchissez soigneusement, <nom>."}, {"Pour devenir un Ingénieur gobelin, cliquer avec le bouton droit sur le Vœu de secret et retourner voir une fois encore Nixx Sprocketspring dans Gadgetzan."}},
[3639] = {"Montrez votre travail", {"En tant que mentor, je veux quelques échantillons de votre travail. Vous êtes un ingénieur habile ; la question n'est pas là, <nom>. Je veux analyser votre travail pour orienter vos efforts futurs. J'ai besoin des échantillons suivants : 20 grandes bombes en fer, 20 bâtons de dynamite solide et 5 moutons explosifs.","Soumettez-moi vos réalisations et je vous ferai votre Carte de membre des Ingénieurs gobelins ; elle est exigée pour accéder à toutes les techniques des maîtres des ingénieurs gobelins !"}, {"Apporter 20 Grandes Bombes en fer, 20 Dynamites Solides et 5 Moutons Explosifs à Nixx Sprocketspring dans Gadgetzan."}},
[3640] = {"Le Vœu du secret", {"L'ingénierie des gnomes consiste à mettre en forme la théorie de l'ingénierie pour servir nos besoins ! Nos schémas nous permettent de construire des appareils contrôlant le monde qui nous entoure. Le manuel que vous avez lu traite de ce sujet.","Il traite également du Vœu du secret qui recouvre nos schémas. Lorsque vous adhérez à l'Ingénierie des gnomes, vous ne pouvez plus revenir en arrière. Vous ne pourrez jamais plus accéder à l'Ingénierie des gobelins.","Si vous souhaitez toujours devenir Ingénieur gnome, signez ce document qui vous engage à garder le secret à vie et revenez me parler. Prenez votre décision après mûre réflexion, <nom>."}, {"Pour devenir un Ingénieur gnome, cliquer avec le bouton droit sur le Vœu de secret et retourner voir une fois encore le maître-artisan Overspark, à Ironforge."}},
[3641] = {"Montrez votre travail", {"En tant que votre mentor, je veux avoir des exemplaires de votre travail. Vous êtes <compétent/compétente> en tant qu'Ingénieur, je ne le remets pas en question, <nom>. Cependant, je souhaite analyser votre travail pour savoir comment introduire les techniques gnomes dans vos travaux futurs. J'ai besoin des objets suivants : six tubes de mithril, une lunette de précision et deux leurres perfectionnés.","Soumettez-moi ces objets et je vous remettrai votre propre Carte de membre des ingénieurs gnomes. Elle vous sera demandée pour aborder tous les Entraîneurs des Ingénieurs gnomes !"}, {"Apporter 6 Tubes de mithril, 1 Lunette de précision et 2 Leurres perfectionnés au Maître artisan Overspark à Ironforge."}},
[3642] = {"Le Vœu du secret", {"L'ingénierie des gnomes consiste à mettre en forme la théorie de l'ingénierie pour servir nos besoins ! Nos schémas nous permettent de construire des appareils contrôlant le monde qui nous entoure. Le manuel que vous avez lu traite de ce sujet.","Il traite également du Vœu du secret qui recouvre nos schémas. Lorsque vous adhérez à l'Ingénierie des gnomes, vous ne pouvez plus revenir en arrière. Vous ne pourrez jamais plus accéder à l'ingénierie des gobelins.","Si vous souhaitez toujours devenir Ingénieur gnome, signez ce document qui vous engage à garder le secret à vie et revenez me parler. Prenez votre décision après mûre réflexion, <nom>."}, {"Pour devenir un Ingénieur gnome, cliquer avec le bouton droit sur le Vœu de secret et retourner voir une fois encore Oglethrope Obnoticus à Baie-du-Butin."}},
[3643] = {"Montrez votre travail", {"En tant que votre mentor, je veux avoir des exemplaires de votre travail. Vous êtes <compétent/compétente> en tant qu'ingénieur, je ne le remets pas en question, <nom>. Cependant, je souhaite analyser votre travail pour savoir comment introduire les techniques gnomes dans vos travaux futurs. J'ai besoin des objets suivants : six tubes de mithril, une lunette de précision et deux cibles avancées.","Soumettez-moi ces objets et je vous remettrai votre propre Carte de membre des ingénieurs gnomes. Elle vous sera demandée pour aborder tous les entraîneurs des ingénieurs gnomes !"}, {"Apporter 6 Tubes de mithril, 1 Lunette de précision et 2 Cibles avancées à Oglethorpe Obnoticus à Baie-du-Butin."}},
