[1963] = {"Mano Destrozada", nil, {"Mata a Tazan y llévale su cartera a Therzok al Circo de las Sombras, en Orgrimmar."}},
[1998] = {"Fenwick Thatros", nil, {"Mata a Fenwick Thatros y llévale su cabeza a Mennet Carkad, que está en el Barrio de los Pícaros de Entrañas."}},
[1999] = {"Herramientas de comercio", nil, {"Encuentra el informe de estado de Dalaran y devuélveselo a Mennet Carkad, que está en el Barrio de los Pícaros de Entrañas."}},
[2038] = {"Suministros de Bingles", nil, {"Localiza las herramientas de Bingles y devuélveselas:\n\nllave inglesa, destornillador, martillo y cartucho de explosivos."}},
[2039] = {"Encuentra a Bingles", nil, {"Encuentra a Bingles Pimpampum en Loch Modan."}},
[2040] = {"Asalto subterráneo", nil, {"Recupera la Dentrituradora goblin de Las Minas de la Muerte y devuélveselo a Shoni la Shilenshiosha, en Ventormenta."}},
[2041] = {"Habla con Shoni", nil, {"Habla con Shoni la Shilenshiosha en el Distrito de los Enanos en Ventormenta."}},
[2058] = {"<UNUSED>", nil, {"Requisitos:"}},
[2098] = {"La búsqueda de Gyromast", nil, {"Encuentra y devuelve los tres fragmentos de la llave de Gelkak Gyromast."}},
[2118] = {"Tierras apestadas", nil, {"Captura un oso cardo rabioso vivo y llévaselo a Tharnariun. \n\nSi no consigues capturar un oso cardo rabioso y pierdes la trampa, pídele otra a Tharnariun Espinarbolado."}},
[2138] = {"La limpieza de la infección", nil, {"Libra al bosque de 20 Osos Cardo rabiosos y ve a buscar a Tharnariun Espinarbolado a Costa Oscura."}},
[2139] = {"La esperanza de Tharnariun", nil, {"Encuentra a la madre del cubil y mátala."}},
[2158] = {"Descanso y relajación", nil, {"Habla con el tabernero Farley de la Posada Orgullo de León."}},
[2159] = {"Paquete de Dolanaar", nil, {"Llévale el paquete de Dolanaar al tabernero Keldamyr."}},
[2160] = {"Suministros para Tannok", nil, {"Lleva a Kharanos el cajón de suministros del mesón y entrégaselo a Tannok Martilloescarcha."}},
[2161] = {"Un cargamento de peón", nil, {"Lleva la carga de Ukor al tabernero Grosk a Cerrotajo."}},
[2178] = {"Guiso de zancudo", nil, {"Llévale 5 piezas de carne de zancudo a Alanndarian Arrullanoche a Auberdine."}},
[2198] = {"El collar hecho añicos", nil, {"Busca al creador del collar hecho añicos y descubre para qué sirve."}},
[2199] = {"Esa información tiene un precio", nil, {"Lleva 5 lingotes de plata a Talvash del Kissel a Forjaz."}},
[2200] = {"El regreso a Uldaman", nil, {"Busca pistas sobre el paradero del collar de Talvash en Uldaman. Dijo que un paladín muerto fue su último dueño."}},
[2201] = {"Encuentra las gemas", nil, {"Encuentra el rubí, el zafiro y el topacio que están desperdigados por Uldaman. Cuando los tengas, contacta con Talvash del Kissel mediante la ampolla de adivinación que él te dio.\n\nA través del diario, sabes que...\n*El rubí está escondido en una zona de Forjatiniebla con barricadas.\n*El topacio permanece oculto en una urna en una de las zonas de los troggs, cerca de algunos enanos de la Alianza.\n\n*El zafiro obra en poder de Grimlok, el líder de los troggs, según él mismo afirma."}},
[2202] = {"Componentes de Uldaman", nil, {"Llévale 12 setas magenta a Jarkal Musgofusión en Kargath."}},
[2204] = {"Restaurar el collar", nil, {"Obtén una fuente de energía del ensamblaje más poderoso que encuentres en Uldaman, y entrégasela a Talvash del Kissel en Forjaz."}},
[2205] = {"Busca el IV:7", nil, {"Entrega el paquete de Keryn Sylvius al maestro Mathias Shaw que está en el Cuartel de Ventormenta."}},
[2206] = {"El robo", nil, {"Encuentra al maestro de embarcadero y recupera el calendario de envíos para el maestro Mathias Shaw.|n"}},
[2218] = {"El camino hacia la salvación", nil, {"Ve a Forjaz y habla con Hulfdan Barbanegra."}},
[2238] = {"Un simple subterfugio", nil, {"Encuentra a Onin MacHammar cerca de Gnomeregan."}},
[2239] = {"El informe de Onin", nil, {"Lleva el informe de Onin a Hulfdan a Forjaz."}},
[2240] = {"La cámara oculta", nil, {"Lee el diario de Baelog, inspecciona la cámara oculta y ve a informar al prospector Pico Tormenta."}},
[2258] = {"Componentes de Tierras Inhóspitas", nil, {"Llévale 5 mollejas de águila ratonera, 10 colmillos de coyote del risco y 5 fragmentos de elemental de roca a Jarkal Musgofusión en Kargath, en las Tierras Inhóspitas."}},
[2278] = {"Los discos de platino", nil, {"Habla con el vigía de piedra y descubre qué conocimiento antiguo alberga. Cuando hayas adquirido el conocimiento que te ofrece, activa los Discos de Norgannon."}},
[2279] = {"Los discos de platino", nil, {"Lleva la reproducción en miniatura de los Discos de Norgannon a la Liga de Expedicionarios de Forjaz."}},
[2280] = {"Los discos de platino", nil, {"Lleva la reproducción en miniatura de los Discos de Norgannon a alguien que esté muy interesado."}},
[2281] = {"Cita en Crestagrana", nil, {"Adéntrate en Villa del Lago, en las Montañas Crestagrana, y habla con Lucius."}},
[2282] = {"Molino de Alther", nil, {"Abre la caja fuerte de Lucius y llévale la muestra de robo a Villa del Lago."}},
[2283] = {"La recuperación del collar", nil, {"Busca el collar en la excavación de Uldaman y llévaselo a Dran Droffers a Orgrimmar. Puede que el collar esté estropeado."}},
[2284] = {"La recuperación del collar, 2ª parte", nil, {"Busca pistas sobre el paradero de las gemas en las profundidades de Uldaman."}},
[2298] = {"Una regia sacudida", nil, {"Viaja a Ventormenta y busca a Renzik \"Chafarote\"."}},
[2299] = {"¡Ve a hablar con Hulfdan!", nil, {"Viaja a Forjaz y habla con Hulfdan Barbanegra."}},
[2318] = {"La traducción del diario", nil, {"Encuentra a alguien que pueda traducir el diario del paladín. El lugar más cercano en el que podrás encontrar a alguien es Kargath, en las Tierras Inhóspitas."}},
[2339] = {"Encuentra las gemas y la fuente de alimentación", nil, {"Recupera las 3 gemas y una fuente de energía para el collar de Uldaman y llévalo todo a Jarkal Musgofusión en Kargath. Jarkal cree que es posible que la fuente de energía más poderosa se halle en un ensamblaje de Uldaman.\n\nPor el diario sabes...\n* Que han ocultado el rubí en una zona cerrada con barricadas en Forjatiniebla.\n* Que el topacio se ha ocultado en una urna en un área trogg, cerca de los enanos de la Alianza.\n* Que Grimlok, el jefe trogg, se ha quedado con el zafiro."}},
[2342] = {"Los tesoros reclamados", nil, {"Saca el tesoro de la familia de Patrick Garrett del cofre de su familia que se encuentra en la Sala Comunal Sur de Uldaman y llévasela a él a Entrañas."}},
[2358] = {"Cuernos de Nez'ra", nil, {"Lleva los Cuernos de Nez'ra a Arantir a Villa del Lago."}},
[2359] = {"La torre de Klaven", nil, {"Roba la Llave de la Torre Defias, entra en la Torre Defias y descubre qué contiene el Cofre del Bosque del Ocaso. Lleva toda la información que encuentres a Mathias Shaw de Ventormenta. Lee el diario de Kearnen para saber cómo acceder a la torre.|n"}},
[2360] = {"Mathias y los Defias", nil, {"Ve a los Páramos de Poniente y encuentra a la agente Kearnen para dar parte."}},
[2378] = {"Encuentra a los Mano Destrozada", nil, {"Viaja a Orgrimmar y habla con Shenthul, que estará en el Circo de las Sombras.|n"}},
[2379] = {"Zando'zan", nil, {"Habla con Zando'zan en el Circo de las Sombras."}},
[2381] = {"Saquear a los saqueadores", nil, {"Llévale el tesoro de los Mares del Sur a Desdio el Desdichado en Trinquete. No olvides conseguir una G.E.A.Q. y herramientas de ladrones del aparato Gizmotrónico de Wrenix. Necesitarás todo eso para completar tu misión.\n\nSi te atacaran unos loros anormalmente agresivos, ¡usa la G.E.A.Q.!|n|n"}},
[2382] = {"Desdio de Trinquete", nil, {"Ve a Trinquete, en Los Baldíos, y habla con Desdio el Desdichado."}},
[2383] = {"Papiro simple", nil, {"Lee el Pergamino simple y habla con Frang en el Valle de los Retos."}},
[2398] = {"Los enanos desaparecidos", nil, {"Encuentra a Baelog en Uldaman."}},
[2399] = {"Flora en brote", nil, nil},
[2418] = {"Las piedras de energía", nil, {"Llévale 8 piedras de energía de dentrio y 8 piedras de energía de An'Alleum a Aparejez en las Tierras Inhóspitas."}},
[2438] = {"El atrapasueños esmeralda", nil, {"Consigue el atrapasueños esmeralda."}},
[2439] = {"Los discos de platino", nil, {"Lleva tu vale de recompensa a Dinita Mantopétreo, que está en las Arcas de Forjaz."}},
[2440] = {"Los discos de platino", nil, {"Llévale este vale de recompensa a Bena Pezuña Invernal, que está en Cima del Trueno."}},
[2458] = {"Agente encubierto", nil, {"Ve a la torre de Ventura y Cía., al norte de El Fangal, y localiza al capataz Fizzule, el agente de Mano Destrozada. \n\nCuando veas la torre, usa la pistola de bengalas para avisar de tu llegada al capataz Fizzule. Lanza 2 bengalas y acércate al capataz para darle el /saludo de los Mano Destrozada. \n\nTienes que hacerlo en este orden, porque Fizzule te matará si no te reconoce."}},
[2459] = {"Ferocitas el Comesueños", nil, {"Mata a 7 místicos Tuercepinos y encuentra la joya perdida."}},
[2460] = {"El saludo de los Mano Destrozada", nil, {"Da el saludo de los Mano Destrozada a Shenthul."}},
[2478] = {"Misión: Posible, pero no probable", nil, {"Roba la llave de la torre del supervisor Silixiz, mata a 2 zánganos mutados de Ventura y Cía., 2 patrullas de Ventura y Cía. y 2 oteadores de Ventura y Cía.\n\nAsesina al gran supervisor Puzik Gallywix y quédate con su cabeza, fuerza la cerradura del arcón de Gallywix y roba la mezcla de Zanzil modificada que allí está oculta. Lleva el alijo y la cabeza de Gallywix a Shenthul en Orgrimmar, él te dará más instrucciones.|n"}},
[2479] = {"La ayuda de Hinott", nil, {"Ve a Molino Tarren, en Laderas de Trabalomas, para entregar la muestra de Zanzil a Serge Hinott. \n\nPara llegar a Molino Tarren, sube al zepelín que va a Entrañas y sigue el camino hacia el sur por el Bosque de Argénteos hacia Trabalomas. ¡Sigue las indicaciones!|n|n"}},
[2498] = {"Regresa junto a Denalan", nil, {"Rellian Verdeguja quiere que hables con Denalan, que está en el Lago Al'Ameth."}},
[2499] = {"Roblegüello", nil, {"Hazte con el tumor gigantesco de Roblegüello."}},
[2500] = {"Componentes de Tierras Inhóspitas", nil, {"Ve a las Tierras Inhóspitas a buscar los ingredientes que te ha pedido Ghak Sanadón y llévaselos a Thelsamar."}},
[2518] = {"Las lágrimas de la Luna", nil, {"Recupera las hileras de plata de Lady Sathrah."}},
[2521] = {"Al servicio de Kum'isha", nil, {"En tus viajes por Las Tierras Devastadas, si te encontraras con una esfera draenetista perfecta, llévasela a Kum'isha el Coleccionista.\n\nTen cuidado, esta gema es uno de los cristales más raros de todo Azeroth. Cualquier criatura de estas tierras podría tener una.\n\nSe te recompensará por cada esfera draenetista perfecta que consigas."}},
[2523] = {"Melodía corrupta", nil, {"Requisitos:"}},
