[2561] = {"El druida de la Zarpa", nil, {"Mata el cuerpo desalmado de Oben Garrafuria y después utiliza el talismán vudú."}},
[2581] = {"Papadas de hiena risitas", nil, {"Llévale 3 papadas de hiena risitas, 2 pulmones de jabalí maldito y 1 pinza de scorpok al mago sangriento Drazial."}},
