[4811] = {"El cristal rojo", nil, {"Viaja al este de Auberdine y busca un gran cristal rojo en la zona montañosa que hay al este de Costa Oscura. Vuelve a Auberdine e informa de lo que has encontrado a la centinela Glynda Nal'Shea."}},
[4812] = {"Agua en cascada", nil, {"Llena el tubo de agua vacío en la Poza de la Luna de Auberdine y luego examina el cristal rojo a lo largo de la pared montañosa oriental de Costa Oscura."}},
[4821] = {"Un huevo extraño", nil, {"Devuélvele el huevo extraño a Hagar Pezuña del Rayo, que está en el Poblado Viento Libre."}},
