[5021] = {"Más vale tarde que nunca", nil, {"Entre lamentos, la mujer fantasmal recordaba tener que entregar un paquete. Según ella estaba junto a su caballo. No puedes entender dónde está el caballo o dónde había que entregar el paquete."}},
[5022] = {"Más vale tarde que nunca", nil, {"Consulta con los agentes reales de Ventormenta para averiguar el paradero de una tal Emma Piedra Mácula. Suele haber un oficial del censo en el Ayuntamiento."}},
