[5055] = {"Brumerán de los Orvallo", nil, {"Ventisca Pezuña Umbría de Vista Eterna, en Cuna del Invierno, quiere que mates a Brumerán."}},
[5056] = {"Shi-Rotam", nil, {"Ventisca Pezuña Umbría de Vista Eterna, en Cuna del Invierno, quiere que acabes con Shi-Rotam."}},
[5061] = {"Forma acuática", nil, {"Vuelve a Darnassus y enséñale el colgante del león marino a Mathrengyl Garroso."}},
[5062] = {"El fuego Sagrado", nil, {"Recoge 10 pitas incendiarias y después pide consejo a Magatha Tótem Siniestro en el Alto de los Ancestros de Cima del Trueno."}},
[5063] = {"Almete del Sabio Escarlata", nil, {"Para crear el Almete del Sabio Escarlata deberás llevarle los siguientes objetos a Malyfous Martilloscuro: 1 pellejo de la Bestia prístino; 5 costuras desgarradas de abominación; 8 cristales Arcanos; 5 hilos escarlata encantados.\n\nTambién tendrás que pagar 50 piezas de oro para la elaboración del almete."}},
