[5084] = {"[DEPRECATED]Sucumbir a la corrupción", nil, {"Busca una pista en el campamento fúrbolg Muertobosque en el norte de Frondavil."}},
[5085] = {"[DEPRECATED]Baba misteriosa", nil, {"Informa sobre tus descubrimientos a Donova Cubilnevado."}},
[5086] = {"[DEPRECATED]Horrores tóxicos", nil, {"Recoge 3 gotitas de horror tóxico para Donova Cubilnevado de Cuna del Invierno."}},
[5087] = {"[DEPRECATED]Corredores Nevada", nil, {"Encuentra a los corredores Nevada y detenlos antes de que entreguen su cargamento en Poblado Nevada."}},
[5088] = {"Arikara", nil, {"Mata a Arikara. Llévale sus restos y el polvo de incendiaria a Motega Pirocabello al Campamento del Tramo Blanco como prueba de tus actos."}},
[5089] = {"Orden del general Drakkisath", nil, {"Llévale la orden del general Drakkisath al mariscal Maxwell en Las Estepas Ardientes."}},
[5091] = {"Un llamamiento a las armas: las Tierras de la Peste", nil, {"Busca al comandante Ashlam Puñovalor. Su campamento base se encuentra en Campamento del Orvallo, al norte de las Montañas de Alterac."}},
[5092] = {"Despejar el camino", nil, {"Mata 10 despellejadores esqueléticos y 10 necrófagos esclavizantes en la Colina de las Penas.\n\nVuelve con el comandante Ashlam Puñovalor en el Campamento del Orvallo, en las Tierras de la Peste del Oeste."}},
[5093] = {"Un llamamiento a las armas: las Tierras de la Peste", nil, {"Busca al sumo ejecutor Derrington; su campamento base está en El Baluarte, al este de Claros de Tirisfal y Entrañas."}},
[5094] = {"Un llamamiento a las armas: las Tierras de la Peste", nil, {"Busca al sumo ejecutor Derrington; su campamento base está en El Baluarte, al este de Claros de Tirisfal y Entrañas."}},
[5095] = {"Un llamamiento a las armas: las Tierras de la Peste", nil, {"Busca al sumo ejecutor Derrington; su campamento base está en El Baluarte, al este de Claros de Tirisfal y Entrañas."}},
[5096] = {"Distracción Escarlata", nil, {"Adéntrate en el campamento base de la Cruzada Escarlata entre el Campo de Piedramácula y el Llanto de Dalson y destruye su tienda de mando.\n\nColoca el estandarte de la Plaga en el campamento y vuelve con el sumo ejecutor Derrington en El Baluarte en las Tierras de la Peste del Oeste."}},
[5097] = {"Todas las atalayas", nil, {"Con la antorcha de aviso, marca las atalayas de Andorhal. Tendrás que estar en la entrada de la atalaya para poder marcarla.\n\nCuando estén marcadas las cuatro atalayas devuelve la antorcha de aviso al comandante Ashlam Puñovalor del Campamento del Orvallo, en las Tierras de la Peste del Oeste."}},
[5098] = {"Todas las atalayas", nil, {"Con la antorcha de aviso, marca las atalayas de Andorhal. Tendrás que estar en la entrada de la atalaya para poder marcarla.\n\nCuando estén marcadas las cuatro atalayas, devuelve la antorcha de aviso al sumo ejecutor Derrington en El Baluarte, en las Tierras de la Peste del Oeste."}},
[5101] = {"Misión de prueba suprema de Lee... ¡apocalíptica!", nil, {"Mata 6 ovejas para Benny ¡y corre como el demonio!\n\n\n\n\nEs una broma... lo de correr como un demonio."}},
[5102] = {"Muerte al general Drakkisath", nil, {"Viaja hasta la Cumbre de Roca Negra y mata al general Drakkisath."}},
