[5305] = {"Dulce serenidad", nil, {"Viaja a Stratholme y mata al forjamartillos Carmesí. Consigue el delantal del forjamartillos Carmesí y regresa con Lilith.|n"}},
[5306] = {"Piedra culebra de la cazadora de las Sombras", nil, {"Viaja a la Cumbre de Roca Negra y mata a la cazadora de las Sombras Vosh'gajin. Recupera la piedra culebra de Vosh'gajin y vuelve con Kilram."}},
[5307] = {"Corrupción", nil, {"Encuentra al armero Guardia Negra en Stratholme y acaba con él. Recupera la Insignia de La Guardia Negra y regresa con Seril Finiquiplaga."}},
[5321] = {"El despertar", nil, {"Escolta a Kerlonian Semprumbrío hasta Liladris Rioluna en la Atalaya de Maestra de Vallefresno."}},
[5341] = {"La fortuna de la familia Barov", nil, {"Aventúrate a Scholomance y recupera la fortuna familiar de los Barov. La fortuna se compone de cuatro escrituras: Las escrituras de Castel Darrow, Las escrituras de Rémol, Las escrituras de Molino Tarren y Las escrituras de Costasur. Regresa con Alexi Barov cuando hayas terminado esta tarea."}},
[5342] = {"El último de los Barov", nil, {"Viaja al Campamento del Orvallo, territorio de la Alianza, y asesina a Weldon Barov. Coge su cabeza y regresa con Alexi Barov.\n\nTen cuidado, Alexi seguramente estará muy protegido por la escoria de la Alianza."}},
[5343] = {"La fortuna de la familia Barov", nil, {"Aventúrate a Scholomance y recupera la fortuna familiar de los Barov. La fortuna se compone de cuatro escrituras: Las escrituras de Castel Darrow, Las escrituras de Rémol, Las escrituras de Molino Tarren y Las escrituras de Costasur. Regresa con Weldon Barov cuando hayas terminado esta tarea."}},
[5344] = {"El último de los Barov", nil, {"Viaja a El Baluarte, territorio de la Horda, y asesina a Alexi Barov. Coge su cabeza y regresa con Weldon Barov.\n\nTen cuidado, Alexi seguramente estará muy protegido por la escoria de la Horda."}},
[5361] = {"Árbol genealógico", nil, {"Entrégale el informe de Vigiarisco Cuerno Largo a Nataka Cuerno Largo que se encuentra en el Campamento del Espíritu Errante de Desolace."}},
[5381] = {"La Mano de Iruxos", nil, {"Extrae la caja de demonio de la Fortaleza del Hacha de Trueno y llévasela a Taiga Crin Sabia a la Aldea Cazasombras junto con el pico de demonio.|n"}},
[5382] = {"Doctor Theolen Krastinov, el Carnicero", nil, {"Busca al doctor Theolen Krastinov en el interior de Scholomance. Acaba con él y quema los restos de Eva Sarkhoff y los restos de Lucien Sarkhoff. Cuando hayas terminado tu tarea regresa con Eva Sarkhoff.|n"}},
[5384] = {"Kirtonos el Heraldo", nil, {"Vuelve a Scholomance con la sangre inocente. Busca el Porche y coloca la Sangre de los inocentes en el blandón. Kirtonos acudirá a devorar tu alma.\n\nLucha con valentía, ¡no cedas ni una pulgada! Destruye a Kirtonos y vuelve con Eva Sarkhoff.|n"}},
[5386] = {"La pesca del día", nil, {"Habla con Jinar'Zillen, en la Aldea Cazasombras, y luego lleva 2 panzasangres a Nataka Cuerno Largo."}},
[5421] = {"Pescado en un balde", nil, {"Jinar'Zillen quiere que recojas 5 mariscos de las trampas que hay cerca de la Aldea Cazasombras."}},
[5441] = {"Peones vagos", nil, {"Utiliza la Cachiporra del supervisor con los peones vagos que estén durmiendo. Despierta a 5 peones y devuélvele la cachiporra al supervisor Thazz'ril en el Valle de los Retos."}},
[5461] = {"El humano, Ras Murmuhielo", nil, {"Viaja a las Tierras Altas de Arathi, a las Ruinas de Stromgarde. En Stromgarde busca un Libro de los Recuerdos. Si lo encuentras llévaselo al magistrado Marduke."}},
[5463] = {"El Obsequio de Menethil", nil, {"Viaja a Stratholme y encuentra el Obsequio de Menethil. Coloca el Libro de los Recuerdos en suelo no consagrado."}},
[5465] = {"El Libro de Memorias del Alma", nil, {"Vuelve con el magistrado Marduke a Castel Darrow. Muéstrale el Libro de Memorias del Alma."}},
[5466] = {"El exánime, Ras Murmuhielo", nil, {"Busca a Ras Murmuhielo en Scholomance. Cuando lo hayas encontrado utiliza el Libro de Memorias del Alma sobre su rostro no-muerto. Si consiguieras convertirlo en mortal, derrótale y recupera la Cabeza humana de Ras Murmuhielo. Lleva la cabeza al magistrado Marduke."}},
[5481] = {"La tarea de Gordo", nil, {"Recoge 3 hierbas luminiscentes y entrégaselas al boticario auxiliar Holland en el cementerio de Rémol."}},
[5482] = {"Apocalíseas", nil, {"Recoge 10 apocalíseas y entrégaselas al boticario auxiliar Holland.|n"}},
[5501] = {"Recolección de kodos", nil, {"Lleva 10 huesos de kodo del Cementerio de Kodos a Bibbly F'utzhebilla a la Cabaña de Kormek."}},
