[6582] = {"La prueba de los cráneos, Arúspice", nil, {"Debes encontrar a Arúspice, el Campeón draco del Vuelo Azul y matarlo. Separa su cráneo de su cuerpo y llévaselo a Brasaliza.\n\nSabes que puedes encontrar a Arúspice en Cuna del Invierno."}},
[6583] = {"La prueba de los cráneos, Somnus", nil, {"Destruye al Campeón del Vuelo Verde, Somnus. Lleva su cráneo a Brasaliza."}},
[6584] = {"La prueba de los cráneos, Chronalis", nil, {"Chronalis, hijo de Nozdormu, custodia las Cavernas del Tiempo en el Desierto de Tanaris. Destrúyelo y lleva su cráneo a Brasaliza."}},
[6602] = {"La sangre de dragón Negro Campeón", nil, {"Viaja a la Cumbre de Roca Negra y mata al general Drakkisath. Recoge su sangre y llévasela a Rokaro."}},
