[6607] = {"Nat Pagle, pescador extremo", nil, {"Nat Pagle quiere que pesques los siguientes peces:\n\nMahi mahi juncobruma de la Playa Juncobruma del Pantano de las Penas.\n\n1 artillero Sar'theris de la Playa de Sar'theris de Desolace.\n\n1 Ahi de Feralas del Río Verdantis de Feralas.\n\nLatipinia azul de La Costa Salvaje de Vega de Tuercespina.\n\nCuando los tengas todos, vuelve a buscar a Nat Pagle."}},
[6608] = {"¡Qué bueno eres!", nil, {"Parece que Lumak piensa que ya no puede enseñarte nada más sobre la pesca. Ve a buscar a su amigo, Nat Pagle, un humano pescador que vive en una isla al sur de Theramore (o eso parece que ha dicho)."}},
[6609] = {"¡Me has dejado seco!", nil, {"Ve a Kalimdor, a la ciudad costera de Theramore. Busca una isla hacia el sur. ¡Nat Pagle te espera!"}},
[6610] = {"Sorpresa de almejate", nil, {"Dirge Hojágil quiere que le consigas lo siguiente:\n\n10 huevos gigantes. Pueden ser huevos de los rocs que deambulan por Tanaris o de cualquier otro pájaro de grandes dimensiones.\n\n10 piezas de carne de almeja sabrosa. ¡Esta la podrás obtener de las almejas, por supuesto!\n\n20 piezas de queso cheddar curado bueno. ¡Podrás encontrarlo en cualquier quesería!\n\nConsigue todo lo que Dirge necesita y llévaselo."}},
[6611] = {"Ve a Gadgetzan", nil, {"Ve a Gadgetzan, en el Desierto de Tanaris, y habla con Dirge Hojágil sobre tus habilidades culinarias."}},
