[7062] = {"El espíritu de esta fiesta", nil, {"Habla con Karnik el Historiador del festín del Festival de Invierno en la Liga de Expedicionarios de Forjaz."}},
[7064] = {"Corrupción de la tierra y de la semilla", nil, {"Mata a la princesa Theradras y ve a ver a Selendra cerca de la Aldea Cazasombras, en Desolace."}},
[7065] = {"Corrupción de la tierra y de la semilla", nil, {"Mata a la princesa Theradras y ve a ver al vigilante Marandis a Punta de Nijel, en Desolace."}},
[7066] = {"La semilla de vida", nil, {"Busca a Remulos en Claro de la Luna y dale la semilla de vida."}},
[7067] = {"Las instrucciones del Paria", nil, {"Lee las instrucciones del Paria. Busca el Amuleto de Unidad en Maraudon y llévaselo al sur de Desolace."}},
[7068] = {"Trozos Oscuros", nil, {"Recoge 10 fragmentos oscuros en Maraudon y llévaselos a Uthel'nay a Orgrimmar."}},
