[7081] = {"Los cementerios del Valle de Alterac", nil, {"Asalta un cementerio y vuelve con el sargento Durgen Pico Tormenta en las Montañas de Alterac."}},
[7082] = {"Los cementerios de Alterac", nil, {"Asalta un cementerio y vuelve con el cabo Teeka Gruñido Sangriento en las Montañas de Alterac."}},
[7101] = {"Las torres y los búnkeres", nil, {"Captura una torre enemiga y vuelve con el cabo Teeka Gruñido Sangriento en las Montañas de Alterac."}},
[7102] = {"Las torres y los búnkeres", nil, {"Destruye el estandarte de una torre enemiga o de un búnker y vuelve junto al sargento Durgen Pico Tormenta en las Montañas de Alterac."}},
[7121] = {"El intendente", nil, {"Habla con el intendente Pico Tormenta."}},
[7122] = {"Capturar una mina", nil, {"Captura una mina que no esté bajo control de los Pico Tormenta y vuelve junto al sargento Durgen Pico Tormenta en las Montañas de Alterac."}},
[7123] = {"Habla con nuestro intendente", nil, {"Habla con el intendente de Lobo Gélido."}},
[7124] = {"Capturar una mina", nil, {"Captura una mina y vuelve con el cabo Teeka Gruñido Sangriento en las Montañas de Alterac."}},
[7141] = {"La batalla de Alterac", nil, {"Adéntrate en el Valle de Alterac, derrota al general Drek'thar de la Horda y vuelve junto a la prospectora Tallapiedra en las Montañas de Alterac."}},
[7142] = {"La batalla por Alterac", nil, {"Adéntrate en el Valle de Alterac y derrota al general enano Vanndar Pico Tormenta. Vuelve entonces junto a Voggah Agarre Letal en las Montañas de Alterac."}},
[7161] = {"Terreno de Pruebas", nil, {"Viaja hasta la caverna Zarpa Salvaje, al sureste del campamento base en el Valle de Alterac y encuentra el estandarte Lobo Gélido. Devuélveselo al maestro de guerra Laggrond. "}},
[7162] = {"Terreno de Pruebas", nil, {"Viaja hasta la cueva Ala Gélida ubicada al suroeste de Dun Baldar en el Valle de Alterac y recupera el estandarte Pico Tormenta. Devuélveselo al teniente Haggerdin en las Montañas de Alterac."}},
