[8460] = {"[DEPRECATED]Aliado Fauces de Madera", nil, {"Grazle quiere que demuestres tu valía matando a 6 guerreros Muertobosque, 6 abrecaminos Muertobosque y 6 jardineros Muertobosque. Cuando lo hayas hecho, vuelve con él al sur de Frondavil, cerca del Santuario Esmeralda."}},
[8461] = {"[DEPRECATED]Los Muertobosque del Norte", nil, {"Nafien quiere que mates a 6 vigías del cubil Muertobosque, 6 vengadores Muertobosque y 6 chamanes Muertobosque. Luego, vuelve a buscarlo al norte de Frondavil, cerca de la entrada del Bastión Fauces de Madera."}},
[8462] = {"[DEPRECATED]Habla con Nafien", nil, {"Viaja hacia el norte por el camino principal de Frondavil y habla con el fúrbolg Nafien. Está haciendo guardia en la entrada del Bastión Fauces de Madera."}},
[8463] = {"Cristales de maná inestables", nil, {"Lleva 6 cristales de maná inestables a Aeldon Marcasol a la Plaza Alalcón."}},
[8464] = {"[DEPRECATED]Actividad de los Nevada", nil, {"Salfa quiere que mates 8 chamanes Nevada, 8 vigías del cubil Nevada y 8 ursas Nevada. Salfa está a las puertas del Bastión Fauces de Madera en Cuna del Invierno.|n"}},
[8465] = {"[DEPRECATED]Habla con Salfa", nil, {"Viaja por el Bastión Fauces de Madera y sal a Cuna del Invierno. Habla con Salfa, que está haciendo guardia en la entrada del Bastión Fauces de Madera."}},
[8468] = {"SE BUSCA: Thaelis el Hambriento", nil, {"Lleva la cabeza de Thaelis al sargento Kan'ren a la Plaza Alalcón, en el Bosque Canción Eterna."}},
[8470] = {"El tótem de ritual Muertobosque", nil, {"Lleva el tótem de ritual Muertobosque al interior del Bastión Fauces de Madera. A ver si alguno de los fúrbolgs que hay en él sabe dar uso al objeto. Los Fauces de Madera no hablarán contigo si no tienes un nivel de reputación neutral o superior con ellos."}},
[8471] = {"Tótem de ritual Nevada", nil, {"Lleva el tótem de ritual Nevada al Bastión Fauces de Madera y averigua si alguno de los fúrbolgs sabe de su utilidad. Los Fauces de Madera no hablarán contigo si tu reputación con ellos no es como mínimo de neutral."}},
[8472] = {"Avería grave", nil, {"Coge 6 núcleos arcanos de los patrulleros Arcanos que recorren las Ruinas de Ciudad de Lunargenta y llévaselos al magister Jaronis a la Plaza Alalcón en el Bosque Canción Eterna."}},
[8473] = {"Una tarea dolorosa", nil, {"Mata a 10 jardineros marchitos en La Arboleda Agostada y ve a informar a Larianna Ríobrisa en la torre que está al noroeste de La Arboleda Agostada en el Bosque Canción Eterna."}},
[8474] = {"El colgante del viejo Cortezablanca", nil, {"Encuentra al artesano que fabricó el colgante del viejo Cortezablanca. Quizás pueda darte información sobre el objeto."}},
[8475] = {"La Cicatriz Muerta", nil, {"Mata a 8 saqueadores huesopeste e informa a la forestal Jaela en La Cicatriz Muerta, en el Bosque Canción Eterna."}},
[8476] = {"Asentamiento Amani", nil, {"La teniente Correalba de El Retiro del Errante en el Bosque Canción Eterna quiere que mates a 5 rabias y 5 lanzahachas Amani."}},
[8477] = {"El martillo del maestro de lanzas", nil, {"Ve a Tor'Watha, al sureste de El Retiro del Errante, para buscar al maestro de lanzas Otembe y lleva el martillo de Otembe a Arathel Forjasol a El Retiro del Errante en el Bosque Canción Eterna."}},
[8479] = {"Zul'Marosh", nil, {"Ven'jashi, el prisionero trol de Tor'Watha, quiere que le lleves la cabeza del jefe Zul'Marosh, que está en Zeb'Watha, al otro lado del Lago Elrendar."}},
[8480] = {"El armamento perdido", nil, {"Lleva 8 armas sin'dorei a Velendris Albodía al Bosque Canción Eterna."}},
[8481] = {"La raíz de todos los males", nil, {"Clava la antorcha para invocar demonios en la entrada de la cueva de Grolnar en el Poblado Nevada de los fúrbolgs. Derrota al demonio y consigue la esencia de Xandivious para Monóculo de Gorn en el Bastión Fauces de Madera."}},
[8482] = {"Documentos incriminadores", nil, {"Lleva los documentos incriminadores a Aeldon Marcasol a la Plaza Alalcón en el Bosque Canción Eterna."}},
[8483] = {"El espía enano", nil, {"Lleva la cabeza del prospector Guardayunque a Aeldon Marcasol a la Plaza Alalcón, en el Bosque Canción Eterna."}},
[8484] = {"Negociar la paz", nil, {"Llévale la ofrenda de paz de los Fauces de Madera al rey Magni Barbabronce de Forjaz."}},
[8486] = {"Inestabilidad arcana", nil, {"La vigilante-Ley Velania quiere que mates 5 espectros de maná y 5 acechadores de maná."}},
[8487] = {"Tierra corrupta", nil, {"La aprendiza Mirveda del Sagrario del Este en el Bosque Canción Eterna quiere que le lleves 8 muestras de tierra mácula."}},
[8488] = {"Resultados inesperados", nil, {"Protege a la aprendiza Mirveda de la emboscada de la Plaga."}},
[8490] = {"Reforzando las defensas", nil, {"Pon el cristal imbuido en la Piedra rúnica del este y protégelo de la Plaga durante 1 minuto. Devuélvele el cristal al celador de las runas Deryan en el Bosque Canción Eterna y tendrás una recompensa."}},
[8491] = {"Caza de pelambres", nil, {"Velan Roblebrillante de la Aldea Brisa Pura en el Bosque Canción Eterna quiere que le lleves 6 pelambres de garragil."}},
[8492] = {"¡La Alianza necesita lingotes de cobre!", nil, {"Llévale 20 lingotes de cobre al sargento Petrocejas en La Sala Militar de Forjaz."}},
[8494] = {"¡La Alianza necesita barras de hierro!", nil, {"Llévale 20 barras de hierro al cabo Carnes en La Sala Militar de Forjaz."}},
[8499] = {"¡La Alianza necesita barras de torio!", nil, {"Llévale 20 barras de torio a Dama Trenzado en La Sala Militar de Forjaz."}},
[8501] = {"Objetivo: los aguijoneros Colmen'Ashi", nil, {"Mata 30 aguijoneros Colmen'Ashi y preséntate ante el comandante Mar'alith en el Fuerte Cenarion, en Silithus. También debes llevar el Informe de tareas de combate XII para poder completar la misión.|n"}},
