[8785] = {"¡La Legión de Orgrimmar necesita mojo!", nil, {"Lleva 6 mojos potentes, 6 frascos de gran mojo y 8 aceites de inmolación a la sacerdotisa de las Sombras Shai cerca de Colmen'Regal, en Silithus. También debes llevar el Informe de tareas logísticas IV para poder completar la misión."}},
[8787] = {"Refuerzos para armadura para el campo de batalla", nil, {"Lleva 8 refuerzos para armadura bastos y 8 refuerzos para armadura pesados a Merok Zancada Larga, cerca de Colmen'Regal. También debes llevar el Informe de tareas logísticas VII para poder completar la misión."}},
[8788] = {"Un obsequio ligeramente agitado", nil, nil},
[8789] = {"Armamentos imperiales qiraji", nil, {"Arygos en el Templo de Ahn'Qiraj creará armamento imbuido de elementium para ti si le llevas armamento imperial qiraji y 3 lingotes de elementium."}},
