[8921] = {"El destilador ectoplásmico", nil, {"Vuelve a ver a Mux Cazamaná a Gadgetzan cuando tengas 1 conversor de arcanita delicado, 4 esencias eternas superiores, 10 unidades de aceite de escama pétrea, 25 cenizas volcánicas y 40 piezas de oro."}},
[8924] = {"Caza de ectoplasmas", nil, {"Utiliza el destilador ectoplásmico cerca de los no-muertos incorpóreos y consigue 12 ectoplasmas agostados en Silithus, 12 ectoplasmas helados en Cuna del Invierno y 12 ectoplasmas estables en las Tierras de la Peste del Este. Llévaselos, junto con el destilador ectoplásmico, a Mux Cazamaná a Gadgetzan."}},
[8925] = {"Fuente de energía portátil", nil, {"Encuentra al Señor del Magma Bokk en Las Estepas Ardientes, consigue el núcleo de arrabio y llévaselo a Mux Cazamaná a Gadgetzan."}},
[8945] = {"La súplica de un muerto", nil, {"Ve a Stratholme y rescata a Ysida Harmon del Barón Osahendido."}},
[8947] = {"La extraña petición de Anthion", nil, {"Lleva 3 barras de hierro negro, 20 retales de cuero encantado, 3 de tela lunar y 4 pellejos bastos curtidos a Anthion Harmon a las Tierras de la Peste del Este."}},
[8949] = {"La vendetta de Falrin", nil, {"Reúne 25 abalorios de guerra de ogro de los ogros de La Masacre o de la Cumbre de Roca Negra y vuelve a El Athenaeum de La Masacre a hablar con Falrin Tallarbol."}},
[8953] = {"La despedida de Anthion", nil, {"Lleva 1 par de botas, 1 manto y 1 par de leotardos de magister a Deliana a Forjaz."}},
[8960] = {"El triste destino de Bodley", nil, {"Viaja hasta la Montaña Roca Negra y usa el detector de fantasmas extradimensionales para encontrar a Bodley cerca de la Cumbre de Roca Negra."}},
[8961] = {"Tres reyes de Fuego", nil, {"Recolecta la incendicita de Incendius, el ascua del Brasadivino y el rescoldo de las brasas junto con el blandón sagrado y llévaselos a Bodley en el interior de la Montaña Roca Negra."}},
[8968] = {"La parte izquierda del amuleto de Lord Valthalak", nil, {"Usa el Blandón de Señalización para invocar al espíritu de Jarien y Sothos y mátalos. Vuelve junto a Bodley en el interior de la Montaña Roca Negra con la parte izquierda del amuleto de Lord Valthalak y el Blandón de Señalización."}},
