[9033] = {"Ecos de Guerra", nil, {"El comandante Eligor Albar de la Capilla de la Esperanza de la Luz, en las Tierras de la Peste del Este, quiere que mates 5 monstruosidades vivientes, 5 gárgolas piel de piedra, 8 capitanes caballero de la Muerte y 3 acechadores venenosos."}},
[9034] = {"La coraza acorator", nil, {"Korfax de la Capilla de la Esperanza de la Luz, en las Tierras de la Peste del Este, te hará una coraza acorator si le llevas lo siguiente: 1 coraza profanada, 25 restos de coraza, 4 barras de arcanita y 2 cristales nexo.|n"}},
[9035] = {"Asaltantes de caminos", nil, {"Habla con el aprendiz Meledor en el Bosque Canción Eterna en el camino hacia el sur desde Ciudad de Lunargenta."}},
[9036] = {"Los quijotes acorator", nil, {"Korfax de la Capilla de la Esperanza de la Luz, en las Tierras de la Peste del Este, te hará unos quijotes acorator si le llevas lo siguiente: 1 par de musleras profanadas, 20 restos de coraza, 4 barras de arcanita y 3 pellejos bastos curtidos."}},
[9040] = {"Los guanteletes acorator", nil, {"Korfax de la Capilla de la Esperanza de la Luz, en las Tierras de la Peste del Este, te hará unos guanteletes acorator si le llevas lo siguiente: 1 par de guanteletes profanados, 8 restos de coraza, 1 barra de arcanita y 5 pellejos bastos curtidos."}},
[9042] = {"Los brazales acorator", nil, {"Korfax de la Capilla de la Esperanza de la Luz, en las Tierras de la Peste del Este, te hará unos brazales acorator si le llevas lo siguiente: 1 par de brazales profanados, 6 restos de coraza, 1 barra de arcanita y 1 cristal nexo."}},
[9052] = {"Veneno de Sangrepétalo", nil, {"Reúne 8 champiñones de sangre y 8 aguijones Gorishi, y ve a ver de nuevo a Torwa Abrecaminos al Cráter de Un'Goro."}},
[9053] = {"Un ingrediente mejor", nil, {"Consigue una vid pútrida de las que custodia el guardián en las profundidades del Templo Sumergido y después ve a ver de nuevo a Torwa Abrecaminos."}},
[9054] = {"La guerrera de acechacriptas", nil, {"El montero Leopold de la Capilla de la Esperanza de la Luz, en las Tierras de la Peste del Este, te hará una guerrera de acechacriptas si le llevas lo siguiente: 1 guerrera profanada, 25 restos de malla, 4 barras de arcanita y 3 pellejos bastos curtidos."}},
