[9060] = {"La faja de acechacriptas", nil, {"El montero Leopold de la Capilla de la Esperanza de la Luz, en las Tierras de la Peste del Este, te hará una faja de acechacriptas si le llevas lo siguiente: 1 faja profanada, 8 restos de malla, 1 barra de arcanita y 3 cristales nexo.|n"}},
[9062] = {"Papel mojado", nil, {"El aprendiz Meledor del Bosque Canción Eterna quiere que busques el grimorio elemental de Antheol en las aguas bajo el puente."}},
[9063] = {"Torwa Abrecaminos", nil, {"Habla con Torwa Abrecaminos en el Cráter de Un'Goro."}},
[9064] = {"Asumiendo la responsabilidad", nil, {"Lleva el grimorio elemental de Antheol al instructor Antheol a la Charca Plácido Susurro en el Bosque Canción Eterna."}},
[9065] = {"Otra especial de Patty", nil, {"¡Destruye esa torre!"}},
[9066] = {"La letra con sangre entra", nil, {"Usa la vara de castigo de Antheol en el aprendiz Ralen y el aprendiz Meledor. Devuélvele la vara a Antheol en Charca Plácido Susurro en Bosque Canción Eterna."}},
[9067] = {"Fiesta interminable", nil, {"Compra 1 botella de Toquesol especial, aperitivos de garragil y 1 paquete de fuegos artificiales y llévaselo todo a Lord Saltheril al Refugio de Saltheril en el Bosque Canción Eterna."}},
