[9634] = {"Depredadores alienígenas", nil, {"Mata 10 proles Bruma de Sangre y vuelve a hablar con Vorkhan el pastor de elekks en la Encrucijada de Kessel en la Isla Bruma de Sangre."}},
[9635] = {"¡El extractor de motas Zapacelerador!", nil, {"Llévale 15 piezas de bomba de vapor a Mack Diver en Zabra'jin, en la Marisma de Zangar."}},
[9636] = {"¡El extractor de motas Zapacelerador!", nil, {"Llévale 15 piezas de bomba de vapor a K. Lee Fritito en Telredor, en la Marisma de Zangar."}},
[9637] = {"La petición de Kalynna", nil, {"Kalynna Lathrojo quiere recuperar el Escrito del Ocaso del brujo supremo Malbisal en Las Salas Arrasadas de la Ciudadela del Fuego Infernal y el Libro de los Nombres Olvidados de Tejeoscuro Syth en las Salas Sethekk de Auchindoun.\n\nEsta misión debe ser completada en dificultad heroica."}},
[9638] = {"En buenas manos", nil, {"Habla con Gradav en la Biblioteca del Guardián, en Karazhan."}},
[9639] = {"Kamsis", nil, {"Habla con Kamsis en la Biblioteca del Guardián, en Karazhan."}},
[9640] = {"La sombra de Aran", nil, {"Consigue el diario de Medivh y vuelve junto a Kamsis en la Biblioteca del Guardián, en Karazhan."}},
[9641] = {"Fragmentos de cristal irradiado", nil, {"El vindicador Boros de la Avanzada de Sangre quiere que le lleves diez fragmentos de cristal radioactivo."}},
[9642] = {"Más fragmentos de cristal irradiado", nil, {"Requisitos:"}},
[9643] = {"Parras constrictoras", nil, {"El rastreador Lyceon en la Avanzada de Sangre quiere que le lleves 6 parras constrictoras espinosas."}},
[9644] = {"Nocturno", nil, {"Ve a El Bancal del Maestro en Karazhan y usa la urna ennegrecida para invocar a Nocturno. Recupera la esencia arcana intangible del cadáver de Nocturno y llévasela al archimago Alturus."}},
[9645] = {"El Bancal del Maestro", nil, {"Ve a El Bancal del Maestro en Karazhan y lee el diario de Medivh."}},
[9646] = {"SE BUSCA: Garraletal", nil, {"Lleva la garra de Garraletal al presagista Mikolaas en la Avanzada de Sangre."}},
[9647] = {"Sacrificar a los aleteadores", nil, {"El rastreador Lyceon en la Avanzada de Sangre quiere que mates 10 aleteadores azul real."}},
[9648] = {"La colección de champiñones de Maatparm", nil, {"Maatparm en la Avanzada de Sangre quiere 1 cuernohediondo acuático, 1 champiñón sanguino, 1 poliespora ruinosa y 1 hongo de bellota vil."}},
[9649] = {"Las lágrimas de Ysera", nil, {"Maatparm en la Avanzada de Sangre quiere 2 lágrimas de Ysera."}},
[9663] = {"La carrera de Kessel", nil, {"Kessel de la Encrucijada de Kessel, en la Isla Bruma de Sangre, quiere que avises al gran jefe Semprepino del Bastión Semprepino, al exarca Menelaous de la Avanzada Azur y al almirante Odesyus del Desembarco de Odesyus sobre la inminente invasión de los elfos de sangre. Tienes 15 minutos. Regresa junto a Kessel cuando hayas completado esta tarea. (Asegúrate de que no toque el agua)."}},
[9664] = {"Nuevas avanzadas", nil, {"Captura la Torre de la Corona, la Torre de la Muralla del Este, la Torre del Paso Norte y la Torre del Bosque de la Peste en las Tierras de la Peste del Este. Tras hacerlo, regresa y habla con el Emisario Barbablanca en la Capilla de la Esperanza de la Luz."}},
[9665] = {"Fortalecimiento de nuestras defensas", nil, {"Captura la Torre de la Corona, la Torre de la Muralla del Este, la Torre del Paso Norte y la Torre del Bosque de la Peste en las Tierras de la Peste del Este. Tras hacerlo, regresa y habla con el Emisario Gormok en la Capilla de la Esperanza de la Luz."}},
[9666] = {"Declaración de poder", nil, {"Kessel de la Encrucijada de Kessel, en la Isla Bruma de Sangre, quiere que mates a Lord Xiz, de la Guarida Escama de Cólera, y atravieses su cadáver con el estandarte draenei."}},
[9667] = {"Salvar a la princesa Semprepino", nil, {"Libera a la Princesa Semprepino de su jaula y habla después con el embajador Semprepino Olorg, en la Avanzada de Sangre.\n\nTienes que matar al gran jefe Erizapúas para conseguir la llave de la jaula."}},
[9668] = {"Informar al exarca Admetius", nil, {"Habla con el exarca Admetius en la Avanzada de Sangre."}},
[9669] = {"La expedición desaparecida", nil, {"Mata 8 hiladoras de bruma, 8 parasitadoras de bruma y a su líder, Zarakh. Vuelve a ver a Achelus en la Avanzada de Sangre cuando hayas completado la tarea."}},
[9670] = {"¡Viven! A lo mejor...", nil, {"El investigador Cornelius ha pedido que liberes 5 investigadores de la expedición."}},
[9671] = {"Entrega urgente", nil, nil},
[9672] = {"El legado de la Sangre Maldita", nil, {"El almirante Odesyus te ha pedido que encuentres al capitán Edward Hanes y hables con él."}},
