[9970] = {"El Círculo de Sangre: Rokdar el Señor Hendido", nil, {"Derrota a Rokdar el Lord Hendido. Si lo logras, habla con Wodin, el sirviente trol, para recibir tu recompensa."}},
[9971] = {"Pistas en el Matorral", nil, {"Investiga el objeto extraño cerca del cadáver de Tábido para intentar averiguar lo que ocurrió en el Matorral Cenarion. Vuelve a ver al vinculador terrestre Tavgren al salir del matorral en el Bosque de Terokkar."}},
[9972] = {"El Círculo de Sangre: Skra'gath", nil, {"Derrota a Skra'gath. Si lo logras, habla con Wodin, el sirviente trol, para recibir tu recompensa."}},
[9973] = {"El Círculo de Sangre: el campeón Mazo de Guerra", nil, {"Derrota al campeón Mazo de Guerra. Si lo logras, habla con Wodin, el sirviente trol, para recibir tu recompensa."}},
[9977] = {"El Círculo de Sangre: el desafío final", nil, {"Derrota a Mogor, héroe de los Mazo de Guerra. Si lo logras, habla con Wodin, el sirviente trol, para recibir tu recompensa."}},
[9978] = {"Cueste lo que cueste", nil, {"Busca a Empoor en la carretera entre Tuurem y Shattrath, en el Bosque de Terokkar. Convéncele para que te cuente lo que sabe."}},
[9979] = {"Comerciante de viento Lathrai", nil, {"Habla con el comerciante de viento Lathrai cerca de la Taberna del Fin del Mundo en la sección del Bajo Arrabal de la Ciudad de Shattrath."}},
[9982] = {"Se hacía llamar Altruis...", nil, {"Si quieres luchar contra la Legión Ardiente, encuentra a Altruis el Sufridor en la parte oeste de Nagrand."}},
