[9993] = {"Aceite de semilla de olemba", nil, {"Lleva 30 semillas de olemba a Rakoria, en el Bastión Rompepedras."}},
[9994] = {"¿Qué son estas cosas?", nil, {"Lleva la caja de piezas a Jenai Susurrestelar en el Bastión Allerian, en el Bosque de Terokkar."}},
[9995] = {"¿Qué son estas cosas?", nil, {"Lleva la caja de piezas a Tooki en el Bastión Rompepedras, en el Bosque de Terokkar."}},
[9996] = {"Ataque en el Alto Ala de Fuego", nil, {"Mata a 10 defensores Ala de Fuego, 10 depositarios de sangre Ala de Fuego y 10 brujos Ala de Fuego. Después, informa al teniente Meridiano en el Puesto Allerian del Bosque de Terokkar."}},
[9997] = {"Ataque en el Alto Ala de Fuego", nil, {"Mata a 10 defensores Ala de Fuego, 10 depositarios de sangre Ala de Fuego y 10 brujos Ala de Fuego. Después, informa al sargento Chawni en el Campamento Rompepedras del Bosque de Terokkar."}},
[9998] = {"Vecinos indisciplinados", nil, {"Mata a 12 peones distorsionados e informa a Theloria Capasombría del Puesto Allerian."}},
[9999] = {"Tiempo extra", nil, {"Altruis el Sufridor, al oeste de Nagrand, te ha pedido que mates a 2 legionarios guardia vil, 3 ingenieros Mo'arg y 8 manitas Gan'arg."}},
[10000] = {"Una presencia inoportuna", nil, {"Mata a 12 peones distorsionados y después preséntate ante el acechasombras Kaide."}},
[10001] = {"El maestro planificador", nil, {"Altruis el Sufridor, en la parte oeste de Nagrand te ha pedido que consigas los planos del maestro planificador."}},
[10002] = {"El contacto Ala de Fuego", nil, {"Localiza y mata a Lisaile Tejefuego y, a continuación, vuelve a ver a Theloria Capasombría."}},
[10003] = {"El contacto Ala de Fuego", nil, {"Localiza y mata a Lisaile Tejefuego y, a continuación, vuelve a ver al acechasombras Kaide."}},
[10004] = {"Paciencia y comprensión", nil, {"Altruis el Sufridor, en el oeste de Nagrand, quiere que lleves los planos del maestro planificador a Sal'salabim en la Ciudad de Shattrath y que le convenzas para que te ayude.|n"}},
[10006] = {"Informar al vinculador terrestre Tavgren", nil, {"Habla con el vinculador terrestre Tavgren justo fuera del Matorral Cenarion, en el Bosque de Terokkar."}},
