[10027] = {"Alteraciones mágicas", nil, {"Mata a 10 acechadores de distorsión y vuelve a ver a Kurgatok en el Bastión Rompepedras."}},
[10028] = {"Vasijas de poder", nil, {"Lleva 8 vasijas draenei a Ros'eleth en el Bastión Allerian."}},
[10030] = {"Recuperar los huesos", nil, {"Consigue 10 huesos sin sosiego y llévaselos a Ramdor el Loco, del lado oeste del Círculo de la Observancia de Auchindoun, en medio de El Vertedero de Huesos, en el Bosque de Terokkar.|n"}},
[10031] = {"Ayudar a Los Perdidos a encontrar su camino", nil, {"Acaba con 10 espíritus perdidos y 10 esqueletos partidos y ve a ver a Soolaveen en la Caravana de Refugiados de El Vertedero de Huesos, en el Bosque de Terokkar.|n"}},
[10032] = {"Artefactos de Tuurem", nil, {"Consigue 6 artefactos Tuurem de los carroñeros Tuurem y los cazadores Tuurem y llévaselos a Rakoria en el Bastión Rompepedras."}},
[10033] = {"SE BUSCA: ¡Muerte a los atizahuesos!", nil, {"Mata a 20 Atizahuesos y después preséntate ante Taela Semprerguida en el Bastión Allerian, en el Bosque de Terokkar."}},
[10034] = {"SE BUSCA: ¡Muerte a los atizahuesos!", nil, {"Mata a 20 Atizahuesos y, a continuación, ve a ver a Mawg Tirosiniestro en el Bastión Rompepedras, en el Bosque de Terokkar."}},
[10035] = {"¡Torgos!", nil, {"Llama a Torgos con el cadáver de Trachela. Consigue una pluma de la cola de Torgos y llévasela a Taela Semprerguida en el Bastión Allerian, en el Bosque de Terokkar."}},
[10036] = {"¡Torgos!", nil, {"Llama a Torgos con el cadáver de Trachela. Consigue una pluma de la cola de Torgos y vuelve a ver a Mawg Tirosiniestro en el Bastión Rompepedras del Bosque de Terokkar."}},
