[10039] = {"Habla con la exploradora Neftis", nil, {"Habla con la exploradora Neftis cerca de la entrada sur del Poblado Grangol'var en el Bosque de Terokkar."}},
[10040] = {"¿Quiénes son?", nil, {"Cuando tengas el disfraz puesto, habla con el iniciado oscuro, el obrero oscuro y el consejero oscuro. Después ve a ver al soldado Semanas en el Poblado Grangol'var, en el Bosque de Terokkar."}},
[10041] = {"¿Quiénes son?", nil, {"Cuando tengas el disfraz puesto, habla con el iniciado oscuro, el obrero oscuro y el consejero oscuro. Después ve a ver a la exploradora Neftis en el Poblado Grangol'var, en el Bosque de Terokkar."}},
[10042] = {"¡Acaba con el Consejo de la Sombra!", nil, {"Mata a 10 verdugos oscuros, a 10 invocadores oscuros y al maestro de las Sombras Pena. A continuación, preséntate ante el teniente Gravamartillo en el Bastión Allerian, en el Bosque de Terokkar."}},
[10043] = {"¡Acaba con el Consejo de la Sombra!", nil, {"Mata a 10 verdugos oscuros, a 10 invocadores oscuros y al maestro de las Sombras Pena. A continuación, preséntate ante la consejera Faila en el Bastión Rompepedras, en el Bosque de Terokkar."}},
[10044] = {"Una visita a la Abuela", nil, {"Habla con la Abuela Geyah y escucha lo que tiene que decirte.|n"}},
[10045] = {"Componentes materiales", nil, {"La Abuela Geyah en Garadar, en Nagrand, te ha pedido 1 raíz de olemba, 1 baya de la marisma, 1 fronda de Telaari y 1 espinazo de dragón.|n"}},
[10047] = {"El Camino a la Gloria", nil, {"Purifica a 8 esqueletos pisoteados en El Camino a la Gloria con agua sagrada draenei y vuelve a ver al arúspice de distorsión Kryv en el Bastión del Honor."}},
[10050] = {"Almas Implacables", nil, {"El guardia de honor Wesilow quiere que derrotes a 12 lacayos Implacables, a 10 hechiceros Implacables y a 5 caballeros Implacables en la Armería de Expedición del sur."}},
[10051] = {"¡Huida del Alto Ala de Fuego!", nil, {"Escolta a Isla Peloestrella hasta un lugar seguro y ve a ver al capitán Auric Cazasol en el Bastión Allerian en el Bosque de Terokkar."}},
[10052] = {"¡Huida del Alto Ala de Fuego!", nil, {"Escolta a Isla Peloestrella hasta un lugar seguro y ve a ver a la consejera Faila en el Bastión Rompepedras en el Bosque de Terokkar."}},
[10055] = {"No desperdicies si puedes evitarlo", nil, {"Lleva 8 piezas de metal rescatado y 8 piezas de madera rescatada a Dumphry en el Bastión del Honor."}},
[10057] = {"El liderazgo en el punto de mira", nil, {"Vence a los fantasmas del archimago Xintor y del teniente coronel Thalvos y vuelve con el guardia de honor Wesilow, a la torre noroeste del Bastión del Honor."}},
[10058] = {"Un viejo regalo", nil, {"Recupera los Misterios de la Luz y llévaselos al padre Malgor Devidicus en el Bastión del Honor."}},
[10063] = {"Liga de Expedicionarios, ¿cosa de gnomos?", nil, {"Encuentra a Clopper Zumbibang que está acampado en un viejo caparazón de tortuga en la costa al norte de la Avanzada de Sangre."}},
[10064] = {"Habla con la Mano", nil, {"Preséntate a la exploradora Jorli en El Reposo del Vindicador."}},
[10065] = {"Abrirse paso", nil, {"Mata a 10 devastadores iracundos e informa a la exploradora Jorli en El Reposo del Vindicador."}},
