[10078] = {"Machacando al enemigo", nil, {"Utiliza la antorcha llameante para quemar los lanzacuchillos de la Horda que dan a El Camino a la Gloria. A continuación, lleva la antorcha llameante a Dumphry en el Bastión del Honor."}},
[10079] = {"Esta mina es un caos", nil, {"Mata a 12 zapadores Gan'arg y vuelve a ver al supervisor Biggums."}},
[10081] = {"Conocer a la Madre Kashur", nil, {"La Abuela Geyah quiere que hables con la Madre Kashur."}},
[10082] = {"Los ancestros agitados", nil, {"La Madre Kashur te ha pedido que acabes con 8 espíritus de orcos perturbados.|n"}},
[10085] = {"Una visita a los ancestros", nil, {"La Madre Kashur en las Tierras Ancestrales, en Nagrand, te ha pedido que visites el Puesto Primasol, las Ruinas Riecráneos, Garadar y las Ruinas Foso Sangrante."}},
[10086] = {"Trabajo... ¡para la Horda!", nil, {"Llévale 8 piezas de metal rescatado y 8 piezas de madera rescatada a Megzeg Quetemeto, en Thrallmar."}},
[10087] = {"Quémalo... ¡por la Horda!", nil, {"Usa la antorcha llameante para quemar los cañones de la Alianza del este y el oeste, y vuelve a ver a Megzeg Quetemeto en Thrallmar."}},
[10091] = {"Los dispositivos de almas", nil, {"Roba 5 dispositivos de alma y entrégaselos a la maestra de espías Mehlisah Solcorona en el Bancal de la Luz en la Ciudad de Shattrath."}},
[10093] = {"El Templo de Telhamat", nil, {"Habla con Amaan el Sabio en el Templo de Telhamat al oeste de la Península del Fuego Infernal."}},
[10094] = {"El Códice de Sangre", nil, {"Lee el Códice de Sangre en el Laberinto de las Sombras en Auchindoun."}},
[10095] = {"En el corazón del laberinto", nil, {"Destruye a Murmullo e informa a la maestra de espías Mehlisah Solcorona en el Bancal de la Luz en la Ciudad de Shattrath sobre los sucesos ocurridos en el Laberinto de las Sombras."}},
[10096] = {"Salvemos a los esporotes", nil, {"Mata 10 dragadores de marisma y 10 rondadores de marisma y ve a ver a Lauranna Thar'well al Refugio Cenarion en la Marisma de Zangar."}},
[10097] = {"Hermano contra hermano", nil, {"Mata a Tejeoscuro Syth en las Salas Sethekk y libera a Lakka. Habla con Isfar fuera de las Salas Sethekk cuando hayas terminado la misión de rescate."}},
[10098] = {"El legado de Terokk", nil, {"Lleva la máscara y la punta de Terokk, además de la saga de Terokk a Isfar a las puertas de las Salas Sethekk."}},
[10099] = {"El cerebro oculto", nil, {"Encuentra y mata a Z'kral en las minas que hay debajo de Bastión del Honor y ve a informar al supervisor Biggums."}},
[10101] = {"Cuando hablan los espíritus", nil, {"Madre Kashur, de las Tierras Ancestrales de Nagrand, te pidió que buscaras a tu visión. Busca a K'ure en Oshu'gun."}},
[10102] = {"Un secreto desvelado", nil, {"Habla con A'dal en la Ciudad de Shattrath."}},
[10103] = {"A las órdenes de Zurai", nil, {"Preséntate ante Zurai en Avanzada Rata del Pantano, en Marisma de Zangar."}},
[10104] = {"Preocupaciones sobre Tuurem", nil, {"Habla con Andarl en el Bastión Allerian en el Bosque de Terokkar."}},
[10105] = {"Noticias para Rakoria", nil, {"Habla con Rakoria en el Bastión Rompepedras, en el Bosque de Terokkar."}},
[10106] = {"Fortificaciones del Fuego Infernal", nil, {"Captura La Dominancia, El Estadium y Cerro Tábido e informa a la suboficial Tracy Pozobravo en el Bastión del Honor, en la Península del Fuego Infernal."}},
[10107] = {"Medidas diplomáticas", nil, {"Jorin Mortojo de Garadar en Nagrand quiere que hables con Lantresor del Filo en las Ruinas Filo Ardiente."}},
[10108] = {"Medidas diplomáticas", nil, {"Mo'mor el Rompedor de Telaar, en Nagrand, quiere que hables con Lantresor del Filo en las Ruinas Filo Ardiente."}},
[10109] = {"¡Los necesito!", nil, {"Wazat de Avanzada del Clan en el sur de Nagrand quiere que le lleves 3 gases de elemental de aire."}},
[10110] = {"Fortificaciones del Fuego Infernal", nil, {"Captura La Dominancia, El Estadium y Cerro Tábido e informa al Gritabatalla Ojonegro en Thrallmar."}},
[10111] = {"¡Tráeme el huevo!", nil, {"Wazat en Nagrand quiere que utilices el Saltatrón 4000 para llegar al nido que está en el árbol cercano y robar el huevo misterioso."}},
[10112] = {"Un pequeño favor", nil, {"Recupera 5 de los objetos robados a Lathrai. Regresa junto al comerciante de viento Lathrai cerca de la Taberna del Fin del Mundo, en el Bajo Arrabal de la Ciudad de Shattrath."}},
[10113] = {"El Safari Nesingwary", nil, {"Jones \"Escopeta\" quiere que hables con Hemet Nesingwary en el Safari Nesingwary a las afueras de Nagrand noreste."}},
[10114] = {"El Safari Nesingwary", nil, {"Ohlorn Errante quiere que hables con Shado 'Fitz' Errante en el Safari Nesingwary en Nagrand."}},
[10115] = {"La perversión Dagapantano", nil, {"Mata a 3 asesinos Dagapantano y 15 habitabostas Dagapantano."}},
[10116] = {"SE BUSCA: Jefe Mummaki", nil, {"Lleva el tótem del cabecilla Mummaki a Ikuti a Puerto Orebor."}},
[10117] = {"SE BUSCA: Jefe Mummaki", nil, {"Lleva el tótem del cabecilla Mummaki al cazador de las Sombras Denjai."}},
[10118] = {"Mensaje a los Dagapantano", nil, {"Mata 3 asesinos Dagapantano y 15 habitabostas Dagapantano y, a continuación, ve a ver al cazador de las Sombras Denjai en Zabra'jin."}},
[10119] = {"A través de El Portal Oscuro", nil, {"Viaja a través de El Portal Oscuro y encuentra al comandante Duron. Te espera al otro lado... en Terrallende.|n"}},
[10120] = {"Llegada a Terrallende", nil, {"El teniente general Orion de El Portal Oscuro en la Península del Fuego Infernal quiere que lleves su informe a Vlagga Plumacalma."}},
[10121] = {"Erradicar a la Legión Ardiente", nil, {"Habla con el sargento Trizacráneo en la Caravana de Provisiones a las afueras de Thrallmar, hacia el este."}},
[10123] = {"El Barranco Llama Infernal", nil, {"El sargento Trizacráneo de la Caravana de Provisiones al este de Thrallmar en la Península del Fuego Infernal quiere que mates a 4 diablillos Despiertallamas, 6 belisarios infernales y 1 invocamiedo."}},
[10124] = {"Base de la vanguardia: Caída del Atracador Vil", nil, {"Preséntate ante el comandante de vanguardia To'arch en la Caída del Atracador Vil."}},
[10129] = {"Misión: Puerta Murketh y Puerta Shaadraz", nil, {"Habla con el comandante del aire Brack, bombardea Puerta Murketh y Puerta Shaadraz y después regresa junto al comandante de vanguardia To'arch en la Caída del Atracador Vil."}},
[10132] = {"La amenaza colosal", nil, {"Mata a 5 colosos enfurecidos y regresa junto a Tola'thion en el Puesto Cenarion, en la Península del Fuego Infernal."}},
[10134] = {"Pista para el cristal carmesí", nil, {"Entrega el fragmento de cristal carmesí a Tola'thion en el Puesto Cenarion, en la Península del Fuego Infernal."}},
[10136] = {"Crueles intenciones", nil, {"Mata a Arazzius el Cruel y vuelve con Nazgrel en Thrallmar."}},
[10140] = {"Viaje al Bastión del Honor", nil, {"Vuela hasta el Bastión del Honor y entrega el informe de Duron al mariscal Isildor."}},
[10141] = {"El renacer de la Legión", nil, {"Habla con la sargento Altumus en la Caravana de Provisiones del Este justo fuera del Bastión del Honor, hacia el este."}},
[10142] = {"El Camino del Tormento", nil, {"La sargento Altumus en la Caravana de Provisiones del Este del Bastión del Honor en la Península del Fuego Infernal, quiere que mates a 4 diablillos despiertallamas, 6 belisarios infernales y 1 invocamiedo."}},
[10143] = {"Punta de Expedición", nil, {"Preséntate ante el comandante de vanguardia Kingston en la Punta de Expedición."}},
[10144] = {"Perturbar sus refuerzos", nil, {"Vence a los enemigos que hay alrededor del Portal Grimh y el Portal Kaalez para reunir piedras rúnicas demoníacas.\n\nUsa el cebo explosivo de Kingston dentro del Portal Grimh y del Portal Kaalez.\n\nVuelve con el comandante de vanguardia Kingston con los explosivos restantes."}},
[10146] = {"Misión: La Puerta Murketh y la Puerta Shaadraz", nil, {"Habla con el comandante del aire Dabir'ee, bombardea la Puerta Murketh y la Puerta Shaadraz y, a continuación, vuelve a ver al comandante de vanguardia Kingston en la Punta de Expedición."}},
[10159] = {"Mantener despejada la Colina Colmillespinado", nil, {"Mata 8 devastadores Colmillespinados y 8 escupetósigos Colmillespinados. A continuación ve a ver a Mahuram Pezuña Recia en el Puesto Cenarion, en la Península del Fuego Infernal."}},
[10160] = {"Conoce a tu enemigo", nil, {"Habla con la teniente Amadi en la vieja torre vigía justo al oeste del Bastión del Honor."}},
[10161] = {"En caso de emergencia...", nil, {"Consigue 30 restos del zepelín y llévaselas a \"Alaridos\" Screed Sinsuerte en el Zepelín Caído."}},
[10162] = {"Misión: La Plataforma Abisal", nil, {"El comandante de vanguardia To'arch de la Caída del Atracador Vil en la Península del Fuego Infernal quiere que destruyas 20 peones de Gan'arg, 5 sobrestantes Mo'arg y 5 cañones viles."}},
[10163] = {"Misión: La Plataforma Abisal", nil, {"El comandante del aire Grifongar del Puesto Devastación, en la Península del Fuego Infernal, ha solicitado que destruyas 20 peones de Gan'arg, 5 sobrestantes Mo'arg y 5 cañones viles."}},
[10164] = {"Todo irá bien", nil, {"Abuelo Aldrimus, fuera de las Criptas Auchenai en Auchindoun, quiere que entres en las criptas y mates al exarca Maladaar."}},
[10165] = {"Eliminar a la competencia", nil, {"El príncipe-nexo Haramad, que está junto a las Tumbas de Maná, quiere que mates al príncipe-nexo Shaffar y le lleves su mortaja."}},
[10166] = {"La memoria de Cortezablanca", nil, {"Larianna Ríobrisa quiere que entierres el colgante del viejo Cortezablanca junto a la piedra rúnica de La Arboleda Agostada."}},
[10167] = {"Auchindoun...", nil, {"Ve a las Criptas Auchenai, en El Vertedero de Huesos del Bosque de Terokkar, y mata al exarca Maladaar para liberar al espíritu de D'ore.|n"}},
[10168] = {"Reflejos del alma", nil, {"Encuentra un espejo de alma en las Criptas Auchenai y úsalo para llamar a un espíritu oscurecido de los antepasados orcos ancestrales en Nagrand. Destruye 15 espíritus oscurecidos para que los antepasados puedan descansar en paz.\n\nDespués ve a ver a Madre Kashur a las Tierras Ancestrales."}},
[10169] = {"Saber perder", nil, {"El magister Falris de Shattrath quiere que lleves el vial de suero sedativo a Bancal de la Luz y lo viertas en la olla del regimiento de Haronem."}},
[10170] = {"Regresa junto a la Abuela", nil, {"Vuelve a Garadar y habla con la Abuela Geyah."}},
[10171] = {"El inconsolable jefe", nil, {"La Abuela Geyah en Garadar te ha pedido que hables con Garrosh."}},
[10172] = {"No hay esperanza", nil, {"Habla con la Abuela Geyah y cuéntale lo que Garrosh te ha dicho."}},
[10173] = {"El bastón del archimago", nil, {"Quítale el bastón del archimago Vargoth a Ekkorash la Inquisidora y llévaselo a Ravandwyr al Área 52. Para sacar a Ekkorash de su escondite, espolvorea el polvo de conjuro en el blandón que hay en el centro de las ruinas."}},
[10174] = {"La maldición de la Torre Violeta", nil, {"Utiliza el bastón del archimago Vargoth para contactar con él.\n\nSi pierdes el bastón del archimago Vargoth, habla con Ravandwyr en el Área 52 para que te dé otro."}},
[10176] = {"Ar'kelos el Guardián", nil, {"Entra en la Torre Violeta en el centro del Poblado Kirin'Var y derrota a Ar'kelos. A continuación, habla con el archimago Vargoth."}},
[10177] = {"Problemas en Auchindoun", nil, {"Preséntate ante el comandante de operaciones Mahfuun en la entrada del Laberinto de las Sombras en Auchindoun, en el Bosque de Terokkar."}},
[10178] = {"Encontrar al espía To'gun", nil, {"Localiza al espía To'gun en el Laberinto de las Sombras, en Auchindoun."}},
[10180] = {"No puedo estar lejos", nil, {"Habla con Isfar. Le encontrarás en las Salas Sethekk en Auchindoun."}},
[10182] = {"Mago de batalla Dathric", nil, {"Encuentra las dependencias del mago de batalla Dathric en el Pasaje del Zahorí y devuelve la espada al expositor. A continuación habla con el custodio Valemuerte en el Poblado Kirin'Var."}},
[10183] = {"Al Área 52", nil, {"Lleva las notas del abisólogo al jefe de cohetes Fuselaje en el Área 52, en la Tormenta Abisal."}},
[10184] = {"Restos malévolos", nil, {"El custodio Valemuerte en el Poblado Kirin'Var quiere que mates 20 espíritus aislados en la Plaza de la Ciudad de Kirin'Var."}},
[10185] = {"Terrible destino", nil, {"Mata a 10 matamagos y 10 buscamanás y ve a ver al custodio Valemuerte al Poblado Kirin'Var."}},
[10186] = {"¡Te contrato!", nil, {"Reúne 10 cristales de la matriz de eterlitio y vuelve a ver al jefe de cohetes Fuselaje en el Área 52 en la Tormenta Abisal."}},
[10188] = {"El sigilo de Krasus", nil, {"Quítale el sigilo de Krasus a la vinculahechizos Maryana en la Forja de Maná Coruu, al noroeste de la Torre Violeta."}},
[10189] = {"Forja de Maná B'naar", nil, {"El maestro de espías Thalodien del Área 52 quiere que te dirijas a las afueras de la Forja de Maná B'naar y recuperes lista de personal de B'naar del capitán Arathyn.\n\nA medida que completas misiones para los Arúspices disminuye tu nivel de reputación con los Aldor."}},
[10190] = {"Recargando las baterías", nil, {"Usa la carga reponbatería en suficientes cazadores de fase para que el nivel de carga suba a 10. Regresa entonces junto a la especialista en robots Alley, en las Ruinas de Enkaat, en Tormenta Abisal."}},
[10191] = {"¡Mark V está vivo!", nil, {"Escolta el Maxx Y. Millón nº. V a través de las Ruinas de Enkaat y llévalo hasta un lugar seguro. Después habla con la especialista en robots Alley, en las afueras de las Ruinas de Enkaat en Tormenta Abisal."}},
[10192] = {"El Compendio de Krasus", nil, {"Recoge el Compendio de Krasus: Capítulo 1, el Compendio de Krasus: Capítulo 2 y el Compendio de Krasus: Capítulo 3 de la plaza del Poblado Kirin'Var. "}},
[10193] = {"Objetivos de gran valor", nil, {"El maestro de espías Thalodien del Área 52 quiere que te dirijas a las afueras de la Forja de Maná B'naar y mates 2 maestros de distorsión Furia del Sol, 6 ingenieros de distorsión Furia del Sol y 8 geólogos Furia del Sol.\n\nCompletar misiones para los Arúspices hará que disminuya tu reputación con los Aldor."}},
[10194] = {"Vuelo sigiloso", nil, {"Habla con Veronia fuera del Área 52 y vuela hasta Forja de Maná Coruu.\n\nCompletar misiones para los Arúspices hará que disminuya tu reputación con los Aldor."}},
[10197] = {"Un disfraz convincente", nil, {"Consigue las togas de arcanista Furia del Sol, un par de guantes de investigador Furia del Sol y un medallón de custodio Furia del Sol y entrégaselos a Caledis Alba Brillante, en Forja de Maná Coruu de Tormenta Abisal.\n\nCompletar misiones para los Arúspices hará que disminuya tu reputación con los Aldor."}},
[10198] = {"Reuniendo pistas", nil, {"Use el disfraz de Furia del Sol, ve a la Forja de Maná Coruu y escucha la conversación entre el comandante Forjalba y el arcanista Ardonis.\n\nA medida que completas tareas para los Arúspices disminuye tu nivel de reputación Aldor."}},
[10199] = {"Ese pequeño toque extra", nil, {"Consigue 5 aguijones de rayas abisales para Botas en el Área 52 de Tormenta Abisal."}},
[10200] = {"Regresa junto a Thalodien", nil, {"Ve a ver al maestro de espías Thalodien en el Área 52, en la Tormenta Abisal."}},
[10201] = {"Y ahora, el momento de la verdad", nil, {"Encuentra a Grek y pídele que pruebe el aceite en su arma. Cuando lo haya hecho, vuelve a ver a Rakoria con los resultados."}},
[10203] = {"El ultra-desconsolidador es muy útil", nil, {"Utiliza el transportador ultra-desconsolidador para enviar al Área 52 la excavamática hiperrotacional, el cangilón de arrastre servoneumático, el analizador de terreno multiespectro y el vagón grande lleno de explosivos. A continuación preséntate ante el jefe de zapadores Estampido en la fisura del Área 52 en la Tormenta Abisal."}},
[10204] = {"Cristales de gema de sangre", nil, {"La magistrix Larynna quiere que vayas a la Forja de Maná B'naar y obtengas un fragmento de gema de sangre de un magister Furia del Sol. Utiliza el fragmento cerca de los cristales grandes de gema de sangre y ve a verla en el Área 52.\n\nCompletar misiones para los Arúspices hará que tu nivel de reputación con los Aldor disminuya."}},
[10205] = {"Asaltante de distorsión Nesaad", nil, {"Mata al asaltante de distorsión Nesaad y ve a ver al acechador abisal Khay'ji al Área 52 en Tormenta Abisal."}},
[10206] = {"Tú eliges", nil, {"Reúne 10 artilugios de tecnología etérea y llévaselos a Papá Ruedas en el Área 52, en la Tormenta Abisal."}},
[10208] = {"Perturbar sus refuerzos", nil, {"Derrota a enemigos alrededor del Portal Xilus y del Portal Kruul para recolectar piedras rúnicas demoníacas.\n\nUsa el cebo explosivo de To'arch del Portal Xilus y del Portal Kruul.\n\nRegresa junto a To'arch con los cebos de To'arch en la Caída del Atracador Vil, en la Península del Fuego Infernal."}},
[10209] = {"El premio del invocador Kanthin", nil, {"Derrota al invocador Kanthin en la Forja de Maná Duro, en la isla que está al norte del Bastión Furia del Sol y reclama la Piedra de Glacius."}},
[10210] = {"A'dal", nil, {"Habla con A'dal, el jefe de los Sha'tar, en el centro del Bancal de la Luz."}},
[10211] = {"La Ciudad de la Luz", nil, {"Sigue al sirviente de Khadgar y escucha la historia de la ciudad. Luego ve a ver a Khadgar."}},
[10212] = {"Héroe Mag'har", nil, {"Ve a ver a Abuela Geyah a Garadar en Nagrand.|n"}},
[10213] = {"Investigar el accidente", nil, {"Busca sobrevivientes en el lugar del accidente.  "}},
[10216] = {"La seguridad es prioritaria", nil, {"El artificiero Morphalius quiere que mates 10 asaltantes de tumbas etéreos, 5 brujas etéreas, 5 acechadores nexo y 5 vinculahechizos etéreos.\n\nCuando lo hayas conseguido, encuentra el transportador etéreo en la cámara anterior a la de Shaffar y actívalo."}},
[10218] = {"El trabajo duro de alguien más nos beneficia", nil, {"Escolta al crioingeniero Sha'heen a través de las Tumbas de Maná para que pueda recoger el éter que está dentro de los recolectores de Shaffar.\n\nPreséntate ante el príncipe-nexo Haramad en las Tumbas de Maná, en el campamento del Consorcio si tienes éxito."}},
[10220] = {"Oblígales a escuchar", nil, {"Mordiba quiere que derrotes a 12 lacayos Implacables, 8 caballeros Implacables y 6 hechiceros Implacables en la Armería de Expedición, al oeste de la Avanzada Rompeloma."}},
[10221] = {"¡Dr. Bum!", nil, {"Mata al Dr. Bum y ve a darle la buena noticia al jefe de zapadores Estampido al Área 52 en Tormenta Abisal."}},
[10222] = {"El cuartel Furia del Sol", nil, {"El custodio Valemuerte en el Poblado Kirin'Var quiere que mates 10 arqueros Furia del Sol."}},
[10223] = {"Abajo con Daellis", nil, {"El custodio Valemuerte, en el Poblado Kirin'Var quiere que mates al maestro Daellis Golpe del Amanecer."}},
[10224] = {"Esencia para los motores", nil, {"Reúne 7 esencias de ánima de maná y regresa junto al ingeniero jefe Trep en el Área 52, en Tormenta Abisal."}},
[10225] = {"Informe para lngeniería", nil, {"Entrégale los cristales de la matriz de eterlitio al ingeniero jefe Trep en el Área 52, en Tormenta Abisal."}},
[10226] = {"Extracción de energía elemental", nil, {"Extrae 5 unidades de energía elemental para el ingeniero jefe Trep en el Área 52, en la Tormenta Abisal."}},
[10227] = {"Veo draenei muertos", nil, {"Habla con Ramdor el Loco junto a la pila de huesos fuera de las Criptas Auchenai."}},
[10228] = {"Ezekiel", nil, {"Busca a Ezekiel de Shattrath en Ciudad de Shattrath."}},
[10229] = {"Descifrar el escrito", nil, {"Lleva el escrito misterioso a Althen el Historiador, en la Avanzada Rompeloma.  "}},
[10230] = {"El cuerno de batalla", nil, {"Toma el cuerno de batalla Implacable del comandante Thalvos y llévaselo Althen, el historiador de la Avanzada Rompeloma."}},
[10231] = {"¿Libro? Yo no veo ninguno", nil, {"Ezekiel dijo que habían robado el libro Será mejor que encuentres a esos rufianes y veas si ellos tienen alguna información.|n"}},
[10232] = {"Movida con la Legión", nil, {"Mata 5 forjadestinos Mo'arg y 15 ingenieros Gan'arg. Después, vuelve a hablar con Papá Ruedas en el Área 52, en Tormenta Abisal."}},
[10233] = {"Fuego en el Bastión Furia del Sol", nil, {"Con una antorcha llameante, acaba con 4 balistas Furia del Sol y 4 tiendas Furia del Sol y regresa junto al teniente hechicero Morran en el Poblado Kirin'Var."}},
[10234] = {"Basura del demonio...", nil, {"Consigue 10 piezas de atracador vil y llévaselas a Mamá Ruedas en el Puesto de la Tierra Media dentro del Ecodomo de la Tierra Media, en la Tormenta Abisal."}},
[10235] = {"Deszarpar a Zarpaletal", nil, {"Consigue la Mano de Zarpaletal y entrégasela a Mamá Ruedas en el Puesto de la Tierra Media, en el Ecodomo de la Tierra Media, en la Tormenta Abisal."}},
[10236] = {"¡Terrallende es un asco!", nil, {"Encuentra 6 piezas de trituradora y regresa junto al supervisor Razelcraz en la mina al noroeste de Thrallmar."}},
[10237] = {"¡Avisa al Área 52!", nil, {"Entrega el manual de construcción de atracadores viles y lleva las malas noticias al jefe de cohetes Fuselaje en el Área 52, en la Tormenta Abisal."}},
[10238] = {"Cómo servir a los goblins", nil, {"Rescata a Manni, Moh y Jakk de los campamentos de los orcos viles y regresa junto al supervisor Razelcraz en la mina al noroeste de Thrallmar."}},
[10239] = {"Fuente de energía potencial", nil, {"El teniente hechicero Morran en el Poblado Kirin'Var quiere que captures 10 cubos de aislamiento de energía de los elfos de sangre en la Forja de Maná Coruu."}},
[10240] = {"Construyendo un perímetro", nil, {"Utiliza el aparato de activación de runa cerca de la runa del este, la runa del noroeste y la runa del oeste. Puedes encontrar las runas alrededor del perímetro del Pasaje del Zahorí y la Plaza de la Ciudad del Poblado Kirin'Var. Ve a ver al teniente hechicero Morran cuando hayas completado la tarea."}},
[10241] = {"Distracción en la Forja de Maná B'naar", nil, {"Mata 8 magistri Furia del Sol y 8 depositarios de sangre Furia del Sol. Después vuelve a ver al exarca Orelis en el Área 52.\n\nCompletar misiones para los Aldor hará que disminuya tu nivel de reputación con los Arúspices."}},
[10242] = {"La Avanzada Rompeloma", nil, {"La boticaria Zelana, en la Caída del Atracador Vil, quiere que hables con el comandante del aire Brack para llevar un dracoleón a la Avanzada Rompeloma y llevar la muestra de sangre Foso Sangrante al boticario Albreck en la Avanzada Rompeloma.  "}},
[10243] = {"Tecnología naaru", nil, {"Entra en la Forja de Maná B'naar y encuentra la consola de control B'naar.\n\nCompletar misiones para los Aldor hará que disminuya tu nivel de reputación con los Arúspices."}},
[10245] = {"Trascripción de la consola de B'naar", nil, {"Entrega la trascripción de consola B'naar a la Anacoreta Karja del Área 52, en Tormenta Abisal.\n\nCompletar misiones para los Aldor hará que disminuya tu nivel de reputación con los Arúspices."}},
[10246] = {"Ataque a la Forja de Maná Coruu", nil, {"Ve a la Forja de Maná Coruu y mata 8 arcanistas Furia del Sol y 5 investigadores Furia del Sol. Vuelve a hablar con el exarca Orelis cuando hayas cumplido esta misión.\n\nCompletar misiones para los Aldor hará que disminuya tu nivel de reputación con los Arúspices."}},
[10247] = {"Doctor Vomisa, Ph.T.", nil, {"Entrega el manual de construcción de atracadores viles al doctor Vomisa Ph.T. en el terreno de pruebas, en la Tormenta Abisal."}},
[10248] = {"Tú, Robot", nil, {"Activa el Atracador desguazador X6000 y prueba todas sus funciones. Después informa al doctor Vomisa, Ph.T en el Terreno de Pruebas de Tormenta Abisal. Encárgate de cualquier problema que pueda surgir."}},
[10249] = {"¡El jefe de nuevo!", nil, {"Preséntate inmediatamente ante el jefe de cohetes Fuselaje en el Área 52, en la Tormenta Abisal."}},
[10250] = {"Venganza sangrienta", nil, {"Haz que suene el cuerno de batalla Implacable cerca del estandarte de la Alianza. Mata a Urtrak y vuelve a hablar con Althen el historiador en la Avanzada Rompeloma."}},
[10251] = {"¿La obra maestra del maestro?", nil, {"Encuentra a Nitrin el Erudito en la Armería Abandonada, en Nagrand."}},
[10252] = {"Visión de los muertos", nil, {"Nitrin, en la Armería Abandonada, en Nagrand, quiere que le lleves 1 globo ocular de gronn montés, 1 pico de vientorroc superior perfecto y 1 grasa de uñagrieta envejecido."}},
[10253] = {"Levixus el Invocador de Almas", nil, {"Destruye a Levixus y coge el libro de los muertos.\n\nEntrega el libro de los muertos a Ramdor el Loco fuera de las Criptas Auchenai, en la entrada de Auchindoun.\n\nTenías un trato, ¿lo recuerdas?"}},
[10254] = {"Comandante en jefe Danath", nil, {"El mariscal Isildor quiere que te presentes ante el comandante en jefe Danath Aterratrols en el Bastión del Honor."}},
[10255] = {"Pruebas de antídoto", nil, {"Usa el antídoto Cenarion sobre un jabinferno descomunal y observa sus efectos. Cuando tengas los resultados del experimento, regresa junto a Thiah Melenarroja del Puesto Cenarion, en la Península del Fuego Infernal."}},
[10256] = {"Buscando a la Maestra de llaves", nil, {"Encuentra a Cumbre y quítale un foco de cristal de Cumbre. Ve a la Torre Violeta y úsalo cerca del orbe del archimago Vargoth para descubrir quién tiene la llave para romper la maldición de la torre."}},
[10257] = {"La piedra angular", nil, {"Adéntrate en El Invernáculo de El Castillo de la Tempestad y recupera la piedra angular de la comandante Sarannis. Llévasela al archimago Vargoth en la Torre Violeta."}},
[10258] = {"Honrar a los caídos", nil, {"Preséntate ante el comandante Hogarth en la Armería de Expedición.  "}},
[10261] = {"SE BUSCA: ¡Servoaniquilador!", nil, {"Consigue un servoaniquilador y entrégaselo a Papá Ruedas en el Área 52 en Tormenta Abisal."}},
[10262] = {"Un montón de etéreos", nil, {"Recoge 10 insignias de Zaxxis y llévaselas al acechador abisal Khay'ji al Área 52 en Tormenta Abisal."}},
[10263] = {"Ayudar al Consorcio", nil, {"Habla con el acechador abisal Khay'ji del Área 52, en la Tormenta Abisal."}},
[10264] = {"Ayudar al Consorcio", nil, {"Habla con el acechador abisal Khay'ji del Área 52, en la Tormenta Abisal."}},
[10265] = {"Colección de cristales de El Consorcio", nil, {"Consigue un artefacto de cristal de Arklon y llévaselo al acechador abisal Khay'ji al Área 52 en Tormenta Abisal."}},
[10266] = {"Petición de ayuda", nil, {"Busca a Gahruj y ofrécele tus servicios. Se encuentra en el Puesto de la Tierra Media, en el Ecodomo de la Tierra Media de Tormenta Abisal."}},
[10267] = {"Legítima recuperación", nil, {"Coge 10 cajas de equipo de análisis y llévaselas a Gahruj al Puesto de la Tierra Media, en el Ecodomo de la Tierra Media, en Tormenta Abisal."}},
[10268] = {"Una audiencia con el príncipe", nil, {"Entrega el equipo de análisis a la Imagen del Príncipe del Nexo Haramad en La Flecha de la Tormenta, en Tormenta Abisal."}},
[10269] = {"Punto de triangulación uno", nil, {"Usa el aparato de triangulación para localizar el primer punto de triangulación. Informa de su ubicación al tratante Hazzin en la Avanzada del Protectorado en la isla de la Forja de Maná Ultris en Tormenta Abisal."}},
[10270] = {"Proposición poco decente", nil, {"Recupera el envase de energía de teletransportador y llévalo a la plataforma de teletransporte etérea en la Cresta Celestial, al este de la Forja de Maná Ultris. Activa la plataforma y dale la fuente de energía a la imagen del comerciante de viento Marid para que la instale."}},
[10271] = {"A trabajar", nil, {"Carga el recolector de esencia usándolo con dracos abisales o con dragones abisales en la Cresta Celestial y después vuelve a la zona de la plataforma de teletransporte etérea."}},
[10272] = {"Un buen comienzo", nil, {"Recolecta 15 huevos de dragonante abisal y llévaselos a Tyri y Jorad a la Cresta Celestial."}},
[10273] = {"Problemas a la vista", nil, {"Habla con el comerciante de viento Marid y aléjalo de su tienda. Ocúpate de él y ve a ver a Tyri y Jorad a la Cresta Celestial."}},
[10274] = {"Proteger la Cresta Celestial", nil, {"Usa el desafío del Vuelo Azul para provocar a Veraku en el extremo sur de Cresta Celestial. Derrótalo y ve a ver a Tyri y Jorad a Cresta Celestial."}},
[10275] = {"Punto de triangulación dos", nil, {"Usa el aparato de triangulación para localizar el segundo punto de triangulación. Informa al comerciante de viento Tuluman en Alto de Tuluman, justo al cruzar el puente de la isla de Forja de Maná Ara en Tormenta Abisal."}},
[10276] = {"Triángulo completo", nil, {"Recupera el cristal de Ata'mal y dáselo a la imagen del Príncipe del Nexo Haramad en La Flecha de la Tormenta, en Tormenta Abisal."}},
[10277] = {"Las Cavernas del Tiempo", nil, {"Andormu de las Cavernas del Tiempo quiere que sigas a la custodia del Tiempo."}},
[10278] = {"Las fallas de distorsión", nil, {"Usa el generador de fallas de distorsión inestable en Los Campos Alabeados. Consigue 3 abismos de distorsión de abisarios inestables y llévaselos a Ogath el Loco, en la Avanzada Rompeloma.  "}},
[10279] = {"La caverna del maestro", nil, {"Habla con Andormu en las Cavernas del Tiempo."}},
[10280] = {"Una entrega especial a la Ciudad de Shattrath", nil, {"Entrega el cristal de Ata'mal a A'dal en el Bancal de la Luz en la Ciudad de Shattrath."}},
[10281] = {"Presentaciones formales", nil, {"Encuentra a Tyri en el extremo norte de Cresta Celestial."}},
[10282] = {"Antigua Trabalomas", nil, {"Andormu de las Cavernas del Tiempo quiere que vayas a la Antigua Trabalomas para hablar con Erozion."}},
[10283] = {"La maniobra de Taretha", nil, {"Ve al Castillo de Durnholde y coloca 5 cargas incendiarias del paquete de bombas incendiarias que te dio Erozion en los barriles que hay en cada pabellón de internamiento.\n\nDespués habla con Thrall en la prisión subterránea del castillo."}},
[10284] = {"Huida de Durnholde", nil, {"Cuando estés listo para proceder, díselo a Thrall. Sigue a Thrall al exterior del Castillo de Durnholde y ayúdale a liberar a Taretha y así cumplir su destino.\n\nHabla con Erozion en Antigua Trabalomas cuando completes esta tarea."}},
[10285] = {"Regresa junto a Andormu", nil, {"Regresa junto al niño, Andormu, en las Cavernas del Tiempo, en el Desierto de Tanaris."}},
[10286] = {"El secreto de Arelion", nil, {"La magistrix Carinda quiere que interrogues al magister Aledis, que salió de la Avanzada del Halcón en dirección a la Marisma de Zangar."}},
[10287] = {"Amante descubierta", nil, {"Ve a ver a la magistrix Carinda a la Avanzada del Halcón en la Península del Fuego Infernal."}},
[10288] = {"Llegada a Terrallende", nil, {"El comandante Duron de El Portal Oscuro en la Península del Fuego Infernal quiere que entregues su informe a Amish Martillo Salvaje."}},
[10289] = {"Viaje a Thrallmar", nil, {"Lleva el jinete del viento de Vlagga Plumacalma a Thrallmar. Entrega el informe de Orion al general Krakork."}},
[10290] = {"Buscando faralita", nil, {"Recolecta 4 piezas de faralita en bruto y llévaselas a Zuben Elgenubi a La Flecha de la Tormenta."}},
[10291] = {"Informar a Nazgrel", nil, {"El general Krakork en Thrallmar quiere que hables con Nazgrel."}},
[10293] = {"A por la veta", nil, {"Zuben Elgenubi de La Flecha de la Tormenta quiere que le lleves un núcleo de faralita."}},
[10294] = {"La Cresta del Vacío", nil, {"Ve a la Cresta del Vacío y mata las criaturas que encuentres. Recolecta 40 fragmentos de alma de la Cresta del Vacío y llévaselos a Ogath el Loco, en la Avanzada Rompeloma."}},
[10295] = {"Desde el abismo", nil, {"Mata al barón del vacío Galaxis y recolecta un fragmento de su alma. Llévale el fragmento a Ogath el Loco en la Avanzada Rompeloma."}},
[10296] = {"La Ciénaga Negra", nil, {"Viaja por las Cavernas del Tiempo hasta la Ciénaga Negra mientras esté abierto El Portal Oscuro y habla con Sa'at."}},
[10297] = {"La apertura de El Portal Oscuro", nil, {"Sa'at en la Ciénaga Negra de las Cavernas del Tiempo te ha encargado que defiendas a Medivh hasta que consiga abrir El Portal Oscuro.\n\nVuelve junto a Sa'at si consigues completar esta tarea."}},
[10298] = {"Adalid del linaje", nil, {"Regresa junto al niño, Andormu, en las Cavernas del Tiempo, en el Desierto de Tanaris."}},
[10299] = {"El cierre de la Forja de Maná B'naar", nil, {"Vuelve a la Forja de Maná B'naar para obtener un cristal de acceso del sobrestante Theredis. Úsalo en la consola de la Forja de Maná B'naar para cerrarla y ve a informar a la Anacoreta Karja.\n\nCompletar misiones para los Aldor hará que disminuya tu nivel de reputación con los Arúspices."}},
[10300] = {"Reconstrucción del bastón", nil, {"Consigue 4 fragmentos de cristal perfectos de los fantasmas de las Ruinas de Enkaat y llévaselos a Ravandwyr en el Área 52."}},
[10301] = {"Abrir el Compendio", nil, {"Consigue el óculo de heliotropo de la atracahechizos Marathelle en el Bastión Furia del Sol. "}},
[10302] = {"Mutaciones volátiles", nil, {"Mata 8 mutaciones volátiles y ve a ver a la botánica Taerix al Lugar del Accidente en el Valle Ammen."}},
[10303] = {"Los elfos de sangre", nil, {"Mata a 10 exploradores elfos de sangre y ve a ver a Tolaan a la Cresta de las Sombras en el Valle Ammen."}},
[10304] = {"El vindicador Aldar", nil, {"Habla con el vindicador Aldar en el Lugar del Accidente en el Valle Ammen."}},
[10305] = {"Abjuradora Belmara", nil, {"Encuentra las dependencias de la abjuradora Belmara en el Pasaje del Zahorí y devuelve el libro a la estantería. A continuación, habla con el custodio Valemuerte en el Poblado Kirin'Var."}},
[10306] = {"Conjurador Luminrath", nil, {"Encuentra las dependencias del conjurador Luminrath, en el Pasaje del Zahorí y devuelve la capa a su vestidor. A continuación, habla con el custodio Valemuerte en el Poblado Kirin'Var."}},
[10307] = {"Cohlien Tejescarcha", nil, {"Encuentra las dependencias de Cohlien Tejescarcha, en el Pasaje del Zahorí y devuelve el sombrero a su baúl. A continuación, habla con el custodio Valemuerte en el Poblado Kirin'Var."}},
[10308] = {"Otro montón de etéreos", nil, {"Requisitos:"}},
[10309] = {"Es un atracador vil... pero con corazón", nil, {"Recupera el corazón del atracador vil y entrégaselo a N. D. Malcampo en La Pila, en Tormenta Abisal."}},
[10310] = {"¡Sabotead el portal de distorsión!", nil, {"Escolta a Drijya hasta el portal de distorsión en el Punto de Invasión: Destructor, y cúbrelo mientras intenta sabotearlo. Después habla con Gahruj en el Puesto de la Tierra Media dentro del Ecodomo de la Tierra Media en Tormenta Abisal."}},
[10311] = {"Drijya necesita tu ayuda", nil, {"Habla con Drijya cerca del Punto de Invasión: Destructor en Tormenta Abisal."}},
[10312] = {"Las Crónicas de Kirin'Var", nil, {"Recupera las Crónicas de Kirin'Var en el ayuntamiento del Poblado Kirin'Var y llévaselas al custodio Valemuerte."}},
[10313] = {"Medir energías de distorsión", nil, {"El exarca Orelis del Área 52 quiere que te lleves el orbe armonizado con la distorsión a la Forja de Maná B'naar y lo uses al final de sus 4 tuberías.\n\nCompletar misiones para los Aldor hará que disminuya tu nivel de reputación con los Arúspices."}},
[10314] = {"Una sospecha latente", nil, {"Consigue 10 restos repugnantes de los espíritus malignos del Patio de la Capilla del Poblado Kirin'Var y llévaselos al custodio Valemuerte."}},
[10315] = {"Neutralizar a los abisálicos", nil, {"Mata a 15 abisálicos Furia del Sol y ve a ver al acechador abisal Oazul al Alto de Tuluman en Tormenta Abisal."}},
[10316] = {"En busca de pruebas", nil, {"Busca pistas sobre el responsable de la manipulación de los espíritus en el Patio de la Capilla del Poblado Kirin'Var."}},
[10317] = {"Tratar con el supervisor", nil, {"Encárgate del supervisor Ocaso en la Mina Trelleum, que está bajo la Forja de Maná Ara en Tormenta Abisal."}},
[10318] = {"Conversaciones con el maestro Supremo", nil, {"Mata al maestro supremo Yugogarr. A continuación ve a ver al comerciante de viento Tuluman en el Alto de Tuluman, en la Tormenta Abisal."}},
[10319] = {"Captura de la filacteria", nil, {"Recupera la filacteria de Naberius en el Patio de la Capilla del Poblado Kirin'Var y llévasela al custodio Valemuerte."}},
[10320] = {"¡Destruir a Naberius!", nil, {"El custodio Valemuerte, en el Poblado Kirin'Var quiere que derrotes a Naberius en el Patio de la Capilla del poblado."}},
[10321] = {"El cierre de la Forja de Maná Coruu", nil, {"Ve a la Forja de Maná Coruu, al este del Área 52, y obtén el cristal de acceso de Coruu del sobrestante Seylanna. Úsalo en la consola de Forja de Maná Coruu para desactivarla y vuelve a hablar con la Anacoreta Karja.\n\nCompletar misiones para los Aldor hará que disminuya tu nivel de reputación con los Arúspices."}},
[10322] = {"El cierre de la Forja de Maná Duro", nil, {"La Anacoreta Karja quiere que vayas a la Forja de Maná Duro y consigas el cristal de acceso del sobrestante Athanel. Utilízalo en la consola de la Forja de Maná Duro para desactivarla.\n\nCompletar misiones para los Aldor hará que disminuya tu nivel de reputación con los Arúspices."}},
[10323] = {"El cierre de la Forja de Maná Ara", nil, {"Ve a la Forja de Maná Ara y obtén el cristal de acceso de Ara del sobrestante Azarad. Úsalo en la consola de Forja de Maná Ara para desactivarla.\n\nCompletar misiones para los Aldor hará que disminuya tu nivel de reputación con los Arúspices."}},
[10324] = {"La gran cacería lunar", nil, {"Lleva 6 pieles de ciervos lunares a Acteon a la Avanzada Azur."}},
[10325] = {"Marcas de Kil'jaeden", nil, {"Lleva 10 marcas de Kil'jaeden a Adyen el Celador de la Luz en la Ciudad de Shattrath.\n\nCompletar misiones para los Aldor hará que disminuya tu nivel de reputación con los Arúspices."}},
