[10780] = {"Plumas de Sketh'lon", nil, {"Consigue 8 plumas de Sketh'lon y llévaselas a Parshah en los Campos de Magma."}},
[10781] = {"La batalla de la Atalaya Carmesí", nil, {"¡Lucha contra las tropas de Illidan en la Atalaya Carmesí y sobrevive!\n\nVe a ver a A'dal a la Ciudad de Shattrath si tienes éxito."}},
