[10805] = {"Masacre en la Guarida de Gruul", nil, {"Mata 10 agentes Aguja del Filo, 5 magos de batalla Aguja del Filo, 5 devastadores Aguja del Filo y a su líder, Fingrom."}},
[10807] = {"Los Tábidos Lengua de ceniza", nil, {"Varen el Reivindicador quiere que vayas a las Ruinas de Baa'ri y mates a 3 tratantes Lengua de ceniza, 4 guerreros Lengua de ceniza y 6 chamanes Lengua de ceniza.\n\nCompletar misiones para los Arúspices hará que disminuya tu nivel de reputación con los Aldor."}},
[10808] = {"Frustra los planes del Cónclave", nil, {"Mata a los ritualistas del Cónclave Oscuro y usa el bastón de Parshah para acabar con el ritual de invocación del Cónclave Oscuro. Después, vuelve a Parshah, a los Campos de Magma."}},
[10809] = {"SE BUSCA: Maestro de huargos Kruush", nil, {"Mata al maestro de huargos Kruush y entrega su cabeza al capitán Aullaoscuro en Avanzada Rompeloma."}},
[10810] = {"La máscara dañada", nil, {"Entrega la máscara dañada a O'Mally Pillazas en Soto Eterno, en las Montañas Filospada."}},
[10811] = {"Buscar a Neltharaku", nil, {"Busca a Nelthakaru, patrón de los dracos Ala Abisal."}},
[10812] = {"La máscara misteriosa", nil, {"Lleva la máscara misteriosa al Señor Salvaje Antelarion en Soto Eterno, en las Montañas Filospada."}},
[10813] = {"Los ojos de Grillok", nil, {"Usa el fragmento de Zezzak para capturar un ojo de Grillok y acércate a la caldera de Zezzak para extraerlo. Una vez que lo hayas sacado, devuélvele el fragmento a Zezzak."}},
[10814] = {"La historia de Neltharaku", nil, {"Habla con Neltharaku y escucha su historia."}},
[10816] = {"Reclamar los terrenos sagrados", nil, {"El vindicador Aluumen quiere que mates 8 asesinos Sombraluna, 8 elegidos Sombraluna y 4 tejeoscuros Sombraluna.\n\nCompletar misiones para los Aldor hará que disminuya tu nivel de reputación con los Arúspices."}},
[10817] = {"El gran castigo", nil, {"Varen el Reivindicador quiere que mates 8 asesinos Sombraluna, 8 elegidos Sombraluna y 4 tejeoscuros Sombraluna.\n\nCompletar misiones para los Arúspices hará que disminuya tu nivel de reputación con los Aldor."}},
[10819] = {"La máscara de gas jurapenas", nil, {"Ponte la máscara de gas Jurapenas y usa el comunicador de la Legión en Campamento Forja: Inquina, en las Montañas Filospada."}},
[10820] = {"Engañar al enemigo", nil, {"Mata 4 auxiliares de la Forja Maldita y 4 ingenieros de la Forja Maldita."}},
