[10825] = {"La verdad sin orbes", nil, {"Entrega el Orbe de los Grishna al celador de árboles Chawn, de Soto Eterno, en las Montañas Filospada."}},
[10826] = {"Marcas de Sargeras", nil, {"Lleva 10 marcas de Sargeras al presagista Saronen en el Valle Sombraluna.\n\nCompletar misiones para los Aldor hará que disminuya tu nivel de reputación con los Arúspices."}},
