[10829] = {"Troncárbol debe saber", nil, {"Habla con Troncárbol, un anciano del Conocimiento que vive en el Bosque del Cuervo de las Montañas Filospada."}},
[10830] = {"El exorcismo de los árboles", nil, {"Consigue 5 orbes de Grishnath y 5 plumas nacientes atroces y después combínalos para crear plumas de exorcismo. Utiliza las plumas para realizar el exorcismo y matar a 5 espíritus koi-koi de los hojabarba del Bosque del Cuervo."}},
[10832] = {"Convertirse en sastre de fuego de hechizo", nil, {"Lleva una muestra de esencia de ánima abisal a Gidge Tejehechizos en el Bajo Arrabal de Shattrath.\n\n*¡ATENCIÓN!* Solo puedes seleccionar una especialidad de sastrería."}},
[10833] = {"Convertirse en un sastre de tejido de sombra", nil, {"Utiliza el cristal de Sombras profundas cerca de El Altar de las Sombras para estar más en sintonía con el altar. A continuación, vuelve a ver a Andrion Giroscuro en el Bajo Arrabal de Shattrath.\n\n*¡ATENCIÓN!* Solo puedes seleccionar una especialidad de sastrería."}},
[10834] = {"Grillok \"Ojosombra\"", nil, {"Mata a Grillok Ojosombra y lleva el parche del ojo de Grillok a Zezzak en la Avanzada Rompeloma."}},
[10835] = {"El boticario Antonivich", nil, {"Lleva los hallazgos de Albreck al boticario Antonivich a Thrallmar, en la Península del Fuego Infernal."}},
[10836] = {"Infiltrarse en la Fortaleza Faucedraco", nil, {"Neltharaku, que sobrevuela los Campos del Ala Abisal en el Valle Sombraluna, quiere que mates a quince orcos Faucedraco.\n\nBastará con cualquier orco Faucedraco de la Fortaleza Faucedraco."}},
[10837] = {"¡Al Arrecife del Ala Abisal!", nil, {"Neltharaku, que sobrevuela los Campos del Ala Abisal en el Valle Sombraluna, quiere que recojas doce cristales de vid abisal del Arrecife del Ala Abisal.|n"}},
[10838] = {"El cristal de adivinación demoníaco", nil, {"Usa el cristal de adivinación demoníaco en la Cuenca del Fuego Infernal, espera a que se sintonice, pide una trascripción.\n\nDevuelve el cristal de adivinación demoníaco y la trascripción del cristal de adivinación demoníaco al boticario Antonivich de Thrallmar."}},
[10839] = {"Velo Skith: Rocanegra de Terokk", nil, {"Usa la vara de purificación sobre la Rocanegra de Terokk, en Velo Skith, y vuelve a Kirrik el Despierto a la Caravana de Refugiados de El Vertedero de Huesos."}},
[10840] = {"La Tumba de las Luces", nil, {"El sumo sacerdote Orglum quiere que vayas a la Tumba de las Luces y mates a 10 abisálicos etéreos y 10 desvalijadores etéreos. A continuación vuelve a verle a la Caravana de Refugiados."}},
[10842] = {"Almas vengativas", nil, {"El sumo sacerdote Orglum quiere que viajes a la Tumba de las Luces y derrotes a 5 draenei vengativos."}},
[10843] = {"Desde tiempos olvidados...", nil, {"Mata a Gnosh Brognat, líder de los ogros en la Avanzada Aguja del Filo."}},
[10845] = {"Matar a la madre de linaje", nil, {"Mata a Alatemible."}},
[10846] = {"Comprender a los Mok'Nathal", nil, {"Mata a Vekh, líder de los arakkoa Vekh'nir."}},
[10847] = {"Los ojos de Skettis", nil, {"Lleva el ojo de Reskk y el ojo de Shienor a Rilak el Redimido en el Bajo Arrabal, en Shattrath."}},
[10848] = {"Velo Rhaze: el mal no viviente", nil, {"Mata 4 espíritus Garramuerte, 4 espíritus estridentes, 2 espíritus de cuervo y después ve a ver a Kirrik el Despierto a la Caravana de Refugiados."}},
[10849] = {"Busca a Kirrik", nil, {"Lleva la misiva de Rilak a Kirrik el Despierto en la Caravana de Refugiados, en El Vertedero de Huesos."}},
[10850] = {"Gas abisal en una máquina de fuego vil", nil, {"Requisitos:"}},
[10851] = {"Los tótems de mi enemigo", nil, {"Recoge 5 tótems Aguja del Filo."}},
[10852] = {"Amigos desaparecidos", nil, {"Ethan quiere que encuentres a doce de sus amigos desaparecidos en Velo Skith y que luego vuelvas con él a la Caravana de Refugiados."}},
[10853] = {"Llamamiento de espíritus", nil, {"Usa el Tótem Llamada de espíritu para recoger ocho espíritus de draco abisal inferiores."}},
[10854] = {"La fuerza de Neltharaku", nil, {"Neltharaku, que sobrevuela los Campos del Ala Abisal, en el Valle Sombraluna, quiere que liberes a 5 dracos Ala Abisal esclavizados."}},
[10855] = {"¡Atracadores viles, no gracias!", nil, {"Consigue 5 unidades de gas abisal condensado de los mekigenieros Gan'arg de la Base Forja: Olvido, al noroeste de La Flecha de la Tormenta y mételas en un atracador vil cercano inactivo. Vuelve a ver al acechador abisal Nauthis cuando hayas completado la tarea."}},
[10856] = {"La mejor defensa", nil, {"El acechador abisal Nauthis quiere que mates 12 encolerizadores en la Base Forja: Gehenna, al norte de La Flecha de la Tormenta."}},
[10857] = {"¡Teletransporta esto!", nil, {"El acechador abisal Nauthis quiere que utilices la Vara de interferencia mental con los Mo'arg en la Base Forja: Gehenna y uses su habilidad de detonar teletransportadores en los tres teletransportadores del Campamento Forja."}},
[10858] = {"Karynaku", nil, {"Busca a Karynaku en la Fortaleza Faucedraco."}},
[10859] = {"Reunir los orbes", nil, {"Usa el tótem colector de orbes para recoger 15 orbes de Luz Razaani."}},
[10860] = {"Regalos de Mok'Nathal", nil, {"Reúne 3 costillas de raptor y 3 piezas de carne de serpiente."}},
[10861] = {"Velo Lítico: ataque preventivo", nil, {"Rompe los huevos malditos de Velo Lítico. Redime a 3 proles y mata a 3 proles malévolas antes de volver con Kirrik el Despierto a la Caravana de Refugiados."}},
[10862] = {"Ríndete ante la Horda", nil, {"Rokag quiere que busques a Rilak el Redimido en el Bajo Arrabal de Shattrath y exijas que él y los suyos se rindan ante la Horda."}},
[10863] = {"Secretos de los arakkoa", nil, {"Lady Dena Kennedy quiere que busques a Rilak el Redimido en el Bajo Arrabal de Shattrath y descubras los secretos que sabe sobre los arakkoa."}},
[10864] = {"Una carga de almas", nil, {"Mata a 20 orcos Mano Destrozada y vuelve con el boticario Antonivich, a Thrallmar."}},
[10865] = {"¡Informa a Leoroxx!", nil, {"Habla con Leoroxx sobre lo que los etéreos de Razaani están tramando. Se le puede encontrar en la Aldea Mok'Nathal, en las Montañas Filospada."}},
[10866] = {"Zuluhed el Demente", nil, {"Mata a Zuluhed el Demente y consigue la llave de Zuluhed. Utiliza la llave de Zuluhed en las cadenas de Zuluhed para liberar a Karynaku."}},
[10867] = {"Solo puede haber una respuesta", nil, {"Recupera la colección de almas."}},
[10868] = {"La ruta de guerra de los arakkoa", nil, {"La consejera Faila te ha pedido que mates a 14 garrosos Shienor, 6 hechiceros Shienor y que, después, vuelvas a verla en el Bastión Rompepedras."}},
[10869] = {"Disminuir la manada", nil, {"El teniente Gravamartillo te ha pedido que mates 14 garrosos Shienor, 6 hechiceros Shienor y que, después, vuelvas a verle en el Bastión Allerian."}},
[10870] = {"Aliado del Ala Abisal", nil, {"Deja que Karynaku te envíe de vuelta a Mordenai en los Campos del Ala Abisal."}},
[10871] = {"Aliado del Ala Abisal", nil, {"Deja que Karynaku te envíe de vuelta a Mordenai en los Campos del Ala Abisal."}},
[10872] = {"Zuluhed el Demente", nil, {"Mata a Zuluhed el Demente y consigue la llave de Zuluhed. Utiliza la llave de Zuluhed en las cadenas de Zuluhed para liberar a Karynaku."}},
[10873] = {"Tomados en la noche", nil, {"El explorador Navrin quiere que vayas a la Cresta Red Abisal, liberes a 6 guerreros atrapados con telaraña de Sha'tar y vuelvas con él al Campamento Sha'tari."}},
[10874] = {"Velo Shalas: señales de fuego", nil, {"Kirrik el Despierto de la Caravana de Refugiados quiere que extingas las cuatro señales de fuego de Velo Shalas."}},
[10875] = {"Informar a Nazgrel", nil, {"Habla con Nazgrel en Thrallmar, en la Península del Fuego Infernal."}},
[10876] = {"El pie de la ciudadela", nil, {"Mata al comandante en jefe Gorax, coloca el Desafío de la Horda sobre su cadáver, mata a la Mano de Kargath, y lleva la cabeza de la Mano de Kargath a Nazgrel, en Thrallmar."}},
[10877] = {"La reliquia de pavor", nil, {"Roblun quiere que viajes al este, hasta la Caravana Derelicta para recuperar la reliquia de pavor. Vuelve con Roblun cuando hayas completado la tarea."}},
[10878] = {"Antes de que caiga la oscuridad", nil, {"Mekeda, de la Caravana de Refugiados quiere que mates a 8 hostigadores de la Cábala, 4 tejehechizos de la Cábala y 2 iniciados a la Cábala."}},
[10879] = {"La ofensiva de Skettis", nil, {"Resiste a los atacantes y habla con Rilak el Redimido."}},
[10880] = {"Órdenes de la Cábala", nil, {"Lleva las órdenes de la Cábala a Mekeda, en la Caravana de Refugiados en el Vertedero de Huesos."}},
[10881] = {"La Tumba Umbría", nil, {"Ve a la Tumba Umbría, al oeste de la Caravana de Refugiados, y recupera el mantón de Arunen, el mazo de K'alen y el pergamino de Atalor. Vuelve con Mekeda a la Caravana de Refugiados cuando hayas completado esta tarea."}},
[10882] = {"Presagista de la Fatalidad", nil, {"Se te ha encomendado que vayas al satélite Arcatraz de El Castillo de la Tempestad y mates al Presagista Cieloriss. Vuelve con A'dal al Bancal de la Luz, en la Ciudad de Shattrath, cuando lo hayas hecho."}},
[10883] = {"La llave de la tempestad", nil, {"Habla con A'dal en la Ciudad de Shattrath.|n"}},
[10884] = {"Prueba de los naaru: piedad", nil, {"A'dal de la Ciudad de Shattrath quiere que recuperes el hacha del verdugo sin usar en Las Salas Arrasadas de la Ciudadela del Fuego Infernal.\n\nEsta misión debe completarse en dificultad de mazmorra heroica."}},
[10885] = {"Prueba de los naaru: fuerza", nil, {"A'dal de la Ciudad de Shattrath quiere que recuperes el tridente de Kalithresh y la esencia de Murmullo.\n\nEsta misión debe completarse en dificultad de mazmorra heroica."}},
[10886] = {"Prueba de los naaru: tenacidad", nil, {"A'dal, de la Ciudad de Shattrath, quiere que rescates a Molino Tormenta de Maná del Arcatraz de El Castillo de la Tempestad.\n\nEsta misión debe completarse en dificultad de mazmorra heroica."}},
[10887] = {"Escapar de la tumba", nil, {"Ayuda a Akuno a encontrar su camino hasta la Caravana de Refugiados. Habla con Mekeda cuando hayas completado esta misión."}},
[10888] = {"Prueba de los naaru: Magtheridon", nil, {"A'dal en la Ciudad de Shattrath quiere que mates a Magtheridon."}},
[10889] = {"Regresa a Shattrath", nil, {"Viaja a la Ciudad de Shattrath y habla con Rilak el Redimido en el Bajo Arrabal."}},
[10890] = {"[UNUSED] <NYI> ", nil, nil},
[10891] = {"Armadura de placas imperiales", nil, {"Habla con Derotain Sorbelodo en Gadgetzan, en Tanaris.|n"}},
[10892] = {"Armadura de placas imperiales", nil, {"Habla con Derotain Sorbelodo en Gadgetzan, en Tanaris.|n"}},
[10893] = {"Largacola es el eje", nil, {"Elimina a Draaca Largacola, la líder de los cultores del Culto Vermis, en el Túnel Calavermis."}},
[10894] = {"Vigía Calavermis", nil, {"Entrega la nota de reunión al vigía Sombra Lunar en la entrada al Túnel Calavermis, en las Montañas Filospada."}},
[10895] = {"¡Zeth'Gor debe arder!", nil, {"Utiliza la baliza de humo en cada una de las torres de Zeth'Gor y, después, vuelve con el comandante del aire Dabir'ee a Punta de Expedición."}},
[10896] = {"Los protectores infestados", nil, {"Lakotae quiere que mates 25 cucarachas de bosque y después vuelvas junto a él a la Caravana de Refugiados. Las cucarachas viven en los cuerpos de furibundos de bosque en podredumbre y caminarraíces infectados.  "}},
[10897] = {"Maestro en pociones", nil, {"Lauranna Thar'well quiere que vayas a El Invernáculo de El Castillo de la Tempestad y consigas la Guía de Campo para Botánicos del gran botánico Freywenn. Además, quiere que le lleves 5 superpociones de sanación, 5 superpociones de maná y 5 pociones de letargo sin sueño sublimes.\n\n*¡ADVERTENCIA!* Solo podrás elegir una especialización de alquimia."}},
[10898] = {"Alacielo", nil, {"Escolta a Alacielo y preséntate ante Rilak el Redimido en la Ciudad de Shattrath."}},
[10899] = {"Maestro de transmutación", nil, {"Zarevhi, de la Flecha de la Tormenta, en Tormenta Abisal, quiere que le lleves 4 muestras de poderío primigenio.\n\n*¡ATENCIÓN!* Solo puedes seleccionar una especialidad de alquimia."}},
[10900] = {"La marca de Vashj", nil, nil},
[10901] = {"La cayada de Kar'desh", nil, {"Skar'this el Herético, en El Reciento de los Esclavos heroico, en la Reserva Colmillo Torcido, quiere que le lleves el sello terráneo y el sello llameante."}},
[10902] = {"Maestro en elixires", nil, {"Ve a La Ciénaga Negra, en las Cavernas del Tiempo, y consigue 10 esencias de infinidad de los señores de la falla y los vigilantes de la falla. Llévalas, junto con 5 elixires de defensa sublime, 5 elixires de maestría y 5 elixires de agilidad sublime a Lorokeem, en el Bajo Arrabal de Shattrath.\n\n*¡ATENCIÓN!* Solo puedes seleccionar una especialidad de alquimia."}},
[10903] = {"Regresa al Bastión del Honor", nil, {"Habla con el ayudante Klatu, en la posada del Bastión del Honor."}},
[10904] = {"Recoger la munición vil", nil, {"Recoge cinco balas de cañón vil."}},
[10905] = {"Maestro en pociones", nil, {"Habla con Lauranna Thar'well en el Refugio Cenarion en la Marisma de Zangar.\n\n*¡ADVERTENCIA!* Solo podrás elegir una especialización de alquimia."}},
[10906] = {"Maestro en elixires", nil, {"Habla con Lorokeem en el Bajo Arrabal de Shattrath.\n\n*¡ADVERTENCIA!* Solo podrás elegir una especialización de alquimia."}},
[10907] = {"Maestro de transmutación", nil, {"Habla con Zarevhi en La Flecha de la Tormenta, en la Tormenta Abisal.\n\n*¡ADVERTENCIA!* Solo podrás elegir una especialización de alquimia."}},
[10908] = {"Hablar con Rilak el Redimido", nil, {"Kirrik el Despierto quiere que hables con Rilak el Redimido en el distrito del Bajo Arrabal de Shattrath."}},
[10909] = {"Espíritus viles", nil, {"Klatu, del Bastión del Honor quiere que uses la reliquia anacoreta en la Avanzada Gor'gaz, al suroeste del Bastión. Mata a todos los rabiosos Mano Destrozada que encuentres cerca de la reliquia y luego mata a 10 espíritus viles liberados para vengar al coronel Jules."}},
[10910] = {"La Puerta de la Muerte", nil, {"Usa la señal para druida en la Puerta de la Muerte para llamar a un druida Soto Eterno para poder hablar con él."}},
[10911] = {"¡Fuego a discreción!", nil, {"Usa la munición naturalizada para controlar los cañones viles de la Puerta de la Muerte. Úsalos para destruir el portal de distorsión del sur y el portal de distorsión del norte."}},
[10913] = {"Un entierro indecoroso", nil, {"El comandante Ra'vaj, del Campamento Sha'tari, en El Vertedero de Huesos del Bosque de Terokkar, quiere que quemes 8 guerreros Auchenai asesinados y 8 vindicadores Sha'tar asesinados."}},
[10914] = {"Se necesita un héroe", nil, {"El comandante Ra'vaj del Campamento Sha'tari, en El Vertedero de Huesos del Bosque de Terokkar, quiere que mates a 12 iniciados Auchenai y a 5 oradores del Sino Auchenai."}},
[10915] = {"El exarca caído", nil, {"El comandante Ra'vaj del Campamento Sha'tari, en El Vertedero de Huesos del Bosque de Terokkar, quiere que destruyas el contenido del ataúd Auchenai."}},
[10916] = {"Desenterrando abalorios de oración", nil, {"Recupera los abalorios de oración Draenei y llévaselas al ayudante Klatu, al Bastión del Honor."}},
[10917] = {"La situación apremiante del marginado", nil, {"Lleva 30 plumas de arakkoa a Vekax en el Bajo Arrabal de Shattrath."}},
[10918] = {"Más plumas", nil, {"Requisitos:"}},
[10919] = {"El tesoro de Fei Fei", nil, {"Requisitos:"}},
[10920] = {"Por los caídos", nil, {"El vindicador Haylen quiere que mates a 20 viudas Colminferno y, después, vuelvas con él, justo al oeste de Cresta Red Abisal."}},
[10921] = {"Terokkarántula", nil, {"El vindicador Haylen quiere que mates a la Terokkarántula y, después, vuelvas con él, al oeste de Cresta Red Abisal."}},
[10922] = {"Excavando huesos", nil, {"Escolta a Letoll y a sus hombres y protégelos mientras excavan. Vuelve a ver a Enaniski al Campamento Sha'tari si tienes éxito.|n"}},
[10923] = {"El mal se acerca", nil, {"Roblun quiere que lleves la reliquia de pavor al Alcor Tortuoso. Cuando estés allí tienes que matar médiums y oradores del Sino Auchenai para conseguir 20 calaveras de fatalidad. Después tendrás que encontrar el círculo de invocación del Alcor Tortuoso y usar la reliquia de pavor para invocar y acabar con Teribus el Maldito. Vuelve a ver a Roblun cuando lo hayas hecho."}},
[10924] = {"¡Totalmente im-posible!", nil, {"Lleva 8 viales de sangre de persecutor de distorsión a Sab'aoth, en Cosmotirón."}},
[10926] = {"Regresa al Campamento Sha'tari", nil, {"El vindicador Haylen quiere que vuelvas al Campamento Sha'tari, en el Vertedero de Huesos, y hables con el explorador Navrin."}},
[10927] = {"Matando a los reptadores", nil, {"Mata 6 reptadores de cavernas."}},
[10928] = {"Matando a los reptadores", nil, {"Mata 6 reptadores de cavernas."}},
[10929] = {"Gusanos", nil, {"Enaniski en Campamento Sha'tari, en El Vertedero de Huesos, quiere que le lleves 8 cadáveres de matizahuesos adultos."}},
[10930] = {"El gran gusano de hueso", nil, {"Enaniski de Campamento Sha'tari, en El Vertedero de Huesos, quiere que consigas los órganos del gran gusano de hueso.\n\nTambién quiere su tambor.|n"}},
[10931] = {"Regresa a El Exodar", nil, {"Habla con Caedmos en El Exodar."}},
[10932] = {"Regresa a El Exodar", nil, {"Habla con Caedmos en El Exodar."}},
[10933] = {"Regresa a Lunargenta", nil, {"Habla con Aldrae en La Ciudad de Lunargenta."}},
[10934] = {"Regresa a Lunargenta", nil, {"Habla con Aldrae en La Ciudad de Lunargenta."}},
[10935] = {"El exorcismo del coronel Jules", nil, {"Habla con el anacoreta Barada. Utiliza los abalorios de oración para ayudar con el ritual y después habla con el coronel Jules cuando esté bien. Por último, vuelve a ver al ayudante Klatu."}},
[10936] = {"Aterratrols te está buscando", nil, {"El asistente Klatu te ha dicho que el comandante en jefe Danath Aterratrols te está buscando. Ve a verle al cuartel del Bastión del Honor, en la Península del Fuego Infernal."}},
[10937] = {"Acaba con el maestro de maniobras", nil, {"El comandante en jefe Danath Aterratrols te ha pedido que mates al maestro de maniobras Zurok inmediatamente. Vuelve a ver al comandante en jefe del Bastión del Honor en la Península del Fuego Infernal cuando hayas matado al maestro de maniobras."}},
[10938] = {"La baraja de Bendiciones de la Luna Negra", nil, {"Lleva la baraja de Bendiciones a la Feria de la Luna Negra cuando pase por la ciudad."}},
[10939] = {"La baraja de Tormentas de la Luna Negra", nil, {"Lleva la baraja de Tormentas a la Feria de la Luna Negra cuando pase por la ciudad."}},
[10940] = {"La baraja de Furias de la Luna Negra", nil, {"Lleva la baraja de Furias a la Feria de la Luna Negra cuando pase por la ciudad."}},
[10941] = {"La baraja de Locuras de la Luna Negra", nil, {"Lleva la baraja de Locuras a la Feria de la Luna Negra cuando pase por la ciudad."}},
[10942] = {"Semana de los Niños", nil, {"Usa el silbato de huérfano elfo de sangre para llamar a la niña a la que cuidarás durante la Semana de los Niños."}},
[10943] = {"Semana de los Niños", nil, {"Usa el silbato de huérfano draenei para llamar a la niña a la que cuidarás durante la Semana de los Niños."}},
[10944] = {"El secreto en peligro", nil, {"Viaja hasta la Jaula de la Guardiana del Valle Sombraluna y habla con Akama."}},
[10945] = {"Hch'uu y el pueblo champiñón", nil, {"Lleva a tu huérfana, Salandria a Esporaggar en la Marisma de Zangar. Asegúrate de llamarla si no está presente cuando llegues. Luego habla con Hch'uu."}},
[10946] = {"Artimaña de los Lengua de ceniza", nil, {"Viaja hasta El Castillo de la Tempestad y mata a Al'ar con la capucha de Lengua de ceniza puesta. Vuelve junto a Akama en el Valle Sombraluna cuando hayas terminado."}},
[10947] = {"Un artefacto del pasado", nil, {"Ve a las Cavernas del Tiempo en Tanaris y consigue entrar en la Batalla del Monte Hyjal. Una vez dentro, vence a Ira Fríoinvierno y llévale la filacteria de fase temporal a Akama en el Valle Sombraluna."}},
[10949] = {"La entrada a El Templo Oscuro", nil, {"Viaja hasta la entrada del Templo Oscuro en el Valle Sombraluna y habla con Xi'ri."}},
[10950] = {"Auchindoun y El Círculo de la Observancia", nil, {"Lleva a tu huérfana, Dornaa, a la roca de encuentro que hay en el Círculo de la Observancia, en medio de Auchindoun. Auchindoun, por su parte, está en medio de El Vertedero de Huesos del Bosque de Terokkar. Asegúrate de que la llamas si no está presente cuando llegues."}},
[10951] = {"Un viaje al Portal Oscuro", nil, {"Lleva a tu huérfana, Salandria, a ascender por Los Peldaños del Destino para plantarse delante del Portal Oscuro en la Península del Fuego Infernal. Asegúrate de llamarla si no está ahí cuando llegues."}},
[10952] = {"Un viaje al Portal Oscuro", nil, {"Lleva a tu huérfana, Dornaa, a ascender por Los Peldaños del Destino para plantarse delante del Portal Oscuro en la Península del Fuego Infernal. Asegúrate de llamarla si no está ahí cuando llegues."}},
[10953] = {"Visita a El Trono de los Elementos", nil, {"Lleva a tu huérfana, Salandria, hasta El Trono de los Elementos en Nagrand. Asegúrate de llamarla si no está ya allí cuando llegues. Luego, habla con el elementalista Sharvak."}},
[10954] = {"¡Jheel está en el Desembarco Aeris!", nil, {"Lleva a tu huérfana, Dornaa, al Desembarco Aeris en Nagrand. Asegúrate de llamarla si no está allí cuando llegues. Luego habla con Jheel."}},
[10955] = {"Morthis Alasusurro", nil, {"Busca a Morthis Alasusurro en el Refugio Cenarion de la Marisma de Zangar."}},
[10956] = {"El Trono de los Naaru", nil, {"Lleva a tu huérfana, Dornaa, hasta O'ros a los pies del Trono de los Naaru, dentro de El Exodar, que está en la Isla Bruma Azur. Asegúrate de que la llamas si no está ya allí cuando llegues.\n\nRecuerda que puedes usar el mapa dentro de la ciudad."}},
[10957] = {"Redención de los Lengua de ceniza", nil, {"Ayuda a Akama a recuperar el control de su alma derrotando a la Sombra de Akama dentro del Templo Oscuro. Vuelve junto al vidente Kanai cuando hayas completado esta misión."}},
[10958] = {"Búsqueda de los Lengua de ceniza", nil, {"Encuentra a los Juramorte de Akama dentro del Templo Oscuro."}},
[10959] = {"La caída de El Traidor", nil, {"El vidente Kanai quiere que derrotes a Illidan dentro del Templo Oscuro."}},
[10960] = {"Cuando sea mayor...", nil, {"Lleva a tu huérfana, Salandria, a los instructores de paladines de la Plaza del Errante de la Ciudad de Lunargenta. Asegúrate de llamarla si no está presente cuando llegues. Luego, habla con Lady Liadrin, la matriarca de los Caballeros de sangre.\n\nRecuerda que puedes usar tu mapa dentro de la ciudad y puedes hablar con los guardias para encontrar a los instructores de paladines."}},
[10961] = {"El resguardo de despertar", nil, {"Lleva 10 cardos de Mantar, 10 líquenes antiguos y 10 glorias de ensueño a Morthis Alasusurro al Refugio Cenarion."}},
[10962] = {"Hora de visitar las Cavernas", nil, {"Lleva a tu huérfana, Dornaa, ante Zaladormu, el dragón del estrado que se encuentra en el centro de las Cavernas del Tiempo. Asegúrate que la llamas si no está presente cuando llegas.\n\nLuego, cómprale un dragón de juguete al intendente de los Vigilantes del Tiempo, Alurmi, cerca del fin del túnel de la entrada."}},
[10963] = {"Hora de visitar las Cavernas", nil, {"Lleva a tu huérfana, Salandria, ante Zaladormu, el dragón del estrado que se encuentra en el centro de las Cavernas del Tiempo. Asegúrate que la llamas si no está presente cuando llegas.\n\nLuego, cómprale un dragón de juguete al intendente de los Vigilantes del Tiempo, Alurmi, cerca del fin del túnel de la entrada."}},
[10964] = {"Despertar al durmiente", nil, {"Usa el resguardo de despertar para ayudar a Clintar Caminasueños a salir del Sueño Esmeralda. Cuando recobre el conocimiento, habla con él."}},
[10965] = {"Se acabó el sueño", nil, {"Protege a Clintar Caminasueños mientras reúne las reliquias de Aviana, luego habla con el celador de sueños Lurosa."}},
[10966] = {"De vuelta al orfanato", nil, {"Devuelve a tu pupila al orfanato de la Ciudad de Shattrath. Entrégale el silbato de draenei huérfano a la supervisora de huérfanos Piedad en el Bajo Arrabal."}},
[10967] = {"De vuelta al orfanato", nil, {"Devuelve a tu pupila al orfanato de la Ciudad de Shattrath. Entrégale el silbato de huérfano elfo de sangre a la supervisora de huérfanos Piedad en el Bajo Arrabal."}},
[10968] = {"Llamada al clarividente", nil, {"Lleva a tu huérfana, Dornaa, a visitar al Clarividente Nobundo en la Sala de Cristal, dentro de El Exodar, que está en la Isla Bruma Azur. Asegúrate de que la llamas si no está ya allí cuando llegues.\n\nRecuerda que puedes usar el mapa dentro de la ciudad."}},
[10969] = {"Busca a Ameer", nil, {"Busca al comandante Ameer a la Avanzada del Protectorado."}},
[10970] = {"Una misión de piedad", nil, {"Consigue la Llave de la prisión de El Etereum rescatada para el comandante Ameer en la Avanzada del Protectorado de Tormenta Abisal."}},
[10971] = {"Secretos de El Etereum", nil, {"El comandante Ameer de la Avanzada del Protectorado de Tormenta Abisal quiere que le lleves una etiqueta de identificación de prisionero de El Etereum."}},
[10972] = {"Catálogo de identificación de prisioneros de El Etereum", nil, {"Requisitos:"}},
[10973] = {"Mil mundos", nil, {"El comandante Ameer de la Avanzada del Protectorado de Tormenta Abisal quiere que le lleves 5 llaves de prisión de El Etereum."}},
[10974] = {"Cámaras de Estasis de Bash'ir", nil, {"Usa la tablilla de llave de El Etereum - Alfa para abrir la Cámara de Estasis Alfa en el Alto Bash'ir de las Montañas Filospada.\n\nVuelve con evidencias de Alfa a la imagen del comandante Ameer en el Alto Bash'ir."}},
[10976] = {"La marca del Rey del Nexo", nil, {"La imagen del comandante Ameer en el Alto Bash'ir de las Montañas Filospada quiere que le traigas una marca del Rey del Nexo."}},
[10977] = {"Cámaras de Estasis de las Tumbas de Maná", nil, {"La imagen del comandante Ameer del Alto Bash'ir en las Montañas Filospada quiere que uses la llave de la Cámara de Estasis de las Tumbas de Maná en las Tumbas de Maná de Auchindoun.\n\nEsta misión solo se puede completar en modo heroico."}},
[10979] = {"A Soto Eterno", nil, {"Habla con Arthorn Son del Viento de Soto Eterno en las Montañas Filospada."}},
[10980] = {"El Libro del Cuervo", nil, {"Usa el ojo de raya de éter con tu piedra de vidente para que se muestren los fantasmas del culto del cuervo. Busca La Cumbre del Vórtice para el espíritu de Sai'kkal el Anciano e interrógalo sobre el Libro del Cuervo. Después vuelve a Arthorn Son del Viento."}},
[10983] = {"Mog'dorg el Marchito", nil, {"Visita a Mog'dorg el Marchito en lo alto de una de las torres que hay justo fuera del Anillo de Sangre en las Montañas Filospada."}},
[10984] = {"Habla con el ogro", nil, {"Habla con Grok, el ogro, en el Bajo Arrabal de la Ciudad de Shattrath."}},
[10985] = {"Una distracción para Akama", nil, {"Asegúrate de que Akama y Maiev entran en El Templo Oscuro, en el Valle Sombraluna, cuando las fuerzas de Xi'ri creen una distracción."}},
[10987] = {"Atrapar un gavilán", nil, {"Usa la red para gavilanes para capturar un gavilán salvaje y llévaselo a la vigía Elaira en la Cresta del Crepúsculo."}},
[10988] = {"Las piedras del Cuervo", nil, {"Reúne 8 Piedras del Cuervo de Skettis y llévaselas a Arthorn Son del Viento a Soto Eterno, en las Montañas Filospada."}},
[10989] = {"Mog'dorg el Marchito", nil, {"Visita a Mog'dorg el Marchito, que se encuentra en lo alto de la torre donde está Chort, justo fuera del Anillo de Sangre en las Montañas Filospada."}},
[10990] = {"La esencia del Águila", nil, {"Derrota al guardián del Águila y obtén la esencia del Águila. Usa el silbato para gavilanes de Arthorn para llamar al gavilán de Arthorn para que lleve las noticias de tu éxito.\n\nSi pierdes el silbato de gavilanes de Arthorn, vuelve a ver a Arthorn Son del Viento en Soto Eterno para que te dé otro."}},
[10994] = {"Tras la piedra lunar", nil, {"Lleva una piedra lunar de Furia del Sur a Morthis Alasusurro al Refugio Cenarion."}},
