[11308] = {"Es hora de limpiar", nil, {"Habla con \"Sierra\" Jenny en Nuevo Agamand."}},
[11309] = {"Partes para el trabajo", nil, {"\"Sierra\" Jenny en Nuevo Agamand quiere que consigas 6 ligamentos de colmipala y 1 porción de medio kilo de carne fresca."}},
