[11569] = {"El maestro de llaves Urmgrgl", nil, {"Glrglrglr en el interior de las Cavernas Aleta Invernal te ha pedido que consigas la llave de Urmgrgl."}},
[11570] = {"Escapa de las Cavernas Aleta Invernal", nil, {"Escolta a Lurgglbr hasta un lugar seguro y después dale la buena noticia al rey Mrgl-Mrgl en el Refugio Aleta Invernal."}},
[11571] = {"Aprender a comunicarse", nil, {"El rey Mrgl-Mrgl te ha dado una caracola para que la uses sobre los restos de Escaldadora. Devuélvele la caracola del Rey llena en el Refugio Aleta Invernal."}},
[11572] = {"Regresa junto a Atuik", nil, {"Dale las malas noticias al ancestro Atuik en Komawa."}},
[11573] = {"Orfus de Komawa", nil, {"Encuentra a Orfus de Komawa en El Antiguo Elevador de Fiordo Aquilonal."}},
[11574] = {"Por los pelos", nil, {"Endorah quiere que vayas a El Saliente Ámbar al noroeste del Bastión Grito de Guerra y les des las gracias a los magos por haberla rescatado."}},
[11575] = {"Justo a tiempo", nil, {"Menudencia de la Fortaleza Denuedo quiere que vayas hasta El Saliente Ámbar al norte y des las gracias a quienes la rescataron."}},
[11576] = {"Observar la falla: anomalía del Acantilado Agrietado", nil, {"Garren de El Saliente Ámbar quiere que utilices el arcanómetro en la anomalía del Acantilado Agrietado."}},
[11578] = {"La misión \"Chow\" (123)aa COPIA", nil, {"Matar alimaña kóbold, dos."}},
[11579] = {"La misión \"Chow\" (123)aa COPIA", nil, {"Matar alimaña kóbold, dos."}},
[11580] = {"¡Profana este fuego!", nil, nil},
[11581] = {"¡Profana este fuego!", nil, nil},
[11582] = {"Observar la falla: Sima Quebrada", nil, {"Garren en El Saliente Ámbar quiere que uses el arcanómetro en la anomalía de la Sima Quebrada y que vuelvas con las lecturas."}},
[11583] = {"Honrar la llama", nil, nil},
[11585] = {"La vigilia de Grito Infernal", nil, {"Habla con Garrosh Grito Infernal en Bastión Grito de Guerra."}},
[11587] = {"Rescata como puedas", nil, {"El bibliotecario Donathan en El Saliente Ámbar quiere que consigas la llave de la prisión de Berilo de alguno de los cazadores de magos de Berilo y la uses para liberar de su prisión arcana a un mago atrapado."}},
[11590] = {"Abducción", nil, {"Donathan quiere que utilices la mordaza Arcana sobre un hechicero de Berilo en Alto de Berilo y lo lleves hasta él en El Saliente Ámbar."}},
[11591] = {"Preséntate en la Caravana de Quijacero", nil, {"Habla con la bruta Puñofuria en la Caravana de Quijacero."}},
[11592] = {"¡Atacamos!", nil, {"Ayuda al enviado Pezuña Orgullosa y a sus asaltantes Grito de Guerra a sobrevivir al ataque en la Caravana de Quijacero y a acabar con el espíritu del comandante en jefe Quijacero.\n\nSi lo consigues, informa al señor supremo Bor'gorok en la Avanzada Bor'gorok."}},
[11593] = {"Homenaje a los difuntos", nil, {"La bruta Puñofuria en la Caravana de Quijacero te ha pedido que uses la antorcha de Puñofuria para quemar cualquier combinación de 10 guardias y trabajadores de la caravana muertos."}},
[11594] = {"Ayúdalos a encontrar la paz", nil, {"Devuelve la paz a 20 espíritus taunka, ya sean sabios fantasmales o enviados resucitados y después vuelve a informar a la bruta Puñofuria en la Caravana de Quijacero."}},
[11596] = {"La defensa del Bastión Grito de Guerra", nil, {"Preséntate ante el señor supremo Razgor en la barraca del flanco sur del Bastión Grito de Guerra."}},
[11598] = {"La reconquista de la Cantera de Piedra de Poderío", nil, {"El señor supremo Razgor en el Bastión Grito de Guerra quiere que mates a 15 nerubianos Nerub'ar."}},
[11599] = {"Thassarian, hermano mío", nil, {"Busca a William Allerton en la Mina de Lindeallá."}},
[11600] = {"El difunto William Allerton", nil, {"Llévale la tarjeta de alistamiento a Leryssa a la Fortaleza Denuedo, en la Tundra Boreal."}},
[11601] = {"Perdido y hallado", nil, {"Habla con James Diácono en la posada de la Fortaleza Denuedo."}},
[11602] = {"Elimina el problema de raíz", nil, {"El señor supremo Razgor, en el Bastión Grito de Guerra, quiere que destruyas 10 sacos de huevos Nerub'ar."}},
[11603] = {"In vino veritas", nil, {"Recupera una botella de vino de Kul Tiras del barco hundido al noreste de la Fortaleza Denuedo y llévasela al viejo Colburn en la posada de la Fortaleza Denuedo."}},
[11604] = {"Un desertor", nil, {"Encuentra la cárcel dentro del cuartel en la Fortaleza Denuedo y habla con el desertor."}},
[11605] = {"Homenaje a los ancestros", nil, {"El ancestro Atkanok a las afueras de la Cantera Frioescollo quiere que identifiques las estatuas del ancestro Kesuk, el ancestro Sagani y del ancestro Takret."}},
[11606] = {"La paciencia es una virtud que no necesitamos", nil, {"El intendente Holgoth en el Bastión Grito de Guerra quiere que recuperes 15 cajones de municiones Grito de Guerra."}},
[11607] = {"Los espíritus perdidos", nil, {"El ancestro Atkanok a las afueras de la Cantera Frioescollo quiere que liberes a los espíritus de 3 artesanos de Kashala y de 3 chamanes de Kashala."}},
[11608] = {"¡Entierra a esas cucarachas!", nil, {"El intendente Holgoth en el Bastión Grito de Guerra quiere que uses el fajo de cargas de profundidad de seforio para destruir los sumideros Nerub'ar al sur, este, oeste y norte del Bastión Grito de Guerra."}},
[11609] = {"Recoger los pedazos", nil, {"Llévale 6 objetos rituales colmillarr al ancestro Atkanok, que se encuentra fuera de la Cantera Frioescollo."}},
[11610] = {"Llevar a los ancestros a casa", nil, {"El ancestro Atkanok quiere que guíes a los espíritus del ancestro Kesuk, del ancestro Sagani y del ancestro Takret hasta sus monumentos de piedra."}},
[11611] = {"Retenidos por la Plaga", nil, {"El supervisor Mortuus del Bastión Grito de Guerra quiere que liberes 5 peones Grito de Guerra."}},
[11612] = {"Recuperar la cantera", nil, {"Etaruk, que está acampado cerca de la entrada a la Cantera Frioescollo, quiere que mates a 12 cazatesoros de Berilo."}},
[11613] = {"El juramento de Karuk", nil, {"Karuk quiere que mates a 6 asaltantes Skadir y a 5 barqueros Skadir."}},
[11614] = {"Verdades jamás contadas", nil, {"Encuentra al acechasombras Luther en la Cantera Piedra de Poderío."}},
[11615] = {"Secretos Nerub'ar", nil, {"Llévale el diario de Luther al acechasombras Barthus al Bastión Grito de Guerra."}},
[11616] = {"Mensaje para Grito Infernal", nil, {"Llévale la nota de Barthus a Garrosh Grito Infernal al Bastión Grito de Guerra."}},
[11617] = {"Impedir que escapen", nil, {"Etaruk, fuera de la Cantera Frioescollo, quiere que destruyas la plataforma norte, la plataforma este y la plataforma oeste."}},
[11618] = {"Llegan los refuerzos...", nil, {"Busca al acechasombras Ickoris en las Granjas Grito de Guerra al oeste del Bastión Grito de Guerra."}},
[11619] = {"Gamel el Cruel", nil, {"Karuk quiere que derrotes a Gamel el Cruel en el Litoral Tralladón."}},
[11620] = {"Las palabras de un padre", nil, {"Busca a Veehja en el Santuario de Escamas en el Litoral Tralladón."}},
[11621] = {"La tablilla de Leviroth", nil, {"Vuelve a ver a Karuk con la información que has encontrado en la tablilla."}},
[11623] = {"Una visita al comisario", nil, {"Etaruk, en las afueras de la Cantera Frioescollo, quiere que mates al comisario Insivius."}},
[11624] = {"El cielo lo sabrá", nil, {"Habla con Imperean en las Ruinas de Eldra'nath."}},
[11625] = {"El tridente de Naz'jan", nil, {"Veehja en Tralladón quiere que vayas al extremo noreste de las Ruinas Tralladón. Una vez allí, mata a Ragnar Drakkarlund y consigue el tridente de Naz'jan que está en su poder."}},
[11626] = {"El emisario", nil, {"Encuentra a Leviroth en las aguas bajo el iceberg que flota en el límite norte de las Ruinas Tralladón. Utiliza el tridente de Naz'jan para matarlo y vuelve hasta Karuk al norte del Litoral Tralladón."}},
[11627] = {"Punto de ebullición", nil, {"Imperean quiere que sometas a Cocer y a Xurn. Cuando lo hayas logrado, vuelve a verla a las Ruinas de Eldra'nath."}},
[11628] = {"Embozos de la Plaga", nil, {"El cabecilla Vendaval de Invierno del Poblado Taunka'le quiere que le lleves 5 pelambres de mamut plagado."}},
[11629] = {"Regresa junto a la médium", nil, {"Lleva el primordial de Imperean a la médium Colmillo Gruñente a la Avanzada Bor'gorok."}},
[11630] = {"La tierra mala", nil, {"Lleva 8 montones de tierra plagada al cabecilla Vendaval de Invierno al Poblado Taunka'le."}},
[11631] = {"Una visión de aire", nil, {"Usa el primordial de Imperean para realizar un ritual en el tótem de Colmillo Gruñente en la Avanzada Bor'gorok y consigue información sobre el clarividente Caminante Siniestro.\n\nDespués, habla con la médium Colmillo Gruñente."}},
[11632] = {"Lo que nos trae el frío...", nil, {"Lleva el caparazón endurecido de Ith'rix al alto señor supremo Colmillosauro al Bastión Grito de Guerra."}},
[11633] = {"Pasar desapercibido", nil, {"El cabecilla Vendaval de Invierno, del Poblado Taunka'le, quiere que te pongas el embozo de la Plaga imbuido en la Ciudad Templo de En'kilah y que investigues la Aguja de Sangre, la Aguja de Dolor y la Aguja de Putrefacción."}},
[11634] = {"Maestro del viento To'bor", nil, {"Preséntate ante el maestro del viento To'bor en el Bastión Grito de Guerra."}},
[11635] = {"El espíritu del clarividente Caminante Siniestro", nil, {"Habla con el espíritu del clarividente Caminante Siniestro dentro de Magmoth."}},
[11636] = {"Viaje en alfombra mágica", nil, {"Habla con Yanni en Bastión Grito de Guerra para organizar un viaje seguro hasta la costa. Cuando llegues al Desembarco de Garrosh habla con Aniquilador Grek'lor."}},
[11637] = {"Kajanishu", nil, {"Recupera el fetiche de Kajanishu y úsalo sobre los restos del clarividente Caminante Siniestro.\n\nTendrás que darle el fetiche al espíritu del clarividente Caminante Siniestro, dentro de Magmoth."}},
[11638] = {"Devuelve mis restos", nil, {"Entrega los restos del clarividente Caminante Siniestro, que descansan debajo de su espíritu, a la médium Colmillo Gruñente en la Avanzada Bor'gorok."}},
[11639] = {"Venganza en Magmoth", nil, {"Mata a 10 chamanes de Magmoth, 5 batidores de Magmoth, 3 trituradores de Magmoth y 3 compañeras de Magmothregar. Después, vuelve a ver a Ortrosh en la Avanzada Bor'gorok."}},
[11640] = {"Palabras de poder", nil, {"Lleva el pergamino del sumo sacerdote Naferset, el pergamino del sumo sacerdote Talet-Kha y el pergamino del sumo sacerdote Andorath al cabecilla Vendaval de Invierno del Poblado Taunka'le."}},
[11641] = {"Un ataque valeroso", nil, {"Mata a 15 necrófagos de En'kilah y a 5 nigromantes de En'kilah y vuelve a informar a Durm Pellejo de Hielo, en el Poblado Taunka'le."}},
[11642] = {"El tanque no se va a reparar solo", nil, {"Habla con Mobu en el Desembarco de Garrosh."}},
[11643] = {"El transijigamárigo neumático para tanques de Mobu", nil, {"Mobu en la Costa de los Ecos de la Tundra Boreal quiere que recuperes su transijigamárigo neumático para tanques.|n"}},
[11644] = {"¡Placas de metal superfuertes!", nil, {"Mobu en la Costa de los Ecos de la Tundra Boreal quiere que consigas 10 placas de metal superfuertes."}},
[11645] = {"¡Sucios y apestosos snóbolds!", nil, {"Recupera 10 cajas de cosas de Ingeniosa y devuélveselas a Ingeniosa Piñonflojo en la Pista de Aterrizaje de Palanqueta."}},
[11646] = {"La inquisición boreal", nil, {"Donathan te ha ordenado que ofrezcas tu ayuda al bibliotecario Normantis en la Torre de los Magos en El Saliente Ámbar."}},
[11647] = {"Neutralizar las calderas", nil, {"La sabia Aeire, del Poblado Taunka'le, quiere que uses su tótem para neutralizar la caldera este, la caldera central y la caldera oeste en la Ciudad Templo de En'kilah."}},
[11648] = {"El arte de la persuasión", nil, {"El bibliotecario Normantis de El Saliente Ámbar quiere que uses el taladro neural con el hechicero de Berilo encarcelado hasta que revele la ubicación de Lady Evanor."}},
[11649] = {"Motas de los iracundos", nil, {"Consigue 5 motas de tempestad y entrégaselas a Imperean en las Ruinas de Eldra'nath."}},
[11650] = {"Un par de cositas más...", nil, {"Consigue las herramientas de Ingeniosa, un condensador sobrecargado y un puñado de polvo de Rocanar. Llévaselos a Ingeniosa Piñonflojo en la Pista de Aterrizaje de Palanqueta."}},
[11651] = {"Muchas gracias...", nil, {"Habla con Aniquilador Grek'lor en la Costa de los Ecos."}},
[11652] = {"Las Llanuras de Nasam", nil, {"Usa el tanque de asedio de la Horda para matar a 100 soldados de la Plaga y rescatar a 3 soldados Grito de Guerra heridos en las Llanuras de Nasam. También tienes que revelar la identidad del líder del asalto de la Plaga.\n\nInforma a Garrosh Grito Infernal en el Bastión Grito de Guerra si tienes éxito."}},
[11653] = {"¡Ja!... ¡Ya no eres tan grande!", nil, {"Pon a prueba el rayo reductor prototípico ultraavanzado de Ingeniosa sobre 5 trituradores de Magmoth o compañeras de Magmothregar. Cuando lo hayas hecho, vuelve a hablar con ella a la Pista de Aterrizaje de Palanqueta."}},
[11654] = {"La Aguja de Sangre", nil, {"Busca más información sobre el vial de sangre fresca en la Aguja de Sangre."}},
[11655] = {"En la niebla", nil, {"Waltor de Pal'ea en la Costa de los Ecos, en la Tundra Boreal, quiere que mates Kvaldir y recuperes 8 reliquias colmillarr."}},
[11656] = {"Quema en efigie", nil, {"Waltor de Pal'ea en la Costa de los Ecos en la Tundra Boreal quiere que utilices la antorcha colmillarr para quemar los barcos Kvaldir en Pal'ea y en el Desembarco de Garrosh. Debes destruir los siguientes barcos: Las Fauces de la Sierpe, El Kur Drakkar, Martillo de Bor y Yunque de Bor.|n"}},
[11657] = {"Caza de antorchas", nil, {"Atrapa 4 antorchas seguidas y después ve a hablar con el maestro tragafuegos."}},
[11658] = {"Plan B", nil, {"Reúne 10 conjuntos Grito de Guerra y un estandarte Grito de Guerra. Llévaselos a Ingeniosa Piñonflojo a la Pista de Aterrizaje de Palanqueta."}},
[11659] = {"¡Destruye los globos!", nil, {"Destruye 5 globos de sangre e informa al rastreador de nieve Junek en el Poblado Taunka'le."}},
[11660] = {"El cuerno del viejo marinero", nil, {"Waltor de Pal'ea en la Costa de los Ecos, en la Tundra Boreal, quiere que le lleves el cuerno del viejo marinero."}},
[11661] = {"Orabus el Timonel", nil, {"Waltor de Pal'ea en la Costa de los Ecos en la Tundra Boreal quiere que uses el cuerno del viejo marinero para invocar a Orabus el Timonel. Cuando lo hayas invocado, mata a Orabus el Timonel."}},
[11662] = {"¡Encuentra a Karuk!", nil, {"Busca a Karuk en el Litoral Tralladón."}},
[11663] = {"Informando de las noticias", nil, {"Normantis te ha pedido que te presentes ante el bibliotecario Donathan en El Saliente Ámbar."}},
[11664] = {"Escapar de la niebla", nil, {"Sigue a Mootoo la Joven para salir de la niebla. Habla con el anciano Mootoo si consigues salvar a Mootoo la Joven."}},
[11665] = {"Crocoliscos en la ciudad", nil, {"Llévale una cría de crocolisco al viejo Barlo, que está pescando en el Lago Silmyr, al noreste de la Ciudad de Shattrath."}},
[11666] = {"Pilluelos de cebo", nil, {"Llévale un dardo aleta negra al viejo Barlo, que está pescando en el Lago Silmyr, al noreste de la Ciudad de Shattrath."}},
[11667] = {"Aquel que se escapó", nil, {"Pesca el pezfango más grande del mundo y llévaselo al viejo Barlo, que está pescando en el Lago Silmyr, al noreste de la Ciudad de Shattrath."}},
[11668] = {"No es fácil pescar gambas", nil, {"Llévale 10 gambas de agua dulce gigantes al viejo Barlo, que está pescando en el Lago Silmyr, al noreste de la Ciudad de Shattrath."}},
[11669] = {"Filete de sangrevil", nil, {"Llévale un pargo sangrevil enorme al viejo Barlo, que está pescando en el Lago Silmyr, al noreste de la Ciudad de Shattrath."}},
[11670] = {"¡Han sido los orcos! ¡Lo juro!", nil, {"Ponte el disfraz de orco Grito de Guerra y a continuación clava el estandarte Grito de Guerra sobre el cadáver de Magmothregar. Después, devuelve el disfraz a Ingeniosa Piñonflojo en la Pista de Aterrizaje de Palanqueta."}},
[11671] = {"Carrera a contrarreloj", nil, {"Donathan en El Saliente Ámbar te ha encargado que uses el detonador de escudo de Berilo para destruir el escudo en Alto de Berilo. Entonces, tendrás que derrotar a Salrand para conseguir la llave que guarda."}},
[11672] = {"Día de alistamiento", nil, {"Reúnete con el general Arlos en el cuartel de la Fortaleza Denuedo."}},
[11673] = {"¡Sácame de aquí!", nil, {"Escolta a Chalati Voltiflop hasta un lugar seguro y después informa a Palanqueta Todogás en la Pista de Aterrizaje de Palanqueta."}},
[11674] = {"La sabia Altamesa ha desaparecido", nil, {"Encuentra a la sabia Altamesa cerca de Los Cubiles de los Moribundos."}},
[11675] = {"Una muerte digna", nil, {"Mata a 10 magnatauros apestados y después vuelve a ver a la sabia Altamesa al sur de Los Cubiles de los Moribundos."}},
[11676] = {"Clemente libertad", nil, {"El acechasombras Canarius en la Avanzada de las Granjas Grito de Guerra quiere que liberes a 5 prisioneros de la Plaga."}},
[11677] = {"Detén a la Plaga", nil, {"La sabia Altamesa, al sur de Los Cubiles de los Moribundos, te ha pedido que uses las semillas de purificación de Altamesa en la caldera de peste de Los Cubiles de los Moribundos para neutralizarla."}},
[11678] = {"Encuentra a Cuernopúa", nil, {"Encuentra al enviado Cuernopúa en Talramas."}},
[11679] = {"Volver a forjar la llave", nil, {"Donathan te ha enviado a hablar con Surristrasz en El Saliente Ámbar."}},
[11680] = {"¡Volando!", nil, {"Cuando estés listo para liberar a Evanor, entrégale la llave de Salrand al mago de guerra Anzim."}},
[11681] = {"Rescatar a Evanor", nil, {"Anzim en El Saliente Ámbar quiere que vueles con sus magos de guerra para liberar a Lady Evanor en Alto de Berilo. Después tendrás que ir a informarle en la Torre de los Magos."}},
[11682] = {"Una charla con el dragón", nil, {"Lady Evanor te ha enviado a hablar con Surristrasz en El Saliente Ámbar."}},
[11683] = {"La necrópolis caída", nil, {"Mata a 20 miembros de la Plaga de cualquier tipo en Talramas y las Pozas Purulentas. Después vuelve junto a la sabia Altamesa que se encuentra justo al sur de Los Cubiles de los Moribundos."}},
[11684] = {"Marcando los sumideros", nil, {"El Abuelo Mahan en el Poblado Taunka'le quiere que uses el mapa de Los Campos de Géiseres para marcar la ubicación del sumidero sur, el sumidero noroeste y el sumidero noreste."}},
[11685] = {"El corazón de los elementos", nil, {"Llévale 5 corazones elementales a la domadora del viento Barah en el Poblado Taunka'le."}},
[11686] = {"Las Granjas Grito de Guerra", nil, {"El acechasombras Ickoris en la Avanzada de las Granjas Grito de Guerra quiere que explores el Granero Grito de Guerra, la Granja de Torp y el Matadero Grito de Guerra."}},
[11687] = {"El doctor y el señor exánime", nil, {"Acaba con el doctor Muecafilada y con el señor exánime Invierno Helado. Después vuelve junto a la sabia Altamesa al sur de Los Cubiles de los Moribundos."}},
[11688] = {"Malditos puercos apestosos", nil, {"El granjero Torp en las Granjas Grito de Guerra quiere que mates a 10 puercos sin vida."}},
[11689] = {"Regresa con las malas noticias", nil, {"Habla con la abuela Taiga en el Poblado Taunka'le."}},
[11690] = {"Tráelos de vuelta con vida", nil, {"El granjero Torp en las Granjas Grito de Guerra quiere que rescates a 8 bestias kodo infectadas."}},
[11691] = {"Invocar a Ahune", nil, {"Lleva el tótem de magma del Anillo de la Tierra a la piedra de hielo."}},
[11692] = {"Preséntate ante Bixie", nil, {"Mordle Volopiñón te ha pedido que encuentres a Bixie Patituerca al sur de Los Cubiles de los Moribundos."}},
[11693] = {"Oh genial... ¡magnatauros apestados!", nil, {"Mata 10 magnatauros apestados y después vuelve a ver a Bixie Patituerca que está al sur de Los Cubiles de los Moribundos."}},
[11694] = {"En esas cuevas pasa algo raro", nil, {"Bixie Patituerca, que merodea al sur de Los Cubiles de los Moribundos, te ha pedido que uses el polvo de inhibición de Bixie en la caldera de peste del Cubil de los Moribundos para neutralizarlo."}},
[11695] = {"El Cuerno de Furia Elemental", nil, {"Reclama la mitad inferior del cuerno y la mitad superior del cuerno al cabecilla Gorgogluglú y al cabecilla Borbogluglú y llévaselas a la domadora del viento Barah en el Poblado Taunka'le."}},
[11696] = {"¡Ahune está aquí!", nil, {"Encuentra a Luma Cielomater en el Recinto de los Esclavos."}},
[11697] = {"¡Demontre, Mañosa se ha metido en la necrópolis!", nil, {"Encuentra a Mañosa Silbatín dentro de Talramas."}},
[11698] = {"También se podría hacer limpieza de la Plaga", nil, {"Mata a 20 miembros de la Plaga de cualquier tipo en Talramas y las Pozas Purulentas. Después vuelve junto a Bixie Patituerca, que se encuentra justo al sur de Los Cubiles de los Moribundos."}},
[11699] = {"Estoy encerrada en esta maldita jaula... ¡Pero no por mucho tiempo!", nil, {"Consigue un cristal del núcleo de motor, un giroscopio mágico y una piedra de la Plaga de pilotaje. Entrégaselas a Mañosa Silbatín en Talramas."}},
[11700] = {"Avisa a Bixie", nil, {"Habla con Bixie Patituerca, justo al sur de Los Cubiles de los Moribundos."}},
[11701] = {"De vuelta a la Pista de Aterrizaje", nil, {"Habla con Palanqueta Todogás en la Pista de Aterrizaje de Palanqueta, en la Tundra Boreal."}},
[11702] = {"El rey Mrgl-Mrgl", nil, {"Habla con el rey Mrgl-Mrgl en el Refugio Aleta Invernal."}},
[11703] = {"Llega hasta Getry", nil, {"Encuentra al acechasombras Getry en las Granjas Grito de Guerra."}},
[11704] = {"El rey Mrgl-Mrgl", nil, {"Habla con el rey Mrgl-Mrgl en el Refugio Aleta Invernal."}},
[11705] = {"Una empresa insensata", nil, {"Ayuda a Getry a derrotar a Varidus el Desollador en las Granjas Grito de Guerra.\n\nInforma a Garrosh Grito Infernal si tienes éxito.|n"}},
[11706] = {"El derrumbamiento", nil, {"La domadora del viento Barah en el Poblado Taunka'le quiere que derrumbes los sumideros nerubianos usando el Cuerno de Furia Elemental y que derrotes a Lord Kryxix."}},
[11707] = {"Llamada de socorro", nil, {"Habla con Palanqueta Todogás en la Pista de Aterrizaje de Palanqueta."}},
[11708] = {"Los mecagnomos", nil, {"Escucha la historia de Palanqueta Todogás y después busca a Jinky Alatuerca en la Pista de Aterrizaje de Palanqueta."}},
[11709] = {"La carga de Nork Ansiasangre", nil, {"Habla con el celador Nork Ansiasangre en el Bastión Grito de Guerra en la Tundra Boreal."}},
[11710] = {"No es seria esta transmateria...", nil, {"Reúne 15 piezas de repuesto de Palanqueta y llévaselas a Mordle Volopiñón en la Pista de Aterrizaje de Palanqueta."}},
[11711] = {"Entrega de cobardes... en menos de 30 minutos, o será gratis", nil, {"Lleva al desertor de la Alianza hasta el cruce al este del Bastión Grito de Guerra y dispara tu pistola de bengalas Grito de Guerra para mandar una señal a la Alianza.\n\nInforma al explorador Tungok cuando hayas llevado al desertor de la Alianza a su destino con éxito."}},
[11712] = {"Remaldeciente", nil, {"Jinky Alatuerca en la Pista de Aterrizaje de Palanqueta te ha pedido que uses la inyección de transmateria remaldeciente en 6 mecagnomos de Palanqueta."}},
[11713] = {"Marcando los sumideros", nil, {"Abner Dinamométrica en la Pista de Aterrizaje de Palanqueta quiere que uses el mapa de Los Campos de Géiseres para marcar la ubicación del sumidero sur, el sumidero noroeste y el sumidero noreste."}},
[11714] = {"Exterminación de alimañas", nil, {"El explorador Tungok en las Llanuras Sanguiespora en la Tundra Boreal quiere que mates a 8 cosechadores Sanguiespora, a 5 ignitores Sanguiespora y a 2 asadores Sanguiespora."}},
[11715] = {"¡Dale más gasolina!", nil, {"Abner Dinamométrica en la Pista de Aterrizaje de Palanqueta quiere que recojas 8 barriles de aceite gracias al recolector de aceite portátil."}},
[11716] = {"Las maravillosas sanguiesporas", nil, {"La maga sangrienta Laurith en las Llanuras Sanguiespora de la Tundra Boreal quiere que recuperes 10 carpelos de sanguiespora."}},
[11717] = {"La fuente del polen", nil, {"La maga sangrienta Laurith, en las Llanuras Sanguiespora de la Tundra Boreal, quiere que recuperes 5 muestras de polen de polilla Sanguiespora.|n"}},
[11718] = {"Un robot bajo pieles de mamut", nil, {"Abner Dinamométrica en la Pista de Aterrizaje de Palanqueta quiere que le lleves 6 pellejos de mamut gruesos."}},
[11719] = {"Un sujeto de prueba adecuado", nil, {"La maga sangrienta Laurith, en las Llanuras Sanguiespora de la Tundra Boreal, te ha pedido que uses la flor sanguiespora polinizada y le permitas ver los resultados."}},
[11720] = {"La invasión de Gammoth", nil, {"Habla con Primigenio Cuerno Poderoso en las Llanuras Sanguiespora en la Tundra Boreal.|n"}},
[11721] = {"Gammothra el Torturador", nil, {"Mata a Gammothra y devuelve la cabeza de Gammothra a Primigenio Cuerno Poderoso en las Llanuras Sanguiespora en la Tundra Boreal."}},
[11722] = {"Trofeos de Gammoth", nil, {"Llévale la cabeza de Gammothra a Garrosh Grito Infernal al Bastión Grito de Guerra."}},
[11723] = {"¡Utiliza el Vibro-Motor 5000!", nil, {"Abner Dinamométrica de la Pista de Aterrizaje de Palanqueta quiere que uses el Vibro-Motor 5000 para derrumbar los túneles nerubianos y que venzas a Lord Kryxix."}},
[11724] = {"¿Tortilla gigante de polilla?", nil, {"Llévale el huevo gigante resplandeciente a la maga sangrienta Laurith que está en las Llanuras Sanguiespora en la Tundra Boreal."}},
[11725] = {"Buscando al piloto Barrena", nil, {"Encuentra a Iggy \"Barrena\" Conmutador en Los Campos de Géiseres."}},
[11726] = {"Un poquito de picante", nil, {"Iggy \"Barrena\" Conmutador, de Las Pozas Escaldantes, quiere que consigas 4 bolsas de especias de górloc."}},
[11727] = {"La hora de los héroes", nil, {"Preséntate ante el Sargento Colinamartillo en la Fortaleza Denuedo."}},
[11728] = {"Lupus Pupus", nil, {"Iggy \"Barrena\" Conmutador de Las Pozas Escaldantes quiere le lleves 8 trozos de microfilm."}},
[11729] = {"El destornillador ultrasónico", nil, {"Entrégale el destornillador ultrasónico a Ingeniosa Piñonflojo en la Pista de Aterrizaje de Palanqueta."}},
