[11865] = {"No se merecen la muerte", nil, {"Zaza en el Campamento D.E.H.T.A. en la Tundra Boreal quiere que utilices el montón de pellejos falsos para tender una trampa a 8 tramperos de Nesingwary.|n"}},
[11866] = {"Las orejas de nuestros enemigos", nil, {"El archidruida Lathorius en el Campamento D.E.H.T.A. en la Tundra Boreal ha dicho que te recompensará si le traes 15 orejas de lacayo de Nesingwary."}},
[11867] = {"No tenemos suficientes orejas...", nil, {"El archidruida Lathorius en el Campamento D.E.H.T.A. en la Tundra Boreal ha dicho que te recompensará si le traes 15 orejas de lacayo de Nesingwary."}},
[11868] = {"La llegada de la Asesina", nil, {"Zaza en el Campamento D.E.H.T.A. en la Tundra Boreal quiere que mates a Karen la Asesina de Caribús."}},
[11869] = {"Cada almeja con su pareja", nil, {"La hierofante Cenius en el Campamento D.E.H.T.A. en la Tundra Boreal quiere que mates a 10 buzos enloquecidos despojadores."}},
[11870] = {"El Tramo Abandonado", nil, {"Busca a la hierofante Liandra en El Tramo Abandonado."}},
[11871] = {"No durante nuestra guardia", nil, {"La hierofante Liandra en El Tramo Abandonado en la Tundra Boreal quiere que recuperes 12 envíos de piezas de animales."}},
[11872] = {"El villano maestro de almejas...", nil, {"Mata al maestro de almejas K y vuelve con la hierofante Cenius en el Campamento D.E.H.T.A.|n"}},
[11873] = {"Dale la noticia a Palanqueta", nil, {"Ábrete camino hasta la Pista de Aterrizaje de Palanqueta y habla con Palanqueta Todogás."}},
[11875] = {"Lograr ventaja", nil, {"Llévale 8 residuos abisales al emisario Mordin en la Ciudad de Shattrath."}},
[11876] = {"Ayuda a aquellos que no pueden valerse por sí mismos", nil, {"El archidruida Lathorius en el Campamento D.E.H.T.A. en la Tundra Boreal quiere que utilices el aplasta trampas de D.E.H.T.A. para liberar a 8 crías de mamut."}},
[11877] = {"Planes de ataque de Furia del Sol", nil, {"Lord Torvos en la Ciudad de Shattrath quiere que consigas los planes de ataque Furia del Sol."}},
[11878] = {"Khu'nok sabrá qué hacer", nil, {"Llévale la cría de mamut huérfana a Khu'nok el Behemoth en la Tundra Boreal."}},
[11879] = {"Kaw el Destructor de Mamuts", nil, {"Mata a Kaw el Destructor de Mamuts y consigue la alabarda de guerra de Kaw. Llévasela al archidruida Lathorius del Campamento D.E.H.T.A. en la Tundra Boreal."}},
[11880] = {"La investigación multifase", nil, {"El presagista Haronem de la Ciudad de Shattrath quiere que uses las gafas espectrográficas multifase para obtener 6 lecturas multifase."}},
[11881] = {"¡Cárgala!", nil, {"Fezzix Girarrosca quiere que ayudes a Jenny a llegar desde el lugar del accidente hasta el Poblado Taunka'le sin perder todo su cargamento."}},
[11882] = {"Jugando con fuego", nil, {"Habla con un maestro tragafuegos en cualquiera de las capitales de la Alianza."}},
[11883] = {"¿Danza con fuego?", nil, {"Habla con un maestro de danza del fuego en cualquier capital de la Horda."}},
[11884] = {"Nedar, señor de rinocerontes...", nil, {"Kilinger el Vigía del Cubil en el Campamento D.E.H.T.A. en la Tundra Boreal quiere que mates a Nedar, señor de rinocerontes y a su compañero, \"Fiambrera.\""}},
[11885] = {"Sangre adversaria", nil, {"Encuentra los montones de cráneos en el centro de los círculos de invocación de Skettis. Invoca y derrota a cada uno de los descendientes usando 10 Pergaminos perdidos en el Tiempo en el montón de cráneos. Vuelve a ver a Hazzik al Alto de los Vientonegro."}},
[11886] = {"Actividad sospechosa", nil, {"Ve hasta el nuevo Campamento Crepuscular, al sur de la entrada a las Cavernas de Brazanegra en La Ensenada de Zoram de Vallefresno, y recupera la correspondencia Crepuscular de los cultores. Después, invoca al guía del Anillo de la Tierra mediante la baliza totémica."}},
[11887] = {"Suministros de emergencia", nil, {"Consigue 7 equipos de emergencia gnomos de los cuerpos de los pilotos de reconocimiento de Palanqueta estrellados y llévaselos a Fezzix Girarrosca al Poblado Taunka'le."}},
[11888] = {"Vuela al Poblado Taunka'le", nil, {"Habla con el sabio Tierra y Cielo en el Poblado Taunka'le."}},
[11889] = {"La muerte cae desde el cielo", nil, {"El sargento Colinamartillo quiere que utilices la red reforzada para ayudarte a matar a 6 escupefuegos plagados a las afueras de la Fortaleza Denuedo."}},
[11890] = {"¿Qué se traen entre manos?", nil, {"Investiga la zona que rodea la Estación de Bombeo de Palanqueta. Después, vuelve a ver al sabio Tierra y Cielo en el Poblado Taunka'le."}},
[11891] = {"Un disfraz inofensivo", nil, {"Utiliza el orbe del reptador para espiar en el nuevo Campamento Crepúsculo al oeste de la entrada de las Cavernas de Brazanegra en La Ensenada de Zoram en Vallefresno."}},
[11892] = {"El asesinato de Harold Sendero", nil, {"El archidruida Lathorius en el Campamento D.E.H.T.A. en la Tundra Boreal quiere que mates a Harold Sendero."}},
[11893] = {"El poder de los elementos", nil, {"Dorain Pezuña Helada en el Poblado Taunka'le quiere que utilices el tótem alma de viento para reunir la energía de 10 furibundos de vapor."}},
[11894] = {"Yo lo remendaba, yo lo remendé", nil, {"Fezzix Girarrosca del Poblado Taunka'le quiere que le lleves 5 cueros curados con vapor."}},
[11895] = {"Domina a la tormenta", nil, {"Vence a la tempestad tormentosa y vuelve junto al sabio Tierra y Cielo en el Poblado Taunka'le."}},
[11896] = {"Débiles ante el relámpago", nil, {"Mientras estén debilitados por el Poder de la tormenta, destruye a 15 unidades robóticas, exceptuando a los mecagnomos, en la gran área de pozas que rodean la Estación de Bombeo de Palanqueta.\n\nDespués, vuelve junto al sabio Tierra y Cielo en el Poblado Taunka'le."}},
[11897] = {"Obstruye los sumideros", nil, {"El sargento Colinamartillo quiere que uses los explosivos incendiarios para hacer explotar los sumideros suroeste y noroeste de la Fortaleza Denuedo."}},
[11898] = {"Abriéndose paso", nil, {"Derrota al príncipe Valanar y a sus tenientes, Vanthryn el Despiadado y Luthion el Vil, e informa de ello al cabecilla Vendaval de Invierno en el Poblado Taunka'le."}},
[11899] = {"Las almas de los desembrujados", nil, {"Usa el cazador de almas de la Abuela para capturar las almas de 10 mecagnomos de Palanqueta. Después, vuelve junto a la abuela Taiga en el Poblado Taunka'le cuando hayas terminado."}},
[11900] = {"Lectura de medidores", nil, {"El archimago Berinand en el Escudo de Tránsito te ha pedido que recojas las lecturas de sus instrumentos en Gelidar."}},
[11901] = {"¿Destacamento? ¿Qué destacamento?", nil, {"Gerald Verde de Lindeallá quiere que vayas a la mina del norte y descubras qué le ocurrió al destacamento militar que enviaron allí."}},
[11902] = {"Una evidencia perniciosa", nil, {"Vuelve junto a Gerald Verde en Lindeallá."}},
[11903] = {"Es hora de actuar", nil, {"Gerald Verde en Lindeallá quiere que mates a 14 carroñeros apestados."}},
[11904] = {"Frutos de nuestro trabajo", nil, {"Ve hasta la mina de Lindeallá, consigue la llave para liberar la vagoneta del capitán Jacobs y utilízala para liberar la vagoneta de mena. Vuelve a ver a Gerald Verde en Lindeallá cuando hayas completado esta misión."}},
[11905] = {"Posponiendo lo inevitable", nil, {"La imagen de la maga de guerra Kaitlyn de El Nexo quiere que uses el refabricador interdimensional cerca de la falla."}},
[11906] = {"Limpiando las Pozas", nil, {"Recoge 15 piezas de repuesto de Palanqueta y entrégaselas a Ojos de Hierro en el Poblado Taunka'le."}},
[11907] = {"Los subtenientes", nil, {"Derrota a Twonky, ED-210, Max Blasto y El Moledor, y vuelve a ver al sabio Tierra y Cielo al Poblado Taunka'le."}},
[11908] = {"Material de referencia", nil, {"Jeremiah Hawning quiere que recuperes el libro \"Campos, fábricas y talleres\" del edificio en llamas junto a la mina en Lindeallá."}},
[11909] = {"Vence al maestro de palancas", nil, {"Busca el manual del maestro de palancas y llévale la cabeza de Mecazod a la abuela Taiga en el Poblado Taunka'le."}},
[11910] = {"Secretos de los ancestros", nil, {"El archimago Berinand en el Escudo de Tránsito quiere que recojas 3 esquirlas glaciales y 3 esquirlas de vinculado a magia de los ancestros de Gelidar."}},
[11911] = {"Acelerando", nil, {"La imagen de la maga de guerra Kaitlyn de El Nexo quiere que reúnas 5 astillas arcanas de protectores cristalinos."}},
[11912] = {"Locos por unos gelifructus", nil, {"La bibliotecaria Serrah del Escudo de Tránsito quiere que recojas 10 gelifructus de los arbustos de gelifructus de Gelidar."}},
[11913] = {"No tientes a la suerte", nil, {"Wendy Darren quiere que uses la antorcha de Wendy para quemar 8 sacos de grano de Lindeallá."}},
[11914] = {"Guarda el secreto", nil, {"La bibliotecaria Serrah del Escudo de Tránsito necesita que mates serpientes arcanas cerca de El Nexo para reunir 5 muestras de esencia de maná nexa."}},
[11915] = {"Jugando con fuego", nil, {"Habla con un maestro tragallamas en cualquiera de las capitales de la Horda."}},
[11916] = {"El campeón de Grito Infernal", nil, {"Busca al cabecilla Vendaval de Invierno en el Poblado Taunka'le."}},
[11918] = {"Entrenamiento básico", nil, {"Raelorasz en el Escudo de Tránsito quiere que mates a 10 tejehechizos de Gelidar."}},
[11919] = {"Caza de dracos", nil, {"Raelorasz quiere que uses la lanza de Raelorasz para dominar a una prole de draco de El Nexo y regreses junto a él en el Escudo de Tránsito."}},
