[12067] = {"Una carta para casa", nil, {"Lleva la carta de la capitana Malin a la comandante Saia Aceroazur a Reposo Estelar."}},
[12068] = {"Voces entre las ruinas", nil, {"Drakuru quiere que reúnas las tablillas Drakkari de las Ruinas de Drakil'jin y 5 muestras de mojo sacro de los oráculos y protectores Drakkari. Después tendrás que utilizar el elixir de Drakuru en el pedestal de Drakuru en las ruinas para invocar su imagen."}},
[12069] = {"El regreso del Gran Jefe", nil, {"Libera al gran jefe Bruma de Hielo utilizando la llave de la prisión Anub'ar y ayúdale a derrotar al infrarrey Anub'et'kan.\n\nRecupera un fragmento del caparazón de Anub'et'kan y devuélveselo al señor supremo Agmar en Martillo de Agmar en el Cementerio de Dragones."}},
[12070] = {"Alentando a las tropas", nil, {"Utiliza el fragmento de la Tierra para alentar a los gigantes de las Colinas Pardas que luchan en las Colinas Pedrusco, mata a cualquier enano férreo que se enfrente a ti y luego ve a informar a Kurun a la colina que da a Thor Modan."}},
[12071] = {"¡Ataque aéreo!", nil, {"Habla con Valnok Furia del Viento en el Martillo de Agmar."}},
[12072] = {"¡Malditas bestias de añublo!", nil, {"Usa la pistola de bengalas de Valnok para llamar a un jinete de guerra Kor'kron en el Poblado Bruma de Hielo. Monta sobre el jinete de guerra Kor'kron y úsalo para matar a 25 bestias de añublo Anub'ar.\n\nSi completas esta misión, vuelve junto a Valnok Furia del Viento en el Martillo de Agmar."}},
[12073] = {"Golpear el hierro", nil, {"Mata a 10 enanos férreos que defienden Thor Modan y vuelve al Campamento Oneqwah para informar al explorador Vor'takh."}},
[12074] = {"Un aliado muy oportuno", nil, {"Habla con Kurun en la colina que da a Thor Modan."}},
[12075] = {"Pocos restos", nil, {"Consigue una muestra de carne de roca del cadáver del gigante de hielo de la entrada a la caverna en el Vicio de Cristal, al norte del emplazamiento de Zort. Cuando hayas hecho esto, vuelve junto a Zort, cerca del centro del cañón."}},
[12076] = {"Negocios sucios", nil, {"Recoge 2 viales de flema corrosiva de los jormungar utilizando la rasqueta en ti. Lleva los viales a Zort en el Vicio de Cristal cuando hayas terminado."}},
[12077] = {"Aplicar dos veces al día", nil, {"Ofrécele a Ko'char el Irrompible el elixir que ha preparado Zort."}},
[12078] = {"Retador de gusanos", nil, {"Entra en las cavernas del Vicio de Cristal y captura a 3 engendros jormungar dentro de cajones robustos. ¡No te olvides de recoger los cajones cuando los engendros jormungar estén bien atrapados dentro!\n\nLlévale los engendros jormungar a Zort en el Vicio de Cristal."}},
[12079] = {"Territorio preferido", nil, {"Adéntrate en la caverna al extremo norte del Vicio de Cristal y mata a 8 alimentadores jormungar Corazón de Hielo para Ko'char el Irrompible."}},
[12080] = {"Un gusano muy grande", nil, {"Entra en la Caverna Corazón de Hielo y mata a Cascabore. Si aún está en tu posesión, asegúrate de usar el elixir protector de Zort para protegerte de la flema corrosiva de jormungar. \n\nVuelve a ver a Ko'char el Irrompible en el Vicio de Cristal cuando hayas completado la tarea."}},
[12081] = {"Gavrock", nil, {"Habla con Gavrock en las Ruinas de Tethys."}},
[12082] = {"¡Tralarí, tralará!", nil, {"Escolta a Harrison de las Ruinas de Drakil'jin, después habla con Harkor en el Campamento de Harkor."}},
[12083] = {"En lo alto del bosque", nil, {"Consigue el amuleto de control de foco de línea Ley del Bosque Lothalor.\n\nDespués, vuelve con la imagen de la archimaga Modera en Reposo Estelar."}},
[12084] = {"En lo alto del bosque", nil, {"Consigue el amuleto de control de foco de línea Ley y úsalo para obtener información sobre el foco de línea Ley en el Bosque Lothalor.\n\nDespués, vuelve con la imagen del archimago Aethas Atracasol en Martillo de Agmar."}},
[12085] = {"Una carta para casa", nil, {"Entrega la carta del teniente Ta'zinni al señor supremo Agmar en Martillo de Agmar."}},
[12086] = {"El hijo de Karkut", nil, {"Ve al Confín de la Muerte, pasando Unu'pe."}},
[12088] = {"Thassarian, el caballero de la Muerte", nil, {"Busca a Thassarian dentro de El Zigurat de los Lamentos, al norte de Confín de la Muerte."}},
[12089] = {"SE BUSCA: Magister Keldonus", nil, {"Mata al magister Keldonus y ve a ver al capitán Gort al Martillo de Agmar en el Cementerio de Dragones."}},
[12090] = {"SE BUSCA: Gigantauro", nil, {"Mata a Gigantauro y vuelve a ver al capitán Gort al Martillo de Agmar en el Cementerio de Dragones.|n"}},
[12091] = {"SE BUSCA: Garra Temible", nil, {"Mata a Garra Temible y vuelve a ver al capitán Gort al Martillo de Agmar en el Cementerio de Dragones."}},
[12092] = {"Devuelve la fuerza a los ancestros", nil, {"Usa la corteza de los caminantes para devolver la fuerza a 3 ancestros Lothalor y luego vuelve con Sarendryana en Reposo Estelar."}},
[12093] = {"Runas de coacción", nil, {"Gavrock en las Ruinas de Tethys quiere que neutralices las cuatro runas de coacción. Para ello tendrás que derrotar al sobrestante Durval, al sobrestante Korgan, al sobrestante Lochli y al sobrestante Brunon."}},
[12094] = {"Poder latente", nil, {"Carga el Fragmento de Gavrock en la primera piedra antigua, la segunda piedra antigua y la tercera piedra antigua, después regresa con Gavrock en las Ruinas de Tethys."}},
[12095] = {"A Caída del Dragón", nil, {"Habla con Rokhan en Caída del Dragón en el Cementerio de Dragones."}},
[12096] = {"Devuelve la fuerza a los ancestros", nil, {"Utiliza la corteza de los caminantes para devolver la fuerza a 3 ancestros Lothalor y vuelve con el celador de la tierra Grife en Martillo de Agmar."}},
[12097] = {"Sarathstra, Plaga del norte", nil, {"Habla con Rokhan para atraer a la vermis de escarcha Sarathstra.\n\nMata a Sarathstra y lleva el corazón congelado de Sarathstra al capitán Gort en Martillo de Agmar."}},
[12098] = {"Busca por el Poblado Indu'le", nil, {"Busca al comandante de las fuerzas de cazadores de magos en el Poblado Indu'le y en el Lago Indu'le."}},
[12099] = {"Por fin libres", nil, {"Utiliza el romperrunas de Gavrock para liberar a 4 gigantes con runas y luego vuelve junto a Gavrock en las Ruinas de Tethys."}},
[12100] = {"Contener la putrefacción", nil, {"Soar Furia de Halcón en Martillo de Agmar en el Cementerio de Dragones quiere que mates a 15 ejemplares de fauna infectada y recuperes un órgano resistente a la putrefacción."}},
