[12104] = {"Regresa junto a Soar", nil, {"Entrega la vacuna de Sintar a Soar Furia de Halcón en Martillo de Agmar."}},
[12105] = {"Descenso a la oscuridad", nil, {"Llévale el diario de Mikhail al teniente Dumont en el Refugio Pino Ámbar."}},
[12106] = {"Busca por el Poblado Indu'le", nil, {"Busca al comandante de las fuerzas de cazadores de magos en el Poblado Indu'le y en el Lago Indu'le."}},
[12107] = {"El final de la línea", nil, {"Utiliza el talismán de control de foco de línea Ley para obtener información del foco de línea Ley bajo el Lago Indu'le. Después, dirígete al este hasta el Santuario de Dragones Azur cerca del sur central del Cementerio de Dragones y observa lo que está ocurriendo desde el extremo oeste.\n\nLuego, vuelve junto a la imagen de la archimaga Modera en Reposo Estelar."}},
[12109] = {"Informa a Gryan Mantorrecio... otra vez", nil, {"Habla con el capitán Gryan Mantorrecio en el Campamento de la Brigada de los Páramos de Poniente."}},
[12110] = {"El final de la línea", nil, {"Utiliza el talismán de control de foco de línea Ley para obtener información del foco de línea Ley bajo el Lago Indu'le. Después, dirígete al este hasta el Santuario de Dragones Azur cerca del sur central del Cementerio de Dragones y observa lo que está ocurriendo desde el extremo oeste.\n\nLuego, vuelve junto a la imagen del archimago Aethas Atracasol en Martillo de Agmar."}},
[12111] = {"Donde vagan las cosas salvajes", nil, {"Soar Furia de Halcón en Martillo de Agmar en el Cementerio de Dragones quiere que uses el paquete de vacunas para vacunar a 5 alces de avalancha y a 5 pardos árticos."}},
[12112] = {"Negociaciones difíciles", nil, {"Encuentra a Zort en el centro del Vicio de Cristal al noreste del Puesto Boquilla Oxidada."}},
[12113] = {"Encantado de carnecerte", nil, {"Harkor en el Campamento de Harkor quiere que consigas 10 trozos de carne fibrosa de huargo de los merodeadores aúllaocaso y 10 filetes de cornopala de los pastadores Pezuña Larga."}},
[12114] = {"Terapia", nil, {"Harkor en el Campamento de Harkor quiere que mates a 10 protectores Drakkari y a 10 oráculos Drakkari fuera de las Ruinas de Drakil'jin."}},
[12115] = {"Koltira y el idioma de la muerte", nil, {"Llévale el escrito encuadernado en carne a Koltira Tejemuerte."}},
[12116] = {"Requiere agallas…", nil, {"Kraz en Campamento de Harkor quiere que entres en las Ruinas de Drakil'jin y recojas 5 vasos canopes Drakkari."}},
[12117] = {"Viaja hasta el Puerto Moa'ki", nil, {"Viaja hasta el Puerto Moa'ki en el Cementerio de Dragones y habla con el ancestro Ko'nani."}},
[12118] = {"Viaja hasta el Puerto Moa'ki", nil, {"Viaja hasta el Puerto Moa'ki en el Cementerio de Dragones y habla con el ancestro Ko'nani."}},
[12119] = {"Conseguir una audiencia", nil, {"Entrega tu carta de presentación para el Templo del Reposo del Dragón a Tariolstrasz en el Templo del Reposo del Dragón."}},
[12120] = {"La marra de Drak'aguul", nil, {"Kraz en el Campamento de Harkor quiere que mates a Drak'aguul a las afueras de las Ruinas de Drakil'jin y vuelvas con la marra de Drakil'jin."}},
[12121] = {"Te veo al otro lado", nil, {"Kraz en Campamento de Harkor quiere que uses la marra de Drakil'jin en los gongs a las afueras de las Ruinas de Drakil'jin. Cuando te hayan matado, deberás entrar a las ruinas y hablar con el espíritu de Gan'jo mientras estés muerto."}},
[12122] = {"Conseguir una audiencia", nil, {"Entrega tu carta de presentación para el Templo del Reposo del Dragón a Tariolstrasz en el Templo del Reposo del Dragón."}},
[12123] = {"Informar a la reina", nil, {"Entrega tu carta de presentación para el Templo del Reposo del Dragón a Alexstrasza la Protectora en el Templo del Reposo del Dragón."}},
[12124] = {"Informar a la reina", nil, {"Entrega tu carta de presentación para el Templo del Reposo del Dragón a Alexstrasza la Protectora en el Templo del Reposo del Dragón."}},
[12125] = {"Al servicio de la sangre", nil, {"Koltira Tejemuerte en Martillo de Agmar en el Cementerio de Dragones quiere que uses la gema de sangre sobre un habitante de Indu'le trastornado y vuelvas con una gema de sangre llena."}},
[12126] = {"Al servicio de lo profano", nil, {"Koltira Tejemuerte en Martillo de Agmar en el Cementerio de Dragones quiere que uses la gema profana sobre el duque Vallenhal y vuelvas con una gema profana llena."}},
[12127] = {"Al servicio de la escarcha", nil, {"Koltira Tejemuerte en Martillo de Agmar en el Cementerio de Dragones quiere que uses la gema de escarcha con aparecidos de hielo y vuelvas con la gema de escarcha llena."}},
[12128] = {"Visita a Raegar", nil, {"Habla con Raegar Rompecejas en el Altozano del Prospector."}},
[12129] = {"El plan perfecto", nil, {"Llévale el plano de gólem de guerra completo a Raegar Rompecejas en Altozano del Prospector."}},
[12130] = {"¿Por qué fabricarlo cuando puedes apropiártelo?", nil, {"Llévale 8 piezas de gólem de guerra a Raegar Rompecejas en el Altozano del Prospector."}},
[12131] = {"Tenemos el poder...", nil, {"Llévale la célula de energía de Durar y la célula de energía de Kathorn a Raegar Rompecejas en el Altozano del Prospector."}},
[12132] = {"El poder para destruir", nil, {"Koltira Tejemuerte en Martillo de Agmar en el Cementerio de Dragones quiere que entres en el mundo de las sombras y derrotes a 6 torturadores enigmáticos.\n\nHabla con Koltira Tejemuerte si necesitas volver a entrar en el mundo de las sombras."}},
[12133] = {"Aplasta la calabaza", nil, {"Dale el símbolo sagrado chamuscado a la supervisora de huérfanos disfrazada."}},
[12134] = {"La caza de Sasha", nil, {"Sasha quiere que mates a 12 cazadores del solsticio en el Poblado Solsticio."}},
[12135] = {"\"¡Dejen que el fuego se acerque!\"", nil, {"La supervisora de huérfanos disfrazada quiere que ayudes a apagar todos los incendios de la ciudad que provoque el Jinete decapitado. Cuando termines, vuelve a hablar con ella."}},
[12136] = {"El escrito traducido", nil, {"Entrega el escrito encuadernado en carne traducido al capitán Gort en Martillo de Agmar."}},
[12137] = {"Relá'hatee, coleega", nil, {"Gan'jo quiere que recojas la nieve de sueño eterno de su cofre en las Ruinas de Drakil'jin.\n\nDeberás usar la nieve sobre los antiguos espíritus Drakkari allí y llevar sus partículas de espíritus Drakkari a Kraz en Campamento de Harkor."}},
[12138] = {"... O quizás no", nil, {"Carga tu gólem de guerra con la energía de 10 avizores de relámpagos y regresa junto a Raegar Rompecejas en Altozano del Prospector."}},
[12139] = {"\"¡Dejen que el fuego se acerque!\"", nil, {"La supervisora de huérfanos enmascarada quiere que ayudes a apagar todos los incendios en los poblados. Cuando estén apagados, vuelve a hablar con ella."}},
[12140] = {"¡Viva Roanauk!", nil, {"Busca a Roanauk Bruma de Hielo en Martillo de Agmar e iníciale como miembro y líder de las fuerzas de la Horda.\n\nVuelve junto al señor supremo Agmar con el juramento de sangre de la Horda cuando completes esta misión.|n"}},
[12141] = {"Una misión diplomática", nil, {"Habla con Karuk en el Litoral Tralladón, al suroeste de la Fortaleza Denuedo."}},
[12142] = {"Control de plagas", nil, {"Duane en el Frente de la Séptima Legión quiere que mates a 10 snóbolds Llano Nevado y a 3 magnatauros del Cementerio de Dragones en los campos de magnatauros que rodean Reposo del Dragón."}},
[12143] = {"Persecución por el cañón", nil, {"Duane quiere que persigas a los snóbolds por el cañón al noreste del Frente de la Séptima Legión y que acabes con su líder."}},
[12144] = {"Control de plagas", nil, {"Kontokanis en Caída del Dragón quiere que mates a 10 discípulos Llano Nevado y a 3 magnatauros alfa en los campos de magnatauros que rodean Reposo del Dragón."}},
[12145] = {"Persecución por el cañón", nil, {"Kontokanis quiere que persigas a los snóbolds por el cañón al sur de Caída del Dragón y que acabes con su líder."}},
[12146] = {"Implicaciones inquietantes", nil, {"Lleva el cuerno de batalla blasonado hasta Reposo del Dragón y busca a alguien que pudiera estar interesado en evaluar el nivel potencial de amenaza."}},
[12147] = {"Implicaciones inquietantes", nil, {"Lleva el cuerno de batalla blasonado hasta Reposo del Dragón y busca a alguien que pudiera estar interesado en evaluar el nivel potencial de amenaza."}},
[12148] = {"Único", nil, {"Encuentra y mata a Hiendehuesos cerca del cañón al noroeste del Santuario de Dragones Azur. Llévale cualquier prueba que encuentres a Aurastrasza en el Templo del Reposo del Dragón."}},
[12149] = {"Un poderoso magnatauro", nil, {"Da caza y muerte a Sangría, Trizahielo y a Dracogor al este de Reposo del Dragón.\n\nSangría se encuentra en el rincón sureste del Baldío del Dragón, al norte del Santuario de Dragones Esmeralda. Trizahielo patrulla el extremo norte de El Espejo del Alba. Dracogor vaga por la parte inferior de los acantilados al oeste del Santuario de Dragones Bronce.\n\nVuelve junto a Aurastrasza en el Templo del Reposo del Dragón cuando hayas matado a los magnatauros."}},
[12150] = {"Un maestro de runas solitario", nil, {"Encuentra a Dregmar Runarrama cerca de su cueva en la senda que dirige a los acantilados que dan a El Espejo del Alba. Desafíale y revela el nombre del líder magnatauro utilizando los medios necesarios. Vuelve junto a Aurastrasza en el Templo del Reposo del Dragón cuando hayas obtenido la información."}},
[12151] = {"Un Señor de la Guerra atroz", nil, {"Viaja hasta el anillo de antorchas al sur del Santuario de Dragones Azur y sopla el cuerno de batalla blasonado. Derrota a Grom'thar el Portador del Trueno y llévale su cabeza a Aurastrasza en el Templo del Reposo del Dragón."}},
[12152] = {"El final de Jin'arrak", nil, {"Kraz en Campamento de Harkor quiere que recojas la ofrenda Drakkari sacra de las Ruinas de Drakil'jin y eches el polvo de espíritus Drakkari sobre ella. Debes usar la ofrenda Drakkari imbuida con los gongs que hay a las afueras de las Ruinas de Drakil'jin."}},
[12153] = {"El señor feudal férreo y su Yunque", nil, {"Utilizando las facultades de tu gólem de guerra, derrota al señor feudal férreo Martillo Furioso y luego vuelve junto a Raegar Rompecejas en Altozano del Prospector."}},
[12154] = {"Apagón", nil, {"Utiliza los explosivos de Raegar para destruir la batería de runas de Dun Argol y luego ve a informar a Raegar Rompecejas en Altozano del Prospector."}},
[12155] = {"Aplasta la calabaza", nil, {"Dale el símbolo sagrado chamuscado a la supervisora de huérfanos enmascarada."}},
[12157] = {"El mensajero perdido", nil, {"Habla con el mensajero Lanson en Reposo Estelar, en el Cementerio de Dragones."}},
[12158] = {"Mina Piedrahueca", nil, {"Busca pistas dentro de la Mina Piedrahueca al noroeste del Campamento de la Brigada de los Páramos de Poniente."}},
[12159] = {"Almas sin descanso", nil, {"Petrov en Mina Piedrahueca quiere que uses la linterna de minero para quemar los cadáveres de 8 mineros Piedrahueca."}},
[12160] = {"Un nombre del pasado", nil, {"Vuelve con el capitán Gryan Mantorrecio en el Campamento de la Brigada de los Páramos de Poniente."}},
[12161] = {"Ruuna la Ciega", nil, {"Habla con Ruuna la Ciega en el Campamento de Ruuna cerca del Paso de Vordrassil."}},
[12164] = {"La hora del huargo", nil, {"Sasha en el Puesto de Venta de Pino Blanco quiere que vayas a la Isla Luna de Sangre y mates a Selas, Varlam, Buchegore y a la sombra de Arugal."}},
[12165] = {"Un plan intrigante", nil, {"Llévale el plano gólem de guerra completo al prospector Rokar en el Campamento Oneqwah."}},
[12166] = {"El fuego líquido de Elune", nil, {"La celadora Jodi Canción de Luna en Reposo Estelar te ha pedido que uses el fuego líquido de Elune para limpiar los cadáveres de 6 alces contagiados y 6 pardos rabiosos."}},
[12167] = {"Mata a los cultores", nil, {"Reúne 5 trajes funcionales de cultor para la celadora Jodi Canción de Luna en Reposo Estelar."}},
[12168] = {"El Favor de Zangus", nil, {"Entrega el Favor de Zangus a la celadora Jodi Canción de Luna en Reposo Estelar."}},
[12169] = {"El sumo cultor", nil, {"La celadora Jodi Canción de Luna en Reposo Estelar te ha pedido que mates al sumo cultor Zangus."}},
[12170] = {"Pelea en Río Negro", nil, {"El capitán de asaltantes Kronn quiere que mates a 10 unidades o jugadores de la Alianza en el Aserradero Río Negro."}},
[12171] = {"De traidores y traición", nil, {"Enséñale la misiva de la Alianza a Palena Nube de Plata en Reposo Estelar."}},
[12172] = {"Armonización hacia Dalaran", nil, {"La imagen de la archimaga Modera en Reposo Estelar te ha pedido que te armonices con las líneas Ley de Rasganorte."}},
[12173] = {"Armonización hacia Dalaran", nil, {"La imagen del archimago Aethas Atracasol en Martillo de Agmar te ha pedido que te armonices con las líneas Ley de Rasganorte."}},
[12174] = {"Alto comandante Halford Aterravermis", nil, {"Entrega la misiva de la Alianza al alto comandante Halford Aterravermis en la Fortaleza de Hibergarde al este del Cementerio de Dragones.|n"}},
[12175] = {"Pieles de huargo gris", nil, {"Jun'ik, el comerciante de pellejos en Bastión de la Conquista quiere que le consigas 3 pieles de huargo gris de los cazadores brumagris que hay en los bosques al norte."}},
[12176] = {"Un pequeño cambio", nil, {"El comerciante de pellejos Jun'ik quiere que le lleves 6 pellejos de oso pardo."}},
[12177] = {"La mentirijilla de Jun'ik", nil, {"Llévale 1 trozo de carbón y 5 paquetes de harina simple al comerciante de pellejos Jun'ik en Bastión de la Conquista en las Colinas Pardas."}},
[12178] = {"Entrega a Krenna", nil, {"Lleva el fardo de pellejos a Krenna en Bastión de la Conquista."}},
[12179] = {"Specialization 1 [PH]", nil, {"LOG: Complete this quest for Specialization 1."}},
[12180] = {"Los prospectores cautivos", nil, {"Libera al prospector Gann, al prospector Torgan y a la prospectora Varana, y luego vuelve junto al montaraz Kilian en Altozano del Prospector."}},
[12181] = {"Ponle un nombre", nil, {"Enséñale el espécimen de añublo a Tobias Sarkhoff en Nuevo Agamand.|n"}},
[12182] = {"¡A Rencor Venenoso!", nil, {"Entrega el espécimen de añublo al jefe pesteador Middleton en Rencor Venenoso, al este del Cementerio de Dragones."}},
