[12191] = {"¡Traga y tira!", nil, {"Utiliza la jarra de degustación de la Fiesta de la cerveza de obsequio para golpear a la G.U.A.O. 5 veces, y luego ve a hablar con Bizzle Birlotutti."}},
[12192] = {"Ese día que estaba borracho...", nil, {"Habla con Bizzle Birlotutti en el campamento de la Fiesta de la Cerveza."}},
[12195] = {"El \"invitado\" inesperado", nil, {"Llévale 5 cuernos de venado adulto a Tormak el Cicatrices en el Campamento Oneqwah."}},
[12196] = {"Desde cero", nil, {"Llévale 8 trozos de gólem de guerra al prospector Rokar en el Campamento Oneqwah."}},
