[12518] = {"La baraja de Magos", nil, {"Usa la baraja de Magos para invocar a una clarividente de la Luna Negra y devuélveles la baraja."}},
[12519] = {"Patrulla trol: ¿Qué quieres, una medalla?", nil, {"El capitán Grondel fuera del Coliseo quiere que recojas 7 medallones Drakkari de los cadáveres Drakkari cercanos."}},
[12520] = {"Domina el rinoceronte: la prueba", nil, {"Mata a 15 rinocerontes Trozocuerno y vuelve a ver a Hemet Nesingwary al Campamento Base de Nesingwary."}},
[12521] = {"¿Dónde se esconde Hemet Nesingwary?", nil, {"Habla con el archimago Pentarus cuando estés listo para ir a la Cuenca de Sholazar. Cuando llegues, habla con Monte Tirohocico en el Manglar Silvestre de la Cuenca de Sholazar."}},
[12522] = {"Necesitar un motor, coger un motor", nil, {"Weslex Llave Rápida en el Campamento Base de Nesingwary quiere que le lleves un motor de máquina voladora."}},
[12523] = {"Tener una parte, dar una parte", nil, {"Trae 7 piezas de repuesto de Ventura y Cía. a Weslex Llave Rápida en el Campamento Base de Nesingwary."}},
[12524] = {"Desventura de Ventura y Cía.", nil, {"Debaar en el Campamento Base de Nesingwary quiere que mates a 15 miembros de Ventura y Cía. Debes matar a rufianes de Ventura y Cía. o a excavadores de Ventura y Cía."}},
[12525] = {"Bórrale esa sonrisa de la cara", nil, {"Mata al supervisor Timomueca y a Pastel de Carne, y vuelve junto a Debaar al Campamento Base de Nesingwary."}},
[12526] = {"Domina el rinoceronte: la persecución", nil, {"Habla con el Oráculo Soo-liloquio en los Huesos de Nozronn."}},
[12527] = {"Rondadores glotones", nil, {"Llévale 5 cristales de basilisco al alquimista Finklestein en el santuario de Heb'Valok."}},
[12528] = {"Seguir la corriente", nil, {"Preséntate ante el alto chamán Rakjak en la Colina Corazón Frenético como el nuevo \"esclavo\"."}},
[12529] = {"El esclavo del cazador de monos", nil, {"Mata a 8 batidores Callonudillos y 6 destreros Callonudillos para el alto chamán Rakjak de la Colina Corazón Frenético. \n\nSi pierdes los grilletes de Goregek, habla con el anciano Harkek en la Colina Corazón Frenético."}},
[12530] = {"Atormentar a los badanudillos", nil, {"Goregek te ha dado un atizador de badanudillos para hacer que la matriarca Callonudillo salga de su escondite. Mata a la matriarca Callonudillo e informa sobre su muerte al alto chamán Rajak en la Colina Corazón Frenético.\n\nSi pierdes los grilletes de Goregek, habla con el anciano Harkek en la Colina Corazón Frenético."}},
[12531] = {"La amenaza subterránea", nil, {"Encuentra a Serfex bajo las arenas del río sur, mátalo y tráele su garra al alto chamán Rakjak en la Colina Corazón Frenético.\n\nSi pierdes la tiza desgastada de Dajik, habla con el anciano Harkek en la Colina Corazón Frenético."}},
[12532] = {"¡Se dieron a la fuga!", nil, {"Atrapa 12 pollos fugitivos para el anciano Harkek.\n\nSi pierdes la tiza desgastada de Dajik, habla con el anciano Harkek en la Colina Corazón Frenético."}},
[12533] = {"El aprendiz de cazador de avispas", nil, {"Mata 6 avispas del Enjambre Zafiro y 9 zánganos del Enjambre Zafiro para el alto chamán Rakjak en la Colina Corazón Frenético.\n\nSi pierdes la tiza desgastada de Dajik, habla con el anciano Harkek en la Colina Corazón Frenético."}},
[12534] = {"La reina Zafiro", nil, {"Llévale el aguijonero de la reina Zafiro al alto chamán Rakjak en la Colina Corazón Frenético.\n\nSi pierdes la tiza desgastada de Dajik, habla con el anciano Harkek en la Colina Corazón Frenético."}},
[12535] = {"Malicia en la creación", nil, {"El alto chamán Rakjak en la Colina Corazón Frenético quiere 8 marañas de cristal Trecho Celestial."}},
[12536] = {"Un viaje complicado", nil, {"Viaja sobre el crocolisco cautivo hasta el Refugio Susurraneblina y habla con Zepik para recibir instrucciones.\n\nSi pierdes el cuerno de caza de Zepik, habla con el anciano Harkek en la Colina Corazón Frenético."}},
[12537] = {"El rayo golpea dos veces sin duda", nil, {"Zepik quiere que rompas las marañas de cristal Trecho Celestial delante del Santuario Climático Susurraneblina.\n\nSi pierdes el cuerno de caza de Zepik, habla con el anciano Harkek en la Colina Corazón Frenético."}},
[12538] = {"La bruma no está escuchando", nil, {"Zepik quiere que mates a 12 górlocs Susurraneblina.\n\nSi pierdes el cuerno de caza de Zepik, habla con el anciano Harkek en la Colina Corazón Frenético."}},
[12539] = {"A pata", nil, {"Vuelve a la Colina Corazón Frenético y habla con el alto chamán Rakjak."}},
[12540] = {"Siguiendo órdenes", nil, {"Encuentra un Oráculo Hablalluvia herido para el alto chamán Rakjak en la Colina Corazón Frenético.|n"}},
[12541] = {"Patrulla trol: el aprendiz de alquimista", nil, {"El alquimista Finklestein en Heb'Valok quiere que lo ayudes en la creación de un suero de la verdad.\n\nCuando estés listo para empezar, habla con él y sigue sus instrucciones."}},
