[12639] = {"La tierra congelada", nil, {"La domadora de elementos Dagoda de Zim'Torga quiere que recolectes 7 esencias de la tierra congelada."}},
[12640] = {"Sellar fallas", nil, {"El arrancacabelleras Ahunae de Zim'Torga te ha pedido que selles 7 fallas elementales."}},
[12641] = {"La muerte viene de arriba", nil, {"El Rey Exánime te ordena que uses el ojo de Acherus para analizar el Bastión Escarlata, la Forja de Nuevo Avalon, el Concejo de Nuevo Avalon y la Capilla de la Llama Carmesí.\n\nControla el ojo de Acherus usando el mecanismo de control del ojo de Acherus."}},
[12642] = {"El espíritu de Rhunok", nil, {"Habla con el espíritu de Rhunok en el Altar de Rhunok."}},
[12643] = {"Un resquicio de esperanza", nil, {"Stefan del Puesto de Vigilancia de Ébano te ha pedido que vayas al Relicario de Agonía y consigas 5 tripas de abominación pútrida y 5 babas de necrófago pegajosas."}},
[12644] = {"Seguimos destilando", nil, {"Completa el proceso de destilación con éxito y trae el ponche salvaje de Cebatruenos a Tragoamargo Cebatruenos en el Campamento Base de Nesingwary."}},
[12645] = {"La prueba de la cata", nil, {"Ofrece una muestra de ponche salvaje de Cebatruenos a Hemet Nesingwary, Hadrius Harlowe y Tamara Piñonflojo, después regresa junto a Tragoamargo Cebatruenos al Campamento Base de Nesingwary."}},
[12646] = {"Mi profeta, mi enemigo", nil, {"El espíritu de Rhunok en el Altar de Rhunok te ha pedido que consigas el mojo de dios oso ártico."}},
[12647] = {"Acabar con el sufrimiento", nil, {"El espíritu de Rhunok te ha pedido que uses el incienso de torturador para despertar su cuerpo. Luego debes matar a Rhunok con el poder que te ha imbuido su espíritu.\n\nSi por alguna razón pierdes el aura de mojo de Rhunok, puedes volver con el espíritu de Rhunok para que te la vuelva a poner."}},
[12648] = {"Vestirse humildemente", nil, {"Stefan del Puesto de Vigilancia de Ébano quiere que uses la gargantilla embrujada para disfrazarte como la Plaga.\n\nMientras estés en forma de la Plaga, tienes que comprar un plasma amargo de Cartilaginoso. \n\nSi pierdes la gargantilla embrujada, vuelve con Stefan en el Puesto de Vigilancia de Ébano para conseguir otra."}},
[12649] = {"¡Vestirse para la ocasión!", nil, {"Stefan del Puesto de Vigilancia de Ébano quiere que uses la gargantilla embrujada para disfrazarte como la Plaga.\n\nMientras estés en forma de la Plaga, tienes que comprar un plasma amargo de Cartilaginoso. \n\nSi pierdes la gargantilla embrujada, vuelve con Stefan en el Puesto de Vigilancia de Ébano para conseguir otra."}},
[12650] = {"Saqueo propio", nil, {"El cronista To'kini de Zim'Torga quiere que recuperes 7 artefactos rhunokianos."}},
[12651] = {"Pista del Lago", nil, {"Habla con Tamara Piñonflojo en la Pista del Lago."}},
[12652] = {"Da'd de comedd a ezoz necrófagoz", nil, {"Cartilaginoso en el Relicario de Agonía quiere que uses el cuenco de intestinos y cerebros cerca de 10 necrófagos putrefactos."}},
[12653] = {"Volver con Har’koa", nil, {"Habla con Har'koa en el Altar de Har'koa."}},
[12654] = {"El cazador a tiempo parcial", nil, {"Lleva los restos de Brea a Tamara Piñonflojo en la Pista del Lago."}},
[12655] = {"La bendición de Zim'Rhuk", nil, {"El médico brujo Khufu quiere que presentes 10 ofrendas Drakkari ante la estatua de Zim'Rhuk."}},
[12656] = {"La bendición de Zim'Rhuk", nil, {"Requisitos:"}},
[12657] = {"El poder de la Plaga", nil, {"El Rey Exánime quiere que lleves sus órdenes al alto señor Darion Mograine en El Bastión de Ébano.\n\nUsa el teletransportador resplandeciente que hay detrás del Rey Exánime para llegar hasta el alto señor Darion Mograine en la Sala de Mando."}},
[12658] = {"Mi mascota roc", nil, {"Lleva 7 huevos de roc a Zootsilbo en la Avanzada de Dorian."}},
[12659] = {"¡Cabelleras!", nil, {"El arrancacabelleras Ahunae en Zim'Torga quiere que utilices el puñal de Ahunae para arrancar la cabellera a 10 trols de Heb'Drakkar.\n\nEl arrancacabelleras te recuerda que primero tienes que despojarlos."}},
[12660] = {"Armas de destrucción", nil, {"El avatar de Freya quiere que vayas al Campamento Mano de la Muerte al noroeste y destruyas 4 explosivos inestables."}},
[12661] = {"Infiltrarse en Voltarus", nil, {"Stefan quiere que utilices la gargantilla embrujada para entrar en Voltarus y hacer lo que te pida el líder de la Plaga.\n\nSi pierdes la gargantilla embrujada, vuelve con Stefan en el Puesto de la Avanzada de Ébano para conseguir otra."}},
[12662] = {"Abate a Heb'Jin", nil, {"La domadora de elementos Dagoda de Zim'Torga quiere que mates a Heb'Jin."}},
[12663] = {"Reunidos", nil, {"Drakuru de Voltarus quiere que hables con Bolsasangrienta y que des una vuelta por Zul'Drak."}},
[12664] = {"Horizonte Oscuro", nil, {"Drakuru de Voltarus quiere que hables con Bolsasangrienta y que des una vuelta por Zul'Drak."}},
[12665] = {"Noto una alteración", nil, {"Har'koa te ha pedido que averigües qué le ha pasado a Quetz'lun. Habla con ella para que te deje a una de sus crías y puedas ir sigilosamente al Altar de Quetz'lun.\n\nVuelve con ella al Altar de Har'koa cuando lo hayas hecho."}},
[12666] = {"Preparaciones para el inframundo", nil, {"Har'koa quiere que recojas 8 adornos sacros. Entrégaselos en el Altar de Har'koa."}},
[12667] = {"Busca a la diosa serpiente alada", nil, {"Busca el espíritu de Quetz'lun en el Altar de Quetz'lun."}},
[12668] = {"Cimientos de venganza", nil, {"El espíritu de Quetz'lun te ha encargado la exterminación de 12 trols, veneradores de Quetz'lun o rabiosos vipéridos, cerca de sus fuentes de almas.\n\nSolo cuando hayas cumplido con esto volverás con ella al Altar de Quetz'lun."}},
[12669] = {"Por ahora todo mal", nil, {"Stefan en el Puesto de Vigilancia de Ébano quiere que vuelvas con Drakuru y completes cualquier tarea que tenga para ti.\n\nMientras estés en el Relicario de Dolor, utiliza el aditivo diluido en 5 calderas de añublo.\n\nSi pierdes tu gargantilla embrujada, vuelve con Stefan del Puesto de Vigilancia de Ébano para conseguir otra."}},
[12670] = {"La cosecha Escarlata", nil, {"Preséntate ante el príncipe Valanar en la Brecha de la Muerte."}},
[12671] = {"Vuelo de reconocimiento", nil, {"Ayuda al piloto Vic a completar el vuelo de reconocimiento."}},
[12672] = {"Preparando el escenario", nil, {"El espíritu de Quetz'lun te ha pedido que recojas 10 fragmentos de poder del inframundo. Vuelve con ella al Altar de Quetz'lun cuando hayas acabado."}},
[12673] = {"Donde hay patrón…", nil, {"El señor supremo Drakuru en Voltarus quiere que utilices el cetro de sugerencia en geists de añublo y que les ordenes recolectar 7 cristales de añublo."}},
[12674] = {"Ira de mujer…", nil, {"El espíritu de Quetz'lun quiere que utilices el palo de maleficios de Quetz'lun contra el sumo sacerdote Mu'funu, la suma sacerdotisa Tua-Tua y el sumo sacerdote Hawinni. Cuando lo hayas hecho, mátalos."}},
[12675] = {"Una última cosa", nil, {"Entrega el fragmento de poder del inframundo a Har'koa y suplícale que te dé una muestra de su espíritu. Aún debe estar en el Altar de Har'koa."}},
[12676] = {"Sabotaje", nil, {"Stefan del Puesto de Vigilancia de Ébano quiere que realices otra tarea para Drakuru.\n\nAdemás, tienes que poner 5 cargas explosivas en los carros de la Plaga en el Relicario de Dolor.\n\nSi pierdes tu gargantilla embrujada, vuelve con Stefan en el Puesto de Vigilancia de Ébano para conseguir otra."}},
[12677] = {"Materiales arriesgados", nil, {"Stefan del Puesto de Vigilancia de Ébano quiere que vayas a Voltarus y completes cualquier tarea que Drakuru tenga para ti. Mientras estés ahí, tienes que robar 5 cristales de añublo recolectados.\n\nSi pierdes la gargantilla embrujada, vuelve con Stefan en el Puesto de Vigilancia de Ébano para conseguir otra."}},
[12678] = {"Cuando llegue el caos, deja que el sufrimiento tome las riendas", nil, {"El príncipe Valanar en la Brecha de la Muerte te ordena que mates a 10 cruzados Escarlata o campesinos Escarlata y a 10 ciudadanos de Villa Refugio."}},
[12679] = {"Esta noche cenamos en Villa Refugio", nil, {"Orithos, el Ensombrecedor del Cielo en Brecha de la Muerte, quiere que recuperes 15 flechas de saronita."}},
[12680] = {"El gran robo de Palomino", nil, {"Salanar el Jinete te pide que robes un caballo del Establo de Villa Refugio y lo lleves a la Brecha de la Muerte."}},
[12681] = {"Agente componente", nil, {"Lleva 5 garras retorcidas de roc a Colvin Norrington en la avanzada de Dorian."}},
[12683] = {"Ansioso por ayudar", nil, {"Colvin Norrington en la avanzada de Dorian quiere que recojas 5 muestras de esputo de hidra y que mates a 5 hidras Olamarga."}},
[12684] = {"Sangre de un dios muerto", nil, {"Har'koa te ha pedido que recojas 7 muestras de sangre de Mam'toth. Llévaselas al Altar de Har'koa."}},
[12685] = {"Recoges lo que siembras", nil, {"Mata al profeta de Quetz'lun extenuado y luego informa al médico brujo Khufu en Zim'Torga."}},
[12686] = {"Tolerancia cero", nil, {"Drakuru en Voltarus quiere que vayas al Relicario de Dolor y que uses el cetro en el sirviente de Drakuru. Luego tendrás que hacer uso de tus talentos para derrotar a Darmuk."}},
[12687] = {"En el Reino de las Sombras", nil, {"Salanar el Jinete, en la Brecha de la Muerte, te desafía a matar al jinete oscuro de Acherus y tomar su destrero de la muerte de Acherus.\n\nSi lo consigues, lleva el destrero de la muerte de Acherus a la Brecha de la Muerte y usa la Llamada del jinete para invocar a Salanar."}},
[12688] = {"Ingeniería de un desastre", nil, {"Ayuda la ingeniera Hélice a escapar de la Excavación de Timomueca y luego habla con Hemet Nesingwary en el Campamento Base de Nesingwary."}},
[12689] = {"Mano de los Oráculos", nil, nil},
[12690] = {"Combustible para el fuego", nil, {"Drakuru en Voltarus quiere que uses el cetro de mando en abominaciones hinchadas y que hagas uso de sus talentos en la Brecha Gélida para matar a 60 trituracráneos Drakkari y que captures a 3 cabecillas Drakkari.\n\nLos cabecillas aparecerán cuando hayas matado suficientes trituracráneos."}},
[12691] = {"Una arca estropeada por el tiempo", nil, {"Consigue una llave de piedra enorme de los guardianes de Sholazar y tráela al arca estropeada por el tiempo en el Mirador de los Creadores."}},
[12692] = {"El retorno del cazador de exánimes", nil, {"Vuelve a la Colina Corazón Frenético y habla con el anciano Harkek."}},
[12695] = {"El retorno del pielseca amistoso", nil, {"Regresa al Canope de Hablalluvia y habla con el sumo Oráculo Soo-pinstán."}},
[12696] = {"Reconocimiento aéreo", nil, {"La profesora Calvert quiere que cojas el vuelo a la Pista del Lago y que hables con el piloto Vic."}},
[12697] = {"Gothik el Cosechador", nil, {"Habla con Gothik el Cosechador en la Brecha de la Muerte."}},
[12698] = {"La ofrenda que sigue dando", nil, {"Gothik el Cosechador en Brecha de la Muerte te ha dado el regalo del Cosechador para usarlo con los mineros escarlatas. Crea 5 necrófagos escarlata con este dispositivo y lleva los necrófagos a Gothik."}},
[12699] = {"Un incidente embarazoso", nil, {"Busca las llaves de Vic dentro del lago en el Corazón del Río."}},
[12700] = {"Un ataque de oportunidad", nil, {"Habla con el príncipe Valanar en la Brecha de la Muerte."}},
[12701] = {"Masacre en la Punta de la Luz", nil, {"El príncipe Valanar, en la Brecha de la Muerte, te ha ordenado que mates a 100 defensores Escarlata."}},
[12702] = {"¡Fiesta de pollos!", nil, {"Captura 12 pollos fugitivos para el anciano Harkek en la Colina Corazón Frenético."}},
[12703] = {"La locura de Kartak", nil, {"Lleva una muestra pura de sangre del Oráculo al altar de Kartak para invocar a Kartak. Con la ayuda de los poderes compartidos de Kartak, mata a 50 de los górlocs Pavesa para el Chamán Jakjek en el Bastión de Kartak."}},
[12704] = {"Apaciguar la Gran Piedra de Lluvia", nil, {"El sumo Oráculo Soo-pinstán, del Canope de Hablalluvia, quiere que elijas un compañero para excavar y conseguir 6 tesoros lustrosos.\n\nAsegúrate de hablar con el sumo Oráculo Soo-pinstán en el Canope de Hablalluvia para conseguir la ayuda de uno de tus anteriores compañeros."}},
[12705] = {"Voluntad de los titanes", nil, {"Consigue un cristal máculo de los wolvar cerca de Retiro Pavesa y llévalo a la Gran Piedra de Rayos para invocar a Soo-brada. Con la ayuda de los poderes compartidos de Soo-brada, mata a 50 de los wolvar Corazón Frenético cercanos, después vuelve junto al Clamarelámpagos Soo-notone en el Retiro Pavesa."}},
[12706] = {"¡Victoria en la Brecha de la Muerte!", nil, {"Entrega el informe del príncipe Valanar al alto señor Darion Mograine en Acherus: El Bastión de Ébano."}},
[12707] = {"El peso de la justicia", nil, {"El arrancacabelleras Ahunae de Zim'Torga quiere que uses el medallón de Mam'toth para calmar y montar sobre un mamut enfurecido. Luego quiere que pisotees 12 discípulos de Mam'toth hasta que mueran."}},
[12708] = {"Guerreros Tiki encantados", nil, {"La domadora de elementos Dagoda en Zim'Torga te ha pedido que acabes con 12 guerreros Tiki encantados."}},
[12709] = {"Alijos embrujados", nil, {"El cronista To'kini en Zim'Torga te ha pedido que consigas 12 alijos del Bastión de Zol'Maz."}},
[12710] = {"Revelación", nil, {"Drakuru en Voltarus quiere que cojas el teletransportador hasta su cámara superior."}},
[12711] = {"Mensaje abandonado", nil, nil},
[12712] = {"La llave del señor de la guerra Zol'Maz", nil, {"Har'koa de Zim'Torga te ha pedido que consigas la llave del señor de la guerra Zol'Maz."}},
[12713] = {"Traición", nil, {"Stefan del Puesto de Vigilancia de Ébano quiere que derrotes a Drakuru.\n\nSi pierdes la gargantilla embrujada, vuelve con Stefan del Puesto de Vigilancia de Ébano para obtener otra."}},
[12714] = {"La voluntad del Rey Exánime", nil, {"Vuelve a Brecha de la Muerte e informa al príncipe Valanar."}},
[12715] = {"La Cripta de los Recuerdos", nil, {"Preséntate ante el príncipe Keleseth en la Cripta de los Recuerdos."}},
[12716] = {"La solicitud del Pesteador", nil, {"Noth el Pesteador ha solicitado que le traigas 1 caldera vacía, 1 cadena de hierro y 10 calaveras de cruzado."}},
[12717] = {"El brebaje especial de Noth", nil, {"Coloca la pila de calaveras de cruzado dentro de la caldera de peste."}},
[12718] = {"Más calaveras para brebaje", nil, {"Requisitos:"}},
[12719] = {"No hay escapatoria", nil, {"El príncipe Keleseth de la Cripta de los Recuerdos te ha ordenado matar al alcalde Quimby y recuperar el registro de Nuevo Avalon. "}},
[12720] = {"Cómo ganar amigos e influenciar a los enemigos", nil, {"El príncipe Keleseth de la Cripta de los Recuerdos te ha ordenado que descubras la verdad sobre el \"Alba Carmesí\".\n\nSaca los persuasores de Keleseth de la caja ornamentada con joyas y úsalos para \"persuadir\" a la Cruzada Escarlata para que hable."}},
[12721] = {"Arrasando", nil, {"Har'koa en Zim'Torga te ha pedido que liberes a Akali y luego informes al médico brujo Khufu en Zim'Torga."}},
[12722] = {"Corderos al matadero", nil, {"El barón Osahendido en la Cripta de los Recuerdos te ha ordenado que mates a 10 soldados de la Cruzada Escarlata y 15 ciudadanos de Nuevo Avalon."}},
[12723] = {"Detrás de las líneas Escarlata", nil, {"Preséntate ante Orbaz Malasangre en la Taberna Escarlata de Nuevo Avalon."}},
[12724] = {"El camino del cruzado honrado", nil, {"Orbaz de la Taberna Escarlata en Nuevo Avalon te ha ordenado que robes el horario de patrullas de Nuevo Avalon que hay en el Bastión Escarlata."}},
[12725] = {"Hermanos en la muerte", nil, {"Busca a Koltira Tejemuerte en el Bastión Escarlata."}},
[12726] = {"Canción de viento y agua", nil, {"Soo-nata quiere que uses los tambores de la tempestad en La Plataforma del Tormentoso y que ayudes a Haifón a devorar 3 aparecidos tormentosos y 3 espíritus acuosos."}},
[12727] = {"Estallido sangriento", nil, {"Mata al Alto Inquisidor Valroth y lleva su cabeza a Thassarian en la Taberna Escarlata de Nuevo Avalon."}},
[12728] = {"Observar la falla: Cavernas Aleta Invernal", nil, {"El bibliotecario Garren en El Saliente Ámbar quiere que uses el arcanómetro en las Cavernas Aleta Invernal."}},
[12729] = {"Los dioses han hablado", nil, {"Entrega el corazón del guardián inmaculado y la esencia ventosa del celador a Har'koa en Zim'Torga."}},
[12730] = {"Convocatoria en Zol'Heb", nil, {"Har'koa en Zim'Torga te ha pedido que mates al profeta de Akali."}},
[12732] = {"La fuerza de corazón de sangre", nil, {"Lleva un vial de corazón de sangre Toquesol a Rejek a la Colina Corazón Frenético."}},
[12733] = {"Desafío de la Muerte", nil, {"Olrun la Clamabatallas te ha ordenado que ganes 5 duelos contra otros caballeros de la Muerte.\n\nDeberías luchar contra caballeros de la Muerte iniciados o jugadores."}},
[12734] = {"Rejek: Primeras manchas de sangre", nil, {"Mancha de sangre la hoja de Rejek en los cadáveres de una avispa del Enjambre Zafiro, un destrero Callonudillo y 3 miembros de la tribu Susurraneblinas. Puedes manchar la hoja con sangre de Oráculos Susurraneblina o de guerreros Susurraneblina. Vuelve junto a Rejek a la Colina Corazón Frenético cuando acabes la tarea."}},
[12735] = {"Canción de purificación", nil, {"Soo-nata, en el Canope de Hablalluvia, quiere que hagas sonar la campana de purificación en el Lago Olamarga, el Río Conquista del Invierno y el Corazón del Río, y que mates a los espíritus maléficos que habitan en sus aguas."}},
[12736] = {"Canción de reflexión", nil, {"Soo-nata, en el Canope de Hablalluvia, quiere que uses el didgeridoo de contemplación en lo alto de El Pilar Iluminado, El Pilar Musgoluz y El Pilar del Trecho Celestial."}},
[12737] = {"Canción de fecundidad", nil, {"Soo-nata en el Canope de Hablalluvia quiere que vayas a La Plataforma del Tormentoso, al noroeste de El Pedestal de los Creadores y que toques el cuerno de fecundidad cerca de 8 montones de tierra fértil empapada."}},
[12738] = {"¡Un grito de venganza!", nil, {"Preséntate ante el caballero Comandante Puño de Peste en la Capilla de la Llama Carmesí."}},
[12739] = {"Una sorpresa especial", nil, {"El caballero Comandante Puño de Peste en la Capilla de la Llama Carmesí te ha ordenado que ejecutes a Malar Cuernobravo, un prisionero de El Alba Argenta dentro de la prisión de los alrededores."}},
[12740] = {"Paracaídas para la Cruzada Argenta", nil, {"El Comandante Falstaav de El Confín Argenta te ha pedido que entregues paracaídas a 10 cruzados Argenta o escuderos Argenta en el patio."}},
[12741] = {"Fuerza de la tempestad", nil, {"Recoge 3 esencias del monzón y 3 esencias de la tormenta y llévalas al Santuario de la Tempestad. Vuelve junto a Rejek a la Colina Corazón Frenético con el poder verdadero de la tempestad."}},
[12742] = {"Una sorpresa especial", nil, {"El caballero Comandante Puño de Peste en la Capilla de la Llama Carmesí te ha ordenado que ejecutes a Ellen Puentecanto, una prisionera de El Alba Argenta dentro de la prisión de los alrededores."}},
[12743] = {"Una sorpresa especial", nil, {"El Caballero Comandante Puño de Peste en la Capilla de la Llama Carmesí te ha ordenado que ejecutes a Jazmina Cuerno de Roble, una prisionera de El Alba Argenta dentro de prisión de los alrededores."}},
[12746] = {"Una sorpresa especial", nil, {"El caballero Comandante Puño de Peste en la Capilla de la Llama Carmesí te ha ordenado que ejecutes a Valok el Honrado, un prisionero de El Alba Argenta dentro de la prisión de los alrededores."}},
[12747] = {"Una sorpresa especial", nil, {"El caballero Comandante Puño de Peste en la Capilla de la Llama Carmesí te ha ordenado que ejecutes a Lady Eonys, una prisionera de El Alba Argenta dentro de la prisión de los alrededores."}},
[12748] = {"Una sorpresa especial", nil, {"El caballero Comandante Puño de Peste en la Capilla de la Llama Carmesí te ha ordenado que ejecutes a Kug Quijahierro, un prisionero de El Alba Argenta dentro de la prisión de los alrededores."}},
[12749] = {"Una sorpresa especial", nil, {"El caballero Comandante Puño de Peste en la Capilla de la Llama Carmesí te ha ordenado que ejecutes a Iggy Colmilloscuro, un prisionero de El Alba Argenta dentro de la prisión de los alrededores."}},
[12750] = {"Una sorpresa especial", nil, {"El caballero Comandante Puño de Peste te ha ordenado que ejecutes a Antoine Brack, un prisionero de El Alba Argenta dentro de la prisión de los alrededores."}},
[12751] = {"Como volver a casa", nil, {"Vuelve a Thassarian en la Taberna Escarlata de Nuevo Avalon con noticias de tu victoria."}},
[12752] = {"Investigación desesperada", nil, {"Habla con el gran boticario Putress en el nuevo Campamento de Boticarios en el Bancal de la Luz de Shattrath."}},
[12753] = {"Una alianza desesperada", nil, {"Habla con el obispo Lazaril en el nuevo campamento de la Alianza en el Bancal de la Luz de Shattrath."}},
[12754] = {"Emboscada en el mirador", nil, {"Orbaz Malasangre en la Taberna Escarlata de Nuevo Avalon te ha ordenado que mates al mensajero Escarlata y que recuperes las pertenencias del mensajero Escarlata y el mensaje del mensajero Escarlata."}},
[12755] = {"Una reunión con el destino", nil, {"Orbaz Malasangre te ha ordenado que entregues el mensaje del mensajero escarlata a la alta general Abbendis en Puerto del Rey."}},
[12756] = {"El Embate Escarlata aparece", nil, {"Entrega El Camino de la Redención a Orbaz Malasangre en la Taberna Escarlata de Nuevo Avalon."}},
[12757] = {"El ejército escarlata se acerca…", nil, {"Entrega El Camino de la Redención al alto señor Darion Mograine en Acherus: El Bastión de Ébano. "}},
[12758] = {"Un casco de héroe", nil, {"Consigue explosivos Ventura y Cía. de cualquier unidad de Ventura y Cía. en la Excavación de Timomueca y úsalos para obtener una cabeza de vigía de tormenta. Lleva la cabeza de vigía de tormenta a Rejek en la Colina Corazón Frenético."}},
