[12907] = {"Castigos ejemplares", nil, {"Mildred la Cruel en la Mina Desolada quiere que mates a Garhal."}},
[12908] = {"Un prisionero concreto", nil, {"Llévale la llave de Mildred a Lok'lira la Vieja Bruja en la Mina Desolada."}},
[12909] = {"Por el olfato se sabe", nil, {"Habla con Khaliisi a los pies de la montaña al este del lugar del accidente de Grom'arsh."}},
[12910] = {"Encontrar al culpable", nil, {"Monta a Mordisquielo y ve con ella a la fuente del olor de la nota de Brann. Luego, recupera el comunicador de Brann y úsalo para contactar con Brann Barbabronce."}},
[12911] = {"Prueba de crédito por muerte", nil, {"Saluda a la prueba de crédito por muerte escribiendo /wave"}},
[12912] = {"Se acerca una gran tormenta", nil, {"Gymer, rey de los gigantes de tormenta, te ha pedido que encuentres un modo de liberarlo de su celda. Habla con el ingeniero Carrizo en el Puesto de Avanzada de los Cruzados."}},
[12913] = {"¡Habla orco, hombre!", nil, {"Habla con Moteha Natoviento en el Lugar del accidente de Grom'arsh."}},
[12914] = {"La salvación de Gymer", nil, {"El ingeniero Carrizo en el Puesto de Avanzada de los Cruzados en Zul'Drak quiere que le traigas 6 esencias de alma en pena y 6 tierras de diatomeas."}},
[12915] = {"Enmiendas", nil, {"Thorim en el Templo de las Tormentas quiere que mates a Fjorn y a 5 gigantes de hierro Tronaforjado en el Yunque de Fjorn, al este de Dun Niffelem.\n\nUsando el talismán de tierra de Thorim invocarás a sus terráneos para que luchen a tu lado. El talismán requiere una roca de granito; se encuentran cerca del Yunque de Fjorn."}},
[12916] = {"Nuestra única esperanza", nil, {"Habla con Gymer en el Fin de Thrym en Zul'Drak cuando hayas usado la carga de seforio enorme para abrir el recinto de la Plaga donde está prisionero."}},
[12917] = {"Hablar con la voz del viento", nil, {"Llévale 5 voces del viento a Moteha Natoviento en el Lugar del accidente de Grom'arsh."}},
[12918] = {"Gemas perfectas", nil, {"Llévale dos citrinos enormes, dos jades oscuros y dos cristales de Sombras a Ounhulo en Valgarde."}},
[12919] = {"La venganza del Rey de la Tormenta", nil, {"Habla con Gymer para empezar.\n\nCon Gymer como montura, destruye a 100 de la Plaga y mata a Algar el Elegido, a Thrym y al príncipe Navarius. Informa al cruzado MacKellar en el Puesto de Avanzada de los Cruzados si tienes éxito."}},
[12920] = {"Alcanzar a Brann", nil, {"Usa el comunicador de Brann para contactar con Brann Barbabronce y habla con él, y luego habla con Boktar Furia Sangrienta en el lugar del accidente de Grom'arsh.\n\nSi pierdes el comunicador de Brann, habla con Moteha Natoviento en el lugar del accidente de Grom'arsh."}},
[12921] = {"Un cambio de escenario", nil, {"Reúnete con Lok'lira la Vieja Bruja en el Poblado Brunnhildar."}},
[12922] = {"El fuego del refinador", nil, {"Debes reunir 10 chispas furiosas de aparecidos hirvientes en el lago Campo de Escarcha y usarlas en el yunque del Yunque de Fjorn."}},
[12924] = {"Forjando una Alianza", nil, {"Debes ir a Dun Niffelem y pedirle al rey Jokkum que permita que se vuelva a forjar la armadura de Thorim. Después de completar la tarea de Jokkum, habla con Njormeld en Dun Niffelem."}},
[12925] = {"Aberraciones", nil, {"Thyra Kvinnshal en el Poblado Brunnhildar quiere que vayas a Valkyrion y consigas viales de aceite de Escarcha de las aspirantes de Valkyrion. Usa los viales de aceite de Escarcha para destruir 30 huevos de protodraco apestado."}},
[12926] = {"Piezas del rompecabezas", nil, {"Usa el comunicador de Brann para contactar con Brann Barbabronce y que te explique qué hacer a continuación."}},
[12927] = {"Extracción de datos", nil, {"Usa el disco de inventor para recuperar 7 juegos de datos ocultos de los bancos de datos. "}},
[12928] = {"El caparazón de Norgannon", nil, {"Recupera el caparazón de Norgannon del archivista Mechaton."}},
[12929] = {"Los terráneos de Ulduar", nil, {"Habla con Pedruscón el Creador de rocas en el Refugio de Pedruscón."}},
[12930] = {"Tierra rara", nil, {"Usa el pico de minería terráneo para minar 7 puñados de tierra encantada. Lleva la tierra y 5 telas de tejido de Escarcha a Pedruscón el Creador de rocas en el Refugio de Pedruscón."}},
[12931] = {"Contraatacando", nil, {"Pedruscón el Creador de rocas quiere que mates a 10 atacantes Tronaforjado y que vuelvas a hablar con él en el Refugio de Pedruscón. Puedes matar a asaltantes Tronaforjado o atracadores Tronaforjado."}},
[12932] = {"El Anfiteatro de la Angustia: ¡Yggdras!", nil, {"¡Entra en la arena del Anfiteatro de la Angustia y derrota a Yggdras! Habla con Wodin, el sirviente trol, para recoger tu recompensa."}},
[12933] = {"El Anfiteatro de la Angustia: ¡Magnatauro!", nil, {"¡Entra en la arena del Anfiteatro de la Angustia y derrota a Barbahedionda! Habla con Wodin, el sirviente trol, para recoger tu recompensa."}},
[12934] = {"El Anfiteatro de la Angustia: ¡del más allá!", nil, {"¡Entra en la arena del Anfiteatro de la Angustia y derrota a uno de los señores elementales! Habla con Wodin, el sirviente trol, para recoger tu recompensa."}},
[12935] = {"El Anfiteatro de la Angustia: ¡Colmillarrmagedón!", nil, {"¡Entra en la arena del Anfiteatro de la Angustia y derrota a Orinoko Rompecolmillos! Habla con Wodin, el sirviente trol, para recoger tu recompensa."}},
[12936] = {"El Anfiteatro de la Angustia: ¡Korrak el Sanguinario!", nil, {"¡Entra en la arena del Anfiteatro de la Angustia y derrota a Korrak el Sanguinario! Habla con Wodin, el sirviente trol, para recoger tu recompensa."}},
[12937] = {"Alivio para los caídos", nil, {"Usa la cataplasma telúrica para sanar a 8 defensores terráneos caídos, después habla con Pedruscón el Creador de rocas en el Refugio de Pedruscón."}},
[12938] = {"El Duque", nil, {"Habla con el duque Lankral en La Cámara de las Sombras."}},
[12939] = {"Desafío de honor", nil, {"El duque Lankral en La Cámara de las Sombras te ha pedido que uses la bandera de desafío para que desafíes y derrotes a 6 combatientes Mjordin."}},
[12940] = {"Cubo de caramelos", nil, nil},
[12941] = {"Cubo de caramelos", nil, nil},
[12942] = {"Fuera sus alas negras", nil, {"Iva la Vengativa en el Poblado Brunnhildar quiere que vayas a Valkyrion y mates a 12 Val'kyr nacientes."}},
[12943] = {"Decreto de La Cámara de las Sombras", nil, {"El duque Lankral en La Cámara de las Sombras te ha ordenado que leas el decreto de La Cámara de las Sombras en presencia del señor feudal Ufrang el Poderoso y después lo mates."}},
