[13082] = {"El favor de A'dal", nil, {"Vuelve con el cruzado Bridenbrad en el Vigía Silencioso, en Corona de Hielo."}},
[13083] = {"Luz en las tinieblas", nil, {"Llévale el tabardo del cruzado Bridenbrad al alto señor Tirion Vadín en el Pináculo de los Cruzados."}},
