[13137] = {"Combate no tan honorable", nil, {"La Bruja Osaria en el poblado Njorndar quiere que derrotes a Iskalder en la Cumbre Marca de Guerra."}},
[13138] = {"¡Estoy fundiendo... fundiendo!", nil, {"Usa la bolsa de fragmentos dentados cerca de uno de los hornos del nivel más profundo de Malykriss. Llévale la barra de metal fundido al cruzado Olakin Sainrith en La Guardia Negra."}},
[13139] = {"Dentro del helado corazón de Rasganorte", nil, {"Llévale el informe del padre Gustav al alto señor Tirion Vadín en La Vanguardia Argenta en Corona de Hielo.|n"}},
[13140] = {"Los forjadores de runas de Malykriss", nil, {"Llévale 5 placas de saronita rúnicas al cruzado Olakin Sainrith en La Guardia Negra."}},
[13141] = {"La batalla por el Pináculo de los Cruzados", nil, {"Aventúrate en el Pináculo de los Cruzados y usa el estandarte de la Cruzada bendecido sobre la pila de calaveras de cruzado. Defiende el estandarte del ataque de la Plaga hasta que la tierra sobre el Pináculo haya quedado santificada, entonces habla con el padre Gustav en el Valle de los Ecos, en Corona de Hielo."}},
[13142] = {"La revancha del alma en pena", nil, {"La Bruja Osaria en el poblado Njorndar te ha pedido que te vengues desafiando y derrotando al señor feudal Supremo Balargarde."}},
[13143] = {"Nuevos reclutas", nil, {"Vereth el Maquiavélico, del Alto del Sufrimiento, te ha pedido que lleves a un acechador ágil subyugado a lo alto de las rocas que se encuentran al noreste del Alto del Sufrimiento."}},
[13144] = {"Matar dos plagas con un esqueleto", nil, {"La jinete oscura Arly quiere que acabes con 3 abominaciones encadenadas con los esqueletos ardientes."}},
[13145] = {"El Bastión Inmundo", nil, {"Vereth el Maquiavélico en el Alto del Sufrimiento te ha pedido que utilices el acechador ágil para rastrear Malykriss."}},
[13146] = {"Abundante generosidad", nil, {"Vereth el Maquiavélico quiere que uses la cadena de hierro de acechador ágil para arrastrar bombas de la Plaga hasta 4 atrocidades torpes dentro de Malykriss."}},
[13147] = {"Emparejamientos", nil, {"Vereth el Maquiavélico en el Alto del Sufrimiento quiere que uses a un acechador ágil para tirar rocas a los toscos umbríos cuando estén cerca de no-muertos inferiores en Malykriss. Vuelve cuando hayas provocado 5 peleas."}},
[13148] = {"Reparar el collar", nil, {"Repara el collar con una calcedonia y devuélveselo a Timothy Jones en la tienda de joyeros del Mercado de Magos de Dalaran."}},
[13149] = {"Disipando ilusiones", nil, {"Cromi quiere que uses el disruptor arcano sobre las cajas sospechosas en el Pasado de Stratholme y después hables con ella cerca de la entrada de Stratholme."}},
[13151] = {"Una escolta real", nil, {"Cromi te ha pedido que acompañes a Arthas en La Matanza de Stratholme. Vuelve a hablar con ella después de haber derrotado a Mal'Ganis."}},
[13152] = {"Una visita al doctor", nil, {"La jinete oscura Arly en La Guardia Negra quiere que destruyas la estaca metálica que aprisiona a Parches en el Sagrario de Reanimación y que lo ayudes a vengarse del doctor Sabnok."}},
[13153] = {"Proteger a los guerreros", nil, {"La hechicera Kaylana en la Fortaleza de Conquista del Invierno quiere que arrebates 10 conjuntos de armadura de la Horda imbuida a los jugadores de la Horda en las Cascadas Glaciales."}},
