[13157] = {"El Pináculo de los Cruzados", nil, {"Busca al alto señor Tirion Vadín en el Pináculo de los Cruzados en Corona de Hielo."}},
[13158] = {"La discreción es la clave", nil, {"Rhonin quiere que vayas al Bastión Violeta en Dalaran y que hables con el celador Alturas."}},
