[13161] = {"El jinete de lo profano", nil, {"Vereth el Maquiavélico en el Alto del Sufrimiento quiere que mates a Rokir, el jinete de lo profano."}},
[13162] = {"La jinete de escarcha", nil, {"Vereth el Maquiavélico en el Alto del Sufrimiento quiere que mates a Sapph, la jinete de lo profano."}},
[13163] = {"El jinete de sangre", nil, {"Vereth el Maquiavélico en el Alto del Sufrimiento quiere que mates a Baelok, el jinete de sangre."}},
[13164] = {"El destino de Malasangre", nil, {"Vereth en el Alto del Sufrimiento te ha enviado a matar a Orbaz Malasangre."}},
[13165] = {"Recuperar Acherus", nil, {"Usa el hechizo Portón de la muerte para volver a Acherus: El Bastión de Ébano. Informa al alto señor Darion Mograine cuando llegues."}},
[13166] = {"La batalla por El Bastión de Ébano", nil, {"Mata a 10 miembros de la Plaga y ayuda a matar a Remendejo. Vuelve con el alto señor Darion Mograine si lo consigues."}},
[13167] = {"Muerte al rey traidor", nil, {"Kilix el Desenmarañador en El Foso de Narjun te ha encargado que derrotes a Anub'arak en Azjol-Nerub. Debes regresar junto a Kilix con el caparazón de Anub'arak roto."}},
