[5726] = {"Ennemis cachés", nil, {"Apporter un Insigne de lieutenant à Thrall à Orgrimmar."}},
[5727] = {"Ennemis cachés", nil, {"Apporter l'Insigne du Lieutenant à Neeru Lamefeu et lui parler. Essayer de déterminer s'il vous croit membre de la Lame ardente puis retourner voir Thrall à Orgrimmar."}},
[5728] = {"Ennemis cachés", nil, {"Tuer Bazzalan et Jergosh l'Invocateur avant de retourner voir Thrall à Orgrimmar."}},
