[5761] = {"Tuer la bête", nil, {"Pénétrer dans le Gouffre de Ragefeu et tuer Taragaman l'Affameur, puis rapporter son cœur à Neeru Lamefeu à Orgrimmar."}},
[5762] = {"Hemet Nesingwary Jr.", nil, {"Apporter la Caisse de Kravel à Hemet Nesingwary junior à la Vallée de Strangleronce."}},
[5763] = {"Chasse à Strangleronce", nil, {"Apportez la Corne de kodo de Roon à Hemet Nesingwary junior à Strangleronce."}},
[5781] = {"Aux mémoires perdues", nil, {"Aller au caveau de Zaeldarr - situé dans la section la plus au sud des Maleterres de l'est - et récupérer le Marteau de Taelan. Retourner auprès de Tirion Fordring après avoir accompli la mission."}},
[5801] = {"Forgée dans la Fournaise", nil, {"Emporter le Moule de Clé squelette et 2 Barres de thorium au sommet de la crête de la Fournaise dans le cratère d'Un'Goro. Utiliser le Moule de Clé squelette au lac de lave pour forger la Clé squelette inachevée.\n\nApporter la Clé squelette inachevée à l'alchimiste Arbington à la pointe du Noroît, dans les Maleterres de l'ouest."}},
[5802] = {"Forgée dans la Fournaise", nil, {"Prendre le moule de la Clé squelette et deux barres de thorium au sommet de la crête de la Fournaise dans le cratère d'Un'Goro. Se servir du moule de la Clé squelette pour forger la Clé squelette inachevée.\n\nApporter la Clé squelette inachevée à l'Apothicaire Dithers à la Barricade, dans les Maleterres de l'Ouest."}},
[5803] = {"Le Scarabée d'Araj", nil, {"Vaincre Araj l'Invocateur et apporter le Scarabée d'Araj à l'alchimiste Arbington à la pointe du Noroît, dans les Maleterres de l'ouest."}},
[5804] = {"Le Scarabée d'Araj", nil, {"Vaincre Araj l'Invocateur et apporter le Scarabée d'Araj à l'apothicaire Dithers à la Barricade, dans les Maleterres de l'ouest."}},
