[5844] = {"Bienvenue !", nil, {"Apportez le Bon-cadeau de Sabot-de-sang à Vorn Oeil-des-cieux au village de Sabot-de-Sang."}},
[5846] = {"L'amour et la famille", nil, {"Voyager jusqu'à l'île de Caer Darrow, dans la région centre-sud des Maleterres, et chercher un indice de l'emplacement du tableau."}},
[5847] = {"Bienvenue !", nil, {"Apportez le Bon-cadeau de Brill à Claire Willower, à Brill."}},
[5848] = {"De l'amour et de la famille", nil, {"Se rendre à Stratholme, dans la partie nord des Maleterres. C'est dans le Bastion écarlate que se trouve le tableau De l'amour et de la famille, caché derrière une autre peinture représentant les lunes jumelles.\n\nRapporter le tableau à Tirion Fordring."}},
