[6961] = {"Grand-père Hiver est ici !", nil, {"Allez voir Grand-père Hiver. Il se trouve près des Gourmandises Fumebois à Orgrimmar."}},
[6962] = {"Des friandises pour Grand-père Hiver", nil, {"Apportez 5 Biscuits en pain d'épice et un Verre de lait glacé au Grand-père Hiver, à Orgrimmar."}},
[6963] = {"Friandises volées de la fête du Voile d'hiver", nil, {"Trouvez l'enquêteur des Gourmandises Fumebois qui est porté disparu dans les zones enneigées des Contreforts de Hautebrande."}},
[6964] = {"L'esprit de la fête", nil, {"Parlez à Sagorne Rôdeur-des-crêtes, dans la Vallée de la Sagesse d'Orgrimmar, de la fête du Voile d'hiver."}},
[6981] = {"L'Éclat luminescent", nil, {"Allez à Cabestan pour trouver quelqu'un qui puisse vous en dire plus sur l'Éclat luminescent.\n\nPuis livrez l'Éclat, selon les instructions."}},
[6982] = {"Fournitures de Froide-dent", nil, {"Apporter 10 Fournitures de Froide-dent à l'intendant de l'Alliance, à Dun Baldar."}},
[6983] = {"La méchanceté vous habite...", nil, {"Récupérez les Friandises volées et libérez Metzen le renne après avoir vaincu l'Abominable Grinche, qui se cache aux alentours de la Caverne Stérile dans les Contreforts de Hautebrande."}},
[6984] = {"Un grand merci des Gourmandises Fumebois !", nil, {"Parler à Grand-père Hiver à Orgrimmar."}},
[6985] = {"Fournitures de Gouffrefer", nil, {"Apporter 10 Fournitures de Gouffrefer à l'Intendant de la Horde, au donjon Loup-de-givre."}},
[7001] = {"Ecuries vides", nil, {"Trouvez un Loup des glaces dans la vallée d'Alterac. Servez-vous de la Muselière de loup des glaces pour « dompter » la bête. Lorsque ce sera fait, le loup des glaces vous suivra jusqu'au Maître des écuries Loup-de-givre. Parlez au Maître des écuries Loup-de-givre pour être crédité de cette capture."}},
[7002] = {"Harnais en cuir de bélier", nil, {"Conditions :"}},
[7003] = {"Géants rétrécis", nil, {"Utilisez l'Ultra-réducteur de Zorbin sur des géants de n'importe quel type en Féralas afin de les rendre plus faciles à gérer. Apportez 15 Résidus de miniaturisation que vous trouverez sur les versions rétrécies des géants à Zorbin Poudrozieu aux docks de la côte Oubliée en Féralas."}},
