[7181] = {"La légende de Korrak", nil, {"D'après la légende, le chef des puissants trolls Haches-d'hiver se montre quand il le décide pour semer la destruction chez les habitants de la vallée d'Alterac.\n\nSi Korrak apparaît, détruisez-le et présentez-vous au Maître de guerre Laggrond."}},
[7201] = {"Le dernier élément", nil, {"Partir pour les Profondeurs de Rochenoire et trouver 10 Essences des éléments. Commencer vos recherches par les golems et par les créateurs de golems. Vivian Lagrave a aussi marmonné quelque chose à propos d'élémentaires."}},
[7202] = {"Korrak le Ragesang", nil, {"D'après la légende, le chef des puissants trolls Haches-d'hiver se montre quand il le décide pour semer la destruction chez les habitants de la vallée d'Alterac.\n\nSi Korrak apparaît, détruisez-le et présentez-vous au Lieutenant Haggerdin dans les montagnes d'Alterac."}},
[7221] = {"Discuter avec la prospectrice Taillepierre", nil, {"Discutez avec la Prospectrice Taillepierre à la Garnison de Dun Baldar."}},
[7222] = {"Discuter avec Voggah Mortepoigne", nil, {"Parlez à Voggah au donjon Loup-de-givre."}},
[7223] = {"Morceaux d'armures", nil, {"Apportez 20 Morceaux d'armure à Murgot Forge-profonde à Dun Baldar."}},
[7224] = {"Pris à l'ennemi", nil, {"Apportez 20 Morceaux d'armure au forgeron Regzar au village Loup-de-givre."}},
[7241] = {"La défense des Loups-de-givre", nil, {"Rendez-vous dans la vallée d'Alterac, à l'intérieur des montagnes d'Alterac. Trouvez le maître de guerre Laggrond à l'extérieur du tunnel d'entrée, parlez-lui et commencez votre carrière comme soldat Loup-de-givre. Vous trouverez la vallée d'Alterac au nord de Moulin-de-Tarren à la base des montagnes d'Alterac."}},
[7261] = {"L'impératif souverain", nil, {"Voyagez jusqu'à la vallée d'Alterac, dans les contreforts de Hautebrande. Présentez-vous au lieutenant Haggerdin à l'extérieur du tunnel d'entrée.\n\nPour la gloire de Barbe-de-bronze !"}},
[7281] = {"Amour fraternel", nil, {"Pénétrez en territoire Foudrepique et tuez le commandant Karl Philips. Lorsque ce sera fait, présentez-vous au commandant Louis Philips."}},
[7282] = {"Amour fraternel", nil, {"Pénétrez en territoire Loup-de-givre et tuez le commandant Louis Philips. Lorsque ce sera fait, présentez-vous au commandant Karl Philips."}},
[7301] = {"Les ailes brisées", nil, {"Enfoncez-vous en territoire Loup-de-givre à la recherche des chefs d'escadrille Slidore, Vipore et Ichman. Si vous les retrouvez, présentez-vous au commandant Duffy."}},
[7302] = {"Les ailes brisées", nil, {"Découvrez ce qui est arrivé aux chefs d'escadrille Guse, Jetzor et Mulverick. Lorsque vous les aurez retrouvés, présentez-vous au commandant Mulfort."}},
[7321] = {"[PÉRIMÉ]Bisque de tortue apaisante", nil, {"Apporter 10 morceaux de Viande de tortue à Christoph Jeffcoat à Moulin-de-Tarren."}},
[7367] = {"Désarmer les mines", nil, {"Trouvez l'Expert en explosifs Loup-de-givre et tuez-le. Lorsque ce sera fait, présentez-vous à l'Expert en explosifs Foudrepique."}},
[7383] = {"Teldrassil : le fardeau des Kaldorei", nil, {"Remplissez la Flasque d'améthyste."}},
[7385] = {"Quelques litres de sang", nil, {"Conditions :"}},
[7386] = {"Grappes de cristaux", nil, {"Conditions :"}},
[7441] = {"Pusillin et l'Ancien Azj'Tordin", nil, {"Rendez-vous à Hache-tripes et trouvez le diablotin Pusillin. Persuadez-le par tous les moyens de vous rendre le Livre d'Incantations d'Azj'Tordin.\n\nSi vous parvenez à le récupérer, retournez voir Azj'Tordin au Pavillon de Lariss, en Féralas."}},
[7461] = {"Folie intérieure", nil, {"Vous devez détruire les gardiens qui protègent les 5 Pylônes qui alimentent la Prison d'Immol'thar. Une fois que les Pylônes seront coupés, le champ de force qui entoure Immol'thar se dissipera.\n\nEntrez dans la Prison d'Immol'thar et éradiquez l'infâme démon qui se trouve en son cœur. Enfin, affrontez le prince Tortheldrin à l'Athenaeum.\n\nLorsque vous aurez terminé, retournez voir l'Ancienne des Shen'dralar dans la cour."}},
[7463] = {"Rafraîchissement arcanique", nil, {"Rendez-vous dans le quartier de Crochebois de Hache-tripes et tuez Hydrogénos, l'élémentaire de l'eau. Retournez voir le Gardien du savoir Lydros à l'Athenaeum avec l'Essence d'Hydrogénos."}},
[7481] = {"Légendes elfiques", nil, {"Fouillez Hache-tripes à la recherche de Telmius Cherche-Rêve. Lorsque vous aurez des informations, donnez-les au Sage Korolusk du Camp Mojache."}},
[7482] = {"Légendes elfiques", nil, {"Fouillez Hache-tripes à la recherche de Telmius Cherche-Rêve. Lorsque vous aurez des informations, donnez-les à l’Erudite Roncerune, à Pennelune."}},
[7483] = {"Libram de rapidité", nil, {"Apportez un Libram de rapidité, 1 Diamant noir parfait, 2 Grands éclats brillants et 2 doses de Sang des héros au Gardien du savoir Lydros à Hache-Tripes pour recevoir un Arcanum de rapidité."}},
[7484] = {"Libram de focalisation", nil, {"Apportez un Libram de focalisation, 1 Diamant noir en parfait état, 4 Grands éclats brillants et 2 Peaux des ombres au Gardien du savoir Lydros à Hache-Tripes pour recevoir un arcanum de focalisation."}},
[7485] = {"Libram de protection", nil, {"Apportez un Libram de protection, 1 Diamant noir en parfait état, 2 Grands éclats brillants et 1 Couture d'abomination effilochée au Gardien du savoir Lydros à Hache-Tripes pour recevoir un Arcanum de protection."}},
[7486] = {"La récompense du héros", nil, {"Allez chercher votre récompense dans le Coffre d'Hydraxis."}},
[7488] = {"Le filet de Lethtendris", nil, {"Rapportez le Filet de Lethtendris à Latronicus Lancelune au bastion de Pennelune en Féralas."}},
[7489] = {"Le filet de Lethtendris", nil, {"Ramenez le Filet de Lethtendris à Talo Sabot-de-ronce au Camp Mojache, en Féralas."}},
[7490] = {"Victoire pour la Horde !", nil, {"Apportez la Tête d’Onyxia à Garrosh à Orgrimmar."}},
